(window.webpackJsonp=window.webpackJsonp||[]).push([[1215],{1238:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u8282 \u5de7\u5408\u4e48\uff1f",content:"<article><h2>\u7b2c\u516d\u8282 \u5de7\u5408\u4e48\uff1f</h2><p>\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5636\u54d1\u3001\u4f4e\u6c89\u3001\u6709\u6c14\u65e0\u529b\uff0c\u50cf\u662f\u884c\u5c06\u5c31\u6728\u7684\u8001\u4eba\u53e3\u4e2d\u53d1\u51fa\u3002</p><p>\u4f46\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u54ea\u4e2a\u8001\u4eba\u80fd\u542c\u61c2\u6c49\u8bed\uff1f</p><p>\u5982\u679c\u4ec5\u4ec5\u5982\u6b64\u5012\u4e5f\u7f62\u4e86\uff0c\u53ef\u80fd\u53ea\u662f\u8c01\u8bef\u6253\u8bef\u649e\u968f\u53e3\u8bf4\u53e5\u8bdd\u2014\u2014\u95ee\u9898\u5728\u4e8e\uff1a\u521a\u624d\u201c\u8bf4\u5f97\u597d\u201d\u8fd9\u4e09\u4e2a\u5b57\uff0c\u5c45\u7136\u4e5f\u662f\u6c49\u8bed\u3002</p><p>\u6709\u4e2a\u8001\u4eba\u5728\u7528\u6c49\u8bed\u5bf9\u743c\u6069\u8bf4\uff1a\u201c\u8bf4\u5f97\u597d\u3002\u201d</p><p>\u743c\u6069\u6162\u6162\u8f6c\u8fc7\u8eab\uff0c\u770b\u5230\u4e00\u4e2a\u79c3\u5934\u8001\u4eba\uff0c\u8eab\u5f62\u4f5d\u507b\uff0c\u795e\u8272\u8870\u9893\uff0c\u624b\u4e2d\u7275\u7740\u4e00\u53ea\u540c\u6837\u8001\u8fc8\u7684\u79c3\u6bdb\u72d7\u3002</p><p>\u9694\u58c1\u7684\u54d1\u5df4\u8001\u4f2f\u3002</p><p>\u743c\u6069\u66fe\u542c\u7236\u6bcd\u8bf4\u8fc7\uff0c\u8fd9\u4f4d\u54d1\u5df4\u8001\u4f2f\u5176\u5b9e\u4ee5\u524d\u4e0d\u662f\u54d1\u5df4\uff0c\u4e0d\u4f46\u4e0d\u54d1\uff0c\u800c\u4e14\u5065\u8c08\u5f97\u5f88\u3002\u59bb\u5b50\u65e9\u6b7b\uff0c\u53c8\u65e0\u5b50\u5973\uff0c\u4e00\u4e2a\u4eba\u72ec\u5c45\u3002\u4f46\u5c31\u5728\u743c\u6069\u51fa\u751f\u90a3\u5e74\uff0c\u51c6\u786e\u5730\u8bf4\u597d\u50cf\u5c31\u662f\u4ed6\u548c\u73ca\u5609\u51fa\u751f\u90a3\u5929\uff0c\u8001\u4f2f\u8d70\u8def\u91cd\u91cd\u6454\u4e86\u4e00\u8de4\uff0c\u8fd9\u79cd\u4e0a\u4e86\u5e74\u7eaa\u7684\u4eba\uff0c\u5f88\u5bb9\u6613\u4e00\u8de4\u5c31\u6454\u51fa\u4eba\u547d\u3002\u8001\u4f2f\u8fd0\u6c14\u597d\uff0c\u867d\u7136\u5f53\u665a\u53d1\u9ad8\u70e7\uff0c\u5230\u5e95\u8fd8\u633a\u4e86\u8fc7\u6765\uff0c\u53ea\u662f\u81ea\u6b64\u4e4b\u540e\u5c31\u6210\u4e86\u54d1\u5df4\uff0c\u518d\u4e5f\u4e0d\u8bf4\u4e00\u53e5\u8bdd\u4e86\uff0c\u6027\u683c\u4e5f\u53d8\u5f97\u602a\u50fb\u8d77\u6765\uff0c\u6574\u5929\u6574\u591c\u628a\u81ea\u5df1\u5173\u5728\u5c4b\u5b50\u91cc\uff0c\u6781\u5c11\u548c\u5916\u4eba\u4ea4\u5f80\u4e86\u3002</p><p>\u4e5f\u56e0\u4e3a\u5982\u6b64\uff0c\u743c\u6069\u548c\u54d1\u5df4\u8001\u4f2f\u7b97\u662f\u4e03\u5e74\u7684\u90bb\u5c45\u4e86\uff0c\u89c1\u9762\u53ef\u80fd\u8fd8\u4e0d\u8d85\u8fc7\u4e00\u767e\u6b21\uff0c\u53cd\u5012\u662f\u4ed6\u5bb6\u7684\u79c3\u6bdb\u72d7\u5929\u5929\u89c1\u5230\u3002\u5373\u4f7f\u5076\u7136\u9047\u89c1\uff0c\u743c\u6069\u4e5f\u61d2\u5f97\u6253\u62db\u547c\uff0c\u54d1\u5df4\u8001\u4f2f\u81ea\u7136\u66f4\u4e0d\u8bf4\u8bdd\uff0c\u4eff\u4f5b\u6ca1\u770b\u89c1\u5f7c\u6b64\u4e00\u822c\u3002\u8fd9\u5012\u4e0d\u80fd\u8bf4\u743c\u6069\u4e0d\u5c0a\u656c\u8001\u4eba\uff0c\u53ea\u662f\u8fd9\u8001\u4f2f\u8eab\u4e0a\u900f\u7740\u4e00\u80a1\u9634\u68ee\u68ee\u7684\u5473\u9053\uff0c\u5b9e\u5728\u8ba9\u743c\u6069\u671b\u800c\u751f\u754f\uff0c\u907f\u800c\u8fdc\u4e4b\u3002</p><p>\u73b0\u5728\uff0c\u5c31\u662f\u8fd9\u6837\u4e00\u4e2a\u8001\u4eba\uff0c\u7ad9\u5728\u5893\u5730\u91cc\uff0c\u5728\u743c\u6069\u80cc\u540e\uff0c\u7528\u4ed6\u524d\u4e16\u7684\u8bed\u8a00\u8bf4\uff1a\u201c\u8bf4\u5f97\u597d\u3002\u201d</p><p>\u8fd9\u771f\u662f\u6d3b\u89c1\u9b3c\u4e86\u3002</p><p>\u743c\u6069\u7d27\u5f20\u5730\u770b\u7740\u4ed6\uff0c\u4e00\u65f6\u624b\u8db3\u65e0\u63aa\uff0c\u4e0d\u77e5\u9053\u8be5\u505a\u4ec0\u4e48\u3002\u8111\u4e2d\u5ff5\u5934\u5ffd\u9690\u5ffd\u73b0\uff0c\u8fde\u6740\u4eba\u706d\u53e3\u7684\u5fc3\u90fd\u8d77\u4e86\u3002\u73ca\u5609\u5012\u6bd4\u8f83\u8f7b\u677e\uff0c\u5979\u4e5f\u6839\u672c\u6ca1\u542c\u61c2\u8001\u5934\u5b50\u8bf4\u4ec0\u4e48\uff0c\u53ea\u4ee5\u4e3a\u662f\u54d1\u5df4\u53d1\u51fa\u7684\u65e0\u610f\u4e49\u97f3\u8282\u3002</p><p>\u201c\u60a8\u597d\u3002\u201d\u5979\u8eac\u8eab\u884c\u4e86\u4e2a\u793c\u3002</p><p>\u8001\u5934\u4e0d\u8bf4\u8bdd\uff0c\u4e00\u53cc\u6df7\u6d4a\u7684\u773c\u775b\u76ef\u7740\u743c\u6069\uff0c\u76f4\u770b\u5f97\u4ed6\u5fc3\u91cc\u53d1\u6bdb\u3002\u73ca\u5609\u53d1\u89c9\u6c14\u6c1b\u6709\u70b9\u4e0d\u5bf9\u52b2\uff0c\u53c8\u4e0d\u77e5\u600e\u4e48\u56de\u4e8b\uff0c\u6b63\u8981\u62c9\u7740\u743c\u6069\u79bb\u5f00\uff0c\u8001\u5934\u7a81\u7136\u671d\u7740\u4ed6\u4eec\u8d70\u8fc7\u6765\u3002</p><p>\u743c\u6069\u7d27\u5f20\u5730\u770b\u7740\u8001\u5934\u3002</p><p>\u8001\u5934\u6162\u60a0\u60a0\u62ac\u8d77\u624b\uff0c\u5728\u743c\u6069\u7684\u5934\u4e0a\u8f7b\u8f7b\u62cd\u4e86\u4e09\u4e0b\uff0c\u5012\u80cc\u7740\u624b\uff0c\u4f5d\u507b\u7740\u8eab\u4f53\u6162\u817e\u817e\u8d70\u4e86\uff0c\u90a3\u53ea\u79c3\u6bdb\u72d7\u8fde\u5fd9\u4e00\u98a0\u4e00\u98a0\u5730\u8ddf\u4e0a\u3002</p><p>\u743c\u6069\u4e00\u65f6\u6709\u4e9b\u832b\u832b\u7136\uff0c\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u3002\u8001\u5bb6\u4f19\u62cd\u8fd9\u4e09\u4e0b\uff0c\u9664\u4e86\u8ba9\u4ed6\u6682\u65f6\u6709\u70b9\u5934\u6655\u4e4b\u5916\uff0c\u4f3c\u4e4e\u4e5f\u522b\u65e0\u6548\u679c\uff0c\u603b\u4e0d\u4f1a\u8bf4\u8001\u5934\u7ec3\u6210\u4e86\u4f20\u8bf4\u4e2d\u7684\u5316\u9aa8\u7ef5\u638c\uff1f\u90a3\u4e5f\u5e94\u8be5\u62cd\u4ed6\u8eab\u4e0a\u624d\u5bf9\u3002</p><p>\u6d51\u6d51\u5669\u5669\u95f4\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u88ab\u59d0\u59d0\u62c9\u51fa\u5893\u5730\u3002\u8def\u4e0a\u88ab\u98ce\u4e00\u5439\uff0c\u5934\u8111\u624d\u7b97\u6e05\u9192\u8fc7\u6765\uff0c\u5f00\u59cb\u6162\u6162\u5206\u6790\u3002</p><p>\u9996\u5148\uff0c\u8fd9\u8001\u5bb6\u4f19\u6781\u6709\u53ef\u80fd\u548c\u81ea\u5df1\u4e00\u6837\uff0c\u662f\u7a7f\u8d8a\u8fc7\u6765\u7684\uff0c\u800c\u4e14\u539f\u672c\u90fd\u662f\u5730\u7403\u4eba\uff0c\u4e5f\u662f\u4e2d\u56fd\u4eba\uff0c\u5426\u5219\u600e\u4e48\u4f1a\u8bf4\u6c49\u8bed\uff0c\u600e\u4e48\u61c2\u4ed6\u8bf4\u7684\u90a3\u53e5\u8bdd\u3002</p><p>\u5176\u6b21\uff0c\u7236\u6bcd\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u8fd9\u8001\u5934\u5c31\u662f\u4ed6\u51fa\u751f\u90a3\u5e74\u5927\u75c5\u4e0d\u6b7b\uff0c\u53d8\u6210\u54d1\u5df4\u3002\u8fd9\u66f4\u8bc1\u660e\u4ed6\u548c\u81ea\u5df1\u4e00\u6837\uff0c\u90fd\u662f\u90a3\u5e74\u7a7f\u8fc7\u6765\u7684\u3002\u4f46\u4ed6\u4e0d\u662f\u50cf\u81ea\u5df1\u4e00\u6837\u6295\u80ce\u8f6c\u4e16\uff0c\u800c\u662f\u76f4\u63a5\u593a\u4f53\u9644\u8eab\uff0c\u7ed3\u679c\u6765\u4e86\u4e4b\u540e\uff0c\u4e0d\u61c2\u8fd9\u4e2a\u4e16\u754c\u7684\u8bed\u8a00\uff0c\u53c8\u6015\u88ab\u4eba\u770b\u51fa\u7834\u7efd\uff0c\u6240\u4ee5\u53ea\u597d\u4e00\u76f4\u88c5\u54d1\u5df4\u3002</p><p>\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u73b0\u5728\u53d1\u73b0\u4e86\u6211\u4e5f\u662f\u7a7f\u8d8a\u8005\uff0c\u53c8\u662f\u4ec0\u4e48\u6253\u7b97\u5462\uff1f\u201d\u743c\u6069\u601d\u7d22\u7740\u3002</p><p>\u201c\u83ab\u975e\u60f3\u5e72\u6389\u6211\u706d\u53e3\uff1f\u4e0d\uff0c\u4e0d\u4f1a\uff0c\u201d\u743c\u6069\u968f\u5373\u81ea\u5df1\u5426\u5b9a\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u201c\u4e0d\u592a\u53ef\u80fd\uff0c\u800c\u4e14\u4e5f\u6ca1\u4ec0\u4e48\u5fc5\u8981\uff0c\u4ed6\u90fd\u8fd9\u4e48\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\uff0c\u4e5f\u6ca1\u51e0\u5e74\u597d\u6d3b\u4e86\uff0c\u4f55\u5fc5\u4e0e\u6211\u4e3a\u96be\u3002\u201d</p><p>\u90a3\u4e48\uff0c\u8001\u5934\u5728\u5934\u4e0a\u62cd\u8fd9\u4e09\u4e0b\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u603b\u4e0d\u6210\u662f\u60f3\u6536\u81ea\u5df1\u4e3a\u5f92\u5427\u3002</p><p>\u7b49\u7b49\uff0c\u6536\u5f92\uff1f\u5934\u4e0a\u62cd\u4e09\u4e0b\uff0c\u5012\u80cc\u7740\u624b\u8d70\u4eba\u2014\u2014\u8fd9\u4e00\u5e55\u597d\u719f\u6089\u554a\uff0c\u4f3c\u4e4e\u5728\u54ea\u672c\u4e66\u4e0a\u8bfb\u5230\u8fc7\uff1f</p><p>\u897f\u6e38\u8bb0\u2026\u2026</p><p>\u203b\u203b\u203b</p><p>\u897f\u6e38\u8bb0\u540d\u5217\u56db\u5927\u540d\u8457\u4e4b\u4e00\uff0c\u743c\u6069\u7a7f\u8d8a\u524d\u81ea\u7136\u662f\u8bfb\u8fc7\u7684\uff0c\u5176\u4e2d\u60c5\u8282\u5c31\u7b97\u8bf4\u4e0d\u4e0a\u5012\u80cc\u5982\u6d41\uff0c\u5374\u4e5f\u6eda\u74dc\u70c2\u719f\u3002</p><p>\u5728\u897f\u6e38\u8bb0\u91cc\uff0c\u66fe\u7ecf\u6709\u67d0\u53ea\u7334\u5b50\u53bb\u62dc\u5e08\uff0c\u5b66\u957f\u751f\u4e0d\u8001\u4e4b\u672f\u3002\u5e08\u7236\u5728\u4ed6\u5934\u4e0a\u62cd\u4e86\u4e09\u4e0b\uff0c\u80cc\u7740\u624b\u8d70\u56de\u53bb\u3002\u8fd9\u7334\u5b50\u4e43\u662f\u5929\u5730\u5b55\u80b2\u7684\u7075\u7269\uff0c\u679c\u7136\u806a\u660e\uff0c\u609f\u51fa\u4e86\u8fd9\u5176\u4e2d\u7684\u7384\u673a\u5965\u5999\uff0c\u4e8e\u662f\u5f53\u5929\u665a\u4e0a\u4e09\u66f4\uff0c\u4ece\u540e\u95e8\u8dd1\u53bb\u89c1\u5e08\u7236\uff0c\u5f97\u4f20\u5927\u9053\uff0c\u4fee\u6210\u4e86\u593a\u5929\u5730\u9020\u5316\u7684\u5927\u795e\u901a\u3002</p><p>\u201c\u8fd9\u8001\u5934\u65e2\u7136\u548c\u6211\u4e00\u6837\u662f\u4ece\u4e2d\u56fd\u7a7f\u8d8a\u6765\u7684\uff0c\u897f\u6e38\u8bb0\u8fd9\u79cd\u5bb6\u55bb\u6237\u6653\u7684\u6545\u4e8b\uff0c\u80af\u5b9a\u4e5f\u662f\u77e5\u9053\u7684\u3002\u5982\u6b64\u770b\u6765\uff0c\u83ab\u975e\u4ed6\u6709\u610f\u8981\u6536\u6211\u4e3a\u5f92\uff1f\u201d</p><p>\u7b97\u4e86\u5427\uff0c\u4e0d\u8fc7\u662f\u4e2a\u884c\u5c06\u5c31\u6728\u7684\u7cdf\u8001\u5934\uff0c\u80fd\u6709\u4ec0\u4e48\u672c\u4e8b\u3002\u771f\u6709\u672c\u4e8b\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u4e03\u5e74\u4e86\u8fd8\u5728\u8fd9\u91cc\u6df7\u65e5\u5b50\u4e86\u3002\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u743c\u6069\u4e5f\u4e0d\u6307\u671b\u8fd9\u8001\u5934\u80fd\u4f20\u6388\u4ec0\u4e48\u672c\u4e8b\uff0c\u4f46\u4f1a\u4e0d\u4f1a\u5927\u53d1\u5584\u5fc3\uff0c\u6307\u5b9a\u81ea\u5df1\u4e3a\u9057\u4ea7\u7ee7\u627f\u4eba\u5462\uff0c\u53cd\u6b63\u4ed6\u4e5f\u6ca1\u51e0\u5e74\u597d\u6d3b\u4e86\u5440\u3002\u867d\u7136\u770b\u4ed6\u5bb6\u7834\u7834\u70c2\u70c2\u7684\u6837\u5b50\uff0c\u4f30\u4e0d\u4f1a\u6709\u591a\u5c11\u79ef\u84c4\uff0c\u4f46\u4eba\u4e0d\u53ef\u8c8c\u76f8\uff0c\u8bf4\u4e0d\u5b9a\u771f\u6709\u70b9\u5b9d\u8d1d\u5462\u3002</p><p>\u5999\u6781\u5999\u6781\u3002</p><p>\u201c\u60f3\u4ec0\u4e48\u5462\uff1f\u201d\u73ca\u5609\u5728\u743c\u6069\u5934\u4e0a\u6572\u4e86\u4e00\u4e0b\uff0c\u201c\u4e00\u4e2a\u4eba\u50bb\u50bb\u5730\u53d1\u7b11\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u59d0\u5f1f\u4fe9\u56de\u5230\u5bb6\u4e2d\uff0c\u4e5f\u65e0\u522b\u8bdd\u3002\u9634\u9b42\u57ce\u4e2d\u7684\u751f\u6d3b\uff0c\u8fd1\u4e4e\u5343\u767e\u5e74\u4e00\u6210\u4e0d\u53d8\uff0c\u65e0\u975e\u662f\u5f00\u5e97\u3001\u4e70\u5356\u3001\u6574\u7406\u6750\u6599\u3001\u5403\u996d\u2014\u2014\u6700\u540e\u5c31\u662f\u591c\u5e55\u964d\u4e34\uff0c\u8be5\u4e0a\u5e8a\u7761\u89c9\u4e86\u3002</p><p>\u7236\u6bcd\u53bb\u4e16\u90a3\u5929\uff0c\u8d81\u7740\u73ca\u5609\u53d1\u70e7\u751f\u75c5\u7684\u673a\u4f1a\uff0c\u743c\u6069\u628a\u59d0\u59d0\u5265\u6210\u8d64\u88f8\u5c0f\u7f94\u7f8a\u4e00\u822c\uff0c\u62b1\u5728\u6000\u91cc\u5165\u7761\uff0c\u603b\u7b97\u662f\u5c0f\u5c0f\u5730\u6ee1\u8db3\u4e86\u4e00\u70b9\u5fc3\u613f\u3002</p><p>\u867d\u8bf4\u53ea\u662f\u4e03\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u5e73\u5766\u7684\u80f8\u90e8\u548c\u7f3a\u4e4f\u66f2\u7ebf\u7684\u8eab\u6750\u8ba9\u4eba\u63d0\u4e0d\u8d77\u591a\u5c11\u6b32\u671b\uff0c\u4f46\u5979\u5929\u751f\u7684\u808c\u80a4\u7ec6\u817b\u5ae9\u6ed1\uff0c\u6402\u62b1\u7740\u5374\u4e5f\u522b\u6709\u4e00\u756a\u6e29\u9999\u8f6f\u7389\u7684\u611f\u89c9\u3002\u81ea\u90a3\u5929\u4e4b\u540e\uff0c\u743c\u6069\u5c31\u501f\u53e3\u665a\u4e0a\u592a\u51b7\uff0c\u7761\u4e0d\u7740\u89c9\uff0c\u975e\u8981\u94bb\u8fdb\u59d0\u59d0\u7684\u88ab\u7a9d\u91cc\u6765\u3002\u597d\u5728\u73ca\u5609\u5374\u4e5f\u6ca1\u6709\u4e0d\u60a6\uff0c\u5927\u7ea6\u4e5f\u53ea\u5f53\u4e2a\u987d\u76ae\u7684\u5f1f\u5f1f\u770b\u5f85\uff0c\u4efb\u7531\u4ed6\u6765\u3002</p><p>\u743c\u6069\u987a\u5229\u5f97\u624b\uff0c\u81ea\u6b64\u6bcf\u665a\u94bb\u5230\u59d0\u59d0\u8eab\u8fb9\uff0c\u6709\u610f\u65e0\u610f\u5730\u4e0a\u4e0b\u5176\u624b\u3002\u5e7d\u5f71\u754c\u662f\u6697\u591c\u5973\u795e\u838e\u5c14\u7684\u5730\u76d8\uff0c\u81ea\u7136\u6ca1\u6709\u592a\u9633\u8fd9\u79cd\u4e1c\u897f\uff0c\u8fde\u5149\u6e90\u90fd\u6ca1\u6709\u3002\u9634\u9b42\u57ce\u867d\u7136\u6709\u4e2a\u4eba\u9020\u7684\u592a\u9633\uff0c\u4f46\u4e3b\u8981\u8fd8\u662f\u8d1f\u8d23\u53d1\u5149\uff0c\u6563\u70ed\u529f\u80fd\u4e0d\u8db3\uff0c\u800c\u4e14\u4e00\u5929\u53ea\u7167\u660e\u516b\u5c0f\u65f6\u3002\u6240\u4ee5\u9634\u9b42\u57ce\u7684\u591c\u665a\uff0c\u662f\u975e\u5e38\u5bd2\u51b7\u7684\u3002</p><p>\u73ca\u5609\u662f\u4e2a\u5973\u5b69\uff0c\u8eab\u4f53\u5929\u751f\u504f\u9634\u6027\uff0c\u665a\u4e0a\u72ec\u81ea\u4e00\u4eba\u7761\u89c9\uff0c\u88ab\u7a9d\u91cc\u603b\u662f\u6696\u4e0d\u8d77\u6765\uff0c\u624b\u8db3\u5f80\u5f80\u534a\u5929\u90fd\u662f\u51b0\u51b7\u7684\u3002\u81ea\u4ece\u88ab\u743c\u6069\u62b1\u7740\uff0c\u4fbf\u7b49\u4e8e\u6709\u4e86\u4e2a\u8d34\u8eab\u7684\u4eba\u8089\u6696\u7089\uff0c\u5012\u662f\u80fd\u6bcf\u665a\u9999\u751c\u5165\u7761\uff0c\u65f6\u95f4\u957f\u4e86\uff0c\u4e5f\u5c31\u4e60\u60ef\u3002</p><p>\u5fd9\u788c\u4e86\u4e00\u5929\uff0c\u73ca\u5609\u5df2\u7ecf\u5f88\u75b2\u5026\u4e86\uff0c\u6d17\u5b8c\u6fa1\u4fbf\u56de\u5230\u5367\u5ba4\uff0c\u7136\u540e\u88ab\u65e9\u5c31\u8eb2\u5728\u88ab\u5b50\u91cc\u7684\u743c\u6069\u4e00\u628a\u62c9\u4e0a\u5e8a\uff0c\u62b1\u5728\u6000\u91cc\u3002\u7d27\u63a5\u7740\uff0c\u73ca\u5609\u4fbf\u611f\u89c9\u5230\u743c\u6069\u7684\u4e00\u53ea\u624b\u79fb\u5230\u81ea\u5df1\u80f8\u53e3\uff0c\u5f00\u59cb\u89e3\u7761\u8863\u7684\u6263\u5b50\u3002</p><p>\u201c\u522b\u95f9\uff0c\u4e56\uff0c\u6211\u56f0\u4e86\u3002\u201d\u73ca\u5609\u8bf4\uff0c\u4f38\u624b\u628a\u743c\u6069\u7684\u624b\u62e8\u5f00\u3002</p><p>\u743c\u6069\u6682\u65f6\u9000\u5374\u4e86\uff0c\u4ece\u540e\u9762\u63fd\u4f4f\u73ca\u5609\u7684\u8170\uff0c\u5c06\u59d0\u59d0\u62b1\u5728\u6000\u91cc\u3002\u5c0f\u5973\u5b69\u5f88\u5feb\u4fbf\u7761\u7740\u4e86\uff0c\u4e8e\u662f\u743c\u6069\u7684\u624b\u6084\u6084\u53c8\u79fb\u5230\u80f8\u524d\uff0c\u4e00\u7c92\u4e00\u7c92\u5730\u5c06\u7761\u8863\u7684\u6263\u5b50\u5168\u89e3\u4e86\u4e0b\u6765\uff0c\u4ece\u5979\u8eab\u4e0a\u6084\u6084\u892a\u4e0b\u3002</p><p>\u4ed6\u4e0d\u559c\u6b22\u73ca\u5609\u7a7f\u7740\u7761\u8863\u3002</p><p>\u7761\u68a6\u4e2d\u7684\u73ca\u5609\u6ca1\u6709\u53cd\u6297\uff0c\u5927\u7ea6\u4e5f\u5df2\u7ecf\u4e60\u60ef\u4e86\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u88ab\u743c\u6069\u5c06\u7761\u8863\u5168\u90e8\u892a\u4e0b\uff0c\u53c8\u5f80\u6e29\u6696\u7684\u6000\u62b1\u91cc\u8d34\u4e86\u8d34\uff0c\u7ee7\u7eed\u7761\u7740\u3002</p><p>\u743c\u6069\u9759\u9759\u5730\u7b49\u5f85\u7740\uff0c\u4e00\u76f4\u7b49\u4ed6\u786e\u5b9a\u73ca\u5609\u5df2\u7ecf\u7761\u719f\uff0c\u7136\u540e\u6084\u6084\u8d77\u8eab\uff0c\u722c\u4e0b\u5e8a\uff0c\u5f00\u95e8\u51fa\u6765\u3002</p><p>\u767d\u5929\u70ed\u95f9\u7684\u8857\u9053\u53d8\u5f97\u51b7\u51b7\u6e05\u6e05\uff0c\u9b54\u6cd5\u70b9\u71c3\u7684\u8def\u706f\u53d1\u7740\u660f\u6697\u7684\u5fae\u5149\uff0c\u968f\u7740\u4ed6\u7684\u811a\u6b65\u58f0\u4e00\u95ea\u4e00\u70c1\u3002\u743c\u6069\u6084\u6084\u8d70\u51fa\u5bb6\u95e8\uff0c\u8d34\u7740\u5899\u6839\uff0c\u4e00\u76f4\u8d70\u5230\u9694\u58c1\u8001\u5934\u7684\u5bb6\u95e8\u53e3\u3002</p><p>\u4f38\u624b\u63a8\u95e8\uff0c\u4e00\u78b0\u5373\u5f00\uff0c\u679c\u7136\u6ca1\u6709\u9501\u4e0a\u3002\u743c\u6069\u505a\u4e86\u4e2a\u6df1\u547c\u5438\uff0c\u58ee\u7740\u80c6\u5b50\u8d70\u8fdb\u53bb\u3002</p><p>\u5c4b\u5b50\u91cc\u9ed1\u6f06\u6f06\u7684\uff0c\u900f\u7740\u80a1\u9634\u68ee\u68ee\u7684\u6c14\u5473\uff0c\u8fd8\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u5947\u602a\u5473\u9053\uff0c\u50cf\u662f\u67d0\u79cd\u836f\u7269\u3002\u743c\u6069\u5b9a\u4e86\u5b9a\u795e\uff0c\u52c9\u529b\u8ba9\u81ea\u5df1\u7684\u773c\u775b\u9002\u5e94\u9ed1\u6697\uff0c\u53c8\u501f\u7740\u5916\u9762\u900f\u8fdb\u6765\u7684\u6708\u5149\uff0c\u603b\u7b97\u6a21\u6a21\u7cca\u7cca\u770b\u5230\u5468\u56f4\u7684\u8f6e\u5ed3\u3002</p><p>\u201c\u6765\u4e86\uff1f\u201d</p><p>\u8001\u5934\u9634\u607b\u607b\u7684\u58f0\u97f3\u7a81\u7136\u5728\u80cc\u540e\u54cd\u8d77\uff0c\u7eb5\u7136\u4ed6\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u4e5f\u4e0d\u7981\u5168\u8eab\u4e00\u6296\u3002\u6765\u4e86\uff0c\u662f\u798f\u662f\u7978\uff0c\u5c31\u8d4c\u8fd9\u4e00\u628a\u3002</p><p>\u743c\u6069\u8f6c\u8fc7\u8eab\u6765\uff0c\u5fae\u5fae\u8eac\u8eab\uff1a\u201c\u8001\u5148\u751f\u3002\u201d</p><p>\u6b64\u6b21\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u7528\u7684\u90fd\u662f\u6c49\u8bed\u3002\u65e2\u7136\u5f7c\u6b64\u77e5\u6839\u77e5\u5e95\uff0c\u4e5f\u5c31\u4e0d\u7528\u9690\u7792\u3002\u743c\u6069\u6765\u8fd9\u4e2a\u4e16\u754c\u4e03\u5e74\uff0c\u5e73\u65f6\u81ea\u7136\u6ca1\u6709\u8bf4\u6c49\u8bed\u7684\u673a\u4f1a\uff0c\u4f46\u597d\u6b79\u8fd9\u662f\u81ea\u5df1\u7684\u6bcd\u8bed\uff0c\u4e0a\u8f88\u5b50\u53ef\u662f\u5df2\u7ecf\u8bf4\u4e86\u6574\u6574\u4e8c\u5341\u591a\u5e74\uff0c\u73b0\u5728\u91cd\u65b0\u8bf4\u8d77\u6765\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6ede\u788d\u3002</p><p>\u201c\u55ef\u3002\u201d\u8001\u5934\u70b9\u70b9\u5934\uff0c\u6253\u4e86\u4e2a\u54cd\u6307\u70b9\u4eae\u706f\u3002\u9634\u9b42\u57ce\u662f\u9b54\u6cd5\u975e\u5e38\u53d1\u8fbe\u7684\u57ce\u5e02\uff0c\u65e5\u5e38\u751f\u6d3b\u7684\u5f88\u591a\u65b9\u9762\u90fd\u5df2\u7ecf\u88ab\u9b54\u6cd5\u6e17\u900f\u3002\u5373\u4f7f\u662f\u5e73\u6c11\u5bb6\u4e2d\uff0c\u751f\u6d3b\u7167\u660e\u3001\u71c3\u6c14\u70ed\u6c34\u8fd9\u4e9b\u8bbe\u65bd\uff0c\u90fd\u662f\u9b54\u6cd5\u7269\u54c1\uff0c\u800c\u4e14\u4ef7\u683c\u6781\u5176\u4fbf\u5b9c\uff0c\u518d\u7a77\u7684\u4eba\u5bb6\u90fd\u7528\u5f97\u8d77\u3002</p><p>\u201c\u65e2\u7136\u6765\u4e86\uff0c\u201d\u4ed6\u58f0\u97f3\u5636\u54d1\u5730\u8bf4\uff0c\u201c\u5750\u7740\u5427\uff0c\u6211\u4eec\u6050\u6015\u8981\u8c08\u5f88\u957f\u65f6\u95f4\u3002\u201d</p><p>\u786e\u5b9e\u8c08\u4e86\u5f88\u4e45\u2014\u2014\u6574\u6574\u4e00\u591c\u3002\u5f53\u743c\u6069\u4ece\u8001\u5934\u2014\u2014\u54e6\uff0c\u73b0\u5728\u5e94\u8be5\u53eb\u4ed6\u5e08\u7236\u2014\u2014\u7684\u5bb6\u4e2d\u8d70\u51fa\u65f6\uff0c\u5916\u9762\u5929\u5df2\u7ecf\u4eae\u4e86\u3002</p></article>"}}}]);
