(window.webpackJsonp=window.webpackJsonp||[]).push([[122],{145:function(p){p.exports={chapterTitle:null,title:"\u7b2c1104\u7ae0 \u8c03\u620f\u8c01\u7684\u5973\u4eba",content:"<article><h2>\u7b2c1104\u7ae0 \u8c03\u620f\u8c01\u7684\u5973\u4eba</h2><p>\u3000\u3000\u6768\u84c9\u5e73\u795e\u8272\u51dd\u91cd\u3002</p><p>\u3000\u3000\u8054\u901a\u8981\u60f3\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u83b7\u5f97\u66f4\u5927\u7684\u6210\u672c\u4f18\u52bf\uff0c\u5c31\u8981\u4e0e\u9526\u6e56\u91c7\u53d6\u66f4\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u751a\u81f3\u8981\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u4e0a\u5168\u9762\u7684\u4f9d\u8d56\u9526\u6e56\u7684\u6280\u672f\u3002</p><p>\u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u8054\u901a\u5229\u7528\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u53d1\u5c55\u65b0\u7528\u6237\u7684\u8282\u594f\u4e0e\u89c4\u6a21\u5c31\u8981\u53d7\u5230\u9526\u6e56\u4ee5\u53ca\u76f8\u5173\u5408\u4f5c\u5382\u5546\u7684\u9650\u5236\uff1b\u540c\u65f6\u9526\u6e56\u4e5f\u5b8c\u5168\u53ef\u4ee5\u501f\u53e3\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u624b\u673a\u82af\u7247\u7ec4\u4ef6\u4ea7\u80fd\u4e0d\u8db3\u5c06\u79fb\u52a8\u901a\u4fe1\u5c40\u6392\u9664\u5728\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u63d0\u8bae\u867d\u7136\u6709\u8fdd\u8054\u901a\u65e2\u5b9a\u7684\u7ecf\u8425\u7b56\u7565\uff0c\u4f46\u662f\u4f1a\u53d7\u5230\u8054\u901a\u5185\u90e8\u6e29\u548c\u6d3e\u7684\u62e5\u62a4\uff0c\u4ed6\u6bd5\u7adf\u63d0\u51fa\u4e00\u6761\u786e\u51ff\u53ef\u884c\u7684\u4e2d\u95f4\u8def\u7ebf\uff0c\u65e2\u80fd\u63a8\u52a8\u8054\u901a\u7684\u5feb\u901f\u53d1\u5c55\uff0c\u4e5f\u907f\u514d\u6fc0\u8fdb\u8def\u7ebf\u53ef\u80fd\u5bfc\u81f4\u7684\u7ecf\u8425\u98ce\u9669\u3002</p><p>\u3000\u3000\u9526\u6e56\u80fd\u4ece\u4e2d\u83b7\u5f97\u5de8\u5927\u5229\u76ca\u4e5f\u662f\u663e\u800c\u6613\u89c1\u3002</p><p>\u3000\u3000\u524d\u671f\u7684\u5408\u4f5c\u8303\u7574\u53ea\u5305\u62ec\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u800c\u5f00\u53d1\u7684\u7cfb\u5217\u624b\u673a\u4ea7\u54c1\uff0c\u9526\u6e56\u4e3b\u8981\u4e3a\u8be5\u7cfb\u5217\u624b\u673a\u4ea7\u54c1\u63d0\u4f9b\u5305\u62ec\u57fa\u5e26\u3001\u5c04\u9891\u7b49\u5728\u5185\u7684\u6838\u5fc3\u5143\u5668\u4ef6\u4ee5\u53ca\u6838\u5fc3\u7684\u4ea7\u54c1\u6280\u672f\u89e3\u51b3\u65b9\u6848\uff0c\u6700\u7ec8\u7684\u624b\u673a\u4ea7\u54c1\u751f\u4ea7\u7531\u8054\u4fe1\u6216\u8005\u79d1\u738b\u9ad8\u79d1\u6216\u8005\u5176\u4ed6\u56fd\u5185\u624b\u673a\u5382\u5546\u5b8c\u6210\u3002</p><p>\u3000\u3000\u5b9e\u65bd\u76f8\u5173\u5408\u4f5c\u65b9\u6848\uff0c\u5c06\u4fc3\u4f7f\u9526\u6e56\u4e00\u6574\u5957\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u624b\u673a\u6280\u672f\u89e3\u51b3\u65b9\u6848\u66f4\u8fc5\u901f\u7684\u5360\u9886\u56fd\u5185\u7684\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\uff0c\u552f\u6709\u8d62\u5f97\u5e02\u573a\u7684\u6280\u672f\u624d\u66f4\u5177\u6210\u957f\u6027\uff0c\u4e5f\u5c06\u4ece\u5e9e\u5927\u5e02\u573a\u6c72\u53d6\u5de8\u989d\u7684\u5229\u6da6\u56de\u62a5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e8c\u811a\u9ad8\u8df7\u7684\u5750\u5728\u90a3\u91cc\u54c1\u5c1d\u4ed6\u63ba\u96ea\u78a7\u52a0\u51b0\u5757\u7684\u5f20\u88d5\u5e72\u7ea2\uff0c\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u5750\u5728\u4ed6\u7684\u659c\u5bf9\u9762\uff0c\u660e\u4eae\u7684\u773c\u7738\u5b50\u4e0d\u77e5\u9053\u662f\u8981\u907f\u5f00\u5f20\u606a\u7684\u89c6\u7ebf\u597d\u8fd8\u662f\u8981\u8fce\u4e0a\u5f20\u606a\u7684\u89c6\u7ebf\u597d\uff0c\u5173\u952e\u662f\u5f20\u606a\u7684\u773c\u795e\u98d8\u6765\u98d8\u53bb\u3001\u4f3c\u770b\u975e\u770b\uff0c\u8ba9\u5979\u96be\u4ee5\u7422\u78e8\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u5c42\u6b21\u7684\u8c08\u8bdd\uff0c\u4e0d\u8981\u8bf4\u5979\u4e86\uff0c\u5c31\u662f\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u8fd9\u65f6\u5019\u4e5f\u63d2\u4e0d\u4e0a\u5634\uff0c\u5c31\u8fde\u8096\u745e\u6c11\u4e5f\u6b63\u895f\u5371\u5750\uff0c\u88c5\u51fa\u5728\u601d\u8003\u7740\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u5728\u9526\u6e56\u4e0e\u8054\u901a\u9762\u524d\uff0c\u8054\u4fe1\u62ab\u7740\u56fd\u8d44\u7684\u76ae\uff0c\u5730\u4f4d\u5b9e\u9645\u4e0a\u662f\u76f8\u5f53\u88ab\u52a8\u7684\uff0c\u8096\u745e\u6c11\u8981\u62c5\u5fc3\u7684\u53ea\u662f\u9526\u6e56\u4f1a\u4e0d\u4f1a\u80cc\u5f03\u5f53\u521d\u7684\u53e3\u5934\u627f\u8bfa\u5c06\u8054\u4fe1\u7529\u5f00\u5355\u72ec\u4e0e\u8054\u901a\u8fdb\u884c\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u7684\u5408\u4f5c\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u7aef\u8d77\u6676\u83b9\u5254\u900f\u7684\u9ad8\u811a\u73bb\u7483\u676f\uff0c\u62bf\u4e86\u4e00\u53e3\u9152\uff0c\u8f6c\u8eab\u770b\u7740\u5f20\u606a\u3001\u9648\u4fe1\u751f\uff1a\u201c\u80fd\u95ee\u9526\u6e56\u7684\u65b0\u5382\u6295\u4ea7\u4e4b\u540e\u4f9b\u8d27\u80fd\u529b\u80fd\u63d0\u9ad8\u5230\u591a\u5c11\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5177\u4f53\u7684\u6570\u636e\u8fd8\u662f\u8981\u95ee\u4e13\u4e1a\u4eba\u58eb\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u4e0d\u7792\u6768\u603b\uff0c\u7b2c\u4e8c\u4ee3ESS\u57fa\u5e26\u82af\u7247\u5df2\u7ecf\u5177\u4f53\u6295\u4ea7\u7684\u6761\u4ef6\uff0c\u5c31\u7b49\u6d77\u5dde\u7684\u65b0\u5de5\u5382\u51c6\u5907\u5c31\u7eea\uff0c\u5c31\u80fd\u6e90\u6e90\u4e0d\u65ad\u7684\u751f\u4ea7\u65b0\u4e00\u4ee3\u7684\u57fa\u9891\u677f\u6765\u3002\u6625\u8282\u8fc7\u540e\u7684\u516d\u4e2a\u6708\u5185\uff0c\u603b\u4f9b\u8d27\u80fd\u529b\u4e0d\u4f1a\u4f4e\u4e8e\u4e09\u767e\u4e07\u5957\uff0c\u4f46\u662f\u60f3\u63d0\u518d\u9ad8\u4e5f\u56f0\u96be\u3002\u516d\u4e2a\u6708\u540e\uff0c\u6211\u4eec\u8ba1\u5212\u63a8\u51fa\u4e8c\u4ee3\u589e\u5f3a\u578b\u7684\u57fa\u5e26\u82af\u7247\uff0c\u5c04\u9891\u3001\u7535\u6e90\u7ba1\u7406\u7b49\u82af\u7247\u6280\u672f\u5230\u65f6\u5019\u4f1a\u6709\u6240\u7a81\u7834\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u8fd9\u65f6\u5019\u9884\u4f30\u8fd8\u5acc\u65e9\u4e86\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u6e56\u5728\u6d77\u5dde\u5df2\u7ecf\u5f62\u6210\u5b8c\u6574\u7684\u751f\u4ea7\u4f53\u7cfb\uff0c\u8dd1\u5230\u6d77\u5dde\u53bb\uff0c\u4f1a\u53d1\u73b0\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\u5dee\u4e0d\u591a\u6709\u8fd1\u4e00\u534a\u5de5\u5382\u90fd\u662f\u5c5e\u4e8e\u9526\u6e56\u7684\uff0c\u8096\u745e\u6c11\u76f8\u4fe1\u9526\u6e56\u54ac\u54ac\u7259\uff0cESS\u57fa\u9891\u677f\u516d\u4e2a\u6708\u603b\u4f9b\u8d27\u80fd\u529b\u7a81\u7834\u4e00\u5343\u4e07\u5957\u90fd\u4e0d\u4f1a\u6210\u4e3a\u591a\u4e48\u8270\u5de8\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u6240\u8c13\u8fc7\u72b9\u4e0d\u53ca\u3001\u6b32\u901f\u5219\u4e0d\u8fbe\uff0c\u9526\u6e56\u8fd8\u662f\u80fd\u5f88\u597d\u638c\u63e1\u5206\u5bf8\u7684\u3002\u9526\u6e56\u4f9b\u8d27\u80fd\u529b\u592a\u5f3a\uff0c\u5728\u4ef7\u683c\u8c08\u5224\u4e0a\u5c31\u4f1a\u5904\u4e8e\u4f18\u52bf\uff1b\u9526\u6e56\u5728\u57fa\u9891\u677f\u4e0a\u7684\u6280\u672f\u50a8\u5907\u5e76\u4e0d\u80fd\u7b97\u7279\u522b\u7684\u5145\u5206\uff0c\u6b64\u65f6\u8fc7\u5feb\u7684\u6269\u5f20\uff0c\u52bf\u5fc5\u4f1a\u5f15\u8d77\u7ade\u4e89\u5bf9\u624b\u53cd\u5f39\u6027\u8d28\u7684\u538b\u5236\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8096\u745e\u6c11\u7709\u5934\u5fae\u8e59\u7740\u5750\u5728\u4e00\u65c1\u4e0d\u542d\u58f0\uff0c\u8ddf\u6768\u84c9\u5e73\u7b11\u7740\u8bf4\uff1a\u201c\u538b\u529b\u5927\u554a\uff0c\u6211\u4eec\u624b\u673a\u4ea7\u80fd\u4eca\u5e74\u4e5f\u53ea\u80fd\u8fbe\u5230\u516d\u767e\u4e07\u4ef6\uff0c\u660e\u5e74\u5728\u624b\u673a\u4ea7\u91cf\u4e0a\u4e5f\u4e0d\u4f1a\u6709\u591a\u5927\u7684\u7a81\u7834\uff0c\u516d\u4e2a\u6708\u989d\u5916\u591a\u4f9b\u5e94\u4e09\u767e\u4e07\u5957\u57fa\u9891\u677f\uff0c\u5dee\u4e0d\u591a\u5df2\u7ecf\u662f\u6211\u4eec\u7684\u6781\u9650\u4e86\u2026\u2026\u53e6\u5916\uff0c\u6768\u603b\u4f3c\u4e4e\u4e5f\u8981\u5173\u5fc3\u4e00\u4e0b\u8054\u4fe1\u7684\u914d\u5957\u4ea7\u80fd\u80fd\u4e0d\u80fd\u8ddf\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u6768\u84c9\u5e73\u5fae\u5fae\u4e00\u6123\uff0c\u5f20\u606a\u8fd9\u662f\u6446\u660e\u6001\u5ea6\u4e0d\u4f1a\u5411\u8054\u901a\u76f4\u63a5\u4f9b\u5e94\u5b9a\u5236\u624b\u673a\uff1b\u53e6\u5916\uff0c\u9526\u6e56\u4e5d\u4e5d\u5e74\u76f4\u63a5\u9500\u552e\u624b\u673a\u6570\u91cf\u5c31\u7a81\u7834\u56db\u767e\u4e07\u4ef6\uff0c\u516d\u767e\u4e07\u4ef6\u7684\u4ea7\u80fd\u4f3c\u4e4e\u4e5f\u592a\u4fdd\u5b88\u4e86\u4e00\u4e9b\uff1f</p><p>\u3000\u3000\u5f20\u606a\u7684\u8868\u6001\uff0c\u8ba9\u8096\u745e\u6c11\u5fc3\u91cc\u5378\u6389\u4e00\u5757\u5de8\u77f3\uff0c\u53ea\u8981\u9526\u6e56\u4e0d\u5c06\u8054\u4fe1\u8e22\u6389\uff0c\u53ea\u8981\u9526\u6e56\u627f\u8bfa\u4e24\u5e74\u65f6\u95f4\u5185\u4e0d\u76f4\u63a5\u751f\u4ea7ESS\u57fa\u9891\u677f\u624b\u673a\uff0c\u5c06\u8054\u901a\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u4e0e\u9526\u6e56\u7684ESS\u57fa\u9891\u677f\u6280\u672f\u6346\u7ed1\u5728\u4e00\u8d77\uff0c\u4e5f\u5c06\u7b26\u5408\u8054\u4fe1\u7684\u6700\u5927\u5229\u76ca\uff0c\u4ed6\u5f53\u7136\u4f1a\u7aed\u5c3d\u5168\u529b\u7684\u652f\u6301\u8bf4\u670d\u6768\u84c9\u5e73\u4ee5\u53ca\u8054\u901a\u5176\u4ed6\u7684\u9ad8\u5c42\u63a5\u53d7\u5f20\u606a\u7684\u5efa\u8bae\u3002</p><p>\u3000\u3000\u8054\u901a\u5c31\u7b97\u63a5\u53d7\u5f20\u606a\u7684\u5efa\u8bae\uff0c\u6682\u7f13\u6267\u884c\u5168\u7ebf\u94fa\u5f00\u7684\u7b56\u7565\uff0c\u96c6\u4e2d\u8d44\u6e90\u5728\u4e00\u4e8c\u7ebf\u4e2d\u5fc3\u57ce\u5e02\u63a8\u5e7f\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u53d1\u5c55\u65b0\u7528\u6237\uff0c\u8054\u901a\u4eca\u5e74\u7684\u5b9a\u5236\u624b\u673a\u91c7\u8d2d\u89c4\u6a21\u9884\u8ba1\u4e5f\u5c06\u8d85\u8fc7\u4e09\u767e\u4e07\u4ef6\u3002</p><p>\u3000\u3000\u5c31\u7b97\u7ed9\u79d1\u738b\u9ad8\u79d1\u8ddf\u5176\u4ed6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u5206\u6389\u4e00\u5927\u534a\uff0c\u8054\u4fe1\u660e\u5e74\u4e5f\u81f3\u5c11\u80fd\u4ece\u8054\u901a\u624b\u91cc\u76f4\u63a5\u83b7\u5f97\u4e00\u767e\u4e07\u4ef6\u7684\u624b\u673a\u5b9a\u5236\u8ba2\u5355\u3002\u8054\u4fe1\u4e5d\u4e5d\u5e74\u7684\u624b\u673a\u9500\u91cf\u8fd8\u521a\u521a\u8fbe\u5230\u4e00\u767e\u56db\u5341\u4e07\u4ef6\uff0c\u867d\u7136\u5b9a\u5236\u624b\u673a\u7684\u5229\u6da6\u7387\u8981\u4f4e\u8bb8\u591a\uff0c\u4f46\u662f\u8096\u745e\u6c11\u80fd\u770b\u5230\u4eca\u5e74\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u7ade\u4e89\u73af\u5883\u4f1a\u53d1\u751f\u5267\u70c8\u7684\u53d8\u5316\uff0c\u7279\u522b\u662f\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u7684\u5229\u6da6\u7387\u4f1a\u5927\u5e45\u4e0b\u964d\uff0c\u8054\u901a\u8fd9\u4e48\u5927\u7684\u5355\u5b50\u5c31\u663e\u5f97\u5c24\u5176\u91cd\u8981\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8096\u745e\u6c11\u4e5f\u80fd\u770b\u5230\u9526\u6e56\u7684\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u8981\u662f\u8054\u901a\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u4e0a\uff0c\u524d\u671f\u5168\u9762\u7684\u503e\u5411\u4f9d\u8d56\u9526\u6e56\u7684\u6280\u672f\uff0c\u518d\u52a0\u9526\u6e56\u5728\u5176\u4ed6\u5e02\u573a\u9886\u57df\u7684\u6269\u5f20\uff0c\u9526\u6e56\u901a\u8fc7\u8054\u4fe1\u3001\u901a\u8fc7\u79d1\u738b\u9ad8\u79d1\u5b9e\u73b0\u5728\u56fd\u5185\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u5b9e\u9645\u5360\u6709\u7387\u8d85\u8fc750\uff05\u5df2\u7ecf\u4e0d\u80fd\u7b97\u662f\u4ec0\u4e48\u795e\u8bdd\u4e86\u3002\u968f\u7740\u901a\u4fe1\u8d44\u8d39\u7684\u5927\u5e45\u4e0b\u964d\uff0c\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u7684\u6269\u5bb9\u901f\u5ea6\u5c06\u6781\u4e3a\u60ca\u4eba\uff0c\u9526\u6e56\u66b4\u9732\u7684\u662f\u79f0\u96c4\u4e2d\u4f4e\u7aef\u5e02\u573a\u7684\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u53ea\u662f\u9526\u6e56\u7684\u6280\u672f\u50a8\u5907\u8fd8\u6709\u6240\u4e0d\u8db3\uff0c\u6240\u4ee5\u9526\u6e56\u66f4\u5e0c\u671b\u56fd\u5185\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u5feb\u901f\u6269\u5bb9\u80fd\u62d6\u540e\u4e00\u6bb5\u65f6\u95f4\uff0c\u8ba9\u4ed6\u4eec\u7684\u51c6\u5907\u65f6\u95f4\u66f4\u5145\u5206\u4e00\u4e9b\uff0c\u81ea\u7136\u4e0d\u5e0c\u671b\u8054\u901a\u4e0e\u79fb\u52a8\u8fd9\u65f6\u5019\u5c31\u785d\u70df\u56db\u8d77\u3001\u6218\u706b\u5f25\u6f2b\u3002</p><p>\u3000\u3000\u4e00\u65b9\u9762\u9526\u6e56\u7ed9\u8054\u901a\u63d0\u4f9b\u66f4\u591a\u7684\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u7684\u6210\u672c\u4f18\u52bf\uff0c\u538b\u5236\u79fb\u52a8\u6709\u6837\u5b66\u6837\u7684\u590d\u5236\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u6a21\u5f0f\uff1b\u4e00\u65b9\u9762\uff0c\u9650\u5236\u8054\u901a\u7684\u6269\u5f20\u6b65\u4f10\uff0c\u4f7f\u8054\u901a\u653e\u7f13\u6269\u5f20\u6b65\u4f10\u4e4b\u65f6\u53d8\u5f97\u66f4\u6709\u5e8f\uff0c\u4e5f\u540c\u6837\u80fd\u591f\u9ebb\u75f9\u79fb\u52a8\u7684\u8b66\u60d5\u6027\u3002</p><p>\u3000\u3000\u6574\u4e2a\u8ba1\u5212\u7684\u6838\u5fc3\u6761\u4ef6\u90fd\u644a\u5f00\u5728\u773c\u524d\uff0c\u6768\u84c9\u5e73\u4e5f\u65e0\u6cd5\u4e0d\u5fc3\u52a8\uff0c\u8096\u745e\u6c11\u8fd8\u662f\u80fd\u8c28\u5b88\u4ed6\u7684\u672c\u4efd\uff0c\u8fd9\u65f6\u5019\u4e0d\u8fc7\u591a\u7684\u63d2\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8bf4\u9053\uff1a\u201c\u53ef\u4ee5\u660e\u786e\u544a\u8bc9\u6768\u603b\u7684\u662f\uff0c\u624b\u673a\u82af\u7247\u7814\u53d1\u90e8\u95e8\u662f\u9526\u6e56\u7814\u53d1\u4f53\u7cfb\u5185\u6700\u6838\u5fc3\u7684\u90e8\u95e8\u4e4b\u4e00\uff0c\u76f4\u63a5\u4e3a\u624b\u673a\u82af\u7247\u7814\u53d1\u90e8\u95e8\u5de5\u4f5c\u7684\u7814\u53d1\u4eba\u5458\u8d85\u8fc7\u4e00\u5343\u4e8c\u767e\u4eba\uff0c\u8fd8\u6709\u4f17\u591a\u7684\u7814\u53d1\u5c0f\u7ec4\u56f4\u7ed5\u624b\u673a\u82af\u7247\u7814\u53d1\u90e8\u95e8\u5f00\u53d1\u5916\u56f4\u6280\u672f\u3002\u6211\u4eec\u5e76\u4e0d\u8bb3\u8a00\u6211\u4eec\u5728\u6210\u672c\u4e0a\u7684\u4f18\u52bf\uff0c\u6211\u4eec\u7684\u624b\u673a\u82af\u7247\u7814\u53d1\u90e8\u95e8\uff0c\u9664\u4e86\u51e0\u4e2a\u6838\u5fc3\u7814\u53d1\u5c0f\u7ec4\u5728\u7f8e\u56fd\u65e7\u91d1\u5c71\u4e0e\u65e5\u672c\u7b51\u6ce2\u5e02\u4e4b\u5916\uff0c\u8d85\u8fc780\uff05\u7684\u7814\u53d1\u4eba\u5458\u4e0e\u8d85\u8fc790\uff05\u7684\u5916\u56f4\u7814\u53d1\u5c0f\u7ec4\u6210\u5458\u90fd\u5728\u56fd\u5185\u3002\u9664\u4e86\u5927\u91cf\u5916\u8058\u7684\u4e2d\u9ad8\u7ea7\u6280\u672f\u4eba\u5458\u4e4b\u5916\uff0c\u9526\u6e56\u81ea\u5df1\u57f9\u517b\u7684\u56fd\u5185\u4e13\u4e1a\u4eba\u624d\u4e5f\u6210\u957f\u8d77\u6765\u4e86\u3002\u7f8e\u56fd\u7845\u8c37\u7684\u7814\u53d1\u4eba\u5458\u5e73\u5747\u5e74\u85aa\u4e3a\u5341\u4e07\u7f8e\u5143\uff0c\u6bcf\u5468\u5de5\u4f5c\u65f6\u95f4\u4e09\u5341\u4e94\u4e2a\u5c0f\u65f6\uff0c\u6211\u4eec\u5728\u56fd\u5185\u7684\u7814\u53d1\u4eba\u5458\u5e73\u5747\u5e74\u85aa\u4e3a\u5341\u4e94\u4e07\u4eba\u6c11\u5e01\uff0c\u5e73\u5747\u6bcf\u5468\u5de5\u4f5c\u65f6\u95f4\u8981\u8d85\u8fc7\u516d\u5341\u5c0f\u65f6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5e74\u85aa\u5341\u4e94\u4e07\u554a\uff0c\u201d\u8054\u901a\u5317\u4eac\u5206\u53f8\u603b\u7ecf\u7406\u6478\u4e86\u6478\u5fae\u79c3\u7684\u524d\u989d\uff0c\u8fd9\u65f6\u5019\u5f00\u73a9\u7b11\u7684\u63d2\u4e86\u4e00\u53e5\u8bdd\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u53ef\u662f\u8fdc\u8fdc\u8d85\u8fc7\u6211\u4eec\u8fd9\u4e9b\u5c06\u4e00\u8f88\u5b50\u90fd\u5949\u732e\u7ed9\u56fd\u5bb6\u7535\u4fe1\u4e8b\u4e1a\u7684\u4eba\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u9526\u6e56\u5e74\u85aa\u5341\u4e94\u4e07\u7684\u5458\u5de5\u4e5f\u4e0d\u6562\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u900d\u9065\uff0c\u624d\u60f3\u8d77\u5230\u8fd9\u65f6\u5019\u90fd\u4e0d\u77e5\u9053\u8fd9\u4e2a\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u59d3\u751a\u540d\u8c01\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u7709\u5934\u5fae\u9501\uff0c\u5f20\u606a\u7684\u8bdd\u5e76\u4e0d\u662f\u70ab\u8000\u9526\u6e56\u65d7\u4e0b\u5458\u5de5\u7684\u85aa\u8d44\u6c34\u5e73\uff0c\u800c\u662f\u544a\u8bc9\u4ed6\u4eec\u9526\u6e56\u624b\u673a\u82af\u7247\u90e8\u95e8\u5b9e\u9645\u7684\u6210\u672c\u4f18\u52bf\u5230\u5e95\u6709\u591a\u5927\u3002</p><p>\u3000\u3000\u5bf9\u6a61\u6811\u56ed\u7814\u53d1\u4eba\u5458\u7684\u85aa\u8d44\u6c34\u5e73\uff0c\u5916\u754c\u4e4b\u524d\u5c31\u6709\u79cd\u79cd\u731c\u6d4b\uff0c\u8fd9\u8981\u7b97\u8096\u745e\u6c11\u7b2c\u4e00\u6b21\u542c\u5230\u9526\u6e56\u516c\u5f00\u7684\u8bf4\u660e\u65d7\u4e0b\u5458\u5de5\u7684\u85aa\u8d44\u72b6\u51b5\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u5317\u4eac\u4f01\u4e8b\u4e1a\u5458\u5de5\u5e73\u5747\u5e74\u85aa\u751a\u81f3\u90fd\u4e0d\u8db3\u4e00\u4e07\u56db\u5343\u5143\uff0c\u6a61\u6811\u56ed\u7684\u7814\u53d1\u4eba\u5458\u5e73\u5747\u5e74\u85aa\u5c31\u8fbe\u5230\u5341\u4e94\u4e07\uff0c\u8096\u745e\u6c11\u76f8\u4fe1\u5341\u4e94\u4e07\u91cc\u8fd8\u4e0d\u5305\u62ec\u5e74\u7ec8\u7ea2\u5229\u4e0e\u671f\u6743\u6fc0\u52b1\u7684\u90e8\u5206\uff0c\u4e5f\u96be\u602a\u597d\u4e9b\u4f01\u4e1a\u90fd\u5728\u62b1\u6028\u9526\u6e56\u5c06\u884c\u4e1a\u5e73\u5747\u52b3\u52a8\u529b\u6210\u672c\u5927\u5927\u7684\u62c9\u9ad8\u4e86\uff0c\u65e5\u5b50\u6ca1\u6709\u4ee5\u524d\u597d\u8fc7\u4e86\u3002</p><p>\u3000\u3000\u5012\u4e5f\u4e0d\u662f\u9526\u6e56\u4e00\u5bb6\uff0c\u524d\u4e9b\u5e74\u56fd\u5185\u8f6f\u786c\u4ef6\u5f00\u53d1\u7684\u4eba\u624d\u5947\u7f3a\uff0c\u884c\u4e1a\u7684\u6574\u4f53\u85aa\u8d44\u6c34\u5e73\u4e00\u76f4\u5904\u4e8e\u9ad8\u4f4d\uff0c\u76f4\u5230\u4e5d\u4e5d\u5e74\u624d\u7a0d\u597d\u4e00\u4e9b\u3002\u8fd9\u51e0\u5e74\u56fd\u5185\u5404\u5927\u9662\u6821\u4e0d\u7ba1\u5177\u4e0d\u5177\u5907\u6761\u4ef6\uff0c\u90fd\u4e00\u62e5\u800c\u4e0a\u7684\u5f00\u8bbe\u7535\u5b50\u4fe1\u606f\u5de5\u7a0b\u3001\u8ba1\u7b97\u673a\u3001\u5fae\u7535\u5b50\u3001\u8f6f\u4ef6\u7f51\u7edc\u4fe1\u606f\u5de5\u7a0b\u7b49\u70ed\u95e8\u4e13\u95e8\uff0c\u53c8\u6709\u6cdb\u6ee5\u4e4b\u52bf\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u9526\u6e56\u652f\u4ed8\u7ed9\u5458\u5de5\u7684\u85aa\u8d44\u6bd4\u884c\u4e1a\u5e73\u5747\u6c34\u5e73\u8fd8\u662f\u8981\u9ad8\u51fa\u592a\u591a\uff0c\u57fa\u672c\u4e0a\u4ee3\u8868\u56fd\u5185\u884c\u4e1a\u6700\u9ad8\u6c34\u5e73\u4e86\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5982\u6b64\uff0c\u9526\u6e56\u624b\u673a\u82af\u7247\u7814\u53d1\u90e8\u95e8\u7684\u5b9e\u9645\u52b3\u52a8\u529b\u6210\u672c\u4e5f\u53ea\u6709\u65e5\u97e9\u6b27\u7f8e\u7b49\u53d1\u8fbe\u56fd\u5bb6\u53ca\u5730\u533a\u7684\u516d\u5206\u4e4b\u4e00\u751a\u81f3\u66f4\u4f4e\u6c34\u5e73\u3002</p><p>\u3000\u3000\u5728\u624b\u673a\u82af\u7247\u4ea7\u4e1a\u91cc\uff0c\u652f\u4ed8\u7ed9\u7814\u53d1\u4eba\u5458\u7684\u85aa\u8d44\u5728\u6210\u672c\u7ed3\u6784\u5360\u4e86\u7edd\u5927\u90e8\u5206\u7684\u6bd4\u4f8b\uff0c\u82af\u7247\u5236\u9020\u6210\u672c\u5b9e\u9645\u6240\u5360\u7684\u6bd4\u4f8b\u5f88\u4f4e\u3002\u5c31\u7b97\u9526\u6e56\u5c06ESS\u57fa\u9891\u82af\u7247\u7ec4\u677f\u5b8c\u5168\u4ea4\u7ed9\u53f0\u8d44\u7535\u5b50\u4f01\u4e1a\u4ee3\u5de5\uff0c\u6bcf\u7247\u8d39\u7528\u4e5f\u4e0d\u4f1a\u8d85\u8fc7\u5341\u4e94\u7f8e\u5143\uff0c\u800c\u9526\u6e56\u5c06ESS\u57fa\u9891\u82af\u7247\u7ec4\u677f\u51fa\u552e\u7ed9\u8054\u4fe1\u6bcf\u7247\u6536\u53d668\u7f8e\u5143\uff0c\u8096\u745e\u6c11\u57fa\u672c\u80fd\u80af\u5b9a\u9526\u6e56\u5728ESS\u57fa\u9891\u82af\u7247\u7ec4\u677f\u4e0a\u7684\u6bdb\u5229\u6da6\u7387\u8981\u670940\u300150\uff05\uff0c\u4f46\u662f\u6b27\u7f8e\u624b\u673a\u82af\u7247\u5382\u5546\u5c06\u540c\u7b49\u89c4\u683c\u7684\u57fa\u9891\u7ec4\u4ef6\u6bcf\u7247\u964d\u523068\u7f8e\u5143\uff0c\u751a\u81f3\u4f1a\u4e8f\u635f\u3002</p><p>\u3000\u3000\u5728\u9526\u6e56\u5982\u6b64\u5de8\u5927\u7684\u6210\u672c\u4f18\u52bf\u9762\u524d\uff0c\u6b27\u7f8e\u51e0\u5927\u624b\u673a\u82af\u7247\u5382\u5546\u6839\u672c\u5c31\u4e0d\u6562\u4e0e\u9526\u6e56\u5728\u4e2d\u4f4e\u7aef\u624b\u673a\u82af\u7247\u5e02\u573a\u6b63\u9762\u53ae\u6740\u3002\u4ee5\u6b27\u7f8e\u4f01\u4e1a\u4e13\u4e1a\u5316\u7684\u7ecf\u8425\u4f20\u7edf\uff0c\u751a\u81f3\u53ef\u80fd\u4f1a\u4e3b\u52a8\u653e\u5f03\u5229\u6da6\u56de\u62a5\u6c34\u5e73\u4f4e\u7684\u4e2d\u4f4e\u7aef\u624b\u673a\u82af\u7247\u4e1a\u52a1\uff0c\u65e9\u524d\u5c31\u6709\u673a\u6784\u9884\u6d4b\u9526\u6e56\u5728\u4e2d\u4f4e\u7aef\u57fa\u5e26\u5c04\u9891\u82af\u7247\u7ec4\u6280\u672f\u53d6\u5f97\u5173\u952e\u7a81\u7834\u4e4b\u540e\uff0c\u5fb7\u4eea\u8fdb\u4e00\u6b65\u7684\u5c06\u4e2d\u4f4e\u7aefGSM\u57fa\u5e26\u5c04\u9891\u82af\u7247\u7ec4\u4e1a\u52a1\u4e0e\u9526\u6e56\u91cd\u7ec4\u624d\u66f4\u5408\u4e4e\u5fb7\u4eea\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u53ef\u80fd\u6027\u7684\u9884\u6d4b\u4ee4\u6b27\u7f8e\u624b\u673a\u82af\u7247\u5382\u5546\u5728\u51b3\u7b56\u4e2d\u4f4e\u7aef\u624b\u673a\u82af\u7247\u7ecf\u8425\u8def\u7ebf\u65f6\u66f4\u52a0\u7684\u9996\u9f20\u4e24\u7aef\u3001\u72b9\u8c6b\u4e0d\u51b3\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u8fd9\u91cc\u6446\u660e\u8f66\u9a6c\uff0c\u4e5f\u4e0d\u662f\u76f4\u63a5\u8ddf\u8054\u901a\u516c\u5f00\u644a\u724c\uff0c\u800c\u662f\u8981\u5c06\u5229\u5bb3\u5173\u7cfb\u8ddf\u6768\u84c9\u5e73\u6446\u660e\u3002\u5373\u4f7f\u8981\u5b9e\u65bd\u8be5\u8ba1\u5212\uff0c\u4e5f\u662f\u8981\u6768\u84c9\u5e73\u5728\u8054\u901a\u5185\u90e8\u53e6\u8d77\u5c71\u5934\uff0c\u9526\u6e56\u5728\u5916\u56f4\u7ed9\u6768\u84c9\u5e73\u6447\u65d7\u5450\u558a\uff0c\u6bd5\u7adf\u9526\u6e56\u662f\u6ca1\u6709\u8d44\u683c\u5bf9\u8054\u901a\u7684\u7ecf\u8425\u8def\u7ebf\u6307\u624b\u5212\u811a\u3001\u6307\u4e09\u9053\u56db\u7684\u3002\u9526\u6e56\u76f4\u63a5\u8fd0\u4f5c\u6b64\u65f6\uff0c\u4f1a\u5f15\u8d77\u53cd\u5f39\u6027\u8d28\u7684\u5bf9\u6297\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u5728\u8054\u901a\u624b\u63e1\u91cd\u6743\uff0c\u4f46\u6bd5\u7adf\u8fd8\u662f\u526f\u804c\uff0c\u63d0\u51fa\u4fee\u6b63\u65e2\u5b9a\u7684\u53d1\u5c55\u7b56\u7565\uff0c\u4e8b\u5b9e\u4e0a\u8981\u5192\u6781\u5927\u7684\u98ce\u9669\uff0c\u4e00\u65e6\u5931\u8d25\uff0c\u8bf4\u4e0d\u5b9a\u4ece\u6b64\u5728\u8054\u901a\u5c31\u6ca1\u6709\u7acb\u8db3\u4e4b\u5730\uff0c\u5c31\u8981\u7ed9\u6392\u6324\u51fa\u8054\u901a\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u4e5f\u6709\u5f88\u5927\u7684\u6210\u529f\u673a\u4f1a\uff0c\u9996\u5148\u8054\u901a\u5185\u90e8\u5bf9\u73b0\u884c\u7684\u53d1\u5c55\u7b56\u7565\u5c31\u6709\u5f88\u5927\u7684\u4e89\u8bae\uff0c\u5f53\u7136\u8fd8\u53ef\u4ee5\u76f4\u63a5\u5411\u4fe1\u4ea7\u90e8\u3001\u5411\u803f\u91cd\u9633\u8bf7\u6c42\u652f\u6301\u3002\u4e8b\u5173\u8054\u4fe1\u7684\u5229\u76ca\uff0c\u8096\u745e\u6c11\u81ea\u7136\u4e5f\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\u3002</p><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u4ece\u4fe1\u4ea7\u90e8\u5212\u5f52\u56fd\u5bb6\u7ecf\u8d38\u90e8\u76f4\u8f96\u4e4b\u540e\uff0c\u5728\u534e\u590f\u7535\u5b50\u5185\u90e8\u95f9\u72ec\u7acb\u7684\u8054\u4fe1\uff0c\u5c24\u5176\u8ba9\u4fe1\u4ea7\u90e8\u7684\u4eba\u770b\u4e86\u5fc3\u6b22\u559c\uff0c\u8096\u745e\u6c11\u6267\u638c\u8054\u4fe1\uff0c\u6700\u521d\u4e5f\u662f\u5f97\u76ca\u4e8e\u803f\u91cd\u9633\u7684\u63d0\u62e8\uff0c\u8096\u745e\u6c11\u81ea\u4ee5\u4e3a\u5728\u803f\u91cd\u9633\u9762\u524d\u8fd8\u80fd\u8bf4\u4e0a\u8bdd\u3002\u53e6\u5916\uff0c\u5c31\u7b97\u9526\u6e56\u4e0d\u76f4\u63a5\u51fa\u9762\uff0c\u9526\u6e56\u5728\u80cc\u540e\u7684\u5f71\u54cd\u529b\u81ea\u7136\u4e5f\u4e0d\u5bb9\u5c0f\u7aa5\u3002</p><p>\u3000\u3000\u5305\u53a2\u95e8\u4e2d\u95f4\u5d4c\u7740\u534a\u900f\u660e\u7684\u96d5\u523b\u73bb\u7483\uff0c\u900f\u7740\u4e9b\u5916\u9762\u5927\u5385\u7684\u706f\u5149\uff0c\u5f20\u606a\u4ed6\u4eec\u5750\u5728\u5305\u53a2\u91cc\u79d8\u8c08\uff0c\u5f00\u59cb\u6ca1\u6709\u6ce8\u610f\u5230\u5927\u5385\u91cc\u7684\u706f\u5149\u53d8\u6697\uff0c\u76f4\u5230\u4e94\u5149\u5341\u8272\u7684\u5149\u67f1\u6253\u5230\u96d5\u523b\u73bb\u7483\u4e0a\u6d41\u5149\u6ea2\u5f69\uff0c\u624d\u6ce8\u610f\u5230\u5927\u5385\u91cc\u60a0\u626c\u7684\u97f3\u4e50\u53d8\u6362\u6210\u8282\u594f\u52a8\u611f\u7684\u6447\u6eda\u3002</p><p>\u3000\u3000\u5f20\u606a\u7591\u60d1\u7684\u770b\u7740\u5370\u5728\u96d5\u523b\u73bb\u7483\u95e8\u4e0a\u7684\u5149\u6591\u4e00\u773c\uff0c\u5750\u5728\u4ed6\u659c\u5bf9\u9762\u7684\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u597d\u4e0d\u5bb9\u6613\u902e\u5230\u673a\u4f1a\u8ddf\u5f20\u606a\u642d\u8bdd\uff0c\u89e3\u91ca\u9053\uff1a\u201c\u5927\u5385\u91cc\u89d2\u6709\u5c0f\u821e\u6c60\uff0c\u6bcf\u5230\u8fd9\u65f6\u5019\uff0c\u90fd\u4f1a\u6709\u4eba\u4e0a\u53bb\u7fe9\u7fe9\u8d77\u821e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c0f\u6210\u4f60\u9080\u8bf7\u606a\u5c11\u51fa\u53bb\u8df3\u4e00\u66f2\u821e\uff0c\u201d\u6768\u84c9\u5e73\u8bf4\u9053\uff0c\u201c\u5149\u4ecb\u7ecd\u53ef\u4e0d\u62b5\u4ec0\u4e48\u7528\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u9053\uff1a\u201c\u6768\u603b\u662f\u602a\u6211\u5bf9\u6f02\u4eae\u7684\u5973\u58eb\u4e0d\u591f\u6bb7\u52e4\uff0c\u201d\u770b\u7740\u5973\u4eba\u773c\u7738\u91cc\u6d41\u9732\u51fa\u9f13\u52b1\u7684\u773c\u795e\uff0c\u4ed6\u5fae\u655b\u7740\u773c\u7738\uff0c\u770b\u4e0a\u5979\u811a\u4e0a\u94f6\u7070\u8272\u7684\u9ad8\u8ddf\u6c34\u6676\u978b\uff0c\u8bf4\u9053\uff0c\u201c\u6210\u5c0f\u59d0\u82e5\u662f\u7b54\u5e94\u4e8b\u540e\u4e0d\u627e\u6211\u8d54\u4f60\u8fd9\u53cc\u6f02\u4eae\u7684\u978b\u5b50\uff0c\u6211\u5012\u662f\u80fd\u9f13\u8d77\u4e00\u4e9b\u52c7\u6c14\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u6210\u5c0f\u59d0\u5ae3\u7136\u4e00\u7b11\uff0c\u5c06\u5236\u670d\u5f0f\u7684\u897f\u88c5\u5916\u5957\u8131\u4e0b\uff0c\u9732\u51fa\u91cc\u9762\u7a7f\u7740\u7684\u6d45\u8272\u6761\u886c\u886b\u6765\uff0c\u505a\u597d\u4e0a\u573a\u7684\u51c6\u5907\uff0c\u8fd9\u65f6\u5019\u624d\u770b\u5230\u5979\u5c06\u4e00\u53ea\u4e73\u767d\u8272\u7684\u7231\u8fbe\u53cc\u5c4f\u624b\u673a\u62ff\u624b\u673a\u7ef3\u7a7f\u7740\u6302\u5728\u8116\u5b50\u4e0a\uff0c\u624b\u673a\u538b\u5728\u80f8\u812f\u7684\u886c\u886b\u4e0a\uff0c\u5c06\u80f8\u90e8\u886c\u5f97\u9ad8\u8038\u4e30\u6ee1\uff0c\u5979\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u53ef\u662f\u7231\u8fbe\u7684\u5fe0\u5b9e\u7528\u6237\u2026\u2026\u201d\u5c06\u624b\u673a\u6258\u8d77\u6765\u7ed9\u5f20\u606a\u770b\u4e86\u770b\uff0c\u53c8\u653e\u56de\u53bb\uff0c\u80fd\u770b\u5230\u624b\u673a\u7ed9\u4e73\u623f\u9876\u4e86\u8f7b\u5f39\u4e86\u4e00\u4e0b\uff0c\u518d\u770b\u5979\u7684\u8170\u80a2\u5904\u7ed9\u84dd\u8272\u77ed\u88d9\u675f\u5f97\u7ec6\u7ec6\u7684\uff0c\u817f\u7ea4\u7ec6\u4e14\u957f\uff0c\u679c\u771f\u662f\u8ff7\u4eba\u7684\u5973\u90ce\u3002</p><p>\u3000\u3000\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u4f30\u6478\u7740\u6447\u6eda\u97f3\u4e50\u5c06\u505c\uff0c\u5f20\u606a\u624d\u7ad9\u8d77\u6765\uff0c\u505a\u4e86\u4e00\u4e2a\u9080\u8bf7\u7684\u624b\u52bf\uff0c\u53c8\u56de\u5934\u5bf9\u59da\u575a\u3001\u5218\u9e23\u950b\u4ee5\u53ca\u8096\u745e\u6c11\u7684\u52a9\u7406\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6765\u5e2e\u6211\u538b\u9635\u2026\u2026\u201d\u7559\u9648\u4fe1\u751f\u4e0e\u8096\u745e\u6c11\u3001\u6768\u84c9\u5e73\u5728\u5305\u53a2\u91cc\u7ee7\u7eed\u5bc6\u8c08\u3002</p><p>\u3000\u3000\u63a5\u4e0b\u6765\u662f\u6162\u4e09\u821e\u66f2\uff0c\u65cb\u5f8b\u60a0\u626c\uff0c\u540d\u6d41\u4ed5\u5973\u7eb7\u7eb7\u76f8\u62e5\u5f80\u5927\u5385\u4e00\u89d2\u7684\u821e\u6c60\u98d8\u53bb\uff0c\u706f\u5149\u67d4\u66fc\uff0c\u97f3\u4e50\u8f7b\u67d4\uff0c\u5f20\u606a\u5fae\u62bf\u7740\u5634\u800c\u7b11\uff0c\u8d70\u5230\u821e\u6c60\u8fb9\u7f18\u624d\u8f7b\u6258\u8d77\u6210\u5c0f\u59d0\u90a3\u53ea\u6b3a\u971c\u8d5b\u96ea\u7684\u7ea4\u7ea4\u7389\u624b\uff0c\u63fd\u8d77\u67d4\u8f6f\u5982\u68a6\u7684\u7ea4\u7ec6\u8170\u80a2\uff0c\u5fc3\u91cc\u6697\u53f9\uff1a\u6f02\u4eae\u5973\u4eba\u603b\u6bd4\u7f8e\u9152\u8981\u9500\u9b42\u4e00\u4e9b\uff0c\u518d\u770b\u821e\u6c60\u91cc\u7684\u7537\u5973\uff0c\u4e5f\u90fd\u7537\u8d22\u5973\u8c8c\u3002</p><p>\u3000\u3000\u201c\u5f20\u5148\u751f\uff0c\u60a8\u8df3\u7684\u771f\u597d\uff0c\u4f60\u521a\u624d\u90a3\u4e48\u8bf4\uff0c\u8fd8\u771f\u8ba9\u6211\u6709\u4e9b\u5fd0\u5fd1\u5462\uff01\u539f\u6765\u5f20\u5148\u751f\u662f\u5f88\u98ce\u8da3\u7684\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u662f\u6210\u5c0f\u59d0\u60a8\u683c\u5916\u8ba9\u4eba\u4e0d\u7d27\u5f20\u3002\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u91cc\u8ba1\u7b97\u7740\u8fd9\u66f2\u97f3\u4e50\u8fd8\u8981\u8fc7\u591a\u4e45\u624d\u4f1a\u7ed3\u675f\uff0c\u7136\u540e\u5c06\u8fd9\u4efd\u82e6\u5dee\u4e8b\u4e22\u7ed9\u59da\u575a\u3001\u5218\u9e23\u950b\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u6210\u5c0f\u59d0\u6302\u5728\u80f8\u524d\u7684\u624b\u673a\u5916\u5c4f\u706f\u95ea\u70c1\u8d77\u6765\uff0c\u5f20\u606a\u677e\u5f00\u624b\uff0c\u5979\u62ff\u8d77\u624b\u673a\u770b\u4e86\u770b\uff0c\u8ddf\u5f20\u606a\u62b1\u6b49\u8bf4\u4e86\u4e00\u58f0\uff0c\u5c31\u5306\u5fd9\u671d\u821e\u6c60\u5916\u8d70\u53bb\u63a5\u542c\u7535\u8bdd\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5f53\u4e00\u6869\u82e6\u5dee\u4e8b\u7ed3\u675f\uff0c\u53cd\u800c\u677e\u4e86\u4e00\u53e3\u6c14\u3002\u59da\u575a\u3001\u5218\u9e23\u950b\u8fd8\u6709\u8096\u745e\u6c11\u7684\u52a9\u7406\u901f\u5ea6\u5012\u662f\u4e0d\u6162\uff0c\u8f6c\u773c\u5de5\u592b\u90fd\u5df2\u7ecf\u627e\u5230\u821e\u4f34\u8fdb\u4e86\u821e\u6c60\u91cc\u6765\uff0c\u5f20\u606a\u5148\u5230\u821e\u6c60\u8fb9\u7684\u5427\u53f0\u6b47\u7740\u7b49\u4ed6\u4eec\uff0c\u542c\u7740\u60a0\u626c\u7684\u97f3\u4e50\uff0c\u770b\u7740\u821e\u6c60\u4ee5\u53ca\u5927\u5385\u91cc\u7a7f\u68ad\u7684\u6f02\u4eae\u5973\u4eba\uff0c\u603b\u6bd4\u95f7\u5728\u5305\u53a2\u91cc\u4e0e\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u4ed6\u4eec\u52fe\u5fc3\u6597\u89d2\u5f3a\u3002</p><p>\u3000\u3000\u542c\u821e\u66f2\u4f3c\u4e4e\u521a\u5230\u534a\u9014\uff0c\u5f20\u606a\u6709\u4e9b\u5c3f\u610f\uff0c\u4e5f\u770b\u4e0d\u89c1\u8054\u901a\u7684\u90a3\u4e2a\u6f02\u4eae\u6210\u5c0f\u59d0\u8eb2\u5230\u54ea\u91cc\u53bb\u901a\u7535\u8bdd\u4e86\uff0c\u4ed6\u8f6c\u8eab\u5faa\u7740\u6807\u8bc6\u4ece\u5149\u7ebf\u6d6e\u5e7d\u7684\u8fc7\u9053\u91cc\u7a7f\u8fc7\u53bb\u627e\u6d17\u624b\u95f4\uff0c\u8f6c\u4e86\u4e24\u4e2a\u5f2f\uff0c\u5927\u5385\u91cc\u7684\u97f3\u4e50\u58f0\u5c31\u8ddf\u6c34\u6ef4\u4f3c\u7684\u7ed9\u8fc7\u9053\u5438\u5f15\u6389\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u6211\u5c31\u5728\u516c\u53f8\u91cc\u52a0\u73ed\uff0c\u6211\u8981\u600e\u4e48\u8ddf\u4f60\u89e3\u91ca\u4f60\u624d\u76f8\u4fe1\u6211\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u7740\u8fc7\u9053\u90a3\u5934\u4f20\u6765\u7684\u5973\u4eba\u58f0\u97f3\u719f\u6089\uff0c\u5df2\u8d70\u8fc7\u62d0\u89d2\uff0c\u770b\u5230\u8054\u901a\u7684\u6210\u5c0f\u59d0\u80cc\u5bf9\u7740\u4ed6\u4e0e\u522b\u4eba\u901a\u7535\u8bdd\uff0c\u4ed6\u60f3\u9000\u56de\u6765\u4e5f\u6765\u4e0d\u53ca\uff0c\u6210\u5c0f\u59d0\u8f6c\u8fc7\u8eab\u6765\u4e00\u8138\u60ca\u8c14\u53c8\u7a98\u8feb\u7684\u770b\u7740\u4ed6\uff0c\u5f20\u606a\u53ea\u80fd\u5c34\u5c2c\u7684\u671d\u8fc7\u9053\u5c3d\u5934\u7684\u6d17\u624b\u95f4\u6307\u4e86\u6307\uff0c\u793a\u610f\u81ea\u5df1\u662f\u8fc7\u6765\u5bfb\u627e\u5395\u6240\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u8ddf\u4f60\u89e3\u91ca\u4e86\uff0c\u624b\u673a\u5feb\u6ca1\u7535\u4e86\uff0c\u6211\u5148\u6302\u7535\u8bdd\u4e86\u3002\u201d\u6210\u5c0f\u59d0\u795e\u8272\u7a98\u8feb\uff0c\u4f3c\u4e4e\u80fd\u4fdd\u6301\u9547\u5b9a\u7684\u538b\u7740\u58f0\u97f3\u8ddf\u7535\u8bdd\u90a3\u5934\u7684\u4eba\u518d\u8bf4\u4e00\u53e5\u624d\u6302\u6389\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5979\u624b\u4e0a\u7684\u52a8\u4f5c\uff0c\u5979\u4f3c\u4e4e\u987a\u4fbf\u5c06\u624b\u673a\u5173\u673a\u4e86\u3002</p><p>\u3000\u3000\u5979\u53c8\u5c34\u5c2c\u7684\u8ddf\u5f20\u606a\u89e3\u91ca\uff1a\u201c\u4e0d\u662f\u60f3\u8bf4\u8c0e\uff0c\u8981\u662f\u8bf4\u5728\u8fd9\u91cc\uff0c\u4ed6\u4f1a\u66f4\u7591\u795e\u7591\u9b3c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u79c1\u4eba\u95f4\u7684\u4e8b\u60c5\u5b9e\u5728\u4e0d\u60f3\u7f6e\u8bc4\uff0c\u5728\u5982\u6b64\u6f02\u4eae\u7684\u5973\u4eba\u80cc\u540e\u85cf\u7740\u4e00\u4e24\u4e2a\u4f24\u5fc3\u6b32\u7edd\u7684\u7537\u4eba\uff0c\u5b9e\u5c5e\u5e73\u5e38\u5f97\u5f88\uff0c\u4f55\u51b5\u8fd8\u662f\u4e00\u4e2a\u770b\u4e0a\u53bb\u4e8b\u4e1a\u6bd4\u8f83\u6210\u529f\u7684\u6f02\u4eae\u5973\u4eba\uff1f\u4ed6\u644a\u624b\u8038\u8038\u80a9\u540e\u9000\u7740\u5f80\u6d17\u624b\u95f4\u90a3\u8fb9\u8d70\uff0c\u6ca1\u6ce8\u610f\u5230\u540e\u9762\u8d70\u8fc7\u4e00\u4e2a\u4eba\uff0c\u8f6c\u8eab\u65f6\u624b\u8098\u987a\u62d0\u7684\u649e\u5230\u90a3\u4eba\u7684\u8eab\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u4f60\u957f\u773c\u775b\u6ca1\u6709\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u611f\u89c9\u624b\u8098\u649e\u4e00\u5904\u67d4\u8f6f\u5f39\u633a\u7684\u5730\u65b9\uff0c\u89e6\u611f\u867d\u7136\u7edd\u5bf9\u7684\u7f8e\u597d\uff0c\u4f46\u4e5f\u77e5\u9053\u649e\u5230\u4e0d\u8be5\u649e\u7684\u5730\u65b9\uff0c\u5fd9\u8f6c\u8eab\u770b\u5230\u90a3\u4e2a\u7ed9\u4ed6\u649e\u5230\u7684\u5973\u4eba\u8fde\u9000\u4e86\u4e24\u6b65\uff0c\u624b\u5403\u75db\u7684\u6342\u7740\u80f8\u53e3\u3002\u5f20\u606a\u4e5f\u77e5\u9053\u81ea\u5df1\u6d3b\u8be5\u7ed9\u9a82\uff0c\u5f88\u4e0d\u597d\u610f\u601d\u7684\u9053\u6b49\uff1a\u201c\u771f\u4e0d\u597d\u610f\u601d\uff0c\u6709\u6ca1\u6709\u649e\u4f24\u4f60\uff1f\u201d\u90a3\u5973\u4eba\u5934\u4f4e\u4e0b\u6765\uff0c\u987a\u76f4\u7684\u957f\u53d1\u6ed1\u8fc7\u6765\u5c06\u8138\u906e\u4f4f\uff0c\u53ea\u770b\u5979\u6de1\u7d2b\u8272\u793c\u670d\u957f\u88d9\u9732\u51fa\u7684\u9999\u80a9\u808c\u80a4\u67d4\u5ae9\u96ea\u767d\u4ee5\u53ca\u5a40\u5a1c\u4fee\u957f\u7684\u8eab\u6750\uff0c\u5c31\u77e5\u9053\u662f\u4e2a\u975e\u5e38\u6f02\u4eae\u5973\u4eba\u3002</p><p>\u3000\u3000\u90a3\u5973\u4eba\u6b47\u4e86\u4e00\u4f1a\u513f\uff0c\u4f3c\u4e4e\u987a\u8fc7\u6c14\uff0c\u4e00\u624b\u634b\u7740\u989d\u524d\u7684\u957f\u53d1\u62ac\u8d77\u5934\u6765\uff0c\u8138\u86cb\u679c\u7136\u6f02\u4eae\uff0c\u53ea\u662f\u5979\u80f8\u53e3\u8fd8\u6709\u75db\uff0c\u7709\u5934\u8f7b\u62e7\u7740\uff0c\u5927\u6982\u662f\u5bf9\u81ea\u5df1\u67d4\u5ae9\u7684\u80f8\u90e8\u7ed9\u649e\u4e86\u4e00\u4e0b\uff0c\u8fd8\u7ed9\u649e\u4e86\u8fd9\u4e48\u91cd\uff0c\u5341\u5206\u7684\u607c\u6068\uff0c\u4e0d\u5ba2\u6c14\u7684\u6559\u8bad\u8d77\u5f20\u606a\u6765\uff1a\u201c\u4f60\u957f\u773c\u775b\u662f\u7528\u6765\u8d70\u8def\u8fd8\u662f\u7528\u6765\u770b\u5973\u4eba\u7684\uff1f\u201d\u5979\u53ea\u5f53\u5f20\u606a\u8f6c\u8eab\u540e\u9000\u7740\u8d70\u662f\u8d2a\u56fe\u770b\u6210\u5c0f\u59d0\u7684\u7f8e\u8272\u624d\u5982\u6b64\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u81f3\u4e8e\u7ed9\u5973\u4eba\u6559\u8bad\u4e24\u53e5\u5c31\u607c\u7f9e\u6210\u6012\uff0c\u8fd8\u60f3\u89e3\u91ca\u4e24\u53e5\uff0c\u53ea\u89c9\u5f97\u773c\u524d\u8fd9\u5973\u4eba\u773c\u719f\uff0c\u597d\u50cf\u662f\u5728\u54ea\u90e8\u5f71\u89c6\u4f5c\u54c1\u91cc\u770b\u5230\u8fc7\u5979\u9732\u8138\uff0c\u8bdd\u5230\u5634\u8fb9\u8bf4\u6253\u4e86\u7ed3\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u7d20\u8d28\uff01\u201d\u90a3\u4e2a\u5973\u7684\u4e22\u4e0b\u4e00\u53e5\u8bdd\uff0c\u624b\u6342\u7740\u80f8\u53e3\u9519\u8eab\u5f80\u5904\u8d70\u3002</p><p>\u3000\u3000\u201c\u4f3c\u4e4e\u771f\u628a\u5979\u649e\u75db\u4e86\uff0c\u201d\u5f20\u606a\u770b\u7740\u7edd\u8272\u5973\u5b50\u7684\u80cc\u5f71\uff0c\u81ea\u5632\u7684\u671d\u8054\u901a\u7684\u6210\u5c0f\u59d0\u644a\u4e86\u644a\u624b\uff0c\u53c8\u95ee\u5979\uff0c\u201c\u4eba\u8f6c\u8eab\u65f6\uff0c\u4f1a\u4e0b\u610f\u8bc6\u7684\u7528\u5f88\u5927\u52b2\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8054\u901a\u7684\u6210\u5c0f\u59d0\u5728\u90a3\u91cc\u8bf4\u8bdd\uff0c\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u8033\u6735\u5c16\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u6120\u6012\u7684\u773c\u795e\u5341\u5206\u4e25\u5389\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u7b11\u7740\u8ddf\u8054\u901a\u7684\u6210\u5c0f\u59d0\u8bf4\uff1a\u201c\u5979\u4e0d\u4f1a\u4ee5\u4e3a\u6211\u662f\u6545\u610f\u7684\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u8bb8\u591a\u613f\u610f\u7ed9\u5f20\u5148\u751f\u60a8\u642d\u8baa\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e0d\u8ddf\u5979\u626f\u66a7\u6627\u4e0d\u660e\u7684\u8bdd\u9898\uff0c\u771f\u89c9\u5f97\u8fd9\u4e2a\u5973\u4eba\u773c\u719f\uff0c\u4e00\u65f6\u4e5f\u60f3\u4e0d\u8d77\u662f\u8c01\u6765\uff0c\u95ee\u9053\uff1a\u201c\u6210\u5c0f\u59d0\u6709\u6ca1\u6709\u89c9\u5f97\u5979\u5f88\u773c\u719f\uff0c\u597d\u50cf\u6f14\u51fa\u4ec0\u4e48\u7535\u5f71\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u5148\u751f\u771f\u662f\u65e5\u7406\u4e07\u673a\u554a\uff0c\u8fde\u5927\u660e\u661f\u5b8b\u5a49\u4f69\u90fd\u4e0d\u8ba4\u8bc6\uff1f\u201d\u6210\u5c0f\u59d0\u7b11\u7740\u8bf4\uff0c\u201c\u4e5f\u96be\u602a\u5176\u4ed6\u5eb8\u8102\u4fd7\u7c89\u5165\u4e0d\u5230\u5f20\u5148\u751f\u7684\u773c\u2026\u2026\u201d\u5979\u7a81\u7136\u89c9\u5f97\u8fd9\u4e48\u8bf4\u6709\u4e9b\u5c06\u81ea\u5df1\u7ed5\u8fdb\u53bb\uff0c\u81ea\u5df1\u4eca\u5929\u665a\u4e0a\u4e0d\u5c31\u662f\u6ca1\u6709\u5165\u5f97\u4e86\u773c\u524d\u8fd9\u4eba\u7684\u773c\uff1f</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u5b8b\u5a49\u4f69\u96f6\u4e8c\u96f6\u4e09\u5e74\u5c31\u662f\u606f\u5f71\u5ac1\u5165\u8c6a\u95e8\u3001\u4ece\u6b64\u6df1\u5c45\u7b80\u51fa\uff0c\u4e5f\u96be\u602a\u6ca1\u6709\u7559\u4e0b\u591a\u6df1\u523b\u7684\u5370\u8c61\uff0c\u7ed9\u8fd9\u4e8b\u4e00\u6253\u5c94\uff0c\u4e5f\u6ca1\u6709\u8981\u53bb\u6d17\u624b\u95f4\u7684\u610f\u601d\uff0c\u53ea\u662f\u5c31\u8fd9\u6837\u8ddf\u8054\u901a\u6210\u5c0f\u59d0\u6298\u56de\u53bb\uff0c\u5c31\u597d\u50cf\u4e13\u95e8\u8fc7\u6765\u5077\u542c\u5979\u6253\u7535\u8bdd\u4f3c\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u8054\u901a\u6210\u5c0f\u59d0\u5148\u56de\u5927\u5385\uff0c\u4ed6\u5230\u6d17\u624b\u95f4\u8f6c\u4e86\u4e00\u5708\uff0c\u6574\u4e86\u6574\u8863\u886b\uff0c\u624d\u8d70\u56de\u5927\u5385\u3002\u8054\u901a\u7684\u6210\u5c0f\u59d0\u5c31\u5728\u8fc7\u9053\u53e3\u5b50\u90a3\u91cc\u7b49\u4ed6\uff0c\u5979\u7684\u795e\u8272\u6062\u590d\u4e86\u6b63\u5e38\uff0c\u597d\u50cf\u521a\u624d\u5728\u8fc7\u9053\u7ed9\u542c\u5230\u7535\u8bdd\u7684\u662f\u5176\u4ed6\u4eba\u3002\u5f20\u606a\u7b11\u7740\u6b63\u60f3\u95ee\u5979\u59da\u575a\u3001\u5218\u9e23\u950b\u4ee5\u53ca\u8096\u745e\u6c11\u7684\u52a9\u7406\u8dd1\u5230\u54ea\u91cc\u52fe\u5f15\u5973\u5b69\u5b50\u53bb\u4e86\uff0c\u5374\u6709\u4e24\u4e2a\u7a7f\u84dd\u897f\u88c5\u7684\u9752\u5e74\u8d70\u8fc7\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u5f84\u76f4\u8d70\u5230\u5f20\u606a\u7684\u9762\u524d\u624b\u642d\u5728\u5f20\u606a\u7684\u80a9\u8180\u4e0a\uff0c\u6311\u773c\u770b\u7740\u4ed6\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u77e5\u9053\u4f60\u521a\u624d\u52a8\u624b\u8c03\u620f\u8c01\u7684\u5973\u4eba\u4e86\uff1f\u201d</p></article>"}}}]);
