(window.webpackJsonp=window.webpackJsonp||[]).push([[1224],{1247:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e94\u8282 \u9b54\u6cd5\u7684\u5b66\u4e60",content:"<article><h2>\u7b2c\u5341\u4e94\u8282 \u9b54\u6cd5\u7684\u5b66\u4e60</h2><p>\u5149\u9634\u98de\u901d\uff0c\u65e5\u6708\u5982\u68ad\uff0c\u4eff\u4f5b\u53ea\u662f\u4e00\u8f6c\u773c\u95f4\uff0c\u743c\u6069\u5df2\u7ecf\u5347\u4e0a\u4e94\u5e74\u7ea7\u4e86\u3002</p><p>\u5c31\u5728\u524d\u5929\uff0c\u4ed6\u5ea6\u8fc7\u4e86\u81ea\u5df1\u5728\u8fd9\u4e2a\u4e16\u754c\u7684\u5341\u4e94\u5c81\u751f\u65e5\uff0c\u8fd9\u4e00\u5929\u540c\u6837\u4e5f\u662f\u73ca\u5609\u7684\u751f\u65e5\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u4e0d\u662f\u5047\u671f\uff0c\u4ed6\u6ca1\u529e\u6cd5\u56de\u5bb6\u548c\u59d0\u59d0\u76f8\u805a\u3002</p><p>\u5feb\u4e86\uff0c\u5c31\u5269\u8fd9\u6700\u540e\u4e00\u5e74\u4e86\u3002</p><p>\u4e00\u5e74\u7ea7\u7684\u65f6\u5019\uff0c\u5b66\u7684\u8fd8\u662f\u57fa\u7840\u7406\u8bba\uff0c\u4ece\u4e8c\u5e74\u7ea7\u5f00\u59cb\uff0c\u5c31\u6b63\u5f0f\u63a5\u89e6\u9b54\u6cd5\u4e86\u3002\u743c\u6069\u7684\u6210\u7ee9\uff0c\u4e5f\u6b63\u662f\u4ece\u6b64\u5f00\u59cb\u6709\u4e86\u66f4\u5feb\u7684\u8fdb\u6b65\uff1b\u5c24\u5176\u662f\u5230\u4e86\u56db\u5e74\u7ea7\uff0c\u5f00\u59cb\u8fdb\u884c\u5b9e\u6218\u8bad\u7ec3\uff0c\u743c\u6069\u7a81\u98de\u731b\u8fdb\uff0c\u5728\u5e74\u672b\u8003\u8bd5\u4e2d\uff0c\u4e00\u4e3e\u53d6\u5f97\u7b2c\u56db\u540d\u3002</p><p>\u5f53\u7136\uff0c\u56e0\u4e3a\u6bcf\u5e74\u7684\u6dd8\u6c70\uff0c\u4ee5\u53ca\u79cd\u79cd\u610f\u5916\u4e8b\u6545\uff0c\u518d\u52a0\u4e0a\u81ea\u884c\u9000\u5b66\u7b49\u5404\u79cd\u539f\u56e0\uff0c\u5982\u4eca\u548c\u4ed6\u540c\u5e74\u7ea7\u7684\u5b66\u751f\uff0c\u5df2\u7ecf\u4ece\u5165\u5b66\u65f6\u7684\u4e00\u767e\u4f59\u540d\uff0c\u51cf\u5c11\u5230\u4e94\u5341\u4e03\u540d\u4e86\u3002</p><p>\u7b49\u5230\u7b2c\u4e94\u5e74\u6700\u540e\u6bd5\u4e1a\uff0c\u8fd9\u4e2a\u6570\u76ee\u81f3\u5c11\u8981\u518d\u524a\u51cf\u6389\u4e00\u534a\uff0c\u5b9e\u9645\u4e0a\u5e94\u8be5\u4f1a\u66f4\u591a\u3002\u5927\u6982\uff0c\u53ea\u6709\u4e0d\u8d85\u8fc7\u4e8c\u5341\u4e2a\u4eba\u80fd\u591f\u6bd5\u4e1a\u5427\u3002\u5f53\u7136\uff0c\u4ee5\u743c\u6069\u73b0\u5728\u7684\u6210\u7ee9\uff0c\u662f\u4e0d\u5fc5\u62c5\u5fc3\u88ab\u6dd8\u6c70\u7684\u95ee\u9898\u4e86\u3002</p><p>\u4e4b\u6240\u4ee5\u5728\u56db\u5e74\u7ea7\u6210\u7ee9\u7a81\u98de\u731b\u8fdb\uff0c\u5012\u5e76\u4e0d\u662f\u56e0\u4e3a\u743c\u6069\u5f88\u64c5\u957f\u5b9e\u6218\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u534a\u70b9\u4e0d\u64c5\u957f\u3002\u4ed6\u524d\u4e16\u57fa\u672c\u7b97\u662f\u4e2a\u5949\u516c\u5b88\u6cd5\u7684\u826f\u597d\u5e02\u6c11\uff0c\u8fd9\u8f88\u5b50\u662f\u4e2a\u5b89\u5206\u5b88\u5df1\u7684\u5c0f\u5546\u4eba\u7684\u5b69\u5b50\uff0c\u8fde\u52a8\u624b\u6253\u67b6\u90fd\u5f88\u5c11\uff0c\u552f\u4e00\u7684\u780d\u6740\u7ecf\u9a8c\u53ea\u5b58\u5728\u4e8e\u6e38\u620f\u548c\u5c0f\u8bf4\u91cc\u3002</p><p>\u8fd9\u4e00\u5207\u4f9d\u7136\u8fd8\u662f\u62dc\u4ed6\u90a3\u4e09\u811a\u732b\u7684\u5185\u529f\u6240\u8d50\u3002</p><p>\u5deb\u5e08\u5b66\u6821\u7684\u8bfe\u7a0b\u8bbe\u7f6e\uff0c\u662f\u6bd4\u8f83\u5408\u7406\u7684\u3002\u7b2c\u4e00\u5e74\u57fa\u672c\u662f\u7eaf\u5b66\u57fa\u7840\u7406\u8bba\uff0c\u7b2c\u4e8c\u5e74\u5230\u7b2c\u4e09\u5e74\uff0c\u4e00\u8fb9\u5b66\u4e60\u66f4\u9ad8\u6df1\u7684\u7406\u8bba\uff0c\u4e00\u8fb9\u8fdb\u884c\u5c11\u91cf\u7684\u6cd5\u672f\u8bad\u7ec3\uff0c\u638c\u63e1\u4e00\u4e9b\u7b80\u5355\u7684\u9b54\u6cd5\u3002\u5347\u5230\u56db\u5e74\u7ea7\uff0c\u8fd8\u80fd\u4e0d\u88ab\u6dd8\u6c70\u4e0b\u53bb\uff0c\u90a3\u4e48\u5c31\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u57fa\u672c\u5408\u683c\u7684\u9b54\u6cd5\u5b66\u5f92\u4e86\u3002</p><p>\u8be5\u5b66\u7684\u9b54\u6cd5\u7406\u8bba\uff0c\u57fa\u672c\u90fd\u5df2\u7ecf\u70c2\u719f\u4e8e\u5fc3\uff0c\u63a5\u4e0b\u6765\u6240\u9700\u8981\u7684\uff0c\u5c31\u662f\u5927\u91cf\u7684\u5b9e\u8df5\u3002</p><p>\u4e5f\u5c31\u662f\u8bad\u7ec3\u65bd\u6cd5\u4e86\u3002</p><p>\u4f5c\u4e3a\u5deb\u5e08\u5b66\u9662\u7684\u56db\u5e74\u7ea7\u5b66\u751f\uff0c\u4e00\u5929\u7684\u6807\u51c6\u65e5\u7a0b\u5b89\u6392\u662f\u8fd9\u6837\u7684\u3002</p><p>\u5728\u516b\u5c0f\u65f6\u7684\u7761\u7720\u4e4b\u540e\uff0c\u65e9\u4e0a\u516d\u70b9\u949f\uff0c\u8d77\u5e8a\u3002</p><p>\u516d\u70b9\u5230\u4e03\u70b9\uff0c\u82b1\u4e00\u5c0f\u65f6\u65f6\u95f4\u51c6\u5907\u9b54\u6cd5\u3002\u8fd9\u4e16\u754c\u91cc\u7684\u6bcf\u4e2a\u5deb\u5e08\u90fd\u662f\u5982\u6b64\uff0c\u6ca1\u6709\u4f8b\u5916\u3002\u9b54\u6cd5\u4e0d\u662f\u7b80\u7b80\u5355\u5355\u968f\u968f\u4fbf\u4fbf\u5c31\u80fd\u91ca\u653e\u7684\uff0c\u5fc5\u987b\u5728\u5145\u8db3\u7684\u7761\u7720\u4e4b\u540e\u52a0\u4e0a\u5145\u8db3\u7684\u51c6\u5907\u2014\u2014\u6240\u8c13\u5145\u8db3\u7684\u51c6\u5907\uff0c\u4e00\u822c\u90fd\u662f\u4e00\u5c0f\u65f6\u3002\u5f53\u7136\uff0c\u8fd9\u662f\u6307\u901a\u5e38\u60c5\u51b5\uff0c\u6709\u4e9b\u5929\u8d4b\u9ad8\u660e\u6216\u8005\u529b\u91cf\u5f3a\u5927\u7684\u5deb\u5e08\uff0c\u662f\u53ef\u4ee5\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u7f29\u77ed\u8fd9\u4e2a\u65f6\u95f4\u7684\u3002</p><p>\u4e03\u70b9\u5230\u4e03\u70b9\u534a\uff0c\u65e9\u9910\u3002\u9634\u9b42\u57ce\u662f\u534a\u519b\u4e8b\u5316\u7ba1\u7406\u7684\u57ce\u5e02\uff0c\u5deb\u5e08\u5b66\u9662\u540c\u6837\u5b9e\u65bd\u7684\u662f\u534a\u519b\u4e8b\u5316\u7ba1\u7406\u3002\u65e9\u665a\u4e09\u9910\u90fd\u662f\u5b9a\u65f6\u5b9a\u91cf\u800c\u4e14\u5b9a\u8d28\u4f9b\u5e94\u7684\uff0c\u8fc7\u65f6\u4e0d\u5019\uff0c\u5f53\u65f6\u4e0d\u5403\u5c31\u7b49\u7740\u534a\u5929\u90fd\u6328\u997f\u5427\u3002</p><p>\u4e03\u70b9\u534a\u5230\u5341\u4e00\u70b9\uff0c\u5927\u7ea6\u4e24\u4e2a\u534a\u5c0f\u65f6\uff0c\u8fd9\u662f\u65bd\u6cd5\u8bad\u7ec3\u7684\u65f6\u95f4\u3002\u5b66\u751f\u4eec\u5728\u5b9e\u9a8c\u5ba4\uff0c\u5728\u64cd\u573a\uff0c\u5728\u8d70\u5eca\uff0c\u5728\u5899\u89d2\uff0c\u603b\u4e4b\u4e00\u5207\u53ef\u80fd\u7684\u5730\u65b9\u65bd\u5c55\u65e9\u4e0a\u51c6\u5907\u597d\u7684\u9b54\u6cd5\uff0c\u7814\u7a76\u3001\u63a2\u8ba8\u3001\u63e3\u6469\u3001\u5206\u6790\u6bd4\u8f83\uff0c\u8be6\u7ec6\u7422\u78e8\u65bd\u6cd5\u52a8\u4f5c\u7684\u6bcf\u4e2a\u59ff\u52bf\u662f\u5426\u6b63\u786e\uff0c\u5492\u8bed\u7684\u6bcf\u4e2a\u97f3\u8282\u662f\u5426\u5b8c\u7f8e\uff0c\u4ee5\u53ca\u81ea\u5df1\u548c\u9b54\u7f51\u7684\u6c9f\u901a\u8054\u63a5\u662f\u5426\u6bd4\u6628\u5929\u66f4\u52a0\u987a\u7545\u4e00\u70b9\u3002</p><p>\u5341\u4e00\u70b9\u5230\u5341\u4e00\u70b9\u534a\uff0c\u5348\u9910\u3002</p><p>\u5348\u9910\u4e4b\u540e\uff0c\u5927\u90e8\u5206\u5b66\u751f\u5c31\u81ea\u7531\u6d3b\u52a8\u4e86\uff0c\u4f46\u6781\u5c11\u6570\u52e4\u52c9\u7684\u5b66\u751f\uff0c\u5219\u4f1a\u6293\u7d27\u65f6\u95f4\u53bb\u7761\u89c9\u3002\u5341\u4e00\u70b9\u534a\u5230\u665a\u4e0a\u4e03\u70b9\u534a\u70b9\uff0c\u53c8\u662f\u4e00\u4e2a\u516b\u5c0f\u65f6\u7684\u4f11\u606f\u5b8c\u6bd5\uff0c\u5b66\u751f\u4eec\u8d77\u5e8a\u3002</p><p>\u4e03\u70b9\u534a\u5230\u516b\u70b9\uff0c\u665a\u9910\u3002</p><p>\u516b\u70b9\u5230\u4e5d\u70b9\uff0c\u53c8\u662f\u4e00\u5c0f\u65f6\u7684\u51c6\u5907\u9b54\u6cd5\u3002</p><p>\u63a5\u4e0b\u6765\uff0c\u5728\u5341\u70b9\u949f\u7184\u706f\u7761\u89c9\u4e4b\u524d\uff0c\u53c8\u6709\u4e00\u5c0f\u65f6\u7684\u65f6\u95f4\uff0c\u53ef\u4ee5\u4f9b\u5b66\u751f\u4eec\u518d\u4e00\u6b21\u8bad\u7ec3\u81ea\u5df1\u7684\u65bd\u6cd5\u6280\u5de7\u3002</p><p>\u8fd9\u5c31\u662f\u4e00\u4e2a\u6807\u51c6\u7684\u56db\u5e74\u7ea7\u5b66\u751f\u7684\u4e00\u5929\u884c\u7a0b\u3002\u5176\u4e2d\u6700\u5173\u952e\u7684\u5730\u65b9\uff0c\u5c31\u5728\u4e8e\u90a3\u516b\u5c0f\u65f6\u7761\u7720\u65f6\u95f4\u3002\u9b54\u6cd5\u662f\u975e\u5e38\u7cbe\u5fae\u6df1\u5965\u7684\u6280\u827a\uff0c\u8981\u6b63\u786e\u5730\u51c6\u5907\u9b54\u6cd5\uff0c\u5fc5\u987b\u8981\u5728\u5934\u8111\u6700\u6e05\u9192\u7684\u65f6\u5019\uff0c\u5426\u5219\u6781\u5176\u5bb9\u6613\u51fa\u5c94\u5b50\uff0c\u8fde\u53d7\u4f24\u4e27\u547d\u90fd\u6709\u53ef\u80fd\uff0c\u6240\u4ee5\u5145\u8db3\u7761\u7720\u5fc5\u987b\u4fdd\u8bc1\uff0c\u4e00\u822c\u4eba\u90fd\u662f\u516b\u5c0f\u65f6\u7761\u7720\u3002\u5f53\u7136\uff0c\u6709\u4e9b\u4eba\u5929\u751f\u7cbe\u529b\u65fa\u76db\u8fc7\u4eba\uff0c\u53ef\u80fd\u7761\u4e0a\u516d\u4e03\u4e2a\u5c0f\u65f6\u5c31\u8db3\u591f\uff0c\u4f46\u8fd9\u6bd5\u7adf\u662f\u6781\u5c11\u6570\u7684\u7279\u4f8b\uff0c\u5728\u5deb\u5e08\u8fd9\u79cd\u7d20\u6765\u4ee5\u4f53\u8d28\u865a\u5f31\u8457\u79f0\u7684\u7fa4\u4f53\u91cc\uff0c\u90a3\u5c31\u66f4\u662f\u51e4\u6bdb\u9e9f\u89d2\uff0c\u53ef\u9047\u4e0d\u53ef\u6c42\uff0c\u5b8c\u5168\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002</p><p>\u6240\u4ee5\uff0c\u5373\u4f7f\u662f\u6700\u6700\u52e4\u594b\u7684\u5b66\u751f\uff0c\u4e00\u5929\u4e5f\u4ec5\u4ec5\u53ea\u6709\u4e24\u6b21\u65bd\u6cd5\u5b9e\u8df5\u8bad\u7ec3\u7684\u673a\u4f1a\u3002\u800c\u4e14\u4e00\u5929\u5185\u8981\u5f3a\u884c\u7761\u7720\u5341\u516d\u4e2a\u5c0f\u65f6\uff0c\u8fd9\u5176\u5b9e\u662f\u4ef6\u975e\u5e38\u975e\u5e38\u56f0\u96be\u7684\u4e8b\u60c5\uff0c\u8fdd\u53cd\u4e86\u6b63\u5e38\u7684\u751f\u7406\u89c4\u5f8b\uff0c\u8981\u60f3\u957f\u671f\u575a\u6301\u5982\u6b64\uff0c\u5fc5\u5b9a\u9700\u8981\u501f\u52a9\u4e00\u4e9b\u5f3a\u529b\u50ac\u7720\u836f\u7269\u2014\u2014\u636e\u743c\u6069\u89c2\u5bdf\uff0c\u56db\u5e74\u7ea7\u5e74\u672b\u8003\u8bd5\uff0c\u6392\u540d\u5728\u4ed6\u524d\u9762\u7684\u90a3\u4e09\u4e2a\u5bb6\u4f19\uff0c\u5168\u90fd\u662f\u628a\u5f3a\u529b\u7761\u7720\u836f\u5f53\u996d\u5403\u7684\u3002</p><p>\u6240\u8c13\u662f\u836f\u4e09\u5206\u6bd2\uff0c\u8fd9\u4e16\u754c\u4e0a\u5c82\u6709\u5168\u65e0\u526f\u4f5c\u7528\u7684\u836f\u7269\uff1f\u5076\u5c14\u4e00\u6b21\u4e24\u6b21\u4e5f\u7f62\u4e86\uff0c\u957f\u671f\u8fd9\u4e48\u73a9\uff0c\u80af\u5b9a\u5927\u5927\u635f\u4f24\u8eab\u4f53\uff0c\u65e5\u540e\u8fd8\u4e0d\u77e5\u9053\u4f1a\u57cb\u4e0b\u591a\u5c11\u9690\u60a3\u3002\u8fd9\u8ba9\u743c\u6069\u60f3\u8d77\u4ed6\u4ee5\u524d\u4e0a\u9ad8\u4e2d\uff0c\u4e34\u8fd1\u9ad8\u8003\u65f6\uff0c\u5f88\u591a\u540c\u5b66\u4e89\u5206\u593a\u79d2\u5730\u80cc\u4e66\u5b66\u4e60\uff0c\u4e3a\u4e86\u8282\u7ea6\u7761\u7720\u65f6\u95f4\uff0c\u4e8e\u662f\u670d\u7528\u5927\u91cf\u63d0\u795e\u836f\u7269\uff0c\u53ea\u8981\u7761\u4e0a\u4e09\u56db\u4e2a\u5c0f\u65f6\uff0c\u5c31\u80fd\u4fdd\u8bc1\u4e00\u5929\u7684\u6e05\u9192\u3002\u5982\u4eca\u5728\u9634\u9b42\u57ce\u91cc\uff0c\u8fd9\u4e9b\u540c\u5b66\u5374\u662f\u4e3a\u4e86\u80fd\u7761\u7740\u800c\u670d\u836f\uff0c\u867d\u8bf4\u65b9\u5f0f\u4e0d\u540c\uff0c\u6839\u672c\u76ee\u7684\u5374\u662f\u4e00\u6837\uff0c\u53ef\u8c13\u662f\u5f02\u66f2\u540c\u5de5\u4e86\u3002</p><p>\u8fd9\u79cd\u7528\u836f\u7269\u5f3a\u884c\u6539\u53d8\u4eba\u4f53\u6b63\u5e38\u751f\u7406\u89c4\u5f8b\u7684\u4f5c\u6cd5\uff0c\u8bf4\u5f97\u76f4\u767d\u70b9\uff0c\u5176\u5b9e\u5c31\u662f\u5728\u900f\u652f\u751f\u547d\u3002\u53ea\u662f\u9053\u7406\u4eba\u4eba\u90fd\u61c2\uff0c\u771f\u5230\u5b9e\u9645\u4e2d\uff0c\u81ea\u7136\u5c31\u6709\u79cd\u79cd\u65e0\u53ef\u5948\u4f55\u4e4b\u5904\uff0c\u4e0d\u5f97\u4e0d\u5982\u6b64\u3002\u9ad8\u8003\u65f6\u8fd9\u4e48\u73a9\uff0c\u662f\u56e0\u4e3a\u9ad8\u8003\u5bf9\u4e8e\u4e00\u4e2a\u5b66\u751f\u6765\u8bf4\u81f3\u5173\u91cd\u8981\uff0c\u51e0\u4e4e\u51b3\u5b9a\u4e86\u4ee5\u540e\u4e00\u8f88\u5b50\u7684\u524d\u9014\uff0c\u4e3a\u6b64\u5c31\u7b97\u6298\u635f\u5341\u5e74\u5bff\u547d\uff0c\u90a3\u4e5f\u5f97\u62fc\u4e86\u3002\u5deb\u5e08\u5b66\u6821\u91cc\u7684\u5b66\u751f\u8fd9\u4e48\u73a9\uff0c\u540c\u6837\u4e5f\u662f\u56e0\u4e3a\u8fd9\u6b21\u6bd5\u4e1a\u8003\u8bd5\uff0c\u975e\u5e38\u975e\u5e38\u5173\u952e\u3002</p><p>\u5982\u679c\u80fd\u53d6\u5f97\u7b2c\u4e00\u540d\uff0c\u90a3\u4e48\u5c31\u6709\u5e0c\u671b\u664b\u5165\u66f4\u9ad8\u7b49\u3001\u66f4\u9690\u853d\uff0c\u4f20\u8bf4\u7531\u9634\u9b42\u57ce\u526f\u57ce\u4e3b\u54c8\u6770\u970d\u7eb3\u5148\u751f\u4eb2\u81ea\u62c5\u4efb\u6821\u957f\u7684\u79d8\u5bc6\u5b66\u6821\u8fdb\u884c\u6df1\u9020\uff0c\u88ab\u4f20\u6388\u4e00\u4e9b\u66f4\u9ad8\u660e\uff0c\u66f4\u7cbe\u6df1\uff0c\u66f4\u795e\u5947\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u81ea\u6b64\u8fdb\u5165\u9634\u9b42\u57ce\u771f\u6b63\u7684\u6743\u529b\u6838\u5fc3\u3002\u5c31\u7b97\u4e0d\u80fd\u593a\u5f97\u7b2c\u4e00\uff0c\u81f3\u5c11\u4e5f\u8981\u4e89\u53d6\u62ff\u4e2a\u597d\u6210\u7ee9\uff0c\u4fbf\u6709\u673a\u4f1a\u8fdb\u5165\u653f\u5e9c\u90e8\u95e8\u4f9b\u804c\uff0c\u6216\u8005\u88ab\u67d0\u4f4d\u5927\u5deb\u5e08\u9752\u773c\u770b\u4e2d\uff0c\u6536\u4e3a\u95e8\u5f92\uff0c\u4ece\u6b64\u524d\u9014\u4e5f\u662f\u4e00\u7247\u5149\u660e\u3002</p><p>\u5018\u82e5\u53ea\u80fd\u4ee5\u666e\u901a\u6210\u7ee9\u6bd5\u4e1a\uff0c\u90a3\u5927\u6982\u5c31\u53ea\u80fd\u53bb\u519b\u961f\u91cc\u5f53\u4e2a\u4f4e\u9636\u519b\u5b98\uff0c\u751a\u81f3\u662f\u58eb\u5175\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5c31\u5728\u548c\u5e7d\u5f71\u754c\u602a\u7269\u7684\u6218\u6597\u4e2d\u58ee\u70c8\u727a\u7272\u4e86\u3002\u5982\u679c\u66f4\u5dee\u52b2\u70b9\uff0c\u88ab\u6bd5\u4e1a\u8003\u8bd5\u6dd8\u6c70\u4e86\uff0c\u90a3\u5c31\u662f\u6eda\u86cb\u56de\u5bb6\uff0c\u4e8c\u767e\u91d1\u5e01\u7684\u5b66\u8d39\uff0c\u4e94\u5e74\u65f6\u95f4\u7684\u82e6\u4fee\uff0c\u5c31\u6b64\u5316\u4e3a\u6ce1\u5f71\uff0c\u843d\u4e2a\u573a\u7a7a\uff0c\u5230\u65f6\u5019\u771f\u662f\u6b32\u54ed\u65e0\u6cea\u3002</p><p>\u7ade\u4e89\u5982\u6b64\u6fc0\u70c8\u6b8b\u9177\uff0c\u5c31\u602a\u4e0d\u5f97\u5927\u5bb6\u7528\u8fd9\u79cd\u8fd1\u4f3c\u81ea\u6b8b\u81ea\u6740\u7684\u65b9\u6cd5\u4e86\u3002</p><p>\u4f46\u743c\u6069\u5c31\u4e0d\u4e00\u6837\uff0c\u522b\u4eba\u9700\u8981\u516b\u5c0f\u65f6\u7684\u7761\u7720\uff0c\u4ed6\u53ea\u9700\u8981\u4e24\u5c0f\u65f6\u7684\u547c\u5438\u5410\u7eb3\u3002\u6240\u4ee5\u4ed6\u6bcf\u5929\u6240\u62e5\u6709\u7684\u65bd\u6cd5\u5b9e\u8df5\u8bad\u7ec3\u673a\u4f1a\uff0c\u4e0d\u662f\u4e24\u6b21\uff0c\u800c\u662f\u56db\u6b21\u751a\u81f3\u4e94\u6b21\u3002</p><p>\u5b9e\u8df5\u51fa\u771f\u77e5\uff0c\u8fd9\u8bdd\u8bf4\u5f97\u5bf9\u6781\u4e86\u3002\u8d85\u8fc7\u540c\u5b66\u4e24\u500d\u8fd8\u591a\u7684\u8bad\u7ec3\u548c\u5b9e\u8df5\u7ecf\u9a8c\uff0c\u8ba9\u743c\u6069\u8fc5\u901f\u4ece\u4e00\u4e2a\u83dc\u9e1f\u6210\u4e3a\u4f7c\u4f7c\u8005\u3002\u8bf4\u5230\u5e95\uff0c\u518d\u9ad8\u660e\u7684\u5965\u672f\u5929\u8d4b\uff0c\u518d\u6e0a\u535a\u7684\u5bb6\u5b66\uff0c\u7ec8\u7a76\u4e5f\u654c\u4e0d\u8fc7\u51e0\u500d\u7684\u5b9e\u8df5\u7ecf\u9a8c\u554a\u3002</p><p>\u743c\u6069\u7684\u540c\u5b66\u4e2d\uff0c\u5e76\u4e0d\u7f3a\u4e4f\u5929\u624d\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u7ecf\u8fc7\u4e00\u5e74\u53c8\u4e00\u5e74\u7684\u6dd8\u6c70\uff0c\u5982\u4eca\u8fd8\u80fd\u7559\u5728\u5b66\u6821\u91cc\u7684\uff0c\u57fa\u672c\u90fd\u53ef\u4ee5\u8bf4\u662f\u5929\u8d44\u51fa\u4f17\uff0c\u5c24\u5176\u662f\u80fd\u6392\u540d\u524d\u5341\u7684\u5bb6\u4f19\uff0c\u5f53\u7136\u743c\u6069\u8981\u9664\u5916\u3002</p><p>\u8fd9\u4e9b\u5bb6\u4f19\u91cc\uff0c\u6709\u4eba\u5929\u751f\u5c31\u80fd\u611f\u5e94\u5230\u9b54\u6cd5\u7f51\u7edc\u7684\u5b58\u5728\uff0c\u6709\u4eba\u542c\u4e00\u904d\u5c31\u80fd\u8bb0\u4e0b\u5197\u957f\u7e41\u590d\u7684\u5492\u6587\uff0c\u6709\u4eba\u770b\u4e00\u773c\u5c31\u80fd\u6a21\u4eff\u5b8c\u6210\u6781\u5176\u8be1\u5f02\u6ede\u788d\u7684\u65bd\u6cd5\u52a8\u4f5c\uff0c\u8fd8\u6709\u8d85\u7ea7\u53d8\u6001\u7684\uff0c\u5c45\u7136\u80fd\u65e0\u5e08\u81ea\u901a\u5730\u5c31\u9886\u609f\u66f4\u8270\u6df1\u7684\u9b54\u6cd5\uff0c\u6bd4\u5982\u53bb\u5e74\u8003\u8bd5\u5f97\u7b2c\u4e00\u540d\u7684\u90a3\u4e2a\u5e93\u80af\uff0c\u7b80\u76f4\u5c31\u4e0d\u5e94\u8be5\u8fd8\u7b97\u662f\u4eba\u7c7b\u3002</p><p>\u743c\u6069\u80fd\u4ee5\u4e0d\u7b97\u591a\u4e48\u6770\u51fa\u7684\u8d44\u8d28\uff0c\u529b\u538b\u8bf8\u4eba\u5f97\u5230\u7b2c\u56db\u540d\uff0c\u518d\u6b21\u8bc1\u660e\u4e86\u5b9e\u8df5\u624d\u662f\u68c0\u9a8c\u771f\u7406\u7684\u552f\u4e00\u6807\u51c6\u2026\u2026\u7b97\u4e86\uff0c\u8fd9\u90fd\u626f\u5230\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u603b\u4e4b\uff0c\u5728\u5fc3\u4e2d\u65e0\u6570\u6b21\u611f\u8c22\u7530\u4f2f\u5149\u4e4b\u540e\uff0c\u743c\u6069\u4ee5\u5168\u5e74\u7ea7\u7b2c\u56db\u540d\u7684\u8eab\u4efd\uff0c\u8e0f\u5165\u4e86\u4e94\u5e74\u7ea7\uff0c\u5f00\u59cb\u4e86\u6700\u540e\u4e00\u5e74\u7684\u51b2\u523a\u3002</p></article>"}}}]);
