(window.webpackJsonp=window.webpackJsonp||[]).push([[1226],{1249:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e03\u8282 \u5e7d\u5f71\u754c\u7684\u5371\u9669",content:"<article><h2>\u7b2c\u5341\u4e03\u8282 \u5e7d\u5f71\u754c\u7684\u5371\u9669</h2><p>\u5e7d\u5f71\u754c\uff0c\u542c\u540d\u5b57\u5c31\u4e0d\u662f\u4e2a\u826f\u5584\u4e4b\u5730\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5728\u8fd9\u4e2a\u4e16\u754c\u7684\u5404\u5927\u4f4d\u9762\u4e4b\u4e2d\uff0c\u5e7d\u5f71\u754c\u6216\u8bb8\u7b97\u4e0d\u4e0a\u662f\u6700\u6050\u6016\u9a87\u4eba\u7684\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u662f\u6700\u8840\u8165\u66b4\u529b\u7684\uff0c\u5f53\u7136\u4e5f\u51b3\u4e0d\u4f1a\u662f\u98ce\u666f\u6700\u6f02\u4eae\u7684\u2014\u2014\u4f46\u7edd\u7edd\u5bf9\u5bf9\u662f\u6700\u9634\u9669\u7684\u3002</p><p>\u7ad9\u5728\u5deb\u5e08\u5b66\u6821\u7684\u9ad8\u697c\u4e0a\uff0c\u8fd0\u8db3\u76ee\u529b\u5f80\u5916\u671b\u53bb\uff0c\u773c\u4e2d\u6240\u89c1\u53ea\u662f\u4e00\u7247\u7070\u8499\u8499\u7684\u5929\u7a7a\u3002\u4ece\u8868\u9762\u4e0a\u770b\uff0c\u5e7d\u5f71\u754c\u4e0d\u8fc7\u5c31\u662f\u65e0\u7a77\u65e0\u5c3d\u7684\u9634\u5f71\u805a\u96c6\uff0c\u91cd\u91cd\u7070\u6697\u7684\u8ff7\u96fe\u4e4b\u4e2d\uff0c\u751a\u81f3\u8fd8\u9690\u7ea6\u53ef\u4ee5\u770b\u89c1\u7c7b\u4f3c\u9646\u5730\u3001\u5c71\u5cb3\u3001\u6cb3\u6d41\u548c\u6df1\u6e0a\u4e4b\u7c7b\u7684\u4e1c\u897f\u3002\u6574\u4f53\u4e0a\uff0c\u9664\u4e86\u6697\u4e00\u4e9b\u51b7\u4e00\u4e9b\uff0c\u6ca1\u6709\u592a\u9633\u6708\u4eae\u548c\u661f\u661f\uff0c\u5e7d\u5f71\u754c\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\u3002</p><p>\u4f46\u5728\u8fd9\u5e73\u51e1\u65e0\u5947\u7684\u5916\u8868\u4e0b\uff0c\u9690\u85cf\u7740\u6700\u9634\u9669\u7684\u5371\u673a\u3002</p><p>\u5deb\u5e08\u5b66\u6821\u4e2d\uff0c\u5f00\u6709\u201c\u4f4d\u9762\u5b66\u201d\u8fd9\u79cd\u8bfe\u7a0b\uff0c\u4e13\u95e8\u8bb2\u8ff0\u5404\u5927\u4f4d\u9762\u7684\u60c5\u5f62\u3002\u5e7d\u5f71\u754c\u4f5c\u4e3a\u9634\u9b42\u57ce\u5343\u4f59\u5e74\u6765\u7684\u6816\u8eab\u4e4b\u5730\uff0c\u81ea\u7136\u662f\u7740\u91cd\u4ecb\u7ecd\uff0c\u8d44\u6599\u8be6\u7ec6\u65e0\u6bd4\u3002</p><p>\u4e0e\u5176\u4ed6\u5404\u5927\u4f4d\u9762\u76f8\u6bd4\u8d77\u6765\uff0c\u5e7d\u5f71\u754c\u548c\u7269\u8d28\u754c\u8054\u7cfb\u6700\u4e3a\u7d27\u5bc6\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u5e7d\u5f71\u754c\u662f\u7269\u8d28\u754c\u7684\u626d\u66f2\u53d8\u5f62\u3002\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u5e7d\u5f71\u754c\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u4ece\u7269\u8d28\u754c\u5ef6\u4f38\u800c\u51fa\u7684\u534a\u4f4d\u9762\uff0c\u72ed\u5c0f\u800c\u7b80\u5355\uff0c\u540e\u6765\u624d\u9010\u6e10\u5f62\u6210\u5982\u4eca\u7684\u5e7f\u88a4\u65e0\u8fb9\uff0c\u6e10\u6e10\u72ec\u7acb\u3002\u4f46\u603b\u7684\u6765\u8bf4\uff0c\u4f9d\u7136\u662f\u548c\u7269\u8d28\u754c\u4fdd\u6301\u7740\u975e\u5e38\u7d27\u5bc6\u7684\u8054\u7cfb\uff0c\u7528\u6559\u79d1\u4e66\u4e0a\u7684\u8bcd\u6765\u5f62\u5bb9\uff0c\u5c31\u662f\u201c\u4ea4\u9519\u878d\u5408\u201d\uff0c\u6216\u8005\u53ef\u4ee5\u8bf4\uff0c\u662f\u7f20\u7ed5\u5728\u4e00\u8d77\u7684\u3002</p><p>\u56e0\u6b64\uff0c\u8981\u6253\u7834\u7a7a\u95f4\u58c1\u969c\uff0c\u8fdb\u884c\u4f4d\u9762\u7a7f\u68ad\uff0c\u539f\u672c\u662f\u4ef6\u975e\u5e38\u8270\u96be\u7684\u4e8b\u60c5\uff0c\u4f46\u82e5\u53ea\u662f\u5728\u5e7d\u5f71\u754c\u548c\u7269\u8d28\u754c\u4e4b\u95f4\u6765\u5f80\uff0c\u5374\u5c31\u5bb9\u6613\u5f97\u591a\u4e86\u3002\u4e5f\u6b63\u5982\u6b64\uff0c\u6240\u4ee5\u9634\u9b42\u57ce\u4e3b\u5f53\u5e74\u80fd\u628a\u6574\u5ea7\u6d6e\u7a7a\u57ce\u90fd\u642c\u8fd0\u8fc7\u6765\uff0c\u56fa\u7136\u662f\u4ed6\u6cd5\u529b\u9ad8\u5f3a\uff0c\u795e\u901a\u5e7f\u5927\uff0c\u4e5f\u662f\u56e0\u4e3a\u8fd9\u4e24\u4e2a\u4f4d\u9762\u5929\u7136\u8054\u7cfb\u7d27\u5bc6\uff0c\u58c1\u969c\u4e0d\u4e25\u7684\u7f18\u6545\u3002</p><p>\u4e0d\u4fe1\u8ba9\u4ed6\u628a\u9634\u9b42\u57ce\u79fb\u5230\u522b\u7684\u4f4d\u9762\u8bd5\u8bd5\uff0c\u4fdd\u8bc1\u7d2f\u6b7b\u4ed6\u90fd\u529e\u4e0d\u5230\u3002</p><p>\u4e60\u60ef\u4e8e\u7269\u8d28\u754c\u751f\u6d3b\u7684\u4eba\uff0c\u4e00\u65e6\u8fdb\u5165\u5e7d\u5f71\u754c\uff0c\u60f3\u5fc5\u4e5f\u4e0d\u4f1a\u591a\u4e48\u964c\u751f\u3002\u6b63\u5982\u524d\u6240\u8ff0\uff0c\u9664\u4e86\u6ca1\u6709\u65e5\u6708\u661f\u8fb0\u5916\uff0c\u8fd9\u91cc\u5176\u5b9e\u548c\u7269\u8d28\u754c\u7684\u81ea\u7136\u73af\u5883\u975e\u5e38\u8fd1\u4f3c\uff0c\u5c71\u5cb3\u5cf0\u5ce6\u3001\u68ee\u6797\u8349\u5730\u3001\u6c5f\u6cb3\u6e56\u6d77\uff0c\u4e00\u5e94\u4ff1\u5168\uff0c\u53ea\u4e0d\u8fc7\u5904\u5904\u90fd\u7b3c\u7f69\u7740\u6c89\u6c89\u9634\u5f71\u8ff7\u96fe\u7f62\u4e86\u3002</p><p>\u9664\u4e86\u5c71\u5ddd\u6cb3\u6d41\u8fd9\u4e9b\u81ea\u7136\u5730\u5f62\u5916\uff0c\u6709\u65f6\u5019\u751a\u81f3\u8fd8\u53ef\u4ee5\u770b\u5230\u57ce\u9547\u3001\u5821\u5792\u4e4b\u7c7b\u7684\u4eba\u4e3a\u5efa\u7b51\u2014\u2014\u4e0d\u8fc7\u8fd9\u4e9b\u7edf\u7edf\u5168\u662f\u5e7b\u8c61\u3002\u5e7d\u5f71\u754c\u6e90\u81ea\u7269\u8d28\u754c\uff0c\u662f\u7269\u8d28\u754c\u7684\u626d\u66f2\u53cd\u6620\uff0c\u6709\u8fd9\u4e9b\u5e7b\u8c61\uff0c\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002</p><p>\u7b80\u5355\u6765\u8bf4\uff0c\u8fd9\u91cc\u770b\u8d77\u6765\u5c31\u662f\u4e00\u7247\u8352\u51c9\u3001\u5bc2\u5be5\u3001\u9634\u68ee\u68ee\u7684\u5e9f\u571f\u3002\u5982\u679c\u5728\u5730\u9762\u4e0a\u591a\u5792\u51e0\u4e2a\u571f\u5305\uff0c\u63d2\u51e0\u5757\u77f3\u7891\uff0c\u518d\u7528\u9b54\u6cd5\u5f04\u4e9b\u51a4\u9b42\u54ed\u568e\u4e4b\u7c7b\u7684\u52a8\u9759\uff0c\u914d\u4e0a\u5e38\u5e74\u7684\u9634\u98ce\u9635\u9635\uff0c\u90a3\u5c31\u662f\u6807\u51c6\u7684\u4ea1\u7075\u4e4b\u5730\u4e86\u3002</p><p>\u8bdd\u8bf4\u56de\u6765\uff0c\u5982\u679c\u771f\u662f\u4ea1\u7075\u5730\uff0c\u5176\u5b9e\u53cd\u5012\u5b89\u5168\u4e9b\u3002\u4ea1\u7075\u8fd9\u79cd\u5b58\u5728\uff0c\u867d\u7136\u5145\u6ee1\u6028\u5ff5\uff0c\u51f6\u623e\u65e0\u6bd4\uff0c\u6a21\u6837\u53c8\u6050\u6016\uff0c\u4f46\u5374\u5927\u591a\u6570\u90fd\u6ca1\u4ec0\u4e48\u667a\u529b\uff0c\u751a\u81f3\u6ca1\u6709\u7406\u667a\u601d\u7ef4\uff0c\u5b8c\u5168\u662f\u88ab\u8d1f\u80fd\u91cf\u9a71\u4f7f\uff0c\u9760\u7740\u672c\u80fd\u884c\u52a8\u3002\u81f3\u4e8e\u4ed6\u4eec\u7684\u52a8\u4f5c\uff0c\u5b8c\u5168\u53ef\u4ee5\u7528\u201c\u7b28\u62d9\u201d\u3001\u201c\u7f13\u6162\u201d\u3001\u201c\u5f31\u667a\u201d\u4e4b\u7c7b\u7684\u8bcd\u6765\u5f62\u5bb9\u3002\u8eab\u4e3a\u9634\u9b42\u57ce\u7684\u51c6\u5deb\u5e08\uff0c\u5982\u679c\u8fd9\u79cd\u5e9f\u7269\u90fd\u8981\u5bb3\u6015\uff0c\u90a3\u5c31\u767d\u5728\u5b66\u6821\u91cc\u4fee\u70bc\u8fd9\u4e48\u591a\u5e74\u4e86\u3002</p><p>\u9664\u4e86\u6781\u5c11\u6570\u9ad8\u9636\u5b58\u5728\uff0c\u7edd\u5927\u90e8\u5206\u4ea1\u7075\uff0c\u5c31\u7b97\u6709\u518d\u5f3a\u608d\u7684\u529b\u91cf\uff0c\u4e5f\u4e0d\u662f\u771f\u6b63\u9700\u8981\u60e7\u6015\u7684\u5bf9\u624b\u3002\u5982\u679c\u5f53\u771f\u9047\u4e0a\u5deb\u5996\u3001\u5438\u8840\u9b3c\u8fd9\u79cd\u65e2\u6709\u5f3a\u5927\u529b\u91cf\u53c8\u6709\u6e05\u9192\u7406\u667a\u7684\u9ad8\u9636\u4ea1\u7075\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u60ca\u614c\u5931\u63aa\uff0c\u9547\u5b9a\u4e0b\u6765\u548c\u5bf9\u65b9\u8c08\u5224\u8bd5\u8bd5\u3002\u53ea\u8981\u6709\u667a\u6167\uff0c\u80fd\u4ea4\u6d41\uff0c\u5c31\u603b\u6709\u53ef\u5546\u91cf\u7684\u4f59\u5730\uff0c\u5f7c\u6b64\u53c8\u6ca1\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4f55\u5fc5\u975e\u8981\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\u3002</p><p>\u5982\u679c\u624b\u4e0a\u7b79\u7801\u592a\u5c11\uff0c\u81ea\u8eab\u529b\u91cf\u592a\u5f31\uff0c\u8fde\u8c08\u5224\u7684\u8d44\u683c\u90fd\u6ca1\u6709\uff0c\u53c8\u6ca1\u6cd5\u9003\u8dd1\uff0c\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0c\u4e56\u4e56\u7b49\u6b7b\u5427\u3002</p><p>\u5e7d\u5f71\u754c\u91cc\u7684\u654c\u4eba\uff0c\u6bd4\u4ea1\u7075\u53ef\u96be\u7f20\u591a\u4e86\u3002</p><p>\u6c89\u6c89\u9634\u5f71\u8ff7\u96fe\u4e4b\u4e2d\uff0c\u9690\u85cf\u7740\u6570\u4e0d\u6e05\u7684\u5947\u5f62\u602a\u7269\u3002\u5728\u9646\u5730\u4e0a\u6709\u516d\u8db3\u7684\u9a6c\uff0c\u4e09\u5934\u7684\u72d7\uff0c\u5728\u68ee\u6797\u91cc\u6709\u91d1\u521a\u4e00\u822c\u7684\u9ed1\u733f\uff0c\u957f\u56db\u53ea\u80f3\u818a\u7684\u7329\u7329\uff0c\u5728\u5730\u4e0b\u6709\u7259\u9f7f\u950b\u9510\u5982\u5200\u7684\u6210\u7fa4\u51f6\u66b4\u9f20\uff0c\u5728\u6c34\u4e2d\u6f5c\u4f0f\u7740\u5e9e\u5927\u7684\u4e4c\u8d3c\u548c\u6d77\u86c7\uff0c\u5728\u7a7a\u4e2d\u6709\u51c4\u5389\u5636\u53eb\u7684\u591c\u67ad\uff0c\u5229\u722a\u5e26\u5267\u6bd2\u7684\u5f71\u9e70\uff0c\u66f4\u522b\u8bf4\u9634\u5f71\u5de8\u9f99\u8fd9\u79cd\u4f20\u8bf4\u4e2d\u7684\u5b58\u5728\u3002</p><p>\u4f5c\u4e3a\u5e7d\u5f71\u754c\u7684\u571f\u8457\u5c45\u6c11\uff0c\u5b83\u4eec\u666e\u904d\u90fd\u80fd\u5728\u9634\u5f71\u8ff7\u96fe\u4e2d\u9690\u9041\u8eab\u5f62\uff0c\u4ee4\u4eba\u96be\u4ee5\u5bdf\u89c9\uff0c\u968f\u65f6\u51c6\u5907\u7740\u7a81\u7136\u5077\u88ad\u3002\u82e5\u8bf4\u5b83\u4eec\u6ca1\u6709\u667a\u529b\uff0c\u5b83\u4eec\u9634\u9669\u5f02\u5e38\uff0c\u72e1\u8bc8\u81f3\u6781\uff0c\u82e5\u8bf4\u5b83\u4eec\u6709\u667a\u529b\uff0c\u504f\u504f\u53c8\u4ece\u65e0\u548c\u5e73\u4ea4\u6d41\u7684\u4f59\u5730\u3002</p><p>\u800c\u4e14\u5b83\u4eec\u666e\u904d\u5730\u654c\u89c6\u9634\u9b42\u57ce\uff0c\u5927\u7ea6\u56e0\u4e3a\u662f\u5916\u6765\u79fb\u6c11\u7684\u7f18\u6545\u3002\u53ea\u8981\u89c1\u5230\u9634\u9b42\u57ce\u7684\u4eba\uff0c\u5f80\u5f80\u7fa4\u8d77\u800c\u653b\u4e4b\uff0c\u51f6\u608d\u81f3\u6781\u3002</p><p>\u9664\u4e86\u9634\u5f71\u8ff7\u96fe\u4e2d\u65e0\u5904\u4e0d\u5728\u7684\u51f6\u72e0\u602a\u7269\u5916\uff0c\u66f4\u4ee4\u4eba\u6050\u60e7\u7684\uff0c\u8fd8\u662f\u201c\u5e7d\u6697\u5883\u201d\u3002</p><p>\u6240\u8c13\u5e7d\u6697\u5883\uff0c\u662f\u8fd1\u4f3c\u4e8e\u4e00\u79cd\u201c\u9ed1\u6d1e\u201d\u6216\u8005\u8bf4\u201c\u7a7a\u95f4\u9677\u9631\u201d\u7684\u5b58\u5728\u3002\u9634\u5f71\u602a\u7269\u518d\u9669\u6076\uff0c\u603b\u4e5f\u8fd8\u53ef\u4ee5\u771f\u5200\u771f\u67aa\u5730\u5e94\u4ed8\uff0c\u8fd9\u5e7d\u6697\u5883\u5374\u662f\u65e0\u5f62\u65e0\u8d28\uff0c\u968f\u673a\u51fa\u73b0\u53c8\u7a81\u7136\u6d88\u5931\uff0c\u6beb\u65e0\u89c4\u5f8b\u53ef\u5faa\uff0c\u4efb\u4f55\u5b58\u5728\u4e00\u65e6\u8e0f\u5165\u201c\u5e7d\u6697\u5883\u201d\u7684\u8303\u56f4\u5185\uff0c\u987f\u65f6\u5c31\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u5f53\u771f\u662f\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u636e\u5b66\u6821\u7684\u6559\u6388\u8bf4\uff0c\u662f\u56e0\u4e3a\u5e7d\u5f71\u754c\u6e90\u51fa\u7269\u8d28\u754c\uff0c\u72ec\u7acb\u5f62\u6210\u7684\u65f6\u95f4\u8fd8\u4e0d\u591f\u957f\uff0c\u4f4d\u9762\u4f9d\u7136\u51fa\u4e8e\u4e0d\u7a33\u5b9a\u7684\u72b6\u6001\uff0c\u6240\u4ee5\u96be\u514d\u5c31\u5f04\u51fa\u8fd9\u4e9b\u7a7a\u95f4\u88c2\u9699\u6765\u3002</p><p>\u4e0d\u8fc7\u5e7d\u6697\u5883\u867d\u7136\u6050\u6016\uff0c\u5374\u51fa\u73b0\u5f97\u5e76\u4e0d\u9891\u7e41\uff0c\u8303\u56f4\u53c8\u5f88\u72ed\u5c0f\uff0c\u6301\u7eed\u65f6\u95f4\u4e5f\u77ed\uff0c\u5f80\u5f80\u5f88\u5feb\u5c31\u6d88\u5931\u3002\u5e78\u597d\u5982\u6b64\uff0c\u5426\u5219\u6574\u4e2a\u5e7d\u5f71\u754c\u7684\u751f\u7269\u90fd\u8981\u88ab\u541e\u8fdb\u53bb\u4e86\u3002</p><p>\u603b\u4e4b\uff0c\u8fd9\u662f\u4e2a\u975e\u5e38\u6050\u6016\u7684\u5730\u65b9\u3002</p><p>\u4f5c\u4e3a\u5e7d\u5f71\u754c\u4e2d\u552f\u4e00\u7684\u4eba\u7c7b\u57ce\u5e02\uff0c\u9762\u4e34\u7740\u65e0\u6570\u5f3a\u654c\u73af\u4f3a\uff0c\u9634\u9b42\u57ce\u80fd\u5728\u8fd9\u79cd\u9669\u6076\u4e4b\u5730\u7a33\u7a33\u5446\u4e0a\u4e00\u5343\u591a\u5e74\uff0c\u9664\u4e86\u838e\u5c14\u5973\u795e\u7684\u62a4\u4f51\u4e4b\u5916\uff0c\u51ed\u501f\u7684\u5c31\u662f\u5f3a\u5927\u7684\u5b9e\u529b\u3002</p><p>\u66fe\u7ecf\u7684\u8010\u745f\u745e\u5c14\u5e1d\u56fd\uff0c\u62e5\u6709\u7740\u8f89\u714c\u707f\u70c2\u7684\u9b54\u6cd5\u6587\u660e\uff0c\u521b\u9020\u4e86\u6570\u4e0d\u6e05\u7684\u5947\u8ff9\u3002\u743c\u6069\u542c\u5deb\u5e08\u5b66\u6821\u7684\u6559\u6388\u8c08\u8d77\uff0c\u8bf4\u968f\u7740\u5f53\u5e74\u5e1d\u56fd\u7684\u9668\u706d\uff0c\u9b54\u6cd5\u6587\u660e\u6bc1\u4e8e\u4e00\u65e6\uff0c\u79cd\u79cd\u7cbe\u5999\u795e\u5947\u7684\u5965\u672f\u51e0\u4e4e\u4e27\u5931\u6b86\u5c3d\uff0c\u6089\u6570\u5931\u4f20\uff0c\u4ee5\u81f3\u4e8e\u4e00\u5343\u4e03\u767e\u4f59\u5e74\u540e\u7684\u4eca\u5929\uff0c\u7269\u8d28\u754c\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u5c45\u7136\u8fd8\u662f\u8fdc\u8fdc\u900a\u8272\u4e8e\u8010\u745f\u65f6\u671f\u7684\u6c34\u51c6\u3002</p><p>\u4f46\u9634\u9b42\u57ce\u4e0d\u540c\uff0c\u4f5c\u4e3a\u552f\u4e00\u5728\u5f53\u5e74\u7684\u6d69\u52ab\u4e2d\u5e78\u5b58\u4e0b\u6765\u7684\u6d6e\u7a7a\u57ce\uff0c\u5b83\u662f\u8010\u745f\u9b54\u6cd5\u6587\u660e\u7684\u6700\u540e\u4f20\u627f\u8005\u3002\u79cd\u79cd\u5728\u7269\u8d28\u754c\u65e9\u5df2\u7edd\u8ff9\u7684\u7cbe\u5999\u9b54\u6cd5\u6280\u827a\uff0c\u5728\u9634\u9b42\u57ce\u4e2d\u4f9d\u7136\u4ee3\u4ee3\u6d41\u4f20\uff0c\u53d1\u626c\u5149\u5927\u3002</p><p>\u9634\u9b42\u57ce\u5efa\u5728\u4e00\u5ea7\u5012\u8f6c\u8fc7\u6765\u7684\u5c71\u5cf0\u4e0a\uff0c\u51ed\u501f\u7740\u67d0\u79cd\u743c\u6069\u6682\u65f6\u8fd8\u6ca1\u8d44\u683c\u4e86\u89e3\u7684\u5f3a\u5927\u9b54\u6cd5\uff0c\u6574\u5ea7\u5c71\u5cf0\u90fd\u957f\u5e74\u6f02\u6d6e\u5728\u7a7a\u4e2d\uff0c\u79fb\u6765\u79fb\u53bb\u3002\u90a3\u4e9b\u5730\u9762\u4e0a\u7684\u602a\u7269\uff0c\u81ea\u7136\u662f\u6ca1\u6cd5\u5948\u4f55\u9634\u9b42\u57ce\uff0c\u5c31\u7b97\u662f\u80fd\u98de\u884c\u7684\uff0c\u4e5f\u4f11\u60f3\u9760\u8fd1\u57ce\u5e02\u3002\u57ce\u5899\u4e0a\u5e38\u5e74\u9a7b\u5b88\u7684\u519b\u961f\uff0c\u4e00\u6392\u6392\u9b54\u6cd5\u70ae\u548c\u5de8\u5f29\uff0c\u96be\u9053\u90fd\u662f\u6446\u8bbe\u4e0d\u6210\uff0c\u53ea\u8981\u6562\u8fdb\u5165\u5341\u82f1\u91cc\u7684\u8303\u56f4\uff0c\u7acb\u523b\u5c31\u662f\u94fa\u5929\u76d6\u5730\u7684\u9b54\u6cd5\u70ae\u8f70\u51fb\uff1b\u5982\u679c\u80fd\u95ef\u5165\u4e94\u82f1\u91cc\u8303\u56f4\uff0c\u66f4\u589e\u52a0\u7bad\u96e8\u6512\u5c04\uff0c\u4fdd\u7ba1\u53d8\u6210\u523a\u732c\u3002</p><p>\u9664\u4e86\u4e25\u5bc6\u9632\u5b88\uff0c\u540c\u65f6\u8fd8\u8981\u6709\u8fdb\u653b\u3002\u4e3a\u4e86\u4fdd\u8bc1\u548c\u5f3a\u5316\u519b\u961f\u7684\u6218\u6597\u529b\uff0c\u4e3a\u4e86\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\u548c\u795e\u6bbf\u7684\u5b9e\u4e60\u7267\u5e08\u80fd\u589e\u52a0\u5b9e\u6218\u7ecf\u9a8c\uff0c\u4e3a\u4e86\u63a0\u593a\u66f4\u591a\u7684\u5974\u96b6\u4fdd\u8bc1\u57ce\u5e02\u8fd0\u8f6c\u53d1\u5c55\uff0c\u4e3a\u4e86\u2026\u2026\u7231\u3001\u548c\u5e73\u548c\u6b63\u4e49\u2026\u2026</p><p>\u603b\u4e4b\uff0c\u9634\u9b42\u57ce\u9694\u4e09\u5c94\u4e94\uff0c\u5c31\u8981\u6d3e\u9063\u5c0f\u5206\u961f\uff0c\u5bf9\u57ce\u5e02\u9644\u8fd1\u7684\u533a\u57df\u8fdb\u884c\u626b\u8361\uff0c\u7f8e\u5176\u540d\u66f0\u201c\u519b\u4e8b\u6f14\u4e60\u201d\u3002\u53e6\u5916\u8fd8\u6709\u5927\u89c4\u6a21\u7684\u8fdc\u5f81\u4f5c\u6218\uff0c\u5f80\u5f80\u662f\u5728\u63a2\u77e5\u67d0\u5904\u602a\u7269\u5982\u4e91\u805a\u96c6\u540e\uff0c\u503e\u5168\u57ce\u5175\u529b\u731b\u7136\u7a81\u88ad\uff0c\u4e00\u822c\u4e00\u5e74\u4f1a\u6765\u4e0a\u4e00\u6b21\u3002</p><p>\u743c\u6069\u4eca\u5929\u53c2\u52a0\u7684\uff0c\u5c31\u662f\u8fd9\u79cd\u5c0f\u5206\u961f\u7684\u519b\u4e8b\u6f14\u4e60\u4e86\u3002</p></article>"}}}]);
