(window.webpackJsonp=window.webpackJsonp||[]).push([[124],{147:function(p){p.exports={chapterTitle:null,title:"\u7b2c1106\u7ae0 \u54ea\u6709\u4e0d\u5403\u7f8a\u7684\u72fc",content:"<article><h2>\u7b2c1106\u7ae0 \u54ea\u6709\u4e0d\u5403\u7f8a\u7684\u72fc</h2><p>\u3000\u3000\u201c\u5b8b\u5c0f\u59d0\u771f\u6709\u5fc3\u8d54\u793c\u9053\u6b49\uff0c\u660e\u5929\u5355\u72ec\u8fc7\u6765\u8d54\u793c\u9053\u6b49\u5c31\u53ef\u4ee5\u4e86\uff0c\u201d\u5f20\u606a\u5c06\u5199\u7740\u4ed6\u5728\u5317\u4eac\u4e34\u65f6\u4f4f\u5740\u7684\u7eb8\u6761\u4e22\u5728\u684c\u4e0a\uff0c\u4e0d\u7ba1\u522b\u4eba\u5fc3\u91cc\u4f1a\u600e\u4e48\u60f3\uff0c\u4ed6\u62ac\u624b\u770b\u4e86\u770b\u8868\uff0c\u7ad9\u8d77\u6765\u4e0e\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u8bf4\u9053\uff0c\u201c\u65f6\u95f4\u4e0d\u65e9\u4e86\uff0c\u4eca\u5929\u662f\u4e0d\u662f\u5c31\u8fd9\u6837\uff01\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u3001\u6768\u84c9\u5e73\u7b49\u4eba\u90fd\u7ad9\u8d77\u6765\u76f8\u9001\u5f20\u606a\u3001\u9648\u4fe1\u751f\u4ed6\u4eec\uff0c\u5c06\u4e00\u8138\u7f9e\u6124\u3001\u60d8\u7136\u65e0\u63aa\u7684\u5b8b\u5a49\u4f69\u4ee5\u53ca\u770b\u4e0d\u51fa\u4ec0\u4e48\u8868\u60c5\u7684\u5b8b\u7956\u8a89\u4e24\u4eba\u4e22\u5728\u5305\u53a2\u91cc\u3002\u79bb\u5f00\u65f6\uff0c\u6768\u84c9\u5e73\u8fd8\u5fcd\u4e0d\u4f4f\u56de\u5934\u7684\u770b\u5b8b\u5a49\u4f69\u4e00\u773c\uff0c\u5973\u4eba\u8ddf\u5973\u4eba\u771f\u662f\u4e0d\u80fd\u653e\u5728\u4e00\u8d77\u6bd4\u8f83\uff0c\u603b\u90e8\u529e\u516c\u5ba4\u4e4b\u82b1\u6210\u6620\u971e\u7ad9\u5728\u5b8b\u5a49\u4f69\u9762\u524d\u5c31\u662f\u9eef\u7136\u5931\u8272\u4e86\uff0c\u771f\u503c\u5f97\u8ba9\u5979\u5355\u72ec\u6765\u8d54\u793c\u9053\u6b49\uff0c\u53ef\u60dc\u5df2\u7ecf\u8ba9\u5f20\u606a\u6377\u8db3\u5148\u767b\u4e86\uff0c\u8d70\u51fa\u5305\u53a2\u65f6\uff0c\u8fd8\u522b\u6709\u610f\u5473\u7684\u6309\u4e86\u6309\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u9f9a\u5fd7\u9e4f\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u7b49\u4f1a\u513f\uff0c\u6211\u4e0e\u8054\u4fe1\u7684\u8096\u603b\u4e5f\u5148\u8d70\uff0c\u5269\u4e0b\u7684\u4e8b\u60c5\uff0c\u4f60\u4e0e\u5c0f\u6210\u7559\u4e0b\u6765\u5904\u7406\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u9f9a\u5fd7\u9e4f\u5fc3\u6709\u9886\u4f1a\u7684\u70b9\u5934\u7b11\u4e86\u7b11\uff0c\u6210\u6620\u971e\u5fc3\u91cc\u5374\u5728\u60f3\u5269\u4e0b\u7684\u4e8b\u60c5\u662f\u6307\u4ec0\u4e48\uff0c\u8981\u662f\u8bf4\u7ed3\u8d26\u7684\u8bdd\uff0c\u4eca\u591c\u7684\u6d88\u8d39\u672c\u5c31\u5e94\u8be5\u662f\u5206\u516c\u53f8\u5356\u5355\uff0c\u6768\u84c9\u5e73\u6839\u672c\u6ca1\u6709\u5fc5\u8981\u989d\u5916\u63d0\u9192\u4e00\u4e0b\u3002\u6210\u6620\u971e\u5fc3\u91cc\u7422\u78e8\u7740\u4e8b\u60c5\uff0c\u966a\u540c\u8d70\u51fa\u4f1a\u6240\uff0c\u770b\u7740\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u4e0e\u5f20\u606a\u3001\u9648\u4fe1\u751f\u7b49\u4eba\u5728\u5eca\u6a90\u524d\u8fb9\u544a\u522b\u8fb9\u7b49\u8f66\u4ece\u505c\u8f66\u573a\u5f00\u8fc7\u6765\uff0c\u770b\u7740\u5f20\u606a\u8eab\u6750\u633a\u62d4\u3001\u8138\u5e9e\u4fca\u7f8e\uff0c\u8c08\u7b11\u98ce\u751f\u7684\u5728\u90a3\u91cc\u4e0e\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u5bd2\u6684\uff0c\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u53ea\u6709\u8d44\u683c\u5728\u4e00\u65c1\u966a\u7b11\u8138\uff0c\u5fc3\u60f3\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u53c8\u8fd9\u4e48\u7684\u5e74\u8f7b\uff0c\u5e94\u8be5\u4f1a\u6709\u5f88\u591a\u7f8e\u5973\u4e89\u5148\u6050\u540e\u7684\u60f3\u4e0a\u4ed6\u7684\u5e8a\u5427\uff0c\u53ea\u662f\u4ed6\u5f3a\u8feb\u5b8b\u5a49\u4f69\u5355\u72ec\u767b\u95e8\u201c\u8d54\u793c\u9053\u6b49\u201d\u7684\u624b\u6bb5\u8fc7\u4e8e\u5f3a\u52bf\uff0c\u7b80\u76f4\u6bd4\u201c\u9738\u738b\u5f3a\u4e0a\u5f13\u201d\u597d\u4e0d\u4e86\u591a\u5c11\uff0c\u8fd9\u96be\u602a\u624d\u662f\u6709\u94b1\u4eba\u7684\u5634\u8138\uff1f</p><p>\u3000\u3000\u5012\u662f\u5f20\u606a\u77ee\u8eab\u94bb\u8fdb\u8f66\u4e4b\u524d\u4e00\u773c\u90fd\u6ca1\u6709\u770b\u8fc7\u6765\uff0c\u610f\u5916\u7684\uff0c\u6210\u6620\u971e\u5fc3\u91cc\u6709\u4e9b\u5fae\u7684\u3001\u96be\u4ee5\u81ea\u6291\u7684\u5931\u843d\u611f\uff0c\u5fc3\u60f3\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u4ed6\u5927\u6982\u90fd\u4e0d\u77e5\u9053\u5427\uff1f\u6768\u84c9\u5e73\u4e0e\u8096\u745e\u6c11\u76ee\u9001\u5f20\u606a\u7b49\u4eba\u7684\u8f66\u9a76\u4e0a\u4e3b\u5e72\u9053\uff0c\u624d\u76f8\u7ee7\u5750\u8f66\u79bb\u5f00\uff0c\u6210\u6620\u971e\u4e0e\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u9f9a\u5fd7\u9e4f\u518d\u8d70\u8fdb\u4f1a\u6240\u7684\u5305\u53a2\uff0c\u661f\u8f89\u5a31\u4e50\u603b\u7406\u7ecf\u5b8b\u7956\u8a89\u4e0e\u5927\u660e\u661f\u5b8b\u5a49\u4f69\u8fd8\u5728\u91cc\u9762\uff0c\u521a\u624d\u4e5f\u770b\u89c1\u8fd8\u6709\u4e00\u4e9b\u661f\u8f89\u5a31\u4e50\u7684\u804c\u5458\u5728\u5927\u5385\u91cc\u4ea4\u5934\u63a5\u8033\uff0c\u4e5f\u4e0d\u77e5\u9053\u79bb\u5f00\u8fd9\u4f1a\u513f\u4ed6\u4eec\u8bf4\u4e86\u4ec0\u4e48\uff0c\u5b8b\u5a49\u4f69\u7684\u773c\u5708\u7ea2\u7ea2\u7684\uff0c\u770b\u4e0a\u53bb\u695a\u695a\u53ef\u601c\uff0c\u5973\u4eba\u770b\u4e86\u90fd\u5fc3\u601c\uff0c\u666e\u901a\u767e\u59d3\u90fd\u770b\u5f97\u89c1\u5b8b\u5a49\u4f69\u5149\u5f69\u7167\u4eba\u7684\u4e00\u9762\uff0c\u8c01\u53c8\u77e5\u9053\u8fd9\u80cc\u5730\u91cc\u7684\u80ae\u810f\u4e0e\u5fc3\u9178\u3002</p><p>\u3000\u3000\u201c\u9f9a\u603b\uff0c\u4f60\u8bf4\u8fd9\u53eb\u4ec0\u4e48\u4e8b\uff1f\u201d\u5b8b\u7956\u8a89\u770b\u7740\u9f9a\u5fd7\u9e4f\u8d70\u8fdb\u6765\uff0c\u62b1\u5c48\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u4e0d\u8981\u770b\u9f9a\u5fd7\u9e4f\u53ea\u662f\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\uff0c\u5728\u5317\u4eac\u4e5f\u7b97\u662f\u4e00\u53f7\u4eba\u7269\u3002</p><p>\u3000\u3000\u8054\u901a\u8fd9\u51e0\u5e74\u5728\u5317\u4eac\u7684\u6295\u8d44\u90fd\u975e\u5e38\u7684\u5927\uff0c\u5317\u4eac\u6bd5\u7adf\u4ee3\u8868\u7740\u95e8\u9762\uff0c\u8054\u901a\u5c31\u7b97\u8d44\u6e90\u518d\u5dee\uff0c\u4e5f\u4e0d\u60f3\u5317\u4eac\u5730\u533a\u90fd\u4e0d\u80fd\u4e89\u53d6\u5230\u4e00\u70b9\u7684\u4e3b\u52a8\uff0c\u5404\u65b9\u9762\u7684\u6295\u5165\u8ddf\u79fb\u52a8\u901a\u4fe1\u5c40\u4e0b\u5c5e\u7684\u5317\u4eac\u5206\u516c\u53f8\u76f8\u6bd4\u8f83\uff0c\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u8054\u901a\u5317\u5206\u6bd4\u5176\u4ed6\u7701\u5e02\u5206\u516c\u53f8\u8981\u5f3a\u592a\u591a\u3002</p><p>\u3000\u3000\u8054\u901a\u5317\u4eac\u516c\u516c\u53f8\u7684\u603b\u7ecf\u7406\u867d\u7136\u53ea\u662f\u5c40\u7ea7\uff0c\u5728\u4e0d\u8981\u89c9\u5f97\u5b98\u5927\u7684\u5317\u4eac\u57ce\uff0c\u9f9a\u5fd7\u9e4f\u8fdc\u6bd4\u90a3\u4e9b\u6b63\u53f8\u7ea7\u5b98\u5458\u66f4\u663e\u5f97\u6709\u80fd\u8010\uff0c\u4e5f\u6d3b\u5f97\u66f4\u6ecb\u6da6\u3002</p><p>\u3000\u3000\u552f\u4e00\u4ee4\u9f9a\u5fd7\u9e4f\u4e0d\u723d\u7684\u4e8b\u60c5\u5c31\u662f\u5206\u516c\u53f8\u8ddf\u96c6\u56e2\u603b\u90e8\u5728\u540c\u4e00\u5ea7\u57ce\u5e02\u91cc\uff0c\u65f6\u4e0d\u65f6\u5728\u96c6\u56e2\u603b\u90e8\u7684\u9886\u5bfc\u9762\u524d\u5c31\u8981\u88c5\u5b59\u5b50\uff0c\u4e0d\u60f3\u5176\u4ed6\u7701\u5e02\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u5929\u9ad8\u7687\u5e1d\u8fdc\u3002</p><p>\u3000\u3000\u9f9a\u5fd7\u9e4f\u5750\u4e0b\u6765\uff0c\u4e8c\u811a\u9ad8\u8df7\uff0c\u88c5\u4e86\u4e00\u591c\u7684\u5b59\u5b50\uff0c\u8fd9\u4e00\u5750\u53c8\u627e\u56de\u9ad8\u9ad8\u5728\u4e0a\u7684\u611f\u89c9\uff0c\u4ed6\u5509\u58f0\u53f9\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u6bd5\u7adf\u662f\u4f60\u4eec\u6709\u9519\u5728\u5148\uff0c\u767b\u95e8\u9053\u6b49\u4f3c\u4e4e\u4e5f\u4e0d\u8fc7\u4efd\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b8b\u7956\u8a89\u5fae\u5fae\u4e00\u6014\uff0c\u6ca1\u60f3\u5230\u9f9a\u5fd7\u9e4f\u5b8c\u5168\u4e0d\u5e2e\u4ed6\u8bf4\u8bdd\uff0c\u767b\u95e8\u9053\u6b49\uff0c\u54ea\u6015\u9009\u4eac\u57ce\u6700\u8d35\u7684\u5730\u65b9\u6446\u9152\u5e2d\u90fd\u6210\uff0c\u8ba9\u5b8b\u5a49\u4f69\u5355\u72ec\u767b\u95e8\u9053\u6b49\uff0c\u9b3c\u90fd\u77e5\u9053\u8981\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u521a\u624d\u7ed9\u6210\u5f3a\u6253\u7535\u8bdd\u60f3\u8bd5\u8bd5\u4ed6\u7684\u53e3\u98ce\uff0c\u6ca1\u60f3\u5230\u4e24\u53e5\u8bdd\u6ca1\u8bf4\u8bdd\uff0c\u5bf9\u65b9\u5c31\u5c06\u7535\u8bdd\u6454\u4e86\uff0c\u53ef\u89c1\u4ed6\u5fc3\u91cc\u867d\u7136\u607c\u706b\u5374\u6ca1\u6709\u80c6\u5b50\u518d\u63d2\u624b\u8fd9\u4ef6\u4e8b\u3002\u5b8b\u7956\u8a89\u8bd5\u63a2\u6027\u7684\u95ee\u4e86\u9f9a\u5fd7\u9e4f\u4e00\u53e5\uff1a\u201c\u9f9a\u603b\uff0c\u90a3\u4f4d\u5f20\u5148\u751f\u5230\u5e95\u662f\u4ec0\u4e48\u6765\u5934\uff1f\u201d</p><p>\u3000\u3000\u5b8b\u7956\u8a89\u8fd9\u65f6\u5019\u6709\u4e9b\u6349\u6478\u4e0d\u5b9a\u4e86\uff0c\u5728\u4ed6\u4eec\u5305\u53a2\u91cc\uff0c\u770b\u8fd9\u4e2a\u81ea\u79f0\u5f20\u606a\u7684\u9752\u5e74\u6c14\u52bf\u5f88\u76db\uff0c\u4ee4\u4eba\u5549\u82e5\u5bd2\u8749\uff0c\u8fd8\u5c06\u6210\u5f3a\u7f9e\u8fb1\u4e86\u4e00\u987f\uff0c\u4ed6\u62c9\u7740\u5b8b\u5a49\u4f69\u8dd1\u8fc7\u6765\u9053\u6b49\uff0c\u89c1\u4ed6\u4eec\u5728\u5305\u53a2\u91cc\u7adf\u7136\u559d\u7684\u662f\u4f4e\u6863\u7684\u56fd\u4ea7\u9152\uff0c\u5c31\u6709\u4e9b\u4e0d\u786e\u5b9a\u4e86\u3002</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u9f9a\u5fd7\u9e4f\u6123\u4e86\u6123\uff0c\u201c\u4f60\u4eec\u4e0d\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u53d1\u751f\u4e86\u4e9b\u8bef\u4f1a\uff0c\u4ed6\u5c06\u6210\u664b\u6770\u7684\u513f\u5b50\u6210\u5f3a\u6559\u8bad\u8d70\uff0c\u7559\u4e0b\u4e00\u4e2a\u4eba\u5c31\u8bf4\u8981\u6536\u8d2d\u661f\u8f89\uff0c\u90a3\u4eba\u53ea\u8ba9\u6211\u62a5\u4ef7\uff0c\u5374\u4e0d\u8bf4\u660e\u4ed6\u4eec\u7684\u8eab\u4efd\u2026\u2026\u201d\u9648\u7956\u8a89\u82e6\u7740\u8138\u8bc9\u82e6\u9053\uff0c\u4ed6\u8fde\u5f97\u7f6a\u7684\u54ea\u91cc\u4eba\u9a6c\u90fd\u4e0d\u6e05\u695a\uff0c\u54ea\u91cc\u7ba1\u5f00\u53e3\u62a5\u4ef7\uff1f</p><p>\u3000\u3000\u201c\u90a3\u4f60\u5c31\u8ddf\u9648\u4fe1\u8fbe\u4e00\u8d77\u5c06\u661f\u8f89\u5356\u7ed9\u4eba\u5bb6\u5f97\u4e86\uff0c\u4f60\u4eec\u4e0d\u662f\u6574\u5929\u8bc9\u82e6\u8bf4\u5e72\u8fd9\u884c\u53c8\u9ed1\u53c8\u7d2f\uff0c\u6ca1\u6709\u610f\u601d\uff0c\u4f60\u62c5\u5fc3\u4eba\u5bb6\u51fa\u4e0d\u8d77\u4ef7\u8fd8\u662f\u600e\u4e48\u8bf4\uff1f\u201d\u9f9a\u5fd7\u9e4f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u55e8\uff0c\u9f9a\u603b\u4e0d\u8981\u4e3a\u96be\u6211\u4e86\uff0c\u4eca\u5929\u7684\u5f00\u9500\u7b97\u4e86\uff0c\u53ea\u6c42\u9f9a\u603b\u7ed9\u6211\u6307\u70b9\u660e\u8def\u2026\u2026\u201d\u5b8b\u7956\u8a89\u6d8e\u8138\u54c0\u6c42\u9053\u3002\u4ed6\u53ea\u77e5\u9053\u5bf9\u65b9\u6bd4\u53d1\u5c55\u8ba1\u5212\u59d4\u526f\u4e3b\u4efb\u6210\u664b\u6770\u66f4\u5f3a\u52bf\uff0c\u8fd8\u62e5\u6709\u968f\u4fbf\u5c31\u6536\u8d2d\u661f\u8f89\u5a31\u4e50\u7684\u5e9e\u5927\u8d22\u5bcc\u3002\u661f\u8f89\u5a31\u4e50\u867d\u7136\u8fdc\u4e0d\u80fd\u8ddf\u4e16\u754c\u7684\u5927\u724c\u7ecf\u7eaa\u516c\u53f8\u76f8\u63d0\u5e76\u8bba\uff0c\u4e5f\u4e0d\u80fd\u8ddf\u9999\u6e2f\u7684\u82f1\u738b\u7b49\u5927\u7ecf\u7eaa\u516c\u53f8\u76f8\u6bd4\uff0c\u4f46\u5728\u56fd\u5185\u4e5f\u8981\u7b97\u4e00\u6d41\u7684\u6f14\u827a\u7ecf\u7eaa\u516c\u53f8\uff0c\u8bf4\u6536\u8d2d\u5c31\u6536\u8d2d\uff0c\u597d\u6b79\u4e5f\u8981\u4e00\u4e24\u4ebf\u7684\u73b0\u91d1\u624d\u884c\u3002\u8fd9\u5e74\u5934\uff0c\u4eac\u57ce\u7684\u592a\u5b50\u7237\u4eec\u4e2a\u4e2a\u90fd\u725b\u6bd4\u53c9\u53c9\uff0c\u4f46\u662f\u6709\u591a\u5c11\u4eba\u6709\u80fd\u529b\u968f\u4fbf\u4e22\u4e00\u4e24\u4ebf\u51fa\u6765\u6597\u6c14\uff1f\u6ca1\u6709\u6478\u6e05\u695a\u5e95\u7ec6\uff0c\u4ed6\u771f\u4e0d\u6562\u968f\u4fbf\u5f00\u4ef7\u3002\u62ff\u94b1\u591a\u4e86\uff0c\u5f97\u7f6a\u4eba\uff0c\u62ff\u94b1\u5c11\u4e86\uff0c\u5f97\u7f6a\u81ea\u5df1\u554a\u3002</p><p>\u3000\u3000\u201c\u8fd8\u662f\u767b\u95e8\u9053\u6b49\u7684\u597d\uff0c\u201d\u9f9a\u5fd7\u9e4f\u8bf4\u9053\uff0c\u4fa7\u8fc7\u8138\u770b\u4e86\u770b\u5b8b\u5a49\u4f69\uff0c\u5fc3\u91cc\u6697\u53f9\u771f\u662f\u5929\u59ff\u56fd\u8272\uff0c\u4ed6\u53ef\u6ca1\u6709\u52c7\u6c14\u53bb\u8ddf\u5f20\u606a\u4e89\u5934\u7b79\uff0c\u5fc3\u60f3\u53ea\u8981\u5979\u4e0b\u4e86\u6c34\uff0c\u4ee5\u4e3a\u4e5f\u5c31\u6709\u4ed6\u7684\u673a\u4f1a\uff0c\u8fd9\u4e48\u60f3\u7740\uff0c\u8fd9\u70b9\u5931\u843d\u7684\u60c5\u7eea\u5728\u4ed6\u5fc3\u91cc\u4e5f\u662f\u4e00\u95ea\u5c31\u8fc7\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u767b\u95e8\u9053\u6b49\uff0c\u5bf9\u5b8b\u603b\uff0c\u5bf9\u5b8b\u5c0f\u59d0\u5c06\u6765\u7684\u53d1\u5c55\u90fd\u662f\u6709\u5229\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u5b8b\u5a49\u4f69\u65e0\u6240\u8c13\uff0c\u5b8b\u7956\u8a89\u66f4\u65e0\u6240\u8c13\uff0c\u51fa\u6765\u6df7\uff0c\u5c31\u4e0d\u56fe\u5356\u4e2a\u597d\u4ef7\u94b1\uff1f\u8fd9\u4e2a\u81ea\u79f0\u5f20\u606a\u7684\u9752\u5e74\uff0c\u660e\u663e\u6bd4\u6210\u5f3a\u8981\u6709\u5206\u91cf\uff0c\u53ea\u662f\u771f\u8981\u5c06\u5b8b\u5a49\u4f69\u9001\u4e0a\u95e8\u53bb\uff0c\u6210\u5f3a\u90a3\u8fb9\u5c31\u8ba8\u7f6a\u6b7b\u4e86\uff0c\u518d\u8bf4\u8fd9\u4e2a\u81ea\u79f0\u5f20\u606a\u7684\u9752\u5e74\u5403\u76f8\u6709\u4e9b\u96be\u770b\uff0c\u5b8b\u7956\u8a89\u4e5f\u89c9\u5f97\u6709\u4e9b\u843d\u4e0d\u4e0b\u9762\u5b50\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u603b\u4e0d\u80fd\u5c31\u8fd9\u4e48\u767b\u95e8\u9053\u6b49\u5427\uff1f\u201d\u5b8b\u7956\u8a89\u8146\u8138\u7b11\u9053\uff0c\u89c1\u9f9a\u5fd7\u9e4f\u8bf4\u5f97\u7b03\u7b03\u7684\uff0c\u4ed6\u7684\u59ff\u6001\u53ea\u80fd\u66f4\u8f6f\u4e00\u4e9b\uff0c\u81f3\u5c11\u77e5\u9053\u767b\u95e8\u9053\u6b49\u5230\u5e95\u5bf9\u4ee5\u540e\u7684\u53d1\u5c55\u600e\u4e48\u4e2a\u6709\u5229\u6cd5\u3002</p><p>\u3000\u3000\u201c\u5b8b\u5c0f\u59d0\u660e\u5929\u767b\u95e8\u9053\u6b49\uff0c\u81ea\u7136\u4f1a\u6709\u4ea4\u6d41\u3001\u4f1a\u6709\u6c9f\u901a\uff0c\u6211\u5c31\u4e0d\u5728\u8fd9\u91cc\u8d8a\u4fce\u4ee3\u5e96\u4e86\uff0c\u201d\u9f9a\u5fd7\u9e4f\u6478\u7740\u5fae\u79c3\u7684\u5934\u9876\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u5b8b\u603b\u5750\u7684\u4f4d\u5b50\u662f\u8054\u901a\u603b\u90e8\u7684\u6768\u84c9\u5e73\u526f\u603b\uff0c\u6768\u526f\u603b\u65c1\u8fb9\u662f\u8054\u4fe1\u7684\u603b\u88c1\u8096\u745e\u6c11\uff0c\u5b8b\u603b\u8981\u662f\u89c9\u5f97\u9053\u4e0d\u9053\u6b49\uff0c\u5c31\u8ba9\u6211\u4eec\u4e3a\u96be\u4e86\uff0c\u6211\u53ef\u662f\u60f3\u4e0e\u661f\u8f89\u7684\u5408\u4f5c\u80fd\u4e00\u76f4\u6109\u5feb\u7684\u8fdb\u884c\u4e0b\u53bb\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u7956\u8a89\u542c\u4e86\u5fc3\u4e00\u60ca\uff0c\u9f9a\u5fd7\u9e4f\u8fd9\u662f\u56fe\u672a\u7a77\u5315\u9996\u73b0\uff0c\u5b8b\u5a49\u4f69\u4e0e\u8054\u901a\u5317\u5206\u7684\u4ee3\u8a00\u5408\u7ea6\u8981\u662f\u8fd9\u6837\u5c31\u89e3\u9664\u4e86\uff0c\u4ed6\u5fc3\u91cc\u4e5f\u6e05\u695a\uff0c\u8054\u901a\u5317\u5206\u4e0d\u4ec5\u4e0d\u4f1a\u652f\u4ed8\u8fdd\u7ea6\u8d54\u507f\uff0c\u8fd8\u4f1a\u641e\u5f97\u5c31\u50cf\u5b8b\u5a49\u4f69\u4e0e\u661f\u8f89\u8fdd\u7ea6\u90a3\u6837\uff0c\u8981\u5b8b\u5a49\u4f69\u4e0e\u661f\u8f89\u8d54\u4e00\u7b14\u94b1\u51fa\u6765\u3002</p><p>\u3000\u3000\u6210\u6620\u971e\u770b\u7740\u5b8b\u5a49\u4f69\u773c\u91cc\u54c0\u4f24\u7684\u795e\u8272\uff0c\u770b\u4e86\u591a\u5c11\u6709\u4e9b\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u53c8\u89c9\u5f97\u5979\u4e0e\u9f9a\u5357\u9e4f\u5750\u5728\u8fd9\u91cc\u7b80\u76f4\u5c31\u662f\u903c\u826f\u4e3a\u5a3c\u517c\u62c9\u76ae\u6761\uff0c\u4e00\u65f6\u5fcd\u4e0d\u4f4f\u5f00\u53e3\u5b89\u6170\u5b8b\u5a49\u4f69\u9053\uff1a\u201c\u4e0e\u5f20\u5148\u751f\u63a5\u89e6\u8fc7\u7684\u4eba\uff0c\u90fd\u77e5\u9053\u5f20\u5148\u751f\u662f\u76f8\u5f53\u4e0d\u9519\u7684\u4eba\u2026\u2026\u201d\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u5c31\u89c9\u5f97\u540e\u6094\uff0c\u5979\u771f\u662f\u6ca1\u6709\u8d44\u683c\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u518d\u8bf4\u5979\u4e0e\u5f20\u606a\u63a5\u89e6\u4e5f\u5c31\u4ec5\u9650\u4e8e\u4eca\u665a\uff0c\u54ea\u91cc\u80fd\u77e5\u9053\u4e00\u4e2a\u4eba\u662f\u4e0d\u9519\u8fd8\u662f\u5f88\u574f\uff1f</p><p>\u3000\u3000\u5b8b\u7956\u8a89\u4e0e\u9f9a\u5fd7\u9e4f\u8c08\u8fc7\uff0c\u4e5f\u77e5\u9053\u8981\u600e\u4e48\u505a\u624d\u5408\u9002\uff0c\u5c31\u7b97\u8fd8\u6709\u4e9b\u4eba\u8109\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u5fc5\u8981\u5927\u52a8\u5e72\u6208\uff0c\u7ad9\u8d77\u6765\u4e0e\u9f9a\u5fd7\u9e4f\u544a\u522b\uff0c\u51fa\u53bb\u65f6\u987a\u4fbf\u5c06\u8fd9\u4e2a\u5305\u53a2\u91cc\u7684\u8d26\u5355\u7ed9\u7ed3\u4e86\uff0c\u5c06\u53d1\u7968\u7559\u7ed9\u9f9a\u5fd7\u9e4f\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u5fc3\u91cc\u771f\u662f\u51c4\u51c9\uff0c\u6f14\u827a\u751f\u6daf\u770b\u4e0a\u53bb\u661f\u5149\u5766\u8361\uff0c\u80cc\u540e\u5374\u662f\u51c4\u82e6\u4e0e\u5fc3\u9178\u3002\u867d\u7136\u5bf9\u6210\u5f3a\u5fc3\u91cc\u4e0d\u559c\u6b22\uff0c\u4f46\u662f\u516c\u53f8\u4e00\u518d\u5f3a\u8c03\u8fd9\u662f\u5de5\u4f5c\u4e0a\u7684\u5e94\u916c\uff0c\u5c31\u7b97\u6210\u5f3a\u6709\u65f6\u5019\u8a00\u884c\u7c97\u964b\u4e00\u4e9b\uff0c\u81f3\u5c11\u5f85\u5979\u8fd8\u80fd\u7a0d\u6709\u8010\u5fc3\uff0c\u7ef4\u6301\u8868\u9762\u4e0a\u7684\u8ba8\u597d\uff0c\u54ea\u91cc\u60f3\u5230\u8fd9\u4e2a\u53eb\u5f20\u606a\u7684\u6df7\u86cb\u6068\u4e0d\u5f97\u5c31\u5728\u5305\u53a2\u91cc\u6252\u5979\u7684\u8863\u670d\uff01\u770b\u7740\u516c\u53f8\u8001\u603b\u5b8b\u7956\u8a89\u8138\u4e0a\u7684\u795e\u8272\uff0c\u5c31\u77e5\u9053\u4ed6\u5df2\u7ecf\u9009\u62e9\u4e86\u9000\u7f29\uff0c\u5979\u8ddf\u516c\u53f8\u6709\u5408\u7ea6\u5728\u8eab\uff0c\u9664\u975e\u4e0d\u987e\u4e00\u5207\u7684\u6495\u6bc1\u5408\u7ea6\uff0c\u8981\u4e48\u518d\u627e\u4e00\u4e2a\u66f4\u5f3a\u52bf\u7684\u9760\u5c71\uff0c\u4f46\u662f\u8fd9\u4e9b\u5403\u4eba\u4e0d\u5410\u9aa8\u5934\u7684\u9760\u5c71\u56fe\u7684\u53c8\u662f\u4ec0\u4e48\uff1f\u5b8b\u5a49\u4f69\u8fd9\u4e24\u5e74\u4e5f\u771f\u662f\u770b\u900f\u4e86\uff0c\u6709\u65f6\u5019\u4e5f\u4f1a\u60f3\u65e0\u975e\u5c31\u56fe\u5356\u4e2a\u597d\u4ef7\u94b1\uff0c\u5ac1\u5165\u8c6a\u95e8\u867d\u7136\u4e0d\u662f\u4ec0\u4e48\u597d\u7684\u9009\u62e9\uff0c\u603b\u6bd4\u5b64\u8eab\u7ad9\u7acb\u8352\u539f\u7684\u72fc\u7fa4\u4e2d\u95f4\u597d\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u4e0a\u8f66\u65f6\uff0c\u8fd8\u770b\u5230\u5b8b\u7956\u8a89\u5c06\u5979\u7684\u7ecf\u7eaa\u4eba\u4e0e\u52a9\u624b\u53eb\u8fc7\u53bb\u4f4e\u58f0\u5429\u5490\u4ec0\u4e48\uff0c\u5b8b\u5a49\u4f69\u5750\u5728\u8f66\u91cc\u66f4\u89c9\u51c4\u51c9\u3002\u5c31\u50cf\u6eba\u6c34\u65f6\u6709\u6839\u7a3b\u8349\u4e5f\u8981\u6293\u4f4f\u7684\u9053\u7406\u4e00\u6837\uff0c\u5b8b\u5a49\u4f69\u7ffb\u5f00\u901a\u8baf\u5f55\u627e\u5230\u6210\u5f3a\u7684\u7535\u8bdd\uff0c\u542c\u7740\u624b\u673a\u4f20\u6765\u5bf9\u65b9\u5173\u673a\u7684\u63d0\u793a\u97f3\uff0c\u5b8b\u5a49\u4f69\u4e00\u9897\u5fc3\u51b7\u5230\u5e95\uff1a\u8fd9\u4e2a\u7537\u4eba\u8ddf\u53ea\u72d7\u4f3c\u7684\u7ed9\u8d76\u51fa\u5305\u53a2\uff0c\u8fd9\u65f6\u5019\u7adf\u7136\u8fde\u5f00\u673a\u7684\u80c6\u5b50\u90fd\u6ca1\u6709\u3002\u552f\u6709\u8054\u901a\u90a3\u4e2a\u5973\u7684\u8bf4\u7684\u8bdd\u8ba9\u5979\u5fc3\u91cc\u8fd8\u6709\u4e9b\u4fa5\u5e78\uff0c\u53c8\u89c9\u5f97\u8fd9\u79cd\u4fa5\u5e78\u5fc3\u7406\u5b9e\u5728\u4e0d\u5e94\u8be5\uff0c\u5929\u4e0b\u54ea\u6709\u4e0d\u5403\u7f8a\u7684\u72fc\uff1f</p></article>"}}}]);
