(window.webpackJsonp=window.webpackJsonp||[]).push([[1243],{1266:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u56db\u8282 \u725b\u5934\u4eba",content:"<article><h2>\u7b2c\u4e09\u5341\u56db\u8282 \u725b\u5934\u4eba</h2><p>\u8ff7\u5bab\u5f88\u5e9e\u5927\uff0c\u4f46\u5e76\u4e0d\u662f\u5206\u6210\u56db\u4e2a\u72ec\u7acb\u7684\u90e8\u5206\uff0c\u800c\u662f\u76f8\u4e92\u8054\u901a\u7684\u3002\u8f6c\u4e86\u534a\u5929\uff0c\u743c\u6069\u548c\u8299\u857e\u72c4\u53d1\u73b0\u901a\u9053\u91cc\u660e\u663e\u6709\u6218\u6597\u8fc7\u7684\u8ff9\u8c61\uff0c\u5899\u58c1\u4e0a\u662f\u706b\u7130\u707c\u70e7\u8fc7\u7684\u75d5\u8ff9\uff0c\u5730\u4e0a\u4e03\u96f6\u516b\u788e\u5730\u6563\u7740\u767d\u9aa8\u548c\u5f13\u7bad\u2014\u2014\u663e\u7136\u662f\u9ab7\u9ac5\u5c04\u624b\u88ab\u6253\u788e\u4e86\u3002</p><p>\u8fd9\u5730\u65b9\u5df2\u7ecf\u6709\u4eba\u6765\u8fc7\u3002</p><p>\u518d\u5f80\u524d\u8d70\uff0c\u8fd9\u4e2a\u5224\u65ad\u66f4\u52a0\u5f97\u5230\u8bc1\u5b9e\uff0c\u4e00\u8def\u4e0a\u7684\u602a\u7269\u5c38\u4f53\u66f4\u591a\u3002\u743c\u6069\u6b63\u51c6\u5907\u4ed4\u7ec6\u5bdf\u770b\u4e00\u4e0b\u4f24\u53e3\uff0c\u770b\u80fd\u4e0d\u80fd\u63a8\u6d4b\u51fa\u662f\u8c01\u5e72\u7684\uff0c\u8299\u857e\u72c4\u7a81\u7136\u53eb\u4ed6\uff1a\u201c\u743c\u6069\uff0c\u4f60\u770b\u90a3\u3002\u201d</p><p>\u743c\u6069\u987a\u7740\u8299\u857e\u72c4\u6240\u6307\u65b9\u5411\u770b\u53bb\uff0c\u501f\u7740\u6cd5\u6756\u9876\u7aef\u7684\u5149\u4eae\uff0c\u4ed6\u770b\u89c1\u524d\u65b9\u9634\u5f71\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e24\u4e2a\u53c8\u9ad8\u53c8\u80d6\u7684\u8eab\u5f71\uff0c\u6b63\u62e5\u62b1\u5728\u4e00\u8d77\uff0c\u90fd\u7ad9\u7740\u4e00\u52a8\u4e0d\u52a8\u3002</p><p>\u9760\u8fd1\u4e9b\uff0c\u624d\u53d1\u89c9\u54ea\u91cc\u662f\u62e5\u62b1\uff0c\u5206\u660e\u662f\u4e24\u4e2a\u7c98\u571f\u9b54\u50cf\uff0c\u6b63\u5728\u626d\u62b1\u53ae\u6253\u95f4\uff0c\u5374\u4eff\u4f5b\u540c\u65f6\u4e2d\u4e86\u5b9a\u8eab\u9b54\u6cd5\uff0c\u4fdd\u6301\u90a3\u4e00\u5239\u90a3\u7684\u59ff\u52bf\u50f5\u76f4\u4e0d\u52a8\u3002</p><p>\u4f46\u7c98\u571f\u9b54\u50cf\u662f\u4e0d\u53ef\u80fd\u4e2d\u5b9a\u8eab\u9b54\u6cd5\u7684\uff0c\u5b9a\u8eab\u672f\u53ea\u5bf9\u6709\u8840\u8089\u6709\u751f\u547d\u7684\u751f\u7269\u6709\u7528\u3002</p><p>\u9b54\u50cf\u7684\u5468\u56f4\uff0c\u6218\u6597\u8fc7\u7684\u75d5\u8ff9\u975e\u5e38\u660e\u663e\uff0c\u743c\u6069\u627e\u5230\u4e86\u534a\u622a\u6298\u65ad\u7684\u6cd5\u6756\u2014\u2014\u548c\u4ed6\u81ea\u5df1\u624b\u4e0a\u7684\u4e00\u6a21\u4e00\u6837\uff0c\u662f\u5b66\u6821\u914d\u53d1\u7ed9\u4e94\u5e74\u7ea7\u5b66\u751f\u7684\u90a3\u79cd\u6cd5\u6756\u3002</p><p>\u60c5\u51b5\u5df2\u7ecf\u5f88\u6e05\u695a\u4e86\u3002</p><p>\u51fa\u53d1\u4e4b\u524d\uff0c\u6559\u6388\u4eec\u5df2\u7ecf\u7ed9\u7c98\u571f\u9b54\u50cf\u8bbe\u5b9a\u597d\u7a0b\u5f0f\uff0c\u5b83\u4eec\u53ea\u542c\u5404\u81ea\u7684\u4e3b\u4eba\uff08\u5305\u62ec\u56db\u540d\u6bd5\u4e1a\u751f\u548c\u56db\u540d\u52a9\u624b\uff09\u7684\u547d\u4ee4\uff0c\u7528\u7cbe\u795e\u9065\u63a7\u3002\u5982\u4eca\u51fa\u73b0\u8fd9\u79cd\u7a81\u7136\u50f5\u5316\u7684\u573a\u9762\uff0c\u53ea\u6709\u4e00\u79cd\u89e3\u91ca\uff0c\u5c31\u662f\u8fd9\u4e24\u5c0a\u7c98\u571f\u9b54\u50cf\uff0c\u7a81\u7136\u548c\u81ea\u5df1\u7684\u4e3b\u4eba\u5b8c\u5168\u65ad\u7edd\u4e86\u7cbe\u795e\u8054\u7cfb\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5b83\u4eec\u7684\u4e3b\u4eba\uff0c\u5df2\u7ecf\u4e27\u5931\u4e86\u6e05\u9192\u7684\u610f\u8bc6\u2014\u2014\u60f3\u5fc5\u662f\u88ab\u81ea\u52a8\u4f20\u9001\u56de\u9634\u9b42\u57ce\u53bb\u6cbb\u7597\u4e86\u5427\u3002</p><p>\u5f88\u597d\uff0c\u975e\u5e38\u597d\uff0c\u597d\u6781\u4e86\u3002</p><p>\u5df2\u7ecf\u53ef\u4ee5\u786e\u5b9a\u5c11\u4e86\u4e24\u4e2a\u5bf9\u624b\uff0c\u8fd9\u81ea\u7136\u4ee4\u4eba\u9ad8\u5174\u3002\u8ff7\u5bab\u91cc\u7684\u602a\u7269\u4f3c\u4e4e\u88ab\u8fd9\u4e24\u4e2a\u5012\u9709\u5bb6\u4f19\u4e00\u8def\u6740\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u8fd9\u66f4\u4ee4\u4eba\u9ad8\u5174\u2014\u2014\u4f46\u6700\u5927\u7684\u96be\u9898\u8fd8\u662f\u6ca1\u6709\u89e3\u51b3\uff0c\u743c\u6069\u548c\u8299\u857e\u72c4\u8f6c\u6765\u8f6c\u53bb\uff0c\u8d8a\u8f6c\u8d8a\u6655\uff0c\u51e0\u4e4e\u5df2\u7ecf\u4e0d\u77e5\u9053\u8eab\u5728\u4f55\u5730\u4e86\u3002</p><p>\u524d\u9762\u53c8\u51fa\u73b0\u4e86\u4e00\u4e2a\u5c94\u8def\u53e3\uff0c\u5de6\u53f3\u5404\u6709\u4e24\u6761\u901a\u9053\u3002\u743c\u6069\u770b\u6765\u770b\u53bb\uff0c\u770b\u4e0d\u51fa\u4ec0\u4e48\u7aef\u502a\uff0c\u6b63\u51c6\u5907\u78b0\u78b0\u8fd0\u6c14\u9009\u4e00\u6761\uff0c\u8299\u857e\u72c4\u7a81\u7136\u62c9\u4f4f\u4ed6\u7684\u624b\uff0c\u8bf4\uff1a\u201c\u8d70\u8fd9\u8fb9\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u4f46\u4e5f\u53ea\u80fd\u76f8\u4fe1\u5979\u3002\u4e24\u4eba\u4e00\u8def\u524d\u8fdb\uff0c\u8299\u857e\u72c4\u4eff\u4f5b\u7a81\u7136\u5f00\u7a8d\u4e86\u4e00\u822c\uff0c\u53c8\u50cf\u662f\u7a81\u7136\u83b7\u5f97\u4e86\u4ec0\u4e48\u7279\u522b\u7684\u80fd\u529b\uff0c\u51e0\u4e4e\u6bcf\u6b21\u90fd\u80fd\u6e05\u695a\u5730\u5728\u5c94\u9053\u4e2d\u9009\u51fa\u5e94\u8be5\u8d70\u7684\u8def\u7ebf\u2014\u2014\u5f53\u7136\u4e5f\u6709\u51e0\u6b21\u9009\u9519\u4e86\uff0c\u7ed3\u679c\u7ed5\u56de\u6765\u3002</p><p>\u636e\u743c\u6069\u6240\u77e5\uff0c\u9884\u8a00\u9b54\u6cd5\u867d\u7136\u4e5f\u6709\u8fd1\u4f3c\u201c\u6390\u7b97\u201d\u7684\u4f5c\u7528\uff0c\u80fd\u5411\u51a5\u51a5\u4e2d\u7684\u672a\u77e5\u5bfb\u6c42\u6307\u5f15\uff0c\u4f46\u90a3\u4e00\u822c\u662f\u5f88\u9ad8\u9636\u7684\u9884\u8a00\u5deb\u5e08\u624d\u80fd\u505a\u5230\u7684\u3002\u8299\u857e\u72c4\u867d\u7136\u5f88\u6709\u5929\u8d4b\uff0c\u4f46\u5230\u5e95\u4e0d\u8fc7\u662f\u4e09\u5e74\u7ea7\u7684\u5b66\u751f\uff0c\u7406\u5f53\u6ca1\u6709\u8fd9\u79cd\u80fd\u529b\u624d\u5bf9\u3002</p><p>\u4e0d\u8fc7\u4e8b\u5230\u5982\u4eca\uff0c\u4e5f\u53ea\u80fd\u76f8\u4fe1\u5979\u4e86\u3002\u5929\u8d4b\u8fd9\u79cd\u4e1c\u897f\uff0c\u539f\u672c\u5c31\u662f\u4e0d\u80fd\u4ee5\u5e38\u7406\u731c\u5ea6\u7684\u5427\uff0c\u743c\u6069\u60f3\u3002</p><p>\u7ec8\u4e8e\uff0c\u4e24\u4eba\u8d70\u8fdb\u4e00\u6761\u901a\u9053\uff0c\u743c\u6069\u60ca\u559c\u5730\u53d1\u73b0\uff0c\u524d\u65b9\u4f20\u6765\u4eae\u5149\u3002</p><p>\u5728\u8fd9\u9ed1\u6c89\u6c89\u7684\u752c\u9053\u8ff7\u5bab\u91cc\uff0c\u4eae\u5149\u53ea\u610f\u5473\u7740\u4e24\u79cd\u53ef\u80fd\uff0c\u4e00\u79cd\u662f\u51fa\u53e3\uff0c\u4e00\u79cd\u5c31\u662f\u4ed6\u4eec\u7684\u76ee\u7684\u5730\u3002</p><p>\u800c\u51fa\u53e3\u7684\u5149\u663e\u7136\u5e94\u8be5\u662f\u660e\u4eae\u7684\u767d\u8272\uff0c\u4e0d\u4f1a\u662f\u8679\u5149\u95ea\u70c1\u4e03\u5f69\u6d41\u6ea2\u7684\u3002</p><p>\u6240\u4ee5\u8fd9\u53ea\u53ef\u80fd\u662f\u90a3\u679a\u8679\u5f69\u6212\u6307\u5728\u6cdb\u5149\u3002</p><p>\u7ec8\u4e8e\uff0c\u627e\u5230\u4e86\uff01</p><p>\u203b\u203b\u203b</p><p>\u5f3a\u637a\u4f4f\u5fc3\u5934\u7684\u6fc0\u52a8\uff0c\u743c\u6069\u793a\u610f\u8299\u857e\u72c4\u91ca\u653e\u6700\u540e\u4e00\u4e2a\u51c6\u5907\u597d\u7684\u9b54\u6cd5\u3002\u8d8a\u662f\u63a5\u8fd1\u6210\u529f\u7684\u65f6\u5019\uff0c\u8d8a\u8981\u5c0f\u5fc3\u8c28\u614e\uff0c\u5343\u4e07\u4e0d\u80fd\u529f\u4e8f\u4e00\u7bd1\uff0c\u8fd9\u662f\u6bcf\u4e2a\u5deb\u5e08\u90fd\u5e94\u8be5\u660e\u767d\u7684\u9053\u7406\u3002</p><p>\u67d4\u548c\u7684\u7c89\u7ea2\u8272\u5149\u8292\u4ece\u8299\u857e\u72c4\u7684\u624b\u4e2d\u6563\u51fa\uff0c\u5316\u4f5c\u96fe\u6c14\u671d\u90a3\u6cdb\u7740\u8679\u5149\u7684\u524d\u65b9\u98d8\u53bb\u3002\u743c\u6069\u51dd\u795e\u89c2\u770b\u53d8\u5316\uff0c\u4ed6\u6ca1\u6709\u53d1\u73b0\u6709\u4efb\u4f55\u5f02\u5e38\u3002</p><p>\u6ca1\u6709\u53d1\u73b0\u6709\u4ec0\u4e48\u9677\u9631\u3002</p><p>\u8299\u857e\u72c4\u6307\u6325\u7740\u9b54\u50cf\uff0c\u5f80\u524d\u5c0f\u5fc3\u5730\u63a8\u8fdb\u7740\u3002\u4fa6\u6d4b\u9677\u9631\u7684\u9b54\u6cd5\u5e76\u4e0d\u80fd\u4fdd\u8bc1\u4e07\u65e0\u4e00\u5931\uff0c\u5982\u679c\u662f\u8bbe\u7f6e\u5de7\u5999\u7684\u9677\u9631\uff0c\u5176\u5b9e\u9b54\u6cd5\u662f\u53d1\u73b0\u4e0d\u4e86\u7684\u3002\u8ba9\u9b54\u50cf\u8d70\u5728\u524d\u9762\uff0c\u53ef\u4ee5\u8d77\u5230\u66ff\u6b7b\u9b3c\u517c\u6321\u7bad\u724c\u7684\u4f5c\u7528\u3002</p><p>\u901a\u9053\u7684\u524d\u65b9\uff0c\u662f\u4e00\u4e2a\u5e76\u4e0d\u5bbd\u655e\u7684\u623f\u95f4\uff0c\u91cc\u9762\u7a7a\u7a7a\u8361\u8361\uff0c\u4e2d\u592e\u6709\u4e00\u4e2a\u65b9\u5f62\u7684\u77f3\u53f0\uff0c\u8679\u5f69\u6d41\u5149\u5c31\u662f\u4ece\u90a3\u77f3\u53f0\u4e0a\u53d1\u51fa\u3002</p><p>\u8299\u857e\u72c4\u6b63\u8981\u4e0a\u524d\u53bb\u53d6\u90a3\u679a\u6212\u6307\uff0c\u88ab\u743c\u6069\u62e6\u4f4f\u4e86\u3002</p><p>\u201c\u5411\u524d\u3002\u201d\u743c\u6069\u6307\u6325\u7740\u9b54\u50cf\u3002</p><p>\u9b54\u50cf\u542c\u547d\u5f80\u524d\uff0c\u6c89\u91cd\u7684\u6b65\u4f10\u9686\u9686\u4f5c\u54cd\uff0c\u5b83\u8de8\u51fa\u4e00\u6b65\u3001\u4e24\u6b65\uff0c\u5c31\u5728\u521a\u51c6\u5907\u8e0f\u51fa\u7b2c\u4e09\u6b65\u7684\u65f6\u5019\uff0c\u9661\u7136\u524d\u65b9\u7684\u7a7a\u6c14\u4e00\u9635\u626d\u66f2\u6ce2\u6298\u3002</p><p>\u539f\u672c\u865a\u65e0\u4e00\u7247\u7684\u7a7a\u6c14\u4e2d\uff0c\u9aa4\u7136\u5f00\u542f\u4e00\u5757\u9ed1\u8272\u7684\u692d\u5706\u5f62\u5927\u6d1e\u3002\u4e00\u4e2a\u9ad8\u5927\u7684\u4eba\u5f62\u751f\u7269\u8fc8\u7740\u6c89\u91cd\u811a\u6b65\uff0c\u4ece\u692d\u5706\u5f62\u9ed1\u6d1e\u4e2d\u94bb\u4e86\u51fa\u6765\u3002</p><p>\u9996\u5148\u6620\u5165\u773c\u5e18\u7684\u662f\u4e00\u53ea\u5de8\u5927\u7684\u725b\u5934\uff0c\u4e00\u5bf9\u9ed1\u8272\u5f2f\u66f2\u725b\u89d2\u5728\u5934\u90e8\u4e24\u8fb9\u9a84\u50b2\u7fd8\u8d77\uff0c\u7c97\u5927\u7684\u9f3b\u5b54\u4e2d\u4e0d\u65f6\u55b7\u51fa\u767d\u6c14\u3002\u5634\u5f20\u5f00\u7740\uff0c\u9732\u51fa\u4e24\u6392\u5c16\u5229\u7684\u7259\u9f7f\uff0c\u9ecf\u547c\u547c\u7684\u53e3\u6c34\u6302\u5728\u5634\u89d2\uff0c\u6709\u7684\u5df2\u7ecf\u53d8\u6210\u767d\u8272\u6ce1\u6cab\u3002\u5b83\u6d51\u8eab\u8986\u76d6\u7740\u7ea2\u7efc\u8272\u7684\u6bdb\u53d1\uff0c\u808c\u8089\u7ea0\u7ed3\uff0c\u53cc\u624b\u63e1\u7740\u4e00\u67c4\u5de8\u5927\u7684\u53cc\u9762\u6218\u65a7\uff0c\u950b\u5203\u4e0a\u5bd2\u5149\u51db\u51db\uff0c\u663e\u793a\u7740\u5b83\u7684\u950b\u9510\uff0c\u76f8\u4fe1\u7edd\u5bf9\u53ef\u4ee5\u4e00\u65a7\u780d\u4e0b\u4efb\u4f55\u751f\u7269\u7684\u5934\u9885\u6765\u3002\u5b83\u6ca1\u6709\u7a7f\u4efb\u4f55\u94e0\u7532\uff0c\u4ec5\u4ec5\u5728\u8170\u95f4\u56f4\u4e86\u4e00\u5708\u6697\u9ec4\u8272\u76ae\u9769\u8170\u5e26\u3002</p><p>\u5b83\u7684\u4e0a\u8eab\u770b\u8d77\u6765\u8fd8\u52c9\u5f3a\u50cf\u4eba\uff0c\u53ea\u662f\u7c97\u58ee\u5f97\u8fc7\u5206\uff0c\u4e0b\u8eab\u5219\u5c31\u662f\u5b8c\u5168\u7684\u725b\u817f\uff0c\u6234\u7740\u4e24\u53ea\u91d1\u8272\u811a\u73af\u3002\u811a\u73af\u4e4b\u4e0b\uff0c\u662f\u4e00\u53cc\u786c\u8e44\uff0c\u8e44\u9762\u5206\u53c9\uff0c\u4eff\u4f5b\u9ed1\u6c89\u6c89\u7684\u94c1\u5757\u3002</p><p>\u8fd9\u662f\u4e00\u53ea\u725b\u5934\u4eba\u3002</p><p>\u6559\u6388\u8bf4\uff0c\u8ff7\u5bab\u91cc\u53ef\u80fd\u4f1a\u9047\u5230\u5730\u7cbe\u3001\u9ab7\u9ac5\u5c04\u624b\u3001\u50f5\u5c38\u548c\u725b\u5934\u4eba\u3002\u73b0\u5728\u770b\u8d77\u6765\uff0c\u8fd9\u8bdd\u8981\u4fee\u6539\u4e00\u4e0b\uff0c\u5730\u7cbe\u3001\u9ab7\u9ac5\u5c04\u624b\u548c\u50f5\u5c38\u90fd\u662f\u201c\u53ef\u80fd\u201d\u4f1a\u9047\u5230\uff0c\u725b\u5934\u4eba\u5374\u662f\u201c\u4e00\u5b9a\u201d\u4f1a\u9047\u5230\u7684\u3002</p><p>\u8fd9\u53ea\u725b\u5934\u4eba\uff0c\u5206\u660e\u5c31\u662f\u5b89\u6392\u5728\u8fd9\u91cc\u5b88\u62a4\u7740\u8679\u5f69\u6212\u6307\u7684\u3002\u4e0d\u7ba1\u662f\u8c01\uff0c\u8981\u6765\u53d6\u8d70\u6212\u6307\uff0c\u90fd\u8981\u5148\u901a\u8fc7\u8fd9\u53ea\u725b\u5934\u4eba\u3002</p><p>\u4e00\u58f0\u6012\u543c\uff0c\u9707\u5f97\u6574\u4e2a\u623f\u95f4\u7c0c\u7c0c\u4f5c\u54cd\u3002\u8fd9\u53ea\u725b\u5934\u4eba\u4e0d\u77e5\u662f\u88ab\u4ece\u54ea\u91cc\u6293\u6765\u7684\uff0c\u5df2\u7ecf\u7981\u9522\u5728\u8fd9\u91cc\u5f88\u4e45\u4e86\uff0c\u76f4\u5230\u6709\u4eba\u9760\u8fd1\u624d\u4f1a\u89e6\u53d1\u9884\u5148\u8bbe\u5b9a\u597d\u7684\u9b54\u6cd5\uff0c\u5c06\u5b83\u91ca\u653e\u51fa\u6765\u3002\u91cd\u83b7\u81ea\u7531\u7684\u725b\u5934\u4eba\u5e76\u6ca1\u6709\u6253\u7b97\u611f\u8c22\u91ca\u653e\u8005\u7684\u610f\u601d\uff0c\u5b83\u663e\u7136\u628a\u743c\u6069\u548c\u8299\u857e\u72c4\u4e24\u4eba\u89c6\u4e3a\u6293\u6355\u5b83\u7684\u654c\u4eba\u7684\u540c\u4f19\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e5f\u6ca1\u9519\u3002\u6293\u5b83\u7684\u80af\u5b9a\u662f\u9634\u9b42\u57ce\u7684\u5deb\u5e08\uff0c\u800c\u743c\u6069\u548c\u8299\u857e\u72c4\u4e5f\u786e\u5b9e\u662f\u2014\u2014\u770b\uff0c\u8fde\u8863\u7740\u6253\u626e\u90fd\u5dee\u4e0d\u591a\uff0c\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u5deb\u5e08\u957f\u888d\u3002\u522b\u8bf4\u8fd9\u4f4d\u725b\u5934\u4eba\u770b\u8d77\u6765\u4e0d\u61c2\u4eba\u7c7b\u8bed\u8a00\uff0c\u5c31\u7b97\u5b83\u61c2\uff0c\u4f30\u8ba1\u4e5f\u542c\u4e0d\u8fdb\u89e3\u91ca\u4e86\u3002</p><p>\u90a3\u4e48\u2026\u2026\u552f\u6709\u4e00\u6218\u3002</p><p>\u201c\u6740\u6b7b\u5b83\u3002\u201d\u743c\u6069\u76f4\u622a\u4e86\u5f53\u5730\u5411\u7c98\u571f\u9b54\u50cf\u4e0b\u8fbe\u4e86\u6e05\u695a\u7684\u547d\u4ee4\u3002</p></article>"}}}]);
