(window.webpackJsonp=window.webpackJsonp||[]).push([[1246],{1269:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e03\u8282 \u59d0\u59d0\u7684\u68a6\u60f3",content:"<article><h2>\u7b2c\u4e09\u5341\u4e03\u8282 \u59d0\u59d0\u7684\u68a6\u60f3</h2><p>\u56de\u5230\u5bb6\u4e2d\uff0c\u5df2\u7ecf\u662f\u508d\u665a\uff0c\u8840\u7ea2\u8272\u7684\u5915\u9633\u897f\u5782\uff0c\u5c06\u743c\u6069\u7684\u5f71\u5b50\u957f\u957f\u5730\u62d6\u5728\u5730\u4e0a\u3002\u8857\u4e0a\u6709\u4e9b\u7a7a\u8361\u8361\u7684\uff0c\u660e\u5929\u662f\u7eff\u8349\u8282\uff0c\u6b64\u65f6\u5927\u5bb6\u5e94\u8be5\u90fd\u5728\u5bb6\u4e2d\u51c6\u5907\u7740\u660e\u5929\u7684\u5e86\u795d\u5427\u3002</p><p>\u73ca\u5609\u5728\u95e8\u53e3\u7b49\u5f85\u7740\u4ed6\u3002</p><p>\u59d0\u59d0\u4eca\u5929\u6ca1\u6709\u50cf\u5f80\u65e5\u90a3\u6837\u7a7f\u7740\u886c\u886b\u957f\u88e4\uff0c\u800c\u662f\u6362\u4e0a\u4e86\u90a3\u5957\u7c89\u7ea2\u8272\u7684\u8fde\u8863\u88d9\uff0c\u4ee5\u53ca\u5979\u5e73\u65f6\u6700\u559c\u6b22\u7684\u5c0f\u718a\u51c9\u978b\u3002\u957f\u53d1\u7528\u4e00\u53ea\u84dd\u8272\u8774\u8776\u5f62\u7684\u53d1\u5939\u5939\u8d77\uff0c\u9732\u51fa\u53f3\u8033\u8033\u5782\u4e0a\u7684\u8718\u86db\u5f62\u8033\u9489\uff0c\u90a3\u662f\u743c\u6069\u4e0a\u6b21\u9001\u7ed9\u5979\u7684\u793c\u7269\u3002</p><p>\u5bb6\u5883\u539f\u672c\u5c31\u5e76\u4e0d\u5bcc\u88d5\uff0c\u743c\u6069\u53bb\u5deb\u5e08\u5b66\u6821\u8bfb\u4e66\uff0c\u53c8\u5e26\u8d70\u4e86\u6240\u6709\u7684\u79ef\u84c4\u3002\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u73ca\u5609\u4e00\u4e2a\u5c0f\u5973\u5b69\uff0c\u72ec\u529b\u7ef4\u6301\u8fd9\u4e2a\u5bb6\uff0c\u867d\u8bf4\u9634\u9b42\u57ce\u7b97\u662f\u7269\u8d28\u6c34\u51c6\u53d1\u8fbe\uff0c\u798f\u5229\u5f85\u9047\u6bd4\u8f83\u597d\u7684\u5730\u65b9\uff0c\u4f46\u5176\u4e2d\u8270\u82e6\uff0c\u4e5f\u4e0d\u95ee\u53ef\u77e5\u3002\u5bb6\u91cc\u7684\u7ecf\u6d4e\u72b6\u51b5\uff0c\u662f\u5e76\u4e0d\u600e\u4e48\u5982\u610f\u7684\u3002</p><p>\u5df2\u7ecf\u5230\u4e86\u5341\u4e94\u5c81\uff0c\u6b63\u662f\u5973\u5b69\u5b50\u6700\u7231\u6f02\u4eae\u7684\u65f6\u5019\uff0c\u4f46\u73ca\u5609\u51e0\u4e4e\u4ece\u672a\u7ed9\u81ea\u5df1\u4e70\u8fc7\u4efb\u4f55\u4e00\u4ef6\u65b0\u7684\u8863\u670d\u548c\u9970\u54c1\uff0c\u5927\u90e8\u5206\u90fd\u662f\u4ee5\u524d\u6bcd\u4eb2\u7684\u8863\u670d\uff0c\u53cd\u6b63\u73ca\u5609\u548c\u6bcd\u4eb2\u8eab\u6750\u76f8\u8fd1\u3002\u8fd9\u4ef6\u7c89\u7ea2\u8272\u8fde\u8863\u88d9\uff0c\u662f\u4e0a\u6b21\u743c\u6069\u575a\u6301\u8981\u4e70\u7684\uff0c\u73ca\u5609\u4e5f\u5c31\u987a\u4e86\u4ed6\u7684\u610f\uff0c\u53ef\u80fd\u662f\u552f\u4e00\u4e00\u4ef6\u65b0\u8863\u670d\u4e86\u5427\u3002</p><p>\u5979\u9760\u5728\u95e8\u69db\uff0c\u770b\u7740\u743c\u6069\uff0c\u5fae\u7b11\u7740\uff0c\u4eff\u4f5b\u59bb\u5b50\u5728\u7b49\u5f85\u5de5\u4f5c\u5f52\u6765\u7684\u4e08\u592b\u3002</p><p>\u201c\u59d0\u59d0\u3002\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u56de\u6765\u4e86\u3002\u201d\u73ca\u5609\u5fae\u7b11\u7740\uff0c\u663e\u5f97\u5f88\u5f00\u5fc3\u7684\u6837\u5b50\uff0c\u62c9\u7740\u4ed6\u8fdb\u5bb6\u95e8\u3002</p><p>\u5deb\u5e08\u5b66\u6821\u6bd5\u4e1a\u8003\u8bd5\u662f\u5f88\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u6d88\u606f\u65e9\u5c31\u4f20\u904d\u4e86\u9634\u9b42\u57ce\uff0c\u73ca\u5609\u81ea\u7136\u4e5f\u65e9\u5df2\u7ecf\u77e5\u9053\u4e86\u7ed3\u679c\u3002\u743c\u6069\u5f53\u7136\u4e5f\u65e9\u5c31\u548c\u73ca\u5609\u63d0\u5230\u8fc7\uff0c\u8bf4\u60f3\u80fd\u83b7\u5f97\u6bd5\u4e1a\u8003\u8bd5\u7684\u7b2c\u4e00\u540d\uff0c\u8fdb\u5165\u5965\u672f\u5e08\u5b66\u6821\u3002\u4e0d\u8fc7\u770b\u8d77\u6765\uff0c\u73ca\u5609\u5e76\u4e0d\u5982\u4f55\u5931\u671b\uff0c\u5927\u6982\uff0c\u743c\u6069\u80fd\u4ee5\u7b2c\u4e8c\u540d\u7684\u6210\u7ee9\u6bd5\u4e1a\uff0c\u5df2\u7ecf\u662f\u5927\u5927\u8d85\u51fa\u5979\u6700\u521d\u7684\u9884\u671f\u4e86\u5427\u3002</p><p>\u6bd5\u4e1a\u751f\u4e2d\uff0c\u7b2c\u4e00\u540d\u8fdb\u5965\u672f\u5e08\u5b66\u6821\u6df1\u9020\uff1b\u5176\u4f59\u4eba\u4e2d\uff0c\u6210\u7ee9\u4f18\u79c0\u7684\u8fdb\u653f\u5e9c\u90e8\u95e8\uff0c\u6210\u7ee9\u7a0d\u900a\u7684\u62db\u8fdb\u519b\u961f\u3002\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\uff0c\u5386\u5e74\u6765\u7684\u89c4\u77e9\u5c31\u662f\u5982\u6b64\u3002\u9634\u9b42\u57ce\u662f\u4e2a\u8bb2\u7a76\u5b9e\u529b\u7684\u5730\u65b9\uff0c\u4e0d\u4f1a\u6d6a\u8d39\u7cbe\u82f1\u4eba\u624d\u3002\u660e\u5929\u662f\u7eff\u8349\u8282\uff0c\u5168\u57ce\u72c2\u6b22\u5e86\u795d\uff0c\u4f30\u8ba1\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fc7\u4e86\u660e\u5929\uff0c\u5927\u6982\u5c31\u4f1a\u6709\u653f\u5e9c\u5b98\u5458\u4e0a\u95e8\uff0c\u901a\u77e5\u743c\u6069\u53bb\u67d0\u4e2a\u90e8\u95e8\u62a5\u9053\u4e86\u3002</p><p>\u4ece\u6b64\uff0c\u743c\u6069\u548c\u73ca\u5609\uff0c\u4e0d\uff0c\u662f\u5170\u5c3c\u65af\u7279\u5bb6\u65cf\uff0c\u4e5f\u80fd\u8dfb\u8eab\u8d35\u65cf\uff0c\u4eab\u53d7\u5404\u79cd\u7279\u6743\u5f85\u9047\uff0c\u6210\u4e3a\u4e0a\u6d41\u793e\u4f1a\u7684\u4e00\u5206\u5b50\uff0c\u518d\u4e0d\u662f\u4e24\u4e2a\u5c0f\u5546\u4eba\u4e86\u3002</p><p>\u5fc5\u987b\u8bf4\uff0c\u5c31\u7b97\u662f\u5728\u9634\u9b42\u57ce\u957f\u8fbe\u4e24\u5343\u5e74\u7684\u5386\u53f2\u4e2d\uff0c\u8fd9\u79cd\u5148\u4f8b\u90fd\u662f\u4e0d\u591a\u7684\u3002\u9ad8\u6602\u7684\u5b66\u8d39\uff0c\u82db\u523b\u7684\u6dd8\u6c70\u6807\u51c6\uff0c\u52a0\u4e0a\u5e73\u6c11\u5b50\u5f1f\u5728\u5bb6\u4f20\u3001\u57fa\u7840\u6559\u80b2\u4e0a\u7684\u8584\u5f31\uff0c\u79cd\u79cd\u56e0\u7d20\u7d2f\u52a0\u8d77\u6765\uff0c\u5bfc\u81f4\u5f88\u96be\u771f\u6709\u5e73\u6c11\u5b50\u5f1f\u9760\u8fd9\u6761\u8def\u51fa\u5934\u3002</p><p>\u4e0d\u8fc7\uff0c\u743c\u6069\u81ea\u5df1\u5e76\u4e0d\u6ee1\u610f\u3002</p><p>\u82e5\u662f\u6362\u4e86\u4ee5\u524d\uff0c\u743c\u6069\u7684\u68a6\u60f3\u5176\u5b9e\u4e5f\u4e0d\u7b97\u591a\u4e48\u8fdc\u5927\uff0c\u65e0\u975e\u4e5f\u5c31\u662f\u60f3\u5f53\u4e0a\u5deb\u5e08\uff0c\u62e5\u6709\u529b\u91cf\u548c\u5730\u4f4d\uff0c\u80fd\u56de\u5230\u7269\u8d28\u754c\uff0c\u4e0d\u8981\u4e00\u8f88\u5b50\u88ab\u5173\u5728\u9634\u9b42\u57ce\u91cc\u2014\u2014\u6700\u540e\u518d\u52a0\u4e0a\u80fd\u6709\u5f88\u591a\u7f8e\u5973\u76f8\u4f34\u3002\u5982\u4eca\u4ed6\u5df2\u7ecf\u662f\u6b63\u5f0f\u5deb\u5e08\uff0c\u6210\u7ee9\u4e0d\u9519\uff0c\u9634\u9b42\u57ce\u53c8\u56de\u5230\u4e86\u7269\u8d28\u754c\uff0c\u4e0d\u51fa\u610f\u5916\u7684\u8bdd\uff0c\u8fd9\u4e9b\u613f\u671b\u7684\u5b9e\u73b0\u90fd\u4e0d\u662f\u4ec0\u4e48\u96be\u4e8b\u3002</p><p>\u4f46\u5deb\u5e08\u5b66\u6821\u4e94\u5e74\u7684\u5b66\u4e60\uff0c\u5df2\u7ecf\u8ba9\u4ed6\u7684\u773c\u754c\u5f00\u9614\uff0c\u5bf9\u8fd9\u4e2a\u4e16\u754c\uff0c\u5bf9\u9b54\u6cd5\u7684\u8ba4\u8bc6\u66f4\u6df1\uff0c\u518d\u4e0d\u662f\u5f53\u5e74\u90a3\u521d\u6765\u4e4d\u5230\u7684\u7a7f\u8d8a\u8005\u4e86\u3002\u4eba\u7684\u6b32\u671b\uff0c\u603b\u662f\u4f1a\u5728\u6084\u6084\u5730\u4e0d\u65ad\u589e\u957f\u3002</p><p>\u4ee5\u524d\u770b\u6b66\u4fa0\u5c0f\u8bf4\uff0c\u770b\u91d1\u5eb8\u7684\u5c04\u96d5\u82f1\u96c4\u4f20\uff0c\u6709\u4e00\u6bb5\u60c5\u8282\u8bb0\u5f97\u7279\u522b\u6e05\u695a\u3002\u90ed\u9756\u4e0a\u6843\u82b1\u5c9b\uff0c\u9047\u4e0a\u5468\u4f2f\u901a\uff0c\u5468\u4f2f\u901a\u8bf4\uff0c\u7ec3\u6b66\u529f\u4e43\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u4e50\u4e8b\uff0c\u5176\u4e2d\u6ecb\u5473\u65e0\u7a77\u3002\u4e16\u4eba\u611a\u8822\u5f97\u7d27\uff0c\u6709\u7684\u7231\u8bfb\u4e66\u505a\u5b98\uff0c\u6709\u7684\u7231\u9ec4\u91d1\u7f8e\u7389\uff0c\u66f4\u6709\u7684\u7231\u7edd\u8272\u7f8e\u5973\uff0c\u4f46\u8fd9\u5176\u4e2d\u7684\u4e50\u8da3\uff0c\u53c8\u600e\u53ca\u5f97\u4e0a\u4e60\u6b66\u7ec3\u529f\u7684\u4e07\u4e00\uff1f\u6240\u4ee5\u5929\u4e0b\u7ec3\u6b66\u529f\u7684\u4eba\uff0c\u53ea\u8981\u80fd\u6709\u673a\u4f1a\u5b66\u5f97\u66f4\u9ad8\u6df1\u7684\u6b66\u827a\uff0c\u65e0\u4e0d\u8d8b\u4e4b\u82e5\u9e5c\uff0c\u62fc\u6b7b\u76f8\u4e89\u3002</p><p>\u5f53\u5e74\u770b\u8fd9\u6bb5\uff0c\u4e5f\u4e0d\u751a\u4ee5\u4e3a\u7136\u3002\u89c9\u5f97\u7ec3\u6b66\u56fa\u7136\u6709\u8da3\uff0c\u7ec8\u7a76\u4e0d\u80fd\u548c\u91d1\u94b1\u7f8e\u5973\u76f8\u6bd4\u5427\u3002\u4f46\u5982\u4eca\uff0c\u81ea\u5df1\u7ec3\u6b66\u867d\u7136\u4e0d\u6210\uff0c\u5728\u9b54\u6cd5\u6280\u827a\u4e0a\u5374\u7b97\u662f\u521d\u7aa5\u95e8\u5f84\uff0c\u8d8a\u5b66\u4fbf\u8d8a\u89c9\u5f97\u5176\u4e2d\u679c\u7136\u662f\u5965\u5999\u65e0\u7a77\u3002</p><p>\u201c\u5982\u679c\u80fd\u8fdb\u5165\u5965\u672f\u5e08\u5b66\u6821\u2026\u2026\u90a3\u8be5\u6709\u591a\u597d\u3002\u201d</p><p>\u867d\u7136\u660e\u77e5\u4e8b\u5b9e\u5df2\u7ecf\u4e0d\u53ef\u6539\u53d8\uff0c\u4f9d\u7136\u5fcd\u4e0d\u4f4f\u5728\u5fc3\u91cc\u53f9\u606f\u3002</p><p>\u73ca\u5609\u770b\u51fa\u4ed6\u60c5\u7eea\u4e0d\u9ad8\uff0c\u4e5f\u5e76\u4e0d\u591a\u8bf4\u8bdd\uff0c\u53ea\u662f\u966a\u7740\u4ed6\u3002\u201c\u5bf9\u4e86\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u5979\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u201c\u5dee\u70b9\u5fd8\u4e86\uff0c\u4f60\u56de\u6765\u4e4b\u524d\uff0c\u6709\u4e2a\u5b98\u5458\u6765\u4e86\uff0c\u8bf4\u540e\u5929\u4e0a\u5348\uff0c\u4f1a\u5e2e\u6211\u4eec\u642c\u5bb6\u5230\u94bb\u77f3\u533a\u53bb\uff0c\u8ba9\u6211\u4eec\u4e8b\u5148\u6536\u62fe\u597d\u3002\u201d</p><p>\u94bb\u77f3\u533a\u662f\u9634\u9b42\u57ce\u7684\u8d35\u65cf\u5c45\u4f4f\u533a\uff0c\u743c\u6069\u5982\u4eca\u5df2\u7ecf\u662f\u6b63\u5f0f\u5deb\u5e08\uff0c\u793e\u4f1a\u5730\u4f4d\u9661\u7136\u63d0\u5347\uff0c\u81ea\u7136\u4e0d\u9002\u5b9c\u518d\u5c45\u4f4f\u5728\u8fd9\u79cd\u5e73\u6c11\u533a\u4e86\u3002\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u9634\u9b42\u57ce\u653f\u5e9c\u7684\u529e\u4e8b\u6548\u7387\u771f\u662f\u9ad8\uff0c\u743c\u6069\u81ea\u5df1\u8fd8\u6ca1\u56de\u5bb6\uff0c\u901a\u77e5\u5c31\u5df2\u7ecf\u5230\u4e86\u3002</p><p>\u201c\u55ef\u3002\u201d\u743c\u6069\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\u5730\u7b54\u5e94\u7740\u3002</p><p>\u5230\u4e86\u665a\u4e0a\uff0c\u743c\u6069\u6b63\u51c6\u5907\u56de\u81ea\u5df1\u7684\u5e8a\u4e0a\u7761\u89c9\uff0c\u73ca\u5609\u7a81\u7136\u8bf4\uff1a\u201c\u743c\u6069\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u665a\u4e0a\u966a\u6211\u7761\u597d\u4e0d\u597d\uff0c\u201d\u59d0\u59d0\u8981\u6c42\uff0c\u201c\u6211\u4e00\u4e2a\u4eba\u5f88\u51b7\u3002\u201d</p><p>\u82e5\u5728\u5f80\u65e5\uff0c\u743c\u6069\u5bf9\u6b64\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u53ea\u662f\u4eca\u5929\u5fc3\u60c5\u4f4e\u843d\uff0c\u5b9e\u5728\u662f\u63d0\u4e0d\u8d77\u592a\u591a\u7cbe\u795e\u3002\u4f46\u73ca\u5609\u65e2\u7136\u5982\u6b64\u8bf7\u6c42\uff0c\u81ea\u7136\u4e0d\u80fd\u4e0d\u7b54\u5e94\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u8131\u4e0b\u7761\u8863\u94bb\u8fdb\u88ab\u5b50\u3002</p><p>\u73ca\u5609\u7a7f\u7740\u7761\u8863\uff0c\u4eca\u5929\u53c8\u6070\u597d\u649e\u4e0a\u5973\u5b69\u5b50\u7684\u201c\u90a3\u51e0\u5929\u201d\uff0c\u743c\u6069\u4e5f\u4e0d\u6253\u7b97\u628a\u5979\u7684\u7761\u8863\u8131\u6389\uff0c\u53ea\u4ece\u540e\u9762\u62b1\u7740\uff0c\u6402\u5728\u6000\u91cc\uff0c\u73ca\u5609\u7684\u80cc\u8d34\u7740\u743c\u6069\u7684\u80f8\u53e3\uff0c\u5934\u6795\u5728\u743c\u6069\u80f3\u818a\u4e0a\u3002\u5f88\u4e45\u90fd\u6ca1\u6709\u8fd9\u6837\u62b1\u7740\u59d0\u59d0\u4e86\uff0c\u7b97\u8d77\u6765\uff0c\u6709\u4e94\u5e74\u4e86\u5462\u3002\u5973\u5b69\u7684\u8eab\u4f53\u4f9d\u65e7\u90a3\u6837\u7684\u808c\u80a4\u67d4\u6ed1\uff0c\u6563\u53d1\u7740\u6de1\u6de1\u7684\u5976\u9999\uff0c\u4e0d\u8fc7\u2026\u2026\u786e\u5b9e\u5df2\u7ecf\u957f\u5927\u4e86\u3002</p><p>\u6000\u91cc\u7684\u8eab\u4f53\uff0c\u5df2\u7ecf\u5b8c\u5168\u6210\u719f\uff0c\u624b\u8f7b\u8f7b\u5730\u79fb\u52a8\uff0c\u611f\u89c9\u5230\u73b2\u73d1\u6d6e\u51f8\u7684\u66f2\u7ebf\uff0c\u4ec5\u4ec5\u662f\u62b1\u7740\uff0c\u4fbf\u6709\u79cd\u975e\u5e38\u6e29\u6696\u6ee1\u8db3\u7684\u611f\u89c9\u3002</p><p>\u8fd9\u5927\u534a\u5e74\u6765\uff0c\u66f4\u591a\u7684\u65f6\u5019\uff0c\u6000\u91cc\u62b1\u7740\u7684\u90fd\u662f\u8299\u857e\u72c4\u3002\u540c\u6837\u662f\u6f02\u4eae\u7684\u5973\u5b50\uff0c\u4f46\u611f\u89c9\u622a\u7136\u4e0d\u540c\uff0c\u8299\u857e\u72c4\u5a07\u5c0f\u5f97\u591a\uff0c\u800c\u4e14\u7761\u89c9\u7684\u65f6\u5019\u7279\u522b\u4e0d\u8001\u5b9e\uff0c\u5c31\u50cf\u4e2a\u5b69\u5b50\u3002\u743c\u6069\u548c\u5979\u5728\u4e00\u8d77\uff0c\u611f\u89c9\u81ea\u5df1\u50cf\u4e2a\u5927\u54e5\u54e5\uff1b\u4f46\u548c\u73ca\u5609\u5728\u4e00\u8d77\uff0c\u5c31\u8981\u5f7b\u5e95\u653e\u677e\u5f97\u591a\uff0c\u611f\u89c9\u81ea\u5df1\u50cf\u4e2a\u5b69\u5b50\u3002</p><p>\u73ca\u5609\u53d6\u4e0b\u4e86\u8033\u9489\uff0c\u5c0f\u5fc3\u5730\u6536\u8d77\u6765\uff0c\u653e\u5728\u6795\u5934\u65c1\u3002</p><p>\u201c\u59d0\u59d0\u5e72\u561b\u4e0d\u6234\u7740\u5b83\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002\u8fd9\u8033\u9489\u662f\u4ed6\u5728\u5b66\u6821\u91cc\u5236\u4f5c\u7684\u51c6\u9b54\u6cd5\u7269\u54c1\uff0c\u867d\u7136\u6ca1\u4ec0\u4e48\u592a\u5927\u80fd\u529b\uff0c\u5374\u80fd\u8ba9\u73ca\u5609\u7684\u8eab\u4f53\u4e00\u76f4\u4fdd\u6301\u6e29\u6696\u3002\u73ca\u5609\u4ece\u5c0f\u4f53\u6e29\u5c31\u6bd4\u5e38\u4eba\u7a0d\u4f4e\u4e9b\uff0c\u76ae\u80a4\u603b\u662f\u51b0\u51b0\u51c9\u51c9\u7684\uff0c\u665a\u4e0a\u7761\u89c9\u90fd\u51b7\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u5979\u8bf4\uff0c\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u6234\u7740\u5b83\uff0c\u5c31\u611f\u89c9\u4e0d\u5230\u4f60\u8eab\u4e0a\u7684\u6e29\u6696\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e0d\u518d\u8bf4\u8bdd\uff0c\u62b1\u7740\u73ca\u5609\uff0c\u4ed6\u62b1\u5f97\u7d27\u7d27\u7684\uff0c\u4ee5\u81f3\u4e8e\u73ca\u5609\u611f\u89c9\u6709\u4e9b\u547c\u5438\u56f0\u96be\u3002\u4f46\u59d0\u59d0\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u9ed8\u9ed8\u5730\u627f\u53d7\u7740\u3002</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u59d0\u59d0\u3002\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u5e72\u561b\u8bf4\u5bf9\u4e0d\u8d77\u554a\uff0c\u201d\u73ca\u5609\u7b11\u7740\uff0c\u7ffb\u8fc7\u8eab\u6765\uff0c\u9762\u5bf9\u7740\u5f1f\u5f1f\uff0c\u201c\u4f60\u505a\u5f97\u5f88\u597d\uff0c\u743c\u6069\uff0c\u8fdc\u8fdc\u8d85\u51fa\u6211\u7684\u671f\u671b\u3002\u7236\u4eb2\u6bcd\u4eb2\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u4f1a\u4e3a\u4f60\u81ea\u8c6a\u7684\u5462\u3002\u201d</p><p>\u201c\u4f60\u662f\u4e2a\u9a84\u50b2\u7684\u4eba\uff0c\u4ece\u5c0f\u5c31\u8fd9\u6837\uff0c\u201d\u59d0\u59d0\u7ee7\u7eed\u8bf4\uff0c\u201c\u867d\u7136\u770b\u4e0a\u53bb\u6709\u70b9\u6f2b\u4e0d\u7ecf\u5fc3\u7684\uff0c\u5176\u5b9e\u81ea\u50b2\u5f97\u5f88\u3002\u4f60\u4e0d\u559c\u6b22\u7684\u4e8b\u60c5\uff0c\u4f60\u4f1a\u4e0d\u5728\u4e4e\uff0c\u65e0\u8bba\u522b\u4eba\u505a\u5f97\u591a\u4e48\u597d\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u5728\u610f\uff1b\u4f46\u5982\u679c\u662f\u4f60\u559c\u6b22\u7684\u4e8b\u60c5\uff0c\u4f60\u770b\u91cd\u7684\u4e8b\u60c5\uff0c\u4f60\u5c31\u4f1a\u5e0c\u671b\u80fd\u505a\u5f97\u6bd4\u4efb\u4f55\u4eba\u90fd\u597d\u5462\u3002\u201d</p><p>\u743c\u6069\u4e0d\u8bf4\u8bdd\uff0c\u628a\u8138\u57cb\u5728\u73ca\u5609\u7684\u80f8\u53e3\u3002</p><p>\u201c\u6700\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u4f60\u60f3\u5f53\u5deb\u5e08\uff0c\u662f\u56e0\u4e3a\u51fa\u4eba\u5934\u5730\u7684\u5e0c\u671b\uff0c\u662f\u56e0\u4e3a\u6211\u4eec\u8981\u4e3a\u7236\u6bcd\u62a5\u4ec7\uff0c\u201d\u73ca\u5609\u8f7b\u629a\u7740\u743c\u6069\u7684\u5934\u53d1\uff0c\u4f4e\u58f0\u8bf4\uff0c\u201c\u90a3\u65f6\u5019\uff0c\u6211\u5e76\u6ca1\u6709\u611f\u89c9\u5230\u4f60\u5bf9\u5deb\u5e08\uff0c\u5bf9\u9b54\u6cd5\uff0c\u6709\u591a\u4e48\u7684\u559c\u7231\u3002\u4f46\u5230\u4e86\u540e\u6765\uff0c\u5c31\u6e10\u6e10\u4e0d\u592a\u4e00\u6837\u4e86\u5462\uff0c\u743c\u6069\uff0c\u4f60\u81ea\u5df1\u90fd\u6ca1\u6709\u53d1\u89c9\u4e48\uff0c\u4f60\u6bcf\u4e2a\u6708\u6708\u672b\u56de\u5bb6\uff0c\u6700\u5f00\u59cb\u53ea\u662f\u8bf4\u4e9b\u5b66\u6821\u91cc\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u540e\u6765\u5c31\u8d8a\u6765\u8d8a\u591a\u5730\u8c08\u8bba\u7740\u9b54\u6cd5\uff0c\u8bf4\u4f60\u7684\u5fc3\u5f97\uff0c\u8bf4\u4f60\u7684\u8fdb\u6b65\u548c\u9886\u609f\u3002\u6bcf\u6b21\u8bf4\u8d77\u8fd9\u4e9b\uff0c\u4f60\u5c31\u7709\u98de\u8272\u821e\uff0c\u7cbe\u795e\u7115\u53d1\uff0c\u90a3\u79cd\u82f1\u6c14\u52c3\u52c3\u795e\u91c7\u98de\u626c\u7684\u6837\u5b50\uff0c\u8fde\u6211\u770b\u4e86\u90fd\u5f88\u7fa1\u6155\u2026\u2026\u90fd\u6709\u4e9b\u5ac9\u5992\u5462\u3002\u201d</p><p>\u201c\u59d0\u59d0\u2026\u2026\u201d</p><p>\u201c\u4e5f\u6ca1\u4ec0\u4e48\u5566\uff0c\u201d\u73ca\u5609\u7b11\u7740\uff0c\u201c\u770b\u5230\u81ea\u5df1\u7684\u5f1f\u5f1f\u90a3\u6837\u610f\u6c14\u98ce\u53d1\uff0c\u603b\u662f\u4f1a\u6709\u4e9b\u7fa1\u6155\u5427\u3002\u80fd\u6709\u81ea\u5df1\u771f\u6b63\u559c\u6b22\u7684\u4e1c\u897f\uff0c\u7f8e\u597d\u6709\u8da3\u7684\u4e1c\u897f\uff0c\u80fd\u6c89\u6d78\u5176\u4e2d\uff0c\u8fd9\u662f\u4ef6\u5f88\u5e78\u798f\u7684\u4e8b\u60c5\u5462\u3002\u5e76\u4e0d\u662f\u6240\u6709\u4eba\uff0c\u90fd\u80fd\u6709\u8fd9\u79cd\u5e78\u798f\u3002\u201d</p><p>\u201c\u90a3\u59d0\u59d0\u7684\u5e78\u798f\u5462\u3002\u201d\u743c\u6069\u4f4e\u58f0\u95ee\u3002</p><p>\u5728\u5fc3\u5e95\uff0c\u4e00\u76f4\u6709\u79cd\u9690\u9690\u7ea6\u7ea6\u7684\u5ff5\u5934\uff0c\u4f46\u4e0d\u613f\u610f\u53bb\u9762\u5bf9\u3002\u81ea\u5df1\u4e3a\u4e86\u81ea\u5df1\u7684\u68a6\u60f3\uff0c\u8fdb\u4e86\u5deb\u5e08\u5b66\u6821\uff0c\u5e26\u8d70\u4e86\u6240\u6709\u7684\u79ef\u84c4\uff0c\u7559\u4e0b\u59d0\u59d0\u4e00\u4e2a\u5341\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u72ec\u81ea\u751f\u6d3b\u3002\u5982\u679c\u2026\u2026\u5982\u679c\u5f53\u5e74\u53bb\u5f53\u5deb\u5e08\u7684\u4e0d\u662f\u81ea\u5df1\uff0c\u800c\u662f\u59d0\u59d0\u5462\uff1f</p><p>\u73ca\u5609\u5f88\u806a\u660e\uff0c\u5982\u679c\u5f53\u5deb\u5e08\uff0c\u4e5f\u80af\u5b9a\u4f1a\u5f88\u51fa\u8272\u5427\u3002\u6216\u8005\u8bf4\uff0c\u50cf\u5979\u8fd9\u6837\u4f18\u79c0\u7684\u5973\u5b69\u5b50\uff0c\u53ea\u8981\u7ed9\u5979\u673a\u4f1a\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u90fd\u4e00\u5b9a\u505a\u5f97\u975e\u5e38\u597d\u5427\u3002</p><p>\u4f46\u56e0\u4e3a\u81ea\u5df1\uff0c\u5979\u53ea\u80fd\u5f53\u4e00\u4e2a\u5c0f\u5546\u4eba\uff0c\u6bcf\u5929\u5de5\u4f5c\u3001\u5f00\u5e97\uff0c\u65e5\u590d\u4e00\u65e5\u505a\u7740\u8fd9\u4e9b\u7e41\u7410\u7684\u4e8b\u60c5\uff0c\u7ef4\u6301\u751f\u8ba1\uff0c\u6708\u672b\u7b49\u5f85\u7740\u81ea\u5df1\u7684\u5f1f\u5f1f\u56de\u5bb6\u3002</p><p>\u662f\u81ea\u5df1\u5265\u593a\u4e86\u59d0\u59d0\u7684\u68a6\u60f3\u4e48\uff1f\u73ca\u5609\uff0c\u4f60\u60f3\u8981\u7684\uff0c\u662f\u4ec0\u4e48\u5462\uff1f</p><p>\u201c\u6211\u73b0\u5728\u5c31\u5f88\u5e78\u798f\u554a\uff0c\u201d\u73ca\u5609\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u5728\u60f3\u4ec0\u4e48\uff0c\u743c\u6069\uff0c\u4f46\u90a3\u4e9b\u90fd\u4e0d\u5b58\u5728\u3002\u6211\u662f\u4e2a\u7b80\u7b80\u5355\u5355\u7684\u5973\u5b69\u5b50\uff0c\u6211\u53ea\u60f3\u8fc7\u7b80\u7b80\u5355\u5355\u7684\u751f\u6d3b\u3002\u4f60\u548c\u6211\u4e0d\u4e00\u6837\uff0c\u4f60\u6709\u68a6\u60f3\uff0c\u4f60\u6709\u91ce\u5fc3\uff0c\u4f60\u4e0d\u4f1a\u7518\u5fc3\u505a\u4e00\u4e2a\u666e\u901a\u4eba\uff0c\u8fc7\u7740\u5e73\u51e1\u7684\u4e00\u751f\uff0c\u4f60\u6ce8\u5b9a\u8981\u6210\u4e3a\u4e3e\u4e16\u77a9\u76ee\u7684\u82f1\u96c4\u3002\u800c\u6211\u4e0d\u540c\uff0c\u743c\u6069\uff0c\u6211\u613f\u610f\u966a\u5728\u4f60\u8eab\u8fb9\uff0c\u613f\u610f\u770b\u7740\u4f60\u610f\u6c14\u98ce\u53d1\u7684\u6837\u5b50\uff0c\u613f\u610f\u770b\u7740\u4f60\u7684\u5149\u8292\u56db\u5c04\uff0c\u613f\u610f\u8fdc\u8fdc\u770b\u7740\u4f60\u5728\u9ad8\u9ad8\u7684\u53f0\u4e0a\uff0c\u8fce\u63a5\u5343\u4e07\u4eba\u7684\u559d\u5f69\uff0c\u613f\u610f\u5915\u9633\u897f\u4e0b\u7684\u508d\u665a\uff0c\u7ad9\u5728\u95e8\u53e3\u7b49\u4f60\u56de\u5bb6\u3002\u201d</p><p>\u201c\u6211\u7684\u68a6\u60f3\uff0c\u4ec5\u4ec5\u5982\u6b64\u3002\u201d</p><p>\u201c\u59d0\u59d0\u2026\u2026\u201d</p><p>\u201c\u5982\u679c\u80fd\u5f97\u5230\u7b2c\u4e00\u540d\uff0c\u90a3\u81ea\u7136\u5f88\u597d\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f46\u65e2\u7136\u6ca1\u6709\u5f97\u5230\uff0c\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u592a\u5927\u5173\u7cfb\u3002\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u6c38\u8fdc\u4e0d\u53ef\u80fd\u603b\u662f\u7b26\u5408\u6211\u4eec\u7684\u5fc3\u610f\uff0c\u53ea\u8981\u66fe\u7ecf\u52aa\u529b\u4e89\u53d6\u8fc7\uff0c\u4e5f\u5c31\u4e0d\u59a8\u7b11\u7740\u8fce\u63a5\u660e\u5929\u3002\u201d</p><p>\u52aa\u529b\u4e86\uff0c\u5c31\u4e0d\u540e\u6094\uff1b\u5931\u8d25\u4e86\uff0c\u5c31\u7b11\u7740\u53bb\u9762\u5bf9\u3002\u660e\u5929\uff0c\u53c8\u662f\u65b0\u7684\u5f00\u59cb\u4e86\u3002</p><p>\u201c\u55ef\u3002\u201d\u743c\u6069\u8ff7\u8ff7\u7cca\u7cca\u5730\u5e94\u7740\u3002</p><p>\u867d\u7136\u73ca\u5609\u7684\u8bdd\uff0c\u542c\u8d77\u6765\u4f3c\u4e4e\u9690\u9690\u6709\u4e9b\u5947\u602a\u2026\u2026\u4e0d\u50cf\u4e00\u4e2a\u59d0\u59d0\u5bf9\u5f1f\u5f1f\u8bf4\u7684\u8bdd\u3002\u4e0d\u8fc7\u743c\u6069\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u4ed6\u7684\u8138\u6df1\u6df1\u57cb\u5728\u73ca\u5609\u7684\u80f8\u53e3\uff0c\u88ab\u90a3\u6e29\u6696\u9999\u751c\u7684\u6c14\u606f\u5305\u56f4\u7740\uff0c\u6e10\u6e10\u5c31\u6709\u4e86\u7761\u610f\u3002</p><p>\u201c\u7761\u5427\uff0c\u4e56\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p></article>"}}}]);
