(window.webpackJsonp=window.webpackJsonp||[]).push([[125],{147:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e03\u8282 \u51fa\u53d1\u5427",content:"<article><h2>\u7b2c\u4e09\u5341\u4e03\u8282 \u51fa\u53d1\u5427</h2><p>\u5f53\u7136\u4e86\uff0c\u90fd\u5df2\u7ecf\u8dd1\u53bb\u5ad6\u5993\u8fd8\u4e0d\u4ed8\u94b1\uff0c\u4e5f\u786e\u5b9e\u662f\u5815\u843d\u4e86\u2026\u2026\u743c\u6069\u6697\u81ea\u5fc3\u91cc\u60f3\uff0c\u5f53\u7136\u8fd9\u8bdd\u4e0d\u80fd\u8bf4\u51fa\u53e3\u3002</p><p>\u4e0d\u8fc7\u8fd9\u6ca1\u5173\u7cfb\uff0c\u9686\u5947\u5815\u843d\u4e0d\u5815\u843d\uff0c\u662f\u4ed6\u81ea\u5df1\u7684\u79c1\u4e8b\u3002\u6885\u83f2\u65af\u6ca1\u5174\u8da3\u53bb\u66ff\u4f0a\u5c14\u739b\u7279\u6559\u4f1a\u6e05\u7406\u95e8\u6237\uff0c\u867d\u8bf4\u5979\u4fe1\u5949\u7684\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u548c\u4f0a\u5c14\u739b\u7279\u7b97\u662f\u76df\u53cb\uff0c\u6559\u4f1a\u5173\u7cfb\u4e0d\u9519\u3002\u743c\u6069\u5c31\u66f4\u52a0\u65e0\u6240\u8c13\u4e86\u3002\u53ea\u662f\u5815\u843d\u800c\u5df2\uff0c\u53c8\u4e0d\u662f\u5c31\u6210\u4e86\u6740\u4eba\u653e\u706b\u7684\u6076\u68cd\uff0c\u8bf4\u4e0d\u5b9a\u53ea\u662f\u5fcd\u53d7\u4e0d\u4e86\u82e6\u884c\u50e7\u7684\u4fee\u884c\uff0c\u60f3\u51fa\u6765\u4eab\u53d7\u751f\u6d3b\u7f62\u4e86\uff0c\u4eba\u4e4b\u5e38\u60c5\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002</p><p>\u800c\u4e14\u5815\u843d\u5f52\u5815\u843d\uff0c\u4ed6\u4ee5\u524d\u523b\u82e6\u4fee\u70bc\u5f97\u6765\u7684\u672c\u4e8b\u53ef\u6ca1\u629b\u4e0b\uff0c\u8fd9\u4e0d\u50cf\u7267\u5e08\u3002\u7267\u5e08\u5982\u679c\u53db\u79bb\u6559\u4f1a\uff0c\u80cc\u5f03\u795e\u7947\uff0c\u81ea\u7136\u4e0d\u4f1a\u518d\u88ab\u795e\u7947\u8d50\u4e88\u4efb\u4f55\u795e\u672f\uff08\u9664\u975e\u4ed6\u53c8\u627e\u5230\u65b0\u7684\u795e\u7947\u6765\u62dc\uff09\uff0c\u5c31\u7b97\u4e0d\u8bf4\u6210\u4e86\u5e9f\u7269\uff0c\u81f3\u5c11\u6218\u6597\u529b\u8981\u524a\u5f31\u4e00\u5927\u534a\u3002\u6b66\u50e7\u7684\u4e00\u8eab\u672c\u4e8b\u5168\u5728\u62f3\u811a\u7b4b\u9aa8\u4e0a\uff0c\u5815\u843d\u867d\u7136\u4e0d\u80fd\u8bf4\u5168\u65e0\u5f71\u54cd\uff08\u6bd4\u5982\u9686\u5947\u7684\u8d2b\u7a77\u8a93\u8a00\u5c31\u81ea\u52a8\u5e9f\u4e86\uff09\uff0c\u4f46\u57fa\u672c\u8fd8\u662f\u80fd\u4fdd\u6301\u539f\u6709\u6c34\u51c6\u7684\u3002</p><p>\u6700\u6700\u5173\u952e\u7684\u4e00\u70b9\u662f\uff0c\u4ed6\u82e6\u4fee\u5f97\u6765\u7684\u90a3\u53d8\u6001\u7684\u62b5\u6297\u9b54\u6cd5\u7684\u80fd\u529b\uff0c\u8fd8\u662f\u7ee7\u7eed\u4fdd\u6301\u7684\u3002\u6b64\u53bb\u5bf9\u4ed8\u62c9\u6c83\u514b\uff0c\u6b63\u8981\u501f\u91cd\u4ed6\u8fd9\u672c\u4e8b\u3002</p><p>\u597d\u5427\uff0c\u5982\u4eca\u5df2\u7ecf\u6709\u4e86\u4e00\u4f4d\u5723\u6b66\u58eb\uff0c\u4e24\u4f4d\u7267\u5e08\uff0c\u4e00\u4f4d\u6b66\u50e7\uff0c\u4ee5\u53ca\u743c\u6069\u8fd9\u4e2a\u5deb\u5e08\uff0c\u4e94\u4eba\u5c0f\u961f\u51d1\u9f50\uff0c\u57fa\u672c\u4e5f\u8be5\u51c6\u5907\u51fa\u53d1\u4e86\u3002</p><p>\u4f46\u662f\u2026\u2026\u6700\u540e\u8fd8\u5c11\u4e00\u4e2a\u4eba\u3002</p><p>\u5c11\u4e00\u4e2a\u9886\u8def\u8005\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u6b21\u7684\u4efb\u52a1\uff0c\u5e76\u4e0d\u662f\u6267\u884c\u4e09\u5149\u653f\u7b56\uff0c\u8f70\u6740\u5de8\u9b54\u5c71\u8109\u91cc\u7684\u5927\u5c0f\u602a\u7269\uff0c\u800c\u662f\u8981\u6084\u6084\u8fdb\u6751\uff0c\u76f4\u5954\u62c9\u6c83\u514b\u7684\u8001\u5de2\uff0c\u5b9e\u884c\u65a9\u9996\u884c\u52a8\uff0c\u5e72\u6389\u8fd9\u53ea\u8001\u5deb\u5996\u2026\u2026\u5f53\u7136\uff0c\u743c\u6069\u662f\u53e6\u6709\u5fc3\u601d\u5c31\u662f\u4e86\u3002</p><p>\u6240\u4ee5\u5f97\u5148\u77e5\u9053\u8def\u5f84\u3002</p><p>\u8389\u62c9\u516c\u7235\u5df2\u7ecf\u6d3e\u4eba\u53bb\u70db\u5821\uff0c\u4ece\u4e4c\u5c14\u5170\u7279\u8001\u5148\u751f\u90a3\u91cc\u5f04\u6765\u4e86\u66f4\u8be6\u7ec6\u7684\u62c9\u6c83\u514b\u7684\u8d44\u6599\uff0c\u5176\u4e2d\u4e5f\u5305\u62ec\u4e00\u5f20\u7b80\u964b\u7684\u5730\u56fe\uff0c\u6807\u51fa\u4e86\u5deb\u5e08\u4e4b\u5893\u7684\u5927\u81f4\u65b9\u4f4d\u3002\u4f46\u95ee\u9898\u662f\uff0c\u4ec5\u6709\u8fd9\u70b9\u4fe1\u606f\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\u3002\u4f60\u4ec0\u4e48\u65f6\u5019\u89c1\u8fc7\u6267\u884c\u65a9\u9996\u884c\u52a8\u7684\u7279\u79cd\u5175\uff0c\u51ed\u4e00\u4e2a\u201c\u5927\u81f4\u65b9\u4f4d\u201d\u5c31\u80fd\u987a\u5229\u627e\u5230\u76ee\u6807\uff0c\u5b8c\u6210\u4efb\u52a1\u3002</p><p>\u5de8\u9b54\u5c71\u8109\u5730\u5f62\u975e\u5e38\u590d\u6742\uff0c\u4ec5\u6709\u4e00\u4e2a\u5927\u81f4\u65b9\u4f4d\uff0c\u53ea\u6015\u627e\u4e0a\u4e00\u5e74\u534a\u8f7d\u4e5f\u5230\u4e0d\u4e86\u76ee\u7684\u5730\uff0c\u4ed6\u4eec\u9700\u8981\u66f4\u8be6\u7ec6\u7684\u5730\u56fe\u2014\u2014\u6700\u597d\u662f\u6d3b\u5730\u56fe\u3002</p><p>\u8fd9\u4e94\u4eba\u4e2d\uff0c\u743c\u6069\u3001\u6885\u83f2\u65af\u3001\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u3001\u6b66\u50e7\u9686\u5947\uff0c\u90fd\u662f\u5916\u6765\u4eba\uff0c\u538b\u6839\u4e0d\u77e5\u9053\u5de8\u9b54\u5c71\u8109\u7684\u8def\u5f84\uff1b\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u8d1d\u7eb3\u7d22\u867d\u7136\u662f\u672c\u5730\u4eba\uff0c\u4f46\u4ed6\u8eab\u4e3a\u6b64\u5730\u795e\u6bbf\u7684\u9996\u5e2d\u7267\u5e08\uff0c\u4f4d\u9ad8\u6743\u91cd\uff0c\u4e8b\u52a1\u7e41\u5fd9\uff0c\u53c8\u600e\u4e48\u53ef\u80fd\u53bb\u8fc7\u5de8\u9b54\u5c71\u8109\u8fd9\u79cd\u9b3c\u5730\u65b9\u3002\u6b64\u6b21\u5982\u679c\u4e0d\u662f\u5927\u516c\u7235\u4eec\u51b3\u8bae\uff0c\u534a\u9080\u8bf7\u534a\u5f3a\u8feb\uff0c\u4ed6\u538b\u6839\u5c31\u4e0d\u4f1a\u53c2\u4e0e\u8fd9\u6b21\u5192\u9669\u3002</p><p>\u663e\u7136\uff0c\u8fd9\u652f\u4e94\u4eba\u5c0f\u961f\uff0c\u5168\u5c5e\u4e8e\u8def\u76f2\uff0c\u9700\u8981\u4e00\u4e2a\u5408\u9002\u7684\u5411\u5bfc\u3002</p><p>\u4f5c\u4e3a\u9ed8\u8ba4\u7684\u961f\u957f\uff0c\u6885\u83f2\u65af\u5411\u8389\u62c9\u516c\u7235\u63d0\u51fa\u4e86\u8fd9\u4e2a\u8981\u6c42\uff0c\u4f46\u540e\u8005\u9762\u6709\u96be\u8272\u3002\u56e0\u4e3a\u5c31\u7b97\u662f\u535a\u5f97\u4e4b\u95e8\u7684\u5c45\u6c11\uff0c\u53ea\u6015\u4e5f\u6ca1\u51e0\u4e2a\u8fdb\u5165\u8fc7\u5de8\u9b54\u5c71\u8109\u7684\u2014\u2014\u6216\u8005\u51c6\u786e\u4e00\u70b9\u8bf4\uff0c\u80fd\u8fdb\u53bb\u8fc7\uff0c\u77e5\u9053\u5927\u81f4\u8def\u5f84\uff0c\u5e76\u4e14\u8fd8\u80fd\u6d3b\u7740\u51fa\u6765\u7684\u4eba\uff0c\u6ca1\u6709\u51e0\u4e2a\u3002\u90a3\u91cc\u5b9e\u5728\u592a\u5371\u9669\u4e86\u3002</p><p>\u8001\u529e\u6cd5\uff0c\u518d\u6b21\u8d34\u51fa\u62db\u52df\u544a\u793a\uff0c\u4f46\u4e00\u5929\u8fc7\u53bb\u4e86\uff0c\u6ca1\u6709\u534a\u4e2a\u4eba\u62a5\u540d\u3002\u6700\u540e\uff0c\u62db\u52df\u544a\u793a\u6ca1\u6709\u8d77\u5230\u4ec0\u4e48\u4f5c\u7528\uff0c\u53cd\u5012\u662f\u4f0a\u5c14\u5766\u516c\u7235\uff0c\u4e5f\u5c31\u662f\u70c8\u7130\u4e4b\u62f3\u7684\u6700\u9ad8\u6307\u6325\u5b98\uff0c\u63a8\u8350\u4e86\u4e2a\u5408\u9002\u7684\u4eba\u9009\uff0c\u662f\u4ed6\u7684\u5c5e\u4e0b\u3002</p><p>\u4e00\u540d\u6e38\u8361\u8005\u3002</p><p>\u6240\u8c13\u6e38\u8361\u8005\uff0c\u8fd9\u662f\u4e00\u79cd\u6bd4\u8f83\u5ba2\u6c14\u7684\u79f0\u547c\uff0c\u540c\u65f6\u4e5f\u662f\u79cd\u542b\u4e49\u975e\u5e38\u5bbd\u6cdb\u7684\u79f0\u547c\u3002\u76d7\u8d3c\u3001\u5bc6\u63a2\u3001\u6740\u624b\u3001\u95f4\u8c0d\u3001\u8d4f\u91d1\u730e\u4eba\uff0c\u751a\u81f3\u6d41\u6d6a\u6076\u68cd\u7b49\u7b49\uff0c\u90fd\u53ef\u4ee5\u88ab\u901a\u79f0\u4e3a\u201c\u6e38\u8361\u8005\u201d\u3002\u5982\u679c\u67d0\u4e2a\u4eba\u81ea\u6211\u4ecb\u7ecd\u662f\u5deb\u5e08\uff0c\u90a3\u4e48\u65e0\u7591\u4ed6\u6240\u64c5\u957f\u7684\u662f\u65bd\u6cd5\uff1b\u5982\u679c\u67d0\u4e2a\u4eba\u81ea\u79f0\u662f\u541f\u6e38\u8bd7\u4eba\uff0c\u90a3\u4e48\u65e0\u7591\u4ed6\u6240\u64c5\u957f\u7684\u662f\u5f39\u594f\u4e50\u5668\u5531\u82f1\u96c4\u53f2\u8bd7\u6216\u8005\u6c11\u6b4c\uff0c\u8fd9\u4e9b\u5927\u4f53\u90fd\u662f\u53ef\u4ee5\u9884\u6599\u5230\u7684\u3002\u4f46\u5982\u679c\u67d0\u4e2a\u4eba\u81ea\u79f0\u662f\u201c\u6e38\u8361\u8005\u201d\uff0c\u90a3\u4e48\u4ed6\u5f88\u53ef\u80fd\u662f\u4e2a\u7cbe\u64c5\u9690\u9041\u6697\u6740\u7684\u6740\u624b\uff0c\u4e5f\u53ef\u80fd\u662f\u4e2a\u98de\u6a90\u8d70\u58c1\u7684\u7a83\u8d3c\uff0c\u8fd8\u53ef\u80fd\u662f\u4e2a\u6210\u5929\u6df7\u8ff9\u9152\u9986\uff0c\u773c\u89c2\u56db\u8def\u8033\u542c\u516b\u65b9\u7684\u516b\u5366\u6d88\u606f\u7231\u597d\u8005\uff1b\u4ed6\u6709\u53ef\u80fd\u7cbe\u901a\u5b89\u88c5\u62c6\u5378\u5404\u79cd\u9677\u9631\u673a\u5173\uff0c\u4e5f\u6709\u53ef\u80fd\u64c5\u957f\u8c03\u914d\u5404\u79cd\u6bd2\u836f\uff0c\u8fd8\u6709\u53ef\u80fd\u662f\u4e2a\u6613\u5bb9\u5316\u88c5\u9ad8\u624b\uff0c\u6216\u8005\u6837\u6837\u90fd\u61c2\u70b9\u3002</p><p>\u4e00\u4e2a\u5c0f\u5077\u53ef\u4ee5\u7b97\u662f\u6e38\u8361\u8005\uff0c\u4e00\u4e2a\u5bc6\u63a2\u53ef\u4ee5\u7b97\u662f\u6e38\u8361\u8005\uff0c\u70db\u5821\u649e\u89c1\u7684\u90a3\u4e2a\u5973\u6740\u624b\uff0c\u4e5f\u53ef\u4ee5\u7b97\u662f\u6e38\u8361\u8005\uff0c\u751a\u81f3\u7530\u4f2f\u5149\u8fd9\u79cd\u91c7\u82b1\u8d3c\uff0c\u4e5f\u53ef\u4ee5\u7b97\u662f\u6e38\u8361\u8005\u3002\u8fd9\u662f\u4e2a\u975e\u5e38\u5bbd\u6cdb\u7684\u6982\u5ff5\uff0c\u5bbd\u6cdb\u5230\u4f60\u6ca1\u529e\u6cd5\u76f4\u63a5\u77e5\u9053\u5bf9\u65b9\u5230\u5e95\u64c5\u957f\u4ec0\u4e48\u3002</p><p>\u6240\u4ee5\uff0c\u5f53\u6885\u83f2\u65af\u770b\u7740\u9762\u524d\u8fd9\u4f4d\u201c\u6e38\u8361\u8005\u201d\u7684\u65f6\u5019\uff0c\u9664\u4e86\u80fd\u4ece\u6709\u4e9b\u5c16\u957f\u7684\u8033\u6735\u548c\u7eff\u8272\u77b3\u5b54\u5224\u65ad\u51fa\u4ed6\u662f\u4e2a\u534a\u7cbe\u7075\u5916\uff0c\u5176\u4ed6\u65b9\u9762\u4f9d\u65e7\u8fd8\u662f\u4e00\u65e0\u6240\u77e5\u3002</p><p>\u201c\u9547\u8000\xb7\u590f\u62c9\u975e\uff0c\u201d\u534a\u7cbe\u7075\u6e38\u8361\u8005\u81ea\u6211\u4ecb\u7ecd\u7740\uff0c\u6df1\u6df1\u884c\u793c\uff0c\u201c\u70c8\u7130\u4e4b\u62f3\u7b2c\u4e09\u5206\u961f\u526f\u961f\u957f\uff0c\u5949\u4f0a\u5c14\u5766\u9601\u4e0b\u4e4b\u547d\u524d\u6765\uff0c\u542c\u5019\u60a8\u7684\u5dee\u9063\u3002\u201d</p><p>\u7b80\u5355\u7684\u4ea4\u8c08\u4e4b\u540e\uff0c\u6885\u83f2\u65af\u5f97\u77e5\uff0c\u8fd9\u4f4d\u9547\u8000\xb7\u590f\u62c9\u975e\uff0c\u5916\u53f7\u201c\u5bc2\u9759\u4e4b\u6b65\u201d\uff0c\u6700\u64c5\u957f\u7684\u662f\u9690\u9041\u6f5c\u884c\uff0c\u57cb\u4f0f\u8eb2\u85cf\uff0c\u5bf9\u53d1\u73b0\u3001\u62c6\u9664\u9677\u9631\u4e5f\u9887\u6709\u5fc3\u5f97\uff0c\u81f3\u4e8e\u98de\u6a90\u8d70\u58c1\u3001\u5f00\u9501\u64ac\u95e8\uff0c\u5219\u5c31\u975e\u5176\u6240\u957f\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u61c2\u4e00\u70b9\u9b54\u6cd5\uff0c\u636e\u8bf4\u662f\u66fe\u88ab\u4e00\u4f4d\u8001\u5deb\u5e08\u6536\u4e3a\u95e8\u4e0b\uff0c\u5b66\u8fc7\u51e0\u5e74\uff0c\u52c9\u5f3a\u4e5f\u80fd\u7b97\u662f\u4e2a\u521d\u7ea7\u5deb\u5e08\u3002</p><p>\u6700\u6700\u5173\u952e\u7684\u662f\uff0c\u4ed6\u66fe\u7ecf\u51e0\u6b21\u8fdb\u5165\u8fc7\u5de8\u9b54\u5c71\u8109\uff0c\u5bf9\u91cc\u9762\u7684\u5730\u5f62\u8def\u5f84\u9887\u4e3a\u719f\u6089\u3002</p><p>\u90a3\u4e48\u2026\u2026\u4e00\u5207\u5c31\u6ca1\u95ee\u9898\u4e86\uff0c\u51fa\u53d1\u5427\u3002</p><p>\u203b\u203b\u203b</p><p>\u51fa\u53d1\u4e4b\u524d\uff0c\u81ea\u7136\u662f\u8981\u505a\u5145\u8db3\u7684\u51c6\u5907\u5de5\u4f5c\u7684\uff0c\u8fd9\u6b21\u4e0d\u662f\u8fdb\u5c71\u6e38\u73a9\uff0c\u662f\u53bb\u5bf9\u4ed8\u4e00\u53ea\u5deb\u5996\u5462\uff0c\u800c\u4e14\u6839\u636e\u590f\u62c9\u975e\u7684\u8bf4\u6cd5\uff0c\u6ca1\u6709\u4e09\u4e94\u5929\u662f\u4e0d\u53ef\u80fd\u627e\u5230\u5deb\u5e08\u4e4b\u5893\u7684\u3002\u98df\u7269\u3001\u7761\u888b\u3001\u836f\u6c34\u3001\u5f13\u5f29\u5f39\u836f\u7b49\u7b49\u90fd\u662f\u8981\u63d0\u524d\u51c6\u5907\u7684\u3002</p><p>\u6885\u83f2\u65af\u6700\u7b80\u5355\uff0c\u4e0d\u8fc7\u4e00\u5251\u4e00\u7532\uff0c\u522b\u65e0\u5176\u4ed6\uff0c\u5979\u7684\u9a91\u672f\u751a\u4f73\uff0c\u5e73\u65f6\u51fa\u95e8\u90fd\u662f\u4f1a\u5907\u9a6c\u5339\uff0c\u4f46\u8fd9\u6b21\u8981\u53bb\u5de8\u9b54\u5c71\u8109\uff0c\u90a3\u91cc\u5730\u5f62\u590d\u6742\uff0c\u6839\u672c\u4e0d\u671b\u9a91\u9a6c\uff0c\u53ea\u80fd\u6b65\u884c\u3002</p><p>\u535a\u5f97\u4e4b\u95e8\u6709\u5170\u68ee\u5fb7\u5c14\u548c\u6c83\u91d1\u7684\u795e\u6bbf\uff0c\u4e24\u4e2a\u7267\u5e08\uff0c\u8d1d\u7eb3\u7d22\u548c\u79d1\u6069\uff0c\u5206\u522b\u5230\u795e\u6bbf\u53bb\u8bf7\u5404\u81ea\u7684\u6559\u53cb\u76f8\u52a9\uff0c\u5236\u4f5c\u4e86\u4e00\u5927\u7968\u795e\u672f\u5377\u8f74\uff0c\u88c5\u4e86\u6574\u6574\u4e24\u53e3\u888b\uff0c\u51c6\u5907\u5728\u8def\u4e0a\u4f7f\u7528\u3002\u8fd9\u9635\u4ed7\u628a\u743c\u6069\u90fd\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u4ed6\u6ca1\u60f3\u8fc7\u771f\u6709\u4eba\u625b\u7740\u6574\u888b\u6574\u888b\u7684\u5377\u8f74\u53bb\u5192\u9669\u6253\u67b6\u7684\uff0c\u5982\u4eca\u7b97\u662f\u89c1\u8bc6\u5230\u4e86\u3002</p><p>\u4e00\u4e2a\u662f\u672c\u5730\u795e\u6bbf\u7684\u9996\u5e2d\u7267\u5e08\uff0c\u4e00\u4e2a\u662f\u4ee5\u8c6a\u5bcc\u9614\u7ef0\u8457\u79f0\u7684\u6c83\u91d1\u7267\u5e08\uff0c\u679c\u7136\u51fa\u624b\u6c14\u6d3e\u4e0d\u51e1\u3002</p><p>\u81f3\u4e8e\u6b66\u50e7\u9686\u5947\uff0c\u4ed6\u7684\u51c6\u5907\u5c31\u7b80\u5355\u591a\u4e86\u3002\u867d\u7136\u8d2b\u7a77\u8a93\u8a00\u65e9\u5c31\u88ab\u4ed6\u80cc\u5f03\u4e86\uff0c\u4f46\u6b66\u50e7\u539f\u672c\u5c31\u4e0d\u5fc5\u4e5f\u4e0d\u5b9c\u501f\u52a9\u5916\u7269\uff0c\u4ed6\u7684\u4e00\u8eab\u672c\u4e8b\uff0c\u5168\u90fd\u5728\u62f3\u811a\u4e0a\uff0c\u5982\u679c\u7ed9\u4ed6\u914d\u4e00\u628a\u5251\uff0c\u53cd\u800c\u4f1a\u788d\u624b\u788d\u811a\uff0c\u5982\u679c\u7ed9\u4ed6\u7a7f\u4e2a\u76d4\u7532\uff0c\u53cd\u800c\u6d3b\u52a8\u4e0d\u7075\uff1b\u4ed6\u53c8\u4e0d\u4f1a\u9b54\u6cd5\uff0c\u5c31\u7b97\u585e\u4e00\u5806\u5377\u8f74\u7ed9\u4ed6\u4e5f\u6ca1\u6cd5\u7528\u3002\u5f53\u65e5\u707f\u91d1\u4e4b\u5bb6\uff0c\u4ed6\u4f7f\u7528\u7684\u90a3\u79cd\u7c7b\u4f3c\u4efb\u610f\u95e8\u7684\u80fd\u529b\uff0c\u5176\u5b9e\u662f\u6b66\u50e7\u7684\u4e00\u79cd\u72ec\u95e8\u79d8\u672f\uff0c\u7c7b\u4f3c\u4e8e\u5fcd\u8005\u7684\u9041\u672f\uff0c\u5e76\u4e0d\u662f\u771f\u7684\u9b54\u6cd5\u3002</p><p>\u4ed6\u4ece\u8389\u62c9\u516c\u7235\u91cc\u63d0\u524d\u62ff\u5230\u7684\u4e00\u534a\u62a5\u916c\uff0c\u5168\u90fd\u88ab\u82b1\u5728\u5403\u559d\u73a9\u4e50\u4e0a\u53bb\u4e86\uff0c\u77ed\u77ed\u4e24\u4e09\u5929\uff0c\u4ed6\u5c31\u5df2\u7ecf\u51e0\u4e4e\u628a\u535a\u5f97\u4e4b\u95e8\u6240\u6709\u7684\u9ad8\u6863\u5a31\u4e50\u573a\u6240\u5168\u90fd\u901b\u4e86\u4e00\u904d\u3002\u4ece\u8fd9\u70b9\u6765\u770b\uff0c\u743c\u6069\u7684\u731c\u6d4b\u5e94\u8be5\u6ca1\u9519\uff0c\u8fd9\u4e2a\u6b66\u50e7\u5341\u6709\u516b\u4e5d\u662f\u56e0\u4e3a\u8010\u4e0d\u4f4f\u4fee\u9053\u9662\u7684\u82e6\u4fee\u751f\u6d3b\uff0c\u8dd1\u51fa\u6765\u4f53\u9a8c\u5916\u9762\u8fd9\u82b1\u82b1\u4e16\u754c\u4e86\u3002</p><p>\u81f3\u4e8e\u534a\u7cbe\u7075\u6e38\u8361\u8005\u590f\u62c9\u975e\uff0c\u539f\u672c\u5c31\u662f\u70c8\u7130\u4e4b\u62f3\u7684\u9ad8\u9636\u519b\u5b98\uff0c\u4e00\u8eab\u88c5\u5907\u65e9\u5c31\u81ea\u5df1\u914d\u5907\u6574\u9f50\uff0c\u65e0\u9700\u64cd\u5fc3\u3002</p><p>\u552f\u4e00\u6bd4\u8f83\u90c1\u95f7\u7684\uff0c\u5c31\u662f\u961f\u4f0d\u91cc\u552f\u4e00\u7684\u5deb\u5e08\u743c\u6069\u3002</p><p>\u56e0\u4e3a\u535a\u5f97\u4e4b\u95e8\u8fd9\u5730\u65b9\u57fa\u672c\u6ca1\u6709\u5deb\u5e08\uff0c\u6240\u4ee5\u4ed6\u538b\u6839\u4e70\u4e0d\u5230\u4ec0\u4e48\u5deb\u5e08\u53ef\u7528\u7684\u88c5\u5907\u3002\u4ed6\u4e5f\u60f3\u548c\u90a3\u4e24\u4e2a\u7267\u5e08\u4e00\u6837\uff0c\u80cc\u7740\u4e00\u53e3\u888b\u7684\u5377\u8f74\u51fa\u95e8\uff0c\u4f46\u95ee\u9898\u662f\u5f04\u4e0d\u5230\u3002\u7267\u5e08\u4f7f\u7528\u7684\u795e\u672f\u5377\u8f74\uff0c\u548c\u5deb\u5e08\u4f7f\u7528\u7684\u5965\u672f\u5377\u8f74\uff0c\u4ece\u5916\u8868\u4e0a\u770b\u8d77\u6765\u90fd\u4e0d\u8fc7\u662f\u4e00\u5f20\u7f8a\u76ae\u7eb8\uff0c\u6ca1\u4ec0\u4e48\u533a\u522b\uff0c\u5176\u5b9e\u5927\u76f8\u5f84\u5ead\uff0c\u4e5f\u6ca1\u6cd5\u901a\u7528\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u53e6\u5916\u4e00\u4ef6\u4e8b\u60c5\u4e5f\u4ee4\u4ed6\u4e0d\u723d\u3002</p><p>\u5728\u51fa\u53d1\u4e4b\u524d\uff0c\u4ed6\u53c8\u6084\u6084\u53bb\u8fc7\u707f\u91d1\u4e4b\u5bb6\uff0c\u8fd9\u6b21\u6709\u4e86\u7ecf\u9a8c\uff0c\u518d\u4e0d\u4f1a\u50cf\u4e0a\u56de\u90a3\u6837\uff0c\u50bb\u4e4e\u4e4e\u5730\u8dd1\u5230\u201c\u94dc\u95e8\u201d\u91cc\u53bb\u3002\u5fc5\u987b\u8bf4\uff0c\u707f\u91d1\u4e4b\u5bb6\u4f5c\u4e3a\u535a\u5f97\u4e4b\u95e8\u7b2c\u4e00\u5a31\u4e50\u573a\u6240\uff0c\u7684\u786e\u662f\u540d\u526f\u5176\u5b9e\u7684\uff0c\u522b\u7684\u4e0d\u8bba\uff0c\u81f3\u5c11\u8fd9\u51e0\u6b21\u743c\u6069\u6240\u89c1\u5230\u7684\u5973\u5b50\uff0c\u8d28\u91cf\u8f83\u4e4b\u4e0a\u6b21\u597d\u5f97\u591a\uff0c\u867d\u7136\u8fd8\u8bf4\u4e0d\u4e0a\u7edd\u8272\u4f73\u4e3d\uff0c\u4f46\u4e5f\u90fd\u80fd\u79f0\u5f97\u4e0a\u4f18\u8d28\u4e0a\u7b49\u4e86\u3002\u8bba\u5bb9\u8c8c\u7f8e\u8272\uff0c\u6216\u8bb8\u6bd4\u73ca\u5609\u6216\u8005\u6885\u83f2\u65af\u900a\u8272\u4e00\u4e9b\uff0c\u4f46\u5374\u90fd\u662f\u4e45\u7ecf\u9635\u4ed7\uff0c\u5e8a\u4e0a\u529f\u592b\u4e00\u6d41\uff0c\u4f8d\u5949\u8d77\u7537\u4eba\u6765\u5c3d\u5fc3\u5c3d\u804c\u3002\u743c\u6069\u6b63\u597d\u501f\u6b64\u673a\u4f1a\uff0c\u7406\u8bba\u7ed3\u5408\u5b9e\u9645\uff0c\u5c06\u4ece\u7530\u4f2f\u5149\u90a3\u91cc\u5b66\u6765\u7684\u5404\u79cd\u7406\u8bba\u77e5\u8bc6\u4ed8\u8bf8\u5b9e\u8df5\uff0c\u5728\u8fd9\u4e9b\u5973\u5b50\u8eab\u4e0a\u4e00\u4e00\u5c1d\u8bd5\uff0c\u4e0d\u4ea6\u4e50\u4e4e\u3002</p><p>\u4f46\u4ed6\u5e76\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\u7684\u771f\u6b63\u76ee\u7684\u3002</p><p>\u4e0a\u6b21\u5728\u70db\u5821\uff0c\u8bef\u6253\u8bef\u649e\u4e4b\u4e0b\uff0c\u4ed6\u7528\u91c7\u7389\u8bc0\u4ece\u5973\u6740\u624b\u838e\u73de\u514b\u4f53\u5185\u5438\u6765\u4e86\u4e00\u70b9\u9634\u5bd2\uff0c\u83ab\u540d\u5176\u5999\u5730\uff0c\u5c45\u7136\u771f\u80fd\u8fd0\u529f\u5316\u89e3\u5f00\u6765\uff0c\u611f\u89c9\u81ea\u8eab\u5185\u606f\u9690\u9690\u6709\u589e\u5f3a\u7684\u8d8b\u5411\u3002\u8fd9\u4e2a\u610f\u5916\u53d1\u73b0\u4ee4\u4ed6\u65e2\u60ca\u4e14\u559c\uff0c\u4e0d\u660e\u6240\u4ee5\u3002</p><p>\u7530\u4f2f\u5149\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u91c7\u7389\u8bc0\u5c31\u662f\u7528\u6765\u5438\u5185\u529b\u7684\uff0c\u901a\u8fc7\u4ea4\u5408\u4ece\u5973\u5b50\u4f53\u5185\u5438\u53d6\u5185\u529b\uff0c\u5316\u5f52\u81ea\u8eab\uff0c\u5bf9\u6b64\u743c\u6069\u6ca1\u4ec0\u4e48\u4e0d\u660e\u767d\u7684\uff0c\u4e0d\u5c31\u662f\u6deb\u8361\u7248\u7684\u5438\u661f\u5927\u6cd5\u6216\u8005\u5317\u6e9f\u795e\u529f\u4e48\uff0c\u5f88\u597d\u7406\u89e3\u2014\u2014\u4f46\u70db\u5821\u7684\u4e8b\u60c5\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u4e24\u79cd\u53ef\u80fd\u3002\u6216\u8005\uff0c\u5973\u6740\u624b\u4e5f\u50cf\u743c\u6069\u4e00\u6837\uff0c\u4fee\u70bc\u8fc7\u5185\u529f\uff0c\u88ab\u743c\u6069\u5438\u4e86\u8fc7\u6765\uff1b\u6216\u8005\uff0c\u5c31\u662f\u91c7\u7389\u8bc0\u7684\u529f\u7528\u4e0d\u4ec5\u4ec5\u53ea\u6709\u7530\u4f2f\u5149\u8bf4\u7684\u5438\u53d6\u5185\u529b\u8fd9\u4e00\u79cd\uff0c\u5b83\u8fd8\u53e6\u6709\u5999\u7528\u3002</p><p>\u743c\u6069\u4e0d\u592a\u76f8\u4fe1\u5973\u6740\u624b\u8eab\u5c45\u5185\u529f\u3002\u4ed6\u81ea\u5df1\u4e5f\u7ec3\u8fc7\u8fd9\u4e48\u591a\u5e74\u5185\u529f\uff0c\u867d\u7136\u4e0d\u6210\u6c14\u5019\uff0c\u4f46\u57fa\u672c\u7684\u773c\u5149\u603b\u8fd8\u6709\u3002\u4e00\u4e2a\u4eba\u6709\u6ca1\u6709\u7ec3\u8fc7\u5185\u529f\uff0c\u666e\u901a\u4eba\u81ea\u7136\u770b\u4e0d\u51fa\u6765\uff0c\u743c\u6069\u8fd9\u79cd\u534a\u5185\u884c\uff0c\u8fd8\u662f\u80fd\u7422\u78e8\u51fa\u5176\u4e2d\u5dee\u522b\u7684\u3002\u8fd9\u51e0\u5929\u6765\uff0c\u4ed6\u8111\u4e2d\u53cd\u590d\u56de\u5fc6\u548c\u5973\u6740\u624b\u6253\u4ea4\u9053\u7684\u4e00\u5e55\u5e55\u573a\u666f\uff0c\u4e00\u904d\u4e00\u904d\u5730\u5206\u6790\u5979\u7684\u6bcf\u4e2a\u52a8\u4f5c\uff0c\u6700\u540e\u5f97\u51fa\u6765\u7684\u7ed3\u8bba\u662f\uff1a\u5973\u6740\u624b\u5e94\u8be5\u6ca1\u6709\u5185\u529f\u3002</p><p>\u5982\u679c\u8fd9\u4e00\u6761\u88ab\u57fa\u672c\u5426\u5b9a\uff0c\u90a3\u4e48\u5c31\u53ea\u5269\u4e0b\u53e6\u5916\u4e00\u79cd\u53ef\u80fd\u6027\u6bd4\u8f83\u5927\uff1a\u91c7\u7389\u8bc0\u4e0d\u4ec5\u4ec5\u53ea\u6709\u5438\u5185\u529b\u8fd9\u4e00\u70b9\uff0c\u5b83\u8fd8\u53e6\u6709\u5176\u4ed6\u7528\u9014\u3002</p><p>\u5177\u4f53\u60c5\u51b5\uff0c\u7530\u4f2f\u5149\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6ca1\u6cd5\u518d\u53bb\u95ee\u4ed6\u2014\u2014\u6216\u8005\u8bf4\uff0c\u8001\u5bb6\u4f19\u8bf4\u4e0d\u5b9a\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002\u743c\u6069\u53ea\u80fd\u81ea\u5df1\u53bb\u6478\u7d22\u7814\u7a76\uff0c\u8fd9\u5c31\u662f\u4ed6\u6765\u707f\u91d1\u4e4b\u5bb6\u7684\u771f\u6b63\u76ee\u7684\u2026\u2026\u597d\u5427\uff0c\u662f\u76ee\u7684\u4e4b\u4e00\u3002</p><p>\u4ed6\u6253\u7b97\u5728\u8fd9\u4e9b\u5993\u5973\u8eab\u4e0a\u8bd5\u9a8c\u4e00\u4e0b\u91c7\u7389\u8bc0\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u7422\u78e8\u51fa\u4ec0\u4e48\u4e1c\u897f\u6765\u3002\u5f88\u9057\u61be\uff0c\u4ed6\u4e00\u65e0\u6240\u83b7\uff0c\u65bd\u5c55\u91c7\u7389\u8bc0\u7684\u552f\u4e00\u7ed3\u679c\uff0c\u5c31\u662f\u6bcf\u4e2a\u548c\u4ed6\u4ea4\u5408\u7684\u5973\u4eba\u90fd\u4f1a\u88ab\u5e72\u5230\u6709\u6c14\u65e0\u529b\uff0c\u4eff\u4f5b\u5927\u75c5\u4e00\u573a\uff0c\u800c\u4ed6\u9664\u4e86\u4e00\u70b9\u6709\u9650\u7684\u5973\u6027\u771f\u9634\uff0c\u4ec0\u4e48\u90fd\u6ca1\u5438\u5230\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728\u8fd9\u4e9b\u5993\u5973\u8eab\u4e0a\uff0c\u91c7\u7389\u8bc0\u4e0d\u8fc7\u5c31\u662f\u9738\u9053\u4e00\u4e9b\u7684\u9634\u9633\u91c7\u8865\u4e4b\u672f\uff0c\u6ca1\u4ec0\u4e48\u7279\u522b\u6548\u7528\u3002</p><p>\u723d\u662f\u723d\u4e86\uff0c\u4f46\u6ca1\u80fd\u53d1\u73b0\u91c7\u7389\u8bc0\u7684\u66f4\u591a\u5965\u5999\uff0c\u603b\u662f\u4e0d\u514d\u9057\u61be\u3002\u53ef\u60dc\u5973\u6740\u624b\u88ab\u6d3b\u57cb\u5728\u70db\u5821\u5730\u4e0b\uff0c\u548c\u94f6\u9f99\u5e7d\u7075\u4f5c\u4f34\u53bb\u4e86\uff0c\u5426\u5219\u5012\u8fd8\u6709\u5e0c\u671b\u6293\u8fc7\u6765\u7814\u7a76\u7814\u7a76\u3002</p><p>\u4e0d\u8fc7\uff0c\u4e5f\u4e0d\u662f\u5b8c\u5168\u7684\u4ee4\u4eba\u6cae\u4e27\uff0c\u81f3\u5c11\u6709\u4e00\u4ef6\u4e8b\uff0c\u8fd8\u662f\u4ee4\u4ed6\u5f88\u5f00\u5fc3\u7684\u3002</p><p>\u7ecf\u8fc7\u4e0d\u61c8\u52aa\u529b\uff0c\u5728\u51fa\u53d1\u7684\u524d\u4e00\u665a\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u7ec8\u4e8e\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u56db\u5c42\u4e86\u3002</p></article>"}}}]);
