(window.webpackJsonp=window.webpackJsonp||[]).push([[1250],{1273:function(p){p.exports={chapterTitle:null,title:"\u5e8f\u7ae0 \u6df1\u6e0a\u4e2d\u7684\u6765\u5ba2\uff08\u4e0b\uff09",content:"<article><h2>\u5e8f\u7ae0 \u6df1\u6e0a\u4e2d\u7684\u6765\u5ba2\uff08\u4e0b\uff09</h2><p>\u591a\u745e\u68ee\u6700\u5927\u7684\u7231\u597d\u662f\u5403\uff0c\u5bf9\u4e8e\u6536\u85cf\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u5174\u8da3\u3002\u4e0d\u8fc7\u4f5c\u4e3a\u4e00\u4e2a\u5b58\u5728\u4e86\u6570\u5343\u5e74\u7684\u6076\u9b54\u9886\u4e3b\uff0c\u4ed6\u591a\u591a\u5c11\u5c11\u603b\u6709\u4e9b\u79ef\u84c4\u3002</p><p>\u65e0\u5c3d\u6df1\u6e0a\u91cc\u6ca1\u6709\u4efb\u4f55\u8d27\u5e01\u4f53\u7cfb\u548c\u5546\u4e1a\u6d3b\u52a8\uff0c\u6240\u4ee5\u91d1\u94f6\u7b49\u8d35\u91d1\u5c5e\u5728\u8fd9\u91cc\u662f\u4e00\u6587\u4e0d\u503c\uff0c\u7b49\u540c\u5e9f\u7269\uff0c\u591a\u745e\u68ee\u4e5f\u4e0d\u5c51\u4e8e\u7406\u4f1a\u3002\u4ed6\u7684\u85cf\u5b9d\u5e93\u91cc\uff0c\u5927\u591a\u90fd\u662f\u4e9b\u9aa8\u9abc\u6240\u5236\u7684\u827a\u672f\u54c1\uff0c\u5c11\u91cf\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u4ee5\u53ca\u67d0\u4e9b\u4ed6\u89c9\u5f97\u53ef\u80fd\u6709\u4ef7\u503c\uff0c\u4f46\u4e00\u65f6\u8fd8\u6ca1\u7814\u7a76\u660e\u767d\u7684\u4e1c\u897f\u3002</p><p>\u4ed6\u7684\u85cf\u5b9d\u5e93\u6df1\u6df1\u5efa\u5728\u5730\u4e0b\uff0c\u9700\u8981\u901a\u8fc7\u873f\u8712\u66f2\u6298\u800c\u72ed\u957f\u7684\u901a\u9053\uff0c\u5165\u53e3\u8bbe\u8ba1\u5f97\u975e\u5e38\u9690\u853d\uff0c\u9664\u4e86\u591a\u745e\u68ee\u4e4b\u5916\uff0c\u6ca1\u6709\u4efb\u4f55\u5176\u4ed6\u4eba\u77e5\u9053\uff0c\u5305\u62ec\u4ed6\u624b\u4e0b\u7684\u98df\u5c38\u9b3c\u3002</p><p>\u867d\u7136\u5f88\u5947\u602a\u6b27\u51ef\u5c06\u519b\u4e3a\u4f55\u63d0\u51fa\u8981\u5230\u4ed6\u7684\u85cf\u5b9d\u5e93\u91cc\u53bb\u770b\u770b\uff0c\u4f46\u591a\u745e\u68ee\u663e\u7136\u4e0d\u6253\u7b97\u62d2\u7edd\u3002\u5982\u679c\u662f\u5965\u5580\u65af\u6216\u8005\u6b27\u51ef\u770b\u4e2d\u4e86\u4ed6\u7684\u67d0\u4ef6\u6536\u85cf\u54c1\uff0c\u90a3\u4e48\u5c31\u8ba9\u4ed6\u62ff\u8d70\u5427\u3002\u5bf9\u4e8e\u591a\u745e\u68ee\u6765\u8bf4\uff0c\u751f\u547d\u4e2d\u6700\u91cd\u8981\u7684\u610f\u4e49\u5728\u4e8e\u5403\uff0c\u53ea\u8981\u4e0d\u5265\u593a\u4ed6\u7684\u98df\u7269\u548c\u4ed6\u8fdb\u98df\u7684\u80fd\u529b\uff0c\u90a3\u4e48\u4e00\u5207\u597d\u8bf4\u3002\u4ed6\u5e76\u4e0d\u662f\u4e2a\u5b88\u8d22\u5974\u3002</p><p>\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u591a\u745e\u68ee\u771f\u53ef\u4ee5\u79f0\u5f97\u4e0a\u662f\u7231\u597d\u5355\u7eaf\u3002</p><p>\u201c\u8bf7\u8fd9\u8fb9\u8d70\u3002\u201d\u591a\u745e\u68ee\u8eac\u8eab\uff0c\u5bf9\u6b27\u51ef\u505a\u4e86\u4e2a\u8bf7\u7684\u624b\u52bf\uff0c\u7136\u540e\u81ea\u5df1\u5f53\u5148\u94bb\u4e0b\u901a\u9053\u3002\u6ca1\u6709\u6076\u9b54\u559c\u6b22\u628a\u540e\u80cc\u66b4\u9732\u7ed9\u522b\u4eba\uff0c\u80fd\u8d70\u5728\u540e\u9762\uff0c\u8fd9\u662f\u5f3a\u8005\u7684\u6743\u5229\u3002</p><p>\u4e24\u4f4d\u9ad8\u9636\u6076\u9b54\u4e00\u524d\u4e00\u540e\u5730\u5728\u901a\u9053\u4e2d\u7f13\u7f13\u524d\u8fdb\uff0c\u4fdd\u6301\u7740\u56fa\u5b9a\u7684\u8ddd\u79bb\u3002\u5927\u7ea6\u8fc7\u4e86\u5341\u591a\u5206\u949f\uff0c\u4ed6\u4eec\u5728\u4e00\u6247\u9aa8\u9abc\u7f16\u7ec7\u6210\u7684\u95e8\u524d\u505c\u4e86\u4e0b\u6765\u3002</p><p>\u95e8\u7684\u6b63\u4e2d\u4f4d\u7f6e\uff0c\u6709\u4e00\u4e2a\u9ab7\u9ac5\u5934\u9aa8\uff0c\u4ece\u5934\u4e0a\u7684\u89d2\u5224\u65ad\uff0c\u5b83\u751f\u524d\u5e94\u8be5\u5c5e\u4e8e\u4e00\u53ea\u72c2\u6218\u9b54\u3002</p><p>\u201c\u5230\u4e86\uff0c\u6b27\u51ef\u5c06\u519b\uff0c\u8bf7\u7a0d\u7b49\u3002\u201d\u591a\u745e\u68ee\u8bf4\uff0c\u4ece\u534e\u4e3d\u7684\u767d\u888d\u4e0b\u53d6\u51fa\u4e00\u5757\u5e72\u67af\u7684\u9ed1\u8272\u8089\u5757\uff0c\u585e\u8fdb\u9ab7\u9ac5\u5934\u9aa8\u5f20\u5f00\u7684\u5634\u91cc\u3002</p><p>\u5934\u9aa8\u5494\u5494\u5730\u6d3b\u52a8\u8d77\u6765\uff0c\u5480\u56bc\u7740\uff0c\u5c06\u90a3\u5757\u8089\u541e\u4e86\u4e0b\u53bb\u3002\u5b83\u7684\u4e24\u4e2a\u7a7a\u6d1e\u773c\u7736\u91cc\u5f00\u59cb\u653e\u51fa\u7ea2\u5149\uff0c\u63a5\u7740\uff0c\u6574\u4e2a\u5934\u9aa8\u4ece\u4e2d\u95f4\u6574\u9f50\u5730\u88c2\u5f00\uff0c\u5206\u6210\u4e24\u534a\u3002</p><p>\u8f70\u7684\u4e00\u58f0\uff0c\u5927\u95e8\u655e\u5f00\u3002</p><p>\u201c\u8bf7\u8fdb\u3002\u201d\u591a\u745e\u68ee\u8bf4\uff0c\u7136\u540e\u4f9d\u65e7\u662f\u81ea\u5df1\u5f53\u5148\u8d70\u4e86\u8fdb\u53bb\u3002</p><p>\u7136\u540e\u4ed6\u6014\u4f4f\u4e86\u3002</p><p>\u56e0\u4e3a\u5b9d\u5e93\u91cc\u6b63\u7ad9\u7740\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u91d1\u53d1\u9752\u5e74\uff0c\u4e00\u88ad\u767d\u888d\uff0c\u7ea4\u5c18\u4e0d\u67d3\uff0c\u6709\u7740\u8679\u5f69\u95ea\u70c1\u7684\u53cc\u773c\uff0c\u6ee1\u9762\u7b11\u5bb9\uff0c\u4eff\u4f5b\u8fd8\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u7684\u6a21\u6837\u3002\u4ed6\u7684\u811a\u8fb9\u653e\u7740\u4e00\u53ea\u5927\u53e3\u888b\uff0c\u4ece\u90a3\u9f13\u9f13\u56ca\u56ca\u7684\u6837\u5b50\uff0c\u4ee5\u53ca\u85cf\u5b9d\u5e93\u91cc\u7a7a\u65f7\u7684\u7a0b\u5ea6\u6765\u5224\u65ad\uff0c\u6050\u6015\u591a\u745e\u68ee\u7684\u5927\u90e8\u5206\u6536\u85cf\u90fd\u5df2\u7ecf\u88ab\u88c5\u8fdb\u91cc\u9762\u4e86\u3002</p><p>\u201c\u5514\uff0c\u4e0b\u5348\u597d\uff0c\u591a\u745e\u68ee\u5148\u751f\u3002\u201d\u4ed6\u6b22\u5feb\u5730\u548c\u591a\u745e\u68ee\u6253\u7740\u62db\u547c\u3002</p><p>\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u201d\u591a\u745e\u68ee\u60ca\u8be7\u65e0\u6bd4\u3002\u867d\u7136\u4ed6\u5bf9\u85cf\u5b9d\u5e93\u5e76\u4e0d\u600e\u4e48\u770b\u91cd\uff0c\u4e5f\u6ca1\u6709\u5b89\u6392\u4ec0\u4e48\u91cd\u5175\u628a\u5b88\uff0c\u4f46\u5982\u679c\u6709\u4eba\u64c5\u81ea\u6f5c\u5165\uff0c\u4ed6\u4e5f\u5e94\u8be5\u80fd\u7acb\u523b\u611f\u5e94\u5230\u624d\u5bf9\u3002\u518d\u8bf4\uff0c\u5927\u95e8\u662f\u5173\u5f97\u597d\u597d\u7684\uff0c\u8fd9\u4e2a\u91d1\u53d1\u9752\u5e74\u662f\u600e\u4e48\u6084\u65e0\u58f0\u606f\u5730\u8fdb\u6765\u4e86\uff1f</p><p>\u6b63\u5982\u524d\u6240\u8ff0\uff0c\u591a\u745e\u68ee\u5e76\u4e0d\u7cbe\u7814\u5965\u6cd5\uff0c\u4f46\u5bf9\u4e0e\u4fdd\u547d\u3001\u9003\u751f\u76f8\u5173\u7684\u9886\u57df\u5374\u975e\u5e38\u611f\u5174\u8da3\u3002\u4e3a\u4e86\u81ea\u8eab\u7684\u5b89\u5168\uff0c\u4ed6\u65e9\u5c31\u5728\u57ce\u5821\u5468\u56f4\u5e03\u4e0b\u4e86\u9b54\u6cd5\u7981\u5236\uff0c\u5982\u679c\u6709\u4eba\u60f3\u7528\u4f20\u9001\u672f\u76f4\u63a5\u8fdb\u5165\u4ed6\u7684\u57ce\u5821\uff0c\u591a\u745e\u68ee\u662f\u80af\u5b9a\u4f1a\u5bdf\u89c9\u5230\u7684\u3002\u521a\u624d\u6b27\u51ef\u5c06\u519b\u524d\u6765\uff0c\u4ed6\u867d\u7136\u65e0\u529b\u62b5\u5fa1\uff0c\u4f46\u81f3\u5c11\u4e5f\u80fd\u6e05\u695a\u5730\u610f\u8bc6\u5230\u3002\u5f53\u7136\uff0c\u8fd9\u4e5f\u6709\u6b27\u51ef\u6545\u610f\u793a\u5a01\u7684\u56e0\u7d20\u5728\u5185\u3002</p><p>\u6b63\u5f53\u4ed6\u83ab\u540d\u60ca\u8be7\u7684\u65f6\u5019\uff0c\u9661\u7136\u611f\u89c9\u5230\u4fa7\u540e\u65b9\u4e00\u9635\u52b2\u98ce\u88ad\u6765\u3002\u98df\u5c38\u9b3c\u541b\u738b\u672c\u80fd\u5730\u60f3\u8981\u8eb2\u95ea\uff0c\u4f46\u653b\u51fb\u5b9e\u5728\u6765\u5f97\u592a\u5feb\uff0c\u6216\u8005\u8bf4\u5077\u88ad\u8005\u79bb\u5f97\u5b9e\u5728\u592a\u8fd1\u3002\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u591a\u745e\u68ee\u7626\u524a\u7684\u8eab\u4f53\u91cd\u91cd\u6454\u98de\u51fa\u53bb\uff0c\u76f4\u8d8a\u8fc7\u5927\u534a\u4e2a\u85cf\u5b9d\u5e93\uff0c\u6389\u8fdb\u4e00\u5806\u5934\u9aa8\u706f\u7b3c\u4e2d\uff0c\u88ab\u57cb\u4e86\u8d77\u6765\u3002</p><p>\u6b27\u51ef\u7529\u4e86\u7529\u624b\uff0c\u201c\u8fd9\u5bb6\u4f19\u7626\u5f97\u5168\u662f\u9aa8\u5934\uff0c\u201d\u4ed6\u62b1\u6028\u7740\uff0c\u4ece\u95e8\u5916\u8d70\u8fdb\u6765\uff0c\u770b\u7740\u91d1\u53d1\u9752\u5e74\u3002\u708e\u9b54\u54a7\u5f00\u5927\u5634\uff0c\u90a3\u98a7\u9aa8\u9ad8\u8038\u7684\u8138\u4e0a\u9732\u51fa\u6050\u6016\u7684\u7b11\u5bb9\u3002\u201c\u55e8\uff0c\u4e0b\u5348\u597d\uff0c\u745e\u6069\u65af\u5766\uff0c\u4eca\u5929\u5929\u6c14\u771f\u4e0d\u9519\u5440\u3002\u201d</p><p>\u745e\u6069\u65af\u5766\u6709\u4e9b\u60ca\u8bb6\u5730\u770b\u7740\u7a81\u7136\u4ece\u540e\u9762\u5192\u51fa\u6765\u7684\u6076\u9b54\u3002</p><p>\u201c\u5514\uff0c\u6b27\u51ef\u554a\uff0c\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u7b97\u662f\u6253\u62db\u547c\uff0c\u201c\u6df1\u6e0a\u8fd9\u4e48\u5927\uff0c\u4e3a\u4ec0\u4e48\u6211\u5230\u5904\u90fd\u80fd\u9047\u4e0a\u4f60\u2026\u2026\u7b49\u7b49\uff0c\u5c71\u7f8a\u5934\uff1f\u201d\u4ed6\u770b\u89c1\u6b27\u51ef\u80f8\u7532\u4e0a\u7684\u6807\u8bb0\uff0c\u201c\u4f60\u53c8\u6362\u8001\u677f\u4e86\uff1f\u201d</p><p>\u201c\u5443\uff0c\u201d\u6b27\u51ef\u7565\u6709\u4e9b\u5c34\u5c2c\uff0c\u201c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u4e0d\u8981\u8bf4\u90a3\u4e2a\u2018\u53c8\u2019\u5b57\u3002\u201d</p><p>\u201c\u54ce\u5440\uff0c\u96be\u9053\u6076\u9b54\u4e5f\u4f1a\u56e0\u4e3a\u80cc\u53db\u800c\u5bb3\u7f9e\u4e48\uff1f\u201d\u745e\u6069\u65af\u5766\u9976\u6709\u5174\u81f4\u5730\u8bc4\u4ef7\u7740\uff0c\u201c\u6211\u771f\u7684\u90fd\u6000\u7591\u4f60\u662f\u4e0d\u662f\u4e2a\u9b54\u9b3c\u4f2a\u88c5\u7684\u4e86\u3002\u201d</p><p>\u6b27\u51ef\u88c5\u4f5c\u6ca1\u542c\u89c1\u3002</p><p>\u201c\u7b2c\u4e8c\u50cf\u9b54\u9b3c\u7684\u6076\u9b54\u201d\uff0c\u8fd9\u662f\u4e00\u4e2a\u7ef0\u53f7\uff0c\u4f46\u5176\u5b9e\u7b97\u4e0d\u4e0a\u5c0a\u79f0\u3002\u6b27\u51ef\u81ea\u5df1\u7684\u5c5e\u4e0b\u662f\u51b3\u4e0d\u4f1a\u8fd9\u4e48\u79f0\u547c\u4ed6\u7684\uff0c\u53ea\u6709\u4ed6\u7684\u654c\u4eba\u624d\u4f1a\u5728\u80cc\u540e\u5982\u6b64\u8ba5\u8bbd\u3002</p><p>\u6076\u9b54\u548c\u9b54\u9b3c\uff0c\u867d\u7136\u540d\u79f0\u76f8\u8fd1\uff0c\u5176\u5b9e\u662f\u8fe5\u7136\u76f8\u5f02\u7684\u4e24\u79cd\u751f\u7269\u3002\u6076\u9b54\u5c45\u4f4f\u5728\u65e0\u5c3d\u6df1\u6e0a\uff0c\u9b54\u9b3c\u76d8\u8e1e\u7740\u4e5d\u5c42\u5730\u72f1\uff0c\u4ed6\u4eec\u662f\u5929\u751f\u7684\u6b7b\u654c\uff0c\u5f7c\u6b64\u4ece\u5934\u5230\u811a\u90fd\u770b\u5bf9\u65b9\u4e0d\u987a\u773c\uff0c\u6bcf\u65f6\u6bcf\u523b\u90fd\u5728\u5f00\u6218\u3002</p><p>\u6ca1\u6709\u54ea\u4e2a\u6076\u9b54\u559c\u6b22\u88ab\u4eba\u5938\u5956\u4e3a\u201c\u4f60\u771f\u50cf\u4e2a\u9b54\u9b3c\u201d\uff0c\u6b63\u5982\u6ca1\u6709\u4f8f\u5112\u559c\u6b22\u88ab\u5938\u5956\u4e3a\u201c\u4f60\u771f\u50cf\u4e2a\u77ee\u4eba\u201d\u3002</p><p>\u6b27\u51ef\u663e\u7136\u5e76\u4e0d\u559c\u6b22\u542c\u5230\u522b\u4eba\u8bf4\u4ed6\u50cf\u9b54\u9b3c\u2026\u2026\u4e0d\u8fc7\uff0c\u4ed6\u53ef\u4e0d\u60f3\u548c\u9762\u524d\u8fd9\u4e2a\u5bb6\u4f19\u52a8\u624b\u3002</p><p>\u5728\u591a\u745e\u68ee\u773c\u4e2d\uff0c\u770b\u5230\u7684\u662f\u4eba\u7c7b\u91d1\u53d1\u9752\u5e74\uff1b\u4f46\u5728\u6b27\u51ef\u90a3\u53cc\u80fd\u76f4\u63a5\u770b\u7834\u5e7b\u8c61\u7684\u773c\u4e2d\uff0c\u5bf9\u65b9\u7684\u5f62\u8c61\u662f\u4e00\u53ea\u7c89\u7ea2\u8272\u5de8\u9f99\u3002</p><p>\u745e\u6069\u65af\u5766\u662f\u4e00\u53ea\u9752\u5e74\u8679\u5f69\u9f99\u3002</p><p>\u8bf4\u4e0d\u4e0a\u670b\u53cb\uff0c\u4f46\u5f7c\u6b64\u4e5f\u7b97\u6253\u8fc7\u4e0d\u5c11\u4ea4\u9053\uff0c\u6b27\u51ef\u5f88\u6e05\u695a\u8679\u5f69\u9f99\u7684\u529b\u91cf\u3002\u82e5\u662f\u5728\u5176\u4ed6\u5730\u65b9\u4ea4\u624b\uff0c\u6b27\u51ef\u81ea\u5ea6\u6ca1\u6709\u80dc\u7b97\uff0c\u5e78\u597d\u6b64\u65f6\u662f\u5728\u6df1\u6e0a\u3002\u6574\u4e2a\u4f4d\u9762\u90fd\u5728\u538b\u5236\u7740\u5bf9\u65b9\uff0c\u8fd9\u8ba9\u6b27\u51ef\u5148\u5929\u5c31\u5360\u636e\u4e86\u51e0\u5206\u4f18\u52bf\u3002\u4f46\u771f\u8981\u52a8\u8d77\u624b\u6765\uff0c\u80dc\u8d1f\u8fd8\u662f\u5728\u672a\u77e5\u4e4b\u6570\u3002</p><p>\u6b27\u51ef\u6b64\u6765\uff0c\u662f\u5949\u5965\u5580\u65af\u4e4b\u547d\uff0c\u524d\u6765\u53d6\u4e00\u4ef6\u4e1c\u897f\u7684\uff0c\u4ed6\u5e76\u4e0d\u6253\u7b97\u4e0e\u4eba\u52a8\u624b\u3002</p><p>\u6240\u4ee5\u2026\u2026</p><p>\u6b27\u51ef\u5fae\u7b11\u7740\uff0c\u4e0d\u8bf4\u8bdd\uff0c\u773c\u5149\u56db\u5904\u641c\u7d22\u3002\u4f5c\u4e3a\u708e\u9b54\uff0c\u4ed6\u7684\u773c\u775b\u5929\u751f\u5c31\u80fd\u7a7f\u900f\u4e00\u5207\u5c4f\u969c\uff0c\u9664\u975e\u7528\u9b54\u6cd5\u963b\u9694\u3002\u4ed6\u9996\u5148\u6ce8\u610f\u5230\uff0c\u5bf9\u65b9\u4f3c\u4e4e\u53d7\u4f24\u4e86\uff0c\u8eab\u4e0a\u6709\u51e0\u5904\u4f24\u53e3\u3002\u708e\u9b54\u5bf9\u6b64\u5f88\u597d\u5947\uff0c\u4e0d\u77e5\u9053\u8679\u5f69\u9f99\u906d\u9047\u4e86\u4ec0\u4e48\u5f3a\u654c\uff0c\u5c45\u7136\u5f04\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u4e0d\u8fc7\u4ed6\u6ca1\u6709\u5f00\u53e3\u8be2\u95ee\u3002\u5728\u626b\u89c6\u4e86\u4e00\u5708\uff0c\u53d1\u73b0\u4e00\u65e0\u6240\u83b7\u540e\uff0c\u4ed6\u628a\u76ee\u5149\u805a\u7126\u5728\u745e\u6069\u65af\u5766\u811a\u8fb9\u7684\u53e3\u888b\u91cc\u3002\u90a3\u91cc\u9762\uff0c\u6709\u4e2a\u95ea\u95ea\u53d1\u5149\u7684\u4e1c\u897f\u5438\u5f15\u4e86\u4ed6\u7684\u6ce8\u610f\u3002</p><p>\u4ed6\u772f\u7f1d\u7740\u773c\uff0c\u4ee5\u4fbf\u80fd\u770b\u5f97\u66f4\u6e05\u6670\u3002\u90a3\u662f\u4e00\u5757\u5df4\u638c\u5927\u5c0f\u7684\u8584\u91d1\u788e\u7247\uff0c\u4e0a\u9762\u9690\u9690\u7ea6\u7ea6\u5730\u6709\u94f6\u8272\u7684\u6587\u5b57\u5728\u6d41\u52a8\u53d8\u5e7b\u3002</p><p>\u6b27\u51ef\u901a\u6653\u4e00\u767e\u591a\u79cd\u8bed\u8a00\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ed6\u4e0d\u8ba4\u8bc6\u7684\u6587\u5b57\uff0c\u4f46\u4ed6\u4f9d\u65e7\u534a\u70b9\u770b\u4e0d\u61c2\u788e\u91d1\u7247\u4e0a\u5199\u7740\u4ec0\u4e48\u3002\u4e0d\u8fc7\u8fd9\u6ca1\u5173\u7cfb\uff0c\u4ed6\u5df2\u7ecf\u786e\u5b9a\uff0c\u8fd9\u5c31\u662f\u5965\u5580\u65af\u547d\u4ee4\u4ed6\u6765\u5bfb\u627e\u7684\u4e1c\u897f\uff0c\u8fd9\u5c31\u662f\u4ed6\u6b64\u884c\u7684\u76ee\u6807\u3002</p><p>\u201c\u521a\u624d\u6211\u597d\u50cf\u5e2e\u4f60\u89e3\u51b3\u4e86\u4e2a\u5c0f\u9ebb\u70e6\uff0c\u201d\u6076\u9b54\u5750\u4e0b\u6765\uff0c\u7528\u957f\u7740\u4e03\u6839\u6307\u5934\u7684\u5927\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u82e5\u6709\u6240\u601d\uff0c\u201c\u90a3\u4e48\uff0c\u63a5\u4e0b\u6765\u6211\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u8c08\u8c08\u62a5\u916c\u7684\u95ee\u9898\uff1f\u201d</p><p>\u4ed6\u5e76\u4e0d\u62c5\u5fc3\u5bf9\u65b9\u4f1a\u62d2\u7edd\uff0c\u56e0\u4e3a\u6b27\u51ef\u5c06\u519b\u5f88\u6e05\u695a\uff0c\u8fd9\u53ea\u75af\u72c2\u70ed\u7231\u6536\u96c6\u5b9d\u7269\u7684\u8679\u5f69\u9f99\uff0c\u6709\u7740\u67d0\u4e9b\u5f88\u7279\u522b\u7684\uff0c\u5e38\u4eba\u65e0\u6cd5\u7406\u89e3\u7684\u4e60\u60ef\u3002</p></article>"}}}]);
