(window.webpackJsonp=window.webpackJsonp||[]).push([[1253],{1276:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u5973\u9a91\u58eb",content:"<article><h2>\u7b2c\u4e09\u8282 \u5973\u9a91\u58eb</h2><p>\u6c99\u6f20\u91cc\u6e3a\u65e0\u4eba\u70df\uff0c\u552f\u6709\u706b\u70ed\u7684\u98ce\u5377\u8d77\u6f2b\u5929\u9ec4\u6c99\uff0c\u743c\u6069\u8bc5\u5492\u7740\uff0c\u5728\u9a86\u9a7c\u4e0a\u6447\u6447\u6643\u6643\uff0c\u8fd9\u5df2\u7ecf\u662f\u4ed6\u51fa\u9634\u9b42\u57ce\u7684\u7b2c\u4e09\u5929\u4e86\u3002</p><p>\u7b2c\u4e00\u5929\uff0c\u5b89\u7136\u65e0\u4e8b\uff0c\u53ef\u4ee5\u79f0\u5f97\u4e0a\u4e00\u5e06\u98ce\u987a\uff0c\u9664\u4e86\u9047\u4e0a\u70b9\u98ce\u6c99\u3002\u7b2c\u4e8c\u5929\uff0c\u649e\u4e0a\u4e86\u51e0\u53ea\u6c99\u6f20\u51f6\u732b\uff0c\u743c\u6069\u76f4\u63a5\u62db\u6765\u4e00\u4e2a\u51b0\u51b7\u6cd5\u7403\uff0c\u628a\u5b83\u4eec\u51bb\u6210\u4e86\u788e\u5757\u3002\u4eca\u5929\u5df2\u7ecf\u662f\u7b2c\u4e09\u5929\u4e86\uff0c\u5012\u8fd8\u7b97\u5e73\u5b89\uff0c\u6839\u636e\u5730\u56fe\u7b97\u7b97\u8def\u7a0b\uff0c\u4f30\u8ba1\u660e\u5929\u6216\u8005\u540e\u5929\uff0c\u5e94\u8be5\u5c31\u80fd\u5230\u90a3\u6761\u201c\u9ed1\u8def\u201d\u4e86\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u80af\u5b9a\u6ca1\u6709\u6c34\u6ce5\u516c\u8def\u8fd9\u79cd\u4e1c\u897f\uff0c\u6240\u8c13\u7684\u9ed1\u8def\uff0c\u4ece\u5730\u56fe\u4e0a\u770b\uff0c\u5176\u5b9e\u4e5f\u5c31\u662f\u5341\u51e0\u4e2a\u7eff\u6d32\u8054\u7ed3\u800c\u6210\u7684\u4e00\u6761\u5546\u65c5\u8def\u7ebf\uff0c\u6b63\u597d\u6a2a\u7a7f\u6574\u4e2a\u6c99\u6f20\u3002\u8bf4\u6765\u4e5f\u5947\u602a\uff0c\u7814\u7a76\u4e86\u534a\u5929\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u7684\u8fd9\u5f20\u5730\u56fe\u540e\uff0c\u743c\u6069\u53d1\u73b0\u8bfa\u5927\u4e00\u7247\u6c99\u6f20\uff0c\u5c45\u7136\u9664\u4e86\u8fd9\u201c\u9ed1\u8def\u201d\u6cbf\u9014\u6709\u5341\u51e0\u4e2a\u5927\u5927\u5c0f\u5c0f\u7684\u7eff\u6d32\u2014\u2014\u6216\u8005\u8bf4\uff0c\u6b63\u662f\u56e0\u4e3a\u8fd9\u5341\u51e0\u4e2a\u7eff\u6d32\uff0c\u6240\u4ee5\u624d\u7ec4\u6210\u4e86\u8fd9\u6761\u201c\u9ed1\u8def\u201d\uff0c\u800c\u6c99\u6f20\u5176\u4ed6\u5730\u65b9\uff0c\u5c45\u7136\u5168\u6ca1\u6709\u534a\u4e2a\u7eff\u6d32\u7684\u6807\u8bc6\u3002</p><p>\u8fd9\u4e5f\u672a\u514d\u592a\u6070\u5de7\u4e86\u70b9\uff0c\u53ef\u80fd\u662f\u5730\u56fe\u4e0a\u6ca1\u6807\u51fa\u6765\u5427\uff0c\u743c\u6069\u60f3\u3002</p><p>\u5df2\u7ecf\u662f\u4e0b\u5348\uff0c\u592a\u9633\u4e5f\u6e10\u6e10\u6709\u897f\u5782\u7684\u8ff9\u8c61\uff0c\u4f46\u6c99\u6f20\u91cc\u7684\u708e\u70ed\u5e76\u672a\u524a\u51cf\u534a\u5206\u3002\u743c\u6069\u6c57\u51fa\u5982\u6d46\uff0c\u8eab\u4e0a\u7684\u8863\u670d\u6e7f\u4e86\u53c8\u5e72\uff0c\u5e72\u4e86\u53c8\u6e7f\uff0c\u73b0\u5728\u4e0a\u9762\u5df2\u7ecf\u7ed3\u4e86\u4e00\u5c42\u76d0\u78b1\uff0c\u7a7f\u7740\u611f\u89c9\u786c\u786c\u7684\uff0c\u50cf\u662f\u76d4\u7532\u3002\u4ed6\u867d\u7136\u5b8c\u5168\u6ca1\u6709\u534a\u70b9\u6c99\u6f20\u65c5\u884c\u7ecf\u9a8c\uff0c\u4f46\u4e5f\u77e5\u9053\u6c34\u662f\u6700\u4e0d\u80fd\u6d6a\u8d39\u4e4b\u7269\uff0c\u8fd9\u51e0\u5929\u522b\u8bf4\u6d17\u6fa1\uff0c\u8fde\u6d17\u8138\u90fd\u7701\u4e86\uff0c\u73b0\u5728\u5934\u53d1\u91cc\u90fd\u662f\u6c99\u5b50\uff0c\u7740\u5b9e\u96be\u53d7\u5f97\u5389\u5bb3\u3002</p><p>\u7b49\u51fa\u4e86\u6c99\u6f20\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u5c31\u662f\u627e\u4e2a\u65c5\u5e97\uff0c\u8df3\u8fdb\u6d74\u7f38\u91cc\u597d\u597d\u6d17\u4e2a\u6fa1\u3002</p><p>\u743c\u6069\u76d8\u7b97\u7740\uff0c\u8fd9\u5df2\u7ecf\u662f\u4ed6\u76ee\u524d\u6700\u5927\u7684\u7406\u60f3\u4e86\u3002</p><p>\u6b63\u60f3\u7740\uff0c\u8033\u8fb9\u7a81\u7136\u4f20\u6765\u547c\u559d\u543c\u53eb\u4e4b\u58f0\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u524d\u65b9\u9690\u9690\u7ea6\u7ea6\u4f3c\u4e4e\u6709\u4e00\u7fa4\u4eba\uff0c\u6b63\u5728\u56f4\u7740\u4e00\u6839\u9ad8\u8038\u7684\u67f1\u5b50\u5728\u6253\u8f6c\uff0c\u5012\u6709\u4e9b\u50cf\u662f\u72e9\u730e\u7684\u573a\u666f\u3002\u518d\u8d70\u8fd1\u4e9b\uff0c\u770b\u5f97\u6e05\u695a\u4e86\uff0c\u987f\u65f6\u51e0\u4e4e\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u9aa8\u866b\uff1f\u201d</p><p>\u54ea\u91cc\u662f\u4ec0\u4e48\u9ad8\u8038\u7684\u67f1\u5b50\uff0c\u5206\u660e\u662f\u4e00\u53ea\u72f0\u72de\u6050\u6016\u7684\u602a\u7269\uff0c\u4eff\u4f5b\u5de8\u5927\u7684\u8708\u86a3\u76f4\u7acb\u8d77\u6765\uff0c\u5927\u7ea6\u6709\u51e0\u5341\u82f1\u5c3a\u9ad8\u3002\u5468\u8eab\u96f6\u788e\u8986\u76d6\u7740\u7126\u9ed1\u8150\u8d25\u7684\u76ae\u8089\uff0c\u9732\u51fa\u5176\u4e2d\u7684\u68ee\u68ee\u9aa8\u67b6\u3002\u5728\u9aa8\u67b6\u7684\u95f4\u9699\uff0c\u6d6e\u51f8\u51fa\u6570\u767e\u5f20\u6b8b\u7f3a\u4e0d\u5168\u7684\u8138\u5b54\uff0c\u4ece\u90a3\u4e9b\u7126\u9ed1\u8150\u8d25\u7684\u76ae\u8089\u4e2d\u6324\u4e86\u51fa\u6765\uff0c\u7eb7\u7eb7\u5927\u58f0\u543c\u53eb\u7740\uff0c\u53d1\u51fa\u5947\u602a\u7684\u58f0\u97f3\uff0c\u50cf\u662f\u4ec0\u4e48\u5723\u6b4c\u6216\u8005\u7977\u8bcd\uff0c\u5374\u53c8\u5145\u6ee1\u4e86\u660e\u663e\u7684\u6068\u610f\u548c\u5632\u5f04\u3002\u5b83\u6ca1\u6709\u660e\u663e\u7684\u5934\u90e8\uff0c\u6574\u4e2a\u770b\u8d77\u6765\u5c31\u50cf\u662f\u9aa8\u9abc\u7ec4\u6210\u7684\u67f1\u5b50\u3002</p><p>\u5728\u602a\u7269\u524d\u65b9\uff0c\u6709\u5927\u7ea6\u51e0\u5341\u4e2a\u4eba\uff0c\u7537\u5973\u8001\u5e7c\u90fd\u6709\uff0c\u8863\u7740\u6253\u626e\u9887\u4e3a\u602a\u5f02\uff0c\u8fd1\u4f3c\u743c\u6069\u524d\u4e16\u6240\u77e5\u9053\u7684\u963f\u62c9\u4f2f\u4eba\uff0c\u5168\u90fd\u624e\u7740\u767d\u8272\u5934\u5dfe\uff0c\u7a7f\u7740\u5c16\u5934\u7fd8\u8d77\u7684\u9ed1\u8272\u957f\u9774\uff0c\u9a91\u7740\u9a86\u9a7c\uff0c\u53e6\u6709\u5341\u51e0\u8f86\u5927\u8f66\uff0c\u6ee1\u8f7d\u8f8e\u91cd\u884c\u674e\u3002\u8fd9\u4e0d\u50cf\u662f\u5546\u961f\uff0c\u5012\u66f4\u50cf\u662f\u67d0\u4e2a\u6e38\u7267\u90e8\u843d\u5927\u642c\u5bb6\u3002\u53e6\u6709\u5341\u4f59\u4e2a\u5f6a\u5f62\u5927\u6c49\uff0c\u624b\u6267\u96ea\u4eae\u5f2f\u5200\uff0c\u6321\u5728\u524d\u9762\u62a4\u536b\uff0c\u548c\u90a3\u9aa8\u9abc\u6784\u6210\u7684\u602a\u7269\u9065\u9065\u5bf9\u5cd9\u3002</p><p>\u602a\u7269\u76f4\u7acb\u7740\uff0c\u6ca1\u6709\u8fdb\u653b\uff0c\u4f46\u771f\u6b63\u6321\u4f4f\u5b83\u7684\u5e76\u4e0d\u662f\u8fd9\u4e9b\u624b\u6267\u5f2f\u5200\u7684\u5927\u6c49\uff0c\u800c\u662f\u53e6\u6709\u5176\u4eba\u3002</p><p>\u5728\u8ddd\u79bb\u602a\u7269\u4e0d\u8db3\u5341\u4f59\u82f1\u5c3a\u7684\u5730\u65b9\uff0c\u7ad9\u7740\u4e09\u4e2a\u4eba\u3002</p><p>\u4ed6\u4eec\u90fd\u7a7f\u7740\u94f6\u8272\u7684\u94e0\u7532\uff0c\u6cdb\u7740\u95ea\u95ea\u5149\u8f89\uff0c\u7eb5\u7136\u5728\u8fd9\u9ec4\u6c99\u906e\u5929\u7684\u6c99\u6f20\u91cc\uff0c\u4e5f\u663e\u5f97\u90a3\u6837\u795e\u5723\u5a01\u4e25\u3002\u4e09\u4e2a\u4eba\u5448\u4e00\u4e2a\u4e09\u89d2\u5f62\uff0c\u5305\u56f4\u4f4f\u602a\u7269\uff0c\u4ed6\u4eec\u624b\u4e2d\u90fd\u9ad8\u4e3e\u7740\u67d0\u4ef6\u4e1c\u897f\uff0c\u50cf\u662f\u5fbd\u7ae0\uff0c\u540c\u6837\u6cdb\u7740\u8000\u773c\u7684\u767d\u5149\u3002</p><p>\u602a\u7269\u5bf9\u90a3\u767d\u5149\u4eff\u4f5b\u975e\u5e38\u754f\u60e7\uff0c\u5b83\u9000\u7f29\u7740\uff0c\u4e0d\u6562\u9760\u8fd1\uff0c\u4f46\u5b83\u5df2\u7ecf\u88ab\u56f4\u4f4f\u4e86\u3002\u4e09\u4e2a\u4eba\u9ad8\u4e3e\u7740\u5723\u5fbd\uff0c\u547c\u558a\u7740\u67d0\u4e2a\u540d\u5b57\u2014\u2014\u743c\u6069\u542c\u51fa\u5176\u4e2d\u4e00\u4eba\u547c\u558a\u7684\u540d\u5b57\u548c\u53e6\u5916\u4e24\u4eba\u4e0d\u540c\u3002</p><p>\u767d\u5149\u8d8a\u53d1\u8000\u773c\uff0c\u7167\u5c04\u5728\u602a\u7269\u8eab\u4e0a\u3002\u7126\u9ed1\u8150\u8d25\u7684\u76ae\u8089\u7eb7\u7eb7\u5316\u4f5c\u9752\u70df\u6563\u53bb\uff0c\u9732\u51fa\u91cc\u9762\u66f4\u591a\u7684\u68ee\u68ee\u9aa8\u67b6\u3002\u4f46\u968f\u5373\uff0c\u602a\u7269\u7684\u9aa8\u67b6\u4e2d\u53c8\u817e\u51fa\u9ed1\u7eff\u8272\u706b\u7130\u6765\uff0c\u751f\u51fa\u4e00\u5757\u5757\u8150\u8d25\u7684\u76ae\u8089\uff0c\u5b83\u663e\u7136\u6709\u81ea\u6211\u6062\u590d\u7684\u80fd\u529b\u3002\u90a3\u6570\u767e\u5f20\u72f0\u72de\u7684\u6b8b\u7f3a\u4e0d\u5168\u7684\u4eba\u8138\u75db\u695a\u5730\u543c\u53eb\u7740\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u5927\uff0c\u4e5f\u8d8a\u6765\u8d8a\u542b\u7cca\uff0c\u6df7\u6742\u5728\u4e00\u8d77\u542c\u4e0d\u6e05\u695a\uff0c\u5c31\u4eff\u4f5b\u662f\u5de8\u5927\u7684\u82cd\u8747\u5728\u55e1\u55e1\u4f5c\u54cd\u3002</p><p>\u743c\u6069\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u602a\u7269\uff0c\u5b83\u662f\u67d0\u79cd\u4ea1\u7075\u5b58\u5728\uff0c\u53eb\u505a\u9aa8\u866b\uff0c\u4ee5\u524d\u5728\u5deb\u5e08\u5b66\u6821\u91cc\u66fe\u7ecf\u542c\u6559\u6388\u8bf4\u8d77\u8fc7\u3002\u9aa8\u866b\u662f\u6c99\u6f20\u91cc\u6b7b\u53bb\u7684\u4eba\u7684\u9aa8\u9ab8\u7ea0\u96c6\u800c\u6210\u3002\u4eba\u6b7b\u4e4b\u540e\uff0c\u7075\u9b42\u524d\u5f80\u751f\u524d\u6240\u4fe1\u4ef0\u7684\u795e\u7947\u7684\u56fd\u5ea6\uff0c\u5c38\u4f53\u81ea\u7136\u8870\u673d\u3002\u4f46\u6709\u4e9b\u65f6\u5019\uff0c\u5c38\u4f53\u4f1a\u5728\u8d1f\u80fd\u91cf\u7684\u652f\u6491\u4e0b\u7ee7\u7eed\u6d3b\u52a8\uff0c\u5f62\u6210\u4ea1\u7075\u3002</p><p>\u50f5\u5c38\u3001\u9ab7\u9ac5\uff0c\u8fd9\u90fd\u662f\u975e\u5e38\u5e38\u89c1\u7684\u4ea1\u7075\uff0c\u743c\u6069\u5728\u5b66\u6821\u91cc\u90fd\u66fe\u89c1\u8fc7\u3002\u4f46\u50cf\u9aa8\u866b\u8fd9\u6837\u6210\u767e\u4e0a\u5343\u7684\u5c38\u9ab8\u7ea0\u96c6\u5728\u4e00\u8d77\uff0c\u5f62\u6210\u4e00\u4e2a\u5982\u6b64\u5de8\u5927\u7684\u8eaf\u4f53\uff0c\u5374\u662f\u975e\u5e38\u7f55\u89c1\u7684\uff0c\u4e5f\u975e\u5e38\u68d8\u624b\uff0c\u5f88\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u3002</p><p>\u8fd9\u4e09\u4e2a\u4eba\uff0c\u5e94\u8be5\u662f\u7267\u5e08\uff0c\u743c\u6069\u60f3\u3002</p><p>\u9634\u9b42\u57ce\u662f\u838e\u5c14\u7684\u5730\u76d8\uff0c\u57ce\u4e2d\u6240\u6709\u5c45\u6c11\u90fd\u662f\u838e\u5c14\u7267\u5e08\uff0c\u4f46\u8fd9\u5e76\u4e0d\u7b49\u4e8e\u8bf4\u743c\u6069\u4e0d\u77e5\u9053\u4e16\u754c\u4e0a\u8fd8\u6709\u5176\u4ed6\u795e\u7947\u3002\u8d39\u4f26\u4e16\u754c\u662f\u4e00\u4e2a\u591a\u795e\u7947\u7684\u56fd\u5ea6\uff0c\u8fd9\u662f\u4f17\u6240\u5468\u77e5\u7684\u5e38\u8bc6\uff0c\u838e\u5c14\u6559\u4f1a\u4e5f\u4e0d\u66fe\u5426\u8ba4\uff0c\u56e0\u4e3a\u4e5f\u65e0\u6cd5\u5426\u8ba4\u3002</p><p>\u5e7d\u5f71\u754c\u4e5f\u6709\u4ea1\u7075\uff0c\u6700\u5e38\u89c1\u7684\u662f\u6ce2\u8fbe\u5c38\u3002\u743c\u6069\u5728\u9634\u9b42\u57ce\uff0c\u968f\u519b\u6f14\u4e60\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u649e\u4e0a\u8fc7\uff0c\u5c31\u89c1\u5230\u519b\u961f\u91cc\u7684\u838e\u5c14\u7267\u5e08\uff0c\u9ad8\u4e3e\u5723\u5fbd\uff0c\u547c\u558a\u5973\u795e\u7684\u795e\u540d\uff0c\u4fbf\u80fd\u538b\u5236\u6216\u8005\u6467\u6bc1\u8fd9\u4e9b\u4ea1\u7075\uff0c\u548c\u73b0\u5728\u8fd9\u4e09\u4e2a\u4eba\u7684\u52a8\u4f5c\u4e00\u6a21\u4e00\u6837\u3002</p><p>\u7267\u5e08\u867d\u7136\u80fd\u538b\u5236\u4ea1\u7075\uff0c\u4e5f\u662f\u8981\u770b\u5f7c\u6b64\u7684\u529b\u91cf\u5bf9\u6bd4\u7684\u3002\u50cf\u9aa8\u866b\u8fd9\u822c\u5e9e\u5927\u7684\u4ea1\u7075\uff0c\u81f3\u5c11\u96c6\u5408\u4e86\u6570\u767e\u4e2a\u6b7b\u8005\u5c38\u9ab8\uff0c\u878d\u6c47\u4ed6\u4eec\u751f\u524d\u7684\u6028\u5ff5\u4e8e\u4e00\u8eab\uff0c\u8fdc\u8fdc\u4e0d\u662f\u5bfb\u5e38\u7684\u50f5\u5c38\u9ab7\u9ac5\u53ef\u6bd4\u3002\u8fd9\u4e09\u4e2a\u7267\u5e08\u80fd\u5c06\u5b83\u7a33\u7a33\u538b\u5236\u4f4f\uff0c\u663e\u7136\u90fd\u4e0d\u662f\u5bfb\u5e38\u89d2\u8272\u3002</p><p>\u4e0d\u8fc7\uff0c\u538b\u5236\u5f52\u538b\u5236\uff0c\u8981\u771f\u6b63\u6467\u6bc1\u5b83\uff0c\u8fd8\u662f\u5f88\u4e0d\u5bb9\u6613\u7684\u3002\u770b\u76ee\u524d\u7684\u60c5\u5f62\uff0c\u53cc\u65b9\u5bf9\u5cd9\u7740\uff0c\u7267\u5e08\u7565\u5360\u4e0a\u98ce\uff0c\u4f46\u8981\u53d6\u80dc\u5374\u96be\u3002</p><p>\u743c\u6069\u50ac\u4fc3\u9a86\u9a7c\u4e0a\u524d\uff0c\u51c6\u5907\u5e2e\u5fd9\uff0c\u7a81\u7136\u542c\u89c1\u4e00\u9635\u9a6c\u8e44\u58f0\uff0c\u8f6c\u5934\u4e00\u770b\uff0c\u89c1\u4e1c\u8fb9\u8fdc\u5904\u75be\u9a70\u800c\u6765\u4e00\u5339\u767d\u8272\u9a8f\u9a6c\u3002\u9a6c\u4e0a\u7684\u9a91\u58eb\u663e\u7136\u548c\u4e09\u4e2a\u7267\u5e08\u662f\u4e00\u8def\uff0c\u540c\u6837\u7a7f\u7740\u707f\u70c2\u7684\u94f6\u8272\u94e0\u7532\uff0c\u6234\u7740\u5934\u76d4\uff0c\u5de6\u624b\u63d0\u7740\u7f30\u7ef3\uff0c\u53f3\u624b\u63d0\u4e00\u628a\u5de8\u5927\u7684\u94f6\u8272\u957f\u5251\uff0c\u6b63\u671d\u8fd9\u8fb9\u51b2\u6765\u3002</p><p>\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u7ecf\u51b2\u8fd1\u3002\u9a91\u58eb\u81ea\u9a6c\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u5c45\u7136\u817e\u8d77\u51e0\u4eba\u9ad8\uff0c\u53cc\u624b\u63e1\u5251\uff0c\u5251\u4e0a\u9aa4\u7136\u653e\u51fa\u707f\u70c2\u5149\u8f89\uff0c\u5c06\u6574\u4e2a\u4eba\u90fd\u7b3c\u7f69\u5728\u5176\u4e2d\u3002\u8fde\u4eba\u5e26\u5251\u4fbf\u4eff\u4f5b\u662f\u4e00\u9897\u71c3\u70e7\u7684\u5c0f\u592a\u9633\u822c\uff0c\u76f4\u76f4\u649e\u5411\u6b63\u52aa\u529b\u62b5\u6297\u7267\u5e08\u7684\u9aa8\u866b\u3002</p><p>\u8f70\u5730\u4e00\u58f0\uff0c\u788e\u9aa8\u5982\u96e8\u7eb7\u5760\uff0c\u5de8\u5927\u7684\u4ea1\u7075\u88ab\u4e00\u51fb\u800c\u6e83\uff0c\u5730\u4e0a\u817e\u8d77\u718a\u718a\u5927\u706b\u6765\u3002\u743c\u6069\u4e00\u60ca\uff0c\u5fc3\u60f3\u90a3\u9a91\u58eb\u5c82\u4e0d\u4e5f\u88ab\u706b\u70e7\u6b7b\u4e86\uff0c\u5374\u89c1\u9ed1\u7eff\u8272\u706b\u7130\u7ffb\u5377\uff0c\u4ece\u4e2d\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\u3002\u6b63\u662f\u521a\u624d\u90a3\u9a91\u58eb\uff0c\u4f9d\u65e7\u662f\u5168\u8eab\u94f6\u7532\u707f\u70c2\u593a\u76ee\uff0c\u624b\u63d0\u5927\u5251\uff0c\u4fbf\u5982\u5168\u7136\u65e0\u4e8b\u822c\uff0c\u81ea\u706b\u6d77\u4e2d\u6f2b\u6b65\u800c\u51fa\u3002</p><p>\u4e09\u4f4d\u7267\u5e08\u9ad8\u4e3e\u5723\u5fbd\uff0c\u653e\u51fa\u8000\u773c\u767d\u5149\uff0c\u8054\u624b\u5c06\u706b\u7130\u538b\u5236\u4e86\u4e0b\u53bb\uff0c\u6e10\u6e10\u7184\u706d\u3002\u9a91\u58eb\u8d70\u51fa\u706b\u6d77\uff0c\u548c\u4e09\u4f4d\u7267\u5e08\u70b9\u5934\u793a\u610f\uff0c\u4f38\u624b\u53d6\u4e0b\u5934\u76d4\u6765\u3002</p><p>\u6b64\u65f6\u743c\u6069\u5df2\u7ecf\u5230\u4e86\u8fd1\u524d\uff0c\u6b63\u60f3\u642d\u8bdd\uff0c\u6b63\u503c\u9a91\u58eb\u53d6\u4e0b\u5934\u76d4\uff0c\u9732\u51fa\u9762\u5bb9\u3002\u743c\u6069\u4e00\u773c\u77a5\u89c1\uff0c\u5012\u662f\u5927\u5403\u4e86\u4e00\u60ca\u3002</p><p>\u8fd9\u4e00\u51fb\u6d88\u706d\u5de8\u5927\u9aa8\u866b\u7684\u9a91\u58eb\uff0c\u5c45\u7136\u8fd8\u662f\u4e2a\u7a1a\u6c14\u4e0d\u8131\u7684\u5c0f\u5973\u5b69\u3002</p></article>"}}}]);
