(window.webpackJsonp=window.webpackJsonp||[]).push([[127],{150:function(p){p.exports={chapterTitle:null,title:"\u7b2c127\u7ae0 \u53d1\u5939\u60f9\u8d77\u7684\u98ce\u6ce2",content:"<article><h2>\u7b2c127\u7ae0 \u53d1\u5939\u60f9\u8d77\u7684\u98ce\u6ce2</h2><p>\u3000\u3000\u7b2c\u4e8c\u5929\u9664\u5915\uff0c\u5f20\u606a\u4e0e\u5988\u5988\u6881\u683c\u73cd\u8ddf\u7740\u5c0f\u53d4\u5f20\u77e5\u975e\u4e00\u5bb6\u8d76\u56de\u4e1c\u793e\u8001\u5bb6\u8fc7\u6625\u8282\u3002</p><p>\u3000\u3000\u8fd9\u662f\u5f20\u77e5\u884c\u575a\u51b3\u8981\u6c42\u7684\uff0c\u5f20\u77e5\u884c\u7559\u5728\u5e02\u91cc\u503c\u73ed\uff0c\u5bb6\u91cc\u4e0d\u7559\u4eba\uff0c\u4e5f\u5c31\u4e0d\u7528\u7ba1\u522b\u4eba\u767b\u95e8\u9001\u793c\u4e86\u3002</p><p>\u3000\u3000\u987e\u5efa\u840d\u4e5f\u9886\u7740\u5510\u5a67\u56de\u65b0\u6cf0\u8001\u5bb6\uff0c\u770b\u6765\u5510\u5b66\u8c26\u8fd9\u6b21\u662f\u52a8\u771f\u606a\u7684\u4e86\u3002\u8fd9\u65f6\u5019\u5e02\u91cc\u8c03\u4e0d\u51fa\u8f66\u6765\uff0c\u5f20\u606a\u8ba9\u5468\u6587\u658c\u5f00\u8f66\u9001\u987e\u5efa\u840d\u3001\u5510\u5a67\u56de\u65b0\u6cf0\uff0c\u514d\u5f97\u5979\u4eec\u5728\u8def\u4e0a\u5403\u82e6\u5934\u3002</p><p>\u3000\u3000\u5c0f\u53d4\u5f20\u77e5\u975e\u867d\u7136\u6253\u7b97\u5c06\u5bb6\u5b89\u5728\u5e02\u91cc\uff0c\u4e0d\u8fc7\u6625\u8282\u8fd8\u662f\u6253\u7b97\u56de\u4e1c\u793e\u8fc7\u7684\uff0c\u53cd\u6b63\u4ed6\u6709\u8f66\uff0c\u6765\u56de\u65b9\u4fbf\u5f97\u5f88\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u540d\u4e0b\u7684\u7956\u5c4b\uff0c\u65e9\u5728\u5165\u51ac\u5c31\u7531\u5f20\u77e5\u5fae\u3001\u5f20\u77e5\u975e\u51e0\u4e2a\u5802\u5144\u5f1f\u96c6\u8d44\u7ffb\u4fee\u8fc7\uff0c\u589e\u52a0\u4e86\u536b\u751f\u8bbe\u65bd\uff0c\u5185\u90e8\u88c5\u4fee\u4e0d\u6bd4\u5e02\u91cc\u7684\u623f\u5b50\u5dee\u3002</p><p>\u3000\u3000\u5927\u4f2f\u5f20\u77e5\u5fae\u5bf9\u5f20\u77e5\u884c\u4e00\u5bb6\u4e09\u53e3\u56de\u4e1c\u793e\u8fc7\u6625\u8282\u5f88\u610f\u5916\uff0c\u8fd9\u5e74\u5934\u54ea\u6709\u5f53\u5b98\u8eb2\u7740\u9001\u793c\u4eba\u7684\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5728\u4e1c\u793e\u8001\u5bb6\u5374\u5f88\u65e0\u804a\uff0c\u6ca1\u4e8b\u5c31\u8ddf\u5510\u5a67\u901a\u7535\u8bdd\uff0c\u4e0e\u8bb8\u601d\u901a\u7535\u8bdd\u5f88\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u9664\u5915\u5728\u5e02\u91cc\u503c\u591c\u73ed\uff0c\u521d\u4e00\u5728\u5e02\u91cc\u503c\u8fc7\u73ed\uff0c\u591c\u91cc\u624d\u8d76\u5230\u4e1c\u793e\uff0c\u7b2c\u4e8c\u5929\u53c8\u5f00\u8f66\u56de\u5e02\u91cc\uff0c\u5468\u5bcc\u660e\u3001\u5510\u5b66\u8c26\u521d\u4e09\u4e00\u8d77\u53bb\u7701\u57ce\u4ee5\u5e02\u91cc\u7684\u540d\u4e49\u7ed9\u5404\u4e2a\u7701\u9886\u5bfc\u62dc\u5e74\uff0c\u793c\u7269\u90fd\u5f97\u7531\u4ed6\u6765\u5b89\u6392\u3002</p><p>\u3000\u3000\u5f20\u606a\u521d\u4e8c\u5c31\u8ddf\u7740\u56de\u5e02\u91cc\uff0c\u521d\u4e09\u4e5f\u8981\u53bb\u7701\u57ce\u7ed9\u5f90\u5b66\u5e73\u62dc\u5e74\u3002\u8003\u8651\u5230\u5e02\u59d4\u4e66\u8bb0\u5468\u5bcc\u660e\u7684\u5173\u7cfb\uff0c\u5f20\u606a\u4e0d\u65b9\u4fbf\u642d\u5e02\u91cc\u7684\u987a\u98ce\u8f66\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5f00\u8f66\u5c06\u5f20\u606a\u4e22\u5728\u8857\u53e3\uff0c\u5fc3\u60f3\u81ea\u5df1\u4eca\u5929\u80af\u5b9a\u7ed9\u7f20\u5728\u5e02\u91cc\u8131\u4e0d\u5f00\u8eab\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u4e2d\u5348\u5230\u5e02\u91cc\u6765\u5403\u996d\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4f60\uff0c\u6211\u4e5f\u997f\u4e0d\u6b7b\uff0c\u201d\u5f20\u606a\u62cd\u62cd\u571f\u8910\u8272\u7684\u77ed\u98ce\u8863\u5916\u5957\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u665a\u4e0a\u56de\u4e1c\u793e\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u53ef\u80fd\u7559\u5728\u5e02\u91cc\u503c\u591c\uff0c\u4f60\u665a\u4e0a\u4e5f\u4f4f\u5e02\u62db\u5f85\u6240\u6765\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u770b\u60c5\u51b5\u5427\uff0c\u660e\u5929\u53c8\u4e0d\u540c\u8def\uff0c\u6211\u8ba9\u9526\u6e56\u516c\u53f8\u6d3e\u8f66\u9001\u6211\u53bb\u7701\u57ce\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u7b11\u4e86\u7b11\uff0c\u4e5f\u4e0d\u7ba1\u513f\u5b50\u665a\u4e0a\u4f1a\u5230\u54ea\u91cc\u53bb\u6d41\u6d6a\uff0c\u5f00\u8f66\u76f4\u63a5\u8d70\u4e86\u3002</p><p>\u3000\u3000\u5f00\u8f66\u4ece\u4e1c\u793e\u8d76\u5230\u5e02\u91cc\u53ea\u9700\u8981\u4e00\u4e2a\u5c0f\u65f6\uff0c\u8d77\u4e86\u5927\u65e9\uff0c\u73b0\u5728\u624d\u65e9\u4e0a\u4e03\u70b9\u949f\uff0c\u8857\u5934\u7684\u96fe\u6c14\u8fd8\u6ca1\u6709\u6d88\u6563\uff0c\u6625\u8282\u57ce\u91cc\u4eba\u90fd\u8d77\u5f97\u8fdf\uff0c\u8857\u4e0a\u51b7\u51b7\u6e05\u6e05\uff0c\u6ca1\u6709\u4ec0\u4e48\u4eba\uff0c\u5230\u5904\u79ef\u7740\u97ad\u70ae\u7684\u788e\u5c51\u3002</p><p>\u3000\u3000\u4e5d\u56db\u5e74\uff0c\u5bf9\u70df\u82b1\u70ae\u7af9\u7684\u71c3\u653e\u7ba1\u5236\u8fd8\u4e0d\u662f\u90a3\u4e48\u4e25\u5389\u3002</p><p>\u3000\u3000\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u5bf9\u8fd9\u5ea7\u57ce\u5e02\u4f9d\u65e7\u6709\u79cd\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u7684\u964c\u751f\u611f\uff0c\u6216\u8bb8\u4ed6\u5df2\u7ecf\u4e60\u60ef\u5341\u56db\u5e74\u540e\u7684\u90fd\u5e02\u3002\u8857\u4e0a\u7684\u62a5\u4ead\u90fd\u6ca1\u6709\u8425\u4e1a\uff0c\u8eab\u4e0a\u6ca1\u6709\u5e26\u78c1\u5361\uff0c\u4e5f\u6253\u4e0d\u4e86\u516c\u7528\u7535\u8bdd\uff0c\u987a\u7740\u8857\u9053\u5f80\u6c99\u7530\u65b9\u5411\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u6025\u7740\u53bb\u627e\u8bb8\u601d\uff0c\u4eba\u5728\u6c99\u7530\u95f2\u901b\u3002\u8fd9\u4e00\u5e26\u7684\u5efa\u7b51\u5927\u7247\u6210\u5efa\u4e8e\u6c11\u56fd\u521d\u5e74\uff0c\u4e2d\u95f4\u5939\u6742\u7740\u5386\u53f2\u66f4\u957f\u4e45\u7684\u5efa\u7b51\u3002\u7ad9\u5728\u8fdc\u5904\u773a\u671b\uff0c\u8d70\u5230\u8fd1\u5904\u7ec6\u89c2\uff0c\u8fd9\u4e9b\u8272\u5f69\u4e0e\u5f62\u72b6\u5b8c\u5168\u4e0d\u540c\u7684\u9662\u5899\u3001\u5c4b\u9876\u3001\u95e8\u5eca\u3001\u74e6\u6a90\u3001\u5eca\u67f1\u53ca\u7a97\u6237\u4e0a\u7684\u6d6e\u96d5\u82b1\u9970\uff0c\u7ed9\u4eba\u7684\u611f\u89c9\u771f\u6b63\u662f\u7433\u7405\u6ee1\u76ee\uff0c\u7f8e\u4e0d\u80dc\u6536\uff0c\u5982\u540c\u4eba\u7684\u5343\u4eba\u5343\u9762\uff0c\u8fd9\u4e9b\u8001\u623f\u5b50\u4e5f\u662f\u4e00\u680b\u4e00\u4e2a\u9762\u5b54\uff0c\u7edd\u65e0\u91cd\u590d\u548c\u96f7\u540c\u3002</p><p>\u3000\u3000\u629b\u5f00\u90a3\u4e9b\u7cbe\u96d5\u7ec6\u9542\u7684\u7ec6\u8282\u4e4b\u7f8e\uff0c\u4e0e\u524d\u95e8\u5546\u8d38\u533a\u7d27\u6328\u7740\u7684\u6c99\u7530\u7ed9\u4eba\u7684\u6574\u4f53\u5370\u8c61\u9648\u65e7\u800c\u6742\u4e71\uff0c\u5728\u4e0d\u5230\u4e24\u5e73\u65b9\u516c\u91cc\u7684\u533a\u57df\u5185\u6324\u8fdb\u8fd1\u4e03\u516b\u5343\u6237\u4eba\u5bb6\uff0c\u52a0\u4e0a\u79df\u4f4f\u4eba\u7fa4\uff0c\u5dee\u4e0d\u591a\u6709\u5c06\u8fd1\u5341\u4e07\u7684\u5c45\u4f4f\u4eba\u53e3\u3002</p><p>\u3000\u3000\u96fe\u6c14\u6e10\u6563\uff0c\u6de1\u9752\u8272\u7684\u5929\u7a7a\u5f25\u8fdc\uff0c\u5fae\u98ce\u6e05\u5bd2\uff0c\u5df7\u5b50\u91cc\u758f\u659c\u679d\u751f\u7684\u6811\u6728\u8fd8\u6709\u75c5\u53f6\u98d8\u53f6\u3002</p><p>\u3000\u3000\u8981\u662f\u80fd\u5c06\u8fd9\u91cc\u597d\u597d\u6536\u62fe\u4e00\u4e0b\uff0c\u5374\u662f\u76f8\u5f53\u9519\u7684\u5c45\u4f4f\u5730\u3002</p><p>\u3000\u3000\u867d\u7136\u5e02\u91cc\u5bf9\u57ce\u5efa\u89c4\u5212\u601d\u8def\u9010\u6e10\u7684\u53d1\u751f\u8f6c\u53d8\uff0c\u4f46\u662f\u5bf9\u6c99\u7530\u8fd9\u4e00\u7247\u9648\u65e7\u4e0d\u582a\u7684\u5efa\u7b51\u7fa4\u5230\u5e95\u662f\u62c6\u662f\u4fee\uff0c\u8fd8\u662f\u5b58\u5728\u7740\u622a\u7136\u4e0d\u540c\u7684\u5bf9\u7acb\u610f\u89c1\u3002</p><p>\u3000\u3000\u5173\u952e\u8fd8\u5728\u4e8e\u4e00\u4e2a\u94b1\u5b57\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u5df1\u521d\u6b65\u4f30\u7b97\u4e86\u4e00\u4e0b\uff0c\u8981\u5c06\u6c99\u7530\u8fd9\u4e00\u5e26\u53e4\u65e7\u5efa\u7b51\u90fd\u8fdb\u884c\u4fdd\u62a4\u6027\u4fee\u7f2e\u6539\u9020\uff0c\u5c06\u9700\u8981\u52a8\u7528\u8d85\u8fc7\u4e09\u5341\u4ebf\u7684\u8d44\u91d1\uff0c\u662f\u6d77\u5dde\u5e02\u4e09\u5e74\u7684\u8d22\u653f\u6536\u5165\uff0c\u8981\u771f\u628a\u8fd9\u4e2a\u9884\u7b97\u62ff\u51fa\u6765\uff0c\u53ea\u6015\u5510\u5b66\u8c26\u4f1a\u7b2c\u4e00\u4e2a\u653e\u5f03\u4fdd\u62a4\u6027\u4fee\u7f2e\u6539\u9020\u7684\u65b9\u6848\u3002</p><p>\u3000\u3000\u5168\u90e8\u62c6\u9664\u91cd\u5efa\u3001\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\uff0c\u5e02\u91cc\u5c06\u53ef\u4ee5\u5229\u7528\u5546\u4e1a\u8d44\u672c\u6765\u5b8c\u6210\u6c99\u7530\u65e7\u57ce\u7684\u6539\u9020\uff0c\u8fd9\u5b8c\u5168\u662f\u4e00\u4ef6\u7701\u4e8b\u7701\u529b\u53c8\u5f97\u653f\u7ee9\u7684\u65b9\u6848\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u521d\uff0c\u56fd\u5185\u5f88\u591a\u57ce\u5e02\u65e7\u57ce\u6539\u9020\u90fd\u653e\u5f03\u5bf9\u53e4\u5efa\u7b51\u7684\u4fdd\u62a4\uff0c\u8fd8\u6709\u4e00\u70b9\u5f88\u91cd\u8981\u7684\u56e0\u7d20\u5c31\u662f\u5e02\u653f\u6295\u5165\u7684\u95ee\u9898\u3002\u53e4\u5efa\u7b51\u4fdd\u62a4\u9664\u4e86\u5e02\u653f\u6295\u5165\u4e4b\u5916\uff0c\u5f88\u96be\u5f15\u5165\u5546\u4e1a\u8d44\u672c\u8fdb\u884c\u8fd0\u4f5c\uff0c\u5728\u4e5d\u5341\u5e74\u4ee3\u521d\u671f\uff0c\u57ce\u5e02\u5efa\u8bbe\u5904\u5904\u7f3a\u94b1\uff0c\u65c5\u6e38\u4ea7\u4e1a\u8fd8\u6ca1\u6709\u5927\u529b\u53d1\u5c55\u8d77\u6765\uff0c\u62ff\u51fa\u4e00\u5e74\u6216\u8005\u51e0\u5e74\u7684\u8d22\u653f\u6536\u5165\u8fdb\u884c\u53e4\u5efa\u7b51\u7fa4\u4fdd\u62a4\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u628a\u95ee\u9898\u62d6\u5ef6\u5230\u5341\u5e74\u4e4b\u540e\u5c31\u80fd\u8fce\u5203\u800c\u89e3\uff0c\u9664\u4e86\u65c5\u6e38\u4ea7\u4e1a\u53d1\u5c55\u3001\u53e4\u57ce\u4fdd\u62a4\u610f\u8bc6\u589e\u5f3a\u3001\u57ce\u5e02\u8425\u9500\u7684\u89c2\u70b9\u5360\u636e\u4e0a\u98ce\u4e4b\u5916\uff0c\u8d22\u653f\u6536\u5165\u5145\u8db3\u770b\u8d77\u6765\u66f4\u50cf\u6700\u5173\u952e\u7684\u56e0\u7d20\u3002</p><p>\u3000\u3000\u4f46\u662f\u54ea\u4e00\u4efb\u5e02\u957f\u4f1a\u5bb9\u5fcd\u6c99\u7530\u50cf\u4e00\u5757\u72d7\u76ae\u818f\u836f\u4e00\u6837\u8d34\u5728\u6d77\u5dde\u7684\u7248\u56fe\u4e0a\u8d34\u5341\u5e74\uff1f\u4f55\u51b5\u8fd9\u91cc\u8fd8\u7d27\u6328\u7740\u5bf8\u571f\u5bf8\u91d1\u7684\u524d\u95e8\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u505a\u4e0d\u5230\u8fd9\u4e48\u6e05\u9ad8\u81ea\u8bb8\u3002</p><p>\u3000\u3000\u758f\u6e2f\u6cb3\u662f\u7b2c\u4e00\u6b65\uff0c\u6c99\u7530\u5c31\u662f\u7b2c\u4e8c\u6b65\uff0c\u5e02\u91cc\u62ff\u4e0d\u51fa\u94b1\u6765\uff0c\u90a3\u53ea\u6709\u4ea4\u7ed9\u5f00\u53d1\u5546\u62c6\u6389\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\uff0c\u53ef\u80fd\u4f1a\u8c61\u5f81\u6027\u7684\u4fdd\u7559\u9752\u7f8a\u9053\u89c2\u3001\u5343\u5e74\u53e4\u6a1f\u522b\u9662\u3001\u4e39\u4e95\u53f0\u7b49\u5efa\u7b51\uff0c\u4e0d\u8fc7\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u610f\u4e49\u3002</p><p>\u3000\u3000\u5f20\u606a\u901b\u5230\u4e39\u4e95\u5df7\u9047\u5230\u8bb8\u9e3f\u4f2f\uff0c\u8bb8\u9e3f\u4f2f\u5927\u51b7\u5929\u4e0b\u8eab\u7a7f\u7740\u706f\u7b3c\u88e4\u3001\u4e0a\u8eab\u662f\u7403\u886b\uff0c\u8863\u670d\u5f88\u5355\u8584\uff0c\u5f20\u606a\u770b\u4e86\u90fd\u89c9\u5f97\u54c6\u55e6\u3002</p><p>\u3000\u3000\u201c\u8bb8\u8001\u5e08\u65e9\u8d77\u953b\u70bc\u5462\uff1f\u201d\u5f20\u606a\u95ee\u5019\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u4e48\u65e9\u5728\u6c99\u7530\u8f6c\u60a0\u5565\uff1f\u201d\u8bb8\u9e3f\u4f2f\u8dd1\u6b65\u8eab\u4e0a\u6b63\u8d77\u6c57\uff0c\u6ca1\u60f3\u5230\u5e74\u521d\u4e8c\u4e00\u5927\u65e9\u5728\u6c99\u7530\u9047\u5230\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6211\u56de\u4e1c\u793e\u8fc7\u5e74\u4e86\uff0c\u4e00\u65e9\u8ddf\u6211\u7238\u8d76\u5230\u5e02\u91cc\uff0c\u6b63\u60f3\u53bb\u8bb8\u8001\u5e08\u5bb6\u62dc\u5e74\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u50cf\u80fd\u8fd9\u4e48\u65e9\u60f3\u5230\u6211\u7684\u4eba\uff0c\u518d\u8bf4\uff0c\u6211\u5bb6\u5728\u90a3\u8fb9\u5462\uff0c\u4f60\u600e\u4e48\u5f80\u8fd9\u8fb9\u8d70\uff1f\u201d\u8bb8\u9e3f\u4f2f\u6233\u7a7f\u5f20\u606a\u7684\u8c0e\u8a00\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5230\u4e86\u6c99\u7530\uff0c\u5c31\u770b\u8fd9\u4e9b\u65e7\u5efa\u7b51\uff0c\u770b\u75f4\u4e86\u795e\uff0c\u8981\u4e0d\u662f\u9047\u5230\u8bb8\u8001\u5e08\u524d\uff0c\u6211\u8fd8\u80fd\u63a5\u7740\u5f80\u524d\u8d70\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u8bb8\u9e3f\u4f2f\u7b11\u4e86\u7b11\uff0c\u5012\u662f\u63a5\u53d7\u5f20\u606a\u7684\u8fd9\u4e2a\u89e3\u91ca\uff0c\u73b0\u5728\u5c11\u5e74\u5b69\u5b50\u53ea\u4f1a\u89c9\u5f97\u6c99\u7530\u8fd9\u91cc\u9648\u65e7\u6742\u4e71\uff0c\u6ca1\u6709\u4eba\u4f1a\u770b\u5165\u4e86\u795e\uff0c\u5927\u6982\u53ea\u6709\u9189\u5fc3\u4e8e\u5efa\u7b51\u6216\u7f8e\u672f\u3001\u6444\u5f71\u7684\u4eba\uff0c\u624d\u4f1a\u89c9\u5bdf\u51fa\u9648\u65e7\u6742\u4e71\u4e4b\u4e0b\u7684\u7f8e\u6765\uff0c\u201c\u6211\u5148\u56de\u53bb\u6362\u8eab\u8863\u670d\uff0c\u4f60\u5148\u53bb\u68cb\u9662\u7b49\u6211\uff0c\u6211\u8bf7\u4f60\u559d\u8336\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8bb8\u9e3f\u4f2f\u6709\u8bdd\u8981\u8bf4\uff0c\u70b9\u70b9\u5934\u7b54\u5e94\u4e0b\u6765\uff0c\u4e5f\u4e0d\u63d0\u62dc\u5e74\u4e0d\u62dc\u5e74\u7684\u4e8b\uff0c\u6cbf\u7740\u4e39\u4e95\u5df7\u7ee7\u7eed\u5f80\u91cc\u8d70\uff0c\u6cbf\u7740\u4e00\u9053\u659c\u91cc\u4f38\u7684\u5df7\u5b50\uff0c\u8f6c\u5230\u8bb8\u601d\u5bb6\u95e8\u524d\u3002</p><p>\u3000\u3000\u542c\u9662\u5b50\u91cc\u6ca1\u6709\u52a8\u9759\uff0c\u5bfb\u601d\u7740\u4e5f\u8bb8\u6ca1\u6709\u8d77\u5e8a\uff0c\u4e0d\u65b9\u4fbf\u8fd9\u5c31\u627e\u8bb8\u601d\uff0c\u8f6c\u8eab\u5f80\u9053\u89c2\u65b9\u5411\u8d70\u3002\u8fd9\u9053\u5df7\u5b50\u91cc\u517b\u72d7\u4eba\u5bb6\u5c45\u591a\uff0c\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u72ac\u5420\u58f0\u4ea4\u9519\uff0c\u8ba9\u5f20\u606a\u5fc3\u60ca\u80c6\u98a4\uff0c\u540e\u6094\u6ca1\u6709\u4ece\u5916\u9762\u7ed5\u3002</p><p>\u3000\u3000\u8d70\u5230\u5df7\u5b50\u53e3\uff0c\u5c31\u63d2\u5230\u9648\u5983\u84c9\u5bb6\uff0c\u9053\u89c2\u5c31\u5728\u9648\u5983\u84c9\u5bb6\u540e\u9762\uff0c\u770b\u89c1\u8bb8\u601d\u4ece\u9648\u5983\u84c9\u5bb6\u7684\u9662\u95e8\u53e3\u5f80\u5916\u63a2\u5934\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u4f60\uff1f\u201d\u8bb8\u601d\u60ca\u559c\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u4eca\u5929\u624d\u521d\u4e8c\uff0c\u4f60\u4e0d\u662f\u56de\u4e1c\u793e\u8001\u5bb6\u8fc7\u6625\u8282\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u6709\u4eba\u731c\u5230\u662f\u6211\u7684\u58f0\u97f3\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\uff0c\u201c\u6211\u53c8\u6ca1\u6709\u54fc\u7740\u6b4c\u8fc7\u6765\uff0c\u8c01\u80fd\u731c\u5230\u6211\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u5230\u5df7\u5b50\u91cc\u72d7\u53eb\u5f97\u5389\u5bb3\uff0c\u8bb8\u7ef4\u4e00\u5b9a\u662f\u4e0d\u5e38\u5e72\u597d\u4e8b\u7684\u5c0f\u574f\u86cb\u6765\u4e86\uff0c\u51fa\u6765\u4e00\u770b\uff0c\u8fd8\u771f\u662f\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u7b97\u5076\u5c14\u5e72\u70b9\u597d\u4e8b\uff0c\u4f60\u4eec\u5c31\u8fd9\u4e48\u8bcb\u6bc1\u6211\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u65e9\u4e0a\u5750\u6211\u7238\u7684\u8f66\u56de\u5e02\u533a\u7684\uff0c\u8bb8\u8001\u5e08\u53eb\u6211\u5230\u8fd9\u91cc\u6765\u7b49\u4ed6\u3002\u4f60\u4eec\u4e00\u5927\u65e9\u90fd\u5230\u9648\u5983\u84c9\u5bb6\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u9648\u5983\u84c9\u5bb6\u660e\u5e74\u8bf7\u5ba2\uff0c\u4eca\u5929\u5c31\u8981\u52a8\u624b\u51c6\u5907\uff0c\u6211\u4eec\u8fc7\u6765\u5e2e\u5fd9\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u63a2\u5934\u5f80\u91cc\u770b\u4e86\u770b\uff0c\u8bb8\u7ef4\u8ddf\u9648\u5983\u84c9\u62ff\u7740\u7fbd\u6bdb\u7403\u62cd\u6b63\u5f80\u8fd9\u8fb9\u770b\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u5927\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u8bf4\u7684\u5427\uff0c\u5df7\u5b50\u91cc\u7684\u72d7\u53ea\u5bf9\u5c0f\u574f\u86cb\u51f6\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7684\u6bcd\u4eb2\u4e0e\u5218\u82ac\u5750\u5728\u8d70\u5eca\u91cc\uff0c\u8fd9\u65f6\u5019\u7ad9\u8d77\u6765\uff0c\u4e0d\u8ba9\u8bb8\u7ef4\u80e1\u8bf4\u516b\u9053\uff0c\u8bf7\u5f20\u606a\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u9648\u53d4\u8bf7\u6211\u660e\u5929\u8fc7\u6765\u5403\u996d\uff0c\u6211\u660e\u5929\u8d76\u7740\u53bb\u7701\u57ce\uff0c\u6240\u4ee5\u4eca\u5929\u63d0\u524d\u6765\u62dc\u5e74\u3002\u201d\u62f1\u624b\u4f5c\u63d6\uff0c\u53c8\u95ee\u8bb8\u601d\uff0c\u201c\u8bb8\u4f2f\u4f2f\u8ddf\u9648\u53d4\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5230\u83dc\u5e02\u573a\u4e70\u660e\u5929\u7684\u83dc\u53bb\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u624d\u521d\u4e8c\uff0c\u83dc\u5e02\u573a\u4f1a\u6709\u4eba\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u8c01\u90fd\u8ddf\u4f60\u5927\u5c11\u7237\u4f3c\u7684\uff0c\u5de5\u5382\u5f00\u5de5\u65e9\u7684\uff0c\u660e\u5929\u5c31\u4e0a\u5de5\u4e86\u3002\u201d\u8bb8\u7ef4\u5728\u4e00\u8fb9\u51b7\u5632\u70ed\u8bbd\u9053\uff0c\u201c\u9020\u7eb8\u5382\u90fd\u4e0d\u653e\u5e74\u5047\uff0c\u6211\u5988\u5927\u5e74\u521d\u4e00\u8fd8\u4e0a\u4e86\u73ed\u3002\u201d</p><p>\u3000\u3000\u9020\u7eb8\u5382\u90a3\u8fb9\u9664\u975e\u68c0\u4fee\u6216\u8005\u673a\u68b0\u6545\u969c\uff0c\u751f\u4ea7\u7ebf\u4e0d\u4f1a\u56e0\u4e3a\u5e74\u5047\u505c\u4e0b\u6765\uff0c\u8bb8\u601d\u6bcd\u4eb2\u5728\u4ed3\u5e93\u53ef\u4ee5\u8f6e\u4f11\u51e0\u5929\uff0c\u7b97\u662f\u6bd4\u8f83\u8f7b\u677e\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6478\u6478\u8111\u888b\uff0c\u8ddf\u8bb8\u7ef4\u6597\u5634\u662f\u4e0d\u660e\u667a\u7684\u884c\u4e3a\uff0c\u7b11\u563b\u563b\u7684\u95ee\u9648\u5983\u84c9\uff1a\u201c\u4f60\u4eec\u5728\u6253\u7fbd\u6bdb\u7403\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8bb8\u7ef4\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u5c06\u7fbd\u6bdb\u7403\u5f80\u4ed6\u8ddf\u524d\u4e00\u9012\uff1a\u201c\u8fd9\u8fd8\u662f\u7f51\u7403\u62cd\uff1f\u6211\u90fd\u7d2f\u4e00\u8eab\u6c57\u4e86\uff0c\u4f60\u6765\u6253\u5427\u3002\u201d</p><p>\u3000\u3000\u8bb8\u7ef4\u5c06\u7403\u62cd\u585e\u5230\u5f20\u606a\u624b\u91cc\uff0c\u9648\u5983\u84c9\u4e5f\u5c06\u7403\u62cd\u585e\u8bb8\u601d\u624b\u91cc\uff1a\u201c\u6211\u4e5f\u7d2f\u4e86\uff0c\u8bb8\u601d\u59d0\u4f60\u6765\u6253\u5427\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u8fd8\u7a7f\u7740\u5916\u5957\uff0c\u4fcf\u8138\u767d\u7699\uff0c\u989d\u5934\u4e0a\u90fd\u6ca1\u6709\u8d77\u6c57\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u521a\u521a\u4e0b\u573a\u3002\u8bb8\u601d\u8138\u7ea2\u6251\u6251\u7684\uff0c\u989d\u5934\u7684\u6c57\u8fd8\u6ca1\u6709\u5e72\u5462\u3002\u89c1\u9648\u5983\u84c9\u4e0d\u613f\u610f\u966a\u81ea\u5df1\u6253\u7403\uff0c\u4e5f\u4e0d\u60f3\u8bb8\u601d\u7d2f\u7740\uff0c\u5f20\u606a\u8038\u8038\u80a9\uff0c\u8d70\u5230\u8d70\u5eca\u91cc\u5c06\u7403\u62cd\u653e\u5c0f\u684c\u5b50\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u559c\u6b22\u7528\u52b2\u6253\uff0c\u4e00\u7528\u52b2\uff0c\u7403\u5c31\u98de\u5230\u5c4b\u9876\u4e0a\u53bb\u4e86\u3002\u201d\u56de\u5934\u5bf9\u8bb8\u601d\u8bf4\uff0c\u201c\u6211\u5230\u68cb\u9662\u7b49\u8bb8\u8001\u5e08\u53bb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8d70\u51fa\u9648\u5983\u84c9\u5bb6\u7684\u9662\u5b50\uff0c\u8bb8\u601d\u4ece\u540e\u9762\u8ffd\u4e0a\u6765\uff0c\u95ee\u4ed6\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u5173\u7cfb\u597d\u8d77\u6765\u4e86\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u65f6\u95f4\u597d\u8fc7\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\uff0c\u7a81\u7136\u60f3\u8d77\u6765\u5e74\u524d\u4ece\u9999\u6e2f\u5e26\u56de\u6765\u7684\u793c\u7269\uff0c\u8bb8\u601d\u90fd\u4e00\u4e00\u770b\u8fc7\uff0c\u90a3\u679a\u5d4c\u788e\u94bb\u7684\u53d1\u5939\u5979\u4e5f\u770b\u89c1\u8fc7\uff0c\u8fd9\u65f6\u5c31\u522b\u5728\u9648\u5983\u84c9\u7684\u5934\u4e0a\uff0c\u201c\u4f60\u662f\u8bf4\u5979\u5934\u4e0a\u7684\u53d1\u5361\uff1f\u201d</p><p>\u3000\u3000\u201c\u5bf9\u554a\uff0c\u4f60\u4ee5\u4e3a\u6211\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8981\u9001\u5979\u793c\u7269\uff0c\u5979\u591a\u534a\u80fd\u4e22\u6211\u8138\u4e0a\u6765\uff0c\u6211\u6562\u5417\uff1f\u201d\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u5510\u5a67\u8ba9\u5979\u5360\u5730\u65b9\u6446\u5730\u644a\u7684\u4e8b\uff0c\u4f60\u77e5\u9053\u5427\u2026\u2026\u53d1\u5939\u662f\u5510\u5a67\u9001\u5979\u7684\uff0c\u6211\u90fd\u5fc3\u75db\u6b7b\u4e86\uff0c\u5e26\u56de\u6765\u90a3\u4e48\u793c\u7269\uff0c\u8fd9\u5c5e\u8fd9\u652f\u53d1\u5939\u8d35\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u62bf\u5634\u7b11\u4e86\u8d77\u6765\uff0c\u4e00\u53cc\u7f8e\u7738\u64a9\u7740\u5f20\u606a\uff1a\u201c\u4f60\u5c0f\u5973\u670b\u53cb\u5012\u86ee\u6177\u6168\u7684\uff0c\u5979\u4e0d\u77e5\u9053\u4f60\u5bf9\u5983\u84c9\u8d77\u6b6a\u5fc3\u601d\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4fa7\u5934\u770b\u7740\u8bb8\u601d\u8273\u7f8e\u7684\u773c\u775b\uff0c\u5634\u89d2\u5fae\u5fae\u5f2f\u8d77\uff0c\u9732\u51fa\u8f7b\u4f7b\u800c\u6e29\u6696\u7684\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u5bf9\u4f60\u8d77\u5fc3\u601d\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u8138\u4e0a\u6de1\u6de1\u7684\u7b11\uff0c\u6df1\u9083\u7684\u9ed1\u8272\u773c\u7738\u91cc\u4f3c\u4e4e\u544a\u8bc9\u81ea\u5df1\u8fd9\u4e0d\u662f\u4e00\u53e5\u73a9\u7b11\u8bdd\uff0c\u8bb8\u601d\u5c06\u8eab\u5b50\u5fae\u5fae\u5f80\u540e\u4ef0\u4e86\u4ef0\uff0c\u867d\u7136\u5f20\u606a\u6ca1\u6709\u52a8\u5f39\uff0c\u5374\u611f\u89c9\u4ed6\u7684\u547c\u5438\u8fd1\u4e86\u4e00\u4e9b\uff0c\u6e29\u70ed\u7684\u9f3b\u9f3b\u90fd\u5feb\u6251\u5230\u81ea\u5df1\u7684\u8138\u4e0a\uff0c\u4e5f\u5c31\u611f\u89c9\u5230\u8138\u6709\u4e9b\u53d1\u70eb\uff0c\u62bf\u7740\u5634\u9a82\u4ed6\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff01\u201d\u773c\u775b\u5374\u8d77\u4e86\u5c11\u5973\u7684\u7f9e\u6da9\uff0c\u4f38\u624b\u63a8\u7740\u4ed6\u5f80\u5916\u8d70\uff0c\u201c\u4f60\u53bb\u627e\u8bb8\u8001\u5e08\uff0c\u6211\u4e0d\u8ddf\u4f60\u8fc7\u53bb\u4e86\uff0c\u7701\u5f97\u542c\u4f60\u75af\u8a00\u75af\u8bed\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u8bb8\u601d\u8eb2\u8fdb\u9662\u5b50\u91cc\uff0c\u5f20\u606a\u5634\u89d2\u6302\u7740\u6e29\u6696\u7684\u5fae\u7b11\u3002\u57ce\u5e02\u91cc\u7684\u6625\u8282\u591a\u5c11\u6709\u4e9b\u65e0\u804a\uff0c\u591a\u662f\u72ec\u751f\u5b50\u5973\uff0c\u90bb\u91cc\u53c8\u6ca1\u591a\u5728\u7684\u5f80\u6765\uff0c\u9053\u89c2\u91cc\u4e00\u65e9\u5c31\u805a\u4e86\u4e9b\u68cb\u9662\u7684\u5b66\u751f\uff0c\u7531\u4e8e\u6d77\u5dde\u6709\u6625\u8282\u4e0d\u7981\u8d4c\u7684\u98ce\u4fd7\uff0c\u51d1\u4e86\u4e24\u684c\u5728\u6253\u7eb8\u724c\uff0c\u8fd8\u6709\u51e0\u4e2a\u5c11\u5e74\u5728\u8d4c\u68cb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5230\u9053\u89c2\u6765\uff0c\u53c8\u4e48\u95f2\u626f\u6de1\uff0c\u8981\u4e48\u76f4\u63a5\u8ddf\u8bb8\u9e3f\u4f2f\u4e0b\u68cb\uff0c\u68cb\u9662\u91cc\u8ba4\u5f97\u5f20\u606a\u7684\u5b66\u751f\uff0c\u90fd\u5c0a\u656c\u4ed6\u7684\uff0c\u770b\u89c1\u4ed6\u8fdb\u6765\uff0c\u6709\u51e0\u4eba\u7ad9\u51fa\u6765\u8ba9\u4ed6\u53c2\u4e0e\u724c\u5c40\u3002</p><p>\u3000\u3000\u4e13\u5fc3\u5b66\u56f4\u68cb\u7684\u4eba\uff0c\u90fd\u7cbe\u4e8e\u5fc3\u7b97\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u628a\u63e1\u80fd\u8d62\u68cb\u9662\u5b66\u751f\u7684\u94b1\uff0c\u7b11\u7740\u8bf4\u7b49\u8bb8\u8001\u5e08\u8fc7\u6765\u3002\u68cb\u9662\u5b66\u751f\u4e00\u542c\u8bb8\u9e3f\u4f2f\u7b49\u4f1a\u513f\u8981\u8fc7\u6765\uff0c\u8fd8\u6709\u4e9b\u754f\u60e7\uff0c\u9a6c\u4e0a\u6536\u62fe\u724c\u5c40\uff0c\u7ea6\u597d\u53bb\u67d0\u67d0\u5bb6\u518d\u6218\uff0c\u8f6c\u773c\u95f4\u5c31\u8d70\u4e86\u4e00\u7a7a\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u8fc7\u6765\u8fd8\u611f\u5230\u5947\u602a\u5462\uff0c\u8fb9\u8de8\u8fdb\u9662\u5b50\u8fb9\u8bf4\uff1a\u201c\u8fd9\u4e9b\u5c0f\u5b69\u600e\u4e48\u4eca\u5929\u90fd\u68cb\u9662\u91cc\u6765\u73a9\uff0c\u600e\u4e48\u8fd9\u4e48\u65e9\u5c31\u8d70\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u800c\u4e0d\u7b54\uff0c\u95ee\u8bb8\u9e3f\u4f2f\uff1a\u201c\u627e\u6211\u8fc7\u6765\uff0c\u8bb8\u8001\u5e08\u6709\u4ec0\u4e48\u4e8b\u8981\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u62ff\u7740\u708a\u5177\u53bb\u70e7\u6c34\uff0c\u5634\u91cc\u5374\u8bf4\uff1a\u201c\u8bb8\u6d77\u5c71\u7684\u4e2b\u5934\u80fd\u8fc7\u6765\u5e2e\u6211\u4eec\u716e\u8336\u5c31\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bb8\u9e3f\u4f2f\u5012\u662f\u6709\u54c1\u5473\uff0c\u8ba9\u8bb8\u601d\u3001\u8bb8\u7ef4\u6765\u4f3a\u5019\u996e\u8336\uff0c\u53ef\u4e0d\u8d76\u4e0a\u65e7\u65f6\u738b\u4faf\u7684\u751f\u6d3b\uff0c\u60f3\u60f3\u5012\u662f\u5012\u7f8e\u7684\uff0c\u4e5f\u4e0d\u76f8\u4fe1\u8bb8\u9e3f\u4f2f\u80fd\u5728\u68cb\u9662\u91cc\u85cf\u4ec0\u4e48\u597d\u8336\uff0c\u8fd8\u4e0d\u8ba9\u90a3\u822c\u5b66\u68cb\u7684\u5c11\u5e74\u90fd\u5077\u7cbe\u5149\uff1f\u770b\u7740\u8bb8\u9e3f\u4f2f\u6446\u51fa\u6574\u5957\u7684\u8336\u5177\uff0c\u5bf9\u7d2b\u7802\u5668\u5177\u4e5f\u6b23\u8d4f\u4e0d\u6765\uff0c\u8fd9\u73a9\u827a\u513f\u5728\u5b9c\u5174\u8857\u5934\u5230\u5904\u90fd\u662f\uff0c\u53ef\u80fd\u4e5f\u5c31\u4e5d\u5341\u5e74\u4ee3\u521d\u671f\u8fd8\u6709\u4e9b\u542b\u91d1\u91cf\u3002</p><p>\u3000\u3000\u65e2\u7136\u8bb8\u9e3f\u4f2f\u618b\u7740\u4e0d\u8bf4\uff0c\u5f20\u606a\u4e5f\u53ea\u5f53\u8fc7\u6765\u996e\u8336\u3002\u592a\u9633\u4e00\u51fa\u6765\uff0c\u9662\u5b50\u91cc\u5c31\u6ca1\u6709\u5bd2\u6c14\u3002\u5f20\u606a\u5750\u5728\u9053\u89c2\u7684\u9662\u5b50\u91cc\uff0c\u542c\u8bb8\u9e3f\u4f2f\u8bf4\u4e86\u4e00\u4e0a\u5348\u996e\u8336\u7684\u5b66\u95ee\uff0c\u5f20\u606a\u4e5f\u704c\u4e86\u4e00\u809a\u5b50\u7684\u8336\u6c34\u3002\u4e34\u8fd1\u4e2d\u5348\uff0c\u9648\u5c71\u8fc7\u6765\u8bf7\u4ed6\u8ddf\u8bb8\u9e3f\u4f2f\u53bb\u559d\u9152\uff0c\u8bb8\u9e3f\u4f2f\u610f\u52a8\u8981\u53bb\uff0c\u5f20\u606a\u62a2\u7740\u8bf4\uff1a\u201c\u996e\u8336\u996e\u9971\u4e86\uff0c\u8d81\u7740\u4e2d\u5348\u8ddf\u8bb8\u8001\u5e08\u5b66\u4e00\u76d8\u68cb\uff0c\u5c11\u5403\u4e00\u987f\u65e0\u788d\u7684\u3002\u201d</p><p>\u3000\u3000\u9648\u5c71\u542c\u8bb8\u601d\u8bf4\u5f20\u606a\u660e\u5929\u8981\u8d76\u7740\u53bb\u7701\u57ce\uff0c\u660e\u5929\u4e0d\u80fd\u8bf7\u4ed6\u5403\u996d\uff0c\u4eca\u5929\u8d81\u4ed6\u4eba\u5728\u6c99\u7530\uff0c\u65e0\u8bba\u5982\u4f55\u90fd\u8981\u8bf7\u4ed6\u5403\u996d\u7684\uff0c\u4e0d\u8bf4\u5e74\u524d\u7684\u6170\u52b3\u91d1\u4ed6\u5e2e\u7740\u8bf4\u8bdd\uff0c\u5e74\u540e\u5218\u82ac\u8981\u53bb\u9020\u7eb8\u5382\u4e0a\u73ed\uff0c\u8fd8\u8981\u4ed6\u5e2e\u7740\u518d\u8a00\u8bed\u4e00\u58f0\u3002\u9648\u5c71\u542c\u5f20\u606a\u5e72\u51c0\u5229\u843d\u7684\u62d2\u7edd\u53bb\u4ed6\u5bb6\u5403\u996d\uff0c\u8138\u8272\u8baa\u8baa\u7684\uff0c\u7ad9\u5728\u90a3\u91cc\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u597d\u3002</p><p>\u3000\u3000\u201c\u68cb\u80fd\u7ba1\u9971\uff1f\u5c11\u5e74\u4eba\u5012\u662f\u597d\u5fc3\u6027\uff0c\u6211\u4e5f\u4e0d\u997f\uff0c\u5c31\u966a\u4f60\u4e0b\u4e00\u76d8\u68cb\u3002\u201d\u8bb8\u9e3f\u4f2f\u542c\u4e86\u722c\u8d77\u8eab\u6765\u53bb\u53d6\u68cb\u3002</p><p>\u3000\u3000\u201c\u90a3\u7b49\u4f60\u4eec\u4e0b\u597d\u68cb\u518d\u5f00\u5e2d\u4e5f\u4e0d\u8fdf\u3002\u201d\u9648\u5c71\u7b11\u7740\u8d70\u51fa\u9053\u89c2\uff0c\u8138\u4e0a\u5012\u6ca1\u6709\u7279\u522b\u7684\u4e0d\u8010\u70e6\u3002</p><p>\u3000\u3000\u201c\u9648\u5c71\u5bb6\u660e\u5929\u8bf7\u5ba2\uff0c\u4eca\u5929\u5f00\u5e2d\u53ef\u662f\u4e13\u95e8\u4e3a\u4f60\u3002\u201d\u8bb8\u9e3f\u4f2f\u62ff\u6765\u68cb\uff0c\u772f\u7740\u773c\u775b\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u52a8\u58f0\u8272\u7684\u8bf4\uff1a\u201c\u4f60\u704c\u4e86\u6211\u4e00\u4e0a\u5348\u7684\u8336\u6c34\uff0c\u6709\u4e8b\u4e0d\u8bf4\uff0c\u4e0d\u7136\u6211\u65e9\u8d70\u4e86\uff0c\u6ca1\u8ba1\u5212\u7559\u4e0b\u6765\u5403\u4e2d\u996d\uff0c\u4f60\u8981\u4e0d\u4e0b\u68cb\uff0c\u6211\u5148\u56de\u53bb\u4e86\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u8d76\u7d27\u653e\u4e0b\u68cb\u76d8\uff0c\u6b63\u4e8b\u6ca1\u8bf4\uff0c\u53ef\u4e0d\u80fd\u653e\u5f20\u606a\u8d70\u4eba\u3002\u5f20\u606a\u521a\u843d\u5b50\u4e09\u8fde\u661f\uff0c\u8bb8\u601d\u63a8\u7740\u9662\u95e8\u8fdb\u6765\uff0c\u62a2\u4e0b\u5f20\u606a\u624b\u91cc\u7684\u68cb\u5b50\u4e22\u68cb\u76d2\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u7b49\u7740\u4f60\u4eec\u5403\u996d\u5462\uff0c\u8fd8\u4e0b\u4ec0\u4e48\u68cb\uff0c\u5148\u6446\u8fd9\u91cc\uff0c\u7b49\u5403\u8fc7\u4e2d\u996d\u518d\u8bf4\u3002\u201d</p><p>\u3000\u3000\u559d\u8336\u6613\u997f\uff0c\u559d\u4e00\u4e0a\u5348\u7684\u8336\uff0c\u4e00\u6ce1\u5c3f\uff0c\u809a\u5b50\u91cc\u5168\u7a7a\u4e86\u3002\u8bb8\u9e3f\u4f2f\u7b11\u7740\u8bf4\uff1a\u201c\u68cb\u4e0b\u4e0d\u6210\uff0c\u53ea\u6709\u53bb\u559d\u9152\u4e86\u3002\u201d\u8bdd\u6ca1\u843d\uff0c\u4eba\u5148\u722c\u4e86\u8d77\u6765\uff0c\u8d70\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u7ed9\u8bb8\u601d\u6293\u7740\u624b\uff0c\u5f20\u606a\u89c1\u5979\u6709\u4e9b\u53d1\u6025\uff0c\u95ee\u5979\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5983\u84c9\u8ba9\u5979\u5988\u8bf4\u54ed\u4e86\uff0c\u4f60\u8fd8\u8eb2\u5728\u8fd9\u91cc\u4e0b\u68cb\uff0c\u4f60\u8bf4\u4f60\u6709\u6ca1\u6709\u826f\u5fc3\uff1f\u201d\u8bb8\u601d\u55d4\u602a\u7684\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u4ece\u8863\u515c\u91cc\u638f\u51fa\u4e1c\u897f\uff0c\u5f80\u5f20\u606a\u9762\u524d\u4e00\u644a\uff0c\u5510\u5a67\u9001\u7ed9\u9648\u5983\u84c9\u7684\u90a3\u679a\u5d4c\u788e\u94bb\u732b\u5f62\u53d1\u5939\u7ed9\u6485\u6210\u4e24\u534a\uff0c\u201c\u5468\u590d\u8fc7\u6765\u8bf4\u8fd9\u53d1\u5939\u7cbe\u8d35\uff0c\u8981\u503c\u597d\u51e0\u5343\uff0c\u5983\u84c9\u8bf4\u662f\u5973\u540c\u5b66\u9001\u7684\uff0c\u522b\u4eba\u90fd\u4e0d\u4fe1\uff0c\u5979\u4e00\u6025\u5c31\u6254\u5730\u4e0a\uff0c\u6454\u6210\u4e24\u63b0\uff0c\u82ac\u59e8\u8ba9\u5979\u6765\u8bf7\u4f60\u8fc7\u53bb\u5403\u996d\uff0c\u5979\u4e0d\u613f\uff0c\u9648\u53d4\u8fc7\u6765\u8bf7\u4f60\uff0c\u4f60\u5012\u662f\u62ff\u67b6\u5b50\u4e86\uff0c\u9648\u53d4\u4e00\u56de\u53bb\uff0c\u82ac\u59e8\u53c8\u8bf4\u4e86\u5979\u4e24\u53e5\uff0c\u5c31\u54ed\u4e86\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8d77\u53d1\u5939\u770b\u4e86\u770b\uff0c\u5482\u5482\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u9576\u5d4c\u6280\u672f\u86ee\u597d\u7684\uff0c\u53d1\u5939\u90fd\u6454\u6210\u4e24\u622a\uff0c\u788e\u94bb\u4e00\u7c92\u90fd\u6ca1\u6709\u6389\u3002\u5468\u590d\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u4e0d\u662f\u8bf7\u4ed6\u8fc7\u6765\u966a\u4f60\uff0c\u4f60\u73b0\u5728\u662f\u5927\u4eba\u7269\u4e86\u2026\u2026\u201d\u89c1\u5f20\u606a\u5634\u89d2\u6302\u7740\u90aa\u9b45\u7684\u7b11\u610f\uff0c\u8bb8\u601d\u6ca1\u597d\u6c14\u7684\u8bf4\uff0c\u201c\u4f60\u554a\uff0c\u600e\u4e48\u8bf4\u4f60\u597d\uff0c\u4f60\u7adf\u7136\u8fd8\u7b11\u5f97\u8d77\u6765\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u53cd\u624b\u63e1\u7740\u8bb8\u601d\u7684\u624b\u638c\uff0c\u624b\u6307\u6263\u7740\u5979\u7684\u624b\u5fc3\uff0c\u611f\u89c9\u6e29\u6da6\u5f97\u5f88\uff0c\u772f\u773c\u770b\u7740\u5979\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u505a\u9519\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5973\u5b69\u5b50\u7684\u5fc3\u601d\uff0c\u4f60\u53c8\u4e0d\u662f\u4e0d\u61c2\uff1f\u201d\u8bb8\u601d\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u89c1\u4ed6\u9732\u51fa\u65e0\u8f9c\u7684\u773c\u795e\uff0c\u54ac\u7740\u4e0b\u5507\u6068\u6068\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u8fd8\u88c5\u4e0d\u61c2\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u5973\u5b69\u5b50\uff0c\u5c31\u662f\u592a\u654f\u611f\u4e86\uff0c\u8fd8\u662f\u8bb8\u601d\u59d0\u597d\u3002\u8fd9\u53d1\u5939\u9000\u7ed9\u6211\uff0c\u4e0d\u5f97\u8ba9\u522b\u4eba\u4ee5\u4e3a\u771f\u662f\u6211\u9001\u5979\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf4\u522b\u4eba\u5c0f\uff0c\u4f60\u591a\u5927\uff1f\u201d\u6709\u65f6\u5019\u613f\u610f\u8ba9\u4ed6\u63e1\u7740\u81ea\u5df1\u7684\u624b\uff0c\u4e00\u542c\u4ed6\u8bf4\u8d77\u5e74\u9f84\uff0c\u8bb8\u601d\u5c31\u5c06\u624b\u62bd\u4e86\u56de\u6765\uff0c\u7adf\u7136\u611f\u5230\u4e00\u4e1d\u5fc3\u9178\uff0c\u89c1\u5f20\u606a\u53c8\u5c06\u6454\u6210\u4e24\u63b0\u7684\u53d1\u5939\u5230\u81ea\u5df1\u624b\u91cc\uff0c\u95ee\u4ed6\uff0c\u201c\u4f60\u4ea4\u7ed9\u6211\uff0c\u6211\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u53d1\u5939\u662f\u5510\u5a67\u9001\u7684\uff0c\u8bb8\u601d\u4e5f\u65e0\u6cd5\u5e2e\u9648\u5983\u84c9\u89e3\u91ca\uff0c\u5c31\u7b97\u89e3\u91ca\u4e86\uff0c\u5979\u7236\u6bcd\u4e5f\u4e0d\u4fe1\uff0c\u5218\u82ac\u4f3c\u4e4e\u66f4\u613f\u610f\u76f8\u4fe1\u662f\u5f20\u606a\u9001\u7684\uff0c\u5468\u590d\u5f00\u73a9\u7b11\u65f6\uff0c\u5218\u82ac\u8fd8\u86ee\u5f97\u610f\uff0c\u4e00\u89c1\u9648\u5983\u84c9\u5c06\u53d1\u5939\u6454\u6210\u4e24\u63b0\uff0c\u624d\u8d77\u4e86\u607c\uff0c\u8bad\u8d77\u9648\u5983\u84c9\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u662f\u53e6\u627e\u5730\u65b9\u5403\u4e2d\u996d\u5427\uff0c\u8fd9\u4e8b\u60c5\u89e3\u91ca\u4e0d\u6e05\uff0c\u7ed9\u5927\u5bb6\u76ef\u7740\u602a\u96be\u53d7\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u62cd\u62cd\u5c41\u80a1\u8d70\u4e86\uff0c\u9648\u5983\u84c9\u90a3\u8fb9\u600e\u4e48\u529e\uff1f\u201d\u8bb8\u601d\u63a8\u4e86\u4ed6\u4e00\u628a\uff0c\u201c\u5c0f\u5fc3\u8bb8\u7ef4\u4e00\u76f4\u627e\u4f60\u7684\u523a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u8d77\u8eab\u63a8\u7740\u8bb8\u601d\u7684\u80a9\u8180\u5f80\u524d\u8d70\uff1a\u201c\u884c\uff0c\u884c\uff0c\u4e0d\u5c31\u5403\u996d\u5417\uff0c\u8fd8\u80fd\u628a\u6211\u5bb0\u4e86\u4e0b\u9152\uff1f\u201d</p><p>\u3000\u3000\u9152\u5e2d\u6446\u5728\u4e1c\u9762\u7684\u9152\u5c4b\u5b50\u91cc\uff0c\u5f20\u606a\u8ddf\u8bb8\u601d\u5148\u8fdb\u9662\u5b50\uff0c\u770b\u89c1\u5468\u590d\u8e72\u5728\u5eca\u6a90\u4e0b\u62bd\u70df\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5927\u8fc7\u5e74\u7684\uff0c\u5468\u5382\u957f\u8dd1\u8fd9\u91cc\u6765\u8e6d\u996d\u5403\u4e86\uff0c\u8d76\u4e0d\u53ca\u660e\u5929\uff1f\u201d</p><p>\u3000\u3000\u5468\u590d\u53c8\u4e0d\u80fd\u89e3\u8bf4\u662f\u9648\u5c71\u7279\u610f\u6253\u7535\u8bdd\u8bf7\u4ed6\u6765\u7684\uff0c\u8baa\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u5929\u8981\u5728\u5382\u5b50\u91cc\u503c\u73ed\uff0c\u53ef\u4e0d\u80fd\u6f0f\u4e86\u8fd9\u987f\u9152\u3002\u201d</p><p>\u3000\u3000\u9648\u5c71\u62db\u547c\u5927\u5bb6\u5230\u9152\u5c4b\u91cc\u53bb\uff0c\u9648\u5983\u84c9\u4ece\u91cc\u53a2\u623f\u51fa\u53bb\uff0c\u8138\u988a\u4e0a\u7684\u6cea\u75d5\u90fd\u6ca1\u6709\u5e72\uff0c\u695a\u695a\u53ef\u601c\uff0c\u76ef\u7740\u5f20\u606a\u95ee\uff1a\u201c\u4f60\u8ddf\u6211\u7238\u5988\u8bf4\uff0c\u53d1\u5939\u5230\u5e95\u662f\u8c01\u9001\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u60f3\u5230\u9648\u5983\u84c9\u8fd9\u822c\u53d7\u59d4\u5c48\u7684\u6837\u5b50\uff0c\u9887\u6709\u4e0d\u53d7\u4e00\u70b9\u803b\u8fb1\u7684\u9aa8\u6c14\uff0c\u5012\u4e5f\u6709\u4e9b\u4e0d\u5fcd\u5fc3\uff0c\u4ece\u8bb8\u601d\u90a3\u91cc\u63a5\u8fc7\u4e24\u63b0\u53d1\u5939\uff1a\u95ee\u9648\u5983\u84c9\uff0c\u201c\u4f60\u8bf4\u8fd9\u4e2a\uff1f\u201d\u89c1\u5927\u5bb6\u90fd\u770b\u5411\u8fd9\u91cc\uff0c\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u5802\u59b9\u5b50\u4ece\u591c\u5e02\u4e70\u7684\u5730\u644a\u8d27\uff0c\u4e5f\u4e0d\u77e5\u9053\u503c\u51e0\u4e2a\u94b1\uff0c\u6211\u4ece\u4f60\u90a3\u4e9b\u62ff\u51e0\u672c\u4e66\uff0c\u6211\u5802\u59b9\u5b50\u53c8\u95f9\u7740\u6446\u644a\u73a9\uff0c\u89c1\u8fd9\u4e48\u9ebb\u70e6\u4f60\u6709\u4e9b\u8fc7\u610f\uff0c\u4f60\u4e0d\u7a00\u7f55\u5c31\u62c9\u5012\u4e86\uff0c\u201d\u56de\u8eab\u5c31\u5c06\u4e24\u622a\u53d1\u5939\u6254\u8fc7\u9662\u5899\uff0c\u53ea\u542c\u89c1\u201c\u5619\u201d\u4e24\u58f0\u7ec6\u5fae\u7684\u6c34\u54cd\uff0c\u5d4c\u6ee1\u788e\u94bb\u7684\u53d1\u5939\u4e22\u4e1c\u9762\u7684\u758f\u6e2f\u6cb3\u91cc\u4e86\uff0c\u5f20\u606a\u62cd\u62cd\u624b\uff0c\u201c\u771f\u5f53\u503c\u51e0\u4e2a\u94b1\uff1f\u597d\u4e86\uff0c\u5403\u996d\u5427\uff0c\u6211\u809a\u5b50\u997f\u4e86\u3002\u201d</p></article>"}}}]);
