(window.webpackJsonp=window.webpackJsonp||[]).push([[1279],{1302:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e5d\u8282 \u80dc\u5229\uff1f",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e5d\u8282 \u80dc\u5229\uff1f</h2><p>\u796d\u575b\u4e4b\u4e0a\uff0c\u6076\u9b54\u6b63\u4e3a\u81ea\u5df1\u7684\u65b0\u53d1\u73b0\u800c\u72c2\u559c\uff0c\u6839\u672c\u5c31\u4e0d\u66fe\u5728\u610f\u8fd9\u5c0f\u5c0f\u7684\u6270\u4e71\uff0c\u4ed6\u51b7\u7b11\u7740\uff0c\u770b\u7740\u6885\u83f2\u65af\u7684\u53cc\u773c\uff0c\u51c6\u5907\u4f7f\u7528\u4e00\u4e2a\u975e\u5e38\u5f3a\u5927\uff0c\u540c\u65f6\u4e5f\u6781\u5176\u8017\u8d39\u80fd\u529b\u7684\u6cd5\u672f\u3002\u5982\u679c\u6210\u529f\uff0c\u4ed6\u4fbf\u53ef\u4ee5\u5b8c\u7f8e\u5730\u5360\u636e\u8fd9\u4f4d\u5973\u5723\u6b66\u58eb\u7684\u8eab\u8eaf\uff0c\u6781\u5927\u5730\u5f3a\u5316\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u76f8\u6bd4\u8f83\u8d77\u6765\uff0c\u9ece\u660e\u4e4b\u77f3\u90fd\u663e\u5f97\u4e0d\u662f\u90a3\u4e48\u7279\u522b\u91cd\u8981\u4e86\u3002</p><p>\u672c\u4ee5\u4e3a\u8fd9\u6b21\u524d\u6765\u6709\u4e9b\u4e0d\u987a\u5229\uff0c\u6ca1\u60f3\u5230\u7ed3\u679c\u5374\u662f\u6709\u610f\u5916\u6536\u83b7\u3002</p><p>\u8482\u5a1c\u5c91\u873f\u8712\u6e38\u52a8\u7740\u8eab\u8eaf\uff0c\u6089\u6089\u7c0c\u7c0c\u5730\u6e38\u4e0b\u796d\u575b\uff0c\u8d8a\u8fc7\u4eba\u9762\u72ee\u7fa4\uff0c\u671d\u743c\u6069\u8d70\u6765\u3002\u5979\u88ab\u9ece\u660e\u4e4b\u77f3\u7684\u5723\u5149\u7167\u5c04\u4f24\u5bb3\uff0c\u867d\u7136\u6709\u683c\u62c9\u5179\u7279\u7ffc\u62a4\uff0c\u7ec8\u7a76\u4e5f\u906d\u4e86\u4e9b\u6c60\u9c7c\u4e4b\u6b83\u3002\u539f\u672c\u7f8e\u4e3d\u7684\u8138\u5e9e\u4e0a\u9732\u51fa\u51e0\u4e2a\u5751\u5751\u6d3c\u6d3c\u7684\u51f9\u9677\u6765\uff0c\u6ee1\u662f\u715e\u6c14\uff0c\u663e\u5f97\u5206\u5916\u72f0\u72de\u6050\u6016\u3002\u521a\u624d\u66f4\u88ab\u6885\u83f2\u65af\u7ed9\u780d\u65ad\u4e86\u5de6\u624b\uff0c\u8fd9\u4e5f\u5bfc\u81f4\u5979\u6682\u65f6\u5df2\u7ecf\u6ca1\u529e\u6cd5\u65bd\u6cd5\u4e86\u2014\u2014\u5965\u672f\u5927\u90e8\u5206\u90fd\u8981\u6c42\u7cbe\u5de7\u590d\u6742\u7684\u624b\u52bf\u914d\u5408\uff0c\u4e24\u53ea\u624b\u90fd\u9700\u8981\u7684\uff0c\u4e00\u53ea\u624b\u6ca1\u6cd5\u5b8c\u6210\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u4e5f\u5e76\u4e0d\u662f\u8bf4\u5979\u4ece\u6b64\u5c31\u6210\u4e86\u5e9f\u4eba\uff0c\u53ea\u8981\u628a\u4eca\u5929\u8fd9\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u4e8b\u60c5\u90fd\u89e3\u51b3\u4e86\uff0c\u683c\u62c9\u5179\u7279\u4e00\u9ad8\u5174\uff0c\u81ea\u7136\u8fd8\u662f\u4f1a\u5e2e\u5979\u63a5\u4e0a\u65ad\u8155\u7684\u3002\u5bf9\u4e8e\u4e4c\u9eef\u541b\u4e3b\u6765\u8bf4\uff0c\u65ad\u80a2\u91cd\u751f\u538b\u6839\u4e0d\u662f\u4ec0\u4e48\u96be\u4e8b\uff0c\u800c\u4e14\u76f8\u6bd4\u8d77\u5176\u4ed6\u6076\u9b54\uff0c\u683c\u62c9\u5179\u7279\u7684\u813e\u6c14\u8fd8\u7b97\u662f\u6bd4\u8f83\u6e29\u548c\u4e86\uff0c\u5e76\u4e0d\u592a\u559c\u6b22\u8fc1\u6012\u4e8e\u4eba\u3002</p><p>\u5148\u5f97\u628a\u8fd9\u4e2a\u7a81\u7136\u5192\u51fa\u6765\u7684\u7537\u4eba\u6740\u6b7b\uff0c\u8482\u5a1c\u82a9\u60f3\u3002</p><p>\u201c\u53ef\u6076\u7684\u4eba\u7c7b\u3002\u201d\u5979\u5636\u543c\u7740\uff0c\u53f3\u624b\u6267\u7740\u94f6\u8272\u86c7\u5f62\u77ed\u5251\uff0c\u8981\u6765\u6740\u6b7b\u743c\u6069\u3002\u5deb\u5e08\u6323\u624e\u7740\uff0c\u60f3\u8981\u8d77\u8eab\u9003\u8dd1\uff0c\u4f46\u4ed6\u53d1\u73b0\u81ea\u5df1\u5168\u8eab\u6bcf\u4e2a\u90e8\u4f4d\u90fd\u5728\u75bc\u75db\uff0c\u56db\u80a2\u6839\u672c\u4e0d\u542c\u4f7f\u5524\u3002\u66f4\u8981\u547d\u7684\u662f\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u8eab\u4e0a\u7684\u7d22\u5c14\u77f3\u884c\u672f\u4f3c\u4e4e\u5931\u6548\u4e86\uff0c\u73b0\u5728\u5df2\u7ecf\u6ca1\u6cd5\u94bb\u5165\u5730\u4e0b\u3002</p><p>\u5b8c\u86cb\uff0c\u96be\u9053\u4eca\u5929\u5c31\u8981\u6b7b\u4e8e\u6b64\u5730\u3002</p><p>\u8482\u5a1c\u5c91\u9ad8\u4e3e\u86c7\u5251\u671d\u5deb\u5e08\u523a\u6765\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u521a\u624d\u88ab\u9707\u98de\u7684\u9f99\u9cde\u76fe\u6025\u901f\u65cb\u56de\uff0c\u6321\u4f4f\u8fd9\u4e00\u51fb\u3002\u86c7\u5251\u649e\u5728\u76fe\u9762\u4e0a\uff0c\u8ff8\u51fa\u9ed1\u8272\u7684\u706b\u661f\u3002\u8482\u5a1c\u5c91\u53cc\u773c\u6cdb\u51fa\u5bd2\u5149\uff0c\u624b\u4e0a\u52a0\u52b2\uff0c\u5251\u5203\u62b5\u5728\u76fe\u9762\u4e0a\u5212\u8fc7\uff0c\u53d1\u51fa\u523a\u8033\u7684\u6469\u64e6\u58f0\u3002</p><p>\u9f99\u9cde\u76fe\u518d\u6b21\u88ab\u9707\u5f00\uff0c\u4f46\u77ed\u6682\u7684\u963b\u62e6\u8ba9\u5deb\u5e08\u83b7\u5f97\u4e86\u5598\u606f\u4e4b\u673a\u3002\u4ed6\u770b\u7740\u8482\u5a1c\u5c91\u9760\u8fd1\uff0c\u7a81\u7136\u6296\u624b\u5f39\u51fa\u4e00\u9897\u5c0f\u5c0f\u7684\u5706\u77f3\uff0c\u671d\u5979\u7838\u53bb\u3002</p><p>\u77f3\u5f39\u672f\u3002</p><p>\u8fd9\u662f\u743c\u6069\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e8c\u5c42\u540e\uff0c\u6240\u638c\u63e1\u7684\u7b2c\u4e00\u4e2a\u9b54\u6cd5\uff0c\u53ef\u8c13\u5341\u5206\u7cbe\u719f\u3002\u4ed6\u77e5\u9053\u81ea\u5df1\u6700\u5927\u7684\u7f3a\u9677\uff0c\u5c31\u5728\u4e8e\u56e0\u4e3a\u53cd\u611f\u7528\u9b54\u6cd5\u76f4\u63a5\u4f24\u5bb3\u4ed6\u4eba\uff0c\u5bfc\u81f4\u5728\u7cbe\u7814\u5b66\u6d3e\u65f6\uff0c\u653e\u5f03\u4e86\u6700\u5177\u653b\u51fb\u6027\u7684\u5851\u80fd\u548c\u4ea1\u7075\u4e24\u5927\u5b66\u6d3e\u3002\u4f46\u4e16\u4e8b\u5343\u53d8\u4e07\u5316\uff0c\u603b\u4f1a\u9047\u4e0a\u9700\u8981\u76f4\u63a5\u653b\u51fb\u7684\u65f6\u5019\uff0c\u5176\u4ed6\u5b66\u6d3e\u9b54\u6cd5\u5728\u8fd9\u65b9\u9762\u5c31\u9887\u4e3a\u4e0d\u8db3\u4e86\u3002\u77f3\u5f39\u672f\u662f\u4ed6\u76ee\u524d\u638c\u63e1\u7684\u9b54\u6cd5\u4e2d\u6700\u5177\u76f4\u63a5\u6740\u4f24\u529b\u7684\uff0c\u603b\u8981\u51c6\u5907\u4e00\u4e2a\uff0c\u4ee5\u9632\u4e07\u4e00\u3002</p><p>\u4ed6\u66fe\u7ecf\u7528\u77f3\u5f39\u672f\u4e00\u53d1\u91cd\u4f24\u8fc7\u9a6c\u65af\u514b\u7684\u7948\u5e76\u8005\uff0c\u70b8\u778e\u4e86\u4ed6\u7684\u773c\u775b\uff0c\u5f53\u7136\u8fd9\u662f\u649e\u4e0a\u4e86\u5927\u8fd0\u3002\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u77f3\u5f39\u672f\u662f\u6ca1\u6709\u8fd9\u7b49\u5a01\u529b\u7684\uff0c\u9664\u975e\u7838\u4e2d\u8981\u5bb3\u90e8\u4f4d\u3002</p><p>\u4ed6\u7167\u7740\u8482\u5a1c\u5c91\u7684\u8138\u7838\u53bb\uff0c\u4f46\u56e0\u4e3a\u5168\u8eab\u75bc\u75db\uff0c\u808c\u8089\u4e0d\u542c\u63a7\u5236\uff0c\u663e\u7136\u504f\u4e86\u51c6\u5934\u3002\u8482\u5a1c\u5c91\u89c1\u77f3\u5f39\u7838\u6765\uff0c\u51b7\u7b11\u7740\u5c06\u5934\u4e00\u504f\uff0c\u773c\u770b\u5c31\u8981\u907f\u8ba9\u8fc7\u53bb\u3002\u77f3\u5f39\u672f\u5176\u5b9e\u8bf4\u5f97\u901a\u4fd7\u70b9\uff0c\u5c31\u6709\u70b9\u7c7b\u4f3c\u5730\u7403\u4e0a\u7684\u624b\u69b4\u5f39\uff0c\u9760\u7684\u5c31\u662f\u51fb\u4e2d\u65f6\u7206\u70b8\u4f24\u4eba\u3002\u4e00\u65e6\u88ab\u907f\u8ba9\u8fc7\u53bb\uff0c\u4e5f\u5c31\u6ca1\u6709\u4ec0\u4e48\u5a01\u529b\u4e86\u3002</p><p>\u8fd9\u4e0b\u771f\u9ed4\u9a74\u6280\u7a77\u4e86\u3002</p><p>\u743c\u6069\u7684\u5de6\u624b\u5df2\u7ecf\u4f38\u5165\u957f\u888d\u5185\u4fa7\uff0c\u63e1\u4f4f\u4e86\u88c5\u6ee1\u7070\u84dd\u8272\u836f\u6c34\u7684\u5c0f\u74f6\uff0c\u51c6\u5907\u559d\u4e0b\u53bb\u3002\u60c5\u51b5\u7d27\u6025\uff0c\u522b\u7684\u90fd\u5df2\u7ecf\u987e\u4e0d\u5f97\uff0c\u53ea\u80fd\u5148\u4fdd\u81ea\u5df1\u7684\u6027\u547d\u4e86\uff0c\u8fd9\u74f6\u836f\u6c34\u53ef\u4ee5\u8ba9\u4ed6\u7684\u8eab\u4f53\u77ac\u95f4\u9690\u5f62\uff0c\u540c\u65f6\u53d8\u6210\u865a\u4f53\uff0c\u65e0\u6cd5\u88ab\u5b9e\u7269\u6240\u78b0\u89e6\u4f24\u5bb3\u3002\u6b63\u5f53\u6b64\u65f6\uff0c\u9661\u7136\u95f4\u89c1\u90a3\u5df2\u7ecf\u4ece\u8482\u5a1c\u5c91\u8138\u8fb9\u98de\u8fc7\u7684\u77f3\u5f39\u786c\u751f\u751f\u5728\u7a7a\u4e2d\u8f6c\u6298\uff0c\u76f4\u76f4\u5730\u7838\u5728\u5bf9\u65b9\u7684\u592a\u9633\u7a74\u4e0a\uff0c\u8f70\u7136\u7206\u70b8\u5f00\u6765\u3002</p><p>\u706b\u5149\u7729\u8000\u3001\u6c14\u6d6a\u7ffb\u6eda\uff0c\u8482\u5a1c\u5c91\u5e9e\u5927\u7684\u534a\u4eba\u534a\u86c7\u8eab\u4f53\u7ed9\u76f4\u76f4\u8f70\u98de\u4e86\u51fa\u53bb\uff0c\u534a\u4e2a\u5934\u9885\u90fd\u5df2\u7ecf\u88ab\u70b8\u788e\uff0c\u5269\u4e0b\u7684\u534a\u5f20\u8138\u4e0a\uff0c\u6b7b\u6b7b\u77aa\u5927\u7684\u773c\u775b\u4e2d\u5145\u6ee1\u60ca\u9a87\u548c\u4e0d\u53ef\u601d\u8bae\uff0c\u4eff\u4f5b\u5230\u6b7b\u4e5f\u4e0d\u76f8\u4fe1\u600e\u4e48\u4f1a\u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\u3002</p><p>\u743c\u6069\u81ea\u5df1\u90fd\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u77f3\u5f39\u672f\u5df2\u7ecf\u6253\u51fa\u53bb\uff0c\u4ed6\u4e5f\u6ca1\u80fd\u529b\u63a7\u5236\u4e2d\u9014\u8f6c\u5411\u7684\u3002\u80fd\u7a81\u7136\u63d2\u624b\u5f71\u54cd\u6539\u53d8\u4e00\u4e2a\u6cd5\u672f\u2014\u2014\u5c3d\u7ba1\u662f\u4e2a\u4f4e\u9636\u6cd5\u672f\u2014\u2014\u7684\u6548\u679c\uff0c\u8fd9\u9700\u8981\u975e\u5e38\u9ad8\u660e\u7684\u6280\u5de7\u548c\u9b54\u6cd5\u9020\u8be3\uff0c\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\u91cc\u7684\u90a3\u4e9b\u6559\u6388\u6216\u8bb8\u80fd\u52c9\u5f3a\u505a\u5230\u3002\u4f46\u95ee\u9898\u8fd8\u4e0d\u4ec5\u5728\u6b64\uff0c\u77f3\u5f39\u672f\u4e0d\u4f46\u4e2d\u9014\u8f6c\u5411\uff0c\u800c\u4e14\u663e\u7136\u5a01\u529b\u9661\u7136\u95f4\u5f3a\u5316\u4e86\u51e0\u500d\u751a\u81f3\u5341\u51e0\u500d\uff0c\u5426\u5219\u81f3\u591a\u8ba9\u8482\u5a1c\u5c91\u53d7\u4f24\uff0c\u7edd\u4e0d\u53ef\u80fd\u76f4\u63a5\u51fb\u6bd9\u5979\u3002</p><p>\u7206\u70b8\u58f0\u5c06\u6b63\u5728\u51c6\u5907\u4fb5\u5360\u6885\u83f2\u65af\u8eab\u4f53\u7684\u6076\u9b54\u7684\u6ce8\u610f\u529b\u4e5f\u5438\u5f15\u8fc7\u6765\uff0c\u4ed6\u671d\u8fd9\u8fb9\u77a5\u4e86\u4e00\u773c\uff0c\u968f\u5373\u6df1\u78a7\u8272\u7684\u77b3\u5b54\u772f\u7f1d\u8d77\u6765\uff0c\u53d8\u5f97\u72ed\u957f\uff0c\u5c31\u50cf\u86c7\u4e00\u6837\uff0c\u900f\u51fa\u6151\u4eba\u7684\u5bd2\u5149\u3002</p><p>\u201c\u5514\uff0c\u6b27\u51ef\uff0c\u662f\u4f60\u554a\u2026\u2026\u201d</p><p>\u6076\u9b54\u7684\u58f0\u97f3\u5e73\u6de1\u60a0\u957f\uff0c\u4eff\u4f5b\u53ea\u662f\u8d70\u8def\u65f6\u5076\u7136\u649e\u4e0a\u4e86\u8001\u670b\u53cb\uff0c\u70b9\u70b9\u5934\uff0c\u6253\u4e2a\u62db\u547c\u800c\u5df2\u3002\u4f46\u8bdd\u97f3\u672a\u843d\uff0c\u5e73\u5730\u547c\u5730\u5377\u8d77\u4e00\u9053\u65cb\u98ce\uff0c\u5176\u4e2d\u5939\u6742\u7740\u65e0\u6570\u7ec6\u5c0f\u7684\u7d2b\u8272\u95ea\u7535\uff0c\u671d\u796d\u575b\u4e0a\u7684\u6076\u9b54\u5377\u53bb\u3002\u6b64\u65f6\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u8fd8\u662f\u865a\u50cf\u72b6\u6001\uff0c\u5df2\u7ecf\u629b\u5f03\u4e86\u7267\u5e08\u7684\u5c38\u8eab\uff0c\u53c8\u8fd8\u672a\u6210\u529f\u4fb5\u5360\u6885\u83f2\u65af\u7684\u8eaf\u4f53\uff0c\u5904\u4e8e\u6700\u865a\u5f31\u7684\u65f6\u671f\u3002\u4ed6\u7684\u53cc\u624b\u865a\u865a\u4e00\u5408\uff0c\u4e00\u9053\u9ed1\u708e\u706b\u5899\u5728\u8eab\u524d\u817e\u8d77\uff0c\u963b\u62e6\u65cb\u98ce\u3002</p><p>\u4e0e\u6b64\u540c\u65f6\uff0c\u5728\u6076\u9b54\u8eab\u540e\uff0c\u9ed1\u8272\u7684\u7a7a\u95f4\u901a\u9053\u7f13\u7f13\u6253\u5f00\uff0c\u4ed6\u51c6\u5907\u64a4\u9000\uff0c\u56de\u5230\u6df1\u6e0a\u3002\u6295\u5f71\u4e0d\u80fd\u5728\u7269\u8d28\u754c\u505c\u7559\u592a\u4e45\uff0c\u800c\u4eca\u5929\u53d1\u751f\u7684\u610f\u5916\u592a\u591a\u4e86\u3002\u5982\u679c\u6363\u4e71\u8005\u4ec5\u4ec5\u662f\u4e00\u4e2a\u5973\u5723\u6b66\u58eb\u548c\u4e00\u4e2a\u4e09\u6d41\u5deb\u5e08\uff0c\u90a3\u4e48\u683c\u62c9\u5179\u7279\u8fd8\u6709\u8db3\u591f\u7684\u81ea\u4fe1\u63a7\u5236\u5c40\u52bf\uff0c\u4f46\u5982\u4eca\u52a0\u4e0a\u6697\u4e2d\u6f5c\u4f0f\u7684\u8fd9\u4e2a\u5bb6\u4f19\u2026\u2026</p><p>\u4e4c\u9eef\u541b\u4e3b\u4ece\u4e0d\u4ee5\u8eab\u6d89\u9669\uff0c\u505a\u6ca1\u628a\u63e1\u7684\u4e8b\u60c5\u3002</p><p>\u4ed6\u54c8\u54c8\u5927\u7b11\u7740\uff0c\u7f13\u7f13\u540e\u9000\uff0c\u8e0f\u5165\u9ed1\u8272\u7684\u7a7a\u95f4\u901a\u9053\uff0c\u8eab\u5f62\u6e10\u6e10\u9690\u6ca1\u3002\u201c\u6211\u8fd8\u4f1a\u56de\u6765\u3002\u201d\u4ed6\u5ba3\u544a\u7740\uff0c\u4e0d\u6025\u4e0d\u8e81\uff0c\u9547\u5b9a\u81ea\u82e5\uff0c\u4eff\u4f5b\u5728\u9648\u8ff0\u4e00\u4ef6\u5df2\u7ecf\u53d1\u751f\u7684\u4e8b\u5b9e\u3002\u6076\u9b54\u7684\u76ee\u5149\u7f13\u7f13\u626b\u89c6\u8fc7\u5927\u5385\uff0c\u6700\u540e\u843d\u5728\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u4ed6\u7684\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u6765\u3002</p><p>\u201c\u6211\u4eec\u8fd8\u4f1a\u518d\u89c1\u5230\u3002\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u968f\u5373\u6d88\u5931\u5728\u865a\u65e0\u4e2d\u3002</p></article>"}}}]);
