(window.webpackJsonp=window.webpackJsonp||[]).push([[128],{151:function(p){p.exports={chapterTitle:null,title:"\u7b2c1109\u7ae0 \u8d4e\u8eab\u4e0e\u5939\u7740\u817f\u8d70\u8def",content:"<article><h2>\u7b2c1109\u7ae0 \u8d4e\u8eab\u4e0e\u5939\u7740\u817f\u8d70\u8def</h2><p>\u3000\u3000\u5f20\u606a\u5c06\u624b\u673a\u8fd8\u7ed9\u59da\u575a\uff0c\u89c1\u5b8b\u5a49\u4f69\u51dd\u7738\u770b\u7740\u81ea\u5df1\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5b8b\u5c0f\u59d0\u7684\u8981\u6c42\u5f88\u7b80\u5355\uff0c\u8fd9\u7b14\u94b1\u6682\u65f6\u7531\u534e\u97f3\u66ff\u4f60\u652f\u4ed8\uff0c\u5b8b\u5c0f\u59d0\u4ec0\u4e48\u65f6\u5019\u66ff\u534e\u97f3\u8d5a\u8db3\u4e00\u5343\u4e94\u767e\u4e07\uff0c\u4ec0\u4e48\u65f6\u5019\u5c31\u6062\u590d\u81ea\u7531\u8eab\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u5f20\u606a\u8bf4\u7684\u8fd9\u4e48\u4e00\u672c\u6b63\u7ecf\uff0c\u516c\u53f8\u53ef\u4ee5\u4e0e\u5b8b\u5c0f\u59d0\u7b7e\u7ea6\u5e38\u89c4\u7684\u7ecf\u7eaa\u5408\u7ea6\uff0c\u9274\u4e8e\u5b8b\u5c0f\u59d0\u5728\u5185\u5730\u5e02\u573a\u7684\u540d\u6c14\uff0c\u6761\u4ef6\u53ef\u4ee5\u6bd4\u7167\u9edb\u513f\uff0c\u552f\u4e00\u591a\u51fa\u6765\u7684\u9650\u5236\u6761\u4ef6\uff0c\u5c31\u662f\u5b8b\u5c0f\u59d0\u7ed9\u516c\u53f8\u521b\u9020\u7684\u76c8\u5229\u4e0d\u8db3\u4e00\u5343\u4e94\u767e\u4e07\uff0c\u5b8b\u5c0f\u59d0\u60f3\u63d0\u524d\u8ddf\u516c\u53f8\u89e3\u7ea6\u6216\u8005\u4e0d\u60f3\u4e0e\u516c\u53f8\u7eed\u7ea6\uff0c\u9700\u6c42\u989d\u5916\u8865\u507f\u4e0d\u8db3\u90e8\u5206\uff0c\u6211\u4eec\u521b\u529e\u4e16\u7eaa\u534e\u97f3\uff0c\u76ee\u7684\u8fd8\u662f\u4e3a\u4e86\u76c8\u5229\uff0c\u5e0c\u671b\u5b8b\u5c0f\u59d0\u80fd\u591f\u8c05\u89e3\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u624b\u6309\u7740\u9910\u684c\uff0c\u5fae\u5fae\u7684\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u4e4b\u524d\u4e5f\u4e0d\u662f\u6ca1\u6709\u5176\u4ed6\u516c\u53f8\u60f3\u5c06\u5979\u4ece\u661f\u8f89\u6316\u8d70\uff0c\u53ea\u662f\u7ed9\u661f\u8f89\u5f00\u51fa\u6765\u9ad8\u8fbe\u56db\u4e94\u5343\u4e07\u7684\u8d4e\u8eab\u8d39\u7ed9\u5413\u8dd1\u4e86\uff0c\u5979\u5c31\u5750\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u9690\u7ea6\u542c\u89c1\u661f\u8f89\u5927\u8001\u677f\u5728\u7535\u8bdd\u91cc\u7684\u8bf4\u8bdd\u5185\u5bb9\uff0c\u542c\u4ed6\u5728\u7535\u8bdd\u91cc\u8ba8\u597d\u7684\u8bed\u6c14\uff0c\u5927\u6982\u6068\u4e0d\u5f97\u5c06\u81ea\u5df1\u8fd9\u4e2a\u9ebb\u70e6\u7ed9\u4e22\u51fa\u53bb\uff0c\u4ed6\u4f3c\u4e4e\u77e5\u9053\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u5728\u7535\u8bdd\u91cc\u201c\u606a\u5c11\u606a\u5c11\u201d\u7684\u53eb\u5524\u8ddf\u53ea\u54c8\u5df4\u72d7\u4f3c\u7684\uff0c\u5b8b\u5a49\u4f69\u7422\u78e8\u7740\u5f20\u606a\u51fa\u8eab\u54ea\u4e2a\u8c6a\u95e8\u5927\u65cf\uff1f\u542c\u8bf4\u4e16\u7eaa\u534e\u97f3\u65e9\u5c31\u6709\u610f\u5168\u9762\u8fdb\u519b\u5185\u5730\u7684\u4f20\u5a92\u5e02\u573a\uff0c\u53ea\u662f\u4e00\u76f4\u90fd\u672a\u89c1\u5176\u6709\u4ec0\u4e48\u7279\u522b\u5927\u7684\u52a8\u4f5c\uff0c\u4e4b\u524d\u5979\u4e0e\u4e16\u7eaa\u534e\u97f3\u7684\u4eba\u63a5\u89e6\uff0c\u4e5f\u6ca1\u6709\u770b\u51fa\u4e16\u7eaa\u534e\u97f3\u5bf9\u5979\u611f\u5174\u8da3\u3002</p><p>\u3000\u3000\u201c\u867d\u7136\u6211\u5f88\u60f3\u62d2\u7edd\u6389\u8fd9\u4efd\u5408\u7ea6\uff0c\u5374\u53c8\u65e0\u6cd5\u62d2\u7edd\u2026\u2026\u201d\u5b8b\u5a49\u4f69\u5e7d\u5e7d\u7684\u8f7b\u53f9\u4e86\u4e00\u58f0\u8bf4\u9053\uff0c\u5fc3\u60f3\u81ea\u5df1\u8fd9\u4e09\u56db\u5e74\u6765\u66ff\u661f\u8f89\u521b\u9020\u7684\u6536\u76ca\u5dee\u4e0d\u591a\u4e5f\u6709\u4e00\u5343\u4e94\u767e\u4e07\u4e86\uff0c\u6ca1\u6709\u5f20\u606a\u7684\u5f3a\u52bf\u51fa\u5934\uff0c\u661f\u8f89\u6839\u672c\u4e0d\u53ef\u80fd\u7b54\u5e94\u4ee5\u8fd9\u4e48\u4f4e\u7684\u8d4e\u8eab\u8d39\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u7b11\u9053\uff1a\u201c\u7ed9\u5b8b\u5c0f\u59d0\u8fd9\u4efd\u5408\u7ea6\uff0c\u7edd\u4e0d\u662f\u4ec0\u4e48\u65bd\u820d\uff0c\u9274\u4e8e\u5b8b\u5c0f\u59d0\u5728\u5185\u5730\u5a31\u4e50\u5e02\u573a\u7684\u4ef7\u503c\uff0c\u6211\u4eec\u751a\u81f3\u8003\u8651\u8fc7\u66ff\u5b8b\u5c0f\u59d0\u7ed9\u661f\u8f89\u652f\u4ed8\u56db\u5343\u4e07\u7684\u89e3\u7ea6\u8d39\u3002\u5f53\u7136\u4e86\uff0c\u89e3\u7ea6\u8d39\u8d8a\u9ad8\uff0c\u516c\u53f8\u627f\u62c5\u7684\u98ce\u9669\u8d8a\u5927\uff0c\u5c06\u6765\u7684\u76c8\u5229\u7a7a\u95f4\u4e5f\u8d8a\u7a84\uff0c\u5bf9\u5b8b\u5c0f\u59d0\u4e2a\u4eba\u4e5f\u76f8\u5f53\u4e0d\u516c\u5e73\uff0c\u80fd\u6709\u8fd9\u6837\u7684\u7ed3\u679c\uff0c\u6211\u4eec\u4e5f\u662f\u677e\u4e86\u4e00\u53e3\u6c14\u2026\u2026\u518d\u8bf4\u5b8b\u5c0f\u59d0\u80fd\u5230\u534e\u97f3\u6765\uff0c\u8981\u662f\u80fd\u4f9d\u7167\u516c\u53f8\u7684\u5b89\u6392\u9274\u4e0b\u4e0e\u79d1\u738b\u9ad8\u79d1\u7684\u4ee3\u8a00\u5408\u540c\uff0c\u9a6c\u4e0a\u5c31\u80fd\u7ed9\u516c\u53f8\u521b\u9020\u6536\u76ca\u4e86\uff0c\u800c\u4e14\u6211\u4eec\u8ba4\u4e3a\u5b8b\u5c0f\u59d0\u5728\u5f71\u89c6\u4e0a\u7684\u53d1\u5c55\u6f5c\u529b\u5f88\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u867d\u7136\u4e0d\u6e05\u695a\u6c5f\u9edb\u513f\u4e0e\u4e16\u7eaa\u534e\u97f3\u5177\u4f53\u7ea6\u5b9a\u4e86\u4ec0\u4e48\u6761\u6b3e\uff0c\u4f46\u662f\u4ece\u6c5f\u9edb\u513f\u7684\u540d\u6c14\u4ee5\u53ca\u5979\u7684\u5de5\u4f5c\u5b89\u6392\uff0c\u5c31\u80fd\u77e5\u9053\u4e16\u7eaa\u534e\u97f3\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u8fc7\u8981\u4ece\u5979\u8eab\u4e0a\u538b\u69a8\u66f4\u591a\u7684\u5229\u6da6\u6765\u3002\u53e6\u5916\uff0c\u4e16\u7eaa\u534e\u97f3\u5728\u5185\u5730\u7b7e\u8d70\u7684\u51e0\u4e2a\u65b0\u4eba\uff0c\u7b7e\u7684\u6761\u4ef6\u4e5f\u76f8\u5f53\u7684\u5bbd\u677e\uff0c\u65b0\u827a\u4eba\u4e0e\u516c\u53f8\u4e4b\u95f4\u7684\u6536\u76ca\u5206\u914d\u4e5f\u8fdc\u6bd4\u5185\u5730\u7684\u7ecf\u7eaa\u516c\u53f8\u5bbd\u677e\u3002\u6700\u4e3a\u5173\u952e\u7684\uff0c\u5185\u5730\u7684\u7ecf\u7eaa\u516c\u53f8\u53ea\u77e5\u9053\u4ece\u827a\u4eba\u5934\u4e0a\u538b\u69a8\u66f4\u591a\u7684\u5229\u6da6\uff0c\u5b8c\u5168\u4e0d\u77e5\u9053\u8981\u5bf9\u827a\u4eba\u8fdb\u884c\u957f\u671f\u7684\u6295\u5165\u4e0e\u57f9\u517b\uff0c\u4ee5\u81f4\u5185\u5730\u8bb8\u591a\u77e5\u540d\u827a\u4eba\u5728\u597d\u4e0d\u5bb9\u6613\u6446\u8131\u7ecf\u7eaa\u516c\u53f8\u7684\u538b\u69a8\u4e4b\u540e\u5c31\u6839\u672c\u4e0d\u6562\u518d\u8ddf\u7ecf\u7eaa\u516c\u53f8\u7b7e\u7ea6\uff0c\u800c\u662f\u76f4\u63a5\u627e\u4eb2\u8fd1\u7684\u5144\u5f1f\u59d0\u59b9\u6216\u4eb2\u8fd1\u7684\u670b\u53cb\u6765\u62c5\u5f53\u81ea\u5df1\u7684\u7ecf\u7eaa\u4eba\u3002\u79c1\u4eba\u7684\u4eba\u8109\u5728\u6f14\u827a\u5708\u5185\u7684\u5f71\u54cd\u529b\u6bd5\u7adf\u6709\u9650\uff0c\u4e5f\u53ea\u6709\u95ef\u51fa\u540d\u6c14\u7684\u827a\u4eba\u624d\u80fd\u8fd9\u4e48\u505a\uff0c\u8fd8\u6562\u8ddf\u7ecf\u7eaa\u516c\u53f8\u8c08\u8c08\u6761\u4ef6\uff0c\u521a\u5165\u884c\u7684\u65b0\u4eba\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6323\u624e\u7684\u4f59\u5730\uff0c\u5c31\u50cf\u5b8b\u5a49\u4f69\u5f53\u521d\u4e0e\u661f\u8f89\u7b7e\u5408\u7ea6\u65f6\u4e5f\u6839\u672c\u6ca1\u6709\u9009\u62e9\u6743\u3002\u4e0d\u8981\u8bf4\u4e16\u7eaa\u534e\u97f3\u4e86\uff0c\u9999\u6e2f\u7684\u4e00\u4e9b\u7ecf\u7eaa\u516c\u53f8\u4e5f\u8981\u6bd4\u5185\u5730\u7684\u7ecf\u7eaa\u516c\u53f8\u66f4\u4e3a\u6b63\u89c4\u3002\u53e6\u5916\uff0c\u4e16\u7eaa\u534e\u97f3\u7684\u8001\u603b\u662f\u4e2a\u5973\u4eba\uff0c\u6c14\u8d28\u5e7d\u96c5\u9ad8\u8d35\uff0c\u5bb9\u8c8c\u6e05\u4e3d\uff0c\u4e5f\u4e0d\u7528\u6574\u5929\u62c5\u5fc3\u7ed9\u6f5c\u89c4\u5219\uff0c\u5fc3\u60f3\u5979\u5927\u6982\u4f1a\u66f4\u91cd\u89c6\u5bf9\u5973\u827a\u4eba\u7684\u4fdd\u62a4\u5427\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u5fae\u4fa7\u7740\u5934\u770b\u7740\u65c1\u8fb9\u7684\u5f20\u606a\u5728\u90a3\u91cc\u4e13\u5fc3\u81f4\u81f4\u7684\u4f3a\u5019\u76d8\u4e2d\u7684\u725b\u6392\uff0c\u5fc3\u91cc\u8fd8\u662f\u6709\u80a1\u5b50\u6c14\uff0c\u53ea\u662f\u5979\u518d\u610f\u6c14\u7528\u4e8b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u50bb\u5230\u62d2\u7edd\u4e16\u7eaa\u534e\u97f3\u66ff\u5979\u4ece\u661f\u8f89\u8d4e\u8eab\uff0c\u70b9\u5934\u7b54\u5e94\u5b59\u9759\u9999\u7684\u540c\u65f6\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u8981\u8bbd\u523a\u5f20\u606a\u4e24\u53e5\uff1a\u201c\u6211\u5012\u4ee5\u4e3a\u5f20\u5148\u751f\u5728\u5f71\u89c6\u4e0a\u7684\u53d1\u5c55\u6f5c\u529b\u66f4\u5927\u5462\u2026\u2026\u201d\u8bf4\u51fa\u8fd9\u8bdd\u53c8\u89c9\u5f97\u6709\u4e9b\u540e\u6094\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u8fd9\u756a\u505a\u4f5c\uff0c\u5979\u5982\u4f55\u80fd\u5982\u6b64\u8f7b\u6613\u7684\u4ece\u661f\u8f89\u8131\u8eab\uff1f\u666e\u901a\u4eba\u53d1\u813e\u6c14\uff0c\u4e00\u6587\u4e0d\u503c\uff0c\u8fd9\u79cd\u6709\u6743\u52bf\u7684\u4eba\u53d1\u813e\u6c14\uff0c\u5c11\u8bf4\u4e5f\u8981\u503c\u4e24\u4e09\u5343\u4e07\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5750\u5728\u5f20\u606a\u7684\u5bf9\u9762\uff0c\u7b11\u76c8\u76c8\u7684\u770b\u7740\u4ed6\uff0c\u9644\u548c\u5b8b\u5a49\u4f69\u8bf4\u9053\uff1a\u201c\u5bf9\u554a\uff0c\u4f60\u8fdb\u516c\u53f8\u7ed9\u6211\u5f53\u5e08\u5f1f\u5f97\u4e86\uff0c\u4e5f\u8ba9\u8bb8\u7ef4\u7ed9\u4f60\u5f53\u7ecf\u7eaa\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u62c9\u5012\u5427\uff0c\u201d\u8bb8\u7ef4\u4e0d\u5c51\u7684\u8bf4\u9053\uff0c\u201c\u4ed6\u8fdb\u516c\u53f8\uff0c\u516c\u53f8\u91cc\u7684\u5973\u827a\u4eba\u90fd\u7ed9\u4ed6\u62d0\u8dd1\u4e86\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u542c\u4e86\u54c8\u54c8\u4e00\u7b11\uff0c\u5f20\u606a\u56bc\u7740\u725b\u6392\u5374\u4e0d\u7406\u4f1a\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u542c\u6c5f\u9edb\u513f\u4e0e\u8bb8\u7ef4\u7684\u8bed\u6c14\uff0c\u5fc3\u60f3\u5979\u4eec\u4e0e\u5f20\u606a\u5e94\u8be5\u662f\u6781\u719f\u6089\u7684\uff0c\u8bf4\u8d77\u6765\uff0c\u5979\u5230\u73b0\u5728\u4e00\u70b9\u90fd\u4e0d\u6e05\u695a\u5f20\u606a\u4e0e\u4e16\u7eaa\u534e\u97f3\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u89c1\u5f20\u606a\u7ed9\u6c5f\u9edb\u513f\u3001\u8bb8\u7ef4\u8bf4\u7b11\u4e5f\u4e0d\u7406\u4f1a\uff0c\u53ea\u662f\u57cb\u5934\u7528\u9910\uff0c\u7a81\u7136\u611f\u52a8\u5c0f\u817f\u7ed9\u5bf9\u9762\u8c01\u8f7b\u8e22\u4e86\u4e00\u4e0b\uff0c\u53ea\u89c1\u6c5f\u9edb\u513f\u795e\u8272\u6709\u4e9b\u5ff8\u634f\uff0c\u4fcf\u8138\u5fae\u7ea2\uff0c\u5fc3\u60f3\u5979\u5927\u6982\u610f\u8bc6\u81ea\u5df1\u5230\u8e22\u9519\u4eba\u4e86\u3002</p><p>\u3000\u3000\u7528\u8fc7\u5348\u9910\uff0c\u5927\u5bb6\u56de\u5230\u5ba2\u5385\u7a0d\u5750\uff0c\u5b8b\u5a49\u4f69\u4e0e\u5b59\u9759\u9999\u3001\u8bb8\u7ef4\u5546\u8c08\u5177\u4f53\u7684\u5408\u7ea6\u6761\u6b3e\uff0c\u4e0e\u5b8b\u5a49\u4f69\u6240\u9884\u6599\u5f97\u5dee\u4e0d\u591a\uff0c\u5979\u90fd\u63d0\u4e0d\u51fa\u4ec0\u4e48\u4fee\u6539\u7684\u5730\u65b9\u6765\uff0c\u60f3\u8bf4\u8981\u5e26\u56de\u53bb\u8bf7\u5f8b\u5e08\u770b\u4e00\u770b\uff0c\u53c8\u60f3\u5230\u5979\u7684\u5f8b\u5e08\u3001\u7ecf\u7eaa\u4eba\u3001\u52a9\u7406\u90fd\u662f\u661f\u8f89\u7684\u5458\u5de5\uff0c\u5979\u5728\u5317\u4eac\u90fd\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u5546\u91cf\u7684\u4eba\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u90fd\u63d0\u4e0d\u51fa\u4ec0\u4e48\u4fee\u6539\u7684\u5730\u65b9\u6765\uff0c\u4e5f\u5c31\u6ca1\u6709\u4ec0\u4e48\u597d\u8c08\u7684\uff0c\u4e00\u5207\u90fd\u590d\u5236\u6c5f\u9edb\u513f\u4e0e\u516c\u53f8\u7b7e\u7f72\u7684\u5408\u7ea6\u5c31\u53ef\u4ee5\u4e86\uff0c\u53ea\u597d\u8c08\u4e00\u4e9b\u5230\u4e16\u7eaa\u534e\u97f3\u4e4b\u540e\u7684\u53d1\u5c55\u8def\u7ebf\uff0c\u8fd9\u65f6\u5b8b\u5a49\u4f69\u624d\u771f\u6b63\u7684\u8ba4\u8bc6\u5230\u4e16\u7eaa\u534e\u97f3\u7684\u786e\u6709\u5728\u6ce8\u610f\u5979\uff0c\u52a0\u5165\u4e16\u7eaa\u534e\u97f3\uff0c\u5c06\u4e4b\u524d\u5728\u661f\u8f89\u7b7e\u7f72\u7684\u5de5\u4f5c\u5408\u7ea6\u8986\u884c\u5b8c\u4e4b\u540e\uff0c\u516c\u53f8\u7ed9\u5b89\u6392\u7684\u5e7f\u544a\u5408\u7ea6\u3001\u5546\u4e1a\u6d3b\u52a8\u5c31\u8981\u7a00\u758f\u8bb8\u591a\uff0c\u4f1a\u8ba9\u5979\u5c06\u66f4\u591a\u7684\u65f6\u95f4\u4e0e\u7cbe\u529b\u6295\u5165\u7684\u5f71\u89c6\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u770b\u5230\u79d1\u738b\u9ad8\u79d1\u7684\u5218\u660e\u8f89\u4e0e\u6628\u5929\u966a\u540c\u5f20\u606a\u51fa\u73b0\u5728\u4f1a\u6240\u7684\u9752\u5e74\u5728\u4e66\u623f\u91cc\u8c08\u4e8b\u60c5\uff0c\u6c5f\u9edb\u513f\u5979\u5988\u5988\u5728\u522b\u5885\u91cc\u901b\u8361\uff0c\u5230\u5904\u8d70\u8d70\u770b\u770b\uff0c\u770b\u5f97\u5f88\u4ed4\u7ec6\uff0c\u4f3c\u4e4e\u5728\u7422\u78e8\u522b\u5885\u7684\u88c5\u4fee\u98ce\u683c\uff0c\u6c5f\u9edb\u513f\u4e0e\u5f20\u606a\u4e24\u4eba\u5374\u4e0d\u89c1\u8e2a\u5f71\uff0c\u4e5f\u6b63\u662f\u5de7\uff0c\u5979\u4eec\u8fd9\u8fb9\u521a\u8c08\u597d\u4e8b\u60c5\uff0c\u6c5f\u9edb\u513f\u4e0e\u5f20\u606a\u5c31\u4ece\u5916\u9762\u8d70\u8fdb\u6765\uff0c\u539f\u6765\u4ed6\u4eec\u521a\u624d\u5728\u9662\u5b50\u91cc\u3002\u4ed6\u4eec\u8981\u5e7d\u4f1a\u9700\u8981\u5230\u5916\u9762\u51b0\u5929\u96ea\u5730\u91cc\u7684\u5446\u7740\u5417\uff1f\u96be\u9053\u8bf4\u4ed6\u4eec\u662f\u5728\u5077\u60c5\uff1f</p><p>\u3000\u3000\u4e0d\u8fc7\u770b\u5f20\u606a\u7684\u8868\u60c5\uff0c\u4e1d\u6beb\u6ca1\u6709\u5077\u60c5\u7684\u75d5\u8ff9\uff0c\u4f3c\u4e4e\u5c31\u662f\u4ed6\u4e0e\u6c5f\u9edb\u513f\u4e24\u4eba\u663e\u5f97\u7279\u522b\u65e0\u804a\uff0c\u5e26\u5979\u51fa\u53bb\u53c2\u89c2\u4e00\u4e0b\u522b\u5885\u7684\u9662\u5b50\uff0c\u4ed6\u6413\u7740\u624b\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u90fd\u8c08\u5b8c\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6709\u4e9b\u4e0d\u660e\u767d\uff0c\u4e16\u7eaa\u534e\u97f3\u4e3a\u4ec0\u4e48\u80fd\u4e0e\u5185\u5730\u7684\u7ecf\u7eaa\u516c\u53f8\u5dee\u5f02\u8fd9\u4e48\u5927\uff1f\u201d\u5b8b\u5a49\u4f69\u51b3\u5b9a\u4e0d\u518d\u8ddf\u5f20\u606a\u6311\u8845\u4e86\uff0c\u5979\u80fd\u610f\u8bc6\u5230\u5f20\u606a\u7684\u8bdd\u5728\u4e16\u7eaa\u534e\u97f3\u91cc\u7684\u5206\u91cf\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u4e16\u7eaa\u534e\u97f3\u80cc\u540e\u7684\u8c6a\u95e8\u5b50\u5f1f\uff0c\u5979\u65e2\u7136\u4ee5\u540e\u5c31\u662f\u4e16\u7eaa\u534e\u97f3\u7684\u827a\u4eba\uff0c\u8fd8\u662f\u8981\u4fdd\u6301\u4e0e\u8001\u677f\u4eec\u4e4b\u95f4\u7684\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u201c\u5185\u5730\u7684\u7ecf\u7eaa\u516c\u53f8\uff0c\u6ca1\u6709\u57f9\u517b\u827a\u4eba\u7684\u7a33\u5b9a\u6a21\u5f0f\uff0c\u65d7\u4e0b\u80fd\u51fa\u4e00\u4e2a\u7ea2\u827a\u4eba\uff0c\u5c31\u8ddf\u62bc\u4e2d\u5b9d\u4f3c\u7684\uff0c\u600e\u4e48\u4e0d\u53ef\u80fd\u5c3d\u6700\u5927\u53ef\u80fd\u7684\u6316\u6398\u7ea2\u827a\u4eba\u7684\u5269\u4f59\u4ef7\u503c\uff1f\u9020\u6210\u8fd9\u4e00\u70b9\u7684\u56e0\u7d20\u6709\u5f88\u591a\uff0c\u5185\u5730\u7684\u5a31\u4e50\u4ea7\u4e1a\u521a\u521a\u53d1\u5c55\u8d77\u6765\uff0c\u7ecf\u7eaa\u516c\u53f8\u7684\u53d1\u5c55\u66f4\u52a0\u6ede\u540e\uff0c\u8fd8\u6ca1\u6709\u51fa\u73b0\u771f\u6b63\u6709\u5b9e\u529b\u7684\u7ecf\u7eaa\u516c\u53f8\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u5bf9\u5a31\u4e50\u4f20\u5a92\u4ea7\u4e1a\u7684\u524d\u666f\u8fd8\u6ca1\u6709\u957f\u671f\u4e50\u89c2\u7684\u8ba4\u77e5\uff0c\u7ecf\u8425\u4e0a\u4e5f\u5c31\u96be\u514d\u6ce8\u91cd\u773c\u524d\u7684\u5229\u76ca\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u7684\u60f3\u6cd5\u5f88\u7b80\u5355\uff0c\u8981\u662f\u4e16\u7eaa\u534e\u97f3\u4e0d\u80fd\u505a\u5f97\u66f4\u597d\uff0c\u4e0d\u80fd\u5bf9\u6574\u4e2a\u5a31\u4e50\u4f20\u5a92\u4ea7\u4e1a\u505a\u4e9b\u6709\u610f\u4e49\u7684\u5f15\u5bfc\uff0c\u8fd8\u4e0d\u5982\u4e0d\u505a\u2026\u2026\u640f\u6fc0\u6d41\u800c\u9006\u8fdb\uff0c\u6709\u5f88\u5927\u53ef\u80fd\u7c89\u8eab\u788e\u9aa8\uff0c\u6210\u4e3a\u6574\u4e2a\u4ea7\u4e1a\u80cc\u666f\u7684\u727a\u7272\u54c1\uff0c\u4e00\u65e6\u6210\u529f\uff0c\u6240\u83b7\u5f97\u7684\u6536\u76ca\u4e5f\u5c06\u8fdc\u8fdc\u8d85\u8fc7\u90a3\u4e9b\u53ea\u6562\u5bf9\u73b0\u72b6\u552f\u552f\u8bfa\u8bfa\u7684\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u6362\u4f5c\u522b\u4eba\u5728\u81ea\u5df1\u9762\u524d\u8bf4\u8fd9\u756a\u542c\u4e0a\u53bb\u5c31\u50cf\u662f\u9a97\u5c0f\u5973\u5b69\u7684\u5927\u8bdd\uff0c\u5b8b\u5a49\u4f69\u4f1a\u55e4\u4e4b\u4ee5\u9f3b\uff0c\u53ea\u662f\u8fd9\u756a\u8bdd\u4ece\u5f20\u606a\u5634\u91cc\u8bf4\u6765\u80fd\u8ba9\u4eba\u795e\u8272\u4e00\u632f\uff0c\u5173\u4e8e\u4e16\u7eaa\u534e\u97f3\u4e1a\u5185\u4e5f\u6709\u8bb8\u591a\u4f20\u95fb\uff0c\u53ea\u662f\u4e16\u7eaa\u534e\u97f3\u662f\u9999\u6e2f\u7684\u7ecf\u7eaa\u516c\u53f8\uff0c\u5b8b\u5a49\u4f69\u4e4b\u524d\u5bf9\u5176\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u5173\u6ce8\uff0c\u53ea\u662f\u5927\u6982\u80fd\u4e86\u89e3\u4e16\u7eaa\u534e\u97f3\u662f\u5bb6\u5f88\u6709\u80cc\u666f\u7684\u7ecf\u7eaa\u6f14\u827a\u516c\u53f8\uff0c\u770b\u5f20\u606a\u6628\u591c\u7684\u8868\u73b0\uff0c\u5c31\u77e5\u9053\u8fd9\u79cd\u4f20\u95fb\u4e0d\u865a\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u5e73\u65f6\u4e5f\u6ca1\u6709\u8003\u8651\u8fd9\u4e48\u6df1\u8fc7\uff0c\u60f3\u5230\u5979\u5e73\u65f6\u6240\u770b\u5230\u4e1a\u5185\u7684\u79cd\u79cd\u73b0\u72b6\uff0c\u518d\u7ec6\u601d\u5f20\u606a\u7684\u8bdd\uff0c\u5f88\u6709\u611f\u89e6\uff0c\u5fae\u5fae\u7684\u70b9\u7740\u5934\uff0c\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u9a6c\u4e0a\u8981\u79bb\u5f00\u5317\u4eac\uff0c\u5218\u603b\u4ed6\u4eec\u8981\u53bb\u5e02\u533a\uff0c\u5b8b\u5c0f\u59d0\u8981\u662f\u6025\u7740\u56de\u5e02\u533a\uff0c\u53ef\u4ee5\u8bf7\u5218\u603b\u634e\u4f60\u4e00\u7a0b\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u867d\u7136\u5f88\u5e0c\u671b\u8d8a\u5feb\u89e3\u9664\u4e0e\u661f\u8f89\u7684\u5408\u7ea6\u8d8a\u597d\uff0c\u4f46\u662f\u4e5f\u77e5\u9053\u4e0d\u5e94\u8be5\u6025\u4e8e\u4eca\u65e5\uff0c\u518d\u8bf4\u5b59\u9759\u9999\u5979\u4eec\u5230\u5317\u4eac\u6765\u4e5f\u662f\u8f66\u9a6c\u52b3\u987f\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u6709\u5176\u4ed6\u7684\u6d3b\u52a8\u5b89\u6392\uff0c\u4fbf\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u9ebb\u70e6\u5218\u603b\u4ed6\u4eec\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5218\u660e\u8f89\u4e0b\u5348\u53bb\u5317\u4eac\u5e02\u5185\u662f\u4e0e\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u63a5\u89e6\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u7684\u4e8b\u5b9c\uff0c\u5b8b\u5a49\u4f69\u867d\u7136\u77e5\u9053\u661f\u8f89\u7684\u8001\u677f\u4e2d\u5348\u5bb4\u8bf7\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u9ad8\u5c42\uff0c\u5979\u8fd8\u662f\u76f4\u63a5\u8fd4\u56de\u516c\u53f8\u3002\u5979\u867d\u7136\u5f88\u5c11\u51fa\u73b0\u5728\u516c\u53f8\u91cc\uff0c\u4e8b\u524d\u4e8b\u540e\u90fd\u6709\u7ecf\u7eaa\u4eba\u4e0e\u52a9\u7406\u5e2e\u5979\u5b89\u6392\uff0c\u4e0d\u8fc7\u5979\u5728\u516c\u53f8\u8fd8\u662f\u6709\u81ea\u5df1\u7684\u4f11\u606f\u5ba4\u3002\u5979\u6ca1\u6709\u63d0\u524d\u8054\u7cfb\u7ecf\u7eaa\u4eba\u4e0e\u52a9\u7406\uff0c\u76f4\u63a5\u8d70\u8fdb\u81ea\u5df1\u7684\u4f11\u606f\u5ba4\uff0c\u6253\u7b97\u5c06\u79c1\u4eba\u7269\u54c1\u6536\u62fe\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4ee5\u4e3a\u5979\u4ee5\u524d\u6709\u591a\u6e05\u9ad8\uff0c\u770b\u5979\u8d70\u8def\u7684\u6837\u5b50\uff0c\u5c31\u77e5\u9053\u4ee5\u524d\u4e0d\u662f\u4ec0\u4e48\u597d\u8d27\uff01\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u8fd9\u624d\u6ce8\u610f\u5230\u4f11\u606f\u5ba4\u7684\u95e8\u6ca1\u6709\u5173\u4e25\uff0c\u5979\u80fd\u542c\u5230\u5916\u9762\u8fc7\u9053\u91cc\u7684\u8bae\u8bba\u58f0\uff0c\u5979\u4e0d\u6e05\u695a\u5979\u8d70\u8def\u7684\u6837\u5b50\u8ddf\u4ee5\u524d\u662f\u4e0d\u662f\u6e05\u9ad8\u6709\u4ec0\u4e48\u5173\u7cfb\u3002</p><p>\u3000\u3000\u201c\u6ca1\u89c9\u5f97\u5979\u8d70\u8def\u8ddf\u4ee5\u524d\u6709\u4ec0\u4e48\u533a\u522b\u554a\uff1f\u201d\u5916\u9762\u53c8\u6709\u8bf4\u8bdd\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u662f\u5c0f\u59d1\u5a18\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u61c2\uff0c\u514d\u5f97\u6559\u574f\u4f60\uff0c\u4e0a\u56de\u674e\u94b0\u4e2d\u5348\u7ed9\u7ea6\u51fa\u6765\u5403\u996d\uff0c\u4f60\u6709\u6ca1\u6709\u53d1\u73b0\u5979\u56de\u6765\u662f\u5939\u7740\u817f\u8d70\u8def\u7684\uff1f\u4e4b\u4e2d\u5979\u6bcf\u56de\u4ece\u4e8c\u8001\u677f\u529e\u516c\u5ba4\u51fa\u6765\uff0c\u4f60\u770b\u5979\u8d70\u8def\u7684\u6837\u5b50\u662f\u4e0d\u662f\u81ea\u7136\u591a\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e7\u2026\u2026\u201d\u53e6\u4e00\u4e2a\u4eba\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u8fd9\u624d\u77e5\u9053\u8d70\u8def\u7684\u59ff\u6001\u8ddf\u6e05\u4e0d\u6e05\u9ad8\u6709\u4ec0\u4e48\u8054\u7cfb\u4e86\uff0c\u542c\u5230\u8fd9\u4e48\u9f8c\u9f8a\u7684\u8bae\u8bba\u5fc3\u95f4\u81ea\u7136\u611f\u5230\u7f9e\u6124\uff0c\u8fd9\u6837\u7684\u8bae\u8bba\u662f\u4ece\u5979\u7684\u7ecf\u7eaa\u4eba\u4e0e\u52a9\u7406\u5634\u91cc\u4f20\u51fa\u6765\uff0c\u5c24\u5176\u8ba9\u5979\u89c9\u5f97\u96be\u8fc7\u3002\u4e0a\u5348\u5728\u53bb\u9999\u6797\u5c0f\u9547\u7684\u8def\u4e0a\uff0c\u5b8b\u5a49\u4f69\u80fd\u770b\u51fa\u6765\u7ecf\u7eaa\u4eba\u4e0e\u52a9\u7406\u8fd8\u662f\u671f\u5f85\u5979\u80fd\u6bc5\u7136\u6495\u6bc1\u4e0e\u516c\u53f8\u7684\u5408\u7ea6\uff0c\u6ca1\u60f3\u5230\u5979\u4eec\u8fd9\u4f1a\u513f\u53c8\u8eb2\u5728\u80cc\u540e\u8fd9\u4e48\u8bae\u8bba\u81ea\u5df1\uff0c\u5979\u8d70\u8fc7\u53bb\u5c06\u95e8\u5173\u4e0a\uff0c\u597d\u8ba9\u5979\u4eec\u77e5\u9053\u91cc\u9762\u6709\u4eba\uff0c\u4e5f\u6ca1\u6709\u60f3\u5c06\u5979\u4eec\u62c9\u8fdb\u6765\u559d\u65a5\u4e00\u901a\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u5c06\u79c1\u4eba\u7269\u54c1\u6536\u62fe\u4e86\u4e00\u4e0b\u5c31\u79bb\u5f00\u516c\u53f8\uff0c\u4e5f\u518d\u6ca1\u6709\u770b\u5230\u5979\u7684\u7ecf\u7eaa\u4eba\u4e0e\u52a9\u7406\uff0c\u4e0d\u77e5\u9053\u5979\u4eec\u662f\u6ca1\u6709\u8138\u8fd8\u662f\u6ca1\u6709\u80c6\u91cf\u518d\u51fa\u6765\u9732\u8138\u3002\u5b8b\u5a49\u4f69\u8fd9\u4f1a\u513f\u5bf9\u5979\u4eec\u5012\u662f\u6ca1\u6709\u4ec0\u4e48\u6028\u6068\u4e86\uff0c\u5979\u81ea\u5df1\u4e4b\u524d\u4e0d\u4e5f\u662f\u62b1\u7740\u8fd9\u6837\u7684\u60f3\u6cd5\u5417\uff1f\u4e0d\u732e\u8eab\uff0c\u54ea\u4e2a\u7537\u4eba\u4f1a\u50bb\u5230\u5e2e\u5979\u89e3\u9664\u4e0e\u661f\u8f89\u7684\u5408\u7ea6\uff1f</p><p>\u3000\u3000\u5750\u5728\u516c\u53f8\u6d3e\u7ed9\u5979\u7684\u8f66\u91cc\uff0c\u5b8b\u5a49\u4f69\u6258\u816e\u51dd\u7738\u770b\u7740\u8f66\u7a97\u5916\u7684\u5929\u7a7a\uff0c\u8fd9\u4e2a\u81ea\u79f0\u5f20\u606a\u7684\u9752\u5e74\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff0c\u5979\u4e4b\u524d\u6240\u63a5\u89e6\u5230\u7684\u7537\u4eba\u5f53\u4e2d\uff0c\u7279\u522b\u662f\u90a3\u4e48\u81ea\u4ee5\u4e3a\u662f\u7684\u7537\u4eba\u91cc\uff0c\u6709\u591a\u5c11\u4e0d\u60f3\u8ba9\u5979\u5939\u7740\u817f\u8d70\u8def\u7684\uff1f\u5373\u4f7f\u90a3\u4e9b\u60f3\u5c06\u5979\u6316\u8fc7\u53bb\u7684\u6f14\u827a\u516c\u53f8\u7684\u8001\u603b\uff0c\u5fc3\u91cc\u4e5f\u6253\u7740\u8ba9\u5979\u5939\u817f\u8d70\u8def\u7684\u5ff5\u5934\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5317\u4eac\u897f\u6cc9\u5e84\u56ed\uff0c\u674e\u5728\u6d19\u6253\u5f00\u7a97\u6237\uff0c\u8ba9\u51db\u51bd\u7684\u5bd2\u98ce\u900f\u8fdb\u6765\uff0c\u8ba9\u5fae\u5fae\u53d1\u80c0\u7684\u8111\u5b50\u80fd\u591f\u6e05\u9192\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u8054\u901a\u6b32\u4ece\u7f8e\u56fd\u9ad8\u901a\u5f15\u8fdb\u6280\u672f\u5efa\u8bbeCDMA\u7f51\u7edc\uff0c\u4e09\u661f\u662f\u8054\u901aCDMA\u9879\u76ee\u7684\u7ec8\u7aef\u8bbe\u5907\u5408\u4f5c\u5382\u5546\uff0c\u4e09\u661f\u4e2d\u56fd\u516c\u53f8\u4e0e\u8054\u901a\u7684\u8054\u7cfb\u5bc6\u5207\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u51fa\u5e2d\u5728\u6628\u591c\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u5e86\u795d\u5bb4\u4f1a\u4e0a\uff0c\u674e\u5728\u6d19\u5f88\u5feb\u5c31\u77e5\u9053\u4e86\u6d88\u606f\u3002\u5f20\u606a\u3001\u9648\u4fe1\u751f\u8fd8\u6709\u8096\u745e\u6c11\u4e0e\u8054\u901a\u96c6\u56e2\u7684\u6768\u84c9\u5e73\u4ee5\u53ca\u5317\u4eac\u5206\u516c\u53f8\u7684\u9f9a\u5fd7\u9e4f\u5728\u5bb4\u4f1a\u4e4b\u540e\u53c8\u53bb\u4e86\u5a31\u4e50\u573a\u6240\uff0c\u6d89\u53ca\u5230\u4eba\u5458\u5f88\u6709\u9650\uff0c\u674e\u5728\u6d19\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u627e\u5730\u65b9\u5bc6\u8c08\u8fd8\u662f\u7eaf\u7cb9\u5bb4\u4f1a\u540e\u53bb\u5a31\u4e50\u573a\u6240\u4f11\u95f2\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u4e2d\u56fd\u7684\u624b\u673a\u5e02\u573a\u4e86\uff0c\u4e9a\u6d32\u5e02\u573a\uff0c\u4e09\u661f\u4e5f\u8d8a\u6765\u8d8a\u611f\u5230\u9526\u6e56\u7684\u5a01\u80c1\uff0c\u7279\u522b\u662f\u9526\u6e56\u7684\u4e2d\u9ad8\u7aef\u624b\u673a\u4e0e\u4e09\u661f\u6709\u7740\u7c7b\u4f3c\u7684\u7279\u8d28\uff0c\u5728\u7ec6\u5206\u5e02\u573a\u4e0a\u7684\u7ade\u4e89\u66f4\u5927\uff0c\u9526\u6e56\u51ed\u501f\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u7684\u9ad8\u901f\u6210\u957f\uff0c\u5728\u4e9a\u6d32\u624b\u673a\u5e02\u573a\u6240\u5360\u7684\u4efd\u989d\u5df2\u7ecf\u4e0e\u4e09\u661f\u76f8\u5dee\u65e0\u51e0\u4e86\u3002\u674e\u5728\u6d19\u73b0\u5728\u7761\u89c9\u5dee\u4e0d\u591a\u4e5f\u6709\u534a\u53ea\u773c\u775b\u7741\u5f00\u76ef\u7740\u9526\u6e56\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u540c\u65f6\u4e0e\u8054\u901a\u65b9\u9762\u63a5\u89e6\uff0c\u53c8\u8ba9\u674e\u5728\u6d19\u5982\u4f55\u80fd\u5b89\u5fc3\u7684\u7761\u89c9\uff1f</p><p>\u3000\u3000\u77e5\u9053\u5f20\u606a\u5728\u4f1a\u6240\u4e0e\u4e2d\u56fd\u56fd\u5bb6\u53d1\u5c55\u8ba1\u5212\u59d4\u526f\u4e3b\u4efb\u6210\u664b\u6770\u7684\u513f\u5b50\u4e3a\u4e00\u4e2a\u5973\u660e\u661f\u4e89\u98ce\u5403\u918b\uff0c\u674e\u5728\u6d19\u5fc3\u91cc\u624d\u7a0d\u5b89\u5fc3\u4e00\u4e9b\u3002\u4e2d\u5348\u53c8\u542c\u5230\u6d88\u606f\u8bf4\u5f20\u606a\u7ea6\u90a3\u5973\u660e\u661f\u5355\u72ec\u89c1\u9762\u4e4b\u540e\u53c8\u8981\u62ff\u94b1\u51fa\u6765\u5e2e\u5979\u89e3\u9664\u4e0e\u539f\u7ecf\u7eaa\u516c\u53f8\u7684\u5408\u7ea6\uff0c\u674e\u5728\u6d19\u542c\u4e86\u4e5f\u4e0d\u7531\u7684\u4e3a\u99a8\u4e88\u611f\u5230\u4e0d\u503c\uff1a\u8fd9\u4e2a\u6df7\u86cb\u73a9\u8d77\u5973\u4eba\u6765\uff0c\u5c31\u4e0d\u77e5\u9053\u6536\u655b\u4e00\u4e9b\uff1f\u6bcf\u6b21\u58f0\u52bf\u90fd\u8fd9\u4e48\u5927\uff0c\u4e0d\u77e5\u9053\u6c60\u4f50\u79c0\u85cf\u542c\u5230\u4e86\u8fd9\u4e2a\u6d88\u606f\u4f1a\u6709\u600e\u6837\u7684\u60f3\u6cd5\uff0c\u5374\u53c8\u62c5\u5fc3\u8fd9\u6d88\u606f\u4f1a\u4f20\u5230\u99a8\u4e88\u7684\u8033\u6735\u91cc\u53bb\u3002\u4ed6\u5012\u662f\u4e0d\u62c5\u5fc3\u99a8\u4e88\u4e0e\u5f20\u606a\u95f9\u7ffb\uff0c\u53ea\u662f\u62c5\u5fc3\u99a8\u4e88\u4e0e\u5f20\u606a\u95f9\u7ffb\u7684\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4ec0\u4e48\u90fd\u7792\u4e0d\u4f4f\u4e86\u3002</p><p>\u3000\u3000\u201c\u79d1\u738b\u9ad8\u79d1\u7684\u5218\u660e\u8f89\u521a\u8d76\u5230\u5317\u4eac\u4e0e\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u9ad8\u5c42\u63a5\u89e6\u2026\u2026\u201d\u91d1\u5357\u52c7\u6572\u95e8\u8d70\u8fdb\u6765\uff0c\u5c06\u6700\u65b0\u7684\u60c5\u51b5\u8ddf\u674e\u5728\u6d19\u6c47\u62a5\uff0c\u4ed6\u4e4b\u524d\u5c31\u662f\u4e09\u661f\u6d3e\u9a7b\u4e2d\u56fd\u7684\u5458\u5de5\uff0c\u4e0e\u674e\u5728\u6d19\u7684\u79c1\u4ea4\u4e5f\u4e0d\u9519\uff0c\u4e3a\u4e86\u5b66\u4e60\u4e2d\u56fd\u7684\u6587\u5316\uff0c\u4ed6\u53c8\u5728\u4e2d\u56fd\u7559\u5b66\u7684\u4e24\u5e74\u4e4b\u540e\u5c31\u4e00\u76f4\u7ed9\u674e\u5728\u6d19\u62c5\u4efb\u7279\u522b\u52a9\u7406\uff0c\u201c\u6768\u84c9\u5e73\u4eca\u5929\u4e0a\u5348\u8054\u901a\u5185\u90e8\u7684\u4f1a\u8bae\u63d0\u51fa\u6765\uff0c\u8981\u8c03\u6574\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u8ba1\u5212\uff1b\u5218\u660e\u8f89\u4eca\u5929\u4e5f\u53ea\u662f\u4e0e\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u63a5\u89e6\uff0c\u4e0e\u8054\u901a\u603b\u90e8\u6ca1\u6709\u4ec0\u4e48\u8054\u7cfb\u2026\u2026\u770b\u60c5\u51b5\uff0c\u6768\u84c9\u5e73\u6628\u5929\u5e94\u8be5\u6ca1\u6709\u80fd\u4e0e\u9526\u6e56\u8fbe\u6210\u4ec0\u4e48\u53e3\u5934\u534f\u8bae\uff0c\u6ca1\u6709\u9526\u6e56\u7684\u652f\u6301\uff0c\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u7684\u4f9b\u8d27\u80fd\u529b\u53ea\u80fd\u4fdd\u8bc1\u8054\u901a\u5728\u5317\u4eac\u5730\u533a\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u529b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u79fb\u52a8\u901a\u4fe1\u5c40\u4e5f\u5c31\u6ca1\u6709\u4ec0\u4e48\u538b\u529b\u4e86\uff0c\u4e5f\u8bb8\u53ea\u6709\u5317\u4eac\u79fb\u52a8\u516c\u53f8\u7684\u538b\u529b\u5927\u4e00\u4e9b\uff0c\u4f60\u89c9\u5f97\u662f\u4e0d\u662f\u8ba9\u624b\u673a\u90e8\u95e8\u8ddf\u5317\u4eac\u79fb\u52a8\u516c\u53f8\u4e3b\u52a8\u63a5\u89e6\u4e00\u4e0b\uff1f\u201d\u674e\u5728\u6d19\u95ee\u9053\u3002</p><p>\u3000\u3000\u5317\u4eac\u8054\u901a\u63a8\u51fa\u5145\u503c\u9001\u624b\u673a\u7684\u65b0\u5e74\u793c\u5305\u6d3b\u52a8\uff0c\u8ba1\u7b97\u8054\u901a\u5728\u5b9a\u5236\u624b\u673a\u4e0a\u7684\u91c7\u8d2d\u6210\u672c\uff0c\u5b9e\u9645\u76f8\u5f53\u7ed9\u65b0\u5f00\u6237\u7528\u6237\u901a\u4fe1\u8d44\u8d39\u6253\u516d\u6298\uff0c\u6d3b\u52a8\u4e00\u7ecf\u63a8\u51fa\uff0c\u5c31\u6709\u5185\u90e8\u6d88\u606f\u4ece\u8054\u901a\u4f20\u51fa\u6765\uff0c\u8bf4\u8054\u901a\u65b0\u589e\u5f00\u6237\u6570\u91cf\u56e0\u6b64\u589e\u6da8\u4e86\u4e00\u500d\u8fd8\u591a\uff0c\u76f4\u903c\u5317\u4eac\u79fb\u52a8\u7684\u65b0\u5f00\u6237\u6570\u91cf\uff0c\u5317\u4eac\u79fb\u52a8\u600e\u4e48\u53ef\u80fd\u611f\u53d7\u4e0d\u5230\u538b\u529b\uff1f\u6709\u538b\u529b\u81ea\u7136\u5c31\u4f1a\u6709\u884c\u52a8\uff0c\u91d1\u5357\u52c7\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u4f4e\u7aef\u624b\u673a\u4e0e\u9526\u6e56\u76f8\u6bd4\uff0c\u6ca1\u6709\u6210\u672c\u4f18\u52bf\u2026\u2026\u201d\u5c31\u7b97\u5317\u4eac\u79fb\u52a8\u5b8c\u5168\u590d\u5236\u5317\u4eac\u8054\u901a\u7684\u6d3b\u52a8\u6a21\u5f0f\uff0c\u4ece\u4e09\u661f\u91c7\u8d2d\u5b9a\u5236\u624b\u673a\uff0c\u5b9e\u9645\u76f8\u5f53\u7ed9\u65b0\u5f00\u6237\u7528\u6237\u901a\u4fe1\u8d44\u8d39\u6253\u56db\u6298\u3002\u65b0\u5f00\u6237\u7528\u6237\u7684\u5b9e\u9645\u611f\u89c9\u8981\u5dee\u65e0\u51e0\uff0c\u4f46\u662f\u5317\u4eac\u79fb\u52a8\u6838\u5fc3\u6210\u672c\u65f6\uff0c\u5374\u8981\u8fdc\u8fdc\u9ad8\u8fc7\u5317\u4eac\u8054\u901a\u3002</p><p>\u3000\u3000\u201c\u770b\u60c5\u5f62\uff0c\u9526\u6e56\u6682\u65f6\u4e0d\u4f1a\u76f4\u63a5\u751f\u4ea7ESS\u624b\u673a\uff0c\u5317\u4eac\u79fb\u52a8\u516c\u53f8\u867d\u7136\u4e0e\u5317\u4eac\u8054\u901a\u6597\u5f97\u5389\u5bb3\uff0c\u4f46\u662f\u4ed6\u4eec\u6682\u65f6\u8fd8\u62c9\u4e0d\u4e0b\u8138\u4e3b\u52a8\u53bb\u6c42\u5317\u4eac\u8054\u901a\u7684\u5408\u4f5c\u5382\u5546\uff0c\u6211\u4eec\u8fd8\u662f\u6709\u673a\u4f1a\u7684\u2026\u2026\u201d\u674e\u5728\u6d19\u5206\u6790\u9053\uff0c\u201c\u6216\u8bb8\u5317\u4eac\u79fb\u52a8\u4f1a\u626c\u957f\u907f\u77ed\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d</p><p>\u3000\u3000\u91d1\u5357\u52c7\u72b9\u89c9\u5f97\u6574\u4e2a\u5c40\u52bf\u770b\u8d77\u6765\u4f9d\u65e7\u6251\u6714\u8ff7\u79bb\uff0c\u4ed6\u4eec\u6240\u80fd\u5f97\u5230\u7684\u4fe1\u606f\u7247\u9762\uff0c\u5f88\u96be\u771f\u6b63\u7684\u6478\u6e05\u695a\u9526\u6e56\u7684\u610f\u56fe\u3002\u6bd4\u8d77\u4ed6\u4eec\uff0c\u4e2d\u56fd\u79fb\u52a8\u901a\u4fe1\u5c40\u5e94\u8be5\u66f4\u6025\u4e8e\u6478\u6e05\u695a\u9526\u6e56\u7684\u610f\u56fe\u5427\uff1f\u4e0d\u8fc7\u8bf4\u4e0d\u5b9a\uff0c\u79fb\u52a8\u901a\u4fe1\u5c40\u90a3\u4e9b\u8001\u7237\u7684\u59ff\u6001\uff0c\u91d1\u5357\u52c7\u4e5f\u6709\u4e9b\u63a5\u89e6\uff0c\u4ed6\u4eec\u8981\u4e0d\u7ed9\u6253\u8138\u6253\u80bf\u4e86\uff0c\u5f88\u96be\u60f3\u8c61\u4ed6\u4eec\u4f1a\u6709\u672a\u96e8\u7ef8\u7f2a\u7684\u8111\u5b50\uff0c\u82e5\u662f\u9526\u6e56\u4e0e\u8054\u901a\u771f\u8981\u8054\u5408\u8d77\u6765\u641e\u4ec0\u4e48\u52a8\u4f5c\uff0c\u79fb\u52a8\u901a\u4fe1\u5c40\u4e5f\u53ea\u4f1a\u62c6\u62db\u5e94\u62db\u3001\u5904\u4e8e\u88ab\u52a8\u6328\u6253\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u867d\u7136\u5212\u5f52\u56fd\u5bb6\u7ecf\u8d38\u59d4\u7ba1\u8f96\uff0c\u4f46\u59cb\u7ec8\u90fd\u8981\u7b97\u4fe1\u4ea7\u90e8\u7cfb\u7edf\u7684\u4f01\u4e1a\uff0c\u4e0e\u79fb\u52a8\u3001\u8054\u901a\u662f\u540c\u95e8\uff0c\u6628\u5929\u591c\u91cc\u3001\u4eca\u5929\u4e2d\u5348\u53d1\u751f\u7684\u90a3\u70b9\u7834\u4e8b\uff0c\u845b\u5efa\u5fb7\u4e5f\u5f88\u5feb\u5c31\u5f97\u5230\u6d88\u606f\uff0c\u4ed6\u8fd8\u662f\u7b49\u5230\u592a\u9633\u5c06\u843d\u5c71\u65f6\u624d\u7ed9\u6210\u664b\u6770\u6253\u7535\u8bdd\u8fc7\u53bb\u95ee\u5019\u4e00\u58f0\uff0c\u5373\u4f7f\u62b1\u7740\u5e78\u707e\u4e50\u7978\u7684\u5fc3\u601d\uff0c\u8bf4\u8bdd\u7684\u8bed\u6c14\u8fd8\u662f\u5f88\u540c\u4ec7\u654c\u5ffe\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u521a\u542c\u8bf4\u6628\u5929\u591c\u91cc\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u8fd9\u5c0f\u5b50\u592a\u56a3\u5f20\u4e86\uff0c\u603b\u8981\u6709\u4eba\u7ed9\u4ed6\u70b9\u6559\u8bad\uff0c\u624d\u80fd\u8ba9\u4ed6\u77e5\u9053\u6536\u655b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u6210\u664b\u6770\u8fd8\u4e0d\u81f3\u4e8e\u627e\u845b\u5efa\u5fb7\u8bc9\u82e6\uff0c\u53ea\u8f7b\u8f7b\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u518d\u8bf4\u5176\u4ed6\u6709\u7684\u6ca1\u7684\uff0c\u201c\u8fd9\u4e9b\u5c0f\u4e8b\u60c5\u90fd\u8981\u8ba9\u845b\u603b\u60e6\u8bb0\u7740\uff0c\u771f\u662f\u8fc7\u610f\u4e0d\u53bb\u3002\u201d</p><p>\u3000\u3000\u9526\u6e56\u5df2\u6210\u6c14\u5019\uff0c\u4e0e\u5730\u65b9\u4e0a\u7684\u5f3a\u52bf\u6d3e\u76d8\u6839\u9519\u8282\u3001\u4e92\u4e3a\u63f4\u5e94\uff0c\u4e2d\u592e\u90e8\u59d4\u91cc\u66ff\u9526\u6e56\u6491\u8170\u7684\u4e5f\u5927\u6709\u4eba\u5728\uff0c\u5c31\u8fde\u803f\u91cd\u9633\u8fd9\u4e2a\u8001\u987d\u56fa\u751a\u81f3\u6709\u65f6\u5019\u8fd8\u4f1a\u66ff\u9526\u6e56\u8bf4\u8bdd\uff0c\u627e\u4e0d\u5230\u9526\u6e56\u7684\u81f4\u547d\u5f31\u70b9\uff0c\u6210\u664b\u6770\u77e5\u9053\u4ed6\u4ee5\u4e2a\u4eba\u7684\u529b\u91cf\u53bb\u8ddf\u6574\u4e2a\u9526\u6e56\u4ee5\u53ca\u9526\u6e56\u80cc\u540e\u7684\u653f\u6cbb\u52bf\u529b\u786c\u62fc\uff0c\u65e0\u7591\u662f\u4ee5\u9e21\u86cb\u7838\u77f3\u5934\u3002\u867d\u7136\u6210\u664b\u6770\u5fc3\u91cc\u5bf9\u6628\u5929\u591c\u91cc\u53d1\u751f\u7684\u4e8b\u60c5\u975e\u5e38\u7684\u607c\u706b\uff0c\u4f46\u662f\u4e5f\u77e5\u9053\u662f\u4ed6\u4eec\u7406\u4e8f\uff0c\u5f20\u606a\u5982\u6b64\u8eab\u4efd\u53d7\u5230\u8fd9\u6837\u7684\u4fae\u8fb1\uff0c\u5373\u4f7f\u5c06\u4ed6\u513f\u5b50\u7ed9\u5f20\u606a\u5f80\u6b7b\u91cc\u6574\uff0c\u5176\u4ed6\u4eba\u4e5f\u53ea\u4f1a\u62b1\u7740\u770b\u597d\u620f\u7684\u5fc3\u6001\u8896\u624b\u65c1\u89c2\u3002</p><p>\u3000\u3000\u6210\u664b\u6770\u4e0e\u845b\u5efa\u5fb7\u5bd2\u6684\u4e86\u51e0\u53e5\uff0c\u653e\u4e0b\u7535\u8bdd\uff0c\u773c\u775b\u6076\u72e0\u72e0\u7684\u7741\u7740\u513f\u5b50\u6210\u5f3a\uff0c\u8bad\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u6d51\u5e10\uff0c\u60f3\u5c06\u7b11\u67c4\u7ed9\u5168\u5317\u4eac\u7684\u4eba\u90fd\u77e5\u9053\uff01\u8ba9\u4f60\u8981\u77e5\u9053\u6536\u655b\uff0c\u8981\u77e5\u9053\u6536\u655b\uff0c\u4f60\u5c31\u662f\u8fd9\u4e48\u7ed9\u6211\u6536\u655b\u7684\uff0c\u4e00\u4e2a\u620f\u5b50\u800c\u5df2\u5c31\u80fd\u8ff7\u4e86\u4f60\u7684\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u5bb6\u4f19\u662f\u524a\u7238\u4f60\u7684\u9762\u5b50\u554a\uff01\u201d\u6210\u5f3a\u72b9\u89c9\u5f97\u4e0d\u6124\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u7684\u9762\u5b50\u662f\u7ed9\u4f60\u8fd9\u4e2a\u6d51\u5e10\u4e22\u5e72\u51c0\u7684\uff01\u201d\u6210\u664b\u6770\u6124\u6068\u7684\u8bf4\u9053\uff0c\u201c\u6211\u90fd\u4e0d\u7ed9\u4ed6\u770b\u5728\u773c\u91cc\uff0c\u4f60\u504f\u80fd\u6311\u4e2d\u8fd9\u4e2a\u523a\u5934\u60f9\uff01\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u8fd9\u4e48\u5927\uff0c\u96be\u9053\u771f\u7684\u5c31\u6ca1\u6709\u4e00\u70b9\u628a\u67c4\u53ef\u6293\uff1f\u201d\u6210\u5f3a\u55eb\u5685\u7740\u8bf4\u9053\uff0c\u8981\u4ed6\u5fcd\u6c14\u541e\u58f0\uff0c\u8fd8\u662f\u5f88\u4e0d\u7518\u5fc3\uff0c\u7279\u522b\u662f\u60f3\u5230\u5b8b\u5a49\u4f69\u8d64\u8eab\u88f8\u4f53\u7684\u8eba\u5728\u5f20\u606a\u8eab\u4e0b\u627f\u6b22\u7684\u60c5\u5f62\uff0c\u4ed6\u5fc3\u91cc\u5c24\u5176\u7684\u89c9\u5f97\u6068\u3002</p><p>\u3000\u3000\u201c\u60f3\u6273\u5012\u9526\u6e56\u4e0d\u662f\u4f60\u4e00\u4e2a\u4eba\uff0c\u201d\u6210\u664b\u6770\u5750\u56de\u6c99\u53d1\u91cc\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u4e0d\u80fd\u7ed9\u4e88\u81f4\u547d\u4e00\u51fb\uff0c\u6311\u4e9b\u9e21\u6bdb\u849c\u76ae\u7684\u7834\u4e8b\uff0c\u6839\u672c\u52a8\u4e0d\u4e86\u9526\u6e56\u7684\u6839\u672c\uff0c\u771f\u6b63\u8981\u52a8\u9526\u6e56\uff0c\u4e5f\u9700\u8981\u4e0a\u9762\u4eba\u70b9\u5934\u3002\u4f60\u4ee5\u4e3a\u4f60\u8001\u5b50\u4e00\u4e2a\u90fd\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u6276\u6b63\u7684\u7834\u526f\u4e3b\u4efb\u9876\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ea\u8981\u9526\u6e56\u8fd8\u6709\u654c\u4eba\u5c31\u884c\uff01\u201d\u6210\u5f3a\u6076\u72e0\u72e0\u7684\u8bf4\u9053\uff0c\u201c\u5927\u4e0d\u4e86\u6682\u65f6\u54bd\u4e0b\u8fd9\u53e3\u6c14\uff01\u201d</p><p>\u3000\u3000\u6210\u664b\u6770\u8fd9\u65f6\u5019\u624d\u60f3\u5230\u521a\u624d\u4e0e\u845b\u5efa\u5fb7\u901a\u8bdd\u65f6\u6709\u4e9b\u51b7\u6de1\u4e86\uff0c\u53c8\u60f3\u5230\u4e25\u6587\u4ecb\uff0c\u60f3\u9526\u6e56\u8fd9\u6837\u7684\u602a\u7269\u5168\u4e2d\u56fd\u4e5f\u4e0d\u662f\u53ea\u6709\u4e00\u5bb6\uff0c\u6d59\u4e1c\u7684\u5b8f\u4fe1\u751a\u81f3\u4e0e\u9526\u6e56\u6709\u7740\u5f88\u6df1\u7684\u77db\u76fe\u3002\u4ed6\u4e4b\u524d\u8fd8\u6ca1\u6709\u60f3\u5230\u8981\u5b8c\u5168\u5012\u5411\u5b8f\u4fe1\uff0c\u4e25\u5bb6\u7684\u8bf7\u6258\uff0c\u6709\u4e9b\u4e8b\u80fd\u5e2e\u5219\u5e2e\uff0c\u6709\u4e9b\u4e8b\u770b\u4e0a\u53bb\u6709\u4e9b\u60ac\u5c31\u80fd\u63a8\u8131\u5219\u63a8\u8131\uff0c\u5982\u6b64\u770b\u6765\uff0c\u4e5f\u6ca1\u6709\u5176\u4ed6\u7684\u9009\u62e9\u4e86\u3002</p></article>"}}}]);
