(window.webpackJsonp=window.webpackJsonp||[]).push([[1283],{1306:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u708e\u9b54\u6b27\u51ef",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u708e\u9b54\u6b27\u51ef</h2><p>\u5df4\u6d1b\u708e\u9b54\u4ece\u9634\u5f71\u4e2d\u6162\u6162\u8d70\u51fa\uff0c\u6253\u91cf\u7740\u6ee1\u5730\u7684\u4eba\u9762\u72ee\u5c38\u4f53\u3002\u4ed6\u4f38\u51fa\u53f3\u624b\u624b\u638c\uff0c\u4e03\u6839\u624b\u6307\u6469\u6332\u7740\u5bbd\u9614\u7684\u4e0b\u5df4\uff0c\u82e5\u6709\u6240\u601d\uff0c\u9752\u8272\u7684\u76ae\u80a4\u5728\u9ed1\u6697\u4e2d\u6cdb\u7740\u5fae\u5fae\u7684\u5149\u3002</p><p>\u201c\u7269\u8d28\u754c\u8fd8\u771f\u662f\u633a\u6709\u8da3\u7684\u3002\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u81ea\u8a00\u81ea\u8bed\u3002</p><p>\u5728\u591a\u745e\u68ee\u7684\u5b9d\u5e93\u4e2d\uff0c\u4ed6\u649e\u4e0a\u4e86\u8679\u5f69\u9f99\u745e\u6069\u65af\u5766\u3002\u4e00\u756a\u8a00\u8bed\u4ea4\u6d89\u540e\uff0c\u4ed6\u62ff\u5230\u4e86\u81ea\u5df1\u60f3\u8981\u7684\u4e1c\u897f\u3002\u968f\u5373\uff0c\u4ed6\u7684\u65b0\u4e3b\u4eba\u5965\u5580\u65af\uff0c\u5c06\u4ed6\u6253\u53d1\u5230\u7269\u8d28\u754c\u6765\u3002</p><p>\u5728\u6c11\u95f4\u4f20\u8bf4\u4e2d\uff0c\u6076\u9b54\u90fd\u4eff\u4f5b\u662f\u4e00\u7fa4\u56da\u72af\uff0c\u6216\u8005\u8bf4\u4e00\u7fa4\u532a\u5f92\u3002\u4ed6\u4eec\u5e73\u65f6\u88ab\u5173\u5728\u4e00\u4e2a\u53eb\u505a\u65e0\u5c3d\u6df1\u6e0a\u7684\u76d1\u72f1\u91cc\uff0c\u65e0\u6240\u4e8b\u4e8b\uff0c\u800c\u4e00\u65e6\u6709\u673a\u4f1a\u80fd\u6765\u5230\u7269\u8d28\u754c\uff0c\u4fbf\u7b80\u76f4\u50cf\u662f\u653e\u98ce\u4e00\u822c\uff0c\u5c31\u4f1a\u7acb\u523b\u5174\u9ad8\u91c7\u70c8\u51b2\u8fc7\u6765\uff0c\u5230\u5904\u70e7\u6740\u62a2\u63a0\uff0c\u65e0\u6076\u4e0d\u4f5c\u3002\u56e0\u4e3a\u6076\u9b54\u975e\u5e38\u5f3a\u5927\uff0c\u65e0\u4eba\u53ef\u5236\uff0c\u6240\u4ee5\u6bcf\u6b21\u90fd\u4f1a\u5728\u7269\u8d28\u754c\u9020\u6210\u5de8\u5927\u7684\u707e\u96be\uff0c\u7136\u540e\u518d\u5fc3\u6ee1\u610f\u8db3\u5730\u79bb\u5f00\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u5b8c\u5168\u662f\u9020\u8c23\u52a0\u8bfd\u8c24\u52a0\u6c61\u8511\u3002</p><p>\u6076\u9b54\u662f\u5f02\u4f4d\u9762\u751f\u7269\uff0c\u662f\u65e0\u5c3d\u6df1\u6e0a\u7684\u6df7\u4e71\u672c\u8d28\u6240\u5177\u8c61\u5316\uff0c\u4ed6\u4eec\u786e\u5b9e\u975e\u5e38\u5f3a\u5927\uff0c\u4f46\u8fd9\u662f\u6307\u5728\u6df1\u6e0a\u4e2d\u3002\u4e00\u65e6\u79bb\u5f00\u6df1\u6e0a\uff0c\u8fdb\u5165\u7269\u8d28\u754c\uff0c\u56e0\u4e3a\u73af\u5883\u7684\u6539\u53d8\uff0c\u6076\u9b54\u4eec\u4f1a\u53d7\u5230\u6574\u4e2a\u4f4d\u9762\u7684\u538b\u5236\uff0c\u529b\u91cf\u5927\u5e45\u5ea6\u524a\u5f31\u3002</p><p>\u5982\u679c\u662f\u4ee5\u6295\u5f71\u65b9\u5f0f\u8fdb\u5165\u7269\u8d28\u754c\uff0c\u90a3\u4e48\u529b\u91cf\u5176\u5b9e\u975e\u5e38\u865a\u5f31\uff0c\u4e0d\u8db3\u672c\u4f53\u5b9e\u529b\u7684\u5341\u5206\u4e4b\u4e00\uff0c\u5f88\u5bb9\u6613\u88ab\u7269\u8d28\u754c\u7684\u5f3a\u8005\u8f70\u6740\uff1b\u82e5\u662f\u4f4e\u9636\u7684\u6076\u9b54\uff0c\u751a\u81f3\u90fd\u6839\u672c\u6ca1\u6709\u6295\u5f71\u7684\u80fd\u529b\u3002\u5982\u679c\u662f\u4ee5\u672c\u4f53\u65b9\u5f0f\u8fdb\u5165\uff0c\u867d\u7136\u6bd4\u6295\u5f71\u5f3a\u5927\u5f97\u591a\uff0c\u4f46\u8fd9\u4e5f\u975e\u5e38\u5371\u9669\u3002\u4e00\u65e6\u672c\u4f53\u8fdb\u5165\u7269\u8d28\u754c\uff0c\u529b\u91cf\u53d7\u5230\u5927\u5e45\u5ea6\u538b\u5236\uff0c\u5f88\u5bb9\u6613\u88ab\u51e1\u4eba\u6740\u6b7b\u2014\u2014\u800c\u6740\u6b7b\u672c\u4f53\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5f7b\u5e95\u7684\u5b8c\u86cb\u3002</p><p>\u6076\u9b54\u786e\u5b9e\u559c\u6b22\u66b4\u529b\u6740\u622e\uff0c\u4f46\u6076\u9b54\u5e76\u4e0d\u662f\u7b28\u86cb\u3002</p><p>\u5176\u5b9e\u6700\u5e38\u89c1\u7684\u60c5\u5f62\u662f\uff1a\u7269\u8d28\u754c\u7684\u4e00\u4f4d\u5deb\u5e08\uff0c\u53ec\u5524\u4e86\u4e00\u53ea\u6076\u9b54\uff0c\u4ed6\u4eec\u8fbe\u6210\u67d0\u4e2a\u534f\u8bae\uff0c\u6076\u9b54\u4e3a\u5deb\u5e08\u6548\u529b\uff0c\u6362\u53d6\u81ea\u5df1\u7684\u81ea\u7531\u2014\u2014\u5176\u5b9e\u6076\u9b54\u5f80\u5f80\u662f\u5904\u4e8e\u5f31\u52bf\u4e00\u65b9\u3002\u6240\u8c13\u201c\u6076\u9b54\u4e00\u65e6\u8fdb\u5165\u7269\u8d28\u754c\uff0c\u5c31\u4f1a\u5927\u8086\u6740\u622e\uff0c\u9020\u6210\u5de8\u5927\u7834\u574f\u707e\u96be\u201d\u7684\u8bf4\u6cd5\uff0c\u538b\u6839\u5c31\u662f\u65e0\u7a3d\u4e4b\u8c08\u3002</p><p>\u8eab\u4e3a\u6076\u9b54\uff0c\u800c\u4e14\u662f\u6700\u9ad8\u4f4d\u9636\u7684\u5df4\u6d1b\u708e\u9b54\uff0c\u6b27\u51ef\u5b8c\u5168\u6e05\u695a\u8fd9\u79cd\u8352\u8c2c\u8a00\u8bba\u7684\u6765\u6e90\uff1a\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u7269\u8d28\u754c\u51e1\u4eba\u5bf9\u6076\u9b54\u7684\u65e0\u77e5\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u5219\u662f\u9b54\u9b3c\u4eec\u2014\u2014\u6076\u9b54\u4eec\u7684\u6b7b\u654c\u2014\u2014\u5728\u63a8\u6ce2\u52a9\u6f9c\uff0c\u717d\u98ce\u70b9\u706b\u3002</p><p>\u5982\u679c\u6b27\u51ef\u8bb0\u5f97\u6ca1\u9519\uff0c\u4e5d\u5c42\u5730\u72f1\u7684\u7b2c\u4e00\u5c42\u9b54\u9b3c\u9886\u4e3b\u62dc\u5c14\uff0c\u5c31\u66fe\u7ecf\u5316\u8eab\u51e1\u4eba\uff0c\u5199\u8fc7\u4e00\u672c\u5947\u5e7b\u5c0f\u8bf4\u5728\u7269\u8d28\u754c\u5e7f\u4e3a\u6d41\u4f20\uff0c\u540d\u5b57\u4f3c\u4e4e\u662f\u53eb\u4ec0\u4e48\u201c\u6076\u9b54\u4e66\u201d\uff0c\u5176\u4e2d\u7740\u529b\u4e11\u5316\u6076\u9b54\uff0c\u63cf\u7ed8\u67d0\u4e2a\u51e1\u4eba\u53ec\u5524\u4e86\u4e00\u53ea\u53eb\u505a\u4f2f\u5fb7\u7684\u6076\u9b54\uff0c\u7136\u540e\u4f2f\u5fb7\u5728\u7269\u8d28\u754c\u5927\u8086\u7834\u574f\u7684\u7ecf\u5386\u3002\u504f\u504f\u8fd9\u672c\u4e66\u8fd8\u5728\u7269\u8d28\u754c\u9887\u4e3a\u6d41\u884c\uff0c\u90a3\u4e9b\u611a\u8822\u7684\u51e1\u4eba\u538b\u6839\u6ca1\u6709\u8bc6\u7834\u8fd9\u5176\u5b9e\u662f\u9b54\u9b3c\u7684\u9634\u8c0b\uff0c\u5bf9\u4e66\u4e2d\u7684\u5185\u5bb9\u4fe1\u4ee5\u4e3a\u771f\uff0c\u4e8e\u662f\u6076\u9b54\u7684\u58f0\u8a89\u66f4\u52a0\u4e0b\u964d\u2014\u2014\u5982\u679c\u6b64\u524d\u8fd8\u6ca1\u6709\u964d\u5230\u8c37\u5e95\u7684\u8bdd\u3002</p><p>\u603b\u4e4b\uff0c\u6076\u9b54\u5176\u5b9e\u5e76\u4e0d\u600e\u4e48\u559c\u6b22\u6765\u5230\u7269\u8d28\u754c\u3002\u5012\u5e76\u4e0d\u8bf4\u5371\u9669\u2014\u2014\u7269\u8d28\u754c\u518d\u5371\u9669\uff0c\u96be\u9053\u8fd8\u6bd4\u5f97\u8fc7\u5145\u6ee1\u8840\u8165\u6740\u622e\u7684\u65e0\u5c3d\u6df1\u6e0a\uff1f\u66f4\u522b\u8bf4\u90a3\u5df2\u7ecf\u548c\u5730\u72f1\u7684\u9b54\u9b3c\u8fdb\u884c\u4e86\u5343\u4e07\u5e74\u7684\u8840\u6218\uff0c\u518d\u5f3a\u5927\u7684\u5b58\u5728\uff0c\u9677\u5165\u8840\u6218\u4e5f\u4e0d\u6562\u4fdd\u8bc1\u80fd\u5168\u8eab\u800c\u9000\u3002</p><p>\u6076\u9b54\u4e4b\u6240\u4ee5\u4e0d\u613f\u610f\u6765\u7269\u8d28\u754c\uff0c\u4e3b\u8981\u8fd8\u662f\u65e0\u5229\u53ef\u56fe\u3002\u94b1\u8d22\uff1f\u7269\u8d28\u754c\u7684\u91d1\u94f6\u73e0\u5b9d\u5728\u6df1\u6e0a\u7b49\u82e5\u5e9f\u7269\uff0c\u6076\u9b54\u7684\u4e16\u754c\u91cc\u6ca1\u6709\u4efb\u4f55\u5546\u4e1a\u4f53\u7cfb\uff0c\u4e5f\u6ca1\u6709\u8d27\u5e01\u6d41\u901a\uff1b\u7f8e\u8272\uff1f\u6df1\u6e0a\u4e2d\u6709\u65e0\u6570\u7684\u9b45\u9b54\uff0c\u90fd\u662f\u5996\u5a06\u7f8e\u8c8c\u7684\u7edd\u8272\u5973\u5b50\uff0c\u800c\u4e14\u53ef\u4ee5\u968f\u4f60\u5fc3\u610f\u5343\u53d8\u4e07\u5316\uff0c\u65e0\u8bba\u662f\u76f8\u8c8c\u8eab\u6750\u8fd8\u662f\u5e8a\u4e0a\u6280\u5de7\uff0c\u90fd\u6bd4\u7269\u8d28\u754c\u7684\u51e1\u4eba\u5973\u5b50\u5f3a\u591a\u4e86\u3002\u7edd\u5927\u591a\u6570\u5728\u7269\u8d28\u754c\u975e\u5e38\u8d35\u91cd\u7684\u4e1c\u897f\uff0c\u5bf9\u4e8e\u6076\u9b54\u6765\u8bf4\u76f8\u5f53\u4e8e\u5783\u573e\uff0c\u9664\u4e86\u4e00\u6837\uff1a\u7075\u9b42\u3002</p><p>\u51e1\u4eba\u7684\u7075\u9b42\uff0c\u80fd\u5f3a\u5316\u6076\u9b54\u7684\u529b\u91cf\uff0c\u5982\u679c\u5e26\u5165\u6df1\u6e0a\uff0c\u8fd8\u80fd\u88ab\u6df7\u4e71\u672c\u8d28\u6240\u5f71\u54cd\uff0c\u8f6c\u5316\u4e3a\u76f8\u5e94\u4f4d\u9636\u7684\u6076\u9b54\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u73cd\u8d35\u7684\u4e1c\u897f\u3002\u4f46\u5982\u679c\u8fd9\u4e48\u505a\uff0c\u5c31\u610f\u5473\u7740\u8981\u5728\u7269\u8d28\u754c\u5927\u8086\u6740\u622e\uff0c\u8fd9\u5f88\u5bb9\u6613\u88ab\u7fa4\u8d77\u800c\u653b\u4e4b\uff0c\u62db\u6765\u65e0\u6570\u5f3a\u654c\u8054\u624b\u527f\u6740\u3002</p><p>\u4f46\u6b27\u51ef\u6ca1\u529e\u6cd5\uff0c\u4ea1\u7075\u541b\u738b\u5965\u5580\u65af\u6d3e\u9063\u4ed6\u6765\u7269\u8d28\u754c\uff0c\u800c\u4e14\u662f\u4ee5\u672c\u4f53\u7684\u65b9\u5f0f\u3002\u6076\u9b54\u5e76\u4e0d\u662f\u670d\u4ece\u547d\u4ee4\u7684\u751f\u7269\uff0c\u4f46\u6076\u9b54\u4f1a\u5c48\u670d\u4e8e\u5f3a\u6743\u2014\u2014\u8eab\u4e3a\u6df1\u6e0a\u4e09\u5927\u9886\u4e3b\u4e4b\u4e00\uff0c\u5965\u5580\u65af\u663e\u7136\u6bd4\u6b27\u51ef\u5f3a\u3002</p><p>\u6240\u4ee5\u6b27\u51ef\u53ea\u80fd\u4e56\u4e56\u542c\u8bdd\u3002</p><p>\u5965\u5580\u65af\u5728\u7269\u8d28\u754c\u7684\u4fe1\u5f92\u4e3e\u884c\u4e86\u79d8\u5bc6\u7684\u796d\u7940\uff0c\u727a\u7272\u4e86\u65e0\u6570\u65e0\u8f9c\u7684\u751f\u547d\u2014\u2014\u5176\u4e2d\u5305\u62ec\u5965\u5580\u65af\u7684\u6570\u540d\u9ad8\u9636\u796d\u53f8\uff0c\u7ec8\u4e8e\u6210\u529f\u6253\u5f00\u4f4d\u9762\u901a\u9053\uff0c\u8ba9\u6b27\u51ef\u6765\u5230\u7269\u8d28\u754c\u3002</p><p>\u6b27\u51ef\u6765\u5230\u7269\u8d28\u754c\u7684\u7b2c\u4e00\u4e2a\u4efb\u52a1\uff0c\u5c31\u662f\u53bb\u5e72\u6389\u683c\u62c9\u5179\u7279\u7684\u4eba\u9762\u72ee\u4fe1\u5f92\u3002\u8eab\u4e3a\u4e4c\u9eef\u541b\u4e3b\u6614\u65e5\u7684\u4eb2\u4fe1\u90e8\u4e0b\uff0c\u6b27\u51ef\u5bf9\u4eba\u9762\u72ee\u7684\u60c5\u51b5\u81ea\u7136\u975e\u5e38\u6e05\u695a\u3002\u4ed6\u987a\u5229\u5730\u6765\u5230\u54c8\u52b3\u683c\u8fbe\u65af\u57ce\uff0c\u53d1\u73b0\u4eba\u9762\u72ee\u4eec\u6b63\u5728\u4e3e\u884c\u796d\u7940\uff0c\u4e8e\u662f\u4ed6\u6084\u6084\u6f5c\u5165\u795e\u6bbf\uff0c\u9690\u85cf\u5728\u4e00\u8fb9\uff0c\u51c6\u5907\u65f6\u673a\u6070\u5f53\u7684\u65f6\u5019\u52a8\u624b\u3002</p><p>\u7136\u540e\u4ed6\u6ce8\u610f\u5230\u4e86\u6885\u83f2\u65af\uff0c\u4e5f\u53d1\u73b0\u4e86\u743c\u6069\u3002</p><p>\u4f5c\u4e3a\u5f3a\u5927\u7684\u708e\u9b54\uff0c\u6b27\u51ef\u7684\u53cc\u773c\u80fd\u770b\u7834\u5e7b\u672f\u548c\u53d8\u5f62\uff0c\u4ed6\u6e05\u695a\u5730\u770b\u89c1\u4eba\u9762\u72ee\u4e2d\u9690\u85cf\u7740\u4e00\u4f4d\u4eba\u7c7b\u5973\u6027\u3002\u6b27\u51ef\u5e76\u4e0d\u77e5\u9053\u6885\u83f2\u65af\u7684\u6765\u610f\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u6e05\u695a\u9ece\u660e\u4e4b\u77f3\u7684\u4e8b\u60c5\uff0c\u4f46\u6b27\u51ef\u77e5\u9053\u8fd9\u4f4d\u4eba\u7c7b\u5973\u5b50\u663e\u7136\u662f\u4eba\u9762\u72ee\u7684\u654c\u4eba\uff0c\u7528\u7d22\u5c14\u77f3\u884c\u672f\u8eb2\u5728\u5730\u4e0b\u7684\u90a3\u4e2a\u5deb\u5e08\u5e94\u8be5\u4e5f\u540c\u6837\u3002</p><p>\u654c\u4eba\u7684\u654c\u4eba\uff0c\u5c31\u662f\u670b\u53cb\u2014\u2014\u81f3\u5c11\u662f\u53ef\u4ee5\u5229\u7528\u7684\u5bf9\u8c61\uff0c\u8fd9\u4e2a\u9053\u7406\u6076\u9b54\u4e5f\u662f\u61c2\u7684\u3002</p><p>\u4ed6\u6084\u6084\u5730\u7528\u4e00\u4e2a\u9b54\u6cd5\u5f3a\u5316\u4e86\u6885\u83f2\u65af\u8eab\u4e0a\u7684\u53d8\u5f62\u672f\uff0c\u63a9\u76d6\u4e86\u5973\u5723\u6b66\u58eb\u8eab\u4e0a\u7684\u795e\u5723\u6c14\u606f\u3002\u6076\u9b54\u5bf9\u6b64\u662f\u5f88\u654f\u611f\u7684\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u683c\u62c9\u5179\u7279\u65e9\u5c31\u5bdf\u89c9\u5230\u6885\u83f2\u65af\u4e86\u3002</p><p>\u4f46\u5728\u6b27\u51ef\u539f\u672c\u7684\u8ba1\u5212\u4e2d\uff0c\u6885\u83f2\u65af\u548c\u743c\u6069\u4e0d\u8fc7\u662f\u727a\u7272\u54c1\uff0c\u662f\u7528\u6765\u6253\u5934\u9635\uff0c\u6d88\u8017\u5bf9\u65b9\u7684\u5b9e\u529b\uff0c\u5438\u5f15\u5bf9\u65b9\u7684\u6ce8\u610f\u529b\u7684\u3002\u6b27\u51ef\u5e76\u4e0d\u51c6\u5907\u6551\u4ed6\u4eec\uff0c\u6076\u9b54\u4e0d\u662f\u6148\u5584\u5bb6\u3002</p><p>\u4f46\u8fd9\u4e2a\u4e3b\u610f\u540e\u6765\u6539\u53d8\u4e86\uff0c\u5728\u6b27\u51ef\u770b\u5230\u743c\u6069\u7684\u9f99\u9cde\u76fe\u4e4b\u540e\u3002</p><p>\u5728\u548c\u8482\u5a1c\u5c91\u4ea4\u624b\u4e2d\uff0c\u743c\u6069\u4f7f\u7528\u4e86\u9f99\u9cde\u76fe\u3002\u6b27\u51ef\u4e00\u773c\u4fbf\u770b\u51fa\u76fe\u724c\u7684\u6750\u8d28\u662f\u8679\u5f69\u9f99\u9cde\uff0c\u8fd9\u8ba9\u4ed6\u5fc3\u4e2d\u4e00\u52a8\u3002</p><p>\u8679\u5f69\u9f99\u662f\u6781\u5176\u7a00\u6709\u7684\u79cd\u65cf\uff0c\u6570\u91cf\u6781\u5c11\u2014\u2014\u6b27\u51ef\u6070\u597d\u5c31\u8ba4\u8bc6\u4e00\u53ea\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u8fd9\u9762\u9f99\u9cde\u76fe\u4e0a\uff0c\u9690\u9690\u9644\u7740\u4e9b\u8bb8\u795e\u5723\u6c14\u606f\uff0c\u610f\u5473\u7740\u90a3\u53ea\u8d21\u732e\u51fa\u9cde\u7247\u7684\u8679\u5f69\u9f99\u5f88\u53ef\u80fd\u8fd8\u662f\u4e00\u4f4d\u795e\u7947\uff0c\u6216\u8005\u6709\u795e\u7947\u7684\u8840\u7edf\u3002</p><p>\u6b27\u51ef\u60f3\u8d77\u6765\uff0c\u5728\u591a\u745e\u68ee\u7684\u5b9d\u5e93\u91cc\u66fe\u7ecf\u89c1\u5230\u745e\u6069\u65af\u5766\uff0c\u4ed6\u597d\u50cf\u5c31\u662f\u524d\u4e0d\u4e45\u53d7\u4e86\u4f24\u3002\u745e\u6069\u65af\u5766\u662f\u4e00\u4f4d\u8679\u5f69\u9f99\uff0c\u540c\u65f6\u4e5f\u662f\u795e\u5b50\uff0c\u5b8c\u5168\u7b26\u5408\u6761\u4ef6\u3002</p><p>\u8fd9\u4e2a\u4eba\u7c7b\u5deb\u5e08\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u548c\u745e\u6069\u65af\u5766\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f</p><p>\u6b27\u51ef\u7528\u53f3\u624b\u6258\u7740\u8138\uff0c\u62c7\u6307\u548c\u98df\u6307\u6469\u6332\u7740\u4e0b\u5df4\uff0c\u601d\u8003\u7740\u3002\u5f88\u5feb\uff0c\u4ed6\u4ece\u743c\u6069\u7684\u5deb\u5e08\u957f\u888d\u5f0f\u6837\u4e2d\uff0c\u5224\u65ad\u51fa\u4e86\u8fd9\u4e2a\u4eba\u7c7b\u7684\u6765\u5386\u3002</p><p>\u201c\u8010\u745f\u745e\u5c14\u2026\u2026\u9634\u9b42\u57ce\uff1f\u201d</p><p>\u6b27\u51ef\u6c89\u541f\u7740\uff0c\u5f00\u59cb\u5bf9\u8fd9\u4e2a\u5e74\u8f7b\u7684\u4eba\u7c7b\u611f\u5174\u8da3\u8d77\u6765\u3002\u6765\u81ea\u9634\u9b42\u57ce\u7684\u5deb\u5e08\uff0c\u4ed6\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u770b\u8d77\u6765\uff0c\u4ed6\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u5f88\u666e\u901a\u7684\u5deb\u5e08\uff0c\u5728\u9634\u9b42\u57ce\u4e2d\u4f4d\u9636\u5b9a\u7136\u4e0d\u9ad8\uff0c\u90a3\u4e48\u8679\u5f69\u9f99\u9cde\u76fe\u8fd9\u79cd\u5b9d\u7269\uff0c\u600e\u4e48\u4f1a\u843d\u5728\u4ed6\u624b\u91cc\uff1f</p><p>\u708e\u9b54\u654f\u9510\u5730\u5bdf\u89c9\u5230\u4e86\u4e00\u4e9b\u4e1c\u897f\uff0c\u4ed6\u51b3\u5b9a\u6551\u4e0b\u8fd9\u4e24\u4e2a\u4eba\u7c7b\uff0c\u8fd9\u5c31\u8981\u9762\u5bf9\u683c\u62c9\u5179\u7279\u3002\u4ee5\u529b\u91cf\u800c\u8bba\uff0c\u6b27\u51ef\u81ea\u7136\u8fdc\u8fdc\u4e0d\u662f\u683c\u62c9\u5179\u7279\u7684\u654c\u624b\uff0c\u4f46\u6b64\u65f6\u60c5\u5f62\u4e0d\u540c\u3002\u8fd9\u91cc\u662f\u7269\u8d28\u754c\uff0c\u4ed6\u4ee5\u672c\u4f53\u524d\u6765\uff0c\u683c\u62c9\u5179\u7279\u5374\u4ec5\u4ec5\u662f\u4e00\u4e2a\u6295\u5f71\u3002</p><p>\u800c\u4e14\u662f\u5df2\u7ecf\u88ab\u6668\u66e6\u5723\u5149\u548c\u9ece\u660e\u4e4b\u77f3\u91cd\u521b\u7684\u6295\u5f71\u3002</p><p>\u4ed6\u6697\u4e2d\u63d2\u624b\uff0c\u6539\u53d8\u4e86\u743c\u6069\u53d1\u51fa\u7684\u77f3\u5f39\u672f\uff0c\u4e00\u51fb\u6740\u6b7b\u4e86\u8482\u5a1c\u5c91\u3002\u6b64\u524d\u683c\u62c9\u5179\u7279\u7684\u5168\u526f\u6ce8\u610f\u529b\u90fd\u653e\u5728\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u5982\u4eca\u867d\u7136\u53d1\u73b0\u6b27\u51ef\uff0c\u5374\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002</p><p>\u683c\u62c9\u5179\u7279\u9000\u8d70\uff0c\u6b27\u51ef\u5e76\u6ca1\u6709\u8d70\u51fa\u3002\u4ed6\u6697\u4e2d\u7528\u4e86\u4e00\u4e2a\u5f3a\u5927\u7684\u5492\u6cd5\uff0c\u6740\u6b7b\u4e86\u6240\u6709\u7684\u4eba\u9762\u72ee\uff0c\u7136\u540e\u9759\u9759\u5730\u5728\u9634\u5f71\u4e2d\u89c2\u5bdf\u7740\u3002</p><p>\u4ed6\u770b\u5230\u743c\u6069\u73a9\u5f04\u7740\u6885\u83f2\u65af\uff0c\u6700\u540e\u5728\u5979\u7684\u5c0f\u5634\u4e2d\u53d1\u6cc4\u3002\u51e1\u4eba\u7684\u6027\u7231\u8ba9\u708e\u9b54\u770b\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u9887\u89c9\u4e0e\u6df1\u6e0a\u4e2d\u6709\u6240\u4e0d\u540c\u3002\u968f\u540e\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u79bb\u5f00\u795e\u6bbf\uff0c\u6b27\u51ef\u624d\u6162\u6162\u8d70\u51fa\u6765\u3002</p><p>\u4eba\u9762\u72ee\u5df2\u7ecf\u5168\u90e8\u6740\u6b7b\uff0c\u683c\u62c9\u5179\u7279\u7684\u796d\u7940\u4e5f\u6ca1\u6709\u6210\u529f\uff0c\u7b2c\u4e00\u4e2a\u4efb\u52a1\u5df2\u7ecf\u5b8c\u6210\u4e86\uff1b\u90a3\u4e48\uff0c\u63a5\u4e0b\u6765\uff0c\u8be5\u53bb\u8fdb\u884c\u6b64\u6b21\u6765\u7269\u8d28\u754c\u7684\u7b2c\u4e8c\u4e2a\u76ee\u7684\u4e86\u3002</p><p>\u8be5\u662f\u53bb\u535a\u5f97\u4e4b\u95e8\u89c1\u62c9\u6c83\u514b\u7684\u65f6\u5019\u4e86\uff0c\u6b27\u51ef\u5fc3\u91cc\u60f3\u3002</p><p>\u521a\u624d\u90a3\u4e24\u4e2a\u4eba\u7c7b\uff0c\u4f3c\u4e4e\u4e5f\u662f\u8981\u9001\u9ece\u660e\u4e4b\u77f3\u53bb\u535a\u5f97\u4e4b\u95e8\u5462\u3002</p></article>"}}}]);
