(window.webpackJsonp=window.webpackJsonp||[]).push([[129],{151:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u53cc\u5934\u5de8\u9b54",content:"<article><h2>\u7b2c\u4e8c\u8282 \u53cc\u5934\u5de8\u9b54</h2><p>\u201c\u53cc\u5934\uff1f\u201d\u4f17\u4eba\u90fd\u4e0d\u514d\u6709\u4e9b\u6115\u7136\uff0c\u5c31\u8fde\u51e0\u6b21\u6765\u8fc7\u5de8\u9b54\u5c71\u8109\uff0c\u53f7\u79f0\u7ecf\u9a8c\u4e30\u5bcc\u7684\u590f\u62c9\u975e\u90fd\u6709\u4e9b\u88ab\u6014\u4f4f\u4e86\u3002\u5de8\u9b54\u5012\u662f\u5927\u5bb6\u90fd\u77e5\u9053\u7684\uff0c\u4f46\u4ec0\u4e48\u65f6\u5019\u542c\u8bf4\u5de8\u9b54\u957f\u4e24\u4e2a\u8111\u888b\u4e86\uff1f</p><p>\u8fd8\u662f\u8bf4\uff0c\u8fd9\u662f\u65b0\u54c1\u79cd\u7684\u53cc\u5934\u5de8\u9b54\uff0c\u57fa\u56e0\u53d8\u5f02\u7684\u4ea7\u7269\uff1f</p><p>\u4f46\u6b64\u65f6\u6765\u4e0d\u53ca\u8003\u8651\u8fd9\u4e9b\uff0c\u53cc\u5934\u5de8\u9b54\u5df2\u7ecf\u5927\u8e0f\u6b65\u51b2\u4e0a\u524d\u6765\uff0c\u5b83\u7684\u53e3\u4e2d\u53d1\u51fa\u5475\u5475\u7684\u4f4e\u6c89\u5578\u58f0\uff0c\u53f3\u624b\u4e2d\u8fd8\u6284\u7740\u4e00\u6839\u5de8\u5927\u7684\u7070\u8272\u7c97\u6728\u68d2\uff0c\u770b\u6765\u6253\u7b97\u628a\u8fd9\u51e0\u4e2a\u4eba\u7c7b\u5f53\u5348\u9910\u4e86\u3002</p><p>\u743c\u6069\u7a0d\u7a0d\u540e\u9000\u4e00\u6b65\uff0c\u4f38\u624b\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u64ae\u7c89\u672b\uff0c\u8111\u4e2d\u5f00\u59cb\u6d6e\u73b0\u5492\u6587\uff0c\u4f46\u5e76\u6ca1\u6709\u6025\u7740\u91ca\u653e\u51fa\u53bb\u3002\u4ec5\u4ec5\u4e00\u53ea\u5de8\u9b54\u7f62\u4e86\uff0c\u4e0d\u8db3\u4e3a\u8651\uff0c\u8fd9\u53ea\u5c0f\u961f\u7684\u76ee\u7684\uff0c\u53ef\u662f\u8981\u53bb\u5e72\u6389\u62c9\u6c83\u514b\u7684\uff0c\u5982\u679c\u8fde\u53ea\u5de8\u9b54\u90fd\u4e0d\u80fd\u8f7b\u677e\u641e\u5b9a\uff0c\u90a3\u76f4\u63a5\u6253\u9053\u56de\u5e9c\u5f97\u4e86\u3002</p><p>\u4ed6\u7528\u773c\u89d2\u4f59\u5149\u77a5\u5411\u961f\u53cb\uff0c\u53d1\u73b0\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u4f3c\u4e4e\u4e5f\u548c\u4ed6\u62b1\u7740\u540c\u6837\u7684\u60f3\u6cd5\uff0c\u795e\u6001\u60a0\u95f2\uff0c\u4e0d\u614c\u4e0d\u5fd9\uff0c\u4e00\u526f\u8896\u624b\u65c1\u89c2\u7684\u67b6\u5f0f\u3002\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u8d1d\u7eb3\u7d22\u5219\u8981\u7d27\u5f20\u591a\u4e86\uff0c\u4ed6\u5306\u5306\u5fd9\u5fd9\u5730\u4ece\u8170\u540e\u53d6\u51fa\u9489\u5934\u9524\u6765\uff0c\u53c8\u51c6\u5907\u4ece\u53e3\u888b\u91cc\u638f\u5377\u8f74\uff0c\u4e00\u65f6\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u3002\u8fd9\u4f4d\u9996\u5e2d\u7267\u5e08\u663e\u7136\u5e73\u7d20\u4e25\u91cd\u7f3a\u4e4f\u5b9e\u6218\u7ecf\u9a8c\uff0c\u6216\u8005\u8bf4\uff0c\u4ed6\u5728\u795e\u6bbf\u91cc\u517b\u5c0a\u5904\u4f18\u5f97\u592a\u4e45\u4e86\u3002</p><p>\u743c\u6069\u66fe\u7ecf\u65c1\u6572\u4fa7\u51fb\u5730\u6253\u542c\u8fc7\u8fd9\u4f4d\u8d1d\u7eb3\u7d22\u7267\u5e08\u7684\u7ecf\u5386\uff0c\u77e5\u9053\u4ed6\u662f\u535a\u5f97\u4e4b\u95e8\u571f\u751f\u571f\u957f\u7684\u5c45\u6c11\uff0c\u5bb6\u65cf\u4e16\u4e16\u4ee3\u4ee3\u90fd\u662f\u6668\u66e6\u4e4b\u795e\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u4ed6\u4ece\u5341\u4e94\u5c81\u5c31\u8fdb\u5165\u795e\u6bbf\uff0c\u4f8d\u5949\u795e\u7947\uff0c\u9010\u6e10\u7d2f\u79ef\u8d44\u5386\uff0c\u63d0\u5347\u4f4d\u9636\uff0c\u6700\u7ec8\u6210\u4e3a\u6559\u4f1a\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u6267\u638c\u672c\u5730\u795e\u6bbf\u3002</p><p>\u4f5c\u4e3a\u9ad8\u9636\u7267\u5e08\uff0c\u4ed6\u81ea\u7136\u4e5f\u63a5\u53d7\u8fc7\u6218\u6597\u8bad\u7ec3\u3002\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7d20\u6765\u4ee5\u201c\u5d07\u5584\u201d\u8457\u79f0\uff0c\u4ee5\u6253\u51fb\u5404\u79cd\u90aa\u6076\u52bf\u529b\uff08\u5c24\u5176\u662f\u4ea1\u7075\uff09\u4e3a\u5df1\u4efb\uff0c\u795e\u804c\u4eba\u5458\u9664\u4e86\u4f20\u6559\u5e03\u9053\u4e4b\u5916\uff0c\u5f80\u5f80\u4e5f\u517c\u4efb\u6b63\u4e49\u4f7f\u8005\u548c\u4ea1\u7075\u730e\u624b\uff0c\u5fc5\u987b\u8981\u6709\u4e00\u5b9a\u7684\u6218\u6597\u80fd\u529b\uff0c\u5426\u5219\u5c82\u4e0d\u662f\u7a7a\u8c08\u3002\u53ea\u662f\u770b\u6765\u662f\u548c\u5e73\u5b89\u9038\u7684\u65e5\u5b50\u8fc7\u5f97\u592a\u4e45\uff0c\u8d1d\u7eb3\u7d22\u5148\u751f\u90fd\u5df2\u7ecf\u628a\u5f53\u5e74\u63a5\u53d7\u7684\u8bad\u7ec3\u5dee\u4e0d\u591a\u7ed9\u5fd8\u5149\u4e86\u3002</p><p>\u590f\u62c9\u975e\u53d6\u4e0b\u5341\u5b57\u5f13\uff0c\u642d\u4e0a\u706b\u7130\u77e2\uff0c\u5df2\u7ecf\u7784\u51c6\u4e86\u5de8\u9b54\uff0c\u4f46\u6ca1\u6709\u5c04\u51fb\uff0c\u4ed6\u4e5f\u5728\u7b49\u5f85\u7740\u3002\u6885\u83f2\u65af\u548c\u9686\u5947\u5df2\u7ecf\u4e00\u5de6\u4e00\u53f3\u5305\u6284\u4e86\u8fc7\u53bb\uff0c\u8fd9\u4e24\u4eba\u4e4b\u524d\u4ece\u6ca1\u5728\u4e00\u8d77\u5408\u4f5c\u8fc7\uff0c\u4e34\u65f6\u914d\u5408\u8d77\u6765\u5012\u662f\u9887\u4e3a\u9ed8\u5951\uff0c\u770b\u8d77\u6765\u90fd\u662f\u5b9e\u6218\u7ecf\u9a8c\u4e30\u5bcc\u4e4b\u8f88\u3002</p><p>\u4e00\u53ea\u5de8\u9b54\u7f62\u4e86\uff0c\u867d\u7136\u957f\u4e24\u4e2a\u8111\u888b\uff0c\u5e94\u8be5\u4e5f\u4e0d\u8db3\u4e3a\u8651\uff0c\u6885\u83f2\u65af\u548c\u9686\u5947\u4e24\u4eba\u51fa\u52a8\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u80fd\u8f7b\u677e\u683c\u6740\u4e86\u3002\u5173\u952e\u5012\u662f\u8981\u901f\u6218\u901f\u51b3\uff0c\u522b\u62d6\u5f97\u4e45\u4e86\uff0c\u5f15\u6765\u66f4\u591a\u7684\u5de8\u9b54\uff0c\u90a3\u5c31\u6bd4\u8f83\u9ebb\u70e6\u4e86\u3002</p><p>\u5de8\u9b54\u98de\u5954\u800c\u6765\uff0c\u89c1\u6885\u83f2\u65af\u548c\u9686\u5947\u8fce\u4e0a\uff0c\u7a81\u5730\u7ad9\u5b9a\u811a\u6b65\uff0c\u5de6\u8fb9\u7684\u90a3\u9897\u8111\u888b\u5f20\u53e3\u5927\u53eb\u4e00\u58f0\uff0c\u4e0d\u77e5\u543c\u4e86\u53e5\u4ec0\u4e48\uff0c\u5de6\u624b\u62ac\u8d77\u4e00\u6307\uff0c\u4e00\u56e2\u7403\u72b6\u95ea\u7535\u8f70\u7136\u4ece\u6307\u5c16\u8ff8\u51fa\uff0c\u671d\u9686\u5947\u98de\u5c04\u8fc7\u53bb\u3002</p><p>\u6240\u6709\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5de8\u9b54\u5c45\u7136\u662f\u4e2a\u5deb\u5e08\u2026\u2026\u8fd8\u662f\u4e2a\u672f\u58eb\uff1f</p><p>\u4f17\u6240\u5468\u77e5\uff0c\u5de8\u9b54\u7684\u8111\u7b4b\u5f88\u4e0d\u7075\u5149\uff0c\u7528\u4eba\u7c7b\u7684\u6807\u51c6\u6765\u770b\u5c31\u662f\u5f31\u667a\uff0c\u4ed6\u4eec\u7e41\u6b96\u80fd\u529b\u53c8\u4e0d\u5f3a\uff0c\u5018\u82e5\u4e0d\u662f\u90a3\u8fd1\u4e4e\u65e0\u9650\u518d\u751f\u7684\u7279\u8d28\uff0c\u53ea\u6015\u65e9\u5c31\u5df2\u7ecf\u88ab\u706d\u7edd\u4e86\u3002\u8fd9\u79cd\u611a\u8822\u7684\u751f\u7269\u600e\u4e48\u53ef\u80fd\u638c\u63e1\u9b54\u6cd5\u8fd9\u79cd\u9ad8\u6df1\u6280\u5de7\uff1f</p><p>\u4f46\u66f4\u4ee4\u4eba\u5403\u60ca\u7684\u4e8b\u60c5\u8fd8\u5728\u540e\u9762\u3002\u7403\u72b6\u95ea\u7535\u671d\u9686\u5947\u75be\u5c04\u800c\u6765\uff0c\u6b66\u50e7\u867d\u7136\u5343\u9524\u767e\u70bc\uff0c\u6709\u62b5\u6297\u9b54\u6cd5\u7684\u80fd\u529b\uff0c\u4f46\u4e5f\u4e0d\u662f\u8bf4\u771f\u50bb\u5230\u4f1a\u5e73\u767d\u65e0\u6545\u53bb\u786c\u6328\u4e00\u8bb0\u3002\u4ed6\u786c\u751f\u751f\u987f\u4f4f\u524d\u51b2\u7684\u8eab\u5f62\uff0c\u4fa7\u6251\u7ffb\u6eda\uff0c\u9669\u9669\u907f\u8ba9\u8fc7\u53bb\u3002\u7403\u72b6\u95ea\u7535\u64e6\u8eab\u800c\u8fc7\uff0c\u51fb\u5728\u4ed6\u8eab\u540e\u4e00\u9897\u6811\u4e0a\uff0c\u767b\u65f6\u5c06\u90a3\u9897\u7897\u53e3\u7c97\u7684\u6811\u62e6\u8170\u6253\u65ad\u3002</p><p>\u9686\u5947\u8fd9\u4fa7\u53d7\u963b\uff0c\u53e6\u5916\u4e00\u8fb9\u6885\u83f2\u65af\u5df2\u7ecf\u903c\u8fd1\uff0c\u5979\u89c1\u8fd9\u5de8\u9b54\u9887\u4e0d\u5bfb\u5e38\uff0c\u957f\u4e24\u4e2a\u8111\u888b\u5c31\u7f62\u4e86\uff0c\u5c45\u7136\u8fd8\u80fd\u91ca\u653e\u95ea\u7535\uff0c\u8fd9\u5df2\u7ecf\u5927\u5927\u8fdd\u80cc\u4e86\u5e38\u8bc6\uff0c\u53ea\u6015\u803d\u6401\u4e0b\u53bb\uff0c\u4e0d\u77e5\u5b83\u8fd8\u8981\u5f04\u51fa\u4ec0\u4e48\u82b1\u6837\u3002\u5f53\u4e0b\u4e5f\u4e0d\u591a\u8bf4\uff0c\u8dc3\u8d77\u8eab\u6765\uff0c\u4e00\u5251\u5f53\u5934\u5288\u4e0b\u3002</p><p>\u5de8\u9b54\u867d\u7136\u76f4\u7acb\u8d77\u6765\u6709\u4e24\u4eba\u9ad8\uff0c\u4f46\u56e0\u4e3a\u524d\u80a2\u592a\u957f\uff0c\u603b\u662f\u4f5d\u507b\u7740\u8eab\u8eaf\uff0c\u6240\u4ee5\u770b\u8d77\u6765\u5e76\u4e0d\u6bd4\u4eba\u7c7b\u9ad8\u591a\u5c11\u3002\u6885\u83f2\u65af\u501f\u7740\u524d\u51b2\u4e4b\u52bf\u4e00\u8dc3\u800c\u8d77\uff0c\u94f6\u5251\u731b\u5288\uff0c\u773c\u770b\u5c31\u8981\u5c06\u8fd9\u53ea\u53cc\u5934\u5de8\u9b54\u5288\u6210\u4e24\u534a\u3002\u5979\u90a3\u628a\u94f6\u5251\u540d\u5b57\u53eb\u201c\u7737\u604b\u201d\uff0c\u542c\u8d77\u6765\u6e29\u67d4\uff0c\u5176\u5b9e\u950b\u9510\u81f3\u6781\uff0c\u65ad\u91d1\u5207\u7389\u90fd\u4e0d\u662f\u96be\u4e8b\u3002</p><p>\u4f46\u5de8\u9b54\u5e76\u6ca1\u6709\u88ab\u5288\u6210\u4e24\u534a\u3002</p><p>\u5b83\u8fc5\u6377\u65e0\u6bd4\u5730\u62ac\u8d77\u53f3\u624b\u4e2d\u63e1\u7740\u7684\u7070\u8272\u7c97\u6728\u68d2\uff0c\u7a33\u7a33\u683c\u4f4f\u4e86\u5288\u4e0b\u6765\u7684\u94f6\u5251\uff0c\u53d1\u51fa\u6c89\u95f7\u7684\u649e\u51fb\u58f0\u3002\u8fd9\u6839\u6728\u68d2\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u6750\u8d28\u505a\u7684\uff0c\u65e2\u4e0d\u50cf\u91d1\u5c5e\uff0c\u53c8\u4e0d\u4f3c\u5bfb\u5e38\u6728\u5934\uff0c\u5c45\u7136\u62b5\u4f4f\u4e86\u6885\u83f2\u65af\u7684\u94f6\u5251\u3002\u5de8\u9b54\u7684\u4e24\u4e2a\u8111\u888b\u4e00\u9f50\u6012\u543c\u7740\uff0c\u624b\u81c2\u4e00\u632f\uff0c\u5c06\u6885\u83f2\u65af\u5012\u649e\u51fa\u53bb\u3002</p><p>\u4ee5\u4eba\u7c7b\u7684\u6807\u51c6\u6765\u8bf4\uff0c\u6885\u83f2\u65af\u7684\u529b\u6c14\u5df2\u7ecf\u7b97\u662f\u6781\u5927\uff0c\u4f46\u548c\u8fd9\u5de8\u9b54\u76f8\u6bd4\u8fd8\u662f\u900a\u8272\u51e0\u5206\uff0c\u5979\u53c8\u8eab\u5728\u7a7a\u4e2d\uff0c\u65e0\u4ece\u501f\u529b\uff0c\u88ab\u5de8\u9b54\u4e00\u63a8\uff0c\u767b\u65f6\u649e\u9000\u56de\u6765\u3002\u521a\u521a\u843d\u5730\uff0c\u6b63\u8981\u518d\u4e0a\u524d\u653b\u51fb\uff0c\u5c31\u89c1\u90a3\u5de8\u9b54\u53f3\u8fb9\u8111\u888b\u4e0a\u53cc\u773c\u4e00\u77aa\uff0c\u5de8\u53e3\u5927\u5f20\uff0c\u8f70\u5730\u55b7\u51fa\u4e00\u9053\u70c8\u7130\u6765\uff0c\u6eda\u6eda\u5982\u6d9b\uff0c\u671d\u6885\u83f2\u65af\u6d8c\u53bb\u3002</p><p>\u706b\u7130\uff01</p><p>\u5de8\u9b54\u4e0d\u662f\u6700\u6015\u706b\u7130\u7684\u4e48\uff0c\u8fd9\u662f\u4ed6\u4eec\u7684\u81f4\u547d\u514b\u661f\u3002\u8fd9\u53ea\u53cc\u5934\u5de8\u9b54\u80fd\u91ca\u653e\u95ea\u7535\u5df2\u7ecf\u591f\u60ca\u4eba\u7684\u4e86\uff0c\u5c45\u7136\u8fd8\u80fd\u53e3\u4e2d\u55b7\u706b\uff0c\u8fd9\u5c82\u4e0d\u662f\u8bf4\u5b83\u8fde\u706b\u7130\u90fd\u4e0d\u6015\uff1f</p><p>\u6240\u6709\u4eba\u90fd\u90d1\u91cd\u8d77\u6765\uff0c\u5c31\u8fde\u4e00\u76f4\u6709\u4e9b\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u6c83\u91d1\u7267\u5e08\u4e5f\u90fd\u4f38\u624b\u63a2\u5165\u8170\u95f4\u7684\u91d1\u8272\u5c0f\u53e3\u888b\uff0c\u4e0d\u77e5\u51c6\u5907\u53d6\u4ec0\u4e48\u4e1c\u897f\u51fa\u6765\u3002\u53cc\u5934\u3001\u80fd\u91ca\u653e\u95ea\u7535\u7403\u3001\u80fd\u55b7\u706b\u2026\u2026\u8fd9\u5df2\u7ecf\u5b8c\u5168\u98a0\u8986\u4e86\u4ed6\u4eec\u5bf9\u5de8\u9b54\u7684\u8ba4\u77e5\u3002</p><p>\u96be\u9053\u8bf4\u8fd9\u662f\u7279\u6b8a\u7684\u5de8\u9b54\uff1f\u8fd8\u662f\u8bf4\uff0c\u5b83\u5176\u5b9e\u538b\u6839\u5c31\u4e0d\u662f\u5de8\u9b54\uff0c\u53ea\u662f\u957f\u5f97\u6709\u4e9b\u50cf\u800c\u5df2\uff1f</p><p>\u6b64\u65f6\u65e0\u6687\u7422\u78e8\u8fd9\u4e9b\u3002\u6885\u83f2\u65af\u89c1\u706b\u7130\u55b7\u6765\uff0c\u5e76\u4e0d\u95ea\u907f\uff0c\u5979\u8eab\u4e0a\u7684\u94f6\u7532\u662f\u94f6\u9f99\u76ae\u6240\u5236\uff0c\u4f9d\u65e7\u4fdd\u6301\u4e86\u94f6\u9f99\u7684\u5bd2\u51b0\u7279\u8d28\uff0c\u8db3\u4ee5\u62b5\u5fa1\u706b\u7130\uff0c\u5f53\u65e5\u51fb\u6740\u9aa8\u866b\u65f6\uff0c\u5979\u5c31\u4ece\u706b\u6d77\u4e2d\u5b89\u7136\u6b65\u51fa\uff0c\u534a\u70b9\u4e0d\u66fe\u53d7\u4f24\u3002\u5982\u4eca\u89c1\u8fd9\u5de8\u9b54\u5c45\u7136\u80fd\u55b7\u706b\uff0c\u867d\u7136\u60ca\u8be7\uff0c\u5374\u4e5f\u5e76\u4e0d\u614c\u5f20\uff0c\u8fce\u7740\u706b\u7130\u518d\u5ea6\u51b2\u4e0a\u3002</p><p>\u8fd9\u8fb9\u6b66\u50e7\u907f\u8fc7\u7403\u72b6\u95ea\u7535\uff0c\u7565\u4e00\u803d\u6401\uff0c\u4e5f\u5df2\u7ecf\u4ece\u53e6\u5916\u4e00\u4fa7\u5939\u653b\u4e0a\u6765\u3002\u5de8\u9b54\u6012\u58f0\u543c\u53eb\uff0c\u9707\u7684\u8fdc\u5904\u5c71\u5ce6\u9690\u9690\u56de\u54cd\uff0c\u4e00\u624b\u6325\u821e\u7c97\u6728\u68d2\uff0c\u671d\u6885\u83f2\u65af\u5f53\u5934\u7838\u53bb\uff0c\u4e00\u624b\u56db\u6307\u5982\u5229\u5203\uff0c\u671d\u9686\u5947\u6293\u6765\u3002</p><p>\u5b83\u540c\u65f6\u653b\u51fb\u4e24\u4eba\uff0c\u52a8\u4f5c\u6beb\u4e0d\u6ede\u788d\uff0c\u7b80\u76f4\u4fbf\u5982\u4f20\u8bf4\u4e2d\u7684\u5206\u5fc3\u4e92\u640f\u4e00\u822c\u3002\u743c\u6069\u5728\u8fdc\u5904\u770b\u5f97\u90fd\u662f\u4e00\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u5b83\u6709\u4e24\u4e2a\u8111\u888b\u561b\u3002</p><p>\u8fd9\u5de8\u9b54\u6709\u4e24\u4e2a\u8111\u888b\uff0c\u5c31\u7b97\u4e0d\u80fd\u667a\u529b\u7ffb\u500d\uff0c\u81f3\u5c11\u89c6\u91ce\u500d\u589e\uff0c\u5bf9\u5468\u56f4\u53cd\u5e94\u66f4\u5feb\uff0c\u5173\u952e\u65f6\u8fd8\u6709\u5206\u5fc3\u4e92\u640f\u7684\u80fd\u529b\uff0c\u53c8\u80fd\u91ca\u653e\u95ea\u7535\uff0c\u53e3\u5410\u70c8\u7130\uff0c\u518d\u52a0\u4e0a\u5929\u8d4b\u602a\u529b\uff0c\u53ea\u6015\u90fd\u80fd\u7b97\u5f97\u4e0a\u8fd9\u5c71\u8109\u4e2d\u7684\u4e00\u9738\u4e86\u3002\u53ea\u662f\u5f88\u53ef\u60dc\uff0c\u5b83\u4eca\u5929\u8fd0\u6c14\u4e0d\u4f73\u3002</p><p>\u6885\u83f2\u65af\u80fd\u53d7\u5c01\u5723\u6b66\u58eb\uff0c\u51ed\u7684\u4e0d\u4ec5\u4ec5\u662f\u4fe1\u4ef0\u8654\u8bda\uff0c\u66f4\u56e0\u4e3a\u5979\u66fe\u8eab\u7ecf\u767e\u6218\uff0c\u4e3a\u6559\u4f1a\u7acb\u4e0b\u5353\u8457\u529f\u52cb\u3002\u5426\u5219\u82e5\u4fe1\u4ef0\u8654\u8bda\u5c31\u662f\u5723\u6b66\u58eb\uff0c\u6559\u4f1a\u7684\u9ad8\u9636\u7267\u5e08\u5c31\u90fd\u662f\u5929\u7136\u7684\u5723\u6b66\u58eb\u4eba\u9009\u4e86\u3002\u65b9\u624d\u8fdb\u653b\u53d7\u632b\uff0c\u53ea\u662f\u8fd9\u5de8\u9b54\u592a\u51fa\u4e4e\u610f\u6599\uff0c\u5b8c\u5168\u8fdd\u80cc\u5df2\u77e5\u5e38\u8bc6\uff0c\u5982\u4eca\u6709\u4e86\u51c6\u5907\uff0c\u81ea\u7136\u518d\u4e0d\u4f1a\u5bb9\u5b83\u9003\u8131\u3002\u5979\u77e5\u9053\u5de8\u9b54\u5929\u751f\u602a\u529b\uff0c\u4e0d\u80fd\u786c\u78b0\uff0c\u89c1\u7c97\u6728\u68d2\u5f53\u5934\u7838\u6765\uff0c\u94f6\u5251\u659c\u659c\u8fce\u4e0a\uff0c\u5c06\u6728\u68d2\u78d5\u5f00\uff0c\u501f\u52bf\u903c\u4e0a\uff0c\u4e00\u5251\u671d\u5de8\u9b54\u80a9\u5934\u5288\u4e0b\uff0c\u6253\u5b9a\u4e3b\u610f\u8981\u5148\u628a\u5b83\u4e00\u53ea\u80f3\u818a\u5378\u4e0b\u6765\u3002</p><p>\u55e4\u5730\u4e00\u58f0\uff0c\u94f6\u5251\u5288\u4e2d\u5de8\u9b54\uff0c\u4f46\u6885\u83f2\u65af\u968f\u5373\u53d1\u89c9\u5230\u4e0d\u5bf9\u52b2\uff0c\u624b\u4e0a\u4f20\u6765\u7684\u89e6\u611f\u597d\u5947\u602a\u3002\u5979\u8fd9\u628a\u94f6\u5251\u201c\u7737\u604b\u201d\u662f\u795e\u7947\u4eb2\u8d50\uff0c\u950b\u9510\u65e0\u6bd4\uff0c\u5f81\u6218\u65a9\u6740\u65e0\u5f80\u4e0d\u5229\uff0c\u83ab\u8bf4\u8840\u8089\u4e4b\u8eaf\uff0c\u5c31\u7b97\u662f\u94c1\u4e5f\u80fd\u780d\u65ad\u4e86\u3002\u600e\u4e48\u8fd9\u6b21\u780d\u4e2d\u5de8\u9b54\uff0c\u94f6\u5251\u5c45\u7136\u53ea\u6ca1\u5165\u534a\u5bf8\uff0c\u63a5\u7740\u5c31\u518d\u4e5f\u780d\u4e0d\u4e0b\u53bb\u4e86\uff0c\u96be\u4e0d\u6210\u8fd9\u5de8\u9b54\u7684\u9aa8\u5934\u662f\u7cbe\u91d1\u505a\u7684\u4e0d\u6210\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u53e6\u5916\u4e00\u4fa7\u6b66\u50e7\u811a\u6b65\u8fde\u9519\uff0c\u7075\u5de7\u5730\u907f\u5f00\u4e86\u5de8\u9b54\u7684\u5229\u722a\uff0c\u4e09\u8f6c\u4e24\u8f6c\u7ed5\u5230\u5de8\u9b54\u8eab\u540e\uff0c\u6325\u62f3\u8fde\u51fb\uff0c\u7830\u7830\u7830\u7830\u7830\uff0c\u4e00\u53e3\u6c14\u6325\u51fa\u4e94\u62f3\uff0c\u91cd\u91cd\u7838\u5728\u5de8\u9b54\u4f5d\u507b\u7740\u7684\u540e\u80cc\u4e0a\u3002</p><p>\u743c\u6069\u7b49\u4eba\u90fd\u662f\u89c1\u8bc6\u8fc7\u6b66\u50e7\u94c1\u62f3\u5a01\u529b\u7684\uff0c\u80fd\u4e00\u62f3\u7838\u7a7f\u5899\u58c1\uff0c\u7b80\u76f4\u6bd4\u7cbe\u91d1\u6218\u9524\u8fd8\u51f6\u608d\u3002\u4e00\u53e3\u6c14\u8f70\u51fa\u4e94\u62f3\uff0c\u7740\u7740\u51fb\u4e2d\uff0c\u83ab\u8bf4\u662f\u5de8\u9b54\uff0c\u5c31\u7b97\u662f\u5de8\u4eba\u4f30\u8ba1\u4e5f\u8981\u88ab\u6253\u6210\u762b\u75ea\u4e86\u3002</p><p>\u4f46\u6b66\u50e7\u548c\u6885\u83f2\u65af\u7684\u53cd\u5e94\u4e00\u6837\uff0c\u62f3\u5934\u8f70\u51fa\uff0c\u987f\u65f6\u5c31\u89c9\u5f97\u4e0d\u5bf9\u52b2\u3002\u62f3\u52b2\u5230\u5904\uff0c\u76ae\u8089\u584c\u9677\uff0c\u4f46\u8fd9\u5de8\u9b54\u7684\u9aa8\u9abc\u5374\u4eff\u4f5b\u575a\u786c\u5f97\u51fa\u5947\uff0c\u6b66\u50e7\u7167\u7740\u80cc\u4e0a\u8fde\u8f70\u4e94\u62f3\u5c45\u7136\u90fd\u672a\u80fd\u628a\u810a\u690e\u7838\u65ad\uff0c\u53cd\u800c\u9707\u7684\u81ea\u5df1\u624b\u81c2\u9690\u9690\u9178\u9ebb\u3002</p><p>\u6885\u83f2\u65af\u64a4\u5251\u9000\u5f00\uff0c\u89c1\u6b66\u50e7\u4e5f\u6709\u4e9b\u60ca\u8be7\u5730\u8dc3\u540e\u4e24\u6b65\uff0c\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u90fd\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u5de8\u9b54\u53ea\u662f\u4ee5\u518d\u751f\u80fd\u529b\u5f3a\u608d\u800c\u8457\u79f0\uff0c\u5230\u5e95\u8fd8\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u5e76\u4e0d\u66fe\u542c\u8bf4\u94a2\u7b4b\u94c1\u9aa8\u554a\u3002</p><p>\u5de8\u9b54\u8fde\u8fde\u653b\u51fb\u4e0d\u4e2d\uff0c\u53cd\u800c\u88ab\u5288\u4e86\u4e00\u5251\uff0c\u53c8\u88ab\u8f70\u4e86\u51e0\u62f3\uff0c\u867d\u7136\u4f3c\u4e4e\u4e0d\u66fe\u53d7\u4f24\uff0c\u5374\u66f4\u52a0\u6fc0\u8d77\u6012\u6c14\uff0c\u4e24\u4e2a\u8111\u888b\u5de8\u543c\u8fde\u8fde\uff0c\u6728\u68d2\u6a2a\u626b\u6325\u821e\uff0c\u6ca1\u5934\u6ca1\u8111\u5730\u671d\u4e24\u4eba\u7838\u4e0b\u6765\u3002\u6885\u83f2\u65af\u548c\u6b66\u50e7\u5404\u81ea\u95ea\u907f\uff0c\u4e00\u65f6\u5fc3\u4e2d\u90fd\u6709\u4e9b\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u5bf9\u4ed8\u8fd9\u53cc\u5934\u5de8\u9b54\u3002</p><p>\u743c\u6069\u7b49\u56db\u4eba\u539f\u672c\u5728\u8fdc\u5904\u8896\u624b\u65c1\u89c2\uff0c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u6ca1\u60f3\u5230\u6885\u83f2\u65af\u548c\u9686\u5947\u4e24\u4eba\u5c45\u7136\u4e24\u4e2a\u7167\u9762\u90fd\u8fd8\u6ca1\u628a\u8fd9\u5de8\u9b54\u6253\u5012\uff0c\u4e0d\u7981\u4e5f\u90fd\u60ca\u8be7\u8d77\u6765\u3002\u590f\u62c9\u975e\u62ac\u8d77\u5341\u5b57\u5f13\uff0c\u8fde\u53d1\u4e24\u652f\u706b\u7130\u77e2\uff0c\u51c6\u786e\u5730\u5c04\u4e2d\u5de8\u9b54\uff0c\u53ea\u662f\u5e76\u6ca1\u8d77\u591a\u5927\u6548\u679c\uff0c\u8fd9\u53cc\u5934\u5de8\u9b54\u679c\u7136\u4e0d\u6015\u706b\u7130\u3002</p><p>\u8d1d\u7eb3\u7d22\u5df2\u7ecf\u5306\u5306\u5fd9\u5fd9\u5c55\u5f00\u5377\u8f74\uff0c\u51c6\u5907\u5ff5\u8bf5\u65bd\u6cd5\uff0c\u5374\u88ab\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u62e6\u4f4f\u4e86\uff0c\u201c\u7b49\u7b49\u3002\u201d\u4ed6\u6c89\u58f0\u8bf4\uff0c\u76ef\u7740\u6b63\u5728\u4e89\u6597\u7684\u4eba\u5f71\uff0c\u867d\u7136\u590f\u62c9\u975e\u7684\u706b\u7130\u77e2\u5e76\u6ca1\u6709\u7ed9\u5de8\u9b54\u9020\u6210\u592a\u5927\u5f71\u54cd\uff0c\u4f46\u5374\u70e7\u5f00\u4e86\u5b83\u7684\u76ae\u8089\uff0c\u9690\u7ea6\u9732\u51fa\u91cc\u9762\u7684\u68ee\u68ee\u767d\u9aa8\u3002\u6c83\u91d1\u7267\u5e08\u5fae\u5fae\u76b1\u8d77\u7709\u5934\uff0c\u4ed6\u4f3c\u4e4e\u53d1\u89c9\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\u52b2\uff0c\u4f46\u8ddd\u79bb\u592a\u8fdc\u4e86\uff0c\u770b\u4e0d\u6e05\u695a\u3002</p><p>\u4ed6\u4f38\u624b\u4ece\u8170\u95f4\u7684\u91d1\u8272\u5c0f\u53e3\u888b\u91cc\u638f\u51fa\u4e00\u679a\u91d1\u5e01\uff0c\u629b\u8d77\u5728\u7a7a\u4e2d\uff0c\u5ff5\u4e86\u4e00\u4e2a\u5b57\u7b26\uff0c\u644a\u5f00\u624b\u638c\u63a5\u4f4f\u3002\u91d1\u5e01\u5728\u4ed6\u7684\u638c\u5fc3\u6025\u901f\u65cb\u8f6c\uff0c\u6cdb\u51fa\u70b9\u70b9\u788e\u5149\uff0c\u6700\u7ec8\u5e7b\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5f71\u50cf\u6765\u3002</p><p>\u4e00\u9897\u6a21\u7cca\u4e0d\u6e05\u7684\u9ab7\u9ac5\u5934\u3002</p></article>"}}}]);
