(window.webpackJsonp=window.webpackJsonp||[]).push([[1292],{1315:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e8c\u8282 \u96f7\u9e23\u4e4b\u5b50",content:"<article><h2>\u7b2c\u56db\u5341\u4e8c\u8282 \u96f7\u9e23\u4e4b\u5b50</h2><p>\u743c\u6069\u770b\u7740\u8001\u77ee\u4eba\uff0c\u786e\u5b9a\u5bf9\u65b9\u6ca1\u6709\u5728\u8ddf\u81ea\u5df1\u5f00\u73a9\u7b11\uff0c\u4e5f\u6ca1\u6709\u53d1\u70e7\u8bf4\u80e1\u8bdd\u7684\u8ff9\u8c61\u3002\u663e\u7136\uff0c\u5bf9\u65b9\u662f\u5f88\u6e05\u695a\u81ea\u5df1\u5728\u8bf4\u4ec0\u4e48\u7684\uff0c\u4e5f\u5f88\u6e05\u695a\u5deb\u5e08\u888d\u5230\u5e95\u662f\u4ec0\u4e48\u4e1c\u897f\u2014\u2014\u4f46\u4e3a\u4ec0\u4e48\u795e\u6bbf\u91cc\u4f1a\u6709\u5356\u5deb\u5e08\u888d\u7684\u5730\u65b9\uff1f\u96be\u9053\u8bf4\u8fd9\u963f\u5fb7\u5df4\u57ce\u571f\u5730\u8d44\u6e90\u592a\u8fc7\u7d27\u5f20\uff0c\u5deb\u5e08\u548c\u7267\u5e08\u7d22\u6027\u90fd\u4f4f\u5728\u4e00\u8d77\u8054\u5408\u529e\u516c\u4e86\u4e48\u2026\u2026</p><p>\u8981\u8bf4\u571f\u5730\u8d44\u6e90\u7d27\u5f20\uff0c\u9634\u9b42\u57ce\u7684\u60c5\u5f62\u7edd\u5bf9\u6bd4\u963f\u5fb7\u5df4\u57ce\u8981\u4e25\u5cfb\u5f97\u591a\u3002\u5f53\u5e74\u5728\u5e7d\u5f71\u754c\uff0c\u5c31\u90a3\u4e48\u4e00\u5ea7\u6f02\u5728\u5929\u4e0a\u7684\u6d6e\u7a7a\u57ce\uff0c\u56e0\u4e3a\u73af\u5883\u592a\u9669\u6076\uff0c\u53c8\u4e0d\u9002\u5408\u53bb\u522b\u7684\u5730\u65b9\u6269\u5f20\u6b96\u6c11\uff0c\u5927\u5bb6\u5168\u90fd\u6324\u5728\u4e00\u5ea7\u57ce\u5e02\u91cc\u3002\u4f46\u5c31\u7b97\u5982\u6b64\uff0c\u5deb\u5e08\u548c\u7267\u5e08\u7684\u5730\u76d8\u4e5f\u90fd\u4e25\u683c\u4fdd\u6301\u8ddd\u79bb\uff0c\u51b3\u4e0d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u8352\u8c2c\u7684\u60c5\u5f62\u3002</p><p>\u96be\u9053\u8bf4\uff0c\u5deb\u5e08\u548c\u7267\u5e08\u4e92\u76f8\u770b\u4e0d\u5bf9\u773c\u2014\u2014\u8fd9\u4e0d\u662f\u5e38\u8bc6\u4e48\uff1f\u6216\u8005\u8bf4\u8fd9\u53ea\u662f\u9634\u9b42\u57ce\u91cc\u7684\u5e38\u8bc6\uff0c\u5728\u5916\u9762\u7684\u4e16\u754c\u5176\u5b9e\u6ca1\u8fd9\u56de\u4e8b\uff1f</p><p>\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8fdb\u53bb\u89c1\u8bc6\u89c1\u8bc6\u3002\u743c\u6069\u8ddf\u7740\u8001\u77ee\u4eba\uff0c\u4e00\u8def\u4e0a\u4e86\u53f0\u9636\uff0c\u8d70\u8fdb\u795e\u6bbf\u6b63\u95e8\u3002\u4ed6\u518d\u6b21\u770b\u89c1\u4e86\u5f88\u591a\u77ee\u4eba\u795e\u7947\u7684\u5851\u50cf\uff0c\u8fd9\u4ee4\u4ed6\u4e5f\u9887\u6709\u4e9b\u597d\u5947\u3002\u56e0\u4e3a\u5728\u9634\u9b42\u57ce\u4e2d\uff0c\u838e\u5c14\u5973\u795e\u5c31\u662f\u552f\u4e00\u7684\u795e\uff0c\u795e\u6bbf\u4e2d\u552f\u6709\u5979\u7684\u795e\u50cf\uff0c\u518d\u65e0\u5176\u4ed6\u3002\u6309\u7167\u743c\u6069\u4ee5\u524d\u5728\u4e66\u4e0a\u6240\u8bfb\uff0c\u4eba\u7c7b\u795e\u7947\u7684\u795e\u6bbf\u4e2d\uff0c\u4e5f\u90fd\u662f\u53ea\u4f1a\u5355\u4e00\u5730\u4f9b\u5949\u67d0\u4e00\u4f4d\u795e\u7684\uff0c\u6781\u5c11\u51fa\u73b0\u4e24\u4f4d\u4ee5\u4e0a\u7684\u795e\u7947\u540c\u65f6\u51fa\u73b0\u5728\u67d0\u4e2a\u795e\u6bbf\u7684\u60c5\u5f62\u3002\u4fbf\u5982\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u548c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\uff0c\u4ed6\u4eec\u7684\u6559\u4f1a\u7d20\u6765\u5173\u7cfb\u878d\u6d3d\uff0c\u8fd9\u6b21\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\u90fd\u662f\u5408\u4f5c\u524d\u6765\u3002\u4f46\u4f60\u8d70\u8fdb\u4efb\u4f55\u4e00\u5ea7\u63d0\u5c14\u795e\u6bbf\u91cc\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd\u770b\u89c1\u5170\u68ee\u5fb7\u5c14\u7684\u795e\u50cf\uff1b\u53cd\u4e4b\u4ea6\u7136\u3002</p><p>\u4eba\u53ea\u80fd\u4fe1\u4ef0\u4e00\u4f4d\u795e\u7947\u2014\u2014\u58f0\u79f0\u540c\u65f6\u4fe1\u4ef0\u591a\u4f4d\u795e\u7947\uff0c\u90a3\u538b\u6839\u5c31\u4e0d\u80fd\u771f\u7b97\u662f\u4fe1\u4ef0\uff0c\u53ea\u80fd\u7b97\u662f\u4f2a\u4fe1\u5f92\u3002\u4e00\u4f4d\u63d0\u5c14\u7684\u4fe1\u5f92\uff0c\u4f8b\u5982\u6885\u83f2\u65af\uff0c\u7eb5\u7136\u5979\u5bf9\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u7684\u6559\u4e49\u4e5f\u9887\u591a\u8d5e\u540c\uff0c\u548c\u5170\u68ee\u5fb7\u5c14\u7684\u7267\u5e08\u5173\u7cfb\u53cb\u5584\uff0c\u5979\u7ec8\u7a76\u4e5f\u4e0d\u53ef\u80fd\u8dd1\u53bb\u62dc\u5170\u68ee\u5fb7\u5c14\uff0c\u8fd9\u662f\u539f\u5219\u95ee\u9898\u3002</p><p>\u6240\u4ee5\u5f53\u743c\u6069\u770b\u89c1\u8fd9\u4e48\u591a\u795e\u7947\u7684\u5851\u50cf\u90fd\u6324\u5728\u4e00\u8d77\uff0c\u4eff\u4f5b\u662f\u4e00\u5927\u5bb6\u5b50\u4f3c\u7684\uff0c\u7740\u5b9e\u6709\u4e9b\u597d\u5947\u3002\u800c\u5f53\u4ed6\u770b\u5230\u8001\u77ee\u4eba\u5148\u62dc\u4e86\u4e00\u4f4d\u795e\u7947\uff0c\u63a5\u7740\u518d\u62dc\u53e6\u5916\u4e00\u4f4d\u795e\u7947\u65f6\uff0c\u5c31\u66f4\u662f\u83ab\u540d\u60ca\u8be7\u4e86\u3002</p><p>\u56e0\u4e3a\u5bf9\u65b9\u6b63\u5728\u8654\u8bda\u793c\u62dc\uff0c\u743c\u6069\u4e0d\u65b9\u4fbf\u6253\u6270\uff0c\u53ea\u597d\u81ea\u5df1\u89c2\u5bdf\u3002\u8001\u77ee\u4eba\u5148\u62dc\u7684\u4e00\u4f4d\u795e\u7947\uff0c\u4f4d\u5c45\u4f17\u795e\u50cf\u7684\u6b63\u4e2d\uff0c\u5c31\u662f\u5916\u9762\u5e7f\u573a\u4e0a\u9886\u5934\u7684\u90a3\u4e2a\uff0c\u957f\u987b\u957f\u53d1\uff0c\u5168\u8eab\u94e0\u7532\uff0c\u80a9\u625b\u6218\u9524\uff0c\u5dcd\u7136\u77d7\u7acb\u3002\u867d\u7136\u5f62\u8c61\u662f\u4e2a\u77ee\u4eba\uff0c\u4f46\u5728\u5851\u50cf\u8005\u7684\u5de7\u5999\u5b89\u6392\u5413\uff0c\u5374\u81ea\u6709\u4e00\u79cd\u9ad8\u5927\u5a01\u731b\u7684\u51db\u51db\u6c14\u6d3e\uff0c\u4e0d\u5f97\u4e0d\u4ee4\u4eba\u8d5e\u53f9\u77ee\u4eba\u679c\u7136\u90fd\u662f\u5929\u751f\u7684\u5de5\u5320\u5927\u5e08\u3002\u8fd9\u4f4d\u795e\u7947\u7684\u795e\u50cf\u5e95\u5ea7\u4e0a\uff0c\u523b\u7740\u4e00\u4e2a\u5fbd\u8bb0\uff0c\u662f\u4e00\u5bf9\u5de8\u5927\u7684\u94c1\u9524\u94c1\u7827\u649e\u51fb\u5728\u4e00\u8d77\u3002</p><p>\u8fd9\u4e2a\u5fbd\u8bb0\u743c\u6069\u521a\u624d\u5728\u5916\u9762\u89c1\u8fc7\uff0c\u5c31\u60ac\u6302\u5728\u795e\u6bbf\u6b63\u95e8\u7684\u9876\u4e0a\u3002\u7531\u6b64\u770b\u6765\uff0c\u8fd9\u4f4d\u77ee\u4eba\u795e\u7947\u5e94\u8be5\u5c31\u662f\u8fd9\u5ea7\u795e\u6bbf\u7684\u4e3b\u8981\u5c0a\u5949\u5bf9\u8c61\u4e86\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u53eb\u4ec0\u4e48\u540d\u5b57\u3002</p><p>\u518d\u770b\u8001\u77ee\u4eba\u6b63\u5728\u793c\u62dc\u7684\u795e\u50cf\uff0c\u540c\u6837\u4e5f\u662f\u4e00\u4f4d\u77ee\u4eba\uff0c\u4e5f\u662f\u4e00\u526f\u6218\u58eb\u6a21\u6837\uff0c\u5de6\u624b\u6301\u76fe\u53f3\u624b\u6301\u9524\uff0c\u6a2a\u7709\u6012\u76ee\uff0c\u4e00\u526f\u51f6\u76f8\u3002\u795e\u50cf\u5e95\u5ea7\u4e0a\uff0c\u4e5f\u523b\u7740\u4ed6\u7684\u5723\u5fbd\uff0c\u5c31\u662f\u4e00\u9762\u7070\u8272\u7684\u76fe\u724c\uff0c\u4e0a\u9762\u6709\u4e00\u652f\u65ad\u88c2\u7684\u7bad\u77e2\uff0c\u548c\u8001\u77ee\u4eba\u677f\u7532\u80f8\u53e3\u7684\u5fbd\u8bb0\u4e00\u6a21\u4e00\u6837\u3002</p><p>\u663e\u7136\uff0c\u8fd9\u4f4d\u5c31\u662f\u8001\u77ee\u4eba\u6240\u4fe1\u4ef0\u7684\u795e\u7947\u4e86\u3002</p><p>\u6b63\u770b\u89c1\uff0c\u53c8\u6709\u51e0\u4e2a\u77ee\u4eba\u8fdb\u6765\u795e\u6bbf\u7948\u7977\uff0c\u4ed6\u4eec\u540c\u6837\u4e5f\u90fd\u662f\u5148\u53bb\u6b63\u4e2d\u62dc\u90a3\u4f4d\u957f\u80e1\u5b50\u77ee\u4eba\u795e\uff0c\u518d\u5206\u522b\u53bb\u62dc\u5404\u81ea\u4fe1\u4ef0\u7684\u795e\u7947\u3002\u743c\u6069\u7ea6\u6478\u6709\u4e9b\u660e\u767d\u4e86\uff0c\u5f88\u663e\u7136\uff0c\u77ee\u4eba\u867d\u7136\u4e5f\u662f\u5404\u81ea\u4fe1\u4ef0\u67d0\u4e00\u4f4d\u795e\u7947\uff0c\u4f46\u5374\u8fd8\u989d\u5916\u6709\u4e00\u4f4d\u5171\u901a\u7684\u5d07\u62dc\u5bf9\u8c61\u3002</p><p>\u201c\u8fd9\u4f4d\u795e\u7947\u662f\u2026\u2026\u201d\u7b49\u8001\u77ee\u4eba\u62dc\u5b8c\u4e86\uff0c\u743c\u6069\u6307\u7740\u6b63\u4e2d\u7684\u90a3\u4e2a\u795e\u50cf\uff0c\u865a\u5fc3\u8bf7\u6559\u3002</p><p>\u8001\u77ee\u4eba\u7684\u795e\u60c5\u987f\u65f6\u8083\u7a46\u8d77\u6765\uff0c\u4ed6\u5148\u7528\u77ee\u4eba\u8bed\u6e05\u695a\u5730\u8bf4\u4e86\u4e00\u904d\uff0c\u867d\u7136\u660e\u77e5\u9053\u743c\u6069\u538b\u6839\u542c\u4e0d\u61c2\uff0c\u7136\u540e\u518d\u7528\u901a\u7528\u8bed\u7ffb\u8bd1\u8bf4\uff1a\u201c\u8fd9\u4f4d\u662f\u953b\u9b42\u8005\u3001\u77ee\u4eba\u4e4b\u7236\u3001\u4e07\u6709\u4e4b\u7236\uff0c\u5728\u5929\u4e0a\u7684\u6301\u76fe\u5144\u5f1f\u7684\u9886\u8896\uff0c\u6700\u53ef\u5c0a\u656c\u7684\u83ab\u62c9\u4e01\u3002\u201d</p><p>\u81ea\u52a8\u5ffd\u7565\u6389\u524d\u9762\u90a3\u4e00\u5806\u4fee\u9970\u8bcd\uff0c\u743c\u6069\u6e05\u695a\u5730\u542c\u5230\u4e86\u6700\u540e\u7684\u540d\u5b57\uff0c\u201c\u83ab\u62c9\u4e01\u2026\u2026\u201d\u4ed6\u56de\u5473\u7740\uff0c\u60f3\u4e86\u8d77\u6765\uff0c\u90a3\u4e2a\u7537\u77ee\u4eba\u8001\u677f\u7ed9\u81ea\u5df1\u6307\u8def\u7684\u65f6\u5019\uff0c\u4e0d\u5c31\u662f\u8bf4\u5230\u201c\u83ab\u62c9\u4e01\u7194\u7089\u4e4b\u6bbf\u201d\u6765\u4e48\u3002</p><p>\u81ea\u5df1\u5f53\u65f6\u542c\u5f97\u4e0d\u751a\u6e05\u695a\uff0c\u52a0\u4e0a\u4e5f\u538b\u6839\u4e0d\u77e5\u9053\u201c\u83ab\u62c9\u4e01\u201d\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff0c\u662f\u540d\u8bcd\u8fd8\u662f\u52a8\u8bcd\u6291\u6216\u5f62\u5bb9\u8bcd\u90fd\u4e0d\u77e5\u9053\uff0c\u4e5f\u61d2\u5f97\u591a\u60f3\u3002\u5982\u4eca\u770b\u5230\u8fd9\u5e45\u60c5\u5f62\uff0c\u518d\u542c\u8001\u77ee\u4eba\u4e00\u8bf4\uff0c\u9661\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u77ee\u4eba\u91cc\u6709\u4e2a\u975e\u5e38\u8457\u540d\u7684\u795e\u7947\uff0c\u597d\u50cf\u5c31\u662f\u53eb\u83ab\u62c9\u4e01\u2026\u2026</p><p>\u518d\u95ee\u8001\u77ee\u4eba\u62dc\u7684\u53e6\u5916\u4e00\u4f4d\u795e\u7947\uff0c\u8bf4\u662f\u201c\u6d41\u653e\u8005\u3001\u7070\u77ee\u4eba\u5b88\u62a4\u8005\u3001\u5de5\u827a\u5927\u5e08\u62c9\u675c\u683c\u201d\uff0c\u540c\u65f6\u8fd8\u662f\u77ee\u4eba\u65cf\u7684\u9b54\u6cd5\u4e4b\u795e\u3002\u8fd9\u8ba9\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u4ed6\u4ece\u6ca1\u60f3\u50cf\u8fc7\uff0c\u4e16\u754c\u4e0a\u6709\u5de6\u624b\u76fe\u53f3\u624b\u6218\u9524\u7684\u5f6a\u608d\u9b54\u6cd5\u795e\uff0c\u4ed6\u4ee5\u4e3a\u90fd\u5e94\u8be5\u662f\u957f\u888d\u6cd5\u6756\uff0c\u6e29\u6587\u5c14\u96c5\u7684\u3002</p><p>\u8001\u77ee\u4eba\u62dc\u7684\u662f\u77ee\u4eba\u65cf\u7684\u9b54\u6cd5\u795e\uff0c\u8054\u60f3\u8d77\u6b64\u524d\u7684\u5bf9\u8bdd\u4e3e\u52a8\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u6709\u4e9b\u6000\u7591\u8d77\u6765\uff0c\u4ed6\u5fc3\u4e2d\u5347\u8d77\u4e00\u4e2a\u5ff5\u5934\uff0c\u4f46\u8fd8\u4e0d\u6562\u80af\u5b9a\uff0c\u6bd5\u7adf\u8fd9\u592a\u504f\u79bb\u4ed6\u7684\u8ba4\u77e5\u8303\u56f4\u4e86\u3002\u201c\u5443\uff0c\u90a3\u4e48\uff0c\u6211\u5192\u6627\u8bf7\u6559\u4e00\u4e0b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u60a8\u662f\u4ed6\u7684\u7267\u5e08\uff1f\u201d</p><p>\u201c\u7267\u5e08\uff1f\u201d\u8001\u77ee\u4eba\u8be7\u5f02\u5730\u770b\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u6211\u662f\u4e2a\u5deb\u5e08\u554a\uff0c\u4f60\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u5947\u602a\u7684\u60f3\u6cd5\uff1f\u201d</p><p>\u203b\u203b\u203b</p><p>\u4ed6\u5988\u7684\uff0c\u743c\u6069\u7b80\u76f4\u6709\u70b9\u60f3\u9a82\u4eba\u3002\u4ed6\u53d1\u73b0\u4eca\u5929\u649e\u5230\u7684\u4e8b\u60c5\uff0c\u65e0\u4e00\u4e0d\u5728\u6311\u6218\u4ed6\u7684\u5e38\u8bc6\u3002\u795e\u6bbf\u91cc\u5356\u5deb\u5e08\u888d\u3001\u5341\u4f59\u4f4d\u795e\u7947\u6324\u5728\u4e00\u8d77\u3001\u77ee\u4eba\u540c\u65f6\u5d07\u62dc\u4e24\u4f4d\u795e\u7947\u2014\u2014\u5982\u4eca\u9762\u524d\u8fd9\u4e2a\u5168\u8eab\u677f\u7532\u91d1\u5149\u95ea\u95ea\u5c41\u80a1\u4e0a\u522b\u7740\u6218\u9524\u7684\u5bb6\u4f19\uff0c\u5c45\u7136\u8fd8\u8ddf\u81ea\u5df1\u8bf4\u4ed6\u662f\u4e2a\u5deb\u5e08\u3002</p><p>\u4e16\u754c\u4e0a\u6709\u8fd9\u4e48\u5f6a\u608d\u7684\u5deb\u5e08\u4e48\uff1f</p><p>\u6ca1\u6709\u54ea\u6761\u6cd5\u5f8b\u7981\u6b62\u5deb\u5e08\u7a7f\u7532\u62ff\u6218\u9524\uff0c\u4f46\u4e8b\u5b9e\u5c31\u662f\u4e0d\u4f1a\u6709\u4eba\u8fd9\u4e48\u5e72\u3002\u5deb\u5e08\u4e2d\u4e5f\u6709\u8eab\u5f3a\u4f53\u58ee\u7684\uff0c\u96be\u9053\u771f\u5c31\u627f\u53d7\u4e0d\u4f4f\u94e0\u7532\u7684\u5206\u91cf\uff1f\u5176\u5b9e\u662f\u56e0\u4e3a\u4e00\u65e6\u7a7f\u4e86\u94e0\u7532\uff0c\u8eab\u4f53\u5173\u8282\u90fd\u4e0d\u7075\u654f\uff0c\u5deb\u5e08\u8fd8\u600e\u4e48\u65bd\u6cd5\u5462\u3002</p><p>\u81f3\u4e8e\u5c41\u80a1\u540e\u9762\u522b\u4e2a\u6218\u9524\uff0c\u90a3\u5c31\u66f4\u641e\u7b11\u4e86\u3002\u5deb\u5e08\u6bd5\u7adf\u953b\u70bc\u5c11\uff0c\u4e0d\u662f\u4e13\u4e1a\u7684\u6218\u58eb\uff0c\u5c31\u7b97\u771f\u8981\u52a8\u624b\uff0c\u4e5f\u666e\u904d\u90fd\u9752\u7750\u5315\u9996\u8fd9\u79cd\u77ed\u5c0f\u8f7b\u8584\u7684\u6b66\u5668\uff0c\u6218\u9524\u53c8\u5927\u53c8\u91cd\uff0c\u6302\u5728\u8eab\u4e0a\u662f\u7d2f\u8d58\uff0c\u62ff\u5728\u624b\u91cc\u662f\u8d1f\u62c5\uff0c\u5f04\u5f97\u4e0d\u597d\u53cd\u800c\u4f24\u5230\u81ea\u5df1\u3002</p><p>\u743c\u6069\u5728\u9634\u9b42\u57ce\u91cc\u89c1\u8fc7\u65e0\u6570\u7684\u5deb\u5e08\uff0c\u65e0\u4e00\u4f8b\u5916\u4e0d\u662f\u7a7f\u957f\u888d\u51fa\u95e8\u3002\u4ed6\u5728\u4e66\u4e0a\u4e5f\u66fe\u7ecf\u8bfb\u5230\u8fc7\uff0c\u8bf4\u7cbe\u7075\u65cf\u6709\u4e00\u79cd\u79d8\u800c\u4e0d\u5ba3\u7684\u6280\u827a\uff0c\u80fd\u628a\u67d0\u4e9b\u8f7b\u8584\u7684\u76ae\u7532\u3001\u94fe\u7532\u886b\u4e4b\u7c7b\uff0c\u5236\u4f5c\u5f97\u4eff\u4f5b\u957f\u888d\u4e00\u6837\u8f7b\u67d4\uff0c\u4e0d\u4f1a\u59a8\u788d\u5deb\u5e08\u65bd\u6cd5\u3002\u4f46\u8fd9\u4e2a\u8001\u77ee\u4eba\u7a7f\u7684\u53ef\u662f\u677f\u7532\uff0c\u5728\u76d4\u7532\u4e2d\u5df2\u7ecf\u5c5e\u4e8e\u91cd\u5206\u91cf\u7684\u8d27\u8272\u4e86\uff0c\u8fdc\u975e\u76ae\u7532\u4e4b\u6d41\u53ef\u6bd4\u3002</p><p>\u8001\u77ee\u4eba\u7a7f\u7740\u8fd9\u4e48\u4e00\u526f\u677f\u7532\uff0c\u5c31\u4e0d\u6015\u65bd\u6cd5\u8fde\u8fde\u5931\u8d25\uff1f\u5bf9\u4e86\uff0c\u4ed6\u7684\u677f\u7532\u4e0a\u8fd8\u7f00\u7740\u8fd9\u4e48\u591a\u91d1\u5c5e\u788e\u7247\uff0c\u8d70\u8d77\u8def\u6765\u53ee\u53ee\u5f53\u5f53\uff0c\u662f\u505a\u4ec0\u4e48\u7528\u7684\uff1f\u88c5\u9970\u4e48\uff1f</p><p>\u4ed6\u5b9e\u5728\u5f88\u6000\u7591\u8001\u77ee\u4eba\u662f\u4e0d\u662f\u5728\u8bf4\u80e1\u8bdd\uff0c\u6216\u8005\u81ea\u5df1\u542c\u9519\u4e86\uff0c\u51e0\u4e4e\u8981\u518d\u8ffd\u95ee\u4e00\u904d\u3002\u4f46\u60f3\u60f3\u8fde\u77ee\u4eba\u7684\u9b54\u6cd5\u795e\u62c9\u675c\u683c\u90fd\u662f\u5de6\u624b\u76fe\u53f3\u624b\u9524\u7684\u6a21\u6837\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u8010\u4e86\u4e0b\u6765\u3002\u201c\u5bf9\u4e0d\u8d77\uff0c\u5bf9\u4e0d\u8d77\uff0c\u201d\u4ed6\u8fde\u58f0\u9053\u6b49\u7740\uff0c\u201c\u6211\uff0c\u5443\uff0c\u7b2c\u4e00\u6b21\u6765\u8fd9\u91cc\uff0c\u8fd8\u4e0d\u592a\u6e05\u695a\u2026\u2026\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\uff0c\u201d\u8001\u77ee\u4eba\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u201c\u6211\u5927\u6982\u80fd\u660e\u767d\uff0c\u4e0d\u540c\u7684\u65cf\u7fa4\uff0c\u603b\u662f\u6709\u4e0d\u540c\u7684\u4e60\u4fd7\u7684\uff0c\u53ef\u80fd\u6211\u4eec\u8fd9\u91cc\u7684\u60c5\u5f62\uff0c\u548c\u4f60\u4ee5\u524d\u6240\u4e60\u60ef\u770b\u5230\u7684\u6709\u6240\u4e0d\u540c\uff0c\u662f\u5427\u3002\u201d</p><p>\u201c\u786e\u5b9e\u3002\u201d\u743c\u6069\u8bf4\uff0c\u5176\u5b9e\u54ea\u91cc\u662f\u6709\u6240\u4e0d\u540c\uff0c\u5206\u660e\u662f\u622a\u7136\u76f8\u53cd\u3002</p><p>\u201c\u521a\u624d\u5fd8\u4e86\u81ea\u6211\u4ecb\u7ecd\uff0c\u201d\u8001\u77ee\u4eba\u8bf4\uff0c\u4e00\u8fb9\u5e26\u7740\u743c\u6069\u7a7f\u8fc7\u8fd9\u4e9b\u795e\u50cf\uff0c\u62d0\u8fdb\u4e00\u6761\u957f\u5eca\uff0c\u201c\u6211\u53eb\u827e\u4f2f\u5854\xb7\u4e39\u5409\u5c14\uff0c\u662f\u8fd9\u91cc\u7684\u9996\u5e2d\u5deb\u5e08\uff0c\u4f60\u5462\uff1f\u201d</p><p>\u201c\u9996\u5e2d\u5deb\u5e08\uff1f\u201d\u743c\u6069\u53c8\u88ab\u9707\u64bc\u4e86\u4e00\u4e0b\uff0c\u8fd9\u4e2a\u8001\u77ee\u4eba\u867d\u8bf4\u6709\u4e9b\u53e4\u602a\uff0c\u5374\u4e5f\u7b97\u5e73\u6613\u548c\u853c\uff0c\u4e3b\u52a8\u5728\u95e8\u53e3\u62db\u547c\u4ed6\uff0c\u5e26\u4ed6\u8fdb\u6765\uff0c\u743c\u6069\u8fd8\u4ee5\u4e3a\u662f\u4e2a\u666e\u901a\u7684\u5deb\u5e08\u751a\u81f3\u662f\u4e2a\u62db\u63fd\u987e\u5ba2\u7684\uff0c\u6ca1\u60f3\u5230\u5c45\u7136\u662f\u201c\u9996\u5e2d\u5deb\u5e08\u201d\u3002</p><p>\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\uff0c\u201d\u4ed6\u8c28\u614e\u5730\u56de\u7b54\uff0c\u201c\u6765\u81ea\u2026\u2026\u5443\uff0c\u4e00\u4e2a\u5f88\u504f\u50fb\u7684\u5c0f\u9547\u3002\u201d</p><p>\u8001\u77ee\u4eba\u4e39\u5409\u5c14\u70b9\u70b9\u5934\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u5728\u610f\uff0c\u867d\u7136\u8fd9\u8bdd\u542c\u8d77\u6765\u660e\u663e\u6709\u4e9b\u6c34\u5206\u3002\u201c\u4e0b\u5348\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u60f3\u4e70\u5deb\u5e08\u888d\u3002\u4f60\u662f\u4e2a\u5deb\u5e08\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u7684\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u4eec\u662f\u540c\u884c\uff0c\u201d\u4e39\u5409\u5c14\u8bf4\uff0c\u6b64\u65f6\u4ed6\u4eec\u5df2\u7ecf\u7a7f\u8fc7\u957f\u5eca\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u62f1\u95e8\uff0c\u7136\u540e\u5c31\u770b\u89c1\u4e86\u56db\u4e94\u4e2a\u540c\u6837\u7a7f\u7740\u7f00\u91d1\u5c5e\u788e\u7247\u76d4\u7532\u7684\u77ee\u4eba\u6b63\u5728\u5fd9\u5fd9\u788c\u788c\uff0c\u4eff\u4f5b\u662f\u5728\u8c03\u914d\u4ec0\u4e48\u836f\u6c34\uff0c\u201c\u6765\uff0c\u8fd9\u8fb9\u8d70\u3002\u201d\u4e39\u5409\u5c14\u8bf4\uff0c\u5f15\u7740\u743c\u6069\u7a7f\u8fc7\u4eba\u7fa4\uff0c\u8d70\u8fdb\u5185\u5ba4\u3002</p><p>\u4e00\u4e2a\u5973\u77ee\u4eba\u6b63\u5728\u684c\u524d\u57cb\u5934\u4e66\u5199\uff0c\u770b\u89c1\u4e39\u5409\u5c14\u8fdb\u6765\u4e5f\u6ca1\u53cd\u5e94\u3002\u4e39\u5409\u5c14\u7b49\u5f85\u4e86\u4e00\u4f1a\uff0c\u6572\u4e86\u6572\u684c\u5b50\uff0c\u8bf4\uff1a\u201c\u5766\u683c\u7eb3\uff0c\u6211\u4eec\u8fd9\u4f4d\u670b\u53cb\u60f3\u4e70\u51e0\u4ef6\u5deb\u5e08\u888d\uff0c\u4f60\u5e2e\u4ed6\u9009\u51e0\u4ef6\u5427\u3002\u201d</p><p>\u5973\u77ee\u4eba\u62ac\u8d77\u5934\uff0c\u51b7\u6de1\u5730\u671d\u743c\u6069\u77a5\u4e86\u4e00\u773c\u3002\u201c\u597d\u7684\uff0c\u201d\u5979\u56de\u7b54\uff0c\u8d77\u8eab\u6253\u5f00\u5899\u89d2\u7684\u67dc\u5b50\uff0c\u4ece\u91cc\u9762\u7ffb\u51fa\u51e0\u4ef6\u957f\u888d\u6765\uff0c\u6254\u7ed9\u743c\u6069\uff0c\u201c\u81ea\u5df1\u770b\uff0c\u4e0a\u9762\u90fd\u6709\u6807\u4ef7\u3002\u201d</p><p>\u4e0d\u518d\u7406\u4f1a\uff0c\u5979\u6709\u81ea\u987e\u81ea\u56de\u684c\u524d\u5750\u4e0b\u4e66\u5199\u3002\u4e39\u5409\u5c14\u8038\u8038\u80a9\uff0c\u671d\u743c\u6069\u505a\u4e86\u4e2a\u65e0\u53ef\u5948\u4f55\u7684\u624b\u52bf\uff0c\u5e26\u4ed6\u53c8\u8fdb\u4e86\u53e6\u5916\u7684\u623f\u95f4\uff0c\u5012\u662f\u7a7a\u7a7a\u8361\u8361\u7684\u6ca1\u6709\u522b\u4eba\u3002\u201c\u8fd9\u662f\u6211\u7684\u623f\u95f4\uff0c\u201d\u4e39\u5409\u5c14\u8bf4\uff0c\u201c\u6162\u6162\u6311\uff0c\u4e0d\u7740\u6025\u3002\u201d</p><p>\u743c\u6069\u62e3\u8d77\u5deb\u5e08\u888d\uff0c\u53d1\u73b0\u90fd\u662f\u540c\u4e00\u4e2a\u5f0f\u6837\uff0c\u8981\u8bba\u88c1\u526a\u624b\u827a\u2026\u2026\u5c31\u7b97\u4e0d\u8bf4\u7c97\u7cd9\uff0c\u81f3\u5c11\u4e5f\u7edd\u5bf9\u8c08\u4e0d\u4e0a\u7cbe\u81f4\u3002\u4e0d\u8fc7\u5176\u4e2d\u5012\u90fd\u5e94\u8be5\u9644\u7740\u67d0\u4e9b\u9b54\u6cd5\uff0c\u4e0d\u662f\u666e\u901a\u7684\u957f\u888d\u3002</p><p>\u4ed6\u6162\u6162\u629a\u6478\u7740\uff0c\u7136\u540e\u5bdf\u89c9\u5230\u6240\u6709\u957f\u888d\u4e2d\u7684\u9b54\u529b\u6ce2\u52a8\uff0c\u5176\u5b9e\u90fd\u6765\u6e90\u4e8e\u9886\u53e3\u90e8\u4f4d\u3002\u4e8e\u662f\u743c\u6069\u62e3\u8d77\u4e00\u4ef6\uff0c\u7ffb\u5f00\u9886\u53e3\u4e00\u770b\uff0c\u679c\u7136\u5185\u4fa7\u954c\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5947\u602a\u56fe\u6848\u3002</p><p>\u8fd9\u6709\u70b9\u50cf\u662f\u9b54\u6cd5\u5370\u8bb0\uff0c\u4f46\u5374\u53c8\u7f3a\u4e4f\u90a3\u79cd\u8054\u7ed3\u5b8c\u6574\u6027\uff0c\u82e5\u8bf4\u662f\u7b26\u53f7\uff0c\u5374\u53c8\u76f8\u5bf9\u590d\u6742\u5f88\u591a\u3002\u201c\u96be\u9053\u662f\u7b26\u7eb9\uff1f\u201d\u4ed6\u731c\u6d4b\u7740\uff0c\u4e0d\u8fc7\u5370\u8bb0\u3001\u7b26\u7eb9\u3001\u6d6e\u96d5\u3001\u7b26\u53f7\u8fd9\u4e9b\u56fe\u5f62\u9b54\u6cd5\u9886\u57df\uff0c\u4e3b\u8981\u662f\u5492\u6cd5\u5b66\u6d3e\u7684\u7814\u7a76\u9886\u57df\uff0c\u5176\u4e2d\u7b26\u7eb9\u662f\u6700\u795e\u79d8\u7684\u4e00\u79cd\uff0c\u743c\u6069\u5e76\u4e0d\u5f88\u61c2\u3002\u4ed6\u5c06\u8be2\u95ee\u7684\u76ee\u5149\u6295\u5411\u8001\u77ee\u4eba\uff0c\u7b49\u5f85\u89e3\u91ca\u3002</p><p>\u201c\u8fd9\u662f\u7b26\u7eb9\uff0c\u201d\u8001\u77ee\u4eba\u4e0d\u65e0\u5f97\u610f\u5730\u8bf4\uff0c\u201c\u522b\u770b\u8fd9\u51e0\u4ef6\u888d\u5b50\u4e0d\u90a3\u4e48\u7279\u522b\u6f02\u4eae\uff0c\u4f46\u53ef\u662f\u975e\u5e38\u5b9e\u7528\u7684\u3002\u6211\u4eec\u901a\u8fc7\u8fd9\u4e9b\u7b26\u7eb9\uff0c\u628a\u5404\u79cd\u539f\u672c\u4f1a\u76f8\u4e92\u51b2\u7a81\u7684\u9b54\u6cd5\u529b\u91cf\u4e00\u9f50\u90fd\u704c\u6ce8\u5728\u91cc\u9762\uff0c\u4fdd\u8bc1\u8ba9\u4f60\u6ee1\u610f\u3002\u201d</p><p>\u63a5\u7740\u8001\u77ee\u4eba\u4e00\u4e2a\u4e00\u4e2a\u5730\u5411\u743c\u6069\u89e3\u91ca\uff0c\u7136\u800c\u5e74\u8f7b\u5deb\u5e08\u7684\u7709\u5934\u6e10\u6e10\u76b1\u5f97\u8d8a\u6765\u8d8a\u7d27\uff0c\u56e0\u4e3a\u4ed6\u53d1\u73b0\u4eba\u7c7b\u548c\u77ee\u4eba\u7684\u601d\u7ef4\u679c\u7136\u6709\u7740\u65e0\u6cd5\u8c03\u548c\u7684\u5dee\u5f02\u3002\u743c\u6069\u5728\u5deb\u5e08\u5b66\u6821\u4e2d\u5b66\u5230\u7684\u57fa\u672c\u7406\u5ff5\uff0c\u662f\u7269\u5c3d\u5176\u7528\u3001\u5404\u53f8\u5176\u804c\uff0c\u5deb\u5e08\u888d\u4e0a\u5e94\u8be5\u9644\u4ec0\u4e48\u9b54\u6cd5\uff1f\u5f53\u7136\u662f\u9632\u62a4\u3001\u5f3a\u5316\u3001\u795d\u798f\u6216\u8005\u8f85\u52a9\u7c7b\u578b\u7684\u9b54\u6cd5\u624d\u5bf9\u3002\u4f46\u8fd9\u4e9b\u77ee\u4eba\u4eec\uff0c\u5c45\u7136\u8d39\u4e86\u4e5d\u725b\u4e8c\u864e\u4e4b\u529b\uff0c\u786c\u662f\u5728\u8fd9\u4e9b\u957f\u888d\u91cc\u704c\u6ce8\u8fdb\u4e86\u706b\u7403\u3001\u95ea\u7535\u675f\u3001\u51b0\u971c\u66b4\u4e4b\u7c7b\u7684\u653b\u51fb\u9b54\u6cd5\u3002\u4fbf\u5982\u743c\u6069\u624b\u91cc\u62ff\u7740\u7684\u8fd9\u4ef6\uff0c\u636e\u8bf4\u53ea\u8981\u4e00\u4e2a\u53e3\u4ee4\u542f\u52a8\uff0c\u5c31\u80fd\u4ece\u957f\u888d\u4e2d\u540c\u65f6\u5c04\u51fa\u4e09\u652f\u51b0\u7bad\u548c\u4e09\u652f\u706b\u7130\u7bad\u6765\u3002</p><p>\u743c\u6069\u5b9e\u5728\u4e0d\u660e\u767d\u8fd9\u4e9b\u77ee\u4eba\u7684\u77f3\u5934\u8111\u888b\u91cc\u90fd\u662f\u600e\u4e48\u60f3\u7684\u3002\u5982\u679c\u8981\u5f3a\u5316\u653b\u51fb\uff0c\u76f4\u63a5\u5728\u6cd5\u6756\u3001\u6212\u6307\u8fd9\u4e9b\u5730\u65b9\u4e0b\u529f\u592b\u5c31\u662f\uff0c\u4f55\u987b\u6253\u5deb\u5e08\u888d\u7684\u4e3b\u610f\u3002\u5176\u5b9e\u5deb\u5e08\u6700\u5927\u7684\u95ee\u9898\uff0c\u4e0d\u5728\u4e8e\u8fdb\u653b\u800c\u5728\u4e8e\u9632\u5fa1\uff0c\u4e0d\u5728\u4e8e\u4f24\u4eba\u800c\u5728\u4e8e\u81ea\u4fdd\uff1b\u4e0e\u5176\u60f3\u50cf\u7740\u4ece\u957f\u888d\u4e2d\u98de\u51fa\u706b\u7403\u628a\u4eba\u8f70\u6b7b\uff0c\u4e0d\u5982\u597d\u597d\u60f3\u60f3\u600e\u4e48\u4fdd\u62a4\u81ea\u5df1\u8fd9\u5177\u8106\u5f31\u8eab\u4f53\u6765\u5f97\u66f4\u5b9e\u9645\u70b9\u3002</p><p>\u679c\u7136\uff0c\u77ee\u4eba\u90fd\u662f\u5d07\u5c1a\u52c7\u6b66\u7684\u79cd\u65cf\uff0c\u8fde\u5deb\u5e08\u90fd\u662f\u8fd9\u822c\u6709\u8840\u6027\u3002</p><p>\u743c\u6069\u5b9e\u5728\u4e0d\u60f3\u4e70\u8fd9\u79cd\u5947\u602a\u7684\u5deb\u5e08\u888d\uff0c\u4f46\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u9009\u62e9\u7684\u4f59\u5730\u3002\u8fd9\u5ea7\u963f\u5fb7\u5df4\u57ce\u91cc\uff0c\u770b\u6837\u5b50\u662f\u4e0d\u4f1a\u6709\u5176\u4ed6\u5730\u65b9\u5356\u5deb\u5e08\u888d\u4e86\uff0c\u518d\u8bf4\u81ea\u5df1\u5df2\u7ecf\u51fa\u6765\u4e00\u4e0b\u5348\uff0c\u4e5f\u6ca1\u65f6\u95f4\u518d\u5230\u5904\u8f6c\u60a0\u3002\u4e8e\u662f\u4ed6\u638f\u51fa\u94b1\uff0c\u4e70\u4e86\u4e09\u4ef6\u957f\u888d\uff0c\u7528\u5305\u88b1\u5305\u597d\u3002</p><p>\u5f53\u8fd9\u4e00\u5207\u505a\u5b8c\u540e\uff0c\u4ed6\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u4e2a\u4e00\u76f4\u5c31\u60f3\u95ee\u7684\u95ee\u9898\u3002</p><p>\u201c\u6055\u6211\u5192\u6627\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f46\u6211\u786e\u5b9e\u5f88\u597d\u5947\uff0c\u6211\u6240\u63a5\u53d7\u7684\u9b54\u6cd5\u6559\u80b2\u90fd\u544a\u8bc9\u6211\uff0c\u5deb\u5e08\u662f\u975e\u5e38\u5fcc\u8bb3\u7a7f\u7740\u539a\u91cd\u7684\u76d4\u7532\u7684\uff0c\u56e0\u4e3a\u8fd9\u4f1a\u4e25\u91cd\u59a8\u788d\u65bd\u6cd5\u7684\u51c6\u786e\u6027\u548c\u6210\u529f\u7387\uff0c\u201d\u743c\u6069\u770b\u7740\u8001\u77ee\u4eba\uff0c\u53d1\u73b0\u5bf9\u65b9\u5728\u5fae\u7b11\uff0c\u201c\u6240\u4ee5\u2026\u2026\u6211\u60f3\u60a8\u77e5\u9053\u6211\u7684\u610f\u601d\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u8001\u77ee\u4eba\u8bf4\uff0c\u201c\u6211\u660e\u767d\u3002\u800c\u4e14\u4f60\u8bf4\u5f97\u6ca1\u9519\uff0c\u5e38\u7406\u662f\u5982\u6b64\uff0c\u201d\u4ed6\u51b2\u7740\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u4eec\u77ee\u4eba\u53e6\u6709\u4ed6\u6cd5\u3002\u6709\u6ca1\u6709\u5174\u8da3\u542c\u6211\u8bf4\u8bf4\uff0c\u5e74\u8f7b\u4eba\u3002\u201d</p><p>\u743c\u6069\u5f53\u7136\u6709\u5174\u8da3\u3002</p><p>\u203b\u203b\u203b</p><p>\u77ee\u4eba\u4ee5\u987d\u56fa\u3001\u6b7b\u677f\u548c\u52c7\u731b\u65e0\u754f\u800c\u8457\u79f0\u2014\u2014\u5f53\u7136\u8fd8\u6709\u5bf9\u8d22\u5bcc\u7684\u8d2a\u5a6a\u3002\u77ee\u4eba\u65cf\u4e2d\uff0c\u4ece\u6765\u5c31\u4e0d\u7f3a\u4e4f\u52c7\u6562\u987d\u5f3a\u7684\u6218\u58eb\uff0c\u4e5f\u4e0d\u4e4f\u8654\u8bda\u7684\u7267\u5e08\uff0c\u4f46\u5374\u6781\u5c11\u4f1a\u51fa\u73b0\u5deb\u5e08\uff0c\u5f53\u7136\u66f4\u8c08\u4e0d\u4e0a\u5929\u8d4b\u9b54\u6cd5\u80fd\u529b\u7684\u672f\u58eb\u4e86\u3002</p><p>\u5f88\u591a\u79cd\u65cf\u90fd\u51fa\u4e0d\u4e86\u5deb\u5e08\uff0c\u6bd4\u5982\u517d\u4eba\uff0c\u56e0\u4e3a\u4ed6\u4eec\u96c6\u4f53\u6bd4\u8f83\u7b28\uff0c\u800c\u5deb\u5e08\u663e\u7136\u5bf9\u667a\u5546\u7684\u8981\u6c42\u5f88\u9ad8\u3002\u4f46\u77ee\u4eba\u5374\u4e0d\u662f\u56e0\u4e3a\u8fd9\u4e2a\u539f\u56e0\uff0c\u77ee\u4eba\u5f88\u806a\u660e\uff0c\u4ec5\u4ee5\u5934\u8111\u800c\u8bba\u7edd\u4e0d\u4e9a\u4e8e\u4eba\u7c7b\uff0c\u4ed6\u4eec\u4e4b\u6240\u4ee5\u6781\u5c11\u51fa\u5deb\u5e08\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u4f20\u7edf\u548c\u89c2\u5ff5\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u81ea\u8eab\u6761\u4ef6\u9650\u5236\u3002</p><p>\u77ee\u4eba\u4e00\u65cf\uff0c\u7d20\u6765\u5d07\u5c1a\u52c7\u731b\u8840\u6027\uff0c\u8bf4\u5f97\u4e0d\u597d\u542c\u70b9\uff0c\u5c31\u662f\u4ee5\u86ee\u529b\u4e3a\u8363\u3002\u4e00\u4e2a\u77ee\u4eba\u5982\u679c\u5168\u8eab\u91cd\u7532\u63d0\u7740\u65a7\u5934\u51b2\u950b\u9677\u9635\uff0c\u4ed6\u4eec\u5c31\u4f1a\u5938\u5956\u4e3a\u82f1\u96c4\u597d\u6c49\uff1b\u4f46\u4e00\u4e2a\u77ee\u4eba\u5982\u679c\u8eb2\u5728\u540e\u9762\u7528\u9b54\u6cd5\u9634\u4eba\uff0c\u4ed6\u4eec\u5c31\u4f1a\u89c6\u4e3a\u5351\u9119\u61e6\u592b\u4e86\u3002\u8fd9\u79cd\u89c2\u5ff5\u5343\u5e74\u6d41\u4f20\uff0c\u4e00\u76f4\u5230\u8fd1\u767e\u5e74\u95f4\u624d\u6709\u6240\u6539\u53d8\u3002\u53e6\u5916\uff0c\u77ee\u4eba\u7684\u8eab\u6750\u81c3\u80bf\uff0c\u624b\u6307\u7c97\u77ed\uff0c\u8fd9\u90fd\u4e0d\u592a\u9002\u5408\u7cbe\u7ec6\u7684\u65bd\u6cd5\u8981\u6c42\u3002\u79cd\u79cd\u56e0\u7d20\u53e0\u52a0\u8d77\u6765\uff0c\u5bfc\u81f4\u7684\u7ed3\u679c\u5c31\u662f\u77ee\u4eba\u4e2d\u5deb\u5e08\u6781\u5c11\u3002</p><p>\u81f3\u4e8e\u7267\u5e08\uff0c\u4ed6\u4eec\u8499\u795e\u7947\u8d50\u4e88\u795e\u672f\uff0c\u548c\u5965\u672f\u8868\u9762\u770b\u8d77\u6765\u8fd1\u4f3c\uff0c\u5176\u5b9e\u662f\u4e24\u79cd\u4e0d\u540c\u7684\u8def\u5b50\u3002\u5965\u672f\u662f\u5deb\u5e08\u81ea\u5df1\u5b66\u4e60\u3001\u953b\u70bc\u800c\u6765\uff0c\u901a\u8fc7\u89e6\u6478\u9b54\u7f51\uff0c\u63d0\u53d6\u529b\u91cf\u5851\u6210\uff0c\u65bd\u5c55\u7684\u65f6\u5019\u9700\u8981\u914d\u5408\u590d\u6742\u7684\u59ff\u52bf\u52a8\u4f5c\uff1b\u795e\u672f\u5374\u5b8c\u5168\u662f\u795e\u7947\u6240\u8d50\u4e0b\uff0c\u901a\u8fc7\u9b54\u7f51\u76f4\u63a5\u704c\u5165\u7267\u5e08\u8eab\u4f53\uff0c\u9700\u8981\u7684\u65f6\u5019\u76f4\u63a5\u91ca\u653e\u5373\u53ef\uff0c\u65e0\u9700\u90a3\u4e9b\u7e41\u590d\u7684\u59ff\u52bf\u52a8\u4f5c\u2014\u2014\u6216\u8005\u8bf4\uff0c\u5deb\u5e08\u8fd1\u4f3c\u4e00\u4e2a\u81ea\u7ed9\u81ea\u8db3\u7684\u624b\u5de5\u4e1a\u8005\uff0c\u7267\u5e08\u8fd1\u4f3c\u4e00\u4e2a\u6709\u540e\u53f0\u8001\u677f\u652f\u6301\u7684\u84c4\u7535\u6c60\u3002</p><p>\u6240\u4ee5\u5deb\u5e08\u53ea\u80fd\u5f88\u53ef\u601c\u5730\u7a7f\u957f\u888d\uff0c\u7a7f\u886c\u886b\uff0c\u5982\u679c\u5929\u51b7\u518d\u52a0\u7f8a\u6bdb\u886b\u548c\u4fdd\u6696\u5185\u8863\u2014\u2014\u7a7f\u68c9\u8884\u90fd\u662f\u4e0d\u667a\u4e4b\u4e3e\u3002\u7267\u5e08\u5c31\u538b\u6839\u65e0\u6240\u8c13\uff0c\u7ecf\u5e38\u7a7f\u7740\u5168\u8eab\u94e0\u7532\u6ee1\u8857\u8dd1\uff0c\u4ee4\u5deb\u5e08\u53c8\u662f\u773c\u7ea2\u53c8\u662f\u5ac9\u5992\u3002</p><p>\u77ee\u4eba\u5934\u8111\u987d\u56fa\uff0c\u53cd\u8fc7\u6765\u8bf4\u5c31\u662f\u4fe1\u4ef0\u5341\u5206\u8654\u8bda\uff0c\u624b\u6307\u7c97\u77ed\u8fd9\u4e9b\u5148\u5929\u4e0d\u8db3\u53c8\u534a\u70b9\u4e0d\u5f71\u54cd\u795e\u672f\u65bd\u6cd5\uff0c\u6240\u4ee5\u77ee\u4eba\u7267\u5e08\u5012\u662f\u4ece\u6765\u4e0d\u5c11\u3002\u7267\u5e08\u80fd\u7a7f\u91cd\u7532\uff0c\u6301\u5de8\u76fe\uff0c\u63d0\u6218\u9524\u5927\u65a7\uff0c\u65e2\u80fd\u4e0a\u9635\u51b2\u950b\uff0c\u53c8\u80fd\u5e87\u62a4\u6218\u53cb\uff0c\u8fd8\u80fd\u6551\u6b7b\u6276\u4f24\uff0c\u9887\u4e3a\u7b26\u5408\u77ee\u4eba\u7684\u82f1\u96c4\u89c2\uff0c\u5730\u4f4d\u7d20\u6765\u5c31\u5f88\u9ad8\u3002</p><p>\u53ea\u662f\uff0c\u4ec5\u9760\u52c7\u6b66\u548c\u8840\u6027\uff0c\u7ec8\u7a76\u662f\u4e0d\u80fd\u538b\u5012\u4e00\u5207\u7684\u3002\u5965\u6cd5\u7684\u7cbe\u5fae\u7384\u5999\uff0c\u8fdc\u8fdc\u4e0d\u662f\u5200\u5251\u6240\u80fd\u6bd4\u62df\u3002\u56e0\u4e3a\u6ca1\u6709\u5f3a\u5927\u7684\u5deb\u5e08\u7fa4\u4f53\u652f\u6301\uff0c\u5f88\u591a\u90e8\u843d\u90fd\u5728\u548c\u5de8\u4eba\u3001\u517d\u4eba\u7684\u6218\u6597\u4e2d\u635f\u8017\u6d88\u4ea1\u4e86\uff0c\u77ee\u4eba\u7684\u6570\u91cf\u8d8a\u6765\u8d8a\u5c11\uff0c\u8fd9\u662f\u5f88\u4ee4\u4eba\u60b2\u4f24\u7684\u4e8b\u60c5\u3002</p><p>\u8fd9\u79cd\u60c5\u5f62\u6301\u7eed\u6570\u5343\u5e74\uff0c\u7ec8\u4e8e\u5728\u8fd1\u4ee3\u53d1\u751f\u4e86\u53d8\u5316\u3002</p><p>1306DR\uff0c\u4f1f\u5927\u7684\u77ee\u4eba\u795e\u7947\u83ab\u62c9\u4e01\u5bf9\u4ed6\u7684\u5b50\u6c11\u964d\u4e0b\u4e86\u4e00\u9053\u795d\u798f\u3002\u77ee\u4eba\u4eec\u628a\u8fd9\u4e2a\u795d\u798f\u79f0\u4e4b\u4e3a\u201c\u94f8\u9020\u201d\u6216\u8005\u201c\u96f7\u9e23\u795d\u798f\u201d\uff0c\u8fd9\u4e00\u5e74\u4e5f\u56e0\u6b64\u88ab\u547d\u540d\u4e3a\u201c\u96f7\u9e23\u795d\u798f\u4e4b\u5e74\u201d\u3002\u56e0\u4e3a\u8fd9\u9053\u795d\u798f\u6216\u8005\u8bf4\u795e\u6069\uff0c\u77ee\u4eba\u5f53\u5e74\u7684\u51fa\u751f\u7387\u9661\u7136\u63d0\u9ad8\u4e86\u4e00\u500d\u3002</p><p>\u8fd9\u4e00\u5e74\u51fa\u751f\u7684\u77ee\u4eba\uff0c\u901a\u5e38\u88ab\u79f0\u4f5c\u96f7\u9e23\u4e4b\u5b50\uff0c\u4ed6\u4eec\u5f53\u4e2d\u63a5\u8fd1\u4e00\u534a\u90fd\u662f\u540c\u6027\u6216\u5f02\u6027\u53cc\u80de\u80ce\u3002\u96f7\u9e23\u4e4b\u5b50\u79c9\u627f\u4e86\u77ee\u4eba\u5148\u8f88\u7684\u52c7\u731b\uff0c\u540c\u65f6\u5374\u4e5f\u4e0d\u6392\u65a5\u5965\u6cd5\u7684\u529b\u91cf\u3002\u5c3d\u7ba1\u5927\u591a\u6570\u77ee\u4eba\u4ecd\u7136\u66f4\u559c\u6b22\u62ff\u65a7\u5b50\u800c\u975e\u6cd5\u6756\uff0c\u4ecd\u6709\u8bb8\u591a\u96f7\u9e23\u4e4b\u5b50\uff0c\u5c24\u5176\u662f\u90a3\u4e9b\u53cc\u80de\u80ce\uff0c\u8e0f\u4e0a\u4e86\u5deb\u5e08\u7684\u9053\u8def\uff0c\u4ed6\u4eec\u6bd4\u5176\u4ed6\u77ee\u4eba\u601d\u60f3\u66f4\u5f00\u653e\uff0c\u773c\u5149\u66f4\u957f\u8fdc\uff0c\u66f4\u613f\u610f\u8d70\u51fa\u4e16\u4ee3\u5c45\u4f4f\u7684\u5bb6\u4e61\uff0c\u8fdb\u5165\u4eba\u7c7b\u7684\u4e16\u754c\uff0c\u5b66\u4e60\u65b0\u7684\u77e5\u8bc6\u548c\u9b54\u6cd5\u3002\u8001\u77ee\u4eba\u4e39\u5409\u5c14\u5c31\u662f\u8fd9\u4e9b\u201c\u96f7\u9e23\u4e4b\u5b50\u201d\u5176\u4e2d\u4e4b\u4e00\uff0c\u4ed6\u770b\u8d77\u6765\u6a21\u6837\u663e\u5f97\u5f88\u8001\uff0c\u5176\u5b9e\u4e5f\u624d\u516d\u5341\u591a\u5c81\uff0c\u5bf9\u77ee\u4eba\u6765\u8bf4\u8fd8\u7b97\u5e74\u8f7b\u3002</p><p>\u96f7\u9e23\u795d\u798f\u4e4b\u5e74\u8ddd\u4eca\u5df2\u7ecf\u516d\u5341\u516d\u5e74\uff0c\u82e5\u662f\u4eba\u7c7b\uff0c\u5f53\u5e74\u51fa\u751f\u7684\u5b69\u5b50\u6b64\u65f6\u90fd\u5dee\u4e0d\u591a\u662f\u5782\u5782\u8001\u673d\uff0c\u4f46\u6362\u4f5c\u77ee\u4eba\uff0c\u5219\u5374\u662f\u6b63\u5f53\u58ee\u5e74\u3002\u8fd9\u4e9b\u96f7\u9e23\u4e4b\u5b50\u5f88\u591a\u90fd\u6210\u4e3a\u5deb\u5e08\uff0c\u800c\u4e14\u56e0\u4e3a\u4ed6\u4eec\u662f\u968f\u96f7\u9e23\u795d\u798f\u964d\u751f\u7684\uff0c\u88ab\u89c6\u4e3a\u662f\u79c9\u627f\u4e86\u83ab\u62c9\u4e01\u7684\u610f\u65e8\u800c\u6765\uff0c\u6240\u4ee5\u8fd1\u4e9b\u5e74\u6765\uff0c\u77ee\u4eba\u65cf\u7684\u89c2\u5ff5\u6e10\u6e10\u6709\u6240\u8f6c\u53d8\uff0c\u4e0d\u518d\u6b67\u89c6\u5deb\u5e08\u548c\u5965\u672f\uff0c\u800c\u662f\u6e10\u6e10\u76f8\u5bf9\u5c0a\u91cd\u548c\u656c\u754f\u8d77\u6765\u3002</p><p>\u5f53\u7136\uff0c\u4e5f\u4ec5\u4ec5\u662f\u76f8\u5bf9\uff0c\u5343\u5e74\u7684\u4f20\u7edf\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u63a8\u7ffb\u7684\u3002\u4fbf\u5982\u8fd9\u963f\u5fb7\u5df4\u57ce\u91cc\uff0c\u5927\u7ea6\u4e24\u4e07\u7684\u4eba\u53e3\uff0c\u5deb\u5e08\u4e5f\u4e0d\u5230\u5341\u4e2a\u3002\u5730\u4f4d\u867d\u7136\u9887\u9ad8\uff0c\u5374\u4e5f\u8fd8\u6bd4\u4e0d\u4e0a\u7267\u5e08\u3002</p><p>\u96f7\u9e23\u4e4b\u5b50\u4ece\u4eba\u7c7b\u90a3\u91cc\u5b66\u5230\u4e86\u9b54\u6cd5\u6280\u827a\uff0c\u4f46\u8fd9\u5e76\u4e0d\u80fd\u539f\u6837\u7167\u642c\u8fc7\u6765\u2014\u2014\u6216\u8005\u8bf4\uff0c\u53ea\u8981\u77ee\u4eba\u7684\u624b\u6307\u5934\u6ca1\u6709\u53d8\u5f97\u7ea4\u7ec6\u4fee\u957f\uff0c\u90a3\u4ed6\u4eec\u5c31\u6ca1\u529e\u6cd5\u76f4\u63a5\u5957\u7528\u4eba\u7c7b\u7684\u65bd\u6cd5\u6280\u5de7\u3002</p><p>\u529e\u6cd5\u90fd\u662f\u4eba\u60f3\u51fa\u6765\u7684\u2026\u2026\u8fd9\u4e2a\u201c\u4eba\u201d\u5f53\u7136\u4e5f\u5305\u62ec\u77ee\u4eba\u3002</p><p>\u96f7\u9e23\u4e4b\u5b50\u4eec\u79c9\u627f\u83ab\u62c9\u4e01\u7684\u795d\u798f\u964d\u751f\uff0c\u4ed6\u4eec\u5148\u5929\u5c31\u662f\u6bd4\u8f83\u7279\u6b8a\u7684\u5b58\u5728\uff0c\u548c\u795e\u7947\u6709\u7740\u76f8\u5bf9\u5bc6\u5207\u7684\u5173\u7cfb\u3002\u5728\u8fd9\u4e2a\u57fa\u7840\u4e0a\uff0c\u4ed6\u4eec\u7cc5\u5408\u4e86\u795e\u672f\u548c\u5965\u672f\u7684\u7279\u70b9\uff0c\u522b\u51fa\u5fc3\u88c1\u5730\u521b\u9020\u51fa\u4e00\u79cd\u65b0\u7684\u65bd\u6cd5\u65b9\u5f0f\uff0c\u7531\u6b64\u4e5f\u8bde\u751f\u4e86\u4e00\u79cd\u65b0\u7684\u5deb\u5e08\u3002</p><p>\u4ed6\u4eec\u81ea\u79f0\u4e3a\u201c\u7b26\u7eb9\u5deb\u5e08\u201d\u3002</p><p>\u548c\u5176\u4ed6\u5deb\u5e08\u4e00\u6837\uff0c\u7b26\u7eb9\u5deb\u5e08\u540c\u6837\u9700\u8981\u5b66\u4e60\u3001\u953b\u70bc\uff0c\u89e6\u6478\u9b54\u7f51\uff0c\u4ece\u4e2d\u63d0\u53d6\u80fd\u91cf\uff0c\u5851\u6210\u9b54\u6cd5\u51c6\u5907\u8d77\u6765\uff0c\u5728\u9700\u8981\u7684\u65f6\u5019\u91ca\u653e\u3002\u4f46\u4e3a\u4e86\u907f\u514d\u77ee\u4eba\u8eab\u4f53\u7684\u52a3\u52bf\uff0c\u4ed6\u4eec\u7cc5\u5408\u795e\u672f\uff0c\u53d1\u660e\u4e86\u7528\u77f3\u677f\u4ee3\u66ff\u81ea\u8eab\u6765\u51c6\u5907\u9b54\u6cd5\u7684\u65b9\u5f0f\u3002</p><p>\u5176\u4ed6\u5deb\u5e08\u51c6\u5907\u5b8c\u9b54\u6cd5\u540e\uff0c\u6cd5\u672f\u4fbf\u5728\u81ea\u8eab\uff1b\u7b26\u7eb9\u5deb\u5e08\u5374\u5c06\u9b54\u6cd5\u51c6\u5907\u5230\u77f3\u677f\u4e0a\uff0c\u4e00\u4e2a\u6cd5\u672f\u7528\u4e00\u5757\u77f3\u677f\uff0c\u901a\u8fc7\u4ed6\u4eec\u7814\u7a76\u51fa\u6765\u7684\u7279\u6b8a\u7b26\u7eb9\u56fa\u5b9a\u3002\u5728\u9700\u8981\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u76f4\u63a5\u62ff\u8d77\u77f3\u677f\uff0c\u65e0\u9700\u592a\u590d\u6742\u7684\u65bd\u6cd5\u52a8\u4f5c\uff0c\u76f4\u63a5\u7528\u5492\u8a00\u642d\u914d\u7b80\u5355\u7684\u624b\u52bf\u6fc0\u53d1\uff0c\u5c31\u80fd\u91ca\u653e\u9b54\u6cd5\uff0c\u8fd9\u5c31\u6709\u4e9b\u8fd1\u4f3c\u795e\u672f\u4e86\u3002</p><p>\u65e2\u7136\u65bd\u6cd5\u59ff\u52bf\u88ab\u5927\u5927\u7b80\u5316\uff0c\u7b26\u7eb9\u5deb\u5e08\u81ea\u7136\u4e5f\u5c31\u4e0d\u9700\u8981\u975e\u7a7f\u67d4\u8f6f\u7684\u5deb\u5e08\u888d\uff0c\u4ed6\u4eec\u5b8c\u5168\u53ef\u4ee5\u7a7f\u76f8\u5bf9\u4e25\u5b9e\u7684\u76d4\u7532\u4e0a\u9635\uff0c\u53ea\u8981\u4e0d\u662f\u592a\u6c89\u91cd\u592a\u8fc7\u5206\u2014\u2014\u6bd4\u5982\u5168\u8eab\u94e0\u7532\u8fd8\u662f\u7a7f\u4e0d\u5f97\uff0c\u90a3\u73a9\u610f\u7b80\u76f4\u662f\u5bc6\u5c01\u94c1\u7f50\u5934\u3002\u4e0d\u8fc7\u8fd9\u6837\u4e00\u6765\uff0c\u7b26\u7eb9\u5deb\u5e08\u51fa\u95e8\u5c31\u5f97\u5e26\u4e0a\u4e00\u5927\u5806\u77f3\u677f\uff1b\u4f4e\u9636\u7684\u65f6\u5019\u8fd8\u597d\u8bf4\uff0c\u7b49\u5230\u80fd\u529b\u63d0\u5347\uff0c\u53ef\u4ee5\u51c6\u5907\u7684\u6cd5\u672f\u8d8a\u6765\u8d8a\u591a\uff0c\u6bcf\u6b21\u9700\u8981\u51e0\u5341\u5757\u77f3\u677f\uff0c\u7ed3\u679c\u5c31\u8981\u5f04\u5230\u625b\u7740\u6c89\u7538\u7538\u5927\u53e3\u888b\u51fa\u95e8\u7684\u60c5\u5f62\uff0c\u8fd9\u5c31\u5b8c\u5168\u4e27\u5931\u4e86\u672c\u610f\uff0c\u7b80\u76f4\u6ca6\u4e3a\u641e\u7b11\u4e86\u3002\u77e5\u9053\u7684\u8bf4\u662f\u5deb\u5e08\uff0c\u4e0d\u77e5\u9053\u7684\u8fd8\u4ee5\u4e3a\u662f\u77ee\u4eba\u77f3\u5320\u3002</p><p>\u4e8e\u662f\u7b26\u7eb9\u5deb\u5e08\u4eec\u518d\u63a5\u518d\u52b1\uff0c\u7ec8\u4e8e\u8fdb\u4e00\u6b65\u63d0\u5347\u6280\u5de7\uff0c\u6210\u529f\u5730\u7528\u91d1\u5c5e\u788e\u7247\u4ee3\u66ff\u4e86\u77f3\u677f\u6765\u51c6\u5907\u9b54\u6cd5\u3002\u91d1\u5c5e\u788e\u7247\u6bd4\u77f3\u677f\u8f7b\u8584\u5f97\u591a\uff0c\u5c31\u7b97\u5e26\u4e0a\u51e0\u5341\u7247\u4e5f\u4e0d\u662f\u5927\u4e8b\uff0c\u95ee\u9898\u7ec8\u4e8e\u89e3\u51b3\u3002\u518d\u5230\u540e\u6765\uff0c\u66f4\u6709\u4eba\u7a81\u53d1\u5947\u60f3\uff0c\u628a\u8fd9\u4e9b\u91d1\u5c5e\u788e\u7247\u5168\u6302\u5728\u8eab\u4e0a\u2026\u2026\u5c31\u662f\u743c\u6069\u9762\u524d\u8fd9\u4f4d\u8001\u77ee\u4eba\u7684\u6a21\u6837\u4e86\u3002</p><p>\u542c\u5b8c\u8001\u77ee\u4eba\u7684\u89e3\u91ca\uff0c\u743c\u6069\u7b97\u662f\u604d\u7136\u5927\u609f\uff0c\u518d\u4ed4\u7ec6\u770b\u8001\u4eba\u7684\u8eab\u4e0a\uff0c\u53d1\u73b0\u90a3\u4e9b\u4eae\u6676\u6676\u7684\u91d1\u5c5e\u7247\u4e0a\uff0c\u786e\u5b9e\u90fd\u9690\u7ea6\u6d6e\u73b0\u7740\u67d0\u79cd\u602a\u5f02\u7684\u7b26\u7eb9\u3002\u4f46\u968f\u5373\uff0c\u4ed6\u5fc3\u5934\u9690\u9690\u5347\u8d77\u4e00\u4e1d\u4e0d\u5b89\u6765\uff0c\u786e\u5b9e\uff0c\u8fd9\u79cd\u4f5c\u6cd5\u7b49\u82e5\u662f\u7cc5\u5408\u4e86\u795e\u672f\u548c\u5965\u672f\u7684\u4f18\u70b9\uff0c\u4e0d\u4f46\u5f25\u8865\u4e86\u77ee\u4eba\u7684\u5148\u5929\u7f3a\u9677\uff0c\u4fbf\u662f\u5bf9\u4e8e\u4eba\u7c7b\u6765\u8bf4\uff0c\u4e5f\u4e0d\u4e4f\u501f\u9274\u610f\u4e49\u3002\u4f46\u7b26\u7eb9\u5deb\u5e08\u80fd\u505a\u5230\u8fd9\u4e00\u70b9\uff0c\u53ea\u6015\u66f4\u591a\u51ed\u501f\u7684\u5176\u5b9e\u4e0d\u662f\u81ea\u8eab\u7684\u7814\u7a76\u52aa\u529b\uff0c\u800c\u662f\u4ed6\u4eec\u7684\u8840\u8109\u2014\u2014\u4ed6\u4eec\u968f\u83ab\u62c9\u4e01\u7684\u96f7\u9e23\u795d\u798f\u964d\u751f\u3002\u6216\u8005\u8bf4\uff0c\u4ed6\u4eec\u5176\u5b9e\u662f\u67d0\u79cd\u7279\u522b\u610f\u4e49\u4e0a\u7684\u672f\u58eb\u3002</p><p>\u8fd9\u5e76\u4e0d\u662f\u4e00\u79cd\u6709\u666e\u904d\u9002\u7528\u610f\u4e49\u7684\u6280\u5de7\uff0c\u4ece\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u6765\u8bf4\uff0c\u8fd9\u5176\u5b9e\u662f\u628a\u5965\u6cd5\u8005\u5f80\u795e\u672f\u8005\u7684\u65b9\u5411\u62c9\u8fd1\u4e86\u4e00\u6b65\u3002\u5927\u6982\u8fd9\u4e5f\u5c31\u662f\u4e3a\u4ec0\u4e48\u5728\u8fd9\u5ea7\u77ee\u4eba\u57ce\u4e2d\uff0c\u5deb\u5e08\u5c45\u7136\u4e5f\u4f4f\u5728\u795e\u6bbf\u91cc\u7684\u7f18\u6545\u3002</p><p>\u8eab\u4e3a\u4e00\u540d\u9634\u9b42\u57ce\u7684\u5deb\u5e08\uff0c\u743c\u6069\u672c\u80fd\u5730\u654c\u89c6\u548c\u53cd\u611f\u8fd9\u70b9\u3002\u4e0d\u8fc7\u8fd9\u662f\u77ee\u4eba\u4eec\u7684\u4e8b\u60c5\uff0c\u4ed6\u81ea\u7136\u7ba1\u4e0d\u7740\u4e5f\u61d2\u5f97\u7ba1\u3002\u53ea\u662f\uff0c\u5982\u679c\u8fd9\u79cd\u4f5c\u6cd5\u63a8\u800c\u5e7f\u4e4b\uff0c\u5982\u679c\u4eba\u7c7b\u795e\u7947\u4e5f\u6a21\u4eff\u83ab\u62c9\u4e01\u63a8\u884c\u8fd9\u4e00\u5957\u2026\u2026</p><p>\u603b\u89c9\u5f97\u5fc3\u4e2d\u9690\u9690\u6709\u4e9b\u4e0d\u5b89\u5462\u3002</p><p>\u7b49\u7b49\u2026\u2026</p><p>\u201c\u90a3\u4e48\uff0c\u96f7\u9e23\u795d\u798f\u4e4b\u5e74\u4ee5\u540e\u51fa\u751f\u7684\u77ee\u4eba\uff0c\u201d\u743c\u6069\u8bd5\u63a2\u5730\u95ee\uff0c\u201c\u540c\u6837\u4e5f\u662f\u96f7\u9e23\u4e4b\u5b50\u5417\uff1f\u5443\uff0c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4ed6\u4eec\u4e5f\u548c\u60a8\u4e00\u6837\u62e5\u6709\u8fd9\u6837\u4f18\u79c0\u7684\u9b54\u6cd5\u5929\u8d4b\u5417\uff1f\u201d</p><p>\u8001\u77ee\u4eba\u6447\u5934\u3002</p><p>\u743c\u6069\u6697\u81ea\u6c89\u601d\u7740\uff0c\u83ab\u62c9\u4e01\u7a81\u7136\u964d\u4e0b\u96f7\u9e23\u795d\u798f\uff0c\u751f\u751f\u9020\u51fa\u8fd9\u4e48\u4e00\u6279\u96f7\u9e23\u4e4b\u5b50\u6765\uff0c\u80cc\u540e\u4f3c\u4e4e\u5927\u6709\u6df1\u610f\uff0c\u53ea\u6015\u4e0d\u4ec5\u4ec5\u662f\u57f9\u517b\u4e00\u6279\u77ee\u4eba\u5deb\u5e08\u8fd9\u4e48\u7b80\u5355\u5427\u3002</p></article>"}}}]);
