(window.webpackJsonp=window.webpackJsonp||[]).push([[1297],{1320:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e00\u8282 \u761f\u75ab\u662f\u4eba\u4e3a",content:"<article><h2>\u7b2c\u4e00\u8282 \u761f\u75ab\u662f\u4eba\u4e3a</h2><p>\u5728\u8def\u4e0a\uff0c\u901a\u8fc7\u6885\u83f2\u65af\u7684\u4ecb\u7ecd\uff0c\u743c\u6069\u5df2\u7ecf\u5bf9\u535a\u5f97\u4e4b\u95e8\u6709\u4e86\u521d\u6b65\u7684\u4e86\u89e3\u3002\u5b83\u539f\u672c\u53ea\u662f\u4e00\u5ea7\u5c0f\u57ce\uff0c\u5730\u7406\u4f4d\u7f6e\u5341\u5206\u4f18\u8d8a\uff0c\u6b63\u597d\u5904\u4e8e\u6df1\u6c34\u57ce\u5230\u5b89\u59c6\u5e1d\u56fd\u7684\u4e2d\u95f4\u3002\u6df1\u6c34\u57ce\u662f\u5317\u65b9\u4e43\u81f3\u6574\u4e2a\u5927\u9646\u7684\u7b2c\u4e00\u5927\u57ce\u5e02\uff0c\u5b89\u59c6\u5219\u662f\u5357\u65b9\u6700\u5bcc\u88d5\u7684\u5e1d\u56fd\u4e4b\u4e00\uff0c\u53f7\u79f0\u201c\u5546\u4eba\u9886\u5730\u201d\u3002\u6240\u6709\u6765\u5f80\u6df1\u6c34\u57ce\u548c\u5b89\u59c6\u5e1d\u56fd\u7684\u5546\u65c5\u884c\u4eba\uff0c\u65e0\u4e00\u4f8b\u5916\u90fd\u4f1a\u5728\u535a\u5f97\u4e4b\u95e8\u505c\u7559\u4f11\u606f\uff0c\u8fd9\u4e5f\u5e26\u52a8\u4e86\u57ce\u5e02\u7684\u53d1\u5c55\uff0c\u4fc3\u8fdb\u8d38\u6613\u7e41\u8363\u3002</p><p>\u8fd9\u5ea7\u57ce\u5e02\u539f\u672c\u5e76\u4e0d\u9760\u6d77\uff0c\u540e\u6765\u968f\u7740\u4eba\u53e3\u89c4\u6a21\u589e\u52a0\uff08\u5982\u4eca\u5df2\u7ecf\u8d85\u8fc7\u56db\u4e07\u4f59\u4eba\uff0c\u63a5\u8fd1\u9634\u9b42\u57ce\u7684\u4e24\u500d\uff09\uff0c\u57ce\u5e02\u7248\u56fe\u4e5f\u9010\u6e10\u5411\u5916\u6269\u5f20\uff0c\u6700\u7ec8\u4e00\u76f4\u62b5\u8fbe\u5b9d\u5251\u6d77\uff0c\u5efa\u8d77\u4e86\u5360\u5730\u5e7f\u9614\u7684\u7801\u5934\u533a\uff0c\u6210\u4e3a\u4e00\u5ea7\u6e2f\u53e3\u57ce\u5e02\u3002</p><p>\u81f3\u4e8e\u201c\u535a\u5f97\u4e4b\u95e8\u201d\u8fd9\u4e2a\u6709\u4e9b\u5947\u602a\u7684\u57ce\u5e02\u540d\u5b57\uff0c\u5219\u662f\u6709\u4e2a\u6545\u4e8b\u3002\u5f88\u4e45\u4ee5\u524d\uff0c\u5728\u8fd9\u5ea7\u57ce\u5e02\u8fd8\u6ca1\u6709\u5b8c\u5168\u6269\u5f20\u5230\u6d77\u8fb9\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u6709\u4e00\u7fa4\u6d77\u76d7\u76d8\u8e1e\u6b64\u5904\u3002\u6d77\u76d7\u7684\u9996\u9886\u540d\u53eb\u535a\u5f97\u5b89\uff0c\u4ed6\u5728\u6d77\u8fb9\u5efa\u7acb\u4e86\u9ad8\u5854\uff0c\u4e00\u65b9\u9762\u4f5c\u4e3a\u56de\u5bb6\u7684\u5bfc\u5f15\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u4e00\u79cd\u9632\u5fa1\u63aa\u65bd\u3002\u6d77\u76d7\u548c\u57ce\u5e02\u4e2d\u7684\u5c45\u6c11\u76f8\u5b89\u65e0\u4e8b\uff0c\u5f7c\u6b64\u5f80\u6765\u3002\u6709\u4e00\u6761\u4e54\u8428\u6cb3\u6a2a\u7a7f\u8fc7\u57ce\u5e02\uff0c\u6d41\u7ecf\u6d77\u76d7\u4eec\u7684\u6839\u636e\u5730\uff0c\u6ce8\u5165\u5b9d\u5251\u6d77\u4e2d\u3002\u5f53\u6d77\u76d7\u4eec\u9700\u8981\u8fdb\u57ce\u8d38\u6613\u65f6\uff0c\u4ed6\u4eec\u5c31\u4e58\u8239\u6cbf\u7740\u4e54\u8428\u6cb3\u9006\u6d41\u800c\u4e0a\uff0c\u8fdb\u5165\u57ce\u533a\u3002</p><p>\u540e\u6765\u57ce\u5e02\u5feb\u901f\u6269\u5f20\uff0c\u903c\u8fd1\u6d77\u76d7\u4eec\u7684\u6839\u636e\u5730\u2014\u2014\u7531\u4e8e\u62c5\u5fc3\u88ab\u6d77\u76d7\u4eec\u88ad\u51fb\uff0c\u57ce\u5e02\u4e2d\u7684\u5546\u4eba\u548c\u519c\u6c11\u7ec4\u6210\u8bae\u4f1a\uff0c\u901a\u8fc7\u51b3\u8bae\uff0c\u4fee\u5efa\u4e86\u4e00\u5ea7\u9ad8\u8038\u57ce\u5899\uff0c\u5c06\u6d77\u76d7\u7684\u6839\u636e\u5730\u7262\u7262\u9694\u5728\u5916\u9762\u3002\u4e0d\u4f46\u5982\u6b64\uff0c\u800c\u4e14\u9881\u5e03\u4e86\u6cd5\u4ee4\uff1a\u5982\u679c\u6d77\u76d7\u4eec\u60f3\u8fdb\u57ce\u8d38\u6613\uff0c\u5fc5\u987b\u4ea4\u7eb3\u9ad8\u989d\u7684\u5165\u57ce\u8d39\u3002\u6d77\u76d7\u4eec\u52c3\u7136\u5927\u6012\uff0c\u62d2\u7edd\u4ea4\u7eb3\uff0c\u53cc\u65b9\u5bf9\u5cd9\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u534a\u517d\u4eba\u548c\u91ce\u86ee\u4eba\u88ad\u51fb\u57ce\u5e02\u2014\u2014\u4e5f\u88ad\u51fb\u6d77\u76d7\uff0c\u53cd\u6b63\u90fd\u89c6\u4e3a\u4e00\u4f53\u3002</p><p>\u6b64\u65f6\u535a\u5f97\u5b89\u51fa\u6d77\u5f52\u6765\uff0c\u89c1\u5230\u8fd9\u79cd\u60c5\u51b5\uff0c\u4ed6\u7acb\u523b\u7387\u9886\u6d77\u76d7\u4eec\u653b\u5165\u57ce\u5e02\uff0c\u6740\u6b7b\u4e86\u5021\u8bae\u4fee\u5efa\u57ce\u5899\u7684\u5546\u4eba\u4eec\uff0c\u5e76\u4e14\u89e3\u6563\u8bae\u4f1a\uff0c\u53ea\u7559\u4e86\u56db\u4e2a\u6210\u5458\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5f53\u65f6\u6295\u4e86\u53cd\u5bf9\u7968\u3002\u63a5\u7740\u535a\u5f97\u5b89\u7387\u9886\u6d77\u76d7\u548c\u5e02\u6c11\u51fb\u6e83\u4e86\u534a\u517d\u4eba\u548c\u91ce\u86ee\u4eba\uff0c\u4fdd\u536b\u4e86\u57ce\u5e02\u3002\u535a\u5f97\u5b89\u6ca1\u6709\u62c6\u9664\u57ce\u5899\uff0c\u800c\u662f\u5728\u6cb3\u4e0a\u4fee\u5efa\u4e86\u4e00\u5ea7\u6865\u95e8\uff0c\u5e76\u547d\u540d\u4e3a\u535a\u5f97\u4e4b\u95e8\uff0c\u540e\u6765\u201c\u535a\u5f97\u4e4b\u95e8\u201d\u6e10\u6e10\u5c31\u53d8\u6210\u4e86\u6574\u4e2a\u57ce\u5e02\u7684\u540d\u5b57\u3002</p><p>\u535a\u5f97\u5b89\u5f53\u65f6\u6307\u5b9a\u4ed6\u7559\u4e0b\u7684\u56db\u4e2a\u8bae\u4f1a\u6210\u5458\u4e3a\u201c\u516c\u7235\u201d\uff0c\u4f5c\u4e3a\u57ce\u5e02\u7684\u9886\u8896\u3002\u540e\u6765\u4ed6\u4eec\u901d\u53bb\uff0c\u4fbf\u7531\u8bae\u4f1a\u9009\u4e3e\u4ea7\u751f\u65b0\u7684\u4eba\u9009\uff0c\u7ee7\u627f\u201c\u516c\u7235\u201d\u540d\u53f7\uff0c\u7edf\u9886\u57ce\u5e02\u3002\u81f3\u4e8e\u535a\u5f97\u5b89\uff0c\u5728\u5efa\u6210\u535a\u5f97\u4e4b\u95e8\u540e\uff0c\u51fa\u6d77\u5bfb\u627e\u4f20\u8bf4\u4e2d\u7684\u5b89\u54e5\u7f8e\u5c9b\uff0c\u81ea\u6b64\u97f3\u4fe1\u5168\u65e0\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u73b0\u5728\u8fd9\u5ea7\u57ce\u5e02\u7684\u6700\u9ad8\u9886\u8896\uff0c\u5c31\u662f\u90a3\u56db\u4e2a\u516c\u7235\u4e86\uff1f\u201d</p><p>\u201c\u6050\u6015\u73b0\u5728\u662f\u4e09\u4f4d\u3002\u201d\u6885\u83f2\u65af\u56de\u7b54\uff0c\u5728\u761f\u75ab\u7684\u4e00\u5f00\u59cb\uff0c\u56db\u4f4d\u9886\u8896\u4e2d\u7684\u8d1d\u5c14\u5854\u516c\u7235\u5c31\u67d3\u75c5\u8eab\u6545\u4e86\u3002\u867d\u7136\u6309\u60ef\u4f8b\uff0c\u6709\u7f3a\u4f4d\u81ea\u7136\u8981\u8865\u9009\uff0c\u4f46\u9762\u4e34\u8fd9\u573a\u5927\u761f\u75ab\uff0c\u4f30\u8ba1\u6682\u65f6\u8c01\u4e5f\u6ca1\u5fc3\u601d\u505a\u8fd9\u79cd\u4e0d\u6025\u4e4b\u52a1\u3002</p><p>\u5979\u4ee5\u524d\u66fe\u7ecf\u6765\u8fc7\u4e00\u6b21\u535a\u5f97\u4e4b\u95e8\uff0c\u5927\u81f4\u8fd8\u8bb0\u5f97\u8def\u5f84\uff0c\u5e26\u7740\u743c\u6069\u4e00\u8def\u76f4\u884c\uff0c\u5230\u4e86\u5e02\u653f\u5e9c\u95e8\u53e3\u3002\u4eae\u51fa\u8eab\u4efd\uff0c\u5bf9\u5b88\u536b\u7684\u58eb\u5175\u8bf4\u660e\u6765\u610f\uff0c\u4e24\u4eba\u4fbf\u88ab\u7acb\u523b\u8bf7\u4e86\u8fdb\u53bb\u3002\u56e0\u4e3a\u8fd9\u573a\u5927\u761f\u75ab\u7684\u7f18\u6545\uff0c\u535a\u5f97\u4e4b\u95e8\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u5bf9\u6885\u83f2\u65af\u2014\u2014\u51c6\u786e\u5730\u8bf4\u662f\u5979\u80cc\u7740\u7684\u9ece\u660e\u4e4b\u77f3\u2014\u2014\u5df2\u7ecf\u76fc\u671b\u5f88\u4e45\u4e86\u3002</p><p>\u51fa\u9762\u63a5\u5f85\u7684\u662f\u8389\u62c9\u516c\u7235\uff0c\u4e00\u4f4d\u4e2d\u5e74\u5973\u6027\uff0c\u5979\u5bf9\u743c\u6069\u548c\u6885\u83f2\u65af\u4e00\u8def\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\u7684\u884c\u4e3a\u5341\u5206\u611f\u8c22\uff0c\u5e76\u5bf9\u4ed6\u4eec\u6765\u5230\u535a\u5f97\u4e4b\u95e8\u8868\u793a\u6b22\u8fce\u2014\u2014\u5f53\u7136\u8fd9\u4e9b\u5168\u662f\u7a7a\u8bdd\u5957\u8bdd\u52a0\u5e9f\u8bdd\uff0c\u6ca1\u6709\u5b9e\u9645\u610f\u4e49\u3002\u5728\u5ba2\u5957\u4e86\u4e00\u756a\u4e4b\u540e\uff0c\u8bdd\u9898\u7ec8\u4e8e\u5207\u5165\u5b9e\u8d28\u3002</p><p>\u8389\u62c9\u516c\u7235\u8bf4\uff0c\u761f\u75ab\u6beb\u65e0\u9884\u5146\u5730\u7a81\u7136\u7206\u53d1\uff0c\u5df2\u7ecf\u6301\u7eed\u8fd1\u4e24\u4e2a\u6708\uff0c\u5e73\u5747\u6bcf\u5929\u90fd\u4f1a\u6709\u4e03\u516b\u5341\u4eba\u53bb\u4e16\u2014\u2014\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6b7b\u4ea1\u4eba\u6570\u5e94\u8be5\u5df2\u7ecf\u8d85\u8fc7\u56db\u5343\u4eba\u4e86\u3002\u535a\u5f97\u4e4b\u95e8\u5168\u90e8\u4eba\u53e3\u4e5f\u4e0d\u8fc7\u56db\u4e07\u4f59\u4eba\uff0c\u5df2\u7ecf\u9661\u7136\u635f\u5931\u4e86\u5341\u5206\u4e4b\u4e00\uff0c\u5176\u4e2d\u5305\u62ec\u5f88\u591a\u9ad8\u5c42\u4eba\u5458\uff0c\u8fd9\u5bfc\u81f4\u6574\u5ea7\u57ce\u5e02\u7684\u8fd0\u4f5c\u90fd\u51e0\u4e4e\u505c\u6ede\u4e0b\u6765\u3002\u800c\u4e14\u535a\u5f97\u4e4b\u95e8\u662f\u4ee5\u8d38\u6613\u800c\u7e41\u8363\uff0c\u5982\u4eca\u761f\u75ab\u4e00\u8d77\uff0c\u5546\u4eba\u4eec\u907f\u4e4b\u552f\u6050\u4e0d\u53ca\uff0c\u6068\u4e0d\u5f97\u7ed5\u9053\u5341\u4e07\u516b\u5343\u91cc\u800c\u884c\uff0c\u987f\u65f6\u5c31\u5be5\u843d\u8870\u8d25\u4e0b\u6765\u3002</p><p>\u5176\u5b9e\u743c\u6069\u4e00\u76f4\u6709\u4e9b\u5947\u602a\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e2a\u4e16\u754c\u867d\u7136\u79d1\u6280\u6c34\u51c6\u5f88\u4f4e\uff0c\u533b\u5b66\u6c34\u51c6\u4e5f\u5f88\u5dee\uff0c\u4e0d\u6bd4\u6b27\u6d32\u4e2d\u4e16\u7eaa\u597d\u591a\u5c11\uff0c\u4f46\u9b54\u6cd5\u5374\u662f\u975e\u5e38\u53d1\u8fbe\u7684\u3002\u8fd9\u662f\u4e2a\u795e\u7947\u6797\u7acb\u7684\u4e16\u754c\uff0c\u7267\u5e08\u4eec\u53ef\u4ee5\u8fd0\u7528\u795e\u672f\u6551\u6b7b\u6276\u4f24\uff0c\u5728\u5730\u7403\u4e0a\u662f\u7531\u533b\u751f\u6765\u627f\u5f53\u7684\u5de5\u4f5c\uff0c\u5728\u8fd9\u91cc\u5219\u7531\u7267\u5e08\u6765\u4ee3\u66ff\u3002\u795e\u672f\u4e2d\u6709\u5927\u91cf\u6cbb\u7597\u3001\u5f3a\u5316\u3001\u795d\u798f\u3001\u795b\u90aa\u7684\u9b54\u6cd5\uff0c\u6bd4\u4ec0\u4e48\u533b\u5b66\u90fd\u6765\u5f97\u6709\u6548\uff0c\u636e\u8bf4\u7279\u522b\u9ad8\u9636\u7684\u795e\u672f\uff0c\u751a\u81f3\u80fd\u6709\u8d77\u6b7b\u56de\u751f\u7684\u6548\u679c\u2014\u2014\u5f53\u7136\u8fd9\u5c31\u8fd1\u4f3c\u4f20\u8bf4\u4e86\uff0c\u8c01\u4e5f\u6ca1\u5f53\u771f\u89c1\u8fc7\u3002</p><p>\u535a\u5f97\u4e4b\u95e8\u662f\u8d39\u4f26\u7684\u5927\u57ce\u5e02\uff0c\u8bba\u89c4\u6a21\u8bba\u4eba\u53e3\u80dc\u8fc7\u9634\u9b42\u57ce\u51e0\u500d\uff0c\u5404\u79cd\u795e\u7947\u7684\u6559\u4f1a\u5f53\u7136\u4f1a\u5728\u6b64\u5efa\u7acb\u795e\u6bbf\u3002\u743c\u6069\u5728\u8def\u4e0a\u4e5f\u95ee\u8fc7\u6885\u83f2\u65af\uff0c\u77e5\u9053\u5728\u535a\u5f97\u4e4b\u95e8\uff0c\u5de5\u827a\u4e0e\u953b\u9020\u4e4b\u795e\u8d21\u5fb7\u3001\u5e78\u8fd0\u5973\u795e\u6cf0\u6469\u62c9\u3001\u6d77\u6d0b\u5973\u795e\u5b89\u535a\u91cc\u8fd9\u4e09\u4f4d\u795e\u7947\u7684\u6559\u4f1a\u6700\u4e3a\u5174\u76db\uff0c\u52bf\u529b\u5e9e\u5927\uff1b\u6b64\u5916\u8fd8\u6709\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u3001\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u3001\u5546\u4e1a\u5973\u795e\u6c83\u91d1\u7b49\u4e03\u516b\u4f4d\u795e\u7947\u7684\u6559\u4f1a\uff0c\u4e5f\u90fd\u5206\u522b\u5efa\u6709\u795e\u6bbf\u3002\u5012\u662f\u6ca1\u6709\u6885\u83f2\u65af\u4fe1\u4ef0\u7684\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u7684\u795e\u6bbf\uff0c\u4e5f\u6ca1\u6709\u6697\u591c\u5973\u795e\u838e\u5c14\u7684\u795e\u6bbf\u3002</p><p>\u65e2\u7136\u6709\u5982\u6b64\u591a\u7684\u795e\u6bbf\u5728\uff0c\u795e\u804c\u4eba\u5458\u5fc5\u5b9a\u4e0d\u5c11\u3002\u867d\u8bf4\u5e76\u4e0d\u662f\u6240\u6709\u7684\u7267\u5e08\u90fd\u80fd\u65bd\u5c55\u795e\u672f\u6cbb\u75c5\u795b\u90aa\uff0c\u4f46\u4e5f\u603b\u4e0d\u81f3\u4e8e\u653e\u4efb\u761f\u75ab\u6a2a\u884c\u8fd1\u4e24\u4e2a\u6708\u800c\u675f\u624b\u65e0\u7b56\uff0c\u773c\u7741\u7741\u5730\u770b\u7740\u6bcf\u5929\u6b7b\u4eba\uff0c\u53ea\u80fd\u7b49\u6885\u83f2\u65af\u9001\u9ece\u660e\u4e4b\u77f3\u8fc7\u6765\u5427\u3002</p><p>\u4ec0\u4e48\u761f\u75ab\u8fd9\u4e48\u5389\u5bb3\u3002</p><p>\u636e\u8389\u62c9\u516c\u7235\u8bf4\uff0c\u8fd9\u573a\u761f\u75ab\u6765\u5f97\u6781\u5176\u7a81\u5140\u800c\u8fc5\u731b\uff0c\u67d3\u75c5\u8005\u4f1a\u611f\u89c9\u5230\u5168\u8eab\u75bc\u75db\u3001\u9178\u9ebb\u65e0\u529b\uff0c\u968f\u5373\u4f1a\u5728\u5341\u4e8c\u4e2a\u5c0f\u65f6\u5185\u6b7b\u53bb\u3002\u54ea\u6015\u662f\u518d\u5f3a\u58ee\u7684\u4eba\uff0c\u8bf7\u518d\u8457\u540d\u7684\u7267\u5e08\uff0c\u518d\u7528\u4ec0\u4e48\u795e\u672f\u6cbb\u7597\uff0c\u4e00\u6982\u90fd\u4e0d\u7ba1\u7528\uff0c\u53ea\u80fd\u773c\u7741\u7741\u5730\u5728\u6050\u60e7\u4e2d\u7b49\u5f85\u6b7b\u4ea1\u7684\u964d\u4e34\u3002\u800c\u4e14\u5f88\u5947\u602a\uff0c\u8fc4\u4eca\u4e3a\u6b62\uff0c\u761f\u75ab\u5e76\u6ca1\u6709\u5411\u522b\u7684\u5730\u533a\u6269\u6563\uff0c\u53ea\u5728\u8fd9\u5ea7\u57ce\u5e02\u4e2d\u8086\u8650\u3002</p><p>\u66f4\u8981\u547d\u7684\u662f\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u8fd8\u4e0d\u6e05\u695a\u8fd9\u761f\u75ab\u5230\u5e95\u662f\u901a\u8fc7\u4ec0\u4e48\u65b9\u5f0f\u4f20\u64ad\u7684\uff0c\u5426\u5219\u8fd8\u53ef\u4ee5\u4ece\u8fd9\u65b9\u9762\u7740\u624b\u60f3\u529e\u6cd5\u3002\u4e09\u4f4d\u516c\u7235\u5546\u8bae\u5230\u6700\u540e\uff0c\u6d3e\u9063\u4f7f\u8005\u53bb\u70db\u5821\uff0c\u8bf7\u90a3\u4f4d\u4ee5\u813e\u6c14\u53e4\u602a\u8457\u79f0\u7684\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u5927\u5deb\u5e08\u5360\u535c\u63a8\u7b97\uff0c\u6700\u540e\u5f97\u51fa\u7684\u7ed3\u8bba\u662f\u2026\u2026</p><p>\u201c\u8fd9\u573a\u761f\u75ab\u662f\u4eba\u4e3a\u9020\u6210\u7684\uff0c\u201d\u8389\u62c9\u516c\u7235\u8bf4\uff0c\u201c\u4f46\u66f4\u5177\u4f53\u7684\u60c5\u51b5\uff0c\u4e4c\u5c14\u5170\u7279\u5148\u751f\u4e5f\u63a8\u7b97\u4e0d\u51fa\u6765\u3002\u201d</p><p>\u77e5\u9053\u761f\u75ab\u662f\u4eba\u4e3a\u9020\u6210\uff0c\u5374\u627e\u4e0d\u5230\u8c01\u662f\u7f6a\u9b41\u7978\u9996\uff0c\u8fd9\u4f9d\u7136\u8fd8\u662f\u4e0d\u80fd\u89e3\u51b3\u95ee\u9898\u3002\u65e5\u5b50\u4e00\u5929\u4e00\u5929\u8fc7\uff0c\u6b7b\u4ea1\u7684\u4eba\u6570\u8d8a\u6765\u8d8a\u591a\uff0c\u5c45\u6c11\u4eec\u4eba\u5fc3\u60f6\u60f6\uff0c\u751f\u6015\u6b7b\u4ea1\u7684\u9634\u5f71\u843d\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u5f88\u591a\u4eba\u90fd\u5df2\u7ecf\u5916\u9003\u5176\u4ed6\u5730\u65b9\u4e86\u3002\u5982\u679c\u76ee\u524d\u8fd9\u79cd\u60c5\u5f62\u8fd8\u5f97\u4e0d\u5230\u6539\u5584\uff0c\u53ea\u8981\u518d\u8fc7\u4e00\u4e24\u4e2a\u6708\uff0c\u8fd9\u5ea7\u8457\u540d\u7684\u57ce\u5e02\u5c31\u8981\u5b8c\u5168\u53d8\u6210\u5e9f\u589f\u4e86\u3002</p><p>\u201c\u4e0d\u8fc7\u73b0\u5728\u597d\u4e86\uff0c\u201d\u8389\u62c9\u5973\u516c\u7235\u5f3a\u8c03\u8bf4\uff0c\u201c\u65e2\u7136\u6709\u4e86\u9ece\u660e\u4e4b\u77f3\uff0c\u90a3\u4e48\u81ea\u7136\u4e00\u5207\u90fd\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u8d5e\u7f8e\u6cf0\u6469\u62c9\uff0c\u201d\u5979\u5728\u80f8\u53e3\u5feb\u901f\u5212\u4e86\u4e2a\u5c0f\u5c0f\u7684\u56fe\u6848\uff0c\u201c\u613f\u5e78\u8fd0\u4e0e\u6211\u4eec\u540c\u5728\u3002\u201d</p><p>\u201c\u8d5e\u7f8e\u63d0\u5c14\u3002\u201d\u6885\u83f2\u65af\u6c89\u7740\u5730\u56de\u7b54\u3002</p><p>\u9ece\u660e\u4e4b\u77f3\u662f\u6668\u66e6\u4e4b\u795e\u521b\u9020\u7684\u5723\u7269\uff0c\u867d\u7136\u6709\u9a71\u6563\u4e00\u5207\u75be\u75c5\u761f\u75ab\u7684\u795e\u529b\uff0c\u4f46\u5374\u4e0d\u662f\u968f\u4fbf\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\u52a8\u7528\u7684\uff0c\u552f\u6709\u5170\u68ee\u5fb7\u5c14\u7684\u9ad8\u9636\u7267\u5e08\u624d\u6709\u8fd9\u79cd\u80fd\u529b\u548c\u8d44\u683c\u3002\u5f53\u7136\uff0c\u8389\u62c9\u5973\u516c\u7235\u65e9\u5c31\u5df2\u7ecf\u6d3e\u4eba\u53bb\u57ce\u4e2d\u7684\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\uff0c\u8bf7\u9996\u5e2d\u7267\u5e08\u8d1d\u7eb3\u7d22\u5148\u751f\u524d\u6765\u3002</p><p>\u8d1d\u7eb3\u7d22\u524d\u6765\uff0c\u770b\u89c1\u9ece\u660e\u4e4b\u77f3\uff0c\u5927\u559c\u8fc7\u671b\u3002\u4ed6\u8bf4\u9700\u8981\u4e3e\u884c\u4e00\u573a\u76db\u5927\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u5b8c\u5168\u6fc0\u53d1\u5723\u7269\u7684\u5723\u6d01\u795e\u529b\uff0c\u9a71\u6563\u7b3c\u7f69\u57ce\u4e2d\u7684\u761f\u75ab\u9634\u5f71\u3002\u4f46\u95ee\u9898\u5728\u4e8e\u2026\u2026</p><p>\u201c\u8fd8\u8981\u518d\u7b49\u5341\u4e09\u5929\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u4e0d\u4f46\u743c\u6069\u6709\u8fd9\u4e2a\u7591\u95ee\uff0c\u8389\u62c9\u5973\u516c\u7235\u548c\u6885\u83f2\u65af\u4e5f\u540c\u6837\u5f88\u5947\u602a\u3002\u4f46\u8d1d\u7eb3\u7d22\u7684\u89e3\u91ca\u8ba9\u5927\u5bb6\u90fd\u65e0\u8bdd\u53ef\u8bf4\uff1a\u56e0\u4e3a\u8fd9\u79cd\u5927\u578b\u7684\u796d\u7940\u4eea\u5f0f\u5fc5\u987b\u5728\u6559\u4f1a\u7684\u5723\u65e5\u4e3e\u884c\uff0c\u800c\u518d\u8fc7\u5341\u4e09\u5929\uff0c\u6b63\u662f\u590f\u81f3\u65e5\uff0c\u4e43\u662f\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7684\u5723\u65e5\u4e4b\u4e00\u3002</p><p>\u4e0d\u8fc7\u8d1d\u7eb3\u7d22\u63a5\u7740\u8bf4\uff0c\u867d\u7136\u796d\u7940\u4eea\u5f0f\u6682\u65f6\u65e0\u6cd5\u4e3e\u884c\uff0c\u761f\u75ab\u4e0d\u80fd\u5b8c\u5168\u9a71\u9664\u3002\u4f46\u53ea\u8981\u5c06\u9ece\u660e\u4e4b\u77f3\u4f9b\u5949\u5728\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u4e2d\uff0c\u81ea\u7136\u4e5f\u80fd\u76f8\u5e94\u5730\u51cf\u8f7b\u761f\u75ab\uff0c\u81f3\u5c11\u53ef\u4ee5\u4fdd\u8bc1\u65b9\u5706\u4e00\u5b9a\u533a\u57df\u5185\u7684\u7edd\u5bf9\u5b89\u5168\u2014\u2014\u5f53\u542c\u5230\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u743c\u6069\u8111\u4e2d\u5df2\u7ecf\u6d6e\u73b0\u51fa\u4e00\u526f\u573a\u666f\uff1a\u65e0\u6570\u5c45\u6c11\u62d6\u5bb6\u5e26\u53e3\u5728\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u5468\u56f4\u642d\u7740\u5e10\u7bf7\uff0c\u6253\u7740\u5730\u94fa\uff0c\u4e0d\u77e5\u9053\u7684\u8fd8\u4ee5\u4e3a\u662f\u96be\u6c11\u8425\u2026\u2026</p><p>\u597d\u5427\uff0c\u53cd\u6b63\u60c5\u51b5\u4e5f\u53ea\u80fd\u8fd9\u6837\u4e86\u3002\u8d1d\u7eb3\u7d22\u662f\u76ee\u524d\u80fd\u627e\u5230\u7684\u6700\u9ad8\u4f4d\u9636\u7684\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\uff0c\u7b97\u662f\u6743\u5a01\u2014\u2014\u539f\u672c\u8ddf\u6885\u83f2\u65af\u4e00\u8d77\u7684\u90a3\u4e2a\u7ea2\u5934\u53d1\u7684\u5766\u7eb3\u65af\u4f4d\u9636\u5012\u662f\u66f4\u9ad8\uff0c\u53ef\u60dc\u8def\u4e0a\u88ab\u4eba\u9762\u72ee\u4e71\u5200\u780d\u6b7b\u4e86\u2014\u2014\u6240\u4ee5\u5927\u5bb6\u53ea\u80fd\u542c\u4ed6\u7684\u3002</p></article>"}}}]);
