(window.webpackJsonp=window.webpackJsonp||[]).push([[1298],{1321:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u524d\u5f80\u70db\u5821",content:"<article><h2>\u7b2c\u4e8c\u8282 \u524d\u5f80\u70db\u5821</h2><p>\u8fd9\u8fb9\u4e8b\u60c5\u57fa\u672c\u7b97\u662f\u6709\u4e86\u7ed3\u679c\uff0c\u5269\u4e0b\u6765\u4e5f\u7528\u4e0d\u7740\u743c\u6069\u548c\u6885\u83f2\u65af\u64cd\u5fc3\u4e86\uff0c\u81ea\u7136\u6709\u8389\u62c9\u5973\u516c\u7235\u548c\u8d1d\u7eb3\u7d22\u7267\u5e08\u7b49\u4eba\u53bb\u5b89\u6392\u7b79\u5907\u3002\u6d88\u606f\u5f88\u5feb\u4f20\u51fa\uff0c\u743c\u6069\u90fd\u5df2\u7ecf\u542c\u5230\u5e02\u653f\u5e9c\u5916\u9762\u7684\u6b22\u547c\u58f0\u3002\u8bf4\u5f97\u51b7\u8840\u70b9\uff0c\u761f\u75ab\u6a2a\u884c\u8fd9\u4e48\u4e45\uff0c\u6bcf\u5929\u90fd\u6b7b\u4eba\uff0c\u5f00\u59cb\u6781\u5ea6\u60ca\u614c\u60f6\u6050\uff0c\u5982\u4eca\u5927\u5bb6\u90fd\u5df2\u7ecf\u6709\u4e9b\u9ebb\u6728\u4e86\uff0c\u4e00\u526f\u542c\u5929\u7531\u547d\u7684\u6d3e\u5934\u3002\u603b\u7b97\u9ece\u660e\u4e4b\u77f3\u5230\u6765\uff0c\u7b97\u662f\u6253\u4e86\u4e00\u8bb0\u5f3a\u884c\u9488\uff0c\u8ba9\u5927\u5bb6\u53c8\u770b\u5230\u4e86\u5e0c\u671b\uff0c\u5426\u5219\u4e0d\u7b49\u761f\u75ab\u628a\u6240\u6709\u4eba\u6740\u6b7b\uff0c\u81ea\u5df1\u5148\u8981\u4ece\u7cbe\u795e\u4e0a\u57ae\u4e86\u3002</p><p>\u6885\u83f2\u65af\u6765\u535a\u5f97\u4e4b\u95e8\uff0c\u5c31\u662f\u4e3a\u4e86\u9001\u9ece\u660e\u4e4b\u77f3\uff0c\u5982\u4eca\u4efb\u52a1\u5b8c\u6210\uff0c\u53ef\u4ee5\u56de\u53bb\u590d\u547d\u4e86\u3002\u743c\u6069\u6765\u6b64\u5374\u662f\u4e3a\u4e86\u627e\u4e2a\u53eb\u62c9\u6c83\u514b\u7684\u8001\u5deb\u5e08\u9001\u4fe1\u7684\uff0c\u4ed6\u672c\u6765\u51c6\u5907\u5411\u8389\u62c9\u5973\u516c\u7235\u6253\u542c\u4e00\u4e0b\uff0c\u4f46\u770b\u5bf9\u65b9\u6b63\u5fd9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u6700\u7ec8\u8fd8\u662f\u6ca1\u6709\u5f00\u53e3\u3002\u8fd9\u4e16\u754c\u4e0a\u53c8\u6ca1\u6709\u8eab\u4efd\u8bc1\u548c\u6237\u53e3\u7c3f\u8fd9\u79cd\u4e1c\u897f\uff0c\u4e00\u4e2a\u8fde\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u90fd\u627e\u4e0d\u5230\u4f4f\u5904\u7684\u8001\u5deb\u5e08\uff0c\u4e0d\u77e5\u9053\u8eb2\u5728\u54ea\u91cc\u9690\u5c45\uff0c\u8389\u62c9\u5973\u516c\u7235\u8eab\u5c45\u9ad8\u4f4d\uff0c\u65e5\u7406\u4e07\u673a\uff0c\u4f30\u8ba1\u4e5f\u4e0d\u4f1a\u77e5\u9053\uff0c\u8fd8\u662f\u81ea\u5df1\u6162\u6162\u6253\u542c\u597d\u4e86\u3002\u5c31\u6015\u8001\u5bb6\u4f19\u8eab\u4f53\u865a\u5f31\uff0c\u5728\u8fd9\u573a\u5927\u761f\u75ab\u4e2d\u65e9\u65e9\u6302\u4e86\uff0c\u90a3\u53ef\u771f\u662f\u9ebb\u70e6\u3002</p><p>\u8389\u62c9\u516c\u7235\u5b89\u6392\u743c\u6069\u548c\u6885\u83f2\u65af\u4f4f\u4e0b\u4f11\u606f\u2014\u2014\u4e3a\u4e86\u5b89\u5168\u8d77\u89c1\uff0c\u76f4\u63a5\u5c31\u5b89\u6392\u8fdb\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u91cc\u4e86\uff0c\u6258\u5e87\u4e8e\u9ece\u660e\u4e4b\u77f3\uff0c\u514d\u5f97\u67d3\u4e0a\u761f\u75ab\u4e00\u547d\u545c\u547c\u3002\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u743c\u6069\u89c9\u5f97\u8389\u62c9\u516c\u7235\u771f\u662f\u4e2a\u597d\u4eba\uff0c\u8003\u8651\u5341\u5206\u5468\u5230\u3002\u6885\u83f2\u65af\u662f\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u4f4f\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u5012\u4e5f\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u4e8b\u60c5\u5c31\u8fd9\u4e48\u5b9a\u4e0b\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u679c\u7136\u5982\u743c\u6069\u6240\u6599\uff0c\u9ece\u660e\u4e4b\u77f3\u7684\u6d88\u606f\u4e00\u4f20\u51fa\uff0c\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u7684\u5468\u56f4\u7acb\u523b\u6324\u6ee1\u4e86\u8fd8\u6ca1\u9003\u8d70\u7684\u5e02\u6c11\u4eec\u3002\u5730\u4f4d\u9ad8\u8005\u76f4\u63a5\u5c31\u642c\u8fdb\u4e86\u795e\u6bbf\u91cc\uff0c\u8389\u62c9\u516c\u7235\u751a\u81f3\u628a\u6574\u4e2a\u5e02\u653f\u5e9c\u7684\u73ed\u5b50\u90fd\u5e26\u8fc7\u6765\u4e86\uff0c\u5c31\u5728\u795e\u6bbf\u540e\u9662\u4e2d\u529e\u516c\u3002</p><p>\u4eba\u4eba\u90fd\u662f\u6015\u6b7b\u7684\uff0c\u5c24\u5176\u662f\u5728\u5df2\u7ecf\u770b\u5230\u751f\u5b58\u7684\u5e0c\u671b\u65f6\u3002</p><p>\u6240\u6709\u7684\u884c\u653f\u5de5\u4f5c\u4eba\u5458\u90fd\u96c6\u4e2d\u5230\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u91cc\u6765\u4e86\uff0c\u6240\u6709\u4eba\u90fd\u5728\u5fd9\u5fd9\u788c\u788c\uff0c\u7b79\u5907\u7740\u590f\u81f3\u65e5\u5c06\u8981\u4e3e\u884c\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u9664\u4e86\u6885\u83f2\u65af\u548c\u743c\u6069\u3002\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u4e0d\u719f\u6089\u60c5\u51b5\uff0c\u4e5f\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\uff0c\u53e6\u4e00\u65b9\u9762\u56e0\u4e3a\u662f\u5ba2\u4eba\uff0c\u5c31\u7b97\u4eba\u624b\u518d\u7f3a\u4e4f\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u6253\u4e3b\u610f\u5230\u4ed6\u4eec\u5934\u4e0a\u3002</p><p>\u6885\u83f2\u65af\u5bf9\u6b64\u65e0\u6240\u8c13\uff0c\u5979\u6bcf\u65e5\u7684\u65e5\u5e38\u5b89\u6392\u5c31\u662f\u5403\u996d\u3001\u7761\u89c9\u3001\u7ec3\u5251\u3001\u770b\u4e66\u3002\u5f88\u591a\u65f6\u5019\uff0c\u5f53\u743c\u6069\u770b\u89c1\u5979\u4e00\u88ad\u4fbf\u88c5\uff0c\u5750\u5728\u82b1\u56ed\u7684\u957f\u6905\u4e0a\u5b89\u5b89\u9759\u9759\u6367\u7740\u4e00\u672c\u4e66\u7684\u65f6\u5019\uff0c\u90fd\u604d\u7136\u6709\u4e00\u79cd\u9519\u89c9\uff0c\u4eff\u4f5b\u770b\u89c1\u7684\u4e0d\u662f\u90a3\u4f4d\u66fe\u7ecf\u6325\u821e\u94f6\u5251\u6240\u5411\u65e0\u654c\u7684\u5973\u5723\u6b66\u58eb\u3002</p><p>\u743c\u6069\u81ea\u7136\u66f4\u65e0\u6240\u8c13\uff0c\u4ed6\u6b63\u597d\u8d81\u8fd9\u4e2a\u7a7a\u95f2\u953b\u70bc\u63d0\u5347\u81ea\u5df1\u7684\u9b54\u6cd5\u6280\u827a\u3002\u51fa\u95e8\u8fd1\u4e24\u4e2a\u6708\uff0c\u4e00\u76f4\u5728\u6c99\u6f20\u548c\u8def\u4e0a\u6298\u817e\uff0c\u57fa\u672c\u6ca1\u6709\u591a\u5c11\u5b66\u4e60\u7684\u65f6\u95f4\uff0c\u4e0d\u4f46\u6ca1\u4ec0\u4e48\u8fdb\u6b65\uff0c\u7b80\u76f4\u90fd\u9690\u7ea6\u6709\u4e9b\u9000\u6b65\u4e86\u3002\u597d\u5728\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\u57fa\u7840\u6559\u80b2\u624e\u5b9e\uff0c\u7a0d\u52a0\u6e29\u4e60\uff0c\u5f88\u5feb\u53c8\u6062\u590d\u8fc7\u6765\u3002</p><p>\u4ed6\u751a\u81f3\u5df2\u7ecf\u9690\u9690\u7ea6\u7ea6\u5730\u611f\u89c9\u81ea\u5df1\u5c06\u8981\u89e6\u6478\u5230\u9b54\u7f51\u7684\u66f4\u6df1\u4e00\u5c42\uff0c\u611f\u89c9\u5230\u81ea\u5df1\u5f88\u5feb\u5c06\u4f1a\u89c1\u8bc6\u5230\u66f4\u795e\u79d8\u66f4\u7384\u5999\u83ab\u6d4b\u7684\u529b\u91cf\u2014\u2014\u4f46\u8fd9\u4e9b\u5e76\u4e0d\u5c31\u610f\u5473\u7740\u66f4\u5f3a\u5927\u7684\u9b54\u6cd5\u3002</p><p>\u5deb\u5e08\u7684\u529b\u91cf\u63d0\u5347\uff0c\u6709\u4e24\u4e2a\u65b9\u9762\u3002\u5176\u4e00\u662f\u89e6\u6478\u9b54\u7f51\u7684\u66f4\u6df1\u5c42\u6b21\uff0c\u6c72\u53d6\u66f4\u5f3a\u5927\u7684\u529b\u91cf\uff1b\u5176\u4e8c\u5219\u662f\u5b66\u4e60\u638c\u63e1\u66f4\u591a\u66f4\u9ad8\u660e\u7684\u9b54\u6cd5\uff0c\u8fd9\u4e24\u8005\u662f\u76f8\u4e92\u4f5c\u7528\u7684\u3002\u7a7a\u81ea\u80fd\u89e6\u6478\u5230\u6df1\u5c42\u9b54\u7f51\uff0c\u6c72\u53d6\u5f3a\u5927\u529b\u91cf\uff0c\u5374\u6ca1\u6709\u638c\u63e1\u76f8\u5e94\u5c42\u7ea7\u7684\u9b54\u6cd5\uff0c\u90a3\u5c31\u50cf\u662f\u660e\u660e\u6709\u4e86\u9ad8\u7ea7\u7684\u539f\u6750\u6599\uff0c\u624b\u91cc\u5374\u7f3a\u5c11\u914d\u65b9\uff0c\u4f9d\u65e7\u53d1\u6325\u4e0d\u51fa\u6548\u7528\u6765\u3002\u9664\u975e\u80fd\u81ea\u521b\u65b0\u914d\u65b9\uff0c\u90a3\u5c31\u4e0d\u662f\u5bfb\u5e38\u4eba\u6240\u80fd\u529e\u5230\u7684\u4e86\uff0c\u975e\u7edd\u9876\u5929\u624d\u52a0\u9876\u7ea7\u5927\u5deb\u5e08\u6c34\u51c6\u4e0d\u53ef\u3002</p><p>\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\u56fe\u4e66\u9986\u91cc\uff0c\u5b66\u751f\u7684\u6743\u9650\u4ec5\u4ec5\u80fd\u6284\u5f55\u5230\u4f4e\u9636\u6cd5\u672f\u3002\u743c\u6069\u7684\u9b54\u6cd5\u4e66\u91cc\u8bb0\u8f7d\u7684\u6cd5\u672f\u4e5f\u4e0d\u591a\uff0c\u57fa\u672c\u90fd\u5df2\u7ecf\u5b66\u8fc7\u4e86\u3002\u8981\u60f3\u8fdb\u4e00\u6b65\u63d0\u5347\uff0c\u4ed6\u9700\u8981\u4e00\u4e2a\u5965\u672f\u56fe\u4e66\u9986\uff0c\u5b66\u4e60\u66f4\u9ad8\u660e\u7684\u6cd5\u672f\uff1b\u6216\u8005\u6709\u4e00\u4f4d\u524d\u8f88\u5deb\u5e08\u6307\u70b9\u3002</p><p>\u535a\u5f97\u4e4b\u95e8\u57ce\u5e02\u91cc\u6709\u8fd9\u6837\u7684\u56fe\u4e66\u9986\u4e48\uff1f</p><p>\u5f53\u4ed6\u5411\u795e\u6bbf\u7684\u7267\u5e08\u63d0\u51fa\u8fd9\u4e2a\u95ee\u9898\u7684\u65f6\u5019\uff0c\u5bf9\u65b9\u6447\u4e86\u6447\u5934\u3002\u201c\u6ca1\u6709\uff0c\u201d\u7267\u5e08\u8bf4\uff0c\u201c\u57ce\u91cc\u6709\u56fe\u4e66\u9986\uff0c\u4f46\u6050\u6015\u4e0d\u4f1a\u6709\u4f60\u9700\u8981\u7684\u4e1c\u897f\u3002\u201d</p><p>\u518d\u4ed4\u7ec6\u4e00\u6253\u542c\uff0c\u624d\u77e5\u9053\u5728\u535a\u5f97\u4e4b\u95e8\u8fd9\u5ea7\u57ce\u5e02\u91cc\uff0c\u5deb\u5e08\u5c45\u7136\u662f\u975e\u5e38\u7f55\u89c1\u7684\u5b58\u5728\uff0c\u51e0\u4e4e\u627e\u4e0d\u51fa\u51e0\u4e2a\uff0c\u5f53\u7136\u4e5f\u5c31\u4e0d\u4f1a\u6709\u4f9b\u5deb\u5e08\u4f7f\u7528\u7684\u56fe\u4e66\u9986\u4e86\u3002</p><p>\u8fd9\u5012\u4e5f\u7f62\u4e86\uff0c\u743c\u6069\u867d\u7136\u5728\u9634\u9b42\u57ce\u8fd9\u79cd\u5deb\u5e08\u904d\u5730\u7684\u5730\u65b9\u5446\u4e60\u60ef\u4e86\uff0c\u4f46\u81f3\u5c11\u4e5f\u5728\u963f\u5fb7\u5df4\u57ce\u505c\u7559\u8fc7\uff0c\u77e5\u9053\u5728\u7269\u8d28\u754c\u7684\u57ce\u5e02\u91cc\u5deb\u5e08\u672a\u5fc5\u5982\u9634\u9b42\u57ce\u4e00\u6837\u666e\u904d\u3002\u4f46\u5982\u679c\u8fd9\u4e48\u8bf4\u7684\u8bdd\u2026\u2026</p><p>\u201c\u90a3\uff0c\u5982\u679c\u6211\u60f3\u5bfb\u627e\u4e00\u4f4d\u8001\u5deb\u5e08\uff0c\u4f46\u9664\u4e86\u540d\u5b57\u5916\u4e00\u65e0\u6240\u77e5\uff0c\u8fd9\u5e94\u8be5\u5982\u4f55\u7740\u624b\u5462\uff1f\u201d</p><p>\u743c\u6069\u5411\u5bf9\u65b9\u865a\u5fc3\u8bf7\u6559\uff0c\u4ed6\u7559\u4e86\u70b9\u5fc3\u773c\uff0c\u6ca1\u6709\u8bf4\u51fa\u62c9\u6c83\u514b\u7684\u540d\u5b57\u3002\u9634\u9b42\u57ce\u5982\u4eca\u540d\u58f0\u5f88\u6076\u52a3\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u663e\u7136\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u9b3c\u77e5\u9053\u4ed6\u8981\u6765\u627e\u7684\u8fd9\u4e2a\u201c\u62c9\u6c83\u514b\u201d\u662f\u4ec0\u4e48\u8d27\u8272\u3002\u60c5\u51b5\u4e0d\u660e\uff0c\u8fd8\u662f\u4e0d\u8981\u8d38\u7136\u8bf4\u51fa\u6765\uff0c\u514d\u5f97\u62db\u6765\u9ebb\u70e6\u3002</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u7267\u5e08\u60f3\u4e86\u60f3\uff0c\u4ed6\u5012\u4e5f\u6ca1\u95ee\u8001\u5deb\u5e08\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u201c\u8fd9\u91cc\u786e\u5b9e\u662f\u6781\u5c11\u6709\u5deb\u5e08\u7684\u2026\u2026\u800c\u4e14\u60a8\u77e5\u9053\u7684\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u8fd9\u761f\u75ab\u4e00\u8d77\uff0c\u4e00\u5207\u90fd\u5f04\u5f97\u4e71\u4e03\u516b\u7cdf\u7684\u3002\u73b0\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u60f3\u627e\u4e2a\u6ca1\u6709\u8be6\u7ec6\u8d44\u6599\u7684\u4eba\u6050\u6015\u662f\u5f88\u96be\u7684\uff0c\u800c\u4e14\u60a8\u4e5f\u4e0d\u65b9\u4fbf\u51fa\u95e8\u554a\uff0c\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u201c\u6211\u770b\uff0c\u60a8\u4e0d\u5982\u5148\u628a\u8fd9\u4ef6\u4e8b\u60c5\u653e\u4e00\u653e\uff0c\u7b49\u8fd9\u644a\u5b50\u4e8b\u60c5\u5b8c\u4e86\uff0c\u518d\u8bf7\u8389\u62c9\u516c\u7235\u5e2e\u5fd9\u770b\u770b\u5427\u3002\u201d</p><p>\u5fc5\u987b\u8bf4\uff0c\u7267\u5e08\u7684\u5efa\u8bae\u5408\u60c5\u5408\u7406\u3002\u8fd9\u79cd\u761f\u75ab\u6a2a\u884c\u7684\u65f6\u671f\uff0c\u8fd8\u662f\u8eb2\u5728\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u91cc\u6bd4\u8f83\u5b89\u5168\uff0c\u6709\u5723\u7269\u5e87\u62a4\u7740\u3002\u8d38\u7136\u8d70\u51fa\u53bb\uff0c\u53ea\u6015\u6b7b\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u6b7b\u7684\u3002</p><p>\u201c\u4e0d\u8fc7\uff0c\u201d\u7267\u5e08\u63a5\u7740\u53c8\u8bf4\uff0c\u201c\u60a8\u8981\u627e\u7684\u90a3\u4f4d\u8001\u5deb\u5e08\uff0c\u4ed6\u786e\u5b9a\u662f\u4f4f\u5728\u8fd9\u5ea7\u57ce\u91cc\u5417\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u53ea\u77e5\u9053\u5728\u8fd9\u9644\u8fd1\uff0c\u672a\u5fc5\u5728\u57ce\u91cc\u3002\u201d\u4ed6\u56de\u7b54\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u5f53\u65f6\u5c31\u662f\u8fd9\u4e48\u8bf4\u7684\u3002</p><p>\u201c\u6211\u731c\u60f3\uff0c\u60a8\u53ef\u4ee5\u7b49\u761f\u75ab\u5e73\u606f\u4e86\uff0c\u53bb\u70db\u5821\u78b0\u78b0\u8fd0\u6c14\uff0c\u201d\u7267\u5e08\u70ed\u5fc3\u5730\u5efa\u8bae\u8bf4\uff0c\u201c\u5f88\u591a\u5deb\u5e08\u90fd\u5c45\u4f4f\u5728\u90a3\u91cc\uff0c\u6216\u8bb8\u5176\u4e2d\u5c31\u6709\u60a8\u8981\u627e\u7684\u4eba\u3002\u800c\u4e14\u60a8\u521a\u624d\u4e0d\u662f\u8be2\u95ee\u56fe\u4e66\u9986\u5417\uff0c\u70db\u5821\u5c31\u6b63\u662f\u5927\u9646\u9996\u5c48\u4e00\u6307\u7684\u56fe\u4e66\u9986\u554a\uff0c\u65e0\u8bba\u60a8\u9700\u8981\u4ec0\u4e48\u8d44\u6599\uff0c\u90a3\u91cc\u90fd\u80fd\u63d0\u4f9b\u2026\u2026\u4e0d\u8fc7\uff0c\u6536\u8d39\u975e\u5e38\u6602\u8d35\uff0c\u60a8\u8981\u6709\u5fc3\u7406\u51c6\u5907\u3002\u201d</p><p>\u743c\u6069\u8fd9\u662f\u7b2c\u4e8c\u6b21\u542c\u4eba\u63d0\u8d77\u70db\u5821\u4e86\u3002\u7b2c\u4e00\u6b21\u662f\u8389\u62c9\u516c\u7235\u5728\u4ecb\u7ecd\u60c5\u51b5\u7684\u65f6\u5019\uff0c\u8bf4\u66fe\u7ecf\u6d3e\u9063\u4f7f\u8005\u53bb\u70db\u5821\uff0c\u8bf7\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u5927\u5deb\u5e08\u5360\u535c\u3002</p><p>\u201c\u4e4c\u5c14\u5170\u7279\u5148\u751f\u554a\uff0c\u201d\u7267\u5e08\u8c28\u614e\u5730\u8bc4\u4ef7\u7740\uff0c\u201c\u4ed6\u662f\u70db\u5821\u7684\u6700\u9ad8\u9886\u8896\uff0c\u4e00\u4f4d\u975e\u5e38\u8457\u540d\u7684\u9884\u8a00\u5e08\uff0c\u8fd8\u6709\u535a\u5b66\u58eb\u7684\u5934\u8854\u3002\u5443\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u8bf7\u5141\u8bb8\u6211\u7ed9\u4f60\u4e00\u4e2a\u8bda\u6073\u7684\u5efa\u8bae\uff0c\u5982\u679c\u60a8\u6709\u673a\u4f1a\u53bb\u70db\u5821\uff0c\u5e76\u4e14\u6709\u673a\u4f1a\u80fd\u548c\u4ed6\u5171\u8fdb\u665a\u9910\uff0c\u60a8\u4e00\u5b9a\u4e0d\u8981\u62d2\u7edd\u56de\u7b54\u4ed6\u7684\u95ee\u9898\u2014\u2014\u4efb\u4f55\u95ee\u9898\u90fd\u4e0d\u8981\u62d2\u7edd\u56de\u7b54\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u201d\u7267\u5e08\u8038\u8038\u80a9\uff0c\u201c\u4ed6\u603b\u662f\u559c\u6b22\u5728\u665a\u9910\u65f6\u63d0\u4e00\u4e9b\u532a\u5937\u6240\u601d\u7684\u95ee\u9898\uff0c\u800c\u5982\u679c\u4f60\u62d2\u7edd\u56de\u7b54\uff0c\u751a\u81f3\u53ea\u662f\u7a0d\u7a0d\u6d41\u9732\u51fa\u72b9\u8c6b\u7684\u610f\u601d\uff0c\u4ed6\u5c31\u53ef\u80fd\u4f1a\u5927\u53d1\u813e\u6c14\uff0c\u628a\u4f60\u8d76\u51fa\u70db\u5821\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u542c\u8d77\u6765\u8fd9\u662f\u4e2a\u813e\u6c14\u53e4\u602a\u7684\u8001\u5bb6\u4f19\uff0c\u5e0c\u671b\u81ea\u5df1\u8981\u627e\u7684\u90a3\u4e2a\u62c9\u6c83\u514b\u4e0d\u662f\u8fd9\u79cd\u7c7b\u578b\uff0c\u5426\u5219\u53ef\u5c31\u6709\u5f97\u5934\u75bc\u4e86\u3002</p><p>\u603b\u4e4b\uff0c\u4ece\u7267\u5e08\u53e3\u4e2d\uff0c\u743c\u6069\u5f97\u5230\u4e86\u4e00\u4e9b\u6709\u4ef7\u503c\u7684\u4fe1\u606f\uff0c\u6700\u91cd\u8981\u7684\u5c31\u662f\u201c\u70db\u5821\u201d\u8fd9\u4e2a\u5927\u56fe\u4e66\u9986\u3002\u4ed6\u7ffb\u4e86\u7ffb\u5730\u56fe\uff0c\u53d1\u73b0\u8ddd\u79bb\u535a\u5f97\u4e4b\u95e8\u4e0d\u8fdc\uff0c\u5750\u9a6c\u8f66\u6700\u591a\u4e24\u5929\u5c31\u5230\uff0c\u5982\u679c\u5feb\u7684\u8bdd\u6e05\u6668\u51fa\u53d1\uff0c\u5f53\u5929\u665a\u4e0a\u90fd\u53ef\u80fd\u5230\u8fbe\u3002</p><p>\u4ed6\u8003\u8651\u662f\u7b49\u761f\u75ab\u7ed3\u675f\u4e86\u518d\u53bb\uff0c\u8fd8\u662f\u73b0\u5728\u5c31\u52a8\u8eab\u3002\u60f3\u6765\u60f3\u53bb\uff0c\u6700\u540e\u8fd8\u662f\u51b3\u5b9a\u660e\u5929\u5c31\u51fa\u53d1\uff0c\u5728\u8fd9\u91cc\u5446\u7740\u65e0\u4e8b\u53ef\u505a\uff0c\u5b9e\u5728\u65e0\u804a\u5f97\u5f88\uff0c\u4ed6\u53c8\u4e0d\u559c\u6b22\u5435\u95f9\uff0c\u6bcf\u5929\u770b\u5230\u795e\u6bbf\u91cc\u4eba\u6765\u4eba\u5f80\uff0c\u795e\u6bbf\u5916\u7199\u7199\u6518\u6518\uff0c\u5c31\u4e0d\u514d\u6709\u4e9b\u5934\u75bc\u3002\u70db\u5821\u867d\u7136\u8ddd\u79bb\u535a\u5f97\u4e4b\u95e8\u5f88\u8fd1\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u67d3\u4e0a\u761f\u75ab\uff0c\u5446\u5728\u90a3\u91cc\u4e5f\u5b89\u5168\u5f97\u5f88\u3002\u6b63\u597d\u770b\u770b\u4e66\uff0c\u7814\u7a76\u9b54\u6cd5\uff0c\u987a\u4fbf\u6253\u63a2\u4e00\u4e0b\u8001\u5deb\u5e08\u62c9\u6c83\u514b\u7684\u6d88\u606f\uff0c\u53ef\u8c13\u4e00\u4e3e\u4e24\u5f97\u3002</p><p>\u7267\u5e08\u8bf4\u70db\u5821\u6536\u8d39\u9ad8\uff0c\u8fd9\u4e2a\u743c\u6069\u5012\u4e0d\u62c5\u5fc3\uff0c\u4ed6\u5982\u4eca\u53e3\u888b\u4e5f\u7b97\u6ee1\u4e86\uff0c\u603b\u4e0d\u81f3\u4e8e\u8fde\u56fe\u4e66\u9986\u7684\u95e8\u7968\u90fd\u4ed8\u4e0d\u8d77\uff1b\u81f3\u4e8e\u90a3\u4e2a\u4e4c\u5c14\u5170\u7279\u8001\u5bb6\u4f19\uff0c\u65e2\u7136\u813e\u6c14\u53e4\u602a\uff0c\u81ea\u5df1\u8eb2\u7740\u4e0d\u62db\u60f9\u5c31\u662f\u3002\u4e0d\u8fc7\u8fd9\u4e00\u8d9f\u53bb\u4e86\uff0c\u81f3\u5c11\u8981\u7b49\u8fd9\u8fb9\u796d\u7940\u4eea\u5f0f\u5b8c\u6210\uff0c\u761f\u75ab\u5e73\u606f\u4e86\u4ed6\u624d\u4f1a\u56de\u6765\uff0c\u4e2d\u95f4\u6709\u5341\u6765\u5929\u7684\u65f6\u95f4\u5462\uff0c\u96be\u9053\u8981\u4e00\u4e2a\u4eba\u5b64\u5355\u5bc2\u5bde\u4e48\u3002</p><p>\u81ea\u7136\u5f97\u8981\u628a\u6885\u83f2\u65af\u5e26\u53bb\u3002</p><p>\u4ed6\u8d70\u5230\u6885\u83f2\u65af\u623f\u95f4\u7684\u95e8\u53e3\uff0c\u6572\u4e86\u6572\u95e8\u3002</p><p>\u201c\u8bf7\u8fdb\u3002\u201d</p><p>\u5c11\u5973\u6b63\u9760\u7740\u5e8a\u5934\u770b\u4e66\uff0c\u5979\u7a7f\u7740\u6de1\u84dd\u8272\u7684\u886c\u886b\uff0c\u6de1\u91d1\u8272\u7684\u77ed\u53d1\u5728\u957f\u9014\u65c5\u884c\u4e2d\u4e0d\u77e5\u4e0d\u89c9\u53d8\u957f\u4e86\uff0c\u5df2\u7ecf\u5782\u5230\u80a9\u5934\uff0c\u906e\u4f4f\u4e86\u5c0f\u534a\u8fb9\u8138\u3002\u5979\u73b0\u5728\u7684\u6a21\u6837\u770b\u8d77\u6765\u5b89\u9759\u800c\u4e56\u5de7\uff0c\u4eff\u4f5b\u8fd8\u53ea\u662f\u4e2a\u7a1a\u6c14\u672a\u8131\u7684\u5c0f\u5973\u5b69\u3002</p><p>\u8fd8\u4e0d\u77e5\u9053\u5979\u7684\u5e74\u9f84\u5462\uff0c\u743c\u6069\u60f3\u3002</p><p>\u95ee\u5973\u6027\u7684\u5e74\u9f84\uff0c\u8fd9\u662f\u6bd4\u8f83\u5931\u793c\u7684\u884c\u4e3a\uff0c\u9664\u975e\u662f\u975e\u5e38\u4eb2\u8fd1\u7684\u4eba\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u5173\u7cfb\u6709\u4e9b\u5fae\u5999\uff0c\u4ed6\u4e5f\u4e00\u76f4\u6ca1\u6709\u95ee\u8fc7\uff0c\u4f46\u731c\u6d4b\u8d77\u6765\uff0c\u4f30\u8ba1\u4e5f\u5c31\u53ea\u548c\u81ea\u5df1\u5e74\u9f84\u76f8\u5f53\uff0c\u5728\u5341\u4e94\u5c81\u5de6\u53f3\uff0c\u4e0d\u4f1a\u592a\u5927\u3002</p><p>\u4ed6\u544a\u8bc9\u6885\u83f2\u65af\uff0c\u6253\u7b97\u53bb\u70db\u5821\u4e00\u8d9f\u3002\u5c11\u5973\u9759\u9759\u5730\u542c\u5b8c\uff0c\u6ca1\u6709\u505a\u4efb\u4f55\u8bc4\u8bba\uff0c\u201c\u4f60\u8981\u6211\u548c\u4f60\u4e00\u8d77\u53bb\uff1f\u201d\u5979\u6700\u540e\u95ee\u3002</p><p>\u201c\u5514\uff0c\u6211\u662f\u6bd4\u8f83\u5e0c\u671b\u2026\u2026\u201d</p><p>\u201c\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\uff1f\u201d\u5979\u76f4\u63a5\u95ee\u3002</p><p>\u201c\u6211\u60f3\u660e\u5929\u65e9\u4e0a\u5c31\u51fa\u53d1\u3002\u201d</p><p>\u6885\u83f2\u65af\u70b9\u70b9\u5934\uff0c\u8868\u793a\u77e5\u9053\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4f4e\u4e0b\u5934\u7ee7\u7eed\u770b\u4e66\u3002\u743c\u6069\u7ad9\u4e86\u4e00\u4f1a\uff0c\u9000\u4e86\u51fa\u53bb\uff0c\u987a\u624b\u628a\u95e8\u5e26\u4e0a\u3002</p></article>"}}}]);
