(window.webpackJsonp=window.webpackJsonp||[]).push([[130],{152:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u4ea1\u7075\uff1f",content:"<article><h2>\u7b2c\u4e09\u8282 \u4ea1\u7075\uff1f</h2><p>\u743c\u6069\u4e0d\u77e5\u9053\u79d1\u6069\u6b64\u4e3e\u4f55\u610f\uff0c\u4f46\u4f5c\u4e3a\u540c\u884c\uff0c\u8d1d\u7eb3\u7d22\u5374\u662f\u660e\u767d\u7684\u3002\u201c\u4ea1\u7075\uff1f\u201d\u4ed6\u60ca\u53eb\u7740\uff0c\u8138\u8272\u6709\u4e9b\u96be\u770b\u3002\u4ed6\u5012\u4e0d\u662f\u5bb3\u6015\uff0c\u800c\u662f\u56e0\u4e3a\u8eab\u4e3a\u6668\u66e6\u4e4b\u795e\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u7406\u5f53\u662f\u4ea1\u7075\u7684\u5929\u7136\u514b\u661f\uff0c\u8fd9\u7b97\u662f\u4ed6\u7684\u4e13\u4e1a\u8303\u7574\uff0c\u7ed3\u679c\u4ed6\u5374\u6ca1\u6709\u5bdf\u89c9\u5230\u8ff9\u8c61\uff0c\u53cd\u5012\u8ba9\u5546\u4e1a\u5973\u795e\u7684\u7267\u5e08\u62d4\u4e86\u5934\u7b79\uff0c\u8fd9\u53ef\u592a\u4e22\u9762\u5b50\u4e86\u3002</p><p>\u88ab\u8d1d\u7eb3\u7d22\u8fd9\u4e48\u4e00\u53eb\uff0c\u743c\u6069\u4e5f\u660e\u767d\u8fc7\u6765\u3002\u79d1\u6069\u7684\u610f\u601d\uff0c\u662f\u8bf4\u5bf9\u9762\u90a3\u53ea\u53cc\u5934\u5de8\u9b54\u662f\u4e2a\u4ea1\u7075\uff1f</p><p>\u4f46\u5b83\u53ef\u5b9e\u5728\u4e0d\u50cf\u4e2a\u4ea1\u7075\u554a\u2026\u2026\u81f3\u5c11\u4ea1\u7075\u5c31\u4ece\u6ca1\u542c\u8bf4\u8fd8\u80fd\u543c\u53eb\u5f97\u8fd9\u4e48\u4e2d\u6c14\u5341\u8db3\u7684\u3002</p><p>\u4f46\u79d1\u6069\u521a\u624d\u4f7f\u7528\u7684\u662f\u6c83\u91d1\u7267\u5e08\u72ec\u6709\u7684\u91d1\u5e01\u5360\u535c\u672f\uff0c\u7406\u5f53\u4e0d\u4f1a\u51fa\u9519\u3002\u65e2\u7136\u91d1\u5e01\u4e0a\u73b0\u51fa\u9ab7\u9ac5\u5934\u6765\uff0c\u8bf4\u660e\u5bf9\u65b9\u80af\u5b9a\u662f\u4e2a\u4ea1\u7075\u2014\u2014\u5c31\u7b97\u5b8c\u5168\u4e0d\u50cf\uff0c\u90a3\u4e5f\u662f\u4e2a\u4f2a\u88c5\u5f97\u975e\u5e38\u597d\u7684\u4ea1\u7075\u3002</p><p>\u65e2\u7136\u77e5\u9053\u5bf9\u65b9\u662f\u4e2a\u4ea1\u7075\uff0c\u90a3\u5c31\u4e00\u5207\u597d\u529e\u4e86\u3002\u8fd9\u91cc\u6709\u4e24\u4e2a\u7267\u5e08\uff0c\u5176\u4e2d\u4e00\u4e2a\u8fd8\u662f\u6668\u66e6\u4e4b\u795e\u7684\u4fe1\u5f92\uff0c\u5bf9\u4ed8\u522b\u7684\u6216\u8bb8\u52c9\u5f3a\uff0c\u5bf9\u4ed8\u4ea1\u7075\u6b63\u662f\u62ff\u624b\u597d\u620f\u3002</p><p>\u6216\u8bb8\u662f\u4e3a\u4e86\u63a9\u76d6\u521a\u624d\u5c45\u7136\u6ca1\u6709\u7b2c\u4e00\u4e2a\u53d1\u73b0\u4ea1\u7075\u7684\u758f\u6f0f\uff0c\u8d1d\u7eb3\u7d22\u6c14\u52bf\u6c79\u6c79\u5730\u51b2\u4e0a\u524d\u53bb\u3002\u5f53\u8ddd\u79bb\u62c9\u8fd1\u5230\u4e00\u5b9a\u7a0b\u5ea6\uff0c\u4ed6\u4fbf\u89c9\u5bdf\u5230\u4e86\u82e5\u6709\u82e5\u65e0\u7684\u4e00\u4e1d\u6b7b\u4ea1\u6c14\u606f\uff1b\u8eab\u4e3a\u6668\u66e6\u4e4b\u795e\u7684\u7267\u5e08\uff0c\u4ed6\u5bf9\u6b64\u7279\u522b\u654f\u611f\u3002</p><p>\u8fd9\u5de8\u9b54\u679c\u7136\u662f\u4e2a\u4f2a\u88c5\u5f97\u597d\u7684\u4ea1\u7075\uff01</p><p>\u6885\u83f2\u65af\u548c\u9686\u5947\u8fd8\u5728\u548c\u5de8\u9b54\u7f20\u6597\uff0c\u4ed6\u4eec\u7684\u5251\u548c\u62f3\u5934\u5df2\u7ecf\u65e0\u6570\u6b21\u51fb\u4e2d\u5bf9\u65b9\uff0c\u4f46\u5c31\u662f\u65e0\u6cd5\u5f7b\u5e95\u6253\u5012\u8fd9\u53ea\u602a\u7269\u3002\u5de8\u9b54\u7684\u4e24\u4e2a\u8111\u888b\u4e2d\u4e0d\u65ad\u55b7\u51fa\u70c8\u7130\u6765\uff0c\u95f4\u6216\u4ece\u6307\u5c16\u8ff8\u51fa\u7403\u72b6\u95ea\u7535\uff0c\u56e0\u4e3a\u8ddd\u79bb\u8d34\u8fd1\uff0c\u51e0\u6b21\u5dee\u70b9\u5c06\u6885\u83f2\u65af\u548c\u9686\u5947\u51fb\u4e2d\uff0c\u5b83\u6765\u6765\u56de\u56de\u4f3c\u4e4e\u4e5f\u5c31\u53ea\u4f1a\u8fd9\u4e24\u62db\uff0c\u522b\u65e0\u5176\u4ed6\u672c\u4e8b\uff0c\u4f46\u4ed7\u7740\u94a2\u7b4b\u94c1\u9aa8\uff0c\u5374\u662f\u7a33\u7a33\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002</p><p>\u590f\u62c9\u975e\u63a5\u8fde\u53d1\u5c04\u706b\u7130\u77e2\u52a9\u653b\uff0c\u5de8\u9b54\u7684\u76ae\u8089\u4e00\u5757\u5757\u88ab\u70e7\u7126\u3001\u8131\u843d\uff0c\u5e76\u4e14\u6ca1\u6709\u518d\u751f\u957f\u51fa\u6765\uff0c\u8fd9\u8bf4\u660e\u706b\u7130\u8fd8\u662f\u80fd\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u4f24\u5bb3\u5230\u8fd9\u53ea\u53cc\u5934\u5de8\u9b54\u7684\uff0c\u867d\u7136\u5b83\u4f1a\u55b7\u706b\u3002\u968f\u7740\u76ae\u8089\u6389\u843d\uff0c\u5de8\u9b54\u7684\u9aa8\u9abc\u4e00\u70b9\u70b9\u66b4\u9732\u51fa\u6765\uff0c\u5728\u4e0b\u5348\u7684\u9633\u5149\u4e0b\u6cdb\u7740\u767d\u68ee\u68ee\u7684\u51b7\u6d0c\u5bd2\u5149\uff0c\u4e0a\u9762\u5c45\u7136\u5149\u6d01\u5982\u65b0\uff0c\u8fde\u4e00\u70b9\u8840\u4e1d\u90fd\u6ca1\u6709\u3002</p><p>\u8fd9\u602a\u7269\u4eff\u4f5b\u5c31\u662f\u4e00\u526f\u9aa8\u5934\u67b6\u5b50\uff0c\u5916\u9762\u5957\u7740\u5de8\u9b54\u7684\u76ae\u56ca\uff0c\u6839\u672c\u5c31\u4e0d\u50cf\u662f\u4e00\u4e2a\u771f\u6b63\u7684\u751f\u547d\u4f53\uff0c\u53cd\u5012\u6709\u70b9\u7c7b\u4f3c\u9b54\u50cf\u5080\u5121\u3002</p><p>\u8d1d\u7eb3\u7d22\u9ad8\u9ad8\u4e3e\u8d77\u53f3\u624b\uff0c\u638c\u5fc3\u63e1\u7740\u4e00\u5757\u7531\u7ea2\u3001\u9ec4\u5404\u79cd\u989c\u8272\u7684\u5b9d\u77f3\u9576\u5d4c\u800c\u6210\u7684\u4e00\u4e2a\u5706\u76d8\uff0c\u90a3\u662f\u6668\u66e6\u4e4b\u795e\u7684\u5723\u5fbd\u3002\u201c\u90aa\u6076\u7684\u4ea1\u7075\uff01\u201d\u4ed6\u5389\u58f0\u5475\u65a5\uff0c\u201c\u4ee5\u6668\u66e6\u4e4b\u540d\uff0c\u7ed3\u675f\u4f60\u8fd9\u626d\u66f2\u7684\u5b58\u5728\u3002\u5c18\u5f52\u4e8e\u5c18\uff0c\u571f\u5f52\u4e8e\u571f\uff0c\u613f\u5149\u8f89\u4e0e\u4f60\u540c\u5728\uff01\u201d</p><p>\u5f53\u201c\u5149\u8f89\u201d\u8fd9\u4e2a\u8bcd\u8bf4\u51fa\u53e3\u65f6\uff0c\u4e00\u9053\u8000\u773c\u7684\u707c\u70ed\u5149\u8f89\u4ece\u4ed6\u7684\u638c\u5fc3\u8ff8\u5c04\u51fa\u6765\uff0c\u7b14\u76f4\u51fb\u5411\u5de8\u9b54\u3002\u5de8\u9b54\u6b32\u5f85\u95ea\u907f\uff0c\u4f46\u5b83\u7684\u4f53\u578b\u592a\u5927\uff0c\u53c8\u88ab\u6885\u83f2\u65af\u548c\u9686\u5947\u903c\u4f4f\uff0c\u8f6c\u52a8\u4e0d\u7075\uff0c\u88ab\u707c\u70ed\u5149\u8f89\u6b63\u4e2d\u80f8\u53e3\u3002</p><p>\u201c\u5657\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u707c\u70ed\u5149\u8f89\u900f\u4f53\u800c\u8fc7\uff0c\u5c06\u5de8\u9b54\u7684\u80f8\u53e3\u51fb\u51fa\u4e00\u4e2a\u5927\u6d1e\u3002\u539f\u672c\u65e0\u6bd4\u987d\u5f3a\u7684\u5de8\u9b54\u88ab\u8fd9\u5149\u8f89\u4e00\u51fb\uff0c\u987f\u65f6\u4eff\u4f5b\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\u4f3c\u7684\uff0c\u8f6f\u8f6f\u762b\u5012\u4e0b\u6765\u3002</p><p>\u8d1d\u7eb3\u7d22\u4e3e\u7740\u5723\u5fbd\uff0c\u653e\u51fa\u8000\u773c\u767d\u5149\uff0c\u5c0f\u5fc3\u5730\u9760\u8fd1\uff0c\u5bb3\u6015\u5de8\u9b54\u8fd8\u6ca1\u6709\u771f\u6b63\u4e27\u5931\u6218\u6597\u529b\uff0c\u4f46\u4e8b\u5b9e\u8bc1\u660e\u5de8\u9b54\u6682\u65f6\u4e0d\u4f1a\u52a8\u5f39\u4e86\uff0c\u867d\u7136\u5b83\u8fd8\u5728\u5fae\u5fae\u5598\u606f\u3002\u6240\u6709\u4eba\u90fd\u9760\u8fd1\u8fc7\u6765\uff0c\u4ed6\u4eec\u9762\u9762\u76f8\u89d1\u7740\uff0c\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002</p><p>\u201c\u5b83\u662f\u4e2a\u4ea1\u7075\uff1f\u201d\u6885\u83f2\u65af\u95ee\u8d1d\u7eb3\u7d22\uff0c\u4f46\u7267\u5e08\u76b1\u7740\u7709\uff0c\u4e5f\u4e0d\u6562\u8d38\u7136\u56de\u7b54\u3002\u8fd9\u53cc\u5934\u5de8\u9b54\u5341\u5206\u53e4\u602a\uff0c\u5b83\u8eab\u4e0a\u6709\u4ea1\u7075\u7684\u6c14\u606f\uff0c\u4f46\u4e5f\u6709\u751f\u8005\u7684\u8ff9\u8c61\uff0c\u5b83\u8fd8\u5728\u5598\u606f\uff0c\u8eab\u4e0a\u88ab\u706b\u7130\u77e2\u5c04\u4e2d\u7684\u5730\u65b9\uff0c\u76ae\u8089\u7126\u9ed1\u8131\u843d\uff0c\u4f46\u88ab\u6885\u83f2\u65af\u7684\u94f6\u5251\u9020\u6210\u7684\u4f24\u53e3\u4e2d\uff0c\u8fd8\u5728\u6d41\u6dcc\u7740\u6d45\u7eff\u8272\u7684\u9c9c\u8840\u2014\u2014\u800c\u4ea1\u7075\u662f\u4e0d\u4f1a\u6d41\u8840\u7684\uff0c\u5c31\u8fde\u5438\u8840\u9b3c\u8fd9\u79cd\u5916\u8868\u975e\u5e38\u8fd1\u4f3c\u6d3b\u4eba\u7684\u4ea1\u7075\u90fd\u4e0d\u53ef\u80fd\u6d41\u8840\u3002</p><p>\u5b83\u4eff\u4f5b\u662f\u4e00\u4e2a\u534a\u751f\u534a\u6b7b\uff0c\u751f\u8005\u4e0e\u4ea1\u7075\u6df7\u5408\u7684\u602a\u7269\u3002</p><p>\u6240\u6709\u4eba\u90fd\u56f4\u4e86\u4e0a\u6765\uff0c\u89c2\u5bdf\u7740\uff0c\u4ed6\u4eec\u8981\u4e48\u662f\u7ecf\u9a8c\u4e30\u5bcc\u89c1\u591a\u8bc6\u5e7f\u7684\u5192\u9669\u8005\uff0c\u8981\u4e48\u662f\u5b66\u8bc6\u6e0a\u535a\u7684\u7267\u5e08\uff0c\u4f46\u8fd9\u4e2a\u602a\u7269\u4ece\u5934\u5230\u5c3e\u90fd\u5df2\u7ecf\u8d85\u51fa\u4e86\u4ed6\u4eec\u7684\u5e38\u8bc6\u8303\u7574\u3002\u5b83\u65e2\u662f\u751f\u8005\u53c8\u662f\u6b7b\u8005\uff0c\u65e2\u662f\u5de8\u9b54\u53c8\u662f\u4ea1\u7075\uff0c\u957f\u7740\u4e24\u9897\u80fd\u55b7\u706b\u7684\u8111\u888b\uff0c\u62e5\u6709\u968f\u610f\u91ca\u653e\u7403\u72b6\u95ea\u7535\u7684\u80fd\u529b\uff0c\u800c\u4e14\u9aa8\u9abc\u575a\u786c\u5f97\u4eff\u4f5b\u7cbe\u91d1\u2014\u2014\u8fd9\u5230\u5e95\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f</p><p>\u8d1d\u7eb3\u7d22\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u4fef\u8eab\u4ed4\u7ec6\u68c0\u67e5\uff0c\u201c\u5b83\u7684\u9aa8\u9ab8\u662f\u4ea1\u7075\u4e4b\u7269\u3002\u201d\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u505a\u51fa\u7ed3\u8bba\u3002</p><p>\u5176\u4ed6\u4eba\u90fd\u6ca1\u542c\u61c2\u8fd9\u53e5\u8bdd\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u590f\u62c9\u975e\u95ee\uff0c\u201c\u4ec0\u4e48\u53eb\u2018\u9aa8\u9ab8\u662f\u4ea1\u7075\u4e4b\u7269\u2019\uff1f\u201d</p><p>\u8d1d\u7eb3\u7d22\u968f\u5373\u4e5f\u610f\u8bc6\u5230\u81ea\u5df1\u7684\u8bf4\u6cd5\u5f88\u6709\u6b67\u4e49\uff0c\u201c\u5443\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5b83\u73b0\u5728\u7684\u9aa8\u9ab8\u2026\u2026\u5443\uff0c\u600e\u4e48\u8bf4\u5462\uff0c\u4e0a\u9762\u7684\u6b7b\u4ea1\u6c14\u606f\u975e\u5e38\u6d53\u91cd\uff0c\u81f3\u5c11\u662f\u5df2\u7ecf\u6b7b\u53bb\u4e0a\u767e\u5e74\u7684\u4ea1\u7075\u4e86\u3002\u201d</p><p>\u8fd9\u8868\u8fbe\u5f97\u8fd8\u662f\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u4f46\u5927\u5bb6\u5dee\u4e0d\u591a\u90fd\u5df2\u7ecf\u660e\u767d\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u53ea\u5de8\u9b54\u7684\u8eab\u4f53\u91cc\uff0c\u6709\u4e00\u526f\u672c\u5e94\u8be5\u5c5e\u4e8e\u4ea1\u7075\u7684\u9aa8\u67b6\u3002</p><p>\u4e00\u4e2a\u6d3b\u7269\u7684\u8eab\u4f53\u91cc\uff0c\u6709\u4e00\u526f\u4ea1\u7075\u7684\u9aa8\u67b6\uff1b\u6216\u8005\u8bf4\uff0c\u4e00\u526f\u4ea1\u7075\u7684\u9aa8\u67b6\uff0c\u5374\u652f\u6491\u7740\u4e00\u4e2a\u6d3b\u7740\u7684\u751f\u547d\u3002</p><p>\u8fd9\u53ef\u771f\u662f\u5947\u5999\uff0c\u6216\u8005\u8bf4\u8be1\u5f02\u3002</p><p>\u4e0d\u4ec5\u5982\u6b64\uff0c\u8d1d\u7eb3\u7d22\u540c\u65f6\u8fd8\u6709\u53e6\u5916\u7684\u53d1\u73b0\uff0c\u5728\u4ed4\u7ec6\u68c0\u67e5\u4e86\u5de8\u9b54\u66b4\u9732\u51fa\u6765\u7684\u9aa8\u9abc\u4e4b\u540e\uff0c\u4ed6\u53d1\u73b0\u4e00\u4e2a\u60ca\u4eba\u7684\u4e8b\u5b9e\u3002</p><p>\u201c\u5929\u54ea\uff0c\u201d\u7267\u5e08\u60ca\u53f9\u7740\uff0c\u201c\u8fd9\u5e45\u9aa8\u67b6\u4e0a\u2026\u2026\u662f\u88ab\u7cbe\u91d1\u9aa8\u9abc\u9b54\u6cd5\u6539\u9020\u8fc7\u7684\u3002\u201d</p><p>\u201c\u7cbe\u91d1\u9aa8\u9abc\u201d\u662f\u4e00\u79cd\u975e\u5e38\u9ad8\u9636\u7684\u6cd5\u672f\uff0c\u6b63\u597d\u5c5e\u4e8e\u53d8\u5316\u5b66\u6d3e\uff0c\u6240\u4ee5\u743c\u6069\u4e5f\u77e5\u9053\u4e00\u4e9b\uff0c\u867d\u7136\u4ee5\u4ed6\u76ee\u524d\u7684\u80fd\u529b\uff0c\u662f\u538b\u6839\u4e0d\u7528\u53bb\u60f3\u5b66\u3002\u4f7f\u7528\u8fd9\u79cd\u6cd5\u672f\uff0c\u8db3\u4ee5\u5c06\u4e00\u4e2a\u4ea1\u7075\u7684\u9aa8\u9ab8\u53d8\u5f97\u5982\u540c\u7cbe\u91d1\u4e00\u822c\u575a\u4e0d\u53ef\u7834\u3001\u7262\u4e0d\u53ef\u6467\uff0c\u7740\u5b9e\u5389\u5bb3\u5f97\u7d27\u3002\u8fd9\u4e5f\u5c31\u662f\u6885\u83f2\u65af\u548c\u9686\u5947\u65e0\u8bba\u600e\u6837\u4e5f\u65e0\u6cd5\u771f\u6b63\u5c06\u5b83\u6253\u5012\u7684\u539f\u56e0\uff0c\u7cbe\u91d1\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u786c\u6700\u575a\u56fa\u7684\u91d1\u5c5e\uff0c\u5c31\u7b97\u662f\u6885\u83f2\u65af\u7684\u201c\u7737\u604b\u201d\u94f6\u5251\u548c\u9686\u5947\u7684\u62f3\u5934\uff0c\u4e5f\u6ca1\u529e\u6cd5\u5bf9\u4ed8\u3002</p><p>\u4f46\u95ee\u9898\u662f\uff0c\u8fd9\u79cd\u6cd5\u672f\u5e76\u4e0d\u662f\u6c38\u4e45\u6027\u7684\uff0c\u5b83\u53ea\u80fd\u8d77\u5230\u6682\u65f6\u6027\u7684\u5f3a\u5316\u4f5c\u7528\u2014\u2014\u5982\u679c\u8981\u5c06\u5b83\u6c38\u4e45\u6027\u5730\u9644\u7740\u5728\u67d0\u4e2a\u4ea1\u7075\u7684\u9aa8\u9ab8\u4e0a\uff0c\u4e5f\u4e0d\u662f\u4e0d\u80fd\uff0c\u4f46\u8fd9\u9700\u8981\u66f4\u52a0\u9ad8\u660e\u2026\u2026\u9ad8\u660e\u5230\u743c\u6069\u6682\u65f6\u90fd\u6709\u4e9b\u4e0d\u6562\u60f3\u50cf\u7684\u9b54\u6cd5\u80fd\u529b\u548c\u6280\u5de7\u3002</p><p>\u6240\u6709\u4eba\u90fd\u6709\u4e9b\u9762\u9762\u76f8\u89d1\uff0c\u4ed6\u4eec\u672c\u80fd\u5730\u610f\u8bc6\u5230\uff1a\u53ef\u80fd\u9047\u4e0a\u5927\u9ebb\u70e6\u4e86\u3002</p><p>\u590f\u62c9\u975e\u63d0\u8bae\u4e0d\u5fc5\u7406\u776c\uff0c\u53cd\u6b63\u73b0\u5728\u5b83\u5df2\u7ecf\u5feb\u5b8c\u86cb\u4e86\u3002\u9644\u7740\u4e86\u7cbe\u91d1\u9aa8\u9abc\u9b54\u6cd5\u7684\u9aa8\u9ab8\u786e\u5b9e\u575a\u56fa\u65e0\u6bd4\uff0c\u4f46\u8fd9\u4ec5\u4ec5\u53ea\u662f\u9488\u5bf9\u7269\u7406\u653b\u51fb\u800c\u8a00\uff0c\u4e0d\u80fd\u62b5\u5fa1\u6cd5\u672f\u3002\u6668\u66e6\u4e4b\u795e\u8d50\u4e88\u7267\u5e08\u7684\u707c\u70ed\u5149\u8f89\u4e25\u91cd\u4f24\u5bb3\u5230\u4e86\u5b83\uff0c\u800c\u706b\u7130\u77e2\u53c8\u6291\u5236\u4f4f\u4e86\u5b83\u7684\u76ae\u8089\u518d\u751f\uff0c\u53ea\u8981\u518d\u6254\u4e24\u74f6\u70bd\u706b\u80f6\uff0c\u5e94\u8be5\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u95ee\u9898\u4e86\u3002\u743c\u6069\u548c\u79d1\u6069\u4e5f\u8d5e\u6210\u8fd9\u4e2a\u770b\u6cd5\uff0c\u6885\u83f2\u65af\u548c\u9686\u5947\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u4f46\u8d1d\u7eb3\u7d22\u53cd\u5bf9\u8fd9\u4e2a\u610f\u89c1\uff0c\u8fd9\u4f4d\u9996\u5e2d\u7267\u5e08\u7684\u8054\u60f3\u80fd\u529b\u975e\u5e38\u4e30\u5bcc\uff0c\u4ed6\u8ba4\u4e3a\u8fd9\u5f88\u53ef\u80fd\u610f\u5473\u7740\u4e00\u79cd\u65b0\u7684\u4ea1\u7075\u51fa\u73b0\uff0c\u751a\u81f3\u4ed6\u6000\u7591\u662f\u6709\u90aa\u6076\u4e4b\u5f92\u6b63\u5728\u6709\u610f\u8bc6\u5730\u5236\u9020\u65b0\u7684\u4ea1\u7075\u3002\u800c\u8eab\u4e3a\u6668\u66e6\u4e4b\u795e\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u5982\u679c\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u7b97\u4e86\uff0c\u65e2\u7136\u4eca\u5929\u649e\u4e0a\uff0c\u5c31\u4e00\u5b9a\u8981\u641e\u6e05\u695a\u662f\u600e\u4e48\u56de\u4e8b\u3002</p><p>\u201c\u600e\u4e48\u641e\u6e05\u695a\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u5982\u4eca\u8fd9\u5de8\u9b54\u5df2\u7ecf\u5944\u5944\u4e00\u606f\uff0c\u773c\u770b\u5c31\u8981\u6b7b\u4e86\uff0c\u96be\u9053\u8fd8\u80fd\u4e25\u5211\u62f7\u6253\uff0c\u903c\u95ee\u53e3\u4f9b\u4e0d\u6210\uff1f\u5c31\u7b97\u5de8\u9b54\u8fd8\u6709\u529b\u6c14\u8bf4\u8bdd\u5e76\u4e14\u613f\u610f\u62db\u8ba4\uff0c\u8fd9\u91cc\u4e5f\u6ca1\u4eba\u80fd\u542c\u61c2\u5de8\u9b54\u7684\u8bdd\u5427\u2026\u2026\u6885\u83f2\u65af\u4f1a\u4e0d\u4f1a\u80fd\u542c\u61c2\uff0c\u5979\u4f3c\u4e4e\u662f\u7cbe\u901a\u5404\u79cd\u8bed\u8a00\u7684\uff0c\u8fde\u4eba\u9762\u72ee\u7684\u8bdd\u90fd\u80fd\u8bf4\u3002</p><p>\u6b63\u5f53\u5927\u5bb6\u5546\u8bae\u672a\u51b3\u7684\u65f6\u5019\uff0c\u90a3\u53ea\u5de8\u9b54\u7a81\u7136\u53d1\u51fa\u4e00\u58f0\u4f4e\u6c89\u7684\u568e\u53eb\uff0c\u4eff\u4f5b\u91ce\u517d\u7edd\u671b\u7684\u54c0\u9e23\uff0c\u4f17\u4eba\u8fd8\u4ee5\u4e3a\u5b83\u8981\u6fd2\u6b7b\u53cd\u51fb\uff0c\u5374\u89c1\u5de8\u9b54\u5168\u8eab\u5267\u70c8\u62bd\u6410\uff0c\u7d27\u63a5\u7740\u53e3\u5410\u767d\u6cab\uff0c\u5c31\u6b64\u6c14\u7edd\u8eab\u4ea1\u3002</p><p>\u201c\u6b7b\u4e86\uff1f\u201d</p><p>\u786e\u5b9e\u662f\u6b7b\u4e86\uff0c\u5728\u573a\u6709\u4e24\u4e2a\u7267\u5e08\uff0c\u4ed6\u4eec\u90fd\u7b2c\u4e00\u65f6\u95f4\u610f\u8bc6\u5230\uff0c\u751f\u547d\u7684\u6c14\u606f\u5df2\u7ecf\u4ece\u8fd9\u53ea\u5de8\u9b54\u8eab\u4e0a\u5feb\u901f\u6d88\u6563\uff0c\u5b83\u5df2\u7ecf\u5f7b\u5e95\u53d8\u6210\u4e86\u4e2a\u6b7b\u5c38\u3002</p><p>\u8fd9\u53ea\u5de8\u9b54\u5b9e\u5728\u592a\u6ca1\u6709\u5de8\u9b54\u7684\u98ce\u8303\uff0c\u5f3a\u608d\u7684\u751f\u547d\u529b\u548c\u81ea\u6211\u518d\u751f\u80fd\u529b\u8fd8\u6ca1\u6765\u5f97\u53ca\u5c55\u793a\uff0c\u5c31\u8fd9\u4e48\u83ab\u540d\u5176\u5999\u5730\u65ad\u6c14\u4e86\uff0c\u8fd9\u5b9e\u5728\u662f\u4ee4\u4eba\u6cae\u4e27\u2026\u2026\u4e0d\u8fc7\u8fd9\u6837\u4e00\u6765\u4e5f\u597d\uff0c\u5982\u679c\u5de8\u9b54\u6ca1\u6b7b\uff0c\u6216\u8bb8\u8d1d\u7eb3\u7d22\u8fd8\u80fd\u8ba9\u5b83\u5f00\u53e3\u62db\u4f9b\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1b\u4f46\u5982\u4eca\u5b83\u6b7b\u4e86\uff0c\u6b63\u597d\u4e00\u4e86\u767e\u4e86\uff0c\u5927\u5bb6\u7ee7\u7eed\u8d76\u8def\u5c31\u662f\u3002</p><p>\u8d1d\u7eb3\u7d22\u4f3c\u4e4e\u5e76\u4e0d\u7518\u5fc3\uff0c\u4f46\u4ed6\u4e5f\u65e0\u8ba1\u53ef\u65bd\u3002\u5176\u5b9e\u5728\u8fd9\u4e2a\u5947\u5e7b\u4e16\u754c\uff0c\u5012\u662f\u6709\u4e00\u4e9b\u9b54\u6cd5\u662f\u80fd\u8ba9\u6b7b\u4eba\u5f00\u53e3\u7684\uff0c\u743c\u6069\u5c31\u542c\u8bf4\u8fc7\u51e0\u4e2a\u80fd\u62f7\u95ee\u6298\u78e8\u6b7b\u8005\u7075\u9b42\uff0c\u903c\u8feb\u5410\u9732\u4fe1\u606f\u7684\u5965\u672f\uff0c\u4f46\u90fd\u5c5e\u4e8e\u4ea1\u7075\u5b66\u6d3e\u7684\u4e13\u4e1a\u8303\u56f4\uff0c\u6070\u597d\u662f\u4ed6\u6700\u53cd\u611f\u7684\u7c7b\u578b\uff0c\u81ea\u7136\u662f\u538b\u6839\u4e0d\u4f1a\u3002</p><p>\u795e\u672f\u4e2d\u4e5f\u6709\u7c7b\u4f3c\u7684\u6cd5\u672f\uff0c\u4f46\u8fd9\u79cd\u60ca\u6270\u3001\u6298\u78e8\u901d\u8005\u4ea1\u9b42\u7684\u884c\u4e3a\uff0c\u7d20\u6765\u90fd\u88ab\u8ba4\u4e3a\u662f\u90aa\u6076\u4e4b\u4e3e\uff0c\u5584\u795e\u662f\u51b3\u4e0d\u4f1a\u8d50\u4e88\u7267\u5e08\u8fd9\u79cd\u795e\u672f\u7684\uff0c\u4e00\u822c\u53ea\u6709\u90aa\u795e\u7684\u7267\u5e08\u624d\u6709\u53ef\u80fd\u4f1a\u3002\u5982\u4eca\u8fd9\u91cc\u4e24\u4e2a\u7267\u5e08\uff0c\u4e00\u4e2a\u4fe1\u5170\u68ee\u5fb7\u5c14\uff0c\u4e00\u4e2a\u4fe1\u6c83\u91d1\uff0c\u62dc\u7684\u90fd\u4e0d\u662f\u90aa\u795e\uff0c\u6ca1\u4ec0\u4e48\u7528\u3002</p><p>\u8d1d\u7eb3\u7d22\u6216\u8bb8\u5f88\u64c5\u957f\u6467\u6bc1\u4e00\u4e2a\u4ea1\u7075\uff0c\u4f46\u8981\u4ed6\u53bb\u62f7\u95ee\u4e00\u4e2a\u6b7b\u8005\u7684\u7075\u9b42\uff0c\u903c\u95ee\u4fe1\u606f\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4ed6\u65e2\u505a\u4e0d\u51fa\u6765\u4e5f\u529e\u4e0d\u5230\uff0c\u6668\u66e6\u4e4b\u795e\u4e0d\u4f1a\u5141\u8bb8\u7684\u3002\u5982\u679c\u743c\u6069\u8bb0\u5f97\u6ca1\u9519\uff0c\u5728\u5bf9\u5f85\u4ea1\u7075\u8fd9\u70b9\u4e0a\uff0c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u548c\u6b7b\u4ea1\u4e4b\u795e\u514b\u5170\u6c83\u7b97\u662f\u5fd7\u540c\u9053\u5408\u7684\u76df\u53cb\u2014\u2014\u800c\u6b7b\u795e\u514b\u5170\u6c83\u66fe\u7ecf\u516c\u5f00\u5c55\u793a\u795e\u8ff9\uff0c\u5411\u4ed6\u7684\u6240\u6709\u4fe1\u5f92\uff0c\u4ee5\u53ca\u501f\u4ed6\u4eec\u4e4b\u53e3\u5411\u5168\u5927\u9646\u7684\u4eba\u5ba3\u5e03\uff1a\u201c\u6211\u5fc5\u4fdd\u8bc1\u6b7b\u8005\u83b7\u5f97\u6c38\u8fdc\u7684\u5b89\u7720\uff0c\u4e0d\u88ab\u4efb\u4f55\u90aa\u6076\u529b\u91cf\u518d\u5ea6\u4fb5\u6270\u3002\u201d</p><p>\u600e\u4e48\u529e\uff1f\u4f3c\u4e4e\u53ea\u80fd\u653e\u5f03\u4e86\u3002</p></article>"}}}]);
