(window.webpackJsonp=window.webpackJsonp||[]).push([[1300],{1323:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u5c11\u5e74",content:"<article><h2>\u7b2c\u56db\u8282 \u5c11\u5e74</h2><p>\u743c\u6069\u8f9b\u8f9b\u82e6\u82e6\u7a7f\u8fc7\u5927\u6c99\u6f20\uff0c\u649e\u4e0a\u4eba\u9762\u72ee\u5dee\u70b9\u628a\u547d\u9001\u6389\uff0c\u8fd8\u5192\u7740\u611f\u67d3\u761f\u75ab\u6302\u6389\u7684\u5371\u9669\uff0c\u4e00\u8def\u5954\u6ce2\u5230\u8fd9\u91cc\uff0c\u6240\u4e3a\u4f55\u6765\uff1f\u4e0d\u5c31\u662f\u4e3a\u4e86\u7ed9\u8fd9\u4e2a\u62c9\u6c83\u514b\u9001\u4fe1\u4e48\u3002</p><p>\u8fd9\u8d9f\u6765\u70db\u5821\uff0c\u4e00\u65b9\u9762\u662f\u4e3a\u4e86\u6284\u5f55\u9b54\u6cd5\uff0c\u63d0\u5347\u6280\u827a\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u5c31\u662f\u4e3a\u4e86\u6253\u542c\u62c9\u6c83\u514b\u7684\u6d88\u606f\u3002\u6ca1\u60f3\u5230\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u5de5\u592b\uff0c\u8fd9\u8001\u5bb6\u4f19\u7684\u8d44\u6599\u5c31\u5728\u9762\u524d\u3002</p><p>\u6309\u637a\u4f4f\u5fc3\u5934\u7684\u559c\u60a6\uff0c\u4ed6\u4f38\u624b\u53bb\u62bd\u51fa\u90a3\u4e2a\u8d34\u7740\u201c\u62c9\u6c83\u514b\u201d\u540d\u5b57\u7684\u6587\u4ef6\u5939\uff0c\u4f46\u5374\u53d1\u73b0\u662f\u8f7b\u98d8\u98d8\u7684\u3002\u4ed6\u5fae\u5fae\u4e00\u6014\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u53d1\u73b0\u91cc\u9762\u8fde\u4e00\u5f20\u7eb8\u4e5f\u6ca1\u6709\uff0c\u662f\u4e2a\u7a7a\u6587\u4ef6\u5939\u3002</p><p>\u201c\u8fd9\u91cc\u4e3a\u4ec0\u4e48\u6ca1\u6709\u8d44\u6599\uff1f\u201d\u743c\u6069\u95ee\u5f15\u5bfc\u8005\u3002</p><p>\u5f15\u5bfc\u8005\u770b\u4e86\u4e00\u773c\uff0c\u201c\u8fd9\u4efd\u8d44\u6599\u524d\u4e9b\u5929\u88ab\u53d6\u8d70\u4e86\uff0c\u201d\u4ed6\u89e3\u91ca\u8bf4\uff0c\u201c\u4e4c\u5c14\u5170\u7279\u9601\u4e0b\u8981\u67e5\u9605\u5b83\u3002\u201d</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u73b0\u5728\u8fd9\u4efd\u8d44\u6599\u5728\u4e2d\u592e\u9ad8\u5854\u91cc\uff0c\u90a3\u4e2a\u4ec0\u4e48\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u7684\u624b\u4e2d\u3002</p><p>\u743c\u6069\u7565\u7565\u6709\u4e9b\u76b1\u7709\uff0c\u8fd9\u4e2a\u610f\u5916\u8ba9\u4ed6\u611f\u5230\u9690\u9690\u7684\u4e0d\u5b89\uff0c\u4e4c\u5c14\u5170\u7279\u67e5\u9605\u62c9\u6c83\u514b\u7684\u8d44\u6599\uff1f\u5f53\u7136\uff0c\u4ed6\u662f\u8fd9\u91cc\u7684\u56fe\u4e66\u9986\u957f\uff0c\u8981\u67e5\u9605\u4efb\u4f55\u4e00\u4efd\u8d44\u6599\u90fd\u6ca1\u95ee\u9898\uff0c\u53ea\u662f\u4f3c\u4e4e\u4e5f\u592a\u5de7\u5408\u4e86\u70b9\u3002</p><p>\u6ca1\u529e\u6cd5\uff0c\u65e2\u7136\u8d44\u6599\u5df2\u7ecf\u4e0d\u5728\uff0c\u743c\u6069\u4e5f\u53ea\u80fd\u653e\u5f03\uff0c\u53e6\u5916\u518d\u60f3\u529e\u6cd5\u3002\u5148\u653e\u4e0b\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u7ee7\u7eed\u53bb\u67e5\u9605\u9b54\u6cd5\u8d44\u6599\uff0c\u65f6\u95f4\u5f88\u5feb\u8fc7\u53bb\u3002</p><p>\u743c\u6069\u627e\u5230\u4e86\u4e00\u4e9b\u975e\u5e38\u4e0d\u9519\u800c\u4e14\u9002\u5408\u7684\u6cd5\u672f\uff0c\u4f46\u9274\u4e8e\u6536\u8d39\u7684\u6602\u8d35\uff0c\u4ed6\u53ea\u53ef\u80fd\u9009\u62e9\u5176\u4e2d\u4e24\u4e09\u4e2a\u6284\u8fdb\u9b54\u6cd5\u4e66\uff0c\u8fd9\u5c31\u5f88\u4ee4\u4ed6\u8e0c\u8e87\u3002\u60f3\u6765\u60f3\u53bb\uff0c\u4ed6\u51b3\u5b9a\u8fd8\u662f\u6682\u4e14\u5148\u4e0d\u505a\u51b3\u5b9a\uff0c\u53cd\u6b63\u70db\u5821\u7684\u89c4\u77e9\u662f\u4efb\u4f55\u5ba2\u4eba\u53ef\u4ee5\u505c\u7559\u5341\u5929\uff0c\u65f6\u95f4\u8fd8\u65e9\u3002</p><p>\u5929\u8272\u5df2\u665a\uff0c\u5f15\u5bfc\u8005\u9001\u743c\u6069\u51fa\u6765\uff0c\u5ba2\u4eba\u662f\u4e0d\u80fd\u5728\u9ad8\u5854\u5185\u8fc7\u591c\u7684\u3002\u70db\u5821\u7684\u5e03\u5c40\uff0c\u6700\u4e2d\u5fc3\u7684\u662f\u4e5d\u5ea7\u9ad8\u5854\uff0c\u9ad8\u5854\u4e4b\u5916\u662f\u82b1\u56ed\uff1b\u82b1\u56ed\u4e4b\u5916\u5219\u662f\u4e00\u5708\u5efa\u7b51\uff0c\u7d27\u8d34\u7740\u57ce\u5899\uff0c\u5305\u62ec\u9a6c\u53a9\u3001\u4ed3\u5e93\u3001\u533b\u9662\u548c\u795e\u6bbf\u7b49\u7b49\uff0c\u4ee5\u53ca\u5ba2\u4eba\u7684\u4f4f\u6240\u3002</p><p>\u6885\u83f2\u65af\u4e5f\u4ece\u9ad8\u5854\u4e2d\u51fa\u6765\uff0c\u4e24\u4eba\u56de\u5230\u4f4f\u6240\u3002\u665a\u9910\u6bd5\uff0c\u743c\u6069\u6253\u7b97\u51fa\u53bb\u968f\u4fbf\u8d70\u8d70\uff0c\u4e86\u89e3\u4e00\u4e0b\u70db\u5821\u7684\u60c5\u51b5\u3002\u9ad8\u5854\u591c\u95f4\u662f\u4e0d\u5bf9\u5916\u5f00\u653e\u7684\uff0c\u81ea\u7136\u6ca1\u6cd5\u8fdb\u53bb\uff1b\u9a6c\u53a9\u3001\u4ed3\u5e93\u4e4b\u7c7b\u4ed6\u4e5f\u6ca1\u5174\u8da3\uff0c\u4fbf\u6162\u6162\u8d70\u5230\u4e86\u795e\u6bbf\u4e2d\u3002</p><p>\u70db\u5821\u662f\u5927\u56fe\u4e66\u9986\uff0c\u77e5\u8bc6\u4e4b\u5730\uff0c\u4f9b\u5949\u7684\u81ea\u7136\u662f\u4ee5\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u4e3a\u9996\u7684\u201c\u77e5\u8bc6\u4f17\u795e\u201d\uff0c\u5305\u62ec\u6b27\u683c\u739b\u3001\u5de5\u827a\u4e0e\u953b\u9020\u4e4b\u795e\u8d21\u5fb7\u3001\u6587\u5b66\u4e0e\u827a\u672f\u4e4b\u795e\u5fb7\u5948\u5c14\u548c\u548f\u5531\u4e4b\u795e\u5bc6\u9ece\u5c14\u3002\u743c\u6069\u4ee5\u524d\u4e5f\u5728\u5b66\u6821\u91cc\u5b66\u8fc7\uff0c\u7565\u6709\u6240\u95fb\uff0c\u4e0d\u7b97\u4e00\u65e0\u6240\u77e5\u3002\u77e5\u8bc6\u65e0\u6240\u8c13\u5584\u6076\uff0c\u7eaf\u7136\u4e2d\u7acb\uff0c\u77e5\u8bc6\u4f17\u795e\u7684\u7acb\u573a\u4e5f\u5927\u591a\u5982\u6b64\uff0c\u65e2\u975e\u5584\u795e\uff0c\u4e5f\u975e\u90aa\u795e\uff0c\u7b97\u662f\u4e2d\u95f4\u6d3e\u3002\u8fd9\u5bfc\u81f4\u7684\u574f\u5904\u662f\u77e5\u8bc6\u4f17\u795e\u7684\u6559\u4f1a\u5e38\u5e38\u7f3a\u5c11\u76df\u53cb\uff1b\u597d\u5904\u5219\u662f\u5f80\u5f80\u80fd\u5728\u4e89\u6597\u4e2d\u7f6e\u8eab\u4e8b\u5916\u3002</p><p>\u743c\u6069\u968f\u610f\u8f6c\u4e86\u8f6c\uff0c\u53d1\u73b0\u6b27\u683c\u739b\u7684\u795e\u6bbf\u5176\u5b9e\u5c31\u7c7b\u4f3c\u4e00\u4e2a\u5c0f\u578b\u7684\u56fe\u4e66\u9986\uff0c\u8d70\u8fdb\u53bb\u90fd\u770b\u4e0d\u89c1\u5899\u58c1\uff0c\u53ea\u6709\u4e66\u67b6\u4e0a\u4e00\u6392\u4e00\u6392\u7684\u4e66\u7c4d\uff1b\u5fb7\u5948\u5c14\u7684\u795e\u6bbf\u98ce\u683c\u5927\u4f53\u7c7b\u4f3c\uff0c\u8fd9\u4f4d\u795e\u7947\u662f\u6b27\u683c\u739b\u7684\u4ece\u795e\uff0c\u53c8\u88ab\u79f0\u4e3a\u201c\u6b27\u683c\u739b\u7684\u4e66\u8bb0\u201d\u3002\u5bc6\u9ece\u5c14\u7684\u795e\u6bbf\u5374\u4eff\u4f5b\u5531\u8bd7\u73ed\u6216\u8005\u6e38\u4e50\u573a\uff0c\u8fd9\u4e48\u665a\u4e86\u95e8\u53e3\u90fd\u8fd8\u6709\u4eba\u5728\u62e8\u7740\u7ad6\u7434\u5f39\u5531\u3002\u743c\u6069\u7ad9\u5b9a\u542c\u4e86\u4e00\u4f1a\uff0c\u53d1\u73b0\u5927\u591a\u662f\u4e9b\u82f1\u96c4\u7684\u5192\u9669\u6545\u4e8b\uff0c\u5176\u5b9e\u542c\u8d77\u6765\u4e5f\u8fd1\u4f3c\u4ed6\u524d\u4e16\u770b\u8fc7\u7684\u7f51\u7edc\u5c0f\u8bf4\uff0c\u5f80\u5f80\u5938\u5f20\u5230\u4e0d\u7740\u8fb9\u9645\u7684\u5730\u6b65\uff0c\u4e00\u542c\u4fbf\u77e5\u662f\u80e1\u7f16\u4e71\u9020\u3002\u795e\u6bbf\u4e2d\u4e5f\u6709\u5f88\u591a\u85cf\u4e66\uff0c\u4f46\u51e0\u4e4e\u90fd\u662f\u6b4c\u8c23\u66f2\u8c31\uff0c\u504f\u91cd\u97f3\u4e50\u65b9\u9762\u3002</p><p>\u6700\u5177\u7279\u8272\u7684\uff0c\u5012\u662f\u8d21\u5fb7\u7684\u795e\u6bbf\u3002\u8d21\u5fb7\u662f\u5de5\u827a\u4e0e\u953b\u9020\u4e4b\u795e\uff0c\u548c\u5176\u4ed6\u4e09\u4f4d\u6587\u8d28\u5f6c\u5f6c\u7684\u5b66\u9662\u6d3e\u4e0d\u540c\uff0c\u4ed6\u662f\u5b9e\u8df5\u6d3e\uff0c\u4fe1\u5f92\u4e5f\u5927\u591a\u90fd\u662f\u5de5\u5320\u3001\u5efa\u7b51\u5e08\u3001\u53d1\u660e\u5bb6\u7b49\u7b49\u3002\u8d21\u5fb7\u88ab\u4fe1\u5f92\u5c0a\u79f0\u4e3a\u201c\u5947\u8ff9\u4f7f\u8005\u201d\u3001\u201c\u953b\u9020\u4e4b\u738b\u201d\uff0c\u4ed6\u6700\u5f97\u610f\u7684\u6210\u5c31\u662f\u53d1\u660e\u4e86\u706b\u836f\uff0c\u4f20\u6388\u7ed9\u4fe1\u5f92\uff0c\u5982\u4eca\u5df2\u7ecf\u6d41\u4f20\u6574\u4e2a\u5927\u9646\u3002</p><p>\u9634\u9b42\u57ce\u4e5f\u62ff\u5230\u4e86\u706b\u836f\u914d\u65b9\uff0c\u8bd5\u5236\u51fa\u706b\u70ae\u6765\u3002\u4f46\u4ee5\u743c\u6069\u7684\u975e\u4e13\u4e1a\u773c\u5149\u6765\u770b\uff0c\u8fd9\u4f4d\u795e\u7947\u7684\u79d1\u6280\u6c34\u51c6\u8fd8\u4e0d\u53ca\u5730\u7403\u4e0a\u7684\u79d1\u5b66\u5bb6\uff0c\u8fd9\u4e9b\u706b\u836f\u5a01\u529b\u8fdc\u6ca1\u6709\u4ed6\u524d\u4e16\u6240\u89c1\u8fc7\u7684\u90a3\u4e48\u5927\u3002\u743c\u6069\u77e5\u9053\u4e00\u4e9b\u4ec0\u4e48\u9ed1\u706b\u836f\u9ec4\u706b\u836f\u4e4b\u7c7b\u7684\u8bf4\u6cd5\uff0c\u4f46\u5177\u4f53\u7684\u4ed6\u4e5f\u4e0d\u61c2\uff0c\u4e5f\u61d2\u5f97\u591a\u7ba1\u3002</p><p>\u8d21\u5fb7\u7684\u795e\u6bbf\u4ece\u5916\u9762\u770b\u8d77\u6765\u5c31\u706b\u5149\u51b2\u5929\uff0c\u4e00\u65e6\u8d70\u8fdb\uff0c\u7acb\u523b\u5c31\u542c\u5230\u53ee\u53ee\u5f53\u5f53\u7684\u91d1\u5c5e\u6572\u6253\u649e\u51fb\u58f0\u3002\u5728\u795e\u6bbf\u5916\u5012\u542c\u4e0d\u89c1\uff0c\u5e94\u8be5\u662f\u7528\u4ec0\u4e48\u6cd5\u672f\u963b\u62e6\u4e86\u58f0\u97f3\uff0c\u5426\u5219\u4fbf\u8981\u62db\u6765\u4e00\u81f4\u6297\u8bae\u4e86\u3002\u6574\u5ea7\u795e\u6bbf\u611f\u89c9\u5c31\u50cf\u662f\u5ea7\u5927\u953b\u9020\u573a\uff0c\u81f3\u5c11\u6709\u4e09\u5206\u4e4b\u4e00\u7684\u7267\u5e08\u90fd\u5728\u5174\u9ad8\u91c7\u70c8\u5730\u953b\u9020\u6253\u94c1\uff0c\u6216\u8005\u4f0f\u6848\u7ed8\u56fe\uff0c\u8fd8\u6709\u7684\u5219\u5728\u6446\u5f04\u5404\u79cd\u7a00\u5947\u53e4\u602a\u7684\u673a\u68b0\u3002\u795e\u6bbf\u56db\u58c1\u90fd\u6446\u6ee1\u4e86\u8fd9\u4e9b\u7267\u5e08\u4eec\u7684\u53d1\u660e\u521b\u9020\uff0c\u53ea\u8981\u743c\u6069\u5bf9\u5176\u4e2d\u67d0\u4e00\u4ef6\u8868\u73b0\u5f97\u7a0d\u611f\u5174\u8da3\uff0c\u7acb\u523b\u5c31\u6709\u4eba\u4e0a\u6765\u6ed4\u6ed4\u4e0d\u7edd\u4e3a\u4ed6\u89e3\u8bf4\u3002</p><p>\u56db\u5ea7\u795e\u6bbf\u8f6c\u5b8c\uff0c\u591c\u5e55\u5df2\u7ecf\u6c89\u6c89\u7b3c\u7f69\uff0c\u743c\u6069\u51c6\u5907\u56de\u4f4f\u5904\u6b47\u606f\uff0c\u5728\u9a6c\u53a9\u95e8\u53e3\u770b\u89c1\u4e86\u4e24\u4e2a\u4eba\u3002</p><p>\u4e00\u8eab\u6de1\u84dd\u8272\u6734\u7d20\u8863\u670d\u7684\u5c11\u5973\uff0c\u662f\u6885\u83f2\u65af\uff1b\u800c\u7ad9\u5728\u6885\u83f2\u65af\u5bf9\u9762\u7684\uff0c\u662f\u4e00\u4e2a\u5c11\u5e74\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u79cd\u8bf4\u6cd5\u4f3c\u4e4e\u4e0d\u592a\u5bf9\u52b2\uff0c\u56e0\u4e3a\u743c\u6069\u81ea\u5df1\u4e5f\u624d\u5341\u4e94\u5c81\u3002\u4f46\u56e0\u4e3a\u8bb0\u5fc6\u7684\u7f18\u6545\uff0c\u4e60\u60ef\u6027\u5730\uff0c\u4ed6\u603b\u662f\u628a\u81ea\u5df1\u89c6\u4e3a\u6210\u5e74\u4eba\u3002</p><p>\u6885\u83f2\u65af\u6b63\u5728\u548c\u5c11\u5e74\u8bf4\u8bdd\uff0c\u770b\u60c5\u5f62\uff0c\u662f\u5728\u95ee\u4ed6\u4ec0\u4e48\u3002\u5c11\u5e74\u660e\u663e\u8b66\u6212\u5fc3\u5f88\u5f3a\uff0c\u4e0d\u613f\u610f\u56de\u7b54\uff0c\u60f3\u65e9\u65e9\u8131\u8eab\uff0c\u4f46\u6885\u83f2\u65af\u62e6\u4f4f\u4e86\u4ed6\u3002</p><p>\u201c\u5e74\u9f84\uff0c\u201d\u5973\u5723\u6b66\u58eb\u7684\u53e3\u543b\u51b0\u51b7\u800c\u9690\u9690\u5e26\u7740\u4e9b\u8bb8\u5a01\u80c1\u7684\u610f\u5473\uff0c\u201c\u4f60\u7684\u5e74\u9f84\u3002\u201d</p><p>\u743c\u6069\u5f88\u5947\u602a\uff0c\u6885\u83f2\u65af\u6ca1\u4e8b\u903c\u95ee\u4eba\u5e74\u9f84\u505a\u4ec0\u4e48\u3002\u867d\u8bf4\u8fd9\u4e16\u754c\u6709\u70b9\u4e0d\u516c\u5e73\uff0c\u95ee\u5973\u4eba\u7684\u5e74\u9f84\u662f\u5931\u793c\uff0c\u95ee\u7537\u4eba\u7684\u5e74\u9f84\u5c31\u4e0d\u7b97\u5931\u793c\u2014\u2014\u4f46\u50cf\u6885\u83f2\u65af\u8fd9\u6837\uff0c\u6293\u7740\u4e00\u4e2a\u964c\u751f\u4eba\u903c\u95ee\uff0c\u800c\u4e14\u663e\u7136\u6709\u5a01\u80c1\u7684\u610f\u601d\uff0c\u8fd9\u5c31\u5f88\u4e0d\u5bf9\u52b2\u4e86\u3002\u5979\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u53c8\u4e0d\u662f\u7ba1\u6237\u7c4d\u7684\u8b66\u5bdf\uff0c\u6ca1\u9053\u7406\u8fd9\u4e48\u5e72\u3002</p><p>\u201c\u548c\u4f60\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u5c11\u5e74\u53cd\u9a73\u3002\u4ed6\u7684\u76f8\u8c8c\u5012\u5f88\u6e05\u79c0\uff0c\u53ea\u662f\u8138\u8272\u6709\u4e9b\u82cd\u767d\uff0c\u6ca1\u4ec0\u4e48\u8840\u8272\uff0c\u4eff\u4f5b\u957f\u5e74\u4e0d\u89c1\u9633\u5149\u4f3c\u7684\u3002\u4ed6\u8eab\u4e0a\u7a7f\u7740\u7070\u8272\u7684\u4e0d\u751a\u5408\u8eab\u7684\u65e7\u957f\u888d\uff0c\u4e24\u8fb9\u9576\u7740\u7eff\u8272\u7684\u6761\u7eb9\u88c5\u9970\uff0c\u8fd9\u79cd\u88c5\u675f\u743c\u6069\u5e76\u4e0d\u964c\u751f\uff0c\u9ad8\u5854\u4e2d\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u662f\u8fd9\u6253\u626e\u3002</p><p>\u8fd9\u5c11\u5e74\u662f\u70db\u5821\u91cc\u7684\u4eba\uff1f</p><p>\u6885\u83f2\u65af\u5bf9\u5c11\u5e74\u7684\u62d2\u7edd\u4f3c\u4e4e\u5145\u8033\u4e0d\u95fb\uff0c\u201c\u4f60\u7684\u5e74\u9f84\u3002\u201d\u5979\u518d\u6b21\u91cd\u590d\u7740\uff0c\u8bed\u6c14\u66f4\u52a0\u51b0\u51b7\uff0c\u743c\u6069\u6000\u7591\u5982\u679c\u5c11\u5e74\u518d\u62d2\u7edd\u56de\u7b54\u7684\u8bdd\uff0c\u6885\u83f2\u65af\u6050\u6015\u8981\u7acb\u523b\u62d4\u51fa\u5251\u6765\u62b5\u5728\u4ed6\u8116\u5b50\u4e0a\u3002</p><p>\u5c11\u5e74\u663e\u7136\u4e5f\u770b\u51fa\u4e86\u8fd9\u70b9\uff0c\u4ed6\u754f\u7f29\u4e86\u4e00\u4e0b\uff0c\u60f3\u5f80\u540e\u9000\uff0c\u4f46\u6885\u83f2\u65af\u5df2\u7ecf\u5c06\u4ed6\u903c\u5230\u5899\u8fb9\u3002\u201c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u5e74\u9f84\uff0c\u201d\u4ed6\u6700\u540e\u575a\u6301\u8bf4\uff0c\u201c\u4f46\u4f60\u5f97\u544a\u8bc9\u6211\u4f60\u662f\u8c01\u3002\u201d</p><p>\u201c\u6885\u83f2\u65af\uff0c\u6765\u81ea\u8ff7\u65af\u5353\u8bfa\uff0c\u201d\u5973\u5723\u6b66\u58eb\u7acb\u523b\u56de\u7b54\uff0c\u201c\u544a\u8bc9\u6211\u4f60\u7684\u5e74\u9f84\u3002\u201d</p><p>\u201c\u5341\u56db\u5c81\u3002\u201d\u5c11\u5e74\u8bf4\u3002</p><p>\u201c\u4f60\u7684\u7236\u4eb2\u662f\u8c01\uff1f\u201d\u6885\u83f2\u65af\u63a5\u7740\u95ee\u3002</p><p>\u5c11\u5e74\u77aa\u7740\u5979\uff0c\u4f46\u5f88\u5feb\u907f\u5f00\u4e86\u5bf9\u89c6\u7684\u76ee\u5149\u3002\u867d\u7136\u4e24\u4eba\u5e74\u9f84\u76f8\u5dee\u4f3c\u4e4e\u4e0d\u5927\uff0c\u4f46\u65c1\u4eba\u4e00\u770b\u4fbf\u89c9\u5f97\u5730\u4f4d\u60ac\u6b8a\u3002\u6885\u83f2\u65af\u8eab\u4e3a\u201c\u795e\u9009\u8005\u201d\u5723\u6b66\u58eb\uff0c\u4e45\u5c45\u9ad8\u4f4d\uff0c\u4e3e\u624b\u6295\u8db3\u81ea\u7136\u800c\u7136\u4fbf\u6709\u4e00\u79cd\u51db\u7136\u5a01\u4e25\uff0c\u8ba9\u5bf9\u65b9\u4e0d\u5f97\u4e0d\u4fef\u9996\u542c\u4ece\u3002</p><p>\u201c\u845b\u7acb\u5b89\u3002\u201d\u5c11\u5e74\u56de\u7b54\u3002</p><p>\u6885\u83f2\u65af\u5e94\u8be5\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u4f46\u5979\u4e5f\u4e0d\u518d\u8ffd\u95ee\u3002\u5c11\u5e74\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u5306\u5306\u5fd9\u5fd9\u79bb\u5f00\uff0c\u7a7f\u8fc7\u82b1\u56ed\u8d70\u8fdb\u5176\u4e2d\u4e00\u5ea7\u9ad8\u5854\u4e2d\uff0c\u4ed6\u679c\u7136\u662f\u70db\u5821\u91cc\u7684\u4eba\uff0c\u5426\u5219\u9ad8\u5854\u665a\u4e0a\u662f\u4e0d\u5bf9\u5ba2\u4eba\u5f00\u653e\u7684\u3002</p><p>\u743c\u6069\u6162\u6162\u8d70\u8fc7\u6765\uff0c\u6885\u83f2\u65af\u5e76\u6ca1\u6709\u60ca\u8bb6\uff0c\u663e\u7136\u65e9\u5c31\u53d1\u73b0\u4ed6\u5728\u65c1\u8fb9\u770b\u7740\u3002</p><p>\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u6885\u83f2\u65af\u6c89\u9ed8\u7740\uff0c\u770b\u7740\u5c11\u5e74\u7684\u80cc\u5f71\uff0c\u6ca1\u6709\u56de\u7b54\u3002\u5979\u7684\u773c\u795e\u6709\u4e9b\u5947\u602a\uff0c\u4eff\u4f5b\u662f\u770b\u89c1\u4e86\u5f88\u719f\u6089\u7684\u4eba\uff0c\u4f46\u5374\u6ca1\u6709\u4e1d\u6beb\u6e29\u548c\u4e4b\u610f\u3002</p><p>\u743c\u6069\u5fc3\u91cc\u5600\u5495\u7740\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u96be\u9053\u8fd9\u5c11\u5e74\u521a\u624d\u5f97\u7f6a\u4e86\u5979\uff1f\u4f46\u743c\u6069\u4e5f\u548c\u6885\u83f2\u65af\u8ba4\u8bc6\u8fd9\u4e48\u957f\u65f6\u95f4\u4e86\uff0c\u4ee5\u4ed6\u5bf9\u5973\u5723\u6b66\u58eb\u7684\u4e86\u89e3\uff0c\u5c31\u7b97\u8fd9\u5c11\u5e74\u81ea\u4e0d\u91cf\u529b\u60f3\u53bb\u5f3a\u5978\u5979\uff0c\u6885\u83f2\u65af\u4e5f\u4e0d\u53ef\u80fd\u4f1a\u662f\u8fd9\u79cd\u53cd\u5e94\u3002</p><p>\u201c\u4ed6\u4e0d\u4f1a\u662f\u4f60\u5f1f\u5f1f\u5427\u3002\u201d\u743c\u6069\u968f\u53e3\u8bf4\uff0c\u4ed6\u8111\u5b50\u91cc\u521a\u624d\u7a81\u7136\u5192\u51fa\u8fd9\u4e2a\u5ff5\u5934\uff0c\u89c9\u5f97\u8fd9\u6865\u6bb5\u4f3c\u4e4e\u5f88\u719f\u6089\u5f88\u8001\u5957\uff0c\u4eff\u4f5b\u5c31\u50cf\u90a3\u4e9b\u7535\u89c6\u5267\u91cc\u6f14\u7684\u4ece\u5c0f\u5931\u6563\u7684\u59d0\u5f1f\u91cd\u9022\uff0c\u8ffd\u95ee\u5bf9\u65b9\u7684\u5e74\u9f84\u4e4b\u7c7b\u2026\u2026\u5f53\u7136\uff0c\u4e5f\u53ef\u80fd\u662f\u5144\u59b9\uff0c\u4f46\u743c\u6069\u672c\u80fd\u5730\u5c31\u9ed8\u8ba4\u6885\u83f2\u65af\u6bd4\u8f83\u5e74\u957f\u3002</p><p>\u4ece\u67d0\u79cd\u7a0b\u5ea6\u8bf4\uff0c\u5728\u8fd9\u70b9\u4e0a\u6885\u83f2\u65af\u548c\u743c\u6069\u5012\u662f\u4e00\u6837\uff0c\u90fd\u662f\u5fc3\u7406\u5e74\u9f84\u8fdc\u8fdc\u8d85\u8fc7\u5b9e\u9645\u5e74\u9f84\u3002</p><p>\u5f53\u7136\uff0c\u8bdd\u4e00\u51fa\u53e3\uff0c\u743c\u6069\u5c31\u77e5\u9053\u4e0d\u5bf9\u3002\u4e14\u4e0d\u8bf4\u8fd9\u8bdd\u5f88\u5931\u793c\uff0c\u4fbf\u662f\u770b\u90a3\u5c11\u5e74\u7684\u76f8\u8c8c\uff0c\u4e5f\u548c\u6885\u83f2\u65af\u6ca1\u6709\u534a\u70b9\u76f8\u50cf\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u6709\u4ec0\u4e48\u8840\u7f18\u5173\u7cfb\u3002\u6885\u83f2\u65af\u7684\u8138\u4e0a\u8f6e\u5ed3\u68f1\u89d2\u5206\u660e\uff0c\u950b\u8292\u9690\u9690\uff0c\u90a3\u4e2a\u5c11\u5e74\u7684\u8138\u578b\u5219\u6241\u5e73\u6a21\u7cca\u591a\u4e86\uff0c\u4e94\u5b98\u4e2d\u89c4\u4e2d\u77e9\u5730\u6392\u5217\u5728\u8138\u4e0a\uff0c\u6beb\u65e0\u534a\u70b9\u5f15\u4eba\u6ce8\u76ee\u4e4b\u5904\uff0c\u770b\u4e0a\u53bb\u5c31\u662f\u4e00\u4e2a\u6700\u6700\u666e\u901a\u7684\u4eba\uff0c\u5c5e\u4e8e\u6df7\u5728\u4eba\u7fa4\u4e2d\u7edd\u5bf9\u627e\u4e0d\u51fa\u6765\u7684\u90a3\u79cd\u3002</p><p>\u4f46\u6885\u83f2\u65af\u7684\u53cd\u5e94\u5374\u6709\u4e9b\u5947\u602a\uff0c\u5c11\u5973\u5e76\u6ca1\u6709\u751f\u6c14\u2014\u2014\u8fd9\u5012\u662f\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u4f46\u4e5f\u5e76\u4e0d\u662f\u6f20\u7136\u5f53\u4f5c\u6ca1\u542c\u89c1\uff0c\u800c\u662f\u671d\u743c\u6069\u770b\u4e86\u4e00\u773c\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u4e0d\u7531\u81ea\u4e3b\u5730\u8df3\u4e86\u4e00\u8df3\uff0c\u6b63\u8981\u9053\u6b49\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u8f6c\u8fc7\u8eab\u79bb\u53bb\u3002</p></article>"}}}]);
