(window.webpackJsonp=window.webpackJsonp||[]).push([[1301],{1324:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u8282 \u5973\u6740\u624b",content:"<article><h2>\u7b2c\u4e94\u8282 \u5973\u6740\u624b</h2><p>\u4f3c\u4e4e\u53ea\u662f\u4e2a\u5c0f\u5c0f\u7684\u63d2\u66f2\uff0c\u6b64\u540e\u5e76\u6ca1\u6709\u53d1\u751f\u4ec0\u4e48\u610f\u6599\u4e4b\u5916\u7684\u4e8b\u60c5\u3002\u743c\u6069\u4f9d\u65e7\u6bcf\u5929\u53bb\u7ffb\u9605\u8d44\u6599\uff0c\u7814\u8bfb\u6cd5\u672f\uff0c\u8bd5\u56fe\u5f3a\u884c\u8bb0\u5fc6\u4e0b\u6765\uff0c\u867d\u7136\u8fd9\u975e\u5e38\u96be\uff1b\u6885\u83f2\u65af\u540c\u6837\u4e5f\u90fd\u5728\u9ad8\u5854\u4e2d\u770b\u4e66\uff0c\u5979\u7684\u9605\u8bfb\u5174\u8da3\u548c\u743c\u6069\u5b8c\u5168\u4e0d\u540c\uff0c\u4e3b\u8981\u504f\u91cd\u5386\u53f2\u548c\u54f2\u5b66\u65b9\u9762\u3002\u70db\u5821\u7684\u85cf\u4e66\u662f\u6309\u7c7b\u578b\u5212\u5206\u7684\uff0c\u5206\u522b\u6536\u5728\u4e0d\u540c\u7684\u9ad8\u5854\u4e2d\uff0c\u6240\u4ee5\u743c\u6069\u548c\u6885\u83f2\u65af\u767d\u5929\u57fa\u672c\u89c1\u4e0d\u5230\u9762\u3002</p><p>\u90a3\u4e2a\u88ab\u6885\u83f2\u65af\u903c\u95ee\u5e74\u9f84\u7684\u5c11\u5e74\uff0c\u5e94\u8be5\u5c31\u4f4f\u5728\u70db\u5821\u4e2d\uff0c\u4f46\u743c\u6069\u6b64\u540e\u5c31\u518d\u6ca1\u89c1\u8fc7\u4e86\u3002\u743c\u6069\u4e5f\u61d2\u5f97\u5728\u610f\uff0c\u4e00\u4e2a\u600e\u4e48\u770b\u600e\u4e48\u5e73\u51e1\u65e0\u5947\u7684\u5c0f\u7537\u5b69\uff0c\u6709\u4ec0\u4e48\u503c\u5f97\u5173\u6ce8\u7684\u3002\u518d\u8bf4\uff0c\u4ed6\u4eec\u4e5f\u53ea\u80fd\u5728\u8fd9\u91cc\u5446\u5341\u5929\uff0c\u7b49\u535a\u5f97\u4e4b\u95e8\u7684\u761f\u75ab\u5e73\u606f\u4e86\uff0c\u81ea\u7136\u5c31\u8981\u8fd4\u56de\u3002</p><p>\u6284\u5199\u9b54\u6cd5\u7684\u95ee\u9898\uff0c\u5176\u5b9e\u743c\u6069\u5012\u4e0d\u662f\u7279\u522b\u5934\u75bc\uff0c\u53cd\u6b63\u53c8\u4e0d\u6025\u4e8e\u4e00\u65f6\u3002\u7b49\u8fd9\u8d9f\u8dd1\u817f\u4efb\u52a1\u5b8c\u4e86\uff0c\u56de\u9634\u9b42\u57ce\uff0c\u81ea\u7136\u4fbf\u6709\u5b66\u4e60\u66f4\u591a\u6cd5\u672f\u7684\u673a\u4f1a\uff0c\u4f55\u5fc5\u5728\u8fd9\u91cc\u6328\u5bb0\u3002\u4ed6\u771f\u6b63\u5934\u75bc\u7684\uff0c\u662f\u62c9\u6c83\u514b\u3002</p><p>\u70db\u5821\u91cc\u539f\u672c\u662f\u5e94\u8be5\u6709\u4e0d\u5c11\u5173\u4e8e\u62c9\u6c83\u514b\u7684\u8d44\u6599\u7684\uff0c\u4f46\u5982\u4eca\u5168\u90fd\u4e0d\u89c1\u4e86\uff0c\u90fd\u88ab\u90a3\u4e2a\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u7ed9\u8c03\u8d70\u4e86\uff0c\u4e0d\u77e5\u9053\u5230\u5e95\u8981\u505a\u4ec0\u4e48\u3002\u743c\u6069\u8bd5\u7740\u5411\u5de5\u4f5c\u4eba\u5458\u6253\u63a2\u6d88\u606f\uff0c\u4f46\u5f97\u5230\u7684\u56de\u7b54\u662f\u8ba9\u4ed6\u81ea\u5df1\u53bb\u67e5\uff0c\u70db\u5821\u4e2d\u53ea\u8d1f\u8d23\u63d0\u4f9b\u8d44\u6599\uff0c\u4e0d\u8d1f\u8d23\u89e3\u7b54\u95ee\u9898\u3002\u4ed6\u62ff\u51fa\u4ecb\u7ecd\u4fe1\uff0c\u8bf7\u6c42\u62dc\u89c1\u5377\u518c\u5b88\u62a4\u8005\uff0c\u88ab\u76f4\u63a5\u544a\u77e5\u201c\u8d44\u683c\u4e0d\u591f\u201d\u3002</p><p>\u743c\u6069\u5bf9\u6b64\u5f88\u607c\u706b\uff0c\u4f46\u65e0\u8ba1\u53ef\u65bd\u3002\u4ed6\u67e5\u9605\u4e86\u6240\u6709\u70db\u5821\u5185\u5c45\u4f4f\u4eba\u5458\u7684\u540d\u5355\uff0c\u53d1\u73b0\u6ca1\u6709\u62c9\u6c83\u514b\u6b64\u4eba\u3002\u4e8b\u60c5\u662f\u8d8a\u6765\u8d8a\u9ebb\u70e6\u4e86\uff0c\u770b\u6765\u8fd8\u662f\u53ea\u6709\u56de\u535a\u5f97\u4e4b\u95e8\uff0c\u8bf7\u8389\u62c9\u5973\u516c\u7235\u5e2e\u5fd9\uff0c\u6216\u8bb8\u80fd\u6709\u5e0c\u671b\u3002\u96be\u602a\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bf4\u4ed6\u7684\u624b\u4e0b\u90fd\u63a8\u8131\u6ca1\u7a7a\uff0c\u8fd9\u79cd\u6beb\u65e0\u534a\u70b9\u5934\u7eea\u7684\u4e8b\u60c5\uff0c\u8c01\u90fd\u6ca1\u6cd5\u505a\uff0c\u7b80\u76f4\u5c31\u50cf\u5927\u6d77\u635e\u9488\u3002</p><p>\u65e2\u7136\u60f3\u660e\u767d\u4e86\u8fd9\u70b9\uff0c\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u5176\u4ed6\u4e8b\u60c5\u4e86\u3002\u6bcf\u5929\u7684\u65e5\u7a0b\u5b89\u6392\uff0c\u4e0d\u8fc7\u662f\u770b\u770b\u4e66\u3001\u7ffb\u8d44\u6599\u3001\u953b\u70bc\u9b54\u6cd5\uff0c\u6e05\u95f2\u81ea\u5728\uff0c\u4eff\u4f5b\u53c8\u56de\u5230\u4e86\u5728\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\u91cc\u7684\u65f6\u5149\u3002\u552f\u4e00\u7684\u9057\u61be\uff0c\u662f\u8eab\u8fb9\u6ca1\u6709\u4e86\u5a07\u5c0f\u6e29\u987a\u7684\u8299\u857e\u72c4\uff0c\u6362\u6210\u4e86\u6885\u83f2\u65af\u3002\u5973\u5723\u6b66\u58eb\u867d\u7136\u5728\u59ff\u5bb9\u6c14\u8d28\u4e0a\u66f4\u80dc\u4e00\u7b79\uff0c\u4f46\u5374\u4e0d\u662f\u90a3\u4e48\u4e56\u5de7\u542c\u8bdd\u2014\u2014\u6216\u8005\u8bf4\uff0c\u867d\u7136\u542c\u8bdd\uff0c\u4f46\u4e24\u4eba\u4e4b\u95f4\u7ec8\u7a76\u9694\u4e86\u4e00\u5c42\u65e0\u5f62\u7684\u5c4f\u969c\uff0c\u65e0\u6cd5\u771f\u6b63\u4eb2\u8fd1\u3002</p><p>\u201c\u6682\u65f6\u4e5f\u4e0d\u80fd\u6307\u671b\u8fd9\u4e48\u591a\u5427\u2026\u2026\u4eba\u4e0d\u8981\u592a\u8d2a\u5fc3\u4e86\u3002\u201d</p><p>\u53ea\u80fd\u8fd9\u6837\u5b89\u6170\u7740\u81ea\u5df1\uff0c\u743c\u6069\u5c06\u5168\u526f\u7cbe\u529b\u6295\u5165\u5230\u5b66\u4e60\u4e2d\u3002\u5728\u7814\u8bfb\u6cd5\u672f\u4e4b\u5916\uff0c\u4ed6\u8fd8\u7ffb\u9605\u4e86\u5927\u91cf\u6709\u5173\u5236\u9020\u9b54\u6cd5\u7269\u54c1\u7684\u8d44\u6599\uff0c\u8fd9\u662f\u4ed6\u7684\u504f\u597d\uff0c\u4ee5\u524d\u5728\u5b66\u6821\u91cc\u5c31\u559c\u6b22\u8fd9\u65b9\u9762\u3002\u4f46\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u7d20\u6765\u662f\u6bd4\u8f83\u5371\u9669\u7684\u4e8b\u60c5\uff0c\u4e3a\u4e86\u5b89\u5168\u8d77\u89c1\uff0c\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\u7981\u6b62\u5b66\u751f\u63a5\u89e6\u6bd4\u8f83\u9ad8\u6df1\u7684\u9b54\u6cd5\u9020\u7269\u5b66\uff0c\u53ea\u4f20\u6388\u57fa\u7840\u539f\u7406\u548c\u5165\u95e8\u6280\u5de7\u3002</p><p>\u9664\u4e86\u9b54\u6cd5\u65b9\u9762\uff0c\u743c\u6069\u4e3b\u8981\u7ffb\u9605\u7684\u5c31\u662f\u7269\u8d28\u754c\u7684\u5730\u7406\u3001\u56fd\u5bb6\u3001\u5b97\u6559\u3001\u5404\u5927\u4e3b\u8981\u52bf\u529b\u7b49\u65b9\u9762\u7684\u8d44\u6599\u4e86\u3002\u4ed6\u9700\u8981\u5c3d\u53ef\u80fd\u591a\u5730\u4e86\u89e3\u8fd9\u4e2a\u4e16\u754c\uff0c\u624d\u80fd\u66f4\u597d\u5730\u878d\u5165\u548c\u9002\u5e94\u8fdb\u6765\u3002\u4ed6\u4e0d\u53ef\u80fd\u4e00\u8f88\u5b50\u5446\u5728\u9634\u9b42\u57ce\u91cc\uff0c\u8fd9\u70b9\u786e\u5b9a\u65e0\u7591\u3002</p><p>\u867d\u7136\u65f6\u95f4\u77ed\u6682\uff0c\u4e0d\u53ef\u80fd\u6709\u592a\u6df1\u5165\u7684\u4e86\u89e3\uff0c\u4f46\u6bd4\u8d77\u9634\u9b42\u57ce\u5b66\u6821\u91cc\u5927\u591a\u90fd\u4e00\u7b14\u5e26\u8fc7\u7684\u6559\u6750\uff0c\u743c\u6069\u8fd8\u662f\u77e5\u9053\u4e86\u5f88\u591a\u4e1c\u897f\u3002</p><p>\u65f6\u95f4\u8fc7\u5f97\u5f88\u5feb\uff0c\u4e00\u8f6c\u773c\u95f4\uff0c\u5df2\u7ecf\u6765\u70db\u5821\u4e03\u5929\u4e86\u3002\u6309\u7167\u70db\u5821\u7684\u89c4\u77e9\uff0c\u4efb\u4f55\u5ba2\u4eba\u53ea\u80fd\u5728\u70db\u5821\u4e2d\u505c\u7559\u5341\u5929\uff0c\u7136\u540e\u5c31\u5fc5\u987b\u79bb\u5f00\uff0c\u5e76\u4e14\u5728\u4e00\u4e2a\u6708\u5185\u4e0d\u51c6\u518d\u6b21\u8fdb\u5165\u3002\u7b97\u7b97\u65f6\u95f4\uff0c\u5df2\u7ecf\u5feb\u5230\u590f\u81f3\u65e5\uff0c\u4e0d\u77e5\u9053\u535a\u5f97\u4e4b\u95e8\u90a3\u8fb9\u7684\u796d\u7940\u4eea\u5f0f\u51c6\u5907\u5f97\u600e\u4e48\u6837\u4e86\u3002</p><p>\u8fd9\u5929\u508d\u665a\uff0c\u4ed6\u4ece\u9ad8\u5854\u51fa\u6765\uff0c\u6b63\u9047\u4e0a\u6885\u83f2\u65af\u3002\u4e24\u4eba\u540c\u884c\u8d70\u51fa\u82b1\u56ed\uff0c\u6b63\u8981\u56de\u4f4f\u5904\u4f11\u606f\uff0c\u542c\u89c1\u57ce\u5821\u5165\u53e3\u5904\u6709\u52a8\u9759\u548c\u8bf4\u8bdd\u58f0\uff0c\u63a5\u7740\uff0c\u4e00\u4e2a\u5973\u4eba\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u5979\u8eab\u4e0a\u7a7f\u7740\u94f6\u8272\u7684\u8f7b\u8584\u94fe\u7532\uff0c\u4f46\u5176\u5b9e\u66f4\u50cf\u662f\u4e00\u79cd\u88c5\u9970\uff0c\u56e0\u4e3a\u4ec5\u4ec5\u53ea\u62a4\u4f4f\u4e86\u8eab\u4e0a\u7684\u67d0\u4e9b\u91cd\u8981\u90e8\u4f4d\uff0c\u9732\u51fa\u5927\u7247\u7684\u808c\u80a4\u2014\u2014\u5979\u5728\u94fe\u7532\u91cc\u4f3c\u4e4e\u90fd\u6ca1\u6709\u7a7f\u886c\u8863\uff0c\u6216\u8bb8\u7a7f\u4e86\u5185\u8863\uff1b\u80f3\u818a\u3001\u5927\u817f\u548c\u5c0f\u8179\u5168\u90fd\u6beb\u4e0d\u63a9\u9970\u5730\u66b4\u9732\u5728\u7a7a\u6c14\u4e2d\uff0c\u7c89\u5149\u81f4\u81f4\uff0c\u8bf1\u60d1\u65e0\u6bd4\u3002</p><p>\u6625\u5929\u5df2\u7ecf\u5feb\u8fc7\u53bb\uff0c\u590f\u65e5\u5c06\u81f3\uff0c\u6c14\u5019\u6e10\u6e10\u6e29\u6696\u8d77\u6765\uff0c\u7a7f\u5f97\u5c11\u70b9\u5012\u4e5f\u6b63\u5e38\u2026\u2026\u4f46\u5c11\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u4f3c\u4e4e\u8fd8\u662f\u592a\u8fc7\u66b4\u9732\u4e86\u70b9\u3002</p><p>\u5973\u4eba\u957f\u5f97\u5e76\u4e0d\u7b97\u7279\u522b\u6f02\u4eae\uff0c\u4f46\u975e\u5e38\u5996\u5a9a\uff0c\u8eab\u6bb5\u5a40\u5a1c\u4fee\u957f\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\u81ea\u6709\u4e00\u79cd\u6210\u719f\u98ce\u60c5\uff0c\u5bf9\u7537\u4eba\u9887\u6709\u5438\u5f15\u529b\u3002\u743c\u6069\u5fcd\u4e0d\u4f4f\u591a\u770b\u4e86\u51e0\u773c\uff0c\u5bf9\u65b9\u4f3c\u4e4e\u53d1\u73b0\u4e86\uff0c\u4e5f\u671d\u8fd9\u8fb9\u770b\u8fc7\u6765\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4e3e\u624b\u6253\u4e86\u4e2a\u62db\u547c\u3002</p><p>\u743c\u6069\u793c\u8c8c\u5730\u70b9\u70b9\u5934\uff0c\u7b97\u662f\u56de\u7b54\u3002\u4ed6\u5feb\u901f\u731c\u6d4b\u7740\u5bf9\u65b9\u7684\u8eab\u4efd\uff0c\u5982\u679c\u4e0d\u662f\u90a3\u5957\u94fe\u7532\u5b9e\u5728\u592a\u8fc7\u66b4\u9732\uff0c\u743c\u6069\u4f1a\u9ed8\u8ba4\u5979\u662f\u4e2a\u8001\u7ec3\u7684\u5192\u9669\u8005\uff1b\u4f46\u5982\u4eca\u2026\u2026\u597d\u5427\uff0c\u867d\u7136\u8fd9\u5f88\u4e0d\u793c\u8c8c\uff0c\u4f46\u743c\u6069\u4e0d\u5f97\u4e0d\u6000\u7591\u5979\u662f\u4e2a\u5993\u5973\uff0c\u6216\u8005\u662f\u6709\u66b4\u9732\u7656\u3002</p><p>\u5929\u8272\u5df2\u665a\uff0c\u536b\u5175\u5e26\u9886\u7740\u5979\u53bb\u4f4f\u5904\uff0c\u4ece\u743c\u6069\u548c\u6885\u83f2\u65af\u8eab\u8fb9\u8d70\u8fc7\u3002\u5728\u64e6\u80a9\u800c\u8fc7\u7684\u65f6\u5019\uff0c\u5973\u4eba\u4fa7\u8fc7\u8138\u6765\uff0c\u671d\u743c\u6069\u7728\u4e86\u7728\u773c\u775b\u3002</p><p>\u201c\u665a\u4e0a\u7761\u89c9\u8981\u8bb0\u5f97\u5173\u597d\u95e8\u54e6\uff0c\u5c0f\u5e05\u54e5\u3002\u201d\u5979\u7528\u5145\u6ee1\u6311\u9017\u7684\u58f0\u97f3\u8f7b\u58f0\u8bf4\uff0c\u54af\u54af\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u8bdd\u8bed\u4e2d\u4f3c\u4e4e\u6709\u67d0\u79cd\u6697\u793a\u610f\u5473\uff0c\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u542c\u4e0d\u51fa\u6765\u3002\u867d\u8bf4\u4ed6\u4e0d\u751a\u559c\u6b22\u8fd9\u79cd\u7c7b\u578b\u7684\u5973\u5b50\uff0c\u4f46\u5982\u679c\u81ea\u5df1\u9001\u4e0a\u95e8\uff0c\u90a3\u4e48\u5e74\u8f7b\u7684\u5deb\u5e08\u4e5f\u5e76\u4e0d\u4ecb\u610f\u5728\u5e8a\u4e0a\u804a\u804a\u5929\u3002</p><p>\u53ea\u662f\uff0c\u81ea\u5df1\u6709\u8fd9\u4e48\u597d\u8fd0\u6c14\u4e48\uff1f</p><p>\u53ef\u60dc\u624b\u5934\u6ca1\u6709\u955c\u5b50\uff0c\u5426\u5219\u743c\u6069\u80af\u5b9a\u5c31\u8981\u638f\u51fa\u6765\u7167\u7167\uff0c\u770b\u81ea\u5df1\u662f\u4e0d\u662f\u53d8\u5e05\u4e86\u5f88\u591a\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u81ea\u8ba4\u957f\u76f8\u8fd8\u4e0d\u9519\uff0c\u5374\u8fd8\u8fdc\u6ca1\u5230\u80fd\u8ba9\u5973\u4eba\u4e3b\u52a8\u6295\u6000\u9001\u62b1\u7684\u5730\u6b65\u2026\u2026</p><p>\u7b97\u4e86\uff0c\u4e0d\u60f3\u8fd9\u4e9b\u3002</p><p>\u773c\u89d2\u4f59\u5149\u77a5\u89c1\u8eab\u8fb9\u7684\u6885\u83f2\u65af\uff0c\u5374\u53d1\u73b0\u5979\u4e5f\u5728\u770b\u7740\u6b63\u6e10\u6e10\u8fdc\u53bb\u7684\u5973\u4eba\u3002\u201c\u5979\u662f\u4e2a\u6740\u624b\uff0c\u201d\u6885\u83f2\u65af\u7a81\u7136\u5f00\u53e3\u8bf4\uff0c\u201c\u5979\u60f3\u6740\u4eba\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u6ca1\u7acb\u523b\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5979\u662f\u4e2a\u6740\u624b\uff0c\u6765\u6b64\u662f\u4e3a\u4e86\u6740\u4eba\u3002\u201d\u6885\u83f2\u65af\u91cd\u590d\u4e86\u4e00\u904d\u3002</p><p>\u743c\u6069\u6709\u4e9b\u4e0d\u592a\u76f8\u4fe1\uff0c\u6885\u83f2\u65af\u53c8\u4e0d\u662f\u9884\u8a00\u5e08\uff0c\u770b\u522b\u4eba\u4e00\u773c\u5c31\u77e5\u9053\u5bf9\u65b9\u8eab\u4efd\u6765\u610f\u3002\u4e0d\u8fc7\uff0c\u5979\u662f\u4e2a\u4f18\u79c0\u7684\u5723\u6b66\u58eb\uff0c\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u6216\u8bb8\u786e\u5b9e\u662f\u770b\u51fa\u4e86\u4ec0\u4e48\u8ff9\u8c61\u5427\u3002</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u5979\u540e\u8170\u4e0a\u6709\u4e24\u67c4\u77ed\u5251\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u90a3\u662f\u6740\u624b\u624d\u4f1a\u7528\u7684\u6b66\u5668\u3002\u5979\u6bcf\u4e00\u6b65\u8fc8\u51fa\u7684\u8ddd\u79bb\u90fd\u662f\u5b8c\u5168\u76f8\u7b49\u7684\uff0c\u8fd9\u8bf4\u660e\u5979\u53d7\u8fc7\u4e25\u683c\u7684\u6280\u5de7\u8bad\u7ec3\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6ce8\u610f\u5230\u90a3\u4e24\u67c4\u77ed\u5251\uff0c\u4e0d\u8fc7\u65e2\u7136\u6885\u83f2\u65af\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4e5f\u81ea\u7136\u76f8\u4fe1\u3002\u95ee\u9898\u662f\uff0c\u4ec5\u4ec5\u8fd9\u4e9b\u7406\u7531\uff0c\u5e76\u4e0d\u8db3\u4ee5\u4f5c\u4e3a\u5224\u5b9a\u5bf9\u65b9\u662f\u4e2a\u6740\u624b\u7684\u4f9d\u636e\u554a\u2014\u2014\u5c31\u7b97\u80fd\uff0c\u90a3\u53c8\u600e\u4e48\u80fd\u770b\u51fa\u5bf9\u65b9\u8981\u6765\u6740\u4eba\u3002</p><p>\u201c\u611f\u89c9\u7f62\u4e86\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u4f3c\u4e4e\u4e0d\u80af\u518d\u591a\u89e3\u91ca\uff0c\u4e5f\u53ef\u80fd\u539f\u672c\u5c31\u65e0\u6cd5\u89e3\u91ca\u3002</p><p>\u743c\u6069\u4e5f\u4e0d\u65b9\u4fbf\u518d\u591a\u95ee\uff0c\u81ea\u56de\u4f4f\u5904\uff0c\u53ea\u662f\u5fc3\u4e2d\u5e76\u4e0d\u592a\u76f8\u4fe1\u6885\u83f2\u65af\u7684\u5224\u65ad\u3002\u70db\u5821\u867d\u7136\u770b\u4e0a\u53bb\u4e0d\u8fc7\u662f\u4e2a\u5e73\u548c\u7684\u56fe\u4e66\u9986\uff0c\u4f46\u4ee5\u8fd9\u6837\u82db\u523b\u5947\u602a\u7684\u89c4\u77e9\uff0c\u8fd9\u6837\u9ad8\u6602\u79bb\u8c31\u7684\u6536\u8d39\uff0c\u53c8\u62e5\u6709\u5982\u6b64\u6d69\u5982\u70df\u6d77\u7684\u73cd\u8d35\u85cf\u4e66\u548c\u7f55\u89c1\u8d44\u6599\uff0c\u5374\u80fd\u4e00\u76f4\u5c79\u7acb\u4e0d\u5012\uff0c\u8fd9\u5176\u4e2d\u7684\u9053\u7406\uff0c\u81ea\u7136\u9887\u503c\u5f97\u5bfb\u5473\u3002</p><p>\u5f88\u5c11\u6709\u4eba\u6562\u5728\u70db\u5821\u95f9\u4e8b\u7684\u3002</p><p>\u70db\u5821\u4e0d\u4ec5\u6709\u77e5\u8bc6\u548c\u4e66\u5377\uff0c\u540c\u6837\u4e5f\u6709\u5200\u5251\u4e0e\u9b54\u6cd5\uff0c\u53ea\u662f\u5e73\u65f6\u6f5c\u85cf\u7740\u7f62\u4e86\u3002\u743c\u6069\u5e76\u4e0d\u6253\u7b97\u53bb\u5c1d\u8bd5\u4e00\u4e0b\uff0c\u4f46\u4ed6\u5446\u4e86\u8fd9\u4e48\u591a\u5929\uff0c\u9690\u9690\u7ea6\u7ea6\u4e5f\u80fd\u611f\u89c9\u5230\u3002</p><p>\u6574\u5ea7\u70db\u5821\uff0c\u5e94\u8be5\u90fd\u5904\u4e8e\u9b54\u6cd5\u7ed3\u754c\u7684\u7b3c\u7f69\u4fdd\u62a4\u4e0b\uff0c\u743c\u6069\u6000\u7591\u5f88\u53ef\u80fd\u662f\u67d0\u79cd\u5927\u578b\u8ff7\u9501\u3002\u5177\u4f53\u662f\u4ec0\u4e48\uff0c\u4ee5\u4ed6\u76ee\u524d\u7684\u80fd\u529b\u8fd8\u611f\u5e94\u4e0d\u51fa\u6765\uff0c\u4f46\u663e\u7136\u975e\u5e38\u7cbe\u5bc6\u800c\u590d\u6742\uff0c\u529b\u91cf\u5f3a\u5927\u3002\u5728\u8fdb\u5165\u70db\u5821\u7684\u7b2c\u4e00\u5929\uff0c\u5f15\u5bfc\u8005\u5c31\u66fe\u7ecf\u8bf4\u8fc7\uff1a\u5728\u6b64\u5730\uff0c\u4e00\u5207\u4f20\u9001\u7c7b\u578b\u7684\u9b54\u6cd5\u5168\u90fd\u88ab\u7981\u5236\u4e86\u3002\u743c\u6069\u77e5\u9053\u9ad8\u660e\u7684\u5deb\u5e08\u662f\u53ef\u4ee5\u7a81\u7136\u6253\u65ad\u5176\u4ed6\u4eba\u4f7f\u7528\u4f20\u9001\u9b54\u6cd5\u7684\uff0c\u4f46\u8981\u8bbe\u4e0b\u8fd9\u79cd\u957f\u671f\u7684\u3001\u5927\u8303\u56f4\u7684\u666e\u904d\u9b54\u6cd5\u7981\u5236\uff0c\u7b49\u4e8e\u662f\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u5c01\u9501\u8fd9\u7247\u533a\u57df\u7684\u6240\u6709\u9b54\u6cd5\u7f51\u7edc\uff0c\u90a3\u5c31\u662f\u6781\u9ad8\u7684\u80fd\u529b\u4e86\u3002\u5c31\u7b97\u662f\u9634\u9b42\u57ce\u7684\u5927\u5deb\u5e08\uff0c\u4e5f\u672a\u5fc5\u80fd\u6709\u51e0\u4e2a\u53ef\u4ee5\u529e\u5230\u7684\u3002\u4ed6\u5012\u4e5f\u4e0d\u76f8\u4fe1\u70db\u5821\u91cc\u6709\u8fd9\u7b49\u4eba\u7269\uff0c\u731c\u6d4b\u66f4\u53ef\u80fd\u662f\u51ed\u501f\u4ec0\u4e48\u5f3a\u529b\u9b54\u6cd5\u7269\u54c1\u6240\u81f4\u3002</p><p>\u603b\u4e4b\uff0c\u8981\u6740\u4eba\uff0c\u70db\u5821\u5e76\u4e0d\u662f\u4e2a\u597d\u5730\u65b9\u3002</p><p>\u56de\u5230\u4f4f\u5904\uff0c\u5403\u5b8c\u665a\u9910\u540e\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5404\u81ea\u56de\u623f\u95f4\u4f11\u606f\u3002\u8fd9\u4e9b\u5929\uff0c\u743c\u6069\u4e13\u5fc3\u4e8e\u9b54\u6cd5\u6280\u5de7\u7684\u953b\u70bc\uff0c\u56de\u5fc6\u6d88\u5316\u767d\u5929\u5728\u9ad8\u5854\u4e2d\u770b\u5230\u7684\u77e5\u8bc6\uff0c\u628a\u665a\u4e0a\u7684\u5a31\u4e50\u6d3b\u52a8\u90fd\u7ed9\u53d6\u6d88\u4e86\uff0c\u6885\u83f2\u65af\u81ea\u7136\u66f4\u6ca1\u6709\u610f\u89c1\u3002</p><p>\u743c\u6069\u56de\u5230\u623f\u95f4\uff0c\u7ffb\u5f00\u9b54\u6cd5\u4e66\uff0c\u5148\u5feb\u901f\u6e29\u4e60\u4e00\u904d\u5df2\u7ecf\u719f\u7ec3\u638c\u63e1\u7684\u6cd5\u672f\uff0c\u518d\u5c06\u4e00\u4e9b\u65e9\u65e9\u8bb0\u5728\u4e66\u4e2d\uff0c\u4f46\u4e00\u76f4\u6ca1\u6709\u4ed4\u7ec6\u7814\u8bfb\u7684\u4f4e\u9636\u6cd5\u672f\u6d4f\u89c8\u4e00\u904d\uff0c\u6700\u540e\u5c0f\u5fc3\u5730\u5728\u623f\u95e8\u4e0a\u548c\u5e8a\u7684\u5468\u56f4\u90fd\u5e03\u4e0b\u8b66\u6212\u7ed3\u754c\uff0c\u8eba\u5728\u5e8a\u4e0a\u5f00\u59cb\u9677\u5165\u51a5\u60f3\u3002</p><p>\u5728\u524d\u51e0\u5929\uff0c\u4ed6\u5df2\u7ecf\u9690\u9690\u7ea6\u7ea6\u5730\u611f\u89c9\u5230\u4e86\u66f4\u6df1\u5c42\u6b21\u7684\u9b54\u7f51\uff0c\u4f46\u603b\u8fd8\u662f\u9694\u4e86\u4e00\u7ebf\u3002\u4e16\u754c\u4e0a\u4e0d\u8bba\u4ec0\u4e48\u6280\u827a\u90fd\u662f\u5982\u6b64\uff0c\u8d8a\u5f80\u4e0a\u8d8a\u96be\uff0c\u8fdb\u6b65\u8d8a\u7f13\u6162\uff0c\u9b54\u6cd5\u4e5f\u4e0d\u4f8b\u5916\u3002\u743c\u6069\u8fd9\u4e9b\u5929\u5df2\u7ecf\u7b97\u662f\u975e\u5e38\u52aa\u529b\uff0c\u4f46\u8fd9\u6700\u540e\u4e00\u6b65\u600e\u4e48\u4e5f\u51b2\u4e0d\u7834\u3002</p><p>\u518d\u4e00\u6b21\u5730\u51dd\u805a\u5fc3\u795e\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u7684\u610f\u8bc6\u7f13\u7f13\u4e0b\u6c89\uff0c\u6d78\u5165\u9ed1\u6697\u7684\u6df1\u6e0a\u4e4b\u4e2d\u3002\u51b0\u51b7\u7684\u611f\u89c9\u4ece\u56db\u9762\u516b\u65b9\u4f20\u6765\uff0c\u4ed6\u52aa\u529b\u5bfb\u627e\u7740\u51fa\u53e3\u7684\u5149\u4eae\uff0c\u5374\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u743c\u6069\u77e5\u9053\u4ed6\u4e0d\u80fd\u5728\u8fd9\u9ed1\u6697\u51b0\u6e0a\u4e2d\u505c\u7559\u592a\u4e45\uff0c\u90a3\u5bf9\u5deb\u5e08\u7684\u5fc3\u667a\u662f\u4e00\u79cd\u4f24\u5bb3\uff0c\u4f46\u5c61\u8bd5\u65e0\u529f\uff0c\u4ed6\u53c8\u5b9e\u5728\u4e0d\u7518\u5fc3\u3002\u6b63\u5f53\u4ed6\u8e0c\u8e87\u662f\u6700\u540e\u5c1d\u8bd5\u4e00\u6b21\uff0c\u8fd8\u662f\u5c31\u6b64\u653e\u624b\u7684\u65f6\u5019\uff0c\u7a81\u7136\u4e00\u9635\u8b66\u5146\u4f20\u6765\u3002</p><p>\u90a3\u662f\u4ed6\u8bbe\u5728\u5e8a\u8fb9\u7684\u8b66\u6212\u7ed3\u754c\uff0c\u4ea7\u751f\u4e00\u4e2a\u534a\u5f84\u5927\u7ea6\u4e8c\u5341\u82f1\u5c3a\u7684\u65e0\u5f62\u5fae\u5f31\u529b\u573a\u3002\u53ea\u8981\u6709\u4eba\u8e0f\u5165\u8fd9\u4e00\u8303\u56f4\uff0c\u743c\u6069\u7acb\u523b\u5c31\u80fd\u5bdf\u89c9\u5230\uff0c\u65e0\u8bba\u4ed6\u662f\u5728\u7761\u7740\u65f6\u8fd8\u662f\u6e05\u9192\u65f6\u3002</p><p>\u6885\u83f2\u65af\uff1f\u4e0d\u5bf9\uff0c\u5973\u5723\u6b66\u58eb\u4ece\u4e0d\u4f1a\u5728\u8fd9\u4e2a\u65f6\u5019\u8fc7\u6765\u4e32\u95e8\uff0c\u4f55\u51b5\u5979\u80af\u5b9a\u4f1a\u5148\u6572\u95e8\u3002</p><p>\u4ed6\u7f13\u7f13\u7741\u5f00\u773c\u775b\uff0c\u8111\u6d77\u4e2d\u5df2\u7ecf\u6d6e\u73b0\u4e00\u6bb5\u5492\u6587\uff0c\u53ea\u8981\u53d1\u73b0\u4e0d\u5bf9\uff0c\u7acb\u523b\u5c31\u5148\u4e0b\u624b\u5236\u4f4f\u5bf9\u65b9\u3002\u4f46\u5f53\u4ed6\u5b8c\u5168\u7741\u5f00\u773c\u775b\u540e\uff0c\u5374\u53d1\u73b0\u623f\u95f4\u91cc\u7a7a\u65e0\u4e00\u4eba\u3002</p><p>\u600e\u4e48\u4f1a\uff1f\u521a\u624d\u660e\u660e\u6709\u4eba\u8fdb\u5165\u4e86\u8b66\u6212\u7ed3\u754c\u7684\u8303\u56f4\uff0c\u5426\u5219\u81ea\u5df1\u600e\u4e48\u4f1a\u611f\u5e94\u5230\u3002</p><p>\u95e8\u7a97\u90fd\u597d\u597d\u5730\u5173\u7740\uff0c\u6ca1\u6709\u4e1d\u6beb\u52a8\u8fc7\u7684\u8ff9\u8c61\uff0c\u623f\u95f4\u91cc\u4e5f\u786e\u5b9e\u6ca1\u6709\u5176\u4ed6\u4eba\uff0c\u743c\u6069\u4ece\u5e8a\u4e0a\u5750\u8d77\uff0c\u7591\u60d1\u7740\uff0c\u96be\u9053\u81ea\u5df1\u7684\u6cd5\u672f\u51fa\u9519\u4e86\uff1f</p><p>\u5bf9\u4e8e\u4e00\u4e2a\u5deb\u5e08\u6765\u8bf4\uff0c\u8fd9\u53ef\u662f\u4e2a\u5927\u95ee\u9898\u3002\u5e73\u65f6\u5012\u8fd8\u7f62\u4e86\uff0c\u5982\u679c\u771f\u5230\u751f\u6b7b\u640f\u6740\u7684\u65f6\u5019\uff0c\u4e00\u70b9\u70b9\u504f\u5dee\u6240\u5bfc\u81f4\u7684\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u81ea\u5df1\u7684\u6027\u547d\u5b8c\u86cb\u3002</p><p>\u6b63\u5f53\u4ed6\u8003\u8651\u8981\u4e0d\u8981\u91cd\u65b0\u590d\u4e60\u4e00\u4e0b\u8fd9\u4e2a\u6cd5\u672f\u7684\u65f6\u5019\uff0c\u89d2\u843d\u7684\u9634\u5f71\u4e2d\uff0c\u7a81\u7136\u4f20\u6765\u4e00\u4e2a\u5a07\u5a9a\u7684\u58f0\u97f3\u3002</p><p>\u201c\u665a\u4e0a\u597d\u554a\uff0c\u5c0f\u5e05\u54e5\uff0c\u4f60\u597d\u50cf\u6ca1\u6709\u5173\u597d\u95e8\u54e6\u3002\u201d</p></article>"}}}]);
