(window.webpackJsonp=window.webpackJsonp||[]).push([[1304],{1327:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516b\u8282 \u53c8\u5fd8\u4e86\u5173\u597d\u95e8",content:"<article><h2>\u7b2c\u516b\u8282 \u53c8\u5fd8\u4e86\u5173\u597d\u95e8</h2><p>\u7b2c\u4e8c\u5929\uff0c\u743c\u6069\u8d77\u5e8a\uff0c\u4f9d\u65e7\u548c\u6885\u83f2\u65af\u53bb\u9ad8\u5854\u770b\u4e66\u3002</p><p>\u6709\u4e00\u4ef6\u4e8b\u5f88\u5947\u602a\uff0c\u6628\u665a\u4ed6\u5728\u623f\u95f4\u91cc\u7528\u4e86\u4e2a\u77f3\u5f39\u672f\uff0c\u7838\u5728\u5899\u58c1\u4e0a\u7206\u88c2\u5f00\u6765\u3002\u867d\u8bf4\u77f3\u5f39\u91cc\u6ca1\u6709\u706b\u836f\uff0c\u4e0d\u81f3\u4e8e\u771f\u50cf\u624b\u69b4\u5f39\u7206\u70b8\u4e00\u6837\u60ca\u5929\u52a8\u5730\uff0c\u4f46\u5230\u5e95\u4e5f\u662f\u52a8\u9759\u4e0d\u5c0f\u3002\u800c\u4e14\u5f53\u65f6\u662f\u6df1\u591c\uff0c\u4e07\u7c41\u4ff1\u5bc2\uff0c\u8fd9\u4e48\u7a81\u7136\u4e00\u58f0\u7206\u70b8\uff0c\u7406\u5f53\u5f88\u591a\u4eba\u90fd\u542c\u89c1\u624d\u5bf9\u3002\u4f46\u5374\u53ea\u6709\u6885\u83f2\u65af\u4e00\u4e2a\u4eba\u8fc7\u6765\uff0c\u5176\u4ed6\u4eba\u4eff\u4f5b\u90fd\u7761\u6b7b\u4e86\u4f3c\u7684\u3002</p><p>\u743c\u6069\u5f53\u7136\u4e0d\u76f8\u4fe1\u70db\u5821\u91cc\u7684\u5bb6\u4f19\u7761\u7720\u8d28\u91cf\u90fd\u8fd9\u4e48\u597d\uff0c\u8fd9\u5176\u4e2d\u5fc5\u5b9a\u6709\u4ec0\u4e48\u7f18\u6545\u3002\u4ed6\u751a\u81f3\u90fd\u80e1\u4e71\u731c\u6d4b\u8fd9\u4e9b\u4eba\u662f\u4e0d\u662f\u90fd\u5df2\u7ecf\u88ab\u5973\u6740\u624b\u5e72\u6389\u706d\u53e3\u4e86\uff0c\u5f53\u7136\u4e8b\u5b9e\u8bc1\u660e\u6ca1\u8fd9\u56de\u4e8b\u3002</p><p>\u96be\u9053\u8bf4\uff0c\u70db\u5821\u8fd9\u4e48\u591a\u5b88\u536b\uff0c\u8fd9\u4e48\u591a\u5deb\u5e08\u548c\u7267\u5e08\uff0c\u90fd\u4e0d\u7ba1\u70db\u5821\u5185\u7684\u6253\u67b6\u6597\u6bb4\u4e48\uff1f\u5982\u679c\u771f\u662f\u8fd9\u6837\uff0c\u90a3\u70db\u5821\u54ea\u91cc\u8fd8\u80fd\u7559\u5b58\u81f3\u4eca\uff0c\u65e9\u8be5\u88ab\u62c6\u6210\u5e73\u5730\u4e86\u624d\u662f\u3002</p><p>\u201c\u65e9\u4e0a\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d\u8d1f\u8d23\u743c\u6069\u7684\u90a3\u4f4d\u5f15\u5bfc\u8005\u6b63\u5728\u82b1\u56ed\u524d\u606d\u5019\u3002</p><p>\u201c\u65e9\u4e0a\u597d\u3002\u201d\u743c\u6069\u8bf4\uff0c\u5fae\u5fae\u8eac\u8eab\uff0c\u8ddf\u968f\u4ed6\u8d70\u8fdb\u9ad8\u5854\u3002</p><p>\u4ed6\u4eca\u5929\u4e3b\u8981\u51c6\u5907\u67e5\u9605\u7684\u8d44\u6599\uff0c\u662f\u5386\u53f2\u4e0a\u8457\u540d\u7684\u5deb\u5e08\u4ecb\u7ecd\uff0c\u6216\u8005\u53ef\u4ee5\u8bf4\u662f\u201c\u5deb\u5e08\u540d\u4eba\u5802\u201d\u3002\u524d\u4e16\u5728\u5927\u5b66\u91cc\u4e0a\u8bfe\u7684\u65f6\u5019\uff0c\u6559\u6388\u4e0a\u8bb2\u53f0\uff0c\u65e2\u4e0d\u8bb2\u539f\u7406\uff0c\u4e5f\u4e0d\u8bb2\u5386\u53f2\uff0c\u9996\u5148\u8be6\u7ec6\u4ecb\u7ecd\u8fd9\u4e00\u5b66\u672f\u9886\u57df\u5185\u7684\u540d\u5bb6\u524d\u8f88\u3002\u5b66\u751f\u4e0d\u89e3\u5176\u6545\uff0c\u6559\u6388\u8bf4\uff1a\u201c\u65e0\u4ed6\uff0c\u65e2\u7136\u5404\u4f4d\u90fd\u5728\u8fd9\u4e2a\u5708\u5b50\u91cc\u6df7\uff0c\u603b\u8981\u77e5\u9053\u8fd9\u4e2a\u5708\u5b50\u91cc\u6709\u54ea\u4e9b\u5f3a\u8005\u3002\u201d\u743c\u6069\u5bf9\u8fd9\u53e5\u8bdd\u5370\u8c61\u6df1\u523b\u3002</p><p>\u786e\u5b9e\uff0c\u65e2\u7136\u6210\u4e3a\u5deb\u5e08\uff0c\u65e2\u7136\u5df2\u7ecf\u8eab\u5728\u7269\u8d28\u754c\uff0c\u603b\u8981\u4e86\u89e3\u5386\u53f2\u4e0a\u66fe\u7ecf\u6709\u8fc7\u54ea\u4e9b\u8457\u540d\u7684\u524d\u8f88\uff0c\u800c\u73b0\u4eca\u4e16\u754c\u4e0a\u53c8\u6709\u54ea\u4e9b\u9ad8\u4eba\u3002</p><p>\u6839\u636e\u4ed6\u5728\u9634\u9b42\u57ce\u91cc\u5b66\u5230\u7684\u77e5\u8bc6\uff1a\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u4eba\u7c7b\u6587\u660e\uff0c\u5927\u4f53\u4e0a\u53ef\u4ee5\u5206\u4e3a\u4e09\u4e2a\u9636\u6bb5\u3002\u7b2c\u4e00\u9636\u6bb5\u662f\u4f20\u8bf4\u65f6\u4ee3\uff0c\u5927\u7ea6\u662f\u6307\u4ece\u521b\u4e16\u4e4b\u521d\u5230\u4e94\u5343\u5e74\u524d\uff0c\u56e0\u4e3a\u8d44\u6599\u532e\u4e4f\uff0c\u65e0\u53f2\u53ef\u5f81\uff0c\u80fd\u6d41\u4f20\u4e0b\u6765\u7684\u57fa\u672c\u90fd\u662f\u4f20\u8bf4\uff1b\u7b2c\u4e8c\u9636\u6bb5\u88ab\u547d\u540d\u4e3a\u8010\u745f\u65f6\u4ee3\uff08\u8fd9\u5f53\u7136\u662f\u9634\u9b42\u57ce\u4ece\u81ea\u5df1\u7684\u7acb\u573a\u4e0a\u6765\u547d\u540d\uff09\uff0c\u4e3b\u8981\u6307\u7684\u662f\u4ece\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u5efa\u7acb\u5230\u9668\u706d\u8fd9\u4e00\u9636\u6bb5\uff08-3859DR\u5230-339DR\uff09\uff0c\u6216\u8005\u901a\u4fd7\u5730\u8bf4\uff0c\u662f\u8ddd\u4eca\u4e94\u5343\u5e74\u524d\u81f3\u8ddd\u4eca\u4e00\u5343\u4e03\u767e\u5e74\u524d\u8fd9\u4e00\u65f6\u671f\uff1b\u8010\u745f\u9668\u706d\u4e4b\u540e\u81f3\u4eca\uff0c\u88ab\u7edf\u79f0\u4e3a\u201c\u540e\u8010\u745f\u65f6\u4ee3\u201d\uff0c\u6216\u8005\u79f0\u4e3a\u201c\u65b0\u65f6\u4ee3\u201d\u3002</p><p>\u8fd9\u79cd\u5206\u7c7b\u65b9\u6cd5\u5e26\u6709\u6781\u5f3a\u7684\u201c\u8010\u745f\u201d\u8272\u5f69\uff0c\u5f88\u6b63\u5e38\uff0c\u56e0\u4e3a\u662f\u9634\u9b42\u57ce\u91cc\u6559\u7684\u3002\u4e0d\u8fc7\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ec0\u4e48\u5206\u7c7b\u65b9\u6cd5\u5e76\u4e0d\u8981\u7d27\uff0c\u53ea\u8981\u80fd\u8ba9\u4ed6\u6e05\u695a\u5730\u4e86\u89e3\u4fe1\u606f\u5c31\u884c\u3002</p><p>\u4f20\u8bf4\u65f6\u4ee3\u7684\u8d44\u6599\u65e0\u53f2\u53ef\u5f81\uff0c\u57fa\u672c\u90fd\u662f\u4f20\u8bf4\uff0c\u4e0d\u5fc5\u8003\u8651\uff1b\u8010\u745f\u65f6\u4ee3\u7684\u8d44\u6599\uff0c\u4ed6\u5728\u5b66\u6821\u56fe\u4e66\u9986\u91cc\u65e9\u5c31\u770b\u8fc7\u4e0d\u5c11\u4e86\uff1b\u552f\u6709\u540e\u8010\u745f\u65f6\u4ee3\uff08\u4e5f\u5c31\u662f\u81ea\u4ece\u8010\u745f\u5e1d\u56fd\u9668\u706d\u540e\u81f3\u4eca\uff09\u8fd9\u6bb5\u65f6\u671f\u7684\u8d44\u6599\uff0c\u56e0\u4e3a\u9634\u9b42\u57ce\u4e00\u76f4\u88ab\u5c01\u5728\u5e7d\u5f71\u754c\u91cc\uff0c\u6d88\u606f\u4e0d\u7545\uff0c\u6240\u4ee5\u4ecb\u7ecd\u6781\u5c11\u3002</p><p>\u743c\u6069\u5feb\u901f\u6d4f\u89c8\u7740\u4e66\u67b6\u4e0a\u7684\u8457\u540d\u5deb\u5e08\u4f20\u8bb0\uff0c\u4ed6\u4e0d\u6307\u671b\u80fd\u5168\u90fd\u8bb0\u719f\uff0c\u53ea\u662f\u8981\u5148\u6709\u4e2a\u5927\u6982\u7684\u4e86\u89e3\u5c31\u884c\u3002\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u8fd8\u662f\u82b1\u4e86\u6574\u6574\u4e00\u5929\uff0c\u76f4\u5230\u628a\u6700\u540e\u4e00\u672c\u4e66\u653e\u56de\u4e66\u67b6\u65f6\uff0c\u7a97\u5916\u5df2\u7ecf\u5915\u9633\u6bb7\u6bb7\u5982\u8840\uff0c\u662f\u508d\u665a\u4e86\u3002</p><p>\u4ed6\u8d70\u51fa\u9ad8\u5854\uff0c\u7a7f\u8fc7\u82b1\u56ed\uff0c\u6b63\u8981\u56de\u5230\u4f4f\u5904\uff0c\u5374\u770b\u89c1\u4e86\u4e0a\u6b21\u548c\u6885\u83f2\u65af\u8bf4\u8bdd\u7684\u90a3\u4e2a\u5c11\u5e74\u3002\u4ed6\u4f9d\u65e7\u8fd8\u662f\u7a7f\u7740\u90a3\u8eab\u7565\u663e\u5bbd\u5927\u7684\u7070\u8272\u957f\u888d\uff0c\u4f4e\u7740\u5934\uff0c\u5306\u5306\u4ece\u8fdc\u5904\u8d70\u6765\uff0c\u7a7f\u8fc7\u82b1\u56ed\uff0c\u8d70\u8fdb\u4e00\u5ea7\u9ad8\u5854\u4e2d\u53bb\u4e86\u3002</p><p>\u743c\u6069\u4e5f\u6ca1\u6709\u592a\u5728\u610f\uff0c\u56de\u5230\u4f4f\u5904\uff0c\u6309\u90e8\u5c31\u73ed\u5730\u665a\u9910\u3001\u6d17\u6f31\uff0c\u56de\u623f\u4f11\u606f\u3002\u4ed6\u63a8\u5f00\u95e8\uff0c\u70b9\u71c3\u8721\u70db\uff0c\u7ffb\u5f00\u9b54\u6cd5\u4e66\u6e29\u4e60\u6cd5\u672f\uff0c\u8fd9\u4e2a\u8fc7\u7a0b\u5927\u7ea6\u82b1\u8d39\u4e86\u4e24\u5c0f\u65f6\u3002\u6700\u7ec8\u5026\u610f\u5fae\u5fae\u6d8c\u4e0a\u6765\uff0c\u4ed6\u5408\u4e0a\u4e66\uff0c\u5439\u7184\u8721\u70db\uff0c\u501f\u7740\u7a97\u5916\u5fae\u5f31\u7684\u6708\u8272\u8131\u4e0b\u957f\u888d\uff0c\u653e\u5728\u5e8a\u8fb9\u7684\u6905\u5b50\u4e0a\uff0c\u722c\u4e0a\u5e8a\u51c6\u5907\u4f11\u606f\u3002</p><p>\u743c\u6069\u4ece\u6765\u6ca1\u6709\u53e0\u88ab\u5b50\u7684\u4e60\u60ef\uff0c\u65e0\u8bba\u4e0a\u8f88\u5b50\u8fd8\u662f\u8fd9\u8f88\u5b50\uff0c\u4ed6\u5f88\u61d2\u3002\u6bcf\u5929\u8d77\u5e8a\u540e\uff0c\u88ab\u5b50\u90fd\u662f\u76f4\u63a5\u5c31\u94fa\u7740\u7684\uff0c\u665a\u4e0a\u7761\u89c9\u65f6\u6380\u5f00\u94bb\u8fdb\u53bb\u5c31\u662f\u3002\u8fd9\u6b21\u4e5f\u4e00\u6837\uff0c\u4ed6\u4f38\u7740\u61d2\u8170\uff0c\u5750\u5728\u5e8a\u6cbf\u4e0a\uff0c\u6380\u5f00\u88ab\u5b50\uff0c\u8eba\u4e86\u8fdb\u53bb\uff0c\u51c6\u5907\u505a\u4e2a\u597d\u68a6\u3002</p><p>\u7136\u540e\uff0c\u4ed6\u611f\u89c9\u78b0\u5230\u4e86\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u6e29\u6696\u2026\u2026\u67d4\u8f6f\u2026\u2026\u5f39\u6027\u2026\u2026\u8089\u611f\u2026\u2026\u51f9\u51f8\u8d77\u4f0f\u2026\u2026\u8fd8\u5e26\u7740\u80a1\u6de1\u6de1\u7684\u9999\u6c14\u3002</p><p>\u5728\u4ed6\u7684\u5927\u8111\u8fd8\u6ca1\u6709\u5b8c\u5168\u53cd\u5e94\u8fc7\u6765\u4e4b\u524d\uff0c\u4e00\u4e2a\u9999\u8f6f\u706b\u70ed\u7684\u8d64\u88f8\u8eab\u4f53\u5c31\u5df2\u7ecf\u8d34\u4e86\u8fc7\u6765\uff0c\u7389\u81c2\u73af\u62b1\uff0c\u50cf\u516b\u722a\u9c7c\u4e00\u822c\u5c06\u4ed6\u7d27\u7d27\u6402\u4f4f\uff0c\u5634\u5507\u8d34\u5728\u4ed6\u7684\u8033\u8fb9\uff0c\u5410\u6c14\u5982\u5170\u3002</p><p>\u201c\u5c0f\u5e05\u54e5\uff0c\u4f60\u53c8\u5fd8\u4e86\u5173\u95e8\u54e6\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u53d1\u8a93\u4ed6\u4ee5\u540e\u4e00\u5b9a\u8981\u517b\u6210\u53e0\u88ab\u5b50\u7684\u4e60\u60ef\uff0c\u8fd9\u6837\u867d\u7136\u9ebb\u70e6\u70b9\uff0c\u81f3\u5c11\u4e0d\u4f1a\u5f04\u5f97\u665a\u4e0a\u4e0a\u5e8a\u65f6\uff0c\u624d\u53d1\u73b0\u88ab\u7a9d\u91cc\u5c45\u7136\u4e00\u76f4\u85cf\u7740\u4e2a\u5973\u4eba\u3002</p><p>\u5982\u679c\u4ec5\u4ec5\u662f\u5973\u4eba\u90a3\u5012\u7f62\u4e86\uff0c\u4f46\u5973\u6740\u624b\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u5979\u662f\u600e\u4e48\u8fdb\u6765\u7684\u3002\u56de\u623f\u95f4\u65f6\u4ed6\u6ce8\u610f\u8fc7\uff0c\u95e8\u9501\u548c\u7a97\u6237\u4e0a\u7684\u8b66\u6212\u7ed3\u754c\u90fd\u662f\u5b8c\u597d\u65e0\u635f\u7684\uff0c\u610f\u5473\u7740\u5e94\u8be5\u6ca1\u6709\u5916\u4eba\u78b0\u89e6\u8fc7\u624d\u5bf9\u2026\u2026\u5979\u96be\u4e0d\u6210\u662f\u4ece\u5730\u5e95\u4e0b\u94bb\u51fa\u6765\u7684\uff1f</p><p>\u4f20\u9001\uff1f\u4e0d\u5bf9\uff0c\u4ed6\u7acb\u523b\u5426\u51b3\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002\u5728\u8fdb\u5165\u70db\u5821\u7684\u7b2c\u4e00\u5929\uff0c\u5f15\u5bfc\u8005\u5c31\u6e05\u695a\u5730\u544a\u8bc9\u8fc7\u4ed6\uff0c\u6b64\u5730\u65e0\u6cd5\u4f20\u9001\u3002\u743c\u6069\u8fd8\u6ca1\u638c\u63e1\u4f20\u9001\u672f\u6216\u8005\u4efb\u610f\u95e8\u8fd9\u79cd\u6bd4\u8f83\u9ad8\u6df1\u7684\u6cd5\u672f\uff0c\u65e0\u6cd5\u8bd5\u9a8c\uff0c\u4f46\u5f15\u5bfc\u8005\u5e94\u8be5\u4e0d\u81f3\u4e8e\u8bf4\u8c0e\u9a97\u4ed6\u3002</p><p>\u4f55\u51b5\u4f20\u9001\u672f\u662f\u975e\u5e38\u9ad8\u6df1\u7684\u6cd5\u672f\uff0c\u5c31\u7b97\u662f\u5f31\u5316\u7248\u672c\u7684\u4efb\u610f\u95e8\uff0c\u743c\u6069\u90fd\u5fc5\u987b\u501f\u52a9\u5377\u8f74\u6765\u65bd\u5c55\u3002\u4ed6\u4e0d\u76f8\u4fe1\u4e00\u4e2a\u6740\u624b\u5c45\u7136\u5728\u9b54\u6cd5\u4e0a\u4e5f\u6709\u8fd9\u79cd\u9020\u8be3\u3002</p><p>\u4f46\u65e0\u8bba\u539f\u56e0\u4e3a\u4f55\uff0c\u53cd\u6b63\u7ed3\u679c\u5c31\u662f\uff1a\u8fd9\u4e2a\u5371\u9669\u7684\u5973\u4eba\u5df2\u7ecf\u8eba\u5728\u5e8a\u4e0a\uff0c\u56db\u80a2\u5e76\u7528\u50cf\u516b\u722a\u9c7c\u4e00\u6837\u7d27\u7d27\u7f20\u7740\u4ed6\u7684\u8eab\u4f53\uff0c\u8138\u8d34\u5728\u8033\u8fb9\u6084\u58f0\u4f4e\u8bed\uff0c\u4eff\u4f5b\u60c5\u4eba\u4eb2\u6635\u3002</p><p>\u8be5\u6b7b\u7684\u3002</p><p>\u603b\u7b97\u4ed6\u5fc3\u7406\u7d20\u8d28\u8fd8\u884c\uff0c\u9aa4\u7136\u4e00\u60ca\uff0c\u968f\u5373\u52c9\u5f3a\u9547\u5b9a\u4e0b\u6765\u3002\u201c\u665a\u4e0a\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u58f0\u97f3\u5df2\u7ecf\u5b8c\u5168\u5e73\u548c\u4e0b\u6765\uff0c\u4eff\u4f5b\u8fd9\u4e00\u5207\u90fd\u5728\u4ed6\u7684\u610f\u6599\u4e4b\u4e2d\uff0c\u201c\u665a\u4e0a\u597d\uff0c\u201d\u4ed6\u53c8\u91cd\u590d\u4e86\u4e00\u904d\uff0c\u201c\u6f02\u4eae\u7684\u5c0f\u59d0\uff0c\u4f60\u6765\u6211\u5e8a\u4e0a\u6709\u4f55\u8d35\u5e72\u3002\u201d</p><p>\u201c\u6211\u53eb\u838e\u73de\u514b\uff0c\u201d\u6740\u624b\u5728\u743c\u6069\u8033\u8fb9\u8bf4\uff0c\u8f7b\u8f7b\u54ac\u7740\u4ed6\u7684\u8033\u5782\uff0c\u201c\u665a\u4e0a\u597d\u554a\uff0c\u5deb\u5e08\uff0c\u80fd\u544a\u8bc9\u6211\u4f60\u7684\u540d\u5b57\u5417\uff1f\u201d</p><p>\u201c\u5170\u5c3c\u65af\u7279\u3002\u201d\u743c\u6069\u56de\u7b54\u3002</p><p>\u201c\u5514\uff0c\u201d\u5979\u53d1\u51fa\u53ef\u7231\u7684\u9f3b\u97f3\uff0c\u4eff\u4f5b\u6709\u4e9b\u4e0d\u6ee1\u610f\u5730\u6447\u6447\u5934\uff0c\u201c\u6211\u95ee\u7684\u4e0d\u662f\u59d3\u54e6\uff0c\u662f\u540d\u5b57\u3002\u201d</p><p>\u201c\u838e\u73de\u514b\u662f\u540d\u5b57\u5417\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u4ed6\u5df2\u7ecf\u5b8c\u5168\u9547\u5b9a\u4e0b\u6765\uff0c\u8111\u4e2d\u6025\u901f\u5206\u6790\u7740\u5f53\u524d\u7684\u5c40\u9762\u3002\u4f5c\u4e3a\u4e00\u4e2a\u5deb\u5e08\uff0c\u4ed6\u88ab\u5bf9\u65b9\u5982\u6b64\u8d34\u8eab\u9760\u8fd1\uff0c\u8fd1\u4e4e\u4e8e\u5b8c\u5168\u4e27\u5931\u4e86\u53cd\u6297\u80fd\u529b\uff0c\u6b64\u65f6\u53eb\u6885\u83f2\u65af\u66f4\u6765\u4e0d\u53ca\uff1b\u5982\u679c\u8fd9\u4e2a\u53eb\u838e\u73de\u514b\u7684\u5973\u4eba\u60f3\u6740\u81ea\u5df1\uff0c\u90a3\u662f\u6839\u672c\u5c31\u6ca1\u6cd5\u62b5\u6321\u7684\uff0c\u53ea\u80fd\u542c\u5929\u7531\u547d\u3002\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u5982\u679c\u5bf9\u65b9\u771f\u7684\u6ca1\u6709\u6076\u610f\u2014\u2014\u6216\u8005\u8bf4\uff0c\u6682\u65f6\u4e0d\u6253\u7b97\u6740\u81ea\u5df1\u5462\uff1f</p><p>\u5982\u679c\u5979\u771f\u60f3\u6740\u81ea\u5df1\uff0c\u65e9\u5c31\u53ef\u4ee5\u52a8\u624b\u4e86\u5427\u3002</p><p>\u65e2\u7136\u5979\u4f3c\u4e4e\u6ca1\u6709\u52a8\u624b\u7684\u610f\u601d\uff0c\u743c\u6069\u53c8\u4e0d\u76f8\u4fe1\u81ea\u5df1\u771f\u6709\u8ba9\u5bf9\u65b9\u4e00\u89c1\u52a8\u60c5\u7684\u9b45\u529b\uff0c\u90a3\u7ed3\u8bba\u5176\u5b9e\u53ea\u6709\u4e00\u6761\uff1a\u81ea\u5df1\u6709\u67d0\u79cd\u4ef7\u503c\u3002</p><p>\u5f53\u7136\uff0c\u662f\u4eba\u90fd\u6709\u4ef7\u503c\uff0c\u518d\u4e0d\u6d4e\u4e22\u5230\u5974\u96b6\u5e02\u573a\u8fd8\u80fd\u5356\u70b9\u94b1\u3002\u4f46\u838e\u73de\u514b\u4e00\u800c\u518d\u5730\u4e3b\u52a8\u63a5\u8fd1\uff0c\u8bf4\u660e\u743c\u6069\u5fc5\u5b9a\u4e8e\u5979\u800c\u8a00\u6709\u67d0\u79cd\u7279\u6b8a\u4ef7\u503c\uff0c\u5176\u4ed6\u4eba\u65e0\u6cd5\u4ee3\u66ff\u3002</p><p>\u660e\u767d\u8fd9\u4e00\u70b9\uff0c\u5269\u4e0b\u6765\u7684\u4e8b\u60c5\u5c31\u597d\u529e\u4e86\u3002\u4e00\u4e2a\u4eba\u8981\u6d3b\u4e0b\u6765\uff0c\u5176\u5b9e\u672a\u5fc5\u9700\u8981\u672c\u8eab\u5b9e\u529b\u6709\u591a\u5f3a\u2014\u2014\u5b9e\u529b\u518d\u5f3a\uff0c\u7ec8\u7a76\u8fd8\u662f\u6709\u66f4\u5f3a\u7684\uff0c\u9664\u975e\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff1b\u66f4\u591a\u7684\u65f6\u5019\uff0c\u80fd\u6d3b\u4e0b\u6765\u662f\u56e0\u4e3a\u5bf9\u522b\u4eba\u6709\u4ef7\u503c\u3002\u6709\u4ef7\u503c\uff0c\u5c31\u6709\u7b79\u7801\uff0c\u5c31\u53ef\u4ee5\u4ea4\u6d41\u8c08\u5224\uff0c\u786e\u5b9a\u5f7c\u6b64\u7684\u5e95\u7ebf\uff0c\u5206\u914d\u5229\u76ca\u548c\u6210\u679c\uff0c\u6700\u4e0d\u6d4e\uff0c\u4e5f\u80fd\u6682\u65f6\u2014\u2014\u5728\u4ef7\u503c\u88ab\u69a8\u5e72\u4e4b\u524d\u2014\u2014\u4fdd\u4f4f\u6027\u547d\uff0c\u627e\u673a\u4f1a\u53cd\u51fb\u6216\u8005\u8131\u8eab\u3002</p><p>\u838e\u73de\u514b\u60f3\u4ece\u81ea\u5df1\u8fd9\u91cc\u5f97\u5230\u4ec0\u4e48\uff1f</p></article>"}}}]);
