(window.webpackJsonp=window.webpackJsonp||[]).push([[131],{153:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u53ef\u7591\u7684\u91d1\u773c\u4f7f\u8005",content:"<article><h2>\u7b2c\u56db\u8282 \u53ef\u7591\u7684\u91d1\u773c\u4f7f\u8005</h2><p>\u5bf9\u6b64\u590f\u62c9\u975e\u548c\u743c\u6069\u662f\u5f88\u8d5e\u6210\uff0c\u4ed6\u4eec\u89c9\u5f97\u6ca1\u5fc5\u8981\u591a\u751f\u679d\u8282\uff1b\u6885\u83f2\u65af\u548c\u9686\u5947\u4e5f\u65e0\u5f02\u8bae\uff0c\u4f46\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u5374\u5728\u8fd9\u5173\u5934\u6363\u4e71\u8d77\u6765\u3002</p><p>\u201c\u6211\u89c9\u5f97\u8d1d\u7eb3\u7d22\u8bf4\u5f97\u6709\u9053\u7406\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u4e2a\u5de8\u9b54\u5b9e\u5728\u592a\u53e4\u602a\u4e86\u3002\u800c\u4e14\uff0c\u5982\u679c\u8bf4\u771f\u662f\u6709\u4eba\u6545\u610f\u5236\u9020\u51fa\u6765\u7684\u8bdd\u2026\u2026\u6781\u6709\u53ef\u80fd\u5c31\u662f\u4e2a\u4ea1\u7075\u5deb\u5e08\u5427\u3002\u201d</p><p>\u4ed6\u6ca1\u6709\u660e\u8bf4\uff0c\u4f46\u8bdd\u4e2d\u7684\u610f\u5473\u5f88\u660e\u663e\u3002\u5de8\u9b54\u5c71\u8109\u91cc\u7684\u4ea1\u7075\u5deb\u5e08\uff0c\u9664\u4e86\u62c9\u6c83\u514b\u8fd8\u80fd\u6709\u8c01\uff0c\u81f3\u5c11\u548c\u8fd9\u8001\u5deb\u5996\u8131\u4e0d\u4e86\u5e72\u7cfb\u3002\u800c\u62c9\u6c83\u514b\uff0c\u6b63\u662f\u4ed6\u4eec\u6b64\u884c\u7684\u76ee\u6807\u3002</p><p>\u5982\u679c\u6b64\u65f6\u771f\u7684\u548c\u62c9\u6c83\u514b\u6709\u5173\uff0c\u5982\u679c\u80fd\u4ece\u8fd9\u5de8\u9b54\u8eab\u4e0a\u591a\u53d1\u73b0\u70b9\u4ec0\u4e48\u4e1c\u897f\uff0c\u672a\u5fc5\u4e0d\u662f\u4ef6\u597d\u4e8b\u3002</p><p>\u4f46\u95ee\u9898\u662f\u73b0\u5728\u5de8\u9b54\u90fd\u6b7b\u4e86\uff0c\u8fd8\u80fd\u600e\u4e48\u529e\uff1f</p><p>\u201c\u6211\u6765\u8bd5\u8bd5\u770b\u597d\u4e86\u3002\u201d\u79d1\u6069\u81ea\u544a\u594b\u52c7\u3002</p><p>\u5176\u4ed6\u6240\u6709\u4eba\u90fd\u7528\u6000\u7591\u7684\u773c\u5149\u770b\u7740\u4ed6\uff0c\u6c83\u91d1\u662f\u5546\u4e1a\u4e0e\u8d22\u5bcc\u5973\u795e\uff0c\u7b97\u4e0d\u4e0a\u5584\u795e\uff0c\u4f46\u4e5f\u7edd\u4e0d\u662f\u90aa\u795e\uff0c\u4e0d\u53ef\u80fd\u8d50\u4e88\u7267\u5e08\u62f7\u95ee\u4ea1\u8005\u7075\u9b42\u4e4b\u7c7b\u7684\u90aa\u6076\u795e\u672f\uff0c\u4ed6\u80fd\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f</p><p>\u79d1\u6069\u5fae\u7b11\u7740\uff0c\u719f\u7ec3\u5730\u8ba9\u4e00\u679a\u91d1\u5e01\u5728\u81ea\u5df1\u7684\u624b\u6307\u95f4\u5feb\u901f\u7ffb\u52a8\u3002\u201c\u60f3\u77e5\u9053\u4e00\u4e2a\u4eba\u7684\u8fc7\u53bb\uff0c\u672a\u5fc5\u4e00\u5b9a\u8981\u95ee\u5b83\u672c\u4eba\u561b\uff0c\u201d\u7267\u5e08\u9887\u6709\u4e9b\u81ea\u8d1f\u5730\u8bf4\uff0c\u76ee\u5149\u4ece\u6240\u6709\u4eba\u8138\u4e0a\u79fb\u8fc7\uff0c\u743c\u6069\u60ca\u8bb6\u5730\u53d1\u73b0\u4ed6\u7684\u773c\u73e0\u4e0d\u77e5\u4f55\u65f6\u53d8\u6210\u4e86\u9690\u9690\u7684\u707f\u91d1\u8272\uff0c\u4eff\u4f5b\u4e24\u56e2\u878d\u5316\u7684\u91d1\u7403\uff0c\u201c\u6211\u4eec\u91d1\u773c\u4f7f\u8005\uff0c\u6700\u64c5\u957f\u7684\u4fbf\u662f\u2018\u4e07\u4e8b\u7686\u77e5\u2019\u554a\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u201c\u91d1\u773c\u4f7f\u8005\u201d\uff0c\u4f46\u4f3c\u4e4e\u6885\u83f2\u65af\u3001\u8d1d\u7eb3\u7d22\u548c\u590f\u62c9\u975e\u90fd\u6709\u4e9b\u660e\u767d\uff0c\u9686\u5947\u4f3c\u4e4e\u4e5f\u6ca1\u542c\u61c2\uff0c\u4f46\u4ed6\u53cd\u6b63\u61d2\u5f97\u5173\u5fc3\u3002</p><p>\u79d1\u6069\u8bf4\u4ed6\u6709\u529e\u6cd5\u80fd\u5224\u65ad\u51fa\u8fd9\u53ea\u5de8\u9b54\u751f\u524d\u53d1\u751f\u8fc7\u54ea\u4e9b\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u8fd9\u5c5e\u4e8e\u4ed6\u4eec\u6c83\u91d1\u6559\u4f1a\u7684\u673a\u5bc6\uff0c\u4e0d\u5b9c\u5916\u6cc4\uff0c\u6240\u4ee5\u62d6\u7740\u5de8\u9b54\u7684\u5c38\u4f53\u5230\u65c1\u8fb9\u7684\u6811\u6797\u91cc\u53bb\u4e86\u3002\u5176\u4ed6\u4eba\u542c\u4ed6\u5982\u6b64\u8bf4\uff0c\u81ea\u7136\u4e5f\u4e0d\u4fbf\u6253\u6270\u3002</p><p>\u539f\u672c\u662f\u51c6\u5907\u4f11\u606f\uff0c\u6ca1\u60f3\u5230\u88ab\u8fd9\u5947\u602a\u53cc\u5934\u5de8\u9b54\u8dd1\u6765\u6363\u4e71\uff0c\u6298\u817e\u4e00\u756a\uff0c\u5927\u5bb6\u4e5f\u90fd\u6709\u4e9b\u75b2\u5026\uff0c\u5728\u5c71\u6d1e\u8fb9\u5750\u4e0b\uff0c\u53d6\u4e86\u98df\u7269\u5404\u81ea\u8fdb\u9910\u6b47\u606f\u3002\u743c\u6069\u6709\u4e9b\u597d\u5947\uff0c\u4fbf\u5c06\u590f\u62c9\u975e\u62c9\u8fc7\u4e00\u65c1\uff0c\u95ee\u6240\u8c13\u7684\u201c\u91d1\u773c\u4f7f\u8005\u201d\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\u3002</p><p>\u201c\u91d1\u773c\u4f7f\u8005\u554a\uff0c\u201d\u534a\u7cbe\u7075\u6e38\u8361\u8005\u6b63\u54ac\u7740\u4e00\u53e3\u9762\u5305\uff0c\u542b\u7cca\u4e0d\u6e05\u5730\u8bf4\uff0c\u201c\u5177\u4f53\u6211\u4e5f\u4e0d\u6e05\u695a\u4e86\uff0c\u53cd\u6b63\u662f\u6c83\u91d1\u6559\u4f1a\u7684\u7cbe\u82f1\u6210\u5458\u5462\u3002\u201d</p><p>\u636e\u590f\u62c9\u975e\u4ecb\u7ecd\uff0c\u80fd\u5f97\u5230\u6c83\u91d1\u5973\u795e\u7279\u522b\u5ba0\u7231\u7684\u4fe1\u5f92\uff0c\u6709\u53ef\u80fd\u88ab\u8d50\u4e88\u4e00\u4e9b\u795e\u529b\uff0c\u6210\u4e3a\u8fd9\u79cd\u201c\u91d1\u773c\u4f7f\u8005\u201d\u3002\u79c9\u627f\u5973\u795e\u7684\u4e00\u8d2f\u98ce\u683c\uff0c\u91d1\u773c\u4f7f\u8005\u6240\u64c5\u957f\u7684\u5e76\u4e0d\u662f\u780d\u6740\u683c\u6597\uff0c\u53cd\u800c\u5728\u4ea4\u5f80\u3001\u63a2\u67e5\u7b49\u65b9\u9762\u6bd4\u8f83\u7a81\u51fa\u3002\u636e\u8bf4\u4ed6\u4eec\u4e0d\u8bba\u662f\u542c\u5230\u4f55\u79cd\u964c\u751f\u7684\u8bed\u8a00\uff0c\u53ea\u8981\u4ed4\u7ec6\u503e\u542c\u4e00\u5206\u949f\uff0c\u5c31\u80fd\u5927\u4f53\u660e\u767d\u8fd9\u79cd\u8bed\u8a00\u7684\u57fa\u7840\u8bed\u6cd5\uff0c\u5e76\u4e14\u5927\u81f4\u80fd\u77e5\u9053\u4e00\u4e9b\u6700\u57fa\u7840\u7684\u8bcd\u6c47\uff0c\u80fd\u548c\u5bf9\u65b9\u7b80\u5355\u4ea4\u6d41\uff1b\u4e0d\u8bba\u662f\u9047\u5230\u4ec0\u4e48\u964c\u751f\u4eba\uff0c\u53ea\u8981\u548c\u4ed6\u4ea4\u8c08\u4e00\u5206\u949f\uff0c\u5c31\u80fd\u901a\u8fc7\u5bdf\u8a00\u89c2\u8272\u5927\u4f53\u731c\u5230\u5bf9\u65b9\u7684\u5fc3\u601d\u3001\u597d\u6076\uff0c\u4ece\u800c\u8ba9\u5f7c\u6b64\u7684\u4ea4\u8c08\u66f4\u52a0\u6109\u5feb\uff1b\u53e6\u5916\uff0c\u65e2\u7136\u662f\u5546\u4e1a\u5973\u795e\u7684\u7cbe\u82f1\u4fe1\u5f92\uff0c\u81ea\u7136\u5bf9\u8d22\u5bcc\u6709\u7740\u683c\u5916\u7684\u654f\u9510\uff0c\u636e\u8bf4\u53ea\u8981\u4ed6\u4eec\u773c\u5149\u4e00\u626b\uff0c\u5c31\u80fd\u7acb\u523b\u5bf9\u6240\u6709\u7684\u91d1\u94f6\u3001\u73e0\u5b9d\u3001\u9b54\u6cd5\u7269\u54c1\u7b49\u7b49\u6709\u4e2a\u7cbe\u786e\u7684\u4f30\u4ef7\u5b9a\u4f4d\uff0c\u8fd9\u4efd\u672c\u4e8b\u8fde\u5728\u5546\u6d77\u4e2d\u6253\u6eda\u4e00\u8f88\u5b50\u7684\u8001\u5546\u4eba\u90fd\u81ea\u53f9\u4e0d\u5982\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u4e9b\u90fd\u8fd8\u4e0d\u662f\u6700\u4e86\u4e0d\u8d77\u7684\u5730\u65b9\u2014\u2014\u771f\u6b63\u5389\u5bb3\u7684\u5730\u65b9\u5728\u4e8e\uff0c\u636e\u8bf4\u91d1\u773c\u4f7f\u8005\u53ea\u8981\u6709\u4e00\u679a\u91d1\u5e01\u5728\u624b\uff0c\u901a\u8fc7\u67d0\u79cd\u4e0d\u4e3a\u4eba\u77e5\u7684\u65b9\u6cd5\uff0c\u4ed6\u4eec\u5c31\u80fd\u65bd\u5c55\u7c7b\u4f3c\u4e8e\u201c\u63a2\u77e5\u672f\u201d\u7684\u5f3a\u5927\u9b54\u6cd5\uff0c\u80fd\u77e5\u6653\u5f88\u591a\u5e38\u4eba\u6240\u65e0\u4ece\u5f97\u77e5\u7684\u79d8\u5bc6\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u4e48\u8bf4\uff0c\u79d1\u6069\u8fd9\u6b21\u53c8\u662f\u8981\u7528\u4ed6\u4eec\u90a3\u79cd\u79d8\u5bc6\u624b\u6cd5\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u53ef\u80fd\u5427\u3002\u201d\u590f\u62c9\u975e\u8bf4\uff0c\u4ed6\u4f3c\u4e4e\u4e0d\u592a\u5728\u610f\uff0c\u6216\u8005\u8bf4\u4e0d\u592a\u5173\u5fc3\u3002\u53cd\u6b63\u4ed6\u53ea\u662f\u4e2a\u5e26\u8def\u7684\uff0c\u5230\u4e86\u5730\u65b9\u5c31\u8d70\u4eba\uff0c\u5269\u4e0b\u6765\u5c31\u6ca1\u4ed6\u7684\u4e8b\u60c5\u4e86\u3002</p><p>\u4f46\u743c\u6069\u4fbf\u5fc3\u4e2d\u9690\u9690\u6709\u4e9b\u4e0d\u5b89\u3002</p><p>\u56e0\u4e3a\u8fd9\u6b21\u4e8b\u60c5\u6bd4\u8f83\u6025\u8feb\uff0c\u80fd\u51d1\u9f50\u8fd9\u51e0\u4e2a\u5fd7\u613f\u8005\u5c31\u5df2\u7ecf\u5f88\u96be\u5f97\uff0c\u4e5f\u6ca1\u529e\u6cd5\u4ed4\u7ec6\u6311\u9009\uff0c\u67e5\u6e05\u5404\u4eba\u7684\u6765\u5386\u5e95\u7ec6\uff0c\u8fd9\u4e5f\u4e0d\u53ef\u80fd\u529e\u5230\u3002\u6240\u6709\u5c3d\u7ba1\u6b64\u65f6\u5927\u5bb6\u7b97\u662f\u961f\u53cb\uff0c\u5f7c\u6b64\u4e4b\u95f4\u5374\u90fd\u8fd8\u662f\u6709\u4e9b\u9690\u9690\u6212\u5fc3\u7684\u3002\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u8fd9\u4e94\u4e2a\u4eba\u4e2d\uff0c\u6885\u83f2\u65af\u81ea\u7136\u4fe1\u4efb\uff0c\u8d1d\u7eb3\u7d22\u548c\u590f\u62c9\u975e\u4e24\u4eba\u90fd\u662f\u535a\u5f97\u4e4b\u95e8\u7684\u672c\u5730\u4eba\uff0c\u4e00\u662f\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u9996\u5e2d\u7267\u5e08\uff0c\u4e00\u662f\u70c8\u7130\u4e4b\u62f3\u7684\u519b\u5b98\uff0c\u540c\u65f6\u88ab\u5927\u516c\u7235\u63a8\u8350\u800c\u6765\uff0c\u4e5f\u5e94\u8be5\u4e0d\u4f1a\u6709\u4ec0\u4e48\u95ee\u9898\uff1b\u4f46\u6b66\u50e7\u9686\u5947\u548c\u8fd9\u4e2a\u6c83\u91d1\u7267\u5e08\u5374\u90fd\u662f\u5916\u5730\u4eba\uff0c\u65e2\u4e0d\u77e5\u6839\u77e5\u5e95\uff0c\u53c8\u672a\u66fe\u6253\u8fc7\u4ea4\u9053\uff0c\u53ea\u6015\u4e0d\u662f\u53ef\u4ee5\u5b8c\u5168\u653e\u5fc3\u7684\u5bf9\u8c61\u3002</p><p>\u6b66\u50e7\u9686\u5947\u5012\u8fd8\u7f62\u4e86\uff0c\u4ece\u79cd\u79cd\u8ff9\u8c61\u4e0a\u5224\u65ad\uff0c\u57fa\u672c\u53ef\u4ee5\u80af\u5b9a\u4ed6\u662f\u4e2a\u80cc\u5f03\u4f0a\u5c14\u739b\u7279\u4fe1\u4ef0\u7684\u5815\u843d\u8005\u3002\u770b\u4ed6\u5728\u535a\u5f97\u4e4b\u95e8\u8fd9\u51e0\u5929\uff0c\u6210\u5929\u5403\u559d\u73a9\u4e50\uff0c\u5927\u4f53\u4e5f\u80fd\u77e5\u9053\u6b64\u4eba\u7684\u813e\u6027\u3002\u743c\u6069\u81ea\u5df1\u5c31\u4e0d\u662f\u4ec0\u4e48\u5584\u826f\u4eba\u58eb\uff0c\u81ea\u7136\u4e5f\u4e0d\u5728\u4e4e\u548c\u4e00\u4e2a\u5815\u843d\u8005\u5408\u4f5c\u2014\u2014\u4f55\u51b5\u5728\u743c\u6069\u770b\u6765\uff0c\u5f53\u6b66\u50e7\uff0c\u53d1\u8d2b\u7a77\u8a93\u8a00\uff0c\u8fd9\u7b80\u76f4\u5c31\u662f\u6cef\u706d\u4eba\u6027\uff0c\u9686\u5947\u5982\u4eca\u80fd\u5e61\u7136\u6094\u609f\uff0c\u8131\u79bb\u82e6\u6d77\uff0c\u6b63\u662f\u5408\u4e4e\u4eba\u60c5\u987a\u5e94\u5929\u7406\u7684\u4e8b\u60c5\uff0c\u5b8c\u5168\u6ca1\u4ec0\u4e48\u4e0d\u59a5\u7684\uff1b\u5982\u679c\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u7b49\u8fd9\u6b21\u4e8b\u60c5\u5b8c\u4e86\uff0c\u743c\u6069\u751a\u81f3\u8003\u8651\u8981\u4e0d\u8981\u548c\u8fd9\u4f4d\u524d\u6b66\u50e7\u4ea4\u6d41\u4e00\u4e0b\u5fc3\u5f97\u4f53\u4f1a\u3002</p><p>\u4f46\u8fd9\u4e2a\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u5c31\u6709\u4e9b\u4e0d\u5bf9\u52b2\u4e86\u3002</p><p>\u8eab\u4e3a\u201c\u4e2d\u91d1\u201d\u7267\u5e08\uff0c\u4ed6\u4e0d\u53bb\u505a\u751f\u610f\u8d5a\u94b1\uff0c\u53cd\u800c\u8dd1\u6765\u53c2\u52a0\u8fd9\u6b21\u5192\u9669\uff0c\u8fd9\u9996\u5148\u5c31\u6bd4\u8f83\u8e4a\u8df7\u3002\u5982\u679c\u8bf4\u662f\u56e0\u4e3a\u6c83\u91d1\u5973\u795e\u590d\u51fa\u540e\u7684\u653f\u7b56\u6539\u53d8\uff0c\u5bfc\u81f4\u7537\u6027\u7267\u5e08\u5730\u4f4d\u4e0b\u964d\uff0c\u5904\u5883\u7a98\u8feb\uff0c\u6240\u4ee5\u51fa\u6765\u6253\u5de5\u7684\u8bdd\uff0c\u5012\u4e5f\u80fd\u8bf4\u5f97\u8fc7\u53bb\u3002\u8389\u62c9\u516c\u7235\u66fe\u901a\u8fc7\u535a\u5f97\u4e4b\u95e8\u7684\u6c83\u91d1\u795e\u6bbf\u786e\u8ba4\u6b64\u4eba\u8eab\u4efd\uff0c\u7ed3\u8bba\u4e5f\u662f\u6beb\u65e0\u95ee\u9898\uff0c\u4ed6\u786e\u5b9e\u662f\u6559\u4f1a\u5185\u7684\u4e2d\u91d1\u7267\u5e08\uff0c\u4e00\u76f4\u5728\u963f\u65af\u5361\u7279\u62c9\u4efb\u804c\u3002\u8fd9\u4ef6\u4e8b\u4e5f\u5c31\u57fa\u672c\u653e\u4e0b\u3002</p><p>\u4f46\u590f\u62c9\u975e\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u5374\u8ba9\u743c\u6069\u5fc3\u5934\u518d\u6b21\u8d77\u4e86\u7591\u60d1\u3002</p><p>\u552f\u6709\u80fd\u5f97\u5230\u6c83\u91d1\u5973\u795e\u7279\u522b\u5ba0\u7231\u7684\u4fe1\u5f92\uff0c\u624d\u6709\u6210\u4e3a\u201c\u91d1\u773c\u4f7f\u8005\u201d\u7684\u8d44\u683c\u2014\u2014\u53cd\u8fc7\u6765\u8bf4\uff0c\u79d1\u6069\u65e2\u7136\u662f\u91d1\u773c\u4f7f\u8005\uff0c\u7406\u5f53\u662f\u6c83\u91d1\u7279\u522b\u5ba0\u7231\u7684\u4fe1\u5f92\u624d\u5bf9\u3002\u90a3\u4e48\uff0c\u4ed6\u6709\u5fc5\u8981\u8dd1\u5230\u8fd9\u91cc\u6765\u53c2\u4e0e\u5192\u9669\u4e48\uff1f</p><p>\u5f53\u7136\uff0c\u4eba\u5404\u6709\u5fd7\uff0c\u8fd9\u4e16\u754c\u4e0a\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u6709\u3002\u6216\u8bb8\u6709\u4eba\u5c31\u662f\u559c\u6b22\u5192\u9669\uff0c\u5bfb\u6c42\u523a\u6fc0\uff0c\u8fd9\u4e5f\u4e0d\u662f\u4e0d\u80fd\u7406\u89e3\u3002\u4f46\u743c\u6069\u5fc3\u5934\u603b\u662f\u96be\u514d\u6709\u4e9b\u7591\u60d1\uff0c\u8fd9\u5e76\u975e\u4ed6\u591a\u7591\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u53ef\u662f\u4e00\u76f4\u6084\u6084\u6253\u7740\u53e6\u5916\u7684\u7b97\u76d8\u7684\u3002</p><p>\u6d51\u6c34\u6478\u9c7c\uff0c\u89c1\u673a\u884c\u4e8b\uff0c\u800c\u4e0d\u662f\u5728\u6df7\u4e71\u4e2d\u628a\u81ea\u5df1\u6302\u4e86\uff0c\u6b7b\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u6b7b\u7684\u3002</p><p>\u4ed6\u8d70\u56de\u4e24\u6b65\uff0c\u601d\u5fd6\u7740\uff0c\u8003\u8651\u81ea\u5df1\u7684\u4e0b\u4e00\u6b65\u884c\u52a8\uff0c\u4e00\u62ac\u5934\u6b63\u8fce\u4e0a\u6885\u83f2\u65af\u7684\u76ee\u5149\u3002\u5c11\u5973\u671d\u4ed6\u5fae\u5fae\u6ce8\u76ee\u793a\u610f\uff0c\u77a5\u4e86\u4e00\u773c\u79d1\u6069\u8fdb\u53bb\u7684\u90a3\u7247\u6811\u6797\uff0c\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u743c\u6069\u660e\u767d\u4e86\u5979\u7684\u610f\u601d\uff0c\u663e\u7136\uff0c\u6885\u83f2\u65af\u4e5f\u4e0d\u592a\u653e\u5fc3\u3002\u4f46\u65e2\u7136\u79d1\u6069\u8bf4\u4e86\u8fd9\u662f\u4ed6\u4eec\u6559\u4f1a\u7684\u673a\u5bc6\uff0c\u6885\u83f2\u65af\u81ea\u7136\u4e0d\u597d\u786c\u8981\u65c1\u89c2\uff0c\u5979\u662f\u4e2a\u5723\u6b66\u58eb\u561b\u2014\u2014\u4f46\u743c\u6069\u53ef\u4e0d\u662f\u5723\u6b66\u58eb\u3002</p><p>\u8d1d\u7eb3\u7d22\u6b63\u5728\u72fc\u541e\u864e\u54bd\u5730\u8fdb\u9910\uff0c\u8fd9\u4f4d\u9996\u5e2d\u7267\u5e08\u770b\u6837\u5b50\u662f\u997f\u5f97\u5389\u5bb3\u4e86\uff0c\u4e00\u8fb9\u5403\u4e00\u8fb9\u5927\u53e3\u5927\u53e3\u5730\u559d\u6c34\uff0c\u5dee\u70b9\u545b\u5230\uff1b\u9686\u5947\u4e00\u4e2a\u4eba\u9ed8\u4e0d\u4f5c\u58f0\u5730\u5446\u5728\u65c1\u8fb9\uff0c\u54ac\u7740\u81ea\u5df1\u624b\u4e2d\u7684\u5e72\u9762\u5305\uff1b\u8c01\u4e5f\u6ca1\u6ce8\u610f\u6885\u83f2\u65af\u548c\u743c\u6069\u7684\u773c\u795e\u6765\u5f80\u3002\u743c\u6069\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u77e5\u9053\uff0c\u4ed6\u62ff\u8d77\u4e00\u5757\u9762\u5305\uff0c\u5939\u7740\u725b\u8089\uff0c\u6162\u6162\u8d70\u5230\u77f3\u58c1\u8fb9\u3002</p><p>\u743c\u6069\u6ca1\u6709\u6f5c\u884c\u9690\u9041\u7684\u672c\u4e8b\uff0c\u8981\u53bb\u770b\u79d1\u6069\u5728\u505a\u4ec0\u4e48\uff0c\u65e0\u975e\u4e24\u79cd\u65b9\u6cd5\u3002\u8981\u4e48\u628a\u81ea\u5df1\u5148\u9690\u5f62\u4e86\uff0c\u7136\u540e\u5077\u5077\u6e9c\u8fc7\u53bb\u770b\uff1b\u8981\u4e48\uff0c\u5c31\u7528\u4e00\u4e9b\u63a2\u6d4b\u9b54\u6cd5\u4e86\u3002</p><p>\u9690\u5f62\u672f\u4ed6\u81ea\u7136\u662f\u4f1a\u7684\uff0c\u800c\u4e14\u4e5f\u51c6\u5907\u4e86\uff0c\u4f46\u8fd9\u5e76\u4e0d\u9002\u5408\u3002\u5982\u4eca\u4e94\u4e2a\u4eba\u90fd\u5728\u8fd9\u4e00\u5c0f\u5757\u5730\u65b9\u6b47\u606f\uff0c\u81ea\u5df1\u7a81\u7136\u9690\u5f62\u6d88\u5931\uff0c\u5f88\u5bb9\u6613\u5c31\u88ab\u5176\u4ed6\u4eba\u53d1\u89c9\uff0c\u8fdb\u800c\u5f15\u8d77\u6000\u7591\uff0c\u53cd\u800c\u9ebb\u70e6\u3002</p><p>\u65e2\u7136\u4e0d\u65b9\u4fbf\u7528\u9690\u5f62\uff0c\u90a3\u5c31\u53ea\u80fd\u53e6\u60f3\u529e\u6cd5\u4e86\u3002\u9884\u8a00\u5b66\u6d3e\u7684\u6cd5\u672f\u4e2d\uff0c\u6709\u4e00\u79cd\u201c\u79d8\u6cd5\u773c\u201d\u80fd\u521b\u9020\u51fa\u6f02\u6d6e\u7684\u7aa5\u89c6\u9b54\u773c\uff0c\u76f8\u5f53\u4e8e\u4f60\u6709\u4e00\u4e2a\u72ec\u7acb\u4e8e\u8eab\u4f53\u4e4b\u5916\u7684\u773c\u7403\uff0c\u65e2\u65b9\u4fbf\u53c8\u9690\u853d\uff0c\u5012\u662f\u4e0a\u7b49\u4e4b\u9009\uff0c\u53ea\u662f\u9700\u8981\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\uff0c\u63d0\u53d6\u80fd\u91cf\u65b9\u80fd\u5851\u6210\uff0c\u7b97\u662f\u6bd4\u8f83\u9ad8\u9636\u7684\u6cd5\u672f\u4e86\u3002</p><p>\u743c\u6069\u5012\u662f\u521a\u521a\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\uff0c\u8fd9\u9053\u6cd5\u672f\u8d44\u6599\u4e5f\u5728\u9b54\u6cd5\u4e66\u91cc\u6284\u5f55\u4e86\u4e00\u4efd\uff0c\u4f46\u8fd8\u6ca1\u6765\u5f97\u53ca\u638c\u63e1\u3002\u5c31\u7b97\u5df2\u7ecf\u638c\u63e1\uff0c\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u4f18\u5148\u51c6\u5907\u8fd9\u79cd\u6cd5\u672f\uff0c\u96be\u9053\u95f2\u7740\u6ca1\u4e8b\u5c31\u6253\u7b97\u7740\u5077\u7aa5\u522b\u4eba\u4e48\uff0c\u81ea\u7136\u8981\u5148\u51c6\u5907\u5176\u4ed6\u66f4\u6709\u7528\u7684\u6cd5\u672f\u3002</p><p>\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u8fd8\u6709\u5377\u8f74\u3002\u4e34\u51fa\u53d1\u524d\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u4e86\u4e03\u516b\u5f20\u5377\u8f74\uff0c\u743c\u6069\u5728\u4eba\u9762\u72ee\u90a3\u91cc\u7528\u6389\u5c06\u8fd1\u4e00\u534a\uff0c\u6362\u6765\u4e86\u6885\u83f2\u65af\uff1b\u5269\u4e0b\u8fd9\u51e0\u5f20\u4e2d\uff0c\u6070\u597d\u5c31\u6709\u201c\u79d8\u6cd5\u773c\u201d\u5377\u8f74\u3002</p><p>\u5fc5\u987b\u8bf4\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u771f\u662f\u4e2a\u597d\u4eba\u3002</p><p>\u4ed6\u9760\u7740\u5899\u58c1\u5750\u4e0b\uff0c\u5c06\u9762\u5305\u653e\u5728\u819d\u76d6\u4e0a\uff0c\u6084\u6084\u4ece\u6000\u4e2d\u6b21\u5143\u888b\u91cc\u53d6\u51fa\u9700\u8981\u7684\u5377\u8f74\uff0c\u8f7b\u8f7b\u5c55\u5f00\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u4f4e\u58f0\u8bf5\u8bfb\u7740\uff1b\u6885\u83f2\u65af\u9ed8\u5951\u5730\u8d70\u8fc7\u6765\uff0c\u770b\u4e0a\u53bb\u53ea\u662f\u5728\u5730\u4e0a\u7684\u5305\u88f9\u91cc\u53d6\u6c34\u888b\uff0c\u5176\u5b9e\u5374\u5c06\u743c\u6069\u906e\u4f4f\uff0c\u8ba9\u5176\u4ed6\u4eba\u770b\u4e0d\u5230\u5deb\u5e08\u5728\u505a\u4ec0\u4e48\u3002\u5492\u6587\u5e76\u4e0d\u957f\uff0c\u4ec5\u4ec5\u5341\u51e0\u4e2a\u97f3\u8282\uff0c\u743c\u6069\u7684\u58f0\u97f3\u538b\u5f97\u975e\u5e38\u4f4e\uff0c\u5176\u4ed6\u4eba\u90fd\u5728\u5fd9\u7740\u8fdb\u98df\u3001\u4f11\u606f\uff0c\u4e1d\u6beb\u6ca1\u6709\u6ce8\u610f\u8fd9\u8fb9\uff0c\u51e0\u79d2\u949f\u540e\uff0c\u5377\u8f74\u5316\u4e3a\u9f51\u7c89\u6d88\u5931\uff0c\u4e00\u9897\u9eef\u6de1\u7684\u94f6\u8272\u773c\u73e0\u5728\u7a7a\u6c14\u4e2d\u7f13\u7f13\u51fa\u73b0\u3002</p></article>"}}}]);
