(window.webpackJsonp=window.webpackJsonp||[]).push([[132],{154:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u8282 \u90aa\u6c14",content:"<article><h2>\u7b2c\u4e94\u8282 \u90aa\u6c14</h2><p>\u743c\u6069\u62ff\u8d77\u9762\u5305\uff0c\u7ee7\u7eed\u8fdb\u9910\uff0c\u4f46\u5374\u6697\u4e2d\u7528\u610f\u8bc6\u6307\u6325\u7740\u8fd9\u9897\u79d8\u6cd5\u773c\uff0c\u8ba9\u5b83\u6084\u6084\u7ed5\u4e86\u4e2a\u5708\u5b50\uff0c\u8eb2\u8fc7\u4f17\u4eba\uff0c\u8d34\u7740\u5730\u9762\u98de\u8fdb\u6811\u6797\u91cc\u53bb\u4e86\u3002</p><p>\u79d8\u6cd5\u773c\u5fc5\u987b\u5728\u65bd\u6cd5\u8005\u7684\u89c6\u7ebf\u4e4b\u5185\uff0c\u5426\u5219\u4fbf\u5bb9\u6613\u5931\u53bb\u63a7\u5236\uff0c\u6240\u4ee5\u743c\u6069\u4e5f\u4e0d\u6562\u8ba9\u5b83\u98de\u5f97\u592a\u8fdc\u3002\u4ed6\u82e5\u65e0\u5176\u4e8b\u5730\u4e00\u8fb9\u54ac\u7740\u9762\u5305\u4e00\u8fb9\u8e31\u7740\uff0c\u4eff\u4f5b\u5728\u6563\u6b65\uff0c\u5374\u6084\u6084\u5730\u5f80\u6811\u6797\u8fb9\u9760\u8fd1\uff0c\u4ee5\u4fbf\u80fd\u8ba9\u79d8\u6cd5\u773c\u66f4\u52a0\u6df1\u5165\u3002\u5e78\u597d\uff0c\u79d1\u6069\u5e76\u6ca1\u6709\u8dd1\u592a\u8fdc\uff0c\u743c\u6069\u64cd\u7eb5\u79d8\u6cd5\u773c\u7ed5\u4e86\u4e24\u4e2a\u5f2f\u4e4b\u540e\uff0c\u5df2\u7ecf\u770b\u89c1\u4e86\u4ed6\uff0c\u6b63\u8e72\u5728\u5de8\u9b54\u7684\u5c38\u4f53\u65c1\u8fb9\uff0c\u4e0d\u77e5\u9053\u5728\u505a\u4e9b\u4ec0\u4e48\u3002</p><p>\u79d8\u6cd5\u773c\u8d34\u5f97\u66f4\u8fd1\uff0c\u5c06\u89c6\u7ebf\u5185\u7684\u4e00\u5207\u90fd\u76f4\u63a5\u4f20\u56de\u743c\u6069\u8111\u4e2d\uff0c\u4eff\u4f5b\u4ed6\u81ea\u5df1\u4eb2\u773c\u6240\u89c1\u4e00\u822c\u3002\u743c\u6069\u770b\u89c1\u79d1\u6069\u4f38\u624b\uff0c\u4ece\u540e\u8170\u4e0a\u53d6\u4e0b\u9489\u5934\u9524\uff0c\u91cd\u91cd\u7838\u5728\u5de8\u9b54\u7684\u5176\u4e2d\u4e00\u9897\u8111\u888b\u4e0a\u3002</p><p>\u743c\u6069\u9887\u4e3a\u60ca\u8be7\uff0c\u5de8\u9b54\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8fd8\u7838\u5b83\u7684\u5c38\u4f53\u505a\u4ec0\u4e48\uff0c\u5f7c\u6b64\u95f4\u53c8\u6ca1\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4f55\u81f3\u4e8e\u6b64\u3002\u5374\u89c1\u79d1\u6069\u4e00\u9524\u53c8\u4e00\u9524\uff0c\u8fde\u8fde\u7838\u4e0b\uff0c\u5de8\u9b54\u5df2\u7ecf\u6b7b\u53bb\uff0c\u8089\u4f53\u518d\u751f\u80fd\u529b\u4e5f\u4e0d\u590d\u5b58\u5728\uff0c\u4e0d\u7247\u523b\u5c31\u88ab\u7838\u5f97\u9885\u9aa8\u788e\u88c2\uff0c\u7070\u7eff\u8272\u7684\u8111\u6d46\u8ff8\u6d41\u3002</p><p>\u743c\u6069\u6b63\u4e0d\u77e5\u4ed6\u4e3a\u4f55\u5982\u6b64\uff0c\u5c31\u89c1\u79d1\u6069\u653e\u4e0b\u9489\u5934\u9524\uff0c\u4f38\u51fa\u624b\u6307\u63d2\u5165\u5de8\u9b54\u5df2\u7ecf\u788e\u88c2\u7684\u8111\u888b\u91cc\uff0c\u8fc7\u4e86\u4e00\u4f1a\u62d4\u51fa\uff0c\u4e94\u6839\u624b\u6307\u4e0a\u90fd\u8638\u6ee1\u4e86\u8111\u6d46\u3002\u7267\u5e08\u8f6e\u6d41\u542e\u5438\u7740\u624b\u6307\uff0c\u5c06\u90a3\u4e9b\u7070\u7eff\u8272\u8111\u6d46\u5168\u6570\u54bd\u4e0b\uff0c\u4ed6\u7684\u8138\u4e0a\u9690\u9690\u900f\u51fa\u4e00\u80a1\u9ed1\u6c14\u6765\uff0c\u5fae\u5fae\u6cdb\u7740\u94f6\u5149\u3002</p><p>\u8fd9\u60c5\u5f62\u53ef\u8c13\u662f\u8be1\u5f02\u4e07\u5206\u3002\u79d1\u6069\u5c45\u7136\u5438\u98df\u5de8\u9b54\u7684\u8111\u6d46\uff0c\u96be\u9053\u8fd9\u5c31\u662f\u4ed6\u4eec\u6c83\u91d1\u6559\u4f1a\u7684\u673a\u5bc6\u624b\u6cd5\uff0c\u8fd9\u5c31\u662f\u91d1\u773c\u4f7f\u8005\u6240\u8c13\u7684\u201c\u4e07\u4e8b\u7686\u77e5\u201d\uff1f\u8fd9\u79cd\u884c\u4e3a\u65e0\u8bba\u600e\u4e48\u770b\u4e5f\u900f\u7740\u6781\u5176\u6d53\u91cd\u7684\u90aa\u6c14\uff0c\u5206\u660e\u662f\u4e00\u6d3e\u90aa\u9b54\u4f5c\u98ce\u3002</p><p>\u6c83\u91d1\u7684\u7267\u5e08\uff0c\u600e\u4f1a\u505a\u8fd9\u79cd\u4e8b\u60c5\u3002</p><p>\u5f53\u771f\u662f\u4e0d\u770b\u4e0d\u77e5\u9053\uff0c\u4e00\u770b\u5413\u4e00\u8df3\u3002\u743c\u6069\u83ab\u540d\u60ca\u8be7\uff0c\u53ea\u89c1\u79d1\u6069\u53c8\u6284\u8d77\u9489\u5934\u9524\uff0c\u91cd\u91cd\u7838\u4e0b\uff0c\u5f88\u5feb\u5c06\u5de8\u9b54\u7684\u53e6\u5916\u4e00\u9897\u8111\u888b\u4e5f\u7838\u5f00\uff0c\u4f9d\u65e7\u5982\u6cd5\u70ae\u5236\uff0c\u5438\u98df\u8111\u6d46\u3002\u4ed6\u7684\u8138\u4e0a\u9ed1\u6c14\u66f4\u52a0\u6d53\u91cd\uff0c\u9690\u9690\u900f\u51fa\u4e9b\u72f0\u72de\u795e\u8272\u6765\u3002</p><p>\u79d8\u6cd5\u773c\u5e76\u4e0d\u80fd\u7ef4\u6301\u592a\u4e45\uff0c\u81f3\u591a\u4e0d\u8fc7\u5341\u5206\u949f\u3002\u743c\u6069\u5df2\u7ecf\u770b\u4e86\u591a\u65f6\uff0c\u6015\u88ab\u7267\u5e08\u53d1\u73b0\u4fbf\u5c31\u4e0d\u59a5\uff0c\u6b63\u8981\u5c06\u79d8\u6cd5\u773c\u53ec\u56de\uff0c\u5374\u89c1\u79d1\u6069\u7a81\u7136\u7ad9\u76f4\u8eab\u6765\uff0c\u770b\u7740\u4e00\u4e2a\u65b9\u5411\uff0c\u8bf4\u4e86\u53e5\u4ec0\u4e48\u8bdd\u3002</p><p>\u79d8\u6cd5\u773c\u53ea\u6709\u89c6\u89c9\u6ca1\u6709\u542c\u89c9\uff0c\u6240\u4ee5\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5230\u5e95\u662f\u5728\u8bf4\u4ec0\u4e48\uff0c\u6b63\u8981\u79fb\u8f6c\u79d8\u6cd5\u773c\uff0c\u671d\u90a3\u4e2a\u65b9\u5411\u770b\u53bb\uff0c\u9661\u5730\u4fbf\u89c9\u773c\u524d\u4e00\u9053\u5bd2\u5149\u70c1\u8fc7\u3002</p><p>\u79d8\u6cd5\u773c\u6240\u89c1\u666f\u8c61\uff0c\u540c\u65f6\u5c31\u662f\u743c\u6069\u6240\u89c1\u666f\u8c61\uff0c\u4ed6\u540c\u65f6\u4fbf\u4e5f\u89c9\u5f97\u773c\u524d\u4e00\u9053\u5bd2\u5149\u9aa4\u7136\u70c1\u8fc7\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u672c\u80fd\u5730\u540e\u9000\u4e86\u534a\u6b65\u3002\u5f85\u56de\u8fc7\u795e\u6765\uff0c\u53d1\u89c9\u79d8\u6cd5\u773c\u5df2\u7ecf\u6d88\u5931\uff0c\u540c\u65f6\u5fc3\u91cc\u4e00\u9635\u9635\u7684\u9690\u9690\u5fc3\u60b8\uff0c\u8bf4\u4e0d\u51fa\u6765\u7684\u96be\u53d7\u3002</p><p>\u8fd9\u8bf4\u660e\u79d8\u6cd5\u773c\u4e0d\u662f\u65f6\u95f4\u5230\u4e86\u81ea\u884c\u6d88\u5931\uff0c\u800c\u662f\u88ab\u5916\u529b\u51fb\u6bc1\u4e86\u3002</p><p>\u79d8\u6cd5\u773c\u6ca1\u6709\u4e1d\u6beb\u7684\u653b\u51fb\u529b\u548c\u9632\u5fa1\u529b\uff0c\u666e\u901a\u4eba\u4e00\u62f3\u90fd\u80fd\u6253\u70c2\uff0c\u8fd9\u4e0d\u8db3\u4e3a\u5947\u3002\u521a\u624d\u773c\u524d\u70c1\u8fc7\u7684\u90a3\u4e00\u9053\u5bd2\u5149\uff0c\u5e94\u8be5\u4fbf\u662f\u653b\u51fb\u4e86\u3002\u4f46\u95ee\u9898\u662f\uff0c\u79d8\u6cd5\u773c\u8ddd\u79bb\u79d1\u6069\u6709\u4e00\u6bb5\u867d\u7136\u4e0d\u8fdc\uff0c\u5374\u4e5f\u4e0d\u8fd1\u7684\u8ddd\u79bb\uff0c\u7267\u5e08\u5e94\u8be5\u6ca1\u6709\u8fd9\u4e48\u5feb\u7684\u52a8\u4f5c\u624d\u5bf9\uff0c\u5c45\u7136\u8ba9\u743c\u6069\u8fde\u53cd\u5e94\u7684\u65f6\u95f4\u90fd\u6ca1\u6709\u3002</p><p>\u662f\u53e6\u5916\u6709\u4eba\u53d1\u73b0\u79d8\u6cd5\u773c\uff0c\u51fa\u624b\u6467\u6bc1\u4e86\uff1f</p><p>\u8fd9\u5ea7\u5de8\u9b54\u5c71\u8109\u91cc\uff0c\u53e6\u5916\u8fd8\u6709\u5176\u4ed6\u4eba\uff1f\u662f\u548c\u6c83\u91d1\u7267\u5e08\u4e00\u4f19\u7684\uff1f</p><p>\u743c\u6069\u5fc3\u5934\u7684\u7591\u8651\u8d8a\u6765\u8d8a\u6df1\uff0c\u4f46\u4ed6\u5df2\u7ecf\u5931\u53bb\u4e86\u79d8\u6cd5\u773c\uff0c\u6ca1\u6cd5\u518d\u770b\u5230\u6811\u6797\u91cc\u53d1\u751f\u7684\u4e00\u5207\u3002\u4ed6\u53ea\u6709\u8fd9\u4e00\u5f20\u5377\u8f74\uff0c\u4e5f\u4e0d\u53ef\u80fd\u518d\u6765\u4e00\u6b21\uff0c\u4f55\u51b5\u5df2\u7ecf\u88ab\u53d1\u89c9\uff0c\u518d\u7528\u4e0d\u8fc7\u662f\u81ea\u53d6\u5176\u8fb1\u3002\u4ed6\u9ed8\u60f3\u4e86\u60f3\uff0c\u8d70\u56de\u6885\u83f2\u65af\u8eab\u8fb9\uff0c\u4f4e\u58f0\u5c06\u521a\u624d\u6240\u770b\u5230\u7684\u4e00\u5207\u544a\u8bc9\u5c11\u5973\u3002</p><p>\u6885\u83f2\u65af\u542c\u4e86\uff0c\u9762\u65e0\u8868\u60c5\uff0c\u4e5f\u5e76\u6ca1\u8bf4\u8bdd\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6c83\u91d1\u7267\u5e08\u4ece\u6811\u6797\u91cc\u8d70\u51fa\u6765\uff0c\u671d\u5927\u5bb6\u5fae\u5fae\u70b9\u5934\u3002</p><p>\u201c\u641e\u6e05\u695a\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6050\u6015\u6211\u4eec\u731c\u5f97\u6ca1\u9519\uff0c\u786e\u5b9e\u662f\u62c9\u6c83\u514b\u5e72\u7684\u597d\u4e8b\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u79d1\u6069\u8bf4\uff0c\u4ed6\u8bfb\u53d6\u4e86\u8fd9\u53ea\u5de8\u9b54\u7684\u8bb0\u5fc6\uff0c\u53d1\u73b0\u4e00\u4e9b\u5f88\u6709\u8da3\u7684\u4e8b\u60c5\u3002</p><p>\u8fd9\u53ea\u5de8\u9b54\u539f\u672c\u5c31\u662f\u4e2a\u975e\u5e38\u6b63\u5e38\u7684\u54c1\u79cd\uff0c\u4e00\u9897\u8111\u888b\u4e24\u53ea\u80f3\u818a\uff0c\u65e2\u4e0d\u4f1a\u55b7\u706b\u4e5f\u4e0d\u4f1a\u653e\u95ea\u7535\uff0c\u5b8c\u5b8c\u5168\u5168\u5c31\u662f\u4e2a\u666e\u901a\u5de8\u9b54\uff0c\u548c\u5176\u4ed6\u540c\u7c7b\u5c45\u4f4f\u751f\u6d3b\u5728\u4e00\u8d77\uff0c\u6bcf\u5929\u72e9\u730e\u3001\u5403\u996d\u3001\u7761\u89c9\uff0c\u8fc7\u5f97\u65e0\u5fe7\u65e0\u8651\u3002</p><p>\u4f46\u6709\u4e00\u6b21\uff0c\u8fd9\u53ea\u5de8\u9b54\u8bef\u6253\u8bef\u649e\uff0c\u8d70\u5230\u4e86\u4e00\u4e2a\u5730\u65b9\u3002\u6839\u636e\u79d1\u6069\u8bfb\u53d6\u8bb0\u5fc6\u7684\u7ed3\u679c\u6765\u770b\uff0c\u90a3\u91cc\u4f3c\u4e4e\u662f\u4e00\u5ea7\u5de8\u5927\u7684\u7684\u53e4\u57ce\u5821\uff0c\u5230\u5904\u90fd\u5ea7\u843d\u7740\u7070\u6697\u7684\u9ad8\u5854\u548c\u65ad\u58c1\u6b8b\u6853\uff0c\u51b7\u5bc2\u8352\u51c9\uff0c\u6ca1\u6709\u534a\u4e2a\u4eba\u5f71\u548c\u6d3b\u7269\u3002</p><p>\u7136\u540e\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u5de8\u9b54\u5c31\u89c9\u5f97\u9762\u524d\u4e00\u7247\u9ed1\u6697\u3002\u5f53\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u6b63\u88ab\u56da\u7981\u5728\u4e00\u4e2a\u73bb\u7483\u7f69\u4e2d\u3002\u5b83\u770b\u89c1\u4e00\u5806\u74f6\u74f6\u7f50\u7f50\u548c\u5947\u602a\u7684\u4eea\u5668\u4e4b\u7c7b\uff0c\u5177\u4f53\u662f\u4ec0\u4e48\uff0c\u4ee5\u5de8\u9b54\u7684\u667a\u529b\u5b83\u65e0\u6cd5\u7406\u89e3\uff0c\u4e5f\u6ca1\u80fd\u6e05\u695a\u8bb0\u5fc6\u4e0b\u6765\uff1b\u4ee4\u5b83\u5370\u8c61\u6700\u6df1\u523b\u7684\uff0c\u662f\u6709\u4e00\u5177\u7a7f\u7740\u534e\u4e3d\u957f\u888d\u7684\u9ab7\u9ac5\u67b6\u5b50\u5728\u5b83\u9762\u524d\u8d70\u6765\u8d70\u53bb\uff0c\u6253\u91cf\u7740\u5b83\u3002\u9ab7\u9ac5\u773c\u7736\u4e2d\u7684\u5fae\u5f31\u7ea2\u5149\u4eff\u4f5b\u9488\u5c16\u4e00\u822c\u9510\u5229\u903c\u4eba\uff0c\u5c31\u7b97\u662f\u5de8\u9b54\u8fd9\u79cd\u7c97\u7ebf\u6761\u6ca1\u8111\u7b4b\u751f\u7269\u90fd\u60c5\u4e0d\u81ea\u7981\u5730\u611f\u89c9\u5230\u6050\u60e7\u3002</p><p>\u81f3\u4e8e\u540e\u6765\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5df2\u7ecf\u5b8c\u5168\u8d85\u51fa\u5de8\u9b54\u7684\u7406\u89e3\u80fd\u529b\uff0c\u5b83\u7684\u8bb0\u5fc6\u4e5f\u88ab\u5f04\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u5b8c\u5168\u662f\u6563\u4e71\u7684\u788e\u7247\uff0c\u79d1\u6069\u6839\u672c\u6ca1\u529e\u6cd5\u4ece\u91cc\u9762\u5f97\u77e5\u4ec0\u4e48\u3002\u53cd\u6b63\u8fc7\u4e86\u5f88\u4e45\uff0c\u5f53\u5de8\u9b54\u518d\u6b21\u6062\u590d\u6e05\u9192\u610f\u8bc6\u7684\u65f6\u5019\uff0c\u5b83\u53d1\u73b0\u81ea\u5df1\u8eba\u5728\u91ce\u5916\uff0c\u5df2\u7ecf\u957f\u51fa\u4e24\u4e2a\u8111\u888b\u4e86\u3002</p><p>\u5de8\u9b54\u662f\u79cd\u6ca1\u8111\u7b4b\u7684\u751f\u7269\uff0c\u5373\u4fbf\u591a\u4e86\u4e00\u4e2a\u8111\u888b\uff0c\u5b83\u4eec\u7684\u667a\u529b\u4e5f\u5e76\u6ca1\u6709\u56e0\u6b64\u63d0\u5347\u3002\u6240\u4ee5\u867d\u7136\u906d\u9047\u8fd9\u79cd\u7a81\u7136\u53d8\u6545\uff0c\u5b83\u5374\u5f88\u5feb\u5c31\u9002\u5e94\u8fc7\u6765\uff0c\u6216\u8005\u8bf4\uff0c\u5b83\u538b\u6839\u5c31\u6ca1\u628a\u8fd9\u4ef6\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002\u4e0d\u5c31\u662f\u591a\u957f\u51fa\u4e00\u4e2a\u8111\u888b\u6765\u4e48\uff0c\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86\u7684\uff0c\u4e0d\u5fc5\u5728\u610f\u3002</p><p>\u4e0d\u4f46\u5b83\u8fd9\u4e48\u60f3\uff0c\u5c31\u8fde\u5b83\u7684\u90a3\u4e9b\u5de8\u9b54\u540c\u4f34\u90fd\u62b1\u8fd9\u79cd\u6001\u5ea6\uff0c\u5f00\u59cb\u65f6\u8fd8\u6709\u4e9b\u60ca\u8bb6\uff0c\u6ca1\u4e24\u5929\u5c31\u4e60\u4ee5\u4e3a\u5e38\uff0c\u597d\u50cf\u5b83\u751f\u6765\u5c31\u662f\u53cc\u5934\u4f3c\u7684\u3002\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u667a\u529b\u4f4e\u7684\u751f\u7269\uff0c\u63a5\u53d7\u65b0\u9c9c\u4e8b\u7269\u7684\u80fd\u529b\u771f\u662f\u5feb\uff0c\u4e1d\u6beb\u6ca1\u6709\u969c\u788d\u4f3c\u7684\u3002</p><p>\u6e10\u6e10\u5730\uff0c\u5de8\u9b54\u53d1\u73b0\u81ea\u5df1\u62e5\u6709\u4e86\u53e3\u5410\u70c8\u706b\u7684\u80fd\u529b\uff0c\u8fd8\u53d1\u73b0\u81ea\u5df1\u80fd\u968f\u5fc3\u6240\u6b32\u5730\u91ca\u653e\u51fa\u7403\u72b6\u95ea\u7535\u6765\u2014\u2014\u548c\u81ea\u5df1\u7a81\u7136\u957f\u51fa\u4e24\u4e2a\u8111\u888b\u4e00\u6837\uff0c\u5de8\u9b54\u975e\u5e38\u81ea\u7136\u5730\u63a5\u53d7\u4e86\u8fd9\u4e00\u5207\u53d8\u5316\uff0c\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u5fc3\u7406\u969c\u788d\u3002\u51ed\u501f\u7740\u8fd9\u4e9b\u7a81\u7136\u5192\u51fa\u6765\u7684\u7279\u5f02\u80fd\u529b\uff0c\u5de8\u9b54\u8fc5\u901f\u6210\u4e3a\u5b83\u90a3\u4e00\u90e8\u843d\u7684\u9886\u8896\u3002</p><p>\u76f4\u5230\u4eca\u5929\u649e\u4e0a\u6885\u83f2\u65af\u7b49\u4eba\u3002</p><p>\u7267\u5e08\u4ece\u5de8\u9b54\u8111\u4e2d\u8bfb\u53d6\u7684\u8bb0\u5fc6\u5927\u81f4\u5982\u6b64\uff0c\u5176\u4e2d\u6700\u5173\u952e\u7684\u90e8\u5206\u2014\u2014\u88ab\u6539\u9020\u7684\u8fc7\u7a0b\u2014\u2014\u4e27\u5931\u4e86\uff0c\u6216\u8005\u8bf4\u4e00\u584c\u7cca\u6d82\uff0c\u6839\u672c\u6574\u7406\u4e0d\u51fa\u5176\u4e2d\u7684\u4fe1\u606f\u3002\u4f46\u4ece\u5df2\u77e5\u7684\u8fd9\u4e9b\u8d44\u6599\u6765\u5224\u65ad\uff0c\u57fa\u672c\u4e0a\u4e5f\u53ef\u4ee5\u5f97\u51fa\u7ed3\u8bba\u4e86\u3002</p><p>\u4e00\u5177\u7a7f\u7740\u534e\u4e3d\u957f\u888d\u7684\u9ab7\u9ac5\u9aa8\u67b6\uff0c\u773c\u7736\u4e2d\u900f\u7740\u5c16\u9510\u7684\u5fae\u5f31\u7ea2\u5149\u2014\u2014\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u662f\u4e2a\u5deb\u5996\uff0c\u666e\u901a\u7684\u9ab7\u9ac5\u5c31\u7b97\u4f1a\u7a7f\u8eab\u957f\u888d\uff0c\u4e5f\u6ca1\u529e\u6cd5\u5728\u773c\u7736\u91cc\u653e\u51fa\u7ea2\u5149\u6765\u3002\u800c\u4e14\u5bfb\u5e38\u7684\u9ab7\u9ac5\u4e0d\u8fc7\u662f\u6700\u4f4e\u7ea7\u7684\u4ea1\u7075\uff0c\u5b8c\u5168\u6ca1\u6709\u81ea\u5df1\u7684\u610f\u8bc6\uff0c\u54ea\u91cc\u53ef\u80fd\u8fd8\u8d70\u6765\u8d70\u53bb\uff0c\u6253\u91cf\u7740\u5de8\u9b54\u3002</p><p>\u5de8\u9b54\u5c71\u8109\u91cc\uff0c\u603b\u4e0d\u4f1a\u6709\u5f88\u591a\u5deb\u5996\u5427\u3002\u80fd\u628a\u4e00\u4e2a\u5de8\u9b54\u6539\u9020\u6210\u8fd9\u79cd\u602a\u7269\uff0c\u8fd8\u80fd\u73a9\u201c\u7cbe\u91d1\u9aa8\u9abc\xb7\u6c38\u4e45\u6052\u5b9a\u201d\u8fd9\u79cd\u53d8\u6001\u9b54\u6cd5\u7684\u5bb6\u4f19\uff0c\u60f3\u6765\u60f3\u53bb\u4e5f\u4e0d\u4f1a\u8fd8\u6709\u522b\u4eba\u3002</p><p>\u4f46\u62c9\u6c83\u514b\u5230\u5e95\u51c6\u5907\u505a\u4ec0\u4e48\u5462\uff1f\u5f04\u51fa\u8fd9\u534a\u751f\u534a\u6b7b\u3001\u751f\u6b7b\u63ba\u6742\u7684\u602a\u7269\u51fa\u6765\uff0c\u96be\u9053\u5f88\u6709\u8da3\u4e48\uff1f</p><p>\u8fd9\u4e9b\u95ee\u9898\u6ca1\u4eba\u53ef\u4ee5\u89e3\u7b54\uff0c\u4e00\u4e2a\u4e24\u5343\u5e74\u7684\u8001\u5deb\u5996\uff0c\u8c01\u77e5\u9053\u4ed6\u8111\u5b50\u91cc\u60f3\u4ec0\u4e48\u3002\u6216\u8bb8\u662f\u5fc3\u8840\u6765\u6f6e\uff0c\u60f3\u521b\u9020\u4e00\u4e2a\u65b0\u602a\u7269\uff1b\u6216\u8bb8\u4ed6\u539f\u672c\u53e6\u6709\u6240\u56fe\uff0c\u53ea\u662f\u5b9e\u9a8c\u5931\u8d25\uff0c\u8fd9\u53ea\u53cc\u5934\u5de8\u9b54\u5176\u5b9e\u662f\u4e2a\u534a\u6210\u54c1\uff1b\u4e5f\u6216\u8bb8\u4ed6\u5728\u79d8\u5bc6\u8fdb\u884c\u4ec0\u4e48\u8ba1\u5212\u2014\u2014\u603b\u4e4b\uff0c\u4ec0\u4e48\u90fd\u6709\u53ef\u80fd\uff0c\u51ed\u7a7a\u731c\u6d4b\u6beb\u65e0\u610f\u4e49\u3002</p><p>\u53ea\u8981\u77e5\u9053\u8fd9\u53ea\u5de8\u9b54\u4e4b\u6240\u4ee5\u5982\u6b64\u5947\u602a\uff0c\u786e\u5b9e\u548c\u62c9\u6c83\u514b\u6709\u5173\u5c31\u884c\u4e86\u3002</p><p>\u6700\u540e\uff0c\u79d1\u6069\u63d0\u4f9b\u4e86\u4e00\u4e2a\u9887\u4e3a\u8010\u4eba\u5bfb\u5473\u7684\u4fe1\u606f\u3002</p><p>\u201c\u90a3\u53ea\u5de8\u9b54\u7684\u8bb0\u5fc6\u91cc\uff0c\u81ea\u4ece\u88ab\u6539\u9020\u4e4b\u540e\uff0c\u6709\u4e00\u4e2a\u8bcd\u5c31\u53cd\u590d\u51fa\u73b0\uff0c\u201d\u4ed6\u8bf4\uff0c\u6a21\u62df\u7740\u53d1\u51fa\u4e00\u4e2a\u53cc\u97f3\u8282\u8bcd\u6765\uff0c\u201c\u5c31\u662f\u8fd9\u4e2a\uff0c\u53cd\u590d\u51fa\u73b0\uff0c\u4f46\u6211\u4e0d\u660e\u767d\u5b83\u7684\u610f\u601d\u3002\u201d</p><p>\u743c\u6069\u7684\u5fc3\u5934\u5fae\u5fae\u4e00\u51db\uff0c\u4ed6\u542c\u5f97\u61c2\u8fd9\u4e2a\u8bcd\uff0c\u8fd9\u662f\u53e4\u8010\u745f\u8bed\uff0c\u800c\u4e14\u662f\u4e00\u4e2a\u9b54\u6cd5\u65b9\u9762\u7684\u4e13\u4e1a\u672f\u8bed\uff1a\u201c\u751f\u6b7b\u8f6c\u5316\u201d\u3002</p><p>\u79d1\u6069\u81ea\u7136\u4e0d\u4f1a\u61c2\u8fd9\u4e2a\u8bcd\uff0c\u91d1\u773c\u4f7f\u8005\u867d\u7136\u62e5\u6709\u5f88\u5f3a\u7684\u8bed\u8a00\u5b66\u4e60\u80fd\u529b\uff0c\u4f46\u4e5f\u8981\u548c\u4eba\u7cfb\u7edf\u4ea4\u8c08\u8fc7\u624d\u884c\uff0c\u5355\u72ec\u5192\u51fa\u4e00\u4e2a\u8bcd\u6765\uff0c\u4ed6\u4e5f\u731c\u4e0d\u51fa\u662f\u4ec0\u4e48\u610f\u601d\u3002\u9686\u5947\u3001\u8d1d\u7eb3\u7d22\u548c\u590f\u62c9\u975e\u663e\u7136\u4e5f\u90fd\u4e0d\u61c2\uff0c\u6885\u83f2\u65af\u5219\u9762\u65e0\u8868\u60c5\uff0c\u743c\u6069\u6000\u7591\u5979\u662f\u80fd\u542c\u61c2\u7684\uff0c\u8fd9\u4e2a\u5c11\u5973\u4f3c\u4e4e\u7cbe\u901a\u4e16\u754c\u4e0a\u6240\u6709\u7684\u8bed\u8a00\uff0c\u4f46\u5979\u6ca1\u4ec0\u4e48\u53cd\u5e94\u3002</p><p>\u5de8\u9b54\u4e5f\u4e0d\u53ef\u80fd\u61c2\u8010\u745f\u8bed\uff0c\u66f4\u4e0d\u4f1a\u77e5\u9053\u201c\u751f\u6b7b\u8f6c\u5316\u201d\u8fd9\u79cd\u4e13\u4e1a\u672f\u8bed\u3002\u8fd9\u662f\u8010\u745f\u5e1d\u56fd\u65f6\u4ee3\u7684\u4e00\u4e2a\u975e\u5e38\u504f\u50fb\u7684\u9b54\u6cd5\u7814\u7a76\u8bfe\u9898\uff0c\u636e\u8bf4\u8fd9\u65b9\u9762\u7684\u6743\u5a01\u4eba\u7269\u662f\u4e00\u4f4d\u53eb\u5965\u6c83\u7684\u5927\u5965\u672f\u5e08\u3002\u5f53\u7136\uff0c\u968f\u7740\u5e1d\u56fd\u9668\u706d\uff0c\u5965\u6c83\u4f30\u8ba1\u4e5f\u65e9\u5c31\u5b8c\u86cb\u4e86\u3002\u743c\u6069\u4ee5\u524d\u66fe\u7ecf\u5728\u4e66\u4e0a\u5076\u7136\u770b\u5230\u8fd9\u4e2a\u8bcd\uff0c\u77e5\u9053\u662f\u7814\u7a76\u201c\u751f\u8005\u201d\u4e0e\u201c\u4ea1\u7075\u201d\u76f8\u4e92\u8f6c\u5316\u7684\u6280\u5de7\uff0c\u5177\u4f53\u7684\u60c5\u51b5\u5c31\u4e0d\u6e05\u695a\u4e86\u3002</p><p>\u5de8\u9b54\u7684\u8bb0\u5fc6\u91cc\uff0c\u4f1a\u53cd\u590d\u51fa\u73b0\u8fd9\u4e2a\u8bcd\uff0c\u53ea\u6709\u4e00\u79cd\u53ef\u80fd\uff0c\u6216\u8005\u8bf4\uff0c\u53ea\u4f1a\u548c\u4e00\u4e2a\u4eba\u6709\u5173\u3002</p><p>\u62c9\u6c83\u514b\u3002</p></article>"}}}]);
