(window.webpackJsonp=window.webpackJsonp||[]).push([[1325],{1348:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e5d\u8282 \u88ab\u62b1\u7740\u7684\u611f\u89c9",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e5d\u8282 \u88ab\u62b1\u7740\u7684\u611f\u89c9</h2><p>\u56de\u5230\u4f4f\u5904\uff0c\u743c\u6069\u53d6\u51fa\u5c0f\u80d6\u5b50\u8ff7\u5e7b\u7ed9\u4ed6\u7684\u73e0\u5b9d\u888b\uff0c\u9012\u7ed9\u6885\u83f2\u65af\uff0c\u8bf7\u5979\u5e2e\u5fd9\u4f30\u4ef7\u3002\u5c11\u5973\u867d\u7136\u5e74\u8f7b\uff0c\u7ecf\u5386\u5374\u591a\uff0c\u65e2\u66fe\u7ecf\u5728\u793e\u4f1a\u6700\u4e0b\u5c42\u6253\u62fc\uff0c\u4e5f\u66fe\u7ecf\u8eab\u5c45\u6559\u4f1a\u9ad8\u9636\uff0c\u773c\u5149\u89c1\u8bc6\u8fdc\u975e\u5e38\u4eba\u53ef\u6bd4\u3002\u5979\u5c06\u888b\u5b50\u91cc\u7684\u73cd\u73e0\u5b9d\u77f3\u5168\u90fd\u4e00\u80a1\u8111\u5012\u5728\u5e8a\u4e0a\uff0c\u501f\u7740\u706f\u5149\u5f00\u59cb\u9274\u522b\u3002</p><p>\u201c\u767d\u73cd\u73e0\u3001\u9ed1\u73cd\u73e0\u3001\u7d2b\u6c34\u6676\u3001\u5929\u84dd\u77f3\u3001\u94bb\u77f3\uff0c\u8fd8\u6709\u7ea2\u5b9d\u77f3\u548c\u84dd\u5b9d\u77f3\uff0c\u201d\u6885\u83f2\u65af\u4e00\u4e00\u62a5\u51fa\u540d\u5b57\uff0c\u7136\u540e\u4ece\u5176\u4e2d\u7279\u522b\u62e3\u51fa\u5341\u51e0\u9897\u6765\uff0c\u653e\u5728\u4e00\u65c1\uff0c\u201c\u8fd9\u4e9b\u4f60\u6216\u8bb8\u6709\u7528\u3002\u201d</p><p>\u743c\u6069\u770b\u53bb\uff0c\u4ed6\u867d\u7136\u4e8e\u73e0\u5b9d\u4e00\u9053\u4e0d\u751a\u719f\u6089\uff0c\u4f46\u6885\u83f2\u65af\u62e3\u51fa\u7684\u8fd9\u4e9b\u4ed6\u5012\u662f\u90fd\u8ba4\u8bc6\u7684\uff0c\u7389\u9ad3\u3001\u7ea2\u9506\u77f3\u3001\u8537\u8587\u8f89\u77f3\u3001\u541b\u738b\u4e4b\u6cea\u3001\u7eef\u7ea2\u4e4b\u6cea\uff0c\u8fd9\u4e9b\u90fd\u662f\u8457\u540d\u7684\u9b54\u6cd5\u5b9d\u77f3\uff0c\u53ef\u4ee5\u7528\u6765\u5236\u4f5c\u5404\u79cd\u76f8\u5e94\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u6bd4\u5982\u7389\u9ad3\uff0c\u5c31\u5f88\u9002\u5408\u5236\u4f5c\u6210\u907f\u4ea1\u62a4\u7b26\uff0c\u80fd\u8ba9\u4f69\u5e26\u8005\u8eb2\u907f\u4f4e\u9636\u4ea1\u7075\u7684\u611f\u77e5\uff0c\u5373\u4f7f\u4ece\u4e00\u7fa4\u50f5\u5c38\u9762\u524d\u8d70\u8fc7\u4e5f\u4e0d\u88ab\u5bdf\u89c9\u3002\u5f53\u7136\uff0c\u5982\u679c\u6709\u4eba\u4ee5\u4e3a\u6234\u7740\u8fd9\u4e1c\u897f\u5c31\u80fd\u53bb\u62db\u60f9\u5438\u8840\u9b3c\u6216\u8005\u5deb\u5996\uff0c\u90a3\u7eaf\u5c5e\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002</p><p>\u743c\u6069\u4e8e\u5236\u9020\u9b54\u6cd5\u7269\u54c1\u4e00\u9053\u5012\u662f\u9887\u6709\u6240\u597d\uff0c\u53ea\u662f\u76ee\u524d\u80fd\u529b\u8fd8\u4e0d\u8db3\uff0c\u624b\u5934\u4e5f\u7f3a\u4e4f\u6750\u6599\uff0c\u4e00\u76f4\u8fd8\u4e0d\u66fe\u771f\u6b63\u81ea\u5df1\u5236\u4f5c\u51fa\u4ec0\u4e48\u4e1c\u897f\u6765\u3002\u5982\u4eca\u610f\u5916\u5f97\u4e86\u8fd9\u4e9b\u5b9d\u77f3\uff0c\u4ef7\u503c\u90fd\u9887\u4e0d\u83f2\uff0c\u82e5\u662f\u81ea\u5df1\u53bb\u5e02\u573a\u8d2d\u4e70\uff0c\u4f30\u8ba1\u51e0\u767e\u4e0a\u5343\u91d1\u5e01\u90fd\u672a\u5fc5\u80fd\u4e70\u5230\uff0c\u771f\u53ef\u8c13\u662f\u5929\u4e0a\u6389\u9985\u997c\u4e86\u3002\u5148\u7559\u7740\uff0c\u65e5\u540e\u603b\u4f1a\u7528\u4e0a\u3002</p><p>\u90a3\u4e9b\u5b8c\u5168\u4e0d\u9002\u5408\u7528\u6765\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u7684\u73e0\u5b9d\uff0c\u6885\u83f2\u65af\u4e5f\u90fd\u7279\u522b\u6311\u62e3\u4e86\u51fa\u6765\uff0c\u5927\u81f4\u4f30\u7b97\u4e86\u4e00\u4e0b\u4ef7\u503c\uff0c\u8db3\u4ee5\u4ed8\u5341\u51e0\u4e2a\u6cd5\u672f\u7684\u6284\u5f55\u8d39\u7528\u4e86\u3002\u8fd9\u8ba9\u743c\u6069\u9ad8\u5174\u81f3\u6781\uff0c\u70db\u5821\u4e00\u884c\uff0c\u7ec8\u4e8e\u7b97\u662f\u6709\u4e86\u6bd4\u8f83\u6ee1\u610f\u7684\u7ed3\u679c\u2026\u2026\u9664\u4e86\u77e5\u9053\u62c9\u6c83\u514b\u662f\u4e2a\u5deb\u5996\u4ee5\u5916\u3002</p><p>\u8fd9\u4ed6\u5988\u7684\u771f\u662f\u4e2a\u5669\u8017\u3002</p><p>\u56e0\u4e3a\u5fc3\u91cc\u591a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u743c\u6069\u8fde\u7761\u89c9\u90fd\u7761\u4e0d\u597d\u4e86\u3002\u4ed6\u8eba\u5728\u5e8a\u4e0a\uff0c\u95ed\u7740\u773c\u775b\uff0c\u539f\u672c\u6253\u7b97\u8fd0\u6c14\u8c03\u606f\uff0c\u4f46\u8111\u4e2d\u8f6c\u6765\u8f6c\u53bb\u7684\uff0c\u5168\u662f\u67d0\u4e2a\u62ab\u7740\u534e\u4e3d\u957f\u888d\u7684\u9ab7\u9ac5\u67b6\u5b50\u2014\u2014\u636e\u8bf4\u5deb\u5996\u5927\u591a\u90fd\u8fd9\u4e2a\u5f62\u8c61\uff0c\u4f30\u8ba1\u62c9\u6c83\u514b\u4e5f\u4e0d\u4f8b\u5916\u5427\u3002</p><p>\u6885\u83f2\u65af\u6e29\u987a\u5730\u8eba\u5728\u743c\u6069\u6000\u91cc\uff0c\u80cc\u8d34\u7740\u4ed6\u8d64\u88f8\u7684\u80f8\u819b\uff0c\u611f\u53d7\u7740\u8d77\u4f0f\u4e0d\u5b9a\u7684\u547c\u5438\u3002\u5c11\u5973\u611f\u89c9\u5230\u743c\u6069\u5e76\u6ca1\u6709\u7761\u7740\uff0c\u4ed6\u6709\u6b63\u5728\u70e6\u607c\u7684\u5fc3\u4e8b\u3002\u4f46\u65e2\u7136\u743c\u6069\u4e0d\u8bf4\uff0c\u5979\u4e5f\u4e0d\u95ee\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5979\u51e0\u4e4e\u4ece\u6765\u4e0d\u66fe\u8fc7\u95ee\u8fc7\u743c\u6069\u5230\u5e95\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u4ec0\u4e48\u6765\u5386\uff0c\u6765\u535a\u5f97\u4e4b\u95e8\u505a\u4ec0\u4e48\u3002\u6240\u6709\u8fd9\u4e9b\uff0c\u6885\u83f2\u65af\u4e00\u6982\u4e0d\u63d0\uff0c\u4eff\u4f5b\u8fd9\u4e2a\u5c11\u5973\u5b8c\u5168\u6ca1\u6709\u597d\u5947\u5fc3\u4f3c\u7684\u3002</p><p>\u4e0e\u81ea\u5df1\u65e0\u5173\u7684\u4e8b\u60c5\uff0c\u5c31\u4e0d\u5fc5\u53bb\u7406\u4f1a\u5427\uff0c\u6885\u83f2\u65af\u4e00\u5411\u90fd\u5982\u6b64\u3002\u4e0d\u662f\u51b0\u51b7\uff0c\u800c\u662f\u51b7\u6de1\uff0c\u5f80\u5f80\u8ba9\u4eba\u89c9\u5f97\u5979\u5bf9\u4ec0\u4e48\u90fd\u6f20\u4e0d\u5173\u5fc3\uff0c\u8fdb\u800c\u5c31\u4f1a\u89c9\u5f97\u8fd9\u4e2a\u5973\u5b69\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\uff0c\u5982\u51b0\u5c71\u822c\u4e0d\u53ef\u4eb2\u8fd1\u3002\u4f46\u5b9e\u9645\u4e0a\uff0c\u5979\u53ea\u4e0d\u8fc7\u662f\u61d2\u800c\u5df2\u3002\u61d2\u5f97\u591a\u8bf4\u5e9f\u8bdd\uff0c\u61d2\u5f97\u591a\u7ba1\u95f2\u4e8b\uff0c\u53ea\u6709\u780d\u4eba\u7684\u65f6\u5019\u4e0d\u61d2\u3002</p><p>\u65f6\u5019\u4e0d\u65e9\u4e86\uff0c\u5c11\u5973\u6b63\u8981\u7761\u53bb\uff0c\u7a81\u7136\u89c9\u5f97\u80cc\u540e\u9690\u9690\u6709\u80a1\u5bd2\u6c14\u900f\u8863\u800c\u6765\uff0c\u77ac\u95f4\u5168\u8eab\u76ae\u80a4\u90fd\u6cdb\u8d77\u7ec6\u5c0f\u5bd2\u7c92\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u5fae\u5fae\u6253\u4e86\u4e2a\u98a4\u3002\u5979\u60ca\u8bb6\u7740\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\uff0c\u53ea\u9690\u7ea6\u53d1\u89c9\u6b63\u6402\u62b1\u7740\u81ea\u5df1\u7684\u8fd9\u4e2a\u7537\u4eba\u4f3c\u4e4e\u4f53\u6e29\u5728\u6025\u5267\u4e0b\u964d\u3002</p><p>\u6885\u83f2\u65af\u7ffb\u8f6c\u8fc7\u8eab\uff0c\u770b\u7740\u743c\u6069\uff0c\u89c1\u4ed6\u8138\u8272\u5fae\u5fae\u53d1\u9752\uff0c\u5374\u65e0\u4ec0\u4e48\u8868\u60c5\uff0c\u53cc\u773c\u7d27\u95ed\uff0c\u4eff\u4f5b\u6c89\u7761\uff0c\u4f46\u547c\u5438\u5374\u77ac\u95f4\u53d8\u5f97\u5e73\u7f13\u60a0\u957f\u8d77\u6765\uff0c\u4e0d\u77e5\u9053\u5230\u5e95\u662f\u7761\u662f\u9192\u3002\u4ed6\u7684\u4f53\u6e29\u8fd8\u5728\u4e0b\u964d\uff0c\u5df2\u7ecf\u89e6\u624b\u751f\u5bd2\uff0c\u5c11\u5973\u968f\u5373\u654f\u9510\u5730\u53d1\u73b0\u8fd9\u80a1\u5bd2\u6c14\u4f3c\u4e4e\u662f\u4ece\u4ed6\u7684\u80f8\u53e3\u90e8\u4f4d\u6563\u53d1\u5f00\u6765\u7684\u3002</p><p>\u8fd9\u6837\u4e0b\u53bb\uff0c\u4ed6\u53ea\u6015\u4f1a\u51bb\u6b7b\u5427\u3002</p><p>\u6885\u83f2\u65af\u4e5f\u4e0d\u77e5\u9053\u743c\u6069\u5f97\u4e86\u4ec0\u4e48\u75c5\uff0c\u66f4\u9ebb\u70e6\u7684\u662f\uff0c\u5979\u867d\u7136\u662f\u5723\u6b66\u58eb\uff0c\u5374\u6ca1\u529e\u6cd5\u4f7f\u7528\u795e\u672f\uff0c\u5426\u5219\u8fd8\u53ef\u4ee5\u521d\u6b65\u6cbb\u7597\u4e00\u4e0b\u3002\u770b\u6765\u53ea\u6709\u53bb\u65c1\u8fb9\u7684\u795e\u6bbf\u8bf7\u7267\u5e08\u4e86\uff0c\u597d\u5728\u79bb\u5f97\u8fd1\u3002</p><p>\u6b63\u8981\u8d77\u8eab\uff0c\u5374\u88ab\u62b1\u4f4f\u4e86\uff0c\u743c\u6069\u4eff\u4f5b\u611f\u89c9\u5230\u6e29\u6696\uff0c\u672c\u80fd\u5730\u5f80\u5979\u8eab\u4e0a\u8d34\u8fc7\u6765\uff0c\u53cc\u81c2\u62b1\u5f97\u7d27\u7d27\u7684\u3002\u6885\u83f2\u65af\u82e5\u8981\u7528\u529b\u6323\u8131\uff0c\u6015\u53c8\u4f24\u4e86\u4ed6\uff0c\u4e0d\u7531\u5f97\u4e00\u65f6\u6709\u4e9b\u8fdb\u9000\u4e24\u96be\uff0c\u7565\u4e00\u72b9\u8c6b\uff0c\u5df2\u7ecf\u88ab\u62b1\u7d27\u5728\u6000\u91cc\u3002</p><p>\u6b64\u65f6\u5df2\u7ecf\u662f\u6625\u590f\u4e4b\u4ea4\uff0c\u6c14\u6e29\u8f83\u9ad8\uff0c\u6885\u83f2\u65af\u7761\u89c9\u53c8\u7a7f\u7740\u886c\u886b\u957f\u88e4\uff0c\u539f\u672c\u5c31\u6709\u4e9b\u70ed\uff0c\u88ab\u4ed6\u8fd9\u4e00\u62b1\uff0c\u5012\u662f\u4e00\u80a1\u51b0\u51c9\u6e05\u723d\u7684\u611f\u89c9\u4f20\u6765\u3002\u51b7\u70ed\u76f8\u6d4e\uff0c\u743c\u6069\u7684\u5bd2\u610f\u4f3c\u4e4e\u4e5f\u5fae\u5fae\u7f13\u548c\uff0c\u8138\u4e0a\u9752\u8272\u90fd\u6709\u4e9b\u6d88\u9000\u3002</p><p>\u5c11\u5973\u4fbf\u4e0d\u6323\u624e\uff0c\u4efb\u4ed6\u62b1\u7740\u3002\u53ea\u662f\u743c\u6069\u867d\u7136\u4f3c\u4e4e\u8ff7\u8ff7\u7cca\u7cca\uff0c\u624b\u5374\u4eff\u4f5b\u4e60\u60ef\u6027\u5730\u6478\u5230\u80f8\u524d\uff0c\u5c31\u53bb\u89e3\u5979\u886c\u886b\u7684\u6263\u5b50\u3002\u5c11\u5973\u5c06\u624b\u62e8\u5f00\uff0c\u4e0d\u7247\u523b\u53c8\u79fb\u4e86\u56de\u6765\uff0c\u5012\u662f\u9532\u800c\u4e0d\u820d\u4e00\u822c\uff0c\u5979\u65e2\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u8f6c\u773c\u89c1\u4ed6\u5df2\u7ecf\u51bb\u5f97\u5168\u8eab\u5fae\u98a4\uff0c\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u4e5f\u5c31\u4e0d\u518d\u62b5\u6297\uff0c\u4efb\u4ed6\u6478\u7d22\u7740\u628a\u6263\u5b50\u5168\u90fd\u89e3\u5f00\uff0c\u5c06\u886c\u886b\u8131\u4e86\u4e0b\u6765\u3002</p><p>\u743c\u6069\u5374\u5f97\u5bf8\u8fdb\u5c3a\uff0c\u4f38\u624b\u5f80\u4e0b\u53c8\u53bb\u892a\u5979\u7684\u957f\u88e4\uff0c\u53ea\u662f\u6885\u83f2\u65af\u8eab\u9ad8\u4e0e\u4ed6\u51e0\u4e4e\u76f8\u5f53\uff0c\u4e24\u4eba\u5e76\u6795\u800c\u7720\uff0c\u53ea\u8981\u5979\u4e0d\u4e3b\u52a8\u914d\u5408\uff0c\u9664\u975e\u743c\u6069\u94bb\u4e0b\u53bb\uff0c\u5426\u5219\u8981\u892a\u4e0b\u957f\u88e4\u8fd8\u771f\u4e0d\u5bb9\u6613\u3002\u5c11\u5973\u5fae\u5fae\u76b1\u7709\uff0c\u7d22\u6027\u81ea\u5df1\u5c06\u957f\u88e4\u892a\u4e86\u4e0b\u6765\uff0c\u5168\u8eab\u8d64\u88f8\uff0c\u4efb\u4ed6\u62b1\u7740\uff0c\u597d\u5728\u743c\u6069\u4e5f\u6ca1\u8fdb\u4e00\u6b65\u52a8\u4f5c\u3002</p><p>\u4e24\u4eba\u808c\u80a4\u76f8\u4eb2\uff0c\u5c11\u4e86\u8863\u670d\u963b\u9694\uff0c\u90a3\u80a1\u5bd2\u6c14\u8d8a\u53d1\u6e05\u695a\u5730\u6e17\u900f\u8fc7\u6765\uff0c\u5c11\u5973\u4e5f\u4e0d\u7981\u5fae\u5fae\u6253\u98a4\uff0c\u611f\u89c9\u81ea\u5df1\u4eff\u4f5b\u6b63\u8d34\u7740\u4e00\u4e2a\u5927\u51b0\u5757\u3002\u5e38\u4eba\u82e5\u662f\u8fd9\u79cd\u4f53\u6e29\uff0c\u6309\u9053\u7406\u65e9\u8be5\u5df2\u7ecf\u51bb\u6b7b\u4e86\u624d\u5bf9\uff0c\u4f46\u743c\u6069\u660e\u660e\u8fd8\u6709\u547c\u5438\u5fc3\u8df3\uff0c\u5b9e\u5728\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002</p><p>\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6885\u83f2\u65af\u81ea\u5df1\u90fd\u89c9\u5f97\u5feb\u8981\u88ab\u51bb\u50f5\u7684\u65f6\u5019\uff0c\u611f\u89c9\u743c\u6069\u7684\u4f53\u6e29\u4f3c\u4e4e\u53c8\u6062\u590d\u8fc7\u6765\uff0c\u6e10\u6e10\u53d1\u70ed\u3002\u5c11\u5973\u51bb\u4e86\u8fd9\u4e48\u4e45\uff0c\u5168\u8eab\u611f\u89c9\u90fd\u6709\u4e9b\u9ebb\u75f9\uff0c\u611f\u53d7\u5230\u7537\u4eba\u6000\u4e2d\u7684\u6e29\u6696\uff0c\u672c\u80fd\u5730\u5f80\u91cc\u8d34\u7d27\uff0c\u8737\u7f29\u7740\u3002</p><p>\u7ec8\u4e8e\uff0c\u4e24\u4eba\u7684\u8eab\u4f53\u90fd\u6e10\u6e10\u6e29\u6696\u8d77\u6765\uff0c\u53ea\u662f\u4f9d\u65e7\u7d27\u7d27\u6402\u62b1\u7740\u3002\u8fd9\u4e24\u5929\u9887\u591a\u610f\u5916\uff0c\u6298\u817e\u4e0d\u4f11\uff0c\u4eca\u665a\u53c8\u4e00\u76f4\u5ffd\u51b7\u5ffd\u70ed\u5730\u5f04\u5230\u73b0\u5728\uff0c\u5c11\u5973\u7740\u5b9e\u662f\u56f0\u5026\u4e86\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u4fbf\u5728\u6e29\u6696\u6000\u62b1\u91cc\u6c89\u6c89\u7761\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u5f53\u743c\u6069\u7741\u5f00\u773c\u775b\u7684\u65f6\u5019\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u7cbe\u795e\u5955\u5955\uff0c\u5145\u6ee1\u6d3b\u529b\u3002</p><p>\u8fd9\u4e0d\u4ec5\u4ec5\u662f\u56e0\u4e3a\u7761\u7720\u7684\u7f18\u6545\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u81ea\u4ece\u5185\u529f\u57fa\u7840\u6253\u7262\uff0c\u4ed6\u5c31\u5f88\u5c11\u9700\u8981\u771f\u6b63\u7684\u7761\u7720\u4e86\u3002\u771f\u6b63\u7684\u539f\u56e0\uff0c\u662f\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u57fa\u672c\u5c06\u4ece\u5973\u6740\u624b\u4f53\u5185\u5438\u6765\u7684\u90a3\u80a1\u5bd2\u6c14\u7ed9\u5316\u89e3\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u53ea\u662f\u201c\u57fa\u672c\u201d\uff0c\u8fd8\u672a\u5b8c\u5168\u5316\u89e3\uff0c\u4f46\u5e94\u8be5\u4e0d\u4f1a\u518d\u51fa\u4ec0\u4e48\u610f\u5916\u4e86\u3002</p><p>\u4ed6\u665a\u4e0a\u5165\u7761\u524d\uff0c\u539f\u672c\u5728\u601d\u8651\u62c9\u6c83\u514b\u7684\u4e8b\u60c5\uff0c\u5343\u5934\u4e07\u7eea\uff0c\u7eb7\u81f3\u6c93\u6765\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5fc3\u70e6\u610f\u4e71\u3002\u5bf9\u4e8e\u4fee\u4e60\u5185\u529f\u7684\u4eba\u800c\u8a00\uff0c\u8fd9\u6b63\u662f\u5927\u5fcc\uff0c\u6070\u6070\u6b64\u65f6\uff0c\u504f\u751f\u80f8\u53e3\u90a3\u80a1\u9634\u5bd2\u4e4b\u6c14\u53c8\u7a81\u7136\u53d1\u4f5c\uff0c\u4e00\u65f6\u95f4\u51e0\u4e4e\u538b\u5236\u4e0d\u4f4f\uff0c\u5468\u8eab\u8840\u8109\u51b0\u51b7\u6d78\u900f\uff0c\u5e78\u4e8f\u6885\u83f2\u65af\u7528\u8eab\u4f53\u6e29\u6696\uff0c\u5018\u82e5\u518d\u7f13\u4e0a\u534a\u523b\uff0c\u53ea\u6015\u5c31\u8981\u51bb\u6210\u5c38\u4f53\u4e86\u3002</p><p>\u603b\u7b97\u501f\u7740\u5c11\u5973\u4f53\u6e29\uff0c\u6e10\u6e10\u7f13\u8fc7\u6c14\u6765\uff0c\u51dd\u805a\u5fc3\u795e\uff0c\u8fd0\u529f\u8c03\u606f\uff0c\u4e0d\u77e5\u82b1\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u7ec8\u4e8e\u628a\u90a3\u80a1\u9634\u5bd2\u4e4b\u6c14\u5927\u4f53\u7ed9\u6d88\u89e3\u4e86\uff0c\u5982\u4eca\u5df2\u7ecf\u968f\u7740\u5185\u606f\u6563\u5e03\u56db\u80a2\u767e\u9ab8\uff0c\u5faa\u73af\u4e0d\u606f\u3002</p><p>\u66f4\u4ee4\u4ed6\u60ca\u559c\u7684\u662f\uff0c\u81ea\u5df1\u6b64\u524d\u7684\u731c\u6d4b\u53ef\u80fd\u6ca1\u9519\uff0c\u8fd9\u4e2a\u9634\u5bd2\u4e4b\u6c14\uff0c\u771f\u7684\u662f\u67d0\u79cd\u5185\u529f\u3002\u56e0\u4e3a\u5c06\u5b83\u6d88\u89e3\u878d\u5316\u540e\uff0c\u743c\u6069\u611f\u89c9\u81ea\u5df1\u539f\u672c\u5fae\u5f31\u5f97\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u7684\u5185\u606f\u660e\u663e\u589e\u5f3a\u8d77\u6765\uff0c\u8c03\u606f\u8fd0\u8f6c\u7684\u65f6\u5019\uff0c\u4ee5\u524d\u5f88\u591a\u8270\u96be\u6ede\u788d\u7684\u5730\u65b9\uff0c\u4e5f\u90fd\u987a\u7545\u65e0\u6bd4\u5730\u51b2\u8fc7\u3002\u8fd9\u610f\u5473\u7740\uff0c\u5728\u82e6\u7ec3\u516b\u5e74\u5374\u4e00\u76f4\u505c\u6ede\u4e0d\u524d\u540e\uff0c\u81ea\u5df1\u7ec8\u4e8e\u8fce\u6765\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7a81\u7834\u3002</p><p>\u6ca1\u60f3\u5230\u6765\u4e00\u8d9f\u70db\u5821\uff0c\u83ab\u540d\u5176\u5999\u5730\u649e\u4e0a\u4e2a\u5973\u6740\u624b\uff0c\u4e00\u756a\u60ca\u9669\u4e4b\u540e\uff0c\u5c45\u7136\u8fd8\u6709\u8fd9\u7b49\u610f\u5916\u597d\u5904\u3002</p><p>\u7741\u5f00\u773c\u775b\uff0c\u53d1\u73b0\u5df2\u7ecf\u662f\u6b21\u65e5\u65e9\u6668\uff0c\u539a\u91cd\u7684\u7a97\u5e18\u867d\u7136\u62c9\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u5374\u4e5f\u5fae\u5fae\u900f\u51fa\u4e9b\u5149\u4eae\u8fdb\u6765\uff0c\u592a\u9633\u663e\u7136\u5df2\u7ecf\u9ad8\u9ad8\u5347\u8d77\u4e86\u3002\u743c\u6069\u4f4e\u5934\u770b\u7740\u81ea\u5df1\u6000\u4e2d\u7684\u6885\u83f2\u65af\uff0c\u5c11\u5973\u4f9d\u65e7\u7761\u5f97\u8ff7\u8ff7\u7cca\u7cca\u7684\uff0c\u5c06\u8eab\u4f53\u53c8\u5f80\u4ed6\u6000\u91cc\u6324\u4e86\u6324\u3002\u5929\u6c14\u6709\u4e9b\u70ed\uff0c\u88ab\u5b50\u88ab\u5979\u65e0\u610f\u95f4\u8e39\u5f00\u4e86\uff0c\u9732\u51fa\u73b2\u73d1\u6d6e\u51f8\u7684\u7389\u4f53\uff0c\u9999\u8273\u65e0\u6bd4\u3002</p><p>\u6628\u665a\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u867d\u7136\u5fd9\u4e8e\u8fd0\u529f\u8c03\u606f\uff0c\u4f46\u4e5f\u90fd\u8fd8\u8bb0\u5f97\uff0c\u77e5\u9053\u81ea\u5df1\u6700\u5371\u9669\u7684\u65f6\u5019\uff0c\u591a\u4e8f\u6885\u83f2\u65af\u6551\u4e86\u4e00\u547d\u3002\u4ed6\u770b\u7740\u6c89\u7761\u7684\u5c11\u5973\uff0c\u89c1\u5979\u9762\u5bb9\u5b89\u9759\uff0c\u60f3\u5fc5\u6b63\u7761\u5f97\u9999\u751c\uff0c\u4fcf\u8138\u4e0a\u900f\u7740\u5fae\u5fae\u7684\u7ea2\u6655\uff0c\u5fcd\u4e0d\u4f4f\u4eb2\u8fd1\u4e4b\u610f\u5927\u8d77\uff0c\u4f4e\u5934\u543b\u4e86\u4e00\u53e3\u3002</p><p>\u5c11\u5973\u5927\u7ea6\u786e\u5b9e\u5026\u4e86\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u5514\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u662f\u5728\u62d2\u7edd\uff0c\u743c\u6069\u5374\u4e0d\u5c31\u6b64\u505c\u624b\uff0c\u4f38\u624b\u8f7b\u63a8\u5979\u7684\u9999\u80a9\uff0c\u8ba9\u5c11\u5973\u5e73\u8eba\u4e0b\u6765\uff0c\u4fef\u8eab\u53bb\u543b\u5979\u7684\u6a31\u5507\u3002</p><p>\u4e00\u543b\u4e4b\u4e0b\uff0c\u5c11\u5973\u6709\u4e9b\u6e05\u9192\u8fc7\u6765\uff0c\u672c\u80fd\u5730\u8981\u63a8\u5f00\u8eab\u4e0a\u7684\u7537\u4eba\uff0c\u5374\u88ab\u743c\u6069\u6309\u4f4f\u53cc\u81c2\uff0c\u7ee7\u7eed\u543b\u5979\u3002\u5c11\u5973\u7565\u7565\u6297\u62d2\uff0c\u4e5f\u5c31\u4e0d\u518d\u6323\u624e\uff0c\u53cd\u800c\u73af\u81c2\u62b1\u7740\u4ed6\u7684\u8116\u9888\uff0c\u6a80\u53e3\u5fae\u5f20\uff0c\u4e3b\u52a8\u8fce\u5408\uff0c\u4e24\u4eba\u5507\u820c\u7ede\u7f20\uff0c\u5f7c\u6b64\u542e\u5438\u7740\u5bf9\u65b9\u53e3\u4e2d\u7684\u9999\u6ed1\u6d25\u6db2\u3002\u743c\u6069\u5659\u7740\u5c11\u5973\u7684\u4e01\u9999\u820c\u5c16\uff0c\u6162\u6162\u54c1\u5c1d\uff0c\u4e0d\u89c9\u6709\u4e9b\u60c5\u52a8\uff0c\u4ed6\u672c\u5c31\u5df2\u7ecf\u618b\u4e86\u591a\u65e5\u672a\u66fe\u53d1\u6cc4\uff0c\u524d\u591c\u88ab\u5973\u6740\u624b\u6311\u8d77\u6b32\u706b\uff0c\u7ed3\u679c\u53c8\u786c\u751f\u751f\u5413\u4e86\u56de\u53bb\u3002\u6b64\u65f6\u6e05\u6668\uff0c\u6b63\u662f\u60c5\u6b32\u6700\u76db\u7684\u65f6\u5019\uff0c\u548c\u6000\u4e2d\u7389\u4eba\u8fd9\u756a\u70ed\u543b\u7f20\u7ef5\uff0c\u4e0d\u7531\u5f97\u6b32\u706b\u96be\u8010\uff0c\u518d\u4e5f\u538b\u6291\u4e0d\u4f4f\u3002\u4e00\u7ffb\u8eab\uff0c\u538b\u5728\u5c11\u5973\u8eab\u4e0a\uff0c\u5206\u5f00\u5979\u7d27\u5e76\u53cc\u817f\uff0c\u60f3\u8981\u7834\u5173\u800c\u5165\u3002</p><p>\u867d\u7136\u6b32\u706b\u711a\u8eab\uff0c\u4ed6\u5012\u4e5f\u6ca1\u6709\u5931\u53bb\u7406\u667a\uff0c\u8fd8\u8bb0\u5f97\u6885\u83f2\u65af\u7684\u8eab\u4f53\u53e6\u6709\u7384\u673a\uff0c\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u5c31\u80fd\u8fdb\u5165\u7684\u3002\u5f85\u8981\u4e0b\u53bb\u4ed4\u7ec6\u5bdf\u770b\u4e00\u4e0b\uff0c\u5c11\u5973\u5374\u6e05\u9192\u8fc7\u6765\uff0c\u8fde\u5fd9\u4f38\u624b\u63a9\u4f4f\u4e0b\u4f53\uff0c\u201c\u4e0d\u884c\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u4eec\u8bf4\u597d\u7684\uff0c\u90a3\u91cc\u4e0d\u884c\u3002\u201d</p><p>\u201c\u6211\u53ea\u770b\u770b\u3002\u201d\u743c\u6069\u54c4\u5979\u3002</p><p>\u201c\u4e0d\u884c\u5566\u3002\u201d</p><p>\u5979\u521a\u521a\u7761\u9192\uff0c\u58f0\u97f3\u91cc\u8fd8\u5e26\u4e86\u4e9b\u6175\u61d2\u4e4b\u610f\uff0c\u8bf4\u4e0d\u51fa\u7684\u8bf1\u4eba\u52a8\u542c\uff0c\u4f46\u8bdd\u4e2d\u7684\u610f\u601d\u5374\u662f\u575a\u51b3\u5f97\u5f88\u3002\u743c\u6069\u4e0d\u60f3\u60f9\u5979\u751f\u6c14\uff0c\u53ea\u5f97\u653e\u5f03\u5ff5\u5934\uff0c\u73af\u81c2\u5c06\u5979\u6402\u5230\u6000\u4e2d\uff0c\u201c\u597d\uff0c\u4e0d\u770b\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u543b\u7740\u5c11\u5973\u7684\u989d\u5934\uff0c\u201c\u518d\u7761\u4f1a\u5427\uff0c\u6628\u665a\u7d2f\u4e86\u3002\u201d</p><p>\u5c11\u5973\u5fae\u5fae\u6447\u5934\uff0c\u793a\u610f\u4e0d\u7528\uff0c\u6b64\u65f6\u6668\u5149\u5927\u4eae\uff0c\u4e5f\u662f\u8be5\u8d77\u5e8a\u7684\u65f6\u5019\u4e86\u3002\u5979\u6b32\u5f85\u8d77\u8eab\uff0c\u743c\u6069\u5374\u4e0d\u80af\u8fd9\u6e29\u67d4\u4e61\u65e9\u65e9\u7ed3\u675f\uff0c\u4ed6\u8fd9\u6bb5\u65f6\u95f4\u6765\u72ec\u5b88\u7a7a\u623f\uff0c\u4e00\u76f4\u6ca1\u6709\u5408\u9002\u7684\u673a\u4f1a\u62b1\u7740\u5c11\u5973\u7f20\u7ef5\uff0c\u4f55\u51b5\u8fd9\u6b21\u8fd8\u662f\u8d64\u88f8\u7389\u4f53\u7684\uff0c\u81ea\u7136\u66f4\u4e0d\u80fd\u653e\u8fc7\uff0c\u4ee5\u524d\u6885\u83f2\u65af\u5c31\u7b97\u80af\u8ba9\u4ed6\u62b1\u7740\uff0c\u4e5f\u662f\u575a\u6301\u8981\u7a7f\u7740\u886c\u886b\u957f\u88e4\u7684\u3002\u743c\u6069\u4f38\u624b\u4e00\u62c9\uff0c\u53c8\u5c06\u5979\u62c9\u5230\u6000\u4e2d\uff0c\u53cc\u81c2\u73af\u62b1\u7740\uff0c\u4e0d\u80af\u653e\u677e\u3002</p><p>\u201c\u518d\u966a\u6211\u4e00\u4f1a\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u5c11\u5973\u5e76\u4e0d\u8fdd\u62d7\uff0c\u8f7b\u8f7b\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u4fbf\u4e56\u4e56\u5730\u8737\u7f29\u5728\u4ed6\u6000\u4e2d\uff0c\u4efb\u4ed6\u7684\u624b\u629a\u6469\u7740\u81ea\u5df1\u5149\u6d01\u67d4\u5ae9\u7684\u810a\u80cc\uff0c\u50cf\u4e00\u53ea\u6e29\u987a\u7684\u5c0f\u732b\u3002\u5979\u80fd\u611f\u89c9\u5230\u7537\u4eba\u547c\u5438\u7684\u7c97\u91cd\uff0c\u5c0f\u8179\u4e0a\u4e5f\u4f20\u6765\u5f02\u6837\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u6709\u4ec0\u4e48\u575a\u786c\u6eda\u70eb\u7684\u4e1c\u897f\u5728\u9876\u7740\u3002\u6885\u83f2\u65af\u8fd8\u662f\u5904\u5973\uff0c\u4f46\u5e76\u975e\u4e0d\u61c2\u4e8b\uff0c\u5979\u77e5\u9053\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u56e0\u4e3a\u81ea\u5df1\u66fe\u7ecf\u591a\u6b21\u5c06\u5b83\u542b\u5728\u53e3\u4e2d\uff0c\u8214\u8210\u7740\uff0c\u8ba9\u5b83\u81a8\u80c0\u575a\u633a\uff0c\u76f4\u81f3\u53d1\u6cc4\u3002\u60f3\u8d77\u8fd9\u70b9\uff0c\u4e0d\u7531\u5f97\u8138\u4e0a\u6709\u4e9b\u53d1\u70e7\u8d77\u6765\u3002</p><p>\u5979\u611f\u89c9\u7537\u4eba\u7684\u624b\u4ece\u81ea\u5df1\u7684\u810a\u80cc\u4e0a\u7f13\u7f13\u5f80\u4e0b\uff0c\u5728\u8170\u95f4\u4f18\u7f8e\u7684\u51f9\u9677\u5904\u7a0d\u7a0d\u505c\u7559\uff0c\u7136\u540e\u79fb\u4e0a\u4e86\u81c0\u90e8\u3002\u201c\u771f\u4e0d\u9519\uff0c\u201d\u5979\u542c\u89c1\u7537\u4eba\u6ee1\u610f\u5730\u8bc4\u4ef7\u7740\uff0c\u201c\u5f88\u7fd8\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u77e5\u9053\u81ea\u5df1\u62e5\u6709\u5b8c\u7f8e\u633a\u7fd8\u7684\u81c0\u90e8\uff0c\u5979\u4e5f\u4e00\u5411\u4ee5\u6b64\u81ea\u50b2\uff0c\u53ea\u662f\u4ee5\u5979\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u8fde\u4e2a\u95fa\u4e2d\u5bc6\u53cb\u90fd\u6ca1\u6709\uff0c\u81ea\u7136\u4e5f\u6ca1\u6709\u4eba\u6b23\u8d4f\u5938\u5956\uff0c\u672a\u514d\u603b\u662f\u7565\u6709\u9057\u61be\u3002\u5982\u4eca\u542c\u7537\u4eba\u8fd9\u4e48\u8bf4\uff0c\u867d\u7136\u7f9e\u6da9\uff0c\u5374\u4e5f\u6709\u4e9b\u751c\u4e1d\u4e1d\u7684\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u7684\u8bed\u6c14\u4e2d\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u6000\u597d\u610f\uff0c\u201c\u4f60\u770b\uff0c\u5b83\u73b0\u5728\u786c\u633a\u7740\uff0c\u5f88\u96be\u53d7\u5462\u3002\u201d</p><p>\u4ed6\u6545\u610f\u633a\u4e86\u633a\u8170\uff0c\u8ba9\u5c11\u5973\u611f\u89c9\u5230\u90a3\u5bb6\u4f19\u7684\u575a\u786c\u6eda\u70eb\u3002\u6885\u83f2\u65af\u771f\u8981\u8bf4\u8bdd\uff0c\u542c\u89c1\u743c\u6069\u53c8\u8bf4\uff1a\u201c\u65e2\u7136\u90a3\u5730\u65b9\u4e0d\u884c\u2026\u2026\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u7684\u624b\u6307\u6273\u5f00\u96ea\u767d\u7684\u81c0\u74e3\uff0c\u98df\u6307\u6307\u5c16\u8f7b\u8f7b\u5730\u6309\u7740\u90a3\u7cbe\u5de7\u7684\u83ca\u7a74\u6d1e\u53e3\uff0c\u201c\u8ba9\u6211\u7528\u8fd9\u91cc\u600e\u4e48\u6837\uff1f\u201d</p><p>\u5c11\u5973\u7684\u8eab\u4f53\u9661\u7136\u4e00\u50f5\uff0c\u5979\u4e0d\u6562\u7f6e\u4fe1\u5730\u62ac\u8138\u770b\u7740\u743c\u6069\uff0c\u201c\u90a3\u91cc\uff1f\u201d\u5979\u5931\u58f0\u95ee\uff0c\u7eb5\u7136\u662f\u8fd9\u6837\u575a\u6bc5\u521a\u5f3a\u7684\u5973\u5b69\uff0c\u542c\u5230\u743c\u6069\u8fd9\u4e2a\u8981\u6c42\u8fd8\u662f\u7981\u4e0d\u4f4f\u773c\u4e2d\u95ea\u8fc7\u6050\u60e7\u4e4b\u8272\uff0c\u201c\u90a3\u91cc\u2026\u2026\u90a3\u91cc\u600e\u4e48\u884c\u7684\u2026\u2026\u201d</p><p>\u201c\u53ef\u4ee5\u7684\uff0c\u201d\u743c\u6069\u54c4\u7740\u5979\uff0c\u201c\u5f88\u591a\u60c5\u4eba\u90fd\u4f1a\u8fd9\u6837\u73a9\u7684\u3002\u201d</p><p>\u5c11\u5973\u8fde\u8fde\u6447\u5934\uff0c\u770b\u8d77\u6765\u65e2\u5bb3\u6015\u53c8\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\u3002\u743c\u6069\u4e0d\u60f3\u5413\u574f\u4e86\u5979\uff0c\u8fde\u5fd9\u4f38\u81c2\u62b1\u4f4f\uff0c\u624b\u8f7b\u629a\u7740\u80cc\uff0c\u5b89\u6170\u7740\u5979\uff0c\u201c\u5f00\u73a9\u7b11\u5566\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u9017\u9017\u4f60\u800c\u5df2\uff0c\u73a9\u7b11\u5566\u3002\u201d</p><p>\u5c11\u5973\u6c89\u9ed8\u7740\uff0c\u5c06\u8138\u8d34\u5728\u4ed6\u80f8\u53e3\u3002\u5979\u5f88\u806a\u660e\uff0c\u5f53\u7136\u80fd\u770b\u5f97\u51fa\u6765\u743c\u6069\u521a\u624d\u5e76\u4e0d\u662f\u5728\u5f00\u73a9\u7b11\uff0c\u4ed6\u662f\u8ba4\u771f\u7684\uff0c\u53ea\u662f\u56e0\u4e3a\u81ea\u5df1\u53cd\u5e94\u592a\u5f3a\u70c8\uff0c\u6240\u4ee5\u6539\u53e3\u7f62\u4e86\u3002\u65e2\u7136\u743c\u6069\u8bf4\u662f\u73a9\u7b11\uff0c\u5979\u81ea\u7136\u4e5f\u4e0d\u4f1a\u518d\u63d0\uff0c\u5f7c\u6b64\u5fc3\u7167\u4e0d\u5ba3\u3002\u53ea\u662f\u2026\u2026\u90a3\u5730\u65b9\u4e5f\u80fd\u7528\u4e48\u2026\u2026</p><p>\u5979\u4e0b\u610f\u8bc6\u5730\u4f38\u624b\u53bb\u6478\uff0c\u7ea4\u7ec6\u4fee\u957f\u7684\u624b\u6307\u5206\u5f00\u81c0\u74e3\uff0c\u6478\u5230\u90a3\u7cbe\u81f4\u5c0f\u5de7\u7684\u83ca\u7a74\u3002\u201c\u8fd9\u4e48\u5c0f\u7684\u5730\u65b9\uff0c\u600e\u4e48\u53ef\u80fd\u88ab\u90a3\u4e48\u5927\u4e1c\u897f\u2026\u2026\u201d\u60f3\u50cf\u743c\u6069\u90a3\u72f0\u72de\u5de8\u5927\u7684\u5bb6\u4f19\u8981\u6324\u5165\u81ea\u5df1\u8fd9\u6837\u7cbe\u5de7\u7684\u540e\u5ead\uff0c\u5c11\u5973\u5c31\u6709\u4e9b\u4e0d\u5bd2\u800c\u6817\uff0c\u611f\u89c9\u81ea\u5df1\u7684\u8eab\u4f53\u4eff\u4f5b\u8981\u88ab\u6495\u88c2\u5f00\u6765\u4e00\u822c\uff0c\u201c\u4f1a\u6b7b\u7684\u5427\u3002\u201d\u5979\u4f4e\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u89c1\u5979\u5bb3\u6015\uff0c\u4e5f\u4e0d\u5f3a\u903c\uff0c\u53ea\u662f\u6e29\u8bed\u5b89\u6170\uff0c\u4e00\u8fb9\u4e58\u673a\u5c06\u624b\u4f38\u5230\u5979\u80f8\u524d\u5360\u4fbf\u5b9c\u3002\u53ea\u662f\u6000\u62b1\u7740\u8fd9\u6837\u7684\u7f8e\u4eba\u5374\u4e0d\u80fd\u5403\uff0c\u6b32\u706b\u90c1\u79ef\uff0c\u5fc3\u60c5\u81ea\u7136\u800c\u7136\u6709\u4e9b\u70e6\u8e81\uff0c\u624b\u4e0a\u4e0d\u77e5\u4e0d\u89c9\u95f4\u7528\u529b\uff0c\u5374\u628a\u5c11\u5973\u5f04\u5f97\u75bc\u4e86\u3002\u90a3\u5730\u65b9\u53c8\u5a07\u5ae9\u65e0\u6bd4\uff0c\u518d\u575a\u5f3a\u7684\u5973\u5b50\u4e5f\u5fcd\u75db\u4e0d\u4f4f\uff0c\u5f85\u4ed6\u610f\u8bc6\u5230\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u773c\u4e2d\u5df2\u7ecf\u9690\u9690\u6709\u4e9b\u6cea\u5149\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e0b\u6127\u759a\uff0c\u6b63\u8981\u9053\u6b49\uff0c\u5374\u89c1\u5c11\u5973\u6402\u7740\u4ed6\u7684\u8170\uff0c\u8eab\u4f53\u7f13\u7f13\u5f80\u4e0b\u94bb\u5230\u88ab\u5b50\u91cc\u53bb\u4e86\u3002</p><p>\u4ed6\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u5c31\u611f\u89c9\u90a3\u4e00\u76f4\u786c\u80c0\u7740\u6709\u4e9b\u96be\u53d7\u7684\u5de8\u7269\u88ab\u5c0f\u5634\u6162\u6162\u542b\u5165\u3002\u743c\u6069\u6ee1\u8db3\u5730\u5514\u4e86\u4e00\u58f0\uff0c\u4eab\u53d7\u7740\u5c11\u5973\u6e29\u6696\u6e7f\u6da6\u7684\u53e3\u8154\uff0c\u4f38\u624b\u629a\u6478\u7740\u5979\u7684\u6de1\u91d1\u8272\u79c0\u53d1\u3002\u8fd9\u4e9b\u5929\u6765\u7684\u8c03\u6559\uff0c\u5df2\u7ecf\u8ba9\u6885\u83f2\u65af\u7684\u5507\u820c\u6280\u5de7\u63d0\u5347\u5f97\u5f88\u5feb\uff0c\u5979\u7684\u820c\u5c16\u7075\u6d3b\u5730\u8214\u8210\u7740\uff0c\u4e0a\u4e0b\u6e38\u8d70\uff0c\u65f6\u800c\u7528\u529b\u542e\u5438\uff0c\u6700\u540e\u8ba9\u5b83\u6df1\u5165\u81ea\u5df1\u7684\u54bd\u5589\u91cc\u3002\u901a\u8fc7\u743c\u6069\u629a\u6478\u5979\u5934\u53d1\u7684\u8f7b\u91cd\u529b\u9053\uff0c\u5979\u5c31\u80fd\u51c6\u786e\u5730\u5224\u65ad\u51fa\u7537\u4eba\u6b64\u523b\u7684\u53cd\u5e94\uff0c\u4ee5\u53ca\u8fdb\u4e00\u6b65\u671f\u671b\u3002</p><p>\u8fd9\u4e9b\u6280\u5de7\u88ab\u5979\u8fd0\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u6ca1\u8fc7\u591a\u4e45\uff0c\u5979\u5c31\u6210\u529f\u5730\u8ba9\u743c\u6069\u5728\u5979\u53e3\u4e2d\u7206\u53d1\u4e86\u3002\u79ef\u84c4\u4e86\u5341\u4f59\u5929\u7684\u6b32\u671b\u4e00\u80a1\u8111\u513f\u55b7\u4e86\u51fa\u6765\uff0c\u5927\u534a\u76f4\u63a5\u5c04\u8fdb\u4e86\u80c3\u90e8\uff0c\u4f46\u8fd8\u6709\u5c0f\u534a\u53d1\u6cc4\u5728\u53e3\u4e2d\u3002\u5979\u52aa\u529b\u5730\u541e\u54bd\u7740\uff0c\u76f4\u5230\u5c06\u6240\u6709\u7684\u6db2\u4f53\u90fd\u541e\u4e0b\uff0c\u7136\u540e\u6e29\u67d4\u5730\u505a\u5b8c\u4e8b\u540e\u7684\u6e05\u7406\u5de5\u4f5c\uff0c\u6162\u6162\u4ece\u88ab\u5b50\u91cc\u94bb\u4e86\u4e0a\u6765\u3002</p><p>\u201c\u8212\u670d\u70b9\u4e86\u5427\u3002\u201d\u5c11\u5973\u8f7b\u58f0\u95ee\u3002</p><p>\u743c\u6069\u62b1\u7740\u5979\uff0c\u70b9\u70b9\u5934\uff0c\u201c\u8c22\u8c22\uff0c\u827e\u5f25\u8587\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u5c11\u5973\u5fae\u7b11\uff0c\u201c\u8fd9\u662f\u6211\u5e94\u8be5\u505a\u7684\u5427\uff0c\u6211\u662f\u4f60\u7684\u2026\u2026\u201d</p><p>\u8bdd\u8bf4\u5230\u8fd9\u91cc\uff0c\u9661\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u4e24\u4eba\u4e0d\u7531\u5f97\u90fd\u6709\u4e9b\u5c34\u5c2c\uff0c\u53c8\u6709\u4e9b\u832b\u7136\u3002\u6211\u662f\u4f60\u7684\u4ec0\u4e48\uff1f\u60c5\u4eba\uff1f\u5e8a\u4f34\uff1f\u968f\u4ece\uff1f\u8fd8\u662f\u5973\u5974\uff1f\u53ea\u6015\u662f\u4ed6\u4eec\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u5427\u3002</p><p>\u743c\u6069\u5b9a\u5b9a\u5730\u770b\u7740\u5c11\u5973\uff0c\u770b\u7740\u5979\u90a3\u56e0\u4e3a\u521a\u624d\u7684\u5267\u70c8\u8fd0\u52a8\u6709\u4e9b\u901a\u7ea2\u7684\u4fcf\u8138\uff0c\u5979\u7684\u773c\u795e\u91cc\u4f3c\u4e4e\u6709\u79cd\u590d\u6742\u7684\u795e\u8272\uff0c\u743c\u6069\u770b\u4e0d\u61c2\u3002\u201c\u5bf9\u554a\uff0c\u201d\u4ed6\u6700\u540e\u7b11\u7740\uff0c\u201c\u4f60\u662f\u6211\u7684\u60c5\u4eba\u561b\u3002\u201d</p><p>\u5c11\u5973\u4e5f\u5fae\u5fae\u7b11\u8d77\u6765\uff0c\u5e76\u4e0d\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u62b1\u7740\u5979\u67d4\u8f6f\u7684\u8eab\u4f53\uff0c\u542c\u7740\u5747\u5300\u5e73\u7f13\u7684\u547c\u5438\uff0c\u4e0d\u77e5\u4e3a\u4f55\uff0c\u7a81\u7136\u6709\u79cd\u7279\u522b\u5b89\u5fc3\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u53ea\u60f3\u7740\u8fd9\u4e00\u523b\u6c38\u8fdc\u505c\u987f\u4e0b\u53bb\u3002\u201c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f60\u6ca1\u53d1\u89c9\u5417\uff0c\u6211\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u597d\u50cf\u6709\u70b9\u6539\u53d8\u5462\u3002\u201d</p><p>\u201c\u6709\u4e48\u3002\u201d\u5c11\u5973\u968f\u53e3\u7b54\u5e94\u7740\u3002</p><p>\u201c\u6709\u554a\uff0c\u4f60\u4e0d\u50cf\u4ee5\u524d\u90a3\u6837\u8ba8\u538c\u6211\u4e86\u561b\u3002\u201d</p><p>\u5c11\u5973\u8f7b\u7b11\u7740\uff0c\u201c\u6211\u672c\u6765\u4e5f\u5c31\u6ca1\u8ba8\u538c\u8fc7\u4f60\u554a\u3002\u201d</p><p>\u201c\u90a3\u5c31\u662f\u4f60\u6bd4\u4ee5\u524d\u66f4\u559c\u6b22\u6211\u4e00\u4e9b\u4e86\u3002\u201d</p><p>\u5c11\u5973\u7a81\u7136\u6c89\u9ed8\u7740\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u743c\u6069\u6709\u4e9b\u6115\u7136\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u8bf4\u9519\u4e86\u4ec0\u4e48\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5c11\u5973\u8f7b\u8f7b\u5f00\u53e3\u3002</p><p>\u201c\u6211\u60f3\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u662f\u559c\u6b22\u88ab\u4eba\u62b1\u7740\u7684\u611f\u89c9\u5427\u3002\u201d</p></article>"}}}]);
