(window.webpackJsonp=window.webpackJsonp||[]).push([[1331],{1354:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e94\u8282 \u8d56\u8d26\u7684\u6b66\u50e7",content:"<article><h2>\u7b2c\u4e09\u5341\u4e94\u8282 \u8d56\u8d26\u7684\u6b66\u50e7</h2><p>\u743c\u6069\u770b\u89c1\u4e86\u521a\u624d\u90a3\u4e2a\u7070\u8272\u4eba\u5f71\uff0c\u662f\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u7537\u4eba\uff0c\u7a7f\u7740\u4e00\u8eab\u7c97\u7cd9\u7684\u7070\u8272\u5e03\u888d\uff0c\u8d64\u7740\u53cc\u8db3\uff0c\u9664\u6b64\u4e4b\u5916\u522b\u65e0\u5176\u4ed6\u8863\u9970\u3002\u56e0\u4e3a\u662f\u5728\u80cc\u540e\uff0c\u770b\u4e0d\u6e05\u8138\uff0c\u611f\u89c9\u50cf\u662f\u4e2a\u4e2d\u5e74\u4eba\uff0c\u4e00\u5934\u9ed1\u53d1\u6709\u4e9b\u84ec\u4e71\uff0c\u9664\u6b64\u4e4b\u5916\u4f3c\u4e4e\u4e5f\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u5f02\u6837\u7684\u3002</p><p>\u8fd9\u4e2a\u7537\u4eba\u7684\u53cc\u624b\u7a7a\u7a7a\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u6709\u643a\u5e26\u4efb\u4f55\u6b66\u5668\uff0c\u743c\u6069\u89c2\u5bdf\u7740\uff0c\u6709\u4e9b\u5947\u602a\u3002\u663e\u7136\uff0c\u521a\u624d\u5c31\u662f\u8fd9\u4e2a\u7537\u4eba\u5728\u8d70\u5eca\u91cc\u6253\u7ffb\u4e86\u90a3\u51e0\u4e2a\u536b\u5175\u3002\u4ece\u58f0\u97f3\u5224\u65ad\uff0c\u6218\u6597\u8fc7\u7a0b\u975e\u5e38\u77ed\u6682\uff0c\u6700\u591a\u4e0d\u8d85\u8fc7\u4e94\u79d2\u949f\u3002</p><p>\u4e00\u4e2a\u8d64\u624b\u7a7a\u62f3\u7684\u4eba\uff0c\u80fd\u5728\u4e94\u79d2\u949f\u4e4b\u5185\uff0c\u6253\u7ffb\u51e0\u4e2a\u6b66\u88c5\u6574\u9f50\u7684\u536b\u5175\u3002\u5c31\u7b97\u53ef\u80fd\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u4e5f\u662f\u975e\u5e38\u9a87\u4eba\u7684\u5b9e\u529b\u4e86\u3002\u743c\u6069\u751a\u81f3\u6000\u7591\u6885\u83f2\u65af\u90fd\u672a\u5fc5\u6709\u8fd9\u4e2a\u672c\u4e8b\uff0c\u5979\u7528\u5251\u81ea\u7136\u6ca1\u95ee\u9898\uff0c\u4f46\u7a7a\u624b\u53ea\u6015\u5c31\u4e0d\u884c\u4e86\u3002</p><p>\u7070\u888d\u7537\u4eba\u597d\u6574\u4ee5\u6687\u5730\u7a7f\u8fc7\u5927\u5802\uff0c\u51c6\u5907\u79bb\u5f00\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u523a\u8033\u7684\u94c3\u58f0\u4ece\u697c\u4e0b\u4f20\u6765\uff0c\u5e94\u8be5\u662f\u521a\u624d\u88ab\u6253\u7ffb\u7684\u90a3\u51e0\u4e2a\u536b\u5175\u62c9\u54cd\u4e86\u4ec0\u4e48\u8b66\u62a5\u3002\u5927\u5802\u91cc\u6b63\u5728\u75db\u996e\u72c2\u6b22\u7684\u9152\u9b3c\u4eec\u90fd\u5446\u4e86\u4e00\u4e0b\uff0c\u987f\u65f6\u5b89\u9759\u4e0b\u6765\u3002\u7d27\u63a5\u7740\uff0c\u743c\u6069\u542c\u89c1\u80cc\u540e\u697c\u68af\u58f0\u54cd\u8d77\uff0c\u4ed6\u4fa7\u8eab\u8ba9\u5f00\uff0c\u89c1\u4e00\u4e2a\u4f8d\u8005\u5654\u5654\u5654\u8dd1\u4e0a\u6765\uff0c\u626b\u89c6\u4e00\u773c\uff0c\u4f38\u624b\u671d\u90a3\u4e2a\u6b63\u51c6\u5907\u51fa\u95e8\u7684\u7684\u7070\u888d\u7537\u4eba\u4e00\u6307\uff1a\u201c\u5c31\u662f\u4ed6\uff01\u201d</p><p>\u987f\u65f6\u8fde\u58f0\u53f1\u559d\u54cd\u8d77\uff0c\u516d\u4e2a\u5168\u526f\u6b66\u88c5\u7684\u536b\u5175\u4ece\u5927\u5802\u5404\u5904\u5954\u8fc7\u6765\uff0c\u8981\u628a\u8fd9\u4e2a\u6363\u4e71\u7684\u5bb6\u4f19\u5236\u670d\u3002\u8fd9\u79cd\u4e8b\u60c5\u4f3c\u4e4e\u7ecf\u5e38\u53d1\u751f\uff0c\u56e0\u4e3a\u90a3\u4e9b\u539f\u672c\u8fd8\u9189\u91ba\u91ba\u7684\u9152\u5ba2\u4eec\u8fc5\u901f\u65e0\u6bd4\u5730\u907f\u8ba9\uff0c\u7a7a\u51fa\u573a\u5730\uff0c\u4e0d\u5c11\u4eba\u8fd8\u6572\u7740\u9152\u74f6\u5f00\u59cb\u9ad8\u58f0\u5450\u558a\uff0c\u4e5f\u4e0d\u77e5\u9053\u5230\u5e95\u662f\u5728\u4e3a\u54ea\u4e00\u65b9\u52a9\u5a01\u3002</p><p>\u4f46\u8fd9\u573a\u51b2\u7a81\u4f3c\u4e4e\u5e76\u6ca1\u6709\u4ec0\u4e48\u60ac\u5ff5\u3002</p><p>\u7070\u888d\u7537\u4eba\u7ad9\u5b9a\uff0c\u53cc\u624b\u7b3c\u5728\u8896\u4e2d\uff0c\u4e24\u4e2a\u536b\u5175\u51b2\u5728\u6700\u524d\u9762\uff0c\u6284\u8d77\u624b\u4e2d\u7684\u94fe\u67b7\u548c\u9489\u5934\u9524\u5c31\u7838\u8fc7\u6765\uff0c\u4e0b\u624b\u5012\u662f\u5f88\u6709\u5206\u5bf8\uff0c\u90fd\u907f\u5f00\u4e86\u8981\u5bb3\u90e8\u4f4d\uff0c\u53ea\u671d\u76ae\u539a\u8089\u591a\u7684\u5730\u65b9\u62db\u547c\uff0c\u663e\u7136\u6ca1\u51c6\u5907\u771f\u7f6e\u4eba\u4e8e\u6b7b\u5730\uff0c\u53ea\u6253\u7b97\u75db\u6253\u4e00\u987f\u3002</p><p>\u4f46\u4ed6\u4eec\u7684\u5bf9\u624b\u4f3c\u4e4e\u5c31\u6ca1\u6709\u8fd9\u4e48\u5ba2\u6c14\u3002</p><p>\u7830\u7830\u4e24\u58f0\uff0c\u743c\u6069\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u8fd8\u6ca1\u770b\u6e05\u695a\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4e24\u4e2a\u536b\u5175\u5df2\u7ecf\u9f50\u9f50\u8138\u4e0a\u5f00\u82b1\uff0c\u9c9c\u8840\u957f\u6d41\uff0c\u76f4\u76f4\u4ef0\u9762\u5f80\u540e\u5012\u4e86\u4e0b\u53bb\u3002</p><p>\u51fa\u624b\u597d\u5feb\uff01\u800c\u4e14\u77ac\u95f4\u5c31\u8ba9\u5bf9\u65b9\u4e27\u5931\u4e86\u6218\u6597\u529b\uff0c\u8fd9\u4efd\u672c\u4e8b\u771f\u4e0d\u53ef\u5c0f\u89d1\u3002</p><p>\u540e\u9762\u56db\u4e2a\u536b\u5175\u89c1\u72b6\uff0c\u811a\u6b65\u7565\u505c\uff0c\u4e92\u76f8\u770b\u4e86\u770b\uff0c\u5927\u7ea6\u4e5f\u90fd\u6709\u4e9b\u5fc3\u60ca\uff0c\u4f46\u804c\u8d23\u6240\u5728\uff0c\u53ea\u5f97\u4e5f\u90fd\u547c\u559d\u4e00\u58f0\uff0c\u4e00\u9f50\u6251\u4e0a\u6765\u3002\u5374\u6709\u4e00\u4e2a\u536b\u5175\u505c\u7559\u5728\u6700\u540e\uff0c\u4ece\u8170\u95f4\u7684\u91d1\u8272\u53e3\u888b\u4e2d\u53d6\u51fa\u4e00\u679a\u6f02\u4eae\u7684\u5b9d\u77f3\u6765\uff0c\u7d27\u63e1\u5728\u624b\u4e2d\uff0c\u53e3\u4e2d\u5ff5\u8bf5\u4e86\u4e00\u4e2a\u8bcd\uff0c\u968f\u5373\u671d\u7070\u888d\u7537\u4eba\u7838\u4e86\u8fc7\u53bb\u3002</p><p>\u201c\u5b9d\u77f3\u70b8\u5f39\uff1f\u201d</p><p>\u7ad9\u5728\u8fdc\u5904\u7684\u743c\u6069\u770b\u5f97\u5206\u660e\uff0c\u5728\u6295\u63b7\u51fa\u6765\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u90a3\u5757\u5b9d\u77f3\u9661\u5730\u53d8\u6210\u4e86\u900f\u660e\u7684\u865a\u4f53\uff0c\u95ea\u70c1\u7740\u4e03\u5f69\u5149\u8292\u3002\u5982\u679c\u4ed6\u770b\u5f97\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u201c\u5b9d\u77f3\u70b8\u5f39\u201d\uff0c\u548c\u77f3\u5f39\u672f\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5904\uff0c\u4f46\u5374\u662f\u795e\u672f\uff0c\u800c\u4e14\u662f\u79cd\u9887\u7f55\u89c1\u7684\u795e\u672f\u3002\u5355\u8bba\u6740\u4f24\u529b\uff0c\u5b9d\u77f3\u70b8\u5f39\u8981\u6bd4\u77f3\u5f39\u672f\u5f3a\u5f88\u591a\uff1b\u4f46\u82e5\u8bba\u6210\u672c\uff0c\u4e00\u9897\u5b9d\u77f3\u548c\u4e00\u9897\u666e\u901a\u7684\u5c0f\u5706\u77f3\uff0c\u4ef7\u503c\u4f55\u6b62\u76f8\u5dee\u767e\u500d\u3002</p><p>\u80fd\u73a9\u5f97\u8d77\u8fd9\u4e48\u5962\u4f88\u7684\u795e\u672f\u2026\u2026\u4e5f\u53ea\u6709\u6c83\u91d1\u7684\u7267\u5e08\u4e86\u5427\u3002\u8fd9\u5ea7\u201c\u707f\u91d1\u4e4b\u5bb6\u201d\u9152\u9986\u662f\u6c83\u91d1\u6559\u4f1a\u7684\u4ea7\u4e1a\uff0c\u6d3e\u51e0\u4e2a\u7267\u5e08\u5728\u8fd9\u91cc\u770b\u573a\u5b50\uff0c\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002</p><p>\u7070\u888d\u7537\u4eba\u88ab\u524d\u9762\u4e09\u4e2a\u536b\u5175\u7ea0\u7f20\u4f4f\u4e86\uff0c\u4e00\u65f6\u672a\u80fd\u8131\u8eab\u3002\u4ed6\u6b64\u524d\u6253\u5012\u4e24\u4e2a\uff0c\u662f\u56e0\u4e3a\u5bf9\u624b\u5e76\u6ca1\u6709\u4e0b\u91cd\u624b\u7684\u610f\u601d\uff0c\u6240\u4ee5\u94bb\u4e86\u7a7a\u5b50\uff1b\u5982\u4eca\u536b\u5175\u4eec\u6253\u8d77\u5341\u4e8c\u5206\u8b66\u60d5\uff0c\u5200\u5251\u9f50\u65bd\uff0c\u4e0b\u624b\u6beb\u4e0d\u5bb9\u60c5\uff0c\u4ed6\u4ee5\u5be1\u654c\u4f17\uff0c\u5c31\u4e0d\u514d\u6709\u4e9b\u5c40\u4fc3\u3002\u4f46\u4ed6\u8eab\u624b\u53cd\u5e94\u6781\u5feb\uff0c\u4efb\u51ed\u5bf9\u65b9\u5f3a\u653b\u731b\u6253\uff0c\u6bcf\u6b21\u603b\u80fd\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u8eb2\u8fc7\u53bb\u3002</p><p>\u5b9d\u77f3\u70b8\u5f39\u547c\u5578\u98de\u6765\uff0c\u7cbe\u786e\u5730\u907f\u5f00\u4e86\u6b63\u5728\u56f4\u653b\u7684\u536b\u5175\uff0c\u7167\u7740\u7070\u888d\u7537\u4eba\u7838\u53bb\u3002\u7267\u5e08\u5230\u5e95\u8fd8\u662f\u4e0d\u6562\u5f04\u51fa\u4eba\u547d\uff0c\u6545\u610f\u907f\u5f00\u4e86\u5934\u8138\u8fd9\u79cd\u8981\u5bb3\uff0c\u53ea\u5bf9\u51c6\u5927\u817f\uff0c\u4f30\u8ba1\u5982\u679c\u7838\u4e2d\uff0c\u8fd9\u7070\u888d\u7537\u4eba\u5927\u6982\u8981\u65ad\u6761\u817f\u4e86\u3002</p><p>\u4f46\u8fd9\u79cd\u4e8b\u60c5\u5e76\u6ca1\u6709\u53d1\u751f\u3002</p><p>\u7070\u888d\u7537\u4eba\u89c1\u5b9d\u77f3\u70b8\u5f39\u7838\u6765\uff0c\u53cd\u800c\u563f\u563f\u7b11\u4e86\u4e00\u58f0\uff0c\u4e0d\u95ea\u4e0d\u907f\uff0c\u53cd\u800c\u4f38\u624b\u8fce\u4e0a\u4e00\u62e8\u3002\u5b9d\u77f3\u649e\u4e0a\u4ed6\u7684\u624b\u638c\uff0c\u8f70\u7684\u4e00\u58f0\u7206\u70b8\u5f00\u6765\uff0c\u4e03\u5f69\u788e\u5149\u5982\u96e8\u56db\u6e85\u3002\u6b63\u5728\u56f4\u653b\u7684\u4e09\u4e2a\u536b\u5175\u63aa\u624b\u4e0d\u53ca\uff0c\u88ab\u8fd9\u788e\u5149\u4e71\u6e85\u6ce2\u53ca\u5230\uff0c\u4e00\u65f6\u90fd\u6709\u4e9b\u72fc\u72c8\u4e0d\u582a\u3002\u7070\u888d\u7537\u4eba\u81ea\u5df1\u9996\u5f53\u5176\u51b2\uff0c\u5374\u4f3c\u4e4e\u534a\u70b9\u90fd\u4e0d\u66fe\u53d7\u4f24\u4e00\u822c\uff0c\u53cc\u62f3\u5982\u95ea\u7535\u822c\u6325\u51fa\uff0c\u91cd\u91cd\u7838\u5728\u4e09\u4e2a\u536b\u5175\u7684\u8138\u4e0a\u3001\u80f8\u53e3\uff0c\u7830\u7830\u7830\u8fde\u54cd\uff0c\u4e09\u4eba\u4fbf\u5982\u6c99\u888b\u4e00\u822c\u8f6f\u8f6f\u762b\u4e0b\u53bb\u3002</p><p>\u7070\u888d\u7537\u4eba\u6253\u5012\u4e09\u4e2a\u536b\u5175\uff0c\u8f6c\u8fc7\u8eab\u4fbf\u671d\u521a\u624d\u65bd\u5c55\u5b9d\u77f3\u70b8\u5f39\u7684\u6c83\u91d1\u7267\u5e08\u51b2\u6765\uff1b\u7267\u5e08\u614c\u5fd9\u540e\u9000\uff0c\u4e00\u628a\u4ece\u8116\u5b50\u4e0a\u62fd\u4e0b\u5723\u5fbd\uff0c\u671d\u7070\u888d\u7537\u4eba\u865a\u865a\u4e00\u6309\uff0c\u201c\u5b9a\uff01\u201d\u4ed6\u5927\u559d\u4e00\u58f0\u3002</p><p>\u6c83\u91d1\u5973\u795e\u7684\u5723\u529b\u4ece\u5723\u5fbd\u4e0a\u8ff8\u53d1\u51fa\u6765\uff0c\u5c06\u7a7a\u6c14\u5316\u4f5c\u65e0\u5f62\u7684\u675f\u7f1a\uff0c\u671d\u7070\u888d\u7537\u4eba\u8eab\u4e0a\u7f20\u53bb\u3002\u6b63\u5728\u524d\u51b2\u7684\u7070\u888d\u7537\u4eba\u5e94\u58f0\u800c\u6b62\uff0c\u786c\u751f\u751f\u987f\u4f4f\u811a\u6b65\uff0c\u505c\u5728\u539f\u5730\uff0c\u7267\u5e08\u5fae\u5fae\u677e\u4e86\u53e3\u6c14\uff0c\u8fd9\u662f\u5b9a\u8eab\u672f\uff0c\u5f88\u5c11\u6709\u4eba\u80fd\u6323\u8131\u8fd9\u6cd5\u672f\u7684\u538b\u5236\uff0c\u867d\u8bf4\u6301\u7eed\u4e0d\u4e86\u534a\u5206\u949f\uff0c\u4f46\u4e5f\u5df2\u7ecf\u8db3\u591f\u4e86\u3002</p><p>\u7267\u5e08\u4e00\u9762\u540e\u9000\uff0c\u8d76\u7d27\u62c9\u5f00\u8ddd\u79bb\uff0c\u4e00\u9762\u4ece\u8170\u95f4\u53e3\u888b\u91cc\u638f\u53d6\u5b9d\u77f3\uff0c\u60f3\u518d\u5ea6\u65bd\u5c55\u4e00\u4e2a\u5b9d\u77f3\u7206\u70b8\u672f\u3002\u5f53\u4e0d\u7b49\u4ed6\u53d6\u51fa\u5b9d\u77f3\uff0c\u88ab\u6cd5\u672f\u5b9a\u4f4f\u7684\u7070\u888d\u7537\u4eba\u9661\u7136\u53c8\u5f00\u59cb\u52a8\u8d77\u6765\uff0c\u7267\u5e08\u53ea\u89c9\u773c\u524d\u7070\u5f71\u4e00\u6643\uff0c\u7d27\u63a5\u7740\u9762\u90e8\u5267\u75db\u4f20\u6765\uff0c\u4ed6\u6e05\u695a\u5730\u542c\u5230\u9f3b\u6881\u9aa8\u65ad\u6298\u7684\u58f0\u97f3\uff0c\u968f\u5373\u4eba\u4e8b\u4e0d\u77e5\u4e86\u3002</p><p>\u7070\u888d\u7537\u4eba\u95ea\u7535\u822c\u6253\u5012\u4e86\u516d\u4e2a\u536b\u5175\uff0c\u81ea\u5df1\u5168\u7136\u65e0\u4e8b\uff0c\u8fd9\u8ba9\u9152\u9986\u91cc\u6240\u6709\u5ba2\u4eba\u90fd\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e0d\u6562\u7f6e\u4fe1\u3002\u4f46\u60ca\u8bb6\u5f52\u60ca\u8bb6\uff0c\u201c\u707f\u91d1\u4e4b\u5bb6\u201d\u4f5c\u4e3a\u535a\u5f97\u4e4b\u95e8\u7684\u7b2c\u4e00\u5927\u9152\u9986\uff0c\u6c83\u91d1\u6559\u4f1a\u91cd\u8981\u6295\u8d44\u4ea7\u4e1a\uff0c\u5018\u82e5\u8fde\u8fd9\u70b9\u4e8b\u60c5\u90fd\u641e\u4e0d\u5b9a\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u518d\u5f00\u5e97\u4e86\uff0c\u8d81\u65e9\u5173\u95e8\u5927\u5409\u3002\u968f\u7740\u4e0d\u77e5\u54ea\u91cc\u54cd\u8d77\u7684\u4e00\u58f0\u547c\u54e8\uff0c\u7a7a\u6c14\u4e2d\u5212\u5f00\u94f6\u8272\u7684\u4f20\u9001\u95e8\uff0c\u516d\u4e03\u4e2a\u6c83\u91d1\u7267\u5e08\u51fa\u73b0\u5728\u9152\u9986\u91cc\uff0c\u4ed6\u4eec\u7684\u8eab\u4efd\u4e00\u671b\u53ef\u77e5\uff0c\u56e0\u4e3a\u5168\u90fd\u7a7f\u7740\u91d1\u707f\u707f\u7684\u94e0\u7532\uff0c\u7f69\u7740\u5927\u7ea2\u62ab\u98ce\uff0c\u6234\u7740\u9576\u6ee1\u94bb\u77f3\u7684\u5934\u76d4\u2014\u2014\u9664\u4e86\u6c83\u91d1\u7267\u5e08\uff0c\u518d\u6ca1\u6709\u8c01\u4ee5\u8fd9\u79cd\u5962\u534e\u9020\u578b\u51fa\u573a\u4e86\u3002</p><p>\u4e0e\u6b64\u540c\u65f6\uff0c\u4ece\u51e0\u4e2a\u4fa7\u95e8\u4e2d\uff0c\u53c8\u6d8c\u51fa\u5341\u591a\u4e2a\u536b\u5175\u6765\uff0c\u4ed6\u4eec\u8fd9\u6b21\u5374\u5b66\u4e56\u4e86\uff0c\u5438\u53d6\u540c\u4f34\u7684\u6559\u8bad\uff0c\u4e00\u4e2a\u4e2a\u90fd\u4e0d\u4e0a\u524d\uff0c\u5168\u90fd\u62bd\u51fa\u5341\u5b57\u5f13\u6765\uff0c\u642d\u4e0a\u7bad\u77e2\uff0c\u7784\u51c6\u7070\u888d\u7537\u4eba\u3002\u53ea\u8981\u4ed6\u6562\u4e71\u52a8\uff0c\u4fbf\u8981\u5c06\u4ed6\u5c04\u6210\u523a\u732c\u3002</p><p>\u4e8b\u60c5\u8d8a\u95f9\u8d8a\u5927\uff0c\u7070\u888d\u7537\u4eba\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u59cb\u6599\u4e0d\u53ca\uff0c\u4ed6\u7eb5\u7136\u518d\u5f3a\u608d\uff0c\u4e5f\u603b\u4e0d\u80fd\u654c\u5f97\u8fc7\u8fd9\u91cd\u91cd\u5305\u56f4\u3002\u8fdc\u5904\u536b\u5175\u7528\u5341\u5b57\u5f13\u903c\u4f4f\uff0c\u53c8\u6709\u6c83\u91d1\u7267\u5e08\u538b\u9635\uff0c\u4fbf\u662f\u60f3\u9003\u8dd1\u4e5f\u8fdf\u4e86\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u5374\u5e76\u4e0d\u7d27\u5f20\uff0c\u795e\u8272\u95f4\u4f9d\u65e7\u8f7b\u677e\u81ea\u82e5\uff0c\u53ea\u662f\u4f3c\u4e4e\u603b\u5e26\u7740\u4e9b\u51b7\u7b11\u610f\u5473\u3002\u201c\u5931\u966a\u4e86\u3002\u201d\u4ed6\u8bf4\uff0c\u8eab\u4f53\u5468\u56f4\u9661\u7136\u6cdb\u8d77\u4e00\u5c42\u6ce2\u7eb9\u72b6\u7a7a\u6c14\uff0c\u968f\u5373\u6574\u4e2a\u4eba\u5feb\u901f\u53d8\u5f97\u900f\u660e\u3001\u865a\u65e0\uff0c\u5b8c\u5168\u6d88\u5931\u3002</p><p>\u6240\u6709\u4eba\u90fd\u9762\u9762\u76f8\u89d1\uff0c\u5305\u62ec\u536b\u5175\u3001\u7267\u5e08\u548c\u4e00\u65c1\u89c2\u6218\u7684\u743c\u6069\uff0c\u8fd9\u6709\u70b9\u50cf\u4f20\u9001\u672f\uff0c\u6216\u8005\u662f\u7b80\u5316\u7248\u672c\u7684\u4efb\u610f\u95e8\uff0c\u603b\u4e4b\u662f\u8fd9\u4e00\u7c7b\u578b\u7684\u7a7a\u95f4\u4f20\u9001\u9b54\u6cd5\u3002\u4f46\u95ee\u9898\u662f\uff0c\u8fd9\u7070\u888d\u7537\u4eba\u662f\u600e\u4e48\u65bd\u5c55\u51fa\u8fd9\u6cd5\u672f\u7684\uff1f</p><p>\u5982\u679c\u4ed6\u662f\u5deb\u5e08\uff0c\u90a3\u4e48\u4ed6\u81f3\u5c11\u8981\u8bf5\u5492\uff0c\u8981\u6709\u65bd\u6cd5\u52a8\u4f5c\uff0c\u8981\u4ece\u6000\u91cc\u53d6\u6750\u6599\uff1b\u5982\u679c\u4ed6\u662f\u7267\u5e08\uff0c\u90a3\u4e48\u81f3\u5c11\u4ed6\u5e94\u8be5\u53d6\u5723\u5fbd\u4e4b\u7c7b\u7684\u6cd5\u5668\u5728\u624b\uff1b\u4f46\u4ed6\u4ec0\u4e48\u90fd\u6ca1\u505a\u2026\u2026\u96be\u9053\u662f\u6697\u4e2d\u542f\u52a8\u4e86\u4ec0\u4e48\u9b54\u6cd5\u7269\u54c1\uff1f</p><p>\u80fd\u77ac\u95f4\u53d1\u52a8\u7a7a\u95f4\u4f20\u9001\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u8fd9\u53ef\u662f\u975e\u5e38\u7f55\u89c1\u975e\u5e38\u96be\u5f97\u5e76\u4e14\u975e\u5e38\u73cd\u8d35\u7684\u5462\u3002\u743c\u6069\u624b\u91cc\u6709\u4e00\u628a\u56de\u8fd4\u5315\u9996\uff0c\u53ea\u4e0d\u8fc7\u80fd\u56fa\u5b9a\u5730\u8fd4\u56de\u9884\u8bbe\u5730\u70b9\uff0c\u5c5e\u4e8e\u6bd4\u8f83\u4f4e\u7ea7\u5446\u677f\u7684\u4f20\u9001\u9b54\u6cd5\uff0c\u5f53\u65f6\u5c31\u5df2\u7ecf\u8ba9\u963f\u5fb7\u5df4\u57ce\u7684\u8001\u77ee\u4eba\u6697\u81ea\u5782\u6d8e\uff0c\u604b\u604b\u4e0d\u820d\u4e86\u3002\u539f\u56e0\u65e0\u4ed6\uff0c\u6709\u6b64\u7c7b\u4e1c\u897f\u5728\u624b\uff0c\u5c31\u7b97\u88ab\u5341\u4e07\u5927\u519b\u91cd\u91cd\u5305\u56f4\uff0c\u4f60\u90fd\u80fd\u7a33\u7a33\u5148\u7acb\u4e8e\u4e0d\u8d25\u2014\u2014\u81f3\u5c11\u662f\u4e0d\u6b7b\u2014\u2014\u4e4b\u5730\uff0c\u7b49\u4e8e\u662f\u4e00\u9053\u4fdd\u547d\u62a4\u8eab\u7b26\uff0c\u5176\u4ef7\u503c\u80fd\u4e0d\u9ad8\u4e48\u3002\u8fd9\u4e2a\u7070\u888d\u7537\u4eba\u770b\u4e0a\u53bb\u4e00\u526f\u5bd2\u9178\u76f8\uff0c\u5168\u8eab\u4e0a\u4e0b\u6ca1\u534a\u70b9\u503c\u94b1\u4e1c\u897f\uff0c\u96be\u9053\u4f1a\u6709\u8fd9\u79cd\u5b9d\u7269\uff1f</p><p>\u7267\u5e08\u62db\u6765\u4f8d\u8005\uff0c\u8be2\u95ee\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u5f97\u5230\u7684\u7b54\u6848\u4ee4\u4eba\u60ca\u8bb6\u3002\u539f\u6765\u4e8b\u60c5\u5f88\u7b80\u5355\uff0c\u53ea\u4e0d\u8fc7\u662f\u8fd9\u4e2a\u7070\u888d\u7537\u4eba\u4e0a\u4e86\u5973\u4eba\u5374\u8d56\u5e10\uff0c\u575a\u6301\u58f0\u79f0\u4ed8\u4e0d\u8d77\u94b1\uff0c\u6240\u4ee5\u624d\u6253\u8d77\u6765\u3002</p><p>\u8fd9\u5c31\u5f88\u5947\u602a\u4e86\u3002</p><p>\u8fd9\u4e16\u754c\u4e0a\u7a77\u4eba\u5f88\u591a\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u4ee5\u521a\u624d\u90a3\u5bb6\u4f19\u7684\u8eab\u624b\uff0c\u968f\u4fbf\u53bb\u505a\u4fdd\u9556\u3001\u505a\u62a4\u536b\u3001\u505a\u4f63\u5175\u3001\u505a\u5192\u9669\u8005\uff0c\u4e0d\u8bf4\u80fd\u53d1\u5927\u8d22\uff0c\u81f3\u5c11\u7528\u4e0d\u7740\u8d56\u8fd9\u70b9\u5e10\u554a\u3002\u8fd9\u662f\u707f\u91d1\u4e4b\u5bb6\u7684\u201c\u94dc\u95e8\u201d\u533a\u57df\uff0c\u539f\u672c\u5c31\u662f\u6d88\u8d39\u6c34\u51c6\u6700\u4f4e\u7684\u4e00\u5757\u4e86\uff0c\u5c31\u7b97\u4f60\u8fde\u4e0a\u5341\u4e2a\u5973\u4eba\uff0c\u4e5f\u82b1\u4e0d\u4e86\u591a\u5c11\u94b1\u7684\u3002\u6709\u5fc5\u8981\u4e3a\u8fd9\u70b9\u5c0f\u94b1\u5192\u88ab\u4eba\u75db\u6253\u7684\u5371\u9669\u4e48\uff0c\u8fd8\u662f\u8bf4\u4ed6\u5c31\u559c\u6b22\u8fd9\u6837\uff1f</p><p>\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u800c\u4e14\u8c01\u4e5f\u4e0d\u8ba4\u8bc6\u90a3\u4e2a\u7070\u888d\u7537\u4eba\u662f\u8c01\uff0c\u90fd\u8bf4\u6b64\u524d\u4ece\u6ca1\u89c1\u8fc7\u3002\u88ab\u6253\u5012\u7684\u51e0\u4e2a\u5b88\u536b\u88ab\u6276\u4e86\u8d77\u6765\uff0c\u4f24\u52bf\u5012\u90fd\u4e0d\u91cd\uff0c\u770b\u8d77\u6765\uff0c\u90a3\u4e2a\u7070\u888d\u7537\u4eba\u4e0b\u624b\u8fd8\u7b97\u9887\u6709\u5206\u5bf8\uff0c\u53ea\u662f\u6253\u6655\u4e86\uff0c\u5e76\u6ca1\u6709\u771f\u6b63\u91cd\u4f24\u3002</p><p>\u5fd9\u4e71\u4e4b\u4e2d\uff0c\u743c\u6069\u8d70\u51fa\u9152\u9986\uff0c\u8fdc\u79bb\u55a7\u56a3\u548c\u6c61\u6d4a\uff0c\u547c\u5438\u5230\u5916\u9762\u7684\u65b0\u9c9c\u7a7a\u6c14\uff0c\u987f\u65f6\u7cbe\u795e\u4e00\u632f\u3002\u4ed6\u5224\u65ad\u4e86\u4e00\u4e0b\u5927\u81f4\u65b9\u4f4d\uff0c\u56de\u5fc6\u7740\u6765\u65f6\u7684\u8def\u5f84\uff0c\u6cbf\u7740\u8857\u9053\u5f00\u59cb\u5f80\u56de\u8d70\u3002</p><p>\u8d70\u4e86\u5927\u7ea6\u5341\u6765\u5206\u949f\uff0c\u4ed6\u5df2\u7ecf\u8fdc\u8fdc\u770b\u89c1\u5e02\u653f\u5e9c\u5e7f\u573a\uff0c\u8f6c\u8fc7\u53bb\u5c31\u662f\u81ea\u5df1\u7684\u4f4f\u5904\u4e86\u3002\u5e02\u653f\u5e9c\u5e7f\u573a\u662f\u53d1\u5e03\u901a\u544a\u3001\u6f14\u8bb2\u96c6\u4f1a\u7684\u6240\u5728\uff0c\u62db\u52df\u5192\u9669\u8005\u7684\u544a\u793a\u5c31\u662f\u5728\u8fd9\u91cc\u8d34\u7684\uff0c\u6b64\u65f6\u662f\u6df1\u591c\uff0c\u5927\u5bb6\u90fd\u5fd9\u4e8e\u73a9\u4e50\u6216\u8005\u4f11\u606f\uff0c\u5e7f\u573a\u4e0a\u9759\u6084\u6084\u7684\uff0c\u7a7a\u65e0\u4e00\u4eba\u3002</p><p>\u4e0d\u5bf9\uff0c\u4e0d\u662f\u7a7a\u65e0\u4e00\u4eba\u3002\u901a\u544a\u680f\u524d\u9762\uff0c\u4f3c\u4e4e\u5c31\u7ad9\u7740\u4e2a\u4eba\uff0c\u6b63\u5728\u770b\u90a3\u5f20\u62db\u52df\u544a\u793a\u3002</p><p>\u743c\u6069\u6709\u4e9b\u597d\u5947\uff0c\u8d70\u8fd1\u4e24\u6b65\uff0c\u60f3\u770b\u770b\u662f\u8c01\u8fd9\u4e48\u5927\u534a\u591c\u6765\u770b\u544a\u793a\u3002\u501f\u7740\u660f\u9ec4\u7684\u8def\u706f\uff0c\u4ed6\u770b\u6e05\u695a\u4e86\u8fd9\u4e2a\u4eba\u7684\u6a21\u6837\uff1a\u9ed1\u8272\u4e71\u4e71\u7684\u5934\u53d1\uff0c\u7070\u8272\u7c97\u5e03\u888d\u5b50\uff0c\u8d64\u7740\u53cc\u8db3\uff0c\u53cc\u624b\u7a7a\u7a7a\uff0c\u73af\u62b1\u5728\u80f8\u524d\u3002</p><p>\u90a3\u4e2a\uff0c\u6709\u53e5\u8bdd\u600e\u4e48\u8bf4\u6765\u7740\u2026\u2026\u662f\u4e86\uff0c\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\u3002\u8c01\u4e5f\u4e0d\u66fe\u60f3\u5230\uff0c\u5728\u4e0d\u5230\u534a\u5c0f\u65f6\u540e\uff0c\u743c\u6069\u4f1a\u518d\u6b21\u649e\u4e0a\u8fd9\u4f4d\u4e0a\u5b8c\u5973\u4eba\u4e0d\u7ed9\u94b1\u7684\u8d56\u5e10\u5bb6\u4f19\u3002</p><p>\u7070\u888d\u7537\u4eba\u4e5f\u53d1\u89c9\u6709\u4eba\u8d70\u8fd1\uff0c\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u53cc\u5c0f\u773c\u775b\u91cc\u7cbe\u5149\u5fae\u5c04\uff0c\u6253\u91cf\u7740\u743c\u6069\u3002\u201c\u665a\u4e0a\u597d\u3002\u201d\u4ed6\u5148\u5f00\u53e3\u3002</p><p>\u201c\u665a\u4e0a\u597d\u3002\u201d\u743c\u6069\u56de\u7b54\uff0c\u4ed6\u5feb\u901f\u731c\u6d4b\u7740\u5bf9\u65b9\u7684\u8eab\u4efd\u3002\u7cbe\u901a\u7a7a\u624b\u640f\u51fb\uff0c\u5bf9\u9b54\u6cd5\u663e\u7136\u6709\u5f88\u5f3a\u7684\u62b5\u6297\uff0c\u80fd\u4f7f\u7528\u7c7b\u4f3c\u201c\u4efb\u610f\u95e8\u201d\u7684\u7a7a\u95f4\u4f20\u9001\u80fd\u529b\uff0c\u518d\u52a0\u4e0a\u8fd9\u4e00\u8eab\u6253\u626e\u2026\u2026\u4f3c\u4e4e\u53ea\u6709\u4e00\u79cd\u53ef\u80fd\u4e86\u3002</p><p>\u8fd9\u4eba\u662f\u4e2a\u6b66\u50e7\u3002</p></article>"}}}]);
