(window.webpackJsonp=window.webpackJsonp||[]).push([[1334],{1357:function(p){p.exports={chapterTitle:"\u7b2c\u4e09\u5377 \u5deb\u5996\u7bc7",title:"\u5e8f\u7ae0 \u6076\u9b54\u7684\u62dc\u8bbf\uff08\u4e0a\uff09",content:"<article><h1>\u7b2c\u4e09\u5377 \u5deb\u5996\u7bc7</h1><h2>\u5e8f\u7ae0 \u6076\u9b54\u7684\u62dc\u8bbf\uff08\u4e0a\uff09</h2><p>\u9ed1\u6c89\u6c89\u7684\u5bc6\u5ba4\u4e2d\uff0c\u6c38\u8fdc\u5f25\u6f2b\u7740\u4e00\u80a1\u9634\u51b7\u548c\u6b7b\u4ea1\u7684\u6c14\u606f\uff0c\u4eff\u4f5b\u5343\u5e74\u5893\u5ba4\u4e00\u822c\u3002\u9ed1\u8272\u7684\u5929\u9e45\u7ed2\u7a97\u5e18\u5c06\u7a97\u6237\u906e\u853d\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u8ba9\u5916\u9762\u7684\u9633\u5149\u534a\u70b9\u4e5f\u900f\u4e0d\u8fdb\u6765\u3002</p><p>\u6b27\u51ef\u5e76\u4e0d\u559c\u6b22\u8fd9\u79cd\u538b\u6291\u7684\u6c14\u6c1b\uff0c\u4ed6\u559c\u6b22\u66f4\u52a0\u660e\u4eae\u3001\u8f7b\u5feb\u4e00\u4e9b\u7684\u8272\u8c03\u2026\u2026\u5443\uff0c\u6216\u8005\u7b80\u5355\u5730\u8bf4\uff0c\u4ed6\u7684\u5ba1\u7f8e\u503e\u5411\u5176\u5b9e\u548c\u4ed6\u7684\u524d\u4efb\u8001\u677f\u683c\u62c9\u5179\u7279\u5f88\u76f8\u50cf\uff0c\u559c\u6b22\u94f6\u8272\u3001\u767d\u8272\u6216\u8005\u8c61\u7259\u8272\u642d\u914d\uff0c\u867d\u7136\u504f\u51b7\uff0c\u5374\u4e0d\u5931\u660e\u5feb\uff0c\u7cbe\u81f4\u4e2d\u900f\u51fa\u5bcc\u4e3d\u5802\u7687\u3002\u683c\u62c9\u5179\u7279\u7684\u94f6\u8272\u738b\u5bab\uff0c\u5c31\u662f\u8fd9\u79cd\u683c\u8c03\uff0c\u5012\u5f71\u5728\u6e56\u6c34\u4e4b\u4e2d\uff0c\u770b\u8d77\u6765\u4eff\u4f5b\u5e7d\u51b7\u7684\u68a6\u5e7b\u57ce\u5821\u3002</p><p>\u53ef\u60dc\u73b0\u5728\u4ed6\u7684\u65b0\u8001\u677f\u662f\u5965\u5580\u65af\uff0c\u4ea1\u7075\u541b\u738b\u2014\u2014\u867d\u7136\u5965\u5580\u65af\u81ea\u5df1\u4e0d\u662f\u4e2a\u4ea1\u7075\uff0c\u4f46\u4ed6\u7684\u624b\u4e0b\u5374\u662f\u5927\u7968\u5927\u7968\u7684\u4ea1\u7075\u3002\u5965\u5580\u65af\u5c45\u4f4f\u5728\u65e0\u5c3d\u6df1\u6e0a\u7684\u7b2c\u4e00\u767e\u4e00\u5341\u4e09\u5c42\uff0c\u90a3\u5730\u65b9\u5929\u7a7a\u662f\u9634\u6c89\u6c89\u7684\uff0c\u9646\u5730\u662f\u9634\u6c89\u6c89\u7684\uff0c\u8fde\u7a7a\u6c14\u90fd\u4eff\u4f5b\u662f\u9634\u6c89\u6c89\u7684\uff0c\u904d\u5730\u90fd\u662f\u575f\u5730\u3001\u5893\u7891\u548c\u6e38\u8361\u7684\u9ab7\u9ac5\u50f5\u5c38\u3002\u5965\u5580\u65af\u5c45\u4f4f\u7684\u57ce\u5821\u53eb\u505a\u201c\u90a3\u62c9\u6cf0\u5c14\u201d\uff0c\u7ffb\u8bd1\u6210\u901a\u7528\u8bed\u5c31\u662f\u201c\u6b7b\u8005\u4e4b\u57ce\u201d\uff08the City of Dead\uff09\uff0c\u662f\u5728\u51b0\u51bb\u6d77\u6d0b\u8868\u9762\u96d5\u5207\u51fa\u7684\u5bd2\u51b7\u56fd\u5ea6\uff0c\u6574\u4e2a\u5c31\u662f\u4e00\u5ea7\u50f5\u51b7\u7684\u5927\u5893\u5730\u3002\u57ce\u4e2d\u6563\u5e03\u7740\u9ad8\u5927\u7684\u9675\u5893\uff0c\u4ee5\u53ca\u9ad8\u8038\u7684\u65b9\u5c16\u7891\uff08\u846c\u793c\u6240\u7528\uff09\uff0c\u5730\u9762\u4e0a\u94fa\u7740\u539a\u5bc6\u800c\u6709\u5f39\u6027\u7684\u5730\u6bef\uff0c\u662f\u4ece\u6210\u5343\u4e0a\u4e07\u65e0\u6cd5\u5b89\u606f\u7684\u6b7b\u8005\u8eab\u4e0a\u53d6\u4e0b\u7684\u6bdb\u53d1\u7f16\u7ec7\u800c\u6210\u3002\u9ab7\u9ac5\u3001\u50f5\u5c38\u3001\u98df\u5c38\u9b3c\u3001\u5438\u8840\u9b3c\u4ee5\u53ca\u5deb\u5996\uff0c\u5728\u57ce\u4e2d\u6e38\u8361\u8005\uff0c\u4eff\u4f5b\u9b3c\u872e\u3002</p><p>\u4f5c\u4e3a\u4e00\u4f4d\u4ee5\u201c\u63a8\u5012\u5973\u795e\u201d\u4e3a\u6bd5\u751f\u7406\u60f3\u7684\u6076\u9b54\uff0c\u6b27\u51ef\u663e\u7136\u5c5e\u4e8e\u6709\u7406\u60f3\u6709\u9053\u5fb7\u6709\u6587\u5316\u2026\u2026\u5443\uff0c\u5f53\u7136\u65e0\u7eaa\u5f8b\u7684\u90a3\u4e00\u7c7b\u578b\uff0c\u4ed6\u5b9e\u5728\u65e0\u6cd5\u5bb9\u5fcd\u5965\u5580\u65af\u8fd9\u79cd\u5dee\u52b2\u7684\u5ba1\u7f8e\u89c2\uff0c\u5728\u6b7b\u8005\u4e4b\u57ce\u91cc\u53ea\u4f4f\u4e86\u534a\u4e2a\u6708\uff0c\u4ed6\u5c31\u611f\u89c9\u81ea\u5df1\u90fd\u5feb\u8981\u53d8\u6210\u4e00\u53ea\u50f5\u5c38\u4e86\u3002\u6240\u4ee5\u8fd9\u6b21\u63a5\u5230\u6307\u4ee4\uff0c\u88ab\u5965\u5580\u65af\u6d3e\u5230\u7269\u8d28\u754c\u6765\u51fa\u5dee\uff0c\u4ed6\u5012\u4e5f\u6697\u4e2d\u8212\u4e86\u53e3\u6c14\u3002</p><p>\u53ea\u662f\u2026\u2026\u8eb2\u5f97\u4e86\u521d\u4e00\uff0c\u8eb2\u4e0d\u8fc7\u5341\u4e94\u3002\u5965\u5580\u65af\u6d3e\u7ed9\u4ed6\u7684\u7b2c\u4e8c\u4e2a\u4efb\u52a1\uff0c\u5c31\u662f\u8981\u6765\u7ed9\u62c9\u6c83\u514b\u9001\u793c\uff0c\u5e73\u606f\u4ed6\u7684\u6012\u6c14\uff0c\u987a\u4fbf\u5e2e\u67d0\u4e2a\u529e\u7838\u4e86\u4e8b\u60c5\u7684\u5012\u9709\u5bb6\u4f19\u64e6\u5c41\u80a1\u3002</p><p>\u62c9\u6c83\u514b\u867d\u7136\u662f\u4e2a\u5deb\u5996\uff0c\u4f46\u81f3\u5c11\u66fe\u7ecf\u662f\u8010\u745f\u5e1d\u56fd\u7684\u5927\u5965\u672f\u5e08\uff0c\u6b27\u51ef\u539f\u672c\u6307\u671b\u4ed6\u7684\u5ba1\u7f8e\u89c2\u80fd\u6bd4\u5965\u5580\u65af\u597d\u4e00\u70b9\uff0c\u628a\u5c45\u6240\u5e03\u7f6e\u5f97\u534e\u4e3d\u7cbe\u7f8e\u4e00\u70b9\u3002\u7136\u800c\u4e8b\u5b9e\u8ba9\u4ed6\u5927\u5931\u6240\u671b\uff0c\u62c9\u6c83\u514b\u7684\u54c1\u4f4d\u548c\u5965\u5580\u65af\u5b8c\u5168\u5904\u4e8e\u540c\u4e00\u4e2a\u6c34\u51c6\uff0c\u5c31\u7b97\u7565\u6709\u63d0\u5347\uff0c\u4e5f\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002</p><p>\u8bdd\u8bf4\u56de\u6765\uff0c\u521a\u521a\u628a\u81ea\u5df1\u9886\u8fdb\u6765\u7684\u90a3\u4e2a\u5973\u5b69\uff0c\u53eb\u8389\u6cd5\u513f\xb7\u94f6\u6708\u7684\uff0c\u5e94\u8be5\u662f\u4e2a\u5438\u8840\u9b3c\u5427\uff0c\u5012\u662f\u633a\u6f02\u4eae\u7684\uff0c\u81f3\u5c11\u4ee5\u4eba\u7c7b\u7684\u5ba1\u7f8e\u89c2\u662f\u5982\u6b64\u3002\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u62c9\u6c83\u514b\u6bd4\u5965\u5580\u65af\u5f3a\uff0c\u81f3\u5c11\u61c2\u5f97\u627e\u4e2a\u7f8e\u5973\u5f53\u52a9\u624b\u2026\u2026\u53ea\u662f\u600e\u4e48\u603b\u89c9\u5f97\u4e0d\u592a\u5bf9\u52b2\uff0c\u4f3c\u4e4e\u548c\u5176\u4ed6\u5438\u8840\u9b3c\u6709\u4e9b\u5730\u65b9\u4e0d\u592a\u50cf\u2026\u2026</p><p>\u201c\u5927\u5965\u672f\u5e08\u9601\u4e0b\uff0c\u201d\u6b27\u51ef\u4f7f\u7528\u719f\u7ec3\u7684\u8010\u745f\u8bed\uff0c\u5411\u62c9\u6c83\u514b\u8eac\u8eab\u884c\u793c\uff0c\u201c\u6211\u5949\u4ea1\u7075\u541b\u738b\u4e4b\u547d\u524d\u6765\uff0c\u4e3a\u5e93\u6797\u590f\u90a3\u4e2a\u7b28\u86cb\u72af\u4e0b\u7684\u611a\u8822\u9519\u8bef\uff0c\u5411\u60a8\u81f4\u4ee5\u6700\u6df1\u5207\u7684\u6b49\u610f\u3002\u201d</p><p>\u6b63\u80cc\u5bf9\u7740\u6b27\u51ef\u7684\u5deb\u5996\u7f13\u7f13\u8f6c\u8fc7\u8eab\u6765\u3002</p><p>\u4e00\u88ad\u534e\u4e3d\u7684\u9ed1\u8272\u4e1d\u7ec7\u957f\u888d\uff0c\u5305\u88f9\u7740\u6d88\u7626\u7684\u8eab\u8eaf\uff0c\u9ab7\u9ac5\u5934\u9aa8\u4eff\u4f5b\u8c61\u7259\u5236\u6210\uff0c\u5373\u4f7f\u5728\u9ed1\u6697\u4e2d\u4e5f\u6563\u53d1\u7740\u67d4\u548c\u7684\u4e73\u767d\u8272\u5fae\u4eae\uff0c\u7a7a\u6d1e\u7684\u773c\u7736\u91cc\u900f\u51fa\u6a58\u7ea2\u8272\u7684\u5149\uff0c\u50cf\u9488\u5c16\u4e00\u822c\u5c16\u9510\u523a\u4eba\u3002</p><p>\u201c\u4e00\u4e2a\u708e\u9b54\uff1f\u201d\u62c9\u6c83\u514b\u95ee\uff0c\u4ed6\u7684\u58f0\u97f3\u9634\u6c89\u3001\u51b0\u51b7\uff0c\u4eff\u4f5b\u5e26\u7740\u8f7b\u5fae\u7684\u91d1\u5c5e\u6469\u64e6\u58f0\u3002\u6b7b\u4ea1\u548c\u6050\u60e7\u7684\u6c14\u606f\u4ece\u4ed6\u8eab\u4e0a\u6563\u53d1\u51fa\u6765\uff0c\u4eff\u4f5b\u6709\u5f62\u7684\u538b\u529b\uff0c\u7262\u7262\u7b3c\u7f69\u4f4f\u6b63\u8eac\u8eab\u884c\u793c\u7684\u6b27\u51ef\u3002</p><p>\u5bdf\u89c9\u5230\u5deb\u5996\u8f7b\u5fae\u7684\u654c\u610f\uff0c\u6b27\u51ef\u4f9d\u65e7\u4e0d\u52a8\u58f0\u8272\u3002\u201c\u80fd\u8ba4\u8bc6\u7269\u8d28\u754c\u6700\u5f3a\u5927\u7684\u5deb\u5e08\uff0c\u6211\u6df1\u611f\u8363\u5e78\uff0c\u201d\u4ed6\u606d\u7ef4\u7740\uff0c\u201c\u6211\u662f\u6b27\u51ef\uff0c\u5982\u60a8\u6240\u89c1\uff0c\u6765\u81ea\u6df1\u6e0a\u3002\u201d</p><p>\u62c9\u6c83\u514b\u76ef\u7740\u6b27\u51ef\uff0c\u4e00\u65f6\u7565\u7565\u6709\u4e9b\u4e0d\u89e3\u3002\u4f5c\u4e3a\u6df1\u6e0a\u6076\u9b54\u4e2d\u6700\u5f3a\u5927\u7684\u5b58\u5728\uff0c\u708e\u9b54\u662f\u6781\u5c11\u6765\u7269\u8d28\u754c\u7684\uff0c\u4f55\u51b5\u5deb\u5996\u6e05\u695a\u5730\u77e5\u9053\u5bf9\u65b9\u5e76\u975e\u4e00\u4e2a\u6295\u5f71\uff0c\u800c\u662f\u672c\u4f53\u3002\u56e0\u4e3a\u8bf8\u795e\u7684\u5e72\u6d89\uff0c\u8d8a\u5f3a\u7684\u6076\u9b54\u8d8a\u96be\u4ee5\u7a7f\u8d8a\u4f4d\u9762\u58c1\u969c\uff0c\u8981\u8ba9\u4e00\u53ea\u708e\u9b54\u7684\u672c\u4f53\u6765\u5230\u7269\u8d28\u754c\uff0c\u9700\u8981\u4ed8\u51fa\u5927\u91cf\u7684\u727a\u7272\u796d\u7940\uff0c\u6781\u5c11\u4f1a\u6709\u4eba\u8fd9\u4e48\u505a\u7684\uff0c\u5f97\u4e0d\u507f\u5931\u3002</p><p>\u5965\u5580\u65af\u4ee5\u524d\u6d3e\u6765\u7684\u90a3\u4e2a\u6076\u9b54\uff0c\u5e93\u6797\u590f\uff0c\u4e0d\u8fc7\u53ea\u662f\u8ff7\u8bf1\u9b54\u7f62\u4e86\uff0c\u5728\u6df1\u6e0a\u4e2d\u4f4d\u9636\u5c5e\u4e8e\u4e2d\u7b49\u504f\u4e0a\uff0c\u4e0d\u7b97\u4ec0\u4e48\u5389\u5bb3\u89d2\u8272\u3002\u8fd9\u5bb6\u4f19\u529e\u7838\u4e86\u4e8b\u60c5\uff0c\u60f9\u6012\u62c9\u6c83\u514b\uff0c\u5982\u4eca\u5df2\u7ecf\u88ab\u5deb\u5996\u53d8\u6210\u4e86\u4e2a\u50f5\u5c38\uff0c\u6b63\u5728\u5916\u9762\u9662\u5b50\u91cc\u5145\u5f53\u5b88\u536b\u3002</p><p>\u867d\u7136\u77e5\u9053\u5965\u5580\u65af\u80af\u5b9a\u4f1a\u6d3e\u65b0\u4f7f\u8005\u524d\u6765\uff0c\u4f46\u5deb\u5996\u4e5f\u6ca1\u60f3\u5230\u5c45\u7136\u4f1a\u662f\u4e00\u53ea\u708e\u9b54\u3002\u4e0d\u662f\u542c\u8bf4\u5965\u5580\u65af\u6b63\u5728\u6df1\u6e0a\u4e2d\u548c\u53e6\u5916\u4e00\u4f4d\u6076\u9b54\u9886\u4e3b\u683c\u62c9\u5179\u7279\u4ea4\u6218\u4e48\uff0c\u708e\u9b54\u8fd9\u79cd\u5f3a\u529b\u624b\u4e0b\uff0c\u5c45\u7136\u4f1a\u88ab\u6253\u53d1\u6765\u7269\u8d28\u754c\u505a\u4f7f\u8005\uff1f</p><p>\u201c\u5965\u5580\u65af\u6d3e\u4f60\u6765\u7684\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u662f\uff0c\u8c28\u4ee3\u8868\u4ea1\u7075\u541b\u738b\u5411\u60a8\u81f4\u6b49\u3002\u201d\u6b27\u51ef\u7b80\u6377\u5730\u56de\u7b54\uff0c\u4ed6\u6765\u4e4b\u524d\u5c31\u542c\u5965\u5580\u65af\u8bf4\u8fc7\uff0c\u62c9\u6c83\u514b\u559c\u6b22\u76f4\u622a\u4e86\u5f53\u7684\u4ea4\u8c08\u65b9\u5f0f\uff0c\u8ba8\u538c\u7f57\u5506\u548c\u7ed5\u5708\u5b50\u3002</p><p>\u89c1\u5deb\u5996\u6ca1\u4ec0\u4e48\u53cd\u5e94\uff0c\u6b27\u51ef\u4f38\u51fa\u53f3\u624b\uff0c\u4e03\u6839\u624b\u6307\u644a\u5f00\uff0c\u708e\u9b54\u7684\u638c\u5fc3\u4e0a\uff0c\u73b0\u51fa\u4e00\u5757\u8584\u91d1\u788e\u7247\u6765\uff0c\u4e0a\u9762\u9690\u9690\u7ea6\u7ea6\u5730\u6709\u94f6\u8272\u7684\u6587\u5b57\u5728\u6d41\u52a8\u53d8\u5e7b\u3002\u201c\u4ea1\u7075\u541b\u738b\u6258\u6211\u5411\u5927\u5965\u672f\u5e08\u9601\u4e0b\u5448\u4e0a\u4e00\u4ef6\u5c0f\u5c0f\u7684\u793c\u7269\uff0c\u201d\u708e\u9b54\u8f7b\u63cf\u6de1\u5199\u5730\u8bf4\uff0c\u201c\u5e0c\u671b\u80fd\u5f25\u8865\u56e0\u4e3a\u5e93\u6797\u590f\u7684\u611a\u8822\uff0c\u7ed9\u6211\u4eec\u5f7c\u6b64\u4e4b\u95f4\u9020\u6210\u7684\u4e00\u4e9b\u5c0f\u5c0f\u4e0d\u6109\u5feb\u3002\u201d</p><p>\u5deb\u5996\u7684\u6ce8\u610f\u529b\u7acb\u523b\u88ab\u90a3\u5757\u8584\u91d1\u788e\u7247\u7ed9\u5438\u5f15\u8fc7\u53bb\u4e86\uff0c\u4ed6\u7684\u5589\u5499\u91cc\u53d1\u51fa\u683c\u683c\u7684\u8f7b\u5fae\u54cd\u58f0\uff0c\u8fd9\u53ea\u5728\u4e24\u79cd\u60c5\u51b5\u4e0b\u53d1\u751f\uff1a\u6216\u8005\uff0c\u662f\u4ed6\u9047\u4e0a\u4e86\u4ec0\u4e48\u5927\u96be\u9898\uff0c\u4e3e\u68cb\u4e0d\u5b9a\uff1b\u6216\u8005\uff0c\u662f\u4ed6\u7684\u5fc3\u60c5\u6781\u5ea6\u6fc0\u52a8\u3002</p><p>\u65e0\u8bba\u54ea\u4e00\u79cd\u60c5\u51b5\uff0c\u5bf9\u4e8e\u4e00\u4e2a\u4e24\u5343\u5e74\u7684\u8001\u5deb\u5996\u6765\u8bf4\uff0c\u90fd\u662f\u6781\u5176\u7f55\u89c1\u7684\u3002</p><p>\u4ed6\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u62ac\u8d77\u53f3\u81c2\uff0c\u767d\u9aa8\u624b\u638c\u4ece\u8896\u4e2d\u4f38\u51fa\uff0c\u98df\u6307\u8f7b\u8f7b\u4e00\u5c48\uff0c\u4ee5\u8089\u773c\u770b\u4e0d\u6e05\u7684\u901f\u5ea6\u5728\u7a7a\u6c14\u4e2d\u753b\u4e86\u4e2a\u5c0f\u5c0f\u7684\u5492\u7b26\u3002</p><p>\u8584\u91d1\u788e\u7247\u4ece\u708e\u9b54\u7684\u638c\u5fc3\u7f13\u7f13\u5347\u8d77\uff0c\u63a5\u7740\u671d\u5deb\u5996\u5e73\u5e73\u98de\u6765\uff0c\u5728\u9762\u524d\u81ea\u52a8\u505c\u4f4f\u3002\u5deb\u5996\u4f38\u624b\u5c06\u8584\u91d1\u788e\u7247\u6293\u4f4f\uff0c\u7528\u98df\u6307\u548c\u62c7\u6307\u5939\u7740\uff0c\u4f4e\u5934\u4ed4\u7ec6\u8fa8\u8ba4\u7740\u4e0a\u9762\u6d41\u52a8\u4e0d\u606f\u7684\u94f6\u8272\u6587\u5b57\u3002\u548c\u6b27\u51ef\u4e0d\u540c\uff0c\u4ed6\u6e05\u695a\u5730\u8ba4\u8bc6\u8fd9\u4e0a\u9762\u5199\u7740\u4ec0\u4e48\uff0c\u8fd9\u548c\u8bed\u8a00\u80fd\u529b\u65e0\u5173\uff0c\u4ec5\u4ec5\u56e0\u4e3a\u4ed6\u66fe\u7ecf\u662f\u8010\u745f\u5e1d\u56fd\u7684\u5927\u5965\u672f\u5e08\u3002</p><p>\u8fd9\u6b63\u662f\u4ed6\u4e24\u5343\u5e74\u6765\u4e00\u76f4\u5728\u5bfb\u627e\u7684\u4e1c\u897f\u2026\u2026\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u5176\u4e2d\u4e4b\u4e00\u3002</p><p>\u5deb\u5996\u6536\u8d77\u8584\u91d1\u788e\u7247\uff0c\u770b\u7740\u708e\u9b54\uff0c\u540e\u8005\u6b63\u5728\u5fae\u7b11\u3002\u201c\u5e0c\u671b\u60a8\u80fd\u559c\u6b22\u8fd9\u4efd\u793c\u7269\u3002\u201d\u6b27\u51ef\u8bf4\uff0c\u867d\u7136\u4ed6\u77e5\u9053\u8fd9\u662f\u5e9f\u8bdd\u3002\u867d\u7136\u5bf9\u4e8e\u7269\u8d28\u754c\u7684\u7edd\u5927\u591a\u6570\u4eba\u6765\u8bf4\uff0c\u8fd9\u5757\u8584\u91d1\u788e\u7247\u90fd\u662f\u6beb\u65e0\u7528\u5904\u7684\u5e9f\u7269\uff0c\u4f46\u5bf9\u67d0\u4e9b\u4eba\u2014\u2014\u6bd4\u5982\u62c9\u6c83\u514b\u2014\u2014\u6765\u8bf4\uff0c\u8fd9\u5c31\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u7269\u3002</p><p>\u5965\u5580\u65af\u715e\u8d39\u82e6\u5fc3\u624d\u6253\u63a2\u5230\u8fd9\u5757\u788e\u7247\u843d\u5230\u591a\u745e\u68ee\u624b\u4e2d\uff0c\u6d3e\u6b27\u51ef\u53d6\u6765\uff0c\u5176\u95f4\u8fd8\u5dee\u70b9\u88ab\u8679\u5f69\u9f99\u745e\u6069\u65af\u5766\u6377\u8db3\u5148\u767b\u3002\u82b1\u8d39\u8fd9\u4e48\u591a\u529f\u592b\uff0c\u81ea\u7136\u662f\u6709\u628a\u63e1\u80fd\u8ba9\u62c9\u6c83\u514b\u6ee1\u610f\u7684\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\uff0c\u62c9\u6c83\u514b\u975e\u5e38\u6ee1\u610f\u2026\u2026\u4e0d\u8fc7\u6bd4\u8d77\u5e93\u6797\u590f\u90a3\u4e2a\u7b28\u86cb\u7ed9\u4ed6\u9020\u6210\u7684\u635f\u5931\uff0c\u8fd9\u70b9\u8865\u507f\u4f3c\u4e4e\u8fd8\u4e0d\u591f\u3002</p></article>"}}}]);
