(window.webpackJsonp=window.webpackJsonp||[]).push([[1341],{1364:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u8282 \u706b\u67aa",content:"<article><h2>\u7b2c\u516d\u8282 \u706b\u67aa</h2><p>\u4f11\u606f\u4e4b\u540e\uff0c\u4e00\u884c\u4eba\u7ee7\u7eed\u4e0a\u8def\u524d\u8fdb\u3002\u743c\u6069\u76f8\u4fe1\u81ea\u5df1\u7528\u79d8\u6cd5\u773c\u5077\u7aa5\u7684\u4e8b\u60c5\u5df2\u7ecf\u88ab\u79d1\u6069\u5bdf\u89c9\uff0c\u4f46\u6c83\u91d1\u7267\u5e08\u7edd\u53e3\u4e0d\u63d0\u6b64\u4e8b\uff0c\u800c\u4e14\u4e5f\u6ca1\u6709\u8868\u73b0\u51fa\u4efb\u4f55\u77e5\u9053\u7684\u8ff9\u8c61\u548c\u53e3\u98ce\uff0c\u751a\u81f3\u6ca1\u6709\u671d\u743c\u6069\u591a\u770b\u4e00\u773c\u3002\u743c\u6069\u81ea\u7136\u4e5f\u4e0d\u4f1a\u63d0\u53ca\uff0c\u53ea\u662f\u6697\u4e2d\u8b66\u60d5\uff0c\u6bcf\u5f53\u60f3\u8d77\u8fd9\u4e2a\u7267\u5e08\u541e\u98df\u5de8\u9b54\u8111\u6d46\u7684\u90a3\u4e00\u5e55\uff0c\u4ed6\u5c31\u9690\u9690\u6709\u4e9b\u60f3\u5455\u5410\u7684\u611f\u89c9\u3002</p><p>\u8fd9\u79cd\u884c\u4e3a\uff0c\u7edd\u975e\u4e00\u4e2a\u5584\u826f\u4eba\u58eb\u6240\u4e3a\uff0c\u636e\u743c\u6069\u6240\u77e5\uff0c\u552f\u6709\u4e00\u4e9b\u90aa\u9b54\u7684\u4fe1\u5f92\uff0c\u624d\u4f1a\u505a\u8fd9\u79cd\u7c7b\u4f3c\u7684\u4e8b\u60c5\u3002\u6df1\u6e0a\u4e09\u5927\u6076\u9b54\u9886\u4e3b\u4e4b\u4e00\u7684\u72c4\u9b54\u9ad8\u6839\uff0c\u4ed6\u7684\u4fe1\u5f92\u5728\u796d\u7940\u65f6\u5c31\u4f1a\u541e\u98df\u53d7\u5bb3\u8005\u7684\u8111\u6d46\u3002</p><p>\u4f46\u8fd9\u4f4d\u82cf\u5c3c\xb7\u79d1\u6069\u5e76\u975e\u90aa\u9b54\u4fe1\u5f92\uff0c\u4ed6\u786e\u786e\u5b9e\u5b9e\u662f\u6c83\u91d1\u6559\u4f1a\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u795e\u6bbf\u51fa\u5177\u7684\u8bc1\u660e\uff0c\u4ee5\u53ca\u4ed6\u7684\u201c\u91d1\u773c\u4f7f\u8005\u201d\u8eab\u4efd\uff0c\u90fd\u8bf4\u660e\u4e86\u8fd9\u70b9\u3002</p><p>\u743c\u6069\u4e0d\u660e\u767d\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u4f46\u663e\u7136\u8fd9\u91cc\u9762\u6709\u540d\u5802\uff0c\u4ed6\u8b66\u60d5\u7740\uff0c\u7559\u795e\u89c2\u5bdf\u79d1\u6069\u7684\u4e3e\u52a8\u3002\u6885\u83f2\u65af\u4f3c\u4e4e\u4e5f\u540c\u6837\uff0c\u5728\u542c\u5230\u743c\u6069\u8f6c\u8ff0\u79d8\u6cd5\u773c\u770b\u5230\u7684\u60c5\u666f\u540e\uff0c\u5979\u8868\u9762\u4e0a\u4e0d\u52a8\u58f0\u8272\uff0c\u4eff\u4f5b\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u53d1\u751f\uff0c\u4f46\u5728\u884c\u52a8\u4e2d\u95f4\uff0c\u5374\u6709\u610f\u65e0\u610f\u5730\u603b\u662f\u4fdd\u6301\u5728\u79d1\u6069\u7684\u5de6\u4fa7\u2014\u2014\u4e00\u822c\u4eba\u90fd\u4e60\u60ef\u7528\u53f3\u624b\uff0c\u5de6\u4fa7\u662f\u76f8\u5bf9\u9632\u5fa1\u8584\u5f31\u7684\u73af\u8282\u3002\u6885\u83f2\u65af\u548c\u79d1\u6069\u5e76\u80a9\u800c\u884c\uff0c\u7267\u5e08\u7684\u5de6\u4fa7\u5c31\u662f\u5979\u7684\u53f3\u4fa7\uff0c\u7267\u5e08\u7684\u8584\u5f31\u73af\u8282\uff0c\u5c31\u662f\u5979\u7684\u4f18\u52bf\u533a\u57df\u3002</p><p>\u5982\u679c\u771f\u8981\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6885\u83f2\u65af\u4fbf\u80fd\u5728\u77ac\u95f4\u51fa\u624b\u5c06\u4ed6\u5236\u670d\u6216\u8005\u65a9\u6740\u3002</p><p>\u7ec4\u961f\u51fa\u6765\u529e\u4e8b\uff0c\u5730\u65b9\u8fd8\u6ca1\u5230\uff0c\u5c31\u5df2\u7ecf\u76f8\u4e92\u63d0\u9632\uff0c\u8fd9\u8ba9\u743c\u6069\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u53ef\u7b11\uff0c\u4f46\u73b0\u5b9e\u5982\u6b64\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u522b\u7684\u8bdd\u597d\u8bf4\u3002\u63d0\u9632\u522b\u4eba\uff0c\u591a\u7559\u5fc3\u773c\uff0c\u603b\u6bd4\u83ab\u540d\u5176\u5999\u88ab\u4eba\u9634\u4e86\u597d\u3002\u8fd9\u4e2a\u6c83\u91d1\u7267\u5e08\uff0c\u786e\u5b9e\u9887\u4e3a\u53ef\u7591\u3002</p><p>\u5bf9\u4e8e\u6885\u83f2\u65af\u9690\u9690\u7ea6\u7ea6\u7684\u9632\u8303\uff0c\u79d1\u6069\u5012\u663e\u5f97\u82e5\u65e0\u5176\u4e8b\uff0c\u4ed6\u663e\u7136\u770b\u5f97\u51fa\u6765\uff0c\u4f46\u4f3c\u4e4e\u5168\u4e0d\u5728\u610f\u3002\u81f3\u4e8e\u5176\u4ed6\u4eba\uff0c\u4e8b\u4e0d\u5173\u5df1\uff0c\u66f4\u52a0\u4e0d\u4f1a\u7406\u4f1a\u3002\u590f\u62c9\u975e\u5728\u524d\u9762\u5f15\u8def\uff0c\u9686\u5947\u548c\u8d1d\u7eb3\u7d22\u90fd\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u4e00\u884c\u4eba\u4fbf\u5728\u8fd9\u6c89\u95f7\u5f97\u6709\u4e9b\u538b\u6291\u7684\u6c14\u6c1b\u4e2d\u524d\u8fdb\u3002</p><p>\u4ed6\u4eec\u518d\u6ca1\u6709\u9047\u4e0a\u5de8\u9b54\uff0c\u4e5f\u6ca1\u6709\u9047\u4e0a\u5176\u4ed6\u602a\u7269\uff0c\u4e00\u8def\u9887\u4e3a\u5b89\u9759\u3002\u5c06\u81f3\u508d\u665a\uff0c\u5915\u9633\u6e10\u5782\uff0c\u8be5\u627e\u4e2a\u5730\u65b9\u5b89\u8425\u4f11\u606f\u4e86\u3002</p><p>\u201c\u5c31\u5728\u8fd9\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u8fd9\u662f\u4e00\u5757\u6bd4\u8f83\u5f00\u9614\u7684\u9ad8\u5904\uff0c\u4e07\u4e00\u665a\u4e0a\u88ab\u602a\u7269\u7a81\u88ad\uff0c\u4e5f\u80fd\u53ca\u65f6\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4eca\u665a\u6211\u548c\u79d1\u6069\u5b88\u591c\uff0c\u201d\u5979\u884c\u4f7f\u7740\u961f\u957f\u7684\u804c\u8d23\uff0c\u5206\u6d3e\u7740\u4efb\u52a1\uff0c\u201c\u6211\u5b88\u4e0a\u534a\u591c\uff0c\u79d1\u6069\u5b88\u4e0b\u534a\u591c\u3002\u201d</p><p>\u6c83\u91d1\u7267\u5e08\u5168\u65e0\u5f02\u8bae\u3002\u4f17\u4eba\u53d6\u4e0b\u5305\u88f9\uff0c\u51c6\u5907\u751f\u8d77\u706b\u5806\uff0c\u6b63\u5fd9\u788c\u95f4\uff0c\u9661\u5730\u542c\u89c1\u8fdc\u5904\u4e18\u9675\u80cc\u540e\uff0c\u4f20\u6765\u4e00\u58f0\u9707\u8033\u6b32\u804b\u7684\u7206\u70b8\u58f0\u3002</p><p>\u203b\u203b\u203b</p><p>\u6240\u6709\u4eba\u90fd\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u7206\u70b8\u58f0\u731b\u5730\u9707\u4e86\u4e00\u4e0b\uff0c\u5f85\u5f97\u53cd\u5e94\u8fc7\u6765\uff0c\u6b63\u8981\u8fc7\u53bb\u5bdf\u770b\uff0c\u5c31\u89c1\u4e00\u4e2a\u4eba\u5f71\u4ece\u4e18\u9675\u540e\u9762\u51fa\u73b0\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u6839\u53c8\u957f\u53c8\u7c97\u7684\u9ed1\u7ba1\u5b50\uff0c\u5934\u4e0a\u6234\u7740\u9876\u9ec4\u8272\u5bbd\u6cbf\u5e3d\uff0c\u538b\u5f97\u9887\u4f4e\uff0c\u770b\u4e0d\u6e05\u8138\uff0c\u4e0a\u8eab\u7a7f\u7740\u4e00\u4ef6\u571f\u7070\u8272\u9a6c\u7532\uff0c\u4e0b\u8eab\u7a7f\u7740\u6df1\u84dd\u8272\u957f\u88e4\uff0c\u80cc\u4e0a\u8fd8\u80cc\u7740\u4e00\u4e2a\u9f13\u9f13\u56ca\u56ca\u7684\u9ed1\u8272\u5927\u5305\u88f9\uff0c\u6b63\u614c\u614c\u5f20\u5f20\u671d\u8fd9\u8fb9\u8dd1\u8fc7\u6765\u3002</p><p>\u743c\u6069\u6b63\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u5c31\u542c\u89c1\u51e0\u58f0\u5486\u54ee\uff0c\u7d27\u63a5\u7740\u770b\u89c1\u56db\u4e2a\u98df\u4eba\u9b54\u4ece\u4e18\u9675\u540e\u9762\u8ffd\u8fc7\u6765\u3002</p><p>\u98df\u4eba\u9b54\u4e5f\u662f\u79cd\u9887\u4e3a\u5e38\u89c1\u7684\u602a\u7269\uff0c\u5e7d\u5f71\u754c\u91cc\u81ea\u7136\u6ca1\u6709\uff0c\u4f46\u743c\u6069\u5728\u5deb\u5e08\u5b66\u6821\u91cc\u66fe\u7ecf\u89c1\u8fc7\u6807\u672c\uff0c\u4e5f\u9605\u8bfb\u8fc7\u76f8\u5173\u7684\u8d44\u6599\uff0c\u6240\u4ee5\u4e00\u773c\u5c31\u8ba4\u4e86\u51fa\u6765\u3002\u98df\u4eba\u9b54\u8eab\u9ad8\u548c\u5de8\u9b54\u5dee\u4e0d\u591a\uff0c\u4f46\u5374\u80a5\u80d6\u2014\u2014\u6216\u8005\u8bf4\u5f3a\u58ee\u591a\u4e86\uff0c\u5b83\u4eec\u7684\u80a4\u8272\u81ea\u6697\u9ec4\u8272\u81f3\u9ed1\u8910\u8272\u4e0d\u7b49\uff0c\u8eab\u4e0a\u957f\u6ee1\u4e86\u9686\u8d77\u7684\u6df1\u8272\u7624\u7ed3\u3002\u98df\u4eba\u9b54\u5634\u5927\u3001\u8116\u5b50\u7c97\u3001\u5934\u53d1\u957f\uff0c\u9f3b\u5b50\u65e2\u9614\u53c8\u6241\u5e73\uff0c\u548c\u5de8\u9b54\u7684\u957f\u9f3b\u5b50\u5b8c\u5168\u76f8\u53cd\uff0c\u4ed6\u4eec\u7684\u773c\u775b\u662f\u7d2b\u8272\u7684\uff0c\u77b3\u5b54\u767d\u8272\uff0c\u7259\u9f7f\u548c\u722a\u5b50\u5168\u662f\u6df1\u9ed1\u8272\u3002\u5b9e\u9a8c\u5ba4\u91cc\u7684\u6807\u672c\u81ea\u7136\u662f\u7ecf\u8fc7\u4e86\u9664\u81ed\u5904\u7406\uff0c\u518d\u8bf4\u90fd\u4e0d\u77e5\u8fc7\u4e86\u51e0\u767e\u5e74\u4e86\uff0c\u4f46\u636e\u6559\u6388\u8bf4\uff0c\u771f\u5b9e\u7684\u98df\u4eba\u9b54\uff0c\u8eab\u4e0a\u6709\u6781\u5176\u6d53\u91cd\u7684\u81ed\u6c14\uff0c\u62b5\u6297\u529b\u5dee\u7684\u4eba\uff0c\u9760\u5f97\u8fd1\u4e86\u90fd\u4f1a\u5f53\u573a\u5455\u5410\u51fa\u6765\u3002</p><p>\u9664\u4e86\u5916\u5f62\u4e0a\u4e00\u80d6\u4e00\u7626\uff0c\u4e00\u4e30\u6ee1\u4e00\u5e72\u67af\uff0c\u8fe5\u7136\u76f8\u5f02\uff0c\u98df\u4eba\u9b54\u548c\u5de8\u9b54\u8fd8\u662f\u9887\u6709\u76f8\u50cf\u4e4b\u5904\u7684\u3002\u8bba\u529b\u6c14\uff0c\u8bba\u5f3a\u58ee\uff0c\u98df\u4eba\u9b54\u90fd\u4e0d\u6bd4\u5de8\u9b54\u900a\u8272\uff0c\u800c\u4e14\u5b83\u4eec\u4e00\u6837\u90fd\u662f\u5f31\u667a\uff0c\u5f53\u7136\u8fd9\u662f\u4ee5\u4eba\u7c7b\u7684\u6807\u51c6\u6765\u770b\u3002\u5de8\u9b54\u6bd4\u98df\u4eba\u9b54\u5f3a\u7684\u5730\u65b9\uff0c\u5728\u4e8e\u5b83\u4eec\u5f3a\u608d\u5230\u53d8\u6001\u7684\u518d\u751f\u80fd\u529b\uff1b\u4e0d\u8fc7\u4f20\u8bf4\u98df\u4eba\u9b54\u662f\u4e00\u79cd\u5f88\u5bb9\u6613\u57fa\u56e0\u53d8\u5f02\u7684\u79cd\u65cf\u2014\u2014\u636e\u6559\u6388\u8bf4\uff0c\u522b\u770b\u666e\u901a\u7684\u98df\u4eba\u9b54\u90fd\u662f\u7b28\u86cb\uff0c\u5b83\u4eec\u5f53\u4e2d\u5f80\u5f80\u80fd\u53d8\u5f02\u51fa\u667a\u529b\u4e0d\u900a\u8272\u4eba\u7c7b\uff0c\u751a\u81f3\u7565\u6709\u8fc7\u4e4b\u7684\u98df\u4eba\u9b54\u5deb\u5e08\u6765\uff0c\u80fd\u65e0\u5e08\u81ea\u901a\u5730\u5b66\u4f1a\u4e00\u4e9b\u7b80\u5355\u4f46\u5374\u5f88\u6709\u6740\u4f24\u529b\u7684\u9b54\u6cd5\u3002</p><p>\u4e0d\u8fc7\u8fd9\u56db\u4e2a\u98df\u4eba\u9b54\u91cc\uff0c\u6ca1\u6709\u53d8\u5f02\u7684\u98df\u4eba\u9b54\u5deb\u5e08\u3002\u8d44\u6599\u4e0a\u660e\u786e\u5f3a\u8c03\u8fc7\uff0c\u98df\u4eba\u9b54\u5deb\u5e08\u8fd9\u79cd\u53d8\u5f02\u54c1\u79cd\u7684\u76f8\u8c8c\u5916\u5f62\u548c\u666e\u901a\u98df\u4eba\u9b54\u662f\u6709\u5f88\u5927\u533a\u522b\u7684\uff0c\u5b83\u4eec\u66f4\u9ad8\u3001\u66f4\u80d6\u3001\u66f4\u5f3a\u58ee\uff0c\u5934\u53d1\u548c\u76ae\u80a4\u90fd\u5448\u6d45\u84dd\u6216\u8005\u6d45\u7eff\u8272\uff0c\u989d\u5934\u4e0a\u8fd8\u4f1a\u957f\u7740\u4e00\u5bf9\u4e73\u767d\u8272\u7684\u77ed\u89d2\u3002</p><p>\u770b\u89c1\u98df\u4eba\u9b54\u8ffd\u8d76\u4eba\u7c7b\uff0c\u867d\u7136\u4e0d\u77e5\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u743c\u6069\u672c\u80fd\u5730\u5c31\u4f38\u624b\u4ece\u53e3\u888b\u91cc\u53d6\u6cd5\u672f\u6750\u6599\uff0c\u51c6\u5907\u65bd\u6cd5\u6551\u4eba\u3002\u4f46\u6709\u4eba\u6bd4\u4ed6\u52a8\u4f5c\u66f4\u5feb\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u63d0\u5251\u51b2\u4e0a\u524d\u53bb\u3002\u5c11\u5973\u7ed5\u8fc7\u90a3\u4e2a\u6234\u9ec4\u5e3d\u5b50\u7684\u4eba\uff0c\u62e6\u4f4f\u98df\u4eba\u9b54\uff0c\u4e3e\u8d77\u5251\u6765\u3002</p><p>\u98df\u4eba\u9b54\u624b\u6301\u6728\u68d2\uff0c\u6b63\u547c\u54e7\u547c\u54e7\u5730\u5598\u7740\u7c97\u6c14\uff0c\u5634\u89d2\u6ea2\u51fa\u767d\u6cab\uff0c\u8ffd\u8d76\u7740\u90a3\u4e2a\u9ec4\u5e3d\u5b50\u5bb6\u4f19\uff0c\u7a81\u7136\u89c1\u6709\u4eba\u51fa\u6765\u62e6\u8def\uff0c\u4ee5\u5b83\u4eec\u7684\u667a\u529b\uff0c\u4e5f\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u76f4\u63a5\u6325\u68d2\u5c31\u7838\u3002</p><p>\u4e00\u9635\u98ce\u6070\u597d\u5439\u8fc7\uff0c\u5c06\u98df\u4eba\u9b54\u8eab\u4e0a\u7684\u81ed\u6c14\u5377\u5230\u743c\u6069\u7b49\u4eba\u8fd9\u8fb9\u6765\uff0c\u6240\u6709\u4eba\u7684\u8138\u8272\u51e0\u4e4e\u90fd\u53d8\u4e86\u53d8\uff0c\u8d1d\u7eb3\u7d22\u5148\u751f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5f2f\u4e0b\u8170\u53bb\u51e0\u4e4e\u8981\u5455\u5410\u3002\u743c\u6069\u548c\u79d1\u6069\u603b\u7b97\u5b9a\u529b\u5f3a\u4e9b\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u614c\u5fd9\u9000\u5f00\uff0c\u5c31\u770b\u89c1\u6b66\u50e7\u4e5f\u51b2\u4e86\u51fa\u53bb\uff0c\u770b\u6837\u5b50\u662f\u6253\u7b97\u901f\u6218\u901f\u51b3\uff0c\u5e2e\u6885\u83f2\u65af\u8d76\u5feb\u628a\u8fd9\u56db\u53ea\u98df\u4eba\u9b54\u5bf9\u4ed8\u6389\u3002</p><p>\u6885\u83f2\u65af\u8d34\u8fd1\u98df\u4eba\u9b54\uff0c\u5374\u4eff\u4f5b\u534a\u70b9\u4e0d\u53d7\u81ed\u6c14\u5f71\u54cd\uff0c\u94f6\u5251\u8fde\u6325\uff0c\u8f6c\u773c\u95f4\u4fbf\u780d\u65ad\u4e86\u4e00\u53ea\u98df\u4eba\u9b54\u7684\u80f3\u818a\u3002\u9686\u5947\u4e0a\u524d\u52a9\u9635\uff0c\u6325\u62f3\u731b\u51fb\uff0c\u53ea\u542c\u5f97\u9aa8\u9abc\u7206\u88c2\u58f0\u8fde\u54cd\uff0c\u4e00\u53ea\u98df\u4eba\u9b54\u88ab\u4ed6\u91cd\u62f3\u8f70\u4e2d\uff0c\u987f\u65f6\u4eff\u4f5b\u5168\u8eab\u788e\u88c2\u4e86\u4e00\u822c\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u8f6f\u762b\u762b\u5730\u5012\u5730\u3002\u8fd9\u4e24\u4eba\u90fd\u79f0\u5f97\u4e0a\u662f\u7b2c\u4e00\u7b49\u7684\u6218\u58eb\uff0c\u5bf9\u4ed8\u90a3\u53ea\u88ab\u7cbe\u91d1\u9aa8\u9abc\u5f3a\u5316\u8fc7\u7684\u53cc\u5934\u5de8\u9b54\u6709\u4e9b\u788d\u624b\u788d\u811a\uff0c\u78b0\u4e0a\u8fd9\u79cd\u98df\u4eba\u9b54\u5374\u8f7b\u677e\u81f3\u6781\u3002</p><p>\u4e0d\u5230\u7247\u523b\uff0c\u4e09\u53ea\u98df\u4eba\u9b54\u5df2\u7ecf\u88ab\u6740\u6b7b\uff0c\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u53ea\u89c1\u52bf\u4e0d\u5999\uff0c\u8f6c\u8eab\u5c31\u9003\u3002\u6885\u83f2\u65af\u6b63\u8981\u8ffd\u8d76\uff0c\u5374\u542c\u5f97\u4fa7\u540e\u65b9\u4f20\u6765\u4e00\u58f0\u60ca\u5929\u52a8\u5730\u7684\u7206\u70b8\uff0c\u706b\u5149\u8000\u773c\uff0c\u4e00\u9053\u9ed1\u5149\u4ece\u773c\u524d\u5212\u8fc7\u3002</p><p>\u548c\u524d\u9762\u90a3\u6b21\u4e00\u6837\uff0c\u4f46\u8fd9\u6b21\u5c31\u5728\u8eab\u8fb9\uff0c\u6240\u4ee5\u58f0\u97f3\u66f4\u52a0\u9707\u8033\u6b32\u804b\uff0c\u8fde\u6885\u83f2\u65af\u548c\u9686\u5947\u8fd9\u7b49\u4eba\u7269\u90fd\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u66f4\u522b\u8bf4\u5728\u540e\u9762\u65c1\u89c2\u7684\u743c\u6069\u7b49\u4eba\uff0c\u611f\u89c9\u8fde\u5730\u9762\u90fd\u5fae\u5fae\u98a4\u4e86\u4e00\u98a4\u3002\u53ea\u89c1\u521a\u624d\u88ab\u98df\u4eba\u9b54\u8ffd\u5f97\u72fc\u72c8\u9003\u8dd1\u7684\u90a3\u4e2a\u9ec4\u5e3d\u5b50\u5bb6\u4f19\uff0c\u6b63\u4e3e\u7740\u4ed6\u624b\u91cc\u90a3\u6839\u9ed1\u4e4e\u4e4e\u7684\u7c97\u7ba1\u5b50\uff0c\u5bf9\u7740\u524d\u65b9\uff0c\u90a3\u53ea\u9003\u8dd1\u7684\u98df\u4eba\u9b54\u5df2\u7ecf\u4ec6\u8857\u5012\u5730\uff0c\u80cc\u4e0a\u88ab\u70b8\u5f00\u4e00\u4e2a\u5de8\u5927\u7684\u6a21\u7cca\u8840\u6d1e\uff0c\u5374\u6ca1\u6709\u5f53\u5373\u6bd9\u547d\uff0c\u6323\u8d77\u6765\u8fd8\u8981\u9003\u8dd1\uff0c\u88ab\u6885\u83f2\u65af\u8d76\u4e0a\u4e00\u5251\u780d\u4e0b\u5934\u6765\u3002</p><p>\u8fd9\u662f\u2026\u2026\u706b\u67aa\uff1f</p><p>\u743c\u6069\u77e5\u9053\u8fd9\u4e2a\u4e16\u754c\u4e5f\u6709\u706b\u836f\uff0c\u662f\u5de5\u827a\u4e0e\u953b\u9020\u4e4b\u795e\u8d21\u5fb7\u53d1\u660e\u7684\uff0c\u4f20\u6388\u7ed9\u4fe1\u5f92\uff0c\u6d41\u4f20\u51fa\u6765\uff0c\u4f46\u6548\u679c\u5f88\u5dee\uff0c\u56e0\u4e3a\u4f7f\u7528\u65f6\u70df\u96fe\u5f88\u5927\uff0c\u6240\u4ee5\u53c8\u79f0\u4e3a\u201c\u53d1\u70df\u706b\u836f\uff08Smokepowder\uff09\u201d\uff0c\u5e76\u4e0d\u5355\u7eaf\u662f\u79d1\u6280\u7684\u4ea7\u7269\uff0c\u91cc\u9762\u6df7\u6742\u4e86\u4e00\u4e9b\u9b54\u6cd5\u7684\u6210\u679c\uff0c\u4e5f\u7b97\u662f\u70bc\u91d1\u672f\u7684\u7814\u7a76\u8303\u7574\u3002\u9634\u9b42\u57ce\u62ff\u5230\u706b\u836f\u914d\u65b9\uff0c\u751a\u81f3\u8fd8\u8fdb\u4e00\u6b65\u8bd5\u9a8c\u7814\u5236\u51fa\u4e86\u706b\u70ae\uff0c\u4f46\u6548\u679c\u5b9e\u5728\u4e0d\u6562\u606d\u7ef4\uff0c\u5c04\u7a0b\u867d\u7136\u5f88\u60ca\u4eba\uff0c\u5a01\u529b\u5374\u8c08\u4e0d\u4e0a\u5f3a\uff0c\u800c\u4e14\u636e\u8bf4\u53ea\u80fd\u8fde\u53d1\u5341\u6b21\uff0c\u7136\u540e\u8981\u51b7\u5374\u5f88\u4e45\uff0c\u5426\u5219\u5c31\u4f1a\u70b8\u819b\u3002</p><p>\u743c\u6069\u77e5\u9053\u8fd9\u5176\u4e2d\u80af\u5b9a\u6709\u4ec0\u4e48\u6280\u672f\u4e0a\u7684\u95ee\u9898\uff0c\u4f46\u4ed6\u65e2\u975e\u519b\u4e8b\u7231\u597d\u8005\u53c8\u975e\u706b\u70ae\u4e13\u5bb6\uff0c\u81ea\u7136\u61d2\u5f97\u591a\u7ba1\u3002\u706b\u836f\u53d1\u660e\u5e76\u4e0d\u4e45\uff0c\u8ddd\u4eca\u4e0d\u8fc7\u5341\u4f59\u5e74\uff0c\u4e5f\u6ca1\u53d1\u5c55\u51fa\u592a\u591a\u7684\u76f8\u5173\u4ea7\u7269\uff0c\u9634\u9b42\u57ce\u7814\u5236\u51fa\u4e86\u706b\u70ae\uff0c\u636e\u8bf4\u8d21\u5fb7\u6559\u4f1a\u79d8\u5bc6\u7814\u5236\u51fa\u4e86\u4e00\u4e9b\u7c7b\u4f3c\u624b\u96f7\u7684\u4e1c\u897f\uff0c\u4f46\u4e5f\u53ea\u662f\u4f20\u95fb\uff0c\u66f4\u6ca1\u542c\u8bf4\u6709\u8c01\u53d1\u660e\u706b\u67aa\u4e86\u3002</p><p>\u5c31\u7b97\u662f\u4e2a\u519b\u4e8b\u5916\u884c\uff0c\u743c\u6069\u4e5f\u77e5\u9053\u5728\u5730\u7403\u4e0a\uff0c\u706b\u70ae\u662f\u6bd4\u706b\u67aa\u5148\u53d1\u660e\u51fa\u6765\u7684\uff0c\u706b\u67aa\u5176\u5b9e\u5c31\u53ef\u4ee5\u89c6\u4e3a\u7cbe\u7ec6\u5fae\u7f29\u7248\u7684\u706b\u70ae\u3002\u9634\u9b42\u57ce\u7814\u5236\u51fa\u706b\u70ae\u4e5f\u4e0d\u8fc7\u662f\u8fd1\u4e24\u5e74\u7684\u4e8b\u60c5\uff0c\u706b\u67aa\u6ca1\u9053\u7406\u8fd9\u4e48\u5feb\u5c31\u51fa\u73b0\u5427\u3002</p><p>\u4f46\u4e8b\u5b9e\u6446\u5728\u9762\u524d\uff0c\u8fd9\u4e2a\u9ec4\u5e3d\u5b50\u5bb6\u4f19\uff0c\u624b\u91cc\u62ff\u7740\u7684\uff0c\u5206\u660e\u5c31\u5e94\u8be5\u662f\u4e00\u652f\u706b\u67aa\u2026\u2026\u81f3\u5c11\uff0c\u662f\u4e00\u652f\u6bd4\u8f83\u539f\u59cb\u7684\u706b\u67aa\u3002</p><p>\u66f4\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u6b63\u88ab\u98df\u4eba\u9b54\u7684\u81ed\u6c14\u718f\u5f97\u51e0\u4e4e\u8981\u5455\u5410\u7684\u8d1d\u7eb3\u7d22\u88ab\u8fd9\u4e00\u58f0\u5de8\u54cd\u5413\u5230\uff0c\u518d\u770b\u90a3\u4e2a\u9ec4\u5e3d\u5b50\u5bb6\u4f19\uff0c\u7a81\u7136\u53eb\u51fa\u5bf9\u65b9\u7684\u540d\u5b57\u6765\uff0c\u201c\u9093\u80af\uff01\u201d\u4ed6\u53eb\u7740\uff0c\u201c\u600e\u4e48\u662f\u4f60\uff1f\u201d\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u7591\u60d1\u7740\uff0c\u76b1\u7740\u7709\u5934\uff0c\u4e00\u8fb9\u4f38\u624b\u5728\u9f3b\u5b50\u524d\u6247\u6765\u6247\u53bb\uff0c\u60f3\u628a\u81ed\u6c14\u8d76\u8d70\uff0c\u201c\u4f60\u600e\u4e48\u8dd1\u8fd9\u91cc\u6765\u4e86\uff1f\u201d</p><p>\u539f\u6765\u8fd8\u662f\u4e2a\u719f\u4eba\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u4e2a\u53eb\u9093\u80af\u7684\u5bb6\u4f19\u662f\u4e2a\u519b\u706b\u5546\u4eba\uff0c\u5728\u5b9d\u5251\u6d77\u6cbf\u5cb8\u4e00\u5e26\u505a\u51b6\u91d1\u548c\u706b\u836f\u751f\u610f\uff0c\u8fd8\u7b97\u9887\u6709\u51e0\u4e9b\u540d\u6c14\u3002\u4ed6\u662f\u535a\u5f97\u4e4b\u95e8\u7684\u672c\u5730\u5c45\u6c11\uff0c\u5728\u57ce\u5916\u7684\u8d1d\u5c14\u82df\u65af\u7279\u9547\u6709\u4e00\u5ea7\u4e0d\u5c0f\u7684\u5e84\u56ed\u3002\u8d1d\u7eb3\u7d22\u662f\u6668\u66e6\u795e\u6bbf\u7684\u9996\u5e2d\u7267\u5e08\uff0c\u7ecf\u5e38\u53c2\u52a0\u5404\u79cd\u4e0a\u6d41\u793e\u4f1a\u7684\u5bb4\u4f1a\uff0c\u6709\u65f6\u4fbf\u80fd\u9047\u4e0a\u9093\u80af\u3002\u53e6\u5916\u8fd9\u4f4d\u519b\u706b\u5546\u8fd8\u662f\u8d21\u5fb7\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u9694\u4e09\u5c94\u4e94\u5c31\u8981\u6765\u57ce\u5185\u7684\u8d21\u5fb7\u795e\u6bbf\u793c\u62dc\u2014\u2014\u800c\u8d21\u5fb7\u795e\u6bbf\u4e0e\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u6b63\u597d\u9694\u58c1\uff0c\u65f6\u95f4\u4e45\u4e86\uff0c\u4e5f\u80fd\u5e38\u5e38\u9047\u89c1\u3002</p><p>\u9093\u80af\u867d\u7136\u662f\u4e2a\u5546\u4eba\uff0c\u4f46\u6700\u70ed\u5fc3\u7684\u5012\u4e0d\u662f\u505a\u751f\u610f\u8d5a\u94b1\uff0c\u800c\u662f\u517c\u804c\u53d1\u660e\u5bb6\uff0c\u6700\u559c\u6b22\u7684\u5c31\u662f\u9f13\u6363\u5404\u79cd\u53d1\u660e\u2014\u2014\u800c\u4e14\u662f\u5371\u9669\u7684\u53d1\u660e\u3002\u81ea\u4ece\u706b\u836f\u88ab\u53d1\u660e\u51fa\u6765\uff0c\u9093\u80af\u6b23\u559c\u82e5\u72c2\uff0c\u8fde\u751f\u610f\u90fd\u4e0d\u505a\u4e86\uff0c\u5168\u4ea4\u7ed9\u624b\u4e0b\u53bb\u6253\u7406\uff0c\u81ea\u5df1\u9664\u4e86\u4f8b\u884c\u53bb\u8d21\u5fb7\u795e\u6bbf\uff0c\u5176\u4f59\u65f6\u95f4\u5c31\u4e00\u95e8\u5fc3\u601d\u8eb2\u5728\u5bb6\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u5728\u9f13\u6363\u4e9b\u4ec0\u4e48\u3002\u8d1d\u7eb3\u7d22\u5df2\u7ecf\u5f88\u4e45\u6ca1\u89c1\u5230\u4ed6\u4e86\uff0c\u4e0d\u60f3\u5728\u8fd9\u91cc\u649e\u4e0a\u3002</p><p>\u4ea4\u8c08\u51e0\u53e5\uff0c\u5f97\u77e5\u4ed6\u662f\u56e0\u4e3a\u9700\u8981\u4e00\u79cd\u73cd\u7a00\u77ff\u77f3\uff0c\u552f\u6709\u5de8\u9b54\u5c71\u8109\u91cc\u624d\u6709\uff0c\u6240\u4ee5\u8fdb\u5c71\u6765\u5bfb\u627e\u3002\u4ed6\u539f\u672c\u5e26\u4e86\u56db\u4e2a\u62a4\u536b\uff0c\u7ed3\u679c\u649e\u4e0a\u4e00\u7fa4\u98df\u4eba\u9b54\uff0c\u4e00\u901a\u4e71\u6218\uff0c\u56db\u4e2a\u62a4\u536b\u5168\u519b\u8986\u6ca1\uff0c\u53ea\u6709\u4ed6\u9003\u4e86\u51fa\u6765\u3002\u8981\u4e0d\u662f\u6b63\u597d\u649e\u4e0a\u6885\u83f2\u65af\u7b49\u4eba\u6551\u547d\uff0c\u4eca\u5929\u5c31\u8981\u6302\u5728\u8fd9\u91cc\u4e86\u3002</p><p>\u8fd9\u4e9b\u743c\u6069\u5012\u4e0d\u5173\u5fc3\uff0c\u9093\u80af\u5c31\u7b97\u6b7b\u4e86\u4ed6\u4e5f\u61d2\u5f97\u5728\u4e4e\u3002\u5deb\u5e08\u771f\u6b63\u6ce8\u610f\u7684\uff0c\u662f\u4ed6\u624b\u4e0a\u90a3\u6839\u9ed1\u4e4e\u4e4e\u7684\u7ba1\u5b50\u2026\u2026\u867d\u7136\u662f\u4e2a\u519b\u4e8b\u76f2\uff0c\u4f46\u743c\u6069\u4e5f\u80fd\u6e05\u695a\u5730\u8ba4\u51fa\u6765\uff0c\u8fd9\u5bb6\u4f19\u62ff\u7740\u7684\uff0c\u786e\u786e\u5b9e\u5b9e\u662f\u4e00\u6746\u706b\u67aa\u3002</p><p>\u6b64\u65f6\u5df2\u7ecf\u5929\u665a\uff0c\u9093\u80af\u81ea\u7136\u4e0d\u53ef\u80fd\u518d\u51fa\u5c71\u53bb\uff0c\u53ea\u80fd\u501f\u5bbf\u4e00\u5bbf\u3002\u591c\u95f4\u6b63\u662f\u5404\u79cd\u602a\u7269\u6d3b\u52a8\u7316\u7357\u7684\u65f6\u5019\uff0c\u4e07\u4e00\u518d\u649e\u4e0a\u51e0\u4e2a\u98df\u4eba\u9b54\uff0c\u4ed6\u5c31\u4e00\u547d\u545c\u547c\u4e86\uff0c\u597d\u5728\u8fd9\u4e9b\u602a\u7269\u5927\u591a\u559c\u6b22\u663c\u4f0f\u591c\u51fa\uff0c\u5f88\u5c11\u6709\u5927\u767d\u5929\u51fa\u6765\u6d3b\u52a8\u7684\u3002\u6b64\u65f6\u82e5\u4e0d\u662f\u63a5\u8fd1\u508d\u665a\uff0c\u98df\u4eba\u9b54\u4e5f\u672a\u5fc5\u4f1a\u51fa\u6d1e\u6765\u3002\u53ea\u8981\u8fc7\u4e86\u4eca\u591c\uff0c\u660e\u5929\u4e00\u65e9\u51fa\u5c71\uff0c\u508d\u665a\u524d\u5e94\u8be5\u80fd\u56de\u535a\u5f97\u4e4b\u95e8\uff0c\u5c31\u6ca1\u4ec0\u4e48\u5927\u95ee\u9898\u4e86\u3002</p><p>\u6885\u83f2\u65af\u548c\u9686\u5947\u4e24\u4eba\u5c06\u90a3\u4e9b\u98df\u4eba\u9b54\u5c38\u4f53\u8fdc\u8fdc\u62d6\u5f00\uff0c\u514d\u5f97\u81ed\u5473\u4f20\u6765\uff0c\u5927\u5bb6\u5c31\u5168\u90fd\u8981\u53d7\u4e0d\u4e86\u4e86\u3002\u706b\u5806\u751f\u8d77\uff0c\u4f17\u4eba\u56f4\u7740\u9ed8\u9ed8\u8fdb\u9910\uff0c\u5927\u5bb6\u90fd\u5bf9\u9093\u80af\u624b\u4e2d\u90a3\u9ed1\u4e4e\u4e4e\u7684\u7ba1\u5b50\u9887\u4e3a\u597d\u5947\uff0c\u5c24\u5176\u662f\u89c1\u4ed6\u4e00\u51fb\u8f70\u8db4\u4e86\u4e00\u53ea\u98df\u4eba\u9b54\uff0c\u66f4\u53ef\u89c1\u5a01\u529b\u975e\u51e1\u3002\u53ea\u662f\u6709\u4e00\u4ef6\u4e8b\u4e0d\u660e\u767d\uff0c\u65e2\u7136\u6b64\u7269\u5982\u6b64\u5389\u5bb3\uff0c\u90a3\u4ed6\u8fd8\u8fd9\u4e48\u4ed3\u7687\u9003\u7a9c\u505a\u4ec0\u4e48\uff0c\u76f4\u63a5\u4e3e\u8d77\u6765\u628a\u8fd9\u4e9b\u98df\u4eba\u9b54\u5168\u6570\u8f70\u98de\u5c31\u662f\u4e86\u3002</p><p>\u201c\u5443\uff0c\u90a3\u4e2a\u554a\uff0c\u201d\u9093\u80af\u6709\u4e9b\u5c34\u5c2c\uff0c\u201c\u8fd9\u4e2a\u2026\u2026\u8fd8\u6709\u4e9b\u6280\u672f\u4e0a\u7684\u5c0f\u95ee\u9898\u6ca1\u89e3\u51b3\u2026\u2026\u53cd\u6b63\uff0c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u201c\u4e0d\u80fd\u8fde\u7eed\u7528\u662f\u5427\u3002\u201d\u8d1d\u7eb3\u7d22\u548c\u4ed6\u76f8\u719f\u4e9b\uff0c\u77e5\u9053\u4ed6\u5728\u8fd9\u65b9\u9762\u6781\u5ea6\u7231\u9762\u5b50\uff0c\u7d22\u6027\u4e00\u53e3\u63ed\u7834\uff0c\u514d\u5f97\u4ed6\u652f\u652f\u543e\u543e\u7684\u3002</p><p>\u201c\u5443\uff0c\u4ece\u67d0\u79cd\u7a0b\u5ea6\u4e0a\uff0c\u662f\u53ef\u4ee5\u8fd9\u4e48\u7406\u89e3\u4e5f\u6ca1\u9519\u4e86\u2026\u2026\u201d</p><p>\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u8d1d\u7eb3\u7d22\u8bf4\u7684\uff0c\u9093\u80af\u82e6\u5fc3\u7814\u7a76\u4e86\u8fd1\u5341\u5e74\uff0c\u53d1\u660e\u51fa\u4e86\u8fd9\u6746\u4ed6\u79f0\u4e3a\u201c\u706b\u7ef3\u94a9\u67aa\u201d\u7684\u4e1c\u897f\u6765\u3002\u4e4b\u6240\u4ee5\u53eb\u8fd9\u4e2a\u540d\u5b57\uff0c\u662f\u56e0\u4e3a\u67aa\u4e0a\u6709\u4e00\u4e2a\u91d1\u5c5e\u5f2f\u94a9\uff0c\u5f2f\u94a9\u7684\u4e00\u7aef\u56fa\u5b9a\u5728\u67aa\u4e0a\uff0c\u5e76\u53ef\u7ed5\u8f74\u65cb\u8f6c\uff0c\u53e6\u4e00\u7aef\u5939\u6301\u4e00\u6839\u706b\u7ef3\uff1b\u53d1\u5c04\u65f6\u5c06\u706b\u7ef3\u70b9\u71c3\uff0c\u7528\u624b\u5c06\u91d1\u5c5e\u5f2f\u94a9\u5f80\u706b\u95e8\u91cc\u63a8\u538b\uff0c\u4f7f\u706b\u7ef3\u70b9\u71c3\u706b\u836f\uff0c\u8fdb\u800c\u5c06\u67aa\u819b\u5185\u88c5\u7684\u5f39\u4e38\u53d1\u5c04\u51fa\u53bb\u3002\u8fd9\u4e00\u7cfb\u5217\u8fc7\u7a0b\u81ea\u7136\u7e41\u7410\u5f97\u7d27\uff0c\u6240\u4ee5\u53d1\u5c04\u7f13\u6162\uff0c\u4e2d\u95f4\u95f4\u9699\u592a\u957f\uff0c\u9047\u4e0a\u5355\u4e2a\u5bf9\u624b\u8fd8\u6709\u7528\uff0c\u4e00\u65e6\u9047\u4e0a\u591a\u4eba\u5c31\u5b8c\u4e86\u3002\u9093\u80af\u6b64\u524d\u53d1\u4e86\u4e00\u67aa\uff0c\u6253\u6b7b\u4e00\u4e2a\u98df\u4eba\u9b54\uff0c\u63a5\u7740\u5c31\u53ea\u80fd\u4ed3\u7687\u9003\u547d\uff0c\u6839\u672c\u6ca1\u65f6\u95f4\u505c\u4e0b\u6765\u6162\u6162\u88c5\u5f39\u70b9\u706b\u53d1\u5c04\u3002\u8981\u4e0d\u662f\u9047\u4e0a\u6551\u661f\uff0c\u80af\u5b9a\u88ab\u98df\u4eba\u9b54\u7684\u5927\u68d2\u7838\u6241\u3002</p></article>"}}}]);
