(window.webpackJsonp=window.webpackJsonp||[]).push([[1342],{1365:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e03\u8282 \u5b88\u591c",content:"<article><h2>\u7b2c\u4e03\u8282 \u5b88\u591c</h2><p>\u542c\u5230\u8fd9\u4e9b\uff0c\u65c1\u4eba\u5012\u8fd8\u7f62\u4e86\uff0c\u867d\u7136\u90fd\u89c9\u5f97\u8fd9\u706b\u67aa\u5a01\u529b\u9887\u5927\uff0c\u4f46\u4f7f\u7528\u8d77\u6765\u5b9e\u5728\u592a\u4e0d\u65b9\u4fbf\uff0c\u5c24\u5176\u662f\u5728\u542c\u9093\u80af\u8bf4\u201c\u6709\u6548\u5c04\u7a0b\u5927\u6982\u4e94\u5341\u82f1\u5c3a\uff08\u7ea6\u5341\u4e94\u7c73\uff09\u201d\u7684\u65f6\u5019\uff0c\u987f\u65f6\u66f4\u89c9\u8fd9\u4e1c\u897f\u6ca1\u7528\uff0c\u8fd8\u4e0d\u5982\u7528\u5f13\u5f29\u3002\u8fd9\u4e2a\u4e16\u754c\u91cc\uff0c\u5bfb\u5e38\u7684\u5f13\u5f29\uff0c\u4e00\u822c\u4e5f\u90fd\u80fd\u5c04\u5230\u4e94\u767e\u82f1\u5c3a\u4e4b\u5916\u4e86\u3002\u4ece\u521a\u624d\u8f70\u90a3\u53ea\u98df\u4eba\u9b54\u7684\u8868\u73b0\u6765\u770b\uff0c\u6740\u4f24\u529b\u4e5f\u5e76\u4e0d\u662f\u7279\u522b\u5389\u5bb3\u3002\u98df\u4eba\u9b54\u6ca1\u7a7f\u94e0\u7532\uff0c\u8840\u8089\u4e4b\u8eaf\u81ea\u7136\u9876\u4e0d\u4f4f\u8fd9\u4e00\u8f70\uff0c\u82e5\u662f\u6362\u4e86\u4e2a\u7a7f\u94e0\u7532\u7684\u6218\u58eb\uff0c\u53ea\u6015\u5c31\u672a\u5fc5\u4f1a\u591a\u4e48\u60e7\u6015\u3002</p><p>\u743c\u6069\u5374\u9887\u611f\u597d\u5947\u3002\u4ed6\u4e0d\u61c2\u67aa\u68b0\uff0c\u4f46\u542c\u8d77\u6765\u89c9\u5f97\u8fd9\u4eff\u4f5b\u5c31\u662f\u6bd4\u8f83\u539f\u59cb\u7684\u706b\u67aa\uff0c\u867d\u7136\u662f\u7c97\u7cd9\u5dee\u52b2\u4e86\u4e9b\uff0c\u4f46\u4e5f\u5df2\u7ecf\u662f\u975e\u5e38\u4e86\u4e0d\u8d77\u7684\u53d1\u660e\u4e86\u3002</p><p>\u81ea\u4ece\u6765\u5230\u8fd9\u4e2a\u4e16\u754c\uff0c\u53d1\u89c9\u867d\u7136\u9b54\u6cd5\u975e\u5e38\u53d1\u8fbe\uff0c\u79d1\u6280\u5374\u9887\u4e3a\u8d2b\u4e4f\uff0c\u51e0\u4e4e\u8fd8\u505c\u7559\u5728\u897f\u6b27\u4e2d\u4e16\u7eaa\u7684\u6c34\u51c6\u2014\u2014\u5f53\u7136\uff0c\u4e0d\u5c11\u539f\u672c\u662f\u5c5e\u4e8e\u79d1\u6280\u5e26\u6765\u7684\u8fdb\u6b65\uff0c\u7531\u9b54\u6cd5\u76f4\u63a5\u4ee3\u66ff\u5b8c\u6210\u4e86\u3002\u8fd9\u4e2a\u4e16\u754c\u6ca1\u7535\u8111\u6ca1\u7f51\u7edc\u6ca1\u7535\u89c6\u6ca1\u51b0\u7bb1\u6ca1\u6709\u4e00\u5207\u7535\u6c14\u73b0\u4ee3\u5316\uff0c\u4ee4\u4ed6\u8fd9\u79cd\u5728\u5730\u7403\u73b0\u4ee3\u4e60\u60ef\u4e8e\u5f53\u5b85\u7537\u7684\u5bb6\u4f19\u9887\u6709\u4e9b\u4e0d\u723d\u3002\u5982\u4eca\u96be\u5f97\u770b\u5230\u6709\u4eba\u53d1\u660e\u706b\u67aa\uff0c\u867d\u7136\u662f\u4e2a\u521d\u7ea7\u54c1\uff0c\u4f46\u4e5f\u5df2\u7ecf\u7b97\u662f\u79d1\u6280\u7684\u8fdb\u6b65\u4e86\uff0c\u4e0d\u514d\u6709\u4e9b\u672c\u80fd\u7684\u4eb2\u8fd1\u611f\u3002</p><p>\u9093\u80af\u89c1\u5927\u5bb6\u5bf9\u81ea\u5df1\u7684\u53d1\u660e\u90fd\u5f88\u8f7b\u89c6\uff0c\u4e0d\u7531\u5f97\u8138\u5fae\u5fae\u6da8\u7ea2\u8d77\u6765\uff0c\u7a81\u7136\u89c1\u743c\u6069\u4f3c\u4e4e\u9887\u6709\u5174\u8da3\u7684\u6837\u5b50\uff0c\u987f\u65f6\u8f6c\u6012\u4e3a\u559c\uff0c\u5750\u8fd1\u4e24\u6b65\uff0c\u5411\u4ed6\u6bb7\u52e4\u4ecb\u7ecd\u8d77\u6765\u3002</p><p>\u4ed6\u6ee1\u53e3\u7684\u4e13\u4e1a\u672f\u8bed\uff0c\u743c\u6069\u5176\u5b9e\u542c\u4e0d\u592a\u61c2\uff0c\u4f46\u89c1\u4ed6\u5174\u81f4\u5982\u6b64\u6d53\u539a\uff0c\u4e5f\u4e0d\u597d\u610f\u601d\u6253\u65ad\uff0c\u4fbf\u4efb\u4ed6\u553e\u6cab\u56db\u6e85\u5730\u8bf4\u7740\uff0c\u81ea\u5df1\u62ff\u8d77\u67aa\u6162\u6162\u7aef\u8be6\u3002\u4ed6\u4e0a\u8f88\u5b50\u6ca1\u89c1\u8fc7\u771f\u7684\u67aa\u652f\uff0c\u4f46\u603b\u4e5f\u89c1\u8fc7\u4e00\u4e9b\u56fe\u7247\uff0c\u770b\u8fc7\u7535\u89c6\u7535\u5f71\uff0c\u603b\u89c9\u5f97\u8fd9\u53ea\u6240\u8c13\u7684\u201c\u706b\u7ef3\u94a9\u67aa\u201d\u7ed3\u6784\u9887\u4e3a\u53e4\u602a\uff0c\u5c45\u7136\u662f\u7528\u706b\u7ef3\u6765\u5f15\u71c3\u53d1\u5c04\uff0c\u96be\u9053\u4e0d\u5e94\u8be5\u662f\u649e\u51fb\u5f0f\u7684\u70b9\u706b\u4e48\uff1b\u800c\u4e14\u542c\u9093\u80af\u8bf4\u4e0a\u5b50\u5f39\u65f6\u662f\u4ece\u524d\u9762\u67aa\u53e3\u585e\u8fdb\u53bb\u7684\uff0c\u8fd9\u4fbf\u66f4\u53e4\u602a\u4e86\uff0c\u4ed6\u8bb0\u5fc6\u91cc\u7684\u706b\u67aa\uff0c\u65e0\u8bba\u662f\u5c0f\u624b\u67aa\u8fd8\u662f\u51b2\u950b\u67aa\u6216\u8005\u673a\u5173\u67aa\uff0c\u4ece\u6765\u90fd\u5e94\u8be5\u662f\u4ece\u540e\u65b9\u88c5\u5f39\u7684\u2014\u2014\u81f3\u5c11\u90a3\u4e9b\u6218\u4e89\u7247\u91cc\u90fd\u662f\u5982\u6b64\u3002</p><p>\u4e0d\u7ba1\u4e86\uff0c\u7ba1\u5b83\u524d\u9762\u88c5\u5f39\u540e\u9762\u88c5\u5f39\uff0c\u53cd\u6b63\u80fd\u88c5\u8fdb\u53bb\u5c31\u884c\u3002\u4e0d\u540c\u7684\u4e16\u754c\uff0c\u81ea\u7136\u4e5f\u6709\u4e0d\u540c\u7684\u706b\u67aa\u9020\u6cd5\u561b\uff0c\u743c\u6069\u60f3\u3002</p><p>\u9093\u80af\u89c1\u4ed6\u770b\u5f97\u8ba4\u771f\uff0c\u8d8a\u53d1\u4ee5\u4e3a\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u6001\u5ea6\u66f4\u52a0\u8bda\u6073\u70ed\u60c5\uff0c\u751a\u81f3\u4e0d\u65e0\u70ab\u8000\u5730\u8bf7\u6559\u8d77\u4ed6\u5bf9\u8fd9\u201c\u706b\u7ef3\u94a9\u67aa\u201d\u7684\u770b\u6cd5\u6765\u3002\u743c\u6069\u7eaf\u7cb9\u662f\u4e2a\u5916\u884c\uff0c\u54ea\u91cc\u80fd\u8bf4\u51fa\u4ec0\u4e48\u770b\u6cd5\uff0c\u4f46\u53c8\u4e0d\u597d\u4e00\u53e3\u63a8\u8131\uff0c\u53ea\u80fd\u968f\u53e3\u5c06\u521a\u624d\u6240\u60f3\u7684\u8bf4\u4e86\u8bf4\u3002</p><p>\u4ed6\u672c\u6765\u4e5f\u6ca1\u591a\u60f3\uff0c\u53ea\u662f\u968f\u4fbf\u8bf4\u8bf4\uff0c\u81ea\u5df1\u90fd\u4e0d\u66fe\u5f53\u771f\uff0c\u4e0d\u60f3\u9093\u80af\u542c\u4e86\uff0c\u987f\u65f6\u6115\u7136\u6123\u4f4f\uff0c\u539f\u672c\u8fd8\u53e3\u82e5\u60ac\u6cb3\u5730\u5728\u8bb2\u8bf4\u7740\uff0c\u4e5f\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u6014\u6014\u601d\u7d22\u3002</p><p>\u201c\u649e\u51fb\u70b9\u706b\u2026\u2026\u201d\u4ed6\u76b1\u7740\u7709\u5934\uff0c\u4e00\u53ea\u624b\u4e0b\u610f\u8bc6\u5730\u6572\u7740\u81ea\u5df1\u8111\u888b\uff0c\u201c\u649e\u51fb\u70b9\u706b\u2026\u2026\u540e\u88c5\u5f39\u2026\u2026\u201d</p><p>\u4ed6\u7684\u7709\u5934\u8d8a\u76b1\u8d8a\u7d27\uff0c\u5df2\u7ecf\u6b7b\u6b7b\u7ea0\u96c6\u5728\u4e00\u8d77\uff0c\u8138\u4e0a\u751a\u81f3\u9732\u51fa\u6709\u4e9b\u75db\u82e6\u7684\u795e\u60c5\u6765\u3002\u5176\u4ed6\u4eba\u4e00\u76f4\u5728\u5fd9\u7740\u5543\u9762\u5305\uff0c\u6839\u672c\u4e0d\u66fe\u6ce8\u610f\u8fd9\u8fb9\uff0c\u7a81\u7136\u89c1\u9093\u80af\u8fd9\u5e45\u6a21\u6837\uff0c\u90fd\u6709\u4e9b\u83ab\u540d\u5176\u5999\u3002\u8fd8\u662f\u8d1d\u7eb3\u7d22\u719f\u6089\u4ed6\uff0c\u77e5\u9053\u80af\u5b9a\u53c8\u662f\u9047\u4e0a\u4ec0\u4e48\u96be\u9898\u4e86\uff0c\u4e5f\u4e0d\u7740\u6025\uff0c\u4e3e\u8d77\u5723\u5fbd\u91ca\u653e\u4e86\u4e00\u4e2a\u201c\u5b89\u5b9a\u5fc3\u795e\u201d\uff0c\u65bd\u52a0\u5230\u9093\u80af\u8eab\u4e0a\u3002\u5230\u5e95\u662f\u6668\u66e6\u4e4b\u795e\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u5728\u8fd9\u79cd\u795d\u798f\u6cbb\u7597\u65b9\u9762\u7684\u795e\u672f\u4e0a\u9020\u8be3\u9887\u6df1\uff0c\u5f88\u5feb\uff0c\u519b\u706b\u5546\u4eba\u517c\u75af\u72c2\u53d1\u660e\u5bb6\u5c31\u6e10\u6e10\u5b89\u9759\u4e0b\u6765\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u8d1d\u7eb3\u7d22\u95ee\uff0c\u9093\u80af\u5374\u4e0d\u8bf4\u8bdd\uff0c\u53ea\u662f\u4e00\u4e2a\u52b2\u6447\u5934\uff0c\u6b7b\u6b7b\u6293\u7740\u90a3\u6746\u201c\u706b\u7ef3\u94a9\u67aa\u201d\u4e0d\u653e\u3002\u4f17\u4eba\u89c1\u4ed6\u5df2\u7ecf\u6ca1\u4e8b\uff0c\u4e5f\u4fbf\u4e0d\u518d\u7ba1\u4ed6\u3002\u5403\u5b8c\u665a\u9910\uff0c\u5404\u81ea\u94bb\u8fdb\u7761\u888b\u4f11\u606f\uff0c\u79d1\u6069\u89c1\u592a\u9633\u4e0b\u5c71\uff0c\u81ea\u5df1\u8d70\u5230\u4e00\u8fb9\uff0c\u5c06\u516d\u679a\u91d1\u5e01\u653e\u5728\u5730\u4e0a\uff0c\u6446\u6210\u4e00\u4e2a\u5706\u5f62\uff0c\u5f00\u59cb\u7948\u7977\u3002\u4e0d\u540c\u7684\u795e\u7947\uff0c\u6240\u559c\u6b22\u7684\u7948\u7977\u65f6\u95f4\u4e5f\u5f80\u5f80\u4e0d\u540c\uff0c\u6668\u66e6\u4e4b\u795e\u5c31\u559c\u6b22\u4ed6\u7684\u4fe1\u5f92\u5728\u9ece\u660e\u65f6\u7948\u7977\uff0c\u5546\u4e1a\u5973\u795e\u5374\u76f8\u53cd\uff0c\u559c\u6b22\u4fe1\u5f92\u5728\u65e5\u843d\u65f6\u7948\u7977\u3002</p><p>\u6885\u83f2\u65af\u63d0\u7740\u5251\uff0c\u5750\u5728\u706b\u5806\u8fb9\u5b88\u591c\uff0c\u9093\u80af\u5374\u72b9\u81ea\u6709\u4e9b\u5931\u9b42\u843d\u9b44\u7684\u6a21\u6837\uff0c\u62b1\u7740\u90a3\u6746\u67aa\u4e5f\u5750\u5728\u706b\u5806\u8fb9\u53d1\u5446\uff0c\u5012\u4e5f\u6ca1\u4eba\u7ba1\u4ed6\u3002</p><p>\u743c\u6069\u89c1\u4ed6\u8fd9\u5e45\u6a21\u6837\uff0c\u4e0d\u7981\u6709\u4e9b\u5fd0\u5fd1\uff0c\u201c\u96be\u4e0d\u6210\u6211\u521a\u624d\u968f\u53e3\u8bf4\u7684\u90a3\u4e9b\u4e1c\u897f\u5f53\u771f\u6709\u7528\uff1f\u201d\u4ed6\u4e8e\u6b64\u9053\u51e0\u4e4e\u5168\u65e0\u6240\u77e5\uff0c\u4e5f\u65e0\u4ece\u731c\u6d4b\uff0c\u53ea\u662f\u770b\u9093\u80af\u8fd9\u6837\u5b50\uff0c\u786e\u5b9e\u50cf\u662f\u88ab\u63d0\u9192\u89e6\u52a8\u4e86\u4e9b\u4ec0\u4e48\u7684\u6837\u5b50\u3002\u201c\u603b\u4e0d\u81f3\u4e8e\u56e0\u4e3a\u6211\u4eca\u5929\u8fd9\u968f\u53e3\u4e00\u8bf4\uff0c\u8fd9\u5bb6\u4f19\u771f\u4f1a\u53d1\u660e\u51fa\u73b0\u4ee3\u706b\u67aa\u5427\u3002\u201d</p><p>\u5982\u679c\u5730\u7403\u4e0a\u90a3\u79cd\u73b0\u4ee3\u706b\u67aa\u5f53\u771f\u80fd\u51fa\u73b0\uff0c\u52bf\u5fc5\u4f1a\u5f15\u8d77\u4e00\u573a\u5267\u53d8\uff0c\u8fd9\u70b9\u5c31\u7b97\u743c\u6069\u8fd9\u79cd\u5916\u884c\u4e5f\u80fd\u60f3\u5230\u3002\u4f46\u8fd9\u6bd5\u7adf\u8ddd\u79bb\u81ea\u5df1\u4f3c\u4e4e\u592a\u9065\u8fdc\u4e86\u4e9b\uff0c\u201c\u7b97\u4e86\uff0c\u4e0d\u60f3\u8fd9\u4e9b\u6709\u7684\u6ca1\u7684\uff0c\u660e\u5929\u8fd8\u8981\u65e9\u8d77\u8d76\u8def\u5462\u3002\u201d\u4ed6\u94bb\u8fdb\u81ea\u5df1\u7684\u7761\u888b\uff0c\u9ed8\u9ed8\u8fd0\u529f\uff0c\u5f00\u59cb\u4f8b\u884c\u7684\u8c03\u606f\u7ec3\u529f\u3002</p><p>\u5468\u56f4\u5b89\u9759\u4e0b\u6765\uff0c\u4ec5\u6709\u706b\u5806\u91cc\u7684\u6728\u67f4\u53d1\u51fa\u8f7b\u5fae\u7684\u7206\u88c2\u58f0\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u4e00\u591c\u5e73\u5b89\u65e0\u4e8b\uff0c\u6ca1\u6709\u4eba\u4e5f\u6ca1\u6709\u602a\u7269\u6253\u6270\u3002\u7b2c\u4e8c\u5929\u6e05\u6668\uff0c\u5929\u5c1a\u672a\u4eae\uff0c\u4f17\u4eba\u4fbf\u8d77\u8eab\u51c6\u5907\u51fa\u53d1\uff0c\u8d1d\u7eb3\u7d22\u7b49\u5f85\u7740\u65ed\u65e5\u521d\u51fa\uff0c\u4ee5\u4fbf\u8fdb\u884c\u7948\u7977\uff1b\u9093\u80af\u5219\u662f\u5728\u706b\u5806\u8fb9\u5446\u5750\u4e86\u4e00\u591c\uff0c\u6b64\u65f6\u9762\u5bb9\u6194\u60b4\uff0c\u773c\u775b\u91cc\u90fd\u5e03\u6ee1\u4e86\u8840\u4e1d\uff0c\u4f46\u795e\u60c5\u5374\u9887\u4e3a\u6109\u5feb\u7684\u6837\u5b50\uff0c\u4eff\u4f5b\u662f\u60f3\u901a\u4e86\u4ec0\u4e48\u957f\u4e45\u4ee5\u6765\u7684\u96be\u9898\u3002</p><p>\u743c\u6069\u7b49\u4eba\u8981\u7ee7\u7eed\u524d\u8fdb\uff0c\u9093\u80af\u5219\u8981\u51fa\u5c71\u56de\u535a\u5f97\u4e4b\u95e8\u53bb\u3002\u6b64\u5730\u8ddd\u79bb\u51fa\u5c71\u8def\u7a0b\u4e0d\u5230\u4e00\u65e5\uff0c\u767d\u5929\u602a\u7269\u6d3b\u52a8\u8f83\u5c11\uff0c\u4ed6\u53c8\u6709\u8fd9\u6746\u706b\u7ef3\u94a9\u67aa\u9632\u8eab\uff0c\u53ea\u8981\u4e0d\u662f\u8fd0\u6c14\u592a\u7cdf\u7cd5\uff0c\u5e94\u8be5\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u610f\u5916\uff1b\u5018\u82e5\u9047\u4e0a\u5730\u7cbe\u7fa4\u7684\u8bdd\uff0c\u53ea\u8981\u8f70\u4e0a\u4e00\u67aa\uff0c\u65e0\u8bba\u6253\u6b7b\u6ca1\u6253\u6b7b\uff0c\u62c5\u4fdd\u90a3\u4e9b\u80c6\u5c0f\u5982\u9f20\u7684\u5bb6\u4f19\u5168\u90fd\u62b1\u5934\u9003\u7a9c\uff0c\u4e0d\u5fc5\u62c5\u5fc3\u3002</p><p>\u5728\u5206\u9053\u626c\u9573\u4e4b\u524d\uff0c\u9093\u80af\u8bf4\u743c\u6069\u7684\u6307\u70b9\u8ba9\u4ed6\u53d1\u73b0\u4e86\u4ee5\u524d\u4ece\u672a\u60f3\u8fc7\u7684\u8def\u5f84\uff0c\u4e3a\u4e86\u8868\u793a\u611f\u8c22\uff0c\u4ed6\u4ece\u5305\u88f9\u91cc\u638f\u51fa\u51e0\u4e2a\u770b\u4e0a\u53bb\u50cf\u662f\u5927\u94c5\u7403\u7684\u4e1c\u897f\u6765\uff0c\u8981\u9001\u7ed9\u743c\u6069\u3002</p><p>\u4ed6\u4e0d\u62ff\u51fa\u6765\u5012\u8fd8\u7f62\u4e86\uff0c\u4e00\u62ff\u51fa\u6765\uff0c\u743c\u6069\u8fd8\u6ca1\u53cd\u5e94\uff0c\u65c1\u8fb9\u8d1d\u7eb3\u7d22\u7267\u5e08\u9661\u5730\u8138\u8272\u5c31\u53d8\u4e86\u3002\u201c\u6668\u66e6\u5728\u4e0a\uff0c\u201d\u4ed6\u5927\u60ca\u5931\u8272\uff0c\u201c\u4f60\u600e\u4e48\u6562\u628a\u8fd9\u4e1c\u897f\u7ed9\u5e26\u51fa\u6765\uff01\u201d</p><p>\u5176\u4ed6\u4eba\u95fb\u58f0\u770b\u8fc7\u6765\uff0c\u590f\u62c9\u975e\u548c\u79d1\u6069\u8138\u8272\u4e5f\u5fae\u5fae\u6709\u4e9b\u6539\u53d8\uff0c\u4ed6\u4eec\u81ea\u7136\u90fd\u662f\u8ba4\u8bc6\u8fd9\u4e1c\u897f\u7684\u3002\u201c\u53d1\u70df\u70b8\u5f39\uff01\u201d\u590f\u62c9\u975e\u6709\u4e9b\u60ca\u60e7\u5730\u8bf4\uff0c\u5f80\u540e\u63a5\u8fde\u9000\u5f00\u51e0\u6b65\u3002</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u53d1\u70df\u70b8\u5f39\uff0c\u542c\u8d1d\u7eb3\u7d22\u89e3\u91ca\u624d\u660e\u767d\uff0c\u539f\u6765\u662f\u8fd9\u4f4d\u9093\u80af\u5148\u751f\u7684\u53e6\u5916\u4e00\u9879\u53d1\u660e\uff0c\u5176\u5b9e\u5c31\u662f\u7528\u53d1\u70df\u706b\u836f\u5236\u4f5c\u6210\u7684\u70b8\u5f39\u3002\u56e0\u4e3a\u53c8\u5927\u53c8\u6c89\u91cd\uff0c\u6740\u4f24\u529b\u53c8\u4e0d\u7b97\u5f88\u5f3a\uff0c\u5b9e\u6218\u4e2d\u7528\u9014\u4e0d\u5927\uff0c\u4f46\u5b83\u7206\u70b8\u5f00\u6765\u4f1a\u4ea7\u751f\u6d53\u5bc6\u7684\u523a\u6fc0\u6027\u70df\u96fe\uff0c\u4e5f\u6b63\u662f\u8fd9\u70b9\u8ba9\u8d1d\u7eb3\u7d22\u7b49\u4eba\u5bb3\u6015\u3002</p><p>\u9093\u80af\u5c45\u7136\u80cc\u5305\u91cc\u8fd8\u5e26\u4e86\u8fd9\u4e48\u51e0\u9897\u53d1\u70df\u70b8\u5f39\uff0c\u5e78\u4e8f\u4e00\u76f4\u6ca1\u51fa\u4e8b\uff0c\u5426\u5219\u771f\u8981\u70b8\u8d77\u6765\uff0c\u4ed6\u81ea\u5df1\u80af\u5b9a\u5148\u7c89\u8eab\u788e\u9aa8\u4e86\u3002\u8fd9\u5bb6\u4f19\u770b\u6765\u7422\u78e8\u706b\u67aa\u5df2\u7ecf\u6709\u4e9b\u8111\u5b50\u4e0d\u6b63\u5e38\uff0c\u5dee\u4e0d\u591a\u8fd1\u4f3c\u4e8e\u90a3\u79cd\u79d1\u5b66\u72c2\u4eba\u4e86\uff0c\u8fd8\u662f\u5c11\u6253\u4ea4\u9053\u4e3a\u5999\u3002</p><p>\u81f3\u4e8e\u90a3\u4e9b\u94c5\u7403\u4f3c\u7684\u70b8\u5f39\uff0c\u743c\u6069\u81ea\u7136\u4e5f\u6ca1\u6709\u63a5\u53d7\uff0c\u59d4\u5a49\u8c22\u7edd\u4e86\u3002\u5f00\u73a9\u7b11\uff0c\u4ed6\u600e\u4e48\u6562\u80cc\u7740\u8fd9\u79cd\u5371\u9669\u7684\u4e1c\u897f\u8d70\u8def\uff0c\u800c\u4e14\u8fd9\u822c\u7b28\u91cd\uff0c\u5c31\u7b97\u9047\u5230\u654c\u4eba\uff0c\u4ed6\u80fd\u6254\u51fa\u53bb\u591a\u8fdc\u90fd\u662f\u95ee\u9898\uff0c\u53ea\u6015\u5148\u628a\u81ea\u5df1\u70b8\u4e86\u3002\u4e0a\u8f88\u5b50\u5b66\u6821\u4f53\u80b2\u8bfe\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u63a8\u94c5\u7403\u6210\u7ee9\u5c31\u4e00\u5411\u5f88\u5dee\u52b2\u3002</p><p>\u9093\u80af\u89c1\u72b6\uff0c\u9887\u6709\u4e9b\u9057\u61be\uff0c\u4f46\u4e5f\u6ca1\u6709\u52c9\u5f3a\uff0c\u53ea\u662f\u8bf7\u743c\u6069\u6b64\u6b21\u56de\u53bb\u4e4b\u540e\uff0c\u4e00\u5b9a\u8981\u53bb\u4ed6\u5bb6\u505a\u5ba2\uff0c\u518d\u63a2\u8ba8\u706b\u67aa\u76f8\u5173\u7684\u95ee\u9898\u3002\u743c\u6069\u4e00\u8fb9\u6697\u81ea\u5192\u51b7\u6c57\u4e00\u8fb9\u6577\u884d\uff0c\u4ed6\u7eaf\u5c5e\u4e2a\u519b\u4e8b\u76f2\uff0c\u5b8c\u5168\u51ed\u7740\u4e00\u70b9\u4ece\u7535\u5f71\u7535\u89c6\u4e0a\u770b\u6765\u7684\u5370\u8c61\u968f\u53e3\u4e71\u8bf4\uff0c\u54ea\u91cc\u6562\u548c\u8fd9\u79cd\u4e13\u4e1a\u4eba\u58eb\u771f\u7684\u8ba8\u8bba\uff0c\u53ea\u6015\u6ca1\u534a\u5929\u529f\u592b\u5c31\u8981\u9732\u9985\u3002\u597d\u5728\u6b64\u4e8b\u8fd8\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u4ee5\u6b64\u4e3a\u501f\u53e3\u63a8\u8131\uff0c\u6682\u65f6\u5148\u628a\u8fd9\u75af\u72c2\u53d1\u660e\u5bb6\u6253\u53d1\u8d70\u4e86\u3002</p><p>\u9093\u80af\u79bb\u53bb\uff0c\u4f17\u4eba\u7ee7\u7eed\u4e0a\u8def\u3002\u8fd9\u4e00\u5929\u4ed6\u4eec\u8fd0\u6c14\u6781\u597d\uff0c\u9664\u4e86\u4e09\u53ea\u8c7a\u72fc\u4eba\u4e4b\u5916\uff0c\u518d\u6ca1\u9047\u4e0a\u4efb\u4f55\u602a\u7269\u2014\u2014\u6216\u8005\u8fd9\u8bdd\u53cd\u8fc7\u6765\u8bf4\uff0c\u5c31\u662f\u90a3\u4e09\u53ea\u8c7a\u72fc\u4eba\u8fd0\u6c14\u6781\u5dee\uff0c\u5c45\u7136\u649e\u4e0a\u4ed6\u4eec\uff0c\u88ab\u6885\u83f2\u65af\u76f4\u63a5\u51e0\u5251\u780d\u4e86\u3002</p><p>\u5f53\u5929\u665a\u4e0a\uff0c\u7ee7\u7eed\u627e\u4e86\u4e2a\u5730\u65b9\u751f\u8d77\u706b\u5806\uff0c\u5b89\u8425\u624e\u5be8\u4f11\u606f\u3002\u590f\u62c9\u975e\u62ff\u7740\u5730\u56fe\u7814\u7a76\u4e86\u534a\u5929\uff0c\u5bf9\u7167\u5468\u56f4\u5730\u5f62\uff0c\u5224\u65ad\u51fa\u6b64\u65f6\u4ed6\u4eec\u6240\u5728\u7684\u4f4d\u7f6e\uff0c\u8ddd\u79bb\u62c9\u6c83\u514b\u7684\u5deb\u5e08\u4e4b\u5893\u5df2\u7ecf\u4e0d\u8fdc\u4e86\u3002\u5982\u679c\u4e00\u5207\u987a\u5229\u7684\u8bdd\uff0c\u5927\u6982\u660e\u5929\u4e0b\u5348\u5c31\u80fd\u62b5\u8fbe\u3002</p><p>\u201c\u597d\u6781\u4e86\uff0c\u201d\u4e00\u8def\u4e0a\u90fd\u6ca1\u600e\u4e48\u8bf4\u8bdd\u7684\u79d1\u6069\u8bf4\uff0c\u867d\u7136\u4ed6\u7684\u8138\u4e0a\u5e76\u6ca1\u6709\u4ec0\u4e48\u9ad8\u5174\u7684\u8868\u60c5\uff0c\u201c\u90a3\u4e48\uff0c\u65e9\u70b9\u4f11\u606f\u5427\uff0c\u660e\u5929\u53bb\u5e72\u6389\u90a3\u53ea\u8be5\u6b7b\u7684\u5deb\u5996\u3002\u201d</p><p>\u201c\u5b83\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u743c\u6069\u63d0\u9192\u3002</p><p>\u201c\u90a3\u5c31\u8ba9\u5b83\u518d\u6b7b\u4e00\u6b21\uff0c\u201d\u6c83\u91d1\u7267\u5e08\u8bf4\uff0c\u201c\u5f7b\u5e95\u7684\u6b7b\u6389\uff0c\u518d\u4e5f\u4e0d\u4f1a\u8dd1\u51fa\u6765\u60f9\u9ebb\u70e6\u3002\u201d</p><p>\u4ed6\u5de6\u53f3\u770b\u770b\uff0c\u8d70\u5230\u4e00\u4e2a\u7a7a\u65f7\u7684\u5730\u65b9\uff0c\u5927\u5bb6\u6b63\u4e0d\u77e5\u9053\u4ed6\u8981\u505a\u4ec0\u4e48\uff0c\u5c31\u89c1\u7267\u5e08\u4f38\u624b\u4ece\u8170\u95f4\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u679a\u91d1\u5e01\u6765\uff0c\u8f7b\u8f7b\u629b\u8d77\u5728\u7a7a\u4e2d\uff0c\u63a5\u4f4f\uff0c\u7ffb\u624b\u5f80\u5730\u4e0a\u4e00\u62cd\u3002</p><p>\u8f70\u5730\u4e00\u58f0\uff0c\u4eff\u4f5b\u53d8\u620f\u6cd5\u4e00\u822c\uff0c\u79d1\u6069\u9762\u524d\u7a81\u7136\u73b0\u51fa\u4e00\u5ea7\u5de8\u5927\u7684\u5706\u5f62\u4e1d\u5236\u5e10\u7bf7\u6765\uff0c\u76f4\u5f84\u81f3\u5c11\u8d85\u8fc7\u4e94\u5341\u82f1\u5c3a\u3002\u7267\u5e08\u6b63\u7ad9\u5728\u5e10\u7bf7\u7684\u95e8\u524d\uff0c\u4ed6\u5fae\u5fae\u8eac\u8eab\uff0c\u4eff\u4f5b\u4e3b\u4eba\u4e00\u822c\u5411\u4f17\u4eba\u505a\u4e86\u4e2a\u9080\u8bf7\u7684\u624b\u52bf\u3002</p><p>\u8d70\u5165\u5e10\u7bf7\uff0c\u53ea\u89c1\u684c\u6905\u5e8a\u94fa\u7b49\u7269\u4e00\u5e94\u4ff1\u5168\uff0c\u4e2d\u95f4\u7684\u5706\u5f62\u5927\u684c\u4e0a\uff0c\u5df2\u7ecf\u6446\u6ee1\u4e86\u5404\u79cd\u70ed\u6c14\u817e\u817e\u7684\u7f8e\u98df\uff0c\u70b9\u71c3\u70db\u5149\uff0c\u4fe8\u7136\u8c6a\u534e\u76db\u5bb4\u3002\u6676\u83b9\u5254\u900f\u7684\u9ad8\u811a\u9152\u676f\u91cc\uff0c\u7edb\u7ea2\u8272\u7684\u8461\u8404\u9152\u8fd8\u5728\u5fae\u5fae\u6447\u6643\uff0c\u4eff\u4f5b\u521a\u521a\u624d\u503e\u5012\u8fdb\u6765\u3002</p><p>\u201c\u8fd9\u662f\u2026\u2026\u201d</p><p>\u743c\u6069\u6709\u4e9b\u60ca\u8bb6\uff0c\u4ed6\u77e5\u9053\u5965\u672f\u4e2d\u4e5f\u6709\u4e00\u79cd\u201c\u8c6a\u5b85\u672f\u201d\uff0c\u5c5e\u4e8e\u5492\u6cd5\u5b66\u6d3e\u7684\u9ad8\u9636\u6cd5\u672f\uff0c\u80fd\u51ed\u7a7a\u521b\u9020\u51fa\u4e00\u5ea7\u4e34\u65f6\u5b85\u9662\u6765\uff0c\u5176\u4e2d\u540c\u6837\u4e5f\u662f\u996e\u98df\u8d77\u5c45\u5404\u79cd\u8bbe\u65bd\u4e00\u5e94\u4ff1\u5168\uff0c\u8fd8\u6709\u9690\u5f62\u5974\u4ec6\u5145\u5f53\u4ec6\u4eba\u3002\u4f46\u4ed6\u4e0d\u77e5\u9053\u539f\u6765\u7267\u5e08\u4e5f\u80fd\u6709\u7c7b\u4f3c\u7684\u795e\u672f\u3002</p><p>\u6216\u8005\u8bf4\uff0c\u8fd9\u662f\u6c83\u91d1\u7267\u5e08\u72ec\u6709\u7684\u795e\u672f\uff0c\u56e0\u4e3a\u8d1d\u7eb3\u7d22\u5148\u751f\u540c\u6837\u4e5f\u5f88\u60ca\u8bb6\u7684\u6837\u5b50\u3002</p><p>\u201c\u660e\u5929\u51b3\u6218\uff0c\u4eca\u665a\u8981\u597d\u597d\u4f11\u606f\uff0c\u201d\u79d1\u6069\u8bf4\uff0c\u201c\u8fd9\u662f\u5149\u8f89\u8425\u5e10\uff0c\u5e0c\u671b\u5404\u4f4d\u6ee1\u610f\u3002\u201d</p><p>\u201c\u4f60\u6628\u665a\u5e72\u561b\u4e0d\u62ff\u51fa\u6765\u3002\u201d\u8d1d\u7eb3\u7d22\u5148\u751f\u6709\u4e9b\u4e0d\u592a\u9ad8\u5174\uff0c\u4ed6\u517b\u5c0a\u5904\u4f18\u60ef\u4e86\uff0c\u8fd9\u79cd\u91ce\u5916\u9732\u8425\u7684\u6ecb\u5473\u60f3\u5fc5\u5f88\u4e45\u6ca1\u5c1d\u8fc7\uff0c\u6628\u665a\u7761\u7761\u888b\u5927\u7ea6\u4e5f\u4e0d\u4f1a\u5f88\u8212\u670d\uff0c\u5982\u4eca\u77e5\u9053\u79d1\u6069\u539f\u6765\u8fd8\u6709\u8fd9\u79cd\u672c\u4e8b\uff0c\u81ea\u7136\u8981\u53d1\u53d1\u7262\u9a9a\u3002</p><p>\u201c\u6ca1\u529e\u6cd5\uff0c\u5973\u795e\u7684\u6069\u8d50\uff0c\u4e00\u5468\u53ea\u80fd\u4f7f\u7528\u4e00\u6b21\u554a\u3002\u201d</p><p>\u79d1\u6069\u7684\u89e3\u91ca\u8ba9\u8d1d\u7eb3\u7d22\u4e5f\u65e0\u8bdd\u53ef\u8bf4\u3002\u8bf8\u4eba\u5165\u5e2d\uff0c\u81ea\u6709\u9690\u5f62\u5974\u4ec6\u5f80\u6765\u670d\u52a1\uff0c\u5176\u95f4\u79d1\u6069\u9891\u9891\u4e3e\u676f\u529d\u9152\uff0c\u5341\u5206\u6bb7\u52e4\u3002\u8fd9\u8ba9\u743c\u6069\u4e0d\u514d\u6709\u4e9b\u9690\u9690\u4e0d\u5b89\u7684\u611f\u89c9\uff0c\u770b\u770b\u6885\u83f2\u65af\uff0c\u53d1\u73b0\u5c11\u5973\u9762\u65e0\u8868\u60c5\u5730\u5728\u9ed8\u9ed8\u8fdb\u98df\uff0c\u5168\u4e0d\u7406\u776c\u3002</p><p>\u743c\u6069\u7559\u795e\u89c2\u5bdf\u7740\u9762\u524d\u7684\u996e\u98df\uff0c\u6b64\u524d\u90fd\u662f\u5404\u4eba\u5403\u5404\u4eba\u643a\u5e26\u7684\u98df\u7269\uff0c\u8fd9\u6b21\u5374\u662f\u79d1\u6069\u7684\u6cd5\u672f\u5f04\u51fa\u6765\u7684\uff0c\u4ed6\u6015\u8fd9\u4e2a\u6c83\u91d1\u7267\u5e08\u5728\u5176\u4e2d\u6363\u9b3c\u3002\u4ed6\u8ddf\u7740\u7530\u4f2f\u5149\u5b66\u8fc7\u51e0\u5e74\uff0c\u81ea\u5df1\u5bb6\u53c8\u5f00\u6750\u6599\u5546\u5e97\uff0c\u540e\u6765\u6210\u4e3a\u5deb\u5e08\uff0c\u7cbe\u7814\u53d8\u5316\u5b66\u6d3e\uff0c\u5728\u836f\u5242\u5b66\u8fd9\u4e00\u9886\u57df\u9887\u6709\u51e0\u5206\u9020\u8be3\u3002\u4e0d\u8fc7\u4ed6\u6700\u540e\u6ca1\u770b\u51fa\u6709\u4ec0\u4e48\u53ef\u7591\u7684\u8ff9\u8c61\uff0c\u65e0\u6bd2\u7d20\uff0c\u65e0\u836f\u7269\uff0c\u4e00\u5207\u6b63\u5e38\u3002\u770b\u5176\u4ed6\u4eba\u5df2\u7ecf\u5927\u5403\u5927\u559d\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5f02\u5e38\u3002</p><p>\u4ed6\u8e0c\u8e87\u7740\uff0c\u6b63\u72b9\u8c6b\u662f\u5426\u8981\u63a8\u8131\u8bf4\u8eab\u4f53\u4e0d\u9002\uff0c\u65e9\u65e9\u9000\u5e2d\uff0c\u5374\u89c1\u6885\u83f2\u65af\u4fa7\u8fc7\u8138\u6765\uff0c\u671d\u4ed6\u5fae\u5fae\u70b9\u5934\uff0c\u793a\u610f\u4e0d\u5fc5\u62c5\u5fc3\u3002\u743c\u6069\u8f6c\u5ff5\u4e00\u60f3\u4e5f\u662f\uff0c\u8d1d\u7eb3\u7d22\u548c\u9686\u5947\u6216\u8bb8\u8fd8\u7f62\u4e86\uff0c\u6885\u83f2\u65af\u548c\u590f\u62c9\u975e\u90fd\u662f\u7ecf\u9a8c\u4e30\u5bcc\u7684\u8001\u624b\uff0c\u5982\u679c\u771f\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u65e9\u4e5f\u770b\u51fa\u6765\u4e86\u3002</p><p>\u665a\u5bb4\u4e4b\u540e\uff0c\u5b89\u6392\u5b88\u591c\u4e8b\u5b9c\uff0c\u6bd5\u7adf\u8fd9\u53ea\u662f\u4e00\u4e2a\u5e10\u7bf7\uff0c\u4e0d\u662f\u57ce\u5821\uff0c\u6321\u4e0d\u4f4f\u602a\u7269\u88ad\u51fb\u3002\u4e00\u5171\u516d\u4e2a\u4eba\uff0c\u743c\u6069\u662f\u5deb\u5e08\uff0c\u5929\u7136\u4eab\u53d7\u4e0d\u5b88\u591c\u7684\u7279\u6743\uff0c\u6885\u83f2\u65af\u548c\u79d1\u6069\u662f\u6628\u665a\u5b88\u591c\u7684\uff0c\u6309\u60ef\u4f8b\u8f6e\u6362\u6389\uff0c\u90a3\u4e48\u5c31\u53ea\u5269\u4e0b\u590f\u62c9\u975e\u3001\u8d1d\u7eb3\u7d22\u548c\u9686\u5947\u4e09\u4eba\u3002</p><p>\u5b88\u591c\u53ea\u9700\u8981\u4e24\u4e2a\u4eba\u8db3\u591f\uff0c\u4e00\u4eba\u5b88\u4e0a\u534a\u591c\uff0c\u4e00\u4eba\u5b88\u4e0b\u534a\u591c\u3002\u7167\u987e\u8d1d\u7eb3\u7d22\u5148\u751f\uff0c\u514d\u9664\u4e86\u4ed6\u7684\u5b88\u591c\u4e49\u52a1\uff0c\u4e8e\u662f\u5c31\u5b9a\u4e0b\u7531\u590f\u62c9\u975e\u548c\u9686\u5947\u62c5\u4efb\u3002\u6e38\u8361\u8005\u5b88\u4e0a\u534a\u591c\uff0c\u6b66\u50e7\u5b88\u4e0b\u534a\u591c\u3002</p><p>\u5bb4\u5e2d\u81ea\u52a8\u6d88\u5931\uff0c\u5404\u4eba\u4e0a\u5e8a\u7761\u89c9\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u51c6\u5907\u660e\u5929\u7684\u6076\u6218\u3002\u867d\u7136\u9488\u5bf9\u8fd9\u6b21\u7684\u4efb\u52a1\uff0c\u4e8b\u5148\u4e5f\u505a\u4e86\u4e00\u5b9a\u7684\u5b89\u6392\uff0c\u53ea\u8981\u4e0d\u51fa\u610f\u5916\u7684\u8bdd\uff0c\u662f\u4e0d\u5fc5\u4ece\u5916\u9762\u4e00\u8def\u51b2\u6740\u8fdb\u53bb\u7684\uff0c\u7528\u6cd5\u672f\u8eb2\u8fc7\u4ea1\u7075\uff0c\u6f5c\u884c\u800c\u5165\u76f4\u6363\u8001\u5de2\u5c31\u884c\u3002\u4f46\u5c31\u7b97\u4e00\u5207\u987a\u5229\uff0c\u6700\u540e\u5bf9\u4e0a\u62c9\u6c83\u514b\uff0c\u603b\u4e5f\u662f\u514d\u4e0d\u4e86\u4e00\u573a\u6076\u6218\u3002</p><p>\u62c9\u6c83\u514b\u8fd9\u79cd\u5927\u5deb\u5996\uff0c\u4e00\u822c\u6765\u8bf4\u8eab\u8fb9\u4e0d\u4f1a\u524d\u547c\u540e\u62e5\u5730\u5e26\u7740\u4e00\u5927\u5806\u968f\u4ece\uff0c\u66f4\u53ef\u80fd\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u8fd9\u662f\u673a\u4f1a\u3002\u4f46\u5c31\u7b97\u662f\u6700\u7406\u60f3\u7684\u60c5\u51b5\uff0c\u4ee5\u4e94\u5bf9\u4e00\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u7a33\u64cd\u80dc\u5238\uff0c\u6bd5\u7adf\uff0c\u5bf9\u624b\u662f\u4e00\u4e2a\u4e24\u5343\u591a\u5e74\u7684\u8001\u5deb\u5996\uff0c\u6614\u65e5\u9b54\u6cd5\u5e1d\u56fd\u8010\u745f\u745e\u5c14\u7684\u5927\u5965\u672f\u5e08\u3002</p><p>\u5bf9\u4e8e\u6885\u83f2\u65af\u548c\u8d1d\u7eb3\u7d22\u6765\u8bf4\uff0c\u6b64\u4e8b\u662f\u804c\u8d23\u6240\u5728\uff0c\u65e0\u8bba\u60c5\u613f\u4e0d\u60c5\u613f\u90fd\u65e0\u6cd5\u63a8\u5374\uff1b\u5bf9\u4e8e\u9686\u5947\u6765\u8bf4\uff0c\u62ff\u4eba\u94b1\u8d22\uff0c\u4e0e\u4eba\u5356\u547d\uff0c\u6240\u8c13\u5bcc\u8d35\u9669\u4e2d\u6c42\uff0c\u4ed6\u5927\u7ea6\u4e5f\u4ed7\u7740\u81ea\u5df1\u4e00\u8eab\u672c\u9886\uff0c\u4e0d\u628a\u5deb\u5996\u653e\u5728\u773c\u91cc\uff1b\u590f\u62c9\u975e\u5219\u53cd\u6b63\u5949\u547d\u800c\u6765\uff0c\u53ea\u7ba1\u5e26\u8def\uff1b\u81f3\u4e8e\u79d1\u6069\uff0c\u5c31\u5b9e\u5728\u4e0d\u6e05\u695a\u4ed6\u5230\u5e95\u6253\u4ec0\u4e48\u4e3b\u610f\u4e86\u3002</p><p>\u743c\u6069\u5219\u53e6\u6709\u60f3\u6cd5\u3002</p><p>\u4e8b\u5230\u5982\u4eca\uff0c\u4ed6\u57fa\u672c\u4e5f\u76f8\u4fe1\u80af\u5b9a\u662f\u62c9\u6c83\u514b\u8fd9\u8001\u5deb\u5996\u5bfc\u81f4\u4e86\u8fd9\u573a\u761f\u75ab\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u5230\u5e95\u662f\u6709\u4ec0\u4e48\u76ee\u7684\uff0c\u6216\u8bb8\u662f\u6709\u610f\uff0c\u6216\u8bb8\u662f\u65e0\u610f\uff0c\u4f46\u4e8b\u5b9e\u662f\u5df2\u7ecf\u9020\u6210\u4e86\u6570\u5343\u4eba\u7684\u6b7b\u4ea1\uff0c\u65e0\u6570\u5e78\u798f\u5bb6\u5ead\u5c31\u6b64\u6bc1\u4e8e\u4e00\u65e6\u3002\u5904\u4e8e\u672c\u80fd\u7684\u540c\u60c5\u548c\u540c\u4ec7\u654c\u5ffe\uff0c\u743c\u6069\u4e5f\u4e0d\u53ef\u80fd\u7ad9\u5728\u5deb\u5996\u8fd9\u8fb9\uff1b\u4f46\u8981\u4ed6\u5f53\u771f\u5e2e\u52a9\u6885\u83f2\u65af\u7b49\u4eba\u628a\u62c9\u6c83\u514b\u780d\u4e86\u2014\u2014\u4e14\u4e0d\u8bba\u80fd\u4e0d\u80fd\u6210\u529f\u2014\u2014\u5374\u4e5f\u9887\u72af\u8e0c\u8e87\u3002</p><p>\u780d\u4e86\u62c9\u6c83\u514b\uff0c\u600e\u4e48\u56de\u53bb\u5411\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4ea4\u4ee3\u3002</p><p>\u5c06\u6765\u56de\u4e86\u9634\u9b42\u57ce\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u95ee\uff1a\u201c\u743c\u6069\uff0c\u4e8b\u60c5\u529e\u5f97\u5982\u4f55\u3002\u201d</p><p>\u743c\u6069\u56de\u7b54\u8bf4\uff1a\u201c\u6211\u53bb\u4e86\u535a\u5f97\u4e4b\u95e8\uff0c\u4e5f\u627e\u5230\u4e86\u62c9\u6c83\u514b\u5148\u751f\uff0c\u4f46\u6211\u5e2e\u51e0\u4e2a\u4eba\u628a\u4ed6\u780d\u4e86\u2026\u2026\u201d</p><p>\u8fd9\u79cd\u5bf9\u8bdd\u5982\u679c\u51fa\u73b0\uff0c\u63a5\u4e0b\u6765\u4e0d\u95ee\u53ef\u77e5\uff0c\u80af\u5b9a\u5c31\u662f\u743c\u6069\u81ea\u5df1\u8981\u88ab\u63a8\u51fa\u53bb\u780d\u4e86\u3002\u9001\u4fe1\u9001\u5230\u628a\u6536\u4fe1\u4eba\u780d\u4e86\u7684\u5730\u6b65\uff0c\u4f7f\u8005\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u597d\u4e0b\u573a\u4e48\u3002</p><p>\u4ed6\u5934\u75bc\u7740\uff0c\u4e0d\u77e5\u9053\u5982\u4f55\u662f\u597d\u3002\u8fd9\u4ef6\u4e8b\u4ece\u4e00\u5f00\u59cb\u5c31\u68d8\u624b\u65e0\u6bd4\uff0c\u5176\u4e2d\u63ba\u6742\u7684\u4e8b\u60c5\u592a\u591a\uff0c\u6839\u672c\u6ca1\u529e\u6cd5\u76f4\u622a\u4e86\u5f53\u5730\u89e3\u51b3\uff0c\u53ea\u80fd\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\u3002\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u5728\u8fd9\u652f\u961f\u4f0d\u91cc\uff0c\u743c\u6069\u53ea\u6015\u8fde\u5de8\u9b54\u5c71\u8109\u90fd\u8fdb\u4e0d\u6765\uff0c\u66f4\u522b\u8bf4\u89c1\u5230\u62c9\u6c83\u514b\u2026\u2026\u4f46\u5982\u4eca\u660e\u5929\u5c31\u8981\u89c1\u5230\u62c9\u6c83\u514b\u4e86\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u8fd9\u644a\u9ebb\u70e6\u600e\u4e48\u5904\u7406\u5462\u3002</p><p>\u4ed6\u5fc3\u795e\u4e0d\u5b9a\uff0c\u7ffb\u6765\u8986\u53bb\u7761\u4e0d\u7740\uff0c\u6700\u7ec8\u5f3a\u8feb\u81ea\u5df1\u6536\u655b\u5fc3\u795e\uff0c\u8fd0\u529f\u8c03\u606f\uff0c\u518d\u5ea6\u9192\u6765\u65f6\uff0c\u5df2\u7ecf\u591c\u6df1\u4eba\u9759\u3002\u4ed6\u770b\u770b\u65c1\u8fb9\uff0c\u53d1\u73b0\u590f\u62c9\u975e\u7684\u5e8a\u4e0a\u662f\u7a7a\u7740\u7684\uff0c\u6b66\u50e7\u5728\u8fd8\u5728\u547c\u547c\u5927\u7761\uff0c\u770b\u6837\u5b50\u8fd8\u6ca1\u5230\u4e0b\u534a\u591c\uff0c\u6ca1\u5230\u6362\u73ed\u7684\u65f6\u5019\u3002</p><p>\u5deb\u5e08\u95ed\u4e0a\u773c\u775b\u9ed8\u9ed8\u5730\u56de\u5fc6\u7740\u81ea\u5df1\u6700\u8fd1\u5b66\u4f1a\u7684\u51e0\u4e2a\u9b54\u6cd5\uff0c\u521a\u521a\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\uff0c\u4ed6\u8fd8\u5e76\u4e0d\u5341\u5206\u719f\u7ec3\uff0c\u9700\u8981\u4e0d\u65ad\u7684\u91cd\u590d\u5f3a\u5316\u3002\u6b63\u51a5\u60f3\u95f4\uff0c\u542c\u89c1\u8f7b\u8f7b\u7684\u811a\u6b65\u58f0\u4ece\u5916\u9762\u8d70\u8fdb\u6765\uff0c\u743c\u6069\u7528\u773c\u89d2\u4f59\u5149\u77a5\u4e86\u77a5\uff0c\u89c1\u662f\u90a3\u4e2a\u534a\u7cbe\u7075\u6e38\u8361\u8005\u590f\u62c9\u975e\u3002</p><p>\u590f\u62c9\u975e\u8d70\u8fdb\u5e10\u7bf7\uff0c\u63a8\u9192\u6b66\u50e7\uff0c\u81ea\u5df1\u56de\u5e8a\u4e0a\u8eba\u4e0b\u7761\u89c9\u3002\u6b66\u50e7\u722c\u8d77\u6765\uff0c\u5957\u4e0a\u4ed6\u90a3\u4ef6\u7c97\u5e03\u7070\u888d\uff0c\u8d70\u51fa\u53bb\u5b88\u591c\u3002\u663e\u7136\uff0c\u5230\u6362\u73ed\u7684\u65f6\u5019\u4e86\u3002</p><p>\u743c\u6069\u4e5f\u5e76\u6ca1\u6709\u591a\u4e48\u5728\u610f\uff0c\u4f9d\u65e7\u7ee7\u7eed\u51a5\u60f3\uff0c\u56de\u5fc6\u9b54\u6cd5\u3002\u4f46\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5927\u7ea6\u5341\u5206\u949f\uff0c\u4ed6\u7a81\u7136\u53c8\u542c\u89c1\u4e86\u8f7b\u5fae\u7684\u52a8\u9759\uff0c\u6709\u4eba\u4ece\u5e8a\u4e0a\u6084\u6084\u722c\u4e86\u8d77\u6765\u3002\u773c\u89d2\u4f59\u5149\u77a5\u53bb\uff0c\u6b63\u662f\u90a3\u4f4d\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u3002</p><p>\u4ed6\u534a\u591c\u4e0d\u7761\u89c9\u8d77\u6765\u505a\u4ec0\u4e48\uff1f</p></article>"}}}]);
