(window.webpackJsonp=window.webpackJsonp||[]).push([[1343],{1366:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516b\u8282 \u91ce\u5408",content:"<article><h2>\u7b2c\u516b\u8282 \u91ce\u5408</h2><p>\u79d1\u6069\u8f7b\u624b\u8f7b\u811a\u5730\u8d70\u51fa\u5e10\u7bf7\uff0c\u63a5\u7740\u5c31\u542c\u89c1\u4ed6\u4f4e\u58f0\u548c\u9686\u5947\u6253\u62db\u547c\uff0c\u4e24\u4eba\u4f3c\u4e4e\u95f2\u8c08\u8d77\u6765\u3002\u743c\u6069\u5fae\u5fae\u76b1\u8d77\u7709\u5934\uff0c\u5947\u602a\uff0c\u8fd9\u5bb6\u4f19\u534a\u591c\u7761\u4e0d\u7740\uff0c\u8dd1\u51fa\u6765\u627e\u4e2a\u4eba\u804a\u5929\u8c08\u5fc3\u4e48\u3002</p><p>\u5e10\u7bf7\u7684\u9694\u97f3\u6548\u679c\u4e0d\u9519\uff0c\u5916\u9762\u4e24\u4eba\u7684\u8c08\u8bdd\u53c8\u5f88\u8f7b\uff0c\u6015\u5435\u9192\u5927\u5bb6\u7761\u89c9\uff0c\u743c\u6069\u542c\u4e0d\u6e05\u695a\u3002\u4ed6\u8f6c\u4e86\u8f6c\u5ff5\u5934\uff0c\u6084\u58f0\u8bf5\u5492\u7ed9\u81ea\u5df1\u52a0\u4e86\u4e2a\u7b80\u5355\u7684\u5c0f\u620f\u6cd5\uff0c\u542c\u529b\u987f\u65f6\u589e\u5f3a\u4e86\u4e00\u500d\uff0c\u987f\u65f6\u5916\u9762\u4e24\u4eba\u7684\u8c08\u8bdd\u6e05\u6670\u591a\u4e86\u3002</p><p>\u542c\u8d77\u6765\uff0c\u79d1\u6069\u548c\u9686\u5947\u4e0d\u8fc7\u662f\u5728\u95f2\u804a\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u3002\u743c\u6069\u751a\u81f3\u90fd\u6709\u4e9b\u6000\u7591\u81ea\u5df1\u662f\u591a\u5fc3\u4e86\uff0c\u6b63\u5f53\u4ed6\u51c6\u5907\u64a4\u9500\u9b54\u6cd5\uff0c\u7ee7\u7eed\u51a5\u60f3\u51c6\u5907\u6cd5\u672f\u7684\u65f6\u5019\uff0c\u7a81\u7136\u542c\u5230\u79d1\u6069\u8bf4\u4e86\u4e00\u53e5\u8bdd\u3002</p><p>\u90a3\u53e5\u8bdd\u8bf4\u5f97\u5f88\u9690\u6666\uff0c\u4f46\u5176\u4e2d\u7684\u610f\u601d\u5374\u6709\u4e9b\u8010\u4eba\u5bfb\u5473\uff0c\u50cf\u662f\u5728\u62c9\u62e2\u6216\u8005\u62db\u63fd\uff1b\u743c\u6069\u8b66\u60d5\u8d77\u6765\uff0c\u7ee7\u7eed\u542c\u4e0b\u53bb\uff0c\u5c31\u89c1\u4e24\u4eba\u8c08\u8bba\u4e86\u4e00\u4f1a\uff0c\u79d1\u6069\u7a81\u7136\u8bdd\u98ce\u4e00\u8f6c\uff0c\u63d0\u51fa\u8981\u9080\u8bf7\u9686\u5947\u201c\u51fa\u53bb\u968f\u4fbf\u6563\u6563\u6b65\u3002\u201d</p><p>\u5f00\u2026\u2026\u5f00\u4ec0\u4e48\u73a9\u7b11\uff0c\u9686\u5947\u73b0\u5728\u53ef\u662f\u5728\u5b88\u591c\uff0c\u4ed6\u8981\u662f\u64c5\u79bb\u804c\u5b88\uff0c\u5230\u65f6\u5019\u6709\u602a\u7269\u7a81\u88ad\uff0c\u8fd9\u5e10\u7bf7\u91cc\u7684\u4eba\u6b7b\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u6b7b\u7684\u3002\u8fd9\u4e2a\u6c83\u91d1\u7267\u5e08\u96be\u9053\u8111\u5b50\u574f\u6389\u4e86\uff0c\u5c45\u7136\u63d0\u8fd9\u79cd\u95ee\u9898\u3002</p><p>\u66f4\u4ee4\u4eba\u60ca\u8bb6\u7684\u662f\uff0c\u9686\u5947\u5c45\u7136\u7b54\u5e94\u4e86\u3002</p><p>\u4e24\u4eba\u8f7b\u8f7b\u8d77\u8eab\uff0c\u811a\u6b65\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u743c\u6069\u7d27\u7d27\u76b1\u7740\u7709\u5934\u3002\u4ed6\u4e0d\u77e5\u9053\u521a\u624d\u79d1\u6069\u548c\u9686\u5947\u5728\u5916\u9762\u5230\u5e95\u5728\u641e\u4ec0\u4e48\u540d\u5802\uff0c\u4ed6\u7684\u9b54\u6cd5\u53ea\u80fd\u8ba9\u4ed6\u542c\u89c1\u5bf9\u8bdd\uff0c\u4f46\u770b\u4e0d\u89c1\u666f\u8c61\uff0c\u5f88\u53ef\u80fd\u4ed6\u4eec\u6697\u4e2d\u5728\u73a9\u4ec0\u4e48\u5c0f\u52a8\u4f5c\u3002</p><p>\u8fd9\u4e9b\u90fd\u53ea\u662f\u731c\u6d4b\uff0c\u4f46\u63a5\u4e0b\u6765\u600e\u4e48\u529e\uff1f</p><p>\u743c\u6069\u6b63\u5728\u72b9\u8c6b\uff0c\u5c31\u89c1\u7761\u5728\u8eab\u65c1\u7684\u6885\u83f2\u65af\u4eff\u4f5b\u662f\u65e0\u610f\u8bc6\u5730\u7ffb\u4e86\u4e2a\u8eab\uff0c\u4fa7\u7740\u9762\u5bf9\u743c\u6069\u3002\u5979\u7684\u773c\u775b\u7741\u5f00\uff0c\u78a7\u7eff\u8272\u53cc\u7738\u5373\u4fbf\u5728\u591c\u95f4\u4e5f\u663e\u5f97\u90a3\u6837\u6e05\u6f88\u900f\u660e\uff0c\u5c11\u5973\u7684\u7709\u6bdb\u5fae\u5fae\u6311\u8d77\uff0c\u671d\u743c\u6069\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u793a\u610f\u4ed6\u53bb\u770b\u770b\u3002</p><p>\u4e3a\u4ec0\u4e48\u603b\u662f\u6211\u3002</p><p>\u867d\u7136\u5fc3\u91cc\u62b1\u6028\u7740\uff0c\u4f46\u743c\u6069\u4e5f\u6ca1\u4ec0\u4e48\u53cd\u5bf9\u610f\u89c1\uff0c\u4ed6\u77e5\u9053\u8fd9\u79cd\u5b89\u6392\u5f88\u5408\u7406\u3002\u6885\u83f2\u65af\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u957f\u4e8e\u51b2\u950b\u9677\u9635\uff0c\u6325\u5251\u6740\u654c\uff0c\u4f46\u5728\u8ddf\u8e2a\u63a2\u542c\u65b9\u9762\u5e76\u4e0d\u64c5\u957f\uff1b\u590f\u62c9\u975e\u5012\u662f\u5f88\u64c5\u957f\u7684\uff0c\u4f46\u6b64\u4e8b\u53c8\u4e0d\u65b9\u4fbf\u8ba9\u4ed6\u77e5\u9053\u3002\u743c\u6069\u662f\u4e2a\u5deb\u5e08\uff0c\u867d\u8bf4\u4e5f\u6ca1\u7ecf\u8fc7\u4e13\u4e1a\u7684\u8c0d\u62a5\u8bad\u7ec3\uff0c\u4f46\u4ed6\u81f3\u5c11\u6709\u5404\u79cd\u9b54\u6cd5\u8f85\u52a9\uff0c\u770b\u5f97\u8fdc\uff0c\u542c\u5f97\u8fdc\uff0c\u8fd8\u80fd\u628a\u81ea\u5df1\u9690\u5f62\uff0c\u5076\u5c14\u517c\u804c\u4e00\u4e0b\u63a2\u5b50\u4e5f\u6ca1\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u4f55\u51b5\u743c\u6069\u8fd8\u7ec3\u8fc7\u51e0\u5e74\u8f7b\u529f\uff0c\u8eab\u624b\u6b65\u4f10\u90fd\u8fd8\u7b97\u654f\u6377\u3002\u6885\u83f2\u65af\u81ea\u7136\u4e0d\u77e5\u9053\u4e16\u754c\u4e0a\u6709\u201c\u8f7b\u529f\u201d\u8fd9\u79cd\u4e1c\u897f\uff0c\u4f46\u548c\u743c\u6069\u8ba4\u8bc6\u8fd9\u4e48\u4e45\u4e86\uff0c\u81ea\u7136\u4e5f\u77e5\u9053\u4ed6\u548c\u5bfb\u5e38\u90a3\u4e9b\u75c5\u6079\u6079\u7684\u5deb\u5e08\u662f\u4e0d\u540c\u7684\uff0c\u8eab\u624b\u8981\u597d\u5f97\u591a\u4e86\u3002</p><p>\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u8868\u793a\u77e5\u9053\uff0c\u4ed6\u6162\u6162\u722c\u8d77\u8eab\u6765\uff0c\u4e0b\u5e8a\u7a7f\u4e0a\u957f\u888d\uff0c\u8d70\u51fa\u5e10\u7bf7\u3002\u521a\u624d\u542c\u79d1\u6069\u548c\u9686\u5947\u7684\u811a\u6b65\u58f0\uff0c\u662f\u5411\u897f\u8fb9\u53bb\u7684\uff0c\u90a3\u91cc\u662f\u4e00\u7247\u8fde\u7ef5\u4e0d\u7edd\u7684\u6811\u6797\u3002</p><p>\u8352\u90ca\u91ce\u5916\uff0c\u602a\u7269\u6a2a\u884c\uff0c\u6b64\u65f6\u53c8\u662f\u6df1\u591c\uff0c\u6709\u4ec0\u4e48\u597d\u6563\u6b65\u7684\uff0c\u771f\u662f\u83ab\u540d\u5176\u5999\u3002</p><p>\u4ed6\u5927\u7ea6\u5224\u65ad\u4e86\u4e00\u4e0b\u65b9\u4f4d\uff0c\u5c0f\u5fc3\u5730\u8ddf\u4e86\u8fc7\u53bb\u3002\u521a\u624d\u90a3\u4e2a\u6cd5\u672f\u7684\u6548\u679c\u8fd8\u6ca1\u6d88\u5931\uff0c\u4ed6\u7684\u542c\u89c9\u8f83\u4e4b\u5e73\u65f6\u5206\u4e3a\u7075\u654f\uff0c\u4f55\u51b5\u6b64\u65f6\u662f\u6df1\u591c\uff0c\u4e07\u7c41\u4ff1\u5bc2\uff0c\u53c8\u6070\u597d\u98ce\u5411\u987a\u7740\u81ea\u5df1\u8fd9\u8fb9\uff0c\u8fdc\u5904\u7ec6\u5fae\u7684\u52a8\u9759\u90fd\u9003\u4e0d\u8fc7\u4ed6\u7684\u8033\u6735\u3002\u5927\u7ea6\u8d70\u4e86\u5341\u6765\u5206\u949f\uff0c\u4ed6\u9690\u9690\u7ea6\u7ea6\u7684\u542c\u89c1\u4e86\u524d\u65b9\u4e24\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u8e29\u5728\u539a\u539a\u7684\u843d\u53f6\u4e0a\u53d1\u51fa\u6c99\u6c99\u8f7b\u54cd\uff0c\u95f4\u6216\u8fd8\u6709\u4f4e\u5fae\u7684\u8c08\u7b11\u58f0\u968f\u98ce\u4f20\u6765\uff0c\u6b63\u662f\u79d1\u6069\u548c\u9686\u5947\u3002</p><p>\u743c\u6069\u4e0d\u6562\u8fc7\u5206\u9760\u8fd1\uff0c\u6015\u88ab\u53d1\u89c9\uff0c\u53ea\u80fd\u4e0d\u8fdc\u4e0d\u8fd1\u5730\u8ddf\u968f\u7740\u3002\u53c8\u8d70\u4e86\u5927\u7ea6\u5341\u5206\u949f\uff0c\u524d\u9762\u7684\u811a\u6b65\u58f0\u505c\u4f4f\u4e86\uff0c\u63a5\u7740\u9690\u9690\u6709\u8c08\u8bdd\u58f0\u4f20\u6765\uff0c\u4f3c\u4e4e\u4e0d\u6b62\u9686\u5947\u548c\u79d1\u6069\uff0c\u53c8\u591a\u4e86\u5176\u4ed6\u4eba\uff0c\u6df7\u6742\u5728\u4e00\u8d77\u4e00\u65f6\u542c\u4e0d\u6e05\u695a\u3002</p><p>\u743c\u6069\u6b63\u8981\u51dd\u795e\u7ec6\u542c\uff0c\u6cd5\u672f\u6548\u679c\u5374\u6070\u597d\u5728\u6b64\u65f6\u6d88\u5931\u4e86\uff0c\u4ed6\u987f\u65f6\u518d\u4e5f\u542c\u4e0d\u5230\u4ec0\u4e48\u8c08\u8bdd\u3002\u8fd9\u4e5f\u6ca1\u529e\u6cd5\uff0c\u8fd9\u79cd\u8fdc\u76ee\u3001\u9510\u8033\u4e4b\u7c7b\u7684\u6cd5\u672f\uff0c\u5f80\u5f80\u90fd\u6301\u7eed\u4e0d\u4e86\u592a\u957f\u65f6\u95f4\u7684\u3002\u8fd9\u53ea\u662f\u4e2a\u5f88\u7b80\u5355\u7684\u5c0f\u6cd5\u672f\uff0c\u4f46\u743c\u6069\u4e5f\u4ece\u4e0d\u66fe\u60f3\u8fc7\u8981\u7ecf\u5e38\u53bb\u5077\u542c\uff0c\u4eca\u5929\u4e5f\u53ea\u51c6\u5907\u4e86\u8fd9\u4e00\u4e2a\uff0c\u7528\u5b8c\u5c31\u6ca1\u4e86\uff0c\u4e34\u65f6\u6ca1\u6cd5\u518d\u6765\u4e00\u6b21\u3002</p><p>\u6ca1\u529e\u6cd5\uff0c\u53ea\u80fd\u518d\u9760\u8fd1\u70b9\u4e86\u3002</p><p>\u5c0f\u5fc3\u4e3a\u4e0a\uff0c\u4ed6\u7d22\u6027\u5148\u7ed9\u81ea\u5df1\u52a0\u4e86\u4e2a\u9690\u5f62\u672f\uff0c\u770b\u7740\u81ea\u5df1\u7684\u8eab\u4f53\u5728\u7a7a\u6c14\u4e2d\u5b8c\u5168\u6de1\u53bb\u6d88\u5931\uff0c\u7136\u540e\u6162\u6162\u524d\u8fdb\u3002\u9690\u5f62\u672f\u53ea\u80fd\u9690\u9041\u8eab\u5f62\uff0c\u4e0d\u662f\u771f\u6b63\u6d88\u5931\uff0c\u4ed6\u7684\u811a\u6b65\u8e29\u5728\u843d\u53f6\u4e0a\uff0c\u4f9d\u65e7\u8fd8\u662f\u6709\u6c99\u6c99\u7684\u54cd\u52a8\uff0c\u6b64\u65f6\u591c\u6df1\u4eba\u9759\uff0c\u4ed6\u6015\u88ab\u524d\u9762\u7684\u4eba\u542c\u5230\uff0c\u53ea\u80fd\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u8e51\u624b\u8e51\u811a\u5730\u524d\u8fdb\uff0c\u4eff\u4f5b\u505a\u8d3c\u4f3c\u7684\u3002</p><p>\u8d70\u4e86\u4e00\u6bb5\uff0c\u6e10\u6e10\u5730\u53c8\u542c\u89c1\u4e86\u4eba\u58f0\uff0c\u5374\u4e0d\u662f\u8bf4\u8bdd\uff0c\u800c\u662f\u4e00\u79cd\u9887\u6709\u4e9b\u5947\u602a\u7684\u58f0\u97f3\uff0c\u4eff\u4f5b\u662f\u5728\u5598\u606f\u3001\u547b\u541f\uff0c\u6df7\u5408\u7740\u6ee1\u8db3\u7684\u6109\u60a6\uff0c\u5012\u50cf\u662f\u7537\u5973\u6b22\u597d\u3002\u743c\u6069\u6115\u7136\u60ca\u8bb6\uff0c\u4ed6\u5bf9\u8fd9\u58f0\u97f3\u81ea\u7136\u4e0d\u964c\u751f\uff0c\u4e5f\u5b8c\u5168\u77e5\u9053\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\u4e8b\u60c5\uff0c\u81ea\u5df1\u5c31\u65e0\u6570\u6b21\u505a\u8fc7\u3002\u4f46\u8fd9\u79cd\u8352\u90ca\u91ce\u5730\uff0c\u6df1\u66f4\u534a\u591c\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u58f0\u97f3\u2026\u2026</p><p>\u9760\uff0c\u4e0d\u4f1a\u90a3\u4e24\u4e2a\u5bb6\u4f19\u662f\u2026\u2026</p><p>\u743c\u6069\u4e00\u9635\u6076\u5bd2\uff0c\u5168\u8eab\u5fcd\u4e0d\u4f4f\u90fd\u6cdb\u8d77\u5bd2\u7c92\u6765\u3002\u5f3a\u5fcd\u4f4f\u60f3\u6389\u5934\u9003\u8dd1\u7684\u51b2\u52a8\uff0c\u4ed6\u8f7b\u624b\u8f7b\u811a\u5730\u8d70\u8fc7\u53bb\u3002\u968f\u7740\u4ed6\u7684\u9760\u8fd1\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u6e05\u6670\u5730\u4f20\u5230\u8033\u4e2d\uff0c\u4e5f\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u6700\u7ec8\u5728\u6811\u6797\u8fb9\u7f18\uff0c\u743c\u6069\u770b\u89c1\u4e86\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u548c\u6b66\u50e7\u9686\u5947\uff0c\u4ed6\u4eec\u6b63\u5728\u6797\u8fb9\u7684\u4e00\u4e2a\u5c0f\u5c71\u6c9f\u4e2d\u3002</p><p>\u6b63\u5728\u505a\u67d0\u79cd\u743c\u6069\u975e\u5e38\u719f\u6089\u7684\u5267\u70c8\u6d3b\u585e\u8fd0\u52a8\u3002</p><p>\u552f\u4e00\u503c\u5f97\u5e86\u5e78\u7684\u662f\uff0c\u5e76\u6ca1\u6709\u51fa\u73b0\u743c\u6069\u9884\u6599\u4e2d\u6700\u6050\u6016\u7684\u4e00\u5e55\u3002\u8fd9\u4e24\u4f4d\u5e76\u975eBL\uff0c\u4ed6\u4eec\u7684\u6027\u53d6\u5411\u5f88\u6b63\u5e38\uff0c\u81f3\u5c11\u6682\u65f6\u770b\u8d77\u6765\u5f88\u6b63\u5e38\uff0c\u56e0\u4e3a\u4ed6\u4eec\u662f\u5728\u548c\u5973\u4eba\u4e0a\u5e8a\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6570\u91cf\u4e0a\u7684\u539f\u56e0\u2014\u2014\u7b80\u5355\u6765\u8bf4\uff0c\u5c31\u662f\u6709\u4e24\u4e2a\u7537\u4eba\uff0c\u5374\u53ea\u6709\u4e00\u4e2a\u5973\u4eba\uff0c\u65e0\u6cd5\u5e73\u5747\u5206\u914d\uff0c\u6240\u4ee5\u5728\u73a9\u4f20\u8bf4\u4e2d\u76843P\u7f62\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u6b66\u50e7\u8eab\u4e0a\u7684\u7c97\u5e03\u7070\u888d\u65e9\u5df2\u7ecf\u8131\u53bb\uff0c\u4ed6\u6b63\u4ef0\u9762\u8eba\u5728\u5730\u4e0a\u3002\u4e00\u4e2a\u8910\u8272\u957f\u53d1\u7684\u5973\u5b50\u6b63\u8dea\u5750\u5728\u4ed6\u7684\u8170\u95f4\uff0c\u4e24\u4eba\u4e0b\u4f53\u7d27\u5bc6\u7ed3\u5408\u7740\uff0c\u6b66\u50e7\u8fd0\u7528\u81ea\u5df1\u8d85\u5f3a\u7684\u8170\u8179\u529b\u91cf\uff0c\u51e0\u4e4e\u662f\u4e00\u53e3\u6c14\u4e0d\u505c\u5730\u633a\u8eab\u51b2\u523a\uff0c\u6beb\u4e0d\u505c\u6b47\u3002\u90a3\u4e2a\u5973\u5b50\u4eff\u4f5b\u7ecf\u53d7\u4e0d\u4f4f\u8fd9\u6837\u6fc0\u70c8\u7684\u51b2\u51fb\uff0c\u53cc\u624b\u7d27\u7d27\u6309\u4f4f\u6b66\u50e7\u7684\u80f8\u819b\uff0c\u4e00\u53cc\u4e30\u6ee1\u7684\u4e73\u7403\u968f\u7740\u8eab\u4f53\u8d77\u4f0f\u800c\u4e0a\u4e0b\u6643\u52a8\uff0c\u957f\u53d1\u62ab\u6563\u4e0b\u6765\uff0c\u906e\u4f4f\u4e86\u5927\u534a\u8fb9\u8138\u3002</p><p>\u6b64\u65f6\u662f\u6df1\u591c\uff0c\u6708\u4eae\u8eb2\u5165\u4e91\u5c42\uff0c\u661f\u5149\u6666\u6697\uff0c\u743c\u6069\u53c8\u6015\u88ab\u4ed6\u4eec\u5bdf\u89c9\uff0c\u4e0d\u6562\u9760\u5f97\u592a\u8fd1\uff0c\u4e5f\u770b\u4e0d\u6e05\u8fd9\u5973\u5b50\u7684\u9762\u5bb9\uff0c\u53ea\u89c9\u8170\u8eab\u7ec6\u7a84\uff0c\u81c0\u4e18\u633a\u7fd8\uff0c\u8eab\u6750\u975e\u5e38\u4e4b\u4f73\uff0c\u663e\u7136\u662f\u4e2a\u6f02\u4eae\u7f8e\u4eba\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u662f\u8c01\uff0c\u600e\u4e48\u4f1a\u6df1\u66f4\u534a\u591c\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u8fd8\u548c\u8fd9\u4e24\u4eba\u52fe\u642d\u4e0a\u4e86\u3002</p><p>\u90a3\u8fb9\u6c83\u91d1\u7267\u5e08\u5374\u6709\u4e9b\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff0c\u4ed6\u5df2\u7ecf\u8131\u53bb\u957f\u88e4\uff0c\u8d64\u88f8\u7740\u4e0b\u534a\u8eab\uff0c\u8d70\u5230\u5973\u5b50\u80cc\u540e\u3002\u6b63\u5728\u51f6\u731b\u8fdb\u653b\u7684\u6b66\u50e7\u505c\u4e86\u4e0b\u6765\uff0c\u7b49\u5f85\u7740\uff0c\u7267\u5e08\u53cc\u624b\u63e1\u7740\u5973\u5b50\u7684\u7ea4\u8170\uff0c\u5bf9\u51c6\u90e8\u4f4d\u7528\u529b\u4e00\u633a\uff0c\u63d2\u5165\u5979\u4e0b\u4f53\u7684\u53e6\u5916\u4e00\u4e2a\u6d1e\u7a74\uff0c\u5f00\u59cb\u633a\u8170\u8fd0\u52a8\u8d77\u6765\u3002</p><p>\u8fd9\u79cd\u4e8b\u60c5\u9700\u8981\u6bd4\u8f83\u826f\u597d\u7684\u9ed8\u5951\uff0c\u8fdb\u9000\u4e4b\u95f4\u8981\u914d\u5408\u597d\uff0c\u5426\u5219\u53cd\u800c\u8d25\u574f\u5174\u81f4\uff0c\u7ed3\u679c\u5c31\u662f\u4e00\u4e2a\u4eba\u723d\u4e86\uff0c\u53e6\u5916\u4e00\u4e2a\u4eba\u767d\u767d\u5f53\u966a\u8bfb\u3002\u8fd9\u4e24\u4eba\u663e\u7136\u4e0d\u662f\u7ecf\u5e38\u5408\u4f5c\uff0c\u5f7c\u6b64\u95f4\u914d\u5408\u5e76\u4e0d\u719f\u7ec3\u2014\u2014\u4e0d\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u6b66\u50e7\u9686\u5947\u663e\u7136\u521d\u7ecf\u6b64\u9053\uff0c\u5b8c\u5168\u4e0d\u77e5\u9053\u5982\u4f55\u914d\u5408\u3002\u6240\u4ee5\u7267\u5e08\u4e00\u8fb9\u5728\u6109\u5feb\u5730\u4eab\u7528\u7740\u5973\u5b50\u7684\u540e\u5ead\uff0c\u4e00\u8fb9\u4e0d\u5fd8\u6307\u70b9\u7740\u540c\u4f34\u7684\u52a8\u4f5c\u3002</p><p>\u6b66\u50e7\u5b66\u5f97\u5f88\u5feb\uff0c\u4ed6\u9a6c\u4e0a\u5c31\u638c\u63e1\u4e86\u8981\u7d27\uff0c\u5f88\u5feb\uff0c\u4e24\u4eba\u4f60\u8fdb\u6211\u9000\u3001\u6211\u653b\u4f60\u5b88\u3001\u6b64\u6d88\u5f7c\u957f\uff0c\u534f\u8c03\u597d\u5f7c\u6b64\u8282\u594f\uff0c\u4eff\u4f5b\u6f14\u7ec3\u8fc7\u65e0\u6570\u904d\u4f3c\u7684\u3002\u7c97\u91cd\u7684\u5598\u606f\u58f0\u548c\u67d4\u5a9a\u8bf1\u4eba\u7684\u5a07\u541f\u6df7\u5408\u5728\u4e00\u8d77\uff0c\u5728\u5bc2\u9759\u7684\u6df1\u591c\u91cc\u968f\u98ce\u4f20\u51fa\uff0c\u542c\u5f97\u8fdc\u5904\u8eb2\u7740\u65c1\u89c2\u7684\u743c\u6069\u90fd\u6709\u4e9b\u6026\u7136\u5fc3\u52a8\u3002</p><p>\u4ed6\u5988\u7684\uff0c\u5927\u5bb6\u4e00\u8d77\u8dd1\u5230\u8fd9\u8352\u5c71\u91ce\u5cad\u6765\uff0c\u6211\u4eec\u53ea\u80fd\u7761\u5e10\u7bf7\uff0c\u4f60\u4eec\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u5c45\u7136\u5077\u5077\u8dd1\u5230\u8fd9\u91cc\u548c\u5973\u4eba\u73a93P\u2026\u2026</p><p>\u8bdd\u867d\u5982\u6b64\uff0c\u743c\u6069\u66f4\u52a0\u7591\u60d1\u7684\u662f\u8fd9\u5973\u5b50\u7684\u8eab\u4efd\u3002\u5de8\u9b54\u5c71\u8109\u53ef\u4e0d\u662f\u5bfb\u5e38\u4eba\u6562\u968f\u4fbf\u6765\u7684\u5730\u65b9\uff0c\u4fbf\u5982\u9093\u80af\u8fd9\u79cd\u5927\u5546\u4eba\uff0c\u5e26\u7740\u62a4\u536b\u8fdb\u5c71\u6765\uff0c\u4e5f\u5dee\u70b9\u88ab\u98df\u4eba\u9b54\u7ed9\u5f53\u6210\u665a\u9910\u3002\u4e00\u4e2a\u5973\u5b50\u6df1\u66f4\u534a\u591c\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u8fd9\u5b9e\u5728\u8be1\u5f02\u5f97\u7d27\u3002</p><p>\u96be\u9053\u8bf4\u5979\u662f\u6c83\u91d1\u7267\u5e08\u7684\u540c\u4f19\uff0c\u4e00\u8def\u8ddf\u7740\u8fc7\u6765\uff1f</p><p>\u8fd9\u4e2a\u5ff5\u5934\u77ac\u5373\u95ea\u8fc7\uff0c\u743c\u6069\u5fc3\u5934\u4e00\u51db\u3002\u8fd9\u5f88\u6709\u53ef\u80fd\uff0c\u4e5f\u662f\u76ee\u524d\u6700\u8bf4\u5f97\u901a\u7684\u89e3\u91ca\uff0c\u603b\u4e0d\u53ef\u80fd\u8bf4\u7267\u5e08\u548c\u6b66\u50e7\u4e24\u4eba\u6070\u597d\u51fa\u6765\u6563\u6b65\uff0c\u7136\u540e\u5c31\u6070\u597d\u649e\u4e0a\u4e2a\u7f8e\u5973\uff0c\u7136\u540e\u4e00\u62cd\u5373\u5408\uff0c\u4e09\u4eba\u5728\u8fd9\u91cc\u6fc0\u60c5\u5927\u6218\u2026\u2026\u9760\uff0c\u4ed6\u4eec\u4e00\u4e0d\u662f\u7384\u5e7b\u5c0f\u8bf4\u4e3b\u89d2\uff0c\u4e8c\u4e0d\u662f\u5e78\u8fd0\u5973\u795e\u6cf0\u6469\u62c9\u7684\u4fe1\u5f92\uff0c\u51ed\u4ec0\u4e48\u80fd\u644a\u4e0a\u8fd9\u79cd\u597d\u4e8b\u3002\u8981\u6709\u8273\u9047\u4e5f\u8be5\u662f\u6211\u8fd9\u4e2a\u7a7f\u8d8a\u8005\u9047\u4e0a\u624d\u5bf9\u2026\u2026</p><p>\u5982\u679c\u662f\u8fd9\u6837\u7684\u8bdd\uff0c\u79d1\u6069\u662f\u65e9\u6709\u6240\u56fe\u2026\u2026\u4ed6\u662f\u8981\u62c9\u62e2\u6b66\u50e7\u9686\u5947\uff1f\u4ed6\u5230\u5e95\u8981\u51c6\u5907\u505a\u4ec0\u4e48\uff1f</p><p>\u6b63\u601d\u5fd6\u95f4\uff0c\u6708\u4eae\u4e0d\u77e5\u4e0d\u89c9\u4ece\u4e91\u5c42\u4e2d\u79fb\u51fa\uff0c\u6e05\u5149\u5982\u6c34\u6d41\u6dcc\u4e0b\u6765\u3002\u8910\u53d1\u5973\u5b50\u88ab\u4e24\u4e2a\u7537\u4eba\u524d\u540e\u5939\u653b\u7740\uff0c\u6b64\u65f6\u5df2\u7ecf\u9999\u6c57\u6dcb\u6f13\uff0c\u5934\u53d1\u88ab\u6253\u6e7f\u4e86\uff0c\u4e00\u7f15\u7f15\u5730\u6cbe\u5728\u8eab\u4e0a\uff0c\u9732\u51fa\u4e86\u8138\u86cb\u6765\u3002</p><p>\u90a3\u662f\u4e00\u5f20\u5e76\u4e0d\u7b97\u5341\u5206\u6f02\u4eae\uff0c\u5374\u975e\u5e38\u5996\u5a9a\u7684\u9762\u5bb9\uff0c\u5c24\u5176\u662f\u5728\u8fd9\u79cd\u6fc0\u70c8\u4ea4\u5408\u7684\u65f6\u5019\uff0c\u4e24\u988a\u901a\u7ea2\u5982\u706b\uff0c\u7709\u773c\u4e2d\u7684\u6625\u610f\u6ee1\u6ea2\u5f97\u4eff\u4f5b\u8981\u6ef4\u51fa\u6765\uff0c\u8138\u4e0a\u5374\u8fd8\u5e26\u7740\u4e00\u4e1d\u82e5\u6709\u82e5\u65e0\u7684\u51b0\u51b7\u7b11\u610f\u3002\u743c\u6069\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u5fc3\u4e2d\u9661\u5730\u4e00\u6c89\uff0c\u4eff\u4f5b\u88ab\u4e00\u76c6\u51b0\u6c34\u5f53\u5934\u6d47\u4e0b\u6765\u2014\u2014\u56e0\u4e3a\u8fd9\u4e2a\u5973\u4eba\u4ed6\u539f\u6765\u8ba4\u8bc6\u3002</p><p>\u5c31\u5728\u524d\u51e0\u5929\uff0c\u4ed6\u4eec\u8fd8\u6253\u8fc7\u4ea4\u9053\u2014\u2014\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u8fd8\u66fe\u7ecf\u5728\u5e8a\u4e0a\u4eb2\u5bc6\u6df1\u5165\u5730\u4ea4\u6d41\u8fc7\uff0c\u4e5f\u5982\u73b0\u5728\u5dee\u4e0d\u591a\uff0c\u867d\u7136\u6700\u540e\u5dee\u70b9\u88ab\u8e22\u4e0b\u5e8a\u3002</p><p>\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u90a3\u4e2a\u539f\u672c\u4ee5\u4e3a\u5df2\u7ecf\u88ab\u6d3b\u57cb\u5728\u73e0\u5b9d\u5730\u4e0b\u548c\u94f6\u9f99\u5e7d\u7075\u4f5c\u4f34\u7684\u5973\u6740\u624b\uff0c\u838e\u73de\u514b\u3002</p></article>"}}}]);
