(window.webpackJsonp=window.webpackJsonp||[]).push([[1355],{1378:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u7075\u9b42\u652b\u53d6",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u7075\u9b42\u652b\u53d6</h2><p>\u6b27\u51ef\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8bf7\u8ddf\u6211\u6765\u2026\u2026\u54e6\uff0c\u7a0d\u7b49\u3002\u201d\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u7740\u8fdc\u5904\u7684\u9686\u5947\u3002\u6b66\u50e7\u5e94\u8be5\u5df2\u7ecf\u65ad\u6c14\uff0c\u8eab\u4f53\u7d27\u7d27\u8737\u6210\u4e00\u56e2\uff0c\u50cf\u4e00\u53ea\u6652\u5e72\u7684\u867e\u7c73\u4f3c\u7684\uff0c\u59ff\u52bf\u9887\u4e3a\u8be1\u5f02\u3002</p><p>\u6b27\u51ef\u62ac\u8d77\u624b\u4e00\u6307\uff0c\u4e00\u9053\u9ed1\u708e\u5c04\u7ebf\u4ece\u6307\u5c16\u8ff8\u51fa\uff0c\u6253\u5728\u9686\u5947\u8eab\u4e0a\u3002\u9686\u5947\u7684\u5de6\u81c2\u987f\u65f6\u9f13\u80c0\u8d77\u6765\uff0c\u8d8a\u6765\u8d8a\u5927\uff0c\u7830\u5730\u70b8\u5f00\u3002\u7d27\u63a5\u7740\uff0c\u6b66\u50e7\u7684\u53f3\u81c2\u3001\u5de6\u817f\u3001\u53f3\u817f\u8f6e\u6d41\u9f13\u80c0\u7206\u5f00\uff0c\u6700\u7ec8\u53ea\u5269\u4e0b\u8eaf\u5e72\u548c\u5934\u3002\u6b27\u51ef\u624b\u4e00\u62ac\uff0c\u6b66\u50e7\u7684\u6b8b\u8eaf\u4e5f\u8ddf\u7740\u98d8\u8d77\u6765\uff0c\u6d6e\u5728\u7a7a\u4e2d\uff0c\u50cf\u5439\u6c14\u7403\u4e00\u6837\u8d8a\u9f13\u8d8a\u5927\uff0c\u8eab\u4e0a\u7684\u8863\u670d\u90fd\u5df2\u7ecf\u6491\u7834\uff0c\u6700\u7ec8\u7830\u5730\u56db\u6563\u98de\u6e85\u3002</p><p>\u7531\u4e8e\u8ddd\u79bb\u8fdc\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u88ab\u8840\u8089\u788e\u672b\u6ce2\u53ca\u6e85\u5c04\u5230\uff0c\u4f46\u4ed6\u4e0d\u660e\u767d\u6b27\u51ef\u4e3a\u4f55\u5982\u6b64\u3002\u5c31\u7b97\u5f7c\u6b64\u95f4\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4e5f\u4e0d\u9700\u8981\u5bf9\u4e00\u4e2a\u6b7b\u4eba\u73a9\u788e\u5c38\u4e07\u6bb5\u7684\u628a\u620f\u5427\u3002\u4f46\u7d27\u63a5\u7740\uff0c\u4ed6\u6ce8\u610f\u5230\u5728\u9686\u5947\u7684\u8eab\u4f53\u7206\u70b8\u5f00\u7684\u65f6\u5019\uff0c\u6b27\u51ef\u9065\u9065\u505a\u4e86\u4e2a\u8be1\u5f02\u7684\u624b\u52bf\uff0c\u50cf\u662f\u9694\u7a7a\u4ece\u4e2d\u652b\u53d6\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u8fd9\u4e2a\u52a8\u4f5c\u2026\u2026\u652b\u53d6\u7075\u9b42\uff1f</p><p>\u743c\u6069\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4ed6\u542c\u8bf4\u8fc7\u4f20\u95fb\uff0c\u636e\u8bf4\u67d0\u4e9b\u529b\u91cf\u5f3a\u5927\u7684\u5bb6\u4f19\uff0c\u6bd4\u5982\u7cbe\u64c5\u4ea1\u7075\u9b54\u6cd5\u7684\u5927\u5deb\u5e08\uff0c\u6216\u8005\u6df1\u6e0a\u4e2d\u7684\u9ad8\u9636\u6076\u9b54\u4e4b\u7c7b\uff0c\u5728\u6740\u6b7b\u4eba\u4e4b\u540e\u53ef\u4ee5\u540c\u65f6\u5c06\u7075\u9b42\u53d6\u8d70\uff0c\u56da\u7981\u8d77\u6765\u3002\u6309\u7167\u6b63\u5e38\u7684\u89c4\u77e9\uff0c\u51e1\u4eba\u6b7b\u540e\u7075\u9b42\u5e94\u8be5\u4f1a\u524d\u5f80\u795e\u56fd\uff0c\u5f52\u6240\u4fe1\u4ef0\u7684\u795e\u7947\u6240\u6709\uff0c\u8fd9\u79cd\u505a\u6cd5\u7b49\u4e8e\u5c31\u662f\u5728\u534a\u8def\u6253\u52ab\u795e\u7947\uff0c\u81ea\u7136\u4e3a\u8bf8\u795e\u6240\u4e0d\u5bb9\uff0c\u57fa\u672c\u4e0a\u5c5e\u4e8e\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\u7684\u7c7b\u578b\u3002\u4f46\u80fd\u73a9\u8fd9\u4e00\u624b\u6444\u53d6\u7075\u9b42\u7684\uff0c\u4e5f\u90fd\u4e0d\u4f1a\u662f\u7b49\u95f2\u4e4b\u8f88\uff0c\u4e0d\u662f\u5927\u5deb\u5e08\u5c31\u662f\u5927\u6076\u9b54\u4e4b\u7c7b\uff0c\u4e5f\u4e0d\u6015\u4eba\u8ffd\u6740\u3002</p><p>\u8fd9\u4e2a\u6b27\u51ef\u5c45\u7136\u80fd\u73a9\u8fd9\u4e00\u624b\u2026\u2026\u5c38\u4f53\u7206\u88c2\u3001\u7075\u9b42\u6444\u53d6\uff0c\u8fd9\u90fd\u662f\u4ea1\u7075\u5b66\u6d3e\u7684\u6781\u9ad8\u6df1\u6cd5\u672f\uff0c\u96be\u9053\u8fd9\u5bb6\u4f19\u662f\u4e2a\u6df1\u85cf\u4e0d\u9732\u7684\u5927\u5deb\u5e08\u2026\u2026\u62c9\u6c83\u514b\uff1f</p><p>\u51e0\u4e4e\u662f\u672c\u80fd\u53cd\u5e94\uff0c\u743c\u6069\u6000\u7591\u8fd9\u5bb6\u4f19\u5176\u5b9e\u5c31\u662f\u62c9\u6c83\u514b\u53d8\u5f62\u5047\u626e\u7684\uff0c\u5426\u5219\u5de8\u9b54\u5c71\u8109\u91cc\u54ea\u6709\u90a3\u4e48\u591a\u5927\u5deb\u5e08\u2026\u2026\u6216\u8005\u662f\u62c9\u6c83\u514b\u7684\u67d0\u4e2a\u5deb\u5996\u90e8\u4e0b\uff1f</p><p>\u5e38\u4eba\u5982\u679c\u60f3\u5230\u81ea\u5df1\u8eab\u8fb9\u7684\u5bb6\u4f19\u53ef\u80fd\u662f\u4e2a\u5343\u5e74\u8001\u5deb\u5996\u5047\u626e\uff0c\u53ea\u6015\u65e9\u5c31\u5413\u5f97\u5c16\u58f0\u5927\u53eb\uff0c\u62d4\u8db3\u72c2\u5954\uff0c\u9003\u5f97\u8d8a\u8fdc\u8d8a\u597d\u3002\u4f46\u743c\u6069\u5374\u662f\u4f8b\u5916\uff0c\u60f3\u5230\u8fd9\u6b27\u51ef\u6709\u53ef\u80fd\u5c31\u662f\u62c9\u6c83\u514b\u53d8\u5f62\u5047\u626e\uff0c\u4ed6\u5012\u662f\u6697\u81ea\u677e\u4e86\u53e3\u6c14\u3002</p><p>\u56e0\u4e3a\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u9762\u89c1\u62c9\u6c83\u514b\uff0c\u8fd9\u662f\u5fc5\u987b\u5b8c\u6210\u7684\u4efb\u52a1\uff0c\u4e0a\u5934\u4e0b\u8fbe\u7684\u6b7b\u547d\u4ee4\u3002\u4ed6\u82e5\u662f\u80c6\u6562\u6297\u547d\u4e0d\u9075\uff0c\u8981\u4e0d\u4e86\u591a\u4e45\u81ea\u5df1\u5c31\u662f\u4e2a\u6b7b\u4eba\uff0c\u743c\u6069\u7edd\u4e0d\u6000\u7591\u9634\u9b42\u57ce\u5bf9\u53db\u5f92\u7684\u5904\u7f6e\u6548\u7387\u3002\u6240\u4ee5\u5bf9\u4e8e\u5e38\u4eba\u6765\u8bf4\uff0c\u5bf9\u5deb\u5996\u7684\u7b2c\u4e00\u53cd\u5e94\u5c31\u662f\u907f\u800c\u8fdc\u4e4b\uff0c\u4f46\u5bf9\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u538b\u6839\u5c31\u6ca1\u6709\u8eb2\u907f\u8fd9\u4e2a\u9009\u9879\u3002\u4ed6\u6240\u8981\u8003\u8651\u7684\uff0c\u4e0d\u662f\u600e\u4e48\u8eb2\u907f\uff0c\u800c\u662f\u600e\u4e48\u964d\u4f4e\u89c1\u9762\u7684\u98ce\u9669\u3002</p><p>\u743c\u6069\u6700\u62c5\u5fc3\u7684\u662f\u4ec0\u4e48\uff1f\u4e0d\u662f\u6015\u62c9\u6c83\u514b\u672c\u9886\u9ad8\u5f3a\uff0c\u800c\u662f\u62c5\u5fc3\u8fd9\u8001\u5deb\u5996\u662f\u4e2a\u559c\u6012\u65e0\u5e38\u7ffb\u8138\u65e0\u60c5\u7684\u6740\u4eba\u72c2\u3002\u5982\u679c\u62c9\u6c83\u514b\u771f\u6709\u8fd9\u4efd\u95f2\u60c5\u9038\u81f4\uff0c\u5c45\u7136\u80af\u8dd1\u6765\u73a9\u89d2\u8272\u626e\u6f14\uff0c\u8ddf\u743c\u6069\u548c\u6885\u83f2\u65af\u6f14\u620f\u6f14\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u90a3\u8fd8\u9700\u8981\u62c5\u5fc3\u4ec0\u4e48\u3002</p><p>\u4e0d\u8fc7\u8fd9\u4e2a\u95ee\u9898\u6682\u65f6\u65e0\u6cd5\u6c42\u8bc1\uff0c\u4e5f\u4e0d\u9700\u8981\uff0c\u5f85\u4f1a\u5230\u4e86\u5deb\u5e08\u4e4b\u5893\u81ea\u7136\u5c31\u77e5\u9053\u3002\u5c31\u7b97\u6b27\u51ef\u4e0d\u662f\u62c9\u6c83\u514b\u4e5f\u6ca1\u5173\u7cfb\uff0c\u4ece\u6b27\u51ef\u521a\u624d\u7684\u53e3\u6c14\u5224\u65ad\uff0c\u4ed6\u53ea\u6015\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u6b64\u6b21\u524d\u6765\u627e\u62c9\u6c83\u514b\u662f\u8d1f\u6709\u7279\u522b\u4efb\u52a1\uff0c\u90a3\u4e48\u62c9\u6c83\u514b\u4e5f\u5f88\u53ef\u80fd\u77e5\u9053\u4e86\u2014\u2014\u90a3\u5c31\u4e00\u5207\u597d\u8bf4\u4e86\uff0c\u770b\u5728\u540c\u662f\u8010\u745f\u4e00\u8109\u7684\u4efd\u4e0a\uff0c\u53c8\u5343\u91cc\u8fe2\u8fe2\u8dd1\u6765\u9001\u4fe1\uff0c\u8001\u5deb\u5996\u603b\u8be5\u624b\u4e0b\u7559\u60c5\u4e9b\u5427\u3002</p><p>\u81f3\u4e8e\u6444\u53d6\u7075\u9b42\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u867d\u7136\u53d1\u89c9\uff0c\u4f46\u624d\u61d2\u5f97\u5728\u4e4e\u3002\u9686\u5947\u662f\u88ab\u9489\u5728\u65e0\u4fe1\u8005\u4e4b\u5899\u4e0a\u4e5f\u597d\uff0c\u662f\u88ab\u4eba\u6444\u53bb\u56da\u7981\u4e5f\u597d\uff0c\u90fd\u548c\u4ed6\u6ca1\u5173\u7cfb\uff0c\u53ea\u8981\u4e0d\u56de\u6765\u627e\u81ea\u5df1\u62a5\u4ec7\u5c31\u884c\u3002</p><p>\u6240\u4ee5\u6574\u4e2a\u8fc7\u7a0b\uff0c\u743c\u6069\u5168\u90fd\u719f\u89c6\u65e0\u7779\uff0c\u5f53\u4f5c\u6ca1\u770b\u89c1\uff0c\u76f4\u5230\u6b27\u51ef\u5b8c\u6210\u65bd\u6cd5\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u9897\u65e0\u8272\u900f\u660e\u7684\u6c34\u6676\u68f1\u67f1\u6765\uff0c\u5de6\u624b\u865a\u7a7a\u4e00\u6293\uff0c\u5c06\u67d0\u6837\u4e1c\u897f\u5f80\u6c34\u6676\u91cc\u4e00\u63a8\u3002</p><p>\u6c34\u6676\u68f1\u67f1\u9aa4\u7136\u53d8\u6210\u4e86\u9ed1\u8272\uff0c\u4f46\u4f9d\u65e7\u900f\u660e\uff0c\u743c\u6069\u80fd\u9690\u7ea6\u770b\u89c1\u5176\u4e2d\u6709\u4e2a\u4eba\u5f71\uff0c\u6599\u60f3\u5c31\u662f\u90a3\u5012\u9709\u7684\u6b66\u50e7\u9686\u5947\u4e86\u3002\u201c\u529b\u91cf\u5012\u662f\u4e0d\u9519\uff0c\u201d\u6b27\u51ef\u81ea\u8a00\u81ea\u8bed\uff0c\u4e00\u8fb9\u89c2\u5bdf\u65c1\u8fb9\u743c\u6069\u7684\u53cd\u5e94\uff0c\u4f46\u5e74\u8f7b\u7684\u5deb\u5e08\u9762\u8272\u5e73\u6de1\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u4eff\u4f5b\u4ec0\u4e48\u90fd\u4e0d\u66fe\u770b\u89c1\u4f3c\u7684\u3002\u201c\u53ef\u60dc\u4e0d\u591f\u7eaf\u7cb9\uff0c\u7ec8\u7a76\u4e0d\u7b97\u4e0a\u7b49\u8d27\u8272\u2026\u2026\u7b97\u4e86\u3002\u201d\u6b27\u51ef\u5482\u5482\u5634\uff0c\u628a\u6c34\u6676\u68f1\u67f1\u6536\u8fdb\u6000\u4e2d\uff0c\u8f6c\u5934\u770b\u7740\u743c\u6069\uff0c\u201c\u8d70\u5427\u3002\u201d</p><p>\u4ed6\u5f53\u5148\u9886\u8def\uff0c\u743c\u6069\u62b1\u8d77\u6885\u83f2\u65af\uff0c\u628a\u81ea\u5df1\u7684\u7cbe\u91d1\u5315\u9996\u63d2\u8fdb\u76ae\u9774\uff0c\u5c06\u5c11\u5973\u7684\u94f6\u5251\u585e\u5230\u6b21\u5143\u888b\u91cc\u53bb\uff0c\u8ddf\u5728\u540e\u9762\u3002\u4ed6\u8fd8\u8003\u8651\u8fc7\u8981\u4e0d\u8981\u5728\u8d1d\u7eb3\u7d22\u548c\u79d1\u6069\u7b49\u4eba\u8eab\u4e0a\u641c\u522e\u4e00\u4e0b\uff0c\u770b\u770b\u6709\u6ca1\u6709\u4ec0\u4e48\u503c\u94b1\u5b9d\u7269\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u535a\u5f97\u4e4b\u95e8\u6668\u66e6\u795e\u6bbf\u7684\u9996\u5e2d\u7267\u5e08\uff0c\u4e00\u4e2a\u662f\u4ee5\u8c6a\u5bcc\u8457\u79f0\u7684\u6c83\u91d1\u9ad8\u9636\u7267\u5e08\uff0c\u6309\u9053\u7406\u8bf4\u8eab\u4e0a\u80af\u5b9a\u5e26\u4e86\u4e0d\u5c11\u5b9d\u8d1d\uff0c\u8bf4\u4e0d\u5b9a\u6709\u51e0\u4ef6\u6709\u7528\u7684\u9b54\u6cd5\u7269\u54c1\u3002\u4f46\u89c1\u6b27\u51ef\u5df2\u7ecf\u5934\u4e5f\u4e0d\u56de\u5730\u8d8a\u8d70\u8d8a\u8fdc\uff0c\u743c\u6069\u4e5f\u65e0\u6687\u518d\u803d\u6401\uff0c\u5306\u5306\u5fd9\u5fd9\u8ddf\u4e86\u4e0a\u53bb\u3002</p><p>\u201c\u7b97\u4e86\uff0c\u53cd\u6b63\u7267\u5e08\u8eab\u4e0a\u7684\u4e1c\u897f\uff0c\u6211\u4e00\u4e2a\u5deb\u5e08\u4e5f\u672a\u5fc5\u80fd\u7528\u3002\u201d\u743c\u6069\u81ea\u6211\u5b89\u6170\u7740\uff0c\u52a0\u5feb\u811a\u6b65\u3002</p><p>\u6b27\u51ef\u811a\u6b65\u77eb\u5065\uff0c\u5728\u8fd9\u5c71\u8def\u4e0a\u884c\u8d70\u5982\u98de\uff0c\u743c\u6069\u867d\u7136\u5e74\u8f7b\u529b\u58ee\uff0c\u4f46\u6000\u91cc\u62b1\u7740\u6885\u83f2\u65af\uff0c\u5c45\u7136\u6e10\u6e10\u8ddf\u4e0d\u4e0a\u4e86\u3002\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u7d22\u6027\u53c8\u7ed9\u81ea\u5df1\u65bd\u4e86\u4e2a\u201c\u811a\u5e95\u62b9\u6cb9\u201d\uff0c\u53d1\u8db3\u72c2\u5954\uff0c\u53c8\u8ffd\u4e86\u4e0a\u6765\u3002</p><p>\u811a\u5e95\u62b9\u6cb9\u662f\u4e2a\u5f88\u5f62\u8c61\u7684\u540d\u79f0\uff0c\u8fd9\u4e2a\u6cd5\u672f\u7684\u4f5c\u7528\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u8ba9\u5deb\u5e08\u6682\u65f6\u6b65\u884c\u901f\u5ea6\u52a0\u5feb\u4e00\u500d\u5de6\u53f3\u3002\u770b\u4e0a\u53bb\u4f3c\u4e4e\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f46\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u6218\u6597\u65f6\u88ab\u4eba\u8fd1\u8eab\u662f\u6700\u5fcc\u8bb3\u7684\u4e8b\u60c5\uff0c\u8ddd\u79bb\u5c31\u610f\u5473\u7740\u5b89\u5168\uff0c\u8fd9\u4e2a\u6cd5\u672f\u80fd\u8ba9\u5deb\u5e08\u548c\u654c\u4eba\u5feb\u901f\u62c9\u5f00\u8ddd\u79bb\uff0c\u81ea\u7136\u5c31\u6709\u4e86\u5f88\u9ad8\u7684\u5b9e\u7528\u4ef7\u503c\u3002</p><p>\u4f46\u5355\u7eaf\u7528\u811a\u5e95\u62b9\u6cb9\u6765\u8d76\u8def\u7684\uff0c\u8fd9\u53ef\u5b9e\u5728\u662f\u5c11\u4e4b\u53c8\u5c11\u3002\u5deb\u5e08\u7684\u9b54\u6cd5\u5168\u90fd\u8981\u63d0\u524d\u51c6\u5907\uff0c\u7528\u5b8c\u4e86\u5c31\u662f\u6ca1\u4e86\uff0c\u9700\u8981\u518d\u82b1\u4e0a\u516b\u4e5d\u4e2a\u5c0f\u65f6\u6765\u91cd\u65b0\u51c6\u5907\u3002\u54ea\u4f1a\u6709\u53d8\u6001\u820d\u5f97\u8fd9\u4e48\u5962\u4f88\u6d6a\u8d39\uff0c\u5c31\u5355\u7eaf\u4e3a\u4e86\u8d70\u8def\u5feb\u4e00\u70b9\uff0c\u4f7f\u7528\u8fd9\u79cd\u5728\u6218\u6597\u4e2d\u53ef\u80fd\u4fdd\u4f4f\u81ea\u5df1\u6027\u547d\u7684\u6cd5\u672f\u3002</p><p>\u4f46\u5bf9\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u5374\u662f\u4e2a\u8d27\u771f\u4ef7\u5b9e\u7684\u53d8\u6001\u3002\u522b\u7684\u5deb\u5e08\u4e00\u822c\u800c\u8a00\u4e00\u5929\u51c6\u5907\u4e00\u6b21\u6cd5\u672f\uff0c\u800c\u4ed6\u5374\u5b8c\u5168\u80fd\u51c6\u5907\u4e24\u4e09\u6b21\uff0c\u56e0\u4e3a\u6240\u9700\u8981\u7684\u65f6\u95f4\u77ed\u3002\u6240\u4ee5\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6cd5\u672f\u4e5f\u5c31\u4e0d\u7b97\u683c\u5916\u73cd\u8d35\u4e86\u3002\u4f55\u51b5\u6b64\u6b21\u662f\u8981\u53bb\u9762\u5bf9\u62c9\u6c83\u514b\uff0c\u5982\u679c\u5bf9\u65b9\u60f3\u6740\u81ea\u5df1\uff0c\u51c6\u5907\u518d\u591a\u6cd5\u672f\u4e5f\u6ca1\u7528\uff0c\u53ea\u80fd\u8001\u8001\u5b9e\u5b9e\u7b49\u6b7b\u3002</p><p>\u203b\u203b\u203b</p><p>\u8bf4\u4e5f\u5947\u602a\uff0c\u539f\u672c\u9884\u8ba1\u9700\u8981\u534a\u5929\u7684\u8def\u7a0b\uff0c\u5c45\u7136\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5c31\u5df2\u7ecf\u8d70\u8fc7\u3002\u5728\u5915\u9633\u5c1a\u672a\u843d\u5c71\u524d\uff0c\u743c\u6069\u5c31\u5df2\u7ecf\u8ddf\u7740\u6b27\u51ef\u8d70\u5230\u4e86\u5deb\u5e08\u4e4b\u5893\u7684\u57ce\u5899\u524d\uff0c\u4e00\u5171\u8fd8\u6ca1\u82b1\u4e09\u4e2a\u5c0f\u65f6\uff0c\u8fd9\u8ba9\u4ed6\u7565\u7565\u6709\u4e9b\u8be7\u5f02\uff0c\u4f46\u4e5f\u61d2\u5f97\u591a\u7ba1\u3002</p><p>\u4e24\u4eba\u76f4\u63a5\u4ece\u57ce\u95e8\u8fdb\u5165\uff0c\u8fd9\u4e2a\u6b27\u51ef\u5c31\u7b97\u4e0d\u662f\u62c9\u6c83\u514b\u672c\u4eba\uff0c\u81f3\u5c11\u4e5f\u548c\u8001\u5deb\u5996\u6709\u975e\u5e38\u6df1\u7684\u6e0a\u6e90\uff0c\u4ed6\u5927\u6447\u5927\u6446\u5730\u8d70\u8fdb\u6765\uff0c\u4e00\u8def\u4e0a\u5c45\u7136\u6ca1\u6709\u534a\u4e2a\u4eba\u963b\u62e6\uff0c\u4ec0\u4e48\u5deb\u5996\u3001\u5438\u8840\u9b3c\u3001\u50f5\u5c38\u3001\u9ab7\u9ac5\u4e4b\u7c7b\uff0c\u534a\u4e2a\u5f71\u5b50\u90fd\u6ca1\u770b\u5230\uff0c\u6574\u5ea7\u5deb\u5e08\u4e4b\u5893\u91cc\u7a7a\u5bc2\u5be5\u5ed3\uff0c\u4eff\u4f5b\u6b7b\u57ce\u2026\u2026\u5443\uff0c\u672c\u6765\u4e5f\u5c31\u662f\u6b7b\u4eba\u57ce\u5e02\u3002</p><p>\u743c\u6069\u4e0d\u7531\u5f97\u6709\u4e9b\u7d27\u5f20\uff0c\u4f46\u5c3d\u91cf\u9547\u5b9a\u7740\uff0c\u8138\u4e0a\u6f20\u65e0\u8868\u60c5\uff0c\u53ea\u7ba1\u8ddf\u968f\u7740\u6b27\u51ef\u524d\u884c\u3002\u6885\u83f2\u65af\u4f9d\u65e7\u5728\u6c89\u7761\uff0c\u8fd8\u6ca1\u9192\u8fc7\u6765\uff0c\u743c\u6069\u957f\u65f6\u95f4\u62b1\u7740\u5979\uff0c\u611f\u89c9\u624b\u81c2\u5df2\u7ecf\u5b8c\u5168\u9178\u9ebb\uff0c\u4e0d\u5f97\u4e0d\u53c8\u62bd\u7a7a\u704c\u4e86\u81ea\u5df1\u4e00\u74f6\u201c\u86ee\u725b\u4e4b\u529b\u201d\u836f\u6c34\uff0c\u8fd9\u624d\u52c9\u5f3a\u652f\u6491\u5230\u73b0\u5728\u3002</p><p>\u592a\u9633\u5df2\u7ecf\u843d\u5c71\uff0c\u5929\u5730\u95f4\u9677\u5165\u4e00\u7247\u9ed1\u6697\uff0c\u5deb\u5e08\u4e4b\u5893\u5468\u56f4\u7684\u5341\u4e8c\u5ea7\u9ad8\u5854\u4e0a\uff0c\u5206\u522b\u73b0\u51fa\u5404\u79cd\u989c\u8272\u7684\u5149\u8292\u6765\uff0c\u4eff\u4f5b\u662f\u67d0\u79cd\u6807\u8bc6\u3002\u6b63\u4e2d\u7684\u9ad8\u5854\u5374\u4f9d\u65e7\u9ed1\u6c89\u6c89\u7684\uff0c\u4ec0\u4e48\u53cd\u5e94\u90fd\u6ca1\u6709\u3002</p><p>\u6b27\u51ef\u5e26\u7740\u743c\u6069\u8d70\u5230\u6b63\u4e2d\u9ad8\u5854\u524d\uff0c\u505c\u4e86\u4e0b\u6765\u3002\u8fd9\u5ea7\u9ad8\u5854\u5f88\u53e4\u602a\uff0c\u4eff\u4f5b\u662f\u4e2a\u5bc6\u5c01\u7684\u6574\u4f53\uff0c\u770b\u4e0d\u89c1\u4efb\u4f55\u53ef\u4f9b\u4eba\u8fdb\u51fa\u7684\u5165\u53e3\u3002\u4f46\u5f53\u6b27\u51ef\u5c48\u8d77\u624b\u6307\uff0c\u5728\u5854\u58c1\u4e0a\u7528\u67d0\u79cd\u8282\u594f\u8f7b\u8f7b\u6572\u51fb\u4e86\u4e09\u4e0b\u540e\uff0c\u743c\u6069\u770b\u89c1\u5854\u58c1\u4e0a\u81ea\u52a8\u51fa\u73b0\u4e86\u4e00\u6247\u692d\u5706\u5f62\u7684\u95e8\uff0c\u91cc\u9762\u9ed1\u6c89\u6c89\u7684\uff0c\u4eff\u4f5b\u901a\u5f80\u65e0\u5c3d\u7684\u865a\u7a7a\u4e2d\u3002</p><p>\u201c\u8bf7\u8fdb\u3002\u201d\u4e00\u4e2a\u4f4e\u6c89\u3001\u4f18\u96c5\uff0c\u900f\u7740\u9690\u9690\u78c1\u6027\u7684\u5973\u58f0\u8bf4\u3002\u743c\u6069\u542c\u5728\u8033\u4e2d\uff0c\u9887\u89c9\u719f\u6089\uff0c\u53ea\u662f\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002</p><p>\u6b27\u51ef\u5f53\u5148\u8d70\u8fdb\u95e8\u53bb\uff0c\u8eab\u5f71\u6d88\u5931\uff1b\u743c\u6069\u7565\u4e00\u72b9\u8c6b\uff0c\u62b1\u7740\u6885\u83f2\u65af\u4e5f\u8d70\u8fdb\u6765\u3002\u5f53\u7a7f\u8fc7\u90a3\u6247\u95e8\u7684\u65f6\u5019\uff0c\u4ed6\u53ea\u89c9\u5f97\u4e00\u9635\u9690\u9690\u7684\u6655\u7729\u4f20\u6765\uff0c\u4eff\u4f5b\u6709\u5343\u4e07\u5c42\u6ce2\u52a8\u5239\u90a3\u95f4\u5728\u81ea\u5df1\u8eab\u4e0a\u626b\u8fc7\uff0c\u4ece\u5934\u5230\u811a\u5de8\u7ec6\u65e0\u9057\uff0c\u5239\u90a3\u95f4\u4eff\u4f5b\u63a0\u8fc7\u65e0\u5c3d\u865a\u7a7a\uff0c\u5404\u79cd\u7eb7\u7e41\u590d\u6742\u7684\u666f\u8c61\u4e00\u8d77\u6d8c\u8fc7\u3002\u6700\u7ec8\u5f53\u4ed6\u56de\u8fc7\u795e\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u811a\u8e0f\u5b9e\u5730\uff0c\u5468\u56f4\u5168\u662f\u6c89\u6c89\u9ed1\u6697\uff0c\u4ed6\u80fd\u611f\u89c9\u5230\u811a\u4e0b\u662f\u67d4\u8f6f\u539a\u5bc6\u7684\u5730\u6bef\uff0c\u8e29\u5728\u4e0a\u9762\u975e\u5e38\u8212\u9002\u3002\u8bf4\u5b9e\u8bdd\uff0c\u8fd9\u51e0\u5929\u4e00\u76f4\u8d70\u5c71\u8def\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u811a\u5e95\u90fd\u5feb\u8981\u8d77\u6ce1\u4e86\u3002</p><p>\u9ed1\u6697\u4e2d\u6709\u4eba\u8f7b\u8f7b\u6263\u52a8\u624b\u6307\uff0c\u51e0\u6735\u51b0\u51b7\u7684\u9ed1\u8272\u706b\u7130\u8ff8\u51fa\u6765\uff0c\u6f02\u6d6e\u5728\u7a7a\u4e2d\uff0c\u56db\u9762\u6563\u5f00\uff0c\u7a0d\u7a0d\u9a71\u9000\u4e86\u9ed1\u6697\uff0c\u8ba9\u5468\u56f4\u53d8\u5f97\u660e\u4eae\u4e00\u4e9b\uff0c\u4e8e\u662f\u743c\u6069\u7ec8\u4e8e\u53ef\u4ee5\u770b\u6e05\u695a\u81ea\u5df1\u539f\u6765\u6b63\u7ad9\u5728\u67d0\u4e2a\u623f\u95f4\u91cc\uff0c\u8fd9\u50cf\u662f\u4f1a\u5ba2\u5ba4\uff0c\u6709\u51e0\u5f20\u5bbd\u5927\u7684\u5ea7\u6905\uff0c\u4e0a\u9762\u8fd8\u94fa\u7740\u517d\u76ae\u3002\u6b27\u51ef\u5374\u4e0d\u77e5\u4f55\u65f6\u6d88\u5931\u4e86\uff0c\u4e0d\u5728\u8eab\u8fb9\u3002</p><p>\u4e00\u4e2a\u5973\u5b50\u4ece\u9ed1\u6697\u4e2d\u8d70\u51fa\u6765\uff0c\u7ad9\u5728\u9762\u524d\uff0c\u5979\u7684\u8138\u86cb\u7cbe\u81f4\uff0c\u9f3b\u6881\u9ad8\u633a\uff0c\u76ae\u80a4\u767d\u7699\u5f97\u6709\u4e9b\u8fc7\u5206\uff0c\u5634\u5507\u5f88\u8584\uff0c\u5168\u65e0\u534a\u70b9\u8840\u8272\uff0c\u4e00\u5934\u6de1\u84dd\u8272\u79c0\u53d1\u67d4\u987a\u5730\u5782\u4e0b\u6765\uff0c\u6b63\u662f\u4eca\u5929\u4e2d\u5348\u5728\u57ce\u5916\u9047\u89c1\u7684\u90a3\u4e2a\u5973\u5438\u8840\u9b3c\uff0c\u8389\u6cd5\u513f\xb7\u94f6\u6708\u3002</p><p>\u6b64\u65f6\u5979\u4f9d\u65e7\u7a7f\u7740\u9ed1\u8272\u7d27\u8eab\u76ae\u7532\uff0c\u62ab\u7740\u6597\u7bf7\uff0c\u4f46\u6ca1\u6709\u62c9\u8d77\u515c\u5e3d\uff0c\u80fd\u6e05\u695a\u5730\u770b\u89c1\u9762\u5bb9\u3002\u5438\u8840\u9b3c\u5fae\u7b11\u7740\uff0c\u5e76\u6ca1\u6709\u9732\u51fa\u7360\u7259\uff0c\u5979\u4f38\u624b\u505a\u4e86\u4e2a\u8bf7\u5750\u7684\u59ff\u52bf\u3002\u201c\u8bf7\u7a0d\u5f85\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u62c9\u6c83\u514b\u5148\u751f\u5f88\u5feb\u5c31\u4f1a\u89c1\u4f60\u4eec\u3002\u201d</p><p>\u5979\u770b\u8d77\u6765\u662f\u62c9\u6c83\u514b\u7684\u79d8\u4e66\uff1f\u5514\uff0c\u90a3\u8001\u5deb\u5996\u679c\u7136\u662f\u8273\u798f\u4e0d\u6d45\uff0c\u4eba\u90fd\u6b7b\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u5c45\u7136\u8fd8\u4e0d\u5fd8\u5f04\u4e2a\u7f8e\u5973\u5c0f\u871c\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u5438\u8840\u9b3c\uff0c\u679c\u7136\u662f\u4e0e\u65f6\u4ff1\u8fdb\u7684\u5178\u8303\u2026\u2026</p><p>\u80e1\u601d\u4e71\u60f3\u7740\uff0c\u743c\u6069\u628a\u6885\u83f2\u65af\u653e\u4e0b\u6765\uff0c\u8ba9\u5979\u5750\u5728\u6905\u4e2d\u3002\u5c11\u5973\u4f9d\u65e7\u6655\u8ff7\uff0c\u6c89\u7761\u4e0d\u9192\uff0c\u743c\u6069\u6709\u4e9b\u62c5\u5fc3\uff0c\u4f46\u542c\u5979\u547c\u5438\u5e73\u7f13\u60a0\u957f\uff0c\u6599\u60f3\u4e5f\u6ca1\u4ec0\u4e48\u5927\u788d\u3002</p><p>\u201c\u5979\u597d\u50cf\u751f\u75c5\u4e86\u5462\u3002\u201d\u5438\u8840\u9b3c\u8f7b\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u55ef\u4e86\u4e00\u58f0\uff0c\u5c0f\u5fc3\u5730\u8ba9\u6885\u83f2\u65af\u8eba\u597d\uff0c\u7136\u540e\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u7740\u8fd9\u4f4d\u53eb\u8389\u6cd5\u513f\xb7\u94f6\u6708\u7684\u5973\u5438\u8840\u9b3c\u3002\u867d\u7136\u6b64\u524d\u6253\u8fc7\u4e00\u67b6\uff0c\u4f46\u743c\u6069\u5012\u4e5f\u6ca1\u4ec0\u4e48\u654c\u610f\uff0c\u4ed6\u552f\u4e00\u611f\u5174\u8da3\u7684\u662f\uff1a\u8fd9\u4e2a\u5438\u8840\u9b3c\u663e\u7136\u5e76\u975e\u5982\u5979\u4e4b\u524d\u81ea\u79f0\u7684\u90a3\u6837\u662f\u4e2a\u54e8\u5175\uff0c\u5979\u663e\u7136\u662f\u62c9\u6c83\u514b\u7684\u4eb2\u4fe1\u52a9\u624b\uff0c\u5426\u5219\u4e0d\u4f1a\u6709\u8d44\u683c\u5728\u8fd9\u9ad8\u5854\u91cc\u62c5\u4efb\u63a5\u5f85\u5ba2\u4eba\u7684\u5de5\u4f5c\u3002</p><p>\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u81ea\u5df1\u4e00\u884c\u4eba\u6b64\u6b21\u524d\u6765\uff0c\u53ea\u6015\u62c9\u6c83\u514b\u65e9\u5c31\u5df2\u7ecf\u77e5\u9053\u5f97\u4e00\u6e05\u4e8c\u695a\u4e86\u3002</p><p>\u201c\u6b27\u51ef\u5148\u751f\u5462\u3002\u201d\u743c\u6069\u8be2\u95ee\uff0c\u4ed6\u5df2\u7ecf\u5728\u5fc3\u4e2d\u731c\u6d4b\u8fd9\u4e2a\u6b27\u51ef\u5176\u5b9e\u5c31\u662f\u62c9\u6c83\u514b\u4e86\u3002\u8bf4\u4e0d\u5b9a\u5f85\u4f1a\u6b27\u51ef\u5c31\u4f1a\u6362\u8eab\u88c5\u675f\u51fa\u6765\uff0c\u8bf4\u201c\u5176\u5b9e\u6211\u5c31\u662f\u62c9\u6c83\u514b\u201d\u2026\u2026\u5514\uff0c\u8fd9\u79cd\u620f\u7801\u592a\u8001\u5957\u4e86\uff0c\u80fd\u4e0d\u80fd\u6765\u70b9\u6709\u65b0\u610f\u7684\u2026\u2026</p><p>\u4eff\u4f5b\u662f\u542c\u89c1\u4e86\u4ed6\u7684\u5fc3\u58f0\uff0c\u65b0\u610f\u679c\u7136\u6765\u4e86\u3002</p><p>\u9ed1\u6697\u4e2d\u8f7b\u8f7b\u54cd\u8d77\u4e00\u9635\u77ed\u6682\u800c\u6e05\u6670\u7684\u6572\u51fb\u58f0\uff0c\u542c\u8d77\u6765\u50cf\u662f\u6709\u4eba\u5728\u7528\u6307\u8282\u6572\u6253\u6728\u5934\u684c\u9762\uff0c\u4f46\u5176\u4e2d\u5374\u4eff\u4f5b\u8574\u542b\u7740\u6151\u4eba\u7684\u9b54\u529b\uff0c\u743c\u6069\u542c\u5728\u8033\u4e2d\uff0c\u53ea\u89c9\u5f97\u9aa4\u7136\u95f4\u547c\u5438\u52a0\u901f\uff0c\u5fc3\u8df3\u5267\u589e\uff0c\u51e0\u4e4e\u8981\u4ece\u5589\u5499\u8e66\u51fa\u6765\u4f3c\u7684\u3002\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u614c\u5fd9\u51dd\u805a\u5fc3\u795e\uff0c\u58f0\u97f3\u5374\u9aa4\u7136\u53c8\u6d88\u5931\u4e86\uff0c\u5239\u90a3\u95f4\u4fbf\u5bc2\u6ca1\u65e0\u58f0\uff0c\u8fde\u8885\u8885\u4f59\u97f3\u90fd\u6ca1\u6709\uff0c\u4eff\u4f5b\u4ece\u6765\u5c31\u4e0d\u66fe\u5b58\u5728\u8fc7\u4e00\u822c\u3002</p><p>\u8389\u6cd5\u513f\u540c\u6837\u4e5f\u542c\u5230\u8fd9\u7a81\u5982\u5176\u6765\u7684\u6572\u51fb\u58f0\uff0c\u4f46\u5979\u5374\u4eff\u4f5b\u534a\u70b9\u4e0d\u53d7\u5f71\u54cd\uff0c\u8fd9\u4e5f\u53ef\u80fd\u56e0\u4e3a\u5979\u662f\u5438\u8840\u9b3c\uff0c\u65e9\u5df2\u7ecf\u6ca1\u6709\u547c\u5438\u5fc3\u8df3\u7684\u7f18\u6545\u3002\u5979\u5fae\u5fae\u7b11\u7740\uff0c\u5bf9\u743c\u6069\u8bf4\uff1a\u201c\u62c9\u6c83\u514b\u5148\u751f\u8bf7\u4f60\u4e0a\u53bb\u3002\u201d</p><p>\u4e0a\u53bb\uff1f\u4e0a\u54ea\u91cc\uff1f</p><p>\u8389\u6cd5\u513f\u4f38\u624b\u5728\u8eab\u540e\u4e0d\u77e5\u505a\u4e86\u4ec0\u4e48\u624b\u811a\uff0c\u53ea\u542c\u89c1\u623f\u95f4\u89d2\u843d\u91cc\u4e00\u9635\u8f7b\u5fae\u54cd\u52a8\uff0c\u63a5\u7740\u51fa\u73b0\u4e00\u9053\u87ba\u65cb\u5f62\u7684\u697c\u68af\u6765\uff0c\u76f4\u901a\u4e0a\u65b9\u7684\u6c89\u6c89\u9ed1\u6697\u3002\u201c\u8bf7\u3002\u201d\u8389\u6cd5\u513f\u8bf4\u3002</p><p>\u743c\u6069\u6b63\u60f3\u62b1\u8d77\u6885\u83f2\u65af\uff0c\u5374\u88ab\u8389\u6cd5\u513f\u963b\u6b62\u4e86\uff0c\u201c\u5927\u5965\u672f\u5e08\u53ea\u5e0c\u671b\u89c1\u4f60\u4e00\u4e2a\u4eba\uff0c\u5e76\u4e0d\u5305\u62ec\u4f60\u7684\u670b\u53cb\uff0c\u201d\u5438\u8840\u9b3c\u67d4\u58f0\u8bf4\uff0c\u201c\u6211\u60f3\u5979\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\u4f11\u606f\u6bd4\u8f83\u5408\u9002\u3002\u201d</p><p>\u7559\u5728\u8fd9\u91cc\u4f11\u606f\uff1f\u4f60\u4e0d\u4f1a\u60f3\u54ac\u5979\u4e00\u53e3\uff0c\u628a\u5979\u4e5f\u53d8\u6210\u5438\u8840\u9b3c\u5427\uff0c\u636e\u8bf4\u5f88\u591a\u5438\u8840\u9b3c\u5bf9\u7e41\u884d\u540e\u88d4\u90fd\u5f88\u6709\u5174\u8da3\u2026\u2026</p></article>"}}}]);
