(window.webpackJsonp=window.webpackJsonp||[]).push([[1358],{1381:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e09\u8282 \u9519\u7efc\u590d\u6742",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e09\u8282 \u9519\u7efc\u590d\u6742</h2><p>\u6b27\u51ef\u629b\u51fa\u7684\u8fd9\u9897\u91cd\u78c5\u516b\u5366\u767b\u65f6\u5c06\u743c\u6069\u548c\u6885\u83f2\u65af\u7ed9\u5f7b\u5e95\u9707\u5230\u4e86\u3002</p><p>\u201c\u8389\u62c9\u516c\u7235\u5c45\u7136\u662f\u5854\u6d1b\u5a1c\u4fe1\u5f92\uff1f\u201d\u743c\u6069\u72b9\u81ea\u8fd8\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\u3002</p><p>\u4ed6\u6765\u535a\u5f97\u4e4b\u95e8\u4e5f\u6709\u4e9b\u65f6\u65e5\uff0c\u5929\u5929\u548c\u8389\u62c9\u5973\u516c\u7235\u6253\u4ea4\u9053\uff0c\u5370\u8c61\u91cc\u5979\u660e\u660e\u662f\u6cf0\u6469\u62c9\u7684\u4fe1\u5f92\u2026\u2026\u81f3\u5c11\u53e3\u5934\u4e0a\u5979\u662f\u7ecf\u5e38\u5ff5\u5e78\u8fd0\u5973\u795e\u7684\u540d\u5b57\u7684\u3002\u518d\u8bf4\u535a\u5f97\u4e4b\u95e8\u7684\u56db\u4f4d\u653f\u6cbb\u9886\u8896\uff0c\u867d\u7136\u540d\u4e49\u4e0a\u79f0\u201c\u516c\u7235\u201d\uff0c\u5176\u5b9e\u8fd9\u53ea\u662f\u4e00\u79cd\u4f20\u7edf\u79f0\u53f7\uff0c\u5e76\u4e0d\u610f\u5473\u7740\u4ed6\u4eec\u771f\u7684\u662f\u4e16\u88ad\u8d35\u65cf\uff0c\u5b9e\u9645\u4e0a\u4ed6\u4eec\u90fd\u662f\u7531\u5e02\u6c11\u548c\u5546\u4eba\u8bae\u4f1a\u9009\u4e3e\u4e0a\u53f0\u7684\u3002\u761f\u75ab\u4e0e\u75be\u75c5\u5973\u795e\u5854\u6d1b\u5a1c\u4e43\u662f\u90aa\u795e\uff0c\u867d\u8bf4\u5d07\u62dc\u90aa\u795e\u5e76\u4e0d\u72af\u6cd5\uff0c\u81f3\u5c11\u5728\u535a\u5f97\u4e4b\u95e8\u8fd9\u5730\u65b9\u4e0d\u8fdd\u6cd5\uff0c\u4f46\u7ec8\u7a76\u8fd8\u662f\u53d7\u654c\u89c6\u7684\u4e8b\u60c5\uff0c\u8389\u62c9\u5982\u679c\u662f\u5854\u6d1b\u5a1c\u4fe1\u5f92\uff0c\u600e\u4e48\u80fd\u88ab\u9009\u4e0a\u5927\u516c\u7235\u4e4b\u4f4d\u3002</p><p>\u4f46\u773c\u524d\u7684\u666f\u8c61\u5df2\u7ecf\u6e05\u6e05\u695a\u695a\u5730\u8bf4\u660e\u4e86\u4e8b\u5b9e\u3002</p><p>\u201c\u8fd9\u4ef6\u4e8b\u4ece\u5934\u5230\u5c3e\uff0c\u672c\u5c31\u662f\u8389\u62c9\u516c\u7235\u4e00\u624b\u7b56\u5212\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u5854\u6d1b\u5a1c\u4fe1\u5f92\u5f04\u51fa\u8fd9\u6b21\u761f\u75ab\u6765\uff0c\u5982\u679c\u4e0d\u662f\u8389\u62c9\u516c\u7235\u4ece\u4e2d\u64cd\u7eb5\uff0c\u65e9\u5c31\u5df2\u7ecf\u9732\u4e86\u5f62\u8ff9\uff0c\u4e5f\u4e0d\u4f1a\u80fd\u7792\u5230\u73b0\u5728\u4e86\u3002\u201d</p><p>\u4f46\u8389\u62c9\u516c\u7235\u8fd9\u4e48\u505a\u662f\u4e3a\u4ec0\u4e48\uff1f</p><p>\u201c\u4e3a\u4e86\u9ece\u660e\u4e4b\u77f3\u5427\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6211\u542c\u5230\u90a3\u7fa4\u5854\u6d1b\u5a1c\u4fe1\u5f92\u8bae\u8bba\uff0c\u8bf4\u662f\u8981\u6bc1\u6389\u9ece\u660e\u4e4b\u77f3\u3002\u201d</p><p>\u8389\u62c9\u516c\u7235\u548c\u5854\u6d1b\u5a1c\u4fe1\u5f92\u5f04\u51fa\u8fd9\u79cd\u5927\u89c4\u6a21\u761f\u75ab\u6765\uff0c\u6b63\u662f\u4e3a\u4e86\u5f15\u9ece\u660e\u4e4b\u77f3\u7684\u51fa\u73b0\uff0c\u4ee5\u4fbf\u5c06\u8fd9\u9897\u795e\u5668\u6bc1\u6389\u3002\u9ece\u660e\u4e4b\u77f3\u5e73\u65f6\u5b89\u7f6e\u5728\u8ff7\u65af\u5353\u8bfa\u7684\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u4e2d\uff0c\u9632\u62a4\u68ee\u4e25\uff0c\u7b49\u95f2\u4eba\u4e0d\u80fd\u9760\u8fd1\uff0c\u4f46\u4e00\u65e6\u88ab\u53d6\u51fa\u6765\uff0c\u9001\u5230\u535a\u5f97\u4e4b\u95e8\uff0c\u8fd8\u4e0d\u4efb\u7531\u8389\u62c9\u516c\u7235\u4ece\u4e2d\u4e0a\u4e0b\u5176\u624b\u3002\u9ece\u660e\u4e4b\u77f3\u9001\u5230\u535a\u5f97\u4e4b\u95e8\u540e\uff0c\u4e00\u76f4\u662f\u7531\u672c\u5730\u7684\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u4fdd\u7ba1\uff0c\u4f46\u5982\u4eca\u9996\u5e2d\u7267\u5e08\u8d1d\u7eb3\u7d22\u90fd\u88ab\u6d3e\u4e86\u51fa\u6765\uff0c\u795e\u6bbf\u65e0\u4eba\u4e3b\u6301\uff0c\u8389\u62c9\u516c\u7235\u8eab\u4e3a\u535a\u5f97\u4e4b\u95e8\u7684\u884c\u653f\u9886\u8896\uff0c\u8981\u628a\u9ece\u660e\u4e4b\u77f3\u62ff\u5230\u624b\uff0c\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\u3002</p><p>\u9ece\u660e\u4e4b\u77f3\u662f\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u521b\u9020\u7684\u795e\u5668\uff0c\u5a01\u529b\u5de8\u5927\uff0c\u82e5\u662f\u5728\u5e73\u65f6\uff0c\u60f3\u6467\u6bc1\u5b83\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u505a\u5230\u7684\u4e8b\u60c5\u3002\u4f46\u5982\u4eca\u4e3a\u4e86\u9a71\u6563\u761f\u75ab\uff0c\u9ece\u660e\u4e4b\u77f3\u5df2\u7ecf\u8017\u8d39\u4e86\u7edd\u5927\u90e8\u5206\u529b\u91cf\uff0c\u4e00\u65f6\u672a\u80fd\u6062\u590d\uff0c\u6b63\u662f\u6700\u8106\u5f31\u7684\u65f6\u5019\u3002\u6b64\u65f6\u4e0b\u624b\uff0c\u8fd8\u771f\u672a\u5fc5\u6ca1\u6709\u5e0c\u671b\u3002</p><p>\u81f3\u4e8e\u5854\u6d1b\u5a1c\u4fe1\u5f92\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u5927\u8d39\u5468\u7ae0\u5bf9\u4ed8\u9ece\u660e\u4e4b\u77f3\uff0c\u8fd9\u5c31\u4e0d\u6e05\u695a\u4e86\u3002</p><p>\u201c\u5854\u6d1b\u5a1c\u662f\u90aa\u795e\u561b\uff0c\u201d\u6b27\u51ef\u8038\u8038\u80a9\uff0c\u201c\u90aa\u795e\u8981\u6bc1\u6389\u5584\u795e\u7684\u795e\u5668\uff0c\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\u554a\u3002\u201d</p><p>\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u542c\u8d77\u6765\u4e5f\u4f3c\u4e4e\u5f88\u5408\u7406\uff0c\u53ea\u662f\u603b\u89c9\u5f97\u54ea\u91cc\u9690\u7ea6\u4e0d\u5bf9\u52b2\uff0c\u4e0d\u8fc7\u6682\u65f6\u4e5f\u7ba1\u4e0d\u4e86\u8fd9\u4e48\u591a\u4e86\u3002</p><p>\u65e2\u7136\u53d1\u73b0\u8389\u62c9\u516c\u7235\u5c31\u662f\u5143\u51f6\uff0c\u6885\u83f2\u65af\u539f\u672c\u7684\u6253\u7b97\u81ea\u7136\u538b\u6839\u884c\u4e0d\u901a\u3002\u5982\u679c\u8fd8\u56de\u535a\u5f97\u4e4b\u95e8\u53bb\uff0c\u4e0d\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u4e5f\u662f\u767d\u8d39\u529b\u6c14\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u8389\u62c9\u516c\u7235\u548c\u5854\u6d1b\u5a1c\u4fe1\u5f92\u8981\u6bc1\u6389\u9ece\u660e\u4e4b\u77f3\uff0c\u8fd9\u53ef\u662f\u6885\u83f2\u65af\u4e07\u4e07\u4e0d\u80fd\u5141\u8bb8\u7684\u4e8b\u60c5\uff0c\u795e\u5668\u662f\u5979\u4ece\u8ff7\u65af\u5353\u8bfa\u5e26\u6765\u7684\uff0c\u82e5\u662f\u771f\u88ab\u6bc1\u4e86\uff0c\u5230\u65f6\u5019\u56de\u53bb\u8981\u5982\u4f55\u4ea4\u5dee\u3002</p><p>\u5b9d\u77f3\u4e0a\u5e7b\u51fa\u7684\u5f71\u50cf\u5df2\u7ecf\u6d88\u5931\uff0c\u4f20\u5f71\u672f\u6301\u7eed\u4e0d\u4e86\u591a\u4e45\uff0c\u4f46\u4e8b\u60c5\u5df2\u7ecf\u5f88\u6e05\u695a\u3002\u5854\u6d1b\u5a1c\u4fe1\u5f92\u5728\u805a\u4f1a\uff0c\u8389\u62c9\u516c\u7235\u5df2\u7ecf\u73b0\u8eab\uff0c\u5e76\u4e14\u53d6\u5230\u4e86\u9ece\u660e\u4e4b\u77f3\u2026\u2026\u4ed6\u4eec\u8981\u4ec0\u4e48\u65f6\u5019\u52a8\u624b\u6bc1\u6389\u8fd9\u4ef6\u795e\u5668\uff1f</p><p>\u201c\u5e94\u8be5\u5c31\u662f\u4eca\u591c\u4e86\u5427\uff0c\u201d\u6b27\u51ef\u4eff\u4f5b\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\uff0c\u201c\u6309\u9053\u7406\u8bf4\uff0c\u5e94\u8be5\u662f\u4eca\u591c\u96f6\u70b9\u4e3e\u884c\u796d\u7940\uff0c\u6467\u6bc1\u9ece\u660e\u4e4b\u77f3\u3002\u201d</p><p>\u73b0\u5728\u5927\u7ea6\u662f\u4e0b\u5348\u516d\u4e03\u70b9\u949f\uff0c\u8ddd\u79bb\u96f6\u70b9\u8fd8\u6709\u4e94\u516d\u4e2a\u5c0f\u65f6\u3002</p><p>\u743c\u6069\u770b\u5411\u6885\u83f2\u65af\uff0c\u7b49\u5f85\u7740\u5c11\u5973\u4f5c\u51fa\u51b3\u5b9a\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6b27\u51ef\u521a\u624d\u7684\u65b0\u60c5\u62a5\uff0c\u5bfc\u81f4\u6446\u5728\u4ed6\u4eec\u9762\u524d\u7684\u8def\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e00\u6761\uff0c\u6ca1\u6709\u5176\u4ed6\u9009\u62e9\u3002</p><p>\u53ea\u80fd\u7acb\u523b\u524d\u5f80\u90a3\u5ea7\u5854\u6d1b\u5a1c\u4fe1\u5f92\u7684\u79d8\u5bc6\u795e\u6bbf\uff0c\u7834\u574f\u4ed6\u4eec\u7684\u796d\u7940\uff0c\u62a2\u56de\u9ece\u660e\u4e4b\u77f3\uff0c\u987a\u4fbf\u6740\u6389\u90a3\u4e9b\u90aa\u6559\u5f92\u4eec\u3002</p><p>\u8fd9\u5b8c\u5168\u7b26\u5408\u743c\u6069\u7684\u9884\u671f\uff0c\u53ea\u662f\u2026\u2026\u6b27\u51ef\u7684\u8fd9\u4e2a\u60c5\u62a5\uff0c\u4e5f\u672a\u514d\u6765\u5f97\u592a\u53ca\u65f6\u592a\u5de7\u4e86\u70b9\u5427\u3002</p><p>\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u8bb0\u5fc6\u6c34\u6676\u548c\u4f20\u5f71\u672f\u662f\u6ca1\u6cd5\u9020\u5047\u7684\uff0c\u6240\u4ee5\u743c\u6069\u770b\u5230\u7684\u5f71\u50cf\u5e94\u8be5\u5c5e\u5b9e\u3002\u4f46\u4ee5\u62c9\u6c83\u514b\u8fd9\u79cd\u7a0b\u5ea6\u7684\u5927\u5deb\u5e08\u6765\u8bf4\uff0c\u5018\u82e5\u771f\u80fd\u5728\u5176\u4e2d\u5f04\u70b9\u624b\u811a\uff0c\u743c\u6069\u4e5f\u5e76\u4e0d\u4f1a\u611f\u5230\u591a\u4e48\u60ca\u8bb6\u3002\u4f55\u51b5\uff0c\u5c31\u7b97\u5f71\u50cf\u5b8c\u5168\u5c5e\u5b9e\uff0c\u8fd9\u80cc\u540e\u4eba\u4e3a\u64cd\u7eb5\u7684\u75d5\u8ff9\uff0c\u4e5f\u5b9e\u5728\u672a\u514d\u592a\u660e\u663e\u4e86\u70b9\uff0c\u53ea\u8981\u662f\u4e2a\u4eba\u5c31\u80fd\u770b\u5f97\u51fa\u6765\u3002</p><p>\u4e0d\u8fc7\u2026\u2026\u4e5f\u65e0\u6240\u8c13\u4e86\uff0c\u4ece\u53e6\u5916\u4e00\u4e2a\u65b9\u9762\u60f3\uff0c\u8fd9\u4e5f\u672a\u5c1d\u4e0d\u662f\u4ef6\u597d\u4e8b\u3002</p><p>\u62c9\u6c83\u514b\u8981\u743c\u6069\u53bb\u627e\u90a3\u7fa4\u5854\u6d1b\u5a1c\u4fe1\u5f92\u7684\u9ebb\u70e6\uff0c\u53e3\u5934\u4e0a\u5ba3\u79f0\u7684\u4ec0\u4e48\u201c\u6f84\u6e05\u4e8b\u5b9e\u201d\u81ea\u7136\u7eaf\u5c5e\u80e1\u626f\uff0c\u4e09\u5c81\u5b69\u5b50\u90fd\u4e0d\u4f1a\u76f8\u4fe1\u3002\u81f3\u4e8e\u4ed6\u771f\u6b63\u7684\u76ee\u7684\u2026\u2026\u9b3c\u624d\u77e5\u9053\uff0c\u4f46\u81f3\u5c11\u5e94\u8be5\u4e0d\u4f1a\u662f\u60f3\u8ba9\u743c\u6069\u53bb\u9001\u6b7b\uff0c\u4ed6\u82e5\u662f\u770b\u743c\u6069\u4e0d\u987a\u773c\uff0c\u62ac\u62ac\u624b\u6307\u5c31\u6740\u4e86\uff0c\u54ea\u91cc\u9700\u8981\u8fd9\u4e48\u8d39\u624b\u811a\u3002\u867d\u8bf4\u743c\u6069\u662f\u9634\u9b42\u57ce\u4f7f\u8005\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u5c31\u662f\u4e2a\u4f7f\u8005\u7f62\u4e86\uff0c\u62c9\u6c83\u514b\u6740\u4e86\u4ed6\uff0c\u96be\u4e0d\u6210\u9634\u9b42\u57ce\u8fd8\u4f1a\u5174\u5e08\u52a8\u4f17\u6765\u62a5\u590d\uff1f\u60f3\u60f3\u5c31\u77e5\u9053\u4e0d\u53ef\u80fd\u3002</p><p>\u65e2\u7136\u4e0d\u662f\u8ba9\u743c\u6069\u53bb\u9001\u6b7b\uff0c\u90a3\u5c31\u4e00\u5207\u597d\u8bf4\u4e86\u2026\u2026\u81f3\u4e8e\u4ed6\u662f\u7eaf\u7cb9\u95f2\u7740\u6ca1\u4e8b\u627e\u4e8b\u4e5f\u597d\uff0c\u5355\u7eaf\u770b\u90a3\u4e9b\u5854\u6d1b\u5a1c\u4fe1\u5f92\u4e0d\u987a\u773c\u4e5f\u597d\uff0c\u60f3\u8bd5\u8bd5\u743c\u6069\u7684\u672c\u4e8b\u4e5f\u597d\uff0c\u6216\u8005\u6709\u4ec0\u4e48\u5176\u4ed6\u6253\u7b97\u2014\u2014\u5168\u90fd\u65e0\u6240\u8c13\uff0c\u743c\u6069\u4e00\u6982\u4e0d\u5173\u5fc3\uff0c\u4e5f\u6ca1\u8d44\u683c\u53bb\u5173\u5fc3\u3002</p><p>\u662f\u62c9\u6c83\u514b\u60f3\u6253\u9ece\u660e\u4e4b\u77f3\u7684\u4e3b\u610f\u4e48\uff1f</p><p>\u8fd9\u4e2a\u5ff5\u5934\u4e00\u95ea\u800c\u8fc7\uff0c\u968f\u5373\u88ab\u5426\u5b9a\u4e86\u3002\u9ece\u660e\u4e4b\u77f3\u867d\u7136\u662f\u73cd\u8d35\u65e0\u6bd4\u7684\u795e\u5668\uff0c\u4f46\u5374\u66f4\u662f\u6668\u66e6\u4e4b\u795e\u7684\u9020\u7269\uff0c\u4e00\u5207\u4ea1\u7075\u7684\u514b\u661f\u3002\u5854\u6d1b\u5a1c\u4fe1\u5f92\u8981\u6467\u6bc1\u6389\u9ece\u660e\u4e4b\u77f3\uff0c\u5bf9\u62c9\u6c83\u514b\u8fd9\u79cd\u5deb\u5996\u6765\u8bf4\u7406\u5f53\u662f\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\u60c5\u3002</p><p>\u53e6\u5916\uff0c\u8fd9\u4e2a\u6b27\u51ef\u5230\u5e95\u5728\u8fd9\u91cc\u9762\u626e\u6f14\u4ec0\u4e48\u89d2\u8272\u5462\u3002\u770b\u4ed6\u548c\u62c9\u6c83\u514b\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u4e0d\u50cf\u662f\u90e8\u5c5e\uff0c\u4f46\u770b\u4ed6\u6240\u4f5c\u6240\u4e3a\u5206\u660e\u53c8\u662f\u4e00\u4f19\u7684\u2026\u2026\u62c9\u6c83\u514b\u7684\u670b\u53cb\u4e48\uff1f</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u6452\u5f03\u6389\u8fd9\u4e2a\u6beb\u65e0\u610f\u4e49\u7684\u5ff5\u5934\u3002\u7b97\u4e86\u5427\uff0c\u5982\u679c\u8fd9\u662f\u4e00\u4e2a\u65e9\u5c31\u5e03\u597d\u7684\u5c40\uff0c\u7b49\u7740\u743c\u6069\u8fdb\u6765\uff0c\u90a3\u4e48\u5c31\u65e9\u65e9\u628a\u5b83\u5b8c\u6210\u5427\uff1b\u5982\u679c\u8fd9\u662f\u4e00\u4e2a\u6e38\u620f\uff0c\u800c\u743c\u6069\u662f\u88ab\u64cd\u63a7\u7684\u68cb\u5b50\uff0c\u90a3\u4e48\u8d76\u5feb\u6309\u8bbe\u8ba1\u597d\u7684\u5267\u60c5\u901a\u5173\u5c31\u662f\uff0c\u770b\u770b\u63a5\u4e0b\u6765\u5230\u5e95\u8981\u53d1\u751f\u4ec0\u4e48\u3002</p><p>\u8fd9\u81ea\u7136\u4e0d\u662f\u4ec0\u4e48\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4e0d\u6ee1\uff0c\u4eba\u8981\u73b0\u5b9e\uff0c\u5426\u5219\u6d3b\u4e0d\u957f\u3002</p><p>\u743c\u6069\u5fc3\u5e73\u6c14\u548c\u5730\u770b\u7740\u6885\u83f2\u65af\uff0c\u7b49\u5f85\u7740\uff0c\u5c11\u5973\u5e76\u6ca1\u6709\u601d\u8003\u5f88\u4e45\uff0c\u5979\u505a\u51b3\u5b9a\u7d20\u6765\u5f88\u5feb\u3002</p><p>\u201c\u6b27\u51ef\u5148\u751f\u8fde\u8fd9\u79cd\u6d88\u606f\u90fd\u80fd\u6536\u96c6\u5230\uff0c\u5b9e\u5728\u8ba9\u4eba\u4f69\u670d\u5f97\u5f88\uff0c\u201d\u6885\u83f2\u65af\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u6211\u8fd8\u4e0d\u66fe\u53c2\u89c2\u8fc7\u5854\u6d1b\u5a1c\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u90a3\u4e48\u5c31\u53bb\u89c1\u8bc6\u89c1\u8bc6\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u82e5\u8bba\u60f3\u6cd5\uff0c\u6885\u83f2\u65af\u81ea\u7136\u548c\u743c\u6069\u5b8c\u5168\u4e0d\u540c\uff0c\u4f46\u5e78\u597d\u5728\u6839\u672c\u7684\u601d\u7ef4\u7acb\u8db3\u70b9\u4e0a\uff0c\u5374\u6ca1\u4ec0\u4e48\u533a\u522b\uff0c\u5c31\u662f\u201c\u627f\u8ba4\u73b0\u5b9e\u201d\u3002</p><p>\u5bf9\u4e8e\u6885\u83f2\u65af\u6765\u8bf4\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u867d\u7136\u8be1\u5f02\u590d\u6742\uff0c\u4f46\u5176\u5b9e\u9700\u8981\u8003\u8651\u7684\u95ee\u9898\u5f88\u7b80\u5355\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u53ea\u8981\u77e5\u9053\u4e00\u70b9\u5c31\u8db3\u591f\uff1a\u201c\u8389\u62c9\u516c\u7235\u548c\u5854\u6d1b\u5a1c\u4fe1\u5f92\u5236\u9020\u761f\u75ab\uff0c\u84c4\u8c0b\u6467\u6bc1\u9ece\u660e\u4e4b\u77f3\uff0c\u662f\u771f\u662f\u5047\uff1f\u201d</p><p>\u53ea\u8981\u7262\u7262\u628a\u63e1\u8fd9\u4e00\u70b9\uff0c\u5176\u4f59\u5168\u90fd\u4e0d\u8fc7\u662f\u65e0\u5173\u7d27\u8981\u7684\u7ec6\u679d\u672b\u8282\uff0c\u53ef\u4ee5\u6682\u65f6\u5ffd\u7565\u4e0d\u8bba\u3002\u62c9\u6c83\u514b\u4e5f\u597d\uff0c\u6b27\u51ef\u4e5f\u597d\uff0c\u5176\u4ed6\u6240\u6709\u4eba\u4e5f\u597d\uff0c\u65e0\u8bba\u6709\u4ec0\u4e48\u6253\u7b97\uff0c\u90fd\u4e0d\u5fc5\u52a0\u4ee5\u8003\u8651\u3002</p><p>\u8bb0\u5fc6\u6c34\u6676\u548c\u4f20\u5f71\u672f\uff0c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\u662f\u4e0d\u80fd\u9020\u5047\u7684\u3002\u6240\u770b\u5230\u7684\u5f71\u50cf\u867d\u7136\u53ea\u662f\u4e00\u4e9b\u7247\u6bb5\uff0c\u4f46\u4e5f\u5df2\u7ecf\u80fd\u57fa\u672c\u8bf4\u660e\u95ee\u9898\uff0c\u6b27\u51ef\u7684\u6d88\u606f\u53ea\u6015\u662f\u771f\u7684\uff0c\u8389\u62c9\u516c\u7235\u786e\u5b9e\u662f\u5854\u6d1b\u5a1c\u4fe1\u5f92\uff0c\u4ed6\u4eec\u6b63\u8981\u7b79\u5212\u6467\u6bc1\u9ece\u660e\u4e4b\u77f3\uff0c\u8fd9\u6b21\u535a\u5f97\u4e4b\u95e8\u7684\u5927\u761f\u75ab\uff0c\u6b7b\u4f24\u5343\u4e07\uff0c\u4ece\u5934\u5230\u5c3e\u5c31\u662f\u4e00\u4e2a\u5708\u5957\u3002</p><p>\u4e0d\u8fc7\uff0c\u771f\u76f8\u63ed\u793a\u5f97\u592a\u8f7b\u6613\uff0c\u53cd\u800c\u7ed9\u4eba\u4e00\u79cd\u96be\u4ee5\u7f6e\u4fe1\u7684\u611f\u89c9\u3002\u8fd9\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u5f88\u591a\u65f6\u5019\u4eb2\u773c\u6240\u89c1\u90fd\u672a\u5fc5\u4e3a\u5b9e\uff0c\u4f55\u51b5\u4ec5\u4ec5\u662f\u770b\u51e0\u4e2a\u5f71\u50cf\u7247\u6bb5\u3002\u6885\u83f2\u65af\u8bf4\u201c\u53bb\u89c1\u8bc6\u89c1\u8bc6\u201d\uff0c\u5176\u4e2d\u4e5f\u4fbf\u9690\u542b\u4e86\u8fd9\u4e2a\u610f\u601d\uff0c\u8981\u53bb\u4eb2\u81ea\u5224\u65ad\u624d\u80fd\u4e0b\u7ed3\u8bba\u3002\u4e0d\u7ba1\u662f\u771f\u662f\u5047\uff0c\u53cd\u6b63\u4e8b\u60c5\u53d1\u5c55\u5230\u8fd9\u4e00\u6b65\uff0c\u53ea\u80fd\u524d\u53bb\u770b\u770b\uff0c\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\u3002</p><p>\u90a3\u4e48\uff0c\u5c31\u53bb\u89c1\u8bc6\u89c1\u8bc6\u5427\u3002</p><p>\u743c\u6069\u7528\u9632\u5fa1\u80f8\u9488\u6362\u6765\u7684\u90a3\u5f20\u5730\u56fe\u4e0a\uff0c\u6807\u660e\u90a3\u5ea7\u5854\u6d1b\u5a1c\u4fe1\u5f92\u7684\u79d8\u5bc6\u795e\u6bbf\u5728\u201c\u5df4\u91cc\u65af\u51ef\u5c14\u6865\u201d\u9644\u8fd1\u3002\u743c\u6069\u4ece\u6ca1\u542c\u8fc7\u8fd9\u4e2a\u5730\u540d\uff0c\u81ea\u7136\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u7684\u611f\u89c9\uff0c\u4f46\u6885\u83f2\u65af\u4f3c\u4e4e\u6709\u4e9b\u53cd\u5e94\uff0c\u743c\u6069\u654f\u9510\u5730\u611f\u89c9\u5230\u5979\u4f3c\u4e4e\u8138\u8272\u6709\u4e9b\u8f7b\u5fae\u53d8\u5316\u3002\u5deb\u5e08\u6709\u4e9b\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u5ea7\u6865\u6709\u4ec0\u4e48\u540d\u5802\uff0c\u4f46\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\uff0c\u6682\u65f6\u4e5f\u4e0d\u65b9\u4fbf\u8be2\u95ee\u3002</p><p>\u201c\u5df4\u91cc\u65af\u51ef\u5c14\u6865\u8ddd\u79bb\u8fd9\u91cc\u5f88\u8fdc\u5462\uff0c\u201d\u743c\u6069\u770b\u7740\u5730\u56fe\u8bf4\uff0c\u201c\u81f3\u5c11\u6709\u4e09\u56db\u5929\u7684\u8def\u7a0b\u5427\u3002\u201d</p><p>\u518d\u8fc7\u4e94\u516d\u4e2a\u5c0f\u65f6\uff0c\u5854\u6d1b\u5a1c\u4fe1\u5f92\u5c31\u8981\u6467\u6bc1\u9ece\u660e\u4e4b\u77f3\uff0c\u8fd9\u4e48\u8bf4\uff0c\u65f6\u95f4\u5df2\u7ecf\u8d76\u4e0d\u53ca\u4e86\uff1f</p><p>\u663e\u7136\u8fd9\u662f\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u8fd9\u91cc\u597d\u6b79\u4e5f\u662f\u9b54\u6cd5\u4e16\u754c\uff0c\u6709\u62c9\u6c83\u514b\u8fd9\u79cd\u5927\u5deb\u5996\u5728\u80cc\u540e\u6491\u8170\uff0c\u8fd9\u70b9\u9ebb\u70e6\u7b97\u4ec0\u4e48\u3002\u679c\u7136\uff0c\u5438\u8840\u9b3c\u8389\u6cd5\u513f\u8bf4\uff0c\u62c9\u6c83\u514b\u5148\u751f\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u4f20\u9001\u95e8\uff0c\u76f4\u63a5\u901a\u5f80\u5854\u6d1b\u5a1c\u4fe1\u5f92\u7684\u795e\u6bbf\uff0c\u53ea\u8981\u8d70\u8fdb\u53bb\u5c31\u884c\u3002</p><p>\u5b89\u6392\u5f97\u771f\u662f\u5468\u5230\u554a\uff0c\u743c\u6069\u5fc3\u4e2d\u5fae\u5fae\u51b7\u7b11\u7740\u3002</p><p>\u65e2\u7136\u6709\u4f20\u9001\u95e8\uff0c\u90a3\u4e48\u5c31\u4e0d\u5fc5\u5fd9\u7740\u8d76\u8def\u4e86\uff0c\u5269\u4e0b\u8fd9\u4e94\u516d\u4e2a\u5c0f\u65f6\u6b63\u597d\u7528\u6765\u4f11\u606f\uff0c\u505a\u51c6\u5907\u5de5\u4f5c\u3002\u743c\u6069\u65e9\u4e0a\u51c6\u5907\u7684\u9b54\u6cd5\uff0c\u57fa\u672c\u90fd\u5df2\u7ecf\u7528\u5b8c\u4e86\uff0c\u6b63\u597d\u73b0\u5728\u91cd\u65b0\u51c6\u5907\uff0c\u82e5\u662f\u6362\u4e86\u522b\u7684\u5deb\u5e08\uff0c\u8fd9\u70b9\u65f6\u95f4\u6839\u672c\u4e0d\u591f\uff0c\u4f46\u5bf9\u4ed6\u6765\u8bf4\u662f\u5df2\u7ecf\u7ef0\u7ef0\u6709\u4f59\u4e86\u3002</p><p>\u8fd9\u6b21\u6765\u5de8\u9b54\u5c71\u8109\uff0c\u539f\u672c\u7684\u76ee\u6807\u662f\u62c9\u6c83\u514b\u8fd9\u4e2a\u8001\u5deb\u5996\uff0c\u8c01\u6599\u53d8\u6545\u63a5\u8fde\u6a2a\u751f\uff0c\u5f04\u5230\u73b0\u5728\u8fd9\u79cd\u5c40\u9762\u3002\u539f\u672c\u7684\u4e94\u4eba\u961f\u4f0d\uff0c\u77ed\u77ed\u534a\u65e5\u5185\u51cf\u5458\u4e09\u4eba\uff0c\u5982\u4eca\u53ea\u5269\u4e0b\u743c\u6069\u548c\u6885\u83f2\u65af\uff0c\u800c\u8981\u5bf9\u4ed8\u7684\u654c\u4eba\uff0c\u4e5f\u4ece\u8001\u5deb\u5996\u53d8\u6210\u4e86\u4e00\u7fa4\u5854\u6d1b\u5a1c\u4fe1\u5f92\u2026\u2026\u5443\uff0c\u8fd9\u4e2a\u201c\u4e00\u7fa4\u201d\uff0c\u5230\u5e95\u662f\u591a\u5c11\uff1f</p><p>\u4ece\u524d\u540e\u4e24\u6b21\u7684\u5f71\u50cf\u7247\u6bb5\u6765\u770b\uff0c\u5927\u6982\u662f\u5341\u51e0\u4e2a\u4eba\uff0c\u4e0d\u8d85\u8fc7\u4e8c\u5341\u3002\u8fd9\u4e2a\u6570\u76ee\u4e0d\u7b97\u591a\uff0c\u4f46\u4e5f\u4e0d\u7b97\u5c11\u4e86\uff0c\u8fd9\u6bd5\u7adf\u662f\u5341\u51e0\u4e2a\u5854\u6d1b\u5a1c\u4fe1\u5f92\uff0c\u4e0d\u662f\u5341\u51e0\u4e2a\u5730\u7cbe\u2014\u2014\u800c\u4e14\u6839\u636e\u4e00\u822c\u7684\u7ecf\u9a8c\u6765\u8bf4\uff0c\u90aa\u6076\u4eba\u58eb\u5f80\u5f80\u6218\u529b\u5f3a\uff0c\u6253\u8d77\u67b6\u6765\u66f4\u5389\u5bb3\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5c31\u9760\u8fd9\u4e2a\u5403\u996d\u3002\u53cd\u5012\u662f\u5584\u826f\u4eba\u58eb\uff0c\u6bd4\u5982\u8d1d\u7eb3\u7d22\u8fd9\u79cd\uff0c\u5730\u4f4d\u867d\u9ad8\uff0c\u638c\u63e1\u7684\u795e\u672f\u4e5f\u4e0d\u53ef\u8c13\u4e0d\u5f3a\uff0c\u4f46\u771f\u5230\u4e86\u5b9e\u6218\u4e2d\u5374\u662f\u5e9f\u67f4\uff0c\u5494\u5693\u4e00\u58f0\u5c31\u88ab\u9686\u5947\u627c\u65ad\u4e86\u8116\u5b50\u3002</p><p>\u5c31\u743c\u6069\u548c\u6885\u83f2\u65af\u4e24\u4e2a\u4eba\uff0c\u80fd\u5bf9\u4ed8\u8fd9\u4e48\u591a\u4e48\uff1f\u8fd9\u4e2a\u4efb\u52a1\u770b\u8d77\u6765\u8fd8\u9887\u6709\u4e9b\u96be\u5ea6\u5462\u3002</p><p>\u8bdd\u8bf4\u56de\u6765\uff0c\u8fd9\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u743c\u6069\u81ea\u4ece\u51fa\u9634\u9b42\u57ce\u4ee5\u6765\uff0c\u6253\u8fc7\u7684\u67b6\u90fd\u662f\u4ee5\u5be1\u654c\u4f17\u5f97\u591a\uff0c\u52a8\u4e0d\u52a8\u5c31\u662f\u88ab\u51e0\u5341\u53ea\u4e0a\u767e\u53ea\u4eba\u9762\u72ee\u56f4\u4e0a\uff0c\u7167\u6837\u4e5f\u597d\u7aef\u7aef\u5730\u6d3b\u5230\u4eca\u5929\u2026\u2026\u5443\uff0c\u5f53\u7136\u4e86\uff0c\u8fd9\u4e3b\u8981\u662f\u6885\u83f2\u65af\u7684\u6218\u529f\uff0c\u4e0d\u80fd\u7b97\u743c\u6069\u81ea\u5df1\u7684\u672c\u4e8b\u3002</p><p>\u867d\u8bf4\u743c\u6069\u76f8\u4fe1\u62c9\u6c83\u514b\u4e0d\u662f\u6545\u610f\u8ba9\u81ea\u5df1\u53bb\u9001\u6b7b\uff0c\u4f46\u6709\u975e\u5e38\u5927\u7684\u53ef\u80fd\u6027\u662f\u60f3\u501f\u6b64\u8bd5\u8bd5\u4ed6\u7684\u672c\u4e8b\u3002\u5982\u679c\u771f\u662f\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u597d\u597d\u8868\u73b0\u5c31\u5f88\u6709\u5fc5\u8981\u4e86\u3002\u867d\u8bf4\u6709\u6885\u83f2\u65af\u8fd9\u79cd\u5f3a\u608d\u65e0\u6bd4\u7684\u5723\u6b66\u58eb\u5728\uff0c\u743c\u6069\u5e76\u4e0d\u592a\u628a\u90a3\u5341\u51e0\u4e2a\u90aa\u6559\u5f92\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u5982\u679c\u5168\u662f\u9760\u5973\u4eba\u7684\u529b\u91cf\uff0c\u81ea\u5df1\u6ca1\u6709\u4ec0\u4e48\u4f18\u79c0\u8868\u73b0\uff0c\u90a3\u663e\u7136\u4e5f\u4e0d\u662f\u4ec0\u4e48\u7406\u60f3\u7ed3\u5c40\u3002</p><p>\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\u7f62\uff0c\u6682\u65f6\u4e5f\u6ca1\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u60c5\u51b5\u8fd8\u4e0d\u592a\u660e\u6670\uff0c\u4e5f\u65e0\u6cd5\u505a\u4ec0\u4e48\u4e8b\u524d\u7b79\u5212\u3002\u4ee4\u4eba\u4e0d\u723d\u7684\u662f\uff0c\u88ab\u90a3\u4e2a\u6b27\u51ef\u8bc8\u9a97\u53bb\u4e86\u4e24\u4ef6\u9b54\u6cd5\u7269\u54c1\uff0c\u4f4d\u79fb\u6597\u7bf7\u4e5f\u5c31\u7b97\u4e86\uff0c\u9632\u5fa1\u80f8\u9488\u53ef\u662f\u5b9e\u7528\u7684\u597d\u4e1c\u897f\uff0c\u5c11\u4e86\u5b83\u5deb\u5e08\u5c31\u663e\u5f97\u8d8a\u53d1\u8106\u5f31\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u5f88\u5947\u602a\u554a\uff0c\u4ee5\u6b27\u51ef\u8868\u73b0\u51fa\u6765\u7684\u5b9e\u529b\u548c\u8d22\u529b\uff0c\u54ea\u91cc\u9700\u8981\u8d2a\u56fe\u8fd9\u70b9\u5c0f\u9b54\u6cd5\u7269\u54c1\uff0c\u4ed6\u53c8\u4f55\u5fc5\u5982\u6b64\uff0c\u96be\u4e0d\u6210\u662f\u62c9\u6c83\u514b\u6388\u610f\u7684\u4e48\uff1f</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u4e00\u65f6\u7406\u4e0d\u6e05\u8fd9\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u5934\u7eea\uff0c\u53ea\u80fd\u6682\u65f6\u7b97\u4e86\u3002\u8fd9\u5ea7\u5deb\u5e08\u5854\u91cc\u7684\u5bb6\u4f19\uff0c\u62c9\u6c83\u514b\u3001\u6b27\u51ef\u3001\u5438\u8840\u9b3c\u8389\u6cd5\u513f\uff0c\u4e2a\u4e2a\u90fd\u900f\u7740\u8be1\u5f02\uff0c\u8111\u5b50\u91cc\u90fd\u4e0d\u77e5\u9053\u5728\u60f3\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e0d\u80fd\u4ee5\u6b63\u5e38\u4eba\u7684\u601d\u7ef4\u6765\u731c\u5ea6\u3002</p><p>\u8389\u6cd5\u513f\u7ed9\u4ed6\u548c\u6885\u83f2\u65af\u5b89\u6392\u4e86\u5355\u72ec\u7684\u623f\u95f4\u4f11\u606f\uff0c\u6ca1\u4eba\u6765\u6253\u6270\u3002\u743c\u6069\u7f13\u6162\u8c03\u606f\u7740\uff0c\u5927\u7ea6\u8fc7\u4e86\u4e24\u4e2a\u5c0f\u65f6\u540e\u9192\u6765\uff0c\u81ea\u89c9\u7cbe\u795e\u5955\u5955\uff0c\u968f\u5373\u5c55\u5f00\u9b54\u6cd5\u4e66\u51c6\u5907\u6cd5\u672f\u3002</p><p>\u5927\u7ea6\u53c8\u82b1\u4e86\u4e00\u4e2a\u5c0f\u65f6\uff0c\u4ed6\u5c06\u6240\u6709\u7684\u6cd5\u672f\u90fd\u51c6\u5907\u59a5\u5f53\uff0c\u6240\u9700\u8981\u7684\u65bd\u6cd5\u6750\u6599\u5206\u95e8\u522b\u7c7b\u5730\u5728\u957f\u888d\u5185\u4fa7\u53e3\u888b\u91cc\u653e\u597d\u3002\u65f6\u5019\u4e5f\u4e0d\u65e9\uff0c\u5dee\u4e0d\u591a\u8be5\u51fa\u53d1\u4e86\u3002</p><p>\u4ed6\u8d70\u51fa\u623f\u95f4\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u5df2\u7ecf\u9759\u9759\u5730\u5728\u7b49\u5f85\u7740\uff0c\u7ecf\u8fc7\u4f11\u606f\uff0c\u6cbb\u7597\u836f\u6c34\u5df2\u7ecf\u5b8c\u5168\u53d1\u6325\u4f5c\u7528\uff0c\u5c11\u5973\u8138\u4e0a\u7684\u706b\u7130\u707c\u4f24\u57fa\u672c\u770b\u4e0d\u89c1\u4e86\uff0c\u53ea\u9690\u9690\u7ea6\u7ea6\u8fd8\u6709\u4e9b\u75d5\u8ff9\uff0c\u518d\u8fc7\u51e0\u5929\u4e5f\u5c31\u6ca1\u4e8b\u3002\u89c1\u743c\u6069\u51fa\u6765\uff0c\u5979\u5fae\u5fae\u70b9\u5934\uff0c\u201c\u8d70\u5427\u3002\u201d</p></article>"}}}]);
