(window.webpackJsonp=window.webpackJsonp||[]).push([[1367],{1390:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e8c\u8282 \u968f\u9047\u800c\u5b89",content:"<article><h2>\u7b2c\u4e09\u5341\u4e8c\u8282 \u968f\u9047\u800c\u5b89</h2><p>\u795e\u653e\u5f03\u4e86\u6211\u3002</p><p>\u4f46\u6211\u6ca1\u6709\u653e\u5f03\u795e\u3002</p><p>\u743c\u6069\u9ed8\u7136\u601d\u7d22\u7740\uff0c\u6162\u6162\u54c1\u5c1d\u5176\u4e2d\u7684\u610f\u5473\u3002\u4fe1\u4ef0\u81ea\u7136\u4e4b\u9053\u7684\u5fb7\u9c81\u4f9d\uff0c\u88ab\u5f3a\u884c\u53d8\u6210\u5438\u8840\u9b3c\u8fd9\u79cd\u4ea1\u7075\u5b58\u5728\uff0c\u88ab\u81ea\u5df1\u7684\u795e\u7947\u6240\u9057\u5f03\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5e76\u4e0d\u662f\u4e00\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u60c5\u5427\u3002\u8389\u6cd5\u513f\u6de1\u6de1\u8bf4\u6765\uff0c\u5766\u7136\u81ea\u82e5\uff0c\u4eff\u4f5b\u5168\u4e0d\u4ecb\u610f\u3002\u4fbf\u662f\u521a\u624d\u6240\u8bf4\uff0c\u201c\u795e\u867d\u7136\u629b\u5f03\u4e86\u6211\uff0c\u6211\u5374\u6ca1\u6709\u629b\u5f03\u795e\u201d\uff0c\u521d\u542c\u4f3c\u4e4e\u8352\u8bde\uff0c\u4ed4\u7ec6\u60f3\u6765\uff0c\u8fd9\u8bdd\u82e5\u975e\u81ea\u5632\uff0c\u90a3\u4e48\u5374\u4e5f\u7740\u5b9e\u4e0d\u5931\u4e3a\u4e00\u79cd\u8c41\u8fbe\u548c\u6d12\u8131\u3002</p><p>\u6613\u5730\u800c\u5904\uff0c\u743c\u6069\u81ea\u5ea6\u53ea\u6015\u505a\u4e0d\u5230\u5427\u3002</p><p>\u5ff5\u53ca\u6b64\u5904\uff0c\u7a81\u7136\u5fc3\u4e2d\u5fae\u5fae\u6709\u4e9b\u6005\u7136\uff0c\u4f38\u81c2\u5c06\u8389\u6cd5\u513f\u62b1\u7d27\uff0c\u8ba9\u5979\u7684\u8138\u8d34\u7740\u81ea\u5df1\u80f8\u53e3\uff0c\u201c\u4f60\u591a\u5927\u4e86\uff1f\u201d\u4ed6\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u95ee\u5973\u6027\u7684\u5e74\u9f84\u662f\u5f88\u4e0d\u793c\u8c8c\u7684\u5462\u3002\u201d\u8389\u6cd5\u513f\u56de\u7b54\uff0c\u4f46\u8bed\u6c14\u4e2d\u5e76\u65e0\u4ec0\u4e48\u607c\u6012\u3002</p><p>\u201c\u6211\u4ee5\u4e3a\u4f60\u4e0d\u4f1a\u4ecb\u610f\u8fd9\u4e9b\u5462\u3002\u201d</p><p>\u201c\u6211\u60f3\u60f3\u554a\uff0c\u201d\u8389\u6cd5\u513f\u56de\u5fc6\u7740\uff0c\u201c\u5728\u8fd9\u91cc\u5446\u4e45\u4e86\uff0c\u90fd\u6ca1\u65f6\u95f4\u6982\u5ff5\u4e86\u2026\u2026\u5e94\u8be5\uff0c\u518d\u8fc7\u51e0\u4e2a\u6708\u5c31\u4e8c\u5341\u5c81\u4e86\u5427\u3002\u201d</p><p>\u8389\u6cd5\u513f\u8bf4\u662f\u4e00\u5e74\u591a\u5e74\u8bef\u5165\u6b64\u5904\uff0c\u88ab\u62c9\u6c83\u514b\u53d8\u6210\u5438\u8840\u9b3c\u7684\uff0c\u8fd9\u4e48\u8bf4\uff0c\u5979\u5f53\u65f6\u5e94\u8be5\u624d\u5341\u516b\u5c81\u3002\u5341\u516b\u5c81\u7684\u5c11\u5973\uff0c\u867d\u7136\u5df2\u7ecf\u7b97\u6210\u5e74\uff0c\u4f46\u9aa4\u7136\u906d\u9047\u8fd9\u7b49\u53d8\u6545\uff0c\u53ea\u6015\u4e5f\u4e0d\u662f\u666e\u901a\u4eba\u6240\u80fd\u627f\u53d7\u7684\u5427\u3002</p><p>\u65e2\u7136\u77e5\u6653\u62c9\u6c83\u514b\u53d1\u660e\u8fd9\u76f4\u63a5\u8f6c\u5316\u5438\u8840\u9b3c\u7684\u65b9\u6cd5\uff0c\u518d\u8054\u60f3\u8d77\u521a\u8fdb\u5de8\u9b54\u5c71\u8109\u7684\u65f6\u5019\uff0c\u649e\u4e0a\u7684\u90a3\u4e2a\u53cc\u5934\u5de8\u9b54\uff0c\u4ee5\u53ca\u201c\u751f\u6b7b\u8f6c\u5316\u201d\u8fd9\u4e2a\u8bcd\uff0c\u743c\u6069\u9690\u7ea6\u731c\u5230\u62c9\u6c83\u514b\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\u3002\u8001\u5deb\u5996\u5b85\u5728\u8fd9\u5deb\u5e08\u4e4b\u5893\u91cc\u4e00\u5343\u591a\u5e74\uff0c\u770b\u6837\u5b50\u53ef\u5b9e\u5728\u6ca1\u95f2\u7740\u554a\u3002</p><p>\u53ea\u662f\uff0c\u6709\u5e0c\u671b\u4e48\uff1f</p><p>\u751f\u6b7b\u8f6c\u5316\uff0c\u662f\u8010\u745f\u65f6\u4ee3\u7684\u9b54\u6cd5\u7814\u7a76\u7684\u4e00\u5927\u8bfe\u9898\uff0c\u5982\u679c\u80fd\u6210\u529f\u7684\u8bdd\uff0c\u51e0\u4e4e\u53ef\u4ee5\u6253\u7834\u751f\u6b7b\u94c1\u5f8b\uff0c\u8ba9\u51e1\u4eba\u6210\u4e3a\u8fd1\u4f3c\u795e\u7947\u4e00\u822c\u7684\u5b58\u5728\uff0c\u6c38\u751f\u4e0d\u673d\u4fbf\u518d\u4e5f\u4e0d\u662f\u68a6\u60f3\u3002\u4f46\u4ee5\u5f53\u65f6\u8010\u745f\u5e1d\u56fd\u7684\u9b54\u6cd5\u4e4b\u53d1\u8fbe\uff0c\u5927\u5965\u672f\u5e08\u4e4b\u591a\uff0c\u8d44\u6e90\u4e4b\u5145\u8db3\uff0c\u7ec8\u7a76\u4e5f\u6ca1\u80fd\u7814\u7a76\u6210\u529f\u3002\u5e1d\u56fd\u9668\u706d\uff0c\u5927\u5965\u672f\u5e08\u4eec\u4f24\u4ea1\u6b86\u5c3d\uff0c\u9b54\u6cd5\u6587\u660e\u6bc1\u4e8e\u4e00\u65e6\uff0c\u62c9\u6c83\u514b\u867d\u7136\u4fa5\u5e78\u9003\u5f97\u6027\u547d\uff0c\u4f46\u743c\u6069\u4e5f\u4e0d\u592a\u76f8\u4fe1\u4ed6\u5c31\u771f\u80fd\u628a\u8fd9\u4e2a\u7814\u7a76\u9879\u76ee\u5b8c\u6210\u3002</p><p>\u5c31\u90a3\u53cc\u5934\u5de8\u9b54\u548c\u8389\u6cd5\u513f\u8fd9\u4e24\u4e2a\u201c\u5b9e\u9a8c\u54c1\u201d\u6765\u770b\uff0c\u663e\u7136\u62c9\u6c83\u514b\u8ddd\u79bb\u6210\u529f\u8fd8\u6709\u5f88\u5927\u7684\u4e00\u6bb5\u8def\u3002\u53cc\u5934\u5de8\u9b54\u4e0d\u8fc7\u662f\u975e\u5e38\u52c9\u5f3a\u5730\u628a\u6d3b\u4f53\u548c\u4ea1\u7075\u62fc\u51d1\u5728\u4e00\u8d77\uff0c\u660e\u663e\u662f\u5931\u8d25\u4e4b\u4f5c\uff1b\u81f3\u4e8e\u8389\u6cd5\u513f\uff0c\u4e00\u4e2a\u4e0d\u80fd\u5b66\u4e60\u638c\u63e1\u9b54\u6cd5\u7684\u5438\u8840\u9b3c\u53c8\u6709\u4ec0\u4e48\u7528\u3002</p><p>\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u9690\u7ea6\u8bb0\u5f97\uff0c\u5f53\u5e74\u5728\u8010\u745f\u5e1d\u56fd\u65f6\u671f\uff0c\u4e3b\u6301\u8fd9\u4e2a\u7814\u7a76\u9879\u76ee\u7684\u5927\u5965\u672f\u5e08\uff0c\u6700\u8457\u540d\u7684\u4e00\u4f4d\u4f3c\u4e4e\u662f\u53eb\u5965\u6c83\u3002\u53ef\u60dc\u968f\u7740\u5e1d\u56fd\u9668\u706d\uff0c\u4f30\u8ba1\u4e5f\u65e9\u5b8c\u86cb\u4e86\uff0c\u5426\u5219\u6362\u4e86\u4ed6\u6765\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6bd4\u62c9\u6c83\u514b\u505a\u5f97\u66f4\u597d\u3002</p><p>\u8fd9\u4e9b\u662f\u62c9\u6c83\u514b\u8981\u5934\u75bc\u7684\u95ee\u9898\uff0c\u743c\u6069\u4e5f\u61d2\u5f97\u591a\u60f3\uff0c\u53ea\u8981\u4e0d\u628a\u81ea\u5df1\u6293\u53bb\u5f53\u5b9e\u9a8c\u54c1\u5c31\u884c\u3002\u5c31\u76ee\u524d\u770b\u8d77\u6765\uff0c\u4f3c\u4e4e\u8fd8\u6ca1\u8fd9\u79cd\u8ff9\u8c61\uff0c\u8fd9\u662f\u5f88\u503c\u5f97\u5e86\u5e78\u7684\u4e00\u4ef6\u4e8b\u60c5\u3002\u53ea\u5e0c\u671b\u8001\u5deb\u5996\u4e0d\u8981\u53d8\u5366\uff0c\u65e9\u65e9\u628a\u56de\u4fe1\u5199\u597d\uff0c\u8ba9\u81ea\u5df1\u5e26\u56de\u9634\u9b42\u57ce\uff0c\u90a3\u5c31\u4e07\u4e8b\u5927\u5409\uff0c\u8fd9\u8d9f\u4efb\u52a1\u5706\u6ee1\u5b8c\u6210\uff0c\u81ea\u5df1\u4e5f\u53ef\u4ee5\u548c\u73ca\u5609\u56e2\u805a\u4e86\u3002</p><p>\u76f8\u6bd4\u8d77\u6765\uff0c\u8389\u6cd5\u513f\u5c31\u8981\u4e0d\u5e78\u591a\u4e86\uff0c\u5979\u6f2b\u957f\u7684\u751f\u547d\uff0c\u5c31\u8981\u5728\u8fd9\u9634\u68ee\u9ad8\u5854\u53e4\u5893\u91cc\u5ea6\u8fc7\u4e48\u3002</p><p>\u201c\u4f60\u6bcf\u5929\u90fd\u5e72\u4ec0\u4e48\u5462\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u8389\u6cd5\u513f\u56de\u5fc6\u7740\uff0c\u201c\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u65e0\u975e\u662f\u770b\u770b\u4e66\uff0c\u968f\u65f6\u542c\u5019\u62c9\u6c83\u514b\u5148\u751f\u7684\u53ec\u5524\uff0c\u6bcf\u9694\u4e24\u4e09\u4e2a\u6708\u53bb\u4e00\u8d9f\u70db\u5821\u9001\u4e66\uff0c\u987a\u4fbf\u53bb\u535a\u5f97\u4e4b\u95e8\u627e\u51e0\u4e2a\u4eba\u7c7b\u5438\u8840\u2026\u2026\u201d</p><p>\u542c\u8d77\u6765\u751f\u6d3b\u771f\u662f\u5f88\u65e0\u8da3\u3002</p><p>\u743c\u6069\u6ce8\u610f\u5230\u4e00\u4e2a\u7ec6\u8282\uff0c\u8389\u6cd5\u513f\u63d0\u5230\u62c9\u6c83\u514b\u7684\u65f6\u5019\uff0c\u7528\u7684\u90fd\u662f\u5c0a\u79f0\uff0c\u867d\u7136\u8bed\u6c14\u91cc\u8c08\u4e0d\u4e0a\u591a\u5c11\u53d1\u81ea\u5185\u5fc3\u7684\u5c0a\u656c\uff0c\u4f46\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u4ec7\u89c6\u548c\u654c\u610f\uff0c\u8fd9\u8ba9\u743c\u6069\u6709\u4e9b\u4e0d\u89e3\u3002\u82e5\u662f\u6362\u4e86\u4ed6\uff0c\u597d\u7aef\u7aef\u5730\u6d3b\u7740\uff0c\u7a81\u7136\u88ab\u4eba\u5f3a\u884c\u53d8\u6210\u4e86\u5438\u8840\u9b3c\uff0c\u4ed6\u80af\u5b9a\u4f1a\u6068\u5f97\u54ac\u7259\u5207\u9f7f\uff0c\u60f3\u5c3d\u4e00\u5207\u529e\u6cd5\u4e5f\u8981\u628a\u90a3\u6df7\u86cb\u7ed9\u6740\u4e86\u62a5\u4ec7\u3002</p><p>\u4ed6\u5c0f\u5fc3\u5730\u5411\u8389\u6cd5\u513f\u63d0\u8d77\u8fd9\u4e2a\u95ee\u9898\uff0c\u5438\u8840\u9b3c\u7684\u56de\u7b54\u9887\u4ee4\u4ed6\u610f\u5916\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u4e2a\u4e48\uff0c\u201d\u5979\u5f88\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\uff0c\u201c\u6211\u81ea\u7136\u662f\u4e0d\u559c\u6b22\u53d8\u6210\u5438\u8840\u9b3c\u5566\uff0c\u4e0d\u8fc7\u65e2\u7136\u5df2\u7ecf\u8fd9\u6837\u4e86\uff0c\u90a3\u5c31\u8fd9\u6837\u5427\u3002\u6211\u662f\u4e0d\u559c\u6b22\u62c9\u6c83\u514b\u5148\u751f\u4e86\uff0c\u4f46\u4e5f\u61d2\u5f97\u53bb\u8ba8\u538c\u5427\u3002\u201d</p><p>\u8fd9\u4e2a\u2026\u2026\u8fd9\u7b97\u4ec0\u4e48\u903b\u8f91\u3002</p><p>\u743c\u6069\u89c9\u5f97\u81ea\u5df1\u6709\u4e9b\u4e0d\u592a\u80fd\u7406\u89e3\u5bf9\u65b9\u7684\u8bdd\uff0c\u4ed6\u6000\u7591\u662f\u4e0d\u662f\u521a\u624d\u6fc0\u70c8\u8fd0\u52a8\u8fc7\u5ea6\uff0c\u8111\u5b50\u73b0\u5728\u8fd8\u4e0d\u592a\u6e05\u695a\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u743c\u6069\u63c9\u7740\u8111\u888b\uff0c\u8270\u96be\u5730\u8003\u8651\u7740\u600e\u4e48\u63aa\u8f9e\uff0c\u201c\u4f60\u539f\u672c\u662f\u4e2a\u5fb7\u9c81\u4f9d\uff0c\u65e0\u5fe7\u65e0\u8651\u5730\u5c45\u4f4f\u5728\u68ee\u6797\u91cc\uff0c\u62e5\u6709\u795e\u7947\u7684\u5ba0\u7231\uff0c\u8fc7\u7740\u5feb\u4e50\u7684\u751f\u6d3b\u3002\u800c\u62c9\u6c83\u514b\u5148\u751f\u7a81\u7136\u63d2\u624b\uff0c\u6253\u7834\u4e86\u4f60\u7684\u751f\u6d3b\uff0c\u6539\u53d8\u4e86\u4f60\u7684\u547d\u8fd0\uff0c\u628a\u4f60\u53d8\u6210\u4e86\u5438\u8840\u9b3c\uff0c\u8ba9\u4f60\u4ece\u6b64\u8fc7\u4e0a\u4e86\u4e00\u79cd\u548c\u8fc7\u53bb\u622a\u7136\u4e0d\u540c\u7684\u751f\u6d3b\u2026\u2026\u5bf9\u4e8e\u8fd9\u4e00\u5207\uff0c\u96be\u9053\u4f60\u4e0d\u89c9\u5f97\u4e0d\u6ee1\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u6211\u662f\u4e2a\u968f\u9047\u800c\u5b89\u7684\u4eba\uff0c\u201d\u8389\u6cd5\u513f\u8bf4\uff0c\u201c\u751f\u547d\u4e0e\u6211\u6765\u8bf4\uff0c\u539f\u672c\u5c31\u662f\u4e00\u6761\u672a\u77e5\u7684\u65c5\u9014\u3002\u6211\u4e0d\u77e5\u9053\u6211\u7684\u672a\u6765\u5e94\u8be5\u662f\u4ec0\u4e48\u6837\uff0c\u6240\u4ee5\u4efb\u4f55\u6539\u53d8\u5bf9\u6211\u6765\u8bf4\u81ea\u7136\u4e5f\u90fd\u5e76\u975e\u6539\u53d8\uff0c\u800c\u662f\u7406\u6240\u5f53\u7136\u3002\u6211\u4ee5\u524d\u662f\u5fb7\u9c81\u4f9d\uff0c\u8fc7\u7740\u5fb7\u9c81\u4f9d\u7684\u751f\u6d3b\uff0c\u540e\u6765\u7a81\u7136\u53d8\u6210\u4e86\u5438\u8840\u9b3c\uff0c\u90a3\u4e48\u5c31\u8fc7\u7740\u5438\u8840\u9b3c\u7684\u751f\u6d3b\uff0c\u8fd9\u4e0d\u662f\u5f88\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u5417\uff1f\u4f60\u89c9\u5f97\u62c9\u6c83\u514b\u5148\u751f\u2018\u6539\u53d8\u2019\u4e86\u6211\u7684\u547d\u8fd0\uff0c\u4f46\u662f\uff0c\u743c\u6069\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u89c9\u5f97\uff0c\u8fd9\u6216\u8bb8\u5c31\u662f\u6211\u672c\u5e94\u8be5\u6709\u7684\u547d\u8fd0\u5462\uff1f\u6bd5\u7adf\uff0c\u6211\u4eec\u8c01\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u547d\u8fd0\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u90a3\u4e48\u8c08\u4f55\u6539\u53d8\u5462\uff1f\u201d</p><p>\u201c\u4e00\u5207\u597d\u7684\u548c\u4e0d\u597d\u7684\uff0c\u559c\u6b22\u7684\u548c\u4e0d\u559c\u6b22\u7684\uff0c\u60f3\u8981\u7684\u548c\u4e0d\u60f3\u8981\u7684\uff0c\u90fd\u662f\u547d\u8fd0\u7684\u4e00\u90e8\u5206\u5427\u3002\u6211\u77e5\u9053\u5f88\u591a\u4eba\uff0c\u60f3\u7740\u80fd\u521b\u9020\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u53ef\u60dc\u6211\u5e76\u6ca1\u6709\u8fd9\u6837\u7684\u8fdc\u5927\u5fd7\u5411\u5462\uff0c\u201d\u8389\u6cd5\u513f\u5fae\u5fae\u7b11\u7740\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u59a9\u5a9a\u81f3\u6781\uff0c\u8ba9\u4eba\u5fc3\u751f\u65e0\u9650\u7231\u601c\uff0c\u201c\u6211\u8bf4\u4e86\uff0c\u6211\u662f\u4e2a\u968f\u9047\u800c\u5b89\u7684\u4eba\uff0c\u6211\u6ca1\u60f3\u8fc7\u53bb\u521b\u9020\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u6211\u53ea\u60f3\u5e73\u9759\u5766\u7136\u5730\u9762\u5bf9\u6240\u53d1\u751f\u7684\u4e00\u5207\u7f62\u4e86\u2026\u2026\u8fd9\u542c\u8d77\u6765\u4f3c\u4e4e\u4e0d\u600e\u4e48\u6709\u8da3\uff0c\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u743c\u6069\u53d1\u89c9\u81ea\u5df1\u6ca1\u6cd5\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\uff0c\u6216\u8005\u8bf4\uff0c\u4ed6\u80fd\u5927\u4f53\u660e\u767d\u8389\u6cd5\u513f\u7684\u610f\u601d\uff0c\u4f46\u8fd9\u79cd\u60f3\u6cd5\u5bf9\u4e8e\u4ed6\u800c\u8a00\u672a\u514d\u592a\u964c\u751f\u9065\u8fdc\u4e86\uff0c\u4ee5\u81f3\u4e8e\u6709\u79cd\u532a\u5937\u6240\u601d\u7684\u611f\u89c9\u3002\u4e0d\u8fc7\u8bf4\u5230\u5e95\uff0c\u8fd9\u4e5f\u4e0d\u8fc7\u662f\u5728\u5e8a\u4e0a\u95f2\u804a\u7f62\u4e86\uff0c\u53c8\u4e0d\u662f\u8981\u8fdb\u884c\u4eba\u751f\u89c2\u6559\u80b2\uff0c\u8389\u6cd5\u513f\u5bf9\u547d\u8fd0\u548c\u4eba\u751f\u6301\u4ec0\u4e48\u6001\u5ea6\uff0c\u90a3\u662f\u5979\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u548c\u743c\u6069\u6ca1\u4ec0\u4e48\u5173\u7cfb\u3002</p><p>\u5927\u7ea6\uff0c\u4e5f\u53ea\u6709\u8fd9\u79cd\u4eba\u751f\u89c2\uff0c\u624d\u4f1a\u5728\u88ab\u795e\u7947\u9057\u5f03\u540e\uff0c\u4f9d\u7136\u80fd\u4fdd\u6301\u81ea\u5df1\u7684\u4fe1\u4ef0\u5427\u3002\u867d\u7136\u5df2\u7ecf\u4e0d\u88ab\u627f\u8ba4\uff0c\u867d\u7136\u5df2\u7ecf\u4e27\u5931\u4e86\u5fb7\u9c81\u4f9d\u7684\u8eab\u4efd\uff0c\u867d\u7136\u5df2\u7ecf\u65e0\u6cd5\u83b7\u5f97\u795e\u672f\uff0c\u7948\u7977\u4e5f\u4e0d\u4f1a\u5f97\u5230\u56de\u5e94\uff0c\u4f46\u8fd9\u4e00\u5207\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\u3002</p><p>\u743c\u6069\u6e05\u695a\u81ea\u5df1\u505a\u4e0d\u5230\u8fd9\u4e48\u5e73\u9759\u5766\u7136\u5730\u9762\u5bf9\u547d\u8fd0\uff0c\u89c6\u4e00\u5207\u53d8\u5316\u4e3a\u7406\u6240\u5f53\u7136\u3002\u4ed6\u751a\u81f3\u6000\u7591\u662f\u4e0d\u662f\u62c9\u6c83\u514b\u8fd9\u8001\u5deb\u5996\u7ed9\u8389\u6cd5\u513f\u6d17\u8111\u4e86\uff0c\u6216\u8005\u662f\u8f6c\u5316\u5438\u8840\u9b3c\u7684\u65f6\u5019\u51fa\u4e86\u4ec0\u4e48\u95ee\u9898\uff0c\u5426\u5219\u8fd9\u79cd\u601d\u7ef4\u65b9\u5f0f\u4f3c\u4e4e\u5b9e\u5728\u592a\u602a\u5f02\u4e86\u70b9\u3002\u4e0d\u8fc7\u73b0\u5728\u8ffd\u7a76\u8fd9\u4e2a\u95ee\u9898\u4e5f\u6ca1\u4ec0\u4e48\u610f\u4e49\uff0c\u4ed6\u8f6c\u79fb\u4e86\u8bdd\u9898\u3002</p><p>\u201c\u4f60\u521a\u624d\u8bf4\uff0c\u6bcf\u9694\u4e24\u4e09\u4e2a\u6708\u5c31\u53bb\u4e00\u8d9f\u70db\u5821\u9001\u4e66\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u662f\u554a\uff0c\u987a\u4fbf\u53bb\u535a\u5f97\u4e4b\u95e8\u91cc\u5438\u8840\u561b\uff0c\u201d\u8389\u6cd5\u513f\u8bf4\uff0c\u201c\u867d\u7136\u6211\u8fd9\u4e2a\u5438\u8840\u9b3c\u53d8\u5f97\u4e0d\u592a\u7eaf\u7cb9\uff0c\u5bf9\u9c9c\u8840\u7684\u6e34\u671b\u4e5f\u4e0d\u600e\u4e48\u5f3a\u70c8\uff0c\u4f46\u6bcf\u4e24\u4e09\u4e2a\u6708\u603b\u8fd8\u662f\u8981\u8865\u5145\u4e00\u6b21\u7684\u3002\u201d</p><p>\u4ea1\u7075\u4e00\u822c\u662f\u4e0d\u9700\u8981\u996e\u98df\u7684\uff0c\u4f46\u5438\u8840\u9b3c\u53c8\u662f\u4e2a\u4f8b\u5916\u2014\u2014\u5b9e\u9645\u4e0a\uff0c\u743c\u6069\u90fd\u89c9\u5f97\u5438\u8840\u9b3c\u7b97\u4e0d\u4e0a\u771f\u6b63\u7684\u4ea1\u7075\uff0c\u800c\u662f\u4e00\u79cd\u4ecb\u4e4e\u4e8e\u6d3b\u4eba\u548c\u4ea1\u7075\u4e4b\u95f4\u7684\u72b6\u6001\uff0c\u4f1a\u7761\u89c9\u3001\u4f1a\u8fdb\u98df\u3001\u4f1a\u6d41\u8840\uff0c\u8fd9\u8fd8\u7b97\u4ea1\u7075\u4e48\uff1f\u5438\u8840\u9b3c\u5fc5\u987b\u9760\u5438\u8840\u6765\u7ef4\u6301\u751f\u547d\uff0c\u5426\u5219\u5c31\u4f1a\u9010\u6e10\u8870\u5f31\uff0c\u751a\u81f3\u201c\u771f\u6b63\u201d\u6b7b\u4ea1\u3002\u4e00\u822c\u7684\u5438\u8840\u9b3c\uff0c\u5927\u6982\u6bcf\u9694\u5341\u5929\u5de6\u53f3\u5c31\u9700\u8981\u4ece\u68fa\u6750\u91cc\u8dd1\u51fa\u6765\u627e\u4eba\u5438\u8840\uff0c\u8389\u6cd5\u513f\u6bd4\u8f83\u7279\u6b8a\uff0c\u4e24\u4e09\u4e2a\u6708\u624d\u5438\u4e00\u6b21\u3002</p><p>\u743c\u6069\u5e76\u4e0d\u5173\u5fc3\u8389\u6cd5\u513f\u7684\u5438\u8840\u9891\u7387\u95ee\u9898\uff0c\u4ed6\u5728\u610f\u7684\u662f\u8389\u6cd5\u513f\u8dd1\u53bb\u70db\u5821\u9001\u4ec0\u4e48\u4e66\u3002</p><p>\u201c\u54e6\uff0c\u4e3b\u8981\u662f\u4e00\u4e9b\u5173\u4e8e\u8010\u745f\u745e\u5c14\u7684\u5386\u53f2\u4e66\u7c4d\uff0c\u201d\u8389\u6cd5\u513f\u8bf4\uff0c\u201c\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\u8bb0\u5f55\u4e0b\u6765\u7684\u90a3\u4e9b\uff0c\u7ecf\u8fc7\u62c9\u6c83\u514b\u5148\u751f\u5ba1\u5b9a\uff0c\u88c5\u8ba2\u6210\u518c\uff0c\u7531\u6211\u9001\u5230\u70db\u5821\u53bb\u4fdd\u5b58\u4e00\u4efd\u3002\u201d</p><p>\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\uff1f</p><p>\u743c\u6069\u81ea\u7136\u6ca1\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u4f46\u4ed6\u7b2c\u4e00\u53cd\u5e94\u5c31\u60f3\u8d77\u4e86\u4eca\u5929\u5728\u56fe\u4e66\u5ba4\u91cc\u770b\u89c1\u7684\u90a3\u4e2a\u57cb\u5934\u4e66\u5199\u7684\u9ad8\u7b49\u5deb\u5996\u3002\u201c\u5c31\u662f\u4ed6\u4e48\uff1f\u201d\u4ed6\u95ee\u8389\u6cd5\u513f\u3002</p><p>\u201c\u539f\u6765\u4f60\u5df2\u7ecf\u89c1\u8fc7\u4ed6\u554a\uff0c\u201d\u8389\u6cd5\u513f\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u55ef\uff0c\u5c31\u662f\u4ed6\uff0c\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\u3002\u201d</p><p>\u201c\u4ed6\u662f\u4e2a\u9ad8\u7b49\u5deb\u5996\u5427\uff0c\u201d\u743c\u6069\u5fc3\u4e2d\u7591\u60d1\u7740\uff0c\u201c\u9ad8\u7b49\u5deb\u5996\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\u2026\u2026\u201d</p><p>\u4ed6\u6ca1\u6709\u660e\u8bf4\uff0c\u4f46\u76f8\u4fe1\u8389\u6cd5\u513f\u5e94\u8be5\u80fd\u61c2\u4ed6\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u518d\u4e00\u6b21\u7684\uff0c\u5438\u8840\u9b3c\u7684\u56de\u7b54\u8ba9\u4ed6\u5403\u60ca\u4e86\uff0c\u867d\u7136\u4eca\u5929\u4ed6\u9047\u4e0a\u7684\u610f\u5916\u5df2\u7ecf\u591f\u591a\u4e86\u3002</p><p>\u201c\u4ed6\u5f53\u7136\u5728\u8fd9\u91cc\u554a\uff0c\u8fd9\u91cc\u539f\u672c\u5c31\u662f\u4ed6\u7684\u6d6e\u7a7a\u57ce\u561b\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u201c\u8fd9\u91cc\u662f\u4ed6\u7684\u6d6e\u7a7a\u57ce\uff1f\u201d\u743c\u6069\u60ca\u8bb6\uff0c\u201c\u8fd9\u91cc\u96be\u9053\u4e0d\u662f\u62c9\u6c83\u514b\u5148\u751f\u7684\u6d6e\u7a7a\u57ce\u9057\u5740\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u4ece\u54ea\u91cc\u542c\u5230\u8fd9\u79cd\u8bf4\u6cd5\u7684\uff1f\u201d\u8389\u6cd5\u513f\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u968f\u5373\u60f3\u8d77\u4ec0\u4e48\uff0c\u683c\u683c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u662f\u4ece\u70db\u5821\u770b\u5230\u7684\u5427\uff1f\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u4ed6\u786e\u5b9e\u662f\u524d\u4e9b\u5929\u5728\u70db\u5821\u7ffb\u9605\u62c9\u6c83\u514b\u7684\u8d44\u6599\u65f6\uff0c\u770b\u5230\u91cc\u9762\u63d0\u53ca\uff0c\u8bf4\u62c9\u6c83\u514b\u5982\u4eca\u5c45\u4f4f\u7684\u5deb\u5e08\u4e4b\u5893\uff0c\u662f\u4ed6\u6614\u65e5\u7684\u6d6e\u7a7a\u57ce\u9057\u5740\uff0c\u5012\u4e5f\u6ca1\u600e\u4e48\u8be6\u7ec6\u53d9\u8bf4\uff0c\u53ea\u662f\u63d0\u4e86\u4e24\u53e5\u3002\u5982\u4eca\u542c\u8389\u6cd5\u513f\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4e0d\u662f\u8fd9\u4e48\u56de\u4e8b\uff1f</p><p>\u201c\u70db\u5821\u7684\u90a3\u4efd\u8d44\u6599\uff0c\u5e94\u8be5\u5c31\u662f\u6211\u9001\u8fc7\u53bb\u7684\u5427\uff0c\u201d\u8389\u6cd5\u513f\u8f7b\u7b11\u7740\uff0c\u201c\u62c9\u6c83\u514b\u5148\u751f\u81ea\u7136\u4e0d\u4f1a\u63d0\u4f9b\u5b8c\u5168\u771f\u5b9e\u7684\u7248\u672c\uff0c\u8fd9\u4e5f\u662f\u5728\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\u5462\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u91cc\u4e0d\u662f\u62c9\u6c83\u514b\u5148\u751f\u7684\u6d6e\u7a7a\u57ce\u9057\u5740\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u201d\u8389\u6cd5\u513f\u8bf4\uff0c\u201c\u62c9\u6c83\u514b\u5148\u751f\u7684\u6d6e\u7a7a\u57ce\u65e9\u5c31\u968f\u7740\u8010\u745f\u5e1d\u56fd\u7684\u9668\u706d\u800c\u5760\u6bc1\u4e86\uff0c\u9057\u8ff9\u5728\u7eb3\u83f2\u5c14\u5e73\u539f\u4e0a\u5462\u3002\u201d</p><p>\u8010\u745f\u5e1d\u56fd\u9668\u706d\u65f6\uff0c\u62c9\u6c83\u514b\u7684\u6d6e\u7a7a\u57ce\u6b63\u6f02\u6d6e\u5728\u5e1d\u56fd\u4e1c\u90e8\uff0c\u7eb3\u83f2\u5c14\u5e73\u539f\u7684\u9ad8\u7a7a\u4e4b\u4e0a\uff0c\u8001\u5deb\u5996\u89c2\u5bdf\u7740\u7eb3\u83f2\u5c14\u5e1d\u56fd\u548c\u6d1b\u739b\u8428\u5e1d\u56fd\u7684\u4ea4\u6218\u3002\u7eb3\u83f2\u5c14\u548c\u6d1b\u739b\u8428\u662f\u548c\u8010\u745f\u540c\u65f6\u4ee3\u7684\u4e24\u5927\u9b54\u6cd5\u5e1d\u56fd\uff0c\u4f4d\u4e8e\u8010\u745f\u4e1c\u8fb9\uff0c\u5176\u4e2d\u7eb3\u83f2\u5c14\u5e1d\u56fd\u4ee5\u201c\u7f1a\u9b54\u5e08\u201d\u8457\u79f0\uff0c\u64c5\u957f\u53ec\u5524\u3001\u63a7\u5236\u6076\u9b54\uff0c\u9a71\u9063\u5b83\u4eec\u4e0a\u9635\u4f5c\u6218\uff0c\u800c\u6d1b\u739b\u8428\u5e1d\u56fd\u5219\u76db\u4ea7\u5f3a\u5927\u7684\u5851\u80fd\u5deb\u5e08\u3002\u8fd9\u4e24\u5927\u9b54\u6cd5\u5e1d\u56fd\u4e43\u662f\u4e16\u4ec7\uff0c\u8fde\u5e74\u5f81\u6218\u4e0d\u4f11\u3002</p><p>\u62c9\u6c83\u514b\u6b63\u89c2\u5bdf\u7740\u4e0b\u65b9\u7684\u4ea4\u6218\uff0c\u7a81\u7136\u5bdf\u89c9\u5230\u8b66\u5146\uff0c\u9884\u8a00\u6cd5\u672f\u544a\u8bc9\u4ed6\u5371\u9669\u5373\u5c06\u6765\u4e34\uff0c\u6d6e\u7a7a\u57ce\u4f1a\u88ab\u6bc1\u706d\u3002\u7d27\u63a5\u7740\uff0c\u8ba9\u6574\u4e2a\u8010\u745f\u5e1d\u56fd\u6bc1\u4e8e\u4e00\u65e6\u7684\u6d69\u52ab\u9aa4\u7136\u53d1\u751f\uff0c\u6240\u6709\u7684\u9b54\u6cd5\u90fd\u7a81\u7136\u6682\u65f6\u5931\u53bb\u4e86\u4f5c\u7528\uff0c\u9760\u9b54\u6cd5\u7ef4\u6301\u7684\u6d6e\u7a7a\u57ce\u7eb7\u7eb7\u5760\u6bc1\uff0c\u57ce\u4e0a\u7684\u5deb\u5e08\u4eec\u4e5f\u65e0\u6cd5\u4f7f\u7528\u9b54\u6cd5\u9003\u751f\u2014\u2014\u4f46\u62c9\u6c83\u514b\u5f88\u5e78\u8fd0\uff0c\u4ed6\u7684\u6d6e\u7a7a\u57ce\u91cc\u6070\u597d\u517b\u4e86\u4e00\u6761\u9f99\uff0c\u9f99\u662f\u957f\u7fc5\u8180\u4f1a\u98de\u7684\u3002</p><p>\u62c9\u6c83\u514b\u5306\u5fd9\u722c\u4e0a\u9f99\u80cc\uff0c\u9003\u5f97\u6027\u547d\uff0c\u4ed6\u7684\u6d6e\u7a7a\u57ce\u5760\u6bc1\u5728\u7eb3\u83f2\u5c14\u5e73\u539f\uff0c\u5c1a\u672a\u88ab\u6454\u6bc1\u7684\u8d22\u5b9d\u548c\u9b54\u6cd5\u7269\u54c1\u843d\u5165\u7eb3\u83f2\u5c14\u5e1d\u56fd\u7684\u7f1a\u9b54\u5e08\u624b\u4e2d\u3002\u8001\u5deb\u5996\u5bf9\u6b64\u4f3c\u4e4e\u5e76\u4e0d\u5341\u5206\u5728\u610f\uff0c\u4e5f\u4e0d\u66fe\u524d\u53bb\u7d22\u8981\uff0c\u4ed6\u4e00\u8def\u6e38\u8361\u5230\u5de8\u9b54\u5c71\u8109\uff0c\u53d1\u73b0\u4e86\u4e00\u5ea7\u5760\u6bc1\u7684\u6d6e\u7a7a\u57ce\u5e9f\u589f\uff0c\u6216\u8bb8\u662f\u7a81\u7136\u5fc3\u8840\u6765\u6f6e\uff0c\u4fbf\u5728\u6b64\u5b9a\u5c45\u4e0b\u6765\uff0c\u5e76\u4e14\u5c06\u5b83\u6539\u9020\u6210\u73b0\u5728\u7684\u5deb\u5e08\u4e4b\u5893\u3002</p><p>\u7136\u540e\uff0c\u62c9\u6c83\u514b\u5728\u63a2\u7d22\u5e9f\u589f\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u4e86\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u7684\u539f\u4e3b\u4eba\uff0c\u4e5f\u5c31\u662f\u745e\u54c8\u683c\u62c9\u65af\u4e86\uff0c\u8fd9\u4f4d\u9ad8\u7b49\u5deb\u5996\u8fd0\u6c14\u4e0d\u9519\uff0c\u5e76\u6ca1\u6709\u6454\u6b7b\uff0c\u4f46\u4ed6\u7684\u9ab7\u9ac5\u8eab\u4f53\u5b8c\u5168\u7c89\u788e\u4e86\uff0c\u800c\u4e14\u88ab\u6df1\u6df1\u57cb\u5728\u5e9f\u589f\u4e0b\u9762\u3002\u8fd9\u672c\u6765\u5012\u4e5f\u6ca1\u4ec0\u4e48\u5173\u7cfb\uff0c\u5deb\u5996\u7684\u8eab\u4f53\u5c31\u7b97\u88ab\u6467\u6bc1\uff0c\u53ea\u8981\u547d\u5323\u4e0d\u574f\uff0c\u4fbf\u80fd\u7f13\u6162\u518d\u751f\uff0c\u53ea\u8981\u6062\u590d\u4e86\u8eab\u4f53\uff0c\u533a\u533a\u5e9f\u589f\u81ea\u7136\u4e5f\u6321\u4e0d\u4f4f\u5deb\u5996\u3002\u7136\u800c\uff0c\u5c31\u5728\u745e\u54c8\u683c\u62c9\u65af\u521a\u521a\u5b8c\u6210\u518d\u751f\u65f6\uff0c\u62c9\u6c83\u514b\u95ef\u4e86\u8fdb\u6765\u3002\u8fd9\u4e24\u4f4d\u5deb\u5996\u539f\u672c\u5c31\u662f\u4ec7\u654c\uff0c\u6b64\u65f6\u76f8\u89c1\uff0c\u66f4\u65e0\u4e8c\u8bdd\uff0c\u4e00\u573a\u6076\u6218\u4e4b\u4e0b\uff0c\u62c9\u6c83\u514b\u6280\u9ad8\u4e00\u7b79\uff0c\u6210\u529f\u5236\u4f4f\u4e86\u745e\u54c8\u683c\u62c9\u65af\uff0c\u8ba9\u4ed6\u81e3\u670d\u4e8e\u81ea\u5df1\uff0c\u6210\u4e3a\u5deb\u5e08\u4e4b\u5893\u7684\u56fe\u4e66\u7ba1\u7406\u5458\uff0c\u4e3b\u8981\u5de5\u4f5c\u5c31\u662f\u7f16\u64b0\u8010\u745f\u745e\u5c14\u53f2\uff0c\u8fd9\u4e2a\u5de5\u7a0b\u770b\u8d77\u6765\u5f88\u6d69\u5927\uff0c\u5230\u73b0\u5728\u5c45\u7136\u8fd8\u6ca1\u6709\u5b8c\u6210\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u743c\u6069\u70b9\u5934\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u62c9\u6c83\u514b\u5148\u751f\u662f\u9e20\u5360\u9e4a\u5de2\u4e86\uff0c\u4ed6\u81ea\u5df1\u7684\u6d6e\u7a7a\u57ce\u5462\uff0c\u96be\u9053\u5c31\u4e22\u5728\u90a3\u91cc\u4e0d\u7ba1\u4e86\u4e48\u3002\u201d</p><p>\u6d6e\u7a7a\u57ce\u5c31\u7b97\u5760\u6bc1\uff0c\u603b\u4e0d\u53ef\u80fd\u91cc\u9762\u6240\u6709\u7684\u4e1c\u897f\u4e5f\u90fd\u5b8c\u86cb\u4e86\uff0c\u91d1\u94f6\u8d22\u5b9d\u81ea\u7136\u65e0\u6240\u8c13\uff0c\u4f46\u62c9\u6c83\u514b\u8eab\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u6d6e\u7a7a\u57ce\u91cc\u80af\u5b9a\u6709\u5f88\u591a\u73cd\u8d35\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u600e\u4e48\u80fd\u653e\u4efb\u4e0d\u7ba1\uff0c\u5c82\u4e0d\u662f\u592a\u53ef\u60dc\u4e86\u4e48\u3002</p><p>\u8389\u6cd5\u513f\u6447\u5934\uff0c\u201c\u8fd9\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u6211\u6765\u8fd9\u91cc\u4e5f\u6ca1\u591a\u4e45\uff0c\u5f88\u591a\u4e8b\u60c5\u53ea\u662f\u542c\u8bf4\u7f62\u4e86\u3002\u4e0d\u8fc7\u62c9\u6c83\u514b\u5148\u751f\u7684\u6d6e\u7a7a\u57ce\u5f53\u65f6\u5760\u6bc1\u540e\uff0c\u91cc\u9762\u7684\u8d22\u5b9d\u90fd\u88ab\u7eb3\u83f2\u5c14\u7684\u7f1a\u9b54\u5e08\u5f97\u53bb\u3002\u540e\u6765\u7eb3\u83f2\u5c14\u5e1d\u56fd\u6bc1\u706d\uff0c\u90a3\u4e9b\u5b9d\u7269\u6d41\u4f20\u8f97\u8f6c\uff0c\u73b0\u5728\u542c\u8bf4\u57fa\u672c\u90fd\u6d41\u843d\u5230\u585e\u5c14\u7684\u7ea2\u888d\u5deb\u5e08\u624b\u91cc\u4e86\u3002\u201d</p><p>\u7ea2\u888d\u5deb\u5e08\u2026\u2026</p><p>\u743c\u6069\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\uff0c\u5728\u70db\u5821\u7684\u65f6\u5019\u5c31\u6ce8\u610f\u8fc7\uff0c\u8fd9\u5e2e\u5bb6\u4f19\u4f3c\u4e4e\u548c\u8010\u745f\u9887\u6709\u6e0a\u6e90\u7684\u6837\u5b50\uff0c\u5f88\u591a\u5730\u65b9\u975e\u5e38\u7684\u76f8\u50cf\u6216\u8005\u8fd1\u4f3c\u3002\u5f53\u7136\uff0c\u4ed6\u4eec\u90a3\u79cd\u5243\u5149\u5934\u7ee3\u523a\u9752\u7684\u7231\u597d\u80af\u5b9a\u662f\u72ec\u521b\uff0c\u8010\u745f\u7edd\u6ca1\u6709\u8fd9\u79cd\u4f20\u7edf\u548c\u98ce\u4fd7\uff0c\u5426\u5219\u743c\u6069\u5c31\u771f\u8981\u5934\u75bc\u4e86\uff0c\u4ed6\u53ef\u4e0d\u613f\u610f\u5f53\u548c\u5c1a\u3002</p><p>\u6b63\u80e1\u601d\u4e71\u60f3\u95f4\uff0c\u611f\u89c9\u8389\u6cd5\u513f\u7684\u624b\u6307\u8f7b\u8f7b\u62c2\u8fc7\u4ed6\u7684\u80f8\u53e3\uff0c\u201c\u4e0d\u8fc7\uff0c\u4f60\u5f88\u5e78\u8fd0\u5462\uff0c\u743c\u6069\uff0c\u201d\u5438\u8840\u9b3c\u8bf4\uff0c\u201c\u5f88\u5c11\u6709\u4eba\u80fd\u89c1\u5230\u4ed6\u7684\u3002\u6211\u542c\u8bf4\uff0c\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u4e60\u60ef\u54e6\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e60\u60ef\uff1f\u201d</p><p>\u201c\u6211\u542c\u8bf4\uff0c\u4ed6\u559c\u6b22\u5411\u6bcf\u4e2a\u521d\u6b21\u89c1\u9762\u7684\u5e74\u8f7b\u5deb\u5e08\u8d60\u9001\u4e00\u4efd\u793c\u7269\u5462\u3002\u201d</p><p>\u743c\u6069\u644a\u5f00\u624b\uff0c\u201c\u770b\u6765\u4ed6\u7684\u4e60\u60ef\u6539\u4e86\uff0c\u201d\u4ed6\u53f9\u606f\u7740\uff0c\u201c\u6211\u5c31\u4ec0\u4e48\u90fd\u6ca1\u5f97\u5230\uff0c\u4ed6\u7406\u90fd\u6ca1\u7406\u6211\u3002\u201d</p><p>\u8389\u6cd5\u513f\u7b11\u541f\u541f\u5730\u6447\u5934\uff0c\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u8fd8\u6ca1\u7761\u7740\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4ed6\u7684\u793c\u7269\uff0c\u53ea\u4f1a\u5728\u68a6\u91cc\u624d\u4f1a\u7ed9\u5462\u3002\u201d</p><p>\u743c\u6069\u6014\u4f4f\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u8389\u6cd5\u513f\u7b11\u800c\u4e0d\u7b54\uff0c\u518d\u95ee\u4f1a\u662f\u4ec0\u4e48\u793c\u7269\uff0c\u5979\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u743c\u6069\u4e5f\u4fbf\u4e0d\u518d\u591a\u95ee\uff0c\u53cd\u6b63\u770b\u60c5\u5f62\u4e5f\u4e0d\u662f\u4ec0\u4e48\u574f\u4e8b\uff0c\u65e2\u7136\u6709\u4eba\u613f\u610f\u9001\u793c\uff0c\u90a3\u4e5f\u6ca1\u6709\u4e0d\u63a5\u7684\u9053\u7406\uff0c\u9759\u89c2\u5176\u53d8\u5c31\u662f\u3002\u5012\u662f\u4f11\u606f\u8fd9\u4e48\u4e45\uff0c\u539f\u672c\u75b2\u8f6f\u7684\u5c0f\u5bb6\u4f19\u53c8\u518d\u6b21\u7cbe\u795e\u6296\u64de\u8d77\u6765\uff0c\u6602\u7136\u633a\u7acb\uff0c\u6740\u6c14\u817e\u817e\uff0c\u770b\u6837\u5b50\u60f3\u518d\u6218\u4e00\u573a\u3002\u53ea\u662f\u770b\u7740\u5e8a\u5355\u4e0a\u90a3\u4e00\u5927\u6ee9\u8840\u8ff9\uff0c\u743c\u6069\u53c8\u8e0c\u8e87\u8d77\u6765\uff0c\u65e0\u9650\u7834\u5904\u4f3c\u4e4e\u542c\u8d77\u6765\u5f88\u7f8e\u5999\uff0c\u505a\u591a\u4e86\u4e5f\u5c31\u90a3\u4e48\u56de\u4e8b\uff0c\u5012\u662f\u5438\u8840\u9b3c\u9700\u8981\u9760\u8840\u6db2\u7ef4\u6301\u751f\u547d\uff0c\u5931\u8840\u8fc7\u591a\u53ea\u6015\u4f1a\u6709\u635f\u4f24\u3002</p><p>\u8389\u6cd5\u513f\u4eff\u4f5b\u731c\u5230\u4e86\u4ed6\u7684\u5fc3\u601d\uff0c\u5979\u4f4e\u58f0\u7b11\u7740\uff0c\u4eb2\u543b\u7740\u743c\u6069\u7684\u80f8\u819b\uff0c\u6162\u6162\u4e0b\u79fb\uff0c\u6700\u7ec8\u8dea\u4f0f\u5728\u817f\u95f4\uff0c\u5f20\u5f00\u6a31\u5507\u5c06\u90a3\u6eda\u70eb\u7684\u5bb6\u4f19\u542b\u4e86\u8fdb\u53bb\u3002\u5979\u6ca1\u4ec0\u4e48\u7ecf\u9a8c\uff0c\u6240\u4ee5\u81ea\u7136\u8c08\u4e0d\u4e0a\u591a\u4e48\u9ad8\u660e\u7684\u6280\u5de7\uff0c\u5f88\u662f\u751f\u758f\uff0c\u4f46\u5979\u786e\u5b9e\u5f88\u806a\u660e\uff0c\u5728\u743c\u6069\u6307\u70b9\u4e0b\u8fdb\u6b65\u5f88\u5feb\uff0c\u800c\u4e14\u5438\u8840\u9b3c\u81ea\u6709\u5438\u8840\u9b3c\u7684\u4f18\u52bf\uff0c\u4e3a\u5e38\u4eba\u6240\u65e0\u6cd5\u4f01\u53ca\uff0c\u5c31\u662f\u5979\u4e0d\u9700\u8981\u547c\u5438\u3002</p><p>\u4e0d\u9700\u8981\u547c\u5438\uff0c\u6240\u4ee5\u5373\u4f7f\u54bd\u5589\u88ab\u5b8c\u5168\u88ab\u5de8\u7269\u4fb5\u5360\u3001\u5145\u585e\uff0c\u5373\u4f7f\u505c\u7559\u518d\u957f\u65f6\u95f4\uff0c\u4e5f\u4e0d\u4f1a\u56e0\u6b64\u800c\u7a92\u606f\u3002\u82e5\u662f\u6362\u6210\u6d3b\u4eba\u6765\u505a\uff0c\u8fd9\u9700\u8981\u6781\u5176\u9ad8\u660e\u7684\u6280\u5de7\uff0c\u743c\u6069\u5728\u535a\u5f97\u4e4b\u95e8\u7684\u65f6\u5019\uff0c\u66fe\u7ecf\u4f53\u9a8c\u8fc7\u707f\u91d1\u4e4b\u5bb6\u91cc\u7684\u5993\u5973\u7684\u6280\u672f\uff0c\u5373\u4fbf\u662f\u5979\u4eec\uff0c\u4e5f\u8fdc\u8fdc\u505a\u4e0d\u5230\u8fd9\u4e48\u51fa\u8272\u3002\u4f46\u5bf9\u4e8e\u4e00\u4e2a\u5438\u8840\u9b3c\u6765\u8bf4\uff0c\u8fd9\u5c31\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\u4e86\u3002</p><p>\u743c\u6069\u6ee1\u610f\u5730\u4eab\u53d7\u7740\u7f8e\u5999\u9500\u9b42\u7684\u6ecb\u5473\uff0c\u4e0d\u8fc7\u8fd9\u6bd5\u7adf\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\u3002</p><p>\u4ed6\u629a\u6478\u7740\u8389\u6cd5\u513f\u7684\u84dd\u8272\u5934\u53d1\uff0c\u611f\u53d7\u7740\u90a3\u4e1d\u4e1d\u67d4\u987a\uff0c\u624b\u7f13\u7f13\u4e0b\u79fb\uff0c\u629a\u8fc7\u9880\u957f\u79c0\u6c14\u7684\u8116\u9888\u548c\u5149\u6d01\u83b9\u7389\u7684\u88f8\u80cc\uff0c\u5728\u540e\u8170\u7684\u51f9\u9677\u5904\u7a0d\u7a0d\u505c\u7559\uff0c\u7136\u540e\u6500\u4e0a\u4e86\u633a\u7fd8\u7684\u81c0\u5cf0\u3002\u4ed6\u4f38\u51fa\u624b\u6307\uff0c\u5206\u5f00\u81c0\u74e3\uff0c\u6478\u7d22\u5230\u4e86\u90a3\u6735\u7cbe\u5de7\u7684\u83ca\u82b1\u3002</p><p>\u201c\u9664\u4e86\u5438\u8840\uff0c\u4f60\u4e0d\u9700\u8981\u996e\u98df\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u743c\u6069\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u95ee\u3002</p><p>\u8389\u6cd5\u513f\u7684\u5c0f\u5634\u88ab\u585e\u5f97\u6ee1\u6ee1\u7684\uff0c\u65e0\u6687\u56de\u7b54\uff0c\u4e0d\u8fc7\u743c\u6069\u539f\u672c\u4e5f\u4e0d\u9700\u8981\u56de\u7b54\uff0c\u7b54\u6848\u662f\u5f88\u660e\u663e\u7684\u3002\u201c\u65e2\u7136\u4e0d\u9700\u8981\u996e\u98df\uff0c\u90a3\u4e48\u8fd9\u91cc\u5e94\u8be5\u4f1a\u5f88\u5e72\u51c0\u5427\u2026\u2026\u6211\u60f3\uff0c\u4e0d\u9700\u8981\u518d\u5148\u505a\u6e05\u6d01\u5de5\u4f5c\u4e86\u2026\u2026\u201d</p></article>"}}}]);
