(window.webpackJsonp=window.webpackJsonp||[]).push([[137],{160:function(p){p.exports={chapterTitle:null,title:"\u7b2c137\u7ae0 \u6536\u8d2d\u58f0\u8fbe\u7535\u5b50",content:"<article><h2>\u7b2c137\u7ae0 \u6536\u8d2d\u58f0\u8fbe\u7535\u5b50</h2><p>\u3000\u3000\u5c0f\u9526\u6e56\u7554\u7684\u522b\u5885\u65e0\u7591\u662f\u6d77\u5dde\u5e02\u6700\u9002\u5408\u5c45\u4f4f\u7684\u5904\u6240\uff0c\u672c\u6765\u5c31\u662f\u5e02\u91cc\u7ed9\u5e02\u5e38\u59d4\u9886\u5bfc\u4eec\u51c6\u5907\u7684\uff0c\u6cbb\u5b89\u4e5f\u5b8c\u5168\u6ca1\u6709\u95ee\u9898\uff0c\u4f46\u4ec5\u662f\u8c22\u665a\u6674\u4e00\u4e2a\u4eba\u5165\u4f4f\uff0c\u672a\u514d\u8fc7\u4e8e\u7a7a\u65f7\u4e86\uff0c\u8fd8\u8981\u989d\u5916\u8058\u8bf7\u4fdd\u59c6\u3001\u53a8\u5a18\uff0c\u53f8\u673a\u5374\u662f\u4e00\u76f4\u66ff\u8c22\u665a\u6674\u5f00\u8f66\u7684\u90a3\u4f4d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u8c22\u665a\u6674\u63d0\u8d77\u8981\u4ee5\u9526\u6e56\u7684\u540d\u4e49\u5728\u6c99\u7530\u8857\u5e02\u4e0e\u4e39\u4e95\u5df7\u5b50\u9009\u62e9\u51e0\u680b\u503c\u5f97\u6295\u8d44\u7684\u6e05\u672b\u5927\u5b85\u4e70\u4e0b\u6765\uff0c\u7b49\u7740\u5e02\u91cc\u5bf9\u6c99\u7530\u65e7\u57ce\u533a\u6539\u9020\u4f7f\u5916\u754c\u73af\u5883\u5f7b\u5e95\u6539\u5584\u4e4b\u540e\uff0c\u8058\u8bf7\u8bbe\u8ba1\u5e08\u5bf9\u8fd9\u4e9b\u6e05\u672b\u5927\u5b85\u8fdb\u884c\u6539\u9020\uff0c\u4fdd\u7559\u6e05\u672b\u5efa\u7b51\u7684\u5916\u58f3\uff0c\u5185\u90e8\u5f53\u7136\u8981\u66f4\u5bcc\u6709\u73b0\u4ee3\u5316\u7684\u751f\u6d3b\u6c14\u606f\uff0c\u9002\u5408\u4eba\u5c45\u4f4f\u624d\u884c\u3002</p><p>\u3000\u3000\u5c31\u7b97\u5f20\u606a\u6ca1\u6709\u5fc3\u91cc\u7684\u6253\u7b97\u90fd\u4e00\u4e00\u89e3\u91ca\u51fa\u6765\uff0c\u8c22\u665a\u6674\u5bf9\u5f20\u606a\u63d0\u7684\u610f\u89c1\u4e5f\u4e0d\u4f1a\u8d28\u7591\uff0c\u9526\u6e56\u6574\u5408\u6574\u4e2a\u6d77\u5dde\u5e02\u9020\u7eb8\u4ea7\u4e1a\u6240\u52a8\u7528\u7684\u8d44\u91d1\u4e5f\u4e0d\u7f3a\u8fd9\u4e00\u4e24\u767e\u4e07\uff0c\u53ea\u9700\u8981\u4fdd\u7559\u4e00\u5b9a\u7684\u6d41\u52a8\u8d44\u91d1\u5c31\u53ef\u4ee5\u4e86\uff0c\u51b5\u4e14\u9526\u6e56\u62e5\u6709\u6d77\u6cf0\u8d38\u661320\uff05\u7684\u80a1\u4efd\uff0c\u53ef\u4ee5\u6e90\u6e90\u4e0d\u65ad\u7684\u62bd\u53d6\u4e00\u5b9a\u91cf\u7684\u8d44\u91d1\uff0c\u800c\u65b0\u5149\u9020\u7eb8\u5382\u6240\u4ea7\u751f\u7684\u5229\u6da6\u5219\u9700\u8981\u4e0d\u65ad\u6295\u5165\u5230\u6269\u5927\u751f\u4ea7\u4e4b\u4e2d\u53bb\u3002</p><p>\u3000\u3000\u53ea\u662f\u9526\u6e56\u76ee\u524d\u7684\u6846\u67b6\u8fd8\u6ca1\u6709\u5b8c\u5168\u7ad6\u7acb\u8d77\u6765\uff0c\u4e00\u65f6\u627e\u4e0d\u5230\u4e13\u95e8\u7684\u4eba\u53bb\u8d1f\u8d23\u8fd9\u4e8b\u3002\u5f20\u606a\u5bf9\u8fd9\u70b9\u5012\u4e0d\u62c5\u5fc3\uff0c\u8bb8\u9e3f\u4f2f\u8fd9\u6bb5\u65f6\u95f4\u4e00\u5934\u94bb\u8fdb\u6c99\u7530\u65e7\u57ce\u7684\u4fdd\u62a4\u7814\u7a76\u4e4b\u4e2d\uff0c\u7a0d\u5fae\u8d77\u773c\u4e00\u70b9\u7684\u5b85\u5b50\uff0c\u4ed6\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u53ea\u8981\u4e0d\u6d89\u53ca\u6587\u7269\u4fdd\u62a4\u5efa\u7b51\uff0c\u8bb8\u9e3f\u4f2f\u4f1a\u4e50\u610f\u63a8\u8350\u51e0\u680b\u6709\u4ef7\u503c\u7684\u6e05\u672b\u5927\u5b85\u7684\u3002\u81f3\u4e8e\u7269\u4e1a\u7684\u4ef7\u683c\uff0c\u5f20\u606a\u8fd8\u4e0d\u60f3\u65a4\u65a4\u8ba1\u8f83\uff0c\u4e5d\u56db\u5e74\u5546\u4e1a\u5730\u6237\u90fd\u6ca1\u6709\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u7092\u4f5c\uff0c\u4ef7\u683c\u672c\u5c31\u5904\u4e8e\u4f4e\u8c37\uff0c\u52a0\u4e0a\u5f20\u606a\u672c\u8eab\u5c31\u60f3\u62ac\u9ad8\u6c99\u7530\u90a3\u7247\u65e7\u5b85\u7684\u4ef7\u683c\uff0c\u9526\u6e56\u524d\u51e0\u5e74\u7684\u53d1\u5c55\u4e5f\u4e0d\u4f1a\u4f9d\u8d56\u4e8e\u5bf9\u7269\u4e1a\u7684\u6295\u8d44\uff0c\u4ef7\u683c\u9ad8\u70b9\u4f4e\u70b9\uff0c\u5bf9\u9526\u6e56\u90fd\u6ca1\u6709\u5b9e\u8d28\u6027\u7684\u5f71\u54cd\u3002\u6240\u4ee5\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u90fd\u4e0d\u9700\u8981\u4e13\u95e8\u7684\u4eba\u53bb\u8d1f\u8d23\uff0c\u5173\u952e\u8981\u9ad8\u8c03\uff0c\u8981\u80fd\u8ba9\u4e00\u4e9b\u4eba\u77e5\u9053\u9526\u6e56\u7684\u52a8\u4f5c\uff0c\u5f15\u5bfc\u4ed6\u4eec\u53bb\u731c\u6d4b\u9526\u6e56\u7684\u5fc3\u601d\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u79cd\u9f8c\u9f8a\u7684\u5fc3\u601d\uff0c\u5f20\u606a\u53ea\u8ddf\u8bb8\u9e3f\u4f2f\u5f7c\u6b64\u6653\u5f97\uff0c\u5176\u4ed6\u4eba\u65e0\u8bba\u662f\u8c22\u665a\u6674\uff0c\u8fd8\u662f\u4ed6\u5c0f\u53d4\u5f20\u77e5\u975e\u90fd\u4e0d\u6e05\u695a\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e8b\u5546\u8bae\u4e0b\u6765\uff0c\u4e5f\u4e0d\u7528\u706b\u6025\u706b\u71ce\u7684\u53bb\u529e\uff0c\u4ece\u4e07\u71d5\u7535\u5b50\u7cfb\u7edf\u516c\u53f8\u8df3\u69fd\u5230\u6d77\u5dde\u7684\u4e01\u69d0\u7b49\u4eba\u4ece\u5e74\u521d\u516d\u5c06\u9646\u7eed\u62b5\u8fbe\u6d77\u5dde\uff0c\u968f\u4e01\u69d0\u7b2c\u4e00\u6279\u5230\u6d77\u5dde\u7684\u8fd8\u6709\u4e00\u4e2a\u76f8\u5f53\u91cd\u8981\u7684\u4eba\u7269\uff0c\u90a3\u5c31\u662f\u5f53\u65f6\u4e07\u71d5\u516c\u53f8\u7684\u751f\u4ea7\u90e8\u526f\u90e8\u957f\u82cf\u6d25\u4e1c\uff0c\u82cf\u6d25\u4e1c\u4e5f\u662f\u641e\u6280\u672f\u51fa\u8eab\uff0c\u540e\u6765\u8c03\u53bb\u641e\u751f\u4ea7\u7ba1\u7406\uff0c\u5f20\u606a\u7ed9\u4ed6\u7684\u5f85\u9047\u662f\u4e0e\u4e01\u69d0\u770b\u9f50\u7684\u3002</p><p>\u3000\u3000\u9664\u4e86\u5141\u8bfa\u7684\u9ad8\u85aa\u5e76\u63d0\u524d\u652f\u4ed8\u4e4b\u5916\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e24\u4eba\u59bb\u5b50\u7684\u5de5\u4f5c\u5173\u7cfb\u5728\u4ed6\u4eec\u62b5\u8fbe\u6d77\u5dde\u4e4b\u524d\uff0c\u5c31\u5df2\u7ecf\u5206\u522b\u6b63\u5f0f\u8c03\u5165\u57ce\u5357\u533a\u6587\u6c47\u4e2d\u5b66\u3001\u57ce\u5357\u533a\u76ca\u5347\u9547\u4e2d\u5fc3\u5c0f\u5b66\u4e86\uff0c\u5176\u4ed6\u968f\u4e01\u69d0\u5230\u6d77\u5dde\u7684\u6280\u672f\u3001\u7ba1\u7406\u4eba\u5458\uff0c\u5f20\u606a\u90fd\u8ba9\u5218\u660e\u8f89\u8d1f\u8d23\u89e3\u51b3\u4ed6\u4eec\u7684\u5bb6\u5c5e\u8c03\u52a8\u95ee\u9898\u3002</p><p>\u3000\u3000\u5230\u4e5d\u4e94\u5e74\uff0c\u4e07\u71d5\u516c\u53f8\u63a8\u51fa\u7684VCD\u82af\u7247\u529f\u80fd\u5df2\u7ecf\u4e0d\u80fd\u7b97\u662f\u5f53\u65f6\u6700\u5148\u8fdb\u7684\u4e86\uff0c\u6570\u5b57\u5f0f\u89c6\u9891\u538b\u7f29\u89e3\u51b3\u65b9\u6848\u7684\u4e3b\u8981\u4f9b\u5e94\u5546C-Cube\u5df2\u7ecf\u7814\u5236\u51fa\u6bd4\u4e07\u71d5\u82af\u7247\u529f\u80fd\u66f4\u52a0\u5f3a\u5927\u7684\u82af\u7247\uff0c\u5f20\u606a\u4e0e\u4e01\u69d0\u3001\u5218\u660e\u8f89\u5e74\u524d\u5230\u9999\u6e2f\u65f6\uff0c\u5c31\u4e0eC-Cube\u516c\u53f8\u7684\u9999\u6e2f\u603b\u4ee3\u7406\u79d1\u6c47\u516c\u53f8\u53d6\u5f97\u8054\u7cfb\uff0c\u4ee5\u6bcf\u72471000\u6e2f\u5e01\u7684\u4ef7\u683c\u4e70\u56de\u5341\u7247\u82af\u7247\u7528\u4ee5\u7814\u7a76\u3002</p><p>\u3000\u3000\u4e01\u69d0\u4ece\u9999\u6e2f\u76f4\u63a5\u56de\u5408\u80a5\u65f6\uff0c\u5c06\u539a\u539a\u7684\u82f1\u6587\u8bf4\u660e\u4e66\u968f\u8eab\u5e26\u7740\uff0c\u6625\u8282\u671f\u95f4\u5c31\u4e0e\u82cf\u6d25\u4e1c\u95ed\u95e8\u5728\u5bb6\u9605\u8bfb\u8bf4\u660e\u4e66\uff0c\u5c1d\u8bd5\u7f16\u5199\u65b0\u7684\u8f6f\u4ef6\uff0c\u505a\u8bd5\u9a8c\u8fd8\u8981\u6d77\u5dde\u8fd9\u8fb9\u51c6\u5907\u6761\u4ef6\u3002</p><p>\u3000\u3000\u5f20\u606a\u9884\u60f3\u5e74\u4e2d\u5c31\u62ff\u51fa\u6837\u673a\uff0c\u5165\u51ac\u524d\u5c31\u4e0a\u751f\u4ea7\u7ebf\u63a8\u51fa\u7b2c\u4e00\u6279\u6837\u54c1\uff0c\u62e9\u5730\u65b0\u5efa\u751f\u4ea7\u7814\u53d1\u57fa\u5730\u90a3\u662f\u4e0d\u73b0\u5b9e\u7684\uff0c\u6700\u7b80\u6377\u7684\u65b9\u6cd5\u5c31\u4e86\u6536\u8d2d\u4e00\u5bb6\u7535\u5b50\u5382\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u6709\u5fc3\u60c5\u5ea6\u5e74\u5047\uff0c\u8fd8\u5728\u8bb8\u601d\u5728\u60e0\u5c71\u6345\u7834\u7a97\u6237\u7eb8\u6b63\u5f0f\u53d1\u5c55\u7537\u5973\u5173\u7cfb\uff0c\u5218\u660e\u8f89\u7684\u6625\u8282\u5c31\u8981\u51c4\u60e8\u5f97\u591a\uff0c\u5e74\u524d\u4ece\u9999\u6e2f\u56de\u5230\u6d77\u5dde\u7acb\u5373\u51b3\u5b9a\u4e0a\u9a6c\u5f71\u789f\u673a\u9879\u76ee\u4e4b\u524d\u7684\u4e00\u4e2a\u6708\u91cc\uff0c\u5f20\u606a\u5c31\u8c03\u7ed9\u4ed6\u4e24\u540d\u7b56\u5212\u90e8\u5458\u5de5\u3001\u4e00\u540d\u4f1a\u8ba1\u5e08\u8ba9\u4ed6\u8d1f\u8d23\u524d\u671f\u7684\u7b79\u5907\u5de5\u4f5c\uff0c\u53ea\u662f\u5728\u4ed6\u4eec\u4ece\u9999\u6e2f\u56de\u5230\u6d77\u5dde\u540e\u624d\u7b97\u6b63\u5f0f\u5bf9\u5185\u90e8\u5ba3\u5e03\u8fd9\u4e2a\u8ba1\u5212\u3002\u76ee\u6807\u65e9\u5c31\u9501\u5b9a\u57ce\u5357\u533a\u4e00\u5bb6\u540d\u4e3a\u58f0\u8fbe\u7535\u5b50\u7684\u751f\u4ea7\u5361\u5e26\u5f0f\u6536\u5f55\u673a\u7684\u7535\u5b50\u5382\uff0c\u8fd9\u5bb6\u7535\u5b50\u5382\u53bb\u5e74\u505a\u4e86\u4e00\u4ef6\u4e0d\u5149\u5f69\u7684\u4e8b\u60c5\uff0c\u5047\u5192\u71d5\u821e\u6536\u5f55\u673a\u7684\u54c1\u724c\u7ed9\u71d5\u821e\u6253\u5047\u6253\u4e0a\u95e8\u6765\u4e86\u3002</p><p>\u3000\u3000\u58f0\u8fbe\u7535\u5b50\u5382\u5c5e\u4e8e\u57ce\u5357\u533a\u76ca\u9686\u9547\u7684\u96c6\u4f53\u4f01\u4e1a\uff0c\u6548\u76ca\u4e00\u76f4\u4e0d\u597d\uff0c\u4e0d\u4ec5\u7ed9\u71d5\u821e\u626f\u5230\u5de5\u5546\u5c40\u6253\u5b98\u53f8\uff0c\u672c\u8eab\u5c31\u9677\u5165\u590d\u6742\u7684\u4e09\u89d2\u503a\u52a1\u4e2d\uff0c\u5e74\u7ec8\u804c\u5de5\u5de5\u8d44\u90fd\u53d1\u51fa\u6765\uff0c\u5382\u957f\u9648\u5f3a\u5e74\u4e09\u5341\u4e4b\u524d\u5c31\u53ea\u80fd\u5929\u5929\u5728\u5382\u5b50\u91cc\u8fc7\u591c\uff0c\u539f\u4ee5\u4e3a\u6b63\u6708\u91cc\u4f1a\u8f7b\u677e\u4e00\u70b9\uff0c\u54ea\u91cc\u60f3\u5230\u5218\u660e\u8f89\u8054\u7edc\u51e0\u4e2a\u58f0\u8fbe\u6b20\u503a\u52a1\u7684\u4f9b\u8d27\u5546\u5929\u5929\u5230\u4ed6\u5bb6\u9759\u5750\u8ba8\u503a\uff0c\u5218\u660e\u8f89\u5219\u8ddf\u76ca\u9686\u9547\u7684\u9547\u9886\u5bfc\u6b63\u5f0f\u8ba8\u8bba\u6536\u8d2d\u58f0\u8fbe\u7535\u5b50\u5382\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u7701\u57ce\u7684\u65f6\u5019\uff0c\u5218\u660e\u8f89\u4fbf\u5c06\u8fd9\u4e8b\u5927\u4f53\u8c08\u4e86\u4e0b\u6765\uff0c\u58f0\u8fbe\u7535\u5b50\u5382\u89c4\u6a21\u5927\u7ea6\u5c31\u4e09\u5341\u4ea9\u5de6\u53f3\uff0c\u4e5d\u56db\u5e74\u5de5\u4e1a\u7528\u5730\u7684\u4ef7\u683c\u538b\u5f97\u5f88\u4f4e\uff0c\u6709\u4e9b\u5730\u65b9\u4e5f\u4e86\u5438\u5f15\u6295\u5546\u5f15\u8d44\uff0c\u53d1\u5c55\u5730\u65b9\u7ecf\u6d4e\uff0c\u751a\u81f3\u514d\u8d39\u6279\u62e8\u5de5\u4e1a\u7528\u5730\uff0c\u76ca\u9686\u9547\u540c\u610f\u7531\u9547\u653f\u5e9c\u63a5\u624b\u58f0\u8fbe\u7535\u5b50\u5382\u7684\u4e09\u89d2\u503a\u52a1\uff0c\u6574\u4e2a\u7535\u5b50\u5382\u6574\u4f53\u51fa\u8ba9\uff0c\u4f5c\u4ef7\u4e00\u767e\u4e94\u5341\u4e07\u3002</p><p>\u3000\u3000\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e00\u884c\u56db\u4eba\u4ece\u5408\u80a5\u4e58\u706b\u8f66\u5728\u60e0\u5c71\u8f6c\u4e58\u957f\u9014\u5ba2\u8f66\u62b5\u8fbe\u6d77\u5dde\uff0c\u5f20\u606a\u4e0e\u5218\u660e\u8f89\u3001\u848b\u8587\u5728\u897f\u57ce\u996d\u5e97\u7ed9\u4ed6\u4eec\u63a5\u98ce\uff0c\u6253\u7b97\u5148\u5b89\u6392\u4ed6\u4eec\u5230\u4f4f\u5904\u4f11\u606f\u3002\u4e01\u69d0\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u4eec\u5728\u706b\u8f66\u4e0a\u7761\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u5728\u957f\u9014\u6c7d\u8f66\u4e5f\u65e0\u6240\u4e8b\u4e8b\uff0c\u54ea\u91cc\u8fd8\u6709\u5fc3\u601d\u518d\u4f11\u606f\uff1f\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u662f\u7b2c\u4e00\u6b21\u89c1\u5f20\u606a\uff0c\u4e4b\u524d\u5c31\u542c\u4e01\u69d0\u63cf\u8ff0\u8fc7\u5f20\u606a\u3002\u5bf9\u4e00\u4e2a\u672a\u66fe\u8c0b\u9762\u3001\u53ea\u542c\u65c1\u4eba\u4ecb\u7ecd\u7684\u5458\u5de5\u5f00\u51fa\u5f53\u65f6\u5f88\u7f55\u89c1\u7684\u9ad8\u85aa\u8d44\uff0c\u8fd9\u6837\u7684\u8001\u677f\u8981\u4e0d\u662f\u90a3\u79cd\u5934\u8111\u5bb9\u6613\u53d1\u70ed\u7684\u7c7b\u578b\uff0c\u5c31\u662f\u6c14\u9b44\u6781\u5927\u7684\u4eba\u7269\uff0c\u4f3c\u4e4e\u8fd9\u4e24\u79cd\u4eba\u5f88\u5206\u8fa8\u6e05\u695a\u3002\u8981\u4e0d\u662f\u5f20\u606a\u5df2\u7ecf\u5c06\u4e5d\u4e94\u5e74\u8fd9\u4e00\u5e74\u5341\u4e94\u4e07\u7684\u85aa\u6c34\u6253\u8fdb\u4ed6\u7684\u79c1\u4eba\u8d26\u6237\uff0c\u82cf\u6d25\u4e1c\u53ea\u6015\u4e0d\u4f1a\u8ddf\u4e01\u69d0\u8fc7\u6765\u51d1\u8fd9\u4e2a\u70ed\u95f9\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4e5d\u56db\u5e74\u5728\u4e07\u71d5\u7684\u5e74\u85aa\u662f\u56db\u4e07\uff0c\u5728\u5408\u80a5\u5df2\u7ecf\u7b97\u662f\u5f88\u4e0d\u9519\u7684\u5de5\u4f5c\uff0c\u5c31\u7b97\u4e3a\u4e86\u5f20\u606a\u5df2\u7ecf\u4ed8\u7ed9\u4ed6\u7684\u5341\u4e94\u4e07\uff0c\u5230\u6d77\u5dde\u6765\u6d6a\u8d39\u4e00\u5e74\u7684\u65f6\u95f4\u53c8\u4f55\u59a8\uff1f\u82cf\u6d25\u4e1c\u79bb\u5f00\u5408\u80a5\u65f6\u5c31\u662f\u8fd9\u4e48\u60f3\u7684\uff0c\u4ed6\u751a\u81f3\u4e0d\u60f3\u5c06\u59bb\u5b50\u7684\u5de5\u4f5c\u5306\u5fd9\u8c03\u5230\u6d77\u5dde\u53bb\uff0c\u4f46\u662f\u6d77\u5dde\u8fd9\u8fb9\u8fd9\u4e48\u70ed\u60c5\uff0c\u4ed6\u4e5f\u4e0d\u4fbf\u62d2\u7edd\u3002</p><p>\u3000\u3000\u62b5\u8fbe\u6d77\u5dde\u4e4b\u540e\u7684\u7b2c\u4e00\u9762\uff0c\u5f20\u606a\u7ed9\u82cf\u6d25\u4e1c\u7684\u5370\u8c61\u5f88\u4e0d\u9519\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u4e01\u69d0\u4e0d\u60f3\u6d6a\u8d39\u65f6\u95f4\u5230\u7ed9\u4ed6\u4eec\u5b89\u6392\u7684\u65b0\u4f4f\u5904\u5148\u770b\u4e00\u773c\uff0c\u4fbf\u8ba9\u5218\u660e\u8f89\u5b89\u6392\u8f66\u5b50\u76f4\u63a5\u5230\u76ca\u9686\u9547\u58f0\u8fbe\u7535\u5b50\u5382\u53bb\uff0c\u80fd\u4e0d\u80fd\u5b9a\u4e0b\u58f0\u8fbe\u7535\u5b50\u5382\uff0c\u5173\u952e\u8981\u542c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u7684\u610f\u89c1\u3002\u5f20\u606a\u966a\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u5750\u5965\u8fea\uff0c\u5218\u660e\u8f89\u3001\u848b\u8587\u966a\u5176\u4ed6\u4e24\u540d\u5de5\u7a0b\u5e08\u5750\u798f\u7279\uff0c\u51fa\u897f\u57ce\u996d\u5e97\u5c31\u76f4\u5954\u4e1c\u5357\u90ca\u3001\u8c61\u5c71\u5f80\u5357\u4e24\u516c\u91cc\u5904\u7684\u76ca\u9686\u9547\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e2a\u4eba\u5f88\u6ee1\u610f\u5c06\u5382\u5b50\u9009\u62e9\u5728\u76ca\u9686\u9547\uff0c\u76ca\u9686\u9547\u5317\u7247\u4e0e\u8c61\u5c71\u68ee\u6797\u4fdd\u62a4\u533a\u76f8\u8fde\uff0c\u4e5f\u6709\u5927\u7247\u4fdd\u62a4\u6797\u5730\uff0c\u6d77\u5dde\u5e02\u5883\u5185\u6700\u5927\u7684\u6c34\u7cfb\u5c0f\u6c5f\u6d41\u7ecf\u76ca\u9686\u9547\u7684\u897f\u5357\uff0c\u81ea\u7136\u98ce\u8c8c\u5b8c\u597d\uff0c\u4e1c\u5357\u7247\u7684\u5de5\u4e1a\u56ed\u533a\u4e0e\u5f00\u53d1\u533a\u76f8\u8fde\uff0c\u7528\u4e0d\u4e86\u591a\u4e45\uff0c\u76ca\u9686\u9547\u4e5f\u5c06\u5212\u5165\u5f00\u53d1\u533a\u3002\u4e0d\u8fc7\u5f20\u606a\u7684\u5fc3\u4e2d\uff0c\u76ca\u9686\u9547\u66f4\u9002\u5408\u53d1\u5c55\u4eba\u7fa4\u5c45\u4f4f\u7684\u65b0\u57ce\uff0c\u800c\u4e8b\u5b9e\u4e0a\uff0c\u6d77\u5dde\u5e02\u51e0\u5e74\u540e\u4e3a\u4e86\u7247\u9762\u8ffd\u6551\u7ecf\u6d4e\u589e\u957f\uff0c\u7adf\u7136\u5c06\u8fd9\u4e00\u7247\u597d\u5730\u89c4\u5212\u4e3a\u91cd\u6c61\u67d3\u7684\u5316\u5de5\u56ed\u533a\u3002</p><p>\u3000\u3000\u8fd9\u5927\u6982\u662f\u6d77\u5dde\u5e02\u653f\u5e9c\u5386\u5e74\u6765\u505a\u51fa\u7684\uff0c\u53ef\u4ee5\u4e0e\u5c06\u6c99\u7530\u5168\u90e8\u62c6\u9664\u91cd\u5efa\u5e76\u8bba\u7684\u6df7\u5e10\u4e8b\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u53ef\u4ee5\u7684\u8bdd\uff0c\u5f20\u606a\u5f53\u7136\u4f1a\u6781\u529b\u963b\u6b62\u8fd9\u6837\u7684\u4e8b\u60c5\u518d\u5ea6\u53d1\u751f\u3002</p><p>\u3000\u3000\u8f66\u5b50\u7ed5\u8fc7\u8c61\u5c71\u65f6\uff0c\u4e01\u69d0\u4e5f\u7981\u4e0d\u4f4f\u7b11\u7740\u8bf4\uff1a\u201c\u5382\u5b50\u80fd\u9009\u5728\u8fd9\u9644\u8fd1\uff0c\u6211\u5012\u6709\u767e\u5206\u4e94\u5341\u7684\u5fc3\u52a8\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u505a\u6280\u672f\u4e5f\u4e0d\u5168\u90fd\u662f\u975e\u5e38\u7406\u6027\u7684\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u58f0\u8fbe\u7535\u5b50\u5382\u8fd9\u8fb9\u63a5\u5230\u7535\u8bdd\u4e4b\u540e\uff0c\u9664\u4e86\u539f\u5382\u957f\u9648\u5f3a\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u540d\u526f\u9547\u957f\u5728\u5382\u5b50\u7b49\u5f20\u606a\u4ed6\u4eec\u8fc7\u6765\u3002\u516b\u4e03\u5e74\u6210\u7acb\u7684\u58f0\u8fbe\u7535\u5b50\u5382\uff0c\u5f00\u59cb\u4e24\u4e09\u5e74\u8fd8\u7ea2\u706b\u4e00\u6bb5\u65f6\u95f4\uff0c\u6536\u5f55\u673a\u6280\u672f\u867d\u7136\u5728\u7535\u5b50\u9886\u57df\u8fd9\u9897\u5927\u6811\u4e0a\u53ea\u7b97\u7b97\u5f88\u5c0f\u4e00\u7247\u53f6\u5b50\uff0c\u800c\u4e14\u5728\u5f71\u789f\u673a\u534e\u4e3d\u767b\u573a\u4e4b\u540e\u5f88\u5feb\u5c31\u4f1a\u67af\u9ec4\uff0c\u4e5f\u4e0d\u662f\u8bf4\u4e0d\u9700\u8981\u6280\u672f\u79ef\u7d2f\u3002\u6ca1\u6709\u6b63\u89c4\u9662\u6821\u57f9\u517b\u51fa\u6765\u7684\u6280\u672f\u4eba\u624d\uff0c\u8fde\u4e00\u540d\u5927\u5b66\u751f\u90fd\u6ca1\u6709\uff0c\u5c31\u9760\u7740\u51e0\u4e2a\u7535\u5b50\u4e13\u4e1a\u7684\u4e2d\u4e13\u751f\uff0c\u80fd\u5c06\u58f0\u8fbe\u7535\u5b50\u5382\u7ef4\u6301\u4e0b\u6765\uff0c\u9648\u5f3a\u4e5f\u4e0d\u5bb9\u6613\u3002</p><p>\u3000\u3000\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u5728\u58f0\u8fbe\u7535\u5b50\u5382\u4e09\u5341\u4ea9\u5730\u4ed4\u7ec6\u7684\u8f6c\u4e86\u597d\u51e0\u5708\uff0c\u9664\u4e86\u95ee\u9648\u5f3a\u4e00\u4e9b\u7ec6\u8282\u95ee\u9898\uff0c\u4e00\u76f4\u90fd\u4e0d\u8868\u6001\uff0c\u5f20\u606a\u66f4\u4e0d\u4f1a\u4e3b\u52a8\u4e71\u8bf4\u8bdd\uff0c\u76ca\u9686\u9547\u90a3\u540d\u526f\u9547\u957f\u591a\u5c11\u6709\u4e9b\u9886\u5bfc\u813e\u6c14\uff0c\u7f3a\u4e4f\u8010\u6027\uff0c\u4ed6\u90a3\u88ab\u9152\u8272\u6dd8\u7a7a\u7684\u8eab\u4f53\u53c8\u660e\u663e\u6bd4\u4e0d\u4e0a\u5f20\u606a\u4ed6\u4eec\u6709\u4f53\u529b\uff0c\u5728\u5382\u5b50\u8d70\u4e86\u4e24\u5708\uff0c\u8017\u53bb\u4e24\u4e09\u4e2a\u5c0f\u65f6\uff0c\u4ed6\u89c1\u5f20\u606a\u4ed6\u4eec\u8fd8\u6ca1\u6709\u610f\u5411\u6027\u7684\u8bf4\u6cd5\uff0c\u5c31\u7740\u607c\u7684\u8eb2\u5230\u529e\u516c\u5ba4\u6b47\u7740\u53bb\u4e86\u3002</p><p>\u3000\u3000\u4e00\u76f4\u5230\u5929\u9ed1\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u624d\u5411\u5f20\u606a\u8868\u660e\u4ed6\u4eec\u7684\u770b\u6cd5\uff1a\u201c\u8fd8\u884c\uff0c\u53ef\u4ee5\u5168\u76d8\u63a5\u53d7\u4e0b\u6765\uff0c\u7279\u522b\u662f\u7535\u5b50\u5382\u7684\u51e0\u540d\u6280\u672f\u5458\u8fd8\u80fd\u5e2e\u4e0a\u5fd9\u3002\u201d</p></article>"}}}]);
