(window.webpackJsonp=window.webpackJsonp||[]).push([[1374],{1397:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u597d\u6d88\u606f\u548c\u574f\u6d88\u606f",content:"<article><h2>\u7b2c\u4e09\u8282 \u597d\u6d88\u606f\u548c\u574f\u6d88\u606f</h2><p>\u4e00\u51fb\u843d\u96f7\u8f70\u4e0b\uff0c\u6467\u7834\u4e86\u5546\u961f\u539f\u672c\u7262\u56fa\u7684\u9632\u5fa1\uff0c\u8725\u8734\u4eba\u4eec\u58eb\u6c14\u5927\u632f\uff0c\u5927\u547c\u5c0f\u53eb\u7740\u8702\u62e5\u800c\u4e0a\u3002\u53cd\u89c2\u5546\u961f\u8fd9\u8fb9\uff0c\u5374\u4e2a\u4e2a\u9762\u82e5\u6b7b\u7070\uff0c\u5df2\u7ecf\u5168\u7136\u4e27\u5931\u6597\u5fd7\uff0c\u90a3\u4e2a\u80d6\u4e4e\u4e4e\u7684\u5546\u961f\u8001\u677f\u5df2\u7ecf\u762b\u8f6f\u5728\u5730\uff0c\u6323\u4e0d\u8d77\u8eab\u4e86\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u4ee5\u4eba\u6570\u800c\u8bba\uff0c\u8725\u8734\u4eba\u4e0d\u8fc7\u4e8c\u5341\u4f59\u4eba\uff0c\u5546\u961f\u8fd9\u8fb9\u4e5f\u6709\u4e03\u516b\u4e2a\u62a4\u536b\uff0c\u52a0\u4e0a\u4ec6\u5f79\u7b49\u4eba\uff0c\u4eba\u6570\u662f\u5c11\u4e9b\uff0c\u4f46\u6b66\u5668\u94e0\u7532\u88c5\u5907\u5374\u6bd4\u8725\u8734\u4eba\u7cbe\u826f\uff0c\u867d\u7136\u9632\u5fa1\u88ab\u51b2\u7834\uff0c\u5374\u672a\u5fc5\u5c31\u771f\u6ca1\u6709\u4e00\u6218\u4e4b\u529b\u3002\u53ea\u662f\u76ee\u7779\u4e86\u521a\u624d\u90a3\u4e00\u51fb\u843d\u96f7\u672f\u4e4b\u540e\uff0c\u5c31\u7b97\u5b8c\u5168\u4e0d\u901a\u6653\u9b54\u6cd5\u4e4b\u4eba\uff0c\u4e5f\u77e5\u9053\u654c\u4eba\u91cc\u9762\u6709\u4e00\u4f4d\u6781\u9ad8\u660e\u7684\u65bd\u6cd5\u8005\u3002\u5fc3\u80c6\u4e00\u5bd2\uff0c\u58eb\u6c14\u4fbf\u6cae\uff0c\u6ca1\u6709\u56db\u6563\u6e83\u9003\u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\u3002</p><p>\u8725\u8734\u4eba\u4ece\u7f3a\u53e3\u51b2\u8fdb\u6765\uff0c\u5546\u961f\u62a4\u536b\u4eec\u4e5f\u53ea\u5f97\u8fce\u4e0a\uff0c\u573a\u5185\u9677\u5165\u4e00\u56e2\u6df7\u6218\uff0c\u4f46\u5c40\u9762\u660e\u663e\u662f\u4e00\u8fb9\u5012\u3002\u8725\u8734\u4eba\u7684\u957f\u77db\u548c\u7bad\u77e2\u4e0a\u4f3c\u4e4e\u62b9\u4e86\u4ec0\u4e48\u6bd2\u836f\uff0c\u4e00\u65e6\u88ab\u523a\u4e2d\uff0c\u7acb\u523b\u5c31\u4f1a\u50cf\u559d\u9189\u4e86\u9152\u4f3c\u7684\u624b\u8db3\u8fdf\u6162\uff0c\u811a\u6b65\u6447\u6643\uff0c\u5f88\u5feb\u4e27\u5931\u6218\u6597\u529b\u3002\u6b64\u5730\u8ddd\u79bb\u9547\u4e0a\u8fd8\u6709\u6bb5\u8ddd\u79bb\uff0c\u867d\u7136\u5728\u4e00\u5f00\u59cb\u906d\u9047\u8725\u8734\u4eba\u7684\u65f6\u5019\uff0c\u5546\u961f\u5c31\u6d3e\u4e86\u4eba\u5feb\u9a6c\u62a5\u4fe1\uff0c\u4f46\u770b\u73b0\u5728\u7684\u60c5\u5f62\uff0c\u53ea\u6015\u652f\u6491\u4e0d\u5230\u63f4\u519b\u8d76\u6765\uff0c\u5c31\u8981\u88ab\u8725\u8734\u4eba\u6740\u5149\u4e86\u3002</p><p>\u5982\u679c\u4e0d\u51fa\u610f\u5916\u7684\u8bdd\u2026\u2026</p><p>\u6885\u83f2\u65af\u539f\u672c\u8ddd\u79bb\u8f83\u8fdc\uff0c\u770b\u5f97\u4e0d\u751a\u5206\u660e\uff0c\u5982\u4eca\u51b2\u5230\u8fd1\u524d\u4e9b\uff0c\u4e00\u773c\u626b\u8fc7\u53bb\uff0c\u987f\u65f6\u5c31\u53d1\u73b0\u4e86\u90a3\u4e2a\u91ca\u653e\u843d\u96f7\u672f\u7684\u5bb6\u4f19\u6240\u5728\u3002\u539f\u56e0\u65e0\u4ed6\uff0c\u56e0\u4e3a\u5b9e\u5728\u592a\u663e\u773c\u4e86\uff0c\u4e00\u7fa4\u8d64\u8eab\u88f8\u4f53\u7684\u8725\u8734\u4eba\u4e2d\uff0c\u552f\u72ec\u5c31\u4ed6\u4e00\u4e2a\u7a7f\u7740\u84dd\u767d\u8272\u7684\u957f\u888d\uff0c\u4e0a\u9762\u7ee3\u7740\u4e00\u9053\u9053\u7206\u88c2\u95ea\u7535\u4f3c\u7684\u8840\u7ea2\u8272\u6761\u7eb9\uff0c\u5de6\u773c\u4e0a\u8fd8\u6234\u7740\u4e2a\u602a\u6a21\u602a\u6837\u7684\u9ed1\u8272\u773c\u7f69\uff0c\u624b\u91cc\u62c4\u7740\u4e00\u6839\u9ed1\u8272\u7684\u957f\u6756\uff0c\u957f\u957f\u7684\u5c3e\u5df4\u4ece\u888d\u5b50\u4e0b\u9762\u62d6\u51fa\u6765\uff0c\u770b\u8d77\u6765\u5341\u5206\u6ed1\u7a3d\u3002\u4ed6\u8eab\u8fb9\u6709\u4e09\u4e2a\u5f3a\u58ee\u9ad8\u5927\u7684\u8725\u8734\u4eba\u62a4\u536b\uff0c\u7559\u5728\u540e\u65b9\uff0c\u5e76\u4e0d\u4e0a\u524d\u53c2\u4e0e\u4f5c\u6218\u3002</p><p>\u7136\u800c\u6885\u83f2\u65af\u7acb\u523b\u505c\u4f4f\u811a\u6b65\uff0c\u5fae\u5fae\u5438\u4e86\u53e3\u6c14\uff0c\u201c\u5854\u6d1b\u65af\uff1f\u201d</p><p>\u743c\u6069\u4ece\u540e\u9762\u8ddf\u4e0a\u6765\uff0c\u6b63\u542c\u89c1\u5979\u8bf4\u51fa\u8fd9\u4e2a\u540d\u5b57\uff0c\u201c\u5854\u6d1b\u65af\uff1f\u98ce\u66b4\u4e4b\u795e\uff1f\u201d</p><p>\u201c\u5b83\u662f\u5854\u6d1b\u65af\u7684\u7267\u5e08\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u6307\u7740\u90a3\u4e2a\u552f\u4e00\u7a7f\u888d\u5b50\u7684\u8725\u8734\u4eba\uff0c\u201c\u800c\u4e14\u4f4d\u9636\u7edd\u5bf9\u4e0d\u4f4e\uff0c\u5426\u5219\u6ca1\u8d44\u683c\u7a7f\u8fd9\u79cd\u670d\u8272\u3002\u201d</p><p>\u795e\u7947\u6559\u4f1a\uff0c\u867d\u7136\u7ec4\u7ec7\u7ed3\u6784\u5343\u5dee\u4e07\u522b\uff0c\u4f46\u5927\u591a\u90fd\u5f88\u6ce8\u91cd\u7b49\u7ea7\u4f4d\u9636\uff0c\u8fd9\u70b9\u5f80\u5f80\u4ece\u670d\u9970\u3001\u888d\u8272\u4e0a\u5c31\u80fd\u770b\u51fa\u4e00\u4e8c\u3002\u98ce\u66b4\u4e4b\u795e\u5854\u6d1b\u65af\u4e5f\u662f\u9ad8\u7b49\u795e\u4e4b\u4e00\uff0c\u5730\u4f4d\u548c\u63d0\u5c14\u3001\u73ed\u6069\u76f8\u5f53\uff0c\u6559\u4f1a\u52bf\u529b\u4e5f\u9887\u5e9e\u5927\uff0c\u800c\u4e14\u56e0\u4e3a\u662f\u90aa\u795e\uff0c\u548c\u63d0\u5c14\u6559\u4f1a\u4e43\u662f\u5bf9\u5934\u3002\u6885\u83f2\u65af\u4e5f\u66fe\u7ecf\u548c\u4e00\u4e9b\u5854\u6d1b\u65af\u6559\u4f1a\u7684\u7267\u5e08\u6253\u8fc7\u4ea4\u9053\uff0c\u4e00\u770b\u888d\u8272\uff0c\u4fbf\u77e5\u9053\u8fd9\u4e2a\u8725\u8734\u4eba\u4f4d\u9636\u4e0d\u4f4e\u3002</p><p>\u7267\u5e08\u7684\u529b\u91cf\u4e3b\u8981\u5728\u4e8e\u795e\u672f\uff0c\u800c\u795e\u672f\u662f\u8499\u795e\u7947\u8d50\u4e88\uff0c\u4f4d\u9636\u8d8a\u9ad8\u7684\u7267\u5e08\uff0c\u795e\u7947\u8d50\u4e0b\u7684\u795e\u672f\u81ea\u7136\u8d8a\u591a\u8d8a\u5f3a\u3002\u6240\u4ee5\u53ea\u8981\u77e5\u9053\u4e00\u4e2a\u7267\u5e08\u7684\u4f4d\u9636\u9ad8\u4f4e\uff0c\u5c31\u80fd\u77e5\u9053\u4ed6\u7684\u5927\u4f53\u5b9e\u529b\uff0c\u8fd9\u5012\u662f\u4ef6\u5f88\u65b9\u4fbf\u7684\u4e8b\u60c5\u3002</p><p>\u65e2\u7136\u77e5\u9053\u8fd9\u4e2a\u8725\u8734\u4eba\u662f\u5854\u6d1b\u65af\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u76f4\u63a5\u51b2\u4e0a\u53bb\u5c31\u662f\u4e0d\u667a\u4e4b\u4e3e\u3002\u5854\u6d1b\u65af\u662f\u98ce\u66b4\u4e4b\u795e\uff0c\u6700\u559c\u6b22\u8d50\u4e0b\u7684\u795e\u672f\u5c31\u662f\u5404\u79cd\u96f7\u9e23\u95ea\u7535\u9b54\u6cd5\uff0c\u6740\u4f24\u529b\u60ca\u4eba\uff0c\u50cf\u521a\u624d\u90a3\u843d\u96f7\u672f\u5c31\u662f\u4f8b\u5b50\u3002\u5982\u679c\u5c31\u8fd9\u4e48\u76f4\u63a5\u4e0a\u524d\uff0c\u5341\u6709\u516b\u4e5d\u8981\u88ab\u7535\u6210\u7126\u70ad\u3002</p><p>\u201c\u9690\u5f62\uff0c\u201d\u6885\u83f2\u65af\u6253\u91cf\u7740\u573a\u5185\u5c40\u52bf\uff0c\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u9632\u62a4\u95ea\u7535\u80fd\u91cf\u4f24\u5bb3\u3002\u201d</p><p>\u743c\u6069\u4f9d\u8a00\uff0c\u4e3a\u5979\u53e0\u52a0\u4e86\u4e24\u4e2a\u6cd5\u672f\uff0c\u6885\u83f2\u65af\u7684\u8eab\u5f62\u4ece\u7a7a\u6c14\u4e2d\u6d88\u5931\uff0c\u968f\u5373\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u8fdc\u53bb\u3002\u5979\u663e\u7136\u60f3\u53bb\u5077\u88ad\u523a\u6740\u6389\u90a3\u4e2a\u8725\u8734\u4eba\u7267\u5e08\uff0c\u5426\u5219\u6709\u8fd9\u6837\u7684\u5f3a\u529b\u65bd\u6cd5\u8005\u5728\uff0c\u5c40\u9762\u5c31\u5f88\u96be\u7ffb\u56de\u6765\u3002</p><p>\u743c\u6069\u6ca1\u6709\u8ddf\u4e0a\u524d\uff0c\u8fd9\u79cd\u4eba\u591a\u6df7\u6742\u7684\u5927\u573a\u9762\uff0c\u4ed6\u4e00\u4e2a\u5deb\u5e08\u51b2\u8fdb\u53bb\uff0c\u4e0d\u662f\u81ea\u5bfb\u6b7b\u8def\u4e48\u3002\u7ed9\u81ea\u5df1\u4e5f\u704c\u4e86\u74f6\u9690\u5f62\u836f\u6c34\uff0c\u4ed6\u6162\u6162\u9760\u8fd1\uff0c\u4fdd\u6301\u7740\u5b89\u5168\u7684\u8ddd\u79bb\uff0c\u9759\u89c2\u5176\u53d8\u3002</p><p>\u8725\u8734\u4eba\u7267\u5e08\u88ab\u4e09\u4e2a\u62a4\u536b\u4fdd\u62a4\u7740\uff0c\u89c2\u5bdf\u6218\u5c40\uff0c\u4ed6\u663e\u7136\u662f\u8fd9\u652f\u8725\u8734\u4eba\u5c0f\u961f\u7684\u9996\u9886\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u53bb\u6307\u6325\u90e8\u4e0b\uff0c\u56e0\u4e3a\u6307\u6325\u4e86\u4e5f\u6ca1\u7528\u3002\u8725\u8734\u4eba\u8fd9\u79cd\u751f\u7269\u7684\u8111\u888b\u91cc\uff0c\u4ece\u6765\u5c31\u6ca1\u6709\u7ec4\u7ec7\u548c\u7eaa\u5f8b\u7684\u6982\u5ff5\u3002\u597d\u5728\u5982\u4eca\u5c40\u9762\u5360\u4f18\uff0c\u4e5f\u4e0d\u7528\u62c5\u5fc3\uff0c\u518d\u6709\u4e94\u5206\u949f\uff0c\u5e94\u8be5\u5c31\u80fd\u6740\u5149\u8fd9\u6279\u4eba\u7c7b\uff0c\u62a2\u5b8c\u4e1c\u897f\u8d70\u4eba\u3002</p><p>\u8d5e\u7f8e\u98ce\u66b4\u4e4b\u795e\uff0c\u5b83\u5728\u5fc3\u4e2d\u7948\u7977\u7740\uff0c\u7136\u540e\u5b83\u7a81\u7136\u611f\u89c9\u5230\u4e00\u9635\u5bd2\u610f\u4f20\u6765\u3002</p><p>\u8725\u8734\u4eba\u7684\u667a\u5546\u662f\u4e0d\u9ad8\uff0c\u4f46\u5bf9\u5371\u9669\u7684\u672c\u80fd\u611f\u5e94\u5374\u6bd4\u4eba\u7c7b\u5f3a\u5f97\u591a\u4e86\uff0c\u5426\u5219\u4e5f\u4e0d\u53ef\u80fd\u5728\u4e1b\u6797\u6cbc\u6cfd\u91cc\u751f\u5b58\u4e0b\u53bb\u3002\u201c\u6709\u654c\u4eba\u9760\u8fd1\uff01\u201d\u51e0\u4e4e\u662f\u4e00\u77ac\u95f4\uff0c\u8725\u8734\u4eba\u7267\u5e08\u7684\u8111\u4e2d\u5c31\u95ea\u8fc7\u8fd9\u4e2a\u5224\u65ad\u3002</p><p>\u5b83\u970d\u7136\u8f6c\u8eab\uff0c\u4e3e\u8d77\u9ed1\u8272\u957f\u6756\u671d\u4e00\u4e2a\u65b9\u5411\u6307\u4e86\u6307\uff0c\u5ff5\u51fa\u4e00\u53e5\u5492\u8bed\u3002\u4e0d\u8fdc\u5904\u7a7a\u6c14\u5fae\u5fae\u4e00\u9635\u6ce2\u6298\uff0c\u968f\u5373\u73b0\u51fa\u4e00\u4e2a\u4eba\u7c7b\u5c11\u5973\u7684\u8eab\u5f71\u6765\uff0c\u91d1\u53d1\u94f6\u7532\uff0c\u624b\u63d0\u5927\u5251\uff0c\u6b63\u662f\u6885\u83f2\u65af\u3002</p><p>\u6ca1\u6599\u5230\u9690\u5f62\u5c45\u7136\u88ab\u8bc6\u7834\uff0c\u6885\u83f2\u65af\u4e5f\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\uff0c\u5979\u539f\u672c\u662f\u60f3\u6084\u6084\u9760\u8fd1\uff0c\u4e00\u4e3e\u683c\u6740\uff0c\u5982\u4eca\u5c1a\u672a\u8fd1\u8eab\u5c31\u5df2\u7ecf\u66b4\u9732\uff0c\u4e5f\u53ea\u80fd\u786c\u51b2\u4e86\u3002</p><p>\u4e09\u652f\u957f\u77db\u4ece\u4e0d\u540c\u7684\u65b9\u4f4d\u671d\u5979\u76f4\u523a\uff0c\u8725\u8734\u4eba\u7684\u4e09\u4e2a\u62a4\u536b\u5012\u90fd\u662f\u7cbe\u660e\u5f3a\u608d\u7684\u6218\u58eb\uff0c\u5239\u90a3\u95f4\u5c31\u53cd\u5e94\u8fc7\u6765\u3002\u6885\u83f2\u65af\u6325\u5251\u683c\u5f00\u4e24\u652f\u957f\u77db\uff0c\u7b2c\u4e09\u652f\u4ece\u5979\u7684\u8170\u4fa7\u64e6\u4e86\u8fc7\u53bb\uff0c\u501a\u4ed7\u7740\u94f6\u7532\u7684\u4fdd\u62a4\uff0c\u5979\u5e76\u6ca1\u6709\u53d7\u4f24\u3002\u8ddd\u79bb\u8fdb\u4e00\u6b65\u62c9\u8fd1\u4e86\uff0c\u5979\u8981\u5728\u7b2c\u4e00\u65f6\u95f4\u65a9\u6740\u6389\u7267\u5e08\uff0c\u4e00\u4e2a\u9ad8\u9636\u65bd\u6cd5\u8005\u592a\u5371\u9669\u4e86\u3002</p><p>\u4f46\u8fd9\u51e0\u4e2a\u8725\u8734\u4eba\u62a4\u536b\u5f88\u96be\u7f20\u3002</p><p>\u4ed6\u4eec\u7684\u6b66\u6280\u7b97\u4e0d\u4e0a\u591a\u4e48\u7cbe\u6e5b\uff0c\u4f46\u914d\u5408\u6070\u5230\u597d\u5904\uff0c\u663e\u7136\u4e45\u7ecf\u8bad\u7ec3\uff0c\u6240\u8868\u73b0\u51fa\u6765\u7684\u9ed8\u5951\u548c\u51b7\u9759\u5b8c\u5168\u4e0d\u50cf\u662f\u8725\u8734\u4eba\u3002\u6885\u83f2\u65af\u94f6\u5251\u731b\u5288\uff0c\u780d\u65ad\u4e86\u4e00\u6839\u957f\u77db\uff0c\u7d27\u63a5\u7740\u987a\u52bf\u524a\u6389\u4e86\u4e00\u53ea\u8725\u8734\u4eba\u7684\u8111\u888b\uff0c\u4f46\u5269\u4e0b\u4e24\u53ea\u8725\u8734\u4eba\u594b\u4e0d\u987e\u8eab\uff0c\u62fc\u6b7b\u963b\u62e6\uff0c\u4e24\u652f\u957f\u77db\u6a2a\u626b\u76f4\u523a\uff0c\u6b7b\u6b7b\u62b5\u4f4f\u4e86\u6885\u83f2\u65af\uff0c\u4e0d\u8ba9\u5979\u518d\u9760\u8fd1\u3002</p><p>\u4e00\u9053\u94f6\u8272\u95ea\u7535\u76f4\u5c04\u8fc7\u6765\uff0c\u51fb\u4e2d\u6885\u83f2\u65af\u7684\u80f8\u53e3\uff0c\u5e78\u597d\u9884\u5148\u6709\u51c6\u5907\uff0c\u95ea\u7535\u4f24\u5bb3\u88ab\u9632\u62a4\u9b54\u6cd5\u62b5\u6d88\u4e86\u5927\u534a\u3002\u867d\u7136\u5982\u6b64\uff0c\u6885\u83f2\u65af\u4f9d\u65e7\u611f\u89c9\u8eab\u4f53\u4e00\u9635\u8f7b\u5fae\u9ebb\u75f9\uff0c\u5979\u77e5\u9053\u4e0d\u80fd\u518d\u62d6\u5ef6\u4e0b\u53bb\uff0c\u5426\u5219\u5bf9\u65b9\u518d\u5c04\u51e0\u9053\u95ea\u7535\u6765\uff0c\u9632\u62a4\u9b54\u6cd5\u80af\u5b9a\u4f1a\u88ab\u51fb\u7834\uff0c\u4f46\u8fd9\u4e24\u4e2a\u8725\u8734\u4eba\u62fc\u6b7b\u963b\u62e6\uff0c\u4e00\u65f6\u5374\u4e5f\u7740\u5b9e\u51b2\u4e0d\u8fc7\u53bb\u3002</p><p>\u4e00\u9053\u8679\u5149\u4ece\u4fa7\u9762\u7838\u6765\uff0c\u91cd\u91cd\u5730\u649e\u5728\u4e00\u53ea\u8725\u8734\u4eba\u7684\u8138\u4e0a\uff0c\u6b63\u662f\u90a3\u9762\u6d3b\u5316\u9f99\u9cde\u76fe\u3002\u743c\u6069\u9690\u8eab\u8ddf\u5728\u540e\u9762\uff0c\u773c\u524d\u60c5\u52bf\u4e0d\u5999\uff0c\u4e00\u65f6\u4e5f\u6765\u4e0d\u53ca\u91ca\u653e\u9b54\u6cd5\uff0c\u7d22\u6027\u76f4\u63a5\u628a\u9f99\u9cde\u76fe\u7838\u4e86\u51fa\u6765\u3002\u90a3\u53ea\u5012\u9709\u7684\u8725\u8734\u4eba\u53ea\u89c9\u53cc\u773c\u4e00\u9ed1\uff0c\u8eab\u4f53\u672c\u80fd\u5f80\u540e\u4e00\u4ef0\uff0c\u8fd8\u6ca1\u56de\u8fc7\u795e\u6765\uff0c\u5c31\u89c9\u80f8\u53e3\u5267\u75db\u3002\u6885\u83f2\u65af\u6293\u4f4f\u673a\u4f1a\uff0c\u4e00\u5251\u523a\u7a7f\u4e86\u8725\u8734\u4eba\u7684\u8eab\u4f53\u3002</p><p>\u743c\u6069\u7838\u51fa\u9f99\u9cde\u76fe\uff0c\u968f\u5373\u8bf5\u5492\u65bd\u6cd5\uff0c\u5bf9\u5269\u4e0b\u4e00\u4e2a\u8725\u8734\u4eba\u7528\u4e86\u4e2a\u8fdf\u7f13\u672f\u3002\u6885\u83f2\u65af\u53cd\u624b\u4e00\u5251\u5c06\u5b83\u780d\u5012\uff0c\u76f4\u63a5\u4ece\u8eab\u65c1\u8d8a\u8fc7\uff0c\u6325\u5251\u5288\u5411\u6b63\u51c6\u5907\u518d\u6b21\u91ca\u653e\u95ea\u7535\u7684\u8725\u8734\u4eba\u7267\u5e08\u3002\u7267\u5e08\u4e0d\u5f97\u4e0d\u53d6\u6d88\u4e86\u5492\u8bed\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u9ed1\u8272\u957f\u6756\u4e00\u683c\uff0c\u67b6\u4f4f\u4e86\u94f6\u5251\u3002\u5b83\u7684\u529b\u6c14\u5012\u662f\u4e0d\u5c0f\uff0c\u624b\u4e2d\u7684\u957f\u6756\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u6750\u8d28\uff0c\u975e\u91d1\u975e\u6728\uff0c\u5c45\u7136\u6ca1\u6709\u88ab\u94f6\u5251\u5288\u65ad\u3002</p><p>\u6885\u83f2\u65af\u624b\u8155\u7ffb\u8f6c\uff0c\u94f6\u5251\u5212\u4e86\u534a\u4e2a\u5f27\u5708\uff0c\u81ea\u4e0b\u53c8\u659c\u6311\u4e0a\u6765\uff0c\u773c\u770b\u5c31\u8981\u5c06\u8fd9\u4e2a\u8725\u8734\u4eba\u5f00\u819b\u7834\u809a\u3002\u5c31\u5728\u8fd9\u547c\u5438\u6beb\u53d1\u4e4b\u95f4\uff0c\u8725\u8734\u4eba\u7267\u5e08\u4e00\u4e2a\u51cc\u7a7a\u5012\u7ffb\uff0c\u5c45\u7136\u8fdc\u8fdc\u7ffb\u51fa\u4e86\u51e0\u5341\u82f1\u5c3a\u4e4b\u5916\uff0c\u59ff\u52bf\u6f47\u6d12\u65e0\u6bd4\uff0c\u5982\u679c\u4e0d\u8ba1\u8f83\u90a3\u53ea\u62d6\u5728\u8eab\u540e\u6447\u66f3\u7684\u5927\u5c3e\u5df4\u7684\u8bdd\u3002</p><p>\u8fd9\u2026\u2026\u8fd9\u5bb6\u4f19\u96be\u9053\u7ec3\u8fc7\u8f7b\u529f\u4e48\uff0c\u8eab\u624b\u5c45\u7136\u8fd9\u4e48\u654f\u6377\u3002\u867d\u7136\u77e5\u9053\u8725\u8734\u4eba\u672c\u5c31\u662f\u540e\u80a2\u53d1\u8fbe\u64c5\u957f\u5f39\u8df3\u7684\u751f\u7269\uff0c\u7136\u800c\u8fd9\u4e5f\u672a\u514d\u592a\u53d8\u6001\u4e86\u70b9\u3002</p><p>\u8725\u8734\u4eba\u7267\u5e08\u8fdc\u8fdc\u9003\u5f00\uff0c\u8840\u7ea2\u7684\u773c\u73e0\u91cc\u900f\u7740\u51f6\u72e0\u7684\u51b7\u5149\uff0c\u76ef\u7740\u8fd9\u4e24\u4e2a\u4e0d\u901f\u4e4b\u5ba2\u3002\u4ed6\u987f\u4e86\u987f\u624b\u4e2d\u7684\u9ed1\u8272\u957f\u6756\uff0c\u543c\u53eb\u4e86\u4e00\u58f0\uff0c\u9762\u524d\u7684\u7a7a\u6c14\u4e2d\u7a81\u7136\u4e00\u9635\u96f7\u5149\u95ea\u8000\uff0c\u8df3\u51fa\u4e00\u4e2a\u94f6\u767d\u8272\u7684\u5de8\u4eba\u6765\uff0c\u5468\u8eab\u7535\u5149\u8426\u7ed5\uff0c\u567c\u556a\u4f5c\u54cd\uff0c\u770b\u8d77\u6765\u5371\u9669\u81f3\u6781\u3002</p><p>\u201c\u4e0a\uff01\u201d\u8725\u8734\u4eba\u671d\u6885\u83f2\u65af\u548c\u743c\u6069\u6307\u4e86\u6307\uff0c\u7528\u9f99\u8bed\u5927\u543c\u3002</p><p>\u6885\u83f2\u65af\u76b1\u7709\uff0c\u5979\u5012\u4e0d\u60e7\u6015\u8fd9\u4e2a\u88ab\u53ec\u5524\u51fa\u6765\u7684\u98ce\u66b4\u5de8\u4eba\uff0c\u4f46\u5374\u4e5f\u6ca1\u628a\u63e1\u80fd\u5feb\u901f\u89e3\u51b3\u6389\u3002\u90a3\u8fb9\u8725\u8734\u4eba\u4eec\u6b63\u5728\u653b\u51fb\u5546\u961f\uff0c\u65f6\u95f4\u7a0d\u7a0d\u62d6\u957f\uff0c\u5546\u961f\u5c31\u8981\u5168\u519b\u8986\u6ca1\u4e86\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u8fdc\u5904\u4f20\u6765\u5feb\u9a6c\u5954\u9a70\u58f0\uff0c\u4fa7\u773c\u77a5\u53bb\uff0c\u539f\u6765\u662f\u53e6\u5916\u4e00\u652f\u5546\u961f\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u89c1\u6b64\u60c5\u5f62\u4e0a\u6765\u52a9\u9635\u3002\u89c1\u6709\u63f4\u519b\u5230\u6765\uff0c\u53c8\u89c1\u91ca\u653e\u843d\u96f7\u672f\u7684\u8725\u8734\u4eba\u7267\u5e08\u88ab\u4eba\u7f20\u4f4f\uff0c\u539f\u672c\u88ab\u8725\u8734\u4eba\u6740\u5f97\u4e03\u96f6\u516b\u843d\u7684\u5546\u961f\u62a4\u536b\u4eec\u58eb\u6c14\u590d\u632f\uff0c\u518d\u5ea6\u7a33\u4f4f\u4e86\u9635\u811a\u3002</p><p>\u8725\u8734\u4eba\u7267\u5e08\u4e5f\u6ce8\u610f\u5230\u4e86\u8fd9\u60c5\u5f62\uff0c\u5b83\u5f53\u673a\u7acb\u65ad\uff0c\u53e3\u4e2d\u53d1\u51fa\u4e00\u58f0\u5c16\u9510\u7684\u547c\u54e8\u3002\u6240\u6709\u7684\u8725\u8734\u4eba\u4e00\u542c\u547c\u54e8\u58f0\uff0c\u987f\u65f6\u4e00\u7a9d\u8702\u5168\u64a4\u9000\u4e86\u56de\u6765\u3002\u7267\u5e08\u671d\u6885\u83f2\u65af\u77aa\u4e86\u773c\uff0c\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u98ce\u66b4\u5de8\u4eba\u65ad\u540e\uff0c\u7387\u9886\u7740\u4e00\u7fa4\u8725\u8734\u4eba\u8fde\u8e66\u5e26\u8df3\u94bb\u8fdb\u65c1\u8fb9\u7684\u6811\u6797\u4e2d\u53bb\u4e86\uff0c\u77ac\u95f4\u5c31\u6d88\u5931\u4e86\u8e2a\u8ff9\u3002</p><p>\u5546\u961f\u6b7b\u91cc\u9003\u751f\uff0c\u81ea\u7136\u4e5f\u4e0d\u6562\u8ffd\u8d76\uff0c\u6885\u83f2\u65af\u66f4\u6ca1\u5174\u8da3\u5355\u67aa\u5339\u9a6c\u53bb\u8ffd\u51fb\u3002\u4e00\u756a\u5fd9\u4e71\u4e4b\u540e\uff0c\u5315\u9996\u6ee9\u9547\u4e0a\u7684\u6c11\u5175\u4e5f\u8d76\u5230\u4e86\uff0c\u62a4\u9001\u7740\u5546\u961f\u524d\u5f80\u9547\u4e0a\u4fee\u6574\uff0c\u6885\u83f2\u65af\u548c\u743c\u6069\u4e5f\u968f\u540c\u524d\u5f80\u3002</p><p>\u201c\u8725\u8734\u4eba\u91cc\u600e\u4e48\u4f1a\u6709\u5854\u6d1b\u65af\u7684\u7267\u5e08\uff1f\u201d\u5728\u8def\u4e0a\uff0c\u6885\u83f2\u65af\u95ee\u6c11\u5175\u961f\u957f\u3002</p><p>\u8725\u8734\u4eba\u8111\u7b4b\u7b28\u62d9\uff0c\u5929\u6027\u6df7\u4e71\u65e0\u5e8f\uff0c\u6d51\u6d51\u5669\u5669\uff0c\u867d\u7136\u52c9\u5f3a\u4e5f\u80fd\u7b97\u662f\u667a\u6167\u751f\u547d\uff0c\u5176\u5b9e\u4e0e\u91ce\u517d\u5dee\u4e0d\u591a\uff0c\u201c\u6740\u201d\u548c\u201c\u5403\u201d\uff0c\u8fd9\u5c31\u662f\u4ed6\u4eec\u751f\u547d\u7684\u5168\u90e8\u610f\u4e49\u6240\u5728\u3002\u5854\u6d1b\u65af\u867d\u7136\u662f\u90aa\u795e\uff0c\u540d\u58f0\u6076\u52a3\uff0c\u4f46\u597d\u6b79\u4e5f\u662f\u9ad8\u7b49\u795e\u4e4b\u4e00\uff0c\u600e\u4e48\u4f1a\u9009\u8fd9\u6837\u7684\u5bb6\u4f19\u5f53\u7267\u5e08\uff0c\u4e5f\u4e0d\u6015\u81ea\u7838\u62db\u724c\u4e48\u3002</p><p>\u6c11\u5175\u961f\u957f\u76b1\u7740\u7709\uff0c\u636e\u4ed6\u8bf4\u8fd9\u662f\u8fd1\u671f\u7a81\u7136\u53d1\u751f\u7684\u53d8\u5316\u3002\u4ee5\u524d\u8725\u8734\u4eba\u867d\u7136\u7ecf\u5e38\u88ad\u51fb\u5315\u9996\u6ee9\uff0c\u4f46\u90fd\u662f\u5c0f\u6253\u5c0f\u95f9\uff0c\u4e0d\u6210\u6c14\u5019\uff1b\u4f46\u6700\u8fd1\u4e00\u4e24\u4e2a\u6708\u6765\uff0c\u8725\u8734\u4eba\u4eff\u4f5b\u53d8\u5f97\u7316\u72c2\u4e86\u5f88\u591a\uff0c\u9891\u7e41\u51fa\u51fb\uff0c\u56db\u5904\u52ab\u63a0\uff0c\u6c14\u7130\u5341\u5206\u56a3\u5f20\u3002</p><p>\u201c\u867d\u8bf4\u590f\u5b63\u662f\u8725\u8734\u4eba\u6d3b\u52a8\u9891\u7e41\u7684\u65f6\u5019\uff0c\u201d\u6c11\u5175\u961f\u957f\u82e6\u7740\u8138\uff0c\u201c\u4f46\u8fd9\u4e5f\u672a\u514d\u592a\u53cd\u5e38\u4e86\u70b9\u3002\u201d</p><p>\u4e0d\u4f46\u5982\u6b64\uff0c\u800c\u4e14\u8725\u8734\u4eba\u4e2d\uff0c\u7a81\u7136\u5192\u51fa\u4e86\u4e00\u4e9b\u9887\u4e3a\u5389\u5bb3\u7684\u5854\u6d1b\u65af\u7267\u5e08\u3002\u539f\u672c\u53ea\u4f1a\u4f7f\u7528\u957f\u77db\u548c\u5f13\u7bad\u7684\u8725\u8734\u4eba\uff0c\u4e00\u65e6\u6709\u4e86\u65bd\u6cd5\u8005\u52a0\u5165\uff0c\u5371\u9669\u7a0b\u5ea6\u987f\u65f6\u5267\u589e\u3002\u5315\u9996\u6ee9\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u9547\uff0c\u5e76\u975e\u4ec0\u4e48\u5927\u57ce\u5e02\uff0c\u6c11\u5175\u4e2d\u8fde\u4e2a\u5deb\u5e08\u90fd\u6ca1\u6709\uff0c\u9762\u5bf9\u8fd9\u7fa4\u5347\u7ea7\u7248\u7684\u8725\u8734\u4eba\uff0c\u5e94\u4ed8\u8d77\u6765\u5c31\u672a\u514d\u8270\u96be\u3002</p><p>\u201c\u6211\u4eec\u5df2\u7ecf\u5411\u6df1\u6c34\u57ce\u8bf7\u6c42\u652f\u63f4\uff0c\u201d\u6c11\u5175\u961f\u957f\u4ecb\u7ecd\u8bf4\uff0c\u56e0\u4e3a\u6885\u83f2\u65af\u4eae\u51fa\u4e86\u63d0\u5c14\u5723\u6b66\u58eb\u7684\u8eab\u4efd\uff0c\u6240\u4ee5\u4ed6\u683c\u5916\u5ba2\u6c14\uff0c\u201c\u4f46\u56e0\u4e3a\u6c99\u6f20\u91cc\u90a3\u5e2e\u9634\u9b42\u4e00\u76f4\u5728\u6298\u817e\uff0c\u4f60\u77e5\u9053\uff0c\u6211\u4eec\u6b63\u5728\u548c\u4ed6\u4eec\u6253\u6218\uff0c\u542c\u8bf4\u51ef\u5c14\u672c\u57ce\u4e3b\u90fd\u4eb2\u4e34\u524d\u7ebf\u4e86\u3002\u6240\u4ee5\u6df1\u6c34\u57ce\u53ea\u6015\u4e00\u65f6\u4e5f\u6ca1\u529e\u6cd5\u62bd\u8c03\u5175\u529b\u8fc7\u6765\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u540e\u8005\u6b63\u82e5\u65e0\u5176\u4e8b\u5730\u4f4e\u5934\u770b\u4e66\u3002</p><p>\u4e00\u756a\u6298\u817e\u4e4b\uff0c\u5927\u7ea6\u8fc7\u4e86\u534a\u4e2a\u5c0f\u65f6\uff0c\u4f17\u4eba\u5230\u8fbe\u9547\u4e0a\uff0c\u5404\u81ea\u53bb\u4f11\u606f\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u627e\u4e86\u5bb6\u9152\u5e97\u4f4f\u4e0b\uff0c\u5f00\u59cb\u7814\u7a76\u4e0b\u4e00\u6b65\u884c\u52a8\u3002\u6309\u7167\u9884\u5b9a\u8ba1\u5212\uff0c\u5e94\u8be5\u5148\u53bb\u6253\u63a2\u5173\u4e8e\u9ed1\u8def\u7684\u6d88\u606f\uff0c\u51b3\u5b9a\u5230\u5e95\u8d70\u54ea\u6761\u8def\u7ebf\uff0c\u53e6\u5916\u6885\u83f2\u65af\u8fd8\u5f97\u53bb\u9547\u4e0a\u7684\u63d0\u5c14\u795e\u6bbf\u4e00\u8d9f\uff0c\u628a\u7ed9\u5927\u4e3b\u6559\u7684\u4fe1\u4f20\u8baf\u8fc7\u53bb\u3002</p><p>\u4e8e\u662f\u5175\u5206\u4e24\u8def\uff0c\u6885\u83f2\u65af\u53bb\u63d0\u5c14\u795e\u6bbf\uff0c\u743c\u6069\u53bb\u9547\u4e0a\u6253\u63a2\u9ed1\u8def\u7684\u6d88\u606f\u3002\u5927\u7ea6\u5728\u9152\u9986\u91cc\u6ce1\u4e86\u4e00\u5c0f\u65f6\uff0c\u4ed8\u4e86\u4e09\u4e2a\u94f6\u5e01\uff0c\u743c\u6069\u5f97\u5230\u4e86\u4ed6\u60f3\u8981\u7684\u7b54\u6848\uff0c\u5174\u51b2\u51b2\u5730\u56de\u5230\u4e86\u9152\u5e97\uff0c\u63a8\u95e8\u8fdb\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u5df2\u7ecf\u5750\u5728\u91cc\u9762\uff0c\u5979\u5148\u56de\u6765\u4e86\u3002</p><p>\u201c\u597d\u6d88\u606f\uff0c\u201d\u743c\u6069\u9ad8\u5174\u5730\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ece\u4e1c\u8fb9\u4f20\u6765\u7684\u6d88\u606f\uff0c\u6563\u5854\u6797\u4f1a\u5728\u6628\u5929\u5df2\u7ecf\u53d6\u6d88\u4e86\u5bf9\u9ed1\u8def\u7684\u5c01\u9501\uff0c\u73b0\u5728\u53ef\u4ee5\u6b63\u5e38\u901a\u884c\u4e86\u3002\u201d</p><p>\u542c\u5230\u8fd9\u4e2a\u671f\u671b\u7684\u6d88\u606f\uff0c\u6885\u83f2\u65af\u5374\u4f3c\u4e4e\u5e76\u6ca1\u6709\u4ec0\u4e48\u559c\u60a6\u795e\u8272\uff0c\u53ea\u662f\u6de1\u6de1\u5730\u55ef\u4e86\u4e00\u58f0\u3002\u867d\u8bf4\u5979\u5e73\u65f6\u4e5f\u5c31\u6de1\u6de1\u6f20\u6f20\u7684\uff0c\u4f46\u73b0\u5728\u8fd9\u60c5\u5f62\u660e\u663e\u662f\u4e0d\u5bf9\u52b2\uff0c\u743c\u6069\u6014\u4e86\u6014\uff0c\u8fd9\u624d\u6ce8\u610f\u5230\u81ea\u4ece\u81ea\u5df1\u8fdb\u623f\u95f4\u4ee5\u6765\uff0c\u6885\u83f2\u65af\u4f3c\u4e4e\u5c31\u6ca1\u52a8\u5f39\u8fc7\uff0c\u4f4e\u7740\u5934\uff0c\u773c\u775b\u770b\u7740\u81ea\u5df1\u7684\u53cc\u624b\uff0c\u8138\u4e0a\u795e\u60c5\u4f3c\u4e4e\u6709\u4e9b\u9eef\u7136\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u60ca\u8bb6\uff0c\u4ed6\u548c\u5c11\u5973\u8ba4\u8bc6\u8fd9\u4e48\u4e45\uff0c\u8fd8\u4ece\u6ca1\u89c1\u5979\u8fd9\u5e45\u6a21\u6837\u8fc7\u3002\u5728\u4ed6\u7684\u5370\u8c61\u91cc\uff0c\u6885\u83f2\u65af\u4f3c\u4e4e\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\uff0c\u6c38\u8fdc\u90fd\u662f\u90a3\u4e48\u82f1\u6c14\u51db\u7136\u65e0\u6240\u754f\u60e7\u2026\u2026\u9664\u4e86\u5076\u5c14\u4eb2\u6635\u7684\u65f6\u5019\uff0c\u4f1a\u50cf\u4e2a\u7f9e\u6da9\u7684\u5c0f\u5973\u5b69\u5b50\u3002</p><p>\u6c89\u9ed8\u4e86\u534a\u5929\uff0c\u6885\u83f2\u65af\u7ec8\u4e8e\u7f13\u7f13\u5f00\u53e3\u4e86\u3002</p><p>\u201c\u6211\u521a\u624d\u53bb\u795e\u6bbf\uff0c\u8bf7\u4ed6\u4eec\u5e2e\u5fd9\u4f20\u8baf\u5230\u8ff7\u65af\u5353\u8bfa\uff0c\u201d\u5979\u8bf4\uff0c\u773c\u5149\u4f9d\u65e7\u505c\u7559\u5728\u81ea\u5df1\u7684\u53cc\u624b\u4e0a\uff0c\u201c\u4fe1\u521a\u4f20\u8fc7\u53bb\uff0c\u5c31\u6536\u5230\u4e00\u4efd\u6555\u4ee4\u2026\u2026\u9488\u5bf9\u6211\u7684\u6555\u4ee4\u3002\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4ed6\u867d\u7136\u4e0d\u719f\u6089\u63d0\u5c14\u6559\u4f1a\uff0c\u4e5f\u77e5\u9053\u6555\u4ee4\u8fd9\u4e2a\u8bcd\u662f\u4ec0\u4e48\u610f\u601d\u3002\u63d0\u5c14\u6559\u4f1a\u6ca1\u6709\u4e00\u4e2a\u201c\u6559\u7687\u201c\u7c7b\u578b\u7684\u6700\u9ad8\u9886\u8896\uff0c\u800c\u662f\u5212\u5206\u5404\u4e2a\u6559\u533a\uff0c\u5206\u8bbe\u5404\u5927\u4e3b\u6559\uff0c\u7531\u795e\u7947\u76f4\u63a5\u9886\u5bfc\u3002\u5927\u4e3b\u6559\u4e00\u7ea7\u7684\u4eba\u7269\uff0c\u6709\u6743\u529b\u53d1\u5e03\u6555\u4ee4\uff0c\u5f80\u5f80\u662f\u9488\u5bf9\u91cd\u5927\u4e8b\u52a1\u4f5c\u51fa\u88c1\u51b3\u548c\u6307\u793a\uff0c\u5728\u672c\u6559\u533a\u4e4b\u5185\u6709\u7b49\u540c\u4e8e\u201c\u6559\u4f1a\u6cd5\u5178\u201d\u7684\u6548\u529b\uff0c\u51e1\u662f\u6559\u5f92\u4e00\u6982\u9075\u884c\u3002\u5982\u4eca\u6885\u83f2\u65af\u8bf4\u5927\u4e3b\u6559\u9488\u5bf9\u5979\u53d1\u4e86\u4e00\u4efd\u6555\u4ee4\u2026\u2026</p><p>\u201c\u6555\u4ee4\u4e0a\u8bf4\uff0c\u6211\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\u4e0d\u529b\uff0c\u81f4\u4f7f\u5723\u7269\u6bc1\u635f\uff1b\u64c5\u6740\u535a\u5f97\u4e4b\u95e8\u7684\u8389\u62c9\u5927\u516c\u7235\uff0c\u5df2\u7ecf\u8fdd\u80cc\u4e86\u6559\u4f1a\u7684\u5f8b\u6cd5\u548c\u5723\u6b66\u58eb\u7684\u51c6\u5219\u3002\u6240\u4ee5\uff0c\u5927\u4e3b\u6559\u5ba3\u5e03\u2026\u2026\u201d\u5979\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u201c\u5265\u593a\u6211\u5723\u6b66\u58eb\u7684\u8eab\u4efd\uff0c\u5f00\u9664\u6559\u7c4d\u3002\u201d</p></article>"}}}]);
