(window.webpackJsonp=window.webpackJsonp||[]).push([[1377],{1400:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u8282 \u522b\u8fd9\u4e48\u8bf4\u8bdd",content:"<article><h2>\u7b2c\u516d\u8282 \u522b\u8fd9\u4e48\u8bf4\u8bdd</h2><p>\u6740\u622e\u4e4b\u795e\u5df4\u5c14\u662f\u90aa\u795e\uff0c\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u662f\u5584\u795e\uff0c\u5f7c\u6b64\u5b8c\u5168\u5bf9\u7acb\u51b2\u7a81\u3002\u6885\u83f2\u65af\u65e2\u7136\u8bf4\u7edd\u5927\u90e8\u5206\u5df4\u5c14\u795e\u529b\u90fd\u5df2\u7ecf\u5728\u5979\u4f53\u5185\uff0c\u65e0\u6cd5\u518d\u63a5\u53d7\u63d0\u5c14\u8d50\u4e88\u7684\u795e\u529b\uff0c\u4e5f\u662f\u5f88\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u5bf9\u3002\u201d\u6885\u83f2\u65af\u70b9\u5934\u3002</p><p>\u201c\u4f46\u4f60\u521a\u624d\u8bf4\u2026\u2026\u201d\u743c\u6069\u8bd5\u63a2\u7740\u95ee\uff0c\u201c\u90a3\u4e9b\u5df4\u5c14\u4e4b\u5b50\u90fd\u662f\u4f60\u6740\u7684\uff1f\u201d</p><p>\u65e2\u7136\u5df4\u5c14\u795e\u529b\u6563\u5e03\u5728\u6570\u4ee5\u5343\u8ba1\u7684\u5b50\u55e3\u4e2d\uff0c\u5982\u4eca\u5374\u57fa\u672c\u88ab\u6885\u83f2\u65af\u6240\u5f97\uff0c\u90a3\u743c\u6069\u5c31\u4e0d\u80fd\u4e0d\u6000\u7591\u5176\u6765\u6e90\u4e86\u3002\u4ee5\u4ed6\u5bf9\u6885\u83f2\u65af\u54c1\u884c\u7684\u4e86\u89e3\uff0c\u5e94\u8be5\u4e0d\u4f1a\u4e3a\u4e86\u878d\u5408\u795e\u529b\u8dd1\u53bb\u5230\u5904\u6740\u4eba\uff0c\u4f46\u82e5\u975e\u5982\u6b64\uff0c\u5979\u7684\u5df4\u5c14\u795e\u529b\u53c8\u662f\u600e\u4e48\u5f97\u6765\u7684\u3002</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u662f\u6211\u6bcd\u4eb2\u6740\u7684\u3002\u201d</p><p>\u743c\u6069\u987f\u65f6\u8083\u7136\u8d77\u656c\uff0c\u6885\u83f2\u65af\u8fd9\u4f4d\u6bcd\u4eb2\u5b9e\u5728\u592a\u5f3a\u608d\u4e86\uff0c\u4e0a\u6b21\u5728\u70db\u5821\uff0c\u743c\u6069\u5f97\u77e5\u5c31\u662f\u5979\u6740\u4e86\u4e00\u53ea\u6210\u5e74\u94f6\u9f99\uff0c\u8fd8\u5265\u76ae\u5236\u7532\uff1b\u5982\u4eca\u53c8\u542c\u8bf4\u5979\u5c45\u7136\u628a\u5df4\u5c14\u4e4b\u5b50\u51e0\u4e4e\u90fd\u6740\u5149\u4e86\u3002\u53ef\u60dc\u4e0a\u6b21\u542c\u6885\u83f2\u65af\u8bf4\u5979\u5df2\u7ecf\u53bb\u4e16\u4e86\uff0c\u5426\u5219\u8fd9\u79cd\u5973\u4e2d\u8c6a\u6770\uff0c\u4e00\u5b9a\u8981\u89c1\u8bc6\u89c1\u8bc6\u624d\u662f\u3002</p><p>\u201c\u73b0\u5728\u4f60\u77e5\u9053\uff0c\u90a3\u4e2a\u6740\u624b\uff0c\u53eb\u838e\u73de\u514b\u662f\u5427\uff0c\u4e3a\u4ec0\u4e48\u4e00\u76f4\u8ffd\u7740\u6211\u4eec\u4e0d\u653e\u4e86\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u76ef\u7740\u743c\u6069\uff0c\u201c\u5979\u4e0d\u662f\u8981\u6740\u4f60\uff0c\u662f\u8981\u6740\u6211\uff0c\u56e0\u4e3a\u5979\u548c\u6211\u4e00\u6837\u3002\u5982\u679c\u6211\u6ca1\u770b\u9519\uff0c\u5979\u7684\u4f53\u5185\u81f3\u5c11\u4e5f\u805a\u96c6\u4e86\u5341\u51e0\u4efd\u5df4\u5c14\u795e\u529b\uff0c\u5982\u679c\u80fd\u6740\u4e86\u6211\uff0c\u5979\u8ddd\u79bb\u795e\u7947\u5c31\u53ea\u6709\u534a\u6b65\u4e4b\u9065\u4e86\u3002\u201d</p><p>\u4f46\u7ed3\u679c\u5374\u662f\u6885\u83f2\u65af\u6740\u4e86\u838e\u73de\u514b\u2026\u2026\u5443\uff0c\u8fd9\u8bdd\u4e5f\u4e0d\u5bf9\uff0c\u6885\u83f2\u65af\u5176\u5b9e\u6ca1\u52a8\u624b\uff0c\u53cd\u6b63\u7ed3\u679c\u662f\u838e\u73de\u514b\u6b7b\u4e86\uff0c\u6885\u83f2\u65af\u6d3b\u7740\uff0c\u8fd9\u4e48\u8bf4\u8d77\u6765\u7684\u8bdd\uff0c\u6885\u83f2\u65af\u5c82\u4e0d\u4e5f\u662f\u8ddd\u79bb\u6210\u4e3a\u795e\u7947\u53ea\u6709\u534a\u6b65\u4e4b\u9065\u4e86\u3002</p><p>\u743c\u6069\u60f3\u8d77\u5f53\u65e5\u7684\u60c5\u5f62\uff0c\u5973\u6740\u624b\u88ab\u70c8\u7130\u711a\u8eab\u800c\u6b7b\uff0c\u706b\u7130\u4e2d\u817e\u8d77\u4e4c\u5149\u649e\u5165\u6885\u83f2\u65af\u4f53\u5185\uff0c\u968f\u5373\u5df4\u5c14\u7684\u865a\u50cf\u51fa\u73b0\uff0c\u4f46\u5374\u5148\u662f\u88ab\u4e00\u53ea\u94f6\u767d\u62a4\u7f69\u7ed9\u5c01\u4f4f\u4e86\uff0c\u540e\u6765\u6253\u7834\u62a4\u7f69\uff0c\u53c8\u88ab\u63d0\u5c14\u65a9\u9000\u3002\u5f53\u65f6\u4ed6\u4e0d\u77e5\u9053\u5185\u60c5\uff0c\u4e0d\u660e\u6240\u4ee5\uff0c\u5982\u4eca\u542c\u6885\u83f2\u65af\u4e00\u8bf4\uff0c\u518d\u56de\u60f3\u8d77\u6765\uff0c\u987f\u65f6\u4fbf\u5168\u660e\u767d\u8fc7\u6765\u3002</p><p>\u201c\u6211\u4e0d\u60f3\u6210\u4e3a\u6740\u622e\u4e4b\u795e\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6240\u4ee5\u795e\u5728\u6211\u8eab\u4e0a\u4e0b\u4e86\u7981\u5236\uff0c\u5c01\u4f4f\u4e86\u5df4\u5c14\u795e\u529b\uff0c\u4f46\u90a3\u6b21\u9669\u4e9b\u5c31\u88ab\u51b2\u7834\u4e86\uff0c\u201d\u5979\u7aef\u7740\u9152\u676f\uff0c\u82e5\u6709\u6240\u601d\uff0c\u201c\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u5982\u4eca\u8fd8\u6d3b\u7740\u7684\u5df4\u5c14\u4e4b\u5b50\uff0c\u53ea\u6015\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u4e2a\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u5979\u8fd9\u4e2a\u6570\u636e\u662f\u600e\u4e48\u7b97\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u5173\u5fc3\uff0c\u540c\u4e3a\u5df4\u5c14\u4e4b\u5b50\uff0c\u53ef\u80fd\u76f8\u4e92\u4e4b\u95f4\u4e5f\u6709\u6240\u611f\u5e94\u5427\u3002\u4ed6\u771f\u6b63\u5728\u610f\u7684\uff0c\u662f\u6885\u83f2\u65af\u521a\u624d\u8bf4\u7684\u90a3\u53e5\u8bdd\u3002</p><p>\u201c\u4f60\u4e0d\u60f3\u5f53\u795e\u7947\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u5947\u602a\u5730\u770b\u7740\u4ed6\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u60f3\uff1f\u201d</p><p>\u201c\u90a3\u4e2a\uff0c\u56e0\u4e3a\u6211\u89c9\u5f97\u80fd\u6210\u4e3a\u795e\u7947\uff0c\u5e94\u8be5\u662f\u5f88\u591a\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u76ee\u6807\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u76b1\u7740\u7709\uff0c\u201c\u5f53\u795e\u7947\u53c8\u6ca1\u4ec0\u4e48\u597d\u7684\u2026\u2026\u96be\u9053\u4f60\u60f3\u6210\u4e3a\u795e\u7947\uff1f\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\u3002\u5e9f\u8bdd\uff0c\u8eab\u4e3a\u51e1\u4eba\uff0c\u5982\u679c\u80fd\u6210\u4e3a\u795e\u7947\uff0c\u6709\u8c01\u4e0d\u60f3\u7684\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\u60f3\u6210\u4e3a\u795e\u7947\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u6210\u4e3a\u795e\u7947\u5c31\u53ef\u4ee5\u6c38\u751f\u4e0d\u6b7b\u5427\u3002\u201d\u743c\u6069\u8bf4\uff0c\u6c38\u751f\u4e0d\u6b7b\uff0c\u5bf9\u4e8e\u51e1\u4eba\u6765\u8bf4\u5e94\u8be5\u662f\u6700\u9ad8\u7684\u68a6\u60f3\u4e86\u3002</p><p>\u201c\u5982\u679c\u4ec5\u4ec5\u662f\u6c38\u751f\u4e0d\u6b7b\u7684\u8bdd\uff0c\u65b9\u6cd5\u4f3c\u4e4e\u662f\u5f88\u591a\u7684\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u677e\u5730\u8bf4\uff0c\u201c\u5deb\u5996\u5c31\u53ef\u4ee5\uff0c\u6bd4\u5982\u62c9\u6c83\u514b\u5148\u751f\u3002\u743c\u6069\uff0c\u4f60\u4e5f\u662f\u4e2a\u5deb\u5e08\uff0c\u8981\u8f6c\u5316\u5deb\u5996\uff0c\u5e76\u4e0d\u662f\u591a\u4e48\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u5427\u3002\u201d</p><p>\u5979\u559d\u4e86\u9152\uff0c\u8bdd\u4e5f\u4f3c\u4e4e\u5f00\u59cb\u53d8\u5f97\u591a\u8d77\u6765\uff0c\u82e5\u662f\u5728\u5e73\u65f6\uff0c\u60f3\u5fc5\u6ca1\u5174\u8da3\u8ddf\u743c\u6069\u8fd9\u4e48\u5e9f\u8bdd\u5427\u3002\u4e0d\u8fc7\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u597d\uff0c\u539f\u672c\u5c31\u662f\u4e2a\u5c0f\u5973\u5b69\uff0c\u6d3b\u6cfc\u4e9b\u603b\u6bd4\u51b7\u51b0\u51b0\u7684\u8ba8\u4eba\u559c\u6b22\u3002</p><p>\u201c\u5deb\u5996\u90a3\u4e0d\u80fd\u7b97\u6c38\u751f\u554a\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u90a3\u6700\u591a\u7b97\u4e0d\u6b7b\u7f62\u4e86\uff0c\u770b\u7740\u81ea\u5df1\u53d8\u6210\u4e00\u526f\u9ab7\u9ac5\u9aa8\u67b6\uff0c\u90a3\u5c31\u7b97\u8fd8\u2018\u6d3b\u7740\u2019\uff0c\u53c8\u6709\u4ec0\u4e48\u610f\u601d\u5462\u3002\u201d</p><p>\u201c\u4e0d\u559c\u6b22\u9ab7\u9ac5\u8eab\u4f53\u662f\u4e48\uff0c\u201d\u6885\u83f2\u65af\u4f3c\u4e4e\u6ca1\u6709\u6e05\u695a\u7406\u89e3\u743c\u6069\u7684\u610f\u601d\uff0c\u201c\u90a3\u4e48\uff0c\u53d8\u6210\u5e7d\u7075\u5982\u4f55\uff1f\u5e72\u8106\u5c31\u6ca1\u6709\u5b9e\u4f53\u4e86\uff0c\u53ea\u6709\u601d\u7ef4\u548c\u610f\u8bc6\u3002\u201d</p><p>\u743c\u6069\u8fde\u8fde\u6447\u5934\uff0c\u201c\u8fde\u8eab\u4f53\u90fd\u6ca1\u6709\uff0c\u90a3\u4e0d\u662f\u6bd4\u5deb\u5996\u66f4\u5dee\u52b2\u4e48\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u554a\uff0c\u201d\u6885\u83f2\u65af\u504f\u7740\u5934\uff0c\u8138\u4e0a\u6cdb\u8d77\u7565\u5e26\u5632\u8bbd\u7684\u7b11\u5bb9\u6765\uff0c\u201c\u542c\u8d77\u6765\uff0c\u743c\u6069\uff0c\u4f60\u597d\u50cf\u4e0d\u77e5\u9053\u4e00\u4ef6\u4e8b\u5462\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u795e\u7947\uff0c\u5c31\u662f\u548c\u5e7d\u7075\u4e00\u6837\uff0c\u6ca1\u6709\u5b9e\u4f53\u7684\u5b58\u5728\u554a\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5728\u4f4d\u9762\u5b66\u4e0a\uff0c\u5c06\u5404\u5927\u4f4d\u9762\u5212\u5206\u4e3a\u4e09\u5927\u7c7b\uff1a\u5916\u5c42\u4f4d\u9762\u3001\u5185\u5c42\u4f4d\u9762\u548c\u7269\u8d28\u754c\u3002</p><p>\u6240\u8c13\u5185\u5c42\u4f4d\u9762\uff0c\u4e43\u662f\u6307\u571f\u3001\u6c34\u3001\u706b\u3001\u98ce\u56db\u5927\u5143\u7d20\u4f4d\u9762\u548c\u6b63\u3001\u8d1f\u80fd\u91cf\u4f4d\u9762\uff0c\u4e00\u5171\u516d\u4e2a\uff0c\u662f\u652f\u6491\u8fd9\u4e2a\u4e16\u754c\u7684\u57fa\u7840\uff0c\u4e00\u5207\u7686\u7531\u8fd9\u56db\u5927\u5143\u7d20\u548c\u6b63\u8d1f\u80fd\u91cf\u6f14\u5316\u800c\u6765\u3002\u6240\u8c13\u5916\u5c42\u4f4d\u9762\uff0c\u6570\u91cf\u7e41\u591a\uff0c\u5b9a\u4e49\u8d77\u6765\u4e5f\u6bd4\u8f83\u9ebb\u70e6\uff0c\u5982\u5e7d\u5f71\u754c\uff0c\u5982\u65e0\u5c3d\u6df1\u6e0a\uff0c\u5982\u4e5d\u5c42\u5730\u72f1\uff0c\u5982\u5929\u5802\u5c71\uff0c\u5982\u660e\u6c34\u5883\u7b49\u7b49\uff0c\u4e43\u662f\u8bf8\u795e\u5c45\u4f4f\u4e4b\u6240\u3002</p><p>\u4f46\u7269\u8d28\u754c\u53ea\u6709\u4e00\u4e2a\u3002</p><p>\u201c\u8bf8\u795e\u56e0\u4fe1\u4ef0\u800c\u5b58\u5728\uff0c\u800c\u4fe1\u4ef0\u53ea\u4f1a\u6765\u6e90\u4e8e\u51e1\u4eba\uff0c\u201d\u6885\u83f2\u65af\u76ef\u7740\u743c\u6069\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8bf8\u795e\u4e3a\u4f55\u4e0d\u5728\u7269\u8d28\u754c\u5b9a\u5c45\uff1f\u4e3a\u4f55\u4e0d\u4eb2\u81ea\u7edf\u9886\u6559\u4f1a\uff1f\u4e3a\u4f55\u4e0d\u968f\u65f6\u5723\u8005\u4e34\u51e1\uff0c\u5c55\u793a\u795e\u8ff9\uff1f\u743c\u6069\uff0c\u4f60\u6ca1\u60f3\u8fc7\u8fd9\u4e9b\u95ee\u9898\u5417\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u4ed6\u4e8e\u795e\u7947\u5b97\u6559\u4e00\u9053\uff0c\u539f\u672c\u5c31\u538b\u6839\u6ca1\u4ec0\u4e48\u5174\u8da3\uff0c\u5e73\u65f6\u7814\u4e60\u9b54\u6cd5\u90fd\u5df2\u7ecf\u5fd9\u4e0d\u8fc7\u6765\uff0c\u54ea\u91cc\u4f1a\u53bb\u8d39\u5fc3\u601d\u60f3\u8fd9\u4e9b\u4e8b\u60c5\u3002\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u56e0\u4e3a\u529e\u4e0d\u5230\u3002\u201d\u6885\u83f2\u65af\u5e72\u8106\u5229\u843d\u5730\u56de\u7b54\u3002</p><p>\u201c\u7269\u8d28\u754c\u4e3a\u4ec0\u4e48\u88ab\u79f0\u4e3a\u7269\u8d28\u754c\uff1f\u56e0\u4e3a\u552f\u6709\u6b64\u4e16\u754c\uff0c\u624d\u662f\u771f\u6b63\u7269\u8d28\u7684\u4e16\u754c\uff1b\u5176\u4ed6\u5404\u5927\u4f4d\u9762\uff0c\u5185\u5c42\u4f4d\u9762\u4e5f\u597d\uff0c\u5916\u5c42\u4f4d\u9762\u4e5f\u597d\uff0c\u90fd\u4e0d\u8fc7\u662f\u610f\u8bc6\u7684\u6295\u5f71\uff0c\u662f\u7cbe\u795e\u7684\u56fd\u5ea6\u2014\u2014\u5e7d\u5f71\u754c\u76f8\u5bf9\u4f8b\u5916\uff0c\u56e0\u4e3a\u5b83\u81ea\u7269\u8d28\u754c\u5206\u79bb\u800c\u51fa\uff0c\u5176\u5b9e\u662f\u7269\u8d28\u754c\u7684\u6298\u5c04\uff0c\u662f\u534a\u7269\u8d28\u534a\u610f\u8bc6\u7684\u5b58\u5728\u3002\u201d</p><p>\u8bf8\u795e\u4e43\u662f\u4fe1\u4ef0\u548c\u610f\u8bc6\u7684\u805a\u5408\uff0c\u662f\u7cbe\u795e\u7684\u5b58\u5728\uff0c\u4ed6\u4eec\u6ca1\u6709\u771f\u6b63\u7684\u7269\u8d28\u5f62\u4f53\u3002\u6240\u4ee5\u8bf8\u795e\u53ef\u4ee5\u5c45\u4f4f\u5728\u5404\u5927\u5916\u5c42\u4f4d\u9762\uff0c\u5374\u65e0\u6cd5\u5728\u7269\u8d28\u754c\u957f\u5b58\u3002\u5f53\u7136\uff0c\u795e\u7947\u4e4b\u6240\u4ee5\u4e3a\u795e\u7947\uff0c\u81ea\u6709\u5176\u4e0d\u53ef\u601d\u8bae\u4e4b\u5904\uff0c\u610f\u8bc6\u7684\u529b\u91cf\u8fbe\u5230\u6781\u81f4\uff0c\u4e5f\u53ef\u4ee5\u5316\u4e3a\u7269\u8d28\u7684\u5b9e\u4f53\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u201c\u5316\u8eab\u201d\u4e86\u3002</p><p>\u6216\u8005\u501f\u52a9\u4fe1\u5f92\u7684\u727a\u7272\uff0c\u6216\u8005\u6d88\u8017\u81ea\u5df1\u7684\u795e\u529b\uff0c\u795e\u7947\u53ef\u4ee5\u521b\u9020\u51fa\u6682\u65f6\u7684\u7269\u8d28\u5316\u5f62\u4f53\uff0c\u8fdb\u5165\u7269\u8d28\u754c\u3002\u4f46\u8fd9\u79cd\u505a\u6cd5\u4ee3\u4ef7\u65e2\u5927\uff0c\u98ce\u9669\u4e5f\u9ad8\uff0c\u4e5f\u4e0d\u80fd\u5728\u7269\u8d28\u754c\u505c\u7559\u591a\u4e45\uff0c\u6240\u4ee5\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u6ca1\u6709\u795e\u7947\u4f1a\u8fd9\u4e48\u505a\u3002</p><p>\u201c\u90a3\u4e48\u73b0\u5728\u4f60\u6e05\u695a\u4e86\uff1f\u201d\u6885\u83f2\u65af\u770b\u7740\u743c\u6069\uff0c\u201c\u5e7d\u7075\u6ca1\u6709\u5b9e\u4f53\uff0c\u795e\u7947\u540c\u6837\u4e5f\u6ca1\u6709\u5b9e\u4f53\uff1b\u795e\u7947\u80fd\u6c38\u751f\u4e0d\u6b7b\uff0c\u5e7d\u7075\u540c\u6837\u4e5f\u80fd\u505a\u5230\u2014\u2014\u90a3\u4e48\uff0c\u4f60\u9009\u62e9\u795e\u7947\u7684\u7406\u7531\u662f\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u4f46\u795e\u7947\u6bd4\u5e7d\u7075\u5f3a\u5927\u5f97\u591a\uff0c\u81f3\u5c11\u5e7d\u7075\u5c31\u6ca1\u529e\u6cd5\u7ed9\u81ea\u5df1\u521b\u9020\u7269\u8d28\u5316\u7684\u8eab\u4f53\u3002\u201d</p><p>\u201c\u5deb\u5996\u540c\u6837\u5f3a\u5927\uff0c\u201d\u6885\u83f2\u65af\u6beb\u4e0d\u72b9\u8c6b\u5730\u56de\u7b54\uff0c\u201c\u53ea\u8981\u4f60\u80fd\u6709\u62c9\u6c83\u514b\u90a3\u6837\u7684\u9020\u8be3\uff0c\u5c31\u7b97\u662f\u795e\u7947\u4e5f\u5948\u4f55\u4f60\u4e0d\u5f97\u3002\u5982\u679c\u4f60\u538c\u6076\u9ab7\u9ac5\u8eaf\u4f53\uff0c\u90a3\u4e48\uff0c\u743c\u6069\uff0c\u544a\u8bc9\u6211\uff0c\u4e00\u4e2a\u6c38\u4e45\u53d8\u5f62\u672f\u5f88\u96be\u5417\uff1f\u201d</p><p>\u5f88\u96be\uff0c\u4f46\u5bf9\u5deb\u5996\u6765\u8bf4\uff0c\u5c24\u5176\u662f\u5bf9\u4e8e\u62c9\u6c83\u514b\u8fd9\u79cd\u7a0b\u5ea6\u7684\u5deb\u5996\u6765\u8bf4\uff0c\u5c31\u5bb9\u6613\u5f97\u4e0d\u503c\u4e00\u63d0\u3002</p><p>\u201c\u7528\u53d8\u5f62\u672f\uff0c\u4f60\u53ef\u4ee5\u53d8\u6210\u4efb\u4f55\u4f60\u60f3\u8981\u7684\u6a21\u6837\uff0c\u201d\u6885\u83f2\u65af\u7ee7\u7eed\u8bf4\uff0c\u201c\u4f60\u4e0d\u9700\u8981\u50cf\u795e\u7947\u4e00\u6837\uff0c\u8017\u8d39\u5de8\u5927\u7684\u529b\u91cf\uff0c\u624d\u80fd\u521b\u9020\u77ed\u6682\u5b58\u5728\u7684\u7269\u8d28\u5f62\u4f53\u3002\u90a3\u4e48\uff0c\u743c\u6069\uff0c\u544a\u8bc9\u6211\uff0c\u4f60\u4e3a\u4ec0\u4e48\u504f\u504f\u820d\u5f03\u5deb\u5996\u8fd9\u6761\u5bb9\u6613\u5f97\u591a\u7684\u8def\u7ebf\uff0c\u5374\u5180\u671b\u4e8e\u6210\u4e3a\u795e\u7947\u8fd9\u79cd\u5f97\u4e0d\u507f\u5931\u7684\u4e8b\u60c5\u5462\uff1f\u201d</p><p>\u201c\u6216\u8005\uff0c\u4f60\u662f\u559c\u6b22\u80fd\u88ab\u5343\u4e07\u4eba\u6240\u4fe1\u4ef0\u5d07\u62dc\u7684\u611f\u89c9\uff1f\u201d\u6885\u83f2\u65af\u8138\u4e0a\u5632\u8bbd\u7684\u7b11\u610f\u8d8a\u6765\u8d8a\u91cd\uff0c\u201c\u6ca1\u770b\u51fa\u6765\u5462\uff0c\u743c\u6069\uff0c\u4f60\u8fd8\u6709\u8fd9\u6837\u4e86\u4e0d\u8d77\u7684\u96c4\u5fc3\u58ee\u5fd7\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u4ed6\u5176\u5b9e\u5012\u6ca1\u8fd9\u79cd\u5ff5\u5934\u3002\u8eab\u5c45\u9ad8\u4f4d\uff0c\u6267\u638c\u5927\u6743\uff0c\u4e07\u4f17\u666f\u4ef0\u671d\u62dc\uff0c\u8fd9\u611f\u89c9\u867d\u7136\u4f3c\u4e4e\u4e0d\u9519\uff0c\u4f46\u5bf9\u743c\u6069\u6765\u8bf4\u4e5f\u8c08\u4e0d\u4e0a\u592a\u591a\u7684\u5438\u5f15\u529b\u3002\u5728\u201c\u6743\u529b\u201d\u7684\u6b32\u671b\u4e0a\uff0c\u4ed6\u7d20\u6765\u6de1\u6f20\u5f97\u5f88\uff0c\u5982\u679c\u8981\u9009\u62e9\u7684\u8bdd\uff0c\u4ed6\u5b81\u613f\u80fd\u591a\u9047\u89c1\u51e0\u4e2a\u6f02\u4eae\u7f8e\u5973\u3002</p><p>\u201c\u6216\u8bb8\u5427\uff0c\u201d\u4ed6\u6e29\u548c\u5730\u8bf4\uff0c\u201c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u597d\u50cf\u795e\u7947\u786e\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u597d\u7684\u3002\u4e0d\u8fc7\u8bdd\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u5bf9\u4e8e\u4f60\u800c\u8a00\uff0c\u6210\u4e3a\u795e\u7947\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\u4f60\u53ef\u4ee5\u770b\u5f97\u5f88\u8f7b\uff1b\u4f46\u6211\u60f3\uff0c\u5bf9\u4e8e\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u7edd\u5927\u591a\u6570\u4eba\u800c\u8a00\uff0c\u80fd\u6210\u4e3a\u795e\u7947\uff0c\u8fd8\u662f\u4f1a\u8ba9\u4ed6\u4eec\u6b23\u559c\u82e5\u72c2\u5427\u3002\u867d\u7136\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u76f8\u4fe1\u795e\u7947\u5e94\u8be5\u8fd8\u662f\u6709\u4e9b\u5bfb\u5e38\u6240\u4e0d\u80fd\u4f01\u53ca\u7684\u4f18\u52bf\u5427\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d\u6885\u83f2\u65af\u53cd\u95ee\uff0c\u201c\u4f60\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u9053\u7406\uff0c\u4ec5\u4ec5\u5c31\u51ed\u611f\u89c9\uff0c\u7a7a\u6d1e\u5730\u8bf4\u4ec0\u4e48\u76f8\u4fe1\uff0c\u743c\u6069\uff0c\u96be\u9053\u4f60\u4e0d\u89c9\u5f97\u8fd9\u5f88\u53ef\u7b11\u5417\uff1f\u5b8c\u5168\u4e0d\u7b26\u5408\u4e00\u4e2a\u5deb\u5e08\u5e94\u6709\u7684\u7d20\u8d28\u5427\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u7b11\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u5230\u6885\u83f2\u65af\u8eab\u65c1\uff0c\u4f38\u624b\u6258\u7740\u5979\u7684\u4e0b\u5df4\uff0c\u4fef\u89c6\u7740\u5979\u3002\u201c\u522b\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u5f88\u4e0d\u8ba8\u4eba\u559c\u6b22\u7684\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u6211\u627f\u8ba4\uff0c\u4f60\u6bd4\u6211\u529b\u91cf\u5f3a\u5927\uff0c\u6bd4\u6211\u89c1\u591a\u8bc6\u5e7f\uff0c\u6bd4\u6211\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u751a\u81f3\u5f88\u591a\u65f6\u5019\u6bd4\u6211\u806a\u660e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u662f\u795e\u5b50\uff0c\u53ea\u8981\u613f\u610f\u5c31\u662f\u795e\uff0c\u800c\u6211\u53ea\u662f\u4e2a\u666e\u901a\u7684\u51e1\u4eba\uff1b\u4f60\u662f\u5723\u6b66\u58eb\uff0c\u662f\u795e\u9009\u8005\uff0c\u800c\u6211\u53ea\u662f\u4e2a\u5bfb\u5e38\u7684\u5c0f\u5deb\u5e08\uff1b\u4f60\u786e\u5b9e\u6bd4\u6211\u5f3a\u5f97\u591a\uff0c\u4f46\u6211\u8fd8\u662f\u4e0d\u559c\u6b22\u4f60\u7528\u8fd9\u79cd\u5c45\u9ad8\u4e34\u4e0b\u7684\u8bed\u6c14\u548c\u6211\u8bf4\u8bdd\u5462\uff0c\u5f88\u4e0d\u559c\u6b22\u3002\u201d</p><p>\u201c\u6709\u4e48\uff1f\u201d</p><p>\u201c\u6709\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u53f9\u606f\u7740\uff0c\u201c\u4f60\u592a\u806a\u660e\uff0c\u4e5f\u592a\u81ea\u8d1f\u806a\u660e\uff0c\u5728\u5fc3\u5e95\u91cc\u5c31\u77a7\u4e0d\u8d77\u90a3\u4e9b\u6bd4\u4f60\u7b28\u7684\u4eba\u3002\u4f60\u4e0d\u89c9\u5f97\u4f60\u521a\u624d\u8bf4\u8bdd\u548c\u5e73\u65f6\u4e0d\u4e00\u6837\u4e48\uff0c\u950b\u8292\u6bd5\u9732\uff0c\u5484\u5484\u903c\u4eba\u2026\u2026\u8fd9\u6837\u4f1a\u8ba9\u6211\u89c9\u5f97\u5f88\u96be\u53d7\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6216\u8bb8\u554a\uff0c\u6216\u8bb8\u8fd9\u5c31\u662f\u6211\u7684\u672c\u6027\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6240\u4ee5\u8bf4\uff0c\u743c\u6069\uff0c\u4f60\u8981\u60f3\u5f97\u5230\u6211\uff0c\u8981\u60f3\u505a\u6211\u7537\u4eba\uff0c\u90a3\u53ef\u662f\u4ef6\u5f88\u4e0d\u5bb9\u6613\u7684\u4e8b\u60c5\u5462\uff0c\u6211\u662f\u4e0d\u4f1a\u559c\u6b22\u4e0a\u4e00\u4e2a\u6bd4\u6211\u5dee\u52b2\u7684\u7537\u4eba\u7684\u3002\u201d</p><p>\u201c\u6211\u4f1a\u52aa\u529b\u7684\uff0c\u201d\u743c\u6069\u634f\u4e86\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u73b0\u5728\uff0c\u522b\u559d\u4e86\uff0c\u7ed9\u6211\u4e56\u4e56\u4e0a\u5e8a\u7761\u89c9\u53bb\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u7740\uff0c\u653e\u4e0b\u9152\u676f\uff0c\u6447\u6447\u6643\u6643\u60f3\u7ad9\u8d77\u8eab\u6765\uff0c\u5374\u9669\u4e9b\u6454\u5012\u4e86\u3002\u743c\u6069\u5c06\u5979\u6276\u4f4f\uff0c\u89c1\u5979\u5df2\u7ecf\u9189\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u661f\u7738\u8ff7\u8499\uff0c\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u62e6\u8170\u5c06\u5979\u62b1\u4e86\u8d77\u6765\u3002</p><p>\u201c\u4ee5\u540e\u518d\u4e0d\u51c6\u4f60\u559d\u9152\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u62cd\u4e86\u62cd\u5979\u7684\u8138\uff0c\u201c\u4e00\u559d\u9189\u4e86\u5c31\u4e71\u8bf4\u8bdd\u3002\u201d</p><p>\u201c\u54ea\u6709\u3002\u201d\u5c0f\u5973\u5b69\u8ff7\u8ff7\u7cca\u7cca\u5730\u6297\u8bae\u7740\u3002</p><p>\u743c\u6069\u4e0d\u7406\u4f1a\u5979\u7684\u6297\u8bae\uff0c\u5c06\u5979\u62b1\u5230\u5e8a\u4e0a\u653e\u4e0b\uff0c\u6b63\u8981\u53bb\u53d6\u6bdb\u6bef\uff0c\u5374\u88ab\u5979\u53cc\u81c2\u4e00\u73af\uff0c\u6263\u4f4f\u8116\u9888\uff0c\u201c\u4e0d\u51c6\u8d70\uff0c\u201d\u5979\u6492\u5a07\u4f3c\u5730\u8bf4\uff0c\u201c\u966a\u6211\u3002\u201d</p><p>\u743c\u6069\u8dea\u5728\u5e8a\u6cbf\uff0c\u4fef\u89c6\u7740\u5979\uff0c\u63a5\u7740\u4f4e\u5934\u53bb\u543b\u5979\u7684\u5507\uff0c\u6885\u83f2\u65af\u8fce\u5408\u7740\uff0c\u4e24\u4eba\u751c\u871c\u5730\u63a5\u543b\u3002\u743c\u6069\u611f\u53d7\u7740\u5c11\u5973\u67d4\u8f6f\u5a07\u5ae9\u7684\u5634\u5507\uff0c\u542e\u5438\u7740\u9999\u6ed1\u7684\u820c\u5c16\uff0c\u54c1\u5c1d\u5e26\u7740\u8461\u8404\u9152\u6c14\u7684\u9999\u751c\u6d25\u6db2\u3002\u5c11\u5973\u7684\u8138\u4e0a\u5728\u9010\u6e10\u53d1\u70eb\uff0c\u547c\u5438\u4e5f\u6e10\u6e10\u53d8\u5f97\u6025\u4fc3\u8d77\u6765\u3002</p><p>\u743c\u6069\u4e00\u8fb9\u4eb2\u543b\u7740\u5979\uff0c\u4e00\u8fb9\u624b\u6084\u6084\u79fb\u4e0a\u5979\u7684\u80f8\u53e3\uff0c\u4e00\u9897\u4e00\u9897\u5730\u89e3\u5f00\u7ebd\u6263\u3002\u65f6\u503c\u590f\u65e5\uff0c\u5929\u6c14\u708e\u70ed\uff0c\u6885\u83f2\u65af\u7a7f\u5f97\u4e5f\u5f88\u5355\u8584\uff0c\u4e0d\u8fc7\u4e00\u5957\u6d45\u7eff\u8272\u7684\u886c\u886b\u957f\u88e4\u3002\u743c\u6069\u6ca1\u82b1\u591a\u5c11\u529f\u592b\uff0c\u5c31\u5df2\u7ecf\u5c06\u886c\u886b\u7684\u7ebd\u6263\u5168\u90e8\u89e3\u5f00\uff0c\u6885\u83f2\u65af\u8fd8\u61f5\u7136\u4e0d\u89c9\uff0c\u4e00\u65e0\u6240\u77e5\u3002\u76f4\u5230\u743c\u6069\u7684\u624b\u6084\u6084\u63a2\u8fdb\u957f\u88e4\u5185\uff0c\u6307\u5c16\u629a\u6478\u7740\u5c0f\u8179\u4e0b\u7684\u5ae9\u6ed1\u7684\u808c\u80a4\uff0c\u5c11\u5973\u624d\u60ca\u89c9\u8fc7\u6765\u3002\u201c\u4e0d\u884c\u5566\uff0c\u201d\u5979\u5a07\u541f\u7740\uff0c\u626d\u52a8\u8eab\u4f53\uff0c\u6297\u62d2\u7740\u743c\u6069\u7684\u88ad\u51fb\uff0c\u201c\u4e0d\u884c\u2026\u2026\u201d</p><p>\u201c\u7ed9\u6211\u4e2a\u7406\u7531\uff0c\u201d\u743c\u6069\u4e0d\u6ee1\uff0c\u201c\u4f60\u603b\u8981\u8ba9\u6211\u77e5\u9053\u4e3a\u4ec0\u4e48\u5427\u3002\u201d</p><p>\u201c\u4ee5\u540e\u518d\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u6211\u73b0\u5728\u5c31\u8981\u77e5\u9053\uff0c\u4e0d\u7136\u6211\u4eca\u665a\u5c31\u628a\u4f60\u5403\u4e86\u3002\u201d</p><p>\u8fd9\u4e2a\u5a01\u80c1\u5f88\u6709\u6548\uff0c\u6885\u83f2\u65af\u7684\u8138\u4e0a\u66f4\u7ea2\u4e86\uff0c\u201c\u4ee5\u540e\u518d\u8ddf\u4f60\u89e3\u91ca\u597d\u4e0d\u597d\uff0c\u201d\u5979\u6073\u6c42\u7740\uff0c\u201c\u6c42\u4f60\u4e86\u3002\u201d</p><p>\u743c\u6069\u89c1\u5979\u5982\u6b64\uff0c\u4e5f\u4e0d\u60f3\u8fc7\u5206\u52c9\u5f3a\uff0c\u600f\u600f\u5730\u5c06\u6b63\u51c6\u5907\u89e3\u5f00\u5979\u7684\u957f\u88e4\u8170\u5e26\u7684\u624b\u7f29\u4e86\u56de\u6765\uff0c\u201c\u4f60\u603b\u662f\u8fd9\u6837\uff0c\u201d\u4ed6\u62a5\u6028\u7740\uff0c\u201c\u5c3d\u627e\u501f\u53e3\u63a8\u8131\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5566\uff0c\u201d\u5c11\u5973\u54ac\u7740\u4e0b\u5507\uff0c\u4f3c\u4e4e\u6709\u4e9b\u59d4\u5c48\u7684\u6837\u5b50\uff0c\u201c\u771f\u7684\u662f\u6709\u539f\u56e0\u7684\u2026\u2026\u660e\u5929\u8ddf\u4f60\u89e3\u91ca\u597d\u4e0d\u597d\uff0c\u4eca\u665a\u6211\u73b0\u5728\u5934\u6655\u4e86\uff0c\u5c31\u60f3\u4f60\u62b1\u7740\u6211\u7761\u3002\u201d</p><p>\u201c\u597d\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u53ea\u662f\u5fc3\u4e2d\u4e5f\u5b9e\u5728\u6709\u4e9b\u7591\u60d1\uff0c\u4e0d\u77e5\u9053\u5979\u5230\u5e95\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u8fd9\u4e48\u575a\u6301\u62d2\u7edd\u548c\u81ea\u5df1\u4e0a\u5e8a\uff0c\u201c\u8bf4\u597d\u4e86\uff0c\u660e\u5929\u7ed9\u6211\u89e3\u91ca\uff0c\u4e0d\u7136\u6211\u5c31\u7b97\u4f60\u800d\u8d56\uff0c\u77e5\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u8f7b\u8f7b\u62cd\u62cd\u5979\u7684\u8138\u86cb\uff0c\u201c\u7761\u5427\uff0c\u6211\u62b1\u7740\u4f60\u3002\u201d</p><p>\u6b63\u51c6\u5907\u8131\u8863\u670d\u4e0a\u5e8a\uff0c\u7a97\u5916\u9661\u7136\u4f20\u6765\u4e00\u9635\u9707\u8033\u6b32\u804b\u7684\u558a\u58f0\uff0c\u9707\u5f97\u73bb\u7483\u90fd\u7c0c\u7c0c\u4f5c\u54cd\u3002\u743c\u6069\u6014\u4e86\u6014\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4e0d\u662f\u8725\u8734\u4eba\u6765\u88ad\u51fb\u57ce\u9547\u4e86\uff0c\u4f46\u4ed4\u7ec6\u518d\u542c\uff0c\u5374\u4f3c\u4e4e\u662f\u6b22\u547c\u4e00\u7247\uff0c\u4eff\u4f5b\u662f\u5168\u9547\u4eba\u5728\u70ed\u70c8\u5e86\u795d\u4ec0\u4e48\u3002\u201c\u8fd9\u6df1\u66f4\u534a\u591c\u7684\u5435\u4ec0\u4e48\u5435\uff01\u201d\u4ed6\u5f88\u6709\u4e9b\u4e0d\u6ee1\uff0c\u4f46\u4e5f\u5947\u602a\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u201c\u4f60\u53bb\u770b\u770b\u5427\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u743c\u6069\u55ef\u4e86\u4e00\u58f0\uff0c\u4fef\u8eab\u5728\u5979\u989d\u5934\u4e0a\u8f7b\u8f7b\u4eb2\u4e86\u53e3\uff0c\u201c\u4e56\u4e56\u7761\uff0c\u6211\u9a6c\u4e0a\u5c31\u56de\u6765\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p></article>"}}}]);
