(window.webpackJsonp=window.webpackJsonp||[]).push([[1387],{1410:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u8679\u5f69\u9f99\u7684\u98ce\u5ea6",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u8679\u5f69\u9f99\u7684\u98ce\u5ea6</h2><p>\u6709\u4e86\u8679\u5f69\u9f99\u8fd9\u4e2a\u6551\u5175\uff0c\u743c\u6069\u5b89\u5fc3\u4e86\u4e9b\uff0c\u7136\u800c\u7ec8\u7a76\u4e0d\u80fd\u628a\u5e0c\u671b\u5168\u5bc4\u6258\u5728\u8fd9\u4e0a\u9762\u3002\u4ed6\u5c06\u6563\u843d\u5728\u684c\u5b50\u4e0a\u7684\u4e1c\u897f\u5168\u90fd\u6536\u4e86\u8d77\u6765\uff0c\u4f69\u6234\u5728\u8eab\u4e0a\uff0c\u6216\u8005\u88c5\u8fdb\u6b21\u5143\u888b\uff0c\u653e\u8fdb\u957f\u888d\u5185\u4fa7\u53e3\u888b\u91cc\u3002\u8679\u5f69\u9f99\u8bf4\u662f\u9a6c\u4e0a\u8d76\u8fc7\u6765\uff0c\u5374\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u80fd\u5230\uff0c\u5728\u6b64\u4e4b\u524d\u5f97\u505a\u597d\u51c6\u5907\u3002</p><p>\u201c\u597d\u50cf\u6709\u70b9\u4e0d\u592a\u5bf9\u52b2\u3002\u201d\u6885\u83f2\u65af\u9759\u9759\u770b\u7740\u4ed6\u6536\u62fe\u4e1c\u897f\uff0c\u7a81\u7136\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\u4e0d\u5bf9\u52b2\uff1f\u201d</p><p>\u201c\u4e0a\u6b21\u6211\u4eec\u60f3\u7528\u4efb\u610f\u95e8\u9003\u8dd1\uff0c\u5965\u6c83\u7acb\u523b\u5c31\u53d1\u89c9\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u7136\u800c\u8fd9\u4e00\u6b21\uff0c\u6211\u4eec\u521a\u5f04\u4e86\u534a\u5929\uff0c\u4ed6\u96be\u9053\u5c31\u4e00\u65e0\u6240\u89c9\uff1f\u201d</p><p>\u743c\u6069\u6014\u4f4f\uff0c\u4ed6\u521a\u624d\u5fd9\u4e8e\u548c\u72d7\u4ed4\u961f\u4e0e\u8679\u5f69\u9f99\u8fd9\u79cd\u53d8\u6001\u5bb6\u4f19\u6253\u4ea4\u9053\uff0c\u5b8c\u5168\u6ca1\u60f3\u5230\u8fd9\u56de\u4e8b\uff0c\u201c\u6216\u8bb8\u662f\u5899\u58c1\u4e0a\u77f3\u5316\u725b\u8840\u7ed8\u6210\u7684\u9b54\u6cd5\u9635\u7684\u7f18\u6545\uff0c\u201d\u4ed6\u731c\u6d4b\u7740\uff0c\u201c\u4f7f\u7528\u4f20\u9001\u7c7b\u578b\u6cd5\u672f\uff0c\u89e6\u52a8\u9b54\u6cd5\u9635\uff0c\u6240\u4ee5\u4ed6\u5c31\u53d1\u89c9\u4e86\u3002\u8fd9\u6b21\u6211\u4eec\u6ca1\u7528\u4f20\u9001\u6cd5\u672f\uff0c\u4ed6\u81ea\u7136\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u8bb8\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u9b54\u6cd5\u8fd9\u65b9\u9762\u6211\u4e0d\u61c2\uff0c\u4f46\u6211\u603b\u89c9\u5f97\u8fd9\u4e0d\u592a\u5bf9\u52b2\u3002\u5965\u6c83\u7a81\u7136\u63d0\u51fa\u8fd9\u4ef6\u4e8b\uff0c\u633a\u5947\u602a\u7684\u3002\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u201d\u743c\u6069\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u4f60\u4e0d\u80fd\u7528\u6b63\u5e38\u4eba\u7684\u601d\u7ef4\u6765\u5224\u65ad\u4ed6\uff0c\u522b\u5fd8\u4e86\uff0c\u8fd9\u6b7b\u80d6\u5b50\u538b\u6839\u5c31\u662f\u4e2a\u5fc3\u7406\u53d8\u6001\uff0c\u4f5c\u51fa\u4ec0\u4e48\u4e8b\u60c5\u6765\u90fd\u4e0d\u5947\u602a\u3002\u518d\u8bf4\uff0c\u5982\u679c\u4ed6\u662f\u6545\u610f\u7684\u8bdd\uff0c\u8fd9\u4e48\u505a\u5bf9\u4ed6\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d</p><p>\u201c\u4e5f\u5bf9\u3002\u201d</p><p>\u4e24\u4eba\u4e0d\u518d\u591a\u60f3\uff0c\u5404\u81ea\u4f11\u606f\uff0c\u517b\u7cbe\u84c4\u9510\u51c6\u5907\u9003\u8dd1\u3002\u90a3\u53ea\u8679\u5f69\u9f99\u662f\u4e2a\u9887\u4e3a\u81ea\u8d1f\u56a3\u5f20\u7684\u5bb6\u4f19\uff0c\u5b8c\u5168\u6ca1\u6709\u4f4e\u8c03\u505a\u4eba\u7684\u6982\u5ff5\uff0c\u4e0a\u6b21\u5728\u70db\u5821\uff0c\u4e3a\u4e86\u4e00\u4efd\u8d44\u6599\u5c31\u5927\u6253\u51fa\u624b\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u662f\u4eb2\u773c\u6240\u89c1\u7684\u3002\u5982\u4eca\u6765\u62a2\u5965\u6c83\u7684\u5b9d\u5e93\uff0c\u5230\u65f6\u5019\u80af\u5b9a\u53c8\u662f\u4e00\u573a\u5267\u6597\uff0c\u5230\u65f6\u5019\u53d8\u4e71\u4e00\u8d77\uff0c\u4e0d\u5fc5\u7b49\u4ed6\u6765\u6551\uff0c\u743c\u6069\u5c31\u53ef\u4ee5\u5e26\u7740\u6885\u83f2\u65af\u4e58\u673a\u9003\u51fa\u53bb\u3002</p><p>\u5965\u6c83\u90a3\u8fb9\u4e5f\u4e00\u76f4\u6ca1\u4ec0\u4e48\u52a8\u9759\uff0c\u53ef\u80fd\u6b63\u5728\u5174\u9ad8\u91c7\u70c8\u5730\u51c6\u5907\u7b79\u5907\u5a5a\u793c\uff0c\u6574\u4e2a\u5730\u4e0b\u5893\u7a74\u91cc\u9759\u6084\u6084\u7684\uff0c\u6b7b\u5bc2\u4e00\u7247\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u81ea\u8fd0\u529f\u8c03\u606f\u4e2d\u6062\u590d\uff0c\u7741\u5f00\u773c\u775b\uff0c\u89c1\u6885\u83f2\u65af\u6b63\u9759\u9759\u5730\u9760\u5728\u6905\u80cc\u4e0a\u95ed\u76ee\u4f11\u606f\u3002\u7b97\u7b97\u65f6\u95f4\uff0c\u5e94\u8be5\u5df2\u7ecf\u662f\u534a\u591c\u4e86\uff0c\u90a3\u53ea\u8679\u5f69\u9f99\u8fd8\u6ca1\u8d76\u5230\u4e48\u3002</p><p>\u6b63\u601d\u5fd6\u95f4\uff0c\u9661\u7136\u611f\u89c9\u811a\u4e0b\u5927\u5730\u98a4\u52a8\uff0c\u7d27\u63a5\u7740\u8fde\u58f0\u8f70\u9e23\u5de8\u54cd\uff0c\u5934\u9876\u4e0a\u8c41\u7136\u5f00\u6717\uff0c\u539f\u672c\u9ed1\u6c89\u6c89\u7684\u5893\u7a74\u7a79\u9876\u88ab\u6380\u5f00\u4e86\u4e00\u4e2a\u5de8\u5927\u7684\u53e3\u5b50\uff0c\u9732\u51fa\u9ed1\u8272\u591c\u7a79\u4e0a\u7684\u5343\u4e07\u9897\u7480\u74a8\u661f\u8fb0\u3002\u5e26\u7740\u9752\u8349\u5473\u9053\u7684\u6e05\u65b0\u7a7a\u6c14\u81ea\u5916\u9762\u6d8c\u5165\uff0c\u8ba9\u5728\u8fd9\u5730\u4e0b\u5893\u7a74\u91cc\u5446\u4e86\u4e09\u5929\u7684\u743c\u6069\u7cbe\u795e\u4e00\u632f\u3002</p><p>\u6765\u4e86\uff01</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u65e9\u6709\u51c6\u5907\uff0c\u5c31\u7b49\u7740\u8fd9\u4e00\u523b\u5230\u6765\uff0c\u8fde\u5fd9\u559d\u4e0b\u98de\u884c\u836f\u6c34\uff0c\u5f80\u4e0a\u98de\u53bb\u3002\u5965\u6c83\u8fd9\u5893\u7a74\u5efa\u5f97\u5f88\u6df1\uff0c\u4e24\u4eba\u597d\u4e0d\u5bb9\u6613\u4e0a\u4e86\u5730\u8868\uff0c\u6b64\u65f6\u5374\u53c8\u662f\u6df1\u591c\uff0c\u770b\u4e0d\u6e05\u5468\u56f4\u60c5\u5f62\uff0c\u4e5f\u4e0d\u77e5\u9053\u8be5\u5f80\u90a3\u4e2a\u65b9\u5411\u9003\u3002</p><p>\u6b63\u72b9\u8c6b\u95f4\uff0c\u731b\u5730\u773c\u524d\u4e03\u5f69\u8679\u5149\u7eda\u70c2\u7efd\u653e\uff0c\u51e0\u4e4e\u5c06\u773c\u775b\u90fd\u6643\u778e\u4e86\u3002\u4e24\u4eba\u62ac\u5934\u671b\u53bb\uff0c\u89c1\u524d\u65b9\u9ad8\u7a7a\u4e4b\u4e2d\uff0c\u4e00\u53ea\u80d6\u80d6\u7684\u7c89\u7ea2\u8272\u5de8\u9f99\u6b63\u5728\u5f20\u7259\u821e\u722a\uff0c\u5468\u8eab\u6d41\u5149\u6ea2\u5f69\uff0c\u53e3\u4e2d\u8fde\u8fde\u55b7\u51fa\u9525\u5f62\u8679\u5149\u6765\uff0c\u6b63\u662f\u745e\u6069\u65af\u5766\u7684\u90a3\u4e2a\u53eb\u201c\u8ff7\u5e7b\u201d\u7684\u5c0f\u80d6\u5b50\u8ddf\u73ed\u3002</p><p>\u53ea\u662f\u867d\u7136\u58f0\u52bf\u51cc\u5389\uff0c\u8fde\u8fde\u55b7\u5410\uff0c\u4f46\u5374\u6ca1\u4e00\u53d1\u771f\u6b63\u51fb\u4e2d\u5bf9\u624b\u3002\u5728\u8ff7\u5e7b\u5468\u56f4\u7684\u7a7a\u6c14\u4e2d\uff0c\u65e0\u6570\u9053\u94f6\u8272\u7684\u4f20\u9001\u95e8\u63a5\u8fde\u5212\u5f00\uff0c\u4e00\u4e2a\u80a5\u80d6\u7684\u8eab\u5f71\u5728\u5176\u4e2d\u500f\u5ffd\u8fdb\u51fa\uff0c\u6e38\u8d70\u4e0d\u5b9a\u3002\u867d\u7136\u8ddd\u79bb\u5f88\u8fdc\uff0c\u4f46\u501f\u7740\u90a3\u6f2b\u5929\u8679\u5149\u7167\u8000\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u4f9d\u7136\u770b\u5f97\u6e05\u695a\uff0c\u6b63\u662f\u5965\u6c83\u90a3\u6b7b\u80d6\u5b50\u3002</p><p>\u5965\u6c83\u907f\u5f00\u51e0\u9053\u8679\u5149\u55b7\u5410\uff0c\u4f4e\u5578\u4e00\u58f0\uff0c\u9aa4\u7136\u53d1\u52a8\u53cd\u51fb\u3002\u5927\u5965\u672f\u5e08\u51fa\u624b\uff0c\u679c\u7136\u975e\u6bd4\u5bfb\u5e38\uff0c\u53ea\u89c1\u80a5\u5deb\u5996\u6325\u52a8\u6743\u6756\uff0c\u5239\u90a3\u95f4\u9634\u98ce\u9635\u9635\uff0c\u6570\u4ee5\u5343\u8ba1\u7684\u51a4\u9b42\u5389\u9b44\u81ea\u5730\u4e0b\u6d8c\u51fa\uff0c\u5316\u4f5c\u9053\u9053\u9ed1\u6c14\uff0c\u4e89\u5148\u6050\u540e\u671d\u8ff7\u5e7b\u4fb5\u8680\u8fc7\u53bb\uff1b\u53c8\u6709\u65e0\u6570\u5bd2\u5149\u68ee\u68ee\u7684\u767d\u9aa8\u957f\u77db\u81ea\u865a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u7eb7\u7eb7\u5982\u96e8\u671d\u8ff7\u5e7b\u6512\u5c04\u8f70\u51fb\uff1b\u4e00\u9053\u9053\u9ed1\u8272\u7130\u706b\u4ece\u5965\u6c83\u7684\u6307\u5c16\u8ff8\u5c04\u51fa\u6765\uff0c\u51fb\u5728\u8ff7\u5e7b\u8eab\u4e0a\uff0c\u8f70\u7136\u71c3\u8d77\u718a\u718a\u5927\u706b\uff0c\u5c06\u6574\u4e2a\u8679\u5f69\u9f99\u5305\u88f9\u4f4f\uff0c\u8fdc\u770b\u4eff\u4f5b\u4e00\u56e2\u5de8\u5927\u7684\u9ed1\u8272\u706b\u7403\u3002</p><p>\u8ff7\u5e7b\u663e\u7136\u5728\u9b54\u6cd5\u4e0a\u7684\u9020\u8be3\u5f88\u4f4e\uff0c\u5168\u9760\u722a\u7259\u4e4b\u529b\u548c\u5929\u8d4b\u7684\u8679\u5149\u5410\u606f\uff0c\u5982\u4eca\u649e\u4e0a\u5965\u6c83\u8fd9\u79cd\u5927\u5965\u672f\u5e08\uff0c\u6240\u6709\u7684\u653b\u51fb\u624b\u6bb5\u5168\u90fd\u5f92\u52b3\u65e0\u529f\uff0c\u53cd\u800c\u88ab\u94fa\u5929\u76d6\u5730\u800c\u6765\u7684\u9b54\u6cd5\u6df9\u6ca1\uff0c\u77ac\u95f4\u5c31\u843d\u4e86\u4e0b\u98ce\u3002\u603b\u7b97\u4ed6\u662f\u8679\u5f69\u9f99\uff0c\u8089\u4f53\u5f3a\u6a2a\uff0c\u53c8\u6709\u5929\u8d4b\u7684\u6297\u9b54\u4e4b\u80fd\uff0c\u5965\u6c83\u7684\u6cd5\u672f\u867d\u7136\u5a01\u529b\u5de8\u5927\uff0c\u4f46\u5374\u4e5f\u786c\u751f\u751f\u6297\u4e86\u4e0b\u6765\u3002\u53ea\u662f\u8fd9\u822c\u53ea\u6328\u6253\u4e0d\u80fd\u8fd8\u624b\uff0c\u7ec8\u7a76\u652f\u6301\u4e0d\u4e86\u591a\u4e45\u3002</p><p>\u60f3\u8d77\u5f53\u65e5\u70db\u5821\u7684\u60c5\u5f62\uff0c\u743c\u6069\u4e5f\u5c31\u660e\u767d\u600e\u4e48\u56de\u4e8b\u3002\u5f88\u663e\u7136\uff0c\u745e\u6069\u65af\u5766\u53c8\u6545\u4f0e\u91cd\u65bd\uff0c\u6d3e\u4ed6\u8fd9\u5c0f\u80d6\u5b50\u8ddf\u73ed\u53bb\u7f20\u4f4f\u5965\u6c83\uff0c\u81ea\u5df1\u5219\u6e9c\u8fdb\u53bb\u5077\u4e1c\u897f\uff0c\u53ea\u662f\u5982\u4eca\u90a3\u5bb6\u4f19\u4eba\u5728\u54ea\u91cc\uff1f</p><p>\u8fd9\u4e9b\u4e8b\u60c5\u4e14\u4e0d\u7ba1\uff0c\u65e2\u7136\u770b\u89c1\u4e86\u5965\u6c83\uff0c\u90a3\u4e48\u5f80\u76f8\u53cd\u7684\u65b9\u5411\u9003\u8dd1\u603b\u6ca1\u9519\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u6b63\u8981\u79bb\u5f00\u6b64\u5904\uff0c\u4e00\u9053\u8679\u5149\u81ea\u9ad8\u7a7a\u4e2d\u75be\u5c04\u4e0b\u6765\uff0c\u843d\u5230\u743c\u6069\u9762\u524d\uff0c\u6b63\u662f\u745e\u6069\u65af\u5766\uff0c\u80a9\u4e0a\u642d\u7740\u4e2a\u5927\u5f97\u60ca\u4eba\u7684\u53e3\u888b\uff0c\u770b\u6837\u5b50\u662f\u51c6\u5907\u5927\u635e\u4e00\u7b14\u3002</p><p>\u201c\u85cf\u5b9d\u5e93\u5728\u54ea\u91cc\uff1f\u201d\u4ed6\u6025\u5207\u5730\u95ee\u3002</p><p>\u743c\u6069\u6b63\u8981\u56de\u7b54\uff0c\u8fdc\u5904\u8ff7\u5e7b\u4f20\u6765\u4e00\u58f0\u60e8\u53eb\uff0c\u51e0\u652f\u767d\u9aa8\u957f\u77db\u7ec8\u4e8e\u7a7f\u900f\u4e86\u4ed6\u7684\u9cde\u7532\uff0c\u6df1\u6df1\u624e\u8fdb\u8eab\u4f53\u91cc\uff0c\u7c89\u7ea2\u8272\u7684\u9f99\u8840\u98d9\u5c04\u51fa\u6765\uff0c\u6d12\u843d\u4e00\u5730\u3002\u201c\u4f60\u90a3\u4e2a\u540c\u4f34\u5feb\u4e0d\u884c\u4e86\u3002\u201d\u743c\u6069\u63d0\u9192\u3002</p><p>\u745e\u6069\u65af\u5766\u56de\u5934\u77a5\u4e86\u773c\uff0c\u201c\u6ca1\u4e8b\uff0c\u8fd8\u80fd\u652f\u6301\u4e00\u4f1a\u3002\u4ed6\u522b\u7684\u672c\u4e8b\u6ca1\u6709\uff0c\u6297\u6253\u80fd\u529b\u4e00\u6d41\uff0c\u8ddf\u7740\u6211\u953b\u70bc\u51fa\u6765\u7684\u3002\u201d</p><p>\u4e3a\u4ec0\u4e48\u8ddf\u7740\u4f60\u4f1a\u953b\u70bc\u51fa\u4e00\u6d41\u7684\u6297\u6253\u80fd\u529b\u2026\u2026</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u5e2e\u4ed6\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u745e\u6069\u65af\u5766\u548c\u5965\u6c83\u8c01\u5f3a\u8c01\u5f31\uff0c\u4f46\u4e24\u53ea\u8679\u5f69\u9f99\u8054\u624b\uff0c\u603b\u5e94\u8be5\u80fd\u628a\u90a3\u6b7b\u80d6\u5b50\u7ed9\u8f70\u4e0b\u6765\u5427\u3002</p><p>\u201c\u6211\u662f\u6709\u539f\u5219\u6709\u54c1\u683c\u7684\u4eba\u554a\uff0c\u201d\u745e\u6069\u65af\u5766\u8bf4\uff0c\u201c\u6211\u53ea\u5077\uff0c\u4ece\u4e0d\u62a2\u7684\uff0c\u90a3\u6837\u592a\u6709\u5931\u98ce\u5ea6\uff0c\u6240\u4ee5\u548c\u4e3b\u4eba\u6253\u67b6\u7684\u4e8b\u60c5\u90fd\u8ba9\u4ed6\u53bb\uff0c\u6211\u4e0d\u63d2\u624b\u7684\u3002\u597d\u4e86\uff0c\u522b\u8bf4\u90a3\u4e48\u591a\uff0c\u544a\u8bc9\u6211\u85cf\u5b9d\u5e93\u5728\u54ea\uff1f\u201d</p><p>\u743c\u6069\u6307\u70b9\u4e86\u65b9\u5411\uff0c\u53c8\u628a\u90a3\u5f20\u5730\u4e0b\u5893\u7a74\u7684\u5730\u56fe\u4e5f\u987a\u624b\u585e\u7ed9\u4e86\u4ed6\uff0c\u8679\u5f69\u9f99\u4e8c\u8bdd\u4e0d\u8bf4\u5c31\u4ece\u521a\u624d\u743c\u6069\u98de\u4e0a\u6765\u7684\u90a3\u4e2a\u6d1e\u53e3\u8df3\u4e86\u4e0b\u53bb\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u62d4\u817f\u5c31\u8dd1\uff0c\u8fd8\u6ca1\u8dd1\u51fa\u591a\u8fdc\uff0c\u9661\u7136\u5730\u9762\u4e00\u9635\u5267\u70c8\u98a4\u52a8\uff0c\u4eff\u4f5b\u5730\u5e95\u4e0b\u53d1\u751f\u4e86\u4ec0\u4e48\u5927\u7206\u70b8\u4f3c\u7684\uff0c\u4e24\u4eba\u7acb\u8db3\u4e0d\u7a33\uff0c\u9669\u4e9b\u6454\u5012\uff0c\u6b63\u4e0d\u77e5\u9053\u53c8\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5f71\u7834\u571f\u800c\u51fa\uff0c\u5012\u649e\u51fa\u6765\uff0c\u6b63\u843d\u5728\u743c\u6069\u524d\u65b9\u3002</p><p>\u743c\u6069\u5b9a\u775b\u770b\u53bb\uff0c\u89c1\u662f\u90a3\u53ea\u8679\u5f69\u9f99\u745e\u6069\u65af\u5766\uff0c\u53ea\u662f\u624d\u8fc7\u4e86\u534a\u5206\u949f\u4e0d\u89c1\uff0c\u4ed6\u7684\u6a21\u6837\u5c31\u5b8c\u5168\u53d8\u4e86\uff0c\u5f04\u5f97\u72fc\u72c8\u4e0d\u582a\uff0c\u5168\u65e0\u5e73\u65f6\u90a3\u822c\u6f47\u6d12\u6c14\u5ea6\u3002\u6d01\u767d\u7684\u957f\u888d\u88ab\u6495\u5f00\u4e86\u65e0\u6570\u53e3\u5b50\uff0c\u7b80\u76f4\u53ef\u4ee5\u7a7f\u4e0a\u5192\u5145\u4e5e\u4e10\u4e86\uff1b\u6ee1\u5934\u91d1\u53d1\u88ab\u70e7\u6389\u4e86\u4e00\u534a\uff0c\u770b\u8d77\u6765\u5341\u5206\u6ed1\u7a3d\uff1b\u4ed6\u7684\u8138\u3001\u624b\u80cc\u7b49\u66b4\u9732\u5728\u5916\u7684\u76ae\u80a4\u4e0a\u5168\u90fd\u4f24\u75d5\u7d2f\u7d2f\uff0c\u6d41\u8840\u4e0d\u6b62\uff0c\u770b\u8d77\u6765\u662f\u5403\u4e86\u5927\u4e8f\uff0c\u80cc\u4e0a\u7684\u53e3\u888b\u4e5f\u4e0d\u77e5\u4e22\u5230\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u743c\u6069\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u4ed6\u662f\u77e5\u9053\u8fd9\u8679\u5f69\u9f99\u7684\u5b9e\u529b\u7684\uff0c\u80fd\u95ef\u8fdb\u9634\u9b42\u57ce\u53c8\u5168\u8eab\u800c\u9000\uff0c\u80fd\u6495\u5f00\u70db\u5821\u7684\u8ff7\u9501\uff0c\u8fd9\u7edd\u5bf9\u5df2\u7ecf\u7b97\u662f\u7b2c\u4e00\u7b49\u7684\u4eba\u7269\u4e86\u3002\u5982\u4eca\u5965\u6c83\u4e00\u76f4\u5728\u88ab\u8ff7\u5e7b\u7f20\u7740\uff0c\u8fd9\u8679\u5f69\u9f99\u5374\u53c8\u662f\u906d\u9047\u4e86\u4ec0\u4e48\u5f3a\u654c\uff1f\u80fd\u7247\u523b\u4e4b\u95f4\u5c31\u628a\u4ed6\u4f24\u6210\u8fd9\u6837\u3002</p><p>\u201c\u6d3b\u5316\u7a96\uff08Living Vault\uff09\uff01\u201d\u745e\u6069\u65af\u5766\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u8fd9\u6b7b\u80d6\u5b50\u5c45\u7136\u5f04\u4e86\u4e2a\u6d3b\u5316\u7a96\u5f53\u85cf\u5b9d\u5e93\uff0c\u5dee\u70b9\u628a\u6211\u7ed9\u541e\u4e86\u4e0b\u53bb\u3002\u8981\u4e0d\u662f\u53cd\u5e94\u5f97\u5feb\u2026\u2026\u201d</p><p>\u6d3b\u5316\u7a96\u662f\u4f20\u8bf4\u4e2d\u7684\u5b58\u5728\uff0c\u5deb\u5e08\u7528\u6765\u4fdd\u62a4\u5b9d\u85cf\u7684\u6700\u5f3a\u624b\u6bb5\uff0c\u7528\u4e0d\u53ef\u601d\u8bae\u7684\u9b54\u6cd5\u5c06\u6574\u4e2a\u201c\u623f\u95f4\u201d\u5f7b\u5e95\u6d3b\u5316\uff0c\u8d4b\u4e88\u610f\u8bc6\u548c\u7075\u89c9\uff0c\u5e76\u9644\u4e0a\u79cd\u79cd\u5f3a\u5927\u9b54\u6cd5\u5728\u5185\uff0c\u9632\u6b62\u5916\u654c\u5165\u4fb5\u3002\u53ea\u662f\u8fd9\u79cd\u505a\u6cd5\u7b49\u82e5\u662f\u5c06\u6b7b\u7269\u5316\u4e3a\u6d3b\u7269\uff0c\u5df2\u7ecf\u8fd1\u4e4e\u9020\u7269\u4e4b\u80fd\u4e86\uff0c\u4ece\u6765\u53ea\u662f\u4f20\u8bf4\uff0c\u5e76\u6ca1\u6709\u4eba\u771f\u6b63\u89c1\u8bc6\u8fc7\u3002\u5965\u6c83\u7cbe\u7814\u4ea1\u7075\u672f\uff0c\u6c89\u8ff7\u4e8e\u751f\u6b7b\u8f6c\u5316\u7684\u8bd5\u9a8c\uff0c\u6ca1\u60f3\u5230\u771f\u5f04\u51fa\u4e86\u6d3b\u5316\u7a96\u8fd9\u79cd\u4e1c\u897f\u3002</p><p>\u745e\u6069\u65af\u5766\u662f\u8679\u5f69\u9f99\uff0c\u5929\u8d4b\u8eab\u4f53\u5f3a\u6a2a\uff0c\u9b54\u6cd5\u4e0a\u7684\u9020\u8be3\u53c8\u9ad8\uff0c\u5e73\u5e38\u7684\u85cf\u5b9d\u5e93\uff0c\u65e0\u8bba\u518d\u591a\u7684\u673a\u5173\u9677\u9631\uff0c\u4ed6\u4e5f\u4e0d\u5f53\u56de\u4e8b\uff0c\u53ea\u7ba1\u786c\u95ef\u3002\u7136\u800c\u8fd9\u6b21\u731d\u4e0d\u53ca\u9632\uff0c\u649e\u4e0a\u4e86\u6d3b\u5316\u7a96\u8fd9\u79cd\u4f20\u8bf4\u4e2d\u7684\u602a\u7269\uff0c\u4e00\u65f6\u5927\u610f\uff0c\u9669\u4e9b\u5c31\u88ab\u541e\u4e86\u4e0b\u53bb\u3002\u81f3\u4e8e\u90a3\u4e9b\u5b9d\u7269\uff0c\u81ea\u7136\u662f\u4e00\u4ef6\u90fd\u6ca1\u5230\u624b\u3002</p><p>\u5174\u51b2\u51b2\u800c\u6765\uff0c\u5374\u649e\u4e0a\u8fd9\u7b49\u610f\u5916\uff0c\u745e\u6069\u65af\u5766\u81ea\u7136\u607c\u6012\u975e\u5e38\u3002\u201c\u8fd9\u6b7b\u80d6\u5b50\u6b3a\u4eba\u592a\u751a\uff0c\u201d\u4ed6\u4e00\u8fb9\u7ed9\u81ea\u5df1\u6cbb\u7597\u4e00\u8fb9\u54ac\u7259\u5207\u9f7f\uff0c\u201c\u4eca\u5929\u6211\u4e0d\u628a\u4ed6\u6253\u6210\u8089\u997c\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u201d</p><p>\u201c\u4f60\u8bf4\u4f60\u53ea\u5077\u4e0d\u62a2\uff0c\u4e0d\u548c\u4e3b\u4eba\u6253\u67b6\u7684\u3002\u201d\u743c\u6069\u63d0\u9192\u4ed6\u3002</p><p>\u201c\u4f60\u6ca1\u770b\u89c1\u6211\u4ec0\u4e48\u90fd\u6ca1\u5077\u5230\u4e48\uff0c\u201d\u745e\u6069\u65af\u5766\u6124\u6012\uff0c\u201c\u6211\u73b0\u5728\u6539\u4e3b\u610f\u4e86\uff0c\u4eca\u5929\u4e0d\u5077\u4e1c\u897f\uff0c\u4e13\u95e8\u6765\u6559\u8bad\u8fd9\u6b7b\u80d6\u5b50\u3002\u201d</p><p>\u5728\u6cbb\u7597\u9b54\u6cd5\u7684\u4f5c\u7528\u4e0b\uff0c\u4ed6\u7684\u4f24\u52bf\u6062\u590d\u5f97\u5f88\u5feb\uff0c\u8138\u4e0a\u5df2\u7ecf\u770b\u4e0d\u51fa\u660e\u663e\u7684\u4f24\u53e3\u4e86\u3002\u743c\u6069\u6ca1\u5174\u8da3\u88ab\u5377\u8fdb\u8679\u5f69\u9f99\u548c\u80a5\u5deb\u5996\u7684\u51b3\u6597\u4e2d\uff0c\u53ea\u60f3\u8d76\u5feb\u9003\u8dd1\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u90a3\u8fb9\u8ff7\u5e7b\u53c8\u5927\u53eb\u4e00\u58f0\uff0c\u5965\u6c83\u4ece\u865a\u7a7a\u4e2d\u62db\u6765\u4e00\u53ea\u5de8\u5927\u7684\u767d\u9aa8\u5229\u722a\uff0c\u4e00\u628a\u627c\u4f4f\u4e86\u8679\u5f69\u9f99\u7684\u54bd\u5589\uff0c\u82e5\u4e0d\u662f\u6323\u8131\u5f97\u5feb\uff0c\u5dee\u70b9\u628a\u8116\u5b50\u90fd\u7ed9\u626d\u65ad\u4e0b\u6765\uff0c\u5f62\u52bf\u662f\u8d8a\u6765\u8d8a\u5371\u6025\u4e86\uff0c\u518d\u8fd9\u6837\u4e0b\u53bb\uff0c\u652f\u6491\u4e0d\u4e86\u7247\u523b\u5c31\u8981\u4e27\u547d\u3002</p><p>\u745e\u6069\u65af\u5766\u4e5f\u770b\u5230\u4e86\u60c5\u5f62\u6025\u8feb\uff0c\u201c\u4f60\u4eec\u5f80\u5317\u8dd1\uff0c\u201d\u4ed6\u5bf9\u743c\u6069\u548c\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u770b\u89c1\u90a3\u7247\u6811\u6797\u6ca1\u6709\uff0c\u90a3\u91cc\u5df2\u7ecf\u662f\u7956\u7236\u4e4b\u6811\u7684\u5b88\u62a4\u8303\u56f4\uff0c\u8dd1\u8fdb\u53bb\u5c31\u6ca1\u4e8b\uff0c\u5965\u6c83\u4e0d\u6562\u8fdb\u53bb\u3002\u201d</p><p>\u4ed6\u987e\u4e0d\u5f97\u518d\u591a\u8bf4\uff0c\u817e\u8eab\u800c\u8d77\uff0c\u5f53\u7a7a\u73b0\u51fa\u8679\u5f69\u9f99\u539f\u5f62\uff0c\u671d\u5965\u6c83\u731b\u6251\u8fc7\u53bb\u3002\u743c\u6069\u5f80\u5317\u65b9\u770b\u53bb\uff0c\u679c\u7136\u53d1\u73b0\u4e00\u7247\u6811\u6797\uff0c\u5176\u4e2d\u95ea\u70c1\u7740\u65e0\u6570\u6676\u83b9\u5149\u70b9\uff0c\u4eff\u4f5b\u8424\u706b\u866b\u6f2b\u6e38\uff0c\u5728\u8fd9\u6df1\u591c\u4e2d\u663e\u5f97\u683c\u5916\u6f02\u4eae\u3002\u4ed6\u62c9\u7740\u6885\u83f2\u65af\uff0c\u4e24\u4eba\u5934\u4e5f\u4e0d\u56de\u5730\u671d\u90a3\u8fb9\u8dd1\u53bb\u3002</p><p>\u521a\u8dd1\u8fdb\u6811\u6797\uff0c\u5c31\u542c\u5f97\u80cc\u540e\u8f70\u7136\u5de8\u54cd\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u89c1\u8ff7\u5e7b\u5de8\u5927\u7684\u8679\u5f69\u9f99\u8eab\u8eaf\u4ece\u9ad8\u7a7a\u4e2d\u6454\u4e86\u4e0b\u6765\uff0c\u5c06\u5730\u9762\u751f\u751f\u7838\u51fa\u4e86\u4e00\u4e2a\u5927\u5751\u3002\u745e\u6069\u65af\u5766\u6012\u4e0d\u53ef\u904f\uff0c\u53e3\u4e2d\u5410\u606f\uff0c\u9f99\u722a\u8fde\u6325\uff0c\u7f16\u7ec7\u6210\u5404\u79cd\u5f3a\u5927\u5492\u6cd5\u671d\u5bf9\u9762\u90a3\u6b7b\u80d6\u5b50\u8f70\u53bb\u3002\u5965\u6c83\u8fde\u8fde\u540e\u9000\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u770b\u6765\u5df2\u7ecf\u843d\u4e86\u4e0b\u98ce\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u745e\u6069\u65af\u5766\u8eab\u540e\u7684\u865a\u7a7a\u4e2d\uff0c\u94f6\u8272\u4f20\u9001\u95e8\u9759\u6084\u6084\u5212\u5f00\uff0c\u4e24\u4e2a\u8eab\u6750\u957f\u76f8\u4e00\u6a21\u4e00\u6837\u7684\u9ed1\u53d1\u5deb\u5e08\u5e76\u80a9\u8d70\u51fa\uff0c\u4ed6\u4eec\u540c\u65f6\u9ad8\u4e3e\u624b\u4e2d\u6cd5\u6756\uff0c\u5bf9\u51c6\u8679\u5f69\u9f99\u4e00\u6307\u3002</p></article>"}}}]);
