(window.webpackJsonp=window.webpackJsonp||[]).push([[139],{161:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u756a\u5929\u5370",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u756a\u5929\u5370</h2><p>\u8fd8\u6709\u4e00\u53e5\u8bdd\uff0c\u4ed6\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u8150\u56ca\u7206\u88c2\u4e5f\u597d\uff0c\u8e42\u8e8f\u5185\u810f\u4e5f\u597d\uff0c\u786e\u5b9e\u662f\u4e5f\u53ef\u4ee5\u9020\u6210\u8fd9\u79cd\u7c7b\u4f3c\u7684\u6548\u679c\uff0c\u4f46\u95ee\u9898\u662f\u8fd9\u90fd\u662f\u4ea1\u7075\u5b66\u6d3e\u7684\u6cd5\u672f\uff0c\u4ed6\u538b\u6839\u5c31\u4e0d\u4f1a\u3002\u5f53\u7136\u8fd9\u79cd\u8bdd\u6ca1\u5fc5\u8981\u8ddf\u5bf9\u65b9\u8bf4\uff0c\u4e0d\u662f\u81ea\u5df1\u66b4\u9732\u81ea\u5df1\u7684\u5f31\u70b9\u4e48\uff0c\u5c31\u7b97\u5bf9\u65b9\u662f\u4e2a\u5feb\u8981\u6b7b\u7684\u4eba\uff0c\u8fd9\u79cd\u7b28\u86cb\u4e8b\u60c5\u4e5f\u4e0d\u80fd\u5e72\u3002</p><p>\u5176\u5b9e\u4ed6\u521a\u624d\u867d\u7136\u5ff5\u4e86\u4e2a\u8bcd\uff0c\u4f46\u538b\u6839\u5c31\u4e0d\u662f\u4ec0\u4e48\u6cd5\u672f\u3002\u771f\u6b63\u7684\u6cd5\u672f\uff0c\u65e9\u5c31\u5728\u6628\u665a\u5c31\u5df2\u7ecf\u65bd\u51fa\u6765\u4e86\uff0c\u7f29\u7269\u672f\uff0c\u53d8\u5316\u5b66\u6d3e\u7684\u4e00\u4e2a\u4f4e\u9636\u6cd5\u672f\uff0c\u53ea\u8981\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u4e09\u5c42\u5c31\u80fd\u529e\u5230\u3002\u743c\u6069\u8fd8\u6ca1\u4ece\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\u6bd5\u4e1a\u65f6\u5c31\u5df2\u7ecf\u5b66\u4f1a\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u524d\u51e0\u5929\u5df2\u7ecf\u5f00\u59cb\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\u4e86\u3002</p><p>\u6839\u636e\u5deb\u5e08\u7684\u80fd\u529b\u9ad8\u4f4e\u4e0d\u540c\uff0c\u4f7f\u7528\u7f29\u7269\u672f\u80fd\u628a\u4e00\u5b9a\u5927\u5c0f\u7684\u975e\u9b54\u6cd5\u7269\u54c1\u7684\u4f53\u79ef\u548c\u8d28\u91cf\u90fd\u7f29\u5c0f\u5230\u539f\u6709\u7684\u56db\u5343\u5206\u4e4b\u4e00\u3002\u8fd9\u79cd\u6cd5\u672f\u88ab\u53d1\u660e\u51fa\u6765\uff0c\u6700\u5f00\u59cb\u7684\u7528\u610f\u662f\u4e3a\u4e86\u8ba9\u51fa\u95e8\u8fdc\u884c\u7684\u5deb\u5e08\u643a\u5e26\u4e1c\u897f\u65b9\u4fbf\u3002\u5deb\u5e08\u4e00\u822c\u8eab\u4f53\u90fd\u4e0d\u592a\u597d\uff0c\u51fa\u95e8\u8fdc\u884c\u5982\u679c\u96c7\u4e0d\u8d77\u9a6c\u8f66\u7684\u8bdd\uff0c\u8fde\u5e72\u7cae\u90fd\u80cc\u4e0d\u52a8\uff0c\u66f4\u522b\u8bf4\u6709\u65f6\u8fd8\u8981\u643a\u5e26\u5176\u4ed6\u4e1c\u897f\uff0c\u6709\u4e86\u7f29\u7269\u672f\uff0c\u4e00\u5207\u5c31\u4e0d\u6210\u95ee\u9898\u3002\u8fd9\u79cd\u6cd5\u672f\u6301\u7eed\u65f6\u95f4\u5f88\u957f\uff0c\u4ee5\u743c\u6069\u76ee\u524d\u7684\u9020\u8be3\uff0c\u53ea\u8981\u81ea\u5df1\u4e0d\u4e3b\u52a8\u89e3\u9664\uff0c\u7f29\u7269\u72b6\u6001\u5927\u6982\u53ef\u4ee5\u4e00\u76f4\u6301\u7eed\u4e03\u5929\u3002</p><p>\u5728\u6628\u5929\u665a\u4e0a\uff0c\u743c\u6069\u60f3\u51fa\u4e86\u4e00\u4e2a\u9634\u6bd2\u7684\u4e3b\u610f\uff0c\u4ed6\u5f53\u65f6\u4ece\u4eba\u9762\u72ee\u7684\u5b9d\u5e93\u91cc\u641c\u522e\u4e86\u4e00\u6279\u8d22\u5b9d\uff0c\u5176\u4e2d\u4e0d\u4e4f\u4e00\u4e9b\u91d1\u5757\u3002\u743c\u6069\u6311\u4e86\u4e00\u5757\uff0c\u7528\u7f29\u7269\u672f\u628a\u5b83\u7f29\u5c0f\u5230\u56db\u5343\u5206\u4e4b\u4e00\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u8089\u773c\u51e0\u4e4e\u90fd\u770b\u4e0d\u89c1\u7684\u91d1\u5c51\uff0c\u5077\u5077\u6253\u5f00\u6b66\u50e7\u7684\u5305\u88f9\uff0c\u653e\u8fdb\u4ed6\u7684\u9762\u5305\u91cc\u3002</p><p>\u6b66\u50e7\u662f\u4fee\u884c\u8005\uff0c\u62e5\u6709\u975e\u5e38\u826f\u597d\u7684\u751f\u6d3b\u4e60\u60ef\u3002\u8fd9\u4e9b\u5929\u6765\u7684\u89c2\u5bdf\uff0c\u743c\u6069\u77e5\u9053\u4ed6\u6bcf\u5929\u5b9a\u65f6\u5403\u996d\uff0c\u5b9a\u65f6\u7761\u89c9\uff0c\u5b9a\u65f6\u6392\u6cc4\u2014\u2014\u4ed6\u6bcf\u5929\u65e9\u4e0a\u8d77\u5e8a\u4e0a\u5395\u6240\uff0c\u7136\u540e\u5403\u65e9\u9910\uff0c\u5403\u559d\u62c9\u6492\u6781\u6709\u89c4\u5f8b\u3002\u8fd9\u662f\u4ef6\u597d\u4e8b\u60c5\uff0c\u8bf4\u660e\u8eab\u4f53\u5f88\u5065\u5eb7\u3002</p><p>\u53e6\u5916\uff0c\u6b66\u50e7\u8fc7\u60ef\u4e86\u82e6\u4fee\u7684\u65e5\u5b50\uff0c\u6240\u4ee5\u4ed6\u4eec\u5bf9\u996e\u98df\u5f88\u4e0d\u6311\u5254\u3002\u9686\u5947\u867d\u7136\u5815\u843d\u4e86\uff0c\u4f1a\u82b1\u5929\u9152\u5730\uff0c\u4f46\u90a3\u662f\u5728\u535a\u5f97\u4e4b\u95e8\u7684\u65f6\u5019\uff0c\u5728\u6709\u6761\u4ef6\u7684\u60c5\u51b5\u4e0b\uff1b\u5982\u4eca\u8352\u90ca\u91ce\u5916\uff0c\u5927\u5bb6\u53ea\u80fd\u5403\u9762\u5305\u5939\u725b\u8089\uff0c\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u610f\u89c1\u3002\u800c\u4e14\u591a\u5e74\u7684\u4e60\u60ef\u517b\u6210\uff0c\u4ed6\u51b3\u4e0d\u4f1a\u6d6a\u8d39\u98df\u7269\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u90a3\u679a\u7ec6\u5c0f\u5f97\u8089\u773c\u51e0\u4e4e\u770b\u4e0d\u89c1\u7684\u91d1\u5c51\uff0c\u57cb\u5728\u9762\u5305\u91cc\uff0c\u662f\u80af\u5b9a\u4f1a\u88ab\u9686\u5947\u5403\u8fdb\u809a\u5b50\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\u4e00\u5207\u987a\u5229\u3002</p><p>\u53ea\u8981\u9686\u5947\u628a\u90a3\u679a\u91d1\u5c51\u5403\u4e0b\u4e86\u809a\u5b50\uff0c\u743c\u6069\u5c31\u7b49\u4e8e\u5b8c\u5168\u638c\u63e1\u4e86\u4ed6\u7684\u547d\u8109\u3002\u5728\u660e\u5929\u65e9\u6668\u4e4b\u524d\u2014\u2014\u4e5f\u5c31\u662f\u9686\u5947\u4e0a\u5395\u6240\u628a\u90a3\u679a\u91d1\u5c51\u62c9\u51fa\u6765\u4e4b\u524d\uff0c\u743c\u6069\u968f\u65f6\u53ef\u4ee5\u7528\u4e00\u4e2a\u53e3\u4ee4\u53d6\u6d88\u7f29\u7269\u72b6\u6001\uff0c\u8ba9\u91d1\u5c51\u5267\u589e\u56db\u5343\u500d\uff0c\u8fd8\u539f\u6210\u91d1\u5757\u3002</p><p>\u6b66\u50e7\u786e\u5b9e\u6709\u8d85\u5f3a\u7684\u6297\u9b54\u80fd\u529b\uff0c\u4f46\u743c\u6069\u538b\u6839\u5c31\u4e0d\u7528\u9b54\u6cd5\u76f4\u63a5\u653b\u51fb\uff1b\u6b66\u50e7\u540c\u6837\u6709\u767e\u6bd2\u4e0d\u4fb5\u7684\u672c\u4e8b\uff0c\u4f46\u743c\u6069\u538b\u6839\u5c31\u4e0d\u4e0b\u6bd2\u3002</p><p>\u743c\u6069\u552f\u4e00\u6240\u505a\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u8ba9\u67d0\u4e2a\u88ab\u7f29\u5c0f\u4e86\u56db\u5343\u500d\u7684\u91d1\u5757\u8fd8\u539f\u800c\u5df2\u2014\u2014\u800c\u8fd9\u679a\u91d1\u5757\u6070\u6070\u6b63\u597d\u5728\u9686\u5947\u7684\u80a0\u80c3\u91cc\u3002</p><p>\u6b66\u50e7\u5c31\u7b97\u953b\u70bc\u6210\u4e86\u94dc\u76ae\u94c1\u9aa8\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u6c34\u706b\u4e0d\u4f24\uff0c\u4ed6\u541e\u4e86\u4e2a\u5982\u6b64\u5927\u91d1\u5757\uff0c\u96be\u4e0d\u6210\u8fd8\u4e0d\u6b7b\uff1f\u5982\u679c\u8fd9\u90fd\u4e0d\u6b7b\uff0c\u8fd9\u4e16\u754c\u4e0a\u5c31\u6ca1\u6709\u541e\u91d1\u81ea\u6740\u8fd9\u56de\u4e8b\u4e86\u3002\u666e\u901a\u4eba\u541e\u91d1\u81ea\u6740\uff0c\u54bd\u5589\u80fd\u541e\u4e0b\u591a\u5927\u7684\u91d1\u5757\uff0c\u5df2\u7ecf\u5c31\u53ef\u4ee5\u81f4\u547d\uff0c\u4f55\u51b5\u8fd9\u6b21\u743c\u6069\u7279\u5730\u6311\u9009\u4e86\u4e2a\u5927\u53f7\u7684\u3002\u4ed6\u7684\u4eba\u4f53\u751f\u7406\u5b66\u5b66\u5f97\u4e0d\u592a\u597d\uff0c\u4e5f\u4e0d\u77e5\u9053\u5982\u4eca\u8fd9\u91d1\u5757\u5927\u6982\u662f\u5728\u9686\u5947\u7684\u80c3\u91cc\u8fd8\u662f\u80a0\u9053\u91cc\uff0c\u4f46\u53cd\u6b63\u90fd\u4e00\u6837\u3002\u5982\u679c\u5728\u80c3\u91cc\uff0c\u4fdd\u7ba1\u628a\u80c3\u538b\u7a7f\uff0c\u5982\u679c\u5728\u80a0\u9053\u91cc\uff0c\u90a3\u4fdd\u7ba1\u80a0\u5b50\u65ad\u88c2\uff0c\u4e0d\u4fe1\u4ed6\u4e0d\u6b7b\u3002</p><p>\u53ea\u662f\u6ca1\u60f3\u5230\u6b66\u50e7\u7684\u8eab\u4f53\u679c\u7136\u5f3a\u58ee\uff0c\u8fd9\u6837\u90fd\u8fd8\u80fd\u6491\u7740\u4e0d\u65ad\u6c14\uff0c\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u770b\u4ed6\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u5df2\u7ecf\u5b8c\u5168\u4e27\u5931\u6218\u6597\u529b\u4e86\uff0c\u6b7b\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\u2026\u2026\u5514\uff0c\u60f3\u8d77\u6765\u4e86\uff0c\u4ed6\u521a\u521a\u80cc\u5f03\u4e86\u4f0a\u5c14\u739b\u7279\u7684\u4fe1\u4ef0\uff0c\u542c\u8bf4\u8fd8\u6ca1\u6709\u5bfb\u627e\u7684\u65b0\u7684\u7688\u4f9d\uff0c\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u4ed6\u662f\u4e2a\u65e0\u4fe1\u8005\u3002</p><p>\u542c\u8bf4\u5728\u8fd9\u4e2a\u795e\u7947\u4f17\u591a\uff0c\u4fe1\u4ef0\u7a7a\u524d\u53d1\u8fbe\u7684\u4e16\u754c\u91cc\uff0c\u65e0\u4fe1\u8005\u6b7b\u540e\u4e0b\u573a\u662f\u8d85\u7ea7\u51c4\u60e8\u7684\uff0c\u4ed6\u4eec\u7684\u7075\u9b42\u4f1a\u88ab\u9489\u5728\u4e00\u9762\u201c\u65e0\u4fe1\u8005\u4e4b\u5899\u201d\u4e0a\uff0c\u5929\u5929\u5fcd\u53d7\u96be\u4ee5\u60f3\u50cf\u7684\u75db\u82e6\u548c\u6298\u78e8\u3002</p><p>\u743c\u6069\u5bf9\u6b66\u50e7\u7684\u8fd9\u79cd\u547d\u8fd0\u6df1\u8868\u540c\u60c5\uff0c\u4e0d\u8fc7\u8fd9\u5e76\u4e0d\u59a8\u788d\u4ed6\u51c6\u5907\u843d\u4e95\u4e0b\u77f3\u3002\u4ed6\u4ece\u9774\u5b50\u91cc\u62d4\u51fa\u90a3\u67c4\u7cbe\u91d1\u5315\u9996\u6765\uff0c\u51c6\u5907\u5272\u65ad\u6b66\u50e7\u7684\u54bd\u5589\u3002\u8fd9\u67c4\u5315\u9996\u4e0a\u9644\u7740\u4e86\u4e00\u4e2a\u56de\u8fd4\u9b54\u6cd5\uff0c\u80fd\u591f\u7528\u6765\u5173\u952e\u65f6\u523b\u4fdd\u547d\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u5b83\u4e5f\u662f\u4e00\u67c4\u975e\u5e38\u950b\u9510\u7684\u6740\u4eba\u5229\u5668\u3002</p><p>\u90a3\u8fb9\u6c83\u91d1\u7267\u5e08\u5df2\u7ecf\u88ab\u6885\u83f2\u65af\u7684\u51cc\u5389\u653b\u51fb\u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u743c\u6069\u6253\u7b97\u8d76\u7d27\u628a\u8fd9\u4e2a\u8fd8\u5269\u4e00\u53e3\u6c14\u7684\u6b66\u50e7\u89e3\u51b3\u6389\uff0c\u8fc7\u53bb\u5e2e\u5fd9\u3002\u4e0d\u77e5\u9053\u90a3\u4e2a\u5973\u6740\u624b\u600e\u4e48\u8fd8\u4e0d\u51fa\u73b0\uff0c\u96be\u9053\u8bf4\u53d1\u73b0\u4e8b\u60c5\u8d25\u9732\uff0c\u6210\u529f\u65e0\u671b\uff0c\u7d22\u6027\u9041\u8d70\u4e86\uff1f</p><p>\u743c\u6069\u4e00\u624b\u63e1\u7740\u5315\u9996\uff0c\u4e00\u624b\u5feb\u901f\u53d6\u6750\u6599\u65bd\u6cd5\uff0c\u5148\u7ed9\u81ea\u5df1\u52a0\u4e0a\u4e00\u9053\u77f3\u80a4\u672f\uff0c\u8fd9\u662f\u4ed6\u65b0\u5b66\u4f1a\u7684\u6cd5\u672f\uff0c\u975e\u5e38\u6709\u7528\u7684\u9632\u62a4\u9b54\u6cd5\uff0c\u5c31\u50cf\u662f\u5728\u8eab\u4e0a\u9644\u7740\u4e00\u5c42\u77f3\u5934\u76ae\u80a4\uff0c\u4e0d\u4f1a\u59a8\u788d\u884c\u52a8\uff0c\u5374\u80fd\u6709\u6548\u5730\u6682\u65f6\u62b5\u5fa1\u653b\u51fb\u3002\u5f53\u6cd5\u672f\u5b8c\u6210\uff0c\u4ed6\u7684\u5168\u8eab\u76ae\u80a4\u4e0a\u90fd\u4eff\u4f5b\u7b3c\u7f69\u4e86\u4e00\u5c42\u5927\u7406\u77f3\u5fae\u5149\uff0c\u743c\u6069\u63e1\u7740\u5315\u9996\uff0c\u671d\u6b66\u50e7\u8d70\u53bb\uff0c\u5bf9\u65b9\u6b63\u75bc\u5f97\u6ee1\u5730\u6253\u6eda\uff0c\u770b\u6837\u5b50\u5230\u771f\u548c\u6025\u6027\u70c2\u5c3e\u2026\u2026\u9519\u4e86\uff0c\u9611\u5c3e\u708e\u7684\u75c7\u72b6\u6709\u4e9b\u76f8\u4f3c\u3002</p><p>\u770b\uff0c\u8fd9\u5c31\u662f\u6028\u5ff5\u7684\u53d1\u4f5c\u3002</p><p>\u743c\u6069\u8111\u4e2d\u83ab\u540d\u5176\u5999\u5730\u5192\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u6014\u4e86\u4e00\u4e0b\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u6765\u7684\u3002\u4f46\u968f\u5373\u4ed6\u6447\u6447\u5934\uff0c\u6452\u5f03\u4e86\u8fd9\u4e2a\u53e4\u602a\u5ff5\u5934\u3002\u8d76\u5feb\u628a\u8fd9\u4e2a\u6b66\u50e7\u89e3\u51b3\u6389\uff0c\u514d\u5f97\u591c\u957f\u68a6\u591a\uff0c\u53c8\u751f\u4ec0\u4e48\u53d8\u6545\u3002</p><p>\u6b63\u5f53\u743c\u6069\u51c6\u5907\u52a8\u624b\u7684\u65f6\u5019\uff0c\u9661\u7136\u4e00\u9635\u5bd2\u6c14\u4ece\u80cc\u540e\u88ad\u6765\uff0c\u4ed6\u7684\u5168\u8eab\u6c57\u6bdb\u90fd\u672c\u80fd\u5730\u7ad6\u8d77\u3002\u5deb\u5e08\u60ca\u9a87\u4e4b\u4e0b\uff0c\u5fc3\u5ff5\u6025\u8f6c\uff0c\u9f99\u9cde\u76fe\u81ea\u884c\u56de\u65cb\u5230\u80cc\u540e\uff0c\u8981\u66ff\u4ed6\u6321\u4f4f\u653b\u51fb\u3002</p><p>\u4f46\u5bf9\u65b9\u7ed5\u8fc7\u4e86\u9f99\u9cde\u76fe\uff0c\u743c\u6069\u53ea\u77a5\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u9053\u5bd2\u5149\u4fbf\u4ece\u4fa7\u9762\u5c04\u6765\u3002\u4ed6\u614c\u5fd9\u4e3e\u5315\u9996\u4e00\u683c\uff0c\u5374\u67b6\u4e86\u4e2a\u7a7a\u3002\u5c16\u9510\u7684\u5229\u5668\u91cd\u91cd\u5730\u523a\u5728\u4ed6\u80f8\u53e3\u4e0a\uff0c\u5e78\u597d\u6709\u77f3\u80a4\u963b\u9694\uff0c\u6ca1\u6709\u523a\u900f\u8fdb\u6765\uff0c\u4f46\u4e5f\u649e\u5f97\u4ed6\u808b\u9aa8\u5267\u75db\uff0c\u51e0\u4e4e\u4e00\u53e3\u6c14\u63d0\u4e0d\u4e0a\u6765\u3002\u773c\u5149\u4e00\u626b\uff0c\u5df2\u7ecf\u770b\u5f97\u6e05\u695a\uff0c\u6765\u88ad\u8005\u6b63\u662f\u5973\u6740\u624b\u838e\u73de\u514b\uff0c\u5927\u6982\u662f\u89c1\u9686\u5947\u7740\u4e86\u743c\u6069\u7684\u9053\uff0c\u4e00\u7167\u9762\u5c31\u88ab\u5e9f\u4e86\uff0c\u4e0d\u5f97\u4e0d\u4eb2\u81ea\u51fa\u624b\u3002</p><p>\u5973\u6740\u624b\u89c1\u743c\u6069\u6709\u77f3\u80a4\u62a4\u4f53\uff0c\u77ed\u5251\u523a\u4e0d\u8fdb\u53bb\uff0c\u968f\u5373\u7ffb\u8f6c\u624b\u8155\uff0c\u4e00\u62f3\u6253\u5728\u743c\u6069\u8138\u4e0a\u3002\u77f3\u80a4\u80fd\u6321\u5200\u5251\u523a\u51fb\uff0c\u5374\u4e0d\u80fd\u963b\u9694\u529b\u9053\uff0c\u4ed6\u7684\u53cd\u5e94\u53c8\u8ddf\u4e0d\u4e0a\u6740\u624b\u7684\u51fa\u624b\u901f\u5ea6\uff0c\u8fd9\u4e00\u62f3\u5c31\u6ca1\u80fd\u907f\u5f00\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u6328\u4e0a\u4e86\uff0c\u767b\u65f6\u88ab\u6253\u5f97\u773c\u5192\u91d1\u661f\uff0c\u8e09\u8dc4\u8dcc\u4e86\u51fa\u53bb\uff0c\u4e00\u65f6\u6709\u4e9b\u5929\u65cb\u5730\u8f6c\uff0c\u4e0d\u77e5\u9053\u8eab\u5904\u4f55\u5730\uff0c\u8fde\u624b\u4e0a\u7684\u5315\u9996\u90fd\u5728\u65e0\u610f\u8bc6\u95f4\u8131\u624b\u6389\u843d\uff0c\u4e0d\u77e5\u6454\u5230\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u838e\u73de\u514b\u6b63\u8981\u8ffd\u88ad\uff0c\u547c\u5730\u4fa7\u9762\u4e00\u9053\u94f6\u5149\u7834\u7a7a\u5288\u6765\uff0c\u6b63\u671d\u81ea\u5df1\u7684\u8116\u9888\u65a9\u53bb\u3002\u6740\u624b\u786c\u751f\u751f\u987f\u4f4f\u811a\u6b65\uff0c\u4e24\u628a\u77ed\u5251\u4ea4\u53c9\u4e00\u683c\uff0c\u62b5\u4f4f\u6885\u83f2\u65af\u7684\u94f6\u5251\u3002\u5979\u7528\u773c\u89d2\u4f59\u5149\u671d\u90a3\u8fb9\u77a5\u53bb\uff0c\u53ea\u89c1\u6c83\u91d1\u7267\u5e08\u79d1\u6069\u5df2\u7ecf\u8eab\u9996\u5f02\u5904\uff0c\u5012\u5728\u5730\u4e0a\u3002</p><p>\u529f\u4e8f\u4e00\u7bd1\u3002</p><p>\u6740\u624b\u54ac\u7d27\u94f6\u7259\uff0c\u53cc\u81c2\u7528\u529b\uff0c\u786c\u751f\u751f\u5c06\u6885\u83f2\u65af\u7684\u94f6\u5251\u63a8\u5f00\uff0c\u8fd9\u4ee4\u5973\u5723\u6b66\u58eb\u9887\u4e3a\u60ca\u8bb6\u3002\u5979\u4eec\u4e0a\u6b21\u66fe\u7ecf\u6709\u8fc7\u4ea4\u624b\uff0c\u867d\u7136\u5f53\u65f6\u6740\u624b\u8d81\u6885\u83f2\u65af\u8eab\u8d1f\u91cd\u4f24\uff0c\u7a81\u7136\u88ad\u51fb\uff0c\u51e0\u4e4e\u523a\u6740\u5f97\u624b\uff0c\u4f46\u7eaf\u4ee5\u529b\u6c14\u800c\u8bba\uff0c\u5979\u662f\u8fdc\u900a\u4e8e\u6885\u83f2\u65af\u7684\u3002</p><p>\u600e\u4e48\u51e0\u65e5\u4e0d\u89c1\uff0c\u5979\u7684\u529b\u6c14\u4eff\u4f5b\u7a81\u7136\u53d8\u5927\u4e86\u3002</p><p>\u4f46\u73b0\u5728\u4e0d\u662f\u8003\u8651\u8fd9\u4e9b\u95ee\u9898\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u6325\u821e\u7740\u94f6\u5251\uff0c\u6a2a\u626b\u76f4\u5288\uff0c\u5927\u5f00\u5927\u9616\uff0c\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\u5730\u671d\u5973\u6740\u624b\u5377\u53bb\u3002\u6740\u624b\u4e00\u53cd\u5e38\u6001\uff0c\u5b8c\u5168\u653e\u5f03\u4ee5\u5f80\u90a3\u79cd\u7075\u5de7\u578b\u7684\u6253\u6cd5\uff0c\u76f4\u63a5\u4ee5\u786c\u78b0\u786c\u3002\u94f6\u5251\u548c\u77ed\u5251\u6fc0\u70c8\u649e\u51fb\u7740\uff0c\u8ff8\u51fa\u706b\u661f\uff0c\u4e24\u4e2a\u5973\u4eba\u5728\u5c71\u8def\u4e0a\u5f00\u59cb\u751f\u6b7b\u640f\u6740\u3002</p><p>\u8fd9\u8fb9\u743c\u6069\u88ab\u6740\u624b\u91cd\u91cd\u7838\u4e86\u4e00\u62f3\uff0c\u534a\u5929\u624d\u56de\u8fc7\u795e\u6765\uff0c\u611f\u89c9\u8138\u4e0a\u706b\u8fa3\u8fa3\u7684\u75bc\uff0c\u6000\u7591\u98a7\u9aa8\u90fd\u8981\u7ed9\u6253\u584c\u4e86\u3002\u4ed6\u7565\u5b9a\u4e86\u5b9a\u795e\uff0c\u770b\u89c1\u6885\u83f2\u65af\u548c\u6740\u624b\u6b63\u5728\u640f\u6740\uff0c\u4e0d\u5047\u601d\u7d22\u4f38\u624b\u638f\u51fa\u6750\u6599\uff0c\u5f53\u7a7a\u4e00\u6d12\uff0c\u4f38\u624b\u6307\u5411\u838e\u73de\u514b\uff0c\u201c\u53d8\u5f62\uff01\u201d\u4ed6\u7528\u9f99\u8bed\u5389\u58f0\u8bf4\u3002</p><p>\u8fd9\u662f\u53d8\u5f62\u672f\uff0c\u4ed6\u5728\u6c99\u6f20\u91cc\u5bf9\u6885\u83f2\u65af\u7528\u8fc7\uff0c\u628a\u5c11\u5973\u53d8\u6210\u4e86\u4e00\u53ea\u4eba\u9762\u72ee\u3002\u5f53\u65f6\u4ed6\u8fd8\u6ca1\u6709\u638c\u63e1\u8fd9\u79cd\u6cd5\u672f\uff0c\u5fc5\u987b\u4f9d\u9760\u5377\u8f74\uff0c\u5982\u4eca\u5c31\u4e0d\u9700\u8981\u4e86\u3002\u4ed6\u8fd9\u6b21\u81ea\u7136\u6ca1\u6253\u7b97\u628a\u6740\u624b\u53d8\u6210\u4eba\u9762\u72ee\uff0c\u800c\u662f\u60f3\u628a\u5979\u53d8\u6210\u4e00\u53ea\u72d7\u2014\u2014\u4e4b\u6240\u4ee5\u662f\u53d8\u6210\u72d7\u800c\u4e0d\u662f\u5176\u4ed6\u52a8\u7269\uff0c\u662f\u56e0\u4e3a\u4ed6\u5bf9\u72d7\u76f8\u5bf9\u6bd4\u8f83\u719f\u6089\uff0c\u80fd\u4ece\u8111\u4e2d\u66f4\u6e05\u6670\u5730\u63cf\u7ed8\u51fa\u6765\u3002\u53d8\u5f62\u672f\u9700\u8981\u65bd\u6cd5\u8005\u7684\u610f\u8bc6\u4e2d\u80fd\u6e05\u6670\u5730\u63cf\u7ed8\u51fa\u53d8\u5f62\u7684\u7ed3\u679c\u3002</p><p>\u53d8\u5316\u5b66\u6d3e\u662f\u4ed6\u7684\u7cbe\u7814\u65b9\u5411\uff0c\u4e13\u4e1a\u9886\u57df\uff0c\u53d8\u5f62\u672f\u662f\u4ed6\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\u4e4b\u540e\u6240\u638c\u63e1\u7684\u7b2c\u4e00\u4e2a\u6cd5\u672f\uff0c\u8fd9\u4e9b\u5929\u6765\u53cd\u590d\u7422\u78e8\uff0c\u5df2\u7ecf\u9887\u4e3a\u719f\u6089\u3002\u743c\u6069\u5bf9\u81ea\u5df1\u7684\u65bd\u6cd5\u6280\u5de7\u5f88\u6709\u81ea\u4fe1\uff0c\u6599\u60f3\u4e0b\u4e00\u79d2\u949f\u5c31\u80fd\u770b\u89c1\u6740\u624b\u53d8\u6210\u4e00\u53ea\u72d7\u2026\u2026\u4f46\u4ee4\u4ed6\u6ca1\u60f3\u5230\u7684\u662f\uff0c\u4ed6\u7684\u6cd5\u672f\u4eff\u4f5b\u4ece\u6740\u624b\u7684\u8eab\u4e0a\u6ed1\u8fc7\u53bb\u4e86\uff0c\u6839\u672c\u6ca1\u6709\u8d77\u5230\u4efb\u4f55\u4f5c\u7528\u3002</p><p>\u611f\u89c9\u5230\u743c\u6069\u671d\u81ea\u5df1\u65bd\u6cd5\uff0c\u6740\u624b\u7684\u8138\u4e0a\u715e\u6c14\u9661\u7136\u4e00\u76db\uff0c\u5979\u7684\u5168\u8eab\u9aa4\u5730\u706b\u5149\u4e00\u5192\uff0c\u968f\u5373\u718a\u718a\u71c3\u70e7\u8d77\u6765\u3002\u5979\u6574\u4e2a\u4eba\u88ab\u5305\u88f9\u5728\u9ed1\u8272\u70c8\u7130\u4e4b\u4e2d\uff0c\u4e24\u67c4\u77ed\u5251\u4e0a\u4e0d\u77e5\u4f55\u65f6\u90fd\u7efd\u51fa\u4e86\u707f\u70c2\u706b\u5149\uff0c\u4eff\u4f5b\u4e24\u6761\u706b\u86c7\u4e00\u822c\uff0c\u6076\u72e0\u72e0\u5730\u671d\u6885\u83f2\u65af\u6251\u6765\u3002</p><p>\u6885\u83f2\u65af\u6325\u5251\u62b5\u5fa1\u7740\uff0c\u5979\u7684\u94f6\u7532\u662f\u94f6\u9f99\u76ae\u9769\u6240\u5236\uff0c\u4e5f\u6709\u5fa1\u706b\u4e4b\u80fd\uff0c\u4fdd\u62a4\u81ea\u5df1\u4e0d\u88ab\u8fd9\u70c8\u7130\u6240\u4f24\u3002\u743c\u6069\u5728\u8fdc\u5904\u89c1\u81ea\u5df1\u7684\u53d8\u5f62\u672f\u5bf9\u6740\u624b\u4e0d\u8d77\u4f5c\u7528\uff0c\u5fc3\u4e2d\u60ca\u8bb6\uff0c\u4e0d\u6562\u518d\u5bf9\u5979\u65bd\u6cd5\uff0c\u597d\u5728\u4ed6\u539f\u672c\u4e5f\u5c31\u4e0d\u64c5\u7528\u6cd5\u672f\u76f4\u63a5\u653b\u51fb\uff0c\u63a2\u624b\u5165\u6000\uff0c\u53c8\u53d6\u4e86\u4e00\u7247\u7518\u8349\u6839\u5207\u7247\u6765\uff0c\u51c6\u5907\u5bf9\u6885\u83f2\u65af\u7528\u4e00\u4e2a\u52a0\u901f\u672f\u3002</p><p>\u6cd5\u672f\u8fd8\u672a\u65bd\u51fa\uff0c\u5c31\u542c\u89c1\u6740\u624b\u53d1\u51fa\u4e00\u58f0\u5c16\u5229\u5578\u53eb\u3002\u5728\u5979\u7684\u4e24\u4fa7\u8eab\u65c1\uff0c\u5341\u5b57\u88c2\u9699\u5728\u7a7a\u6c14\u4e2d\u6025\u901f\u6269\u5927\uff0c\u718a\u718a\u70c8\u706b\u71c3\u70e7\u7740\uff0c\u9aa8\u9abc\u6784\u6210\u7684\u6df1\u6e0a\u4e4b\u95e8\u8f70\u7136\u655e\u5f00\u3002\u968f\u7740\u4e24\u58f0\u4f4e\u6c89\u7684\u5636\u543c\uff0c\u4e24\u53ea\u72f0\u72de\u7684\u602a\u7269\u51fa\u73b0\u5728\u743c\u6069\u7684\u89c6\u7ebf\u91cc\u3002</p><p>\u90a3\u662f\u4e24\u53ea\u4e00\u6a21\u4e00\u6837\u7684\u602a\u7269\uff0c\u5b83\u4eec\u4f53\u578b\u5e9e\u5927\uff0c\u957f\u7740\u72d7\u4e00\u6837\u7684\u5934\uff0c\u76ae\u80a4\u4e0a\u6df1\u8910\u8272\u548c\u9ed1\u8272\u4ea4\u9519\u6df7\u6742\uff0c\u53cc\u773c\u95ea\u70c1\u7740\u7d2b\u8272\u7684\u90aa\u5f02\u5149\u8292\uff0c\u5b83\u4eec\u90fd\u957f\u7740\u56db\u53ea\u624b\u81c2\uff0c\u4e24\u53ea\u5982\u540c\u4eba\u624b\uff0c\u4e24\u53ea\u5374\u6709\u5982\u87f9\u87af\u3002</p><p>\u743c\u6069\u611f\u89c9\u80cc\u4e0a\u6709\u70b9\u53d1\u51b7\uff0c\u4ed6\u8ba4\u51fa\u4e86\u8fd9\u79cd\u9020\u578b\u6781\u6709\u7279\u8272\u7684\u602a\u7269\uff1a\u8ff7\u8bf1\u9b54\u3002\u8fd9\u662f\u65e0\u5c3d\u6df1\u6e0a\u4e2d\u7684\u4e00\u79cd\u6076\u9b54\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u5df4\u6d1b\u708e\u9b54\u3001\u516d\u81c2\u86c7\u9b54\u822c\u5f3a\u5927\uff0c\u4f46\u4e5f\u5df2\u7ecf\u662f\u9887\u4e3a\u9ad8\u9636\u7684\u5b58\u5728\u4e86\u3002</p><p>\u800c\u4e14\u8fd8\u662f\u4e00\u6b21\u5192\u51fa\u4e24\u4e2a\u3002</p><p>\u56e0\u4e3a\u6885\u83f2\u65af\u8bf4\u6c83\u91d1\u7267\u5e08\u7528\u6df1\u6e0a\u8bed\u7948\u7977\uff0c\u743c\u6069\u4e00\u76f4\u4ee5\u4e3a\u4ed6\u548c\u6076\u9b54\u6709\u52fe\u7ed3\uff0c\u4f46\u5982\u4eca\u770b\u8d77\u6765\uff0c\u8fd9\u4e2a\u731c\u6d4b\u6216\u8bb8\u6ca1\u9519\uff0c\u5374\u80af\u5b9a\u4e0d\u591f\u7cbe\u786e\u2026\u2026\u56e0\u4e3a\u5973\u6740\u624b\u5c45\u7136\u4e5f\u4ece\u6df1\u6e0a\u4e2d\u53ec\u5524\u4e86\u4e24\u53ea\u9ad8\u9636\u7684\u6076\u9b54\u6765\u3002</p><p>\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff0c\u600e\u4e48\u611f\u89c9\u8fd9\u4ef6\u4e8b\u60c5\u7684\u80cc\u540e\uff0c\u6709\u4e00\u53ea\u6076\u9b54\u7684\u624b\u5728\u6697\u4e2d\u64cd\u7eb5\u3002</p><p>\u4e0d\u53ca\u601d\u8003\u8fd9\u4e9b\uff0c\u4e24\u53ea\u8ff7\u8bf1\u9b54\u5df2\u7ecf\u542c\u4ece\u6740\u624b\u7684\u547d\u4ee4\uff0c\u4e00\u53ea\u4e0a\u524d\u5939\u653b\u6885\u83f2\u65af\uff0c\u53e6\u5916\u4e00\u53ea\u671d\u743c\u6069\u5954\u6765\u3002\u743c\u6069\u8fde\u5fd9\u4e34\u65f6\u4e2d\u65ad\u4e86\u52a0\u901f\u672f\uff0c\u638f\u51fa\u4e00\u679a\u77f3\u5f39\u63b7\u8fc7\u53bb\u3002</p><p>\u8fd9\u662f\u4ed6\u7528\u5f97\u975e\u5e38\u719f\u7ec3\u7684\u4e00\u62db\uff0c\u53ea\u662f\u504f\u504f\u603b\u662f\u5931\u624b\u3002\u5728\u6c99\u6f20\u4e2d\u5bf9\u5973\u4eba\u9762\u72ee\u8482\u5a1c\u82a9\u5c04\u5931\u4e86\uff0c\u5728\u70db\u5821\u5bf9\u5973\u6740\u624b\u4e5f\u5c04\u5931\u4e86\uff0c\u8fd9\u4ee4\u4ed6\u9887\u4e3a\u6cae\u4e27\u3002\u5e78\u597d\uff0c\u8fd9\u4e00\u6b21\u6ca1\u6709\u5931\u624b\uff0c\u77f3\u5f39\u51c6\u786e\u5730\u7838\u4e2d\u4e86\u8ff7\u8bf1\u9b54\u3002</p><p>\u8ff7\u8bf1\u9b54\u7684\u8179\u90e8\u88ab\u70b8\u5f00\u4e86\u4e00\u4e2a\u8840\u8089\u6a21\u7cca\u7684\u6d1e\uff0c\u4f46\u8fd9\u79cd\u6765\u81ea\u6df1\u6e0a\u7684\u6076\u9b54\u82e5\u65e0\u5176\u4e8b\uff0c\u5bf9\u8fd9\u70b9\u4f24\u4eff\u4f5b\u5168\u4e0d\u5728\u610f\u3002\u5b83\u4f4e\u543c\u7740\uff0c\u6325\u821e\u7740\u50cf\u87f9\u87af\u4e00\u6837\u7684\u524d\u80a2\uff0c\u671d\u743c\u6069\u6293\u4e0b\u6765\u3002</p><p>\u5deb\u5e08\u7528\u9f99\u9cde\u76fe\u6321\u4f4f\u87f9\u87af\uff0c\u81ea\u5df1\u8f6c\u8eab\u5c31\u9003\uff0c\u4ed6\u53ef\u4e07\u4e07\u7ecf\u4e0d\u4f4f\u8fd9\u4e00\u6293\uff0c\u6b64\u65f6\u4e5f\u6765\u4e0d\u53ca\u518d\u65bd\u65b0\u7684\u6cd5\u672f\u3002\u8ff7\u8bf1\u9b54\u4e00\u628a\u51fb\u5f00\u9f99\u9cde\u76fe\uff0c\u8ffd\u8d76\u8fc7\u6765\uff0c\u5b83\u7684\u817f\u53ef\u6bd4\u743c\u6069\u957f\u5f97\u592a\u591a\u4e86\uff0c\u52a8\u4f5c\u53c8\u8fc5\u6377\uff0c\u8fc8\u5f00\u5927\u6b65\uff0c\u4e09\u6b65\u4e24\u6b65\u5c31\u8ffd\u4e86\u4e0a\u6765\u3002</p><p>\u743c\u6069\u6b63\u5728\u72c2\u5954\uff0c\u731b\u7136\u611f\u89c9\u5934\u4e0a\u4e00\u9635\u9634\u5f71\u7f69\u4e0b\u6765\uff0c\u6b64\u65f6\u53c8\u4e0d\u53ca\u65bd\u6cd5\uff0c\u767e\u5fd9\u4e2d\u9f99\u9cde\u76fe\u65cb\u56de\u4e00\u6321\uff0c\u8ff7\u8bf1\u9b54\u7684\u4e24\u53ea\u87f9\u87af\u6a2a\u626b\u8fc7\u6765\uff0c\u8fd9\u79cd\u6076\u9b54\u7684\u529b\u6c14\u5927\u5f97\u51fa\u5947\uff0c\u6bd4\u7269\u8d28\u754c\u6700\u5f3a\u58ee\u7684\u5de8\u4eba\u8fd8\u8981\u80dc\u4e0a\u51e0\u5206\uff0c\u201c\u94ff\u201d\u5730\u4e00\u58f0\u5982\u540c\u91d1\u94c1\u4ea4\u9e23\uff0c\u9f99\u9cde\u76fe\u88ab\u8fdc\u8fdc\u6253\u98de\u4e86\u51fa\u53bb\u3002\u87f9\u87af\u6765\u52bf\u4e0d\u51cf\uff0c\u91cd\u91cd\u626b\u5728\u743c\u6069\u8eab\u4e0a\u3002</p><p>\u548c\u90a3\u6b21\u5728\u4eba\u9762\u72ee\u795e\u6bbf\u91cc\u7684\u60c5\u5f62\u4e00\u6837\uff0c\u743c\u6069\u957f\u888d\u4e0a\u7684\u9632\u5fa1\u80f8\u9488\u518d\u6b21\u53d1\u6325\u4f5c\u7528\uff0c\u66ff\u4ed6\u5378\u4e0b\u4e86\u4e00\u90e8\u5206\u51b2\u51fb\u529b\uff0c\u4f46\u4ed6\u4f9d\u65e7\u6beb\u65e0\u60ac\u5ff5\u5730\u88ab\u8f70\u98de\u4e86\u51fa\u53bb\u3002</p><p>\u8ff7\u8bf1\u9b54\u7684\u529b\u6c14\u53ef\u6bd4\u5973\u4eba\u9762\u72ee\u5927\u5f97\u591a\uff0c\u6240\u4ee5\u743c\u6069\u8fd9\u6b21\u98de\u4e5f\u66f4\u8fdc\uff0c\u5f53\u4ed6\u53cd\u5e94\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u843d\u5230\u4e00\u9053\u5c71\u574e\u540e\u9762\u3002\u8ddd\u79bb\u8111\u888b\u4e0d\u8db3\u534a\u5c3a\u7684\u5730\u65b9\uff0c\u5c31\u6709\u4e00\u5757\u5c11\u8bf4\u4e5f\u8fd1\u5343\u65a4\u7684\u5de8\u77f3\uff0c\u68f1\u89d2\u5c16\u9510\uff0c\u8fd9\u8ba9\u743c\u6069\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u5018\u82e5\u81ea\u5df1\u521a\u624d\u8fd0\u6c14\u7a0d\u5dee\uff0c\u4e0d\u662f\u843d\u5728\u677e\u8f6f\u7684\u571f\u5730\u800c\u662f\u8111\u888b\u649e\u5728\u8fd9\u5757\u77f3\u5934\u4e0a\u2026\u2026</p><p>\u4f46\u968f\u5373\u4ed6\u5927\u559c\u8fc7\u671b\uff0c\u987e\u4e0d\u5f97\u5168\u8eab\u75bc\u75db\uff0c\u6323\u624e\u7740\u4f38\u624b\u6309\u5728\u90a3\u5757\u5de8\u77f3\u4e0a\uff0c\u201c\u7f29\u7269\uff01\u201d\u4ed6\u4f4e\u559d\u7740\u3002</p><p>\u8c22\u5929\u8c22\u5730\uff0c\u5e78\u4e8f\u81ea\u5df1\u4e3a\u4e86\u9884\u9632\u4e07\u4e00\uff0c\u8fd8\u7279\u5730\u591a\u51c6\u5907\u4e86\u4e00\u4e2a\u7f29\u7269\u672f\u3002</p><p>\u5de8\u77f3\u4e0a\u6cdb\u8d77\u4e00\u5c42\u5fae\u5149\uff0c\u968f\u5373\u6025\u5267\u7f29\u5c0f\uff0c\u53d8\u6210\u4e00\u9897\u5c0f\u5c0f\u7684\u77f3\u5757\uff0c\u743c\u6069\u4e00\u628a\u6293\u8d77\u6765\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u5934\u9876\u4e0a\u7684\u9634\u5f71\u518d\u6b21\u538b\u4e0b\uff0c\u5c71\u574e\u4e0a\u65b9\u5df2\u7ecf\u9732\u51fa\u8ff7\u8bf1\u9b54\u7684\u72d7\u5934\u6765\u3002</p><p>\u743c\u6069\u4e0d\u5047\u601d\u7d22\uff0c\u62ac\u624b\u5c06\u77f3\u5757\u7167\u7740\u90a3\u9897\u72d7\u5934\u7838\u4e86\u8fc7\u53bb\u3002</p></article>"}}}]);
