(window.webpackJsonp=window.webpackJsonp||[]).push([[1391],{1414:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u5b9d\u77f3\u9b54\u6cd5",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u5b9d\u77f3\u9b54\u6cd5</h2><p>\u6bd4\u8d5b\u573a\u5185\u987f\u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u6240\u6709\u7684\u89c2\u4f17\u8083\u7136\u633a\u5750\uff0c\u518d\u4e0d\u547c\u558a\u6216\u8005\u4ea4\u8c08\u534a\u53e5\u3002\u743c\u6069\u8eab\u65c1\u7684\u7ea2\u888d\u5deb\u5e08\u4e5f\u7acb\u523b\u95ed\u4e0a\u4e86\u5634\uff0c\u4f46\u4ed6\u5c06\u624b\u4e0a\u7a81\u7136\u51fa\u73b0\u7684\u4e00\u5f20\u7eb8\u9012\u7ed9\u4e86\u743c\u6069\u3002</p><p>\u743c\u6069\u7591\u60d1\u5730\u63a5\u8fc7\uff0c\u4e00\u770b\u624d\u77e5\u9053\u8fd9\u662f\u6bd4\u8d5b\u65e5\u7a0b\u5b89\u6392\uff0c\u4e0b\u9762\u6709\u4eca\u5929\u56db\u4f4d\u53c2\u8d5b\u8005\u7684\u540d\u5b57\u3002\u743c\u6069\u7684\u76ee\u5149\u4e0b\u79fb\uff0c\u627e\u5230\u4e86\u201c\u51db\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u540e\u9762\u6709\u51e0\u53e5\u975e\u5e38\u7b80\u5355\u7684\u4ecb\u7ecd\u3002</p><p>\u201c\u4e13\u7cbe\u5b66\u6d3e\uff1a\u5851\u80fd\u3002\u201d</p><p>\u743c\u6069\u7709\u5934\u5fae\u5fae\u4e00\u76b1\uff0c\u5728\u5fc3\u4e2d\u5bf9\u8fd9\u4f4d\u51db\u5c0f\u59d0\u7684\u8bc4\u4ef7\u987f\u65f6\u6084\u6084\u964d\u4f4e\u4e86\u4e24\u5206\uff0c\u9b54\u6cd5\u516b\u5927\u5b66\u6d3e\u4e4b\u4e2d\uff0c\u4ed6\u7d20\u6765\u6700\u770b\u4e0d\u8d77\u7684\u5c31\u662f\u5851\u80fd\uff0c\u8fde\u5e26\u7684\u81ea\u7136\u4e5f\u770b\u4e0d\u8d77\u4e13\u7cbe\u5851\u80fd\u7684\u5deb\u5e08\u3002\u201c\u8fd9\u4e48\u6f02\u4eae\u7684\u5c0f\u5973\u5b69\uff0c\u73a9\u4ec0\u4e48\u5851\u80fd\uff0c\u4e5f\u4e0d\u6015\u522b\u4eba\u8bf4\u66b4\u529b\u4e48\u3002\u201d\u4ed6\u4e00\u8fb9\u6697\u4e2d\u6447\u5934\uff0c\u4e00\u8fb9\u7ee7\u7eed\u5f80\u4e0b\u770b\u3002</p><p>\u201c\u653e\u5f03\u5b66\u6d3e\uff0c\u4ea1\u7075\u3001\u5e7b\u672f\u3001\u9632\u62a4\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u201c\u600e\u4e48\u4f1a\u653e\u5f03\u6389\u4e09\u4e2a\u5b66\u6d3e\uff1f\u201d</p><p>\u9b54\u6cd5\u516b\u5927\u5b66\u6d3e\uff0c\u867d\u7136\u5728\u743c\u6069\u773c\u4e2d\u662f\u6709\u4f18\u52a3\u9ad8\u4e0b\u4e4b\u5206\uff0c\u4f46\u4ed6\u540c\u6837\u4e5f\u77e5\u9053\uff0c\u5deb\u5e08\u53ef\u80fd\u906d\u9047\u7684\u60c5\u5f62\u5343\u53d8\u4e07\u5316\uff0c\u9760\u7cbe\u7814\u4e00\u95e8\u5c31\u60f3\u5e94\u4ed8\u6240\u6709\u95ee\u9898\uff0c\u90a3\u662f\u4e0d\u73b0\u5b9e\u7684\u60f3\u6cd5\uff0c\u603b\u8fd8\u662f\u8981\u5c3d\u53ef\u80fd\u6d89\u730e\u65c1\u901a\u7684\u3002\u6839\u636e\u4ed6\u5728\u5b66\u6821\u91cc\u5b66\u5230\u7684\u57fa\u672c\u9b54\u6cd5\u539f\u7406\uff0c\u4e13\u7cbe\u5deb\u5e08\u53ea\u9700\u8981\u653e\u5f03\u6389\u4e00\u4e24\u4e2a\u5b66\u6d3e\uff0c\u5c31\u8db3\u4ee5\u786e\u4fdd\u81ea\u5df1\u5b89\u5168\uff0c\u4e0d\u81f3\u4e8e\u88ab\u7d0a\u4e71\u7684\u9b54\u6cd5\u529b\u91cf\u53cd\u566c\u4f24\u5bb3\u3002\u743c\u6069\u81ea\u5df1\u7cbe\u7814\u53d8\u5316\uff0c\u653e\u5f03\u6389\u4e86\u5851\u80fd\u548c\u4ea1\u7075\u4e24\u7cfb\uff0c\u8fd9\u6b21\u51fa\u95e8\u4ee5\u6765\uff0c\u5c31\u5df2\u7ecf\u5c61\u5c61\u611f\u89c9\u6349\u895f\u89c1\u8098\uff0c\u5e94\u4ed8\u8270\u96be\uff1b\u8fd9\u4f4d\u51db\u5c0f\u59d0\u8111\u5b50\u574f\u6389\u4e86\u4e0d\u6210\uff0c\u5c45\u7136\u6562\u653e\u5f03\u4e09\u7cfb\u3002</p><p>\u201c\u4ea1\u7075\u653e\u5f03\u4e5f\u5c31\u7b97\u4e86\uff0c\u53cd\u6b63\u8fd9\u4e00\u5b66\u6d3e\u7684\u6cd5\u672f\u5927\u591a\u6076\u5fc3\u96be\u770b\uff0c\u5973\u5b69\u5b50\u4e5f\u4e0d\u9002\u5b9c\u5b66\uff1b\u5e7b\u672f\u2026\u2026\u4e5f\u65e0\u6240\u8c13\u5427\uff0c\u4f46\u600e\u4e48\u6562\u8fde\u9632\u62a4\u90fd\u653e\u5f03\u4e86\u3002\u201d</p><p>\u9632\u62a4\u5b66\u6d3e\u64c5\u957f\u7684\u662f\u9632\u62a4\u81ea\u8eab\u548c\u6467\u7834\u5bf9\u65b9\u7684\u9b54\u6cd5\u9632\u5fa1\uff0c\u5bf9\u8106\u5f31\u7684\u5deb\u5e08\u6765\u8bf4\u5f88\u662f\u91cd\u8981\uff0c\u5f88\u5c11\u6709\u4eba\u6562\u653e\u5f03\u7684\u3002</p><p>\u201c\u4e13\u7cbe\u653b\u51fb\u6027\u6700\u5f3a\u7684\u5851\u80fd\uff0c\u653e\u5f03\u9632\u62a4\u5b88\u5fa1\u2026\u2026\u53ea\u653b\u4e0d\u5b88\uff0c\u4e0d\u4f1a\u662f\u4e2a\u66b4\u529b\u5973\u5427\u3002\u201d</p><p>\u7136\u800c\u773c\u5149\u77a5\u4e86\u4e00\u773c\u5176\u4ed6\u53c2\u8d5b\u8005\u7684\u8d44\u6599\uff0c\u743c\u6069\u53d1\u73b0\u539f\u6765\u4e0d\u4ec5\u4ec5\u662f\u51db\u4e00\u4e2a\u4eba\u8fd9\u4e48\u82f1\u52c7\uff1b\u5176\u4ed6\u4e09\u4f4d\u5deb\u5e08\uff0c\u5206\u522b\u662f\u9632\u62a4\u5deb\u5e08\u3001\u5492\u6cd5\u5deb\u5e08\u548c\u4ea1\u7075\u5deb\u5e08\uff0c\u4e5f\u540c\u6837\u90fd\u653e\u5f03\u4e86\u4e09\u4e2a\u5b66\u6d3e\u3002\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u5012\u4e0d\u662f\u51db\u7684\u95ee\u9898\uff0c\u5e94\u8be5\u662f\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u666e\u904d\u89c4\u77e9\u4e86\uff1f</p><p>\u7ee7\u7eed\u5f80\u4e0b\u770b\uff0c\u201c\u9b54\u7f51\u9020\u8be3\uff1a\u7b2c\u56db\u5c42\u3002\u201d</p><p>\u201c\u9b54\u7f51\u7b2c\u56db\u5c42\uff0c\u548c\u6211\u6c34\u51c6\u5dee\u4e0d\u591a\uff0c\u201d\u743c\u6069\u5fc3\u60f3\uff0c\u518d\u770b\u51db\u7684\u5bf9\u624b\u8d44\u6599\uff0c\u662f\u4e00\u4f4d\u4e13\u7cbe\u9632\u62a4\u5b66\u6d3e\u7684\u5deb\u5e08\uff0c\u653e\u5f03\u6389\u4e86\u9644\u9b54\u3001\u4ea1\u7075\u548c\u53d8\u5316\u4e09\u4e2a\u5b66\u6d3e\uff0c\u9b54\u7f51\u9020\u8be3\u540c\u6837\u4e5f\u662f\u7b2c\u56db\u5c42\u3002</p><p>\u60f3\u60f3\u4e5f\u662f\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bf4\u8fc7\uff0c\u8fd9\u79cd\u664b\u9636\u8d5b\u662f\u4e3a\u4e86\u9009\u62d4\u201c\u73af\u4e4b\u5bfc\u5e08\u201d\u505a\u51c6\u5907\u7684\u3002\u80fd\u4e00\u8def\u6253\u5230\u534a\u51b3\u8d5b\u7684\u4eba\u7269\uff0c\u81ea\u7136\u90fd\u4e0d\u53ef\u80fd\u662f\u6cdb\u6cdb\u4e4b\u8f88\u4e86\u3002</p><p>\u5f53\u4ed6\u770b\u5b8c\u8d44\u6599\u7684\u65f6\u5019\uff0c\u64c2\u53f0\u4e0a\u7684\u4e24\u4f4d\u5deb\u5e08\u5df2\u7ecf\u5f7c\u6b64\u97a0\u8eac\uff0c\u5c65\u884c\u5b8c\u5deb\u5e08\u51b3\u6597\u524d\u7684\u901a\u7528\u793c\u8282\uff0c\u5404\u81ea\u540e\u9000\u62c9\u5f00\u8ddd\u79bb\u3002\u743c\u6069\u51dd\u795e\u76ef\u7740\u51db\u7684\u53cc\u624b\uff0c\u60f3\u770b\u5979\u5230\u5e95\u600e\u4e48\u65bd\u6cd5\u3002</p><p>\u64c2\u53f0\u4e0a\u7a7a\u7684\u5149\u5934\u8001\u8005\u637b\u52a8\u624b\u6307\uff0c\u7206\u51fa\u4e00\u9053\u7729\u76ee\u95ea\u5149\uff0c\u6253\u5728\u64c2\u53f0\u4e2d\u592e\uff0c\u8fd9\u662f\u4fe1\u53f7\uff0c\u610f\u5473\u7740\u4e24\u4f4d\u53c2\u8d5b\u8005\u53ef\u4ee5\u5f00\u59cb\u65bd\u6cd5\u4e86\u3002</p><p>\u51db\u7684\u53f3\u624b\u4e00\u76f4\u653e\u5728\u8170\u95f4\u7ea2\u8272\u53e3\u888b\u4e2d\uff0c\u4e00\u89c1\u95ea\u5149\u7206\u8d77\uff0c\u7acb\u523b\u62bd\u4e86\u51fa\u6765\uff0c\u4fee\u957f\u7684\u624b\u6307\u95f4\u5df2\u7ecf\u5939\u7740\u4e00\u679a\u6d45\u7ea2\u8272\u5b9d\u77f3\uff0c\u62ac\u624b\u671d\u7a7a\u4e2d\u63b7\u53bb\u3002</p><p>\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u77f3\u5728\u64c2\u53f0\u4e0a\u7a7a\u70b8\u5f00\uff0c\u56db\u9053\u706b\u5149\u4ece\u4e2d\u5f39\u51fa\uff0c\u50cf\u793c\u82b1\u4e00\u6837\u6563\u843d\u5760\u5230\u64c2\u53f0\u4e0a\uff0c\u8f70\u7136\u71c3\u8d77\u56db\u5806\u5927\u706b\uff0c\u5176\u4e2d\u4e00\u5806\u6b63\u5c31\u5728\u51db\u7684\u8eab\u4fa7\u3002</p><p>\u201c\u8d76\u5feb\u8db4\u4e0b\u6765\u6c42\u9976\u5427\uff01\u201d\u5979\u5de6\u624b\u53c9\u8170\uff0c\u53f3\u624b\u6307\u7740\u5bf9\u9762\u7684\u9632\u62a4\u5deb\u5e08\uff0c\u5e05\u6c14\u903c\u4eba\u5730\u8bf4\uff0c\u201c\u4eca\u5929\u53ea\u7528\u4e00\u51fb\u5c31\u8981\u6253\u5012\u4f60\u3002\u201d</p><p>\u53e3\u51fa\u72c2\u8a00\uff0c\u8ba9\u743c\u6069\u8fd9\u4e2a\u65c1\u89c2\u8005\u4e0d\u7981\u76b1\u7709\uff0c\u8fd9\u79cd\u6c34\u5e73\u76f8\u8fd1\u7684\u5deb\u5e08\u5bf9\u51b3\uff0c\u54ea\u6709\u6562\u8fd9\u4e48\u5439\u5618\u7684\u3002\u770b\u5979\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u7b2c\u4e00\u65f6\u95f4\u4e0d\u7ed9\u81ea\u5df1\u65bd\u52a0\u9632\u5fa1\uff0c\u4e0d\u53bb\u5e72\u6270\u5bf9\u624b\u65bd\u6cd5\uff0c\u4e0d\u62a2\u5148\u538b\u5236\u4e5f\u4e0d\u505a\u534a\u70b9\u8f85\u52a9\u5de5\u4f5c\uff0c\u53cd\u5012\u597d\u6574\u4ee5\u6687\u5730\u5728\u64c2\u53f0\u4e0a\u70b9\u706b\u5806\u2026\u2026\u5979\u4ee5\u4e3a\u8fd9\u662f\u5728\u505a\u4ec0\u4e48\uff1f\u51c6\u5907\u91ce\u8425\u70e7\u70e4\u4e48\uff1f</p><p>\u82e5\u662f\u6362\u4e86\u743c\u6069\u5f53\u5bf9\u624b\uff0c\u76f4\u63a5\u4e00\u53d1\u77f3\u5f39\u5c31\u7838\u8fc7\u53bb\u4e86\u3002\u7136\u800c\u51db\u7684\u8fd9\u4f4d\u5bf9\u624b\u4f3c\u4e4e\u4e5f\u8111\u7b4b\u574f\u6389\u4e86\uff0c\u4e0d\u8d81\u673a\u653b\u51fb\uff0c\u53cd\u800c\u7a33\u624e\u7a33\u6253\u5730\u7ed9\u81ea\u5df1\u65bd\u52a0\u9632\u5fa1\u8d77\u6765\u3002</p><p>\u4ed6\u5148\u505a\u4e86\u4e2a\u7b80\u5355\u7684\u624b\u52bf\uff0c\u5ff5\u51fa\u4e00\u4e2a\u8bcd\uff0c\u5168\u8eab\u968f\u5373\u6cdb\u8d77\u7ea2\u8272\u7684\u9b54\u6cd5\u5fae\u5149\u6765\uff0c\u743c\u6069\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u6cd5\u672f\uff0c\u201c\u9632\u62a4\u706b\u7130\u80fd\u91cf\u4f24\u5bb3\u201d\u3002\u7d27\u63a5\u7740\uff0c\u4ed6\u634f\u788e\u4e86\u4e00\u4e2a\u6c34\u6676\u73e0\u5b50\uff0c\u5ff5\u8bf5\u5492\u8bed\uff0c\u53cc\u624b\u865a\u865a\u4e00\u5408\u3002\u82e5\u662f\u666e\u901a\u4eba\u6765\u770b\uff0c\u53ea\u4f1a\u89c9\u5f97\u6beb\u65e0\u5f02\u6837\uff0c\u4f46\u5982\u4eca\u5728\u573a\u7684\u89c2\u4f17\u5168\u90fd\u662f\u5deb\u5e08\uff0c\u4ed6\u4eec\u773c\u4e2d\u80fd\u591f\u6e05\u695a\u5730\u770b\u89c1\u968f\u7740\u6cd5\u672f\u5b8c\u6210\uff0c\u9632\u62a4\u5deb\u5e08\u7684\u8eab\u5468\u987f\u65f6\u51fa\u73b0\u4e86\u4e00\u4e2a\u95ea\u70c1\u7740\u5fae\u5149\u7684\u900f\u660e\u7403\u5f62\u62a4\u7f69\u3002</p><p>\u201c\u6b21\u7ea7\u6cd5\u672f\u65e0\u6548\u7ed3\u754c\u3002\u201d\u743c\u6069\u867d\u7136\u4e0d\u7cbe\u901a\u9632\u62a4\u9b54\u6cd5\uff0c\u4f46\u4e5f\u51c6\u786e\u731c\u51fa\u4e86\u8fd9\u4e2a\u6cd5\u672f\u7684\u540d\u79f0\u3002\u4ed6\u81ea\u5df1\u7684\u6212\u6307\u91cc\u5c31\u4e5f\u6709\u8fd9\u4e2a\u6cd5\u672f\uff0c\u53ef\u4ee5\u5728\u5deb\u5e08\u8eab\u5468\u5f62\u6210\u7403\u5f62\u529b\u573a\uff0c\u963b\u9694\u6389\u4f4e\u9636\u6cd5\u672f\u7684\u4fb5\u88ad\u3002</p><p>\u9632\u62a4\u5deb\u5e08\u7684\u65bd\u6cd5\u52a8\u4f5c\u975e\u5e38\u6d41\u7545\u7cbe\u51c6\uff0c\u6cd5\u672f\u6548\u679c\u5b8c\u7f8e\u65e0\u7f3a\u3002\u82e5\u662f\u6362\u4e86\u743c\u6069\u6765\uff0c\u5927\u6982\u8981\u82b1\u4e24\u500d\u7684\u65f6\u95f4\u624d\u80fd\u5b8c\u6210\u8fd9\u4e24\u4e2a\u6cd5\u672f\uff0c\u800c\u4e14\u8fd8\u505a\u4e0d\u5230\u8fd9\u4e48\u5b8c\u5584\u3002\u7136\u800c\u4ed6\u5fc3\u5934\u7684\u7591\u60d1\u66f4\u52a0\u6df1\u4e86\uff0c\u9762\u5bf9\u4e00\u4e2a\u6beb\u65e0\u9632\u5fa1\u51c6\u5907\u7684\u5bf9\u624b\uff0c\u8fd9\u4f4d\u9632\u62a4\u5deb\u5e08\u5374\u5982\u4e34\u5927\u654c\u5730\u628a\u81ea\u5df1\u5f04\u6210\u8fd9\u5e45\u4e4c\u9f9f\u58f3\u76f8\uff0c\u6709\u5fc5\u8981\u4e48\u3002\u518d\u8bf4\uff0c\u4ed6\u5c31\u80fd\u786e\u5b9a\u5bf9\u624b\u4e00\u5b9a\u4f1a\u7528\u706b\u7130\u7c7b\u578b\u7684\u9b54\u6cd5\u653b\u51fb\uff1f\u5982\u679c\u51db\u7838\u4e00\u51fb\u5bd2\u51b0\u9525\u6216\u8005\u95ea\u7535\u675f\u6765\uff0c\u4ed6\u90a3\u201c\u9632\u62a4\u706b\u7130\u80fd\u91cf\u4f24\u5bb3\u201d\u5c82\u4e0d\u5c31\u5b8c\u5168\u6210\u4e86\u6446\u8bbe\uff0c\u767d\u767d\u6d6a\u8d39\u3002</p><p>\u4e0d\u7406\u4f1a\u743c\u6069\u7684\u7591\u60d1\uff0c\u9632\u62a4\u5deb\u5e08\u518d\u6b21\u65bd\u6cd5\uff0c\u4ed6\u7684\u811a\u4e0b\u5192\u51fa\u4e00\u5708\u7ea2\u5149\uff0c\u968f\u5373\u817e\u8d77\u534a\u4eba\u9ad8\u7684\u70c8\u7130\u6765\uff0c\u4eff\u4f5b\u62a4\u76fe\u4e00\u822c\u5c06\u5deb\u5e08\u4fdd\u62a4\u5728\u4e2d\u95f4\u3002\u743c\u6069\u770b\u5f97\u5927\u6447\u5176\u5934\uff0c\u8fd9\u662f\u706b\u7130\u62a4\u76fe\uff0c\u4f9d\u65e7\u8fd8\u662f\u7528\u6765\u62b5\u5fa1\u706b\u7130\u7c7b\u578b\u9b54\u6cd5\u653b\u51fb\u7684\u3002\u8fd9\u9632\u62a4\u5deb\u5e08\u771f\u662f\u8111\u5b50\u4e00\u6839\u7b4b\u5230\u5e95\u4e86\u3002</p><p>\u773c\u770b\u5bf9\u624b\u5df2\u7ecf\u5b8c\u6210\u4e86\u4e09\u4e2a\u9632\u5fa1\u6cd5\u672f\uff0c\u4e00\u76f4\u5728\u6446\u9020\u578b\u7684\u51db\u5c0f\u59d0\u7ec8\u4e8e\u6709\u4e86\u52a8\u4f5c\uff0c\u5979\u61d2\u6d0b\u6d0b\u5730\u4f38\u624b\uff0c\u4ece\u8170\u95f4\u53e3\u888b\u91cc\u53d6\u51fa\u6df1\u7ea2\u8272\u7684\u83f1\u5f62\u5b9d\u77f3\uff0c\u62ac\u624b\u5c31\u671d\u9632\u62a4\u5deb\u5e08\u7838\u8fc7\u53bb\u3002</p><p>\u201c\u5b9d\u77f3\u70b8\u5f39\u4e48\uff1f\u201d\u743c\u6069\u5fc3\u60f3\uff0c\u201c\u7136\u800c\u90a3\u662f\u6c83\u91d1\u5973\u795e\u8d50\u4e88\u7267\u5e08\u7684\u795e\u672f\u554a\u3002\u201d</p><p>\u5ff5\u5934\u53cd\u8f6c\uff0c\u5c31\u542c\u5f97\u8f70\u7136\u4e00\u58f0\uff0c\u90a3\u9897\u5b9d\u77f3\u5728\u534a\u9014\u5316\u4f5c\u4e00\u56e2\u5de8\u5927\u706b\u7403\uff0c\u52bf\u4e0d\u53ef\u6321\u5730\u671d\u9632\u62a4\u5deb\u5e08\u7838\u53bb\u3002</p><p>\u201c\u7b28\u86cb\uff01\u201d\u743c\u6069\u5728\u5fc3\u91cc\u8bc4\u4ef7\uff0c\u5bf9\u624b\u5df2\u7ecf\u6784\u5efa\u4e86\u5b8c\u5584\u7684\u9632\u5fa1\uff0c\u800c\u4e14\u662f\u4e13\u95e8\u9488\u5bf9\u706b\u7130\u9b54\u6cd5\u7684\uff0c\u9762\u5bf9\u6b64\u60c5\u666f\uff0c\u8fd9\u4f4d\u51db\u5c0f\u59d0\u5c45\u7136\u8fd8\u7528\u706b\u7403\u786c\u7838\u2026\u2026\u8fd9\u573a\u51b3\u6597\u771f\u662f\u592a\u6ca1\u6280\u672f\u542b\u91cf\u4e86\u3002</p><p>\u6b63\u5728\u5fc3\u4e2d\u5bf9\u7ea2\u888d\u5deb\u5e08\u7684\u6574\u4f53\u8bc4\u4ef7\u53c8\u4e0b\u964d\u4e24\u4e2a\u6863\u6b21\u7684\u65f6\u5019\uff0c\u706b\u7403\u5df2\u7ecf\u7838\u5230\u9632\u62a4\u5deb\u5e08\u7684\u8ddf\u524d\u3002\u900f\u660e\u7403\u5f62\u62a4\u7f69\u53ea\u5fae\u5fae\u95ea\u70c1\u4e86\u4e00\u4e0b\uff0c\u5c31\u6beb\u65e0\u62b5\u6297\u5730\u4efb\u7531\u706b\u7403\u900f\u5165\uff0c\u649e\u5728\u706b\u7130\u62a4\u76fe\u4e0a\u3002\u4e00\u649e\u4e4b\u4e0b\uff0c\u706b\u7130\u62a4\u76fe\u77ac\u95f4\u7184\u706d\uff0c\u706b\u7403\u53bb\u52bf\u4e0d\u51cf\uff0c\u773c\u770b\u5c31\u8981\u7838\u4e2d\u9632\u62a4\u5deb\u5e08\u7684\u8eab\u4f53\u3002\u4e00\u9762\u8d64\u7ea2\u8272\u900f\u660e\u76fe\u724c\u51ed\u7a7a\u51fa\u73b0\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u9694\u4f4f\u4e86\u706b\u7403\uff0c\u8fd9\u662f\u9632\u62a4\u706b\u7130\u80fd\u91cf\u4f24\u5bb3\u6cd5\u672f\u5728\u53d1\u6325\u4f5c\u7528\u3002</p><p>\u201c\u6321\u4f4f\u4e86\u3002\u201d\u743c\u6069\u5fc3\u60f3\uff0c\u4ee5\u8fd9\u4e2a\u9632\u62a4\u5deb\u5e08\u7684\u9020\u8be3\uff0c\u521b\u9020\u51fa\u6765\u7684\u80fd\u91cf\u76fe\u724c\u63a5\u4e0b\u4e09\u56db\u53d1\u706b\u7403\u8f70\u70b8\u5e94\u8be5\u90fd\u6ca1\u95ee\u9898\u3002</p><p>\u7136\u800c\u4e8b\u5b9e\u518d\u4e00\u6b21\u51fa\u4eba\u610f\u6599\uff0c\u4ec5\u4ec5\u53ea\u963b\u9694\u4e86\u4e00\u77ac\uff0c\u8d64\u7ea2\u76fe\u724c\u4fbf\u6084\u7136\u788e\u88c2\uff0c\u6563\u5f52\u65e0\u5f62\uff0c\u706b\u7403\u867d\u7136\u9eef\u6de1\u4e0d\u5c11\uff0c\u4f46\u4f59\u5a01\u5c1a\u5728\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u7838\u5728\u5deb\u5e08\u8eab\u4e0a\uff0c\u628a\u4ed6\u53d8\u6210\u4e86\u4e00\u56e2\u71c3\u70e7\u7684\u4eba\u5f62\u706b\u7130\u3002\u5168\u573a\u9e26\u96c0\u65e0\u58f0\uff0c\u4f3c\u4e4e\u6240\u6709\u4eba\u90fd\u88ab\u60ca\u5446\u4e86\uff0c\u53ea\u6709\u51db\u5c0f\u59d0\u94f6\u94c3\u822c\u7684\u6e05\u8106\u7b11\u58f0\u4ece\u53f0\u4e0a\u4f20\u6765\u3002</p><p>\u201c\u6211\u8bf4\u53ea\u7528\u4e00\u51fb\u5c31\u8981\u6253\u5012\u4f60\u561b\u3002\u201d\u5979\u5f97\u610f\u6d0b\u6d0b\u5730\u505a\u7740\u80dc\u5229\u5ba3\u544a\u3002</p><p>\u64c2\u53f0\u4e0a\u7a7a\u7684\u5149\u5934\u8001\u4eba\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u9632\u62a4\u5deb\u5e08\u8eab\u4e0a\u7684\u706b\u7130\u77ac\u95f4\u7184\u706d\u4e86\uff0c\u7136\u800c\u4ed6\u4e5f\u5df2\u7ecf\u5944\u5944\u4e00\u606f\uff0c\u88ab\u62ac\u4e86\u4e0b\u53bb\u3002\u201c\u51db\u83b7\u80dc\uff0c\u201d\u8001\u4eba\u6beb\u65e0\u611f\u60c5\u5730\u5ba3\u5e03\u7740\u6bd4\u8d5b\u7ed3\u679c\uff0c\u201c\u4e0b\u4e00\u573a\uff0c\u5492\u6cd5\u5b66\u6d3e\u674e\u65af\u7279\u5bf9\u4ea1\u7075\u5b66\u6d3e\u8d39\u5357\u591a\u3002\u201d</p><p>\u8981\u8bba\u6280\u672f\u542b\u91cf\uff0c\u7b2c\u4e8c\u573a\u6bd4\u8d5b\u6bd4\u7b2c\u4e00\u573a\u6bd4\u8d5b\u7cbe\u5f69\u5f97\u591a\uff0c\u8fd9\u4e00\u6218\u8db3\u8db3\u6253\u4e86\u5c06\u8fd1\u5341\u5206\u949f\uff0c\u4e24\u4f4d\u9009\u624b\u771f\u6b63\u5c55\u73b0\u51fa\u4e86\u7ea2\u888d\u5deb\u5e08\u7684\u7d20\u8d28\uff0c\u5404\u5c55\u6240\u957f\u7a77\u6781\u5de7\u5999\uff0c\u5404\u79cd\u4ece\u5f02\u6b21\u5143\u53ec\u5524\u6765\u7684\u602a\u7269\u548c\u9ab7\u9ac5\u4ea1\u7075\u4eec\u5728\u64c2\u53f0\u4e0a\u93d6\u6218\u53ae\u6740\uff0c\u5c40\u9762\u4e00\u5ea6\u50f5\u6301\u4e0d\u4e0b\uff0c\u6700\u7ec8\u8fd8\u662f\u5492\u6cd5\u5deb\u5e08\u6280\u9ad8\u4e00\u7b79\uff0c\u4ece\u4e0b\u5c42\u754c\u62db\u6765\u4e00\u53ea\u70bc\u72f1\u56db\u81c2\u7329\u7329\uff0c\u624b\u6301\u5927\u68d2\u6a2a\u626b\u5168\u519b\uff0c\u628a\u4ea1\u7075\u5deb\u5e08\u8d76\u4e0b\u4e86\u64c2\u53f0\u3002</p><p>\u6bd4\u8d5b\u770b\u5b8c\uff0c\u743c\u6069\u968f\u540c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u56de\u53bb\uff0c\u4ed6\u5fc3\u4e2d\u5b9e\u5728\u7591\u60d1\u4e0d\u89e3\uff0c\u4fbf\u5411\u738b\u5b50\u8bf7\u6559\u3002\u201c\u90a3\u4e2a\u53eb\u51db\u7684\u5deb\u5e08\uff0c\u600e\u4e48\u65bd\u5c55\u706b\u7403\u672f\u7684\u65f6\u5019\u7528\u7684\u662f\u5b9d\u77f3\uff1f\u201d</p><p>\u201c\u90a3\u662f\u5b9d\u77f3\u9b54\u6cd5\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u4e00\u79cd\u5931\u4f20\u5f88\u4e45\u7684\u9b54\u6cd5\u6280\u827a\u4e86\u3002\u201d</p><p>\u548c\u743c\u6069\u5f53\u65e5\u5728\u77ee\u4eba\u57ce\u5e02\u963f\u5fb7\u5df4\u57ce\u89c1\u8bc6\u8fc7\u7684\u201c\u7b26\u6587\u9b54\u6cd5\u201d\u5f88\u7c7b\u4f3c\uff0c\u5b9d\u77f3\u9b54\u6cd5\u662f\u5deb\u5e08\u628a\u6bcf\u65e5\u51c6\u5907\u597d\u7684\u6cd5\u672f\u704c\u6ce8\u5230\u5b9d\u77f3\u4e2d\uff0c\u65bd\u6cd5\u65f6\u4fbf\u65e0\u9700\u518d\u8bf5\u5492\u3001\u52a8\u4f5c\u3001\u53d6\u6750\u6599\u7b49\u6b65\u9aa4\uff0c\u53ea\u8981\u628a\u5b9d\u77f3\u7838\u51fa\u53bb\u6216\u8005\u6309\u7167\u9884\u5148\u8bbe\u5b9a\u7684\u65b9\u5f0f\u542f\u52a8\u5373\u53ef\uff0c\u6240\u4ee5\u65bd\u6cd5\u901f\u5ea6\u6bd4\u666e\u901a\u5deb\u5e08\u8981\u5feb\u5f88\u591a\u3002</p><p>\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u56e0\u4e3a\u662f\u501f\u52a9\u5b9d\u77f3\u65bd\u6cd5\uff0c\u800c\u4f60\u77e5\u9053\u7684\uff0c\u5f88\u591a\u5b9d\u77f3\u672c\u8eab\u5c31\u6709\u9b54\u6cd5\u529b\u91cf\u3002\u5c31\u50cf\u5979\u7528\u7684\u706b\u86cb\u767d\u77f3\u3001\u7ea2\u9506\u77f3\u3001\u7ea2\u5b9d\u77f3\u8fd9\u4e9b\uff0c\u90fd\u662f\u80fd\u5f3a\u5316\u706b\u7130\u9b54\u6cd5\u5a01\u529b\u7684\u3002\u201d</p><p>\u738b\u5b50\u89e3\u91ca\u8bf4\uff0c\u51db\u4eca\u5929\u91ca\u653e\u7684\u90a3\u9897\u706b\u7403\uff0c\u5c31\u662f\u704c\u6ce8\u5728\u4e00\u9897\u7ea2\u9506\u77f3\u91cc\u9762\uff0c\u6240\u4ee5\u5a01\u529b\u6bd4\u666e\u901a\u706b\u7403\u63d0\u5347\u4e86\u597d\u51e0\u500d\uff0c\u96be\u602a\u90a3\u4e2a\u5492\u6cd5\u5deb\u5e08\u91cd\u91cd\u9632\u62a4\uff0c\u8fde\u7528\u706b\u7130\u62a4\u76fe\u548c\u9632\u62a4\u706b\u7130\u80fd\u91cf\u4f24\u5bb3\u90fd\u62b5\u6321\u4e0d\u4f4f\u3002\u201c\u4e0d\u8fc7\u5f88\u5947\u602a\uff0c\u201d\u738b\u5b50\u76b1\u7709\uff0c\u201c\u6309\u9053\u7406\u8bf4\uff0c\u706b\u7403\u672f\u867d\u7136\u5a01\u529b\u5f3a\u5927\uff0c\u7ec8\u7a76\u8c03\u7528\u7684\u4e5f\u4e0d\u8fc7\u662f\u9b54\u7f51\u7b2c\u4e09\u5c42\u7684\u529b\u91cf\uff0c\u6b21\u7ea7\u6cd5\u672f\u65e0\u6548\u7ed3\u754c\u7406\u5f53\u662f\u80fd\u6321\u4f4f\u624d\u5bf9\u5440\u3002\u201d</p><p>\u738b\u5b50\u65e2\u7136\u90fd\u7591\u60d1\uff0c\u743c\u6069\u81ea\u7136\u66f4\u60f3\u4e0d\u660e\u767d\uff0c\u4ed6\u5173\u5fc3\u7684\u662f\u53e6\u5916\u4e00\u4e2a\u95ee\u9898\uff1a\u5b9d\u77f3\u9b54\u6cd5\u548c\u7b26\u6587\u9b54\u6cd5\u542c\u8d77\u6765\u5f88\u7c7b\u4f3c\uff0c\u540c\u6837\u90fd\u662f\u628a\u51c6\u5907\u7684\u6cd5\u672f\u704c\u6ce8\u5230\u7269\u54c1\u4e0a\uff0c\u501f\u52a9\u7269\u54c1\u76f4\u63a5\u65bd\u6cd5\uff0c\u8fd9\u79cd\u505a\u6cd5\u65e0\u7591\u66f4\u5feb\u6377\u66f4\u65b9\u4fbf\uff0c\u6bd4\u666e\u901a\u7684\u65bd\u6cd5\u65b9\u5f0f\u6548\u7387\u9ad8\u3002\u4f46\u90a3\u4e9b\u77ee\u4eba\u4eec\u80fd\u638c\u63e1\u7b26\u6587\u9b54\u6cd5\uff0c\u9760\u7684\u662f\u201c\u96f7\u9e23\u4e4b\u5b50\u201d\u8fd9\u79cd\u795e\u6388\u8840\u8109\uff0c\u90a3\u4e48\u8fd9\u79cd\u5b9d\u77f3\u9b54\u6cd5\uff0c\u662f\u4eba\u4eba\u90fd\u80fd\u5b66\u4e60\u638c\u63e1\u7684\u4e48\uff1f\u5982\u679c\u80fd\u591f\u7684\u8bdd\uff0c\u53ea\u6015\u5f88\u591a\u5deb\u5e08\u90fd\u8981\u8d8b\u4e4b\u82e5\u9e5c\u4e86\u5427\u3002\u867d\u7136\u6bcf\u65bd\u4e00\u6b21\u6cd5\u672f\u5c31\u8981\u8017\u8d39\u4e00\u9897\u5b9d\u77f3\uff0c\u662f\u592a\u5962\u4f88\u4e86\u70b9\uff0c\u4f46\u5deb\u5e08\u672c\u6765\u5c31\u662f\u70e7\u94b1\u7684\u804c\u4e1a\uff0c\u80fd\u8fdb\u5165\u8fd9\u4e00\u884c\u7684\uff0c\u5f88\u591a\u4e5f\u90fd\u662f\u5bcc\u8c6a\u51fa\u8eab\uff0c\u672a\u5fc5\u591a\u4e48\u5728\u4e4e\u3002</p><p>\u201c\u5b66\u4e0d\u4f1a\u7684\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u80fd\u5426\u638c\u63e1\u5b9d\u77f3\u9b54\u6cd5\uff0c\u9760\u7684\u5b8c\u5168\u662f\u5929\u8d4b\uff0c\u540e\u5929\u518d\u600e\u4e48\u52aa\u529b\u4e5f\u6ca1\u7528\u3002\u8fd9\u4f4d\u51db\u5c0f\u59d0\u2026\u2026\u201d\u4ed6\u987f\u4e86\u987f\uff0c\u201c\u53ea\u6015\u6765\u5386\u975e\u51e1\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5173\u4e8e\u5979\u7684\u8d44\u6599\u4e48\uff1f\u201d\u743c\u6069\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u6ca1\u6709\uff0c\u53ea\u77e5\u9053\u5979\u5341\u4e8c\u5c81\u7684\u65f6\u5019\u51fa\u73b0\u5728\u585e\u5c14\uff0c\u88ab\u5851\u80fd\u5b66\u6d3e\u9996\u5e2d\u8ff0\u5c14\u5148\u751f\u4e00\u773c\u770b\u4e2d\uff0c\u6536\u4e3a\u95e8\u4e0b\uff0c\u5f53\u65f6\u5979\u5df2\u7ecf\u662f\u4e2a\u5c0f\u6709\u6240\u6210\u7684\u5deb\u5e08\uff0c\u62e5\u6709\u5236\u4f5c\u9b54\u6cd5\u5b9d\u77f3\u7684\u80fd\u529b\u4e86\uff0c\u5728\u6b64\u4e4b\u524d\u7684\u4e8b\u60c5\u5c31\u4e00\u65e0\u6240\u77e5\u3002\u52a0\u5165\u7ea2\u888d\u5deb\u5e08\u4f1a\u540e\uff0c\u4e24\u5e74\u65f6\u95f4\u6210\u4e3a\u6b63\u5f0f\u7ea2\u888d\uff0c\u4eca\u5e74\u2026\u2026\u201d\u738b\u5b50\u7b97\u4e86\u7b97\uff0c\u201c\u5e94\u8be5\u5feb\u5341\u516d\u5c81\u4e86\u5427\u3002\u201d</p><p>\u201c\u5851\u80fd\u5b66\u6d3e\u9996\u5e2d\u7684\u5b66\u751f\uff1f\u201d\u743c\u6069\u604d\u7136\u5927\u609f\u5730\u70b9\u70b9\u5934\uff0c\u201c\u96be\u602a\u80fd\u7528\u5f97\u8d77\u8fd9\u4e48\u591a\u5b9d\u77f3\u3002\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u5012\u4e0d\u662f\uff0c\u201d\u738b\u5b50\u89e3\u91ca\uff0c\u201c\u5979\u6240\u6709\u7684\u5b9d\u77f3\u90fd\u662f\u81ea\u5df1\u7684\uff0c\u5e76\u975e\u8001\u5e08\u7684\u8d60\u9001\uff0c\u5979\u81ea\u79f0\u662f\u7236\u4eb2\u7684\u9057\u4ea7\u2014\u2014\u4f46\u6240\u6709\u4eba\u90fd\u4e0d\u77e5\u9053\u5979\u7236\u4eb2\u662f\u8c01\uff0c\u800c\u4e14\u4f60\u770b\u5230\u4e86\uff0c\u5979\u7684\u540d\u5b57\u53ea\u6709\u4e00\u4e2a\u5355\u5b57\u2018\u51db\u2019\uff0c\u4e0d\u80af\u900f\u9732\u7236\u59d3\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u603b\u4e4b\u8fd9\u4f4d\u51db\u5c0f\u59d0\u4e0d\u50cf\u4e2a\u6b63\u5e38\u4eba\u3002\u81ea\u5df1\u649e\u4e0a\u7684\uff0c\u679c\u7136\u90fd\u4e0d\u4f1a\u662f\u6b63\u5e38\u4eba\u5462\u2026\u2026\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u81ea\u5df1\u8fd9\u4e2a\u7a7f\u8d8a\u8005\uff0c\u53ea\u6015\u4e5f\u8c08\u4e0d\u4e0a\u201c\u6b63\u5e38\u201d\u4e24\u4e2a\u5b57\u5427\u3002</p><p>\u56de\u5230\u4f4f\u5904\uff0c\u738b\u5b50\u5df2\u7ecf\u6d3e\u4eba\u7ed9\u4ed6\u548c\u6885\u83f2\u65af\u5b89\u6392\u597d\u4e86\u623f\u95f4\uff0c\u201c\u52a0\u6cb9\u554a\uff0c\u743c\u6069\u3002\u201d\u5728\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u738b\u5b50\u671d\u743c\u6069\u80a9\u8180\u4e0a\u62cd\u4e86\u62cd\uff0c\u7a81\u7136\u8bf4\u4e86\u8fd9\u53e5\u83ab\u540d\u5176\u5999\u7684\u8bdd\u3002\u4ed6\u671d\u6885\u83f2\u65af\u7684\u623f\u95f4\u6307\u4e86\u6307\uff0c\u8138\u4e0a\u7b11\u5f97\u5f88\u662f\u707f\u70c2\uff0c\u7136\u800c\u743c\u6069\u603b\u89c9\u5f97\u4f3c\u4e4e\u6709\u4e9b\u6deb\u8361\u2026\u2026</p><p>\u6885\u83f2\u65af\u6b63\u5728\u623f\u4e2d\u770b\u4e66\uff0c\u743c\u6069\u8fdb\u6765\uff0c\u7b80\u5355\u8bf4\u4e86\u4e0b\u60c5\u51b5\uff0c\u4f46\u6d89\u53ca\u5230\u7ea2\u888d\u5185\u90e8\u9690\u79d8\u7684\u4e8b\u60c5\uff0c\u4e00\u65f6\u4e5f\u4e0d\u65b9\u4fbf\u544a\u8bc9\u6885\u83f2\u65af\uff0c\u53ea\u8bf4\u4e86\u8bf4\u521a\u624d\u53bb\u770b\u664b\u9636\u8d5b\u7684\u89c1\u95fb\u3002\u6885\u83f2\u65af\u5bf9\u7ea2\u888d\u5deb\u5e08\u51b3\u6597\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u5174\u8da3\uff0c\u4e5f\u53ea\u662f\u6de1\u6de1\u5730\u542c\u7740\uff0c\u4f46\u5f53\u743c\u6069\u8bf4\u6709\u4e00\u4e2a\u53eb\u505a\u51db\u7684\u5973\u5deb\u5e08\u7a7f\u8d85\u77ed\u88d9\u4e0a\u573a\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u79c0\u7709\u5fae\u5fae\u4e00\u6311\uff0c\u795e\u8272\u8ba4\u771f\u8d77\u6765\u3002</p><p>\u201c\u90a3\u4e2a\u51db\u957f\u4ec0\u4e48\u6837\u7684\uff1f\u201d</p><p>\u743c\u6069\u7b80\u5355\u63cf\u8ff0\u4e86\u4e00\u4e0b\u51db\u7684\u76f8\u8c8c\uff0c\u91cd\u70b9\u5f3a\u8c03\u5979\u7684\u8d85\u77ed\u88d9\u548c\u957f\u7b52\u889c\uff0c\u4e0d\u8fc7\u6885\u83f2\u65af\u6240\u5173\u6ce8\u7684\u663e\u7136\u4e0d\u662f\u8fd9\u4e2a\u3002\u201c\u5979\u7528\u5b9d\u77f3\u65bd\u6cd5\uff1f\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u6885\u83f2\u65af\u82e5\u6709\u6240\u601d\u5730\u70b9\u70b9\u5934\uff0c\u201c\u660e\u5929\u662f\u51b3\u8d5b\u662f\u5427\uff0c\u6211\u8ddf\u4f60\u4e00\u8d77\u53bb\u3002\u201d</p></article>"}}}]);
