(window.webpackJsonp=window.webpackJsonp||[]).push([[1394],{1417:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e09\u8282 \u51db\u7684\u5b9e\u529b",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e09\u8282 \u51db\u7684\u5b9e\u529b</h2><p>\u5168\u573a\u5148\u662f\u6c89\u5bc2\u7247\u523b\uff0c\u968f\u5373\u4e00\u7247\u54d7\u7136\uff0c\u6240\u6709\u4eba\u90fd\u542c\u660e\u767d\u4e86\u51db\u7684\u610f\u601d\uff0c\u5979\u662f\u5728\u4ee5\u7ea2\u888d\u5deb\u5e08\u7684\u8eab\u4efd\uff0c\u5411\u9634\u9b42\u57ce\u7684\u5ba2\u4eba\u4e0b\u6311\u6218\u4e66\u3002</p><p>\u6b63\u5982\u51db\u6240\u8bf4\uff0c\u8eab\u4e3a\u5deb\u5e08\uff0c\u8010\u745f\u745e\u5c14\u662f\u6240\u6709\u4eba\u5fc3\u4e2d\u7684\u61a7\u61ac\u548c\u68a6\u60f3\uff0c\u662f\u65e0\u53ef\u4f01\u53ca\u7684\u707f\u70c2\u8f89\u714c\u3002\u7136\u800c\uff0c\u53e4\u5e1d\u56fd\u5df2\u7ecf\u9668\u706d\u5343\u5e74\uff0c\u4eca\u65e5\u7684\u201c\u9b54\u6cd5\u5e1d\u56fd\u201d\uff0c\u5df2\u7ecf\u662f\u7ea2\u888d\u5deb\u5e08\u4f1a\u5efa\u7acb\u7684\u585e\u5c14\u3002</p><p>\u9634\u9b42\u57ce\u4ec5\u6709\u4e00\u57ce\u4e4b\u5730\uff0c\u4e24\u4e07\u4f59\u4eba\uff0c\u81ea\u7136\u8fdc\u8fdc\u4e0d\u8db3\u4ee5\u4e0e\u585e\u5c14\u76f8\u8f83\uff0c\u4f46\u5374\u662f\u53e4\u9b54\u6cd5\u5e1d\u56fd\u7684\u6587\u660e\u4f20\u627f\u3002\u5f53\u4eca\u5927\u9646\uff0c\u5deb\u5e08\u6570\u91cf\u4e0d\u8db3\u4e07\u4eba\uff0c\u9634\u9b42\u57ce\u548c\u585e\u5c14\u5c31\u5404\u5360\u4e86\u4e00\u5343\uff0c\u53ef\u4ee5\u8bf4\u4ee3\u8868\u4e86\u5982\u4eca\u4e16\u4e0a\u6700\u9ad8\u7684\u9b54\u6cd5\u6c34\u51c6\u3002</p><p>\u90a3\u4e48\uff0c\u6240\u6709\u4eba\u5fc3\u4e2d\u5c31\u4e0d\u514d\u4f1a\u6709\u4e2a\u7591\u95ee\uff1a\u9634\u9b42\u57ce\u548c\u585e\u5c14\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u5230\u5e95\u8c01\u66f4\u80dc\u4e00\u7b79\uff1f\u6362\u53e5\u8bdd\u8bf4\uff0c\u6614\u65e5\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u548c\u5982\u4eca\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u7a76\u7adf\u662f\u8c01\u66f4\u540d\u526f\u5176\u5b9e\uff1f</p><p>\u662f\u6614\u80dc\u4e8e\u4eca\uff0c\u8fd8\u662f\u4eca\u80dc\u4e8e\u6614\uff1f</p><p>\u60f3\u6cd5\u4eba\u4eba\u90fd\u6709\uff0c\u6562\u8fd9\u6837\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u516c\u7136\u6311\u6218\u7684\uff0c\u5374\u662f\u9664\u4e86\u8fd9\u4f4d\u51db\u5c0f\u59d0\u518d\u65e0\u4eba\u6709\u8fd9\u822c\u80c6\u91cf\u3002\u7136\u800c\u5979\u8bdd\u5df2\u7ecf\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\u8bf4\u51fa\u6765\uff0c\u4fbf\u4e0d\u53ef\u80fd\u5f53\u4f5c\u6ca1\u542c\u89c1\uff0c\u4f55\u51b5\u53c8\u5df2\u7ecf\u9690\u9690\u6697\u793a\u8fd9\u5e76\u975e\u4e00\u5df1\u4e89\u6597\uff0c\u4ee3\u8868\u7684\u4e43\u662f\u4e24\u4ee3\u9b54\u6cd5\u5e1d\u56fd\u7684\u6c34\u51c6\u8f83\u91cf\u3002\u9634\u9b42\u57ce\u5018\u82e5\u4e0d\u505a\u56de\u5e94\uff0c\u5c82\u4e0d\u7b49\u4e8e\u662f\u81ea\u52a8\u8ba4\u8f93\u4e86\u3002</p><p>\u7136\u800c\u9634\u9b42\u57ce\u5982\u4eca\u5728\u573a\u7684\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u548c\u743c\u6069\u3002\u51db\u8bf4\u8bdd\u7684\u5bf9\u8c61\uff0c\u8868\u9762\u4e0a\u4e00\u76f4\u662f\u6307\u5411\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u53ea\u662f\u5018\u82e5\u738b\u5b50\u771f\u80af\u4e0a\u573a\uff0c\u8981\u51fb\u8d25\u51db\u81ea\u7136\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u4f46\u90a3\u4fbf\u662f\u4ee5\u5927\u6b3a\u5c0f\uff0c\u80dc\u4e86\u4e5f\u6beb\u65e0\u5149\u5f69\uff0c\u53cd\u800c\u60f9\u4eba\u7b11\u8bdd\u3002\u90a3\u4e48\uff0c\u4eba\u9009\u5c31\u53ea\u5269\u4e0b\u4e00\u4e2a\uff0c\u5012\u4e5f\u662f\u5f88\u5408\u9002\u7684\u4e00\u4e2a\uff0c\u5c31\u662f\u540c\u6837\u4e5f\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\uff0c\u9020\u8be3\u548c\u51db\u76f8\u5f53\u7684\u743c\u6069\u4e86\u3002</p><p>\u201c\u738b\u5b50\u6025\u62db\u6211\u6765\u585e\u5c14\uff0c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u5ff5\u5934\u95ea\u8fc7\uff0c\u671d\u8bc4\u59d4\u5e2d\u4e0a\u770b\u53bb\uff0c\u5374\u89c1\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8138\u4e0a\u4e5f\u5fae\u5fae\u6709\u8bb6\u5f02\u4e4b\u8272\uff0c\u663e\u7136\u6ca1\u9884\u6599\u5230\u4f1a\u6709\u8fd9\u4e00\u51fa\u3002\u4ed6\u548c\u8428\u624e\u65af\u5766\u5148\u751f\u4f4e\u58f0\u4ea4\u8c08\u4e86\u51e0\u53e5\uff0c\u7136\u540e\u8fdc\u8fdc\u671d\u743c\u6069\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u65e2\u7136\u51db\u5c0f\u59d0\u6709\u5982\u6b64\u5174\u81f4\uff0c\u6211\u53c8\u5c82\u80fd\u63a8\u5374\uff0c\u201d\u738b\u5b50\u6e29\u548c\u5730\u7b11\u7740\uff0c\u201c\u53ea\u662f\u6211\u6700\u8fd1\u8eab\u4f53\u4e0d\u9002\uff0c\u8fd9\u6837\u5427\uff0c\u5c31\u7531\u6211\u7684\u526f\u624b\u5170\u5c3c\u65af\u7279\u5148\u751f\u4ee3\u6211\u4e0a\u573a\uff0c\u9886\u6559\u7ea2\u888d\u5deb\u5e08\u7684\u7cbe\u5999\u5965\u4e49\u5982\u4f55\u3002\u201d</p><p>\u738b\u5b50\u65e2\u7136\u8bf4\u4e86\u8bdd\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u6709\u53d1\u8868\u53cd\u5bf9\u610f\u89c1\u7684\u4f59\u5730\u3002\u4f55\u51b5\u5c31\u5982\u6b64\u524d\u6240\u60f3\u7684\uff0c\u9634\u9b42\u57ce\u7684\u4f7f\u8005\u53ea\u6709\u4e24\u4e2a\uff0c\u738b\u5b50\u81ea\u5df1\u4e0d\u53ef\u80fd\u4e0b\u573a\uff0c\u90a3\u8fd9\u5de5\u4f5c\u5c31\u6beb\u65e0\u9009\u62e9\u5730\u5fc5\u987b\u7531\u743c\u6069\u6765\u627f\u62c5\u4e86\u3002</p><p>\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u7136\u800c\u5728\u4e1d\u6beb\u6ca1\u6709\u51c6\u5907\u7684\u60c5\u51b5\u4e0b\u88ab\u63a8\u4e0a\u573a\u51b3\u6597\uff0c\u8fd9\u5bf9\u5deb\u5e08\u6765\u8bf4\u662f\u5f88\u7cdf\u7cd5\u7684\u5c40\u9762\u5427\u3002\u743c\u6069\u4eca\u5929\u5f53\u7136\u4e5f\u51c6\u5907\u4e86\u9b54\u6cd5\uff0c\u4f46\u5e76\u6ca1\u6709\u51e0\u4e2a\u80fd\u7528\u6765\u62b5\u6297\u706b\u7130\u7684\u6cd5\u672f\uff0c\u8c01\u4f1a\u9884\u6599\u5230\u8981\u53bb\u9762\u5bf9\u4e00\u4e2a\u559c\u6b22\u75af\u72c2\u7838\u706b\u7403\u800c\u4e14\u4e5f\u53ea\u4f1a\u7838\u706b\u7403\u7684\u5deb\u5e08\u5462\u3002</p><p>\u5e78\u597d\uff0c\u738b\u5b50\u66ff\u4ed6\u89e3\u4e86\u56f4\uff0c\u201c\u4eca\u5929\u662f\u8d35\u4f1a\u7684\u664b\u9636\u8d5b\u51b3\u8d5b\uff0c\u6211\u60f3\u6211\u4eec\u4e0d\u4fbf\u6253\u6270\uff0c\u201d\u4ed6\u5f6c\u5f6c\u6709\u793c\u5730\u5bf9\u8428\u624e\u65af\u5766\u8bf4\uff0c\u201c\u55a7\u5bbe\u593a\u4e3b\u662f\u5f88\u5931\u793c\u7684\u884c\u4e3a\uff0c\u4f55\u51b5\u51db\u5c0f\u59d0\u5df2\u7ecf\u521a\u521a\u6fc0\u6218\u8fc7\u4e00\u573a\uff0c\u76f8\u4fe1\u4e5f\u8feb\u5207\u9700\u8981\u4f11\u606f\u5427\u3002\u6211\u770b\u8fd9\u573a\u6bd4\u8d5b\u4e0d\u59a8\u7a0d\u7a0d\u5ef6\u540e\uff0c\u660e\u5929\u4e3e\u884c\u4e0d\u8fdf\uff0c\u60a8\u8ba4\u4e3a\u5462\uff0c\u9996\u5e2d\u9601\u4e0b\u3002\u201d</p><p>\u8428\u624e\u65af\u5766\u70b9\u5934\u8d5e\u540c\uff0c\u5ba3\u5e03\u743c\u6069\u548c\u51db\u7684\u5deb\u5e08\u51b3\u6597\u660e\u5929\u4e3e\u884c\uff0c\u8fd9\u79cd\u5b89\u6392\u5408\u60c5\u5408\u7406\uff0c\u6240\u4ee5\u5927\u5bb6\u4e5f\u5168\u65e0\u5f02\u8bae\uff0c\u53cd\u5012\u8d8a\u53d1\u671f\u5f85\u8d77\u6765\u3002\u8c01\u4e5f\u4e0d\u66fe\u60f3\u5230\uff0c\u5728\u7cbe\u5f69\u7684\u664b\u9636\u8d5b\u7ed3\u675f\u540e\uff0c\u539f\u672c\u4ee5\u4e3a\u6ce2\u6f9c\u5c06\u606f\uff0c\u8c01\u77e5\u5c45\u7136\u53c8\u9ad8\u6f6e\u518d\u8d77\u3002\u743c\u6069\u5bf9\u51db\u3001\u9634\u9b42\u57ce\u5bf9\u585e\u5c14\u3001\u8fdc\u53e4\u9b54\u6cd5\u5e1d\u56fd\u7684\u8010\u745f\u4f20\u627f\u5bf9\u4eca\u65e5\u9b54\u6cd5\u5e1d\u56fd\u7684\u7ea2\u888d\u79d8\u672f\uff0c\u8fd9\u4e00\u5207\u8ba9\u6240\u6709\u7ea2\u888d\u5deb\u5e08\u548c\u5b66\u5f92\u7684\u795e\u7ecf\u90fd\u5174\u594b\u8d77\u6765\uff0c\u4ed6\u4eec\u8feb\u4e0d\u53ca\u5f85\u5730\u7b49\u5f85\u7740\u660e\u5929\u7684\u5230\u6765\u3002</p><p>\u5f53\u7136\uff0c\u6700\u540e\u4f46\u7edd\u975e\u6700\u4e0d\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff1a\u743c\u6069\u662f\u4e2a\u7537\u6027\uff0c\u6240\u4ee5\u8fd9\u6837\u4e00\u6765\uff0c\u5927\u5bb6\u53c8\u53ef\u4ee5\u770b\u5230\u51db\u7a7f\u90a3\u8eab\u8d85\u77ed\u88d9\u767b\u573a\u4e86\uff0c\u4e0d\u5fc5\u7b49\u5230\u660e\u5e74\u7684\u664b\u9636\u8d5b\u3002\u8fd9\u5bf9\u4e8e\u7edd\u5927\u591a\u6570\u6210\u5458\u90fd\u662f\u7537\u6027\u7684\u7ea2\u888d\u5deb\u5e08\u6765\u8bf4\uff0c\u662f\u4e2a\u5927\u5927\u7684\u597d\u6d88\u606f\u3002</p><p>\u6bd4\u8d5b\u7ed3\u675f\u540e\uff0c\u6309\u7167\u89c4\u77e9\u662f\u6388\u5956\u4eea\u5f0f\uff0c\u7531\u9884\u8a00\u5b66\u6d3e\u7684\u96c5\u83f2\u5c14\u5973\u58eb\u51fa\u9762\uff0c\u4e3a\u51db\u6234\u4e0a\u4e86\u4e00\u53ea\u6d45\u7eff\u8272\u6302\u5760\u3002\u743c\u6069\u4e0d\u77e5\u9053\u90a3\u5230\u5e95\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u592a\u5728\u610f\uff0c\u53cd\u6b63\u6309\u7167\u89c4\u77e9\uff0c\u5deb\u5e08\u51b3\u6597\u4e0d\u80fd\u501f\u52a9\u88c5\u5907\uff0c\u5c31\u7b97\u662f\u795e\u5668\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\u3002</p><p>\u5f53\u8fd9\u4e00\u5207\u505a\u5b8c\uff0c\u5404\u4f4d\u9996\u5e2d\u548c\u8bc4\u59d4\u4eec\u7eb7\u7eb7\u79bb\u53bb\uff0c\u738b\u5b50\u5e26\u7740\u743c\u6069\u5750\u9a6c\u8f66\u56de\u4f4f\u5904\uff0c\u6885\u83f2\u65af\u5374\u88ab\u51db\u62c9\u53bb\u4e86\uff0c\u8bf4\u662f\u5e26\u5979\u53bb\u901b\u8857\u8d2d\u7269\u3002\u770b\u7740\u4e24\u4e2a\u7f8e\u5c11\u5973\u624b\u7275\u7740\u624b\u4e00\u8def\u8c08\u7b11\u7740\u79bb\u5f00\u8d5b\u573a\uff0c\u743c\u6069\u6000\u7591\u81ea\u5df1\u662f\u4e0d\u662f\u51fa\u73b0\u4e86\u9519\u89c9\uff0c\u4ed6\u4ece\u6ca1\u60f3\u5230\u6885\u83f2\u65af\u4e5f\u4f1a\u6709\u8fd9\u4e48\u201c\u5c11\u5973\u201d\u7684\u4e00\u9762\u3002</p><p>\u5514\uff0c\u5979\u4eec\u770b\u6837\u5b50\u771f\u7684\u662f\u5f88\u597d\u7684\u670b\u53cb\u5462\u2026\u2026\u4f46\u4e3a\u4ec0\u4e48\u603b\u89c9\u5f97\u54ea\u91cc\u53c8\u602a\u602a\u7684\u3002\u800c\u4e14\uff0c\u65e2\u7136\u662f\u597d\u670b\u53cb\uff0c\u90a3\u770b\u5728\u6885\u83f2\u65af\u7684\u4efd\u4e0a\uff0c\u4e5f\u4e0d\u5e94\u8be5\u4e00\u89c1\u9762\u5c31\u5411\u6211\u53d1\u51fa\u6311\u6218\u5427\u3002</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u5728\u76ee\u7779\u4e86\u4e24\u573a\u6bd4\u8d5b\u540e\uff0c\u743c\u6069\u5e76\u4e0d\u5f88\u613f\u610f\u548c\u8fd9\u4f4d\u51db\u5c0f\u59d0\u4ea4\u624b\u3002\u867d\u7136\u5979\u504f\u79d1\u504f\u5f97\u5389\u5bb3\uff0c\u4e0d\u4f46\u653e\u5f03\u4e86\u4e09\u7cfb\u6cd5\u672f\uff0c\u800c\u4e14\u53ea\u4f1a\u7528\u706b\u7130\u9b54\u6cd5\u4f5c\u4e3a\u653b\u51fb\u624b\u6bb5\uff0c\u7136\u800c\u5fc5\u987b\u627f\u8ba4\u7684\u662f\uff0c\u5979\u7684\u706b\u7403\u771f\u7684\u5f88\u5f3a\u608d\u3002\u6628\u5929\u4e00\u53d1\u706b\u7403\u8f70\u51fa\u6765\uff0c\u8fde\u90a3\u4f4d\u4e13\u7cbe\u9632\u62a4\u7684\u7ea2\u888d\u5deb\u5e08\u90fd\u63a5\u4e0d\u4e0b\uff0c\u867d\u7136\u5e94\u8be5\u4e5f\u662f\u6709\u67d0\u79cd\u51fa\u5947\u5236\u80dc\u7684\u53d6\u5de7\u56e0\u7d20\u5728\u5176\u4e2d\uff0c\u4f46\u4f9d\u65e7\u8db3\u4ee5\u8ba9\u743c\u6069\u5927\u76b1\u7709\u5934\u4e86\u3002</p><p>\u66f4\u4f55\u51b5\uff0c\u5982\u4eca\u5df2\u7ecf\u4e0d\u662f\u5355\u7eaf\u7684\u4e24\u4e2a\u5deb\u5e08\u51b3\u6597\uff0c\u80cc\u540e\u4ee3\u8868\u7684\u53ef\u662f\u9634\u9b42\u57ce\u548c\u585e\u5c14\uff0c\u4ee3\u8868\u7684\u662f\u4e0d\u540c\u65f6\u4ee3\u4e24\u5927\u9b54\u6cd5\u5e1d\u56fd\u5462\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u4f1a\u7a81\u7136\u88ab\u8d4b\u4e88\u8fd9\u4e48\u91cd\u8981\u7684\u4f7f\u547d\u5462\u3002\u201d\u743c\u6069\u82e6\u7b11\u7740\uff0c\u770b\u738b\u5b50\u7684\u795e\u8272\uff0c\u8fd9\u4ef6\u4e8b\u5e76\u975e\u4ed6\u9884\u5148\u5b89\u6392\uff0c\u90a3\u5c31\u5b8c\u5168\u662f\u90a3\u4f4d\u51db\u5c0f\u59d0\u7684\u5fc3\u8840\u6765\u6f6e\u4e86\u3002\u53ea\u662f\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u5df2\u7ecf\u65e0\u6240\u8c13\u7f18\u7531\u4e86\u5427\uff0c\u5173\u952e\u5728\u4e8e\u7ed3\u5c40\u3002</p><p>\u738b\u5b50\u8212\u9002\u5730\u5750\u5728\u8f66\u53a2\u5bf9\u9762\uff0c\u9ed8\u7136\u6c89\u601d\u7740\uff0c\u81ea\u4ece\u4e0a\u8f66\u4ee5\u6765\u5c31\u6ca1\u6709\u8bf4\u8bdd\uff0c\u743c\u6069\u4e5f\u4e0d\u6562\u6253\u6270\u3002\u5927\u7ea6\u8d70\u5230\u534a\u8def\uff0c\u738b\u5b50\u7a81\u7136\u62ac\u8d77\u5934\u6765\uff0c\u5c55\u989c\u5fae\u7b11\uff0c\u201c\u771f\u6709\u8da3\u5462\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u867d\u7136\u5b8c\u5168\u4e0d\u5728\u610f\u6599\u4e4b\u4e2d\u3002\u4e0d\u8fc7\u5012\u4e5f\u4e0d\u662f\u4ec0\u4e48\u574f\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u2026\u2026\u5514\uff0c\u603b\u4e4b\uff0c\u53ea\u8981\u6253\u8d62\u4e86\u5c31\u4e00\u5207\u90fd\u65e0\u6240\u8c13\u5427\u3002\u201d</p><p>\u662f\u554a\uff0c\u53ea\u8981\u6253\u8d62\u4e86\u81ea\u7136\u5c31\u4e00\u5207\u597d\u8bf4\u3002\u53ea\u662f\u5766\u767d\u5730\u8bf4\uff0c\u743c\u6069\u5bf9\u8fd9\u573a\u7a81\u5982\u5176\u6765\u7684\u6bd4\u8d5b\uff0c\u5e76\u65e0\u591a\u5c11\u5fc5\u80dc\u7684\u628a\u63e1\u5462\u3002</p><p>\u201c\u786e\u5b9e\u662f\u633a\u96be\u5bf9\u4ed8\uff0c\u201d\u738b\u5b50\u8f7b\u8f7b\u6309\u7740\u81ea\u5df1\u7684\u773c\u89d2\uff0c\u201c\u867d\u8bf4\u6211\u4e5f\u4e0d\u592a\u559c\u6b22\u5851\u80fd\u9b54\u6cd5\uff0c\u4f46\u4e0d\u5f97\u4e0d\u627f\u8ba4\u7684\u662f\uff0c\u4ee5\u76f4\u63a5\u7684\u653b\u51fb\u529b\u800c\u8bba\uff0c\u5851\u80fd\u9b54\u6cd5\u786e\u5b9e\u662f\u516b\u5927\u5b66\u6d3e\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u5728\u8fd9\u79cd\u5c0f\u8303\u56f4\u7684\u64c2\u53f0\u8d5b\u4e0a\u66f4\u80fd\u53d1\u6325\u5176\u5a01\u529b\uff0c\u56e0\u4e3a\u6ca1\u6709\u7f13\u51b2\u7684\u4f59\u5730\uff0c\u4e5f\u65e0\u6cd5\u501f\u52a9\u5730\u7406\u73af\u5883\u2026\u2026\u5514\uff0c\u518d\u600e\u4e48\u8bf4\uff0c\u4f60\u6240\u7cbe\u7814\u7684\u53d8\u5316\u9b54\u6cd5\uff0c\u786e\u5b9e\u662f\u5e76\u4e0d\u9002\u5408\u8fd9\u79cd\u786c\u78b0\u786c\u7684\u6b63\u9762\u5bf9\u51b3\u5462\u3002\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u738b\u5b50\u8bf4\u5f97\u662f\u6beb\u65e0\u7591\u95ee\u7684\u4e8b\u5b9e\uff0c\u8fd9\u4e5f\u662f\u4ed6\u4e0d\u592a\u60c5\u613f\u548c\u51db\u5bf9\u51b3\u7684\u539f\u56e0\u3002\u7136\u800c\uff0c\u73b0\u5728\u4e0d\u662f\u5df2\u7ecf\u6ca1\u6709\u9000\u8def\u4e86\u4e48\u3002</p><p>\u201c\u5e78\u597d\u6211\u4eec\u8fd8\u6709\u534a\u5929\u7684\u65f6\u95f4\u51c6\u5907\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u867d\u7136\u7d27\u4e86\u70b9\uff0c\u4e0d\u8fc7\u4e5f\u8db3\u591f\u4e86\u3002\u4f60\u5148\u4e86\u89e3\u4e00\u4e0b\u4f60\u7684\u5bf9\u624b\uff0c\u7136\u540e\u6211\u4eec\u518d\u8003\u8651\u600e\u4e48\u5e94\u5bf9\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u6240\u8c13\u4e86\u89e3\u5bf9\u624b\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u8dd1\u53bb\u5411\u7ea2\u888d\u5deb\u5e08\u4eec\u6253\u63a2\u6d88\u606f\u3002\u738b\u5b50\u5c06\u743c\u6069\u5e26\u8fdb\u4ed6\u7684\u623f\u95f4\uff0c\u5ff5\u4e86\u53e5\u5492\u8bed\uff0c\u4f38\u624b\u4ece\u7a7a\u6c14\u4e2d\u53d6\u51fa\u51e0\u9897\u6d45\u84dd\u8272\u7684\u6c34\u6676\u7403\u6765\u3002</p><p>\u201c\u8bb0\u5fc6\u6c34\u6676\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5e78\u597d\u6211\u628a\u6b64\u524d\u6bd4\u8d5b\u7684\u60c5\u5f62\u5168\u90fd\u8bb0\u5f55\u4e86\u4e0b\u6765\uff0c\u8d44\u6599\u5907\u4efd\u561b\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u8fd9\u51e0\u9897\u6c34\u6676\u91cc\u8bb0\u5f55\u7684\u662f\u90a3\u4f4d\u51db\u5c0f\u59d0\u7684\u6240\u6709\u6bd4\u8d5b\uff0c\u4f60\u5148\u770b\u770b\u5427\u3002\u201d</p><p>\u8bb0\u5fc6\u6c34\u6676\u53ea\u80fd\u5b58\u50a8\u5f88\u77ed\u7684\u5f71\u50cf\uff0c\u4f46\u591f\u7528\u4e86\uff0c\u56e0\u4e3a\u51db\u7684\u6bcf\u573a\u6bd4\u8d5b\u90fd\u8d62\u5f97\u5341\u5206\u5e72\u8106\u5229\u843d\uff0c\u4ece\u6765\u6ca1\u8d85\u8fc7\u4e09\u5206\u949f\u7684\u3002\u743c\u6069\u82b1\u4e86\u534a\u4e2a\u5c0f\u65f6\uff0c\u4ed4\u4ed4\u7ec6\u7ec6\u5730\u53cd\u590d\u770b\u4e86\u4e24\u904d\uff0c\u5c06\u6c34\u6676\u7403\u653e\u56de\u5230\u684c\u4e0a\u3002</p><p>\u201c\u600e\u4e48\u6837\uff0c\u770b\u51fa\u4ec0\u4e48\u4e1c\u897f\u6ca1\u6709\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u4ece\u8fd9\u4e9b\u6bd4\u8d5b\u6765\u770b\uff0c\u5979\u53ea\u4f1a\u7528\u706b\u7130\u9b54\u6cd5\u653b\u51fb\uff0c\u5bf9\u5176\u4ed6\u7c7b\u578b\u7684\u9b54\u6cd5\uff0c\u867d\u7136\u4e0d\u6562\u8bf4\u5979\u4e0d\u4f1a\uff0c\u4f46\u5fc5\u5b9a\u5f88\u4e0d\u7cbe\u901a\u3002\u5979\u7684\u5bf9\u624b\u663e\u7136\u4e5f\u90fd\u975e\u5e38\u786e\u5b9a\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u94f8\u9020\u7684\u9632\u5fa1\u5168\u90fd\u662f\u9488\u5bf9\u706b\u7130\u9b54\u6cd5\u7684\u3002\u201d</p><p>\u201c\u653b\u51fb\u624b\u6bb5\u5982\u6b64\u5355\u8c03\uff0c\u6309\u9053\u7406\u5e94\u8be5\u5f88\u5bb9\u6613\u88ab\u514b\u5236\u624d\u5bf9\uff0c\u7136\u800c\u5979\u5374\u4e00\u8def\u8fc7\u5173\u65a9\u5c06\uff0c\u6a2a\u626b\u6240\u6709\u5bf9\u624b\uff0c\u8fd9\u5176\u4e2d\u7684\u7f18\u6545\uff0c\u5c31\u662f\u5979\u91ca\u653e\u7684\u706b\u7130\u9b54\u6cd5\u5b9e\u5728\u5a01\u529b\u60ca\u4eba\u3002\u7cbe\u7814\u5851\u80fd\uff0c\u539f\u672c\u5c31\u5f3a\u8c03\u7834\u574f\u529b\uff0c\u518d\u52a0\u4e0a\u9b54\u6cd5\u5b9d\u77f3\u7684\u589e\u5e45\uff0c\u8ba9\u5979\u7838\u51fa\u6765\u7684\u706b\u7403\uff0c\u5a01\u529b\u80dc\u8fc7\u540c\u6c34\u51c6\u666e\u901a\u5deb\u5e08\u7684\u4e09\u56db\u500d\u3002\u6240\u8c13\u4ee5\u529b\u7834\u5de7\uff0c\u5728\u8fd9\u79cd\u60ac\u6b8a\u7684\u529b\u91cf\u5dee\u8ddd\u4e0b\uff0c\u5f88\u96be\u6709\u6280\u5de7\u53d1\u6325\u7684\u4f59\u5730\u3002\u201d</p><p>\u201c\u6b63\u786e\uff0c\u201d\u738b\u5b50\u5341\u6307\u4ea4\u53c9\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u743c\u6069\uff0c\u4f60\u5e94\u8be5\u4e5f\u6ce8\u610f\u5230\u4e86\u5427\uff0c\u7ea2\u888d\u5deb\u5e08\u4eec\uff0c\u548c\u6211\u4eec\u901a\u5e38\u6240\u77e5\u7684\u5e38\u8bc6\u6709\u6240\u4e0d\u540c\uff0c\u4ed6\u4eec\u666e\u904d\u5730\u653e\u5f03\u4e09\u4e2a\u5b66\u6d3e\u7684\u6cd5\u672f\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u53d1\u73b0\u4e86\uff0c\u4f46\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6781\u5ea6\u4e13\u7cbe\uff0c\u201d\u738b\u5b50\u7f13\u7f13\u5410\u51fa\u4e00\u4e2a\u8bcd\uff0c\u201c\u8fd9\u662f\u7ea2\u888d\u5deb\u5e08\u4eec\u666e\u904d\u901a\u884c\u7684\u7406\u5ff5\uff0c\u5728\u4ed6\u4eec\u773c\u91cc\uff0c\u2018\u901a\u624d\u2019\u5c31\u7b49\u4e8e\u2018\u5e9f\u7269\u2019\uff0c\u2018\u4e13\u4e1a\u2019\u5219\u662f\u2018\u7cbe\u82f1\u2019\u7684\u540c\u4e49\u8bcd\uff0c\u6240\u4ee5\u4ed6\u4eec\u7814\u7a76\u51fa\u4e86\u4e00\u5957\u72ec\u7279\u7684\u79d8\u672f\uff0c\u80fd\u591f\u5728\u4e13\u7cbe\u5deb\u5e08\u7684\u57fa\u7840\u4e0a\u518d\u8fdb\u4e00\u6b65\u4e13\u7cbe\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u743c\u6069\u5927\u4f53\u660e\u767d\u4e86\u3002\u6210\u4e3a\u4e13\u7cbe\u5deb\u5e08\u7684\u65b9\u6cd5\u5e76\u4e0d\u662f\u4ec0\u4e48\u79d8\u5bc6\uff0c\u53ea\u662f\u5f88\u57fa\u7840\u7684\u9b54\u6cd5\u5e38\u8bc6\uff0c\u53ea\u8981\u662f\u5deb\u5e08\u90fd\u53ef\u4ee5\u9009\u62e9\u8fd9\u6761\u9053\u8def\uff0c\u53ea\u662f\u672a\u5fc5\u6709\u591a\u5c11\u4eba\u613f\u610f\u7f62\u4e86\u3002\u800c\u7ea2\u888d\u5deb\u5e08\uff0c\u663e\u7136\u662f\u7528\u4e86\u67d0\u79cd\u72ec\u95e8\u79d8\u672f\uff0c\u5728\u8fd9\u79cd\u57fa\u7840\u4e0a\u518d\u8fdb\u4e00\u6b65\u4e13\u4e1a\u5316\uff0c\u653e\u5f03\u66f4\u591a\u7684\u5b66\u6d3e\uff0c\u4ece\u800c\u83b7\u5f97\u66f4\u9ad8\u7684\u7cbe\u7814\u6210\u5c31\u3002</p><p>\u201c\u51db\u9996\u5148\u662f\u4e2a\u4e13\u7cbe\u5851\u80fd\u7684\u5deb\u5e08\uff0c\u5176\u6b21\u5979\u53c8\u901a\u8fc7\u8003\u9a8c\uff0c\u6210\u4e3a\u4e86\u6b63\u5f0f\u7684\u7ea2\u888d\u5deb\u5e08\u3002\u5979\u653e\u5f03\u4e86\u5927\u91cf\u7684\u6cd5\u672f\uff0c\u5168\u5fc3\u5168\u610f\u6295\u5165\u5851\u80fd\u9b54\u6cd5\u2026\u2026\u4e0d\uff0c\u6211\u60f3\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u6295\u5165\u5230\u5851\u80fd\u9b54\u6cd5\u4e2d\u6709\u5173\u2018\u706b\u7130\u2019\u7c7b\u578b\u7684\u5206\u652f\u7814\u7a76\u4e2d\uff0c\u201d\u738b\u5b50\u53f9\u6c14\uff0c\u201c\u518d\u52a0\u4e0a\u5979\u7684\u5929\u8d4b\u5f02\u80fd\uff0c\u501f\u52a9\u5b9d\u77f3\u5f3a\u5316\u589e\u5e45\u6cd5\u672f\u5a01\u529b\uff0c\u771f\u662f\u4e2a\u5f88\u96be\u5bf9\u4ed8\u7684\u5c0f\u4e2b\u5934\u5462\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5979\u662f\u7cbe\u7814\u5851\u80fd\uff0c\u6240\u4ee5\u5728\u62b5\u6297\u5851\u80fd\u9b54\u6cd5\u4e0a\u6709\u72ec\u5230\u4e4b\u79d8\uff0c\u201d\u738b\u5b50\u7ee7\u7eed\u8bf4\uff0c\u201c\u4f60\u4e5f\u770b\u5230\u4e86\uff0c\u5979\u4ece\u6765\u4e0d\u7ed9\u81ea\u5df1\u65bd\u52a0\u4efb\u4f55\u9632\u5fa1\uff0c\u5374\u65e0\u60e7\u4e8e\u5bf9\u624b\u7684\u653b\u51fb\uff0c\u8fd9\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u5979\u90a3\u706b\u4e2d\u8df3\u8dc3\u7684\u5929\u8d4b\u5f02\u80fd\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u56e0\u4e3a\u5979\u5e76\u4e0d\u600e\u4e48\u60e7\u6015\u5851\u80fd\u9b54\u6cd5\u3002\u201d</p><p>\u800c\u5851\u80fd\u9b54\u6cd5\u662f\u6700\u5177\u653b\u51fb\u529b\u7684\u9b54\u6cd5\u2014\u2014\u81f3\u5c11\u5728\u64c2\u53f0\u8d5b\u8fd9\u79cd\u5c0f\u8303\u56f4\u786c\u78b0\u786c\u7684\u5deb\u5e08\u51b3\u6597\u4e2d\u662f\u5982\u6b64\u3002</p><p>\u5982\u6b64\u8bf4\u6765\uff0c\u51db\u5c31\u50cf\u662f\u4e00\u8f86\u5766\u514b\uff0c\u62e5\u6709\u8d85\u5f3a\u7684\u653b\u51fb\uff0c\u53c8\u6709\u4e0d\u9519\u7684\u9632\u5fa1\uff0c\u96be\u602a\u80fd\u5728\u64c2\u53f0\u4e0a\u4e00\u5f80\u65e0\u524d\uff0c\u52bf\u4e0d\u53ef\u6321\u2026\u2026\u4e0d\uff0c\u9664\u4e86\u653b\u51fb\u548c\u9632\u5fa1\uff0c\u5979\u8fd8\u62e5\u6709\u4e00\u6d41\u7684\u673a\u52a8\u529b\u3002</p><p>\u201c\u60a8\u521a\u624d\u8bf4\uff0c\u706b\u4e2d\u8df3\u8dc3\uff1f\u201d\u743c\u6069\u91cd\u590d\u7740\u8fd9\u4e2a\u65b0\u9c9c\u540d\u8bcd\u3002</p><p>\u201c\u55ef\uff0c\u706b\u4e2d\u8df3\u8dc3\uff0c\u4e0d\u8fc7\u8fd9\u5176\u5b9e\u4e5f\u4e0d\u592a\u51c6\u786e\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u8fd9\u539f\u672c\u662f\u7ea2\u888d\u5deb\u5e08\u53d1\u660e\u7684\u4e00\u79cd\u6cd5\u672f\uff0c\u80fd\u591f\u8ba9\u4eba\u501f\u52a9\u706b\u7130\u77ac\u95f4\u4f20\u9001\u3002\u4f46\u5982\u679c\u771f\u662f\u6cd5\u672f\u7684\u8bdd\uff0c\u5e94\u8be5\u662f\u53ea\u80fd\u4f20\u9001\u4e00\u6b21\u5c31\u5931\u6548\u7684\uff0c\u7edd\u4e0d\u53ef\u80fd\u50cf\u5979\u8fd9\u6837\u6beb\u65e0\u9650\u5236\u7684\u968f\u610f\u4f7f\u7528\u3002\u8fd9\u4e2a\uff0c\u53ea\u80fd\u7406\u89e3\u4e3a\u5979\u7684\u4e00\u79cd\u5929\u8d4b\u5f02\u80fd\u3002\u201d</p><p>\u6240\u8c13\u5929\u8d4b\u5f02\u80fd\uff0c\u5c31\u50cf\u7ea2\u9f99\u4f1a\u5410\u706b\uff0c\u9e1f\u4f1a\u98de\u7fd4\u4e00\u6837\uff0c\u5728\u5176\u4ed6\u751f\u7269\u770b\u6765\u4e0d\u53ef\u601d\u8bae\uff0c\u5728\u4ed6\u4eec\u81ea\u5df1\u800c\u8a00\u5374\u5982\u547c\u5438\u7761\u89c9\u4e00\u822c\u5e73\u5e38\uff0c\u81ea\u7136\u4e0d\u80fd\u4ee5\u6cd5\u672f\u7684\u6807\u51c6\u6765\u8861\u91cf\u3002</p><p>\u201c\u53c8\u662f\u5929\u8d4b\u5f02\u80fd\u3002\u201d\u743c\u6069\u53f9\u6c14\uff0c\u8fd9\u4f4d\u51db\u5230\u5e95\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u80fd\u4f7f\u7528\u5b9d\u77f3\u9b54\u6cd5\u5df2\u7ecf\u5927\u5360\u4fbf\u5b9c\u4e86\uff0c\u518d\u52a0\u4e0a\u8fd9\u79cd\u65e0\u9650\u706b\u4e2d\u8df3\u8dc3\u2014\u2014\u59d1\u4e14\u8fd9\u4e48\u79f0\u547c\u2014\u2014\u7684\u80fd\u529b\uff0c\u7b80\u76f4\u5c31\u662f\u653b\u9632\u4e00\u4f53\uff0c\u4ee4\u4eba\u65e0\u61c8\u53ef\u51fb\u3002</p><p>\u201c\u5929\u8d4b\u5f02\u80fd\u4e48\uff0c\u81ea\u7136\u610f\u5473\u7740\u975e\u540c\u5bfb\u5e38\u7684\u8840\u8109\uff0c\u201d\u738b\u5b50\u4f3c\u4e4e\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\uff0c\u201c\u867d\u8bf4\u5386\u53f2\u4e0a\u4e5f\u66fe\u51fa\u73b0\u8fc7\u660e\u660e\u6ca1\u6709\u534a\u70b9\u795e\u79d8\u8840\u8109\uff0c\u504f\u504f\u5374\u62e5\u6709\u5929\u8d4b\u5f02\u80fd\u7684\u4f8b\u5b50\uff0c\u4e0d\u8fc7\u6211\u60f3\u51db\u5c0f\u59d0\u5e76\u4e0d\u5c5e\u4e8e\u6b64\u5217\u3002\u5982\u679c\u8981\u8ba9\u6211\u8bf4\u7684\u8bdd\uff0c\u5979\u5e94\u8be5\u4e0d\u662f\u4e2a\u7eaf\u7cb9\u7684\u4eba\u7c7b\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u6bbf\u4e0b\uff0c\u60a8\u662f\u8bf4\u2026\u2026\u201d</p><p>\u201c\u731c\u6d4b\u7f62\u4e86\uff0c\u4e5f\u6ca1\u8bc1\u636e\u3002\u201d</p><p>\u4f46\u743c\u6069\u77e5\u9053\uff0c\u5e03\u96f7\u7eb3\u65af\u662f\u4e00\u6d41\u7684\u9884\u8a00\u5e08\uff0c\u62e5\u6709\u201c\u535a\u5b66\u58eb\u201d\u7684\u5934\u8854\uff0c\u5341\u4e8c\u9634\u9b42\u738b\u5b50\u4e2d\u6700\u4ee5\u201c\u89c1\u8bc6\u5e7f\u535a\u201d\u800c\u8457\u79f0\u8005\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u53bb\u8d1f\u8d23\u8003\u53e4\u5de5\u4f5c\u4e86\u3002\u4ed6\u65e2\u7136\u8bf4\u51db\u4e0d\u662f\u7eaf\u7cb9\u7684\u4eba\u7c7b\uff0c\u90a3\u53ea\u6015\u5341\u6709\u516b\u4e5d\u5c31\u4e0d\u662f\u4e86\u3002</p><p>\u4e0d\u662f\u7eaf\u7cb9\u7684\u4eba\u7c7b\uff0c\u8fd8\u6709\u53ef\u80fd\u662f\u534a\u7cbe\u7075\u3001\u534a\u517d\u4eba\u4e4b\u7c7b\u7684\u6df7\u8840\uff0c\u4f46\u738b\u5b50\u660e\u663e\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u743c\u6069\u6e05\u695a\u5730\u660e\u767d\u8fd9\u70b9\u3002\u8fd9\u4e9b\u90fd\u8fd8\u592a\u201c\u6b63\u5e38\u201d\u4e86\uff0c\u4e0d\u8db3\u4ee5\u5f15\u8d77\u738b\u5b50\u7684\u6ce8\u610f\u3002</p><p>\u5982\u6b64\u8bf4\u8d77\u6765\uff0c\u8fd9\u4e2a\u51db\u8fd8\u771f\u662f\u68d8\u624b\u5462\uff0c\u65e0\u8bba\u662f\u653b\u51fb\u3001\u9632\u5fa1\u8fd8\u662f\u673a\u52a8\u529b\uff0c\u90fd\u79f0\u5f97\u4e0a\u662f\u7b2c\u4e00\u6d41\u3002\u8981\u5bf9\u4ed8\u8fd9\u6837\u7684\u5deb\u5e08\u2026\u2026</p><p>\u201c\u867d\u7136\u5982\u6b64\uff0c\u4f46\u653b\u51fb\u624b\u6bb5\u7684\u6781\u5ea6\u5355\u8c03\uff0c\u4f9d\u65e7\u662f\u81f4\u547d\u7684\u7f3a\u9677\u5427\uff0c\u201d\u743c\u6069\u601d\u7d22\u7740\uff0c\u63d0\u51fa\u4e00\u4e2a\u7591\u60d1\u6765\uff0c\u201c\u5979\u7684\u706b\u7130\u6cd5\u672f\u867d\u7136\u51cc\u5389\uff0c\u5374\u4e5f\u4e0d\u662f\u5c31\u65e0\u6cd5\u62b5\u6321\uff0c\u53ec\u5524\u706b\u5143\u7d20\u5c31\u53ef\u4ee5\u8ba9\u5979\u7684\u653b\u51fb\u5b8c\u5168\u5f92\u52b3\u65e0\u529f\u3002\u4f46\u4e3a\u4ec0\u4e48\u5979\u7684\u5bf9\u624b\u5f53\u4e2d\uff0c\u6211\u6ca1\u770b\u89c1\u4e00\u4e2a\u4eba\u8fd9\u4e48\u5e72\u5462\u3002\u201d</p><p>\u706b\u5143\u7d20\u672c\u8eab\u5c31\u662f\u706b\u7130\u7684\u805a\u5408\u4f53\uff0c\u81ea\u7136\u5b8c\u5168\u4e0d\u6015\u706b\u7130\u653b\u51fb\u3002\u5c31\u50cf\u4eca\u5929\u7684\u90a3\u4f4d\u5492\u6cd5\u5deb\u5e08\uff0c\u5982\u679c\u4e0d\u662f\u53ec\u5524\u4e00\u5806\u4e71\u4e03\u516b\u7cdf\u7684\u602a\u7269\uff0c\u6539\u4e3a\u5168\u90e8\u53ec\u5524\u706b\u5143\u7d20\u7684\u8bdd\uff0c\u5927\u6709\u5e0c\u671b\u51fb\u8d25\u51db\u3002\u743c\u6069\u5b9e\u5728\u4e0d\u660e\u767d\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8fd9\u4e48\u505a\u3002</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u201d\u738b\u5b50\u53f9\u6c14\uff0c\u201c\u8fd9\u5c31\u53c8\u6d89\u53ca\u5230\u5979\u7684\u4e00\u9879\u5929\u8d4b\u5f02\u80fd\u4e86\u2014\u2014\u743c\u6069\uff0c\u542c\u597d\u4e86\uff0c\u636e\u53ef\u9760\u6d88\u606f\uff0c\u8fd9\u4f4d\u51db\u5c0f\u59d0\uff0c\u80fd\u4efb\u610f\u63a7\u5236\u706b\u5143\u7d20\u3001\u706b\u5de8\u7075\u6216\u8005\u706b\u8725\u8734\u8fd9\u79cd\u706b\u7cfb\u751f\u7269\u3002\u5f53\u7136\uff0c\u592a\u5389\u5bb3\u7684\u4f30\u8ba1\u4e0d\u884c\uff0c\u4f46\u4ee5\u4f60\u4eec\u7684\u6c34\u51c6\uff0c\u8d39\u52b2\u534a\u5929\u53ec\u5524\u51fa\u6765\u7684\u5bb6\u4f19\uff0c\u53ea\u8981\u5979\u8bf4\u4e00\u4e2a\u5b57\uff0c\u5c31\u80fd\u8f7b\u677e\u593a\u8d70\u63a7\u5236\u6743\u3002\u8fd9\u5c31\u662f\u4e3a\u4ec0\u4e48\u6ca1\u6709\u4eba\u6562\u5728\u5979\u9762\u524d\u53ec\u5524\u706b\u5143\u7d20\u7684\u539f\u56e0\uff0c\u56e0\u4e3a\u90a3\u53ea\u4f1a\u5e73\u767d\u7ed9\u5979\u589e\u52a0\u6218\u6597\u529b\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u4e0d\u4f1a\u5427\u3002\u201d</p><p>\u743c\u6069\u60ca\u8bb6\u5f97\u51e0\u4e4e\u8981\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ec\u5524\u602a\u7269\uff0c\u5c24\u5176\u662f\u53ec\u5524\u5143\u7d20\uff0c\u662f\u4e00\u79cd\u9887\u5177\u98ce\u9669\u6027\u7684\u884c\u4e3a\u3002\u8d8a\u5f3a\u7684\u5143\u7d20\u5f80\u5f80\u8d8a\u4e0d\u542c\u8bdd\uff0c\u5b83\u4eec\u867d\u7136\u662f\u5f3a\u529b\u7684\u5e2e\u624b\uff0c\u4f46\u4e5f\u5b8c\u5168\u53ef\u4ee5\u7ffb\u8138\u53d8\u6210\u68d8\u624b\u7684\u654c\u4eba\u3002\u4e3a\u4e86\u907f\u514d\u8fd9\u79cd\u60c5\u51b5\u7684\u53d1\u751f\uff0c\u5deb\u5e08\u548c\u4ed6\u6240\u53ec\u5524\u7684\u5143\u7d20\u4e4b\u95f4\u4f1a\u6709\u4e00\u79cd\u65e0\u5f62\u7684\u7cbe\u795e\u8054\u63a5\uff0c\u53ea\u8981\u53ec\u5524\u8005\u8db3\u591f\u5f3a\u5927\uff0c\u88ab\u53ec\u5524\u8005\u5c31\u65e0\u6cd5\u53cd\u6297\uff0c\u53ea\u80fd\u4fef\u9996\u542c\u547d\u3002</p><p>\u5982\u679c\u8981\u593a\u53d6\u4e00\u4e2a\u5deb\u5e08\u5bf9\u5176\u53ec\u5524\u5143\u7d20\u7684\u63a7\u5236\u6743\uff0c\u90a3\u5c31\u5f97\u5148\u5207\u65ad\u8fd9\u79cd\u7cbe\u795e\u8054\u63a5\uff0c\u518d\u4e34\u65f6\u6784\u5efa\u8d77\u81ea\u5df1\u548c\u5143\u7d20\u7684\u7cbe\u795e\u8054\u63a5\uff0c\u5e76\u4e14\u7acb\u523b\u538b\u5236\u4f4f\u5b83\uff0c\u8fd9\u79cd\u5de5\u4f5c\u7684\u96be\u5ea6\u53ef\u60f3\u800c\u77e5\u3002\u9664\u975e\u5728\u9b54\u6cd5\u9020\u8be3\u548c\u6280\u5de7\u4e0a\u6bd4\u5bf9\u65b9\u9ad8\u51fa\u4e00\u5927\u622a\uff0c\u5426\u5219\u662f\u7edd\u5bf9\u505a\u4e0d\u5230\u7684\u3002</p><p>\u518d\u8bf4\uff0c\u53ec\u5524\u5f02\u6b21\u5143\u751f\u7269\uff0c\u8fd9\u662f\u5492\u6cd5\u5b66\u6d3e\u7684\u7cbe\u7814\u9886\u57df\uff0c\u51db\u662f\u4e2a\u5851\u80fd\u5deb\u5e08\uff0c\u5728\u8fd9\u65b9\u9762\u5e94\u8be5\u6ca1\u4ec0\u4e48\u9020\u8be3\u624d\u5bf9\u3002</p><p>\u201c\u90fd\u8bf4\u4e86\u662f\u5929\u8d4b\u5f02\u80fd\uff0c\u201d\u738b\u5b50\u53f9\u6c14\uff0c\u201c\u65e2\u7136\u662f\u5929\u8d4b\u5f02\u80fd\uff0c\u81ea\u7136\u4e0d\u80fd\u7528\u6cd5\u672f\u7684\u6807\u51c6\u6765\u8861\u91cf\u3002\u201d</p><p>\u6b63\u5982\u4eba\u7c7b\u8981\u8f9b\u82e6\u5b66\u4e60\u9b54\u6cd5\u624d\u80fd\u91ca\u653e\u51e0\u4e2a\u706b\u7403\uff0c\u7ea2\u9f99\u5374\u5f20\u53e3\u5c31\u80fd\u55b7\u5410\u70c8\u706b\uff0c\u6240\u8c13\u5929\u8d4b\uff0c\u672c\u6765\u5c31\u662f\u4e0d\u8bb2\u9053\u7406\u7684\u4e1c\u897f\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u521a\u624d\u5fd8\u4e86\u8bf4\uff0c\u5343\u4e07\u4e0d\u8981\u6253\u2018\u628a\u81ea\u5df1\u53d8\u6210\u706b\u5143\u7d20\u2019\u7684\u4e3b\u610f\uff0c\u56e0\u4e3a\u8fd9\u6837\u4e00\u6765\uff0c\u5979\u5c31\u80fd\u76f4\u63a5\u8fde\u4f60\u90fd\u63a7\u5236\u4e86\u3002\u6839\u636e\u6211\u641c\u96c6\u5230\u7684\u8d44\u6599\uff0c\u4ee5\u524d\u6709\u4e2a\u7ea2\u888d\u5deb\u5e08\u5c31\u60f3\u8fd9\u4e48\u5e72\uff0c\u7ed3\u679c\u76f4\u63a5\u88ab\u5979\u63a7\u5236\u7740\u8df3\u8fdb\u6e56\u91cc\u53bb\u4e86\u3002\u201d</p><p>\u743c\u6069\u5728\u5fc3\u4e2d\u5bf9\u90a3\u4f4d\u5012\u9709\u7684\u7ea2\u888d\u5deb\u5e08\u6df1\u8868\u540c\u60c5\uff0c\u53d8\u5f62\u6210\u706b\u5143\u7d20\u5374\u88ab\u5f04\u5230\u6e56\u91cc\u53bb\uff0c\u81f3\u5c11\u8981\u5927\u75c5\u4e00\u573a\u4e86\u3002</p><p>\u201c\u65e0\u9650\u706b\u4e2d\u8df3\u8dc3\uff0c\u4efb\u610f\u63a7\u5236\u706b\u7cfb\u751f\u7269\u2026\u2026\u5979\u4e0d\u4f1a\u662f\u4ec0\u4e48\u706b\u5143\u7d20\u7684\u540e\u88d4\u5427\u3002\u201d</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u5982\u679c\u4e0d\u662f\u6885\u83f2\u65af\u5df2\u7ecf\u8bf4\u8fc7\u5979\u4e0d\u662f\u795e\u5b50\uff0c\u743c\u6069\u7b80\u76f4\u8981\u6000\u7591\u51db\u662f\u90a3\u4f4d\u706b\u5143\u7d20\u795e\u5361\u66d9\u65af\u7684\u5973\u513f\u4e86\u3002</p><p>\u201c\u80e1\u626f\uff0c\u201d\u738b\u5b50\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6709\u70b9\u5e38\u8bc6\u597d\u4e0d\u597d\uff0c\u5143\u7d20\u751f\u7269\u6ca1\u6709\u6027\u522b\uff0c\u6ca1\u6709\u751f\u80b2\u7e41\u6b96\u8fd9\u56de\u4e8b\uff0c\u90fd\u662f\u81ea\u5143\u7d20\u4f4d\u9762\u4e2d\u81ea\u7136\u8bde\u751f\u7684\uff0c\u4e16\u754c\u4e0a\u54ea\u6709\u4ec0\u4e48\u706b\u5143\u7d20\u7684\u540e\u88d4\u3002\u201d</p><p>\u201c\u9664\u4e86\u8fd9\u4e9b\u4e4b\u5916\uff0c\u201d\u738b\u5b50\u4e25\u8083\u5730\u6307\u51fa\uff0c\u201c\u6700\u540e\u6211\u9700\u8981\u544a\u8bc9\u4f60\u7684\u662f\uff0c\u5979\u662f\u4e2a\u8d27\u771f\u4ef7\u5b9e\u7684\u7ea2\u888d\u5deb\u5e08\uff0c\u6240\u4ee5\u5979\u8eab\u4e0a\u540c\u6837\u4e5f\u6709\u9b54\u6cd5\u523a\u9752\u2014\u2014\u4f60\u77e5\u9053\u4ec0\u4e48\u662f\u9b54\u6cd5\u523a\u9752\u5427\u3002\u201d</p><p>\u201c\u77e5\u9053\u3002\u201d</p><p>\u6240\u8c13\u9b54\u6cd5\u523a\u9752\uff0c\u8bf4\u5f97\u7b80\u5355\u70b9\uff0c\u5c31\u662f\u7ea2\u888d\u5deb\u5e08\u6240\u72ec\u6709\u7684\u4e00\u95e8\u79d8\u6280\uff0c\u80fd\u591f\u5728\u76ae\u80a4\u4e0a\u7eb9\u7279\u522b\u7684\u523a\u9752\uff0c\u7528\u4e8e\u50a8\u5b58\u9b54\u6cd5\uff0c\u636e\u8bf4\u6700\u591a\u80fd\u7eb9\u4e03\u4e2a\uff0c\u5f53\u7136\u8fd9\u662f\u9ad8\u9636\u7ea2\u888d\u624d\u6709\u7684\u5f85\u9047\uff0c\u51db\u662f\u80af\u5b9a\u6ca1\u8d44\u683c\u7684\u3002</p><p>\u201c\u4ee5\u5979\u5728\u7ea2\u888d\u91cc\u7684\u4f4d\u9636\u6765\u5224\u65ad\uff0c\u8eab\u4e0a\u5e94\u8be5\u6709\u4e09\u4e2a\u523a\u9752\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u6709\u4e00\u4e2a\u523a\u9752\u5e94\u8be5\u662f\u5728\u5de6\u81c2\u4e0a\uff0c\u4eca\u5929\u6bd4\u8d5b\u7684\u65f6\u5019\uff0c\u5979\u5c31\u662f\u53d1\u52a8\u5176\u4e2d\u50a8\u5b58\u7684\u9020\u98ce\u672f\uff0c\u5377\u52a8\u706b\u5899\u70e7\u6b7b\u4e86\u5bf9\u624b\u7684\u53ec\u5524\u602a\u7269\uff1b\u53e6\u5916\u4e24\u4e2a\u523a\u9752\u4f4d\u7f6e\u4e0d\u660e\uff0c\u6b64\u524d\u6bd4\u8d5b\u91cc\u6ca1\u89c1\u5979\u52a8\u7528\u8fc7\u3002\u5979\u8fd9\u6b21\u83b7\u5f97\u51a0\u519b\uff0c\u6309\u89c4\u77e9\u662f\u53ef\u4ee5\u518d\u83b7\u5f97\u4e00\u4e2a\u523a\u9752\u7684\uff0c\u4e0d\u8fc7\u90a3\u662f\u51e0\u5929\u4ee5\u540e\u7684\u4e8b\u60c5\u4e86\uff0c\u6682\u65f6\u4e0d\u7528\u8003\u8651\u3002\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u51db\u7684\u8eab\u4e0a\u6709\u4e09\u4e2a\u9b54\u6cd5\u523a\u9752\uff0c\u6240\u4ee5\u867d\u7136\u9b54\u7f51\u9020\u8be3\u5dee\u4e0d\u591a\uff0c\u4f46\u5979\u5b9e\u9645\u4e0a\u6bd4\u6211\u989d\u5916\u591a\u4e86\u4e09\u4e2a\u6cd5\u672f\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u4f60\u7b97\u9519\u4e86\uff0c\u201d\u738b\u5b50\u6447\u52a8\u624b\u6307\uff0c\u6307\u51fa\u743c\u6069\u7684\u9519\u8bef\uff0c\u201c\u4f60\u770b\uff0c\u4f60\u662f\u4e13\u7cbe\u5deb\u5e08\uff0c\u6bd4\u8d77\u90a3\u4e9b\u666e\u901a\u7684\u5deb\u5e08\uff0c\u4f60\u548c\u9b54\u7f51\u7684\u8054\u63a5\u76f8\u5bf9\u66f4\u52a0\u7d27\u5bc6\uff0c\u4f60\u6bcf\u5929\u53ef\u4ee5\u51c6\u5907\u5e76\u4e14\u627f\u53d7\u7684\u6cd5\u672f\u6bd4\u4ed6\u4eec\u591a\u4e00\u4e9b\uff0c\u5bf9\u5427\u3002\u540c\u6837\u7684\uff0c\u7ea2\u888d\u5deb\u5e08\u65e2\u7136\u662f\u5728\u4e13\u7cbe\u7684\u57fa\u7840\u4e0a\u91cd\u590d\u4e13\u7cbe\uff0c\u90a3\u4e48\u2026\u2026\u51db\u5c0f\u59d0\u5b9e\u9645\u4e0a\u6bd4\u4f60\u989d\u5916\u591a\u51fa\u6765\u7684\u6cd5\u672f\u6570\u91cf\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4e09\u4e2a\u4e86\u3002\u201d</p><p>\u743c\u6069\u542c\u5f97\u76b1\u7709\u4e0d\u5df2\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u975e\u5e38\u5173\u952e\u7684\u4e8b\u60c5\u6765\uff0c\u201c\u5979\u7684\u5b9d\u77f3\u9b54\u6cd5\u662f\u5c06\u6cd5\u672f\u704c\u6ce8\u5230\u5b9d\u77f3\u91cc\u65bd\u6cd5\uff0c\u90a3\u5979\u4e0d\u4f1a\u79ef\u6512\u4e00\u5927\u5806\u9b54\u6cd5\u5b9d\u77f3\u7528\u6765\u653b\u51fb\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4f60\u5012\u4e0d\u7528\u62c5\u5fc3\u4e86\uff0c\u201d\u738b\u5b50\u8f7b\u677e\u5730\u8bf4\uff0c\u201c\u704c\u6ce8\u8fdb\u5b9d\u77f3\u7684\u6cd5\u672f\u4e5f\u662f\u6709\u65f6\u95f4\u9650\u5236\u7684\uff0c\u8d85\u8fc7\u4e00\u5929\u5c31\u81ea\u52a8\u6d88\u5931\u4e86\uff0c\u7b2c\u4e8c\u5929\u9700\u8981\u518d\u6b21\u704c\u6ce8\uff0c\u6240\u4ee5\u4f60\u4e0d\u7528\u62c5\u5fc3\u4f1a\u88ab\u4e00\u5806\u9b54\u6cd5\u5b9d\u77f3\u96e8\u7838\u6b7b\u3002\u5c31\u8fd9\u70b9\u6765\u8bf4\uff0c\u4f60\u4eec\u5012\u662f\u516c\u5e73\u7684\u3002\u201d</p><p>\u516c\u5e73\u4e2a\u9b3c\u3002</p><p>\u867d\u7136\u4e0d\u6562\u9ad8\u58f0\u53cd\u9a73\uff0c\u4f46\u5fc3\u4e2d\u7684\u6028\u6c14\u603b\u662f\u514d\u4e0d\u4e86\u7684\u3002\u660e\u5929\u8fd9\u573a\u51b3\u6597\uff0c\u8bf4\u662f\u4ee3\u8868\u7740\u4e24\u4ee3\u9b54\u6cd5\u5e1d\u56fd\u5728\u8f83\u91cf\uff0c\u7136\u800c\u5bf9\u65b9\u53c8\u662f\u7ea2\u888d\u79d8\u672f\uff0c\u53c8\u662f\u5929\u8d4b\u5f02\u80fd\uff0c\u53c8\u662f\u6781\u5ea6\u4e13\u7cbe\uff0c\u53c8\u662f\u9b54\u6cd5\u523a\u9752\uff0c\u4ec0\u4e48\u532a\u5937\u6240\u601d\u7684\u80fd\u529b\u90fd\u51fa\u6765\u4e86\uff1b\u53cd\u8fc7\u6765\u770b\u770b\u81ea\u5df1\u8fd9\u8fb9\uff0c\u8bf4\u662f\u8bf4\u51fa\u8eab\u9634\u9b42\u57ce\uff0c\u79c9\u627f\u53e4\u9b54\u6cd5\u5e1d\u56fd\u7684\u6587\u660e\u9057\u8109\uff0c\u7136\u800c\u6240\u5b66\u8fc7\u7684\u4e1c\u897f\uff0c\u4e5f\u65e0\u975e\u5c31\u662f\u4e9b\u201c\u6b63\u5e38\u201d\u77e5\u8bc6\uff0c\u600e\u4e48\u5c31\u4e0d\u6765\u70b9\u4ec0\u4e48\u8010\u745f\u5e1d\u56fd\u7684\u72ec\u95e8\u79d8\u672f\u5462\u3002</p></article>"}}}]);
