(window.webpackJsonp=window.webpackJsonp||[]).push([[1395],{1418:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u56db\u8282 \u51db\u7684\u79d8\u5bc6",content:"<article><h2>\u7b2c\u4e8c\u5341\u56db\u8282 \u51db\u7684\u79d8\u5bc6</h2><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u201d\u743c\u6069\u6c89\u541f\u7740\uff0c\u201c\u5f88\u9ebb\u70e6\u5462\u3002\u201d</p><p>\u8868\u9762\u4e0a\u770b\u8d77\u6765\uff0c\u4f3c\u4e4e\u662f\u9020\u8be3\u5dee\u4e0d\u591a\u7684\u5deb\u5e08\u51b3\u6597\uff0c\u7136\u800c\u5b9e\u9645\u4e0a\u4e00\u7b97\uff0c\u56e0\u4e3a\u7ea2\u888d\u79d8\u672f\u548c\u5929\u8d4b\u5f02\u80fd\uff0c\u51db\u65e0\u8bba\u662f\u5728\u6cd5\u672f\u6570\u91cf\u4e0a\uff0c\u653b\u51fb\u529b\u9632\u5fa1\u529b\u4e0a\uff0c\u90fd\u660e\u663e\u80dc\u8fc7\u743c\u6069\u4e00\u7b79\u3002\u5c0f\u8303\u56f4\u64c2\u53f0\u8d5b\u7684\u5f62\u5f0f\uff0c\u53c8\u5927\u5927\u5f3a\u5316\u4e86\u5851\u80fd\u9b54\u6cd5\u7684\u5a01\u529b\uff0c\u8fd9\u573a\u6bd4\u8d5b\u8fd8\u600e\u4e48\u6253\u3002</p><p>\u201c\u7ea2\u888d\u79d8\u672f\uff0c\u7ec8\u7a76\u53ea\u662f\u4e9b\u7ec6\u679d\u672b\u8282\u7684\u6280\u5de7\uff0c\u4e0d\u6210\u5927\u5668\uff0c\u201d\u65e0\u89c6\u743c\u6069\u7684\u5934\u75bc\uff0c\u738b\u5b50\u5927\u8a00\u4e0d\u60ed\u5730\u6279\u8bc4\u7740\uff0c\u201c\u771f\u6b63\u8981\u8bba\u9b54\u6cd5\u7684\u7cbe\u8981\u5965\u5999\uff0c\u8fd8\u662f\u8981\u770b\u6211\u4eec\u8010\u745f\u9b54\u6cd5\u2026\u2026\u4e0d\u8fc7\u6709\u70b9\u5c0f\u9ebb\u70e6\uff0c\u201d\u738b\u5b50\u671d\u743c\u6069\u5fae\u7b11\u7740\uff0c\u201c\u8d8a\u9ad8\u660e\u7684\u5b66\u95ee\uff0c\u5165\u95e8\u96be\u5ea6\u81ea\u7136\u4e5f\u5c31\u8d8a\u9ad8\uff0c\u4ee5\u4f60\u76ee\u524d\u7684\u9020\u8be3\uff0c\u8fd8\u4e0d\u8db3\u4ee5\u53bb\u63a5\u89e6\u90a3\u4e9b\u66f4\u9ad8\u6df1\u7684\u77e5\u8bc6\uff0c\u6240\u4ee5\u5c31\u6ca1\u529e\u6cd5\u4e86\u2026\u2026\u201d</p><p>\u4ed6\u5988\u7684\u3002</p><p>\u743c\u6069\u5f88\u60f3\u7834\u53e3\u5927\u9a82\uff0c\u8bf4\u6765\u8bf4\u53bb\u8fd9\u4e0d\u8fd8\u662f\u7a7a\u8c08\u4e48\uff0c\u5148\u5439\u5618\u8010\u745f\u9b54\u6cd5\u591a\u4e48\u591a\u4e48\u4f18\u79c0\uff0c\u63a5\u7740\u6765\u4e00\u53e5\u201c\u4f60\u73b0\u5728\u8fd8\u5b66\u4e0d\u4e86\u201d\uff0c\u90a3\u8fd9\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\u3002</p><p>\u5e78\u597d\u738b\u5b50\u8fd8\u6709\u4e0b\u6587\u3002\u201c\u6211\u8fd9\u91cc\u6709\u4e00\u4e2a\u6cd5\u672f\uff0c\u5f88\u9002\u5408\u7528\u6765\u5bf9\u4ed8\u8fd9\u4f4d\u53ea\u4f1a\u6254\u706b\u7403\u7684\u5c0f\u59d0\uff0c\u53ea\u662f\u65f6\u95f4\u77ed\u4e86\u70b9\uff0c\u4f60\u6765\u4e0d\u53ca\u5b66\u4e86\uff0c\u6211\u76f4\u63a5\u628a\u5b83\u5370\u8fdb\u4f60\u7684\u8bb0\u5fc6\u91cc\u53bb\uff0c\u201d\u4ed6\u4ece\u53e3\u888b\u91cc\u638f\u51fa\u4e00\u5f20\u6cdb\u7740\u5fae\u5fae\u7ea2\u5149\u7684\u5377\u8f74\u6765\uff0c\u201c\u4e0d\u8fc7\u8fd9\u79cd\u5f3a\u884c\u523b\u5370\uff0c\u5927\u7ea6\u8fc7\u4e00\u661f\u671f\u4f1a\u6e10\u6e10\u9057\u5fd8\u6389\uff0c\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u5148\u8fc7\u4e86\u660e\u5929\u8fd9\u4e00\u5173\uff0c\u4f60\u4ee5\u540e\u518d\u6162\u6162\u638c\u63e1\u5c31\u597d\u3002\u201d</p><p>\u4ed6\u5c06\u5377\u8f74\u653e\u5728\u684c\u4e0a\uff0c\u53e3\u4e2d\u8bf5\u5492\uff0c\u624b\u6307\u6025\u901f\u98a4\u52a8\uff0c\u5728\u7a7a\u6c14\u4e2d\u5212\u51fa\u4e00\u4e2a\u53c8\u4e00\u4e2a\u7ec6\u5c0f\u5230\u8089\u773c\u65e0\u6cd5\u8fa8\u8bc6\u7684\u9b54\u6cd5\u7b26\u5370\u6765\uff0c\u4eff\u4f5b\u8424\u706b\u866b\u4e00\u822c\u7ed5\u7740\u743c\u6069\u98de\u821e\uff0c\u6700\u7ec8\u51dd\u7ed3\u6210\u4e00\u4e2a\u6de1\u91d1\u8272\u9b54\u6cd5\u9635\u6765\u3002\u738b\u5b50\u505c\u6b62\u8bf5\u5492\uff0c\u6293\u8d77\u5377\u8f74\u5f80\u6cd5\u9635\u4e2d\u4e00\u63b7\u3002\u5377\u8f74\u81ea\u884c\u71c3\u70e7\u8d77\u6765\uff0c\u706b\u5149\u4e2d\u6d6e\u51fa\u65e0\u6570\u8d64\u7ea2\u8272\u6587\u5b57\uff0c\u95ea\u70c1\u751f\u8f89\u3002\u5f53\u5377\u8f74\u5b8c\u5168\u70e7\u5c3d\u65f6\uff0c\u738b\u5b50\u6025\u901f\u4f38\u51fa\u53f3\u624b\u98df\u6307\uff0c\u6307\u5c16\u5149\u8292\u7efd\u653e\uff0c\u5728\u743c\u6069\u989d\u5934\u4e0a\u91cd\u91cd\u4e00\u70b9\u3002</p><p>\u201c\u563f\uff01\u201d</p><p>\u743c\u6069\u53ea\u89c9\u4e00\u80a1\u5f3a\u5927\u7684\u610f\u8bc6\u5982\u6f6e\u6c34\u822c\u6c79\u6d8c\u800c\u6765\uff0c\u77ac\u95f4\u649e\u5165\u81ea\u5df1\u8111\u4e2d\u3002\u4ed6\u4e00\u9635\u6655\u7729\uff0c\u5f53\u56de\u8fc7\u795e\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u9b54\u6cd5\u9635\u5df2\u7ecf\u6d88\u5931\uff0c\u81ea\u5df1\u6b63\u8dcc\u5750\u5728\u6905\u4e2d\uff0c\u53cc\u624b\u7d27\u63e1\u6276\u624b\u3002\u738b\u5b50\u5750\u5728\u4ed6\u5bf9\u9762\uff0c\u9762\u65e0\u8868\u60c5\uff0c\u7aef\u7740\u4e00\u676f\u70ed\u6c14\u817e\u817e\u7684\u5496\u5561\u5728\u6162\u6162\u54c1\u5473\u7740\u3002</p><p>\u201c\u9006\u8f6c\u7206\u70b8\u3002\u201d</p><p>\u521a\u52a8\u5ff5\u60f3\u770b\u770b\u738b\u5b50\u7ed9\u81ea\u5df1\u5f3a\u884c\u5370\u8fdb\u4e86\u4ec0\u4e48\u6cd5\u672f\uff0c\u8111\u6d77\u4e2d\u5c31\u6d6e\u73b0\u51fa\u5b8c\u6574\u7684\u9b54\u6cd5\u8d44\u6599\u6765\uff0c\u8fd9\u662f\u4e2a\u4ece\u6ca1\u542c\u8fc7\u7684\u6cd5\u672f\uff0c\u743c\u6069\u6b63\u8981\u4ed4\u7ec6\u518d\u770b\u5185\u5bb9\uff0c\u5c31\u542c\u5bf9\u9762\u7684\u738b\u5b50\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u8fd9\u9053\u6cd5\u672f\u53ef\u4ee5\u53cd\u5f39\u6240\u6709\u4ee5\u4f60\u4e3a\u76f4\u63a5\u76ee\u6807\u7684\u706b\u7130\u653b\u51fb\uff0c\u201d\u738b\u5b50\u8f7b\u58f0\u8bf4\uff0c\u4ed6\u4f3c\u4e4e\u6709\u4e9b\u75b2\u60eb\u7684\u6a21\u6837\uff0c\u201c\u5982\u679c\u5979\u7528\u706b\u7403\u4e4b\u7c7b\u7684\u6cd5\u672f\u653b\u51fb\u4f60\uff0c\u5c31\u4f1a\u88ab\u76f4\u63a5\u9006\u8f6c\u53cd\u5f39\u56de\u53bb\uff1b\u4f46\u5982\u679c\u7528\u706b\u5899\u8fd9\u79cd\u5927\u8303\u56f4\u653b\u51fb\uff0c\u90a3\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u4e0d\u8fc7\u8fd9\u9053\u6cd5\u672f\u540c\u65f6\u4e5f\u4f1a\u589e\u5f3a\u4f60\u62b5\u6297\u706b\u7130\u7684\u80fd\u529b\uff0c\u6240\u4ee5\u4e0d\u7528\u592a\u62c5\u5fc3\uff0c\u201d\u4ed6\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u201c\u5f53\u7136\uff0c\u5b83\u4e5f\u662f\u6709\u9650\u5ea6\u7684\uff0c\u6ca1\u529e\u6cd5\u4e00\u76f4\u4fdd\u62a4\u4f60\uff0c\u4f46\u6211\u4f30\u8ba1\u81f3\u5c11\u6321\u4f4f\u4e09\u56db\u8f6e\u653b\u51fb\u5e94\u8be5\u6ca1\u95ee\u9898\u3002\u201d</p><p>\u6709\u70b9\u50cf\u201c\u6cd5\u672f\u9006\u8f6c\u201d\uff0c\u743c\u6069\u601d\u5fd6\u7740\uff0c\u9632\u62a4\u9b54\u6cd5\u4e4b\u4e2d\uff0c\u6709\u4e00\u79cd\u6781\u9ad8\u660e\u7684\u6cd5\u672f\uff0c\u80fd\u591f\u5728\u5deb\u5e08\u8eab\u5468\u5f62\u6210\u5f3a\u5927\u7684\u529b\u573a\uff0c\u53cd\u5f39\u4e00\u5207\u76f4\u63a5\u653b\u51fb\u6765\u7684\u9b54\u6cd5\uff08\u5f53\u7136\uff0c\u4e5f\u662f\u6709\u9650\u5ea6\u7684\uff09\u3002\u5728\u5965\u6c83\u7684\u85cf\u5b9d\u5e93\u91cc\uff0c\u5c31\u6709\u8fd9\u6837\u4e00\u679a\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u4ee4\u743c\u6069\u7fa1\u6155\u5f97\u8981\u547d\u3002\u5982\u4eca\u738b\u5b50\u62ff\u51fa\u6765\u7684\u8fd9\u4e2a\u9006\u8f6c\u7206\u70b8\uff0c\u7b49\u4e8e\u5c31\u662f\u6cd5\u672f\u9006\u8f6c\u7684\u8d85\u7ea7\u7b80\u5316\u7248\u672c\uff0c\u53ea\u80fd\u9488\u5bf9\u706b\u7130\u9b54\u6cd5\uff0c\u7528\u9014\u975e\u5e38\u72ed\u9698\u3002</p><p>\u4e0d\u8fc7\uff0c\u5728\u5982\u4eca\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u8981\u5bf9\u4ed8\u7684\u662f\u51db\u8fd9\u6837\u53ea\u4f1a\u7528\u706b\u7130\u9b54\u6cd5\u7684\u5deb\u5e08\uff0c\u8fd9\u4e2a\u6cd5\u672f\u5c31\u6781\u5176\u6709\u7528\u4e86\u3002\u8fd9\u5c31\u662f\u771f\u6b63\u7684\u201c\u8010\u745f\u9b54\u6cd5\u201d\u4e48\uff1f</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u6211\u8bf4\u4e86\uff0c\u8010\u745f\u9b54\u6cd5\u7684\u7cbe\u4e49\uff0c\u4e0d\u5728\u4e8e\u8fd9\u4e9b\u7ec6\u679d\u672b\u8282\u7684\u6280\u5de7\uff0c\u5728\u4e8e\u771f\u6b63\u7684\u6839\u672c\u2026\u2026\u4f60\u73b0\u5728\u9020\u8be3\u4e0d\u591f\uff0c\u4e5f\u6ca1\u6cd5\u638c\u63e1\uff0c\u4ee5\u540e\u81ea\u7136\u660e\u767d\u3002\u81f3\u4e8e\u8fd9\u4e2a\u6cd5\u672f\uff0c\u662f\u6211\u4ece\u8d39\u6797\u9b54\u8475\u90a3\u91cc\u5b66\u6765\u7684\u3002\u201d</p><p>\u8d39\u6797\u9b54\u8475\uff1f\u90a3\u4e0d\u662f\u6614\u65e5\u6bc1\u706d\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u602a\u7269\uff0c\u571f\u5730\u6c99\u5316\u6210\u5927\u6c99\u6f20\u7684\u5143\u51f6\uff0c\u9634\u9b42\u57ce\u7684\u6700\u5927\u4ec7\u654c\u4e48\uff1f</p><p>\u201c\u8d39\u6797\u9b54\u8475\u8fd9\u79cd\u602a\u7269\uff0c\u6700\u64c5\u957f\u7684\u5c31\u662f\u514b\u5236\u9b54\u6cd5\uff0c\u201d\u738b\u5b50\u53f9\u606f\u7740\uff0c\u201c\u6614\u65e5\u5e1d\u56fd\u4f55\u7b49\u5f3a\u76db\uff0c\u5374\u88ab\u9b54\u8475\u8086\u8650\uff0c\u8fd9\u4e5f\u662f\u5f88\u91cd\u8981\u7684\u539f\u56e0\u3002\u201d</p><p>\u8010\u745f\u745e\u5c14\u5d07\u5c1a\u9b54\u6cd5\uff0c\u4e3e\u56fd\u7814\u4e60\u5965\u672f\uff0c\u904d\u5730\u90fd\u662f\u5deb\u5e08\uff0c\u8fd9\u56fa\u7136\u9020\u5c31\u4e86\u8f89\u714c\u7684\u9b54\u6cd5\u6587\u660e\uff0c\u4f46\u5374\u4e5f\u57cb\u4e0b\u9690\u60a3\u5427\u3002\u649e\u4e0a\u8d39\u6797\u9b54\u8475\u8fd9\u79cd\u64c5\u957f\u514b\u5236\u9b54\u6cd5\u7684\u602a\u7269\uff0c\u5c31\u4e0d\u514d\u8981\u5403\u4e8f\u4e86\u3002</p><p>\u201c\u9b54\u8475\u88ab\u57cb\u5728\u5927\u6c99\u6f20\u4e0b\uff0c\u6211\u8003\u53e4\u7684\u65f6\u5019\uff0c\u6070\u597d\u6316\u5230\u4e86\u51e0\u53ea\uff0c\u201d\u738b\u5b50\u89e3\u91ca\uff0c\u201c\u641c\u7d22\u4e86\u4e00\u4e0b\u5b83\u4eec\u7684\u8bb0\u5fc6\uff0c\u53d1\u73b0\u4e86\u8fd9\u4e2a\u6cd5\u672f\uff0c\u4eca\u5929\u6b63\u597d\u7528\u5f97\u4e0a\u3002\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u5fc3\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u6709\u4e86\u8fd9\u9053\u6cd5\u672f\uff0c\u53ea\u8981\u591a\u51c6\u5907\u51e0\u4e2a\uff0c\u57fa\u672c\u5c31\u4e0d\u5fc5\u62c5\u5fc3\u51db\u7684\u653b\u51fb\u4e86\u3002\u5269\u4e0b\u6765\u7684\u95ee\u9898\uff0c\u5c31\u662f\u600e\u4e48\u624d\u80fd\u83b7\u80dc\u4e86\u3002</p><p>\u201c\u65e0\u9650\u706b\u4e2d\u8df3\u8dc3\uff0c\u7b49\u4e8e\u5f00\u4e86\u4e00\u5806\u4f20\u9001\u95e8\uff0c\u201d\u743c\u6069\u601d\u7d22\u7740\uff0c\u201c\u60f3\u6253\u5230\u5979\u53ef\u592a\u96be\u4e86\u3002\u201d</p><p>\u53ef\u60dc\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6ca1\u6709\u81ea\u6765\u6c34\u548c\u9ad8\u538b\u6c34\u67aa\u8fd9\u79cd\u4e1c\u897f\uff0c\u5426\u5219\u63d0\u7740\u6c34\u67aa\u62d6\u7740\u957f\u957f\u76ae\u7ba1\u4e0a\u573a\uff0c\u51db\u70b9\u4e00\u4e2a\u706b\u5806\u743c\u6069\u5c31\u6d47\u7184\u4e00\u4e2a\uff0c\u770b\u5979\u8fd8\u600e\u4e48\u706b\u4e2d\u8df3\u8dc3\u53bb\u3002\u5982\u4eca\u600e\u4e48\u529e\uff0c\u96be\u4e0d\u6210\u8fd8\u62ce\u7740\u4e24\u6876\u6c34\u4e0a\u573a\u4e48\uff0c\u90a3\u53ea\u6015\u8fd8\u6ca1\u5f00\u59cb\u6bd4\u8d5b\uff0c\u5c31\u8981\u88ab\u4eba\u7b11\u6b7b\u4e86\u3002</p><p>\u6b63\u60f3\u5411\u738b\u5b50\u8ba8\u6559\u4e3b\u610f\uff0c\u5374\u53d1\u73b0\u540e\u8005\u8138\u8272\u6709\u4e9b\u4e0d\u592a\u597d\uff0c\u9887\u4e3a\u75b2\u5026\u7684\u6837\u5b50\u3002\u201c\u5c31\u8fd9\u6837\u5427\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u6325\u6325\u624b\uff0c\u201c\u4f60\u5148\u56de\u53bb\u51c6\u5907\u3002\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u743c\u6069\u8d77\u8eab\u544a\u8f9e\uff0c\u51c6\u5907\u56de\u81ea\u5df1\u623f\u95f4\u4f11\u606f\uff0c\u521a\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u53c8\u542c\u89c1\u738b\u5b50\u7684\u58f0\u97f3\u4ece\u80cc\u540e\u4f20\u6765\uff0c\u201c\u55ef\uff0c\u743c\u6069\uff0c\u8bb0\u5f97\u8981\u5c0f\u5fc3\u2026\u2026\u201d</p><p>\u201c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d\u743c\u6069\u8bef\u89e3\u4e86\u738b\u5b50\u7684\u610f\u601d\uff0c\u9a6c\u4e0a\u56de\u7b54\u8bf4\u3002</p><p>\u201c\u54e6\uff0c\u4e0d\u662f\uff0c\u6211\u662f\u8bf4\uff0c\u624b\u4e0b\u7559\u60c5\uff0c\u6ce8\u610f\u5206\u5bf8\uff0c\u522b\u771f\u5f04\u51fa\u592a\u5927\u635f\u4f24\uff0c\u6bd5\u7adf\u6211\u4eec\u662f\u5ba2\u4eba\u3002\u201d</p><p>\u9760\uff01</p><p>\u743c\u6069\u89c9\u5f97\u738b\u5b50\u5927\u6982\u662f\u7d2f\u4e86\uff0c\u795e\u667a\u6709\u70b9\u4e0d\u6e05\u3002\u201c\u6ce8\u610f\u5206\u5bf8\uff0c\u522b\u5f04\u51fa\u592a\u5927\u635f\u4f24\u201d\uff1f\u8fd9\u8bdd\u5e94\u8be5\u662f\u5bf9\u90a3\u4f4d\u559c\u6b22\u75af\u72c2\u7838\u706b\u7403\u7684\u51db\u5c0f\u59d0\u8bf4\u624d\u5bf9\u5427\u3002\u5c31\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u5e0c\u671b\u771f\u5f04\u51fa\u4ec0\u4e48\u4eba\u547d\u4f24\u4ea1\u6765\uff0c\u4f55\u51b5\u51db\u8fd8\u662f\u6885\u83f2\u65af\u7684\u597d\u53cb\u3002\u4f46\u8fd9\u4e8b\u60c5\u662f\u4ed6\u80fd\u63a7\u5236\u7684\u4e48\u3002</p><p>\u4ed6\u4e0d\u518d\u591a\u8bf4\uff0c\u884c\u793c\u544a\u8f9e\u3002\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u8eba\u5728\u5e8a\u4e0a\u72ec\u81ea\u6c89\u601d\uff0c\u628a\u521a\u624d\u770b\u5230\u7684\u90a3\u51e0\u573a\u51db\u7684\u6bd4\u8d5b\u7ffb\u6765\u8986\u53bb\u7684\u56de\u5fc6\uff0c\u60f3\u627e\u51fa\u5979\u7684\u5f31\u70b9\u6765\u3002\u201c\u53ef\u60dc\u5979\u4e4b\u524d\u7684\u5bf9\u624b\u4e2d\u90fd\u6ca1\u6709\u5e7b\u672f\u5e08\u6216\u8005\u9644\u9b54\u5e08\uff0c\u201d\u743c\u6069\u60cb\u60dc\u7740\uff0c\u201c\u5426\u5219\u5012\u53ef\u4ee5\u770b\u770b\u5979\u5bf9\u8fd9\u65b9\u9762\u9b54\u6cd5\u7684\u62b5\u6297\u80fd\u529b\u5982\u4f55\u3002\u201d</p><p>\u5deb\u5e08\u7684\u4ef7\u503c\uff0c\u539f\u672c\u5c31\u4e0d\u662f\u9b54\u6cd5\u70ae\u53f0\u2026\u2026\u5f53\u7136\uff0c\u51db\u8fd9\u79cd\u5df2\u7ecf\u5c5e\u4e8e\u8d85\u7ea7\u9b54\u6cd5\u70ae\uff0c\u8981\u51fb\u8d25\u5bf9\u624b\uff0c\u672a\u5fc5\u4e00\u5b9a\u8981\u786c\u78b0\u786c\u5730\u5bf9\u6297\uff0c\u6070\u5f53\u5730\u8fd0\u7528\u5e7b\u672f\u3001\u9b45\u60d1\u3001\u7cbe\u795e\u63a7\u5236\u8fd9\u4e9b\u8f85\u52a9\u6cd5\u672f\uff0c\u4e5f\u5f80\u5f80\u6709\u610f\u60f3\u4e0d\u5230\u7684\u6548\u679c\u3002\u53ef\u60dc\u743c\u6069\u81ea\u5df1\u5728\u8fd9\u4e9b\u65b9\u9762\u4e5f\u4e0d\u751a\u7cbe\u901a\uff0c\u6ca1\u4ec0\u4e48\u628a\u63e1\u3002</p><p>\u65f6\u95f4\u4e0d\u77e5\u4e0d\u89c9\u8fc7\u53bb\uff0c\u76f4\u5230\u591c\u5e55\u964d\u4e34\uff0c\u6885\u83f2\u65af\u624d\u4ece\u5916\u9762\u56de\u6765\uff0c\u9887\u4e3a\u9ad8\u5174\u5730\u63d0\u7740\u4e00\u5305\u4e1c\u897f\u3002\u201c\u4f60\u4e70\u4e86\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5f88\u597d\u5947\uff0c\u7136\u540e\u770b\u89c1\u5979\u628a\u5305\u88f9\u5c55\u5f00\uff0c\u5f80\u5e8a\u4e0a\u4e00\u5012\uff0c\u54d7\u5566\u54d7\u5566\u54d7\u5566\u2026\u2026</p><p>\u6212\u6307\u3001\u8033\u73af\u3001\u540a\u5760\u3001\u80f8\u9488\u2026\u2026\u4ee5\u53ca\u66f4\u591a\u743c\u6069\u53eb\u4e0d\u51fa\u540d\u5b57\u6216\u8005\u4e00\u65f6\u60f3\u4e0d\u8d77\u540d\u5b57\u7684\u4e1c\u897f\uff0c\u5168\u662f\u5973\u5b69\u5b50\u559c\u6b22\u7684\u90a3\u4e9b\u5c0f\u73a9\u610f\uff0c\u800c\u4e14\u6570\u91cf\u6781\u591a\u3002\u743c\u6069\u7a0d\u7a0d\u77a5\u4e86\u773c\uff0c\u53d1\u73b0\u5149\u80f8\u9488\u5c31\u6709\u4e03\u516b\u4e2a\uff0c\u6837\u5f0f\u5404\u5f02\uff0c\u5236\u4f5c\u7cbe\u826f\uff0c\u867d\u7136\u6ca1\u6709\u9576\u5d4c\u4ec0\u4e48\u91d1\u7389\u5b9d\u77f3\uff0c\u4f46\u770b\u6837\u5b50\u4ef7\u683c\u4e5f\u4e0d\u83f2\u3002</p><p>\u6885\u83f2\u65af\u4ee5\u524d\u662f\u6559\u4f1a\u7684\u5723\u6b66\u58eb\uff0c\u8fd9\u6b21\u51fa\u8fdc\u95e8\u529e\u4e8b\uff0c\u56ca\u4e2d\u8d44\u91d1\u81ea\u7136\u5bbd\u88d5\uff0c\u8fd9\u70b9\u743c\u6069\u662f\u77e5\u9053\u7684\uff0c\u4f46\u5374\u4ece\u6ca1\u60f3\u5230\u5979\u4f1a\u82b1\u94b1\u4e70\u8fd9\u4e9b\u4e1c\u897f\uff0c\u800c\u4e14\u8fd8\u4e70\u8fd9\u4e48\u591a\u3002\u201c\u4f60\u771f\u5962\u4f88\uff0c\u201d\u4ed6\u534a\u771f\u534a\u5047\u5730\u611f\u53f9\u7740\uff0c\u201c\u8fd9\u4e48\u591a\u4f60\u7528\u5f97\u8fc7\u6765\u4e48\u3002\u201d</p><p>\u201c\u53cd\u6b63\u90fd\u662f\u5979\u9001\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8981\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u4e00\u8fb9\u6574\u7406\u7740\u90a3\u4e9b\u5c0f\u73a9\u610f\uff0c\u4e00\u8fb9\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u51db\u8bf4\uff0c\u8fd9\u4e9b\u4e1c\u897f\u539f\u672c\u90fd\u5e94\u8be5\u662f\u7537\u5b69\u5b50\u9001\u7684\uff0c\u65e2\u7136\u6211\u9047\u5230\u7684\u662f\u4e2a\u7b28\u86cb\uff0c\u90a3\u4e48\u5979\u8fd9\u4e2a\u670b\u53cb\u4e5f\u5c31\u53ea\u597d\u52c9\u4e3a\u5176\u96be\u6765\u5e2e\u5e2e\u5fd9\u4e86\u3002\u201d</p><p>\u201c\u5443\uff0c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u201c\u5979\u8fd8\u9080\u8bf7\u6211\u642c\u8fc7\u53bb\u4f4f\u5462\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u4e0d\u884c\uff01\u201d\u743c\u6069\u672c\u80fd\u5730\u53cd\u5bf9\u3002</p><p>\u201c\u653e\u5fc3\uff0c\u6211\u62d2\u7edd\u4e86\uff0c\u6211\u624d\u4e0d\u4f1a\u7ed9\u5979\u5632\u7b11\u6211\u7684\u673a\u4f1a\u5462\u3002\u201d</p><p>\u201c\u5514\uff1f\u5632\u7b11\u4f60\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5632\u7b11\u6211\u8eab\u8fb9\u7684\u5bb6\u4f19\u662f\u4e2a\u7b28\u86cb\uff01\u201d\u6885\u83f2\u65af\u8f6c\u8fc7\u8138\u6765\uff0c\u77aa\u7740\u743c\u6069\uff0c\u6076\u72e0\u72e0\u5730\u8bf4\uff0c\u201c\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\u4f60\u90fd\u4e0d\u660e\u767d\u5417\uff1f\u6789\u4f60\u8fd8\u662f\u4e2a\u5deb\u5e08\u2026\u2026\u7b97\u4e86\uff0c\u201d\u5979\u6709\u6c14\u65e0\u529b\u5730\u6325\u6325\u624b\uff0c\u201c\u6bcf\u6b21\u89c1\u5230\u5979\u90fd\u8981\u5435\u67b6\uff0c\u813e\u6c14\u5c31\u4f1a\u53d8\u574f\uff0c\u522b\u4ecb\u610f\uff0c\u8fc7\u6bb5\u65f6\u95f4\u5c31\u597d\u3002\u201d</p><p>\u201c\u554a\uff0c\u6ca1\u4e8b\u3002\u201d\u743c\u6069\u6084\u6084\u62b9\u6389\u51b7\u6c57\uff0c\u8fd9\u4e24\u4eba\u5230\u5e95\u662f\u4ec0\u4e48\u670b\u53cb\u554a\u3002</p><p>\u201c\u53cd\u6b63\u6211\u62d2\u7edd\u4e86\u5c31\u662f\uff0c\u5979\u7761\u89c9\u4e60\u60ef\u53c8\u5dee\uff0c\u53c8\u559c\u6b22\u8e39\u88ab\u5b50\u53c8\u559c\u6b22\u8e22\u4eba\uff0c\u505a\u6076\u68a6\u4e86\u8fd8\u559c\u6b22\u628a\u4eba\u62b1\u5f97\u7d27\u7d27\uff0c\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\uff0c\u6211\u624d\u4e0d\u613f\u610f\u53bb\u81ea\u627e\u82e6\u5403\u5462\u3002\u201d</p><p>\u201c\u5514\uff1f\u4f60\u4eec\u7ecf\u5e38\u7761\u5728\u4e00\u8d77\u554a\uff1f\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u4ee5\u524d\u6211\u4eec\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u665a\u4e0a\u5979\u6ca1\u6211\u966a\u7740\u5c31\u7761\u4e0d\u7740\u5462\uff0c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u89c9\u5f97\u602a\u602a\u7684\u3002\u201d</p><p>\u201c\u5f88\u6b63\u5e38\u5427\uff0c\u201d\u6885\u83f2\u65af\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u5973\u5b69\u5b50\u7ecf\u5e38\u4e00\u8d77\u7761\u89c9\u7684\u3002\u201d</p><p>\u4e5f\u662f\uff0c\u7537\u5973\u5728\u8fd9\u65b9\u9762\u6027\u522b\u5dee\u5f02\u662f\u5f88\u5927\u7684\uff0c\u5973\u5b69\u5b50\u4e4b\u95f4\u8eab\u4f53\u63a5\u89e6\u8f83\u591a\uff0c\u4e0a\u8857\u7275\u7740\u624b\uff0c\u6402\u6402\u62b1\u62b1\u90fd\u5f88\u6b63\u5e38\uff1b\u7537\u4eba\u5982\u679c\u8fd9\u4e48\u5e72\uff0c\u90a3\u5c31\u51e0\u4e4e\u53ef\u4ee5\u80af\u5b9a\u662f\u73bb\u7483\u4e86\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u4f60\u77e5\u9053\u6211\u90a3\u4f4d\u670b\u53cb\u662f\u600e\u4e48\u8bc4\u4ef7\u4f60\u7684\u5417\uff1f\u201d</p><p>\u201c\u7b28\u86cb\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u4e5f\u6ca1\u9519\uff0c\u4e0d\u8fc7\u8fd9\u662f\u6700\u540e\u7684\u5b9a\u6027\u603b\u7ed3\uff0c\u5728\u6b64\u4e4b\u524d\u8fd8\u6709\u66f4\u591a\u8be6\u7ec6\u7684\u5185\u5bb9\uff0c\u6709\u6ca1\u6709\u5174\u8da3\u542c\u542c\uff1f\u201d</p><p>\u201c\u8bf4\u5427\uff0c\u6211\u505a\u597d\u5fc3\u7406\u51c6\u5907\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u522b\u90a3\u4e48\u7d27\u5f20\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5e76\u4e0d\u5168\u662f\u8d1f\u9762\u8bc4\u4ef7\u2014\u2014\u6211\u662f\u8bf4\uff0c\u81f3\u5c11\u8fd8\u6709\u4e00\u4e9b\u662f\u504f\u5411\u6b63\u9762\u7684\u8bc4\u4ef7\u3002\u6bd4\u5982\u8bf4\u5979\u5938\u5956\u4f60\u5f88\u6709\u98ce\u5ea6\uff0c\u6ca1\u6709\u50cf\u5176\u4ed6\u7537\u4eba\u90a3\u6837\u5fd9\u7740\u5077\u770b\u5979\u7684\u88d9\u5b50\u548c\u5927\u817f\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6211\u662f\u6b63\u4eba\u541b\u5b50\u561b\u3002\u201d</p><p>\u201c\u7136\u800c\u5979\u62a5\u6028\u4f60\u7684\u76ee\u5149\u4e00\u76f4\u76ef\u7740\u5979\u7684\u80f8\u53e3\u3002\u201d</p><p>\u201c\u5443\uff0c\u8fd9\u4e2a\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u4f3c\u7b11\u975e\u7b11\u5730\u770b\u7740\u4ed6\uff0c\u201c\u5f88\u5927\u662f\u5417\uff1f\u201d\u5979\u7a81\u7136\u95ee\u3002</p><p>\u201c\u554a\uff0c\u4ec0\u4e48\uff1f\u201d\u6b63\u5904\u4e8e\u5c34\u5c2c\u72b6\u6001\u7684\u743c\u6069\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u201d\u6885\u83f2\u65af\u4fa7\u8eab\u8fc7\u6765\uff0c\u4fef\u89c6\u7740\u6b63\u9760\u5728\u5e8a\u5934\u7684\u743c\u6069\uff0c\u201c\u6211\u662f\u8bf4\uff0c\u5979\u90a3\u5730\u65b9\u5f88\u5927\uff0c\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u201c\u5443\uff0c\u8fd9\u4e2a\u4e48\u2026\u2026\u201d</p><p>\u201c\u6bd4\u6211\u5927\u662f\u5427\uff1f\u201d\u5c11\u5973\u8ffd\u95ee\u3002</p><p>\u8fce\u7740\u6885\u83f2\u65af\u5484\u5484\u903c\u4eba\u7684\u76ee\u5149\uff0c\u743c\u6069\u5b9e\u5728\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u56de\u7b54\u3002\u5b9e\u4e8b\u6c42\u662f\u5730\u8bf4\uff0c\u51db\u7684\u5c3a\u5bf8\u81f3\u5c11\u6709C\uff0c\u8fdc\u4e0d\u662f\u6885\u83f2\u65af\u53ef\u6bd4\u7684\u3002\u5982\u679c\u7167\u5b9e\u56de\u7b54\u5f97\u8bdd\uff0c\u6885\u83f2\u65af\u80af\u5b9a\u4f1a\u4e0d\u9ad8\u5174\u5427\uff0c\u4f46\u5982\u679c\u5426\u8ba4\uff0c\u5374\u53c8\u4f3c\u4e4e\u592a\u7f54\u987e\u4e8b\u5b9e\u4e86\u70b9\uff0c\u4e00\u542c\u5c31\u77e5\u9053\u662f\u5047\u8bdd\u3002</p><p>\u201c\u679c\u7136\uff0c\u7537\u4eba\u90fd\u559c\u6b22\u6bd4\u8f83\u5927\u7684\u5462\uff0c\u201d\u6885\u83f2\u65af\u53f9\u6c14\uff0c\u5e7d\u5e7d\u5730\u8bf4\uff0c\u201c\u4f60\u4e5f\u6bd4\u8f83\u559c\u6b22\u5979\u90a3\u79cd\u662f\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\u7684\u4e8b\uff01\u201d\u610f\u8bc6\u5230\u518d\u4e0d\u5b89\u6170\u7684\u8bdd\uff0c\u5c11\u5973\u5c31\u8981\u66b4\u8d70\u4e86\uff0c\u743c\u6069\u8d76\u5fd9\u8868\u660e\u7acb\u573a\uff0c\u201c\u6211\u5f53\u7136\u662f\u6bd4\u8f83\u559c\u6b22\u4f60\u7684\u2026\u2026\u90a3\u5730\u65b9\u3002\u201d</p><p>\u8fd9\u8bdd\u542c\u8d77\u6765\u771f\u5947\u602a\uff0c\u4e0d\u8fc7\u6885\u83f2\u65af\u5e76\u6ca1\u6709\u4ecb\u610f\u7684\u6837\u5b50\uff0c\u201c\u53ef\u662f\uff0c\u201d\u5979\u7591\u60d1\u7740\uff0c\u201c\u6211\u4e0d\u662f\u542c\u8bf4\u7537\u4eba\u90fd\u559c\u6b22\u6bd4\u8f83\u5927\u7684\u5417\uff1f\u201d</p><p>\u201c\u90a3\u4e2a\uff0c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\u7684\u5427\uff0c\u201d\u743c\u6069\u968f\u53e3\u56de\u7b54\uff0c\u201c\u5973\u5b69\u5b50\u2026\u2026\u90a3\u5730\u65b9\u53c8\u4e0d\u662f\u5355\u7eaf\u770b\u5927\u5c0f\u5c3a\u5bf8\u7684\uff0c\u8fd8\u5e94\u8be5\u770b\u5f62\u72b6\u548c\u624b\u611f\u624d\u5bf9\uff0c\u8fd8\u6709\u2026\u2026\u201d</p><p>\u201c\u54e6\uff0c\u542c\u8d77\u6765\uff0c\u4f60\u4f3c\u4e4e\u5f88\u6e05\u695a\u5979\u90a3\u5730\u65b9\u7684\u5f62\u72b6\u548c\u624b\u611f\u4f3c\u7684\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u54ea\u91cc\u8868\u8fbe\u4e86\u8fd9\u79cd\u610f\u601d\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u5f88\u6e05\u695a\u7684\u903b\u8f91\u63a8\u7406\u5417\uff1f\u201d\u6885\u83f2\u65af\u7406\u6240\u5f53\u7136\u5730\u8bf4\uff0c\u201c\u4f60\u627f\u8ba4\u4e86\uff0c\u5979\u90a3\u91cc\u6bd4\u6211\u5927\uff1b\u4f60\u4e5f\u627f\u8ba4\u4e86\uff0c\u7537\u4eba\u90fd\u559c\u6b22\u5927\u7684\uff0c\u53e6\u5916\u4e24\u9879\u53c2\u8003\u56e0\u7d20\u662f\u5f62\u72b6\u548c\u624b\u611f\uff1b\u800c\u4f60\u53c8\u8bf4\u4f60\u66f4\u559c\u6b22\u6211\u2014\u2014\u8fd9\u4e0d\u662f\u5145\u5206\u8bf4\u660e\u4f60\u5df2\u7ecf\u786e\u5b9a\u4e86\u5979\u90a3\u5730\u65b9\u7684\u5f62\u72b6\u548c\u624b\u611f\u4e0d\u5982\u6211\uff0c\u6240\u4ee5\u624d\u4f5c\u51fa\u8fd9\u79cd\u9009\u62e9\u7684\u5417\uff1f\u201d</p><p>\u201c\u6211\u2026\u2026\u6211\u8bf4\uff0c\u827e\u5f25\u8587\uff0c\u903b\u8f91\u63a8\u7406\u4e0d\u8981\u968f\u4fbf\u7528\u5230\u8fd9\u79cd\u4e8b\u60c5\u4e0a\u5427\u3002\u201d</p><p>\u201c\u7136\u800c\u6211\u89c9\u5f97\u6211\u7684\u63a8\u7406\u65e0\u61c8\u53ef\u51fb\u554a\u3002\u201d</p><p>\u201c\u522b\u5f00\u8fd9\u79cd\u73a9\u7b11\u554a\uff0c\u6211\u548c\u4f60\u4e00\u6837\u90fd\u662f\u6628\u5929\u624d\u521a\u5230\u8fd9\u91cc\uff0c\u4e4b\u524d\u538b\u6839\u4e0d\u8ba4\u8bc6\u5979\uff0c\u54ea\u6709\u673a\u4f1a\u53bb\u2026\u2026\u201d</p><p>\u201c\u54e6\u54e6\uff0c\u8fd9\u4e48\u8bf4\uff0c\u5982\u679c\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u4f60\u5f88\u613f\u610f\u53bb\u5b9e\u9645\u8003\u5bdf\u4e00\u4e0b\u5bf9\u5427\uff1f\u201d</p><p>\u743c\u6069\u54d1\u53e3\u65e0\u8a00\uff0c\u4ed4\u7ec6\u6253\u91cf\u7740\u6b63\u5c45\u9ad8\u4e34\u4e0b\u4fef\u89c6\u81ea\u5df1\u7684\u5c11\u5973\uff0c\u201c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u7591\u60d1\u7740\uff0c\u201c\u600e\u4e48\u611f\u89c9\u534a\u5929\u4e0d\u89c1\uff0c\u4f60\u6709\u70b9\u4e0d\u592a\u4e00\u6837\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u201d\u5c11\u5973\u4ef0\u8d77\u8138\uff0c\u82e5\u6709\u6240\u601d\uff0c\u201c\u6709\u53ef\u80fd\uff0c\u5927\u6982\u662f\u56e0\u4e3a\u548c\u90a3\u4e2a\u5bb6\u4f19\u5728\u4e00\u8d77\u5446\u4e86\u534a\u5929\u7684\u7f18\u6545\uff0c\u88ab\u5f71\u54cd\u4e86\u5427\u3002\u201d</p><p>\u88ab\u5f71\u54cd\u4e48\u2026\u2026\u8fd9\u5f71\u54cd\u901f\u5ea6\u53ef\u771f\u5feb\u554a\u3002</p><p>\u201c\u770b\u6765\u4f60\u90a3\u4f4d\u670b\u53cb\u8fd8\u771f\u662f\u6709\u611f\u67d3\u529b\u5462\uff0c\u201d\u743c\u6069\u53f9\u606f\u7740\uff0c\u9aa4\u7136\u5feb\u82e5\u95ea\u7535\u5730\u4f38\u51fa\u53f3\u624b\uff0c\u6309\u5728\u5c11\u5973\u7684\u5de6\u80a9\u548c\u8116\u9888\u76f8\u8fde\u7684\u5730\u65b9\uff0c\u4ed6\u7684\u76ee\u5149\u5239\u90a3\u95f4\u53d8\u5f97\u9510\u5229\u8d77\u6765\uff0c\u201c\u5bf9\u4e0d\u5bf9\uff0c\u51db\u5c0f\u59d0\u3002\u201d</p><p>\u5c11\u5973\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u5fae\u7b11\u8d77\u6765\uff0c\u201c\u554a\u5440\u554a\u5440\uff0c\u88ab\u4f60\u770b\u7a7f\u4e86\u5462\u3002\u201d\u5979\u6447\u4e86\u6447\u5934\uff0c\u5ff5\u51fa\u4e00\u53e5\u5492\u8bed\uff0c\u9762\u5bb9\u968f\u5373\u53d1\u751f\u6539\u53d8\uff0c\u8eab\u5f62\u4e5f\u53d8\u5316\u8d77\u6765\uff0c\u4ece\u91d1\u8272\u77ed\u53d1\u7684\u5723\u6b66\u58eb\u53d8\u6210\u4e86\u7a7f\u7740\u7ea2\u886c\u886b\u3001\u8d85\u77ed\u88d9\u548c\u9ed1\u8272\u957f\u7b52\u889c\u7684\u7ea2\u888d\u5851\u80fd\u5deb\u5e08\u3002</p><p>\u4f46\u4e24\u4eba\u4f9d\u65e7\u7ef4\u6301\u7740\u539f\u672c\u7684\u59ff\u52bf\uff0c\u5c11\u5973\u8dea\u5728\u5e8a\u7684\u8fb9\u6cbf\uff0c\u4fef\u8eab\u770b\u7740\u743c\u6069\uff1b\u743c\u6069\u9760\u5728\u5e8a\u5934\u8eba\u7740\uff0c\u53f3\u624b\u5374\u6309\u4f4f\u4e86\u5c11\u5973\u7684\u80a9\u3002</p><p>\u201c\u4f60\u6765\u6b64\u6709\u4f55\u8d35\u5e72\uff0c\u51db\u5c0f\u59d0\u3002\u201d\u743c\u6069\u8bf4\uff0c\u6ca1\u6709\u677e\u5f00\u624b\u7684\u610f\u601d\u3002\u867d\u7136\u4ed6\u5e76\u4e0d\u8ba4\u4e3a\u5bf9\u65b9\u662f\u51c6\u5907\u6765\u63d0\u524d\u5f00\u6218\uff0c\u4f46\u88ab\u4eba\u620f\u800d\u603b\u662f\u5fc3\u6709\u4e0d\u5feb\u3002</p><p>\u4ed6\u6614\u65e5\u8ddf\u968f\u7530\u4f2f\u5149\u5b66\u6b66\u827a\uff0c\u867d\u7136\u6ca1\u591a\u9ad8\u7684\u6210\u5c31\uff0c\u4f46\u597d\u6b79\u4e5f\u638c\u63e1\u4e86\u4e9b\u8fd1\u8eab\u683c\u6597\u7684\u77e5\u8bc6\u3002\u521a\u624d\u5728\u51fa\u624b\u4e4b\u524d\uff0c\u4ed6\u5df2\u7ecf\u6697\u4e2d\u9ed8\u53d1\u4e86\u201c\u86ee\u725b\u4e4b\u529b\u201d\u6cd5\u672f\uff0c\u8ba9\u81ea\u5df1\u7684\u529b\u6c14\u9661\u7136\u589e\u5f3a\u3002\u5982\u4eca\u4ed6\u7684\u624b\u6309\u7684\u4f4d\u7f6e\uff0c\u867d\u7136\u770b\u4e0a\u53bb\u6ca1\u4ec0\u4e48\uff0c\u7136\u800c\u53ea\u8981\u613f\u610f\uff0c\u5c31\u80fd\u4e25\u91cd\u5730\u4f24\u5bb3\u5230\u8fd9\u4e2a\u72e1\u733e\u7684\u5c0f\u5973\u5deb\u3002</p><p>\u201c\u54a6\uff0c\u201d\u5b8c\u5168\u65e0\u89c6\u81ea\u5df1\u7684\u5904\u5883\uff0c\u4e5f\u6ca1\u6709\u8d77\u8eab\u7684\u610f\u601d\uff0c\u51db\u53cd\u800c\u6709\u4e9b\u60ca\u8bb6\u8d77\u6765\uff0c\u201c\u4f60\u7684\u529b\u6c14\u4e0d\u5c0f\u5462\uff0c\u51fa\u624b\u4e5f\u5f88\u5feb\uff0c\u4f3c\u4e4e\u6709\u4e24\u4e0b\u5b50\u7684\u6837\u5b50\uff0c\u548c\u827e\u5f25\u8587\u544a\u8bc9\u6211\u7684\u60c5\u51b5\u4e0d\u592a\u4e00\u6837\u561b\u3002\u201d</p><p>\u201c\u55ef\uff1f\u827e\u5f25\u8587\u8ddf\u4f60\u8bf4\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u827e\u5f25\u8587\u544a\u8bc9\u6211\u8bf4\uff0c\u4f60\u529b\u6c14\u4e5f\u4e0d\u5927\uff0c\u8dd1\u5f97\u4e5f\u4e0d\u5feb\uff0c\u8eab\u624b\u4e5f\u7b28\u62d9\uff0c\u8111\u7b4b\u4e5f\u4e0d\u7075\u5149\uff0c\u9664\u4e86\u4f1a\u70b9\u534a\u8c03\u5b50\u9b54\u6cd5\u5c31\u4e00\u65e0\u662f\u5904\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u5f31\u4e0d\u7981\u98ce\u4f46\u4e5f\u76f8\u5dee\u65e0\u51e0\uff0c\u6bcf\u6b21\u9047\u5230\u5371\u9669\u90fd\u9700\u8981\u9760\u5979\u8fd9\u4e2a\u5973\u5b69\u5b50\u6765\u4fdd\u62a4\uff0c\u7b80\u76f4\u5c31\u662f\u5dee\u52b2\u5f97\u4e00\u584c\u7cca\u6d82\u2026\u2026\u201d</p><p>\u201c\u6211\u6ca1\u8fd9\u4e48\u8bf4\u8fc7\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u58f0\u97f3\u4ece\u95e8\u5916\u4f20\u6765\uff0c\u6253\u65ad\u4e86\u51db\u6ed4\u6ed4\u4e0d\u7edd\u7684\u63cf\u8ff0\uff0c\u743c\u6069\u4fa7\u8138\u770b\u53bb\uff0c\u89c1\u7a7f\u6de1\u84dd\u8272\u886c\u886b\u7684\u5723\u6b66\u58eb\u63a8\u95e8\u8d70\u5165\u3002\u201c\u522b\u4e71\u7be1\u6539\u522b\u4eba\u7684\u8bdd\uff0c\u51db\u3002\u201d</p><p>\u201c\u4f46\u4f60\u8bf4\u5f97\u5176\u5b9e\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u561b\u3002\u201d\u51db\u5929\u771f\u65e0\u90aa\u5730\u7b11\u7740\uff0c\u8f7b\u677e\u5730\u4ece\u5e8a\u4e0a\u8df3\u4e0b\u6765\u3002\u743c\u6069\u539f\u672c\u6309\u4f4f\u4e86\u5979\u7684\u80a9\u5934\uff0c\u4f46\u9661\u7136\u611f\u89c9\u624b\u4e0a\u4e00\u6ed1\uff0c\u4eff\u4f5b\u89e6\u5230\u4e86\u4ec0\u4e48\u51b0\u51b7\u575a\u786c\u7684\u4e1c\u897f\uff0c\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u51db\u5c31\u5df2\u7ecf\u6446\u8131\u4e86\u4ed6\u7684\u538b\u5236\u3002</p><p>\u201c\u4f60\u626d\u66f2\u4e86\u6211\u7684\u610f\u601d\u3002\u201d\u6885\u83f2\u65af\u6c89\u9759\u4f46\u575a\u51b3\u5730\u53cd\u9a73\u3002</p><p>\u201c\u597d\u5566\u597d\u5566\uff0c\u201d\u51db\u4e0d\u4ee5\u4e3a\u610f\u5730\u6446\u6446\u624b\uff0c\u201c\u53cd\u6b63\u5927\u81f4\u4e0d\u5dee\u5c31\u53ef\u4ee5\u4e86\u561b\uff0c\u522b\u90a3\u4e48\u8ba4\u771f\u3002\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u201d\u5979\u8f6c\u8fc7\u5934\u6765\uff0c\u76f4\u547c\u743c\u6069\u7684\u540d\u5b57\uff0c\u4eff\u4f5b\u662f\u76f8\u719f\u7684\u670b\u53cb\u4f3c\u7684\uff0c\u201c\u6211\u7684\u53d8\u5f62\u5e94\u8be5\u6ca1\u6709\u7834\u7efd\u5427\uff0c\u6a21\u4eff\u827e\u5f25\u8587\u5e94\u8be5\u4e5f\u6ca1\u6709\u95ee\u9898\u624d\u5bf9\uff0c\u4f60\u662f\u600e\u4e48\u770b\u51fa\u6765\u662f\u6211\u7684\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u611f\u89c9\u3002\u201d</p><p>\u201c\u611f\u89c9\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u4e5f\u8d77\u8eab\u4e0b\u5e8a\uff0c\u7ad9\u5728\u5899\u8fb9\uff0c\u201c\u8fd9\u5f88\u5bb9\u6613\u7406\u89e3\u5427\uff0c\u6c14\u8d28\u622a\u7136\u4e0d\u540c\u7684\u4e24\u4e2a\u4eba\uff0c\u5c31\u7b97\u662f\u518d\u4eb2\u5bc6\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u6ca1\u6cd5\u5b8c\u5168\u6a21\u4eff\u5f97\u4e00\u6a21\u4e00\u6837\u7684\u5427\u3002\u201d</p><p>\u201c\u54e6\u54e6\uff0c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u4f60\u7684\u827e\u5f25\u8587\u662f\u5584\u826f\u7eaf\u6d01\u5929\u771f\u65e0\u7455\u7684\u5929\u5802\u5c71\u5723\u5929\u4f7f\uff0c\u800c\u6211\u5c31\u662f\u6df1\u6e0a\u91cc\u7684\u5c0f\u6076\u9b54\u4e86\uff1f\u201d</p><p>\u201c\u6211\u4f3c\u4e4e\u6ca1\u8fd9\u4e48\u8bf4\u5427\u3002\u201d</p><p>\u201c\u4f46\u5fc3\u91cc\u5c31\u662f\u8fd9\u4e48\u60f3\u7684\u5bf9\u5427\u3002\u201d</p><p>\u743c\u6069\u51b3\u5b9a\u6c89\u9ed8\uff0c\u8ddf\u5973\u5b69\u5b50\u6597\u5634\u6ca1\u610f\u601d\u3002</p><p>\u6885\u83f2\u65af\u6070\u5230\u597d\u5904\u5730\u8fc7\u6765\u89e3\u56f4\uff0c\u201c\u597d\u4e86\uff0c\u51db\uff0c\u4f60\u5df2\u7ecf\u9001\u6211\u56de\u6765\u4e86\uff0c\u65f6\u5019\u4e0d\u65e9\uff0c\u4f60\u4e5f\u8be5\u65e9\u70b9\u56de\u53bb\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u660e\u5929\u4e0d\u662f\u8fd8\u6709\u51b3\u6597\u5417\uff0c\u5e94\u8be5\u65e9\u4f5c\u51c6\u5907\u7684\u5427\u3002\u201d</p><p>\u201c\u54e6\uff0c\u653e\u5fc3\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u8fd9\u79cd\u6beb\u65e0\u60ac\u5ff5\u7684\u6218\u6597\u4e0d\u7528\u51c6\u5907\u4e5f\u6ca1\u5173\u7cfb\u7684\uff0c\u201d\u51db\u53ef\u7231\u5730\u62cd\u62cd\u624b\uff0c\u201c\u4e0d\u8fc7\u653e\u5fc3\u597d\u4e86\uff0c\u770b\u5728\u827e\u5f25\u8587\u7684\u4efd\u4e0a\uff0c\u6211\u4e00\u5b9a\u4f1a\u624b\u4e0b\u7559\u60c5\u7684\u5462\u2026\u2026\u5bf9\u5427\uff0c\u743c\u6069\u3002\u201d\u6700\u540e\u4e00\u53e5\u8bdd\u662f\u5bf9\u743c\u6069\u8bf4\u7684\u3002</p><p>\u201c\u540c\u6837\uff0c\u770b\u5728\u827e\u5f25\u8587\u7684\u4efd\u4e0a\uff0c\u6211\u4e5f\u4f1a\u624b\u4e0b\u7559\u60c5\u7684\u3002\u201d\u743c\u6069\u56de\u7b54\u3002</p><p>\u201c\u554a\u5440\u554a\u5440\uff0c\u201d\u51db\u6447\u5934\uff0c\u201c\u53ea\u4f1a\u6284\u88ad\u522b\u4eba\u7684\u8bdd\uff0c\u771f\u662f\u4e00\u70b9\u521b\u610f\u90fd\u6ca1\u6709\u5462\uff0c\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\u8fd9\u662f\u5927\u5fcc\u554a\uff0c\u6ce8\u5b9a\u4e86\u5c06\u6765\u4e0d\u4f1a\u6709\u5149\u660e\u7684\u524d\u9014\u5462\u3002\u827e\u5f25\u8587\uff0c\u5982\u679c\u8ba9\u6211\u5efa\u8bae\u7684\u8bdd\uff0c\u6211\u89c9\u5f97\u4f60\u5b9e\u5728\u5e94\u8be5\u91cd\u65b0\u8003\u8651\u2026\u2026\u201d</p><p>\u201c\u51db\uff01\u201d\u6885\u83f2\u65af\u7565\u6709\u4e9b\u4e0d\u5feb\u5730\u559d\u65ad\u4e86\u5979\uff0c\u201c\u6211\u60f3\u6709\u4e9b\u4e8b\u60c5\uff0c\u8fd8\u662f\u7531\u6211\u81ea\u5df1\u6765\u5224\u65ad\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u201c\u4e5f\u5bf9\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u5c3d\u670b\u53cb\u7684\u4e49\u52a1\u505a\u51fa\u5fe0\u544a\u561b\uff0c\u201d\u51db\u6325\u6325\u624b\uff0c\u201c\u90a3\u4e48\uff0c\u660e\u5929\u89c1\uff0c\u4e24\u4f4d\u3002\u5bf9\u4e86\uff0c\u8d81\u65f6\u95f4\u8fd8\u65e9\uff0c\u53bb\u627e\u95f4\u795e\u6bbf\u4e70\u51e0\u74f6\u6cbb\u7597\u836f\u6c34\u5427\uff0c\u5361\u66d9\u65af\u7684\u795e\u6bbf\u4e00\u822c\u5f88\u665a\u624d\u5173\u95e8\u7684\u3002\u201d</p><p>\u201c\u591a\u8c22\u3002\u201d</p><p>\u201c\u90a3\u4e48\u6211\u8d70\u4e86\u3002\u201d\u7528\u8d85\u7ea7\u53ef\u7231\u7684\u58f0\u97f3\u544a\u522b\uff0c\u8d85\u77ed\u88d9\u5c11\u5973\u8e66\u8e66\u8df3\u8df3\u5730\u51fa\u95e8\u53bb\u4e86\u3002</p><p>\u201c\u5979\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u786e\u5b9a\u90a3\u4f4d\u5c0f\u6076\u9b54\u822c\u7684\u5c11\u5973\u5df2\u7ecf\u79bb\u5f00\uff0c\u743c\u6069\u8f6c\u5934\u95ee\u6885\u83f2\u65af\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u5979\u575a\u6301\u8981\u6c42\u6765\u770b\u770b\u4f60\u662f\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u6211\u62d7\u4e0d\u8fc7\u5979\uff0c\u53ea\u80fd\u7b54\u5e94\u4e86\u3002\u201d</p><p>\u201c\u4f46\u90a3\u4e5f\u4e0d\u7528\u53d8\u6210\u4f60\u7684\u6837\u5b50\u6765\u9a97\u6211\u5427\u3002\u201d</p><p>\u201c\u5979\u8bf4\u5728\u5916\u4eba\u9762\u524d\u4eba\u4f1a\u672c\u80fd\u5730\u4f2a\u88c5\u81ea\u5df1\uff0c\u53ea\u6709\u5728\u81ea\u5df1\u719f\u6089\u7684\u4eba\u9762\u524d\u624d\u4f1a\u663e\u5f97\u6bd4\u8f83\u771f\u5b9e\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u6211\u627f\u8ba4\u8fd9\u8bdd\u4e5f\u6ca1\u9519\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u6885\u83f2\u65af\u9053\u6b49\u7740\uff0c\u201c\u4e0d\u8fc7\u5979\u6ca1\u6709\u4ec0\u4e48\u6076\u610f\uff0c\u8fd9\u70b9\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u51db\u867d\u7136\u4efb\u6027\u4e86\u70b9\uff0c\u4f46\u5fc3\u5730\u5176\u5b9e\u5f88\u5584\u826f\u7684\u3002\u201d</p><p>\u89c1\u9b3c\u4e86\uff0c\u5979\u5982\u679c\u90fd\u80fd\u79f0\u5f97\u4e0a\u5584\u826f\uff0c\u90a3\u6211\u7b80\u76f4\u5c31\u662f\u5723\u6b66\u58eb\uff0c\u800c\u4f60\u8fd9\u4e2a\u5723\u6b66\u58eb\u5c31\u662f\u6b63\u4e49\u4e4b\u795e\u4e86\u3002</p><p>\u4e0d\u8fc7\u743c\u6069\u4e0d\u60f3\u80cc\u540e\u6311\u62e8\u4ed6\u4eba\u670b\u53cb\u7684\u611f\u60c5\uff0c\u8fd9\u79cd\u8bdd\u5c31\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u53ea\u662f\u773c\u795e\u91cc\u660e\u663e\u6d41\u9732\u51fa\u4e0d\u4fe1\uff0c\u6885\u83f2\u65af\u4e5f\u5e76\u4e0d\u5728\u610f\u3002\u201c\u65e9\u70b9\u4f11\u606f\u5427\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u660e\u5929\u8fd8\u6709\u51b3\u6597\u5462\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d\u743c\u6069\u53f9\u6c14\uff0c\u60f3\u4e86\u4e00\u4e0b\u5348\uff0c\u4e5f\u6ca1\u60f3\u51fa\u4ec0\u4e48\u4ee4\u4eba\u6ee1\u610f\u7684\u65b9\u6848\u6765\u3002\u65e2\u8981\u4fdd\u8bc1\u53d6\u80dc\uff0c\u53c8\u4e0d\u80fd\u5f53\u771f\u91cd\u4f24\u5bf9\u624b\uff0c\u5426\u5219\u4ed6\u90fd\u8003\u8651\u5e26\u4e00\u5806\u7f29\u7269\u8fc7\u7684\u77f3\u5934\u4e0a\u53bb\u7528\u7ffb\u5929\u5370\u4e71\u7838\u4e86\uff0c\u53cd\u6b63\u51db\u653e\u5f03\u4e86\u9632\u62a4\u5b66\u6d3e\uff0c\u4f30\u8ba1\u4e0d\u4f1a\u89e3\u9664\u9b54\u6cd5\u3002\u4f46\u5982\u679c\u771f\u5931\u624b\u628a\u5979\u7838\u6210\u8089\u997c\u4e86\uff0c\u7ea2\u888d\u90a3\u8fb9\u4e14\u4e0d\u8bf4\uff0c\u6885\u83f2\u65af\u8fd9\u8fb9\u5c31\u6ca1\u6cd5\u4ea4\u4ee3\u3002</p><p>\u201c\u4f60\u770b\u8d77\u6765\u5f88\u56f0\u6270\u5462\u3002\u201d</p><p>\u201c\u90a3\u662f\u81ea\u7136\u7684\u5427\uff0c\u4f60\u90a3\u4f4d\u670b\u53cb\u53ef\u5b9e\u5728\u4e0d\u597d\u5bf9\u4ed8\u554a\uff0c\u201d\u743c\u6069\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u4e00\u8fb9\u8f6c\u8eab\u51c6\u5907\u518d\u8eba\u56de\u5e8a\u4e0a\u53bb\uff0c\u4e00\u8fb9\u968f\u53e3\u8bf4\uff0c\u201c\u6211\u90fd\u60f3\u8fc7\u534a\u591c\u6e9c\u8fdb\u5979\u7684\u623f\u95f4\u53bb\u9a9a\u6270\uff0c\u8ba9\u5979\u7761\u4e0d\u6210\u89c9\uff0c\u660e\u5929\u5c31\u6ca1\u6cd5\u51c6\u5907\u9b54\u6cd5\u4e86\u2026\u2026\u201d</p><p>\u6740\u6c14\uff01</p><p>\u743c\u6069\u672c\u80fd\u5730\u611f\u89c9\u80cc\u4e0a\u4e00\u51db\uff0c\u6709\u79cd\u5371\u9669\u7684\u6c14\u606f\u51db\u51db\u8feb\u6765\uff0c\u8ba9\u4ed6\u8eab\u4f53\u9aa4\u7136\u50f5\u4f4f\u4e86\u3002\u4e00\u79d2\u949f\u4e4b\u540e\uff0c\u8fd9\u79cd\u611f\u89c9\u624d\u6d88\u5931\uff0c\u743c\u6069\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u89c1\u6885\u83f2\u65af\u6b63\u4f3c\u4e4e\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u6446\u5f04\u7740\u4e00\u679a\u8033\u5760\u3002</p><p>\u201c\u5f00\u73a9\u7b11\u5566\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u6de1\u6de1\u5730\u56de\u7b54\u7740\uff0c\u201c\u800c\u4e14\u4f60\u5982\u679c\u771f\u6562\u90a3\u4e48\u505a\u7684\u8bdd\uff0c\u4e0d\u7528\u7b49\u5230\u660e\u5929\u6bd4\u8d5b\u5b8c\uff0c\u5c31\u9700\u8981\u7528\u4e0a\u6cbb\u7597\u836f\u6c34\u4e86\u3002\u201d</p><p>\u201c\u6211\u8bf4\u4e86\u53ea\u662f\u5f00\u73a9\u7b11\u5566\uff0c\u522b\u751f\u6c14\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u548c\u6211\u65e0\u5173\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u4f1a\u88ab\u5979\u6253\u6210\u9aa8\u6298\u7684\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u4fe1\uff0c\u4ed6\u81ea\u6043\u597d\u6b79\u4e5f\u7ec3\u8fc7\u51e0\u5e74\u529f\u592b\uff0c\u96be\u9053\u8fde\u8fd9\u6837\u4e00\u4e2a\u5c0f\u4e2b\u5934\u90fd\u6536\u62fe\u4e0d\u4e0b\u6765\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\u5427\uff0c\u743c\u6069\uff0c\u5982\u679c\u4f60\u548c\u6211\u5f92\u624b\u640f\u51fb\u7684\u8bdd\uff0c\u4f60\u89c9\u5f97\u8c01\u7684\u80dc\u7b97\u6bd4\u8f83\u5927\u3002\u201d</p><p>\u201c\u90a3\u5f53\u7136\u662f\u4f60\u4e86\u3002\u201d</p><p>\u867d\u7136\u6ca1\u771f\u7684\u6253\u8fc7\uff0c\u4f46\u60f3\u60f3\u4e5f\u77e5\u9053\uff0c\u81ea\u5df1\u662f\u4e0d\u53ef\u80fd\u6709\u51e0\u6210\u80dc\u7b97\u7684\u5427\u3002</p><p>\u201c\u5982\u679c\u4f60\u4e8b\u5148\u7528\u4e0a\u5404\u79cd\u9b54\u6cd5\u6765\u8f85\u52a9\u5f3a\u5316\u5462\u3002\u201d</p><p>\u201c\u90a3\u2026\u2026\u5e94\u8be5\u4e5f\u8fd8\u662f\u4e0d\u884c\u5427\u3002\u201d</p><p>\u5b9e\u529b\u5dee\u8ddd\u592a\u5927\uff0c\u7ec8\u7a76\u4e0d\u662f\u9760\u9b54\u6cd5\u8f85\u52a9\u5c31\u53ef\u4ee5\u5f25\u8865\u7684\u5462\u3002</p><p>\u201c\u90a3\u4e48\u6211\u544a\u8bc9\u4f60\uff0c\u5982\u679c\u4e0d\u7528\u5175\u5668\u62a4\u7532\uff0c\u5355\u7eaf\u5f92\u624b\u640f\u51fb\u7684\u8bdd\uff0c\u201d\u6885\u83f2\u65af\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u6211\u90fd\u6253\u4e0d\u8fc7\u5979\u3002\u201d</p><p>\u201c\u4f60\u6ca1\u8ddf\u6211\u5f00\u73a9\u7b11\u5427\u3002\u201d</p><p>\u201c\u5982\u679c\u4f60\u6709\u5174\u8da3\uff0c\u4eb2\u81ea\u53bb\u8bd5\u8bd5\u5c31\u77e5\u9053\u4e86\u3002\u201d</p><p>\u4ece\u5c11\u5973\u7684\u8bed\u6c14\u4e2d\uff0c\u743c\u6069\u77e5\u9053\u5979\u8bf4\u5f97\u5b8c\u5168\u662f\u4e8b\u5b9e\uff0c\u800c\u8fd9\u8ba9\u4ed6\u7684\u5934\u66f4\u52a0\u75bc\u8d77\u6765\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u786e\u5b9e\u66fe\u7ecf\u6253\u8fc7\u8fd1\u8eab\u640f\u6597\u7684\u4e3b\u610f\uff0c\u51ed\u501f\u7740\u81ea\u5df1\u7684\u90a3\u70b9\u529f\u592b\uff0c\u518d\u914d\u5408\u4e0a\u5404\u79cd\u53d8\u5316\u9b54\u6cd5\u7684\u5f3a\u5316\u8f85\u52a9\uff0c\u589e\u5f3a\u529b\u91cf\uff0c\u52a0\u5feb\u901f\u5ea6\uff0c\u63d0\u5347\u53cd\u5e94\uff0c\u81ea\u89c9\u65e0\u8bba\u5982\u4f55\u4e5f\u5e94\u8be5\u80fd\u8f7b\u677e\u5bf9\u4ed8\u51db\u3002\u867d\u8bf4\u4e24\u4e2a\u5deb\u5e08\u51b3\u6597\u5f04\u5230\u9760\u8089\u640f\u53d6\u80dc\uff0c\u4f3c\u4e4e\u662f\u6709\u4e9b\u4e0d\u5bf9\u52b2\u7684\u6837\u5b50\uff0c\u4f46\u8fd9\u5df2\u7ecf\u662f\u6700\u6709\u628a\u63e1\u5e76\u4e14\u4e0d\u4f1a\u5f04\u5f97\u4f24\u4ea1\u60e8\u91cd\u7684\u65b9\u6848\u4e86\u3002\u53d8\u5316\u5b66\u6d3e\u7684\u9b54\u6cd5\uff0c\u539f\u672c\u6ce8\u91cd\u7684\u4e5f\u5c31\u662f\u5f3a\u5316\u8f85\u52a9\uff0c\u8fd9\u4e48\u505a\u4e5f\u4e0d\u7b97\u591a\u4e48\u903e\u8d8a\u672c\u5206\uff0c\u532a\u5937\u6240\u601d\u3002</p><p>\u7136\u800c\uff0c\u6309\u7167\u6885\u83f2\u65af\u8fd9\u4e48\u4e00\u8bf4\uff0c\u90a3\u8fd9\u5982\u610f\u7b97\u76d8\u5c31\u5b8c\u5168\u6253\u4e0d\u901a\u4e86\u554a\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u547b\u541f\u7740\uff0c\u201c\u4e3a\u4ec0\u4e48\u4f60\u7684\u670b\u53cb\u90fd\u662f\u8fd9\u6837\u7684\u602a\u80ce\u554a\u3002\u201d</p><p>\u201c\u8fd9\u8bdd\u8bf4\u5f97\u4e0d\u5bf9\u5427\uff0c\u201d\u6885\u83f2\u65af\u51b7\u9759\u5730\u6307\u51fa\u4ed6\u7684\u9519\u8bef\uff0c\u201c\u9996\u5148\uff0c\u4f60\u53ea\u89c1\u8fc7\u6211\u8fd9\u4e00\u4e2a\u670b\u53cb\uff0c\u8bf7\u4e0d\u8981\u7528\u2018\u90fd\u662f\u2019\u8fd9\u79cd\u6982\u62ec\u6027\u7684\u8bcd\u8bed\uff1b\u5176\u6b21\uff0c\u6211\u89c9\u5f97\u51c6\u786e\u5730\u8bf4\uff0c\u5e94\u8be5\u662f\u6211\u81ea\u4ece\u8ba4\u8bc6\u4f60\uff0c\u9047\u89c1\u7684\u5c31\u90fd\u662f\u602a\u80ce\u4e86\u3002\u201d</p><p>\u81ea\u4ece\u6c99\u6f20\u76f8\u8bc6\u4ee5\u6765\uff0c\u6df1\u6e0a\u6076\u9b54\u9886\u4e3b\u3001\u5973\u6740\u624b\u3001\u8679\u5f69\u9f99\u3001\u6b27\u51ef\u3001\u62c9\u6c83\u514b\uff0c\u4ee5\u53ca\u6700\u8fd1\u7684\u90a3\u4e2a\u6b7b\u80d6\u5b50\u5965\u6c83\u2026\u2026\u597d\u5427\uff0c\u743c\u6069\u53ea\u80fd\u627f\u8ba4\uff0c\u6bd4\u8d77\u81ea\u5df1\u7684\u62a5\u6028\uff0c\u6885\u83f2\u65af\u7684\u8bf4\u6cd5\u66f4\u63a5\u8fd1\u4e8b\u5b9e\u771f\u76f8\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u6709\u4ef6\u4e8b\u60c5\u6211\u5f88\u5947\u602a\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u662f\u795e\u5b50\u5bf9\u5427\uff0c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u6ca1\u6709\u8868\u73b0\u51fa\u4ec0\u4e48\u5929\u8d4b\u5f02\u80fd\u5462\u3002\u201d</p><p>\u65e0\u8bba\u8fd9\u4f4d\u51db\u5c0f\u59d0\u7684\u8840\u8109\u518d\u795e\u79d8\uff0c\u8eab\u4efd\u518d\u4f20\u5947\uff0c\u603b\u4e5f\u6bd4\u4e0d\u8fc7\u795e\u5b50\u5427\u3002\u5979\u90fd\u80fd\u6709\u8fd9\u6837\u60ca\u4eba\u7684\u5404\u79cd\u5929\u8d4b\u5f02\u80fd\uff0c\u600e\u4e48\u6885\u83f2\u65af\u8eab\u4e0a\u4f3c\u4e4e\u538b\u6839\u6ca1\u770b\u5230\u534a\u70b9\u5462\u3002</p><p>\u201c\u6211\u7684\u795e\u7947\u7684\u795e\u529b\uff0c\u548c\u6211\u7236\u4eb2\u7684\u795e\u529b\u5f7c\u6b64\u51b2\u7a81\u62b5\u6d88\u6389\u4e86\uff0c\u81ea\u7136\u4e0d\u4f1a\u663e\u793a\u51fa\u4ec0\u4e48\u5929\u8d4b\u5f02\u80fd\u6765\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u677e\u5730\u8bf4\uff0c\u201c\u6240\u4ee5\u5f88\u5e78\u8fd0\uff0c\u6211\u80fd\u505a\u4e2a\u666e\u901a\u4eba\u3002\u201d</p><p>\u5c31\u7b97\u662f\u8fd9\u6837\uff0c\u4f60\u4e5f\u600e\u4e48\u770b\u600e\u4e48\u4e0d\u50cf\u666e\u901a\u4eba\u5427\u2026\u2026</p><p>\u201c\u65e2\u7136\u662f\u8fd9\u6837\u7684\u8bdd\uff0c\u201d\u5c11\u5973\u628a\u624b\u4e2d\u7684\u8033\u5760\u653e\u5230\u65c1\u8fb9\u684c\u4e0a\uff0c\u62ac\u8d77\u8138\uff0c\u8ba4\u771f\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u90a3\u4e48\uff0c\u6211\u544a\u8bc9\u4f60\u4e00\u9879\u5979\u7684\u5f31\u70b9\u5427\uff0c\u6216\u8bb8\u53ef\u4ee5\u5e2e\u52a9\u4f60\u660e\u5929\u83b7\u80dc\u3002\u201d</p><p>\u201c\u54a6\uff0c\u771f\u7684\u5417\u2026\u2026\u5443\uff0c\u6211\u662f\u8bf4\uff0c\u8fd9\u6837\u4f60\u4e0d\u4f1a\u89c9\u5f97\u4e8e\u5fc3\u4e0d\u5b89\u5417\uff1f\u6bd5\u7adf\u5979\u662f\u4f60\u7684\u597d\u670b\u53cb\u5427\u3002\u201d</p><p>\u201c\u6bd4\u8d77\u80cc\u53db\u8fd9\u79cd\u670b\u53cb\u6240\u5f15\u8d77\u7684\u4e9b\u8bb8\u6127\u759a\uff0c\u6211\u60f3\u6211\u66f4\u5e0c\u671b\u770b\u5230\u6211\u559c\u6b22\u7684\u4eba\u83b7\u80dc\u5427\u3002\u201d</p><p>\u7528\u5e73\u9759\u4f46\u662f\u65a9\u9489\u622a\u94c1\u7684\u58f0\u97f3\uff0c\u6885\u83f2\u65af\u5982\u6b64\u8bf4\u3002</p></article>"}}}]);
