(window.webpackJsonp=window.webpackJsonp||[]).push([[1397],{1420:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516d\u8282 \u80dc\u5229",content:"<article><h2>\u7b2c\u4e8c\u5341\u516d\u8282 \u80dc\u5229</h2><p>\u4e09\u9897\u5b9d\u77f3\u540c\u65f6\u5c04\u51fa\uff0c\u5316\u4f5c\u4e09\u9897\u66f4\u52a0\u5de8\u5927\u7684\u706b\u7403\u4e00\u9f50\u7838\u6765\uff0c\u8fd9\u5c31\u662f\u5b9d\u77f3\u9b54\u6cd5\u7684\u4f18\u52bf\u4e4b\u4e00\uff0c\u53ef\u4ee5\u5c06\u591a\u4e2a\u6cd5\u672f\u540c\u65f6\u91ca\u653e\u3002\u6362\u4e86\u522b\u7684\u5deb\u5e08\uff0c\u518d\u5982\u4f55\u52aa\u529b\u4e5f\u6ca1\u6cd5\u505a\u5230\u8fd9\u70b9\u3002</p><p>\u743c\u6069\u907f\u5f00\u4e86\u4e00\u9897\u706b\u7403\uff0c\u88ab\u53e6\u5916\u4e24\u9897\u7838\u4e2d\uff0c\u7136\u540e\u53cd\u5f39\u4e86\u56de\u53bb\u3002\u51db\u8fd9\u6b21\u65e9\u6709\u51c6\u5907\uff0c\u501f\u52a9\u706b\u4e2d\u8df3\u8dc3\u77ac\u95f4\u907f\u5f00\uff0c\u8f6c\u79fb\u5230\u64c2\u53f0\u7684\u53e6\u5916\u4e00\u4e2a\u89d2\u843d\uff0c\u5f53\u5979\u4ece\u706b\u4e2d\u8d70\u51fa\u65f6\uff0c\u53d1\u73b0\u743c\u6069\u8eab\u4e0a\u8986\u76d6\u7684\u7ea2\u5149\u6d88\u5931\u4e86\uff0c\u968f\u5373\u770b\u89c1\u4ed6\u518d\u4e00\u6b21\u8bf5\u5492\u65bd\u6cd5\uff0c\u91cd\u65b0\u4e3a\u81ea\u5df1\u5e03\u4e0a\u4e86\u9632\u5fa1\u3002</p><p>\u524d\u540e\u56db\u9897\u706b\u7403\uff0c\u51fb\u7834\u4e86\u9006\u8f6c\u7206\u70b8\u7684\u9632\u5fa1\uff0c\u597d\u5728\u743c\u6069\u4e8b\u5148\u591a\u51c6\u5907\u4e86\u597d\u51e0\u4e2a\u6cd5\u672f\uff0c\u81ea\u4fe1\u5bf9\u8017\u4e0b\u53bb\u7edd\u5bf9\u4e0d\u4f1a\u5403\u4e8f\u3002\u5f53\u7136\uff0c\u524d\u63d0\u662f\u51db\u613f\u610f\u5bf9\u8017\u7684\u8bdd\u3002</p><p>\u663e\u7136\u51db\u4e0d\u613f\u610f\u3002</p><p>\u201c\u5514\u5514\uff0c\u539f\u6765\u4e0d\u662f\u6cd5\u672f\u9006\u8f6c\u5462\uff0c\u5413\u4e86\u6211\u4e00\u5927\u8df3\uff0c\u201d\u7ea2\u8863\u5c0f\u5973\u5deb\u8bc4\u4ef7\u7740\uff0c\u201c\u770b\u8d77\u6765\u4e0d\u8fc7\u662f\u4e2a\u8d5d\u54c1\u7f62\u4e86\u3002\u201d</p><p>\u201c\u4f46\u5bf9\u4f60\u8db3\u591f\u7528\u4e86\u3002\u201d\u743c\u6069\u7b80\u77ed\u5730\u56de\u7b54\uff0c\u5374\u5e76\u6ca1\u6709\u4e58\u673a\u53cd\u51fb\u3002</p><p>\u201c\u54fc\u54fc\u3002\u201d</p><p>\u51db\u51b7\u7b11\u7740\uff0c\u518d\u5ea6\u53d6\u51fa\u4e24\u9897\u5b9d\u77f3\uff0c\u5728\u534a\u7a7a\u4e2d\u70b8\u5f00\uff0c\u4e8e\u662f\u64c2\u53f0\u4e0a\u53c8\u591a\u4e86\u516b\u4e2a\u706b\u5806\uff0c\u5927\u90e8\u5206\u90fd\u843d\u5728\u743c\u6069\u8eab\u8fb9\u3002\u743c\u6069\u5e76\u6ca1\u6709\u95ea\u907f\uff0c\u800c\u662f\u9976\u6709\u5174\u81f4\u5730\u770b\u7740\u5bf9\u624b\u3002</p><p>\u201c\u8981\u6253\u4e00\u573a\u8fd1\u8eab\u6218\u5417\uff0c\u51db\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u90a3\u4e48\u5bb9\u6613\u5c31\u88ab\u4f60\u731c\u5230\u7684\u6beb\u65e0\u6280\u672f\u542b\u91cf\u7684\u4e8b\u60c5\uff0c\u6211\u624d\u4e0d\u5c51\u4e8e\u53bb\u505a\u5462\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u4e48\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u662f\u8981\u7528\u66f4\u6709\u6280\u672f\u542b\u91cf\u7684\u65b9\u6cd5\u6765\u51fa\u5947\u5236\u80dc\u5427\u3002\u201d</p><p>\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u4f60\u5c31\u7b49\u7740\u77a7\u5427\u3002\u201d</p><p>\u5b8c\u5168\u65e0\u89c6\u6570\u767e\u4f4d\u7ea2\u888d\u5deb\u5e08\u548c\u516b\u4e5d\u4f4d\u5927\u5deb\u5e08\u7684\u65c1\u89c2\uff0c\u4e24\u4f4d\u5deb\u5e08\u5c45\u7136\u5728\u53f0\u4e0a\u6597\u8d77\u5634\u6765\uff0c\u8bf4\u7740\u6beb\u65e0\u610f\u4e49\u7684\u5e9f\u8bdd\uff0c\u5374\u8c01\u90fd\u6ca1\u6709\u4e58\u673a\u8d76\u5feb\u65bd\u6cd5\u3002\u743c\u6069\u5e76\u4e0d\u6025\u4e8e\u8fdb\u653b\uff0c\u4ed6\u5728\u7b49\u5f85\u673a\u4f1a\uff0c\u81f3\u4e8e\u51db\uff0c\u5219\u4f3c\u4e4e\u662f\u88ab\u6b64\u524d\u7684\u5c40\u9762\u4e0d\u987a\u7ed9\u6fc0\u6012\u4e86\uff0c\u5728\u7b79\u5212\u7740\u4e00\u51fb\u5b9a\u80dc\u8d1f\u7684\u65b9\u6848\u3002</p><p>\u518d\u4e00\u6b21\u7684\uff0c\u51db\u53c8\u63b7\u51fa\u4e00\u9897\u5b9d\u77f3\uff0c\u5374\u4e0d\u662f\u5316\u4f5c\u706b\u7403\uff0c\u800c\u662f\u534a\u9014\u70b8\u88c2\u5f00\u6765\uff0c\u5316\u4f5c\u4e03\u516b\u652f\u706b\u7130\u7bad\u8fde\u73af\u5c04\u51fb\u3002\u51ed\u501f\u7740\u5bf9\u706b\u7130\u9b54\u6cd5\u4ee5\u53ca\u76f8\u5173\u653b\u51fb\u9632\u5fa1\u624b\u6bb5\u7684\u7cbe\u7814\uff0c\u5979\u654f\u9510\u5730\u770b\u51fa\u8fd9\u4e2a\u9006\u8f6c\u7206\u70b8\u6cd5\u672f\u7684\u7834\u7efd\uff1a\u548c\u201c\u9632\u62a4\u80fd\u91cf\u4f24\u5bb3\u201d\u7b49\u7c7b\u578b\u7684\u6cd5\u672f\u4e0d\u540c\uff0c\u5b83\u7684\u201c\u9650\u5ea6\u201d\u4e0d\u5728\u4e8e\u6240\u627f\u53d7\u7684\u6cd5\u672f\u5a01\u529b\u5927\u5c0f\uff0c\u800c\u5728\u4e8e\u6570\u91cf\u3002</p><p>\u6b64\u524d\u7528\u4e86\u56db\u4e2a\u5f3a\u5316\u8fc7\u7684\u706b\u7403\uff0c\u624d\u6210\u529f\u653b\u7834\u4e86\u9632\u5fa1\uff0c\u4f46\u8fd9\u6b21\u53ea\u8981\u7528\u4e00\u8f6e\u706b\u7130\u7bad\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u743c\u6069\u95ea\u907f\u7740\uff0c\u4f9d\u65e7\u6ca1\u6709\u53cd\u51fb\uff0c\u800c\u6b63\u5982\u51db\u6240\u6599\uff0c\u4ed6\u7684\u9632\u5fa1\u6cd5\u672f\u518d\u5ea6\u88ab\u51fb\u7834\u4e86\u3002\u4ed6\u53cc\u624b\u62ac\u8d77\uff0c\u6b63\u8981\u518d\u4e00\u6b21\u65bd\u6cd5\uff0c\u8fdc\u5904\u7684\u51db\u731b\u7136\u62ac\u8d77\u5de6\u624b\uff0c\u624b\u81c2\u4e0a\u6d6e\u8d77\u4e00\u56e2\u767d\u5149\uff0c\u6563\u6210\u4e03\u516b\u9053\u5149\u7bad\u5f80\u524d\u649e\u53bb\uff0c\u5374\u4e0d\u662f\u76f4\u63a5\u653b\u51fb\u743c\u6069\uff0c\u76ee\u6807\u662f\u4ed6\u8eab\u65c1\u7684\u51e0\u4e2a\u706b\u5806\u3002</p><p>\u8f6c\u77ac\u4e4b\u95f4\uff0c\u90a3\u51e0\u4e2a\u706b\u5806\u91cc\u7a81\u7136\u5192\u51fa\u817e\u817e\u9ed1\u70df\u6765\uff0c\u6eda\u6eda\u5982\u9f99\uff0c\u77ac\u95f4\u5c06\u5468\u56f4\u4e00\u7247\u90fd\u7b3c\u7f69\u4f4f\u4e86\uff0c\u743c\u6069\u63aa\u624b\u4e0d\u53ca\uff0c\u4e5f\u88ab\u5305\u88f9\u8fdb\u8fd9\u9ed1\u70df\u4e4b\u4e2d\uff0c\u5267\u70c8\u7684\u54b3\u55fd\u58f0\u7acb\u523b\u4ece\u4e2d\u4f20\u51fa\u6765\u3002\u201c\u4f60\u5e94\u8be5\u5f88\u719f\u6089\u8fd9\u4e2a\u6cd5\u672f\u5427\uff0c\u662f\u4f60\u7684\u4e13\u4e1a\u8303\u56f4\u5462\uff0c\u201d\u51db\u5f97\u610f\u6d0b\u6d0b\u5730\u8bf4\uff0c\u201c\u70df\u706b\u672f\uff0c\u4e0d\u8fc7\u6211\u505a\u4e86\u70b9\u5c0f\u5c0f\u7684\u6539\u826f\u3002\u201d</p><p>\u743c\u6069\u66fe\u7ecf\u7528\u8fc7\u8fd9\u4e2a\u6cd5\u672f\uff0c\u5728\u5bf9\u4ed8\u8389\u62c9\u516c\u7235\u7684\u65f6\u5019\uff0c\u4e3a\u4e86\u626d\u8f6c\u52a3\u52bf\uff0c\u7d22\u6027\u7528\u8fd9\u62db\u8ba9\u81ea\u5df1\u548c\u5bf9\u624b\u90fd\u53d8\u6210\u778e\u5b50\u3002\u4f46\u51db\u5982\u4eca\u7528\u7684\u663e\u7136\u662f\u6539\u826f\u7248\u672c\uff0c\u4e0d\u4f46\u6709\u9694\u65ad\u89c6\u89c9\u7684\u6548\u679c\uff0c\u751a\u81f3\u8fd8\u5e26\u4e86\u545b\u4eba\u7a92\u606f\u7684\u6548\u679c\u3002</p><p>\u5982\u6b64\u4e00\u6765\uff0c\u743c\u6069\u5c31\u4e0d\u80fd\u6d41\u7545\u5730\u8bf5\u5492\u4e86\u5427\u3002</p><p>\u51db\u7684\u8138\u4e0a\u91cd\u65b0\u6d6e\u8d77\u795e\u6c14\u7684\u7b11\u5bb9\u6765\uff0c\u6307\u5c16\u5df2\u7ecf\u7a33\u7a33\u5939\u4e0a\u4e86\u4e00\u679a\u7c89\u7ea2\u8272\u692d\u5706\u5f62\u5b9d\u77f3\uff0c\u5374\u5e76\u6ca1\u6709\u7acb\u523b\u5c04\u51fa\u53bb\u3002\u6d53\u70df\u867d\u7136\u7b3c\u7f69\u4f4f\u4e86\u743c\u6069\uff0c\u8ba9\u4ed6\u770b\u4e0d\u89c1\u5916\u9762\u7684\u60c5\u5f62\uff0c\u540c\u6837\u4e5f\u8ba9\u51db\u6ca1\u6cd5\u770b\u5230\u4ed6\u7684\u4f4d\u7f6e\u4e86\u3002\u4e0d\u8fc7\u8fd9\u6ca1\u5173\u7cfb\uff0c\u8eab\u5904\u545b\u4eba\u6d53\u70df\u4e4b\u4e2d\uff0c\u522b\u8bf4\u4e0d\u80fd\u8bf5\u5492\u65bd\u6cd5\uff0c\u5c31\u662f\u547c\u5438\u90fd\u56f0\u96be\uff0c\u743c\u6069\u4e0d\u53ef\u80fd\u575a\u6301\u5446\u5728\u91cc\u9762\uff0c\u4e00\u5b9a\u4f1a\u51b2\u51fa\u6765\u3002</p><p>\u5728\u4ed6\u51b2\u51fa\u6765\u7684\u65f6\u5019\uff0c\u7ed9\u4e88\u8fce\u5934\u75db\u51fb\u3002\u4ed6\u6ca1\u6709\u5145\u8db3\u7684\u65bd\u6cd5\u8bf5\u5492\u65f6\u95f4\uff0c\u9632\u5fa1\u6cd5\u672f\u65e0\u6cd5\u5f62\u6210\uff0c\u4e00\u5b9a\u4f1a\u88ab\u4e00\u51fb\u8f70\u5012\u7684\u3002</p><p>\u201c\u4e0d\u8fc7\uff0c\u770b\u5728\u827e\u5f25\u8587\u7684\u4efd\u4e0a\uff0c\u7a0d\u7a0d\u624b\u4e0b\u7559\u60c5\u70b9\u5c31\u662f\u4e86\u3002\u201d\u51db\u5982\u6b64\u60f3\uff0c\u76ef\u7740\u90a3\u56e2\u6eda\u6eda\u6d53\u70df\u3002</p><p>\u4e00\u5207\u5982\u5979\u6240\u6599\uff0c\u5939\u6742\u7740\u4e00\u8eab\u7684\u70df\u5c18\uff0c\u743c\u6069\u731b\u51b2\u4e86\u51fa\u6765\u3002</p><p>\u5c31\u662f\u73b0\u5728\uff01</p><p>\u51db\u4e0d\u5047\u601d\u7d22\u5730\u6296\u624b\uff0c\u5c06\u5b9d\u77f3\u7838\u4e86\u51fa\u53bb\u3002\u548c\u5979\u5e73\u65f6\u6240\u53d1\u51fa\u7684\u58f0\u52bf\u714a\u8d6b\u7684\u706b\u7403\u4e0d\u540c\uff0c\u8fd9\u679a\u5b9d\u77f3\u7684\u5a01\u529b\u8981\u5c0f\u5f88\u591a\u3002\u5c31\u7b97\u88ab\u51fb\u4e2d\uff0c\u743c\u6069\u4e5f\u4e0d\u81f3\u4e8e\u5f53\u573a\u6bd9\u547d\uff0c\u4e8b\u540e\u9001\u5230\u795e\u6bbf\u91cc\u627e\u8db3\u591f\u5f3a\u529b\u7684\u7267\u5e08\u6cbb\u7597\u5c31\u662f\u3002</p><p>\u7136\u800c\u5979\u7684\u9884\u6599\u843d\u7a7a\u4e86\u3002</p><p>\u743c\u6069\u51b2\u51fa\u6d53\u70df\uff0c\u65e2\u6ca1\u6709\u505c\u4e0b\u6765\u89c2\u5bdf\u5c40\u52bf\uff0c\u4e5f\u6ca1\u6709\u7acb\u523b\u65bd\u6cd5\u8bf5\u5492\uff0c\u800c\u662f\u76f4\u76f4\u671d\u51db\u51b2\u6765\u3002\u51db\u7684\u5b9d\u77f3\u5316\u4f5c\u706b\u7403\u7cbe\u786e\u65e0\u8bef\u5730\u7838\u5728\u4ed6\u8eab\u4e0a\uff0c\u7136\u540e\u2014\u2014</p><p>\u4ee5\u66f4\u5feb\u7684\u901f\u5ea6\u53cd\u5f39\u56de\u6765\u3002</p><p>\u51fa\u4e4e\u610f\u6599\u7684\u7ed3\u679c\uff0c\u8ba9\u51db\u5403\u4e86\u4e00\u60ca\uff0c\u6bd4\u8d77\u4e00\u5f00\u573a\u7684\u90a3\u6b21\uff0c\u8fd9\u6b21\u4e24\u4eba\u7684\u8ddd\u79bb\u66f4\u8fd1\uff0c\u53cd\u5e94\u65f6\u95f4\u66f4\u77ed\u3002\u597d\u5728\u603b\u7b97\u662f\u5df2\u7ecf\u6709\u8fc7\u4e00\u6b21\u7ecf\u9a8c\uff0c\u51db\u867d\u60ca\u4e0d\u4e71\uff0c\u672c\u80fd\u5730\u6a2a\u8de8\u4e00\u6b65\u5c31\u60f3\u8eb2\u5230\u65c1\u8fb9\u7684\u706b\u5806\u91cc\u53bb\u3002</p><p>\u7136\u800c\u2026\u2026</p><p>\u201c\u54c7\uff01\u201d</p><p>\u7eb5\u7136\u662f\u88ab\u81ea\u5df1\u7684\u706b\u7403\u51fb\u4e2d\uff0c\u51db\u4e5f\u4e00\u76f4\u4fdd\u6301\u7740\u4ece\u5bb9\u4e0d\u8feb\u7684\u6c14\u5ea6\uff0c\u7136\u800c\u6b64\u65f6\u6b64\u523b\uff0c\u8fd9\u4f4d\u5deb\u5e08\u5374\u50cf\u4e2a\u53d7\u60ca\u7684\u5c0f\u5b69\u5b50\u4e00\u6837\u8df3\u8d77\u6765\uff0c\u4ed3\u7687\u540e\u9000\u3002\u56e0\u4e3a\u5979\u51c6\u5907\u8e0f\u5165\u7684\u90a3\u56e2\u706b\u7130\uff0c\u4e0d\u77e5\u4f55\u65f6\u706b\u5149\u5347\u817e\uff0c\u626d\u66f2\u53d8\u5e7b\u7740\uff0c\u5851\u6210\u4e00\u5f20\u5de8\u5927\u7684\uff0c\u5fae\u7b11\u7740\u7684\u72d0\u72f8\u8138\u6765\u3002</p><p>\u201c\u5e7b\u672f\u7f62\u4e86\uff01\u201d\u5979\u7acb\u523b\u9547\u5b9a\u4e0b\u6765\u3002</p><p>\u521a\u624d\u867d\u7136\u88ab\u5413\u9000\uff0c\u6ca1\u80fd\u5b8c\u6210\u706b\u4e2d\u8df3\u8dc3\uff0c\u5374\u4e5f\u6070\u597d\u907f\u8fc7\u4e86\u53cd\u5f39\u7684\u706b\u7403\u3002\u867d\u7136\u4e0d\u660e\u767d\u743c\u6069\u4f55\u65f6\u5c45\u7136\u53c8\u5b8c\u6210\u4e86\u9632\u5fa1\u6cd5\u672f\uff0c\u4f46\u8fd9\u4e5f\u65e0\u5173\u7d27\u8981\uff0c\u53ea\u4f1a\u6328\u6253\u662f\u4e0d\u80fd\u53d6\u5f97\u80dc\u5229\u7684\uff0c\u4e3b\u52a8\u6743\u4f9d\u65e7\u638c\u63e1\u5728\u81ea\u5df1\u4e00\u8fb9\u3002</p><p>\u5979\u6325\u4e86\u6325\u624b\uff0c\u751a\u81f3\u8fde\u65bd\u6cd5\u90fd\u4e0d\u9700\u8981\uff0c\u5c31\u76f4\u63a5\u62b9\u53bb\u4e86\u706b\u7130\u4e0a\u7684\u72d0\u72f8\u5e7b\u8c61\u3002\u5979\u5728\u706b\u7130\u9b54\u6cd5\u4e0a\u7684\u9020\u8be3\u592a\u6df1\uff0c\u8fd9\u706b\u5806\u53c8\u662f\u5979\u6240\u521b\u9020\uff0c\u8f7b\u800c\u6613\u4e3e\u5c31\u7834\u89e3\u4e86\u743c\u6069\u501f\u52a9\u706b\u7130\u5f04\u51fa\u7684\u5e7b\u672f\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u7ec8\u7a76\u5fc3\u6709\u4f59\u60b8\uff0c\u51db\u4e0d\u6562\u518d\u7528\u706b\u4e2d\u8df3\u8dc3\uff0c\u5979\u9000\u540e\u4e24\u6b65\uff0c\u624b\u5df2\u7ecf\u518d\u5ea6\u4f38\u8fdb\u4e86\u8170\u95f4\u53e3\u888b\uff0c\u51c6\u5907\u91ca\u653e\u5979\u6240\u51c6\u5907\u7684\u6700\u5f3a\u529b\u6cd5\u672f\u3002\u5979\u5f88\u6709\u4fe1\u5fc3\uff0c\u5c31\u7b97\u8fd9\u4e2a\u6cd5\u672f\u4e0d\u6210\u529f\uff0c\u88ab\u743c\u6069\u51b2\u8fc7\u6765\u4e5f\u65e0\u6240\u8c13\uff0c\u6b63\u597d\u8ba9\u4ed6\u9886\u6559\u4e00\u4e0b\u81ea\u5df1\u7684\u62f3\u811a\u529f\u592b\u3002</p><p>\u4f46\u5979\u6ca1\u6709\u673a\u4f1a\u3002</p><p>\u811a\u6b65\u521a\u521a\u7ad9\u7a33\uff0c\u773c\u524d\u7684\u7a7a\u6c14\u4e2d\u9661\u7136\u8dc3\u51fa\u4e00\u56e2\u9ed1\u5f71\uff0c\u671d\u51db\u731b\u6251\u8fc7\u6765\u3002\u8ddd\u79bb\u5982\u6b64\u4e4b\u8fd1\uff0c\u51db\u4e0d\u5047\u601d\u7d22\u5730\u6325\u62f3\uff0c\u540c\u65f6\u5979\u4e5f\u770b\u6e05\u695a\u4e86\u90a3\u4e1c\u897f\uff0c\u4e0d\u8fc7\u662f\u4e00\u53ea\u5c0f\u578b\u7684\u70bc\u72f1\u51f6\u66b4\u72ac\u7f62\u4e86\uff0c\u975e\u5e38\u4f4e\u7ea7\u7684\u53ec\u5524\u602a\u7269\u3002</p><p>\u201c\u4ec0\u4e48\u65f6\u5019\u5c45\u7136\u5077\u5077\u53ec\u5524\u4e86\u4e00\u53ea\u602a\u7269\uff0c\u53c8\u7528\u9690\u5f62\u6cd5\u672f\u8ba9\u5b83\u9760\u8fd1\u81ea\u5df1\uff0c\u60f3\u8981\u641e\u7a81\u7136\u88ad\u51fb\u4e48\uff1f\u201d\u51db\u7684\u5634\u89d2\u6d6e\u51fa\u5632\u8bbd\u7684\u51b7\u7b11\u6765\uff0c\u201c\u4e3b\u610f\u5012\u662f\u6253\u5f97\u4e0d\u9519\uff0c\u53ef\u60dc\u554a\uff0c\u6211\u53ef\u4e0d\u662f\u90a3\u79cd\u5f31\u4e0d\u7981\u98ce\u7684\u5deb\u5e08\u5462\u3002\u201d</p><p>\u53ea\u8981\u4e00\u62f3\uff0c\u5c31\u80fd\u628a\u8fd9\u53ea\u5c0f\u70bc\u72f1\u51f6\u66b4\u72ac\u6253\u98de\u51fa\u53bb\uff0c\u51db\u6709\u8fd9\u79cd\u81ea\u4fe1\u3002</p><p>\u201c\u53d8\u5f62\uff01\u201d</p><p>\u4f4e\u58f0\u7684\u9f99\u8bed\u4f4e\u559d\u4ece\u743c\u6069\u53e3\u4e2d\u5410\u51fa\uff0c\u968f\u7740\u5492\u8bed\u7684\u5b8c\u6210\uff0c\u90a3\u53ea\u70bc\u72f1\u51f6\u66b4\u72ac\u9aa4\u7136\u53d8\u6210\u4e86\u4e00\u53ea\u5168\u8eab\u706b\u7ea2\u8272\u7684\u5c0f\u72d0\u72f8\u3002\u201c\u55f7\uff01\u201d\u5b83\u53d1\u51fa\u7c7b\u4f3c\u5c0f\u72d7\u4e00\u6837\u7684\u53eb\u58f0\uff0c\u5728\u7a7a\u4e2d\u7075\u5de7\u5730\u5c48\u8eab\uff0c\u4ee5\u65e0\u6bd4\u53ef\u7231\u7684\u59ff\u52bf\u671d\u51db\u7684\u6000\u91cc\u6251\u8fc7\u6765\u3002</p><p>\u8fd9\u6837\u6f02\u4eae\u7684\u5c0f\u72d0\u72f8\uff0c\u6216\u8bb8\u5f88\u591a\u5973\u5b69\u5b50\u4f1a\u4e24\u773c\u653e\u5149\u5730\u4e00\u628a\u62b1\u8fc7\uff0c\u4f46\u51db\u663e\u7136\u4e0d\u5728\u6b64\u7c7b\u3002\u201c\u554a\u554a\u554a\u554a\uff01\u201d\u5979\u53d1\u51fa\u60ca\u60f6\u5931\u63aa\u7684\u53eb\u58f0\uff0c\u5c31\u50cf\u770b\u5230\u4e00\u53ea\u6df1\u72f1\u6076\u9b3c\u4f3c\u7684\uff0c\u4ed3\u7687\u540e\u9000\u3002</p><p>\u7136\u800c\u5979\u672c\u6765\u5c31\u5df2\u7ecf\u5728\u64c2\u53f0\u8fb9\uff0c\u5982\u4eca\u518d\u9000\u51fa\u4e24\u6b65\uff0c\u811a\u8ddf\u5df2\u7ecf\u60ac\u7a7a\u5728\u5916\uff0c\u9669\u4e9b\u5c31\u8981\u6389\u4e0b\u53bb\u3002\u521a\u521a\u610f\u8bc6\u5230\u8fd9\u4e00\u70b9\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u505a\u51fa\u53cd\u5e94\uff0c\u5c0f\u72d0\u72f8\u5df2\u7ecf\u6251\u5230\u8ddf\u524d\u3002</p><p>\u51db\u4ee5\u4e0d\u53ef\u601d\u8bae\u7684\u7075\u5de7\u52a8\u4f5c\u4fa7\u8eab\uff0c\u907f\u5f00\u4e86\u72d0\u72f8\uff0c\u4f46\u5979\u7684\u8eab\u4f53\u5931\u53bb\u4e86\u5e73\u8861\u3002\u743c\u6069\u8fdc\u8fdc\u7ad9\u5b9a\uff0c\u4f38\u624b\u865a\u865a\u4e00\u6309\uff0c\u9694\u7740\u7a7a\u6c14\u671d\u5979\u8f7b\u8f7b\u4e00\u63a8\u3002</p><p>\u8fd9\u662f\u5deb\u5e08\u4e4b\u624b\uff0c\u975e\u5e38\u7b80\u5355\u7684\u4e00\u79cd\u5c0f\u620f\u6cd5\uff0c\u53ea\u80fd\u63a8\u52a8\u5206\u91cf\u5f88\u8f7b\u7684\u4e1c\u897f\u3002\u5018\u82e5\u5728\u5e73\u65f6\uff0c\u5b83\u662f\u7edd\u5bf9\u63a8\u4e0d\u52a8\u4e00\u4e2a\u4eba\u7684\uff0c\u7136\u800c\u5982\u4eca\u51db\u6b63\u5904\u4e8e\u6447\u6447\u6b32\u5760\u7684\u72b6\u6001\uff0c\u6b63\u5982\u9a86\u9a7c\u88ab\u538b\u57ae\u53ea\u9700\u8981\u6700\u540e\u4e00\u6839\u7a3b\u8349\u3002</p><p>\u6240\u4ee5\u5979\u5c31\u6454\u4e86\u4e0b\u53bb\u2026\u2026</p><p>\u4e0d\uff0c\u8fd8\u6ca1\u6709\u6454\u4e0b\u53bb\u3002</p><p>\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u5979\u7684\u80cc\u540e\u6cdb\u8d77\u4e00\u5c42\u5fae\u5149\uff0c\u968f\u5373\u6574\u4e2a\u4eba\u505c\u6b62\u4e86\u4e0b\u5760\uff0c\u60ac\u6d6e\u5728\u7a7a\u4e2d\uff0c\u63a5\u7740\u6162\u6162\u76f4\u8d77\u8eab\u4f53\uff0c\u51cc\u7a7a\u8e0f\u6b65\uff0c\u773c\u770b\u5c31\u8981\u8d70\u56de\u64c2\u53f0\u3002\u201c\u98de\u884c\u672f\u201d\uff0c\u743c\u6069\u7acb\u523b\u5224\u65ad\u51fa\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5f88\u663e\u7136\uff0c\u9664\u4e86\u5de6\u624b\u624b\u81c2\u4e0a\u7684\u523a\u9752\u5916\uff0c\u7b2c\u4e8c\u4e2a\u523a\u9752\u5728\u80cc\u4e0a\uff0c\u50a8\u5b58\u4e86\u4e00\u4e2a\u98de\u884c\u672f\uff0c\u5173\u952e\u65f6\u523b\u6fc0\u53d1\u51fa\u6765\u3002</p><p>\u867d\u7136\u51fa\u4e4e\u610f\u6599\uff0c\u4f46\u4ed6\u4e5f\u5e76\u975e\u6ca1\u6709\u5e94\u5bf9\u7684\u51c6\u5907\u3002\u5feb\u901f\u4ece\u957f\u888d\u5185\u4fa7\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u9897\u7a7a\u8327\u6765\u634f\u788e\uff0c\u201c\u53d8\u5f62\uff01\u201d\u4ed6\u5bf9\u7740\u90a3\u53ea\u521a\u521a\u6251\u4e86\u4e2a\u7a7a\uff0c\u6b63\u51c6\u5907\u518d\u6b21\u8dc3\u8d77\u7684\u5c0f\u72d0\u72f8\u4e00\u6307\u3002</p><p>\u72d0\u72f8\u7684\u4f53\u578b\u9661\u7136\u6da8\u5927\uff0c\u53d8\u6210\u4e00\u53ea\u5de8\u5927\u5982\u725b\u728a\u7684\u9ed1\u72d7\uff0c\u5b83\u8dc3\u8d77\u7684\u901f\u5ea6\u4e1d\u6beb\u4e0d\u51cf\uff0c\u50cf\u4e00\u53d1\u70ae\u5f39\u4f3c\u7684\u649e\u4e0a\u4e86\u5373\u5c06\u8981\u8e0f\u4e0a\u64c2\u53f0\u7684\u51db\u3002\u8eab\u5728\u865a\u7a7a\uff0c\u5b8c\u5168\u9760\u98de\u884c\u672f\u7ef4\u6301\u7740\u8eab\u4f53\u5e73\u8861\uff0c\u51db\u7684\u8eab\u624b\u65e0\u6cd5\u4fdd\u6301\u50cf\u811a\u8e0f\u5b9e\u5730\u65f6\u90a3\u6837\u7075\u654f\uff0c\u538b\u6839\u65e0\u4ece\u95ea\u907f\u3002\u51e0\u4e4e\u662f\u6beb\u65e0\u60ac\u5ff5\u5730\uff0c\u5979\u88ab\u6251\u5012\u5728\u5730\uff0c\u5c31\u7b97\u662f\u98de\u884c\u672f\u4e5f\u627f\u8f7d\u4e0d\u4e86\u8fd9\u989d\u5916\u7684\u5206\u91cf\u3002</p><p>\u5f53\u5979\u72fc\u72c8\u5730\u4e00\u62f3\u5c06\u9ed1\u72d7\u7838\u98de\uff0c\u722c\u8d77\u8eab\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u5728\u64c2\u53f0\u4e4b\u4e0b\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u64c2\u53f0\u8d5b\u7684\u89c4\u77e9\uff0c\u6454\u51fa\u573a\u5916\u5c31\u662f\u8f93\u3002\u51db\u867d\u7136\u4e1d\u6beb\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u786e\u786e\u5b9e\u5b9e\u662f\u5df2\u7ecf\u88ab\u4ece\u64c2\u53f0\u4e0a\u6253\u4e86\u4e0b\u6765\uff0c\u6240\u4ee5\u5979\u8f93\u4e86\u3002</p><p>\u8fd9\u6837\u7684\u5931\u8d25\uff0c\u76f8\u4fe1\u8c01\u4e5f\u4e0d\u4f1a\u7518\u5fc3\uff0c\u6240\u4ee5\u5f88\u7406\u6240\u5f53\u7136\u7684\uff0c\u5f53\u743c\u6069\u8d70\u4e0b\u64c2\u53f0\u7684\u65f6\u5019\uff0c\u542c\u89c1\u4e86\u5bf9\u624b\u7684\u62b1\u6028\u3002</p><p>\u201c\u5982\u679c\u4e0d\u662f\u827e\u5f25\u8587\u90a3\u5bb6\u4f19\u544a\u8bc9\u4f60\u6211\u7684\u79d8\u5bc6\uff0c\u4f60\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u6709\u800d\u82b1\u62db\u7684\u673a\u4f1a\uff01\u201d</p><p>\u88f8\u9732\u7740\u534a\u622a\u83b9\u6da6\u5982\u7389\u7684\u5de6\u81c2\uff0c\u51db\u6c14\u9f13\u9f13\u5730\u6307\u7740\u743c\u6069\u8bf4\u3002</p><p>\u201c\u54e6\uff0c\u539f\u6765\u80dc\u5229\u4e86\u5c31\u662f\u7406\u6240\u5f53\u7136\uff0c\u5931\u8d25\u4e86\u5c31\u8981\u5411\u4ed6\u4eba\u63a8\u5378\u8d23\u4efb\uff0c\u8fd9\u8fd8\u771f\u662f\u5927\u5c0f\u59d0\u7684\u505a\u6d3e\u554a\u3002\u201d</p><p>\u743c\u6069\u5632\u8bbd\u7740\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u81ea\u5df1\u90fd\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u5728\u6b64\u4e4b\u524d\u4ed6\u4ece\u6ca1\u53d1\u73b0\u81ea\u5df1\u6709\u548c\u5973\u5b69\u5b50\u6597\u5634\u7684\u4e60\u60ef\u548c\u7231\u597d\uff0c\u7136\u800c\u81ea\u4ece\u9047\u5230\u8fd9\u4f4d\u51db\u5c0f\u59d0\uff0c\u83ab\u540d\u5176\u5999\u5730\u5c31\u591a\u4e86\u8fd9\u9879\u80fd\u529b\u3002\u5f88\u591a\u539f\u672c\u4e0d\u4f1a\u8bf4\u7684\u8bdd\u90fd\u6d41\u7545\u81ea\u7136\u5730\u8bf4\u4e86\u51fa\u6765\uff0c\u4eff\u4f5b\u9762\u5bf9\u7684\u662f\u4e2a\u547d\u4e2d\u6ce8\u5b9a\u7684\u51a4\u5bb6\u5bf9\u5934\u4f3c\u7684\u3002</p><p>\u5514\uff0c\u6ca1\u9519\uff0c\u5c31\u662f\u8fd9\u79cd\u611f\u89c9\u2026\u2026\u5982\u679c\u4e0d\u6070\u5f53\u5730\u5f62\u5bb9\u7684\u8bdd\uff0c\u5c31\u662f\u770b\u89c1\u5979\u5c31\u6709\u6c14\uff0c\u5c31\u5f88\u60f3\u53bb\u75db\u75db\u5feb\u5feb\u5730\u6559\u8bad\u5979\u4e00\u987f\u5427\u3002\u540c\u6837\u90fd\u662f\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u662f\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u4e3a\u4ec0\u4e48\u59d0\u59d0\u73ca\u5609\u5c31\u90a3\u4e48\u6e29\u5a49\uff0c\u6885\u83f2\u65af\u5c31\u90a3\u4e48\u6c89\u7740\u7406\u667a\uff0c\u5c31\u8fde\u90a3\u4f4d\u5438\u8840\u9b3c\u8389\u6cd5\u513f\u90fd\u79f0\u5f97\u4e0a\u662f\u4e56\u5de7\uff0c\u504f\u504f\u8fd9\u4f4d\u5927\u5c0f\u59d0\u5c31\u8fd9\u6837\u8ba9\u4eba\u5934\u75bc\u5462\u3002</p><p>\u201c\u6211\u54ea\u91cc\u5931\u8d25\u4e86\uff1f\u5982\u679c\u4e0d\u662f\u8fd9\u8be5\u6b7b\u7684\u64c2\u53f0\u8d5b\u89c4\u77e9\uff0c\u6211\u73b0\u5728\u65e9\u5c31\u5df2\u7ecf\u628a\u4f60\u6253\u5f97\u8db4\u5728\u5730\u4e0a\u6c42\u9976\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u9996\u5148\uff0c\u5973\u5b69\u5b50\u4e0d\u8981\u8bf4\u90a3\u4e48\u91ce\u86ee\u7684\u8bdd\uff0c\u8fd9\u6837\u5b50\u4ee5\u540e\u5ac1\u4e0d\u51fa\u53bb\uff1b\u5176\u6b21\uff0c\u5982\u679c\u4e0d\u662f\u8fd9\u79cd\u64c2\u53f0\u8d5b\u7684\u5f62\u5f0f\uff0c\u4f60\u7684\u5851\u80fd\u9b54\u6cd5\u4e5f\u6ca1\u529e\u6cd5\u8fd9\u4e48\u98ce\u5149\u5427\u3002\u540c\u6837\u4e00\u5957\u89c4\u5219\uff0c\u5728\u5bf9\u81ea\u5df1\u6709\u5229\u7684\u65f6\u5019\u5c31\u6d0b\u6d0b\u5f97\u610f\uff0c\u5728\u5bf9\u81ea\u5df1\u4e0d\u5229\u7684\u65f6\u5019\u5c31\u62b1\u6028\u6765\u62b1\u6028\u53bb\uff0c\u8fd9\u662f\u4e00\u4e2a\u5deb\u5e08\u5e94\u8be5\u6709\u7684\u7d20\u8d28\u5417\uff1f\u201d</p><p>\u4f5c\u4e3a\u80dc\u5229\u8005\uff0c\u743c\u6069\u51b7\u9759\u5730\u53cd\u9a73\u7740\u5bf9\u65b9\u7684\u8bdd\uff0c\u867d\u7136\u6709\u4e9b\u5f3a\u8bcd\u593a\u7406\u7684\u6210\u5206\uff0c\u800c\u8fd9\u573a\u6bd4\u8d5b\u4e5f\u786e\u5b9e\u80dc\u5f97\u4e0d\u591f\u5e72\u51c0\u5229\u843d\u3002\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u6309\u7167\u89c4\u5219\u5c06\u51db\u6253\u4e0b\u4e86\u64c2\u53f0\uff0c\u8fd9\u662f\u5168\u573a\u6570\u767e\u4eba\u6240\u5171\u89c1\u7684\u4e8b\u5b9e\uff0c\u6ca1\u6709\u8c01\u53ef\u4ee5\u5426\u8ba4\u3002\u800c\u4e14\u4ed6\u8fd0\u7528\u7684\u4e5f\u786e\u5b9e\u662f\u5404\u79cd\u9b54\u6cd5\u6280\u5de7\uff0c\u65e0\u8bba\u53ec\u5524\u3001\u9690\u5f62\u3001\u5e7b\u672f\u3001\u620f\u6cd5\u8fd8\u662f\u6700\u540e\u7684\u53d8\u5f62\uff0c\u90fd\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u65e0\u6127\u4e8e\u5deb\u5e08\u51b3\u6597\u7684\u540d\u58f0\u3002</p><p>\u201c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u770b\u5728\u827e\u5f25\u8587\u7684\u4efd\u4e0a\uff0c\u4e00\u5f00\u59cb\u5bf9\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u6211\u65e9\u5c31\u7528\u706b\u5899\u628a\u4f60\u70e7\u6210\u7070\u70ec\u4e86\u3002\u201d</p><p>\u201c\u540c\u6837\u554a\uff0c\u5c0f\u59d0\uff0c\u5982\u679c\u4e0d\u662f\u8003\u8651\u5230\u66ff\u4f60\u4fdd\u6301\u5f62\u8c61\uff0c\u6211\u7684\u53d8\u5f62\u672f\u5c31\u4e0d\u4f1a\u7528\u5728\u53ec\u5524\u751f\u7269\u4e0a\uff0c\u76f4\u63a5\u628a\u4f60\u53d8\u6210\u4e00\u53ea\u677e\u9f20\u7136\u540e\u8e22\u4e0b\u53bb\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4ee5\u4e3a\u4f60\u7684\u53d8\u5f62\u672f\u5bf9\u6211\u4f1a\u6709\u6548\u5417\uff1f\u201d</p><p>\u201c\u6211\u662f\u7cbe\u7814\u53d8\u5316\u7684\u5deb\u5e08\uff0c\u8fd9\u53ef\u662f\u6211\u7684\u4e13\u4e1a\u9886\u57df\u554a\uff0c\u867d\u7136\u4e0d\u6562\u8bf4\u5341\u62ff\u4e5d\u7a33\uff0c\u4f46\u591a\u8bd5\u4e24\u6b21\u603b\u8be5\u6ca1\u95ee\u9898\u5427\u3002\u76f8\u53cd\uff0c\u636e\u6211\u6240\u77e5\uff0c\u706b\u5899\u672f\u9700\u8981\u5deb\u5e08\u5168\u795e\u8d2f\u6ce8\u5730\u7ef4\u6301\uff0c\u800c\u4e14\u8def\u5f84\u56fa\u5b9a\uff0c\u4e5f\u5f88\u5bb9\u6613\u88ab\u8eb2\u5f00\u7684\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6700\u7ec8\u6253\u65ad\u4e24\u4f4d\u5deb\u5e08\u6beb\u65e0\u6280\u672f\u542b\u91cf\u7684\u4e89\u5435\u7684\uff0c\u662f\u7ea2\u888d\u5deb\u5e08\u4f1a\u4ea1\u7075\u5b66\u6d3e\u9996\u5e2d\u8428\u624e\u65af\u5766\u5148\u751f\u3002\u201c\u5f88\u6709\u8da3\u7684\u6280\u5de7\u8fd0\u7528\uff0c\u201d\u4ed6\u5938\u5956\u7740\uff0c\u4f46\u8fd9\u663e\u7136\u5e76\u4e0d\u662f\u5bf9\u9b54\u6cd5\u9020\u8be3\u6216\u8005\u4ec0\u4e48\u7684\u505a\u51fa\u80af\u5b9a\uff0c\u6216\u8005\u8bf4\uff0c\u907f\u800c\u4e0d\u8c08\uff0c\u201c\u90a3\u4e48\uff0c\u6bbf\u4e0b\uff0c\u201d\u4ed6\u770b\u7740\u65c1\u8fb9\u7684\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u201c\u4f60\u6709\u4e00\u4e2a\u5f88\u806a\u660e\u7684\u526f\u624b\u5462\u3002\u4eca\u5929\u8fd9\u573a\u6bd4\u8d5b\u2026\u2026\u201d</p><p>\u201c\u6211\u60f3\u79f0\u4e3a\u6e38\u620f\u66f4\u5408\u9002\u5427\uff0c\u201d\u738b\u5b50\u5fae\u7b11\u7740\uff0c\u5ba2\u6c14\u5730\u6253\u65ad\u4e86\u7ea2\u888d\u9996\u5e2d\u7684\u8bdd\uff0c\u201c\u65e2\u7136\u53ea\u662f\u6e38\u620f\uff0c\u4e5f\u5c31\u4e0d\u7528\u5206\u4ec0\u4e48\u80dc\u8d1f\u4e86\u3002\u201d</p><p>\u5728\u5ba2\u6c14\u7684\u8c26\u900a\u4e2d\uff0c\u8fd9\u573a\u4e8b\u5148\u5f04\u5f97\u5927\u5f20\u65d7\u9f13\u7684\u6bd4\u8d5b\u5c31\u6b64\u7ed3\u675f\u3002\u51db\u9887\u4e3a\u4e0d\u6ee1\u5730\u62c9\u7740\u6885\u83f2\u65af\u53bb\u7b97\u8d26\uff0c\u743c\u6069\u5bf9\u5979\u4eec\u7684\u4ea4\u6d89\u6ca1\u6709\u5174\u8da3\uff0c\u53cd\u6b63\u6885\u83f2\u65af\u5e94\u8be5\u4e0d\u4f1a\u5403\u4e8f\u5c31\u662f\u3002\u4ed6\u8ddf\u968f\u738b\u5b50\u56de\u5230\u4f4f\u5904\uff0c\u8fdb\u4e86\u623f\u95f4\uff0c\u201c\u5e72\u5f97\u4e0d\u9519\uff0c\u201d\u738b\u5b50\u5938\u5956\u8bf4\uff0c\u201c\u6bd4\u6211\u9884\u60f3\u5f97\u8981\u597d\u3002\u201d</p><p>\u201c\u4fa5\u5e78\u7f62\u4e86\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u738b\u5b50\u5fae\u7b11\uff0c\u5728\u6905\u5b50\u4e0a\u5750\u4e0b\u6765\uff0c\u4ed6\u7684\u795e\u8272\u7a81\u7136\u53d8\u5f97\u4e25\u8083\u8d77\u6765\u3002\u743c\u6069\u95ed\u7740\u5634\uff0c\u9759\u9759\u5750\u7740\uff0c\u4ed6\u77e5\u9053\u8be5\u5230\u8bf4\u6b63\u4e8b\u7684\u65f6\u5019\u4e86\u3002</p></article>"}}}]);
