(window.webpackJsonp=window.webpackJsonp||[]).push([[14],{37:function(p){p.exports={chapterTitle:null,title:"\u7b2c14\u7ae0 \u5dee\u70b9\u65e0\u6cd5\u51fa\u5934\u7684\u6e14\u7fc1",content:"<article><h2>\u7b2c14\u7ae0 \u5dee\u70b9\u65e0\u6cd5\u51fa\u5934\u7684\u6e14\u7fc1</h2><p>\u3000\u3000\u5f20\u77e5\u884c\u4ece\u66fe\u5efa\u534e\u59d0\u59d0\u5634\u91cc\u77e5\u9053\u66fe\u5efa\u534e\u5377\u6b3e\u5916\u9003\u8d77\uff0c\u5c31\u77e5\u9053\u4e8b\u60c5\u4e0d\u5927\u5999\uff0c\u4f46\u662f\u90a3\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u6839\u672c\u60f3\u4e0d\u900f\u5f7b\uff0c\u4e5f\u6765\u4e0d\u53ca\u60f3\u4ec0\u4e48\u5bf9\u7b56\uff0c\u51e0\u4e4e\u88ab\u52a8\u7684\u987a\u7740\u513f\u5b50\u5f20\u606a\u62c9\u7740\u8d70\uff0c\u4e0b\u697c\u662f\u8fd9\u6837\uff0c\u9047\u5230\u697c\u4e0b\u7684\u4fbf\u8863\u4e5f\u662f\u8fd9\u6837\uff0c\u79bb\u5f00\u4fbf\u8863\u7684\u89c6\u7ebf\uff0c\u5927\u6b65\u6e9c\u8fdb\u8f66\u91cc\u4e5f\u8fd9\u6837\uff0c\u8fd9\u65f6\u5019\u8fd8\u60ca\u9b42\u672a\u5b9a\uff0c\u542c\u513f\u5b50\u5429\u5490\u5802\u5f1f\u76f4\u63a5\u5f00\u8f66\u79bb\u5f00\u6d77\u5dde\uff0c\u4e5f\u4e0b\u610f\u8bc6\u7684\u8bf4\uff1a\u201c\u5bf9\uff0c\u9a6c\u4e0a\u79bb\u5f00\u6d77\u5dde\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\uff1f\u201d\u5f20\u77e5\u975e\u4e00\u8fb9\u53d1\u52a8\u8f66\u5b50\uff0c\u4e00\u8fb9\u56de\u8fc7\u5934\u95ee\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5f85\u8981\u7a0d\u5b9a\u5fc3\u7eea\uff0c\u6574\u7406\u601d\u8def\uff0c\u5f20\u606a\u5728\u65c1\u8fb9\u62a2\u7740\u8bf4\uff1a\u201c\u66fe\u5efa\u534e\u5377\u6b3e\u5916\u9003\uff0c\u6709\u4fbf\u8863\u5b88\u5728\u4ed6\u5bb6\u697c\u4e0b\uff0c\u5176\u4e2d\u4e00\u4e2a\u4fbf\u8863\u8fd8\u662f\u6628\u5929\u6211\u4eec\u5728\u5510\u4f2f\u4f2f\u5bb6\u697c\u4e0b\u89c1\u8fc7\uff0c\u4ed6\u80af\u5b9a\u4e5f\u8ba4\u51fa\u6211\u6765\u4e86\uff0c\u8ba9\u6211\u7cca\u5f04\u4e86\u4e00\u4e0b\uff0c\u4ed6\u4eec\u6b63\u4e0a\u697c\u53bb\u786e\u8ba4\u60c5\u51b5\uff0c\u76f8\u4fe1\u5f88\u5feb\u5c31\u4f1a\u53d1\u73b0\u4e0d\u5bf9\uff0c\u4e07\u4e00\u4ed6\u4eec\u4e5f\u662f\u4e01\u5411\u5c71\u7684\u4eba\uff0c\u6211\u4eec\u518d\u4e0d\u8d70\uff0c\u5c31\u8d70\u4e0d\u4e86\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u77e5\u975e\u4e0d\u6562\u803d\u6401\uff0c\u8e29\u4e0b\u6cb9\u95e8\uff0c\u8f66\u5b50\u98de\u5feb\u7684\u7a9c\u4e86\u51fa\u53bb\uff0c\u5f80\u53bb\u7701\u57ce\u7684\u9ad8\u901f\u516c\u8def\u5f00\u53bb\u3002</p><p>\u3000\u3000\u8f66\u5b50\u4e0a\u4e86\u9ad8\u901f\uff0c\u5f20\u77e5\u884c\u624d\u5c06\u601d\u8def\u7406\u987a\uff0c\u8d8a\u53d1\u89c9\u5f97\u513f\u5b50\u4e0d\u53ef\u5c0f\u7aa5\uff0c\u6362\u4f5c\u81ea\u5df1\uff0c\u8981\u662f\u9661\u7136\u9047\u89c1\u90a3\u4e2a\u4fbf\u8863\uff0c\u8868\u73b0\u672a\u5fc5\u5982\u4ed6\u8fd9\u4e48\u51b7\u9759\uff0c\u66f4\u4e0d\u8981\u8bf4\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u5c06\u5176\u4e2d\u7684\u5229\u5bb3\u5173\u7cfb\u60f3\u901a\u5f7b\uff0c\u5e76\u505a\u51fa\u6700\u597d\u7684\u9009\u62e9\u3002\u4e0d\u9519\uff0c\u8fd9\u65f6\u5019\u60df\u6709\u53bb\u7701\u57ce\uff0c\u4e01\u5411\u5c71\u5728\u6d77\u5dde\u4e00\u624b\u906e\u5929\uff0c\u7559\u5728\u6d77\u5dde\u592a\u5371\u9669\uff0c\u53ea\u6709\u53bb\u7701\u57ce\u627e\u80fd\u63ed\u5f00\u8fd9\u4e2a\u60ca\u5929\u5927\u6848\u7684\u94c1\u8155\u4eba\u7269\uff0c\u624b\u91cc\u7684\u8bc1\u636e\u867d\u7136\u8fd8\u4e0d\u662f\u5f88\u786e\u51ff\uff0c\u4f46\u5df2\u7ecf\u6709\u8db3\u591f\u7684\u7406\u7531\u4ece\u65b0\u4e30\u96c6\u56e2\u8fd9\u6761\u7ebf\u4e0a\u6df1\u67e5\u4e0b\u53bb\u3002\u518d\u8bf4\u5510\u5b66\u8c26\u662f\u6d77\u5dde\u5e02\u59d4\u7684\u5e38\u59d4\u4e4b\u4e00\uff0c\u63d0\u4f9b\u8db3\u591f\u591a\u7684\u7591\u70b9\uff0c\u7701\u91cc\u662f\u4e0d\u4f1a\u8349\u7387\u7ed3\u6848\u7684\uff0c\u8fd9\u65f6\u5019\u53bb\u7701\u57ce\u662f\u60df\u4e00\u7684\u6b63\u786e\u9009\u62e9\u3002\u4f46\u662f\u513f\u5b50\u4e3a\u4ec0\u4e48\u80fd\u5728\u7b2c\u4e00\u65f6\u95f4\u505a\u51fa\u8fd9\u6837\u7684\u5224\u65ad\uff0c\u73b0\u5728\u7684\u5c0f\u5b69\u5b50\u8fd8\u771f\u4e86\u4e0d\u5f97\u554a\u3002</p><p>\u3000\u3000\u201c\u66fe\u5efa\u534e\u4e3a\u4ec0\u4e48\u5377\u6b3e\u5916\u9003\uff1f\u201d\u5f20\u77e5\u975e\u4e0d\u6653\u5f97\u5802\u5144\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u89c9\u5f97\u66fe\u5efa\u534e\u5377\u6b3e\u5916\u9003\u5f88\u5947\u602a\uff0c\u201c\u96be\u9053\u662f\u4ed6\u9003\u8dd1\u524d\u5c06\u672c\u5b50\u4ea4\u7ed9\u5510\u5b66\u8c26\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u521a\u624d\u7684\u8868\u73b0\uff0c\u5927\u6982\u8ba9\u7238\u7238\u5927\u5403\u4e00\u60ca\u5427\uff0c\u4e0d\u8fc7\u60c5\u51b5\u90a3\u4e48\u7d27\u6025\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002\u7238\u7238\u6ca1\u6709\u6025\u667a\uff0c\u8fd9\u4e9b\u5de5\u592b\uff0c\u60f3\u5fc5\u4e5f\u80fd\u5c06\u4e8b\u60c5\u7684\u524d\u56e0\u540e\u679c\u60f3\u660e\u767d\u3002</p><p>\u3000\u3000\u4f46\u662f\u8868\u73b0\u518d\u51fa\u683c\uff0c\u4e5f\u4e0d\u7528\u6015\u7238\u7238\u4f1a\u6000\u7591\u81ea\u5df1\u518d\u4e16\u4e3a\u4eba\uff0c\u53ea\u8981\u8bf4\u7684\u8bdd\u6709\u9053\u7406\uff0c\u80fd\u8ba9\u4ed6\u4fe1\u670d\uff0c\u8bf4\u4e0d\u5b9a\u8ba9\u7238\u7238\u5bf9\u81ea\u5df1\u65e9\u653e\u5f00\u624b\u811a\u3002\u5f20\u606a\u7406\u4e86\u7406\u601d\u7eea\uff1a\u201c\u4e8b\u60c5\u53ef\u80fd\u4e0d\u50cf\u5377\u6b3e\u5916\u9003\u8fd9\u4e48\u7b80\u5355\uff0c\u5510\u4f2f\u4f2f\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\uff0c\u65b0\u4e30\u96c6\u56e2\u8d22\u52a1\u90e8\u957f\u5377\u6b3e\u5916\u9003\uff0c\u8fd9\u6837\u7684\u6848\u5b50\u7edd\u5bf9\u80fd\u9707\u60ca\u6d77\u5dde\uff0c\u4e3a\u4ec0\u4e48\u6211\u4eec\u90fd\u4e0d\u77e5\u9053\uff1f\u518d\u8bf4\u4e86\uff0c\u66fe\u5efa\u534e\u4e0e\u59dc\u660e\u57ce\u3001\u4e01\u5411\u5c71\u540c\u6d41\u5408\u6c61\uff0c\u5728\u6d77\u5dde\u4ec0\u4e48\u597d\u5904\u4eab\u53d7\u4e0d\u5230\uff0c\u6709\u5fc5\u8981\u5377\u6b3e\u5916\u9003\u5417\uff1f\u66f4\u5927\u7684\u53ef\u80fd\u662f\u5931\u8e2a\u4e86\uff0c\u8fd9\u672c\u5b50\u662f\u5728\u4ed6\u5931\u8e2a\u540e\u843d\u5230\u5510\u4f2f\u4f2f\u624b\u91cc\uff0c\u5510\u4f2f\u4f2f\u6b63\u60f3\u7834\u89e3\u672c\u5b50\u91cc\u7684\u79d8\u5bc6\uff0c\u53ef\u80fd\u8ba9\u53f6\u65b0\u660e\u77e5\u9053\u4e86\u4e9b\u4ec0\u4e48\uff0c\u8fd9\u624d\u5f15\u8d77\u4e4b\u540e\u4e00\u7cfb\u5217\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0d\u5f97\u4e0d\u627f\u8ba4\u513f\u5b50\u5206\u6790\u5f97\u4e1d\u4e1d\u5165\u6263\uff0c\u53ea\u662f\u597d\u5947\uff0c\u5e73\u65f6\u5728\u5bb6\u91cc\u770b\u4e0a\u53bb\u6709\u70b9\u95f7\u7684\u513f\u5b50\uff0c\u4ec0\u4e48\u65f6\u5019\u6709\u8fd9\u4e48\u597d\u7684\u8111\u5b50\uff0c\u6709\u4e9b\u95ee\u9898\uff0c\u8fd8\u4e0d\u662f\u6709\u597d\u8111\u5b50\u5c31\u80fd\u60f3\u660e\u767d\u7684\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u7ed9\u59bb\u5b50\u6302\u4e86\u7535\u8bdd\uff0c\u8ba9\u5979\u4e0b\u73ed\u540e\u4e0d\u8981\u72ec\u81ea\u56de\u5bb6\u91cc\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u6d77\u5dde\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6216\u8bb8\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u53d1\u751f\uff0c\u6216\u8bb8\u5df2\u7ecf\u641e\u5f97\u60ca\u5929\u52a8\u5730\uff0c\u6ca1\u6709\u53ef\u9760\u7684\u4eba\uff0c\u4ec0\u4e48\u6d88\u606f\u90fd\u6253\u542c\u4e0d\u5230\u3002</p><p>\u3000\u3000\u6377\u8fbe\u8f66\u5728\u9ad8\u901f\u8def\u4e0a\u5feb\u901f\u7684\u884c\u9a76\u7740\uff0c\u5f20\u606a\u5728\u60f3\u624b\u91cc\u7684\u8bc1\u636e\u5e94\u8be5\u4ea4\u7ed9\u8c01\uff0c\u4e07\u4e00\u6240\u6258\u975e\u4eba\uff0c\u5c31\u4e07\u4e8b\u4e0d\u5999\uff0c\u60f3\u8d77\u4e00\u4ef6\u4e8b\u60c5\uff0c\u626d\u8fc7\u5934\u95ee\u7238\u7238\uff1a\u201c\u7238\uff0c\u4e01\u5411\u5c71\u4e3a\u4ec0\u4e48\u4f1a\u8ba9\u5510\u4f2f\u4f2f\u53bb\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\u5de5\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u773c\u775b\u4e00\u4eae\uff0c\u53c8\u8ba9\u513f\u5b50\u95ee\u5230\u5173\u7a8d\u5904\u3002</p><p>\u3000\u3000\u201c\u4e01\u5411\u5c71\u5e94\u8be5\u6e05\u695a\u5510\u4f2f\u4f2f\u7684\u4e3a\u4eba\uff0c\u6309\u7406\u6765\u8bf4\uff0c\u4ed6\u4e0d\u8be5\u8ba9\u5510\u4f2f\u4f2f\u53bb\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\u5de5\u4f5c\u624d\u5bf9\uff1f\u201d</p><p>\u3000\u3000\u201c\u7531\u8c01\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\u5374\u662f\u5e02\u5e38\u59d4\u4f1a\u8bae\u51b3\u5b9a\uff0c\u9664\u4e86\u5e02\u957f\u5468\u5bcc\u660e\u4e4b\u5916\uff0c\u7531\u5176\u4ed6\u51e0\u4e2a\u526f\u5e02\u957f\u4e3b\u6301\u90fd\u4e0d\u610f\u5916\uff0c\u5f53\u7136\u5510\u5b66\u8c26\u662f\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u7531\u4ed6\u6765\u4e3b\u6301\u6539\u5236\u5de5\u4f5c\uff0c\u66f4\u6070\u5f53\u4e00\u4e9b\uff0c\u636e\u8bf4\u5e38\u59d4\u4f1a\u8bae\u4e0a\uff0c\u4e01\u5411\u5c71\u4ee5\u5510\u5b66\u8c26\u80a9\u4e0a\u7684\u4efb\u52a1\u592a\u91cd\uff0c\u8981\u6c42\u6362\u522b\u4eba\uff0c\u662f\u5468\u5bcc\u660e\u6bd4\u8f83\u575a\u51b3\u7684\u8981\u6c42\u5510\u5b66\u8c26\u6765\u4e3b\u6301\u6539\u5236\u5de5\u4f5c\uff0c\u65b0\u4e30\u96c6\u56e2\u6539\u5236\u5de5\u4f5c\u672c\u6765\u5c31\u5c5e\u4e8e\u5e02\u653f\u5e9c\u7684\u7ba1\u8f96\u8303\u56f4\uff0c\u4e01\u5411\u5c71\u4e5f\u65e0\u6cd5\u575a\u6301\u4ec0\u4e48\uff0c\u8fd9\u624d\u843d\u5230\u5510\u5b66\u8c26\u7684\u5934\u4e0a\u3002\u201d\u5f20\u77e5\u884c\u8fd9\u65f6\u5df2\u7ecf\u5b8c\u5168\u628a\u513f\u5b50\u5f53\u6210\u53ef\u5546\u91cf\u4e8b\u60c5\u7684\u5bf9\u8c61\uff0c\u5c06\u5fc3\u91cc\u60f3\u7684\u4e8b\u60c5\u90fd\u8bf4\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u5468\u5bcc\u660e\u8fd9\u53ea\u8001\u72d0\u72f8\uff0c\u53ef\u80fd\u65e9\u5c31\u53d1\u89c9\u65b0\u4e30\u96c6\u56e2\u5b58\u5728\u732b\u817b\uff0c\u8fd9\u624d\u5c06\u5510\u5b66\u8c26\u63a8\u5230\u67aa\u53e3\u4e0a\u53bb\uff0c\u73b0\u5728\u89c1\u60c5\u5f62\u4e0d\u5bf9\uff0c\u53c8\u8eb2\u4e86\u8d77\u6765\u2026\u2026\u201d\u5f20\u606a\u5206\u6790\u9053\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u5468\u5bcc\u660e\u4e0e\u4e01\u5411\u5c71\u4e0d\u5e94\u8be5\u662f\u4e00\u4f19\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ef\u80fd\u662f\u4e00\u4f19\u7684\uff1f\u201d\u5c0f\u53d4\u5f20\u77e5\u975e\u5728\u524d\u5934\u4fa7\u4e86\u4fa7\u5934\uff0c\u8bf4\uff0c\u201c\u4e24\u4eba\u5728\u6d77\u5dde\u4e89\u4e86\u5341\u51e0\u5e74\u4e86\uff0c\u4e24\u4eba\u4e89\u5de5\u59d4\u4e66\u8bb0\u3001\u4e89\u5e02\u957f\u3001\u4e89\u5e02\u59d4\u4e66\u8bb0\uff0c\u90fd\u6597\u5f97\u5f88\u5389\u5bb3\uff0c\u6d77\u5dde\u4eba\u90fd\u77e5\u9053\uff0c\u4f46\u662f\u5462\uff0c\u4e01\u5411\u5c71\u59cb\u7ec8\u538b\u7740\u5468\u5bcc\u660e\u4e00\u5934\uff0c\u8981\u6709\u673a\u4f1a\uff0c\u5468\u5bcc\u660e\u6068\u4e0d\u5f97\u5c06\u4e01\u5411\u5c71\u4e22\u6cb9\u9505\u91cc\u70b8\u7740\u5403\uff0c\u770b\u6765\u5468\u5bcc\u660e\u8ba9\u5510\u5b66\u8c26\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\u5de5\u4f5c\uff0c\u5c31\u662f\u7ed9\u4e01\u5411\u5c71\u4f7f\u7eca\uff0c\u6210\u529f\u4e86\uff0c\u4e01\u5411\u5c71\u4e0b\u6765\uff0c\u4ed6\u4e0a\u53bb\uff0c\u4e0d\u6210\u529f\uff0c\u53ea\u662f\u727a\u7272\u5510\u5b66\u8c26\u800c\u5df2\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53ef\u80fd\u5468\u5bcc\u660e\u80fd\u5e2e\u6211\u4eec\u51fa\u51fa\u4e3b\u610f\uff1f\u201d</p><p>\u3000\u3000\u542c\u513f\u5b50\u8fd9\u4e48\u8bf4\uff0c\u5f20\u77e5\u884c\u6709\u4e9b\u8fdf\u7591\uff0c\u62ff\u8d77\u5927\u54e5\u5927\uff0c\u5374\u4e0d\u6562\u8f7b\u6613\u505a\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u4e01\u5411\u5c71\u6848\u53d1\u5165\u72f1\uff0c\u5468\u5bcc\u660e\u5f53\u4e0a\u5e02\u59d4\u4e66\u8bb0\uff0c\u4ec5\u4ece\u8fd9\u70b9\u4e0a\u6765\u770b\uff0c\u5468\u5bcc\u660e\u4e0e\u4e01\u5411\u5c71\u51b3\u4e0d\u4f1a\u6709\u4ec0\u4e48\u74dc\u845b\uff0c\u6b64\u65f6\u5411\u5468\u5bcc\u660e\u6c42\u63f4\u662f\u6070\u5f53\u7684\uff0c\u4e0d\u7136\u5c31\u7b97\u5230\u7701\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u5c06\u4e1c\u897f\u4ea4\u7ed9\u8c01\uff1f\u4e07\u4e00\u4ea4\u9519\u4e86\uff0c\u540e\u6094\u5c31\u6765\u4e0d\u53ca\u4e86\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8fdf\u7591\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u6309\u4e0b\u4e00\u4e2a\u4e2a\u6309\u952e\uff0c\u6377\u8fbe\u8f66\u5728\u9ad8\u901f\u8def\u4e0a\u5feb\u901f\u884c\u9a76\uff0c\u8f66\u7a97\u7d27\u95ed\uff0c\u8f66\u91cc\u5374\u5f88\u5b89\u9759\uff0c\u5927\u54e5\u5927\u91cc\u7684\u58f0\u97f3\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\u3002</p><p>\u3000\u3000\u201c\u5468\u5e02\u957f\uff0c\u6211\u662f\u5f20\u77e5\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f20\u77e5\u884c\uff0c\u4f60\u5728\u54ea\u91cc\uff0c\u5e02\u5c40\u600e\u4e48\u521a\u521a\u7ed9\u6211\u901a\u77e5\uff0c\u8bf4\u4f60\u754f\u7f6a\u6f5c\u9003\uff1f\u201d\u5468\u5bcc\u660e\u8bed\u901f\u6025\u4fc3\u800c\u54cd\u4eae\uff0c\u8fd9\u624d\u79bb\u5f00\u66fe\u5efa\u534e\u5bb6\u4e00\u4e2a\u534a\u5c0f\u65f6\uff0c\u4e01\u5411\u5c71\u7684\u901f\u5ea6\u4e5f\u592a\u5feb\u4e86\u5427\u3002</p><p>\u3000\u3000\u201c\u5e02\u5c40\u7ed9\u6211\u6309\u4e86\u4ec0\u4e48\u7f6a\u540d\uff0c\u8bf4\u6211\u56e0\u4e3a\u4ec0\u4e48\u754f\u7f6a\u6f5c\u9003\uff1f\u201d\u5f20\u77e5\u884c\u4e5f\u6ca1\u60f3\u5230\u4e8b\u60c5\u4f1a\u6765\u5f97\u8fd9\u4e48\u5feb\uff0c\u4e0d\u8fc7\u6709\u8fc7\u9884\u6599\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u60ca\u614c\u7684\u3002</p><p>\u3000\u3000\u201c\u7701\u68c0\u67e5\u7ec4\u56e0\u4e3a\u5510\u5b66\u8c26\u6848\u8981\u627e\u4f60\u8c03\u67e5\u60c5\u51b5\uff0c\u7535\u8bdd\u6253\u5230\u4f60\u4e1c\u793e\u8001\u5bb6\uff0c\u8bf4\u4f60\u5df2\u7ecf\u8fd4\u56de\u5e02\u91cc\uff0c\u4f60\u4e0e\u65b0\u4e30\u96c6\u56e2\u7684\u8d22\u52a1\u90e8\u957f\u66fe\u5efa\u534e\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4e3a\u4ec0\u4e48\u8fd4\u56de\u6d77\u5dde\u4e0d\u56de\u5e02\u91cc\u62a5\u9053\uff0c\u53cd\u800c\u53bb\u627e\u66fe\u5efa\u534e\uff1f\u4f60\u77e5\u9053\u4e0d\u77e5\u9053\u66fe\u5efa\u534e\u534a\u4e2a\u6708\u5377\u8d70\u65b0\u4e30\u96c6\u56e2\u8d26\u4e0a\u56db\u767e\u591a\u4e07\uff0c\u8fc4\u4eca\u884c\u8e2a\u4e0d\u660e\uff0c\u5e02\u5c40\u4e00\u76f4\u5728\u79d8\u5bc6\u4fa6\u67e5\uff0c\u5728\u66fe\u5efa\u534e\u5bb6\u697c\u4e0b\u5e03\u4e0b\u76d1\u63a2\u70b9\uff1f\u201d\u7535\u8bdd\u91cc\u5468\u5bcc\u660e\u7684\u58f0\u97f3\u7f13\u548c\u4e0b\u6765\uff0c\u4f3c\u4e4e\u8fd9\u53ea\u8001\u72d0\u72f8\u4ece\u7238\u7238\u5e73\u9759\u7684\u8bed\u6c14\u91cc\u55c5\u51fa\u70b9\u4ec0\u4e48\uff0c\u201c\u5e02\u5c40\u901a\u77e5\u6211\u7684\u8bed\u6c14\u662f\u91cd\u4e86\u4e00\u70b9\uff0c\u5012\u4e5f\u6ca1\u8bf4\u4f60\u72af\u4e86\u4ec0\u4e48\u7f6a\uff0c\u771f\u6ca1\u6709\u4ec0\u4e48\u95ee\u9898\u7684\u8bdd\uff0c\u4f60\u8fd8\u662f\u56de\u6d77\u5dde\u5427\uff0c\u628a\u8bef\u4f1a\u89e3\u91ca\u6e05\u695a\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u53ea\u8001\u72d0\u72f8\uff0c\u660e\u6446\u7740\u8981\u7238\u7238\u4e0d\u8981\u6025\u7740\u56de\u6d77\u5dde\u3001\u7ee7\u7eed\u628a\u4e8b\u6405\u6d51\u6d4a\u3002</p><p>\u3000\u3000\u201c\u5510\u5e02\u957f\u7684\u8bef\u4f1a\u6709\u6ca1\u6709\u89e3\u91ca\u6e05\u695a\uff1f\u201d\u5f20\u77e5\u884c\u7ee7\u7eed\u95ee\u3002</p><p>\u3000\u3000\u201c\u5510\u5b66\u8c26\u554a\uff0c\u201d\u5468\u5bcc\u660e\u5728\u7535\u8bdd\u91cc\u6253\u4e86\u4e2a\u987f\uff0c\u201c\u5510\u5b66\u8c26\u597d\u50cf\u4e0d\u662f\u4ec0\u4e48\u8bef\u4f1a\u95ee\u9898\uff0c\u7701\u68c0\u67e5\u7ec4\u65e9\u4e0a\u5df2\u7ecf\u8981\u6c42\u5510\u5b66\u8c26\u7684\u7231\u4eba\u63a5\u53d7\u8c03\u67e5\uff0c\u4f60\u6216\u8bb8\u8fd8\u4e0d\u77e5\u9053\uff0c\u65b0\u4e30\u96c6\u56e2\u7684\u603b\u7ecf\u7406\u59dc\u660e\u57ce\u3001\u4eba\u4e8b\u526f\u7ecf\u7406\u8bb8\u601d\uff0c\u5df2\u7ecf\u5411\u7701\u68c0\u67e5\u7ec4\u81ea\u9996\u4ea4\u5f85\u95ee\u9898\u4e86\uff0c\u7701\u68c0\u67e5\u7ec4\u6b63\u6309\u7167\u4ed6\u4eec\u63d0\u4f9b\u7684\u7ebf\u7d22\uff0c\u641c\u96c6\u8bc1\u636e\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u523b\u610f\u5728\u7535\u8bdd\u5c06\u60c5\u51b5\u5f88\u8be6\u7ec6\u7684\u505a\u4e86\u4e00\u4e2a\u8bf4\u660e\uff0c\u5f20\u606a\u6000\u7591\u8fd9\u53ea\u8001\u72d0\u72f8\u4ece\u5934\u5230\u5c3e\u90fd\u77e5\u9053\u5510\u5b66\u8c26\u662f\u65e0\u8f9c\u7684\uff0c\u8fd9\u624d\u662f\u4e00\u4e2a\u6807\u51c6\u7684\u653f\u5ba2\u3002\u5f20\u606a\u5fc3\u60f3\u7238\u7238\u8981\u80fd\u8fbe\u5230\u8fd9\u79cd\u6c34\u51c6\uff0c\u5728\u5b98\u573a\u91cc\u5c31\u6e38\u5203\u6709\u4f59\u4e86\u3002</p><p>\u3000\u3000\u201c\u5468\u5e02\u957f\uff0c\u6211\u624b\u5934\u6709\u4e9b\u6750\u6599\uff0c\u80fd\u8bc1\u660e\u5510\u5e02\u957f\u7684\u6848\u5b50\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\uff0c\u8fd9\u4efd\u6750\u6599\u80fd\u4e0d\u80fd\u76f4\u63a5\u4ea4\u5230\u4f60\u624b\u91cc\u4e0d\u51fa\u610f\u5916\uff1f\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u5728\u7535\u8bdd\u90a3\u5934\u6c89\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u80fd\u786e\u5b9a\u5510\u5b66\u8c26\u7684\u6848\u5b50\u4e0d\u7b80\u5355\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b0\u4e30\u96c6\u56e2\u7684\u90a3\u4e2a\u4eba\u4e8b\u526f\u7ecf\u7406\u4f3c\u4e4e\u8ddf\u4e01\u4e66\u8bb0\u5173\u7cfb\u4e0d\u7b80\u5355\u2026\u2026\u201d\u5f20\u77e5\u884c\u7a0d\u7a0d\u900f\u9732\u4e86\u4e00\u70b9\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u5230\u7535\u8bdd\u91cc\u6709\u62cd\u684c\u5b50\u7684\u58f0\u97f3\uff0c\u7d27\u63a5\u7740\u5468\u5bcc\u660e\u7684\u58f0\u97f3\u5c31\u6291\u4e0d\u4f4f\u7684\u9ad8\u4e86\u4e9b\u8bb8\uff1a\u201c\u4f60\u56de\u6d77\u5dde\uff0c\u5c31\u8981\u63a5\u53d7\u7701\u68c0\u67e5\u7ec4\u7684\u8c03\u67e5\uff0c\u8fd8\u8981\u5411\u5e02\u5c40\u89e3\u91ca\u4e3a\u4ec0\u4e48\u65e9\u4e0a\u4f1a\u53bb\u66fe\u5efa\u534e\u5bb6\uff0c\u53ea\u6015\u4e0d\u4f1a\u76f4\u63a5\u5c31\u80fd\u89c1\u5230\u6211\uff0c\u8fd9\u4e0d\u4fdd\u9669\uff0c\u4f60\u76f4\u63a5\u53bb\u7701\u91cc\u627e\u5f90\u5b66\u5e73\u4e66\u8bb0\uff0c\u6211\u5e2e\u4f60\u5148\u6253\u7535\u8bdd\u8054\u7cfb\u4e00\u4e0b\u2026\u2026\u201d</p></article>"}}}]);
