(window.webpackJsonp=window.webpackJsonp||[]).push([[140],{162:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e09\u8282 \u964c\u751f\u4eba",content:"<article><h2>\u7b2c\u5341\u4e09\u8282 \u964c\u751f\u4eba</h2><p>\u56e0\u4e3a\u5c71\u574e\u7684\u963b\u9694\uff0c\u8ff7\u8bf1\u9b54\u5e76\u6ca1\u6709\u770b\u5230\u743c\u6069\u521a\u624d\u5728\u505a\u4ec0\u4e48\uff0c\u5b83\u89c1\u77f3\u5757\u7838\u6765\uff0c\u8fd8\u4ee5\u4e3a\u743c\u6069\u6545\u6280\u91cd\u65bd\uff0c\u53c8\u8981\u4f7f\u7528\u77f3\u5f39\u672f\u3002\u8fd9\u79cd\u6765\u81ea\u6df1\u6e0a\u7684\u6076\u9b54\u8eab\u7ecf\u767e\u6218\uff0c\u54ea\u91cc\u5728\u4e4e\u8fd9\u70b9\u4f24\u5bb3\uff0c\u538b\u6839\u61d2\u5f97\u7406\u776c\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u62ac\u817f\u5c31\u51c6\u5907\u8de8\u8fc7\u5c71\u574e\uff0c\u628a\u8fd9\u4e2a\u4eba\u7c7b\u6740\u6b7b\u3002</p><p>\u77f3\u5757\u98de\u901f\u7838\u5230\uff0c\u773c\u770b\u5c31\u8981\u5728\u8ff7\u8bf1\u9b54\u7684\u72d7\u5934\u4e0a\u8f7b\u8f7b\u4e00\u649e\uff0c\u7136\u540e\u5f39\u5f00\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u743c\u6069\u9661\u7136\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u6062\u590d\uff01\u201d</p><p>\u5c0f\u77f3\u5757\u9aa4\u7136\u81a8\u80c0\uff0c\u5316\u4e3a\u5343\u65a4\u5de8\u77f3\u671d\u8ff7\u8bf1\u9b54\u5f53\u5934\u7838\u4e0b\u6765\u3002</p><p>\u8fd9\u4e00\u62db\u662f\u5f53\u5e74\u5728\u5deb\u5e08\u5b66\u6821\u8bfb\u4e66\u65f6\uff0c\u53d8\u5316\u5b66\u6d3e\u7684\u4e00\u4f4d\u6559\u6388\u4f20\u6388\u7684\u4e00\u79cd\u6280\u5de7\u3002\u56e0\u4e3a\u548c\u5c01\u795e\u6f14\u4e49\u4e2d\u67d0\u4f4d\u4ed9\u4eba\u7684\u6cd5\u5b9d\u5f88\u50cf\uff0c\u88ab\u743c\u6069\u620f\u79f0\u4e3a\u201c\u756a\u5929\u5370\u201d\uff0c\u662f\u4e00\u79cd\u5f88\u5de7\u5999\u4e5f\u5f88\u9634\u6bd2\u7684\u6218\u672f\u3002</p><p>\u743c\u6069\u4e5f\u66fe\u7ecf\u8003\u8651\u8fc7\u7528\u8fd9\u4e00\u62db\u6765\u5bf9\u4ed8\u9686\u5947\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u653e\u5f03\u4e86\u3002\u9686\u5947\u662f\u4e2a\u6b66\u50e7\uff0c\u8eab\u4f53\u529b\u58ee\uff0c\u53cd\u5e94\u6781\u5feb\uff0c\u8fd9\u4e00\u62db\u672a\u5fc5\u771f\u80fd\u7838\u5f97\u4e2d\u4ed6\uff0c\u8fd8\u662f\u7528\u5f3a\u5236\u541e\u91d1\u6cd5\u6765\u5f97\u4fdd\u9669\u3002</p><p>\u6b64\u65f6\u9762\u5bf9\u8ff7\u8bf1\u9b54\uff0c\u743c\u6069\u4e00\u65f6\u65e0\u8ba1\u53ef\u65bd\uff0c\u773c\u89c1\u65c1\u8fb9\u6b63\u6709\u5757\u5927\u77f3\u5934\uff0c\u7a81\u5730\u60f3\u8d77\u8fd9\u4e2a\u65b9\u6cd5\u6765\u3002</p><p>\u8ff7\u8bf1\u9b54\u6b63\u51c6\u5907\u4fef\u8eab\u6765\u6740\u6b7b\u743c\u6069\uff0c\u9aa4\u7136\u89c9\u5f97\u773c\u524d\u4e00\u7247\u9ed1\u6697\u538b\u5230\uff0c\u5b83\u8eab\u8eaf\u5e9e\u5927\uff0c\u817e\u632a\u95ea\u907f\u4e0d\u7075\uff0c\u4f46\u53cd\u5e94\u5012\u4e5f\u6781\u5feb\uff0c\u672c\u80fd\u5730\u62ac\u8d77\u4e24\u53ea\u87f9\u87af\uff0c\u5f80\u9762\u524d\u4e00\u6321\u3002</p><p>\u6e05\u8106\u7684\u9aa8\u9abc\u788e\u88c2\u58f0\u54cd\u8d77\uff0c\u4e24\u53ea\u87f9\u87af\u5f53\u5373\u6298\u65ad\uff0c\u5de8\u77f3\u4f9d\u65e7\u76f4\u76f4\u5730\u7838\u5728\u8ff7\u8bf1\u9b54\u7684\u8138\u4e0a\uff0c\u51e0\u4e4e\u5c06\u5b83\u534a\u8fb9\u8111\u888b\u90fd\u7ed9\u7838\u6241\u4e86\uff0c\u6210\u4e86\u4e00\u6ee9\u8840\u8089\u6a21\u7cca\u3002\u8ff7\u8bf1\u9b54\u7684\u4e24\u53ea\u7c7b\u4eba\u624b\u81c2\u5728\u7a7a\u4e2d\u80e1\u4e71\u5730\u6325\u821e\u7740\uff0c\u6447\u6447\u6b32\u5012\u3002</p><p>\u6210\u529f\u4e86\u3002</p><p>\u743c\u6069\u6b63\u81ea\u5e86\u5e78\u5f97\u624b\uff0c\u5374\u89c1\u90a3\u53ea\u8ff7\u8bf1\u9b54\u671d\u81ea\u5df1\u6251\u5012\u4e0b\u6765\u3002\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u5f85\u8981\u7ffb\u8eab\u907f\u8ba9\uff0c\u65e0\u5948\u5468\u8eab\u75bc\u75db\uff0c\u521a\u624d\u63b7\u51fa\u77f3\u5934\u5c31\u5df2\u7ecf\u8017\u8d39\u4e86\u4ec5\u5b58\u7684\u4e00\u70b9\u529b\u6c14\uff0c\u6b64\u65f6\u5b9e\u5728\u65e0\u6cd5\u52a8\u5f39\uff0c\u53ea\u80fd\u773c\u7741\u7741\u5730\u770b\u7740\u8ff7\u8bf1\u9b54\u7684\u5e9e\u5927\u8eab\u8eaf\u671d\u81ea\u5df1\u8f70\u7136\u7838\u4e0b\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u538b\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u4ed6\u53ea\u89c9\u80f8\u53e3\u6c14\u8840\u7ffb\u6d8c\uff0c\u5589\u5934\u4e00\u751c\uff0c\u9669\u4e9b\u6ca1\u5410\u51fa\u8840\u6765\uff0c\u6000\u7591\u808b\u9aa8\u662f\u4e0d\u662f\u90fd\u88ab\u538b\u65ad\u4e86\u51e0\u6839\u3002</p><p>\u743c\u6069\u76f4\u88ab\u538b\u5f97\u773c\u5192\u91d1\u661f\uff0c\u8fc7\u4e86\u597d\u534a\u5929\u603b\u7b97\u6062\u590d\u4e86\u70b9\u529b\u6c14\uff0c\u8270\u96be\u5730\u4ece\u8ff7\u8bf1\u9b54\u7684\u5c38\u4f53\u4e0b\u722c\u4e86\u51fa\u6765\uff0c\u56db\u80a2\u644a\u5f00\u5448\u5927\u5b57\u72b6\uff0c\u4ef0\u9762\u8eba\u5728\u5730\u4e0a\u547c\u547c\u5598\u6c14\u3002\u5927\u6982\u662f\u521a\u624d\u538b\u5f97\u592a\u91cd\uff0c\u4ed6\u611f\u89c9\u89c6\u7ebf\u6a21\u7cca\u4e0d\u6e05\uff0c\u851a\u84dd\u7684\u5929\u7a7a\u770b\u8d77\u6765\u90fd\u6709\u4e9b\u865a\u65e0\u98d8\u6e3a\uff0c\u8033\u8fb9\u4f3c\u4e4e\u4ec0\u4e48\u58f0\u97f3\u90fd\u542c\u4e0d\u89c1\u4e86\u3002\u4ed6\u5988\u7684\uff0c\u743c\u6069\u5fc3\u4e2d\u8bc5\u5492\uff0c\u600e\u4e48\u81ea\u5df1\u5c3d\u644a\u4e0a\u8fd9\u79cd\u5012\u9709\u7684\u4e8b\u60c5\u3002\u672c\u6765\u8fd8\u4ee5\u4e3a\u4e8b\u5148\u7b97\u8ba1\u4e86\u6b66\u50e7\uff0c\u4e00\u5207\u90fd\u5728\u638c\u63e1\u4e4b\u4e2d\uff0c\u5355\u5355\u4e00\u4e2a\u5973\u6740\u624b\u65e0\u8bba\u5982\u4f55\u4e0d\u662f\u81ea\u5df1\u548c\u6885\u83f2\u65af\u4e24\u4eba\u7684\u5bf9\u624b\uff0c\u8c01\u60f3\u51e0\u5929\u4e0d\u89c1\uff0c\u5979\u5c45\u7136\u90fd\u5b66\u4f1a\u53ec\u5524\u6076\u9b54\u4e86\u3002</p><p>\u53ec\u5524\u5f02\u4f4d\u9762\u7684\u5b58\u5728\uff0c\u8fd9\u5c5e\u4e8e\u5492\u6cd5\u5b66\u6d3e\u7684\u5206\u652f\u4e13\u4e1a\uff0c\u4e0d\u662f\u743c\u6069\u7684\u7cbe\u7814\u65b9\u5411\uff0c\u4f46\u4ed6\u5927\u7565\u4e5f\u77e5\u9053\uff0c\u5982\u679c\u4e0d\u4e3e\u884c\u89c4\u6a21\u5e9e\u5927\u7684\u732e\u796d\u4eea\u5f0f\uff0c\u4ec5\u4ec5\u662f\u7528\u6cd5\u672f\u5f3a\u884c\u53ec\u5524\u7684\u8bdd\uff0c\u80fd\u4e00\u6b21\u6027\u62db\u6765\u4e24\u53ea\u8ff7\u8bf1\u9b54\uff0c\u8fd9\u5df2\u7ecf\u662f\u7b2c\u4e00\u6d41\u5deb\u5e08\u7684\u6c34\u51c6\u4e86\u3002\u800c\u4e14\u5deb\u5e08\u62db\u6765\u6076\u9b54\uff0c\u5f80\u5f80\u8fd8\u9700\u8981\u8017\u8d39\u5927\u91cf\u7684\u7cbe\u529b\u53bb\u538b\u5236\u3001\u675f\u7f1a\uff0c\u5426\u5219\u6076\u9b54\u4e0d\u4f1a\u4fef\u9996\u542c\u547d\u3002\u8fd9\u4e2a\u5973\u6740\u624b\u4e0d\u66fe\u65bd\u6cd5\u8bf5\u5492\uff0c\u76f4\u63a5\u5c31\u62db\u51fa\u4e24\u53ea\u8ff7\u8bf1\u9b54\u6765\uff0c\u800c\u4e14\u5f53\u5373\u53d1\u53f7\u65bd\u4ee4\uff0c\u6307\u6325\u5982\u610f\uff0c\u8fd9\u53ef\u7740\u5b9e\u8d85\u51fa\u4e86\u743c\u6069\u7684\u77e5\u8bc6\u8303\u7574\uff0c\u4ed6\u60f3\u8c61\u4e0d\u51fa\u5973\u6740\u624b\u5230\u5e95\u662f\u600e\u4e48\u505a\u5230\u8fd9\u70b9\u7684\u3002</p><p>\u9664\u975e\u2026\u2026\u5979\u80fd\u6709\u66f4\u9ad8\u4f4d\u9636\u6076\u9b54\u7684\u6697\u4e2d\u652f\u6301\u2026\u2026</p><p>\u6fc0\u70c8\u7684\u5175\u5203\u649e\u51fb\u58f0\u6253\u65ad\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u743c\u6069\u7684\u542c\u529b\u7ec8\u4e8e\u5f00\u59cb\u6e10\u6e10\u6062\u590d\u4e00\u70b9\uff0c\u4ed6\u968f\u5373\u60f3\u8d77\u6885\u83f2\u65af\u8fd8\u6b63\u548c\u5973\u6740\u624b\u5728\u6b8a\u6b7b\u640f\u6597\uff0c\u4e0d\u77e5\u9053\u7ed3\u679c\u5982\u4f55\u3002</p><p>\u4ed6\u6323\u624e\u7740\uff0c\u5f3a\u5fcd\u5168\u8eab\u5267\u75db\uff0c\u8270\u96be\u5730\u722c\u4e0a\u5c71\u574e\uff0c\u89c2\u5bdf\u6218\u5c40\u3002\u5fc5\u987b\u8bf4\uff0c\u60c5\u5f62\u5f88\u4e0d\u4e50\u89c2\uff0c\u9762\u5bf9\u5973\u6740\u624b\u548c\u8ff7\u8bf1\u9b54\u7684\u5939\u653b\uff0c\u6885\u83f2\u65af\u62db\u67b6\u5f97\u975e\u5e38\u5403\u529b\uff0c\u5df2\u7ecf\u660e\u663e\u843d\u4e86\u4e0b\u98ce\u3002\u5973\u6740\u624b\u6709\u4e86\u8ff7\u8bf1\u9b54\u8fd9\u4e2a\u5f3a\u529b\u8089\u76fe\uff0c\u767b\u65f6\u5c3d\u5c55\u6240\u957f\uff0c\u8eab\u5f62\u795e\u51fa\u9b3c\u6ca1\uff0c\u4e0d\u65f6\u9aa4\u7136\u6d88\u5931\uff0c\u968f\u5373\u4ece\u5730\u4e0a\u7684\u9634\u5f71\u4e2d\u5e7b\u5316\u51fa\u6765\uff0c\u53d1\u52a8\u7a81\u7136\u88ad\u51fb\u3002\u5018\u82e5\u4e0d\u662f\u4e0a\u6b21\u6885\u83f2\u65af\u5c31\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u8fd9\u4e00\u62db\uff0c\u65e9\u6709\u9632\u5907\uff0c\u53ea\u6015\u771f\u8981\u5403\u4e8f\u4e86\u3002</p><p>\u743c\u6069\u5f88\u60f3\u4e0a\u53bb\u5e2e\u5fd9\uff0c\u4f46\u4ed6\u6b64\u65f6\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u66f4\u522b\u8bf4\u8bf5\u5492\u65bd\u6cd5\u4e86\u3002\u800c\u4e14\uff0c\u5728\u521a\u624d\u7684\u6218\u6597\u4e2d\uff0c\u4ed6\u5df2\u7ecf\u63a5\u8fde\u4f7f\u7528\u4e86\u77f3\u80a4\u3001\u53d8\u5f62\u3001\u52a0\u901f\u3001\u77f3\u5f39\u3001\u7f29\u7269\u7b49\u6cd5\u672f\uff0c\u8fd9\u4e9b\u662f\u4ed6\u4eca\u65e9\u51fa\u53d1\u524d\u51c6\u5907\u7684\u6700\u5f3a\u6709\u529b\u7684\u6cd5\u672f\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u5269\u4f59\u7684\u6cd5\u672f\u4f3c\u4e4e\u90fd\u5f88\u96be\u5728\u8fd9\u573a\u6076\u6597\u4e2d\u6d3e\u4e0a\u591a\u5c11\u7528\u573a\u3002</p><p>\u4ed6\u53ea\u80fd\u773c\u7741\u7741\u5730\u770b\u7740\uff0c\u4e00\u8fb9\u52aa\u529b\u5e73\u9759\u4e0b\u6765\uff0c\u8fd0\u529f\u8c03\u606f\uff0c\u5c3d\u5feb\u8ba9\u81ea\u5df1\u6062\u590d\u4f53\u529b\u3002</p><p>\u90a3\u8fb9\u7684\u93d6\u6218\u4e2d\uff0c\u6885\u83f2\u65af\u7ec8\u4e8e\u6293\u4f4f\u4e86\u4e00\u4e2a\u673a\u4f1a\uff0c\u5979\u7684\u94f6\u5251\u4e0a\u9661\u7136\u7efd\u653e\u51fa\u8000\u773c\u767d\u5149\u6765\uff0c\u5c06\u5979\u6574\u4e2a\u4eba\u90fd\u5305\u88f9\u5176\u4e2d\uff0c\u4eff\u4f5b\u4e00\u9897\u5c0f\u592a\u9633\u822c\u671d\u8ff7\u8bf1\u9b54\u6fc0\u649e\u800c\u53bb\u3002\u743c\u6069\u8ba4\u8bc6\u8fd9\u4e00\u62db\uff0c\u4ed6\u7b2c\u4e00\u6b21\u9047\u89c1\u6885\u83f2\u65af\u7684\u65f6\u5019\uff0c\u5c11\u5973\u5c31\u7528\u8fd9\u4e00\u51fb\u8f70\u6740\u4e86\u5de8\u5927\u7684\u9aa8\u866b\u3002\u4ed6\u5f88\u6000\u7591\u8fd9\u5c31\u662f\u4f20\u8bf4\u4e2d\u5723\u6b66\u58eb\u6240\u72ec\u6709\u7684\u201c\u7834\u90aa\u65a9\u201d\uff0c\u4f46\u4e00\u76f4\u6ca1\u5411\u6885\u83f2\u65af\u6c42\u8bc1\u8fc7\u3002</p><p>\u65e0\u8bba\u8fd9\u4e00\u62db\u662f\u4e0d\u662f\u7834\u90aa\u65a9\uff0c\u5b83\u7684\u5a01\u529b\u663e\u7136\u8d27\u771f\u4ef7\u5b9e\u3002\u7830\u5730\u4e00\u58f0\uff0c\u8ff7\u8bf1\u9b54\u88ab\u6885\u83f2\u65af\u7684\u94f6\u5251\u5288\u4e2d\uff0c\u5b83\u5267\u70c8\u5730\u568e\u53eb\u7740\uff0c\u8eab\u4f53\u88ab\u70bd\u70ed\u767d\u5149\u77ac\u95f4\u6e6e\u6ca1\uff0c\u7247\u523b\u95f4\u5316\u4f5c\u98de\u7070\uff0c\u6d88\u6563\u65e0\u8e2a\u3002</p><p>\u68d2\u6781\u4e86\u3002</p><p>\u743c\u6069\u6b63\u8981\u5927\u58f0\u559d\u5f69\uff0c\u5c31\u89c1\u6740\u624b\u8d81\u8fd9\u4e2a\u673a\u4f1a\u8d34\u8fd1\u8eab\u6765\uff0c\u4e24\u67c4\u5982\u706b\u86c7\u7f20\u7ed5\u7684\u77ed\u5251\u540c\u65f6\u671d\u6885\u83f2\u65af\u80cc\u540e\u523a\u51fa\u3002\u5c11\u5973\u521a\u521a\u5168\u529b\u4e00\u51fb\u65a9\u6740\u4e86\u8ff7\u8bf1\u9b54\uff0c\u6765\u4e0d\u53ca\u8f6c\u8eab\u62b5\u6321\uff0c\u5306\u5fd9\u5f80\u524d\u659c\u8de8\u4e00\u6b65\uff0c\u7ffb\u8eab\u4e00\u5251\u56de\u5288\u4e0b\u6765\uff0c\u8eab\u4f53\u501f\u52bf\u65cb\u8f6c\u3002</p><p>\u6740\u624b\u53cc\u624b\u77ed\u5251\u4ea4\u53c9\u4e00\u683c\uff0c\u67b6\u4f4f\u94f6\u5251\uff0c\u53f3\u624b\u987a\u52bf\u4e0b\u524a\uff0c\u5feb\u82e5\u95ea\u7535\u5730\u671d\u6885\u83f2\u65af\u624b\u8155\u5207\u53bb\u3002\u6885\u83f2\u65af\u7acb\u8db3\u4e0d\u7a33\uff0c\u88ab\u5bf9\u65b9\u5360\u4e86\u5148\u673a\uff0c\u53ea\u5f97\u8fde\u8fde\u9000\u907f\u3002\u6740\u624b\u5374\u4e0d\u4f9d\u9976\uff0c\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\u5730\u671d\u6885\u83f2\u65af\u7684\u5934\u8138\u90e8\u4f4d\u523a\u53bb\uff0c\u6b63\u662f\u5973\u5723\u6b66\u58eb\u7684\u94f6\u7532\u7ffc\u62a4\u4e0d\u5230\u4e4b\u5904\u3002</p><p>\u6740\u624b\u7684\u653b\u51fb\u8d8a\u6765\u8d8a\u51cc\u5389\uff0c\u6885\u83f2\u65af\u5de6\u652f\u53f3\u7ecc\uff0c\u6e10\u6e10\u6709\u4e9b\u72fc\u72c8\u3002\u521a\u624d\u4e00\u51fb\u65a9\u6740\u8ff7\u8bf1\u9b54\uff0c\u867d\u7136\u770b\u8d77\u6765\u5a01\u98ce\uff0c\u4f46\u5374\u5fc5\u5b9a\u4e5f\u6d88\u8017\u4e86\u5979\u5927\u91cf\u7684\u529b\u6c14\uff0c\u611f\u89c9\u9aa4\u7136\u95f4\u4fbf\u865a\u5f31\u4e86\u5f88\u591a\u3002\u539f\u672c\u5979\u5728\u529b\u6c14\u4e0a\u548c\u6740\u624b\u65d7\u9f13\u76f8\u5f53\uff0c\u5982\u4eca\u5374\u5c61\u5c61\u843d\u4e86\u4e0b\u98ce\uff0c\u94f6\u5251\u51e0\u6b21\u88ab\u5bf9\u65b9\u7684\u77ed\u5251\u53cd\u63a8\u56de\u6765\uff0c\u867d\u7136\u4e00\u65f6\u8fd8\u4e0d\u66fe\u53d7\u4f24\uff0c\u4f46\u8fd9\u5df2\u7ecf\u662f\u4e0d\u8be6\u7684\u9884\u5146\u3002</p><p>\u6740\u624b\u7684\u77ed\u5251\u4e0a\u8403\u6709\u5267\u6bd2\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5728\u70db\u5821\u65f6\u5c31\u89c1\u8bc6\u8fc7\uff0c\u867d\u7136\u4f3c\u4e4e\u4e0d\u80fd\u89c1\u8840\u5c01\u5589\uff0c\u5374\u8db3\u4ee5\u8ba9\u4e00\u4e2a\u4eba\u6682\u65f6\u5b8c\u5168\u5931\u53bb\u6218\u6597\u529b\uff0c\u53ea\u80fd\u5012\u5728\u5730\u4e0a\u4efb\u4eba\u5bb0\u5272\u3002\u6885\u83f2\u65af\u8eab\u4e0a\u6709\u94f6\u9f99\u76ae\u7532\u4fdd\u62a4\uff0c\u575a\u6108\u91d1\u94c1\uff0c\u77ed\u5251\u96be\u4ee5\u523a\u8fdb\u53bb\uff0c\u4f46\u53ea\u8981\u7a0d\u4e00\u4e0d\u614e\uff0c\u8ba9\u6740\u624b\u5728\u8138\u4e0a\u5212\u4e2d\u4e00\u9053\uff0c\u90a3\u5c31\u7acb\u523b\u5b8c\u86cb\u4e86\u3002</p><p>\u743c\u6069\u770b\u7684\u7126\u6025\u4e07\u5206\uff0c\u65e0\u5948\u81ea\u5df1\u6b64\u65f6\u5e2e\u4e0d\u4e0a\u534a\u70b9\u5fd9\uff0c\u5c31\u8fde\u5450\u558a\u52a9\u5a01\u90fd\u6ca1\u529b\u6c14\u3002\u6b63\u81ea\u7740\u6025\uff0c\u7a81\u7136\u53d1\u89c9\u8eab\u8fb9\u4f3c\u4e4e\u4e0d\u58f0\u4e0d\u54cd\u5730\u591a\u4e86\u4e2a\u4eba\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u614c\u5fd9\u504f\u5934\u770b\u53bb\uff0c\u89c1\u662f\u4e00\u4e2a\u6234\u7740\u9ed1\u8272\u5bbd\u6cbf\u5e3d\uff0c\u7a7f\u7740\u571f\u9ec4\u8272\u98ce\u8863\u7684\u5bb6\u4f19\u3002</p><p>\u8fd9\u4eba\u770b\u8d77\u6765\u5e74\u7eaa\u5927\u7ea6\u5728\u4e09\u5341\u5de6\u53f3\uff0c\u7559\u7740\u77ed\u77ed\u7684\u80e1\u987b\uff0c\u4e00\u53cc\u773c\u775b\u5fae\u5fae\u772f\u7f1d\uff0c\u5012\u662f\u95ea\u70c1\u6709\u795e\uff0c\u5176\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u4e94\u5b98\u6392\u5217\u4e2d\u89c4\u4e2d\u77e9\uff0c\u548c\u5927\u8857\u4e0a\u7684\u666e\u901a\u4eba\u5168\u65e0\u5206\u522b\u2026\u2026\u4e0d\uff0c\u6700\u5927\u7684\u533a\u522b\uff0c\u5c31\u5728\u4e8e\u4e00\u4e2a\u666e\u901a\u4eba\u600e\u4e48\u4f1a\u8db4\u5728\u8fd9\u91cc\uff1f</p></article>"}}}]);
