(window.webpackJsonp=window.webpackJsonp||[]).push([[1400],{1423:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e5d\u8282 \u961f\u957f\u95ee\u9898",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e5d\u8282 \u961f\u957f\u95ee\u9898</h2><p>\u56de\u5230\u623f\u95f4\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u5df2\u7ecf\u56de\u6765\u4e86\uff0c\u201c\u600e\u4e48\u6837\uff0c\u201d\u743c\u6069\u968f\u53e3\u95ee\uff0c\u201c\u90a3\u4f4d\u5c0f\u9b54\u5973\u6ca1\u627e\u4f60\u9ebb\u70e6\u5427\u3002\u201d</p><p>\u201c\u5c0f\u9b54\u5973\uff1f\u201d\u6885\u83f2\u65af\u6014\u4e86\u4e0b\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u54e6\uff0c\u6ca1\u4e8b\uff0c\u5979\u80fd\u8bf4\u4ec0\u4e48\uff0c\u95f9\u817e\u4e00\u4f1a\u4e5f\u5c31\u7b97\u4e86\uff0c\u540e\u6765\u6211\u4eec\u7ee7\u7eed\u901b\u8857\u53bb\u4e86\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u65e0\u8bed\uff0c\u5973\u5b69\u5b50\u96be\u9053\u90fd\u8fd9\u4e48\u559c\u6b22\u901b\u8857\u4e48\uff0c\u5c31\u8fde\u6885\u83f2\u65af\u8fd9\u79cd\u770b\u8d77\u6765\u6bd4\u8f83\u521a\u786c\u7684\u7c7b\u578b\uff0c\u4f3c\u4e4e\u4e5f\u5bf9\u8fd9\u9879\u6d3b\u52a8\u4e50\u6b64\u4e0d\u75b2\uff0c\u6628\u5929\u53bb\u4e86\u4eca\u5929\u53c8\u53bb\u3002\u201c\u90a3\u4e70\u4e86\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u4ed6\u95ee\uff0c\u56e0\u4e3a\u4f3c\u4e4e\u6ca1\u770b\u5230\u623f\u95f4\u91cc\u65b0\u589e\u52a0\u4ec0\u4e48\u5546\u54c1\u3002</p><p>\u201c\u54e6\uff0c\u4ec0\u4e48\u90fd\u6ca1\u4e70\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8be5\u4e70\u7684\u4e1c\u897f\u6628\u5929\u90fd\u5df2\u7ecf\u4e70\u5b8c\u4e86\uff0c\u4eca\u5929\u81ea\u7136\u5c31\u4e0d\u7528\u4e86\u3002\u201d</p><p>\u201c\u5514\uff1f\u65e2\u7136\u4e0d\u7528\u4e70\u4e1c\u897f\uff0c\u90a3\u53bb\u901b\u8857\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5c31\u662f\u901b\u8857\u554a\u3002\u201d</p><p>\u201c\u4f46\u901b\u8857\u4e0d\u5c31\u662f\u4e3a\u4e86\u4e70\u4e1c\u897f\u5417\uff1f\u201d</p><p>\u201c\u8c01\u8bf4\u7684\uff0c\u901b\u8857\u662f\u901b\u8857\uff0c\u4e70\u4e1c\u897f\u662f\u4e70\u4e1c\u897f\uff0c\u8fd9\u5b8c\u5168\u662f\u4e24\u7801\u4e8b\u5427\uff0c\u4e0d\u8981\u6df7\u4e3a\u4e00\u8c08\u554a\u3002\u201d</p><p>\u662f\u4e24\u7801\u4e8b\u4e48\u2026\u2026\u7b97\u4e86\uff0c\u4e0d\u8ddf\u5973\u5b69\u5b50\u63a2\u8ba8\u8fd9\u79cd\u6982\u5ff5\u95ee\u9898\u3002</p><p>\u743c\u6069\u628a\u521a\u624d\u738b\u5b50\u8bf4\u7684\u4e8b\u60c5\u5411\u6885\u83f2\u65af\u8f6c\u8ff0\u4e86\u4e00\u904d\uff0c\u5374\u9690\u7792\u4e86\u201c\u51db\u53ef\u80fd\u662f\u6f5c\u5728\u654c\u4eba\u201d\u8fd9\u70b9\uff0c\u6682\u65f6\u4e0d\u60f3\u8ba9\u5979\u77e5\u9053\u3002\u6885\u83f2\u65af\u9759\u9759\u542c\u5b8c\uff0c\u6c89\u601d\u4e00\u4f1a\uff0c\u201c\u5e03\u96f7\u7eb3\u65af\u6bbf\u4e0b\u8bf4\u5f97\u6ca1\u9519\uff0c\u963f\u683c\u62c9\u9686\u90a3\u8fb9\uff0c\u786e\u5b9e\u9664\u4e86\u6b23\u5e03\u5973\u738b\u5c31\u518d\u6ca1\u4ec0\u4e48\u8457\u540d\u7684\u4eba\u624d\u3002\u800c\u4e14\uff0c\u5982\u679c\u8bf4\u73b0\u5728\u6301\u6709\u6b7b\u6708\u6cd5\u73e0\u7684\u662f\u4e2a\u7267\u5e08\u7684\u8bdd\u2026\u2026\u636e\u6211\u6240\u77e5\uff0c\u5e94\u8be5\u6ca1\u6709\u8fd9\u4e48\u9ad8\u660e\u7684\u89d2\u8272\u624d\u5bf9\u3002\u201d</p><p>\u201c\u4f60\u5bf9\u963f\u683c\u62c9\u9686\u90a3\u8fb9\u7684\u6559\u4f1a\u60c5\u51b5\u5f88\u719f\u6089\uff1f\u201d</p><p>\u201c\u8bf4\u4e0d\u4e0a\u719f\u6089\uff0c\u4f46\u77e5\u9053\u4e00\u4e9b\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8ff7\u65af\u5353\u8bfa\u548c\u963f\u683c\u62c9\u9686\u4e00\u6837\uff0c\u4ee5\u524d\u90fd\u662f\u7cbe\u7075\u738b\u56fd\uff0c\u8fd9\u4e24\u5730\u7684\u6559\u4f1a\uff0c\u5927\u91cf\u7684\u4fe1\u5f92\u548c\u7267\u5e08\u90fd\u662f\u7cbe\u7075\u3001\u534a\u7cbe\u7075\u6216\u8005\u5e26\u7cbe\u7075\u8840\u7edf\uff0c\u4e24\u5730\u6765\u5f80\u662f\u975e\u5e38\u5bc6\u5207\u7684\u3002\u6211\u4ee5\u524d\u5728\u6559\u4f1a\uff0c\u591a\u591a\u5c11\u5c11\u603b\u4e5f\u4e86\u89e3\u4e9b\u60c5\u51b5\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u554a\u3002\u201d\u743c\u6069\u70b9\u70b9\u5934\u3002</p><p>\u201c\u6b7b\u6708\u6cd5\u73e0\u6211\u4e0d\u6e05\u695a\uff0c\u4f46\u4ee5\u5e38\u7406\u6765\u63a8\u6d4b\u7684\u8bdd\uff0c\u8fd9\u79cd\u90aa\u6076\u795e\u5668\uff0c\u53c8\u662f\u5927\u5965\u672f\u5e08\u6240\u521b\u9020\uff0c\u80af\u5b9a\u662f\u4e13\u95e8\u4e3a\u5deb\u5e08\u800c\u8bbe\u8ba1\u7684\uff0c\u201d\u6885\u83f2\u65af\u5206\u6790\u7740\uff0c\u201c\u90a3\u4e48\uff0c\u8981\u6301\u6709\u5b83\u800c\u4e0d\u88ab\u4f24\u5bb3\uff0c\u4ee5\u5deb\u5e08\u6700\u4e3a\u5bb9\u6613\uff0c\u4ee5\u7267\u5e08\u7b49\u795e\u804c\u8005\u6700\u96be\u3002\u201d</p><p>\u201c\u662f\uff0c\u201d\u743c\u6069\u8d5e\u540c\uff0c\u201c\u6bbf\u4e0b\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u795e\u804c\u8005\u6309\u9053\u7406\u662f\u4e0d\u6562\u78b0\u6b7b\u6708\u6cd5\u73e0\u7684\u3002\u7136\u800c\u65e2\u7136\u4ed6\u6562\u78b0\uff0c\u5c31\u53ea\u80fd\u8bf4\u660e\u529b\u91cf\u5f3a\u5f97\u5df2\u7ecf\u53ef\u4ee5\u538b\u5236\u4f4f\u795e\u5668\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u5f88\u96be\uff0c\u201d\u6885\u83f2\u65af\u6447\u5934\uff0c\u201c\u4f60\u548c\u51db\u90fd\u662f\u5deb\u5e08\uff0c\u9020\u8be3\u4e5f\u4e0d\u5dee\uff0c\u8fd0\u7528\u6b7b\u6708\u6cd5\u73e0\u7684\u529b\u91cf\u4f30\u8ba1\u505a\u4e0d\u5230\uff0c\u77ed\u6682\u6301\u6709\u5e94\u8be5\u662f\u4e0d\u4f1a\u88ab\u4f24\u5bb3\u7684\u3002\u4f46\u80fd\u505a\u5230\u8fd9\u70b9\u7684\u795e\u804c\u8005\u2026\u2026\u6211\u60f3\u8fd9\u4e16\u754c\u4e0a\u6ca1\u6709\u591a\u5c11\u3002\u81f3\u5c11\u963f\u683c\u62c9\u9686\u90a3\u8fb9\u7684\u51e0\u4e2a\u6559\u4f1a\uff0c\u5c31\u7b97\u662f\u6700\u9ad8\u9636\u7684\u7267\u5e08\u4e5f\u5e94\u8be5\u529e\u4e0d\u5230\uff0c\u201d</p><p>\u201c\u53ef\u80fd\u662f\u4ec0\u4e48\u90aa\u795e\u7684\u5730\u4e0b\u6559\u4f1a\u6210\u5458\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u4f9d\u65e7\u6447\u5934\uff0c\u201c\u963f\u683c\u62c9\u9686\u90a3\u8fb9\u6ca1\u4ec0\u4e48\u90aa\u795e\u6559\u4f1a\uff0c\u5c31\u7b97\u6709\uff0c\u4e5f\u80af\u5b9a\u4e0d\u6210\u6c14\u5019\uff0c\u201d\u5979\u89e3\u91ca\uff0c\u201c\u5728\u6253\u51fb\u90aa\u795e\u6559\u4f1a\u8fd9\u70b9\u4e0a\uff0c\u6b23\u5e03\u5973\u738b\u4ece\u6765\u662f\u4e0d\u9057\u4f59\u529b\u7684\u3002\u201d</p><p>\u90a3\u5c31\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\u4e86\u2014\u2014\u8bdd\u8bf4\u56de\u6765\uff0c\u6b63\u56e0\u4e3a\u6309\u7167\u5e38\u7406\u63a8\u6d4b\u4e0d\u51fa\u8fd9\u4e2a\u6301\u6709\u6b7b\u6708\u6cd5\u73e0\u7684\u5bb6\u4f19\u5230\u5e95\u662f\u8c01\uff0c\u6240\u4ee5\u624d\u9700\u8981\u6d3e\u4eba\u4eb2\u81ea\u8fc7\u53bb\u627e\u5427\u3002\u4e0d\u7ba1\u4e86\uff0c\u53cd\u6b63\u53ea\u8981\u9762\u5bf9\u7684\u662f\u795e\u672f\u65bd\u6cd5\u8005\uff0c\u65e0\u8bba\u7267\u5e08\u4e5f\u597d\uff0c\u5fb7\u9c81\u4f9d\u4e5f\u7f62\uff0c\u743c\u6069\u90fd\u8fd8\u662f\u6709\u51e0\u5206\u4fe1\u5fc3\u7684\uff0c\u6bd5\u7adf\u4ed6\u6709\u5929\u751f\u7684\u795e\u672f\u514d\u75ab\u80fd\u529b\u3002\u8bf4\u8d77\u6765\uff0c\u8fd9\u4e5f\u7b97\u662f\u4e00\u79cd\u5929\u8d4b\u5f02\u80fd\u4e86\u3002</p><p>\u201c\u8981\u662f\u4ee5\u540e\u9047\u4e0a\u7684\u5bf9\u624b\u5168\u662f\u7267\u5e08\u5c31\u597d\u4e86\u3002\u201d\u4ed6\u6709\u4e9b\u4e0d\u5207\u5b9e\u9645\u5730\u60f3\u3002</p><p>\u6b64\u540e\u4e09\u5929\u65f6\u95f4\u91cc\uff0c\u743c\u6069\u57cb\u5934\u7814\u8bfb\u738b\u5b50\u9001\u8fc7\u6765\u7684\u90a3\u4e00\u5806\u8d44\u6599\u3002\u5fc5\u987b\u8bf4\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u679c\u7136\u662f\u5e72\u8003\u53e4\u7684\uff0c\u8d44\u6599\u6536\u96c6\u5de5\u4f5c\u505a\u5f97\u6781\u5176\u8be6\u7ec6\uff0c\u5386\u53f2\u5730\u7406\u98ce\u571f\u4eba\u60c5\u65e0\u6240\u4e0d\u5305\uff0c\u628a\u743c\u6069\u770b\u5f97\u6655\u5934\u8f6c\u5411\uff0c\u7b80\u76f4\u90fd\u8981\u6000\u7591\u738b\u5b50\u624d\u662f\u963f\u683c\u62c9\u9686\u7684\u7edf\u6cbb\u8005\uff0c\u800c\u4e0d\u662f\u90a3\u4f4d\u6b23\u5e03\u5973\u738b\u4e86\u3002</p><p>\u5927\u4f53\u6765\u8bf4\uff0c\u963f\u683c\u62c9\u9686\u8fd9\u5757\u5730\u65b9\u4ee5\u524d\u662f\u7cbe\u7075\u738b\u56fd\uff0c\u5e76\u65e0\u4eba\u7c7b\u5c45\u4f4f\u3002\u540e\u6765\u968f\u7740\u8457\u540d\u7684\u201c\u7cbe\u7075\u5927\u64a4\u9000\u201d\uff0c\u6b64\u5730\u7684\u5927\u90e8\u5206\u7cbe\u7075\u90fd\u8fc1\u5f99\u5230\u4e86\u6d77\u5916\u7684\u6c38\u805a\u5c9b\uff0c\u4e5f\u6709\u5c0f\u90e8\u5206\u7cbe\u7075\u4e0d\u613f\u610f\u79bb\u5f00\u6545\u571f\uff0c\u4f9d\u65e7\u56fa\u5b88\u5b9a\u5c45\u3002\u518d\u5230\u540e\u6765\uff0c\u4eba\u7c7b\u738b\u56fd\u5f00\u59cb\u5d1b\u8d77\uff0c\u5927\u91cf\u7684\u4eba\u7c7b\u8fdb\u5165\u6b64\u5730\uff0c\u4e0e\u7cbe\u7075\u65cf\u6742\u5c45\u3001\u901a\u5a5a\uff0c\u8bde\u751f\u4e86\u5f88\u591a\u534a\u7cbe\u7075\u3002</p><p>\u4eba\u7c7b\u3001\u7cbe\u7075\u548c\u534a\u7cbe\u7075\u76f8\u5b89\u65e0\u4e8b\u5730\u548c\u5e73\u5171\u5904\u4e86\u4e09\u767e\u5e74\u5de6\u53f3\uff0c\u77db\u76fe\u7ec8\u4e8e\u7206\u53d1\u4e86\u3002\u8d77\u56e0\u662f\u4eba\u7c7b\u4e0d\u65ad\u7e41\u884d\u3001\u6269\u5c55\uff0c\u9700\u8981\u66f4\u591a\u7684\u5b9a\u5c45\u5730\u548c\u8d44\u6e90\uff0c\u4e8e\u662f\u5f00\u59cb\u5927\u91cf\u780d\u4f10\u68ee\u6797\uff0c\u8fd9\u60f9\u6012\u4e86\u89c6\u68ee\u6797\u4e3a\u795e\u5723\u4e4b\u5730\u7684\u7cbe\u7075\u65cf\uff0c\u6700\u7ec8\u4e00\u573a\u79cd\u65cf\u5927\u6218\u7206\u53d1\uff0c\u88ab\u79f0\u4e4b\u4e3a\u201c\u82f1\u8fbe\u5c14\u4e4b\u81c2\u6218\u5f79\u201d\u3002\u6218\u4e89\u7684\u7ed3\u679c\u662f\u548c\u5e73\uff0c\u4e00\u4e2a\u65b0\u7684\u56fd\u5bb6\u8bde\u751f\u4e86\uff0c\u7531\u4eba\u7c7b\u548c\u7cbe\u7075\u5171\u638c\u6743\u529b\uff0c\u800c\u56fd\u738b\u5219\u4e16\u4e16\u4ee3\u4ee3\u7531\u534a\u7cbe\u7075\u62c5\u4efb\uff0c\u8fd9\u5c31\u662f\u201c\u963f\u683c\u62c9\u9686\u738b\u56fd\u201d\u3002</p><p>\u4e0d\u8fc7\u738b\u4f4d\u4f20\u5230\u8fd9\u4e00\u4ee3\uff0c\u5374\u4e0d\u518d\u662f\u7531\u534a\u7cbe\u7075\u62c5\u4efb\u4e86\u3002\u4e0a\u4e00\u4ee3\u7684\u56fd\u738b\u4e3d\u5965\u5185\u9009\u62e9\u4e86\u5979\u7684\u5f1f\u5b50\u201c\u6b23\u5e03\u201d\u6765\u7ee7\u627f\u738b\u4f4d\u3002\u6b23\u5e03\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u540c\u65f6\u4e5f\u662f\u8457\u540d\u7684\u5927\u5deb\u5e08\uff0c\u81ea\u4ece\u62c5\u4efb\u56fd\u738b\u4ee5\u6765\uff0c\u5c61\u6b21\u7387\u9886\u963f\u683c\u62c9\u9686\u4eba\u6c11\u6253\u9000\u4e86\u585e\u5c14\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u5165\u4fb5\uff0c\u56e0\u6b64\u58f0\u671b\u6781\u9ad8\uff0c\u5728\u56fd\u5185\u51e0\u4e4e\u662f\u4eba\u4eba\u5d07\u62dc\u7684\u5076\u50cf\u3002</p><p>\u6b63\u5982\u738b\u5b50\u548c\u6885\u83f2\u65af\u6240\u8bf4\uff0c\u963f\u683c\u62c9\u9686\u56fd\u529b\u4e0d\u76db\uff0c\u5185\u90e8\u5e76\u65e0\u4ec0\u4e48\u6770\u51fa\u4eba\u624d\uff0c\u800c\u4e14\u5927\u90e8\u5206\u5730\u533a\u90fd\u6392\u65a5\u9b54\u6cd5\u7684\u529b\u91cf\uff0c\u65e0\u8bba\u662f\u795e\u672f\u8fd8\u662f\u5965\u672f\u3002\u4eec\u4e0d\u559c\u6b22\u9b54\u6cd5\uff0c\u8ba4\u4e3a\u9b54\u6cd5\u662f\u90aa\u6076\u4e4b\u5f92\u8150\u5316\u4ed6\u4eba\u7684\u5de5\u5177\uff0c\u800c\u7ea2\u888d\u6cd5\u5e08\u5c31\u662f\u6700\u597d\u7684\u8bc1\u660e\u2014\u2014\u5f53\u7136\uff0c\u4ed6\u4eec\u4e5f\u627f\u8ba4\u5c11\u6570\u7279\u4f8b\uff0c\u6bd4\u5982\u4ed6\u4eec\u7684\u7edf\u6cbb\u8005\u6b23\u5e03\u5973\u738b\uff0c\u603b\u662f\u7528\u9b54\u6cd5\u4fdd\u62a4\u7740\u4ed6\u4eec\u3002\u963f\u683c\u62c9\u9686\u4eba\u666e\u904d\u4fe1\u4ef0\u795e\u5723\u7684\u529b\u91cf\uff0c\u4f46\u6559\u4f1a\u5374\u4e5f\u5e76\u4e0d\u53d1\u8fbe\uff0c\u6574\u4e2a\u738b\u56fd\u53ea\u6709\u5f88\u5c11\u7684\u51e0\u5ea7\u5927\u578b\u795e\u6bbf\uff0c\u7267\u5e08\u4e5f\u5f88\u7a00\u7f3a\u3002</p><p>\u8fd9\u6b21\u7684\u76ee\u7684\u5730\u53eb\u505a\u201c\u745e\u5361\u4e4b\u8db3\u201d\uff0c\u662f\u963f\u683c\u62c9\u9686\u7684\u4e00\u5ea7\u5c0f\u57ce\uff0c\u4eba\u53e3\u4e0d\u8fc7\u4e94\u5343\uff0c\u5927\u90e8\u5206\u90fd\u662f\u534a\u7cbe\u7075\uff0c\u56db\u9762\u68ee\u6797\u73af\u7ed5\u3002\u6839\u636e\u738b\u5b50\u548c\u96c5\u83f2\u5c14\u5973\u58eb\u7684\u9884\u8a00\u9b54\u6cd5\uff0c\u4ee5\u53ca\u5176\u4ed6\u5404\u79cd\u8d44\u6599\u7684\u63a8\u6d4b\uff0c\u6b7b\u6708\u6cd5\u73e0\u5e94\u8be5\u5c31\u662f\u843d\u5230\u4e86\u8fd9\u4e2a\u5730\u65b9\u3002\u81f3\u4e8e\u5177\u4f53\u88ab\u8c01\u5f97\u5230\uff0c\u90a3\u5c31\u53ea\u80fd\u6162\u6162\u53bb\u627e\u4e86\u3002</p><p>\u4e09\u5929\u65f6\u95f4\u5f88\u5feb\u8fc7\u53bb\uff0c\u5728\u51fa\u53d1\u4e4b\u524d\uff0c\u743c\u6069\u901a\u8fc7\u738b\u5b50\uff0c\u4ece\u7ea2\u888d\u5deb\u5e08\u4f1a\u5f00\u7684\u5546\u5e97\u91cc\u5f04\u5230\u4e86\u4e0a\u767e\u5f20\u9b54\u6cd5\u5377\u8f74\u3002\u4ed6\u539f\u672c\u662f\u8003\u8651\u8981\u51e0\u6839\u9b54\u6756\u4e4b\u7c7b\uff0c\u6216\u8005\u5f04\u51e0\u4e2a\u9b54\u6cd5\u88c5\u5907\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u653e\u5f03\u4e86\u8fd9\u79cd\u60f3\u6cd5\u3002\u9b54\u6756\u548c\u88c5\u5907\u867d\u7136\u597d\uff0c\u7ec8\u7a76\u7528\u9014\u592a\u5355\u4e00\uff0c\u4e0d\u80fd\u5e94\u4ed8\u5404\u79cd\u5343\u53d8\u4e07\u5316\u7684\u5c40\u9762\u3002\u5377\u8f74\u867d\u7136\u662f\u4e00\u6b21\u6027\u6d88\u8017\u54c1\uff0c\u80dc\u5728\u79cd\u7c7b\u7e41\u591a\uff0c\u9047\u4e0a\u4ec0\u4e48\u9ebb\u70e6\u90fd\u6709\u529e\u6cd5\u5bf9\u4ed8\u3002\u8fd9\u4e0a\u767e\u5f20\u9b54\u6cd5\u5377\u8f74\uff0c\u5e02\u573a\u4ef7\u503c\u5927\u7ea6\u8d85\u8fc7\u51e0\u4e07\u91d1\u5e01\uff0c\u5c31\u7b97\u662f\u8bba\u6210\u672c\u4ef7\u5e94\u8be5\u4e5f\u6709\u4e0a\u5343\u4e86\uff0c\u743c\u6069\u8fd8\u662f\u7b2c\u4e00\u6b21\u8fd9\u4e48\u5962\u4f88\uff0c\u53cd\u6b63\u7ea2\u888d\u5deb\u5e08\u4f1a\u4ed8\u8d26\uff0c\u8fd9\u4fbf\u5b9c\u4e0d\u5360\u767d\u4e0d\u5360\u3002</p><p>\u6885\u83f2\u65af\u6ca1\u4ec0\u4e48\u53ef\u51c6\u5907\u7684\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u5236\u9020\u7684\u90a3\u4e9b\u4e1c\u897f\u5979\u5927\u591a\u7528\u4e0d\u4e86\uff0c\u4e5f\u4e0d\u559c\u6b22\u7528\u3002\u81f3\u4e8e\u51db\uff0c\u5219\u662f\u4e00\u53e3\u6c14\u7d22\u8981\u4e86\u5341\u6839\u706b\u7403\u9b54\u6756\uff0c\u628a\u743c\u6069\u90fd\u7ed9\u5413\u5230\u4e86\uff0c\u201c\u4f60\u8981\u8fd9\u4e48\u591a\u706b\u7403\u9b54\u6756\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u51c6\u5907\u7528\u6765\u6253\u67b6\u554a\uff0c\u591a\u51c6\u5907\u70b9\u53c8\u4e0d\u662f\u574f\u4e8b\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u6211\u662f\u8bf4\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53ea\u8981\u8fd9\u4e00\u79cd\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u5c31\u559c\u6b22\u8fd9\u4e00\u79cd\uff0c\u4f60\u6709\u610f\u89c1\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u6ca1\u6709\u3002\u201d</p><p>\u5341\u6839\u706b\u7403\u9b54\u6756\uff0c\u6bcf\u6839\u80fd\u53d1\u5c04\u4e94\u5341\u53d1\u706b\u7403\u672f\uff0c\u65e0\u9700\u51c6\u5907\u65e0\u9700\u8bf5\u5492\uff0c\u5df2\u7ecf\u5c5e\u4e8e\u975e\u5e38\u6602\u8d35\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5176\u4ef7\u683c\u53ea\u6015\u8fd8\u8d85\u8fc7\u743c\u6069\u90a3\u4e0a\u767e\u5f20\u5377\u8f74\uff0c\u8fd9\u5c0f\u9b54\u5973\u679c\u7136\u8d2a\u5fc3\u5f97\u5f88\u3002\u51db\u540c\u6837\u4e5f\u6709\u6b21\u5143\u888b\uff0c\u76f4\u63a5\u628a\u5341\u6839\u706b\u7403\u9b54\u6756\u90fd\u4e22\u4e86\u8fdb\u53bb\uff0c\u5979\u8fd9\u6b21\u6ca1\u518d\u7a7f\u90a3\u8eab\u8d85\u77ed\u88d9\uff0c\u4e0a\u8eab\u662f\u6d45\u7ea2\u8272\u886c\u886b\uff0c\u4e0b\u8eab\u9ed1\u8272\u957f\u88e4\uff0c\u5916\u9762\u5957\u7740\u4e00\u4ef6\u7ea2\u8272\u5927\u8863\uff0c\u8fd9\u8eab\u6253\u626e\u8ba9\u743c\u6069\u9887\u89c9\u60ca\u8bb6\uff0c\u56e0\u4e3a\u73b0\u5728\u5df2\u7ecf\u662f\u590f\u5929\u4e86\uff0c\u6c14\u5019\u708e\u70ed\uff0c\u5979\u7a7f\u8fd9\u4e48\u591a\u96be\u9053\u4e0d\u89c9\u5f97\u70ed\u4e48\u3002</p><p>\u201c\u6ca1\u4e8b\uff0c\u522b\u7ba1\u5979\uff0c\u5979\u662f\u51b7\u8840\u52a8\u7269\uff0c\u6ca1\u6709\u51b7\u70ed\u6982\u5ff5\u7684\u3002\u201d\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u8bf4\u3002</p><p>\u201c\u5514\u5514\uff0c\u827e\u5f25\u8587\uff0c\u80cc\u540e\u8bf4\u4eba\u574f\u8bdd\u662f\u5f88\u4e0d\u597d\u7684\u884c\u4e3a\u5462\uff0c\u4e0d\u7b26\u5408\u4f60\u5723\u6b66\u58eb\u7684\u8eab\u4efd\u5427\uff0c\u201d\u5c0f\u9b54\u5973\u8f6c\u8fc7\u8138\u6765\uff0c\u201c\u8fd8\u662f\u8bf4\uff0c\u6709\u4e86\u60c5\u4eba\u5c31\u628a\u8001\u670b\u53cb\u4e22\u5230\u4e00\u8fb9\u4e0d\u7ba1\u4e86\u3002\u201d</p><p>\u201c\u80cc\u540e\u8bf4\u4eba\u662f\u4e0d\u597d\uff0c\u6240\u4ee5\u6211\u8fd9\u662f\u5728\u5f53\u9762\u8bf4\u554a\u3002\u201d\u6885\u83f2\u65af\u51b7\u9759\u5730\u56de\u7b54\u3002</p><p>\u201c\u2026\u2026\u90a3\u4e2a\uff0c\u743c\u6069\uff0c\u201d\u51db\u8f6c\u79fb\u4e86\u76ee\u6807\uff0c\u201c\u65e2\u7136\u6211\u4eec\u8fd9\u6b21\u8981\u51fa\u53d1\u4e86\uff0c\u4e5f\u5e94\u8be5\u5148\u628a\u961f\u957f\u662f\u8c01\u7ed9\u5b9a\u4e0b\u6765\u5427\uff0c\u6211\u770b\u5c31\u7531\u6211\u6765\u5f53\u597d\u4e86\uff0c\u4f60\u6ca1\u610f\u89c1\u5427\u3002\u201d</p><p>\u5f53\u7136\u6709\u610f\u89c1\uff0c\u201c\u7b49\u7b49\uff0c\u8fd9\u6b21\u4efb\u52a1\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u4f60\u662f\u6211\u7684\u52a9\u624b\uff0c\u4e3a\u4ec0\u4e48\u4f60\u53cd\u800c\u6210\u4e86\u961f\u957f\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u4e0d\u9002\u5408\u5f53\u961f\u957f\u554a\uff0c\u6240\u4ee5\u6211\u53ea\u597d\u52c9\u4e3a\u5176\u96be\u5730\u5e2e\u5fd9\u4e86\u3002\u201d</p><p>\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u9002\u5408\u5f53\u961f\u957f\uff1f\u201d</p><p>\u201c\u554a\u5440\uff0c\u8fd9\u4e48\u7b80\u5355\u7684\u9053\u7406\u4f60\u90fd\u4e0d\u61c2\u4e48\uff0c\u4f60\u770b\uff0c\u6211\u4eec\u8981\u53bb\u7684\u5730\u65b9\u662f\u963f\u683c\u62c9\u9686\uff0c\u90a3\u5730\u65b9\u5f88\u6392\u65a5\u5deb\u5e08\u7684\u3002\u6211\u4eec\u8fd9\u6b21\u53bb\u662f\u8981\u627e\u4e1c\u897f\uff0c\u80af\u5b9a\u8981\u548c\u4eba\u6253\u542c\uff0c\u8981\u4ea4\u6d89\u8c08\u8bdd\uff0c\u8981\u63a2\u542c\u6d88\u606f\uff0c\u5982\u679c\u7531\u4f60\u51fa\u9762\u7684\u8bdd\uff0c\u4ed6\u4eec\u4e00\u770b\u4f60\u662f\u4e2a\u5deb\u5e08\uff0c\u80af\u5b9a\u7406\u90fd\u4e0d\u7406\u4f60\uff0c\u8fd9\u6b21\u4efb\u52a1\u4e0d\u5c31\u6ca1\u6cd5\u5b8c\u6210\u4e86\u561b\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u4e5f\u662f\u4e2a\u5deb\u5e08\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u4f46\u6211\u53c8\u4e0d\u9700\u8981\u7a7f\u5deb\u5e08\u888d\uff0c\u8c01\u77e5\u9053\u6211\u662f\u4e2a\u5deb\u5e08\uff1f\u201d\u5979\u751c\u751c\u5730\u7b11\u7740\uff0c\u201c\u800c\u4e14\u50cf\u6211\u8fd9\u6837\u7684\u6f02\u4eae\u5973\u5b69\uff0c\u51fa\u9762\u548c\u4eba\u4ea4\u6d89\u7684\u8bdd\uff0c\u80af\u5b9a\u4f1a\u5f97\u5230\u989d\u5916\u4f18\u5f85\u7684\u5462\uff0c\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4f46\u4ed6\u4e0d\u53ef\u80fd\u8ba9\u51db\u5f53\u961f\u957f\u3002\u5982\u679c\u627f\u8ba4\u5979\u662f\u961f\u957f\uff0c\u90a3\u4ee5\u540e\u50cf\u884c\u7a0b\u5b89\u6392\u3001\u4f5c\u6218\u8ba1\u5212\u3001\u51fa\u9762\u4ea4\u6d89\u7b49\u4e8b\u60c5\u5c31\u90fd\u7531\u5979\u53bb\u8d1f\u8d23\u4e86\uff0c\u4e14\u4e0d\u8bf4\u743c\u6069\u77e5\u9053\u5979\u6709\u53ef\u80fd\u662f\u6f5c\u5728\u7684\u654c\u4eba\uff0c\u5c31\u7b97\u6ca1\u8fd9\u56e0\u7d20\uff0c\u4ed6\u4e5f\u4e0d\u613f\u610f\u542c\u8fd9\u5c0f\u4e2b\u5934\u6307\u6325\u3002</p><p>\u4e0d\u8fc7\u5979\u8bf4\u7684\u8bdd\uff0c\u5012\u4e5f\u4e0d\u65e0\u9053\u7406\uff0c\u743c\u6069\u5f53\u961f\u957f\u786e\u5b9e\u4e0d\u9002\u5408\u3002</p><p>\u201c\u90a3\u4e5f\u7b80\u5355\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5c31\u7531\u827e\u5f25\u8587\u6765\u5f53\u961f\u957f\u597d\u4e86\uff0c\u5bf9\u5427\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8f6c\u8138\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u662f\u5723\u6b66\u58eb\uff0c\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u5f53\u961f\u957f\u539f\u672c\u5c31\u6700\u5408\u9002\uff0c\u53c8\u4e0d\u662f\u5deb\u5e08\uff0c\u4e0d\u4f1a\u88ab\u4eba\u6392\u65a5\uff0c\u800c\u4e14\u8981\u8bba\u51fa\u9762\u4ea4\u6d89\u4e4b\u7c7b\u7684\u4e8b\u60c5\uff0c\u80af\u5b9a\u6bd4\u90a3\u4e2a\u5c0f\u4e2b\u5934\u64c5\u957f\u591a\u4e86\u3002\u201d</p><p>\u201c\u5582\uff0c\u4f60\u8bf4\u8c01\u662f\u5c0f\u4e2b\u5934\u2026\u2026\u201d</p><p>\u201c\u4e0d\u8fc7\u827e\u5f25\u8587\u5723\u6b66\u58eb\u7684\u8eab\u4efd\u4e5f\u4e0d\u597d\u663e\u9732\uff0c\u201d\u743c\u6069\u4e0d\u7406\u776c\u5979\uff0c\u201c\u592a\u5f15\u4eba\u6ce8\u610f\u4e86\u3002\u90a3\u4e48\u5c31\u8fd9\u6837\uff0c\u8fd9\u6b21\u6211\u4eec\u5c31\u4f5c\u4e3a\u65c5\u6e38\u8005\u3002\u827e\u5f25\u8587\u662f\u51fa\u95e8\u65c5\u6e38\u7684\u8d35\u65cf\u5c0f\u59d0\uff0c\u6211\u662f\u5979\u7684\u5deb\u5e08\u62a4\u536b\uff0c\u8fd9\u6837\u5c31\u6ca1\u95ee\u9898\u4e86\u3002\u201d</p><p>\u201c\u90a3\u6211\u5462\u3002\u201d\u51db\u95ee\u3002</p><p>\u201c\u4f60\u4e48\uff0c\u5c31\u4f5c\u4e3a\u827e\u5f25\u8587\u7684\u4f8d\u5973\u2026\u2026\u7b97\u4e86\uff0c\u201d\u770b\u89c1\u51db\u9762\u8272\u4e0d\u5584\uff0c\u743c\u6069\u8fde\u5fd9\u6539\u53e3\uff0c\u201c\u4f60\u5c31\u5f53\u5979\u59b9\u59b9\u5427\u3002\u201d</p><p>\u201c\u5582\u5582\uff0c\u6211\u8bf4\u743c\u6069\uff0c\u4f60\u662f\u4e0d\u662f\u641e\u9519\u4e86\u4ef6\u4e8b\u60c5\uff0c\u6211\u53ef\u662f\u6bd4\u827e\u5f25\u8587\u5927\u7684\uff0c\u6574\u6574\u5927\u4e24\u5c81\u5462\u3002\u201d</p><p>\u201c\u4e00\u5c81\u534a\u3002\u201d\u6885\u83f2\u65af\u7ea0\u6b63\u3002</p><p>\u201c\u5c31\u7b97\u4e00\u5c81\u534a\uff0c\u603b\u4e4b\u4e5f\u6ca1\u9053\u7406\u8ba9\u6211\u505a\u59b9\u59b9\u554a\uff0c\u201d\u51db\u6c14\u9f13\u9f13\u7684\uff0c\u201c\u660e\u660e\u5e94\u8be5\u6211\u662f\u59d0\u59d0\uff0c\u5979\u662f\u59b9\u59b9\u624d\u5bf9\u3002\u201d</p><p>\u201c\u4ece\u5e74\u9f84\u4e0a\u6765\u8bf4\uff0c\u597d\u50cf\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u7136\u800c\u8bdd\u8bf4\u56de\u6765\uff0c\u770b\u5230\u4f60\u4eec\u4e24\u4e2a\uff0c\u6709\u8c01\u4f1a\u76f8\u4fe1\u4f60\u662f\u59d0\u59d0\u5462\uff1f\u201d</p><p>\u201c\u6211\u54ea\u91cc\u4e0d\u50cf\u5979\u59d0\u59d0\u4e86\uff1f\u201d</p><p>\u201c\u54ea\u91cc\u90fd\u4e0d\u50cf\u554a\uff0c\u4f60\u6c14\u8d28\u6ca1\u5979\u6210\u719f\uff0c\u4e2a\u5934\u6ca1\u5979\u9ad8\uff0c\u8eab\u6750\u6ca1\u5979\u597d\u2026\u2026\u201d</p><p>\u201c\u6211\u80f8\u6bd4\u5979\u5927\uff01\u201d</p><p>\u743c\u6069\u54d1\u7136\uff0c\u540e\u9762\u672c\u6765\u60f3\u8bf4\u7684\u8bdd\u987f\u65f6\u5168\u7ed9\u5fd8\u4e86\u3002\u51db\u4f3c\u4e4e\u4e5f\u88ab\u81ea\u5df1\u521a\u624d\u8131\u53e3\u800c\u51fa\u7684\u8bdd\u7ed9\u5413\u5230\u4e86\uff0c\u4e24\u4e2a\u4eba\u4e92\u76f8\u76ee\u77aa\u53e3\u5446\u5730\u770b\u7740\uff0c\u6700\u540e\u662f\u6885\u83f2\u65af\u6253\u7834\u4e86\u5e73\u9759\u3002\u201c\u597d\u4e86\uff0c\u5c31\u8fd9\u6837\u51b3\u5b9a\uff0c\u6211\u5f53\u961f\u957f\uff0c\u884c\u7a0b\u5b89\u6392\u548c\u51fa\u9762\u4ea4\u6d89\u7684\u4e8b\u60c5\u90fd\u7531\u6211\u6765\u8d1f\u8d23\u3002\u5bf9\u5916\u6211\u662f\u6765\u65c5\u6e38\u7684\u8d35\u65cf\u5c0f\u59d0\uff0c\u743c\u6069\u662f\u6211\u7684\u5deb\u5e08\u62a4\u536b\uff0c\u51db\u662f\u6211\u7684\u59b9\u59b9\uff0c\u5c31\u8fd9\u6837\u3002\u201d</p><p>\u201c\u6211\u624d\u4e0d\u8981\u5f53\u4f60\u59b9\u59b9\uff01\u201d\u51db\u6297\u8bae\uff0c\u201c\u6211\u660e\u660e\u6bd4\u4f60\u5927\uff0c\u51ed\u4ec0\u4e48\u8981\u5f53\u59b9\u59b9\u3002\u201d</p><p>\u201c\u54e6\uff0c\u8981\u6bd4\u6211\u5927\u4e48\uff0c\u90a3\u5c31\u5f53\u963f\u59e8\u597d\u4e86\uff0c\u201d\u6885\u83f2\u65af\u9976\u6709\u5174\u81f4\u5730\u770b\u7740\u51db\uff0c\u201c\u8fd9\u4e3b\u610f\u600e\u4e48\u6837\uff1f\u201d</p><p>\u201c\u5c31\u8fd9\u6837\u5427\uff0c\u201d\u743c\u6069\u63d2\u5634\uff0c\u201c\u4e09\u4e2a\u4eba\uff0c\u4e24\u4eba\u8d5e\u540c\uff0c\u4e00\u4eba\u53cd\u5bf9\uff0c\u65b9\u6848\u901a\u8fc7\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4eec\u4e24\u4e2a\u8054\u5408\u8d77\u6765\u6b3a\u8d1f\u6211\uff01\u201d</p><p>\u201c\u770b\uff0c\u8fd9\u6837\u4e00\u6492\u5a07\u5c31\u66f4\u50cf\u5c0f\u59b9\u59b9\u4e86\uff0c\u201d\u6885\u83f2\u65af\u5f88\u6ee1\u610f\u5730\u70b9\u5934\uff0c\u201c\u6211\u4ee5\u524d\u5c31\u8bf4\u8fc7\uff0c\u4f60\u5f88\u6709\u8868\u6f14\u5929\u8d4b\u7684\uff0c\u201d\u5979\u5fae\u7b11\u7740\uff0c\u8d70\u5230\u65c1\u8fb9\u6478\u6478\u51db\u7684\u5934\u53d1\uff0c\u201c\u6765\uff0c\u8d70\u5427\uff0c\u5c0f\u59b9\u59b9\u3002\u201d</p></article>"}}}]);
