(window.webpackJsonp=window.webpackJsonp||[]).push([[1405],{1428:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u56db\u8282 \u8981\u5e2e\u5fd9\u63a8\u5012\u5417",content:"<article><h2>\u7b2c\u4e09\u5341\u56db\u8282 \u8981\u5e2e\u5fd9\u63a8\u5012\u5417</h2><p>\u623f\u95f4\u91cc\u5e73\u9759\u4e0b\u6765\uff0c\u53ea\u542c\u5230\u51db\u7684\u5598\u606f\u58f0\u6e10\u6e10\u5e73\u590d\uff0c\u5979\u8d77\u8eab\u5c06\u6240\u6709\u7684\u5b9d\u77f3\u6536\u8fdb\u90a3\u4e2a\u7ea2\u8272\u5c0f\u53e3\u888b\u91cc\uff0c\u7136\u540e\u8d64\u88f8\u7740\u8eab\u4f53\u4e0b\u5e8a\uff0c\u8d70\u5230\u6d74\u5ba4\u91cc\u53bb\u4e86\uff0c\u5f88\u5feb\u4f20\u6765\u54d7\u54d7\u7684\u6c34\u58f0\u3002\u743c\u6069\u6084\u6084\u9000\u540e\uff0c\u8f7b\u624b\u8f7b\u811a\u5730\u8d70\u56de\u81ea\u5df1\u623f\u95f4\uff0c\u4e00\u65f6\u8fd8\u6709\u4e9b\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u521a\u624d\u6240\u770b\u5230\u7684\u60c5\u666f\u3002</p><p>\u5b9d\u77f3\u9b54\u6cd5\u662f\u9700\u8981\u5deb\u5e08\u5c06\u51c6\u5907\u597d\u7684\u6cd5\u672f\u704c\u6ce8\u8fdb\u5b9d\u77f3\u91cc\uff0c\u4f46\u51db\u521a\u624d\u7684\u4e3e\u52a8\uff0c\u5b8c\u5168\u4e0d\u66fe\u65bd\u6cd5\u8bf5\u5492\uff0c\u660e\u663e\u4e0d\u50cf\u662f\u5728\u704c\u6ce8\u9b54\u6cd5\uff0c\u5e94\u8be5\u662f\u5728\u7528\u8fd9\u79cd\u65b9\u5f0f\u5f3a\u5316\u63d0\u5347\u5b9d\u77f3\u7684\u589e\u5e45\u5a01\u529b\u5427\u3002</p><p>\u7279\u5b9a\u5c5e\u6027\u7684\u5b9d\u77f3\uff0c\u786e\u5b9e\u662f\u53ef\u4ee5\u5f3a\u5316\u76f8\u5e94\u9b54\u6cd5\u7684\u5a01\u529b\u7684\uff0c\u8fd9\u70b9\u8fde\u743c\u6069\u90fd\u77e5\u9053\u3002\u7ea2\u5b9d\u77f3\u6216\u8005\u7ea2\u9506\u77f3\uff0c\u9576\u5d4c\u5728\u6cd5\u6756\u4e0a\u80fd\u589e\u52a0\u706b\u7130\u9b54\u6cd5\u7684\u5a01\u529b\uff0c\u5e1d\u738b\u4e4b\u6cea\u5b9d\u77f3\u53ef\u4ee5\u7528\u6765\u5f3a\u5316\u9884\u8a00\u9b54\u6cd5\u7684\u6548\u679c\uff0c\u84dd\u5b9d\u77f3\u53ef\u4ee5\u5f3a\u5316\u53ec\u5524\u9b54\u6cd5\u7684\u6548\u529b\uff0c\u8bf8\u5982\u6b64\u7c7b\u4e0d\u4e00\u800c\u8db3\uff0c\u53ea\u662f\u6ca1\u4eba\u80fd\u5982\u51db\u4e00\u822c\u76f4\u63a5\u628a\u9b54\u6cd5\u704c\u6ce8\u8fdb\u5b9d\u77f3\u7f62\u4e86\u3002\u743c\u6069\u5176\u5b9e\u4e5f\u4e00\u76f4\u7591\u60d1\uff0c\u56e0\u4e3a\u51db\u7684\u706b\u7403\u7838\u51fa\u6765\uff0c\u8f83\u4e4b\u5bfb\u5e38\u5deb\u5e08\u81f3\u5c11\u8981\u5f3a\u4e0a\u4e09\u56db\u500d\uff0c\u5c31\u7b97\u7528\u5b9d\u77f3\u589e\u5e45\u5e94\u8be5\u4e5f\u4e0d\u81f3\u4e8e\u5230\u8fd9\u79cd\u53d8\u6001\u7a0b\u5ea6\uff0c\u5982\u4eca\u6765\u770b\uff0c\u539f\u6765\u662f\u6709\u989d\u5916\u7684\u5f3a\u5316\u65b9\u6cd5\u2026\u2026</p><p>\u201c\u9f99\u8109\u8005\uff0c\u201d\u743c\u6069\u9ed8\u60f3\u7740\uff0c\u201c\u662f\u7ea2\u9f99\u5427\u3002\u201d</p><p>\u539f\u672c\u5728\u5916\u5954\u6ce2\u4e86\u4e00\u5929\uff0c\u7cbe\u795e\u9887\u4e3a\u5026\u6020\uff0c\u60f3\u56de\u6765\u65e9\u65e9\u4f11\u606f\uff0c\u5982\u4eca\u610f\u5916\u53d1\u73b0\u4e86\u51db\u7684\u79d8\u5bc6\uff0c\u53c8\u6b23\u8d4f\u5230\u9999\u8273\u666f\u8272\uff0c\u4e00\u65f6\u5012\u7761\u4e0d\u7740\u4e86\uff0c\u7d22\u6027\u6253\u5f00\u9b54\u6cd5\u4e66\uff0c\u5f00\u59cb\u7814\u8bfb\u65b0\u5b66\u4f1a\u7684\u51e0\u4e2a\u6cd5\u672f\u3002\u8fd9\u4e9b\u5929\u6765\uff0c\u738b\u5b50\u4f20\u6388\u4e86\u9006\u8f6c\u7206\u70b8\uff0c\u53c8\u4ece\u51db\u7684\u9b54\u6cd5\u4e66\u91cc\u6284\u5f55\u6765\u4e86\u9ad8\u7b49\u5deb\u5e08\u62a4\u7532\u548c\u706b\u4e2d\u8df3\u8dc3\uff0c\u4f46\u4e00\u76f4\u5728\u8def\u4e0a\u5954\u6ce2\uff0c\u4e5f\u65e0\u6687\u9759\u5fc3\u53bb\u5b66\u3002\u5982\u4eca\u603b\u7b97\u662f\u6682\u65f6\u5b9a\u4f4f\u4e0b\u6765\uff0c\u770b\u76ee\u524d\u60c5\u5f62\uff0c\u53ea\u6015\u5f88\u8981\u5728\u8fd9\u5c0f\u57ce\u91cc\u5f85\u4e0a\u4e00\u6bb5\u65f6\u95f4\uff0c\u6b63\u597d\u501f\u6b64\u673a\u4f1a\u6e29\u4e60\u3002</p><p>\u9b54\u6cd5\u4e5f\u50cf\u8bfb\u4e66\uff0c\u957f\u65f6\u95f4\u4e0d\u78b0\u5c31\u4f1a\u751f\u758f\u7684\u3002</p><p>\u8fc7\u4e86\u5927\u7ea6\u4e24\u5c0f\u65f6\uff0c\u6885\u83f2\u65af\u4e5f\u4ece\u5916\u9762\u56de\u6765\uff0c\u5f7c\u6b64\u95ee\u8d77\uff0c\u90fd\u662f\u4e00\u65e0\u6240\u83b7\u3002\u8fd9\u51e0\u5929\u6765\uff0c\u6885\u83f2\u65af\u51e0\u4e4e\u5df2\u7ecf\u628a\u57ce\u4e2d\u6240\u6709\u5c45\u6c11\u90fd\u8c03\u67e5\u4e86\u4e00\u904d\uff0c\u6ca1\u53d1\u73b0\u534a\u70b9\u53ef\u7591\u8ff9\u8c61\u3002\u743c\u6069\u548c\u51db\u4e5f\u662f\u4e00\u6837\uff0c\u4e09\u4eba\u90fd\u4e0d\u7981\u6709\u4e9b\u4e27\u6c14\u3002</p><p>\u201c\u4eca\u5929\u662f\u661f\u671f\u516d\uff0c\u201d\u743c\u6069\u6700\u540e\u8bf4\uff0c\u201c\u518d\u627e\u56db\u5929\uff0c\u5230\u8fd9\u4e2a\u5468\u672b\u3002\u5982\u679c\u8fd8\u6ca1\u7ed3\u679c\uff0c\u90a3\u4e5f\u53ea\u597d\u5148\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4e5f\u53ea\u80fd\u5982\u6b64\u4e86\u3002</p><p>\u7b2c\u4e8c\u5929\uff0c\u4f9d\u65e7\u6309\u7167\u8ba1\u5212\uff0c\u6885\u83f2\u65af\u7559\u5728\u57ce\u5185\uff0c\u743c\u6069\u548c\u51db\u53bb\u57ce\u5916\u641c\u5bfb\u3002\u548c\u524d\u51e0\u5929\u4e00\u6837\uff0c\u53c8\u662f\u5f92\u52b3\u65e0\u529f\uff0c\u508d\u665a\u6253\u9053\u56de\u57ce\u7684\u65f6\u5019\uff0c\u5728\u8def\u4e0a\u5012\u662f\u610f\u5916\u5730\u78b0\u89c1\u4e86\u51db\u3002</p><p>\u201c\u600e\u4e48\u6837\uff0c\u51db\u3002\u201d</p><p>\u51db\u6447\u6447\u5934\uff0c\u201c\u4e00\u65e0\u6240\u83b7\u3002\u201d</p><p>\u4e24\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u968f\u53e3\u95f2\u8c08\u7740\uff0c\u743c\u6069\u60f3\u8d77\u6628\u665a\u770b\u5230\u7684\u573a\u666f\uff0c\u4fbf\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\u3002\u51db\u5f88\u5feb\u53d1\u89c9\u4e86\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u4f60\u4eca\u5929\u597d\u50cf\u9b42\u4e0d\u5b88\u820d\u4f3c\u7684\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6ca1\u4e8b\uff0c\u5927\u6982\u6709\u70b9\u7d2f\u4e86\u3002\u201d</p><p>\u743c\u6069\u6577\u884d\u7740\uff0c\u4e00\u8fb9\u5077\u770b\u5979\u7684\u80f8\uff0c\u786e\u5b9e\u5f88\u5927\uff0c\u800c\u4e14\u56e0\u4e3a\u5e74\u8f7b\uff0c\u6b63\u9a84\u50b2\u5730\u633a\u7acb\u7740\uff0c\u5b8c\u5168\u65e0\u89c6\u5730\u5fc3\u5f15\u529b\uff0c\u5c31\u50cf\u4e00\u5bf9\u7f8e\u5473\u7684\u6c34\u679c\uff0c\u8ba9\u4eba\u770b\u89c1\u5c31\u5fcd\u4e0d\u4f4f\u60f3\u6251\u4e0a\u53bb\u54ac\u4e00\u53e3\u3002\u201c\u5fcd\u4f4f\u5fcd\u4f4f\uff01\u201d\u4ed6\u5728\u5fc3\u91cc\u544a\u8beb\u7740\u81ea\u5df1\uff0c\u867d\u8bf4\u8fd9\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u8d70\u5728\u9762\u524d\uff0c\u662f\u4e2a\u7537\u4eba\u5c31\u60f3\u63a8\u5012\uff0c\u4f46\u5979\u662f\u6885\u83f2\u65af\u7684\u597d\u53cb\uff0c\u6709\u4e9b\u4e8b\u60c5\u8fd8\u662f\u60f3\u60f3\u5c31\u7b97\u4e86\uff0c\u522b\u5230\u65f6\u5019\u5f04\u5f97\u5927\u5bb6\u90fd\u96be\u582a\u3002</p><p>\u51db\u5012\u662f\u4e00\u526f\u7cbe\u795e\u6296\u64de\u7684\u6837\u5b50\uff0c\u5168\u7136\u4e0d\u50cf\u662f\u5728\u68ee\u6797\u91cc\u641c\u5bfb\u4e86\u4e00\u5929\u7684\u4eba\uff0c\u9f99\u8109\u8005\u679c\u7136\u4e0d\u540c\u5bfb\u5e38\u3002\u201c\u554a\uff0c\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u201d\u5979\u7a81\u7136\u8f6c\u8fc7\u8138\u6765\uff0c\u201c\u4e0a\u6b21\u4f60\u6551\u6211\uff0c\u6211\u8fd8\u6ca1\u611f\u8c22\u4f60\u5462\u3002\u201d</p><p>\u201c\u55ef\uff1f\u54e6\uff0c\u90a3\u6b21\u554a\uff0c\u53cd\u6b63\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u3002\u201d</p><p>\u201c\u4e0d\u884c\u4e0d\u884c\uff0c\u201d\u51db\u4e49\u6b63\u8bcd\u4e25\u5730\u8bf4\uff0c\u201c\u53d7\u4eba\u6069\u60e0\uff0c\u5c31\u8981\u62a5\u7b54\uff0c\u5426\u5219\u6211\u5fc3\u91cc\u4f1a\u5f88\u4e0d\u5b89\u7684\u3002\u201d</p><p>\u662f\u4e48\u2026\u2026\u4e3a\u4ec0\u4e48\u6211\u603b\u89c9\u5f97\u4f60\u597d\u50cf\u53e6\u6709\u4f01\u56fe\u4f3c\u7684\uff1f</p><p>\u201c\u54e6\uff0c\u90a3\u5c31\u9001\u51e0\u9897\u5b9d\u77f3\u7ed9\u6211\u5427\uff0c\u201d\u743c\u6069\u968f\u53e3\u8bf4\uff0c\u201c\u6211\u4e00\u5411\u90fd\u5f88\u7f3a\u94b1\u3002\u201d</p><p>\u201c\u90a3\u600e\u4e48\u884c\uff0c\u8fd9\u4e9b\u5b9d\u77f3\u662f\u6211\u7528\u6765\u65bd\u6cd5\u7684\u9053\u5177\uff0c\u4f60\u4f1a\u628a\u4f60\u7684\u9b54\u6cd5\u4e66\u968f\u4fbf\u9001\u7ed9\u4eba\u5417\uff1f\u201d</p><p>\u65bd\u6cd5\u9053\u5177\u662f\u6ca1\u9519\uff0c\u4f46\u6211\u53ef\u4e0d\u4f1a\u5077\u5077\u81ea\u6e0e\u7136\u540e\u5c04\u5728\u9b54\u6cd5\u4e66\u4e0a\u554a\u2026\u2026</p><p>\u201c\u5582\u5582\uff0c\u4f60\u7b11\u5f97\u90a3\u4e48\u6deb\u90aa\u505a\u4ec0\u4e48\uff0c\u201d\u51db\u77aa\u7740\u743c\u6069\uff0c\u201c\u80af\u5b9a\u5728\u6ca1\u60f3\u597d\u4e8b\u60c5\u5427\u3002\u201d</p><p>\u201c\u5514\uff0c\u6211\u89c9\u5f97\u662f\u5f88\u597d\u7684\u4e8b\u60c5\u554a\u3002\u597d\u4e86\u597d\u4e86\uff0c\u51db\uff0c\u4f60\u8981\u600e\u4e48\u62a5\u7b54\u6211\u5c31\u76f4\u63a5\u8bf4\u5427\uff0c\u5c31\u7b97\u4ee5\u8eab\u76f8\u8bb8\u6211\u90fd\u4f1a\u63a5\u53d7\u7684\u3002\u201d</p><p>\u201c\u54e6\u54e6\uff0c\u6211\u5012\u662f\u4e0d\u4ecb\u610f\u5462\uff0c\u201d\u51db\u7b11\u772f\u772f\u7684\uff0c\u201c\u4e0d\u8fc7\u5982\u679c\u8ba9\u827e\u5f25\u8587\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u4f60\u731c\u5979\u4f1a\u4e0d\u4f1a\u63d0\u5251\u6765\u780d\u4f60\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u827e\u5f25\u8587\u5f88\u4e56\u7684\uff0c\u54ea\u6709\u4f60\u8fd9\u4e48\u91ce\u86ee\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u5f88\u4e56\uff0c\u5c31\u662f\u4e0d\u80af\u771f\u548c\u4f60\u505a\u662f\u5427\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u201c\u4f60\u662f\u77e5\u9053\u539f\u56e0\u7684\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6211\u4eec\u53ef\u662f\u597d\u670b\u53cb\u554a\uff0c\u5979\u6709\u4ec0\u4e48\u79d8\u5bc6\u6211\u4e0d\u6e05\u695a\u7684\uff1f\u201d\u51db\u795e\u6c14\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u4e3a\u4ec0\u4e48\uff0c\u65e2\u7136\u5979\u81ea\u5df1\u6ca1\u544a\u8bc9\u4f60\uff0c\u90a3\u5c31\u8bf4\u660e\u6682\u65f6\u8fd8\u4e0d\u60f3\u8ba9\u4f60\u77e5\u9053\uff0c\u6216\u8005\u8fd8\u6ca1\u505a\u597d\u5fc3\u7406\u51c6\u5907\u3002\u6211\u4f5c\u4e3a\u670b\u53cb\uff0c\u5f53\u7136\u8981\u66ff\u5979\u4fdd\u5b88\u79d8\u5bc6\u3002\u201d</p><p>\u201c\u884c\u884c\u884c\uff0c\u201d\u743c\u6069\u6446\u6446\u624b\uff0c\u201c\u90a3\u4f60\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\uff0c\u8bf4\u5427\u3002\u201d</p><p>\u201c\u522b\u8fd9\u4e48\u4e0d\u8010\u70e6\u561b\uff0c\u6211\u53ef\u662f\u771f\u5fc3\u8bda\u610f\u5730\u60f3\u5e2e\u4f60\u5fd9\u5462\u3002\u743c\u6069\uff0c\u4f60\u603b\u4e0d\u4f1a\u5c31\u60f3\u4e00\u76f4\u8fd9\u4e48\u4e0b\u53bb\u5427\uff0c\u4f60\u4eec\u5c31\u8fd9\u6837\u2026\u2026\u7528\u90a3\u79cd\u65b9\u5f0f\u89e3\u51b3\uff1f\u201d</p><p>\u201c\u6211\u8bf4\uff0c\u51db\u5c0f\u59d0\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u548c\u4f60\u8ba8\u8bba\u8fd9\u79cd\u5947\u602a\u7684\u95ee\u9898\u554a\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u53ef\u4ee5\u5e2e\u4f60\u89e3\u51b3\u8fd9\u4e2a\u9ebb\u70e6\u54e6\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u8bf4\u4e86\u5979\u4e0d\u80fd\u2026\u2026\u201d</p><p>\u201c\u5582\uff0c\u522b\u628a\u6211\u5f53\u4ec0\u4e48\u90fd\u4e0d\u61c2\u7684\u5c0f\u5b69\u5b50\u554a\uff0c\u201d\u51db\u6297\u8bae\uff0c\u201c\u5973\u4eba\u53c8\u4e0d\u662f\u9664\u4e86\u90a3\u91cc\u5c31\u6ca1\u522b\u7684\u5730\u65b9\u53ef\u4ee5\u7528\u6765\u505a\u7231\u4e86\u3002\u201d</p><p>\u743c\u6069\u89c9\u5f97\u81ea\u5df1\u6709\u79cd\u60f3\u4ec6\u8857\u7684\u611f\u89c9\uff0c\u201c\u51db\u5c0f\u59d0\uff0c\u4f60\u8bf4\u8bdd\u4e5f\u672a\u514d\u592a\u76f4\u767d\u4e86\u5427\uff0c\u8fd9\u662f\u4e00\u4f4d\u50cf\u4f60\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u5e94\u8be5\u8bf4\u7684\u8bdd\u5417\uff1f\u201d</p><p>\u201c\u6211\u5766\u8bda\u561b\uff0c\u5927\u5bb6\u90fd\u8bf4\u5766\u8bda\u662f\u7f8e\u5fb7\u3002\u201d</p><p>\u201c\u5766\u8bda\u662f\u7f8e\u5fb7\u6ca1\u9519\uff0c\u4f46\u4f60\u8d77\u7801\u7167\u987e\u4e00\u70b9\u4f60\u5728\u6211\u5fc3\u76ee\u4e2d\u7684\u7f8e\u597d\u5f62\u8c61\u554a\u3002\u201d</p><p>\u201c\u54e6\u54e6\uff0c\u539f\u6765\u6211\u5728\u4f60\u5fc3\u76ee\u4e2d\u7684\u5f62\u8c61\u5f88\u7f8e\u597d\u662f\u4e48\uff0c\u201d\u5979\u707f\u70c2\u5730\u7b11\u7740\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u5728\u4f60\u773c\u91cc\u6211\u5c31\u662f\u7528\u6765\u70d8\u6258\u827e\u5f25\u8587\u7684\u966a\u886c\u5462\u3002\u201d</p><p>\u201c\u4e0d\u7528\u8fd9\u4e48\u5984\u81ea\u83f2\u8584\u5427\uff0c\u50cf\u4f60\u8fd9\u6837\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u559c\u6b22\u7684\u554a\u3002\u201d</p><p>\u201c\u6211\u60f3\u4e5f\u662f\uff0c\u201d\u5979\u7406\u6240\u5f53\u7136\u5730\u70b9\u70b9\u5934\uff0c\u201c\u50cf\u6211\u8fd9\u6837\u53c8\u6f02\u4eae\u53c8\u53ef\u7231\u53c8\u4e56\u5de7\u53c8\u5584\u89e3\u4eba\u610f\u4e50\u4e8e\u52a9\u4eba\u7684\u5973\u5b69\u5b50\uff0c\u600e\u4e48\u53ef\u80fd\u6709\u4eba\u4e0d\u559c\u6b22\u5462\u3002\u201d</p><p>\u2026\u2026\u6211\u73b0\u5728\u8003\u8651\u6536\u56de\u524d\u9762\u7684\u8bdd\u3002</p><p>\u201c\u4e0d\u8fc7\u5f88\u5947\u602a\u554a\uff0c\u201d\u5979\u4e00\u8138\u5929\u771f\u65e0\u90aa\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u6211\u8fd9\u4e48\u7eaf\u6d01\u7684\u5973\u5b69\u5b50\u90fd\u77e5\u9053\u8fd9\u70b9\uff0c\u96be\u9053\u4f60\u53cd\u800c\u60f3\u4e0d\u5230\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u8981\u8bf4\u5f97\u6211\u597d\u50cf\u591a\u4e48\u90aa\u6076\u554a\uff0c\u201d\u743c\u6069\u5f88\u4e0d\u6ee1\uff0c\u201c\u81f3\u4e8e\u90a3\u4e2a\u4e48\uff0c\u6211\u662f\u63d0\u8fc7\uff0c\u4f46\u827e\u5f25\u8587\u4e0d\u540c\u610f\u2026\u2026\u7b49\u7b49\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u548c\u4f60\u8bf4\u8fd9\u4e9b\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u6ca1\u4e8b\uff0c\u522b\u4ecb\u610f\uff0c\u201d\u51db\u8f7b\u677e\u5730\u6325\u624b\uff0c\u201c\u8fd9\u8bf4\u660e\u6211\u5f88\u6709\u4eb2\u548c\u529b\u561b\u3002\u201d</p><p>\u201c\u968f\u4fbf\u5427\uff0c\u201d\u743c\u6069\u6709\u6c14\u65e0\u529b\u5730\u8bf4\uff0c\u201c\u8bdd\u9898\u4e00\u626f\u5c31\u4e0d\u77e5\u9053\u626f\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u4f60\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\u554a\uff1f\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u6211\u53ef\u4ee5\u5e2e\u4f60\u8bf4\u670d\u827e\u5f25\u8587\uff0c\u5514\u5514\uff0c\u548c\u4f60\u90a3\u4e2a\u54e6\u3002\u201d</p><p>\u743c\u6069\u505c\u4f4f\u811a\u6b65\uff0c\u770b\u7740\u51db\uff0c\u540e\u8005\u6b63\u751c\u751c\u5730\u7b11\u7740\uff0c\u707f\u70c2\u5f97\u4eff\u4f5b\u521d\u5347\u7684\u671d\u9633\uff0c\u201c\u600e\u4e48\u6837\uff0c\u8fd9\u4e2a\u62a5\u7b54\u65b9\u6cd5\u4f60\u6ee1\u610f\u5427\u3002\u201d</p><p>\u6ee1\u610f\u81ea\u7136\u662f\u6ee1\u610f\uff0c\u53ea\u662f\u2026\u2026</p><p>\u201c\u8fd8\u662f\u8bf4\u4e0d\u76f8\u4fe1\u6211\u80fd\u505a\u5230\uff1f\u201d\u51db\u504f\u7740\u5c0f\u8111\u888b\u770b\u7740\u743c\u6069\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u6211\u548c\u827e\u5f25\u8587\u53ef\u662f\u5341\u4f59\u5e74\u7684\u4ea4\u60c5\u5462\uff0c\u5979\u4f1a\u542c\u6211\u7684\u8bdd\u7684\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u5012\u4e0d\u662f\u4e0d\u76f8\u4fe1\u8fd9\u4e2a\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u81ea\u7136\u77e5\u9053\u4ee5\u51db\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\u4e4b\u6df1\u539a\uff0c\u786e\u5b9e\u662f\u5f88\u6709\u53ef\u80fd\u7684\uff0c\u4f46\u4ed6\u7591\u60d1\u7684\u662f\u2026\u2026</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\u5462\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u4e3a\u4ec0\u4e48\uff1f\u8fd9\u4e48\u7b80\u5355\u7684\u9053\u7406\u8fd8\u9700\u8981\u95ee\u5417\uff1f\u6211\u662f\u827e\u5f25\u8587\u7684\u597d\u670b\u53cb\uff0c\u81ea\u7136\u671f\u671b\u5979\u80fd\u6709\u6bd4\u8f83\u6b63\u5e38\u4e00\u4e9b\u7684\u2026\u2026\u90a3\u4e2a\uff0c\u60c5\u7231\u65b9\u5f0f\u561b\u3002\u5728\u529b\u6240\u80fd\u53ca\u7684\u8303\u56f4\u5185\u5e2e\u5fd9\uff0c\u4e5f\u662f\u4f5c\u4e3a\u670b\u53cb\u7684\u4e49\u52a1\u554a\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u4e48\uff1f\u201d\u743c\u6069\u7528\u6000\u7591\u7684\u76ee\u5149\u770b\u7740\u5979\u3002</p><p>\u201c\u5514\u5514\uff0c\u8fd9\u5f53\u7136\u662f\u6700\u4e3b\u8981\u7684\u539f\u56e0\u5566\uff0c\u81f3\u4e8e\u5176\u4ed6\u7684\u2026\u2026\u201d\u5979\u683c\u683c\u5a07\u7b11\u8d77\u6765\uff0c\u201c\u743c\u6069\u554a\uff0c\u4f60\u4e0d\u89c9\u5f97\uff0c\u827e\u5f25\u8587\u592a\u51b7\u9759\u592a\u6c89\u7a33\u4e86\u5417\uff1f\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\u90fd\u662f\u90a3\u6837\u6de1\u6f20\u81ea\u7136\uff0c\u5ba0\u8fb1\u4e0d\u60ca\u7684\u6837\u5b50\uff0c\u54ea\u91cc\u50cf\u4e2a\u5341\u56db\u5c81\u7684\u5c0f\u5973\u5b69\u5462\u3002\u201d</p><p>\u201c\u8fd9\u8bdd\u5012\u662f\u6ca1\u9519\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u827e\u5f25\u8587\u662f\u592a\u6210\u719f\u4e86\uff0c\u76f8\u6bd4\u8d77\u6765\uff0c\u53cd\u5012\u662f\u4f60\u6bd4\u8f83\u7b26\u5408\u5e74\u9f84\u3002\u201d</p><p>\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u6211\u6bd4\u8f83\u5e7c\u7a1a\uff1f\u201d</p><p>\u201c\u5582\uff0c\u6211\u53ef\u6ca1\u8fd9\u4e48\u8bf4\u554a\uff0c\u522b\u4e71\u6b6a\u66f2\u522b\u4eba\u7684\u610f\u601d\u3002\u201d</p><p>\u201c\u4f46\u4f60\u5fc3\u91cc\u660e\u660e\u5c31\u8fd9\u4e48\u60f3\u7684\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5fc3\u91cc\u600e\u4e48\u60f3\u7684\uff1f\u201d</p><p>\u201c\u53cd\u6b63\u6211\u5c31\u77e5\u9053\u554a\u3002\u201d</p><p>\u201c\u7b97\u4e86\uff0c\u201d\u743c\u6069\u4e3e\u8d77\u624b\u6295\u964d\uff0c\u201c\u6211\u4e0d\u8ddf\u4f60\u5435\u67b6\u3002\u201d</p><p>\u201c\u54fc\u3002\u201d</p><p>\u201c\u771f\u662f\u7684\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u62b1\u6028\u7740\uff0c\u201c\u559c\u6b22\u5435\u67b6\u627e\u4f60\u60c5\u4eba\u53bb\u554a\uff0c\u627e\u6211\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u60c5\u4eba\u4e4b\u95f4\u624d\u4e0d\u4f1a\u5435\u67b6\u5462\uff0c\u201d\u5c0f\u5973\u5b69\u8033\u6735\u5c16\u5f97\u5f88\uff0c\u201c\u4f60\u770b\u4f60\u548c\u827e\u5f25\u8587\uff0c\u4ece\u6765\u5c31\u4e0d\u5435\u67b6\u7684\u5bf9\u5427\u3002\u201d</p><p>\u8fd9\u5012\u4e5f\u662f\u2026\u2026\u7b49\u7b49\uff0c\u600e\u4e48\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4e25\u91cd\u4e0d\u5bf9\u52b2\uff0c\u597d\u50cf\u5b8c\u5168\u641e\u53cd\u4e86\u4f3c\u7684\u2026\u2026</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u51db\u7a81\u7136\u89c1\u743c\u6069\u5b8c\u5168\u6ca1\u52a8\u9759\u4e86\uff0c\u62ac\u5934\u4e00\u770b\u89c1\u4ed6\u8138\u8272\u4e0d\u592a\u597d\u770b\uff0c\u201c\u5582\u5582\uff0c\u6211\u8bf4\u7740\u73a9\u7684\uff0c\u522b\u751f\u6c14\u554a\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u6211\u7a81\u7136\u597d\u50cf\u60f3\u8d77\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6ca1\u4e8b\u6ca1\u4e8b\uff0c\u201d\u743c\u6069\u6446\u6446\u624b\uff0c\u201c\u597d\u5427\uff0c\u7ee7\u7eed\u539f\u672c\u7684\u8bdd\u9898\uff0c\u4f60\u8bf4\u827e\u5f25\u8587\u592a\u6c89\u7a33\u51b7\u9759\uff0c\u90a3\u53c8\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u559c\u6b22\u5979\u8fd9\u6837\u5b50\u554a\uff0c\u5979\u4ece\u5c0f\u5c31\u8fd9\u6837\uff0c\u641e\u5f97\u660e\u660e\u6bd4\u6211\u5c0f\uff0c\u770b\u8d77\u6765\u5374\u6bd4\u6211\u8fd8\u5927\u4f3c\u7684\uff0c\u4e00\u70b9\u90fd\u4e0d\u597d\u73a9\uff0c\u201d\u51db\u62b1\u6028\u7740\uff0c\u201c\u867d\u7136\u5728\u4e00\u8d77\u4e5f\u4f1a\u8bf4\u7b11\uff0c\u4e5f\u4f1a\u804a\u5929\uff0c\u4e5f\u4f1a\u901b\u8857\u4e70\u4e1c\u897f\uff0c\u4e5f\u4f1a\u62b1\u5728\u4e00\u8d77\u7761\uff0c\u4f46\u603b\u89c9\u5f97\u5979\u4e0d\u50cf\u4e2a\u5973\u5b69\u5b50\u5462\u3002\u6240\u4ee5\u5462\uff0c\u201d\u5979\u53c8\u9732\u51fa\u90a3\u751c\u751c\u7684\u7b11\u5bb9\u6765\uff0c\u201c\u6240\u4ee5\u6211\u5c31\u4e00\u76f4\u90fd\u5f88\u60f3\u770b\u5230\u5979\u8138\u7ea2\u5bb3\u7f9e\u624b\u8db3\u65e0\u63aa\u7684\u6837\u5b50\u5462\u3002\u201d</p><p>\u201c\u539f\u6765\u4f60\u662f\u8fd9\u4e2a\u76ee\u7684\u554a\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4e0d\u8fc7\u8bf4\u5b9e\u8bdd\uff0c\u88ab\u51db\u8fd9\u4e48\u4e00\u8bf4\uff0c\u81ea\u5df1\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u5fc3\u52a8\uff0c\u4e0d\u662f\u56e0\u4e3a\u80fd\u548c\u6885\u83f2\u65af\u66f4\u8fdb\u4e00\u6b65\u8eab\u4f53\u4eb2\u8fd1\uff0c\u800c\u662f\u4e5f\u5f88\u60f3\u770b\u770b\u5979\u201c\u8138\u7ea2\u5bb3\u7f9e\u624b\u8db3\u65e0\u63aa\u7684\u6837\u5b50\u201d\u3002</p><p>\u4ece\u521d\u8bc6\u65f6\u90a3\u6837\u82f1\u6b66\u521a\u786c\u7684\u5723\u6b66\u58eb\uff0c\u73b0\u5728\u4e5f\u6e10\u6e10\u53d8\u5f97\u67d4\u8f6f\uff0c\u53d8\u5f97\u7231\u7b11\uff0c\u53d8\u5f97\u66f4\u50cf\u4e2a\u5973\u5b69\u5b50\u4e86\uff0c\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u8fd8\u771f\u5e0c\u671b\u770b\u5230\u5979\u5bb3\u7f9e\uff0c\u770b\u5230\u5979\u6492\u5a07\uff0c\u770b\u5230\u5979\u5378\u4e0b\u4e00\u5207\u9632\u5907\u548c\u8d23\u4efb\uff0c\u505a\u56de\u65e0\u5fe7\u65e0\u8651\u7684\u5341\u56db\u5c81\u5c0f\u5973\u5b69\u5462\u3002</p><p>\u201c\u600e\u4e48\u6837\uff0c\u4f60\u4e5f\u5f88\u671f\u5f85\u5bf9\u5427\uff0c\u201d\u51db\u7b11\u5f97\u4eff\u4f5b\u50cf\u4e2a\u72e1\u733e\u7684\u5c0f\u6076\u9b54\uff0c\u201c\u90a3\u4e48\u5c31\u8bf4\u5b9a\u4e86\uff0c\u4eca\u5929\u6050\u6015\u4e0d\u884c\uff0c\u660e\u5929\u6216\u8005\u540e\u5929\u665a\u4e0a\u5427\uff0c\u7b49\u6211\u7ed9\u4f60\u53d1\u4fe1\u53f7\u54e6\u3002\u201d</p><p>\u201c\u5514\uff0c\u597d\u3002\u201d</p><p>\u4e3a\u4ec0\u4e48\u2026\u2026\u6709\u79cd\u51c6\u5907\u5408\u8c0b\u505a\u574f\u4e8b\u7684\u611f\u89c9\u3002\u4e0d\u8fc7\u4e00\u60f3\u60f3\u5230\u65f6\u5019\u7684\u60c5\u666f\uff0c\u5fc3\u5c31\u7830\u7830\u52a0\u901f\u8d77\u6765\u4e86\uff0c\u771f\u671f\u5f85\u554a\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e24\u4eba\u56de\u5230\u57ce\u5185\u4f4f\u5904\uff0c\u548c\u6885\u83f2\u65af\u4f1a\u5408\uff0c\u6b63\u8bf4\u4e00\u5929\u7684\u89c1\u95fb\uff0c\u7a81\u7136\u542c\u89c1\u5c16\u9510\u6025\u4fc3\u7684\u53f7\u89d2\u58f0\u4f20\u6765\uff0c\u7d27\u63a5\u7740\u542c\u5230\u5916\u9762\u5fd9\u4e71\u7684\u811a\u6b65\u58f0\u3002\u201c\u8fd9\u662f\u2026\u2026\u50cf\u662f\u6709\u654c\u88ad\uff1f\u201d\u6885\u83f2\u65af\u51ed\u7ecf\u9a8c\u505a\u51fa\u4e86\u5224\u65ad\u3002</p><p>\u4e09\u4eba\u51fa\u95e8\u4e00\u770b\uff0c\u53d1\u73b0\u679c\u7136\u662f\u6709\u654c\u4eba\u6765\u88ad\uff0c\u57ce\u95e8\u5df2\u7ecf\u5173\u95ed\uff0c\u6c11\u5175\u4eec\u4ece\u5404\u81ea\u5bb6\u4e2d\u5954\u51fa\uff0c\u88c5\u675f\u6574\u9f50\uff0c\u624b\u6301\u5175\u5668\uff0c\u961f\u957f\u6b63\u5728\u70b9\u540d\u96c6\u5408\u3002\u867d\u7136\u4e8b\u51fa\u7a81\u7136\uff0c\u5012\u4e5f\u5e76\u6ca1\u9020\u6210\u4ec0\u4e48\u614c\u4e71\uff0c\u57ce\u4e2d\u5c45\u6c11\u90fd\u4eff\u4f5b\u4e60\u4ee5\u4e3a\u5e38\u4f3c\u7684\uff0c\u5e76\u65e0\u6050\u60e7\u4e4b\u8272\uff0c\u53cd\u5012\u6709\u4e0d\u5c11\u4eba\u4e0a\u4e86\u57ce\u5934\u89c2\u770b\uff0c\u743c\u6069\u7b49\u4e09\u4eba\u4e5f\u968f\u7740\u4e0a\u4e86\u57ce\u5899\uff0c\u770b\u770b\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u539f\u6765\u662f\u4e00\u961f\u6c11\u5175\u5728\u57ce\u5916\u5de1\u903b\u65f6\uff0c\u649e\u4e0a\u4e86\u4e00\u7fa4\u8c7a\u72fc\u4eba\uff0c\u4e3a\u6570\u5927\u7ea6\u4e8c\u5341\u591a\u4e2a\u3002\u53cc\u65b9\u5f00\u6218\uff0c\u6c11\u5175\u4e0d\u654c\uff0c\u4e00\u8def\u5f80\u56de\u64a4\u9000\u3002\u56e0\u4e3a\u5bb3\u6015\u8c7a\u72fc\u4eba\u8d81\u52bf\u5165\u57ce\uff0c\u57ce\u95e8\u6682\u65f6\u4e0d\u6562\u6253\u5f00\uff0c\u57ce\u5185\u6c11\u5175\u5c1a\u672a\u96c6\u5408\u5b8c\u6bd5\uff0c\u57ce\u5916\u7684\u8fd9\u961f\u6c11\u5175\u88ab\u8c7a\u72fc\u4eba\u8ffd\u8d76\u4e0a\u4e86\uff0c\u6b63\u5728\u57ce\u95e8\u524d\u6fc0\u6218\uff0c\u53ea\u662f\u5be1\u4e0d\u654c\u4f17\uff0c\u773c\u770b\u5c31\u8981\u652f\u6301\u4e0d\u4f4f\u4e86\u3002</p><p>\u51db\u773c\u89c1\u6b64\u60c5\u5f62\uff0c\u4f38\u624b\u5c31\u51c6\u5907\u4ece\u53e3\u888b\u91cc\u638f\u5b9d\u77f3\u6765\u5e2e\u5fd9\uff0c\u5374\u88ab\u6885\u83f2\u65af\u6084\u6084\u6309\u4f4f\u4e86\u624b\u3002\u201c\u522b\u52a8\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u7b49\u7b49\u3002\u201d</p><p>\u811a\u6b65\u58f0\u54cd\uff0c\u4e00\u4e2a\u7a7f\u7740\u7070\u8272\u76ae\u7532\u7684\u7537\u4eba\u4e0a\u4e86\u57ce\u5934\uff0c\u4ed6\u7684\u8138\u8272\u82cd\u767d\uff0c\u53cc\u773c\u9510\u5229\u5982\u9e70\uff0c\u95ea\u7740\u9634\u68ee\u68ee\u7684\u5bd2\u5149\uff0c\u5468\u56f4\u7684\u4eba\u7eb7\u7eb7\u8ba9\u5f00\uff0c\u4e3a\u4ed6\u7a7a\u51fa\u4e00\u5757\u5730\u65b9\u3002\u201c\u8428\u74e6\u5c14\u3002\u201d\u6885\u83f2\u65af\u6084\u58f0\u5bf9\u743c\u6069\u548c\u51db\u8bf4\uff0c\u8fd9\u4eba\u5c31\u662f\u90a3\u4f4d\u57ce\u4e2d\u552f\u4e00\u7684\u7267\u5e08\u4e86\u3002</p><p>\u8428\u74e6\u5c14\u4e0a\u4e86\u57ce\u5934\uff0c\u7565\u7565\u770b\u4e86\u770b\u5916\u9762\u7684\u5c40\u52bf\uff0c\u4ece\u8116\u5b50\u4e0a\u626f\u4e0b\u5723\u5fbd\u6765\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u5bf9\u7740\u57ce\u5916\u4e00\u58f0\u5389\u559d\u3002\u53ea\u89c1\u57ce\u5916\u5730\u9762\u4e0a\uff0c\u9661\u7136\u5347\u8d77\u65e0\u6570\u652f\u6ce5\u6d46\u8513\u85e4\u6765\uff0c\u5c06\u90a3\u4e9b\u8c7a\u72fc\u4eba\u6b7b\u6b7b\u7f20\u4f4f\uff0c\u987f\u65f6\u518d\u4e5f\u52a8\u5f39\u4e0d\u5f97\u3002\u6c11\u5175\u4e58\u673a\u64a4\u9000\uff0c\u548c\u57ce\u5185\u6c11\u5175\u4f1a\u5408\u4e00\u5904\uff0c\u7247\u523b\u95f4\u5f13\u7bad\u9f50\u53d1\uff0c\u5c06\u8fd9\u4e9b\u8c7a\u72fc\u4eba\u6740\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002</p><p>\u5f88\u7b80\u5355\u7684\u6218\u6597\uff0c\u65e0\u8bba\u6c11\u5175\u8fd8\u662f\u8c7a\u72fc\u4eba\uff0c\u90fd\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u53ef\u5708\u53ef\u70b9\u4e4b\u5904\u3002\u743c\u6069\u770b\u5f97\u6709\u4e9b\u610f\u5174\u7d22\u7136\uff0c\u4e0e\u4e24\u4f4d\u5c11\u5973\u4e0b\u4e86\u57ce\u5899\uff0c\u56de\u5230\u4f4f\u5904\u3002\u8d70\u8fdb\u623f\u95f4\uff0c\u6b63\u8981\u7ee7\u7eed\u6b64\u524d\u7684\u8bdd\u9898\uff0c\u6885\u83f2\u65af\u7a81\u7136\u5f00\u53e3\u3002</p><p>\u201c\u90a3\u4e2a\u7267\u5e08\u5f88\u53ef\u7591\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u4f60\u4e0d\u662f\u8bf4\u4ed6\u8eab\u4e0a\u6ca1\u4ec0\u4e48\u795e\u68cd\u6c14\u2026\u2026\u795e\u804c\u8005\u7684\u611f\u89c9\uff0c\u4e0d\u4f1a\u662f\u6211\u4eec\u8981\u627e\u7684\u4eba\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u80af\u5b9a\u6ca1\u9519\uff0c\u4f46\u4f60\u77e5\u9053\u4ed6\u521a\u624d\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u672f\uff1f\u201d</p><p>\u743c\u6069\u548c\u51db\u90fd\u6447\u5934\uff0c\u201c\u4e0d\u6e05\u695a\u3002\u201d\u4ed6\u4eec\u5bf9\u795e\u672f\u5e76\u4e0d\u719f\u6089\u3002</p><p>\u201c\u90a3\u662f\u2018\u5927\u5730\u6bcd\u795e\u4e4b\u675f\u7f1a\u2019\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u662f\u5927\u5730\u5973\u795e\u88f3\u63d0\u963f\u8d50\u4e88\u4fe1\u5f92\u7684\u7279\u6709\u795e\u672f\uff0c\u4ed6\u4e00\u4e2a\u6885\u4e3d\u51ef\u7684\u7267\u5e08\uff0c\u600e\u4e48\u53ef\u80fd\u4f1a\uff1f\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u201d\u6885\u83f2\u65af\u7ee7\u7eed\u8bf4\uff0c\u201c\u4ed6\u90a3\u4e2a\u5723\u5fbd\uff0c\u867d\u7136\u786e\u5b9e\u662f\u6885\u4e3d\u51ef\u7684\u5723\u5fbd\u6ca1\u9519\uff0c\u4f46\u65bd\u6cd5\u7684\u65f6\u5019\u538b\u6839\u5c31\u6ca1\u7528\u4e0a\uff0c\u662f\u4e2a\u6446\u8bbe\uff0c\u6cd5\u672f\u6839\u672c\u5c31\u4e0d\u662f\u901a\u8fc7\u90a3\u4e2a\u5723\u5fbd\u91ca\u653e\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u5176\u5b9e\u662f\u4e2a\u5927\u5730\u5973\u795e\u88f3\u63d0\u963f\u7684\u7267\u5e08\uff0c\u4f2a\u88c5\u6210\u6885\u4e3d\u51ef\u7267\u5e08\uff1f\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u5947\u602a\u7684\u5730\u65b9\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5927\u5730\u5973\u795e\u5728\u963f\u683c\u62c9\u9686\u662f\u5f88\u6709\u5f71\u54cd\u529b\u7684\u4fe1\u4ef0\uff0c\u4fe1\u5f92\u4f17\u591a\uff0c\u4e5f\u9887\u53d7\u653f\u5e9c\u652f\u6301\u3002\u5982\u679c\u4ed6\u662f\u5927\u5730\u5973\u795e\u7684\u7267\u5e08\uff0c\u90a3\u6709\u4ec0\u4e48\u5fc5\u8981\u9690\u85cf\u8eab\u4efd\uff0c\u53cd\u800c\u53bb\u5192\u5145\u5176\u4ed6\u795e\u7947\u7684\u7267\u5e08\u5462\uff1f\u201d</p><p>\u8fd9\u4e2a\u95ee\u9898\uff0c\u743c\u6069\u548c\u51db\u81ea\u7136\u4e5f\u6ca1\u6cd5\u56de\u7b54\uff0c\u4e8b\u5b9e\u4e0a\u5728\u795e\u7947\u3001\u5b97\u6559\u8fd9\u65b9\u9762\uff0c\u4e24\u4f4d\u5deb\u5e08\u660e\u663e\u4e0d\u5982\u6885\u83f2\u65af\u8fd9\u4e2a\u5723\u6b66\u58eb\u6709\u53d1\u8a00\u6743\u3002\u5979\u65e2\u7136\u8bf4\u5f97\u5982\u6b64\u80af\u5b9a\uff0c\u743c\u6069\u81ea\u7136\u4e5f\u4e0d\u4f1a\u4e0d\u76f8\u4fe1\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4e2a\u53eb\u505a\u8428\u74e6\u5c14\u7684\u7267\u5e08\uff0c\u8fd8\u771f\u7684\u662f\u5f88\u53ef\u7591\u554a\u3002</p><p>\u201c\u4f46\u4f60\u786e\u5b9a\u4ed6\u4e0d\u53ef\u80fd\u662f\u4ec0\u4e48\u9ad8\u9636\u7267\u5e08\u5427\uff0c\u4e0d\u53ef\u80fd\u6709\u538b\u5236\u6b7b\u6708\u6cd5\u73e0\u7684\u80fd\u529b\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u80fd\u786e\u5b9a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u4ed6\u786e\u5b9e\u5f88\u53ef\u7591\u2026\u2026\u6211\u770b\uff0c\u6211\u4eec\u6050\u6015\u5f97\u4ed4\u7ec6\u67e5\u67e5\u8fd9\u4e2a\u4eba\u7684\u8eab\u4efd\u4e86\u3002\u201d</p></article>"}}}]);
