(window.webpackJsonp=window.webpackJsonp||[]).push([[141],{163:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u72d7\u4ed4\u961f",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u72d7\u4ed4\u961f</h2><p>\u201c\u5514\uff0c\u90a3\u8fb9\u597d\u50cf\u6253\u5f97\u5f88\u6fc0\u70c8\u554a\u3002\u201d\u5b8c\u5168\u65e0\u89c6\u743c\u6069\u7684\u60ca\u8be7\uff0c\u8fd9\u5bb6\u4f19\u4e5f\u5b66\u6a21\u5b66\u6837\u5730\u8db4\u5728\u571f\u574e\u4e0a\uff0c\u9732\u7740\u4e2a\u5934\uff0c\u89c2\u6d4b\u7740\u8fdc\u5904\u7684\u6218\u5c40\u3002\u66f4\u4ee4\u4eba\u6124\u6168\u7684\u662f\uff0c\u4ed6\u624b\u4e2d\u5c45\u7136\u8fd8\u62ff\u7740\u4e00\u652f\u7b14\uff0c\u9762\u524d\u653e\u7740\u4e00\u5f20\u7eb8\uff0c\u4e00\u8fb9\u89c2\u770b\u4e00\u8fb9\u5728\u7eb8\u4e0a\u5feb\u901f\u8bb0\u5f55\u7740\uff0c\u743c\u6069\u77a5\u4e86\u4e00\u773c\uff0c\u53d1\u73b0\u662f\u4e00\u79cd\u81ea\u5df1\u4e0d\u8ba4\u8bc6\u7684\u6587\u5b57\uff0c\u6216\u8005\u66f4\u53ef\u80fd\u662f\u67d0\u79cd\u7b80\u5199\u4ee3\u7801\uff0c\u5b57\u8ff9\u6f66\u8349\uff0c\u770b\u4e0d\u6e05\u695a\u3002</p><p>\u8fd9\u5bb6\u4f19\u662f\u5e72\u4ec0\u4e48\u7684\uff1f</p><p>\u743c\u6069\u5fc3\u4e2d\u60ca\u8bb6\uff0c\u4ed6\u867d\u7136\u6b64\u65f6\u5168\u8eab\u75bc\u75db\u63d0\u4e0d\u8d77\u529b\u6c14\uff0c\u4f46\u542c\u89c9\u89c6\u89c9\u65e9\u5df2\u6062\u590d\uff0c\u4fee\u4e60\u5185\u529f\u4e5f\u8fd9\u4e48\u591a\u5e74\uff0c\u4e5f\u7b97\u5f97\u4e0a\u8033\u806a\u76ee\u660e\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u4e2a\u5947\u602a\u5bb6\u4f19\u5230\u4e86\u8eab\u8fb9\u81ea\u5df1\u624d\u53d1\u89c9\u3002</p><p>\u770b\u60c5\u5f62\uff0c\u4f3c\u4e4e\u4e0d\u50cf\u662f\u654c\u4eba\uff0c\u5426\u5219\u4e0d\u4f1a\u653e\u7740\u743c\u6069\u8fd9\u4e2a\u6beb\u65e0\u53cd\u6297\u80fd\u529b\u7684\u5bb6\u4f19\u5728\u4e00\u8fb9\u7f6e\u4e4b\u4e0d\u7406\uff1b\u4f46\u4e5f\u4e0d\u50cf\u662f\u670b\u53cb\uff0c\u5426\u5219\u65e9\u5c31\u5e2e\u5fd9\u4e86\uff0c\u54ea\u91cc\u8fd8\u6709\u529f\u592b\u505a\u8bb0\u5f55\u3002\u770b\u4ed6\u8fd9\u4e0b\u7b14\u5982\u98de\u7684\u719f\u7ec3\u67b6\u5f0f\uff0c\u5012\u662f\u5f88\u4ee4\u4eba\u6000\u7591\u662f\u67d0\u4e2a\u516b\u5366\u5c0f\u62a5\u7684\u8bb0\u8005\u3002\u743c\u6069\u6b32\u5f85\u5f00\u53e3\u8be2\u95ee\uff0c\u534a\u53e5\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u53ea\u80fd\u653e\u5f03\u3002</p><p>\u5bf9\u65b9\u4f3c\u4e4e\u542c\u5230\u4e86\u743c\u6069\u7684\u5fc3\u58f0\uff0c\u4ed6\u8f6c\u8fc7\u8138\u6765\uff0c\u88c2\u5f00\u5927\u5634\u5475\u5475\u7b11\u7740\uff0c\u201c\u4e0b\u5348\u597d\u554a\uff0c\u5e74\u8f7b\u4eba\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u8db4\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u743c\u6069\u624d\u5341\u4e94\u5c81\uff0c\u8fd9\u4eba\u5e74\u7eaa\u770b\u8d77\u6765\u6bd4\u743c\u6069\u5927\u4e86\u4e00\u500d\uff0c\u8bf4\u201c\u5e74\u8f7b\u4eba\u201d\u5012\u4e5f\u662f\u7406\u6240\u5f53\u7136\uff0c\u4f46\u743c\u6069\u542c\u8d77\u6765\u4f9d\u65e7\u5f88\u4e0d\u723d\u3002\u5988\u7684\uff0c\u8fd9\u53e5\u8bdd\u5e94\u8be5\u662f\u6211\u95ee\u4f60\u624d\u5bf9\u5427\u3002\u743c\u6069\u5728\u5fc3\u4e2d\u5492\u9a82\u7740\uff0c\u4f46\u4ed6\u6b64\u523b\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002</p><p>\u89c1\u4ed6\u4e0d\u56de\u7b54\uff0c\u770b\u4e86\u770b\u4ed6\u7684\u904d\u4f53\u9cde\u4f24\uff0c\u518d\u770b\u770b\u6b63\u5728\u505a\u6b8a\u6b7b\u6076\u6597\u7684\u6885\u83f2\u65af\u548c\u838e\u73de\u514b\uff0c\u987f\u65f6\u8138\u4e0a\u9732\u51fa\u4e00\u79cd\u604d\u7136\u5927\u609f\u7684\u66a7\u6627\u795e\u60c5\u6765\uff0c\u201c\u539f\u6765\u5982\u6b64\uff0c\u6211\u660e\u767d\u4e86\uff0c\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u201c\u5e74\u8f7b\u4eba\uff0c\u811a\u8e0f\u4e24\u6761\u8239\u662f\u5f88\u4e0d\u597d\u7684\u884c\u4e3a\u5440\uff0c\u4f60\u770b\uff0c\u73b0\u5728\u4e1c\u7a97\u4e8b\u53d1\u4e86\uff0c\u6253\u8d77\u6765\u4e86\u5427\u3002\u6211\u8ddf\u4f60\u8bf4\uff0c\u5973\u4eba\u53d1\u8d77\u72e0\u6253\u8d77\u67b6\u6765\uff0c\u90a3\u662f\u8d85\u7ea7\u53ef\u6015\u7684\u2026\u2026\u201d</p><p>\u743c\u6069\u5dee\u70b9\u6c14\u5f97\u8981\u5410\u8840\uff0c\u90fd\u5df2\u7ecf\u8fd9\u79cd\u65f6\u5019\uff0c\u5c45\u7136\u4e0d\u77e5\u4ece\u54ea\u91cc\u5192\u51fa\u8fd9\u4e2a\u83ab\u540d\u5176\u5999\u7684\u5bb6\u4f19\u6765\uff0c\u8fd8\u8bf4\u8fd9\u79cd\u4e0d\u7740\u8fb9\u9645\u7684\u8bdd\uff0c\u7b80\u76f4\u5c31\u5e94\u8be5\u4e00\u811a\u8e22\u5230\u5b9d\u5251\u6d77\u91cc\u53bb\u5582\u9ca8\u9c7c\u3002\u66f4\u8fc7\u5206\u7684\u662f\uff0c\u4ec0\u4e48\u811a\u8e0f\u4e24\u6761\u8239\uff0c\u8fd9\u4e24\u4e2a\u5973\u4eba\uff0c\u6885\u83f2\u65af\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u7b97\u662f\uff0c\u5973\u6740\u624b\u53ea\u4e0a\u8fc7\u4e00\u6b21\u5e8a\uff0c\u800c\u4e14\u8fd8\u4e2d\u9014\u7ffb\u8138\uff0c\u5dee\u70b9\u88ab\u5979\u6740\u4e86\uff0c\u5bb3\u5f97\u743c\u6069\u5f53\u65f6\u90fd\u6ca1\u53d1\u6cc4\u51fa\u6765\uff0c\u618b\u7740\u96be\u53d7\u4e86\u534a\u5929\u3002\u8fd9\u90fd\u80fd\u88ab\u626f\u4e0a\u662f\u811a\u8e0f\u4e24\u6761\u8239\uff0c\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u516c\u7406\u4e48\u3002</p><p>\u4f46\u8fd9\u4e0d\u77e5\u9053\u4ece\u54ea\u5192\u51fa\u6765\u7684\u8ba8\u538c\u5bb6\u4f19\u4f3c\u4e4e\u5b8c\u5168\u6ca1\u6709\u610f\u8bc6\u5230\u743c\u6069\u7684\u4e0d\u5feb\uff0c\u4ed6\u4f9d\u65e7\u4e00\u8fb9\u89c2\u6218\u4e00\u8fb9\u5728\u7eb8\u4e0a\u8bb0\u8ff0\u7740\uff0c\u4e00\u8fb9\u8fd8\u4e0d\u671b\u7d6e\u7d6e\u53e8\u53e8\u5730\u6559\u8bad\u743c\u6069\u3002\u4ed6\u8bf4\u8bdd\u4eff\u4f5b\u4e1c\u62c9\u897f\u626f\uff0c\u5230\u540e\u6765\u5df2\u7ecf\u4e0d\u77e5\u626f\u5230\u5341\u4e07\u516b\u5343\u91cc\u4e4b\u5916\uff0c\u743c\u6069\u4e5f\u538b\u6839\u61d2\u5f97\u7406\u776c\u4ed6\uff0c\u53ea\u7ba1\u81ea\u5df1\u9ed8\u9ed8\u8c03\u606f\u8fd0\u529f\uff0c\u52a0\u7d27\u81ea\u6108\u3002</p><p>\u7ec8\u4e8e\uff0c\u4eff\u4f5b\u8fc7\u4e86\u975e\u5e38\u6f2b\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u5f53\u7136\u4e5f\u53ef\u80fd\u5176\u5b9e\u5f88\u77ed\u6682\uff0c\u743c\u6069\u53d1\u89c9\u81ea\u5df1\u539f\u672c\u6ca1\u6709\u77e5\u89c9\u7684\u4e0a\u4e0b\u9102\u4f3c\u4e4e\u6062\u590d\u4e86\u6d3b\u52a8\u80fd\u529b\uff0c\u4ed6\u7ec8\u4e8e\u53ef\u4ee5\u52c9\u5f3a\u5f00\u53e3\u8bf4\u8bdd\u4e86\u3002\u4f46\u8fd9\u8fd8\u662f\u6ca1\u7528\uff0c\u56e0\u4e3a\u4ed6\u7684\u8eab\u4f53\u8fd8\u662f\u57fa\u672c\u52a8\u5f39\u4e0d\u4e86\uff0c\u800c\u5927\u90e8\u5206\u6cd5\u672f\u7684\u65bd\u5c55\uff0c\u9664\u4e86\u8bf5\u5492\uff0c\u8fd8\u9700\u8981\u8db3\u591f\u7684\u59ff\u52bf\u914d\u5408\uff0c\u743c\u6069\u76ee\u524d\u65e0\u6cd5\u505a\u5230\u3002</p><p>\u90a3\u8fb9\u4e24\u4e2a\u5973\u4eba\u7684\u62fc\u6740\u4f9d\u7136\u5728\u7ee7\u7eed\uff0c\u6885\u83f2\u65af\u663e\u7136\u5df2\u7ecf\u975e\u5e38\u75b2\u5026\uff0c\u6740\u624b\u5374\u4f3c\u4e4e\u66f4\u52a0\u52c7\u731b\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u5979\u5468\u8eab\u70c8\u7130\u66b4\u6da8\uff0c\u5de6\u624b\u4e00\u6296\uff0c\u539f\u672c\u63e1\u5728\u638c\u4e2d\u7684\u77ed\u5251\u9661\u5730\u53d8\u6210\u4e86\u4e00\u628a\u70c8\u7130\u7f20\u7ed5\u7684\u957f\u97ad\u6765\u3002\u6740\u624b\u53f3\u624b\u77ed\u5251\uff0c\u5de6\u624b\u70c8\u7130\u957f\u97ad\u6025\u901f\u6325\u821e\uff0c\u97ad\u5f71\u5982\u9f99\uff0c\u5e7b\u6210\u4e00\u5f20\u5bc6\u96c6\u706b\u7f51\u5c06\u6885\u83f2\u65af\u7b3c\u7f69\u5176\u4e2d\uff0c\u5979\u4f7f\u7528\u957f\u97ad\u7684\u6280\u5de7\uff0c\u5c45\u7136\u4f3c\u4e4e\u6bd4\u7528\u77ed\u5251\u66f4\u52a0\u9ad8\u660e\u51e0\u5206\u3002\u6885\u83f2\u65af\u731d\u4e0d\u53ca\u9632\u4e0b\uff0c\u5df2\u7ecf\u88ab\u97ad\u7f51\u7f69\u4f4f\uff0c\u556a\u556a\u556a\u556a\u8fde\u54cd\uff0c\u8eab\u4e0a\u63a5\u8fde\u6328\u4e86\u51e0\u97ad\uff0c\u603b\u7b97\u62a4\u4f4f\u5934\u8138\uff0c\u8eab\u4e0a\u53c8\u6709\u94f6\u9f99\u76ae\u7532\u62b5\u5fa1\uff0c\u5c1a\u65e0\u5927\u788d\uff0c\u53ea\u662f\u811a\u6b65\u4e0d\u7a33\uff0c\u8eab\u5f62\u8e09\u8dc4\uff0c\u773c\u89c1\u66f4\u52a0\u4e0d\u654c\u3002</p><p>\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u5dee\u70b9\u5fd8\u4e86\u554a\uff0c\u201d\u743c\u6069\u6b63\u5728\u7126\u6025\u53c8\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u6b63\u5728\u65c1\u8fb9\u5feb\u901f\u505a\u8bb0\u5f55\u7684\u90a3\u5bb6\u4f19\u7a81\u7136\u4e00\u62cd\u8111\u888b\uff0c\u4ece\u6000\u91cc\u9012\u8fc7\u4e00\u5f20\u957f\u65b9\u5f62\u5c0f\u94f6\u7247\u6765\uff0c\u89c1\u743c\u6069\u4e0d\u63a5\uff0c\u76f4\u63a5\u653e\u5728\u4ed6\u9762\u524d\uff0c\u201c\u8fd9\u662f\u6211\u7684\u540d\u7247\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6709\u673a\u4f1a\u7684\u8bdd\u591a\u591a\u5173\u7167\u554a\u3002\u201d</p><p>\u743c\u6069\u4f4e\u5934\u770b\u53bb\uff0c\u89c1\u662f\u4e00\u5f20\u79d8\u94f6\u5236\u6210\u7684\u957f\u65b9\u5f62\u8584\u7247\uff0c\u4e0a\u9762\u7528\u9ed1\u91d1\u954c\u7740\u4e24\u884c\u5b57\u3002\u6b63\u4e2d\u5199\u7740\u201c\u4fe1\u606f\u5c31\u662f\u529b\u91cf\u201d\uff0c\u53f3\u4e0b\u4fa7\u7528\u5c0f\u5b57\u5199\u7740\uff1a\u201c\u4fe1\u606f\u6536\u96c6\u5458\u6b27\u51ef\u3002\u201d</p><p>\u4fe1\u606f\u6536\u96c6\u5458\uff1f</p><p>\u743c\u6069\u77e5\u9053\u8fd9\u4e2a\u4e16\u754c\u4e0a\u786e\u5b9e\u662f\u6709\u8d29\u5356\u4fe1\u606f\u60c5\u62a5\u7684\u7ec4\u7ec7\uff0c\u6700\u5e38\u89c1\u7684\u81ea\u7136\u662f\u5404\u79cd\u76d7\u8d3c\u516c\u4f1a\uff0c\u5728\u6df1\u6c34\u57ce\u3001\u963f\u65af\u5361\u7279\u62c9\u7b49\u5927\u57ce\u5e02\u91cc\u4e5f\u6709\u62a5\u793e\uff0c\u65e2\u53d1\u884c\u62a5\u7eb8\uff0c\u540c\u65f6\u4e5f\u987a\u4fbf\u505a\u8d29\u5356\u4fe1\u606f\u7684\u52fe\u5f53\u3002\u518d\u5982\u70db\u5821\uff0c\u5176\u5b9e\u4e5f\u53ef\u4ee5\u7b97\u662f\u6b64\u7c7b\uff0c\u800c\u4e14\u662f\u6536\u8d39\u6700\u6602\u8d35\uff0c\u5bb0\u5ba2\u6700\u51f6\u72e0\u7684\u3002</p><p>\u65e2\u7136\u6709\u8fd9\u79cd\u7ec4\u7ec7\uff0c\u90a3\u4e48\u6709\u6240\u8c13\u7684\u201c\u4fe1\u606f\u6536\u96c6\u5458\u201d\u4e5f\u6ca1\u4ec0\u4e48\u5947\u602a\u7684\u3002\u770b\u8fd9\u5bb6\u4f19\u7684\u6253\u626e\uff0c\u4e0d\u50cf\u662f\u76d7\u8d3c\u516c\u4f1a\u7684\uff0c\u96be\u9053\u662f\u70db\u5821\u91cc\u7684\u4eba\uff1f\u8fd8\u662f\u62a5\u793e\u72d7\u4ed4\u961f\u7684\uff1f\u53ea\u662f\u6ca1\u542c\u8bf4\u535a\u5f97\u4e4b\u95e8\u6709\u62a5\u793e\u554a\u3002\u8fd9\u4e2a\u4e16\u754c\u56e0\u4e3a\u6ca1\u6709\u53d1\u660e\u9020\u7eb8\u672f\uff0c\u7f8a\u76ae\u7eb8\u867d\u7136\u5927\u91cf\u4f7f\u7528\uff0c\u6210\u672c\u4e5f\u4e0d\u7b97\u7279\u522b\u9ad8\uff0c\u7ec8\u7a76\u8fd8\u4e0d\u662f\u4eba\u4eba\u90fd\u4eab\u7528\u5f97\u8d77\u7684\u3002\u62a5\u7eb8\u662f\u7528\u7f8a\u76ae\u7eb8\u505a\u6750\u6599\uff0c\u9762\u5411\u7684\u6d88\u8d39\u7fa4\u4f53\u4f9d\u65e7\u8fd8\u662f\u4e2d\u4e0a\u6d41\u793e\u4f1a\uff0c\u53ea\u6709\u6df1\u6c34\u57ce\u3001\u963f\u65af\u5361\u7279\u62c9\u3001\u94f6\u6708\u57ce\u8fd9\u79cd\u7b2c\u4e00\u7b49\u7684\u5927\u57ce\u5e02\u91cc\u624d\u6709\uff0c\u535a\u5f97\u4e4b\u95e8\u7ec8\u7a76\u8fd8\u662f\u900a\u8272\u534a\u7b79\u3002</p><p>\u8fd9\u4e2a\u53eb\u6b27\u51ef\u7684\u5bb6\u4f19\uff0c\u5c45\u7136\u80fd\u7528\u79d8\u94f6\u505a\u540d\u7247\uff0c\u770b\u6765\u6765\u5386\u4e0d\u51e1\u554a\uff0c\u5230\u5e95\u662f\u4f55\u65b9\u4eba\u7269\uff1f</p><p>\u201c\u4f60\u662f\u5e72\u561b\u7684\uff1f\u201d\u743c\u6069\u5fcd\u4e0d\u4f4f\u95ee\u3002</p><p>\u201c\u4fe1\u606f\u6536\u96c6\u5458\u554a\uff0c\u201d\u6b27\u51ef\u4e0d\u4ee5\u4e3a\u7136\u5730\u8bf4\uff0c\u201c\u8fd9\u91cc\u4e0d\u662f\u5199\u7740\u4e48\uff1f\u201d</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u4f60\u66ff\u8c01\u5de5\u4f5c\uff1f\u201d</p><p>\u201c\u6211\u81ea\u5df1\uff0c\u201d\u6b27\u51ef\u54a7\u5634\u7b11\u7740\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u770b\u8d77\u6765\u9887\u4e3a\u5f97\u610f\uff0c\u201c\u6211\u51c6\u5907\u5728\u535a\u5f97\u4e4b\u95e8\u529e\u4e00\u4efd\u300a\u5192\u9669\u8005\u65e5\u62a5\u300b\uff0c\u4e13\u95e8\u4e3a\u5404\u4f4d\u6709\u5fd7\u4e8e\u6253\u5012\u6076\u9f99\u3001\u62ef\u6551\u516c\u4e3b\uff0c\u4ee5\u7ef4\u62a4\u4e16\u754c\u548c\u5e73\u4e3a\u5df1\u4efb\u7684\u70ed\u8840\u9752\u5e74\u4eec\u63d0\u4f9b\u6d88\u606f\u3002\u201d</p><p>\u539f\u6765\u662f\u4e2a\u51c6\u5907\u529e\u62a5\u793e\u7684\u72d7\u4ed4\u961f\u2026\u2026\u5192\u9669\u8005\u65e5\u62a5\uff1f\u8fd9\u4e2a\u521b\u610f\u4f3c\u4e4e\u4e0d\u9519\u554a\uff0c\u636e\u743c\u6069\u6240\u77e5\u76ee\u524d\u5927\u9646\u4e0a\u4f3c\u4e4e\u8fd8\u6ca1\u6709\u7c7b\u4f3c\u7684\u4e1c\u897f\u3002\u5192\u9669\u8005\u548c\u4f63\u5175\u4eec\u4e00\u822c\u90fd\u662f\u5728\u9152\u9986\u91cc\u5b88\u7740\uff0c\u4e00\u8fb9\u559d\u9152\u5435\u95f9\u8c03\u620f\u4f8d\u5973\uff0c\u4e00\u8fb9\u7b49\u5f85\u7740\u987e\u5ba2\u4e0a\u95e8\uff0c\u8fd9\u4e48\u505a\u5b9e\u5728\u662f\u6709\u4e9b\u7f3a\u4e4f\u6548\u7387\u3002\u5982\u679c\u771f\u6709\u8fd9\u4e48\u4e00\u4efd\u62a5\u7eb8\uff0c\u6bcf\u5929\u767b\u8f7d\u5404\u5730\u7684\u60ac\u8d4f\u4efb\u52a1\u548c\u602a\u7269\u51fa\u6ca1\u4fe1\u606f\uff0c\u4e00\u5b9a\u5f88\u7545\u9500\u3002\u5192\u9669\u8005\u4eec\u4e00\u822c\u90fd\u4e0d\u7231\u770b\u4e66\u8bfb\u62a5\uff0c\u4f46\u8fd9\u79cd\u62a5\u7eb8\u5e94\u8be5\u8fd8\u662f\u4f1a\u6709\u5174\u8da3\u7684\uff0c\u800c\u4e14\u4ed6\u4eec\u5927\u591a\u56ca\u4e2d\u5bbd\u88d5\uff0c\u51fa\u624b\u4e5f\u9614\u7ef0\uff0c\u6323\u94b1\u5feb\u4e5f\u82b1\u94b1\u5feb\u3002</p><p>\u743c\u6069\u4e0d\u7531\u5f97\u5bf9\u4ed6\u8d77\u4e86\u4e9b\u656c\u610f\u3002\u8fd9\u4e16\u754c\u4e0a\u6709\u80fd\u529b\u6709\u672c\u4e8b\u7684\u4eba\u5176\u5b9e\u4e0d\u5c11\uff0c\u4f46\u80fd\u6709\u4e00\u6d41\u521b\u610f\u7684\u9886\u5934\u8005\u5e76\u4e0d\u591a\uff0c\u800c\u8fd9\u662f\u6210\u529f\u7684\u5fc5\u8981\u524d\u63d0\u3002\u5982\u679c\u8fd9\u5bb6\u4f19\u771f\u80fd\u628a\u8fd9\u5192\u9669\u8005\u65e5\u62a5\u7ed9\u529e\u8d77\u6765\uff0c\u90a3\u60f3\u5fc5\u662f\u4f1a\u4e00\u4e3e\u6210\u540d\u5427\u3002</p><p>\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u8dd1\u5230\u5de8\u9b54\u5c71\u8109\u8fd9\u91cc\u6765\uff0c\u662f\u4e3a\u4e86\u6536\u96c6\u4fe1\u606f\u7684\uff1f</p><p>\u743c\u6069\u7a81\u7136\u5fae\u5fae\u4e00\u51db\uff0c\u5de8\u9b54\u5c71\u8109\u91cc\u867d\u7136\u602a\u7269\u6a2a\u884c\uff0c\u4f46\u6700\u6709\u65b0\u95fb\u4ef7\u503c\u7684\uff0c\u53ea\u6015\u5c31\u662f\u90a3\u4e2a\u8001\u5deb\u5996\u4e86\u3002\u8fd9\u4f4d\u72d7\u4ed4\u961f\u540c\u5b66\u51fa\u73b0\u5728\u8fd9\u5deb\u5e08\u4e4b\u5893\u9644\u8fd1\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4e2a\u5de7\u5408\u5427\u3002\u5c31\u7b97\u4e0d\u662f\u5982\u6b64\uff0c\u80c6\u6562\u5b64\u8eab\u6765\u5de8\u9b54\u5c71\u8109\uff0c\u8fd8\u82e5\u65e0\u5176\u4e8b\u5730\u8db4\u5728\u8fd9\u91cc\u770b\u6253\u67b6\uff0c\u53ea\u8981\u662f\u4e2a\u6b63\u5e38\u4eba\uff0c\u5c31\u4f1a\u77e5\u9053\u8fd9\u5bb6\u4f19\u4e0d\u7b80\u5355\u3002</p></article>"}}}]);
