(window.webpackJsonp=window.webpackJsonp||[]).push([[1411],{1434:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u8282 \u7ef4\u6c99\u4f26\u964d\u4e34",content:"<article><h2>\u7b2c\u56db\u5341\u8282 \u7ef4\u6c99\u4f26\u964d\u4e34</h2><p>\u6216\u8bb8\u5728\u573a\u7684\u5f88\u591a\u4eba\u90fd\u6ca1\u89c1\u8fc7\u8fd9\u4f4d\u94f6\u53d1\u9ed1\u888d\u5973\u5b50\uff0c\u4f46\u5f53\u8ff0\u5c14\u8bf4\u51fa\u201c\u98ce\u66b4\u5973\u738b\u201d\u7684\u65f6\u5019\uff0c\u6240\u6709\u4eba\u90fd\u7acb\u523b\u77e5\u9053\u4e86\u5979\u7684\u8eab\u4efd\u3002</p><p>\u5979\u6b63\u662f\u963f\u683c\u62c9\u9686\u7684\u7edf\u6cbb\u8005\uff0c\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u7684\u9009\u6c11\u4e4b\u4e00\uff0c\u6b23\u5e03\u3002</p><p>\u201c\u6211\u4eec\u5df2\u7ecf\u4f11\u6218\u3002\u201d\u8ff0\u5c14\u6307\u51fa\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u548c\u6b23\u5e03\u53bb\u5e74\u5df2\u7ecf\u7b7e\u7f72\u4e86\u505c\u6218\u534f\u8bae\u3002</p><p>\u201c\u4f46\u4f60\u60f3\u6740\u6211\u7684\u5b66\u751f\u3002\u201d</p><p>\u8ff0\u5c14\u4e00\u6014\uff0c\u4e0d\u660e\u767d\u6b23\u5e03\u6240\u6307\uff0c\u4f46\u98ce\u66b4\u5973\u738b\u5df2\u7ecf\u6ca1\u6709\u8010\u5fc3\u89e3\u91ca\uff0c\u95ea\u8000\u7740\u84dd\u767d\u8272\u5149\u4eae\u7684\u706b\u7130\u4ece\u5979\u8eab\u4e0a\u6c79\u6d8c\u800c\u51fa\uff0c\u5316\u4f5c\u70bd\u70c8\u7684\u98d3\u98ce\u5411\u8ff0\u5c14\u6025\u901f\u88ad\u53bb\u3002\u8ff0\u5c14\u8eb2\u907f\u4e0d\u53ca\uff0c\u53cc\u624b\u4e00\u5408\uff0c\u811a\u4e0b\u817e\u8d77\u4e00\u5708\u706b\u7130\u62a4\u76fe\uff0c\u8bd5\u56fe\u62b5\u6321\u98d3\u98ce\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4ed6\u7684\u5168\u8eab\u88ab\u4e00\u5c42\u95ea\u70c1\u7740\u900f\u660e\u5fae\u5149\u7684\u7403\u4f53\u5b8c\u5168\u7b3c\u7f69\u8d77\u6765\u3002</p><p>\u98d3\u98ce\u51b2\u7834\u4e86\u706b\u7130\u62a4\u76fe\uff0c\u76f4\u76f4\u649e\u5411\u8ff0\u5c14\uff0c\u5728\u4ed6\u7684\u8170\u90e8\u63a0\u8fc7\uff0c\u6495\u5f00\u4e00\u4e2a\u6df1\u6df1\u7684\u4f24\u53e3\u3002\u8ff0\u5c14\u4f4e\u543c\u7740\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u4f38\u624b\u5728\u7a7a\u4e2d\u865a\u6293\uff0c\u4e00\u53ea\u94f6\u767d\u8272\u91d1\u5c5e\u5149\u6cfd\u7684\u534a\u900f\u660e\u5de8\u638c\u5feb\u901f\u51dd\u7ed3\u6210\u578b\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u52b3\u6cfd\u745e\u5c14\u4ece\u4fa7\u9762\u5b8c\u6210\u5bf9\u6b23\u5e03\u53d1\u52a8\u4e86\u88ad\u51fb\uff0c\u201c\u9707\u6151\uff01\u201d\u4ed6\u5389\u58f0\u559d\u7740\uff0c\u4f38\u624b\u9065\u6307\uff0c\u4e00\u56e2\u7ea2\u5149\u8f70\u7136\u81ea\u6b23\u5e03\u811a\u4e0b\u7684\u5730\u9762\u6d8c\u51fa\uff0c\u50cf\u662f\u8981\u5c06\u8fd9\u4f4d\u5973\u738b\u541e\u566c\u4e00\u822c\u3002</p><p>\u6b23\u5e03\u5468\u8eab\u9aa4\u7136\u95f4\u5149\u660e\u5927\u653e\uff0c\u94f6\u767d\u8272\u7684\u706b\u7130\u4ece\u4f53\u5185\u55b7\u6d8c\u800c\u51fa\uff0c\u98de\u821e\u65cb\u8f6c\u7740\uff0c\u73af\u7ed5\u8eab\u4f53\u5468\u56f4\u3002\u7ea2\u5149\u649e\u4e0a\u94f6\u767d\u8272\u706b\u7130\uff0c\u77ac\u95f4\u6e6e\u6ca1\u65e0\u5f62\uff0c\u4eff\u4f5b\u4ece\u6765\u5c31\u4e0d\u5b58\u5728\u8fc7\u4e00\u822c\u3002\u201c\u8be5\u6b7b\u7684\u94f6\u706b\uff01\u201d\u9644\u9b54\u9996\u5e2d\u8bc5\u5492\u7740\uff0c\u201c\u857e\u62c9\u82ae\uff0c\u7528\u5927\u88c2\u89e3\uff01\u201d\u4ed6\u5927\u58f0\u62db\u547c\u7740\u9632\u62a4\u5b66\u6d3e\u7684\u9996\u5e2d\u3002</p><p>\u201c\u6211\u4eca\u5929\u6ca1\u51c6\u5907\u5927\u88c2\u89e3\u3002\u201d\u857e\u62c9\u82ae\u5973\u58eb\u7528\u61d2\u6d0b\u6d0b\u7684\u58f0\u97f3\u56de\u7b54\u540c\u4f34\uff0c\u5979\u5e76\u6ca1\u6709\u6025\u7740\u53c2\u4e0e\u6218\u6597\uff0c\u5374\u597d\u6574\u4ee5\u6687\u5730\u7ed9\u81ea\u5df1\u63a5\u8fde\u53e0\u52a0\u4e86\u51e0\u9053\u9632\u5fa1\u3002\u5bf9\u4e8e\u8fd9\u6837\u6beb\u65e0\u534a\u70b9\u534f\u4f5c\u7cbe\u795e\u7684\u540c\u4f34\uff0c\u52b3\u6cfd\u745e\u5c14\u4f4e\u58f0\u5492\u9a82\u7740\uff0c\u4ece\u6307\u5c16\u5c04\u51fa\u4e00\u9053\u8d64\u7ea2\u8272\u7684\u706b\u7bad\u3002</p><p>\u8ff0\u5c14\u7684\u94f6\u8272\u5de8\u638c\u540c\u65f6\u5b8c\u6210\uff0c\u534a\u7a7a\u4e2d\u51dd\u7ed3\u6210\u62f3\uff0c\u51f6\u731b\u65e0\u6bd4\u5730\u671d\u6b23\u5e03\u7838\u6765\u3002\u98ce\u66b4\u5973\u738b\u51dd\u795e\u8bf5\u5492\uff0c\u53cc\u624b\u4ea4\u53e0\u505a\u4e86\u4e2a\u590d\u6742\u7684\u59ff\u52bf\uff0c\u53d1\u51fa\u4e00\u9053\u7eff\u8272\u5c04\u7ebf\uff0c\u7c89\u788e\u4e86\u5de8\u62f3\uff0c\u4f46\u5979\u4e5f\u56e0\u6b64\u6765\u4e0d\u53ca\u8eb2\u95ea\u52b3\u6cfd\u745e\u5c14\u7684\u706b\u7bad\u3002\u81f3\u5c11\u8d85\u8fc7\u4e94\u9053\u9632\u5fa1\u6cd5\u672f\u77ac\u95f4\u88ab\u6fc0\u53d1\u51fa\u6765\uff0c\u7b3c\u7f69\u5728\u6b23\u5e03\u8eab\u4e0a\uff0c\u4f46\u90fd\u672a\u80fd\u62b5\u6321\u4f4f\u9644\u9b54\u9996\u5e2d\u7684\u8fd9\u6b21\u653b\u51fb\u3002\u6b23\u5e03\u88ab\u51fb\u4e2d\u4e86\uff0c\u706b\u7bad\u4ece\u5979\u7684\u53f3\u81c2\u7a7f\u900f\u8fc7\u53bb\uff0c\u53d1\u51fa\u55e4\u55e4\u7684\u58f0\u54cd\uff0c\u5973\u738b\u53d7\u4f24\u4e86\u3002</p><p>\u8ff0\u5c14\u5927\u58f0\u5938\u5956\u7740\u540c\u4f34\uff0c\u638c\u5fc3\u4e2d\u55b7\u51fa\u4e0d\u65ad\u53d8\u5e7b\u95ea\u70c1\u7684\u8679\u5149\u6765\uff0c\u671d\u6b23\u5e03\u5c04\u53bb\u3002\u52b3\u6cfd\u745e\u5c14\u8c28\u614e\u5730\u51c6\u5907\u7740\u4e0b\u4e00\u4e2a\u6cd5\u672f\uff0c\u800c\u4e00\u76f4\u6162\u541e\u541e\u7684\u857e\u62c9\u82ae\u5973\u58eb\u7ec8\u4e8e\u6709\u4e86\u52a8\u4f5c\uff0c\u5979\u91ca\u653e\u51fa\u4e00\u9053\u767d\u5149\uff0c\u5316\u4f5c\u96fe\u6c14\u4ece\u6b23\u5e03\u8eab\u4e0a\u51b2\u8fc7\uff0c\u6d88\u89e3\u6389\u4e86\u5973\u738b\u7684\u5927\u90e8\u5206\u6cd5\u672f\u9632\u5fa1\uff0c\u5305\u62ec\u73af\u7ed5\u5728\u8eab\u5468\u7684\u94f6\u767d\u706b\u7130\u3002\u773c\u770b\u8ff0\u5c14\u53d1\u51fa\u7684\u8679\u5149\u5c06\u8981\u5c04\u4e2d\uff0c\u6b23\u5e03\u731b\u7136\u9000\u540e\u534a\u6b65\uff0c\u968f\u5373\u6574\u4e2a\u8eab\u5f62\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\uff0c\u907f\u5f00\u4e86\u6cd5\u672f\u7684\u653b\u51fb\u3002</p><p>\u4e0b\u4e00\u77ac\u95f4\uff0c\u5979\u51fa\u73b0\u5728\u9ad8\u7a7a\u4e0a\u65b9\uff0c\u4e00\u56e2\u94f6\u706b\u518d\u5ea6\u4ece\u4f53\u5185\u6d8c\u51fa\uff0c\u4fee\u590d\u4e86\u624b\u81c2\u4e0a\u88ab\u52b3\u6cfd\u745e\u5c14\u7684\u706b\u7bad\u6495\u5f00\u7684\u4f24\u53e3\u3002\u5979\u7684\u94f6\u8272\u957f\u53d1\u7eb7\u4e71\u98de\u821e\uff0c\u773c\u4e2d\u7684\u706b\u7130\u718a\u718a\u8df3\u52a8\u8d77\u6765\uff0c\u4f38\u624b\u671d\u52b3\u6cfd\u745e\u5c14\u4e00\u6307\uff0c\u56db\u9897\u5de8\u5927\u7684\u706b\u7403\u81ea\u9ad8\u7a7a\u4e2d\u76f4\u76f4\u5760\u843d\u4e0b\u6765\uff0c\u4eff\u4f5b\u9668\u77f3\u6d41\u661f\uff0c\u671d\u8fd9\u4f4d\u9644\u9b54\u9996\u5e2d\u7838\u53bb\u3002\u5728\u5373\u5c06\u7838\u5230\u7684\u65f6\u5019\uff0c\u6b23\u5e03\u5ff5\u51fa\u4e86\u4e00\u4e2a\u5b57\u7b26\uff0c\u56db\u9897\u706b\u7403\u9aa4\u7136\u6536\u7f29\uff0c\u53d8\u6210\u4e86\u56db\u56e2\u567c\u556a\u4f5c\u54cd\u7684\u7403\u72b6\u95ea\u7535\u3002</p><p>\u52b3\u6cfd\u745e\u5c14\u4ece\u5de8\u77f3\u9876\u4e0a\u6454\u4e86\u4e0b\u6765\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u4ed6\u542f\u52a8\u4e86\u957f\u888d\u4e0a\u7684\u7fbd\u843d\u672f\uff0c\u5b89\u7136\u65e0\u6059\u5730\u843d\u5230\u5730\u9762\uff0c\u7403\u72b6\u95ea\u7535\u5c06\u4ed6\u539f\u672c\u7ad9\u7acb\u7684\u5de8\u77f3\u9876\u4e0a\u8f70\u51fa\u4e86\u4e00\u4e2a\u6df1\u5751\u3002\u9644\u9b54\u9996\u5e2d\u6012\u543c\u7740\uff0c\u518d\u6b21\u53d1\u5c04\u51fa\u4e00\u9053\u706b\u7bad\uff0c\u4f46\u6b23\u5e03\u8eab\u4e0a\u7684\u94f6\u706b\u5851\u6210\u4e86\u4e00\u9762\u76fe\u724c\uff0c\u6321\u4f4f\u4e86\u653b\u51fb\u3002</p><p>\u8ff0\u5c14\u653e\u5f03\u4e86\u76f4\u63a5\u7684\u6cd5\u672f\u653b\u51fb\uff0c\u521b\u9020\u51fa\u4e00\u67c4\u53c8\u4e00\u67c4\u900f\u660e\u7684\u529b\u573a\u957f\u5251\u6765\uff0c\u4eff\u4f5b\u6709\u751f\u547d\u4e00\u822c\u81ea\u884c\u98de\u821e\u7740\uff0c\u671d\u6b23\u5e03\u523a\u53bb\u3002\u857e\u62c9\u82ae\u8c28\u614e\u5730\u4fdd\u6301\u7740\u81ea\u5df1\u7684\u5b89\u5168\uff0c\u95f4\u6216\u7528\u89e3\u9664\u9b54\u6cd5\u6467\u7834\u6b23\u5e03\u7684\u9632\u5fa1\u6cd5\u672f\uff0c\u4e3a\u540c\u4f34\u7684\u653b\u51fb\u521b\u9020\u673a\u4f1a\u3002\u52b3\u6cfd\u745e\u5c14\u610f\u8bc6\u5230\u81ea\u5df1\u6700\u62ff\u624b\u7684\u7cbe\u795e\u63a7\u5236\u6cd5\u672f\u5bf9\u6b23\u5e03\u5f88\u96be\u8d77\u5230\u4ec0\u4e48\u4f5c\u7528\uff0c\u4ed6\u7d22\u6027\u4e00\u6b21\u6b21\u5730\u4ece\u6307\u5c16\u91ca\u653e\u51fa\u706b\u7bad\u6765\u3002\u90a3\u5e76\u975e\u662f\u666e\u901a\u7684\u706b\u7130\u7bad\uff0c\u800c\u662f\u5c06\u51c6\u5907\u597d\u7684\u6cd5\u672f\u8fd8\u539f\u6210\u539f\u521d\u7684\u9b54\u6cd5\u80fd\u91cf\u53d1\u5c04\u51fa\u53bb\uff0c\u56e0\u6b64\u5f88\u96be\u88ab\u901a\u5e38\u7684\u65b9\u6cd5\u62b5\u5fa1\u6216\u8005\u6d88\u89e3\u3002</p><p>\u4e09\u4f4d\u7ea2\u888d\u9996\u5e2d\u7684\u8054\u624b\uff0c\u6210\u529f\u538b\u5236\u4f4f\u4e86\u98ce\u66b4\u5973\u738b\u3002\u867d\u7136\u8eab\u4e3a\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u6b23\u5e03\u62e5\u6709\u201c\u94f6\u706b\u201d\uff0c\u90a3\u662f\u4e00\u79cd\u83b7\u81ea\u5973\u795e\u7684\u795e\u529b\uff0c\u65e2\u53ef\u4ee5\u7528\u4e8e\u653b\u51fb\u4e5f\u53ef\u4ee5\u9632\u5fa1\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u7528\u4e8e\u6cbb\u7597\uff0c\u5e76\u4e14\u548c\u5965\u706b\u4e00\u6837\u96be\u4ee5\u88ab\u666e\u901a\u7684\u65b9\u6cd5\u9632\u5fa1\u6216\u6d88\u89e3\u3002\u5979\u9891\u9891\u7ed9\u5bf9\u624b\u9020\u6210\u4e86\u4f24\u5bb3\uff0c\u4f46\u59cb\u7ec8\u6ca1\u529e\u6cd5\u771f\u6b63\u5730\u91cd\u521b\uff0c\u7ea2\u888d\u9996\u5e2d\u4eec\u867d\u7136\u65e0\u6cd5\u7528\u6cd5\u672f\u6cbb\u7597\uff0c\u5374\u4e5f\u643a\u5e26\u4e86\u8db3\u591f\u7684\u836f\u6c34\u3002\u6bcf\u5f53\u67d0\u4e00\u4f4d\u9996\u5e2d\u9677\u5165\u5371\u673a\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4f1a\u806a\u660e\u5730\u9000\u51fa\u6218\u56e2\uff0c\u7a0d\u4e8b\u5598\u606f\uff0c\u800c\u7531\u4e24\u4f4d\u540c\u4f34\u6682\u65f6\u62b5\u6321\u6b23\u5e03\u3002\u8fd9\u79cd\u6218\u672f\u83b7\u5f97\u4e86\u6210\u529f\uff0c\u5deb\u5e08\u7684\u6cd5\u672f\u7ec8\u7a76\u662f\u6709\u9650\u7684\uff0c\u7eb5\u7136\u662f\u6b23\u5e03\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4ee5\u4e00\u5bf9\u4e09\uff0c\u5982\u679c\u4e0d\u80fd\u901f\u6218\u901f\u51b3\uff0c\u6301\u4e45\u6d88\u8017\u4e0b\u53bb\u5c31\u53ea\u80fd\u610f\u5473\u7740\u5931\u8d25\u4e86\u3002</p><p>\u4f46\u5c31\u5728\u5c40\u52bf\u6e10\u6e10\u53d8\u5f97\u660e\u6717\u7684\u65f6\u5019\uff0c\u53d8\u5316\u53c8\u7a81\u7136\u53d1\u751f\u4e86\u3002</p><p>\u8ff0\u5c14\u518d\u6b21\u53ec\u5524\u51fa\u4e86\u6bd5\u683c\u6bd4\u7c89\u788e\u638c\uff0c\u671d\u6b23\u5e03\u91cd\u51fb\u8fc7\u53bb\uff0c\u4f46\u5728\u534a\u9014\u88ab\u4e00\u9762\u81ea\u865a\u7a7a\u4e2d\u9661\u7136\u6d6e\u73b0\u7684\u767d\u9aa8\u4e4b\u5899\u7ed9\u6321\u4f4f\u4e86\u3002\u4e00\u4e2a\u6709\u7740\u9e70\u94a9\u9f3b\u5b50\u7684\u9ed1\u987b\u4e2d\u5e74\u4eba\u5fae\u7b11\u7740\u5728\u8ddd\u79bb\u6b23\u5e03\u4e0d\u8fdc\u7684\u5730\u65b9\u73b0\u8eab\uff0c\u201c\u591a\u65e5\u4e0d\u89c1\u4e86\uff0c\u965b\u4e0b\u3002\u201d\u4ed6\u4f18\u96c5\u5730\u5fae\u5fae\u8eac\u8eab\u884c\u793c\u3002</p><p>\u6b63\u5728\u62fc\u6597\u4e2d\u7684\u5927\u5deb\u5e08\u4eec\u4e00\u9f50\u505c\u624b\uff0c\u6b23\u5e03\u76b1\u7740\u7709\u5934\uff0c\u770b\u7740\u8fd9\u4f4d\u7a81\u7136\u51fa\u73b0\u7684\u5e2e\u624b\uff0c\u201c\u6211\u53ef\u4e0d\u8bb0\u5f97\u5141\u8bb8\u4f60\u6765\u963f\u683c\u62c9\u9686\uff0c\u8428\u624e\u65af\u5766\u3002\u201d</p><p>\u201c\u6211\u4e5f\u6df1\u611f\u62b1\u6b49\uff0c\u201d\u8428\u624e\u65af\u5766\u53f9\u6c14\uff0c\u201c\u4e0d\u8fc7\u965b\u4e0b\u4f60\u770b\uff0c\u6682\u65f6\u6211\u4eec\u662f\u4e0d\u662f\u5148\u4e0d\u7528\u8ba1\u8f83\u8fd9\u4e2a\u95ee\u9898\uff0c\u6bd5\u7adf\u6211\u4eec\u6709\u5171\u540c\u7684\u5bf9\u624b\u3002\u201d</p><p>\u201c\u6211\u53ef\u6ca1\u5174\u8da3\u548c\u4f60\u8054\u624b\u3002\u201d</p><p>\u201c\u4ec5\u4ec5\u662f\u76ee\u6807\u4e00\u81f4\u7f62\u4e86\uff0c\u8c08\u4e0d\u4e0a\u8054\u624b\uff0c\u201d\u8428\u624e\u65af\u5766\u6e29\u548c\u5730\u5fae\u7b11\uff0c\u201c\u800c\u4e14\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u965b\u4e0b\uff0c\u4e00\u76f4\u4ee5\u6765\u6211\u66ff\u60a8\u7684\u5b66\u751f\u9690\u7792\u8eab\u4efd\uff0c\u4f20\u6388\u5979\u6cd5\u672f\uff0c\u8ba9\u5979\u80fd\u5728\u585e\u5c14\u5ea6\u8fc7\u6109\u5feb\u7684\u4e09\u5e74\u65f6\u5149\u2014\u2014\u60a8\u6b20\u6211\u4e00\u4efd\u4eba\u60c5\u5462\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u201d\u6b23\u5e03\u60a0\u7136\u8bf4\uff0c\u5979\u7684\u5507\u8fb9\u6cdb\u8d77\u4e00\u4e1d\u5e73\u9759\u800c\u8be1\u5f02\u7684\u7b11\u610f\u6765\uff0c\u76ee\u5149\u4ece\u8ff0\u5c14\u3001\u52b3\u6cfd\u745e\u5c14\u548c\u857e\u62c9\u82ae\u4e09\u4eba\u8eab\u4e0a\u79fb\u8fc7\uff0c\u201c\u90a3\u4e48\u4e5f\u65e0\u6240\u8c13\u4e86\uff0c\u5c31\u8fd9\u6837\u5427\u3002\u201d</p><p>\u73b0\u5728\u4eba\u6570\u53d8\u6210\u4e86\u4e8c\u6bd4\u4e09\uff0c\u5c40\u52bf\u987f\u65f6\u9006\u8f6c\uff0c\u8428\u624e\u65af\u5766\u8eab\u4e3a\u4ea1\u7075\u9996\u5e2d\uff0c\u7a33\u7a33\u628a\u6301\u7ea2\u888d\u5deb\u5e08\u4f1a\uff0c\u5176\u5b9e\u529b\u8fdc\u80dc\u5176\u4ed6\u9996\u5e2d\uff0c\u4ed6\u548c\u6b23\u5e03\u8054\u624b\uff0c\u8db3\u4ee5\u538b\u5012\u8ff0\u5c14\u7b49\u4e09\u4eba\u3002</p><p>\u8ff0\u5c14\u76b1\u7740\u7709\uff0c\u201c\u5982\u679c\u65e9\u77e5\u9053\u51db\u5c0f\u59d0\u662f\u5973\u738b\u965b\u4e0b\u7684\u5b66\u751f\uff0c\u90a3\u4e48\u6211\u4e00\u5b9a\u4f1a\u66f4\u52a0\u793c\u8c8c\u4e9b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u8fd8\u4e00\u5ea6\u6000\u7591\u5979\u662f\u4f60\u7684\u79c1\u751f\u5973\u5462\uff0c\u8428\u624e\u65af\u5766\u3002\u201d</p><p>\u201c\u6211\u53ef\u6ca1\u6709\u8fd9\u6837\u7684\u8363\u5e78\u3002\u201d\u8428\u624e\u65af\u5766\u5fae\u7b11\u3002</p><p>\u8ff0\u5c14\u4e0d\u7406\u776c\u4ed6\uff0c\u5c06\u76ee\u5149\u8f6c\u5411\u6b23\u5e03\uff0c\u8eac\u8eab\u884c\u793c\uff0c\u201c\u5982\u4f60\u6240\u89c1\uff0c\u5973\u738b\u965b\u4e0b\uff0c\u8fd9\u5176\u5b9e\u662f\u4e2a\u8bef\u4f1a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u60a8\u73b0\u5728\u5c31\u53ef\u4ee5\u5e26\u8d70\u60a8\u7684\u5b66\u751f\uff0c\u6211\u4fdd\u8bc1\u5979\u4e00\u6839\u5934\u53d1\u90fd\u6ca1\u6709\u4f24\u5230\u3002\u6211\u4eec\u7ea2\u888d\u5deb\u5e08\u4f1a\u5185\u90e8\u6709\u4e9b\u5c0f\u5c0f\u7684\u4e89\u6267\uff0c\u5728\u5904\u7406\u5b8c\u540e\u7acb\u523b\u4fbf\u4f1a\u9000\u51fa\u963f\u683c\u62c9\u9686\uff0c\u51b3\u4e0d\u4f1a\u591a\u505c\u7559\u534a\u79d2\u949f\uff0c\u60a8\u770b\u5982\u4f55\uff1f\u201d</p><p>\u201c\u5f88\u597d\u7684\u63d0\u8bae\uff0c\u4e0d\u8fc7\u6211\u62d2\u7edd\uff0c\u201d\u6b23\u5e03\u8bf4\uff0c\u5979\u7565\u5e26\u90aa\u6076\u5730\u5fae\u7b11\u7740\uff0c\u201c\u56e0\u4e3a\u80fd\u591a\u6740\u51e0\u4e2a\u7ea2\u888d\u5deb\u5e08\u4f1a\u8ba9\u6211\u611f\u89c9\u5fc3\u60c5\u6109\u5feb\u3002\u201d</p><p>\u201c\u90a3\u4e48\u5c31\u6ca1\u529e\u6cd5\u4e86\uff0c\u201d\u8ff0\u5c14\u6447\u5934\uff0c\u53cc\u624b\u4e00\u62cd\u3002</p><p>\u5de8\u5927\u7684\u9b54\u6cd5\u9635\u9aa4\u7136\u5728\u865a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u8ff0\u5c14\u3001\u52b3\u6cfd\u745e\u5c14\u548c\u857e\u62c9\u82ae\u4e0d\u52a8\u58f0\u8272\u95f4\u5df2\u7ecf\u5404\u81ea\u5360\u636e\u4e00\u65b9\uff0c\u6700\u540e\u4e00\u4e2a\u7a7a\u4f4d\u4e0a\u7f13\u7f13\u6d6e\u73b0\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u8eab\u6750\u77ee\u5c0f\uff0c\u6234\u7740\u4e00\u526f\u84dd\u6c34\u6676\u78e8\u6210\u7684\u773c\u955c\uff0c\u957f\u957f\u7684\u80e1\u987b\u76f4\u5782\u5230\u80f8\u8179\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u8001\u5b66\u7a76\u3002</p><p>\u201c\u554a\u5440\u554a\u5440\uff0c\u201d\u4ed6\u53f9\u6c14\uff0c\u201c\u4e0d\u662f\u8bf4\u597d\u56db\u4e2a\u5bf9\u4e00\u4e2a\u7684\u4e48\u3002\u201d</p><p>\u201c\u4eba\u751f\u96be\u514d\u603b\u662f\u4f1a\u6709\u610f\u5916\u561b\uff0c\u5948\u5f17\u6717\u3002\u201d</p><p>\u5492\u6cd5\u5b66\u6d3e\u7684\u9996\u5e2d\u5deb\u5e08\u95fb\u8a00\u6447\u5934\uff0c\u201c\u6211\u8ba8\u538c\u610f\u5916\u3002\u201d\u4ed6\u62b1\u6028\u7740\u3002</p><p>\u201c\u90a3\u4e48\u5c31\u628a\u610f\u5916\u89e3\u51b3\u6389\u3002\u201d\u52b3\u6cfd\u745e\u5c14\u4e0d\u8010\u70e6\u5730\u8bf4\u3002</p><p>\u5c40\u52bf\u518d\u4e00\u6b21\u53d1\u751f\u6539\u53d8\uff0c\u73b0\u5728\u6210\u4e86\u4e8c\u6bd4\u56db\u3002\u8ff0\u5c14\u7b49\u4eba\u663e\u7136\u6ca1\u6709\u6599\u5230\u6b23\u5e03\u4f1a\u524d\u6765\u63d2\u624b\uff0c\u539f\u672c\u8fd9\u4e2a\u5e03\u7f6e\u662f\u9488\u5bf9\u8428\u624e\u65af\u5766\uff0c\u4ee5\u56db\u654c\u4e00\uff0c\u53ef\u4ee5\u7a33\u64cd\u80dc\u5238\uff0c\u5982\u4eca\u5c31\u6ca1\u8fd9\u4e48\u5bb9\u6613\u4e86\u3002</p><p>\u6218\u6597\u91cd\u65b0\u7206\u53d1\uff0c\u800c\u4e14\u6bd4\u4e4b\u524d\u66f4\u52a0\u6fc0\u70c8\uff0c\u8428\u624e\u65af\u5766\u62db\u6765\u4e86\u5404\u79cd\u6050\u6016\u7684\u4ea1\u7075\uff0c\u548c\u5948\u5f17\u5170\u7684\u5f02\u6b21\u5143\u602a\u7269\u5927\u519b\u53ae\u6740\u5728\u4e00\u8d77\uff0c\u857e\u62c9\u82ae\u4e3a\u81ea\u5df1\u548c\u540c\u4f34\u5e03\u4e0b\u4e86\u91cd\u91cd\u9632\u5fa1\uff0c\u8fdb\u653b\u7684\u5de5\u4f5c\u5219\u7531\u8ff0\u5c14\u548c\u52b3\u6cfd\u745e\u5c14\u62c5\u4efb\u3002\u6b23\u5e03\u8eab\u4e0a\u7684\u94f6\u706b\u707f\u70c2\u71c3\u70e7\uff0c\u5979\u6beb\u65e0\u987e\u5fcc\u5730\u6254\u51fa\u5404\u79cd\u8303\u56f4\u5de8\u5927\u7684\u6740\u4f24\u6cd5\u672f\uff0c\u6709\u51e0\u6b21\u9669\u4e9b\u628a\u8428\u624e\u65af\u5766\u90fd\u5377\u8fdb\u53bb\uff0c\u4ea1\u7075\u9996\u5e2d\u53ea\u80fd\u82e6\u7b11\u7740\u8eb2\u907f\u3002</p><p>\u743c\u6069\u5728\u5730\u4e0a\u65c1\u89c2\u7740\u8fd9\u573a\u7a7a\u4e2d\u7684\u9b54\u6cd5\u5927\u6218\uff0c\u4ed6\u4ee5\u524d\u4e5f\u66fe\u7ecf\u51e0\u6b21\u548c\u5deb\u5e08\u5bf9\u51b3\uff0c\u5c61\u5c61\u53ef\u4ee5\u501f\u52a9\u4e00\u4e9b\u201c\u6280\u5de7\u201d\u53d6\u80dc\uff0c\u8fd9\u4f3c\u4e4e\u5370\u8bc1\u4e86\u4e00\u6761\u6240\u6709\u5deb\u5e08\u90fd\u8033\u719f\u80fd\u8be6\u7684\u9053\u7406\uff1a\u201c\u6ca1\u6709\u65e0\u7528\u7684\u6cd5\u672f\uff0c\u53ea\u6709\u65e0\u80fd\u7684\u5deb\u5e08\u201d\u3002\u4f46\u4ed6\u53d1\u89c9\u8fd9\u4e2a\u9053\u7406\u5982\u4eca\u597d\u50cf\u53d8\u5f97\u4e0d\u518d\u9002\u7528\uff0c\u65e0\u8bba\u662f\u6b23\u5e03\u5973\u738b\u4e5f\u597d\uff0c\u7ea2\u888d\u9996\u5e2d\u4e5f\u597d\uff0c\u540c\u4e3a\u7b2c\u4e00\u6d41\u7684\u65bd\u6cd5\u8005\uff0c\u5bf9\u6cd5\u672f\u7684\u638c\u63e1\u8fd0\u7528\u5df2\u7ecf\u5230\u8fbe\u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\uff0c\u4e00\u5207\u6240\u8c13\u7684\u201c\u6280\u5de7\u201d\u5728\u4ed6\u4eec\u9762\u524d\u90fd\u4e0d\u8fc7\u662f\u7b11\u8c08\uff0c\u56e0\u4e3a\u90a3\u4e9b\u62db\u6570\u7a8d\u95e8\u5f7c\u6b64\u65e9\u5c31\u90fd\u4e86\u7136\u4e8e\u5fc3\uff0c\u719f\u6089\u65e0\u6bd4\u3002\u8fd9\u79cd\u5927\u5deb\u5e08\u4e4b\u95f4\u7684\u51b3\u6597\uff0c\u552f\u4e00\u7684\u9014\u5f84\u5c31\u662f\u4ee5\u529b\u7834\u529b\uff0c\u5b8c\u5168\u4e0d\u5fc5\u5962\u671b\u80fd\u641e\u4ec0\u4e48\u522b\u51fa\u5fc3\u88c1\u51fa\u5947\u5236\u80dc\u3002</p><p>\u5404\u79cd\u5f3a\u5927\u7684\u9b54\u6cd5\u4ea4\u7ec7\u7eb7\u9519\uff0c\u9020\u6210\u7684\u5267\u70c8\u7206\u70b8\u8ba9\u6574\u4e2a\u7a7a\u95f4\u90fd\u4eff\u4f5b\u5728\u52a8\u8361\uff0c\u743c\u6069\u611f\u89c9\u81ea\u5df1\u811a\u4e0b\u7684\u5730\u9762\u90fd\u5728\u6447\u6643\u98a4\u6296\uff0c\u8fd9\u8ba9\u4ed6\u975e\u5e38\u62c5\u5fc3\u4f1a\u4e0d\u4f1a\u53d1\u751f\u5730\u9707\u3002\u4f46\u6bd4\u8d77\u8fd9\u4e2a\uff0c\u66f4\u8feb\u5728\u7709\u776b\u7684\u95ee\u9898\uff0c\u662f\u7ef4\u6c99\u4f26\u4fe1\u5f92\u4eec\u7684\u7948\u7977\u5df2\u7ecf\u7ed3\u675f\uff0c\u796d\u7940\u4eea\u5f0f\u5f00\u59cb\u4e86\u3002</p><p>\u6b7b\u6708\u6cd5\u73e0\u88ab\u6446\u4e0a\u4e86\u796d\u575b\uff0c\u4e94\u4f4d\u770b\u8d77\u6765\u4f4d\u9636\u6700\u9ad8\u7684\u7ef4\u6c99\u4f26\u7267\u5e08\u73af\u7ed5\u530d\u5310\u5728\u5730\uff0c\u9ad8\u58f0\u5531\u9882\u7740\u795e\u7947\u7684\u8d5e\u7f8e\u7977\u6587\u3002\u7ef4\u6c99\u4f26\u6210\u4e3a\u795e\u7947\u4e0d\u8fc7\u51e0\u5e74\uff0c\u6559\u4f1a\u52bf\u529b\u5fae\u5f31\uff0c\u4fe1\u5f92\u5be5\u5be5\uff0c\u4f46\u6574\u5957\u796d\u7940\u4eea\u5f0f\u5012\u662f\u5f04\u5f97\u50cf\u6a21\u50cf\u6837\uff0c\u715e\u6709\u4ecb\u4e8b\uff0c\u8fde\u8d5e\u6b4c\u90fd\u534e\u7f8e\u5178\u96c5\u5f97\u5f88\u3002\u968f\u7740\u7977\u6587\u7684\u5ff5\u8bf5\uff0c\u796d\u575b\u4e0a\u7a7a\u7684\u7a7a\u95f4\u5f00\u59cb\u626d\u66f2\u53d8\u5f62\uff0c\u7f13\u7f13\u6495\u88c2\uff0c\u73b0\u51fa\u4e00\u4e2a\u72ed\u957f\u7684\u9ed1\u8272\u88c2\u9699\u6765\u3002</p><p>\u4e94\u4f4d\u7ef4\u6c99\u4f26\u7267\u5e08\u5927\u559c\u8fc7\u671b\uff0c\u56e0\u4e3a\u8fd9\u610f\u5473\u7740\u4f4d\u9762\u7684\u901a\u9053\u5df2\u7ecf\u521d\u6b65\u6253\u5f00\uff0c\u795e\u7947\u5373\u5c06\u964d\u4e34\u51e1\u95f4\u3002\u201c\u628a\u796d\u54c1\u5e26\u4e0a\u6765\uff01\u201d\u7ef4\u6c99\u4f26\u9996\u5e2d\u7267\u5e08\u5927\u58f0\u543c\u53eb\uff0c\u4e00\u8fb9\u6307\u6325\u7740\u5c5e\u4e0b\uff0c\u4e00\u8fb9\u7387\u9886\u5176\u4f59\u56db\u4f4d\u7267\u5e08\u7ee7\u7eed\u5ff5\u8bf5\u7977\u6587\u3002\u4e24\u4e2a\u8138\u8272\u6b7b\u767d\u7684\u7ef4\u6c99\u4f26\u6559\u5f92\u671d\u743c\u6069\u7b49\u4eba\u8dd1\u8fc7\u6765\uff0c\u6253\u7b97\u5c06\u6885\u83f2\u65af\u548c\u51db\u5e26\u4e0a\u796d\u575b\u3002</p><p>\u201c\u8be5\u6b7b\uff01\u201d</p><p>\u743c\u6069\u7126\u6025\u7740\uff0c\u4f46\u5851\u80fd\u9996\u5e2d\u8ff0\u5c14\u5927\u5deb\u5e08\u65bd\u5c55\u51fa\u6765\u7684\u529b\u573a\u9b54\u6cd5\uff0c\u538b\u6839\u5c31\u4e0d\u662f\u4ed6\u6240\u80fd\u6323\u8131\u7684\u3002\u6b63\u4ed3\u7687\u65e0\u8ba1\u95f4\uff0c\u4ed6\u9aa4\u7136\u611f\u89c9\u81ea\u5df1\u7684\u8eab\u4f53\u4e00\u677e\uff0c\u539f\u672c\u4e00\u76f4\u7d27\u7d27\u675f\u7f1a\u7740\u7684\u65e0\u5f62\u529b\u573a\u6084\u65e0\u58f0\u606f\u5730\u6d88\u5931\u4e86\u3002\u5927\u559c\u8fc7\u671b\u4e4b\u4e0b\uff0c\u4ed6\u6765\u4e0d\u53ca\u601d\u8003\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u6025\u5fd9\u8dc3\u8d77\u8eab\u6765\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u9897\u7a7a\u8327\u634f\u788e\uff0c\u4f38\u624b\u5bf9\u7740\u51b2\u5728\u524d\u9762\u7684\u7ef4\u6c99\u4f26\u6559\u5f92\u4e00\u6307\uff1a\u201c\u53d8\u5f62\uff01\u201d</p><p>\u4e00\u9053\u767d\u5149\u95ea\u8fc7\uff0c\u88ab\u6cd5\u672f\u51fb\u4e2d\u7684\u7ef4\u6c99\u4f26\u6559\u5f92\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u6761\u6b63\u5728\u5730\u4e0a\u6251\u817e\u7740\u5c3e\u5df4\u7684\u91d1\u9c7c\u3002\u743c\u6069\u81ea\u4ece\u5b66\u4f1a\u53d8\u5f62\u672f\u4ee5\u6765\uff0c\u4ece\u6765\u6ca1\u7528\u5f97\u8fd9\u4e48\u987a\u7545\u6d41\u5229\u8fc7\uff0c\u4ed6\u4e00\u51fb\u5f97\u624b\uff0c\u6b63\u51c6\u5907\u518d\u6b21\u65bd\u6cd5\uff0c\u5374\u89c1\u6885\u83f2\u65af\u548c\u51db\u4e5f\u7a81\u7136\u6323\u8131\u4e86\u529b\u573a\u675f\u7f1a\uff0c\u7ad9\u8d77\u8eab\u6765\u3002\u51db\u62ac\u624b\u53d1\u51fa\u56db\u9897\u9b54\u6cd5\u98de\u5f39\uff0c\u5e72\u8106\u5229\u843d\u5730\u5c06\u7b2c\u4e8c\u4e2a\u7ef4\u6c99\u4f26\u6559\u5f92\u6253\u5012\u5728\u5730\u3002</p><p>\u5176\u4ed6\u7ef4\u6c99\u4f26\u6559\u5f92\u4eec\u53d1\u73b0\u4e86\u8fd9\u8fb9\u7684\u53d8\u6545\uff0c\u9664\u4e86\u6b63\u5728\u5ff5\u8bf5\u7977\u6587\u7684\u4e94\u4f4d\u7267\u5e08\u4e0d\u80fd\u5206\u8eab\uff0c\u5269\u4e0b\u5341\u51e0\u4e2a\u5bb6\u4f19\u90fd\u51b2\u4e86\u8fc7\u6765\uff0c\u5176\u4e2d\u6709\u4e00\u534a\u5de6\u53f3\u5e94\u8be5\u662f\u4ea1\u7075\u5deb\u5e08\uff0c\u9f50\u9f50\u7ad9\u5b9a\u8bf5\u5492\u65bd\u6cd5\u3002\u743c\u6069\u6025\u5fd9\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u9053\u5377\u8f74\uff0c\u5c55\u5f00\u5ff5\u8bf5\uff0c\u4ece\u8eab\u65c1\u7684\u5de8\u77f3\u4e2d\u5ef6\u4f38\u62c9\u626f\u51fa\u4e00\u9053\u77f3\u5899\u6765\uff0c\u6321\u5728\u8eab\u524d\u3002\u4ed6\u521a\u521a\u5b8c\u6210\u6cd5\u672f\uff0c\u4e03\u516b\u652f\u767d\u9aa8\u957f\u77db\u5c31\u7834\u7a7a\u5c04\u6765\uff0c\u6b63\u6253\u5728\u77f3\u5899\u4e0a\uff0c\u53d1\u51fa\u731b\u70c8\u7684\u7206\u70b8\u58f0\u3002</p><p>\u201c\u5feb\u8fc7\u6765\uff01\u201d\u4ed6\u5927\u58f0\u62db\u547c\u3002</p><p>\u6885\u83f2\u65af\u548c\u51db\u4e5f\u8dd1\u4e86\u8fc7\u6765\uff0c\u501f\u52a9\u77f3\u5899\u62b5\u6321\u7740\u7ef4\u6c99\u4f26\u6559\u5f92\u7684\u653b\u51fb\u3002\u767d\u9aa8\u957f\u77db\u63a5\u4e8c\u8fde\u4e09\u5730\u6253\u6765\uff0c\u7247\u523b\u95f4\u8f70\u7834\u4e86\u77f3\u5899\uff0c\u4e00\u7fa4\u5168\u8eab\u9752\u78a7\u6d6e\u80bf\u7684\u80a5\u80d6\u50f5\u5c38\u4e0d\u77e5\u4ece\u54ea\u91cc\u88ab\u53ec\u5524\u800c\u51fa\uff0c\u6325\u821e\u7740\u5c60\u5200\u51b2\u4e0a\u524d\u6765\u3002\u51db\u4ece\u6000\u4e2d\u62bd\u51fa\u706b\u7403\u9b54\u6756\uff0c\u8f70\u8f70\u8fde\u653e\uff0c\u8f6c\u77ac\u95f4\u5c06\u90a3\u7fa4\u50f5\u5c38\u5168\u90e8\u70e7\u6210\u4e86\u7070\u70ec\u3002\u7d27\u63a5\u7740\u5979\u542f\u52a8\u4e86\u5de6\u81c2\u4e0a\u9b54\u6cd5\u523a\u9752\uff0c\u5728\u9762\u524d\u5851\u8d77\u4e00\u9762\u516b\u5c3a\u9ad8\u7684\u51b0\u5899\u6765\uff0c\u518d\u5ea6\u6682\u65f6\u62b5\u5fa1\u4f4f\u4e86\u653b\u51fb\u3002</p><p>\u5f88\u5e78\u8fd0\uff0c\u867d\u7136\u51db\u53ea\u7231\u7528\u706b\u7130\u7c7b\u578b\u7684\u9b54\u6cd5\u653b\u51fb\u4eba\uff0c\u4f46\u5374\u8fd8\u61c2\u5f97\u5728\u9b54\u6cd5\u523a\u9752\u91cc\u50a8\u5b58\u4e00\u4e9b\u7528\u4e8e\u8f85\u52a9\u548c\u9632\u5fa1\u7684\u6cd5\u672f\uff0c\u5c31\u5982\u4ee5\u524d\u5728\u64c2\u53f0\u4e0a\u7528\u8fc7\u7684\u9020\u98ce\u672f\u548c\u98de\u884c\u672f\uff0c\u8fd9\u771f\u662f\u4e2a\u597d\u4e60\u60ef\u3002\u501f\u52a9\u51b0\u5899\u7684\u63a9\u62a4\uff0c\u743c\u6069\u548c\u51db\u75af\u72c2\u5730\u8fde\u7528\u6cd5\u672f\u3001\u5377\u8f74\u548c\u706b\u7403\u9b54\u6756\uff0c\u7247\u523b\u95f4\u5c06\u90a3\u7fa4\u7ef4\u6c99\u4f26\u6559\u5f92\u6740\u6b7b\u5927\u534a\u3002</p><p>\u201c\u5e72\u5f97\u6f02\u4eae\u3002\u201d\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u5728\u80cc\u540e\u8bf4\u3002</p><p>\u743c\u6069\u56de\u5934\uff0c\u53d1\u73b0\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u6b63\u60a0\u95f2\u5730\u9760\u5728\u4e00\u5757\u5de8\u77f3\u8fb9\uff0c\u4e00\u88ad\u7070\u888d\uff0c\u62ab\u7740\u6597\u7bf7\uff0c\u53cc\u624b\u73af\u62b1\u80f8\u524d\uff0c\u9976\u6709\u5174\u81f4\u5730\u6b23\u8d4f\u7740\u8fd9\u573a\u5e76\u4e0d\u7cbe\u5f69\u7684\u6218\u6597\uff0c\u53cd\u800c\u5bf9\u9ad8\u7a7a\u4e2d\u516d\u4f4d\u5927\u5deb\u5e08\u7684\u62fc\u6740\u663e\u5f97\u5174\u8da3\u7f3a\u7f3a\uff0c\u4e5f\u5b8c\u5168\u6ca1\u6709\u63d2\u624b\u53c2\u6218\u7684\u610f\u601d\u3002</p><p>\u201c\u6bbf\u4e0b\u2026\u2026\u201d</p><p>\u743c\u6069\u6b63\u8981\u8bf4\u8bdd\uff0c\u9aa4\u7136\u95f4\u796d\u575b\u90a3\u8fb9\u4f20\u6765\u4e00\u58f0\u5c16\u9510\u75db\u82e6\u7684\u568e\u53eb\uff0c\u50cf\u94a2\u9488\u4e00\u6837\u523a\u5f97\u4ed6\u5fc3\u5934\u4e00\u98a4\u3002\u6115\u7136\u62ac\u5934\u671b\u53bb\uff0c\u5374\u89c1\u539f\u6765\u662f\u90a3\u4f4d\u7ef4\u6c99\u4f26\u9996\u5e2d\u7267\u5e08\u773c\u89c1\u60c5\u5f62\u4e0d\u5bf9\uff0c\u5df2\u7ecf\u6765\u4e0d\u53ca\u5c06\u743c\u6069\u7b49\u4eba\u6293\u6765\u5f53\u796d\u54c1\uff0c\u7d22\u6027\u62ac\u624b\u4e00\u6293\uff0c\u4e94\u6307\u751f\u751f\u63d2\u5165\u8eab\u65c1\u4e00\u4f4d\u7267\u5e08\u7684\u5934\u9885\u91cc\uff0c\u5c06\u5934\u76d6\u9aa8\u6380\u4e86\u5f00\u6765\uff0c\u767d\u8272\u7684\u8111\u6d46\u55b7\u5c04\u800c\u51fa\uff0c\u6e85\u5f97\u8001\u7267\u5e08\u4e00\u8138\u90fd\u662f\uff0c\u4ed6\u5374\u6beb\u4e0d\u5728\u4e4e\uff0c\u5c06\u624b\u63d2\u8fdb\u8111\u6d46\u91cc\uff0c\u9ad8\u58f0\u5ff5\u8bf5\u5492\u6587\u3002\u88ab\u6380\u5f00\u5934\u76d6\u9aa8\u7684\u7267\u5e08\u539f\u672c\u8fd8\u5728\u62bd\u6410\u6323\u624e\uff0c\u7247\u523b\u95f4\u4fbf\u4e0d\u518d\u52a8\u5f39\uff0c\u5168\u8eab\u6cdb\u8d77\u9752\u9ed1\u8272\u7684\u9b54\u6cd5\u7075\u5149\u6765\uff0c\u6700\u7ec8\u76ae\u8089\u6025\u901f\u840e\u7f29\uff0c\u53d8\u5f97\u7126\u9ed1\u5982\u70ad\uff0c\u6574\u4e2a\u4eba\u6210\u4e86\u4e00\u5177\u5e72\u5c38\u3002</p><p>\u8001\u7267\u5e08\u62ac\u624b\u4e00\u6325\uff0c\u90a3\u5177\u5e72\u5c38\u5e73\u5e73\u6d6e\u8d77\uff0c\u98de\u8fdb\u4e86\u6b63\u9010\u6e10\u6269\u5927\u7684\u4f4d\u9762\u88c2\u9699\u4e2d\uff0c\u968f\u5373\u9690\u9690\u6709\u79cd\u9634\u51b7\u7684\u72de\u7b11\u58f0\u4f20\u51fa\u3002\u4e00\u4e2a\u95ea\u70c1\u4e0d\u5b9a\u7684\u7ef4\u6c99\u4f26\u5723\u5fbd\u7f13\u7f13\u4ece\u4e2d\u6d6e\u73b0\uff0c\u8d8a\u53d8\u8d8a\u5927\uff0c\u60ac\u5728\u796d\u575b\u4e0a\u7a7a\uff0c\u626d\u66f2\u7740\uff0c\u5316\u4f5c\u4e00\u4e2a\u6a21\u7cca\u7684\u4eba\u5f71\uff0c\u6b63\u4e00\u70b9\u70b9\u53d8\u5f97\u6e05\u6670\u8d77\u6765\u3002</p><p>\u5deb\u5996\u4e4b\u795e\u7ef4\u6c99\u4f26\u7684\u5316\u8eab\u5373\u5c06\u964d\u4e34\u7269\u8d28\u754c\u3002</p><p>\u5728\u573a\u6240\u6709\u4eba\u90fd\u5728\u4e00\u77ac\u95f4\u540c\u65f6\u610f\u8bc6\u5230\u8fd9\u70b9\uff0c\u7ef4\u6c99\u4f26\u6559\u5f92\u4eec\u7edf\u7edf\u530d\u5310\u5728\u5730\uff0c\u5411\u795e\u7947\u9876\u793c\u671d\u62dc\uff1b\u9ad8\u7a7a\u4e2d\u7684\u9b54\u6cd5\u5927\u6218\u8fd8\u5728\u7ee7\u7eed\uff0c\u6b23\u5e03\u548c\u8428\u624e\u65af\u5766\u5df2\u7ecf\u5360\u4e86\u4e0a\u98ce\uff0c\u4f46\u4e00\u65f6\u95f4\u8fd8\u770b\u4e0d\u5230\u80dc\u5229\u7684\u5e0c\u671b\u3002\u743c\u6069\u4e0d\u660e\u767d\u738b\u5b50\u8fd8\u60a0\u95f2\u5730\u7ad9\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff0c\u4e00\u65e6\u7b49\u7ef4\u6c99\u4f26\u5316\u8eab\u964d\u4e34\uff0c\u5f62\u52bf\u5c31\u4f1a\u6025\u8f6c\u76f4\u4e0b\u4e86\u3002\u552f\u6709\u7acb\u523b\u5e2e\u5fd9\u628a\u8ff0\u5c14\u7b49\u56db\u4e2a\u7ea2\u888d\u9996\u5e2d\u5e72\u6389\uff0c\u6216\u8bb8\u8fd8\u80fd\u6709\u4e00\u6218\u4e4b\u529b\u3002</p><p>\u4f46\u738b\u5b50\u5e76\u4e0d\u52a8\u5f39\u3002</p><p>\u7ef4\u6c99\u4f26\u7684\u5316\u8eab\u6e10\u6e10\u5177\u73b0\uff0c\u4ece\u6a21\u7cca\u7684\u5f71\u5b50\u53d8\u6210\u4e86\u6e05\u6670\u7684\u5b9e\u4f53\uff0c\u4ed6\u770b\u8d77\u6765\u50cf\u662f\u4e00\u5177\u67af\u840e\u7684\u5e72\u5c38\uff0c\u62ab\u7740\u7b80\u964b\u7684\u9ed1\u888d\uff0c\u6697\u9ec4\u8272\u7684\u76ae\u80a4\u4e0a\u957f\u6ee1\u9ed1\u8272\u7684\u6591\u5757\uff0c\u624b\u4e2d\u62c4\u7740\u4e00\u6839\u957f\u957f\u7684\u6cd5\u6756\uff0c\u6756\u5934\u662f\u4e00\u9897\u9ab7\u9ac5\u5934\u9aa8\u3002</p><p>\u4ed6\u4ece\u865a\u7a7a\u4e2d\u8d70\u4e0b\u6765\uff0c\u8e0f\u4e0a\u796d\u575b\uff0c\u4f38\u624b\u53bb\u6293\u6b7b\u6708\u6cd5\u73e0\u3002\u4f46\u8fd9\u4e2a\u52a8\u4f5c\u6ca1\u6709\u6210\u529f\uff0c\u5728\u795e\u7947\u89e6\u53ca\u5230\u6cd5\u73e0\u4e4b\u524d\uff0c\u4e00\u53ea\u767d\u9aa8\u624b\u638c\u4ece\u7a7a\u6c14\u4e2d\u4f38\u51fa\uff0c\u62a2\u5148\u5c06\u90a3\u679a\u795e\u5668\u53d6\u5728\u624b\u4e2d\u3002</p><p>\u6b7b\u6708\u6cd5\u73e0\u5728\u624b\uff0c\u9690\u5f62\u6cd5\u672f\u4e5f\u5c31\u968f\u4e4b\u6d88\u5931\uff0c\u767d\u9aa8\u624b\u638c\u7684\u4e3b\u4eba\u4ece\u7a7a\u6c14\u4e2d\u663e\u73b0\u51fa\u6765\u3002\u7cbe\u81f4\u534e\u4e3d\u7684\u957f\u888d\u4e0b\u88f9\u7740\u6d88\u7626\u7684\u9ab7\u9ac5\u8eab\u4f53\uff0c\u6570\u5341\u9897\u7d2b\u7ea2\u8272\u7684\u827e\u6b27\u77f3\u5728\u5934\u9876\u65cb\u8f6c\u73af\u7ed5\uff0c\u7a7a\u6d1e\u7684\u773c\u7736\u91cc\u900f\u51fa\u5c16\u9510\u7684\u7ea2\u5149\u6765\uff0c\u548c\u796d\u575b\u4e0a\u7684\u5deb\u5996\u4e4b\u795e\u5bf9\u89c6\u7740\uff0c\u6216\u8bb8\u662f\u9519\u89c9\uff0c\u4f46\u743c\u6069\u89c9\u5f97\u4ed6\u7684\u9ab7\u9ac5\u8138\u4e0a\u4f3c\u4e4e\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u3002</p><p>\u201c\u62c9\u6c83\u514b\uff1f\u201d\u7ef4\u6c99\u4f26\u76ef\u7740\u7a81\u7136\u51fa\u73b0\u7684\u5deb\u5996\uff0c\u58f0\u97f3\u5636\u54d1\u5730\u8bf4\u3002</p></article>"}}}]);
