(window.webpackJsonp=window.webpackJsonp||[]).push([[1413],{1436:function(p){p.exports={chapterTitle:"\u7b2c\u4e94\u5377 \u9eef\u6708\u7bc7",title:"\u5e8f\u7ae0 \u795e\u8c15",content:"<article><h1>\u7b2c\u4e94\u5377 \u9eef\u6708\u7bc7</h1><h2>\u5e8f\u7ae0 \u795e\u8c15</h2><p>\u201c\u6700\u8fd1\u6709\u4ec0\u4e48\u6709\u8da3\u7684\u4e8b\u60c5\u5417\uff1f\u201d\u5e03\u96f7\u7eb3\u65af\u4e00\u8fb9\u8d70\u7740\uff0c\u4e00\u8fb9\u968f\u53e3\u95ee\u4ed6\u8eab\u65c1\u7684\u62c9\u83ab\u6765\u514b\u3002</p><p>\u548c\u5e03\u96f7\u7eb3\u65af\u4e00\u6837\uff0c\u62c9\u83ab\u6765\u514b\xb7\u5766\u8212\u5c14\u540c\u6837\u4e5f\u662f\u5341\u4e8c\u9634\u9b42\u738b\u5b50\u4e4b\u4e00\uff0c\u4ed6\u9886\u5bfc\u7740\u201c\u51b3\u5b9a\u8005\u516c\u4f1a\u201d\uff0c\u4e3b\u8981\u5de5\u4f5c\u662f\u7387\u9886\u90e8\u4e0b\u5bf9\u9634\u9b42\u57ce\u7684\u5c45\u6c11\u8fdb\u884c\u76d1\u89c6\u2026\u2026\u5443\uff0c\u6216\u8005\u8bf4\u8003\u5bdf\uff0c\u4ece\u4e2d\u6311\u9009\u51fa\u90a3\u4e9b\u5965\u672f\u5929\u8d4b\u6781\u9ad8\u7684\u5b69\u5b50\uff0c\u9001\u8fdb\u7279\u522b\u7684\u5b66\u6821\uff0c\u79d8\u5bc6\u57f9\u517b\u6210\u4e3a\u51fa\u8272\u7684\u5deb\u5e08\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u8d1f\u8d23\u4e00\u9879\u66f4\u52a0\u91cd\u8981\u7684\u5de5\u4f5c\u2014\u2014\u8fd9\u9879\u5de5\u4f5c\u662f\u5982\u6b64\u7684\u9690\u79d8\uff0c\u4ee5\u81f3\u4e8e\u8fde\u540c\u4e3a\u9634\u9b42\u738b\u5b50\u7684\u5e03\u96f7\u7eb3\u65af\u90fd\u53ea\u662f\u7565\u77e5\u5927\u6982\uff0c\u81f3\u4e8e\u5177\u4f53\u7684\u7ec6\u8282\u548c\u8fdb\u5c55\uff0c\u552f\u6709\u9634\u9b42\u57ce\u4e3b\u548c\u526f\u57ce\u4e3b\u970d\u6770\u54c8\u7eb3\u4e24\u4eba\u77e5\u6653\u3002</p><p>\u957f\u5e74\u505a\u8fd9\u79cd\u79d8\u5bc6\u5de5\u4f5c\uff0c\u591a\u5c11\u4e5f\u4f1a\u5f71\u54cd\u4eba\u7684\u6027\u60c5\uff0c\u62c9\u83ab\u6765\u514b\u5c31\u6bd4\u8f83\u9634\u6c89\uff0c\u4e0d\u7231\u8bf4\u8bdd\uff0c\u5f80\u5f80\u6574\u6574\u4e00\u5929\u90fd\u4e0d\u53d1\u4e00\u8a00\uff0c\u548c\u5176\u4ed6\u9634\u9b42\u738b\u5b50\u4e5f\u5e76\u4e0d\u4eb2\u8fd1\u3002\u4f46\u4ed6\u548c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7684\u4ea4\u60c5\u5012\u8fd8\u4e0d\u9519\u2014\u2014\u8fd9\u5e76\u975e\u662f\u7531\u4e8e\u5e03\u96f7\u7eb3\u65af\u7684\u4eba\u683c\u9b45\u529b\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u4eec\u6709\u5171\u540c\u7684\u7231\u597d\uff1a\u4e0b\u68cb\u3002</p><p>\u5e03\u96f7\u7eb3\u65af\u548c\u62c9\u83ab\u6765\u514b\u662f\u68cb\u53cb\uff0c\u4e24\u4f4d\u738b\u5b50\u95f2\u6687\u65e0\u4e8b\u7684\u65f6\u5019\uff0c\u7ecf\u5e38\u4f1a\u6446\u5f00\u68cb\u5c40\u5bf9\u5f08\u4e00\u76d8\u3002\u5fc5\u987b\u8bf4\uff0c\u8fd9\u662f\u79cd\u5f88\u6709\u6548\u7684\u4ea4\u6d41\u611f\u60c5\u7684\u65b9\u5f0f\u3002</p><p>\u201c\u6700\u8fd1\u4e48\uff0c\u6ca1\u4ec0\u4e48\u5927\u4e8b\uff0c\u201d\u62c9\u83ab\u6765\u514b\u60f3\u4e86\u60f3\uff0c\u56de\u7b54\u8bf4\uff0c\u201c\u4e0a\u6b21\u4e00\u6218\u635f\u5931\u4e0d\u5c11\uff0c\u5404\u81ea\u4fee\u6574\u2026\u2026\u5bf9\u4e86\uff0c\u5012\u662f\u6709\u53ea\u8679\u5f69\u9f99\u6765\u6363\u4e71\u3002\u201d</p><p>\u201c\u8679\u5f69\u9f99\uff1f\u201d</p><p>\u201c\u5c31\u662f\u90a3\u53ea\u53eb\u745e\u6069\u65af\u5766\u7684\u3002\u201d\u62c9\u83ab\u6765\u514b\u8bf4\u3002</p><p>\u201c\u54e6\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u70b9\u70b9\u5934\uff0c\u201c\u4ed6\u53c8\u505a\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u82e5\u8bf4\u8d77\u6765\uff0c\u8fd9\u53ea\u8679\u5f69\u9f99\u8fd8\u771f\u548c\u9634\u9b42\u57ce\u6709\u7f18\uff0c\u5148\u662f\u6765\u76d7\u7a83\u57ce\u4e3b\u7684\u6d6e\u7a7a\u8247\u88ab\u8d76\u8d70\uff0c\u540e\u6765\u53c8\u56e0\u4e3a\u63d2\u624b\u9634\u9b42\u57ce\u7684\u4e8b\u60c5\uff0c\u88ab\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bbe\u8ba1\uff0c\u8bf7\u5965\u6c83\u548c\u4e24\u4f4d\u9634\u9b42\u738b\u5b50\u201c\u6559\u80b2\u201d\u4e86\u4e00\u987f\u3002\u51ed\u501f\u7740\u8679\u5f69\u9f99\u7684\u5929\u8d4b\u5f3a\u608d\u8eab\u4f53\uff0c\u4ed6\u6bcf\u6b21\u867d\u7136\u72fc\u72c8\uff0c\u5374\u603b\u80fd\u9003\u5f97\u6027\u547d\uff0c\u5927\u6982\u662f\u4f11\u517b\u597d\u4e86\u53c8\u6765\u62a5\u590d\u3002</p><p>\u201c\u4ed6\u7ea0\u96c6\u4e86\u4e00\u7fa4\u9f99\uff0c\u5927\u7ea6\u4e8c\u5341\u591a\u5934\uff0c\u201d\u62c9\u83ab\u6765\u514b\u8bf4\uff0c\u201c\u5e26\u7740\u4ed6\u4eec\u6765\u9634\u9b42\u57ce\u5468\u56f4\u6363\u4e71\uff0c\u88ad\u51fb\u519b\u961f\uff0c\u7834\u574f\u57ce\u5899\uff0c\u8fd8\u66fe\u7ecf\u4e00\u5ea6\u653b\u8fdb\u57ce\u91cc\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u76b1\u7709\uff0c\u201c\u4ed6\u600e\u4e48\u80fd\u7ea0\u96c6\u5230\u8fd9\u4e48\u591a\u9f99\uff1f\u201d</p><p>\u9f99\u662f\u4e16\u754c\u4e0a\u6700\u5b85\u7684\u751f\u7269\uff0c\u5e73\u751f\u6700\u7231\u5e72\u7684\u4e8b\u60c5\u5c31\u662f\u547c\u547c\u7761\u5927\u89c9\u2014\u2014\u5e78\u4e8f\u5982\u6b64\uff0c\u5426\u5219\u4e16\u754c\u65e9\u88ab\u8fd9\u4e9b\u5de8\u9f99\u5360\u9886\u4e86\u3002\u745e\u6069\u65af\u5766\u7528\u4e86\u4ec0\u4e48\u65b9\u6cd5\uff0c\u5c45\u7136\u80fd\u7ea0\u96c6\u5230\u4e8c\u5341\u591a\u6761\u9f99\u6765\u653b\u6253\u9634\u9b42\u57ce\uff0c\u8fd9\u79cd\u58ee\u89c2\u573a\u9762\uff0c\u6050\u6015\u53ea\u6709\u5728\u4f20\u8bf4\u4e2d\u7684\u201c\u8fdc\u53e4\u5de8\u9f99\u65f6\u4ee3\u201d\u624d\u6709\u53ef\u80fd\u770b\u5230\u4e86\u3002</p><p>\u201c\u54e6\uff0c\u4ed6\u7528\u7684\u662f\u6700\u76f4\u622a\u4e86\u5f53\u7684\u65b9\u6cd5\uff0c\u201d\u62c9\u83ab\u6765\u514b\u8bf4\uff0c\u201c\u95ef\u8fdb\u9f99\u7a74\uff0c\u6bb4\u6253\uff0c\u628a\u8fd9\u4e9b\u9f99\u7684\u5b9d\u85cf\u641c\u522e\u4e00\u7a7a\uff0c\u7136\u540e\u52d2\u4ee4\u4ed6\u4eec\u6765\u53c2\u6218\u3002\u8fd9\u4e9b\u9f99\u90fd\u6253\u4e0d\u8fc7\u4ed6\uff0c\u53ea\u80fd\u4e56\u4e56\u542c\u547d\u3002\u201d</p><p>\u201c\u597d\u65b9\u6cd5\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u5938\u5956\uff0c\u201c\u540e\u6765\u600e\u4e48\u6837\u4e86\uff1f\u201d</p><p>\u201c\u540e\u6765\u4ed6\u624b\u4e0b\u7684\u90a3\u4e9b\u9f99\u79d8\u5bc6\u8054\u7edc\u6211\u4eec\uff0c\u8bbe\u4e86\u4e2a\u5708\u5957\uff0c\u5927\u5bb6\u8054\u624b\u628a\u4ed6\u6253\u4e86\u4e2a\u534a\u6b7b\uff0c\u53ef\u60dc\u8fd8\u662f\u88ab\u4ed6\u9003\u6389\u3002\u5fb7\u82cf\u5f97\u5728\u4ed6\u8eab\u4e0a\u4e0b\u4e86\u4e2a\u6df1\u9eef\u8bc5\u5492\uff0c\u81f3\u5c11\u5341\u5929\u534a\u6708\u91cc\uff0c\u4ed6\u662f\u522b\u60f3\u52a8\u5f39\u4e86\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u54c8\u54c8\u5927\u7b11\uff0c\u548c\u62c9\u83ab\u6765\u514b\u5e76\u80a9\u8d70\u8fdb\u4e86\u8bae\u4e8b\u5385\u3002</p><p>\u9ed1\u6697\u7684\u8bae\u4e8b\u5385\u4e2d\uff0c\u6ca1\u6709\u4efb\u4f55\u706f\u706b\u6216\u8005\u7167\u660e\uff0c\u4f46\u8fd9\u5bf9\u9634\u9b42\u738b\u5b50\u800c\u8a00\u5e76\u65e0\u4efb\u4f55\u59a8\u788d\u3002\u957f\u5f62\u8bae\u4e8b\u684c\u8fb9\uff0c\u5df2\u7ecf\u5750\u4e86\u51e0\u4e2a\u4eba\u3002\u4e3b\u5e2d\u4f4d\u662f\u7a7a\u7f6e\u7684\uff0c\u6682\u65f6\u65e0\u4eba\uff0c\u5de6\u4fa7\u7b2c\u4e00\u4f4d\u662f\u745e\u74e6\u5170\xb7\u5766\u8212\u5c14\uff0c\u9634\u9b42\u57ce\u4e3b\u7684\u957f\u5b50\uff0c\u838e\u5c14\u9996\u5e2d\u5927\u7267\u5e08\uff1b\u7b2c\u4e8c\u4f4d\u662f\u9ea6\u52d2\u521a\u7279\xb7\u5766\u8212\u5c14\uff0c\u7687\u5bb6\u65a5\u5019\u7684\u8d1f\u8d23\u4eba\uff1b\u970d\u6770\u54c8\u7eb3\u7684\u53f3\u4fa7\uff0c\u5750\u7740\u5fb7\u82cf\u5f97\xb7\u5766\u8212\u5c14\uff0c\u5c31\u662f\u521a\u624d\u5e03\u96f7\u7eb3\u65af\u548c\u62c9\u83ab\u6765\u514b\u63d0\u53ca\u7684\u90a3\u4f4d\uff0c\u5728\u8679\u5f69\u9f99\u8eab\u4e0a\u4e0b\u4e86\u4e00\u4e2a\u6df1\u9eef\u8bc5\u5492\uff0c\u4ed6\u662f\u4e00\u4f4d\u6770\u51fa\u7684\u4ea1\u7075\u5deb\u5e08\uff0c\u8d1f\u8d23\u4eba\u4e8b\u90e8\u95e8\uff0c\u540c\u65f6\u4e5f\u662f\u519b\u4e8b\u59d4\u5458\u4f1a\u7684\u6210\u5458\u4e4b\u4e00\u3002</p><p>\u5e03\u96f7\u7eb3\u65af\u548c\u62c9\u83ab\u6765\u514b\u5728\u5fb7\u82cf\u5f97\u7684\u8eab\u65c1\u4f9d\u6b21\u5750\u4e0b\u3002\u4e94\u4f4d\u9634\u9b42\u738b\u5b50\u90fd\u7a7f\u7740\u7070\u8272\u957f\u888d\uff0c\u5916\u7f69\u6597\u7bf7\uff0c\u5728\u80f8\u53e3\u90e8\u4f4d\u7ee3\u7740\u5404\u81ea\u7684\u5fbd\u7ae0\uff0c\u5728\u9ed1\u6697\u4e2d\u95ea\u70c1\u751f\u8f89\u3002\u8fd9\u4e9b\u5fbd\u7ae0\u5e76\u4e0d\u4ec5\u4ec5\u662f\u8eab\u4efd\u7684\u8fa8\u8bc6\uff0c\u540c\u65f6\u4e5f\u662f\u5730\u4f4d\u7684\u8c61\u5f81\uff0c\u66f4\u6709\u72ec\u7279\u7684\u9b54\u6cd5\u529b\u91cf\u8574\u542b\u5176\u4e2d\u3002\u5728\u9634\u9b42\u57ce\uff0c\u552f\u6709\u8db3\u591f\u9ad8\u9636\u7684\u6210\u5458\uff0c\u624d\u6709\u8d44\u683c\u6709\u80fd\u529b\u62e5\u6709\u81ea\u5df1\u7684\u5fbd\u7ae0\u3002</p><p>\u4e94\u4f4d\u9634\u9b42\u738b\u5b50\u7b80\u5355\u5730\u6253\u7740\u62db\u547c\uff0c\u6216\u8005\u6c89\u9ed8\u4e0d\u8bed\uff0c\u9759\u9759\u5730\u7b49\u5f85\u7740\u3002\u8fc7\u4e86\u5927\u7ea6\u4e94\u5206\u949f\uff0c\u4e3b\u5e2d\u4f4d\u4e0a\u7a81\u7136\u5fae\u5149\u4e00\u95ea\uff0c\u7a7a\u95f4\u626d\u66f2\u6210\u4e00\u56e2\u9ed1\u8272\u7684\u6f29\u6da1\uff0c\u4ece\u4e2d\u7f13\u7f13\u6e38\u51fa\u4e00\u6761\u94f6\u8272\u7684\u7ffc\u86c7\u6765\uff0c\u8fd9\u662f\u526f\u57ce\u4e3b\u970d\u6770\u54c8\u7eb3\u7684\u5fbd\u7ae0\u3002\u7d27\u63a5\u7740\uff0c\u9ed1\u8272\u6f29\u6da1\u548c\u94f6\u8272\u7ffc\u86c7\u540c\u65f6\u6d88\u5931\uff0c\u5927\u5deb\u5e08\u4ece\u865a\u7a7a\u4e2d\u73b0\u51fa\u8eab\u5f62\uff0c\u7aef\u5750\u5728\u4e3b\u5e2d\u4f4d\u4e0a\uff0c\u773c\u5149\u4ece\u4e94\u4f4d\u738b\u5b50\u8138\u4e0a\u626b\u8fc7\u3002</p><p>\u201c\u90fd\u6765\u9f50\u4e86\uff0c\u90a3\u4e48\u5f00\u59cb\u5427\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u5341\u4e8c\u9634\u9b42\u738b\u5b50\uff0c\u5e76\u975e\u90fd\u613f\u610f\u53c2\u4e0e\u653f\u4e8b\uff0c\u4fbf\u5982\u79d1\u62c9\u745e\u535a\u7eb3\u65af\uff0c\u66f4\u559c\u6b22\u5728\u519b\u961f\u91cc\u4e0e\u58eb\u5175\u6253\u6210\u4e00\u56e2\uff0c\u53c8\u5982\u739b\u63d0\u514b\u3001\u74e6\u63d0\u514b\u8fd9\u5bf9\u53cc\u80de\u80ce\u5144\u5f1f\uff0c\u6c89\u8ff7\u4e8e\u5e7b\u672f\u548c\u70bc\u91d1\u672f\u7684\u5b66\u672f\u7814\u7a76\uff0c\u518d\u5982\u827e\u683c\u62c9\u745e\u662f\u7687\u5bb6\u536b\u961f\u7684\u961f\u957f\uff0c\u96c5\u8fbe\u662f\u795e\u6bbf\u536b\u961f\u7684\u961f\u957f\uff0c\u4ed6\u4eec\u90fd\u662f\u4e0d\u4f1a\u53c2\u4e0e\u8fd9\u79cd\u653f\u6cbb\u4f1a\u8bae\u7684\u3002</p><p>\u8fd9\u6b21\u4f1a\u8bae\u8981\u8ba8\u8bba\u7684\u8bae\u9898\uff0c\u662f\u9634\u9b42\u57ce\u4e0b\u4e00\u6b65\u7684\u51b3\u7b56\u8d70\u5411\u3002\u81ea\u4ece\u5e74\u521d\u56de\u5230\u7269\u8d28\u754c\uff0c\u5982\u4eca\u5df2\u7ecf\u8fc7\u53bb\u5927\u534a\u5e74\uff0c\u9634\u9b42\u57ce\u521b\u9020\u9634\u5f71\u5927\u5e55\u7684\u8ba1\u5212\u88ab\u7834\u574f\uff0c\u548c\u79d1\u7c73\u5c14\u3001\u6df1\u6c34\u57ce\u3001\u94f6\u6708\u8054\u90a6\u7684\u8054\u519b\u6253\u4e86\u51e0\u4ed7\uff0c\u5404\u6709\u635f\u4f24\uff0c\u6682\u65f6\u4f11\u6218\u3002\u63a5\u4e0b\u6765\u8981\u505a\u4ec0\u4e48\u2014\u2014\u6216\u8005\u8bf4\uff0c\u5982\u4f55\u624d\u80fd\u8fbe\u6210\u6062\u590d\u8010\u745f\u5e1d\u56fd\u7684\u76ee\u6807\uff0c\u8fd9\u662f\u4e2a\u6446\u5728\u9762\u524d\u7684\u95ee\u9898\u3002</p><p>\u201c\u9996\u8981\u7684\u95ee\u9898\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u6269\u5f20\u519b\u5907\uff0c\u201d\u5fb7\u82cf\u5f97\u8bf4\uff0c\u201c\u81ea\u4ece\u56de\u5230\u7269\u8d28\u754c\uff0c\u81f3\u4eca\u65e5\u4e3a\u6b62\uff0c\u6211\u4eec\u63a0\u593a\u5230\u4e86\u5927\u7ea6\u4e00\u4e07\u4e94\u5343\u540d\u5974\u96b6\uff0c\u5176\u4e2d\u5341\u5206\u4e4b\u4e00\u8865\u5145\u8fdb\u4e86\u519b\u961f\uff0c\u4f46\u662f\uff0c\u201d\u4ed6\u53f9\u6c14\uff0c\u201c\u8fd9\u5df2\u7ecf\u662f\u57ce\u5e02\u7684\u627f\u53d7\u6781\u9650\u4e86\u3002\u201d</p><p>\u5176\u4ed6\u4eba\u90fd\u9ed8\u7136\uff0c\u9634\u9b42\u57ce\u6700\u5927\u7684\u95ee\u9898\uff0c\u5c31\u5728\u4e8e\u5b83\u662f\u4e00\u5ea7\u5b64\u57ce\uff0c\u5730\u76d8\u65e2\u5c0f\uff0c\u4eba\u53e3\u4e5f\u5c11\uff0c\u66f4\u8c08\u4e0d\u4e0a\u591a\u5c11\u8d44\u6e90\uff0c\u8fd9\u5c31\u96be\u4ee5\u652f\u6491\u8d77\u8db3\u591f\u5f3a\u5927\u7684\u519b\u5907\u3002\u800c\u6ca1\u6709\u8db3\u591f\u5f3a\u5927\u7684\u519b\u5907\uff0c\u8981\u6062\u590d\u8010\u745f\u4e0d\u8fc7\u5c31\u662f\u7a7a\u8c08\u3002</p><p>\u5fb7\u82cf\u5f97\u6325\u624b\uff0c\u5728\u684c\u4e0a\u5c55\u5f00\u4e00\u5f20\u5730\u56fe\uff0c\u201c\u6211\u4eec\u7684\u5468\u56f4\uff0c\u662f\u5e7f\u9614\u65e0\u9645\u7684\u6c99\u6f20\uff0c\u5b83\u65e2\u66ff\u6211\u4eec\u963b\u6321\u4e86\u654c\u4eba\u7684\u8fdb\u653b\uff0c\u540c\u65f6\u4e5f\u662f\u6211\u4eec\u5411\u5916\u6269\u5c55\u7684\u969c\u788d\uff0c\u201d\u4ed6\u7684\u624b\u6307\u79fb\u52a8\u7740\uff0c\u201c\u6c99\u6f20\u4e4b\u5916\uff0c\u6211\u4eec\u88ab\u5f3a\u5927\u7684\u56fd\u5bb6\u548c\u57ce\u90a6\u73af\u7ed5\uff0c\u4e1c\u65b9\u662f\u6563\u5854\u6797\u4f1a\u63a7\u5236\u7684\u6708\u6d77\u5730\u533a\uff0c\u6211\u8ba4\u4e3a\u6211\u4eec\u76ee\u524d\u4e0d\u5b9c\u548c\u5b83\u53d1\u751f\u51b2\u7a81\uff1b\u4e1c\u5357\u65b9\u5411\u662f\u79d1\u7c73\u5c14\u738b\u56fd\uff0c\u6211\u4eec\u5df2\u7ecf\u548c\u4ed6\u4eec\u4ea4\u6218\u8fc7\uff1b\u897f\u5357\u662f\u827e\u5f17\u62c9\u65af\u5361\uff0c\u6211\u4eec\u66fe\u7ecf\u60f3\u653b\u5360\u5b83\uff0c\u4f46\u6ca1\u6709\u6210\u529f\uff1b\u897f\u5317\u65b9\u662f\u94f6\u6708\u8054\u90a6\uff0c\u4ee5\u53ca\u540e\u9762\u7684\u6df1\u6c34\u57ce\uff0c\u516c\u5141\u5730\u8bf4\uff0c\u4ed6\u4eec\u79f0\u5f97\u4e0a\u662f\u76ee\u524d\u7269\u8d28\u754c\u6700\u5f3a\u5927\u7684\u52bf\u529b\uff0c\u201d\u4ed6\u6447\u7740\u5934\uff0c\u201c\u6211\u4eec\u9700\u8981\u627e\u5230\u4e00\u5757\u5408\u9002\u7684\u5730\u65b9\u4f5c\u4e3a\u6839\u57fa\uff0c\u800c\u4e0d\u662f\u50cf\u73b0\u5728\u8fd9\u6837\u5728\u7a7a\u4e2d\u6f02\u6d6e\uff0c\u4f46\u8fd9\u4f3c\u4e4e\u5f88\u96be\u3002\u201d</p><p>\u201c\u8ff7\u65af\u5353\u8bfa\u3002\u201d\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bf4\u3002</p><p>\u5fb7\u82cf\u5f97\u6447\u5934\uff0c\u201c\u539f\u5b9a\u8ba1\u5212\u662f\u8fd9\u6837\uff0c\u4f46\u73b0\u5728\u770b\u8d77\u6765\uff0c\u8fd9\u4e2a\u8ba1\u5212\u8981\u505a\u4e00\u4e9b\u6539\u53d8\u4e86\uff0c\u201d\u4ed6\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u53e0\u7eb8\u6765\uff0c\u5728\u6bcf\u4f4d\u738b\u5b50\u9762\u524d\u653e\u4e86\u4e00\u4efd\uff0c\u4f46\u6ca1\u6709\u9012\u7ed9\u970d\u6770\u54c8\u7eb3\uff0c\u201c\u534a\u5c0f\u65f6\u524d\u6211\u521a\u521a\u63a5\u5230\u7684\u60c5\u62a5\uff0c\u6c38\u805a\u5c9b\u7684\u7cbe\u7075\u4eec\u6709\u5927\u52a8\u4f5c\u3002\u201d</p><p>\u201c\u62a2\u5728\u7cbe\u7075\u4eec\u884c\u52a8\u4e4b\u524d\uff0c\u6211\u4eec\u53ef\u4ee5\u53bb\u8ff7\u65af\u5353\u8bfa\u53d6\u56de\u5377\u8f74\uff0c\u201d\u970d\u6770\u54c8\u7eb3\u6162\u6162\u5f00\u53e3\uff0c\u201c\u4f46\u90a3\u4e2a\u5730\u65b9\u5df2\u7ecf\u4e0d\u9002\u5408\u4f5c\u4e3a\u957f\u671f\u7684\u57fa\u5730\u4e86\u3002\u6211\u4eec\u9700\u8981\u53e6\u5916\u9009\u62e9\u4e00\u5757\u5730\u65b9\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u7709\u5934\u7d27\u9501\u7740\uff0c\u8fd9\u4e2a\u7a81\u7136\u7684\u6d88\u606f\u6253\u4e71\u4e86\u4ed6\u539f\u672c\u7684\u76d8\u7b97\uff0c\u5176\u4ed6\u738b\u5b50\u4e5f\u540c\u6837\u5728\u6c89\u601d\uff0c\u4f46\u6700\u540e\uff0c\u9996\u5e2d\u5927\u7267\u5e08\u745e\u74e6\u5170\u6253\u7834\u4e86\u5bc2\u9759\u3002</p><p>\u201c\u5b9e\u9645\u4e0a\uff0c\u6211\u8ba4\u4e3a\u6211\u4eec\u7684\u773c\u5149\u5e94\u8be5\u53d8\u5f97\u66f4\u52a0\u5f00\u9614\u4e00\u4e9b\u624d\u5bf9\uff0c\u201d\u4ed6\u4e0d\u52a8\u58f0\u8272\u5730\u8bf4\uff0c\u201c\u6709\u4e00\u5757\u5730\u65b9\uff0c\u6bd4\u8ff7\u65af\u5353\u8bfa\u66f4\u52a0\u9002\u5408\u767e\u500d\uff0c\u5b8c\u5168\u7b26\u5408\u6211\u4eec\u76ee\u524d\u7684\u6240\u6709\u8981\u6c42\u3002\u201d</p><p>\u201c\u90a3\u91cc\u6709\u5e7f\u9614\u7684\u5730\u57df\u53ef\u4ee5\u4f9b\u6211\u4eec\u8bad\u7ec3\u519b\u961f\uff0c\u6709\u4e30\u5bcc\u7684\u77ff\u4ea7\u8d44\u6e90\u53ef\u4ee5\u4f9b\u6211\u4eec\u5236\u9020\u6b66\u5668\uff0c\u6709\u8db3\u591f\u591a\u800c\u4e14\u8db3\u591f\u5f3a\u58ee\u7684\u5974\u96b6\u53ef\u4ee5\u5145\u5f53\u58eb\u5175\uff0c\u5c31\u7b97\u8981\u5efa\u5341\u4e2a\u5e08\u90fd\u7ef0\u7ef0\u6709\u4f59\uff0c\u800c\u4e14\u8fd9\u4e00\u5207\u51b3\u4e0d\u4f1a\u5f15\u8d77\u90a3\u4e9b\u8ba8\u538c\u7684\u788d\u4e8b\u8005\u7684\u6ce8\u610f\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u548c\u4ed6\u4eec\u53d1\u751f\u4efb\u4f55\u51b2\u7a81\uff0c\u201d\u4ed6\u73af\u89c6\u7740\u526f\u57ce\u4e3b\u548c\u4ed6\u7684\u5f1f\u5f1f\u4eec\uff0c\u4f38\u624b\u5728\u5730\u56fe\u4e0a\u4e00\u5708\uff0c\u201c\u8fd9\u91cc\u3002\u201d</p><p>\u6240\u6709\u4eba\u987a\u7740\u4ed6\u7684\u624b\u6307\u770b\u53bb\uff0c\u7ed3\u679c\u53d1\u73b0\u4ed6\u6307\u5411\u7684\u5730\u65b9\u5c31\u662f\u9634\u9b42\u57ce\u6240\u5728\u7684\u8fd9\u7247\u5927\u6c99\u6f20\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5fb7\u82cf\u5f97\u7591\u60d1\u5730\u95ee\u3002</p><p>\u201c\u4e0b\u9762\uff0c\u201d\u745e\u74e6\u5170\u8bf4\uff0c\u201c\u6c99\u6f20\u4e0b\u9762\u3002\u201d</p><p>\u201c\u5e7d\u6697\u5730\u57df\uff1f\u201d\u5fb7\u82cf\u5f97\u4e00\u6014\uff0c\u968f\u5373\u8fde\u8fde\u6447\u5934\uff0c\u201c\u8352\u5510\uff0c\u201d\u4ed6\u6beb\u4e0d\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u4f60\u8981\u77e5\u9053\uff0c\u81ea\u4ece\u2018\u5927\u6c89\u964d\u2019\u4ee5\u6765\uff0c\u5c31\u4ece\u6ca1\u6709\u90a3\u652f\u5730\u8868\u519b\u961f\u6210\u529f\u653b\u9677\u8fc7\u5e7d\u6697\u5730\u57df\u3002\u201d</p><p>\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u4f46\u8fd9\u6b21\u4e0d\u540c\uff0c\u201d\u745e\u74e6\u5170\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u8fd9\u6b21\u6211\u4eec\u6709\u5973\u795e\u7684\u5e87\u4f51\u3002\u201d</p><p>\u201c\u90a3\u4e9b\u5353\u5c14\u4eec\u4e5f\u6709\u8718\u86db\u7684\u5e87\u4f51\u3002\u201d</p><p>\u745e\u74e6\u5170\u5634\u89d2\u7684\u7b11\u610f\u66f4\u6df1\u4e86\uff0c\u201c\u95ee\u9898\u7684\u5173\u952e\u6070\u6070\u5c31\u5728\u8fd9\u91cc\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5973\u795e\u7684\u795e\u8c15\u6307\u793a\uff1a\u90a3\u4e9b\u9ed1\u76ae\u80a4\u7684\u5353\u5c14\u4eec\uff0c\u5df2\u7ecf\u6ca1\u6709\u8718\u86db\u7684\u5e87\u4f51\u4e86\u3002\u201d</p></article>"}}}]);
