(window.webpackJsonp=window.webpackJsonp||[]).push([[1414],{1437:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e00\u8282 \u670b\u53cb",content:"<article><h2>\u7b2c\u4e00\u8282 \u670b\u53cb</h2><p>\u5728\u585e\u5c14\u65f6\uff0c\u60f3\u5230\u9a6c\u4e0a\u5c31\u80fd\u56de\u9634\u9b42\u57ce\u89c1\u5230\u59d0\u59d0\uff0c\u743c\u6069\u5fc3\u4e2d\u4fbf\u5174\u594b\u4e0d\u5df2\uff0c\u7136\u800c\u603b\u89c9\u5f97\u4f3c\u4e4e\u54ea\u5730\u65b9\u4e0d\u592a\u5bf9\u52b2\u2014\u2014\u5230\u5e95\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\u52b2\uff0c\u5374\u662f\u600e\u4e48\u60f3\u4e5f\u60f3\u4e0d\u8d77\u6765\uff0c\u53ea\u597d\u5f53\u4f5c\u81ea\u5df1\u795e\u7ecf\u8fc7\u654f\u3002</p><p>\u5982\u4eca\u771f\u56de\u5230\u9634\u9b42\u57ce\uff0c\u56de\u5230\u5bb6\u4e2d\uff0c\u4ed6\u624d\u7ec8\u4e8e\u60f3\u8d77\u6765\u662f\u600e\u4e48\u56de\u4e8b\u2026\u2026\u7136\u800c\u5df2\u7ecf\u665a\u4e86\u3002</p><p>\u5750\u5728\u9910\u684c\u8fb9\uff0c\u4e24\u4e2a\u5973\u5b69\u5b50\u8c08\u7b11\u81ea\u5982\uff0c\u73ca\u5609\u770b\u8d77\u6765\u5f88\u559c\u6b22\u6885\u83f2\u65af\uff0c\u62c9\u7740\u5979\u7684\u624b\u8bf4\u4e2a\u4e0d\u505c\uff0c\u6885\u83f2\u65af\u4e0d\u592a\u64c5\u957f\u8fd9\u4e9b\u5bb6\u5e38\u95f2\u8c08\uff0c\u5f00\u59cb\u6709\u4e9b\u5c40\u4fc3\uff0c\u4f46\u6bd5\u7adf\u90fd\u662f\u5973\u5b69\u5b50\uff0c\u5f88\u5feb\u4e5f\u6e10\u6e10\u653e\u677e\u4e0b\u6765\u3002\u5979\u4eec\u8bf4\u5f97\u9ad8\u5174\uff0c\u5012\u628a\u743c\u6069\u5b8c\u5168\u6254\u5728\u4e00\u8fb9\uff0c\u4eff\u4f5b\u4ed6\u4e0d\u5b58\u5728\u4f3c\u7684\u3002\u6885\u83f2\u65af\u7cbe\u901a\u5404\u79cd\u8bed\u8a00\uff0c\u8fde\u4eba\u9762\u72ee\u548c\u72ec\u89d2\u517d\u90fd\u80fd\u6d41\u5229\u4ea4\u8c08\uff0c\u5728\u8010\u745f\u8bed\u4e0a\u7684\u9020\u8be3\u4f3c\u4e4e\u4e5f\u4e0d\u592a\u5dee\uff0c\u548c\u73ca\u5609\u4ea4\u8c08\uff0c\u521d\u65f6\u8fd8\u6709\u4e9b\u751f\u6da9\uff0c\u6e10\u6e10\u4fbf\u987a\u7545\u6d41\u5229\u8d77\u6765\u3002</p><p>\u6309\u9053\u7406\u8bf4\uff0c\u770b\u5230\u4e24\u4f4d\u5973\u5b69\u5b50\u5173\u7cfb\u5982\u6b64\u878d\u6d3d\uff0c\u743c\u6069\u5e94\u8be5\u5f88\u9ad8\u5174\u624d\u5bf9\u2026\u2026\u4f46\u4e3a\u4ec0\u4e48\uff0c\u603b\u662f\u89c9\u5f97\u80cc\u540e\u5192\u51b7\u6c14\u5462\u3002</p><p>\u56e0\u4e3a\u6ca1\u6599\u5230\u743c\u6069\u7a81\u7136\u56de\u6765\uff0c\u665a\u9910\u4e5f\u5f88\u7b80\u5355\uff0c\u548c\u4ee5\u524d\u5728\u5e73\u6c11\u533a\u7684\u65f6\u5019\u51e0\u4e4e\u6ca1\u591a\u5927\u533a\u522b\u3002\u5982\u4eca\u5170\u5c3c\u65af\u7279\u5bb6\u5df2\u7ecf\u664b\u5347\u4e3a\u8d35\u65cf\uff0c\u743c\u6069\u662f\u516c\u52a1\u5458\uff0c\u96b6\u5c5e\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u90a3\u4e2a\u4ec0\u4e48\u8003\u53e4\u90e8\u95e8\uff0c\u867d\u7136\u4ed6\u4eba\u4e0d\u5728\u57ce\u4e2d\uff0c\u4f46\u85aa\u6c34\u8fd8\u662f\u7167\u6837\u53d1\u5230\u73ca\u5609\u624b\u91cc\u3002\u53ea\u8981\u73ca\u5609\u613f\u610f\uff0c\u7eb5\u7136\u4e0d\u8bf4\u9526\u8863\u7389\u98df\uff0c\u4e5f\u5b8c\u5168\u4e0d\u5fc5\u8fd9\u6837\u8282\u4fed\uff0c\u4f46\u5979\u53ea\u5fae\u5fae\u7b11\u7b11\uff0c\u8bf4\u201c\u4e60\u60ef\u4e86\u201d\u3002</p><p>\u4e0d\u8fc7\u6539\u53d8\u4e5f\u8fd8\u662f\u6709\u7684\uff0c\u665a\u9910\u867d\u7136\u6ca1\u6709\u53d8\u5f97\u66f4\u7cbe\u7f8e\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u662f\u6bd4\u4ee5\u524d\u66f4\u96be\u5403\u4e86\u70b9\uff0c\u56e0\u4e3a\u53a8\u5e08\u6362\u4e86\u4eba\u3002\u4ee5\u524d\u662f\u73ca\u5609\u4eb2\u81ea\u4e0b\u53a8\uff0c\u5982\u4eca\u662f\u4e2a\u5973\u4ec6\uff0c\u624b\u827a\u4f3c\u4e4e\u4e0d\u600e\u4e48\u6837\u3002\u743c\u6069\u968f\u53e3\u95ee\u8d77\u8fd9\u4e2a\u5973\u4ec6\u7684\u6765\u5386\uff0c\u73ca\u5609\u8bf4\u662f\u670b\u53cb\u9001\u7684\u3002\u743c\u6069\u6709\u70b9\u5947\u602a\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5728\u9634\u9b42\u57ce\u91cc\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u670b\u53cb\u2014\u2014\u5c31\u7b97\u6709\uff0c\u4e5f\u662f\u4ee5\u524d\u5728\u5e73\u6c11\u533a\u7684\u670b\u53cb\uff0c\u600e\u4e48\u53ef\u80fd\u9001\u5f97\u8d77\u5973\u4ec6\u3002</p><p>\u4f46\u73ca\u5609\u5fd9\u4e8e\u548c\u6885\u83f2\u65af\u4ea4\u6d41\u611f\u60c5\uff0c\u61d2\u5f97\u7406\u776c\u4ed6\u8fd9\u4e2a\u8fdc\u9053\u5f52\u6765\u7684\u5f1f\u5f1f\uff0c\u4e5f\u6ca1\u7a7a\u591a\u89e3\u91ca\uff0c\u743c\u6069\u53ea\u8981\u90c1\u95f7\u5730\u72ec\u81ea\u5bf9\u4ed8\u9762\u524d\u7684\u90a3\u4efd\u725b\u6392\u3002\u5b9e\u9645\u4e0a\uff0c\u4ed6\u77e5\u9053\u73ca\u5609\u6050\u6015\u6709\u70b9\u4e0d\u592a\u9ad8\u5174\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u6ca1\u529e\u6cd5\u3002</p><p>\u665a\u9910\u540e\uff0c\u73ca\u5609\u5e26\u7740\u743c\u6069\u548c\u6885\u83f2\u65af\u719f\u6089\u65b0\u5c45\uff0c\u8fd9\u662f\u4e00\u5ea7\u4e24\u5c42\u7684\u5c0f\u697c\uff0c\u6a21\u6837\u7565\u6709\u4e9b\u53e4\u65e7\uff0c\u4ee5\u524d\u4e0d\u77e5\u5f52\u5c5e\u54ea\u4e2a\u6ca1\u843d\u7684\u5bb6\u65cf\u6240\u6709\uff0c\u73ca\u5609\u642c\u8fdb\u6765\u540e\u4e5f\u5e76\u6ca1\u6709\u8fc7\u591a\u7684\u6539\u9020\uff0c\u57fa\u672c\u8fd8\u662f\u4fdd\u6301\u539f\u72b6\u3002\u697c\u4e0b\u662f\u5ba2\u5385\u3001\u53a8\u623f\u548c\u536b\u751f\u95f4\uff0c\u4ee5\u53ca\u4e00\u4e2a\u9690\u853d\u7684\u5deb\u5e08\u5b9e\u9a8c\u5ba4\uff0c\u697c\u4e0a\u5219\u662f\u5367\u5ba4\u548c\u4e66\u623f\u3002</p><p>\u201c\u8fd9\u662f\u4ed6\u7684\u5367\u5ba4\uff0c\u201d\u73ca\u5609\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u5c31\u4f4f\u8fd9\u95f4\u5427\uff0c\u6211\u5c31\u5728\u9694\u58c1\u3002\u201d</p><p>\u201c\u90a3\u6211\u4f4f\u54ea\u91cc\uff1f\u201d\u743c\u6069\u5728\u540e\u9762\u63d2\u5634\u95ee\u3002</p><p>\u201c\u4f60\u7761\u4e66\u623f\u53bb\uff0c\u62b1\u5e8a\u88ab\u5b50\u94fa\u5728\u5730\u4e0a\u5c31\u884c\u4e86\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u5bf9\u8fd9\u4e2a\u7ed3\u679c\u53ea\u80fd\u8868\u793a\u63a5\u53d7\u3002\u56e0\u4e3a\u6ca1\u60f3\u5230\u4ed6\u4f1a\u5e26\u4eba\u56de\u6765\uff0c\u73ca\u5609\u53ea\u51c6\u5907\u4e86\u4e09\u4e2a\u5367\u5ba4\uff0c\u81ea\u5df1\u3001\u5973\u4ec6\u548c\u743c\u6069\uff0c\u4e00\u4eba\u4e00\u95f4\u3002\u5982\u4eca\u591a\u4e86\u6885\u83f2\u65af\uff0c\u81ea\u7136\u5c31\u9700\u8981\u628a\u743c\u6069\u7ed9\u8d76\u8d70\u4e86\u3002</p><p>\u7761\u4e66\u623f\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53cd\u6b63\u5728\u5916\u9762\u7684\u65f6\u5019\u8fde\u6df1\u5c71\u8352\u91ce\u90fd\u5bbf\u8425\u8fc7\uff0c\u743c\u6069\u5bf9\u6b64\u5e76\u4e0d\u5728\u4e4e\u3002\u4ed6\u771f\u6b63\u5728\u4e4e\u7684\u662f\uff0c\u5bb6\u91cc\u660e\u660e\u6709\u4e24\u4e2a\u6f02\u4eae\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u90fd\u662f\u81ea\u5df1\u559c\u6b22\u7684\uff0c\u90a3\u4e48\u6709\u4ec0\u4e48\u7406\u7531\u8981\u72ec\u81ea\u4e00\u4eba\u5165\u7761\u5462\u3002</p><p>\u4e0d\u8fc7\uff0c\u6709\u4e9b\u4e8b\u60c5\u4f3c\u4e4e\u6682\u65f6\u8fd8\u4e0d\u5b9c\u505a\u5f97\u592a\u660e\u663e\u2026\u2026</p><p>\u534a\u591c\u65f6\u5206\uff0c\u4f30\u6478\u7740\u73ca\u5609\u548c\u6885\u83f2\u65af\u90fd\u5e94\u8be5\u7761\u7740\u4e86\uff0c\u4ed6\u6084\u6084\u5730\u51fa\u4e86\u623f\u95f4\uff0c\u8d70\u5230\u73ca\u5609\u7684\u5367\u5ba4\u95e8\u53e3\u3002\u4f38\u624b\u63a8\u95e8\uff0c\u610f\u5916\u5730\u53d1\u73b0\u6839\u672c\u5c31\u6ca1\u9501\u4e0a\uff0c\u53ea\u662f\u865a\u63a9\u7740\uff0c\u743c\u6069\u8f7b\u8f7b\u8d70\u4e86\u8fdb\u6765\uff0c\u53cd\u624b\u628a\u95e8\u5173\u4e0a\u3002\u73ca\u5609\u5e94\u8be5\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u623f\u95f4\u91cc\u4e00\u7247\u6f06\u9ed1\u5bc2\u9759\uff0c\u5e78\u597d\u4ed6\u65e9\u6709\u51c6\u5907\uff0c\u4e8b\u5148\u5df2\u7ecf\u7ed9\u81ea\u5df1\u65bd\u52a0\u4e86\u9ed1\u6697\u89c6\u89c9\u3002\u51ed\u501f\u9b54\u6cd5\u7684\u5e2e\u52a9\uff0c\u4ed6\u6e05\u695a\u5730\u770b\u89c1\u4e86\u5e8a\u7684\u4f4d\u7f6e\uff0c\u73ca\u5609\u6b63\u88f9\u5728\u6bdb\u6bef\u91cc\uff0c\u4fa7\u8eab\u5411\u5185\u7761\u7740\u3002</p><p>\u743c\u6069\u8d70\u5230\u5e8a\u524d\uff0c\u6b63\u72b9\u8c6b\u662f\u76f4\u63a5\u6e9c\u4e0a\u5e8a\u8fd8\u662f\u5148\u53eb\u9192\u5979\uff0c\u73ca\u5609\u7a81\u7136\u7ffb\u8fc7\u8eab\u6765\uff0c\u7741\u5f00\u773c\u775b\u770b\u7740\u4ed6\u3002\u201c\u5e72\u561b\u5462\uff0c\u201d\u5979\u95ee\uff0c\u5bf9\u5f1f\u5f1f\u534a\u591c\u8dd1\u5230\u81ea\u5df1\u5367\u5ba4\u91cc\u8fd9\u4ef6\u4e8b\u4f3c\u4e4e\u5e76\u4e0d\u611f\u89c9\u4e1d\u6beb\u60ca\u8bb6\uff0c\u201c\u5f88\u665a\u4e86\uff0c\u8fd8\u4e0d\u56de\u53bb\u7761\u89c9\u3002\u201d</p><p>\u201c\u4f46\u6211\u7761\u4e0d\u7740\uff0c\u60f3\u59d0\u59d0\u966a\u7740\u7761\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u90fd\u8fd9\u4e48\u5927\u4eba\u4e86\uff0c\u8fd8\u548c\u59d0\u59d0\u7761\u5728\u4e00\u8d77\u50cf\u4ec0\u4e48\u6837\u5b50\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u743c\u6069\u5df2\u7ecf\u4e09\u4e0b\u4e94\u9664\u4e8c\u8131\u6389\u5916\u5957\uff0c\u94bb\u8fdb\u4e86\u6bef\u5b50\u91cc\u3002\u4ed6\u62b1\u7740\u73ca\u5609\u7684\u8170\uff0c\u5c06\u8eab\u4f53\u7d27\u8d34\u7740\uff0c\u50cf\u5b69\u5b50\u4e00\u6837\u628a\u8138\u57cb\u5728\u59d0\u59d0\u80f8\u53e3\uff0c\u9694\u7740\u7761\u8863\uff0c\u4e00\u80a1\u719f\u6089\u7684\u4e73\u9999\u6251\u9762\u800c\u6765\u3002\u201c\u771f\u8f6f\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u81ea\u8a00\u81ea\u8bed\uff0c\u201c\u600e\u4e48\u597d\u50cf\u53c8\u53d8\u5927\u4e86\u2026\u2026\u201d</p><p>\u201c\u5582\u5582\uff0c\u5e72\u561b\u5462\uff0c\u201d\u73ca\u5609\u8f7b\u8f7b\u62cd\u7740\u4ed6\u7684\u8111\u888b\uff0c\u201c\u4e0a\u6765\uff0c\u7761\u5230\u6795\u5934\u4e0a\u6765\uff0c\u53c8\u4e0d\u662f\u5c0f\u5b69\u5b50\uff0c\u90a3\u4e48\u559c\u6b22\u7f29\u5728\u5988\u5988\u6000\u91cc\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u5f53\u6211\u662f\u5c0f\u5b69\u5b50\u4e0d\u5c31\u884c\u4e86\u3002\u201d</p><p>\u201c\u5c0f\u5b69\u5b50\uff1f\u4f60\u591a\u5927\u4e86\uff1f\u201d</p><p>\u201c\u5341\u4e94\u2026\u2026\u5341\u4e94\u4e2a\u6708\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5341\u4e94\u4e2a\u6708\u554a\uff0c\u201d\u73ca\u5609\u63c9\u63c9\u4ed6\u7684\u5934\u53d1\uff0c\u201c\u73b0\u5728\u7684\u5b69\u5b50\u53d1\u80b2\u771f\u5feb\uff0c\u5341\u4e94\u4e2a\u6708\u5c31\u957f\u8fd9\u4e48\u5927\u4e86\u3002\u201d</p><p>\u201c\u90a3\u6709\u4ec0\u4e48\uff0c\u5341\u4e94\u4e2a\u6708\u7684\u5c0f\u8c61\u6bd4\u6211\u5927\u591a\u4e86\u5462\u3002\u201d</p><p>\u73ca\u5609\u6014\u4e86\u4e0b\uff0c\u8f7b\u8f7b\u7b11\u4e86\u8d77\u6765\uff0c\u4f38\u81c2\u6e29\u67d4\u5730\u5c06\u743c\u6069\u62b1\u5728\u6000\u91cc\uff0c\u4efb\u4ed6\u7528\u8138\u5728\u80f8\u53e3\u4e0d\u6000\u597d\u610f\u5730\u8e6d\u6765\u8e6d\u53bb\uff0c\u201c\u4f60\u8fd9\u5c0f\u5bb6\u4f19\uff0c\u201d\u5979\u957f\u957f\u5410\u4e86\u53e3\u6c14\uff0c\u4eff\u4f5b\u5982\u91ca\u91cd\u8d1f\uff0c\u201c\u603b\u7b97\u56de\u6765\u4e86\uff0c\u628a\u6211\u62c5\u5fc3\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u59d0\u59d0\uff0c\u6211\u4e5f\u6ca1\u60f3\u5230\u4f1a\u7a81\u7136\u63a5\u5230\u4efb\u52a1\u5440\uff0c\u201d\u4ed6\u62b1\u6028\u7740\uff0c\u4e00\u8fb9\u5077\u5077\u7528\u7259\u9f7f\u54ac\u5f00\u73ca\u5609\u7761\u8863\u80f8\u524d\u7684\u7ebd\u6263\uff0c\u201c\u800c\u4e14\u8fd8\u8fd9\u4e48\u9ebb\u70e6\uff0c\u9669\u4e9b\u90fd\u56de\u4e0d\u6765\u4e86\u3002\u201d</p><p>\u201c\u5916\u9762\u5f88\u5371\u9669\u4e48\u3002\u201d</p><p>\u201c\u55ef\uff0c\u4e00\u8def\u4e0a\u649e\u5230\u7684\u5168\u662f\u53d8\u6001\uff0c\u5c31\u6ca1\u4e00\u4e2a\u6b63\u5e38\u4eba\u2026\u2026\u4e0d\u5bf9\uff0c\u51c6\u786e\u6765\u8bf4\uff0c\u662f\u2018\u4eba\u2019\u90fd\u6ca1\u51e0\u4e2a\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u6885\u83f2\u65af\u5c0f\u59d0\u4e0d\u7b97\u662f\u6b63\u5e38\u4eba\u4e48\u3002\u201d</p><p>\u201c\u5979\u5f53\u7136\u4e0d\u662f\u6b63\u5e38\u2026\u2026\u201d\u743c\u6069\u4e00\u8fb9\u52aa\u529b\u5bf9\u4ed8\u7740\u7b2c\u4e09\u7c92\u7ebd\u6263\uff0c\u4e00\u8fb9\u968f\u53e3\u56de\u7b54\uff0c\u773c\u524d\u5df2\u7ecf\u9690\u7ea6\u53ef\u89c1\u7684\u6625\u5149\u628a\u4ed6\u7684\u6ce8\u610f\u529b\u5b8c\u5168\u5438\u5f15\u8fc7\u53bb\uff0c\u4ee5\u81f3\u4e8e\u8bdd\u8bf4\u4e86\u4e00\u534a\u7ec8\u4e8e\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u59d0\u59d0\uff0c\u751f\u6c14\u4e86\uff1f\u201d\u4ed6\u4f4e\u58f0\u95ee\u3002</p><p>\u201c\u4f60\u8bf4\u5462\uff0c\u201d\u73ca\u5609\u6572\u4e86\u6572\u4ed6\u7684\u8111\u888b\uff0c\u201c\u4e00\u58f0\u4e0d\u54cd\u5c31\u7a81\u7136\u8d70\u4e86\uff0c\u8fde\u4e2a\u62db\u547c\u90fd\u6ca1\u6253\uff1b\u4e00\u51fa\u53bb\u5c31\u662f\u5feb\u56db\u4e2a\u6708\uff0c\u4e00\u70b9\u6d88\u606f\u90fd\u6ca1\u6709\uff0c\u8ba9\u6211\u5929\u5929\u5728\u5bb6\u91cc\u4e3a\u4f60\u62c5\u5fc3\uff1b\u597d\u4e0d\u5bb9\u6613\u56de\u6765\u4e86\uff0c\u8fd8\u5e26\u4e2a\u6f02\u4eae\u5973\u5b69\u5b50\u2026\u2026\u771f\u662f\u7684\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e00\u70b9\u90fd\u4e0d\u4e56\u3002\u201d</p><p>\u201c\u59d0\u59d0\u5ac9\u5992\u4e86\u5462\u3002\u201d</p><p>\u201c\u5f53\u7136\u5ac9\u5992\u554a\uff0c\u4ee5\u524d\u4f60\u662f\u6211\u5f1f\u5f1f\uff0c\u6211\u4eec\u76f8\u4f9d\u4e3a\u547d\uff0c\u8fd9\u4e48\u591a\u5e74\u4e00\u8d77\u8fc7\u6765\uff0c\u73b0\u5728\u773c\u770b\u5c31\u8981\u88ab\u522b\u7684\u5973\u5b69\u5b50\u62a2\u8d70\u4e86\u2014\u2014\u5982\u679c\u4f60\u6709\u4e2a\u59b9\u59b9\uff0c\u4ece\u5c0f\u8ddf\u4f60\u5728\u4e00\u8d77\uff0c\u73b0\u5728\u957f\u5927\u4e86\uff0c\u559c\u6b22\u522b\u7684\u7537\u5b69\u5b50\u4e86\uff0c\u5e26\u56de\u5bb6\u6765\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u5ac9\u5992\uff1f\u201d</p><p>\u201c\u5f53\u7136\u5ac9\u5992\u3002\u201d\u743c\u6069\u5df2\u7ecf\u6210\u529f\u54ac\u5f00\u4e86\u7b2c\u4e09\u7c92\u7ebd\u6263\uff0c\u628a\u8138\u6df1\u6df1\u57cb\u8fdb\u90a3\u56e2\u6d53\u90c1\u4e73\u9999\u4e2d\u4eab\u53d7\u7740\uff0c\u4e00\u8fb9\u542b\u542b\u7cca\u7cca\u5730\u8bf4\uff0c\u4f46\u4ed6\u540e\u9762\u5176\u5b9e\u8fd8\u6709\u53e5\u8bdd\u6ca1\u8bf4\u51fa\u53e3\u3002</p><p>\u201c\u4f55\u6b62\u5ac9\u5992\uff0c\u6211\u80af\u5b9a\u628a\u90a3\u4e2a\u7537\u4eba\u7ed9\u6740\u4e86\u3002\u5982\u679c\u6211\u6709\u4e2a\u6f02\u4eae\u59b9\u59b9\uff0c\u80af\u5b9a\u81ea\u5df1\u5148\u5077\u5403\u4e86\uff0c\u600e\u4e48\u53ef\u80fd\u8ba9\u7ed9\u522b\u4eba\u3002\u201d</p><p>\u201c\u90a3\u5c31\u662f\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u5f53\u7136\u4f1a\u5ac9\u5992\u561b\u3002\u4e0d\u8fc7\u8fd9\u4e5f\u662f\u6ca1\u529e\u6cd5\u7684\u4e8b\u60c5\u5566\uff0c\u5c0f\u5bb6\u4f19\u957f\u5927\u4e86\u5462\uff0c\u77e5\u9053\u80cc\u7740\u59d0\u59d0\u5077\u5077\u52fe\u5f15\u5973\u5b69\u5b50\u4e86\u3002\u201d</p><p>\u201c\u5582\uff0c\u59d0\u59d0\uff0c\u4ec0\u4e48\u53eb\u80cc\u7740\u4f60\u5077\u5077\u52fe\u5f15\uff0c\u201d\u743c\u6069\u5bf9\u8fd9\u4e2a\u8bc4\u4ef7\u5f88\u4e0d\u6ee1\uff0c\u201c\u6211\u5728\u5916\u9762\u5462\uff0c\u53c8\u6ca1\u6cd5\u56de\u57ce\u6765\uff0c\u600e\u4e48\u80fd\u7b97\u80cc\u7740\u4f60\uff0c\u53c8\u600e\u4e48\u7b97\u5077\u5077\u52fe\u5f15\u5462\u3002\u201d</p><p>\u201c\u54e6\uff0c\u662f\u4e48\u2026\u2026\u201d\u73ca\u5609\u6478\u7740\u4ed6\u7684\u5934\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u5982\u679c\u59d0\u59d0\u5728\u8eab\u8fb9\u7684\u8bdd\uff0c\u4f60\u770b\u4e0a\u54ea\u4e2a\u5973\u5b69\u5b50\u4e86\uff0c\u4f1a\u544a\u8bc9\u59d0\u59d0\uff1f\u201d</p><p>\u201c\u5f53\u7136\u5566\uff0c\u6211\u6709\u4ec0\u4e48\u4e8b\u4f1a\u7792\u7740\u59d0\u59d0\u5462\u3002\u201d</p><p>\u201c\u55ef\uff0c\u90a3\u5c31\u597d\uff0c\u4e0d\u7136\u59d0\u59d0\u4f1a\u751f\u6c14\u7684\u5462\u3002\u201d</p><p>\u201c\u5514\u5514\uff0c\u201d\u743c\u6069\u542b\u542b\u7cca\u7cca\u5730\u7b54\u5e94\u7740\uff0c\u8d34\u5728\u73ca\u5609\u6000\u91cc\uff0c\u95fb\u7740\u719f\u6089\u7684\u4f53\u9999\uff0c\u6709\u79cd\u7279\u522b\u5b89\u5fc3\u7684\u611f\u89c9\uff0c\u4ed6\u672c\u6765\u6ca1\u4ec0\u4e48\u7761\u610f\uff0c\u5982\u4eca\u5374\u4e5f\u6e10\u6e10\u89c9\u5f97\u6709\u4e9b\u8ff7\u7cca\u4e86\u3002\u201c\u59d0\u59d0\uff0c\u5f88\u665a\u4e86\uff0c\u7761\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u73ca\u5609\u5e94\u7740\uff0c\u65e0\u610f\u95f4\u6478\u5230\u4ed6\u7684\u624b\u81c2\u3002\u743c\u6069\u4e0a\u5e8a\u524d\u8131\u53bb\u4e86\u5916\u5957\uff0c\u53ea\u7a7f\u4e86\u8d34\u8eab\u7684\u5185\u8863\uff0c\u80f3\u818a\u90fd\u9732\u5728\u5916\u9762\uff0c\u73ca\u5609\u867d\u7136\u5728\u591c\u95f4\u9ed1\u6697\u91cc\u770b\u4e0d\u89c1\uff0c\u4f46\u624b\u4e00\u6478\u4e0a\u53bb\uff0c\u4fbf\u611f\u89c9\u5230\u4e86\u5f02\u6837\uff0c\u201c\u4f60\u8eab\u4e0a\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5514\uff1f\u54e6\uff0c\u59d0\u59d0\uff0c\u90a3\u662f\u9b54\u6cd5\u523a\u9752\uff0c\u662f\u524d\u5929\u5728\u585e\u5c14\u7684\u65f6\u5019\u7eb9\u4e0a\u7684\u3002\u201d</p><p>\u201c\u523a\u9752\uff1f\u8ba9\u6211\u770b\u770b\u3002\u201d</p><p>\u201c\u54e6\uff0c\u597d\u3002\u201d</p><p>\u743c\u6069\u6b63\u51c6\u5907\u8d77\u8eab\u70b9\u706f\uff0c\u5374\u542c\u89c1\u73ca\u5609\u8f7b\u8f7b\u5ff5\u8bf5\u4e86\u4e00\u53e5\u5492\u8bed\uff0c\u5c48\u6307\u4e00\u5f39\uff0c\u4e00\u56e2\u660e\u4eae\u7684\u5149\u7403\u4fbf\u4ece\u5979\u6307\u5c16\u98de\u51fa\uff0c\u5189\u5189\u6d6e\u5728\u534a\u7a7a\uff0c\u5c06\u5468\u56f4\u7684\u9ed1\u6697\u9a71\u6563\u4e86\u3002\u743c\u6069\u6014\u4f4f\uff0c\u4ed6\u81ea\u7136\u8ba4\u8bc6\u8fd9\u4e2a\u521d\u7ea7\u7684\u5c0f\u620f\u6cd5\uff0c\u5deb\u5e08\u5b66\u6821\u91cc\u53ea\u8981\u662f\u4e8c\u5e74\u7ea7\u4ee5\u4e0a\u7684\u5b66\u751f\u5c31\u6ca1\u6709\u4e0d\u4f1a\u7684\u2026\u2026\u4f46\u73ca\u5609\u6ca1\u5b66\u8fc7\u9b54\u6cd5\u554a\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u4f1a\u2026\u2026\u201d</p><p>\u201c\u4f60\u8d70\u4e86\uff0c\u5bb6\u53c8\u642c\u5230\u8fd9\u91cc\u6765\uff0c\u4ee5\u524d\u7684\u5546\u5e97\u4e5f\u4e0d\u7528\u5f00\u4e86\uff0c\u6211\u4e00\u4e2a\u4eba\u5728\u5bb6\u95f2\u5f97\u65e0\u804a\uff0c\u5c31\u5b66\u4e86\u70b9\u73a9\u73a9\u3002\u201d</p><p>\u201c\u4f60\u81ea\u5df1\u5b66\u7684\uff1f\u201d\u743c\u6069\u66f4\u52a0\u60ca\u8bb6\u4e86\u3002</p><p>\u9b54\u6cd5\u8fd9\u95e8\u5b66\u95ee\uff0c\u9664\u4e86\u90a3\u4e9b\u5929\u8d4b\u795e\u79d8\u8840\u8109\u7684\u672f\u58eb\u5916\uff0c\u57fa\u672c\u4e0d\u53ef\u80fd\u65e0\u5e08\u81ea\u901a\u3002\u73ca\u5609\u53c8\u4e0d\u662f\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\uff0c\u77ed\u77ed\u51e0\u4e2a\u6708\u5c45\u7136\u5c31\u80fd\u6d41\u5229\u65bd\u5c55\u5149\u4eae\u672f\u2014\u2014\u867d\u7136\u8fd9\u4ec5\u4ec5\u662f\u4e2a\u6700\u521d\u7ea7\u7684\u6cd5\u672f\uff0c\u4f46\u4e5f\u5df2\u7ecf\u5f88\u4e86\u4e0d\u8d77\u4e86\u3002</p><p>\u201c\u4e0d\u662f\uff0c\u670b\u53cb\u6559\u6211\u7684\uff0c\u201d\u73ca\u5609\u4e00\u8fb9\u8bf4\u4e00\u8fb9\u547d\u4ee4\u743c\u6069\u8db4\u5012\uff0c\u501f\u7740\u5149\u7403\u4ed4\u7ec6\u770b\u4ed6\u56db\u80a2\u4e0a\u7eb9\u7684\u523a\u9752\uff0c\u201c\u53cd\u6b63\u4e5f\u6ca1\u522b\u7684\u4e8b\u60c5\uff0c\u5b66\u7740\u73a9\u73a9\uff0c\u53ef\u60dc\u6211\u7b28\u5f97\u5f88\uff0c\u5230\u73b0\u5728\u4e5f\u53ea\u5b66\u4f1a\u4e86\u8fd9\u4e00\u4e2a\uff0c\u4f46\u4e5f\u65e0\u6240\u8c13\u4e86\u3002\u201d</p><p>\u670b\u53cb\uff1f\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u54ea\u4e2a\u670b\u53cb\uff1f</p><p>\u5148\u6709\u670b\u53cb\u9001\u4e86\u4e2a\u5973\u4ec6\uff0c\u5982\u4eca\u53c8\u6709\u670b\u53cb\u6559\u73ca\u5609\u9b54\u6cd5\u2014\u2014\u5e94\u8be5\u662f\u540c\u4e00\u4e2a\u4eba\uff0c\u800c\u4e14\u663e\u7136\u662f\u4e2a\u8d35\u65cf\uff0c\u4f46\u743c\u6069\u600e\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\u6709\u8fd9\u79cd\u670b\u53cb\u554a\u3002\u5170\u5c3c\u65af\u7279\u5bb6\u65cf\u4ee5\u524d\u4e16\u4ee3\u5e73\u6c11\uff0c\u548c\u8d35\u65cf\u4eec\u4ece\u65e0\u4ea4\u5f80\u7684\uff0c\u662f\u8fd9\u51e0\u4e2a\u6708\u91cc\u73ca\u5609\u65b0\u7ed3\u8bc6\u7684\u670b\u53cb\uff1f\u4f3c\u4e4e\u6709\u4e9b\u8fc7\u4e8e\u6bb7\u52e4\u5427\u3002</p><p>\u4e0d\u4f1a\u662f\u4e2a\u7537\u7684\u5427\u2026\u2026</p><p>\u743c\u6069\u53ea\u611f\u89c9\u6740\u610f\u4ece\u5fc3\u5934\u6084\u7136\u6d8c\u8d77\uff0c\u4eff\u4f5b\u60f3\u9a6c\u4e0a\u5c31\u51b2\u51fa\u53bb\u627e\u5230\u90a3\u4e2a\u80c6\u6562\u52fe\u5f15\u59d0\u59d0\u7684\u6df7\u86cb\uff0c\u7136\u540e\u628a\u4ed6\u75db\u6253\u4e00\u987f\uff0c\u52d2\u4ee4\u4ed6\u4ece\u6b64\u4e0d\u51c6\u63a5\u8fd1\u73ca\u5609\u767e\u6b65\u4e4b\u5185\uff0c\u5982\u679c\u65f6\u673a\u5408\u9002\u7d22\u6027\u5c31\u6740\u4eba\u706d\u53e3\u6bc1\u5c38\u706d\u8ff9\u3002\u4f46\u6b63\u5728\u4ed6\u6ee1\u8111\u5b50\u60f3\u7740\u66b4\u529b\u4e8b\u4ef6\u7684\u65f6\u5019\uff0c\u73ca\u5609\u8f7b\u8f7b\u62cd\u4e86\u62cd\u4ed6\u7684\u80cc\u3002</p><p>\u201c\u5514\uff0c\u7eb9\u5f97\u4e0d\u9519\u561b\uff0c\u633a\u6f02\u4eae\u7684\uff0c\u201d\u73ca\u5609\u4ed4\u7ec6\u770b\u5b8c\uff0c\u8bc4\u4ef7\u8bf4\uff0c\u201c\u5c31\u662f\u8272\u8c03\u4e0d\u592a\u597d\uff0c\u90fd\u504f\u9634\u51b7\uff0c\u5e94\u8be5\u7528\u4e9b\u660e\u5feb\u7684\u989c\u8272\u2026\u2026\u8fd9\u523a\u9752\u6709\u4ec0\u4e48\u7528\u554a\u3002\u201d</p><p>\u201c\u662f\u7528\u6765\u50a8\u5b58\u9b54\u6cd5\u7684\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u56db\u4e2a\u523a\u9752\uff0c\u5206\u522b\u8054\u63a5\u9b54\u7f51\u7684\u524d\u56db\u5c42\uff0c\u53ef\u4ee5\u5404\u81ea\u50a8\u5b58\u4e00\u4e2a\u6cd5\u672f\uff0c\u9700\u8981\u7684\u65f6\u5019\u5c31\u80fd\u76f4\u63a5\u6fc0\u53d1\u4e86\uff0c\u8fd9\u662f\u585e\u5c14\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u79d8\u6280\u3002\u201d</p><p>\u201c\u90a3\u53ea\u80fd\u7eb9\u56db\u4e2a\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u6700\u591a\u80fd\u7eb9\u4e03\u4e2a\uff0c\u4f46\u6211\u6682\u65f6\u7eb9\u4e0d\u4e86\uff0c\u8fd9\u4e1c\u897f\u662f\u548c\u5deb\u5e08\u7684\u9020\u8be3\u76f8\u5173\u7684\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u7184\u706d\u4e86\u5149\u7403\uff0c\u91cd\u65b0\u8eba\u4e86\u4e0b\u6765\uff0c\u4efb\u7531\u743c\u6069\u5c06\u5979\u62b1\u7740\u3002\u201c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5f00\u53e3\u95ee\uff0c\u201c\u4f60\u8bf4\u7684\u90a3\u4e2a\u6559\u4f60\u9b54\u6cd5\u7684\u670b\u53cb\u662f\u8c01\u554a\u3002\u201d</p><p>\u201c\u5c31\u662f\u6211\u4eec\u7684\u9694\u58c1\u90bb\u5c45\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u53c8\u4e0d\u7231\u51fa\u95e8\uff0c\u4e0d\u559c\u6b22\u4ea4\u9645\uff0c\u642c\u5230\u8fd9\u91cc\u4f4f\u4e86\u51e0\u4e2a\u6708\uff0c\u4e5f\u5c31\u8ba4\u8bc6\u5468\u56f4\u7684\u90bb\u5c45\u3002\u201d</p><p>\u9694\u58c1\u90bb\u5c45\uff1f\u743c\u6069\u56de\u5fc6\u7740\uff0c\u7136\u800c\u4ed6\u56de\u6765\u65f6\u4e00\u5fc3\u53ea\u60f3\u7740\u8d76\u5feb\u89c1\u5230\u73ca\u5609\uff0c\u538b\u6839\u5c31\u6ca1\u5728\u610f\u65c1\u8fb9\u7684\u623f\u5b50\u5efa\u7b51\uff0c\u4ec0\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\u3002\u201c\u90a3\u4e2a\u670b\u53cb\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u201c\u83ab\u5c3c\u5361\u3002\u201d</p><p>\u83ab\u5c3c\u5361\uff0c\u597d\uff0c\u8bb0\u4f4f\u4e86\uff0c\u660e\u5929\u53bb\u627e\u4ed6\u7b97\u8d26\uff0c\u7b49\u7b49\u2026\u2026\u8fd9\u540d\u5b57\u4e3a\u4ec0\u4e48\u542c\u8d77\u6765\u8fd9\u4e48\u8033\u719f\u7684\u611f\u89c9\u2026\u2026</p><p>\u201c\u5bf9\u4e86\uff0c\u4f60\u5e94\u8be5\u4e5f\u8ba4\u8bc6\u5440\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u4f4e\u5934\u5fae\u7b11\u7740\u770b\u4ed6\uff0c\u201c\u662f\u4e00\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\uff0c\u59d0\u59d0\u53eb\u8299\u8389\u5a05\uff0c\u59b9\u59b9\u53eb\u8299\u857e\u72c4\u2026\u2026\u6211\u7684\u9b54\u6cd5\u5c31\u662f\u8299\u857e\u72c4\u6559\u7684\uff0c\u5979\u4ee5\u524d\u662f\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\uff0c\u8bf4\u8ba4\u8bc6\u4f60\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u59d0\u59d0\uff0c\u4f60\u8bf4\u5979\u2018\u4ee5\u524d\u2019\u662f\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\uff1f\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u5979\u9000\u5b66\u4e86\u2014\u2014\u4e0d\u8fc7\u8fd9\u4e2a\u95ee\u9898\u6211\u4eec\u4ee5\u540e\u518d\u8c08\uff0c\u743c\u6069\uff0c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u521a\u624d\u597d\u50cf\u6709\u4e2a\u4eba\u8bf4\uff0c\u4ed6\u8bf4\u5982\u679c\u59d0\u59d0\u5728\u8eab\u8fb9\u7684\u8bdd\uff0c\u4ed6\u770b\u4e0a\u522b\u7684\u5973\u5b69\u5b50\uff0c\u662f\u4f1a\u544a\u8bc9\u59d0\u59d0\u7684\u54e6\u3002\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u6c89\u9ed8\u3002</p><p>\u201c\u5582\u5582\uff0c\u522b\u88c5\u7761\uff0c\u201d\u73ca\u5609\u63a8\u7740\u8d56\u5728\u6000\u91cc\u7684\u5f1f\u5f1f\uff0c\u4f46\u743c\u6069\u628a\u8138\u6df1\u57cb\u5728\u5979\u7684\u80f8\u53e3\uff0c\u518d\u4e5f\u4e0d\u80af\u9732\u9762\uff0c\u201c\u8d77\u6765\u628a\u8fd9\u4e2a\u95ee\u9898\u89e3\u91ca\u6e05\u695a\u3002\u201d</p><p>\u201c\u6211\u7761\u7740\u4e86\uff0c\u542c\u4e0d\u89c1\u3002\u201d</p></article>"}}}]);
